INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('01', '01 - O SANGUE DE JESUS TEM PODER', 'O SANGUE DE JESUS TEM PODER,\nPODER QUE A MIM PODE VALER,\nSE COMUNHÃO NÃO POSSO EU SENTIR, SENHOR,\nDERRAMA DO TEU SANGUE REMIDOR.\n\nCORO\nDERRAMA DO TEU SANGUE SOBRE MIM, Ó SENHOR,\nDERRAMA DO TEU SANGUE SOBRE MIM, Ó SENHOR,\nSEI QUE ESTÁS AQUI E ISTO QUERO EU SENTIR,\nDERRAMA DO TEU SANGUE SOBRE MIM.\n\nEM TUA PRESENÇA ESTOU, SENHOR,\nDE TI NECESSITO MUITO AMOR,\nPELO PODER QUE HÁ EM TEU SANGUE REMIDOR,\nÓ VEM PURIFICAR-ME, MEU SENHOR.\n\nCORO\nDERRAMA DO TEU SANGUE SOBRE MIM, Ó SENHOR,\nDERRAMA DO TEU SANGUE SOBRE MIM, Ó SENHOR,\nSEI QUE ESTÁS AQUI E ISTO QUERO EU SENTIR,\nDERRAMA DO TEU SANGUE SOBRE MIM.', 'O SANGUE DE JESUS TEM PODER, PODER QUE A MIM PODE VALER, SE COMUNHÃO NÃO POSSO EU SENTIR, SENHOR, DERRAMA DO TEU SANGUE REMIDOR. CORO DERRAMA DO TEU SANGUE SOBRE MIM, Ó SENHOR, DERRAMA DO TEU SANGUE SOBRE MIM, Ó SENHOR, SEI QUE ESTÁS AQUI E ISTO QUERO EU SENTIR, DERRAMA DO TEU SANGUE SOBRE MIM. EM TUA PRESENÇA ESTOU, SENHOR, DE TI NECESSITO MUITO AMOR, PELO PODER QUE HÁ EM TEU SANGUE REMIDOR, Ó VEM PURIFICAR-ME, MEU SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('02', '02 - O SANGUE DE JESUS TEM PODER PARA SALVAR', 'O SANGUE DE JESUS TEM PODER (2x)\nO SANGUE DE JESUS, O SANGUE DE JESUS,\nO SANGUE DE JESUS TEM PODER.\n\nO SANGUE DE JESUS TEM PODER PARA SALVAR,\nO SANGUE DE JESUS TEM PODER PARA CURAR,\nO SANGUE DE JESUS, O SANGUE DE JESUS\nTEM PODER PARA O OPRIMIDO LIBERTAR.', 'O SANGUE DE JESUS TEM PODER  O SANGUE DE JESUS, O SANGUE DE JESUS, O SANGUE DE JESUS TEM PODER. O SANGUE DE JESUS TEM PODER PARA SALVAR, O SANGUE DE JESUS TEM PODER PARA CURAR, O SANGUE DE JESUS, O SANGUE DE JESUS TEM PODER PARA O OPRIMIDO LIBERTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('03', '03 - CLAMO A TI', 'CLAMO A TI, Ó MEU SENHOR,\nCLAMO A TI, MEU SALVADOR,\nPELO TEU SANGUE, QUE É VIDA,\nÓ BENDITO, VEM ME LIBERTAR. \n\nQUANDO ALÉM DO VÉU ENTRAR,\nIREI TE DAR TODO O MEU SER,	\nEM TUA PRESENÇA, TE ADORAR,\nTE BENDIZER.	 \nREPETIR A 1ª ESTROFE 1X\nBIS', 'CLAMO A TI, Ó MEU SENHOR, CLAMO A TI, MEU SALVADOR, PELO TEU SANGUE, QUE É VIDA, Ó BENDITO, VEM ME LIBERTAR. QUANDO ALÉM DO VÉU ENTRAR, IREI TE DAR TODO O MEU SER, EM TUA PRESENÇA, TE ADORAR, TE BENDIZER. REPETIR A 1ª ESTROFE', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('04', '04 - QUANDO TE PROSTRARES', 'QUANDO TE PROSTRARES DIANTE DE DEUS,\nLEMBRA QUE JESUS ESTÁ CONTIGO, \nPEDE SEU AUXÍLIO, PEDE SEU PERDÃO \nLEMBRA QUE JESUS É TEU ABRIGO.\nBIS\n\nDEIXA QUE O SENHOR\nMUDE O TEU VIVER;\nLEMBRA QUE JESUS É TEU AMIGO.\n\nVENHA SOBRE NÓS, \n(Ó DEUS) VARÕES\nVENHA SOBRE NÓS, \nSEJA SOBRE NÓS A TUA GRAÇA.\nBIS', 'QUANDO TE PROSTRARES DIANTE DE DEUS, LEMBRA QUE JESUS ESTÁ CONTIGO, PEDE SEU AUXÍLIO, PEDE SEU PERDÃO LEMBRA QUE JESUS É TEU ABRIGO. DEIXA QUE O SENHOR MUDE O TEU VIVER; LEMBRA QUE JESUS É TEU AMIGO. VENHA SOBRE NÓS, (Ó DEUS) VARÕES VENHA SOBRE NÓS, SEJA SOBRE NÓS A TUA GRAÇA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('05', '05 - CLAMANDO ESTOU', 'CLAMANDO ESTOU, Ó MEU JESUS,\nPOR TEU SANGUE REMIDOR;\nSOCORRO EU SEI QUE MANDARÁS\nE LIVRAMENTO A MIM.\n\nTEU SANGUE PURO DERRAMASTE\nPARA ME PURIFICAR;\nENTÃO ADORAREI A TI,\nÓ MEU SENHOR.', 'CLAMANDO ESTOU, Ó MEU JESUS, POR TEU SANGUE REMIDOR; SOCORRO EU SEI QUE MANDARÁS E LIVRAMENTO A MIM. TEU SANGUE PURO DERRAMASTE PARA ME PURIFICAR; ENTÃO ADORAREI A TI, Ó MEU SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('06', '06 - JESUS, Ó MEU JESUS', 'JESUS, Ó MEU JESUS,\nNESTA HORA ME CHEGO A TI\nPRA CLAMAR PELO TEU SANGUE\nE SENTIR-TE PERTO DE MIM.\n\nÓ DÁ-ME LIBERTAÇÃO E MUITA COMUNHÃO, \nE PERMITE, Ó SENHOR,\nQUE EU SEJA USADO EM TUAS MÃOS.\nBIS', 'JESUS, Ó MEU JESUS, NESTA HORA ME CHEGO A TI PRA CLAMAR PELO TEU SANGUE E SENTIR-TE PERTO DE MIM. Ó DÁ-ME LIBERTAÇÃO E MUITA COMUNHÃO, E PERMITE, Ó SENHOR, QUE EU SEJA USADO EM TUAS MÃOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('07', '07 - NESTA HORA EM ORAÇÃO', 'NESTA HORA EM ORAÇÃO, \nPEDIMOS O PERDÃO NO SANGUE DE JESUS.\nNESTA HORA EM ORAÇÃO, \nBUSCAMOS COMUNHÃO, PRA ADORAR.\n\nABENÇOA-NOS SENHOR,\nDERRAMA SOBRE NÓS TUA PAZ\nABENÇOA-NOS SENHOR,\nDERRAMA SOBRE NÓS TEU AMOR.\n\nINSTRUMENTOS\n\nNESTA HORA EM ORAÇÃO, \nPEDIMOS O PERDÃO NO SANGUE DE JESUS.\nNESTA HORA EM ORAÇÃO, \nBUSCAMOS COMUNHÃO, PRA ADORAR.\n\nABENÇOA-NOS SENHOR,\nDERRAMA SOBRE NÓS TUA PAZ\nABENÇOA-NOS SENHOR,\nDERRAMA SOBRE NÓS TEU AMOR.', 'NESTA HORA EM ORAÇÃO, PEDIMOS O PERDÃO NO SANGUE DE JESUS. NESTA HORA EM ORAÇÃO, BUSCAMOS COMUNHÃO, PRA ADORAR. ABENÇOA-NOS SENHOR, DERRAMA SOBRE NÓS TUA PAZ ABENÇOA-NOS SENHOR, DERRAMA SOBRE NÓS TEU AMOR. INSTRUMENTOS ABENÇOA-NOS SENHOR, DERRAMA SOBRE NÓS TUA PAZ ABENÇOA-NOS SENHOR, DERRAMA SOBRE NÓS TEU AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('08', '08 - PELO SANGUE DE JESUS', 'PELO SANGUE DE JESUS\nCLAMO A TI, MEU SENHOR;\nNELE HÁ PERDÃO, PAZ, COMUNHÃO,\nVIDA PARA O MEU SER!\n\nNOS TEUS ÁTRIOS QUERO ENTRAR\nCOBERTO POR ESTE SANGUE,\nPARA TUA GLÓRIA VER E TE ADORAR!\n\nPELO SANGUE DE JESUS\nCLAMO A TI, MEU SENHOR;\nNELE HÁ PERDÃO, PAZ, COMUNHÃO,\nVIDA PARA O MEU SER!\n\nNOS TEUS ÁTRIOS QUERO ENTRAR\nCOBERTO POR ESTE SANGUE,\nPARA TUA GLÓRIA VER E TE ADORAR!\n\nFINAL: \nPARA TUA GLÓRIA VER E TE ADORAR!', 'PELO SANGUE DE JESUS CLAMO A TI, MEU SENHOR; NELE HÁ PERDÃO, PAZ, COMUNHÃO, VIDA PARA O MEU SER! NOS TEUS ÁTRIOS QUERO ENTRAR COBERTO POR ESTE SANGUE, PARA TUA GLÓRIA VER E TE ADORAR! NOS TEUS ÁTRIOS QUERO ENTRAR COBERTO POR ESTE SANGUE, PARA TUA GLÓRIA VER E TE ADORAR!  PARA TUA GLÓRIA VER E TE ADORAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('09', '09 - SENHOR, ME CHEGO A TI', 'SENHOR, ME CHEGO A TI,\nNESTA HORA DE ORAÇÃO;\nSENHOR, ME CHEGO A TI,\nPRA PEDIR-TE MAIS COMUNHÃO E AMOR.\nHUMILDE E FRACO SOU,\n\nHUMILDE E FRACO SOU,\nFAZE-ME VIVER NA TUA LUZ;\nLIBERTA-ME, SENHOR,\nÓ VEM PASSAR O TEU SANGUE\nE PURIFICA-ME, SENHOR.', 'SENHOR, ME CHEGO A TI, NESTA HORA DE ORAÇÃO; SENHOR, ME CHEGO A TI, PRA PEDIR-TE MAIS COMUNHÃO E AMOR. HUMILDE E FRACO SOU, HUMILDE E FRACO SOU, FAZE-ME VIVER NA TUA LUZ; LIBERTA-ME, SENHOR, Ó VEM PASSAR O TEU SANGUE E PURIFICA-ME, SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('10', '10 - VAMOS LAVAR AS VESTES', 'VAMOS LAVAR AS VESTES \nNO SANGUE DO CORDEIRO. \n\nQUANDO JESUS VIER NAQUELE GRANDE DIA,\nAS VESTES ESTANDO LIMPAS,\nTEMOS PAZ E ALEGRIA.\nBIS', 'VAMOS LAVAR AS VESTES NO SANGUE DO CORDEIRO. QUANDO JESUS VIER NAQUELE GRANDE DIA, AS VESTES ESTANDO LIMPAS, TEMOS PAZ E ALEGRIA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('11', '11 - REVESTE, SENHOR, TEU POVO', 'REVESTE, SENHOR, TEU POVO \n(SIM, REVESTE)\n\nCOM O SANGUE DA CRUZ,\nO SANGUE DE JESUS,\nREVESTE, SENHOR, TEU POVO.\nBIS', 'REVESTE, SENHOR, TEU POVO (SIM, REVESTE) COM O SANGUE DA CRUZ, O SANGUE DE JESUS, REVESTE, SENHOR, TEU POVO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('12', '12 - HÁ VITÓRIA PARA MIM', 'HÁ VITÓRIA PARA MIM,  (BIS)\nPELO SANGUE DO CORDEIRO,\nHÁ VITÓRIA PARA MIM.\n\nVENCER! VENCER! VENCER! VENCER!\nPELO SANGUE DO CORDEIRO,\nEU IREI VENCER.', 'HÁ VITÓRIA PARA MIM,  PELO SANGUE DO CORDEIRO, HÁ VITÓRIA PARA MIM. VENCER! VENCER! VENCER! VENCER! PELO SANGUE DO CORDEIRO, EU IREI VENCER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('13', '13 - PÕE TEU SANGUE SOBRE MIM', 'PÕE TEU SANGUE SOBRE MIM, (BIS)\nPORÇÃO DOBRADA, Ó SENHOR, ME DÁ,\nPÕE TEU SANGUE SOBRE MIM.', 'PÕE TEU SANGUE SOBRE MIM,  PORÇÃO DOBRADA, Ó SENHOR, ME DÁ, PÕE TEU SANGUE SOBRE MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('14', '14 - EM TEU ALTAR, SENHOR', 'EM TEU ALTAR, SENHOR, ENTREGO MINHA VIDA,\nEM TEU ALTAR, SENHOR, PURIFICA MINHA VIDA,\nCOM O SANGUE DO CORDEIRO;\nMINHA VIDA ENTREGO EM TEU ALTAR.\n\nREPETIR O LOUVOR', 'EM TEU ALTAR, SENHOR, ENTREGO MINHA VIDA, EM TEU ALTAR, SENHOR, PURIFICA MINHA VIDA, COM O SANGUE DO CORDEIRO; MINHA VIDA ENTREGO EM TEU ALTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('15', '15 - CLAMAREI, CLAMAREI', 'CLAMAREI, CLAMAREI\nPELO SANGUE DE JESUS;\nELE É PAZ E SEGURANÇA,\nALEGRIA, VIDA E LUZ.', 'CLAMAREI, CLAMAREI PELO SANGUE DE JESUS; ELE É PAZ E SEGURANÇA, ALEGRIA, VIDA E LUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('16', '16 - SE ANDARMOS NA LUZ', 'SE ANDARMOS NA LUZ, \nCOMO NA LUZ ELE ESTÁ, \n\nTEMOS COMUNHÃO COM O SENHOR\nE TAMBÉM COM OS IRMÃOS,\nSE ANDARMOS NA LUZ,\nCOMO NA LUZ ELE ESTÁ.\nBIS\n\nE ASSIM O SANGUE DE JESUS \nTEM PODER PARA SALVAR, \nTEM PODER PARA CURAR,  \nTEM PODER PARA PERDOAR \nE TAMBÉM PURIFICAR.\nBIS', 'SE ANDARMOS NA LUZ, COMO NA LUZ ELE ESTÁ, TEMOS COMUNHÃO COM O SENHOR E TAMBÉM COM OS IRMÃOS, SE ANDARMOS NA LUZ, COMO NA LUZ ELE ESTÁ. E ASSIM O SANGUE DE JESUS TEM PODER PARA SALVAR, TEM PODER PARA CURAR, TEM PODER PARA PERDOAR E TAMBÉM PURIFICAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('17', '17 - EM TUA PRESENÇA ENTRAMOS', 'EM TUA PRESENÇA ENTRAMOS\nCLAMANDO A TI, Ó SENHOR,\nLAVA-NOS COM O TEU SANGUE,\nRENOVA EM NÓS TEU AMOR.\n\nCOM TEU ESPÍRITO SANTO\nVEM ENTRE NÓS PRESIDIR.\nSEJA ESTE CULTO UM INCENSO\nQUE POSSA AOS CÉUS SUBIR.\n\nEM TEU ALTAR ENTREGAMOS\nAS NOSSAS VIDAS, SENHOR,\nDE CORAÇÃO OFERTAMOS\nA TI O NOSSO LOUVOR.\n\nÓ CRISTO VIVO, REVELA\nTUA PRESENÇA ENTRE NÓS,\nCOM TUA GRAÇA OPERA,\nFAZE-NOS OUVIR TUA VOZ.\n\nEM TUA PRESENÇA ENTRAMOS\nCLAMANDO A TI, Ó SENHOR,\nLAVA-NOS COM O TEU SANGUE,\nRENOVA EM NÓS TEU AMOR.', 'EM TUA PRESENÇA ENTRAMOS CLAMANDO A TI, Ó SENHOR, LAVA-NOS COM O TEU SANGUE, RENOVA EM NÓS TEU AMOR. COM TEU ESPÍRITO SANTO VEM ENTRE NÓS PRESIDIR. SEJA ESTE CULTO UM INCENSO QUE POSSA AOS CÉUS SUBIR. EM TEU ALTAR ENTREGAMOS AS NOSSAS VIDAS, SENHOR, DE CORAÇÃO OFERTAMOS A TI O NOSSO LOUVOR. Ó CRISTO VIVO, REVELA TUA PRESENÇA ENTRE NÓS, COM TUA GRAÇA OPERA, FAZE-NOS OUVIR TUA VOZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('18', '18 - PROSTRADO ESTOU DIANTE DE TI', 'PROSTRADO ESTOU DIANTE DE TI;\nPERDÃO IMPLORO POR MEU PECAR.\nÉ GRANDE A MINHA CULPA, MAS ÉS AMOR!\nESCUTA A VOZ DO MEU CLAMOR.', 'PROSTRADO ESTOU DIANTE DE TI; PERDÃO IMPLORO POR MEU PECAR. É GRANDE A MINHA CULPA, MAS ÉS AMOR! ESCUTA A VOZ DO MEU CLAMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('19', '19 - TEU POVO CLAMA', 'TEU POVO CLAMA EM ORAÇÃO \nPELO TEU SANGUE, LIBERTAÇÃO, \nPELO PECADO, MISERICÓRDIA, \nTEU POVO CLAMA, SENHOR JESUS.\nBIS', 'TEU POVO CLAMA EM ORAÇÃO PELO TEU SANGUE, LIBERTAÇÃO, PELO PECADO, MISERICÓRDIA, TEU POVO CLAMA, SENHOR JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('20', '20 - O TEU PECADO TU QUERES DEIXAR?', 'O TEU PECADO TU QUERES DEIXAR?\nNO SANGUE HÁ PODER, SIM, HÁ PODER.\nQUERES DO MAL A VITÓRIA GANHAR?\nSEU SANGUE TEM ESTE PODER.\n\nCORO\nHÁ PODER, SIM, FORÇA E VIGOR,\nNESTE SANGUE DE JESUS;\nHÁ PODER, SIM, NO BOM SALVADOR,\nOH! CONFIA NO CRISTO DA CRUZ.\n\nQUERES OS VÍCIOS ABANDONAR?\nNO SANGUE HÁ PODER, SIM, HÁ PODER.\nCONFIA EM CRISTO PARA TE CURAR,\nSEU SANGUE TEM ESTE PODER.\n\nCORO\nHÁ PODER, SIM, FORÇA E VIGOR,\nNESTE SANGUE DE JESUS;\nHÁ PODER, SIM, NO BOM SALVADOR,\nOH! CONFIA NO CRISTO DA CRUZ.\n\nQUERES PUREZA PARA O TEU CORAÇÃO?\nNO SANGUE HÁ PODER, SIM, HÁ PODER.\nMAIS LEALDADE, MAIS CONSAGRAÇÃO,\nSEU SANGUE TEM ESTE PODER.\n\nCORO\nHÁ PODER, SIM, FORÇA E VIGOR,\nNESTE SANGUE DE JESUS;\nHÁ PODER, SIM, NO BOM SALVADOR,\nOH! CONFIA NO CRISTO DA CRUZ.\n\nQUERES DE CRISTO A MENSAGEM LEVAR?\nNO SANGUE HÁ PODER, SIM, HÁ PODER.\nQUERES COM OS ANJOS NA GLÓRIA CANTAR? \nSEU SANGUE TEM ESTE PODER.\n\nCORO\nHÁ PODER, SIM, FORÇA E VIGOR,\nNESTE SANGUE DE JESUS;\nHÁ PODER, SIM, NO BOM SALVADOR,\nOH! CONFIA NO CRISTO DA CRUZ.', 'O TEU PECADO TU QUERES DEIXAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. QUERES DO MAL A VITÓRIA GANHAR? SEU SANGUE TEM ESTE PODER. CORO HÁ PODER, SIM, FORÇA E VIGOR, NESTE SANGUE DE JESUS; HÁ PODER, SIM, NO BOM SALVADOR, OH! CONFIA NO CRISTO DA CRUZ. QUERES OS VÍCIOS ABANDONAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. CONFIA EM CRISTO PARA TE CURAR, SEU SANGUE TEM ESTE PODER. QUERES PUREZA PARA O TEU CORAÇÃO? NO SANGUE HÁ PODER, SIM, HÁ PODER. MAIS LEALDADE, MAIS CONSAGRAÇÃO, SEU SANGUE TEM ESTE PODER. QUERES DE CRISTO A MENSAGEM LEVAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. QUERES COM OS ANJOS NA GLÓRIA CANTAR? SEU SANGUE TEM ESTE PODER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('21', '21 - NESTA HORA DE CLAMOR', 'NESTA HORA DE CLAMOR\nPELO SANGUE DE JESUS (VARÕES)\nNECESSITADO, DEPENDENTE,\nME COLOCO AOS TEUS PÉS, Ó PAI.\n\nCORO\nDÁ-ME O PÃO COTIDIANO, \nESSE PÃO ÉS TU JESUS;\nTUA PAZ É O MEU ALIMENTO,\nDÁ-ME DE TI, SENHOR.\n\nAS RIQUEZAS QUE EU ENCONTRO EM TI,\nSÃO PRA MIM MEU BEM MAIOR.\nEM TI ESPERO, EM TI CONFIO.\nDE TI VEM MINHA SALVAÇÃO.\n\nCORO\nDÁ-ME O PÃO COTIDIANO, \nESSE PÃO ÉS TU JESUS;\nTUA PAZ É O MEU ALIMENTO,\nDÁ-ME DE TI, SENHOR.\n\nINSTRUMENTOS\n\nNA ETERNIDADE HABITAREI.\nLÁ VEREI O MEU GRANDE REI!\nRUAS DE OURO, RIOS DE CRISTAL.\nGLÓRIA AO TEU SANTO ESPÍRITO!\nBIS', 'NESTA HORA DE CLAMOR PELO SANGUE DE JESUS  NECESSITADO, DEPENDENTE, ME COLOCO AOS TEUS PÉS, Ó PAI. CORO DÁ-ME O PÃO COTIDIANO, ESSE PÃO ÉS TU JESUS; TUA PAZ É O MEU ALIMENTO, DÁ-ME DE TI, SENHOR. AS RIQUEZAS QUE EU ENCONTRO EM TI, SÃO PRA MIM MEU BEM MAIOR. EM TI ESPERO, EM TI CONFIO. DE TI VEM MINHA SALVAÇÃO. CORO DÁ-ME O PÃO COTIDIANO, ESSE PÃO ÉS TU JESUS; TUA PAZ É O MEU ALIMENTO, DÁ-ME DE TI, SENHOR. INSTRUMENTOS NA ETERNIDADE HABITAREI. LÁ VEREI O MEU GRANDE REI! RUAS DE OURO, RIOS DE CRISTAL. GLÓRIA AO TEU SANTO ESPÍRITO!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('22', '22 - BENDITO SEJA O CORDEIRO', 'BENDITO SEJA O CORDEIRO\nQUE NA CRUZ POR NÓS PADECEU!\nBENDITO SEJA O SEU SANGUE\nQUE POR NÓS ALI ELE VERTEU!\nEIS NESSE SANGUE LAVADOS,\nCOM ROUPAS QUE TÃO ALVAS SÃO,\nOS PECADORES REMIDOS\nQUE PERANTE SEU DEUS JÁ ESTÃO!\n\nCORO\nALVO MAIS QUE A NEVE! (BIS)\nSIM, NESTE SANGUE LAVADO,\nMAIS ALVO QUE A NEVE SEREI!\n\nQUÃO ESPINHOSA COROA\nQUE JESUS POR NÓS SUPORTOU!\nOH! QUÃO PROFUNDAS AS CHAGAS\nQUE NOS PROVAM QUANTO ELE AMOU!\n\nEIS NESSAS CHAGAS PUREZA\nPARA O MAIOR PECADOR!\nPOIS QUE MAIS ALVOS QUE A NEVE,\nO TEU SANGUE NOS TORNA, SENHOR!\n\nCORO\nALVO MAIS QUE A NEVE! (BIS)\nSIM, NESTE SANGUE LAVADO,\nMAIS ALVO QUE A NEVE SEREI!\n\nSE NÓS A TI CONFESSARMOS\nE SEGUIRMOS NA TUA LUZ,\nTU NÃO SOMENTE PERDOAS,\nPURIFICAS TAMBÉM, Ó JESUS!\n\nSIM, E DE TODO O PECADO!\nQUE MARAVILHA DE AMOR!\nPOIS QUE MAIS ALVOS QUE A NEVE\nO TEU SANGUE NOS TORNA, SENHOR!\n\nCORO\nALVO MAIS QUE A NEVE! (BIS)\nSIM, NESTE SANGUE LAVADO,\nMAIS ALVO QUE A NEVE SEREI!', 'BENDITO SEJA O CORDEIRO QUE NA CRUZ POR NÓS PADECEU! BENDITO SEJA O SEU SANGUE QUE POR NÓS ALI ELE VERTEU! EIS NESSE SANGUE LAVADOS, COM ROUPAS QUE TÃO ALVAS SÃO, OS PECADORES REMIDOS QUE PERANTE SEU DEUS JÁ ESTÃO! CORO ALVO MAIS QUE A NEVE!  SIM, NESTE SANGUE LAVADO, MAIS ALVO QUE A NEVE SEREI! QUÃO ESPINHOSA COROA QUE JESUS POR NÓS SUPORTOU! OH! QUÃO PROFUNDAS AS CHAGAS QUE NOS PROVAM QUANTO ELE AMOU! EIS NESSAS CHAGAS PUREZA PARA O MAIOR PECADOR! POIS QUE MAIS ALVOS QUE A NEVE, O TEU SANGUE NOS TORNA, SENHOR! SE NÓS A TI CONFESSARMOS E SEGUIRMOS NA TUA LUZ, TU NÃO SOMENTE PERDOAS, PURIFICAS TAMBÉM, Ó JESUS! SIM, E DE TODO O PECADO! QUE MARAVILHA DE AMOR! POIS QUE MAIS ALVOS QUE A NEVE O TEU SANGUE NOS TORNA, SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('23', '23 - SENHOR, OUVE A MINHA  ORAÇÃO', 'SENHOR, OUVE A MINHA ORAÇÃO\nE DÁ-ME PLENA COMUNHÃO,\nLIBERTA DO MEU VIL PECADO;\nSENHOR, OUVE A MINHA ORAÇÃO.\n\nTRANSBORDA, INUNDA O MEU SER,\nTRANSFORMA, FAZE EM MIM TEU QUERER,\nDERRAMA DO SANGUE DE CRISTO;\nSENHOR, OUVE A MINHA ORAÇÃO.', 'SENHOR, OUVE A MINHA ORAÇÃO E DÁ-ME PLENA COMUNHÃO, LIBERTA DO MEU VIL PECADO; SENHOR, OUVE A MINHA ORAÇÃO. TRANSBORDA, INUNDA O MEU SER, TRANSFORMA, FAZE EM MIM TEU QUERER, DERRAMA DO SANGUE DE CRISTO; SENHOR, OUVE A MINHA ORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('24', '24 - EIS O CORDEIRO DE DEUS', 'EIS O CORDEIRO DE DEUS\nQUE TIRA O PECADO DO MUNDO,\nQUE MORREU NO MEU LUGAR,\nENTÃO PODE ME PERDOAR.\n\nCORO\nCLAMO AGORA PEDINDO:\nVEM PERDOAR, (VEM PERDOAR) \nPURIFICAR, (PURIFICAR) \nVEM TRANSFORMAR E RENOVAR,\nVEM RESTAURAR.\n\nDIANTE DO TEU ALTAR\nDEIXO A MINHA ANSIEDADE,\nPELO SANGUE DE JESUS, \nTENHO VIDA.\n\nCORO\nCLAMO AGORA PEDINDO:\nVEM PERDOAR, (VEM PERDOAR) \nPURIFICAR, (PURIFICAR) \nVEM TRANSFORMAR E RENOVAR,\nVEM RESTAURAR.\n\nFINAL: VEM RESTAURAR.', 'EIS O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, QUE MORREU NO MEU LUGAR, ENTÃO PODE ME PERDOAR. CORO CLAMO AGORA PEDINDO: VEM PERDOAR, (VEM PERDOAR) PURIFICAR, (PURIFICAR) VEM TRANSFORMAR E RENOVAR, VEM RESTAURAR. DIANTE DO TEU ALTAR DEIXO A MINHA ANSIEDADE, PELO SANGUE DE JESUS, TENHO VIDA. CORO CLAMO AGORA PEDINDO: VEM PERDOAR, (VEM PERDOAR) PURIFICAR, (PURIFICAR) VEM TRANSFORMAR E RENOVAR, VEM RESTAURAR.  VEM RESTAURAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('25', '25 - SENHOR, A TI CLAMO', 'SENHOR, A TI CLAMO,\nINCLINA OS TEUS OUVIDOS \nÀ MINHA VOZ; LAVA A MINH’ALMA \nCOM O SANGUE DE JESUS,  \nSUPLICO, SENHOR!\nBIS\n\nDÁ-ME UM CÂNTICO NOVO,\nFAZ MEU LOUVOR TOCAR JESUS;\nÓ DEUS, INCLINA OS TEUS \nOUVIDOS E OUVE A MINHA VOZ!\nBIS', 'SENHOR, A TI CLAMO, INCLINA OS TEUS OUVIDOS À MINHA VOZ; LAVA A MINH’ALMA COM O SANGUE DE JESUS, SUPLICO, SENHOR! DÁ-ME UM CÂNTICO NOVO, FAZ MEU LOUVOR TOCAR JESUS; Ó DEUS, INCLINA OS TEUS OUVIDOS E OUVE A MINHA VOZ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('26', '26 - SEJA SÓ O SENHOR LOUVADO AQUI', 'SEJA SÓ O SENHOR LOUVADO AQUI, ALELUIA!\nNESTE ABENÇOADO MAANAIM, ALELUIA!\nPOIS PRESENTE ELE ESTÁ,\nQUER A TODOS ABENÇOAR,\nSUA GRAÇA DERRAMAR, ALELUIA!\n\nE PELO TEU SANGUE CLAMAMOS, SENHOR, ALELUIA!\nTEU PRECIOSO SANGUE REMIDOR, ALELUIA!\nPÕE TEU SANGUE SOBRE MIM,\nDE TODO O MAL LIVRA-ME E ASSIM,\nGOZO ENTÃO TEREI SEM FIM, ALELUIA!', 'SEJA SÓ O SENHOR LOUVADO AQUI, ALELUIA! NESTE ABENÇOADO MAANAIM, ALELUIA! POIS PRESENTE ELE ESTÁ, QUER A TODOS ABENÇOAR, SUA GRAÇA DERRAMAR, ALELUIA! E PELO TEU SANGUE CLAMAMOS, SENHOR, ALELUIA! TEU PRECIOSO SANGUE REMIDOR, ALELUIA! PÕE TEU SANGUE SOBRE MIM, DE TODO O MAL LIVRA-ME E ASSIM, GOZO ENTÃO TEREI SEM FIM, ALELUIA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('27', '27 - CORDEIRO ETERNO', 'CORDEIRO ETERNO, \nVIESTE PRA ME DAR\nTUA VIDA, TEU SANGUE, \nPRA QUE EU PUDESSE TER\nA VIDA ETERNA E O PERDÃO,\nA CURA, A PAZ, LIBERTAÇÃO,\nCORDEIRO ETERNO DE DEUS.\n\nE HOJE TEU SANGUE ESTÁ \nAGINDO EM MIM,\nLIMPANDO, CURANDO, \nMUDANDO O MEU SER;\nOH! QUE PODER E VIDA HÁ\nNO SANGUE QUE JESUS VERTEU, \nCORDEIRO ETERNO DE DEUS. \n\nFINAL: \nCORDEIRO ETERNO DE DEUS.\nBIS', 'CORDEIRO ETERNO, VIESTE PRA ME DAR TUA VIDA, TEU SANGUE, PRA QUE EU PUDESSE TER A VIDA ETERNA E O PERDÃO, A CURA, A PAZ, LIBERTAÇÃO, CORDEIRO ETERNO DE DEUS. E HOJE TEU SANGUE ESTÁ AGINDO EM MIM, LIMPANDO, CURANDO, MUDANDO O MEU SER; OH! QUE PODER E VIDA HÁ NO SANGUE QUE JESUS VERTEU, CORDEIRO ETERNO DE DEUS.  CORDEIRO ETERNO DE DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('28', '28 - O PODER DO SANGUE DE JESUS', 'O PODER DO SANGUE DE JESUS\nHÁ DE ME PURIFICAR,\nMEU PECADO PERDOAR;\nO PODER DO SANGUE DE JESUS\nDÁ-ME PLENA COMUNHÃO,\nTRAZ-ME A REVELAÇÃO.\n\nCORO\nESTOU LIBERTO NA PRESENÇA DO SENHOR,\nNOS SEUS ÁTRIOS HEI DE ENTRAR,\nCOM LOUVOR GLORIFICAR;\nSUA GLÓRIA RESPLANDECE NO ALTAR,\nSEU ESPÍRITO ME ALCANÇA COM AMOR.\n\nO PODER DO SANGUE DE JESUS\nFAZ-ME CAMINHAR NA LUZ,\nBEM SEGURO ME CONDUZ.\n\nCORO\nESTOU LIBERTO NA PRESENÇA DO SENHOR,\nNOS SEUS ÁTRIOS HEI DE ENTRAR,\nCOM LOUVOR GLORIFICAR;\nSUA GLÓRIA RESPLANDECE NO ALTAR,\nSEU ESPÍRITO ME ALCANÇA COM AMOR.\n\nFINAL:	\nHÁ PODER NO SANGUE DE JESUS (BIS)', 'O PODER DO SANGUE DE JESUS HÁ DE ME PURIFICAR, MEU PECADO PERDOAR; O PODER DO SANGUE DE JESUS DÁ-ME PLENA COMUNHÃO, TRAZ-ME A REVELAÇÃO. CORO ESTOU LIBERTO NA PRESENÇA DO SENHOR, NOS SEUS ÁTRIOS HEI DE ENTRAR, COM LOUVOR GLORIFICAR; SUA GLÓRIA RESPLANDECE NO ALTAR, SEU ESPÍRITO ME ALCANÇA COM AMOR. O PODER DO SANGUE DE JESUS FAZ-ME CAMINHAR NA LUZ, BEM SEGURO ME CONDUZ. CORO ESTOU LIBERTO NA PRESENÇA DO SENHOR, NOS SEUS ÁTRIOS HEI DE ENTRAR, COM LOUVOR GLORIFICAR; SUA GLÓRIA RESPLANDECE NO ALTAR, SEU ESPÍRITO ME ALCANÇA COM AMOR.  HÁ PODER NO SANGUE DE JESUS', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('29', '29 - TRANSBORDA-ME, SENHOR', 'TRANSBORDA-ME, SENHOR, DÁ-ME COMUNHÃO;\nSENTIR TUA PRESENÇA EU QUERO, Ó PAI;\nFALA AO MEU CORAÇÃO, NO SANGUE HÁ VIDA,\nHÁ LIBERTAÇÃO, SEGURANÇA E PAZ,  \nTOCA-ME, SENHOR.\n\nTRANSBORDA-ME, SENHOR, DÁ-ME COMUNHÃO;\nSENTIR TUA PRESENÇA EU QUERO, Ó PAI;\nFALA AO MEU CORAÇÃO, NO SANGUE HÁ VIDA,\nHÁ LIBERTAÇÃO, SEGURANÇA E PAZ,  \nTOCA-ME, SENHOR.\nBIS', 'TRANSBORDA-ME, SENHOR, DÁ-ME COMUNHÃO; SENTIR TUA PRESENÇA EU QUERO, Ó PAI; FALA AO MEU CORAÇÃO, NO SANGUE HÁ VIDA, HÁ LIBERTAÇÃO, SEGURANÇA E PAZ, TOCA-ME, SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('30', '30 - OH! QUE PRECIOSO SANGUE', 'OH! QUE PRECIOSO SANGUE\nO SENHOR VERTEU,\nQUANDO PARA RESGATAR-NOS PADECEU!\n\nOH! QUE PRECIOSO SANGUE,\nSANGUE DE JESUS,\nQUE POR NÓS FOI DERRAMADO\nSOBRE A CRUZ!\n\nOH! QUE PRECIOSO SANGUE, SACRIFICIAL,\nPOIS APAGA DE NOSSA ALMA TODO O MAL!\n\nOH! QUE PRECIOSO SANGUE,\nFALA-NOS DE PAZ,\nTUDO QUANTO A LEI EXIGE SATISFAZ!', 'OH! QUE PRECIOSO SANGUE O SENHOR VERTEU, QUANDO PARA RESGATAR-NOS PADECEU! OH! QUE PRECIOSO SANGUE, SANGUE DE JESUS, QUE POR NÓS FOI DERRAMADO SOBRE A CRUZ! OH! QUE PRECIOSO SANGUE, SACRIFICIAL, POIS APAGA DE NOSSA ALMA TODO O MAL! OH! QUE PRECIOSO SANGUE, FALA-NOS DE PAZ, TUDO QUANTO A LEI EXIGE SATISFAZ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('31', '31 - JUNTO A TI SUPLICO, Ó PAI', 'JUNTO A TI SUPLICO, Ó PAI,\nPELO SANGUE DE JESUS;\nCURVO-ME DIANTE DE TI,\nOUVE-ME, Ó MEU SENHOR.\n\nHÁ EM MIM SINCERIDADE\nEM QUERER LIBERTAÇÃO;\nLAVA-ME COM ESTE SANGUE,\nDÁ-ME PLENA COMUNHÃO.', 'JUNTO A TI SUPLICO, Ó PAI, PELO SANGUE DE JESUS; CURVO-ME DIANTE DE TI, OUVE-ME, Ó MEU SENHOR. HÁ EM MIM SINCERIDADE EM QUERER LIBERTAÇÃO; LAVA-ME COM ESTE SANGUE, DÁ-ME PLENA COMUNHÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('32', '32 - O SANGUE DE JESUS, VERTIDO ALI NA CRUZ', 'O SANGUE DE JESUS, VERTIDO ALI NA CRUZ, \nTEM PODER PARA ME PURIFICAR.\nO SANGUE DE JESUS, VERTIDO ALI NA CRUZ, \nTEM PODER PARA ME LIBERTAR.\n\nPÕE TEU SANGUE EM MIM, SENHOR,\nPÕE TEU SANGUE EM MIM!\nE DÁ-ME LIBERTAÇÃO\nE UMA PLENA COMUNHÃO.', 'O SANGUE DE JESUS, VERTIDO ALI NA CRUZ, TEM PODER PARA ME PURIFICAR. O SANGUE DE JESUS, VERTIDO ALI NA CRUZ, TEM PODER PARA ME LIBERTAR. PÕE TEU SANGUE EM MIM, SENHOR, PÕE TEU SANGUE EM MIM! E DÁ-ME LIBERTAÇÃO E UMA PLENA COMUNHÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('33', '33 - EU ME PROSTRO EM TEU ALTAR', 'EU ME PROSTRO EM TEU ALTAR\nNESTA HORA DE CLAMOR,\nLEMBRO-ME DE TUA DOR\nQUE SOFRESTE POR ME AMAR,\nO TEU SANGUE TEM PODER\nPARA ME PURIFICAR.\n\nNESSE SANGUE HÁ COMUNHÃO,\nCURA E LIBERTAÇÃO;\nVISITA-ME COM TUA PAZ,\nTEU ESPÍRITO RELUZ, \nVIVIFICA-ME, JESUS, \nE MINH’ALMA SATISFAZ. \n\nFINAL: \nE MINH’ALMA SATISFAZ.\nBIS', 'EU ME PROSTRO EM TEU ALTAR NESTA HORA DE CLAMOR, LEMBRO-ME DE TUA DOR QUE SOFRESTE POR ME AMAR, O TEU SANGUE TEM PODER PARA ME PURIFICAR. NESSE SANGUE HÁ COMUNHÃO, CURA E LIBERTAÇÃO; VISITA-ME COM TUA PAZ, TEU ESPÍRITO RELUZ, VIVIFICA-ME, JESUS, E MINH’ALMA SATISFAZ.  E MINH’ALMA SATISFAZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('34', '34 - QUANDO BUSCAMOS', 'QUANDO BUSCAMOS A TI, SENHOR,\nE COM FÉ PEDIMOS TEU PERDÃO,\nCONFESSAMOS NOSSOS PECADOS,\nVEM COM TEU SANGUE NOS PURIFICAR.\n\nCORO\nRENOVA A NOSSA COMUNHÃO\n(NOSSA COMUNHÃO) SERVAS\nRENOVA A FÉ EM NOSSOS CORAÇÕES\nPARA QUE SEJAMOS MAIS FIÉIS A TI\nE ENTREGUEMOS NOSSAS VIDAS \n(NOSSAS VIDAS) VARÕES\nCADA VEZ MAIS NO TEU ALTAR.\n\nOS CÉUS SE ABREM E COM PODER\nOS ANJOS TRAZEM RESPOSTAS DO SENHOR;\nGLORIFICAMOS AO NOSSO DEUS\nQUE ESTÁ VIVO E REINA ENTRE NÓS.\n\nCORO\nRENOVA A NOSSA COMUNHÃO\n(NOSSA COMUNHÃO) SERVAS\nRENOVA A FÉ EM NOSSOS CORAÇÕES\nPARA QUE SEJAMOS MAIS FIÉIS A TI\nE ENTREGUEMOS NOSSAS VIDAS \n(NOSSAS VIDAS) VARÕES\nCADA VEZ MAIS NO TEU ALTAR.\nFINAL:\nCADA VEZ MAIS NO TEU ALTAR.', 'QUANDO BUSCAMOS A TI, SENHOR, E COM FÉ PEDIMOS TEU PERDÃO, CONFESSAMOS NOSSOS PECADOS, VEM COM TEU SANGUE NOS PURIFICAR. CORO RENOVA A NOSSA COMUNHÃO (NOSSA COMUNHÃO) SERVAS RENOVA A FÉ EM NOSSOS CORAÇÕES PARA QUE SEJAMOS MAIS FIÉIS A TI E ENTREGUEMOS NOSSAS VIDAS (NOSSAS VIDAS) VARÕES CADA VEZ MAIS NO TEU ALTAR. OS CÉUS SE ABREM E COM PODER OS ANJOS TRAZEM RESPOSTAS DO SENHOR; GLORIFICAMOS AO NOSSO DEUS QUE ESTÁ VIVO E REINA ENTRE NÓS. CORO RENOVA A NOSSA COMUNHÃO (NOSSA COMUNHÃO) SERVAS RENOVA A FÉ EM NOSSOS CORAÇÕES PARA QUE SEJAMOS MAIS FIÉIS A TI E ENTREGUEMOS NOSSAS VIDAS (NOSSAS VIDAS) VARÕES CADA VEZ MAIS NO TEU ALTAR.  CADA VEZ MAIS NO TEU ALTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('35', '35 - A MEU SENHOR', 'A MEU SENHOR E CRISTO JESUS\nVENHO AGORA CLAMAR;\nVEM COM TEU SANGUE LAVAR \nMINHAS VESTES, VEM ME PURIFICAR.\n\nVEM LIBERTAR A MINH’ALMA, \nJESUS, DE MIM VEM CUIDAR;\nVEM COM TEU SANGUE \nLAVAR MINHAS VESTES,\nVEM ME PURIFICAR.\n\nFINAL: \nVEM ME PURIFICAR.\nBIS', 'A MEU SENHOR E CRISTO JESUS VENHO AGORA CLAMAR; VEM COM TEU SANGUE LAVAR MINHAS VESTES, VEM ME PURIFICAR. VEM LIBERTAR A MINH’ALMA, JESUS, DE MIM VEM CUIDAR; VEM COM TEU SANGUE LAVAR MINHAS VESTES, VEM ME PURIFICAR.  VEM ME PURIFICAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('36', '36 - CRUCIFICADO FOI MEU JESUS', 'CRUCIFICADO FOI MEU JESUS,\nDOR E OPRÓBRIO SOFREU NA CRUZ,\nCAMINHO NOVO NOS CONSAGROU\nAO SANTUÁRIO DO REI SENHOR.\n\nCORO\nNA SUA PRESENÇA EU ENTRO ASSIM,\nCRENDO QUE O SANGUE VERTEU POR MIM;\nVEM SER PROPÍCIO AO PECADOR,\nJÁ ME ALCANÇASTE POR TEU AMOR.\n\nMEU TRISTE ESTADO ELE MUDOU,\nEM NOVA VIDA ME TRANSFORMOU,\nCONFESSO AGORA O SEU PERDÃO,\nCONFIANDO A ELE MEU CORAÇÃO.\n\nCORO\nNA SUA PRESENÇA EU ENTRO ASSIM,\nCRENDO QUE O SANGUE VERTEU POR MIM;\nVEM SER PROPÍCIO AO PECADOR,\nJÁ ME ALCANÇASTE POR TEU AMOR.', 'CRUCIFICADO FOI MEU JESUS, DOR E OPRÓBRIO SOFREU NA CRUZ, CAMINHO NOVO NOS CONSAGROU AO SANTUÁRIO DO REI SENHOR. CORO NA SUA PRESENÇA EU ENTRO ASSIM, CRENDO QUE O SANGUE VERTEU POR MIM; VEM SER PROPÍCIO AO PECADOR, JÁ ME ALCANÇASTE POR TEU AMOR. MEU TRISTE ESTADO ELE MUDOU, EM NOVA VIDA ME TRANSFORMOU, CONFESSO AGORA O SEU PERDÃO, CONFIANDO A ELE MEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('37', '37 - NESTA HORA ESQUEÇO O MUNDO', 'NESTA HORA ESQUEÇO O MUNDO,\nTODA A PREOCUPAÇÃO,\nME COLOCO EM TUA PRESENÇA,\nSOU CARENTE DO TEU PERDÃO.\n\nCONSIDERO O MEU PECADO,\nRECONHEÇO O TEU AMOR,\nTEU ESPÍRITO ME TEM LEVADO\nA ADORAR-TE MEU SALVADOR.\n\nCORO\nOH! QUE SANGUE REMIDOR\nQUE RETIRA A MINHA DOR\nE ME LIVRA DO PECADO, \nÓ JESUS, TU ÉS AMADO. \n\nINSTRUMENTOS\n\nPOR TEU SANGUE ESTOU LIBERTO,\nPOSSO TE GLORIFICAR,\nDE MEU PAI, SIM, AGORA ESTOU PERTO,\nALELUIA, VOU TE LOUVAR.\n\nCORO\nOH! QUE SANGUE REMIDOR\nQUE RETIRA A MINHA DOR\nE ME LIVRA DO PECADO, \nÓ JESUS, TU ÉS AMADO.\nBIS', 'NESTA HORA ESQUEÇO O MUNDO, TODA A PREOCUPAÇÃO, ME COLOCO EM TUA PRESENÇA, SOU CARENTE DO TEU PERDÃO. CONSIDERO O MEU PECADO, RECONHEÇO O TEU AMOR, TEU ESPÍRITO ME TEM LEVADO A ADORAR-TE MEU SALVADOR. CORO OH! QUE SANGUE REMIDOR QUE RETIRA A MINHA DOR E ME LIVRA DO PECADO, Ó JESUS, TU ÉS AMADO. INSTRUMENTOS POR TEU SANGUE ESTOU LIBERTO, POSSO TE GLORIFICAR, DE MEU PAI, SIM, AGORA ESTOU PERTO, ALELUIA, VOU TE LOUVAR. CORO OH! QUE SANGUE REMIDOR QUE RETIRA A MINHA DOR E ME LIVRA DO PECADO, Ó JESUS, TU ÉS AMADO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('38', '38 - PELO SANGUE DE JESUS IREI CLAMAR', 'PELO SANGUE DE JESUS IREI CLAMAR,\nMEUS PECADOS NO ALTAR IREI DEIXAR;\nSEU PERDÃO QUE ME ALCANÇA\nEU JÁ POSSO CONTEMPLAR.\nALELUIA, EU TE AMO, MEU JESUS.\n\nTODA MINHA GRATIDÃO TE QUERO DAR,\nPELA PAZ E PELA FÉ EM TI, SENHOR;\nTEU ESPÍRITO TRANSBORDA\nE MEU SER QUER TE ADORAR\nCOM OS DONS MARAVILHOSOS DE JESUS.', 'PELO SANGUE DE JESUS IREI CLAMAR, MEUS PECADOS NO ALTAR IREI DEIXAR; SEU PERDÃO QUE ME ALCANÇA EU JÁ POSSO CONTEMPLAR. ALELUIA, EU TE AMO, MEU JESUS. TODA MINHA GRATIDÃO TE QUERO DAR, PELA PAZ E PELA FÉ EM TI, SENHOR; TEU ESPÍRITO TRANSBORDA E MEU SER QUER TE ADORAR COM OS DONS MARAVILHOSOS DE JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('39', '39 - JESUS, TU ÉS O MEU DEUS', 'JESUS, TU ÉS O MEU DEUS,\nA MINH’ALMA TEM SEDE DE TI,\nEU TE DESEJO, Ó DEUS,\nNECESSITADO ESTOU.\n\nNO TEU SANGUE HÁ RENOVO,\nPODER PARA TRANSFORMAR,\nPARA LIBERTAR E PERDOAR,\nDERRAMA DESTE SANGUE EM MIM.\n\nE ASSIM TE BENDIREI \nENQUANTO EU VIVER, \nEM TEU NOME FALAREI, \nOS MEUS LÁBIOS TE LOUVARÃO. \n\nALELUIA, AMÉM (2X)\nBIS', 'JESUS, TU ÉS O MEU DEUS, A MINH’ALMA TEM SEDE DE TI, EU TE DESEJO, Ó DEUS, NECESSITADO ESTOU. NO TEU SANGUE HÁ RENOVO, PODER PARA TRANSFORMAR, PARA LIBERTAR E PERDOAR, DERRAMA DESTE SANGUE EM MIM. E ASSIM TE BENDIREI ENQUANTO EU VIVER, EM TEU NOME FALAREI, OS MEUS LÁBIOS TE LOUVARÃO. ALELUIA, AMÉM', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('40', '40 - OH! QUE SANGUE PODEROSO', 'OH! QUE SANGUE PODEROSO!\nOH! QUE SANGUE CHEIO DE VIRTUDE,\nSANGUE QUE POR NÓS FOI DERRAMADO;\nQUE NOS DÁ PLENO PERDÃO,\nCOMUNHÃO, TOTAL LIBERTAÇÃO,\nROMPENDO AS BARREIRAS DO PECADO.\n\nCLAMAMOS A TI, SENHOR, \nPELO PODER QUE HÁ NO \nSANGUE DE JESUS \nPARA PEDIR O TEU PERDÃO \nE TOTAL LIBERTAÇÃO.\n\nFINAL: E TOTAL LIBERTAÇÃO.\nBIS', 'OH! QUE SANGUE PODEROSO! OH! QUE SANGUE CHEIO DE VIRTUDE, SANGUE QUE POR NÓS FOI DERRAMADO; QUE NOS DÁ PLENO PERDÃO, COMUNHÃO, TOTAL LIBERTAÇÃO, ROMPENDO AS BARREIRAS DO PECADO. CLAMAMOS A TI, SENHOR, PELO PODER QUE HÁ NO SANGUE DE JESUS PARA PEDIR O TEU PERDÃO E TOTAL LIBERTAÇÃO.  E TOTAL LIBERTAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('41', '41 - ALÉM DO VÉU EU QUERO ESTAR', 'ALÉM DO VÉU EU QUERO ESTAR,\nALÉM DO VÉU COM JESUS,\nPELO CAMINHO QUE ELE \nABRIU NAQUELA CRUZ;\nCOM OUSADIA VOU ENTRAR \nE POR SEU SANGUE VOU CLAMAR,\nALÉM DO VÉU ONDE ELE QUER \nME ABENÇOAR.\n\nALÉM DO VÉU A SUA GLÓRIA \nPOSSO AGORA CONTEMPLAR;\nDO AMOR DE DEUS \nQUEM PODERÁ ME SEPARAR?\nCOM QUERUBINS E SERAFINS\nMIL ALELUIAS VOU CANTAR,\nALÉM DO VÉU, ONDE PRA \nSEMPRE QUERO ESTAR.\nBIS', 'ALÉM DO VÉU EU QUERO ESTAR, ALÉM DO VÉU COM JESUS, PELO CAMINHO QUE ELE ABRIU NAQUELA CRUZ; COM OUSADIA VOU ENTRAR E POR SEU SANGUE VOU CLAMAR, ALÉM DO VÉU ONDE ELE QUER ME ABENÇOAR. ALÉM DO VÉU A SUA GLÓRIA POSSO AGORA CONTEMPLAR; DO AMOR DE DEUS QUEM PODERÁ ME SEPARAR? COM QUERUBINS E SERAFINS MIL ALELUIAS VOU CANTAR, ALÉM DO VÉU, ONDE PRA SEMPRE QUERO ESTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('42', '42 - SENHOR, SUPLICO A TI EM ORAÇÃO', 'SENHOR, SUPLICO A TI EM ORAÇÃO:\nFAZE DE MIM UM VASO NOVO,\nDERRAMA O SANGUE DE JESUS EM MIM.\n\nCORO\nPERMITE-ME QUE ENTRE NO SANTO \nDOS SANTOS, PERDOA-ME DO VIL PECADO, \nESTENDE A TUA MÃO.\n\nPRECISO CONTEMPLAR A TUA GLÓRIA,\nCONCEDE-ME TUA VITÓRIA,\nDERRAMA O SANGUE DE JESUS EM MIM.\n\nCORO\nPERMITE-ME QUE ENTRE NO SANTO \nDOS SANTOS, PERDOA-ME DO VIL PECADO, \nESTENDE A TUA MÃO. \n\nFINAL: SENHOR (5X)', 'SENHOR, SUPLICO A TI EM ORAÇÃO: FAZE DE MIM UM VASO NOVO, DERRAMA O SANGUE DE JESUS EM MIM. CORO PERMITE-ME QUE ENTRE NO SANTO DOS SANTOS, PERDOA-ME DO VIL PECADO, ESTENDE A TUA MÃO. PRECISO CONTEMPLAR A TUA GLÓRIA, CONCEDE-ME TUA VITÓRIA, DERRAMA O SANGUE DE JESUS EM MIM. CORO PERMITE-ME QUE ENTRE NO SANTO DOS SANTOS, PERDOA-ME DO VIL PECADO, ESTENDE A TUA MÃO.  SENHOR (5X)', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('43', '43 - VOU CLAMAR', 'VOU CLAMAR POR TEU SANGUE, Ó JESUS,\nPRA TE OFERECER A MINHA VIDA. \nVOU CLAMAR POR TEU SANGUE, Ó JESUS,\nPRA TE OFERECER A MINHA VIDA.\n\nE QUEM TIVER TEU SANGUE SOBRE SI \nTERÁ VITÓRIA, VITÓRIA EM TI, JESUS.\nE QUEM TIVER TEU SANGUE SOBRE SI \nTERÁ VITÓRIA, VITÓRIA EM TI, JESUS. \n\nINSTRUMENTOS\n\nE QUEM TIVER TEU SANGUE SOBRE SI \nTERÁ VITÓRIA, VITÓRIA EM TI, JESUS.\nE QUEM TIVER TEU SANGUE SOBRE SI \nTERÁ VITÓRIA, VITÓRIA EM TI, JESUS.', 'VOU CLAMAR POR TEU SANGUE, Ó JESUS, PRA TE OFERECER A MINHA VIDA. VOU CLAMAR POR TEU SANGUE, Ó JESUS, PRA TE OFERECER A MINHA VIDA. E QUEM TIVER TEU SANGUE SOBRE SI TERÁ VITÓRIA, VITÓRIA EM TI, JESUS. E QUEM TIVER TEU SANGUE SOBRE SI TERÁ VITÓRIA, VITÓRIA EM TI, JESUS. INSTRUMENTOS E QUEM TIVER TEU SANGUE SOBRE SI TERÁ VITÓRIA, VITÓRIA EM TI, JESUS. E QUEM TIVER TEU SANGUE SOBRE SI TERÁ VITÓRIA, VITÓRIA EM TI, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('44', '44 - CONFORME A SUA INFINITA GRAÇA', 'CONFORME A TUA INFINITA GRAÇA,\nÓ PAI PERDOA A MINHA TRANSGRESSÃO;\nFAZE-ME SENTIR MAIS UMA VEZ A GRAÇA\nE DÁ-ME TUA PAZ AO CORAÇÃO.\n\nCORO\nMAIS UMA VEZ EU SINTO O MEU PECADO\nQUE É CONTRA TI, Ó PAI, Ó PAI DE AMOR;\nMINH’ALMA PEDE A TUA ALEGRIA,\nRENOVA EM MIM O TEU AMOR,\nÓ MEU SENHOR.\n\nMEU CORAÇÃO A TI EU OFEREÇO,\nPERDÃO IMPLORO, MEU SENHOR E REI;\nCONFORME A TUA INFINITA GRAÇA,\nMISERICÓRDIA ASSIM ALCANÇAREI.\n\nCORO\nMAIS UMA VEZ EU SINTO O MEU PECADO\nQUE É CONTRA TI, Ó PAI, Ó PAI DE AMOR;\nMINH’ALMA PEDE A TUA ALEGRIA,\nRENOVA EM MIM O TEU AMOR,\nÓ MEU SENHOR.\n\nDEUS DE BONDADE A TI EU AGRADEÇO\nTEU PATERNAL CUIDADO SOBRE MIM;\nTODO MEU SER A TI EU OFEREÇO,\nLOUVAR-TE QUERO SEMPRE ATÉ O FIM.\n\nCORO\nMAIS UMA VEZ EU SINTO O MEU PECADO\nQUE É CONTRA TI, Ó PAI, Ó PAI DE AMOR;\nMINH’ALMA PEDE A TUA ALEGRIA,\nRENOVA EM MIM O TEU AMOR,\nÓ MEU SENHOR.', 'CONFORME A TUA INFINITA GRAÇA, Ó PAI PERDOA A MINHA TRANSGRESSÃO; FAZE-ME SENTIR MAIS UMA VEZ A GRAÇA E DÁ-ME TUA PAZ AO CORAÇÃO. CORO MAIS UMA VEZ EU SINTO O MEU PECADO QUE É CONTRA TI, Ó PAI, Ó PAI DE AMOR; MINH’ALMA PEDE A TUA ALEGRIA, RENOVA EM MIM O TEU AMOR, Ó MEU SENHOR. MEU CORAÇÃO A TI EU OFEREÇO, PERDÃO IMPLORO, MEU SENHOR E REI; CONFORME A TUA INFINITA GRAÇA, MISERICÓRDIA ASSIM ALCANÇAREI. DEUS DE BONDADE A TI EU AGRADEÇO TEU PATERNAL CUIDADO SOBRE MIM; TODO MEU SER A TI EU OFEREÇO, LOUVAR-TE QUERO SEMPRE ATÉ O FIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('45', '45 - NOS ÁTRIOS DO SENHOR ENTRAMOS', 'NOS ÁTRIOS DO SENHOR ENTRAMOS,\nPURIFICADOS PELO SANGUE DE JESUS.\nVIMOS LOUVAR-TE E ADORAR-TE, \nÉS NOSSO DEUS!\nVIMOS TRAZER-TE O NOSSO INCENSO,\nOS NOSSOS HOLOCAUSTOS DE LOUVOR.\n\nCORO \nSÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO,\nÉS NOSSO DEUS!\n\nQUANTA BELEZA EM TI ACHAMOS,\nAO CONTEMPLARMOS TUA SANTIDADE.\nÓ DEUS PERFEITO, Ó DEUS TRIUNO, \nÉS NOSSO DEUS!\nE COMO RESPLANDECE A TUA LUZ,\nINCOMPARÁVEL É TUA GLÓRIA.\n\nCORO \nSÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO,\nÉS NOSSO DEUS!\n\nPERANTE A IMENSIDÃO DO TEU AMOR,\nQUEBRANTA-SE DE GRATIDÃO O NOSSO SER. \nÓ, QUANTO AMASTE, QUANTO SOFRESTE, \nÉS NOSSO DEUS!\nTUDO VENCESTE PELO TEU PODER,\nPRA SEMPRE FIRME ESTÁ TEU TRONO!\n\nCORO \nSÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO,\nÉS NOSSO DEUS!', 'NOS ÁTRIOS DO SENHOR ENTRAMOS, PURIFICADOS PELO SANGUE DE JESUS. VIMOS LOUVAR-TE E ADORAR-TE, ÉS NOSSO DEUS! VIMOS TRAZER-TE O NOSSO INCENSO, OS NOSSOS HOLOCAUSTOS DE LOUVOR. CORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS! QUANTA BELEZA EM TI ACHAMOS, AO CONTEMPLARMOS TUA SANTIDADE. Ó DEUS PERFEITO, Ó DEUS TRIUNO, ÉS NOSSO DEUS! E COMO RESPLANDECE A TUA LUZ, INCOMPARÁVEL É TUA GLÓRIA. CORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS! PERANTE A IMENSIDÃO DO TEU AMOR, QUEBRANTA-SE DE GRATIDÃO O NOSSO SER. Ó, QUANTO AMASTE, QUANTO SOFRESTE, ÉS NOSSO DEUS! TUDO VENCESTE PELO TEU PODER, PRA SEMPRE FIRME ESTÁ TEU TRONO! CORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('46', '46 - AO SENTIR O TEU PERDÃO', 'AO SENTIR O TEU PERDÃO\nPOR TEU SANGUE REMIDOR,\nRECEBI LIBERTAÇÃO, \nALEGRIA DO SENHOR.\nALELUIA, GLÓRIA A JESUS.\nINSTRUMENTOS\n\nNESTA HORA DE LOUVOR,\nQUE TRAZ GOZO AO CORAÇÃO,\nQUERO DAR-TE, MEU SENHOR,\nTODA A MINHA GRATIDÃO.\nALELUIA, GLÓRIA A JESUS.\n\nINSTRUMENTOS\n\nQUERO ENTÃO TE ADORAR,\nPOIS EM COMUNHÃO ESTOU,\nEM MINH’ALMA TE EXALTAR,\nALELUIA, EU TE DOU.\nALELUIA, GLÓRIA A JESUS. (BIS)', 'AO SENTIR O TEU PERDÃO POR TEU SANGUE REMIDOR, RECEBI LIBERTAÇÃO, ALEGRIA DO SENHOR. ALELUIA, GLÓRIA A JESUS. INSTRUMENTOS NESTA HORA DE LOUVOR, QUE TRAZ GOZO AO CORAÇÃO, QUERO DAR-TE, MEU SENHOR, TODA A MINHA GRATIDÃO. ALELUIA, GLÓRIA A JESUS. INSTRUMENTOS QUERO ENTÃO TE ADORAR, POIS EM COMUNHÃO ESTOU, EM MINH’ALMA TE EXALTAR, ALELUIA, EU TE DOU. ALELUIA, GLÓRIA A JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('47', '47 - É TEU SANGUE', 'É TEU SANGUE QUE ME LIMPA,\nÉ TEU SANGUE QUE VIDA ME DÁ,\nESTE SANGUE DO SACRIFÍCIO,\nDERRAMADO EM MEU LUGAR, \nE QUE ME TORNA ALVO MAIS QUE A NEVE, MEU JESUS, OFERTA PERFEITA NO ALTAR.\nINSTRUMENTOS\n\nPOR TEU SANGUE CLAMO AGORA,\nPOR TEU SANGUE EU OUSO ENTRAR\nNO SANTUÁRIO, ONDE O DEUS VIVO\nQUER A MIM SE REVELAR. \nCORDEIRO ETERNO!\nÉS O HOLOCAUSTO QUE ME SALVOU!\nOFERTA PERFEITA NO ALTAR.', 'É TEU SANGUE QUE ME LIMPA, É TEU SANGUE QUE VIDA ME DÁ, ESTE SANGUE DO SACRIFÍCIO, DERRAMADO EM MEU LUGAR, E QUE ME TORNA ALVO MAIS QUE A NEVE, MEU JESUS, OFERTA PERFEITA NO ALTAR. INSTRUMENTOS POR TEU SANGUE CLAMO AGORA, POR TEU SANGUE EU OUSO ENTRAR NO SANTUÁRIO, ONDE O DEUS VIVO QUER A MIM SE REVELAR. CORDEIRO ETERNO! ÉS O HOLOCAUSTO QUE ME SALVOU! OFERTA PERFEITA NO ALTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('48', '48 - PELO SANGUE DE JESUS EU VOU CLAMAR', 'PELO SANGUE DE JESUS EU VOU CLAMAR\nE O PERDÃO DOS MEUS PECADOS ALCANÇAR,\nPOIS SÓ ASSIM A CADA DIA POSSO ENTRAR\nPERANTE A TUA FACE, Ó SENHOR.\n\nPURIFICA-ME E LIBERTA-ME, \nSENHOR COM O TEU PODER!\nPRA TE ADORAR E TE EXALTAR, OH SENHOR!\n\nFINAL: \nPURIFICA-ME E LIBERTA-ME, \nSENHOR COM O TEU PODER! \nAMÉM!', 'PELO SANGUE DE JESUS EU VOU CLAMAR E O PERDÃO DOS MEUS PECADOS ALCANÇAR, POIS SÓ ASSIM A CADA DIA POSSO ENTRAR PERANTE A TUA FACE, Ó SENHOR. PURIFICA-ME E LIBERTA-ME, SENHOR COM O TEU PODER! PRA TE ADORAR E TE EXALTAR, OH SENHOR!  PURIFICA-ME E LIBERTA-ME, SENHOR COM O TEU PODER! AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('49', '49 - NA HORA EM QUE EU QUISER FALAR COM DEUS', 'NA HORA EM QUE EU QUISER FALAR COM DEUS,\nDOBRAR OS MEUS JOELHOS E ORAR,\n\nEU VOU CLAMAR PRIMEIRO \nPELO SANGUE DE JESUS,              \nSÓ ELE PODE ME PURIFICAR.\nBIS\n\nSANGUE PRECIOSO, \nQUE RENOVA A COMUNHÃO, \nDÁ VIDA E PLENA PAZ AO CORAÇÃO.\nSANGUE PRECIOSO, \nQUE RENOVA A COMUNHÃO, \nDÁ VIDA E PLENA PAZ AO CORAÇÃO.\n\nNA HORA EM QUE EU \nQUISER FALAR COM DEUS,\nDOBRAR OS MEUS JOELHOS E ORAR,\n\nEU VOU CLAMAR PRIMEIRO \nPELO SANGUE DE JESUS,\nSÓ ELE PODE ME PURIFICAR.\nEU VOU CLAMAR PRIMEIRO \nPELO SANGUE DE JESUS,\nSÓ ELE PODE ME PURIFICAR.\n\nSANGUE PRECIOSO, \nQUE RENOVA A COMUNHÃO, \nDÁ VIDA E PLENA PAZ AO CORAÇÃO.\nSANGUE PRECIOSO, \nQUE RENOVA A COMUNHÃO, \nDÁ VIDA E PLENA PAZ AO CORAÇÃO.', 'NA HORA EM QUE EU QUISER FALAR COM DEUS, DOBRAR OS MEUS JOELHOS E ORAR, EU VOU CLAMAR PRIMEIRO PELO SANGUE DE JESUS, SÓ ELE PODE ME PURIFICAR. SANGUE PRECIOSO, QUE RENOVA A COMUNHÃO, DÁ VIDA E PLENA PAZ AO CORAÇÃO. SANGUE PRECIOSO, QUE RENOVA A COMUNHÃO, DÁ VIDA E PLENA PAZ AO CORAÇÃO. NA HORA EM QUE EU QUISER FALAR COM DEUS, DOBRAR OS MEUS JOELHOS E ORAR, EU VOU CLAMAR PRIMEIRO PELO SANGUE DE JESUS, SÓ ELE PODE ME PURIFICAR. EU VOU CLAMAR PRIMEIRO PELO SANGUE DE JESUS, SÓ ELE PODE ME PURIFICAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('50', '50 - O SANGUE DE JESUS', 'O SANGUE DE JESUS\nQUE POR NÓS FOI DERRAMADO\nPARA NOS CONCEDER\nO PERDÃO DOS NOSSOS PECADOS.\nESTE SANGUE TÃO PURO\nVEM TRAZER-NOS SALVAÇÃO,\nVITÓRIA, PAZ E LIBERTAÇÃO.\n\nO SANGUE DE JESUS\nQUE POR NÓS FOI DERRAMADO\nPARA NOS CONCEDER\nO PERDÃO DOS NOSSOS PECADOS.\nESTE SANGUE TÃO PURO\nVEM TRAZER-NOS SALVAÇÃO,\nVITÓRIA, PAZ E LIBERTAÇÃO.\nBIS', 'O SANGUE DE JESUS QUE POR NÓS FOI DERRAMADO PARA NOS CONCEDER O PERDÃO DOS NOSSOS PECADOS. ESTE SANGUE TÃO PURO VEM TRAZER-NOS SALVAÇÃO, VITÓRIA, PAZ E LIBERTAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('51', '51 - O CORDEIRO QUE NA CRUZ MORREU', 'O CORDEIRO QUE NA CRUZ MORREU\nVIVO ESTÁ ENTRE NÓS,\nDERRAMOU SEU SANGUE INOCENTE\nPARA NOS PURIFICAR.\n\nCORO\nNÓS CLAMAMOS POR ESTE SANGUE,\nQUE É ESPÍRITO DE VIDA,\nÉ O NOSSO ESCUDO, NOSSA PROTEÇÃO.\nESTE SANGUE NOS TRAZ O PERDÃO.\n\nDE ALTO A BAIXO O VÉU SE RASGOU\nQUANDO JESUS EXPIROU,\nPERMITINDO A NOSSA ENTRADA\nNO SANTÍSSIMO LUGAR.\n\nCORO\nNÓS CLAMAMOS POR ESTE SANGUE,\nQUE É ESPÍRITO DE VIDA,\nÉ O NOSSO ESCUDO, NOSSA PROTEÇÃO.\nESTE SANGUE NOS TRAZ O PERDÃO.\n\nFINAL:  \nÉ O NOSSO ESCUDO, \nNOSSA PROTEÇÃO.\nESTE SANGUE NOS TRAZ COMUNHÃO.', 'O CORDEIRO QUE NA CRUZ MORREU VIVO ESTÁ ENTRE NÓS, DERRAMOU SEU SANGUE INOCENTE PARA NOS PURIFICAR. CORO NÓS CLAMAMOS POR ESTE SANGUE, QUE É ESPÍRITO DE VIDA, É O NOSSO ESCUDO, NOSSA PROTEÇÃO. ESTE SANGUE NOS TRAZ O PERDÃO. DE ALTO A BAIXO O VÉU SE RASGOU QUANDO JESUS EXPIROU, PERMITINDO A NOSSA ENTRADA NO SANTÍSSIMO LUGAR. É O NOSSO ESCUDO, NOSSA PROTEÇÃO. ESTE SANGUE NOS TRAZ COMUNHÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('52', '52 - ESCUTA-ME, SENHOR', 'ESCUTA-ME, SENHOR, \nESCUTA AS MINHAS SÚPLICAS,\nINCLINA OS TEUS OUVIDOS À MINHA VOZ.\nCLAMO, Ó SENHOR, PELO SANGUE DE JESUS, \nVEM ME LIBERTAR E ME TRANSFORMAR.\n\nINSTRUMENTOS\n\nRENOVA A MINHA VIDA, \nENCHE-ME DE GOZO E PAZ,\nUNGE-ME A CABEÇA \nCOM O ÓLEO DO TEU ESPÍRITO\nE O MEU CÁLICE TRANSBORDA, \nRENOVA-ME, SENHOR.\n\nCLAMO, Ó SENHOR, \nPELO SANGUE DE JESUS,\nVEM ME LIBERTAR E ME TRANSFORMAR,\nRENOVA A MINHA VIDA,\nENCHE-ME DE GOZO E PAZ,\nTRANSBORDA EM MIM, SENHOR,\nA TUA GRAÇA E TEU AMOR.', 'ESCUTA-ME, SENHOR, ESCUTA AS MINHAS SÚPLICAS, INCLINA OS TEUS OUVIDOS À MINHA VOZ. CLAMO, Ó SENHOR, PELO SANGUE DE JESUS, VEM ME LIBERTAR E ME TRANSFORMAR. INSTRUMENTOS RENOVA A MINHA VIDA, ENCHE-ME DE GOZO E PAZ, UNGE-ME A CABEÇA COM O ÓLEO DO TEU ESPÍRITO E O MEU CÁLICE TRANSBORDA, RENOVA-ME, SENHOR. CLAMO, Ó SENHOR, PELO SANGUE DE JESUS, VEM ME LIBERTAR E ME TRANSFORMAR, RENOVA A MINHA VIDA, ENCHE-ME DE GOZO E PAZ, TRANSBORDA EM MIM, SENHOR, A TUA GRAÇA E TEU AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('53', '53 - O SANGUE DO CORDEIRO DE DEUS', 'O SANGUE DO CORDEIRO DE DEUS\nFOI VERTIDO ALI NA CRUZ,\nNOS TRAZENDO REDENÇÃO \nE COMUNHÃO COM DEUS.\n\nCORO\nPURIFICA-NOS, SENHOR,\nSANTIFICA-NOS PELO SANGUE REMIDOR,\nSÊ PROPÍCIO, Ó, VEM PERDOAR-NOS,\nPOIS COM FÉ NÓS CLAMAMOS.\n\nINSTRUMENTOS\n\nO SUMO SACERDOTE, JESUS,\nFOI PERFEITO NO SEU GESTO\nE PAGOU COM O SEU SANGUE \nA NOSSA SALVAÇÃO.\n\nFINAL: \nPURIFICA-NOS, SENHOR,\nSANTIFICA-NOS PELO SANGUE REMIDOR,\nSÊ PROPÍCIO, Ó, VEM PERDOAR-NOS,\nPOIS COM FÉ (POIS COM FÉ)\nNÓS CLAMAMOS (NÓS CLAMAMOS)\nPOIS COM FÉ (POIS COM FÉ)\nNÓS CLAMAMOS.', 'O SANGUE DO CORDEIRO DE DEUS FOI VERTIDO ALI NA CRUZ, NOS TRAZENDO REDENÇÃO E COMUNHÃO COM DEUS. CORO PURIFICA-NOS, SENHOR, SANTIFICA-NOS PELO SANGUE REMIDOR, SÊ PROPÍCIO, Ó, VEM PERDOAR-NOS, POIS COM FÉ NÓS CLAMAMOS. INSTRUMENTOS O SUMO SACERDOTE, JESUS, FOI PERFEITO NO SEU GESTO E PAGOU COM O SEU SANGUE A NOSSA SALVAÇÃO. PURIFICA-NOS, SENHOR, SANTIFICA-NOS PELO SANGUE REMIDOR, SÊ PROPÍCIO, Ó, VEM PERDOAR-NOS, POIS COM FÉ (POIS COM FÉ) NÓS CLAMAMOS (NÓS CLAMAMOS) POIS COM FÉ (POIS COM FÉ) NÓS CLAMAMOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
