INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('55', '55 - SENHOR, MEU DEUS E PAI', 'SENHOR, MEU DEUS E PAI\nEU ME PROSTRO DIANTE DE TI:\nPERDÃO TE PEÇO, AGORA,\nPELO SANGUE DE JESUS.\n\nLIBERTA-ME, SENHOR; \nPURIFICA-ME, Ó PAI \nPOR JESUS, QUE É O MEU REI, \nAMIGO E REMIDOR. \nINSTRUMENTOS\nBIS\n\nSENHOR, MEU DEUS E PAI\nEU ME PROSTRO DIANTE DE TI:\nPERDÃO TE PEÇO, AGORA,\nPELO SANGUE DE JESUS.\n\nLIBERTA-ME, SENHOR; \nPURIFICA-ME, Ó PAI \nPOR JESUS, QUE É O MEU REI, \nAMIGO E REMIDOR.\nBIS', 'SENHOR, MEU DEUS E PAI EU ME PROSTRO DIANTE DE TI: PERDÃO TE PEÇO, AGORA, PELO SANGUE DE JESUS. LIBERTA-ME, SENHOR; PURIFICA-ME, Ó PAI POR JESUS, QUE É O MEU REI, AMIGO E REMIDOR. INSTRUMENTOS SENHOR, MEU DEUS E PAI EU ME PROSTRO DIANTE DE TI: PERDÃO TE PEÇO, AGORA, PELO SANGUE DE JESUS. LIBERTA-ME, SENHOR; PURIFICA-ME, Ó PAI POR JESUS, QUE É O MEU REI, AMIGO E REMIDOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('56', '56 - VITÓRIA SOBRE VITÓRIA', 'VITÓRIA SOBRE VITÓRIA A TI PERTENCE\nPOIS O PREÇO LÁ NA CRUZ TU JÁ PAGASTE;\nTEU SANGUE PURO DERRAMASTE,\nALELUIA, GLÓRIA AO REI!\nSANTO É O DEUS FORTE! (BIS)\n\nO ESPÍRITO ESTÁ LUTANDO CONTRA \nA CARNE, MAS A VITÓRIA JÁ FOI DECRETADA;\nTUA VONTADE PREVALECEU,\nALELUIA, GLÓRIA AO REI!\nSANTO É O DEUS FORTE! (BIS)', 'VITÓRIA SOBRE VITÓRIA A TI PERTENCE POIS O PREÇO LÁ NA CRUZ TU JÁ PAGASTE; TEU SANGUE PURO DERRAMASTE, ALELUIA, GLÓRIA AO REI! SANTO É O DEUS FORTE! O ESPÍRITO ESTÁ LUTANDO CONTRA A CARNE, MAS A VITÓRIA JÁ FOI DECRETADA; TUA VONTADE PREVALECEU, ALELUIA, GLÓRIA AO REI! SANTO É O DEUS FORTE!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('57', '57 - EM FERVENTE ORAÇÃO', 'EM FERVENTE ORAÇÃO, \nVENS O TEU CORAÇÃO\nNA PRESENÇA DE DEUS DERRAMAR;\nMAS NÃO PODES FRUIR O QUE ESTÁS A PEDIR, \nSEM QUE TUDO ABANDONES NO ALTAR.\n\nCORO\nQUANDO TUDO PERANTE O SENHOR ESTIVER,\nE TODO O TEU SER ELE CONTROLAR,\nSÓ ENTÃO HÁS DE VER\nQUE O SENHOR TEM PODER,\nQUANDO TUDO DEIXARES NO ALTAR.\n\nMARAVILHAS DE AMOR TE FARÁ O SENHOR,\nATENDENDO A ORAÇÃO QUE ACEITAR;\nSEU IMENSO PODER TE VIRÁ SOCORRER,\nQUANDO TUDO DEIXARES NO ALTAR.\n\nCORO\nQUANDO TUDO PERANTE O SENHOR ESTIVER,\nE TODO O TEU SER ELE CONTROLAR,\nSÓ ENTÃO HÁS DE VER\nQUE O SENHOR TEM PODER,\nQUANDO TUDO DEIXARES NO ALTAR.\n\nSE ORARES, ENTÃO, SEM QUE O TEU CORAÇÃO\nGOZE A PAZ QUE O SENHOR PODE DAR,\nÉ QUE DEUS NÃO SENTIU\nQUE TUA ALMA SE ABRIU,\nTUDO, TUDO DEIXANDO NO ALTAR.\n\nCORO\nQUANDO TUDO PERANTE O SENHOR ESTIVER,\nE TODO O TEU SER ELE CONTROLAR,\nSÓ ENTÃO HÁS DE VER\nQUE O SENHOR TEM PODER,\nQUANDO TUDO DEIXARES NO ALTAR.', 'EM FERVENTE ORAÇÃO, VENS O TEU CORAÇÃO NA PRESENÇA DE DEUS DERRAMAR; MAS NÃO PODES FRUIR O QUE ESTÁS A PEDIR, SEM QUE TUDO ABANDONES NO ALTAR. CORO QUANDO TUDO PERANTE O SENHOR ESTIVER, E TODO O TEU SER ELE CONTROLAR, SÓ ENTÃO HÁS DE VER QUE O SENHOR TEM PODER, QUANDO TUDO DEIXARES NO ALTAR. MARAVILHAS DE AMOR TE FARÁ O SENHOR, ATENDENDO A ORAÇÃO QUE ACEITAR; SEU IMENSO PODER TE VIRÁ SOCORRER, QUANDO TUDO DEIXARES NO ALTAR. SE ORARES, ENTÃO, SEM QUE O TEU CORAÇÃO GOZE A PAZ QUE O SENHOR PODE DAR, É QUE DEUS NÃO SENTIU QUE TUA ALMA SE ABRIU, TUDO, TUDO DEIXANDO NO ALTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('58', '58 - ATENDE A VOZ DO MEU CLAMOR', 'ATENDE A VOZ DO MEU CLAMOR,\nÓ REI, DEUS MEU,\nPOIS EU A TI ORAREI COM MAIS FERVOR,\nDÁ OUVIDOS À VOZ DA MINHA ORAÇÃO,\nA MINHA MEDITAÇÃO ELEVO A TI.\n\nCORO\nPELA MANHÃ TU OUVIRÁS A MINHA \nVOZ, EM LONGAS NOITES EU A TI VIGIAREI;\nE O TEU REINO, Ó DEUS BENDITO, VENHA A NÓS,\nDERRAMA AS TUAS BÊNÇÃOS À TUA GREI.\n\nÓ DEUS BENDITO,\nNÃO TENS PRAZER NA INIQUIDADE;\nTUA PALAVRA É ESCUDO E BROQUEL;\nELA EXTERMINA DESTE MUNDO TODA A MALDADE \nE CONSERVA EM RETIDÃO QUEM FOR FIEL.\n\nCORO\nPELA MANHÃ TU OUVIRÁS A MINHA \nVOZ, EM LONGAS NOITES EU A TI VIGIAREI;\nE O TEU REINO, Ó DEUS BENDITO, VENHA A NÓS,\nDERRAMA AS TUAS BÊNÇÃOS À TUA GREI.\n\nEU ENTRAREI EM TUA CASA PELA GRANDEZA, \nE EM TEU TEMPLO ME INCLINAREI PERANTE TI,\nGUIA MEUS PASSOS EM TUA JUSTIÇA E REALEZA \nE VENCE O INIMIGO PARA MIM.', 'ATENDE A VOZ DO MEU CLAMOR, Ó REI, DEUS MEU, POIS EU A TI ORAREI COM MAIS FERVOR, DÁ OUVIDOS À VOZ DA MINHA ORAÇÃO, A MINHA MEDITAÇÃO ELEVO A TI. CORO PELA MANHÃ TU OUVIRÁS A MINHA VOZ, EM LONGAS NOITES EU A TI VIGIAREI; E O TEU REINO, Ó DEUS BENDITO, VENHA A NÓS, DERRAMA AS TUAS BÊNÇÃOS À TUA GREI. Ó DEUS BENDITO, NÃO TENS PRAZER NA INIQUIDADE; TUA PALAVRA É ESCUDO E BROQUEL; ELA EXTERMINA DESTE MUNDO TODA A MALDADE E CONSERVA EM RETIDÃO QUEM FOR FIEL. EU ENTRAREI EM TUA CASA PELA GRANDEZA, E EM TEU TEMPLO ME INCLINAREI PERANTE TI, GUIA MEUS PASSOS EM TUA JUSTIÇA E REALEZA E VENCE O INIMIGO PARA MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('59', '59 - SE ORARMOS AO SENHOR', 'SE ORARMOS AO SENHOR,\nSE CANTARMOS SEUS LOUVORES,\nSUA GLÓRIA ENCHERÁ\nESTE LUGAR, ESTE LUGAR.\nRIOS DE ÁGUAS VIVAS FLUIRÃO\nNO MEU, NO TEU CORAÇÃO.\nE O GRANDE AMOR DE DEUS,\nREVELADO EM JESUS,\nSIM, ENCHERÁ ESTE LUGAR.\nINSTRUMENTOS\n\nSE ORARMOS AO SENHOR,\nSE CANTARMOS SEUS LOUVORES,\nSUA GLÓRIA ENCHERÁ\nESTE LUGAR, ESTE LUGAR.\nRIOS DE ÁGUAS VIVAS FLUIRÃO\nNO MEU, NO TEU CORAÇÃO.\nE O GRANDE AMOR DE DEUS,\nREVELADO EM JESUS,\nSIM, ENCHERÁ ESTE LUGAR.\n\nFINAL: \nRIOS DE ÁGUAS VIVAS FLUIRÃO\nNO MEU, NO TEU CORAÇÃO.\nE O GRANDE AMOR DE DEUS,\nREVELADO EM JESUS,	\nSIM, ENCHERÁ ESTE LUGAR. (3X)', 'SE ORARMOS AO SENHOR, SE CANTARMOS SEUS LOUVORES, SUA GLÓRIA ENCHERÁ ESTE LUGAR, ESTE LUGAR. RIOS DE ÁGUAS VIVAS FLUIRÃO NO MEU, NO TEU CORAÇÃO. E O GRANDE AMOR DE DEUS, REVELADO EM JESUS, SIM, ENCHERÁ ESTE LUGAR. INSTRUMENTOS SE ORARMOS AO SENHOR, SE CANTARMOS SEUS LOUVORES, SUA GLÓRIA ENCHERÁ ESTE LUGAR, ESTE LUGAR. RIOS DE ÁGUAS VIVAS FLUIRÃO NO MEU, NO TEU CORAÇÃO. E O GRANDE AMOR DE DEUS, REVELADO EM JESUS, SIM, ENCHERÁ ESTE LUGAR. RIOS DE ÁGUAS VIVAS FLUIRÃO NO MEU, NO TEU CORAÇÃO. E O GRANDE AMOR DE DEUS, REVELADO EM JESUS, SIM, ENCHERÁ ESTE LUGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('60', '60 - BEM DE MANHÃ, EMBORA O CÉU SERENO', 'BEM DE MANHÃ, EMBORA O CÉU SERENO\nPAREÇA UM DIA CALMO ANUNCIAR,\nVIGIA E ORA, O CORAÇÃO PEQUENO\nUM TEMPORAL PODE ABRIGAR.\n\nCORO\nBEM DE MANHÃ, E SEM CESSAR,\nVIGIAR E ORAR!\n\nAO MEIO DIA, E QUANDO OS SONS DA TERRA\nABAFAM MAIS DE DEUS A VOZ DE AMOR,\nRECORRE À ORAÇÃO, EVITA A GUERRA,\nE GOZA PAZ COM O SENHOR.\n\nCORO\nBEM DE MANHÃ, E SEM CESSAR,\nVIGIAR E ORAR!\n\nDO DIA AO FIM, APÓS OS TEUS LIDARES,\nRELEMBRA AS BÊNÇÃOS DO CELESTE AMOR,\nE CONTA A DEUS PRAZERES E PESARES,\nDEPONDO EM SUAS MÃOS A DOR.\n\nCORO\nBEM DE MANHÃ, E SEM CESSAR,\nVIGIAR E ORAR!\n\nE SEM CESSAR, VIGIA A TODO INSTANTE,\nQUE O INIMIGO ATACA SEM PARAR,\nSÓ COM JESUS EM COMUNHÃO CONSTANTE,\nPODE O MORTAL AO CÉU CHEGAR.', 'BEM DE MANHÃ, EMBORA O CÉU SERENO PAREÇA UM DIA CALMO ANUNCIAR, VIGIA E ORA, O CORAÇÃO PEQUENO UM TEMPORAL PODE ABRIGAR. CORO BEM DE MANHÃ, E SEM CESSAR, VIGIAR E ORAR! AO MEIO DIA, E QUANDO OS SONS DA TERRA ABAFAM MAIS DE DEUS A VOZ DE AMOR, RECORRE À ORAÇÃO, EVITA A GUERRA, E GOZA PAZ COM O SENHOR. CORO BEM DE MANHÃ, E SEM CESSAR, VIGIAR E ORAR! DO DIA AO FIM, APÓS OS TEUS LIDARES, RELEMBRA AS BÊNÇÃOS DO CELESTE AMOR, E CONTA A DEUS PRAZERES E PESARES, DEPONDO EM SUAS MÃOS A DOR. CORO BEM DE MANHÃ, E SEM CESSAR, VIGIAR E ORAR! E SEM CESSAR, VIGIA A TODO INSTANTE, QUE O INIMIGO ATACA SEM PARAR, SÓ COM JESUS EM COMUNHÃO CONSTANTE, PODE O MORTAL AO CÉU CHEGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('61', '61 - SUBA A MINHA ORAÇÃO', 'SUBA A MINHA ORAÇÃO\nPERANTE A TUA FACE COMO INCENSO;\nSEJA O LEVANTAR DE MINHAS MÃOS\nCOMO SACRIFÍCIO DA TARDE.\n\nCORO \nESCUTA-ME, SENHOR!\nINCLINA OS TEUS OUVIDOS \nÀ MINHA VOZ QUANDO A TI CLAMAR.\n\nNÃO ME DEIXES CAMINHAR\nSEM TER TUA PRESENÇA,\nÓ SENHOR, MEU DEUS!\n“SIM, CONTIGO IRÁ MINHA PRESENÇA\nPARA TE FAZER DESCANSAR”.\n\nCORO (2X)\nESCUTA-ME, SENHOR!\nINCLINA OS TEUS OUVIDOS \nÀ MINHA VOZ QUANDO A TI CLAMAR.', 'SUBA A MINHA ORAÇÃO PERANTE A TUA FACE COMO INCENSO; SEJA O LEVANTAR DE MINHAS MÃOS COMO SACRIFÍCIO DA TARDE. CORO ESCUTA-ME, SENHOR! INCLINA OS TEUS OUVIDOS À MINHA VOZ QUANDO A TI CLAMAR. NÃO ME DEIXES CAMINHAR SEM TER TUA PRESENÇA, Ó SENHOR, MEU DEUS! SIM, CONTIGO IRÁ MINHA PRESENÇA PARA TE FAZER DESCANSAR. ESCUTA-ME, SENHOR! INCLINA OS TEUS OUVIDOS À MINHA VOZ QUANDO A TI CLAMAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('62', '62 - NO SILÊNCIO DA NOITE', 'NO SILÊNCIO DA NOITE EU ORO AO SENHOR;\nNO SILÊNCIO DA NOITE SUPLICO AO SENHOR;\nMURMURO, BAIXINHO, ESTA LINDA CANÇÃO, \nNO SILÊNCIO DA NOITE\nEU FAÇO A MINHA ORAÇÃO.\n\nAS ESTRELAS DO CÉU ESTÃO A CINTILAR\nE EU, AO SENHOR, ESTOU A SUPLICAR;\nMURMURO, BAIXINHO, ESTA LINDA CANÇÃO; \nNO SILÊNCIO DA NOITE EU FAÇO A MINHA ORAÇÃO.\n\nE QUANDO PELA ALVORADA RETORNO AO SENHOR,\nDELE RECEBO BÊNÇÃOS E PODER PRA VENCER;\nENTREGO MINHA VIDA AO SERVIÇO DO REI; \nNO SILÊNCIO DA NOITE EU FAÇO A MINHA ORAÇÃO.', 'NO SILÊNCIO DA NOITE EU ORO AO SENHOR; NO SILÊNCIO DA NOITE SUPLICO AO SENHOR; MURMURO, BAIXINHO, ESTA LINDA CANÇÃO, NO SILÊNCIO DA NOITE EU FAÇO A MINHA ORAÇÃO. AS ESTRELAS DO CÉU ESTÃO A CINTILAR E EU, AO SENHOR, ESTOU A SUPLICAR; MURMURO, BAIXINHO, ESTA LINDA CANÇÃO; NO SILÊNCIO DA NOITE EU FAÇO A MINHA ORAÇÃO. E QUANDO PELA ALVORADA RETORNO AO SENHOR, DELE RECEBO BÊNÇÃOS E PODER PRA VENCER; ENTREGO MINHA VIDA AO SERVIÇO DO REI; NO SILÊNCIO DA NOITE EU FAÇO A MINHA ORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('63', '63 - INVOCA-ME E TE RESPONDEREI', 'INVOCA-ME E TE RESPONDEREI, \nANUNCIAR-TE-EI COISAS GRANDES \nE OCULTAS QUE NÃO SABES, \nPORQUE ASSIM DIZ O SENHOR,\nO DEUS DE ISRAEL.\n\nINVOCA-ME E TE RESPONDEREI, \nANUNCIAR-TE-EI COISAS GRANDES \nE OCULTAS QUE NÃO SABES, \nPORQUE ASSIM DIZ O SENHOR,\nO DEUS DE ISRAEL. (3X)', 'INVOCA-ME E TE RESPONDEREI, ANUNCIAR-TE-EI COISAS GRANDES E OCULTAS QUE NÃO SABES, PORQUE ASSIM DIZ O SENHOR, O DEUS DE ISRAEL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('64', '64 - BRILHE SOBRE MIM', 'BRILHE SOBRE MIM A TUA LUZ PRA TODO SEMPRE,\nPRA QUE EU POSSA COMPREENDER TEU CAMINHO.\nBRILHE SOBRE MIM A TUA LUZ\nPRA TODO SEMPRE, PRA QUE EU POSSA \nCOMPREENDER TEU FALAR.\n\n“CLAMA A MIM E RESPONDER-TE-EI,\nANUNCIAR-TE-EI COISAS GRANDES\nE OCULTAS QUE NÃO SABES, MAS EU SEI.\nCLAMA A MIM E RESPONDER-TE-EI,\nANUNCIAR-TE-EI COISAS GRANDES\nE OCULTAS QUE SÓ EU SEI.”\n\nINSTRUMENTOS\n\nBRILHE SOBRE MIM A TUA LUZ PRA TODO SEMPRE,\nPRA QUE EU POSSA COMPREENDER TEU CAMINHO.\nBRILHE SOBRE MIM A TUA LUZ\nPRA TODO SEMPRE, PRA QUE EU POSSA \nCOMPREENDER TEU FALAR.\n\n“CLAMA A MIM E RESPONDER-TE-EI,\nANUNCIAR-TE-EI COISAS GRANDES\nE OCULTAS QUE NÃO SABES, MAS EU SEI.\nCLAMA A MIM E RESPONDER-TE-EI,\nANUNCIAR-TE-EI COISAS GRANDES\nE OCULTAS QUE SÓ EU SEI.”', 'BRILHE SOBRE MIM A TUA LUZ PRA TODO SEMPRE, PRA QUE EU POSSA COMPREENDER TEU CAMINHO. BRILHE SOBRE MIM A TUA LUZ PRA TODO SEMPRE, PRA QUE EU POSSA COMPREENDER TEU FALAR. CLAMA A MIM E RESPONDER-TE-EI, ANUNCIAR-TE-EI COISAS GRANDES E OCULTAS QUE NÃO SABES, MAS EU SEI. CLAMA A MIM E RESPONDER-TE-EI, ANUNCIAR-TE-EI COISAS GRANDES E OCULTAS QUE SÓ EU SEI. INSTRUMENTOS CLAMA A MIM E RESPONDER-TE-EI, ANUNCIAR-TE-EI COISAS GRANDES E OCULTAS QUE NÃO SABES, MAS EU SEI. CLAMA A MIM E RESPONDER-TE-EI, ANUNCIAR-TE-EI COISAS GRANDES E OCULTAS QUE SÓ EU SEI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('65', '65 - QUANDO O DIA AMANHECE', 'QUANDO O DIA AMANHECE \nEU BUSCO AO SENHOR.\nELE ME FAZ SENTIR O SEU GRANDE AMOR;\nENSINA-ME, Ó SENHOR, CONTIGO CAMINHAR,\nOUVIR A TUA DOCE VOZ A ME CHAMAR.\n\nOUVE A MINHA ORAÇÃO, SENHOR,\nPOIS EU QUERO TE SERVIR,	\nSENHOR DE AMOR, DE AMOR.\n\nQUANDO O DIA AMANHECE \nEU BUSCO AO SENHOR.\nELE ME FAZ SENTIR O SEU GRANDE AMOR;\nENSINA-ME, Ó SENHOR, CONTIGO CAMINHAR,\nOUVIR A TUA DOCE VOZ A ME CHAMAR.\n\nOUVE A MINHA ORAÇÃO, SENHOR,\nPOIS EU QUERO TE SERVIR,	\nSENHOR DE AMOR, DE AMOR.\n\nFINAL: DE AMOR', 'QUANDO O DIA AMANHECE EU BUSCO AO SENHOR. ELE ME FAZ SENTIR O SEU GRANDE AMOR; ENSINA-ME, Ó SENHOR, CONTIGO CAMINHAR, OUVIR A TUA DOCE VOZ A ME CHAMAR. OUVE A MINHA ORAÇÃO, SENHOR, POIS EU QUERO TE SERVIR, SENHOR DE AMOR, DE AMOR. OUVE A MINHA ORAÇÃO, SENHOR, POIS EU QUERO TE SERVIR, SENHOR DE AMOR, DE AMOR.  DE AMOR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('66', '66 - DE MADRUGADA EU BUSCO A FACE DO SENHOR', 'DE MADRUGADA EU BUSCO A FACE \nDO SENHOR PEDINDO BÊNÇÃOS \nE BATISMO COM AMOR;\nO GALO CANTA, \nMADRUGADA VAI CHEGAR, \nEU, DE JOELHOS, \nAO SENHOR VOU ADORAR.\n\nALELUIA, ALELUIA, MEU JESUS!\nTU ÉS A LUZ DO DIA QUE VAI COMEÇAR.\nALELUIA, ALELUIA, MEU JESUS!\nEU, DE JOELHOS, AO SENHOR VOU ADORAR.', 'DE MADRUGADA EU BUSCO A FACE DO SENHOR PEDINDO BÊNÇÃOS E BATISMO COM AMOR; O GALO CANTA, MADRUGADA VAI CHEGAR, EU, DE JOELHOS, AO SENHOR VOU ADORAR. ALELUIA, ALELUIA, MEU JESUS! TU ÉS A LUZ DO DIA QUE VAI COMEÇAR. ALELUIA, ALELUIA, MEU JESUS! EU, DE JOELHOS, AO SENHOR VOU ADORAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('67', '67 - TOCA PELA FÉ', 'TOCA PELA FÉ NO VESTIDO DE JESUS\nE DELE VAI SAIR VIRTUDE,\nPEDE SEM TEMER, ELE VAI TE OUVIR,\nTOCA PELA FÉ EM JESUS.\n\nCORO (2X)\nPOIS TODO O QUE PEDE, RECEBE, \nE O QUE BUSCA, ENCONTRA O SENHOR.\n \nINSTRUMENTOS\n\nPODE O PAI NEGAR AO FILHO SEU\nE DAR PEDRA AO INVÉS DE PÃO?\nQUANTO MAIS O PAI QUE ESTÁ NO CÉU, \nTUDO QUE PEDIRES TE DARÁ.\n\nCORO (2X)\nPOIS TODO O QUE PEDE, RECEBE, E \nO QUE BUSCA, ENCONTRA O SENHOR. \n\nFINAL: \nPEDE SEM TEMER, ELE VAI TE OUVIR, \nTOCA PELA FÉ EM JESUS.', 'TOCA PELA FÉ NO VESTIDO DE JESUS E DELE VAI SAIR VIRTUDE, PEDE SEM TEMER, ELE VAI TE OUVIR, TOCA PELA FÉ EM JESUS. POIS TODO O QUE PEDE, RECEBE, E O QUE BUSCA, ENCONTRA O SENHOR. INSTRUMENTOS PODE O PAI NEGAR AO FILHO SEU E DAR PEDRA AO INVÉS DE PÃO? QUANTO MAIS O PAI QUE ESTÁ NO CÉU, TUDO QUE PEDIRES TE DARÁ. POIS TODO O QUE PEDE, RECEBE, E O QUE BUSCA, ENCONTRA O SENHOR.  PEDE SEM TEMER, ELE VAI TE OUVIR, TOCA PELA FÉ EM JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('68', '68 - LÁ NO CIMO DO MONTE', 'LÁ NO CIMO DO MONTE EU ESTOU \nME ENCURVANDO,\nBUSCANDO TUA FACE, MEU DIVINO SENHOR;\nMEU FARDO É PESADO, ESTOU TÃO CANSADO, \nMAS SEI QUE TU ÉS AMOR.\nOH! VEM RENOVAR, EM MEU CORAÇÃO\nA FÉ, O PODER, ME DÁ TUA MÃO;\nRENOVA, SENHOR.\n\nCOMO TERRA SEDENTA, \nA MINH’ALMA ANSEIA POR TEU SANTO ESPÍRITO, \nQUE DÁ VIDA E PODER, E QUANDO ME ERGUER,\nEU SEI QUE VOU TER MAIS FORÇA E ALEGRIA;\nDE GLÓRIA EM GLÓRIA ME VAIS TRANSFORMANDO, \nQUANDO EU, LÁ NO MONTE, \nESTOU ME ENCURVANDO,\nBUSCANDO-TE, Ó DEUS!', 'LÁ NO CIMO DO MONTE EU ESTOU ME ENCURVANDO, BUSCANDO TUA FACE, MEU DIVINO SENHOR; MEU FARDO É PESADO, ESTOU TÃO CANSADO, MAS SEI QUE TU ÉS AMOR. OH! VEM RENOVAR, EM MEU CORAÇÃO A FÉ, O PODER, ME DÁ TUA MÃO; RENOVA, SENHOR. COMO TERRA SEDENTA, A MINH’ALMA ANSEIA POR TEU SANTO ESPÍRITO, QUE DÁ VIDA E PODER, E QUANDO ME ERGUER, EU SEI QUE VOU TER MAIS FORÇA E ALEGRIA; DE GLÓRIA EM GLÓRIA ME VAIS TRANSFORMANDO, QUANDO EU, LÁ NO MONTE, ESTOU ME ENCURVANDO, BUSCANDO-TE, Ó DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('69', '69 - O ANJO DO SENHOR ESTÁ PASSEANDO', 'O ANJO DO SENHOR ESTÁ PASSEANDO,\nESTÁ PASSEANDO AQUI NESTE LUGAR. \n\nDÁ LUGAR, IRMÃO, DÁ LUGAR, IRMÃO, \nE DEIXA O ANJO DO SENHOR TE VISITAR.\nBIS\nBIS', 'O ANJO DO SENHOR ESTÁ PASSEANDO, ESTÁ PASSEANDO AQUI NESTE LUGAR. DÁ LUGAR, IRMÃO, DÁ LUGAR, IRMÃO, E DEIXA O ANJO DO SENHOR TE VISITAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('70', '70 - PRECIOSAS SÃO AS HORAS', 'PRECIOSAS SÃO AS HORAS\nNA PRESENÇA DE JESUS!\nCOMUNHÃO DELICIOSA \nDA MINHA ALMA COM A LUZ!\nOS CUIDADOS DESTE MUNDO\nNUNCA PODEM ME ABALAR,\nPOIS É ELE O MEU ABRIGO,\nQUANDO O TENTADOR CHEGAR (BIS)\n\nAO SENTIR-ME RODEADO\nDE CUIDADOS TERREAIS,\nIRRITADO, ABATIDO OU EM DÚVIDAS FATAIS,\nA JESUS EU ME DIRIJO	\nNESTES TEMPOS DE AFLIÇÃO;\nAS PALAVRAS QUE ELE FALA\nTRAZEM-ME CONSOLAÇÃO (BIS)\n\nSE CONFESSO MEUS TEMORES,\nTODA A MINHA IMPERFEIÇÃO,\nELE ESCUTA COM PACIÊNCIA\nESSA TRISTE CONFISSÃO;	\nCOM TERNURA REPREENDE\nMEU PECADO E TODO MAL,\nELE É SEMPRE O MEU AMIGO,\nO MELHOR E MAIS LEAL (BIS)\n\nSE QUEREIS SABER QUÃO DOCE\nÉ A SECRETA COMUNHÃO,\nPODEREIS MUI BEM PROVÁ-LA\nE TEREIS COMPENSAÇÃO;\nPROCURAI ESTAR SOZINHOS\nEM CONVERSA COM JESUS,\nPROVAREIS NA VOSSA VIDA\nO ESPÍRITO DA CRUZ (BIS)', 'PRECIOSAS SÃO AS HORAS NA PRESENÇA DE JESUS! COMUNHÃO DELICIOSA DA MINHA ALMA COM A LUZ! OS CUIDADOS DESTE MUNDO NUNCA PODEM ME ABALAR, POIS É ELE O MEU ABRIGO, QUANDO O TENTADOR CHEGAR AO SENTIR-ME RODEADO DE CUIDADOS TERREAIS, IRRITADO, ABATIDO OU EM DÚVIDAS FATAIS, A JESUS EU ME DIRIJO NESTES TEMPOS DE AFLIÇÃO; AS PALAVRAS QUE ELE FALA TRAZEM-ME CONSOLAÇÃO SE CONFESSO MEUS TEMORES, TODA A MINHA IMPERFEIÇÃO, ELE ESCUTA COM PACIÊNCIA ESSA TRISTE CONFISSÃO; COM TERNURA REPREENDE MEU PECADO E TODO MAL, ELE É SEMPRE O MEU AMIGO, O MELHOR E MAIS LEAL SE QUEREIS SABER QUÃO DOCE É A SECRETA COMUNHÃO, PODEREIS MUI BEM PROVÁ-LA E TEREIS COMPENSAÇÃO; PROCURAI ESTAR SOZINHOS EM CONVERSA COM JESUS, PROVAREIS NA VOSSA VIDA O ESPÍRITO DA CRUZ', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('71', '71 - VEM, AMADO MEU', 'VEM, AMADO MEU,\nVEM VISITAR A TUA IGREJA;\nVEM, DEPRESSA, VEM, \nMINH’ALMA ANSEIA POR TUA PRESENÇA.\n\nPERTENÇO A TI, ÉS MEU AMADO,\nJUNTO A TI QUERO ESTAR\nPARA SEMPRE, VEM ME BUSCAR.\n\nREPETIR O LOUVOR', 'VEM, AMADO MEU, VEM VISITAR A TUA IGREJA; VEM, DEPRESSA, VEM, MINH’ALMA ANSEIA POR TUA PRESENÇA. PERTENÇO A TI, ÉS MEU AMADO, JUNTO A TI QUERO ESTAR PARA SEMPRE, VEM ME BUSCAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('72', '72 - É PRECISO CONFIAR E BUSCAR A DEUS', 'É PRECISO CONFIAR E BUSCAR A DEUS,\nE O SENHOR DOS EXÉRCITOS TE OUVIRÁ.\n\nO SENHOR TE GUIARÁ, \nÉ PRECISO CONFIAR,\nE O SENHOR DOS EXÉRCITOS TE GUIARÁ.\n\nCORO\nBUSCA A DEUS \n(BUSCA A DEUS) \nQUE ELE TE OUVIRÁ.\n\nBUSCAI AO SENHOR ENQUANTO \nSE PODE ACHAR, INVOCAI-O \nENQUANTO ESTÁ PERTO.\n\nFINAL: \nÉ PRECISO CONFIAR,\nE O SENHOR \nDOS EXÉRCITOS TE GUIARÁ.\nBIS\nBIS', 'É PRECISO CONFIAR E BUSCAR A DEUS, E O SENHOR DOS EXÉRCITOS TE OUVIRÁ. O SENHOR TE GUIARÁ, É PRECISO CONFIAR, E O SENHOR DOS EXÉRCITOS TE GUIARÁ. CORO BUSCA A DEUS (BUSCA A DEUS) QUE ELE TE OUVIRÁ. BUSCAI AO SENHOR ENQUANTO SE PODE ACHAR, INVOCAI-O ENQUANTO ESTÁ PERTO.  É PRECISO CONFIAR, E O SENHOR DOS EXÉRCITOS TE GUIARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('73', '73 - DEUS ESTÁ NO TEMPLO', 'DEUS ESTÁ NO TEMPLO,\nPAI ONIPOTENTE,\nDIANTE DELE NOS PROSTREMOS!\nREVERENTEMENTE,\nSERVOS CONSAGRADOS,\nAO ALTÍSSIMO ADOREMOS!\nCOM FAVOR, COM AMOR,\nESPIRITUALMENTE,\nDEUS ESTÁ NO TEMPLO!\n\nCRISTO ESTÁ NO TEMPLO,\nSUMO BENEFÍCIO\nRECEBEMOS DE SEU SANGUE.\nO CORDEIRO ETERNO\nFOI O SACRIFÍCIO,\nQUE NA CRUZ MORREU EXANGUE;\nESCOLHEU E SOFREU\nO MORTAL SUPLÍCIO;\nCRISTO ESTÁ NO TEMPLO!\n\nVEM, OCUPA O TEMPLO,\nINSTRUTOR DIVINO,\nNOSSOS CORAÇÕES HABITA.\nMESTRE INCOMPARÁVEL,\nDÁ-NOS TEU ENSINO,\nACLARANDO A LEI BENDITA;\nCOM PRAZER E PODER,\nOH! FAVOR SUPINO!\nELE ESTÁ NO TEMPLO!', 'DEUS ESTÁ NO TEMPLO, PAI ONIPOTENTE, DIANTE DELE NOS PROSTREMOS! REVERENTEMENTE, SERVOS CONSAGRADOS, AO ALTÍSSIMO ADOREMOS! COM FAVOR, COM AMOR, ESPIRITUALMENTE, DEUS ESTÁ NO TEMPLO! CRISTO ESTÁ NO TEMPLO, SUMO BENEFÍCIO RECEBEMOS DE SEU SANGUE. O CORDEIRO ETERNO FOI O SACRIFÍCIO, QUE NA CRUZ MORREU EXANGUE; ESCOLHEU E SOFREU O MORTAL SUPLÍCIO; CRISTO ESTÁ NO TEMPLO! VEM, OCUPA O TEMPLO, INSTRUTOR DIVINO, NOSSOS CORAÇÕES HABITA. MESTRE INCOMPARÁVEL, DÁ-NOS TEU ENSINO, ACLARANDO A LEI BENDITA; COM PRAZER E PODER, OH! FAVOR SUPINO! ELE ESTÁ NO TEMPLO!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('74', '74 - SEI, SENHOR', 'SEI, SENHOR, QUE CARINHOSAMENTE\nME TROUXESTE AQUI\nE O MEU CORAÇÃO QUERO \nTE OFERTAR.\n\nSENTIR TUA PRESENÇA,\nGOZAR DE COMUNHÃO;\nVEM, SENHOR, ME ABRAÇA,\nME CONFORTA, ÉS AMOR;\nSOU TEU, SOMENTE TEU,\nÓ VEM ME ABENÇOAR.\nBIS', 'SEI, SENHOR, QUE CARINHOSAMENTE ME TROUXESTE AQUI E O MEU CORAÇÃO QUERO TE OFERTAR. SENTIR TUA PRESENÇA, GOZAR DE COMUNHÃO; VEM, SENHOR, ME ABRAÇA, ME CONFORTA, ÉS AMOR; SOU TEU, SOMENTE TEU, Ó VEM ME ABENÇOAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('75', '75 - SENTE EM TUA FACE O ROÇAR', 'SENTE EM TUA FACE O ROÇAR\nDA ORLA DO VESTIDO DE JESUS,\nSENTE QUE O SENHOR ESTÁ AQUI\nE QUER OUVIR O TEU LOUVOR.\n\nCANTEMOS COM ALEGRIA\nE OREMOS COM DEVOÇÃO;\nVEM, SENHOR, MORAR \nEM MEU CORAÇÃO.\nBIS', 'SENTE EM TUA FACE O ROÇAR DA ORLA DO VESTIDO DE JESUS, SENTE QUE O SENHOR ESTÁ AQUI E QUER OUVIR O TEU LOUVOR. CANTEMOS COM ALEGRIA E OREMOS COM DEVOÇÃO; VEM, SENHOR, MORAR EM MEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('76', '76 - NESTA NOITE FELIZ', 'NESTA NOITE FELIZ, NESTE SANTO LUGAR,\nEU MARQUEI UM ENCONTRO COM DEUS;\nSEU AMOR É REAL, SUA PAZ GOZAREI,\nEU MARQUEI UM ENCONTRO COM DEUS.\n\nEU MARQUEI UM ENCONTRO COM DEUS\nNESTE SANTO LUGAR DE ORAÇÃO;\nNESTE ENCONTRO DE AMOR \nPEDIREI AO SENHOR\nUMA BÊNÇÃO PARA O MEU CORAÇÃO.', 'NESTA NOITE FELIZ, NESTE SANTO LUGAR, EU MARQUEI UM ENCONTRO COM DEUS; SEU AMOR É REAL, SUA PAZ GOZAREI, EU MARQUEI UM ENCONTRO COM DEUS. EU MARQUEI UM ENCONTRO COM DEUS NESTE SANTO LUGAR DE ORAÇÃO; NESTE ENCONTRO DE AMOR PEDIREI AO SENHOR UMA BÊNÇÃO PARA O MEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('77', '77 - VEM, VISITA TUA IGREJA', 'VEM, VISITA TUA IGREJA,\nÓ BENDITO SALVADOR!	\nSEM TUA GRAÇA, \nELA MURCHA FICARÁ E SEM VIGOR.\n\nVIVIFICA, VIVIFICA \nNOSSAS ALMAS, Ó SENHOR!\nBIS', 'VEM, VISITA TUA IGREJA, Ó BENDITO SALVADOR! SEM TUA GRAÇA, ELA MURCHA FICARÁ E SEM VIGOR. VIVIFICA, VIVIFICA NOSSAS ALMAS, Ó SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('78', '78 - UMA VOZ MANSA E DELICADA', 'UMA VOZ MANSA E DELICADA\nFALA AO MEU CORAÇÃO,\nVOZ TÃO SUAVE, MARAVILHOSA,\nTRAZ PAZ E CONSOLAÇÃO.\nOH! QUE VOZ MANSA E DELICADA,\nQUE FALA AO MEU CORAÇÃO.\n\nESTA VOZ DOCE QUE EU ESCUTO,\nÉ A VOZ DO MEU SENHOR,\nELE ESTÁ VIVO, FALA COMIGO,\nELE É O MEU CONSOLADOR.\nOH! QUE VOZ MANSA E DELICADA, \nQUE FALA AO MEU CORAÇÃO.\nBIS', 'UMA VOZ MANSA E DELICADA FALA AO MEU CORAÇÃO, VOZ TÃO SUAVE, MARAVILHOSA, TRAZ PAZ E CONSOLAÇÃO. OH! QUE VOZ MANSA E DELICADA, QUE FALA AO MEU CORAÇÃO. ESTA VOZ DOCE QUE EU ESCUTO, É A VOZ DO MEU SENHOR, ELE ESTÁ VIVO, FALA COMIGO, ELE É O MEU CONSOLADOR. OH! QUE VOZ MANSA E DELICADA, QUE FALA AO MEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('79', '79 - DEUS ESTÁ PRESENTE ENTRE  PECADORES COMO NÓS', 'DEUS ESTÁ PRESENTE (BIS) \nENTRE PECADORES COMO NÓS.\nDEUS ESTÁ PRESENTE, (BIS) 	\nDEUS ESTÁ PRESENTE, \nPRONTO A PERDOAR.\nBIS\n\nDEUS IRÁ OUVIR NOSSA ORAÇÃO,\nNOSSO FARDO VAI SOBRE SI LEVAR;\nPOIS DEUS ESTÁ PRESENTE,	\nPRONTO PRA SALVAR,\nPRONTO PRA AJUDAR,\nDEUS ESTÁ PRESENTE.', 'DEUS ESTÁ PRESENTE  ENTRE PECADORES COMO NÓS. DEUS ESTÁ PRESENTE,  DEUS ESTÁ PRESENTE, PRONTO A PERDOAR. DEUS IRÁ OUVIR NOSSA ORAÇÃO, NOSSO FARDO VAI SOBRE SI LEVAR; POIS DEUS ESTÁ PRESENTE, PRONTO PRA SALVAR, PRONTO PRA AJUDAR, DEUS ESTÁ PRESENTE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('80', '80 - VIVES MUITO ALÉM DO CÉU', 'VIVES MUITO ALÉM DO CÉU,\nMUITO ALÉM DO QUE MEUS OLHOS\nPODEM ALCANÇAR,\nMAS POSSO PELA FÉ TOCAR TUAS \nVESTES E RECEBER DE TI GRAÇA E PAZ;\nPOSSO SEMPRE QUE ORO A TI,  JESUS\nPOSSO SEMPRE QUE  ORO A TI, JESUS\n\nTU DEIXASTE TEU ESPÍRITO CONSOLADOR,\nTÃO AMIGO, TÃO FIEL, AJUDADOR,\nTÉ QUE VENHAS COM PODER PRA NOS BUSCAR\nPRA ENTÃO ETERNAMENTE CONTIGO ESTAR;\nJUNTO, SEMPRE, ESTAREI DE TI, JESUS, \nESTAREI DE TI, JESUS.\nBIS', 'VIVES MUITO ALÉM DO CÉU, MUITO ALÉM DO QUE MEUS OLHOS PODEM ALCANÇAR, MAS POSSO PELA FÉ TOCAR TUAS VESTES E RECEBER DE TI GRAÇA E PAZ; POSSO SEMPRE QUE ORO A TI, JESUS POSSO SEMPRE QUE ORO A TI, JESUS TU DEIXASTE TEU ESPÍRITO CONSOLADOR, TÃO AMIGO, TÃO FIEL, AJUDADOR, TÉ QUE VENHAS COM PODER PRA NOS BUSCAR PRA ENTÃO ETERNAMENTE CONTIGO ESTAR; JUNTO, SEMPRE, ESTAREI DE TI, JESUS, ESTAREI DE TI, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('81', '81 - GRANDE DEUS, ÚNICO DEUS', 'GRANDE DEUS, ÚNICO DEUS,\nCRIADOR DE TUDO QUANTO HÁ\nDE MAIS PROFUNDO, DE MAIS VALOR,\nO SENHOR, O MEU GRANDE DEUS.\n\nPAI, TU QUE ME OUVES\nMESMO QUANDO A VOZ NÃO SAI,\nMEU GRANDE PAI, TU ÉS AMIGO, \nPREFERIDO, MAIS QUE IRMÃO, \nMEU GRANDE PAI, \nTU ÉS AMIGO, MEU PREFERIDO, \nDÁ-ME A TUA PAZ.\nBIS', 'GRANDE DEUS, ÚNICO DEUS, CRIADOR DE TUDO QUANTO HÁ DE MAIS PROFUNDO, DE MAIS VALOR, O SENHOR, O MEU GRANDE DEUS. PAI, TU QUE ME OUVES MESMO QUANDO A VOZ NÃO SAI, MEU GRANDE PAI, TU ÉS AMIGO, PREFERIDO, MAIS QUE IRMÃO, MEU GRANDE PAI, TU ÉS AMIGO, MEU PREFERIDO, DÁ-ME A TUA PAZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('82', '82 - CADA INSTANTE', 'CADA INSTANTE CONTIGO, SENHOR,\nQUE PASSO A TEUS PÉS,\nEU SOU MAIS FELIZ.\nCADA INSTANTE CONTIGO, JESUS,\nÉ PAZ EM MINH’ALMA,\nSUAVE HARMONIA,\nNO TEU GRANDE AMOR.\nBIS', 'CADA INSTANTE CONTIGO, SENHOR, QUE PASSO A TEUS PÉS, EU SOU MAIS FELIZ. CADA INSTANTE CONTIGO, JESUS, É PAZ EM MINH’ALMA, SUAVE HARMONIA, NO TEU GRANDE AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('83', '83 - MAIS UMA VEZ', 'MAIS UMA VEZ, ESTAMOS AQUI, SENHOR\nMAIS UMA VEZ PEDINDO COMUNHÃO\nPARA TE LOUVAR, TE GLORIFICAR,\nPARA TE EXALTARMOS, SENHOR JESUS.\n\nMAIS UMA VEZ, ESTAMOS AQUI, SENHOR\nMAIS UMA VEZ PEDINDO COMUNHÃO\nPARA TE LOUVAR, TE GLORIFICAR,\nPARA TE EXALTARMOS, SENHOR JESUS. \n\nFINAL: \nPARA TE EXALTARMOS, \nSENHOR JESUS.', 'MAIS UMA VEZ, ESTAMOS AQUI, SENHOR MAIS UMA VEZ PEDINDO COMUNHÃO PARA TE LOUVAR, TE GLORIFICAR, PARA TE EXALTARMOS, SENHOR JESUS. MAIS UMA VEZ, ESTAMOS AQUI, SENHOR MAIS UMA VEZ PEDINDO COMUNHÃO PARA TE LOUVAR, TE GLORIFICAR, PARA TE EXALTARMOS, SENHOR JESUS.  PARA TE EXALTARMOS, SENHOR JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('84', '84 - ATENDE, SENHOR, A MINHA ORAÇÃO', 'ATENDE, SENHOR, A MINHA ORAÇÃO,\nA TI, LEVANTO MINHAS MÃOS;\nA MINHA ALMA ANSEIA POR TI,\nDÁ-TE PRESSA EM ME SOCORRER.\n\nFAZE-ME OUVIR NA MANHÃ, \nDA TUA GRAÇA,\nENSINA-ME A FAZER A TUA VONTADE\nPOIS EU SOU TEU E TU ÉS MEU, \nEM TI CONFIO, SENHOR. \n\nFINAL: \nPOIS EU SOU TEU E TU ÉS MEU,\nEM TI CONFIO, SENHOR (3X)\nBIS', 'ATENDE, SENHOR, A MINHA ORAÇÃO, A TI, LEVANTO MINHAS MÃOS; A MINHA ALMA ANSEIA POR TI, DÁ-TE PRESSA EM ME SOCORRER. FAZE-ME OUVIR NA MANHÃ, DA TUA GRAÇA, ENSINA-ME A FAZER A TUA VONTADE POIS EU SOU TEU E TU ÉS MEU, EM TI CONFIO, SENHOR.  POIS EU SOU TEU E TU ÉS MEU, EM TI CONFIO, SENHOR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('85', '85 - ENVIA A TUA LUZ', 'ENVIA A TUA LUZ, Ó DEUS,\nENVIA A TUA VERDADE, MEU SENHOR,\nPRA QUE ME GUIEM AO TEU SANTO MONTE,\nAOS TEUS TABERNÁCULOS, \nMEU SENHOR, MEU CRIADOR, \nMEU REI, Ó MEU SENHOR.\n\nENTÃO IREI AO ALTAR DE DEUS,\nDO DEUS QUE É MINHA GRANDE ALEGRIA. \n\nE COM HARPA LOUVAREI A DEUS,\nDEUS, MEU SENHOR.\nBIS', 'ENVIA A TUA LUZ, Ó DEUS, ENVIA A TUA VERDADE, MEU SENHOR, PRA QUE ME GUIEM AO TEU SANTO MONTE, AOS TEUS TABERNÁCULOS, MEU SENHOR, MEU CRIADOR, MEU REI, Ó MEU SENHOR. ENTÃO IREI AO ALTAR DE DEUS, DO DEUS QUE É MINHA GRANDE ALEGRIA. E COM HARPA LOUVAREI A DEUS, DEUS, MEU SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('86', '86 - EM TEU NOME', 'EM TEU NOME ESTAMOS REUNIDOS AQUI, \nNOSSA ALMA ANSEIA E TEM SEDE DE TI!\nQUEREMOS VER TUA GLÓRIA ENCHENDO ESTE LUGAR, \nTU ESTÁS PRESENTE,  PRONTO A ABENÇOAR.\n\nCORO\nESTÁS AQUI, BOM SALVADOR,\nMEIGO E GENTIL, ABENÇOADOR;\nESTÁS AQUI COM TEU PODER\nPARA DAR LIBERDADE E MUDAR \nO NOSSO SER.\n\nINSTRUMENTOS\n\nTEU PERFUME É SUAVE COMO A ROSA E O JASMIM,\nTUA PRESENÇA ME ENCHE DE ALEGRIA SEM FIM,\nFONTE INESGOTÁVEL QUE DO CÉU VIDA TRAZ,\nFONTE DE ÁGUA VIVA QUE ME CURA E DÁ PAZ.\n\nCORO (2	X)\nESTÁS AQUI, BOM SALVADOR,\nMEIGO E GENTIL, ABENÇOADOR;\nESTÁS AQUI COM TEU PODER\nPARA DAR LIBERDADE E MUDAR \nO NOSSO SER.', 'EM TEU NOME ESTAMOS REUNIDOS AQUI, NOSSA ALMA ANSEIA E TEM SEDE DE TI! QUEREMOS VER TUA GLÓRIA ENCHENDO ESTE LUGAR, TU ESTÁS PRESENTE, PRONTO A ABENÇOAR. CORO ESTÁS AQUI, BOM SALVADOR, MEIGO E GENTIL, ABENÇOADOR; ESTÁS AQUI COM TEU PODER PARA DAR LIBERDADE E MUDAR O NOSSO SER. INSTRUMENTOS TEU PERFUME É SUAVE COMO A ROSA E O JASMIM, TUA PRESENÇA ME ENCHE DE ALEGRIA SEM FIM, FONTE INESGOTÁVEL QUE DO CÉU VIDA TRAZ, FONTE DE ÁGUA VIVA QUE ME CURA E DÁ PAZ. CORO (2 X) ESTÁS AQUI, BOM SALVADOR, MEIGO E GENTIL, ABENÇOADOR; ESTÁS AQUI COM TEU PODER PARA DAR LIBERDADE E MUDAR O NOSSO SER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('87', '87 - MEU PECADO RESGATADO', 'MEU PECADO RESGATADO\nFOI NA CRUZ, POR TEU AMOR,\nE DA MORTE, TRISTE SORTE,\nME LIVRASTE TU, SENHOR!\n\nCORO\nVEM! INFLAMA VIVA CHAMA\nEM MEU PEITO, BEM SEM FIM!\nEU TE ADORO, SEMPRE IMPLORO:\nÓ JESUS, HABITA EM MIM!\n\nSE HESITANTE, VACILANTE,\nOUÇO A VOZ DO TENTADOR,\nTU ME GUIAS, ME AUXILIAS\nE ME TORNAS VENCEDOR.\n\nCORO\nVEM! INFLAMA VIVA CHAMA\nEM MEU PEITO, BEM SEM FIM!\nEU TE ADORO, SEMPRE IMPLORO:\nÓ JESUS, HABITA EM MIM!\n\nREDIMIDA, SÓ TEM VIDA\nA MINHA ALMA EM TEU AMOR;\nCOM APREÇO, RECONHEÇO\nQUANTO DEVO A TI, SENHOR!\n\nCORO\nVEM! INFLAMA VIVA CHAMA\nEM MEU PEITO, BEM SEM FIM!\nEU TE ADORO, SEMPRE IMPLORO:\nÓ JESUS, HABITA EM MIM!', 'MEU PECADO RESGATADO FOI NA CRUZ, POR TEU AMOR, E DA MORTE, TRISTE SORTE, ME LIVRASTE TU, SENHOR! CORO VEM! INFLAMA VIVA CHAMA EM MEU PEITO, BEM SEM FIM! EU TE ADORO, SEMPRE IMPLORO: Ó JESUS, HABITA EM MIM! SE HESITANTE, VACILANTE, OUÇO A VOZ DO TENTADOR, TU ME GUIAS, ME AUXILIAS E ME TORNAS VENCEDOR. REDIMIDA, SÓ TEM VIDA A MINHA ALMA EM TEU AMOR; COM APREÇO, RECONHEÇO QUANTO DEVO A TI, SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('88', '88 - DAS PROFUNDEZAS', 'DAS PROFUNDEZAS \nCLAMO A TI, Ó SENHOR,\nÉS A MINHA FORÇA,\nÉS MINHA ROCHA E ALTO RETIRO MEU,\nEM TI EU CONFIAREI.\nBIS', 'DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR, ÉS A MINHA FORÇA, ÉS MINHA ROCHA E ALTO RETIRO MEU, EM TI EU CONFIAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('89', '89 - DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR!', 'DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR!\nESCUTA A MINHA VOZ, ESCUTA A MINHA SÚPLICA;\nESPERO EM TI, SENHOR, O TEU PERDÃO,\nESPERO EM TI, SENHOR, O TEU PERDÃO.\n\nAGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI MAIS QUE OS GUARDAS \nPELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (NO SENHOR) \nPORQUE NELE HÁ  (MISERICÓRDIA)\nE REDENÇÃO.\n\nINSTRUMENTOS\n\nELE TE REMIRÁ DE TODA TRANSGRESSÃO\nPOIS O SENHOR QUE ESTÁ NOS CÉUS TE OUVIRÁ;\n“ESPERA, POVO MEU, POIS VENHO JÁ,\nESPERA, ISRAEL, NO TEU SENHOR E REI”.\n\nAGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI MAIS QUE OS GUARDAS \nPELO ROMPER DA MANHÃ. ESPERA, ISRAEL,  (NO SENHOR) \nPORQUE NELE HÁ  (MISERICÓRDIA)\n\nAGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, \nA MINH’ALMA ANSEIA POR TI, MAIS QUE OS GUARDAS \nPELO ROMPER DA MANHÃ. ESPERA, ISRAEL,  \n(ESPERA EM DEUS) \nESPERA, ISRAEL,  (NO SENHOR) \nPORQUE NELE HÁ (MISERICÓRDIA) \nE REDENÇÃO.', 'DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR! ESCUTA A MINHA VOZ, ESCUTA A MINHA SÚPLICA; ESPERO EM TI, SENHOR, O TEU PERDÃO, ESPERO EM TI, SENHOR, O TEU PERDÃO. AGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA) E REDENÇÃO. INSTRUMENTOS ELE TE REMIRÁ DE TODA TRANSGRESSÃO POIS O SENHOR QUE ESTÁ NOS CÉUS TE OUVIRÁ; ESPERA, POVO MEU, POIS VENHO JÁ, ESPERA, ISRAEL, NO TEU SENHOR E REI. AGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA) AGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI, MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (ESPERA EM DEUS) ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA) E REDENÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('90', '90 - EM TUAS MÃOS, SENHOR', 'EM TUAS MÃOS, SENHOR, EU ME COLOCO AGORA;\nFALA COMIGO, NESTA HORA EM QUE MEU CORAÇÃO \nSUPLICA O TEU AMOR.\nEU PEÇO SALVAÇÃO, PEÇO TEU PERDÃO, AGORA, \nVEM ME DIZER QUE A DOR FINDOU \nE QUE A FELICIDADE NÃO DEMORA.\nFALA COMIGO, AGORA (4X)', 'EM TUAS MÃOS, SENHOR, EU ME COLOCO AGORA; FALA COMIGO, NESTA HORA EM QUE MEU CORAÇÃO SUPLICA O TEU AMOR. EU PEÇO SALVAÇÃO, PEÇO TEU PERDÃO, AGORA, VEM ME DIZER QUE A DOR FINDOU E QUE A FELICIDADE NÃO DEMORA. FALA COMIGO, AGORA', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('91', '91 - JESUS, A MINH’ALMA TEM SEDE DE TI', 'JESUS, A MINH’ALMA TEM SEDE DE TI,\nOUVE-ME QUANDO A TI CLAMO,\nLIVRA-ME DOS MEUS ADVERSÁRIOS,\nPOIS ELES ESTÃO QUERENDO ROUBAR\nA MINHA SALVAÇÃO.\n\nCOBRE-ME COM O TEU SANGUE,\nDÁ-ME ESTA PROTEÇÃO,\nJESUS, EU QUERO ESTAR \nEM PERFEITA COMUNHÃO,\nJESUS, EU QUERO ESTAR \nEM CONSTANTE COMUNHÃO.', 'JESUS, A MINH’ALMA TEM SEDE DE TI, OUVE-ME QUANDO A TI CLAMO, LIVRA-ME DOS MEUS ADVERSÁRIOS, POIS ELES ESTÃO QUERENDO ROUBAR A MINHA SALVAÇÃO. COBRE-ME COM O TEU SANGUE, DÁ-ME ESTA PROTEÇÃO, JESUS, EU QUERO ESTAR EM PERFEITA COMUNHÃO, JESUS, EU QUERO ESTAR EM CONSTANTE COMUNHÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('92', '92 - PAI CELESTE', 'PAI CELESTE, PAI CELESTE,\nVEM FICAR COMIGO,\nPAI CELESTE.\n\nJESUS CRISTO, JESUS CRISTO,\nVEM FICAR COMIGO,\nJESUS CRISTO.\n\nSANTO ESPÍRITO, SANTO ESPÍRITO,\nVEM FICAR COMIGO,\nSANTO ESPÍRITO.', 'PAI CELESTE, PAI CELESTE, VEM FICAR COMIGO, PAI CELESTE. JESUS CRISTO, JESUS CRISTO, VEM FICAR COMIGO, JESUS CRISTO. SANTO ESPÍRITO, SANTO ESPÍRITO, VEM FICAR COMIGO, SANTO ESPÍRITO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('93', '93 - LOGO DE MANHÃ', 'LOGO DE MANHÃ, QUERO TE BUSCAR,\nTUA VOZ OUVIR, TEU AMOR SENTIR.\nE ESTENDER AS MÃOS PARA TE LOUVAR,\nDERRAMAR MEU CORAÇÃO SOBRE TEU ALTAR.\n\nPOIS TU SABES BEM TUDO QUANDO HÁ EM MIM:\nQUE VOU TE SEGUIR E TE AMAR ATÉ O FIM.\n\nE, NO FIM DO DIA, QUANDO O SOL SE FOR,\nTE ADORAREI, TE DAREI LOUVOR.\nMESMA ESCURA A NOITE, BRILHA A TUA LUZ.\nE EM TEUS BRAÇOS EU DESCANSO,\nMEU SENHOR JESUS.', 'LOGO DE MANHÃ, QUERO TE BUSCAR, TUA VOZ OUVIR, TEU AMOR SENTIR. E ESTENDER AS MÃOS PARA TE LOUVAR, DERRAMAR MEU CORAÇÃO SOBRE TEU ALTAR. POIS TU SABES BEM TUDO QUANDO HÁ EM MIM: QUE VOU TE SEGUIR E TE AMAR ATÉ O FIM. E, NO FIM DO DIA, QUANDO O SOL SE FOR, TE ADORAREI, TE DAREI LOUVOR. MESMA ESCURA A NOITE, BRILHA A TUA LUZ. E EM TEUS BRAÇOS EU DESCANSO, MEU SENHOR JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('94', '94 - BENDITA A HORA DE ORAÇÃO', 'BENDITA A HORA DE ORAÇÃO\nQUE ACALMA O AFLITO CORAÇÃO.\nQUE LEVA AO TRONO DE JESUS\nOS ROGOS PARA AUXÍLIO E LUZ!\nEM TEMPOS DE CUIDADO E DOR,\nREFÚGIO TENHO EM MEU SENHOR.\nVENCENDO O ARDIL E A TENTAÇÃO,\nBENDIGO A HORA DE ORAÇÃO.\n\nBENDITA A HORA DE ORAÇÃO,\nQUANDO A FERVENTE PETIÇÃO\nSOBE AO BENIGNO SALVADOR,\nQUE ATENDE A VOZ DO MEU CLAMOR!\nJESUS ME ORDENA A RECORRER\nAO SEU AMOR, AO SEU PODER.\nCONTENTE E SEM PERTURBAÇÃO\nEU BUSCO A HORA DE ORAÇÃO.\n\nBENDITA A HORA DE ORAÇÃO,\nDE SANTA PAZ E COMUNHÃO!\nDESEJO ENQUANTO AQUI ME ACHAR,\nCOM FÉ CONSTANTE HUMILDE ORAR.\nE ENFIM, NO RESPLENDOR DE DEUS,\nNA GLÓRIA DOS MAIS ALTOS CÉUS,\nLEMBRAR-ME-EI COM GRATIDÃO\nDAS HORAS SUAVES DE ORAÇÃO.\n\nFINAL: AMÉM', 'BENDITA A HORA DE ORAÇÃO QUE ACALMA O AFLITO CORAÇÃO. QUE LEVA AO TRONO DE JESUS OS ROGOS PARA AUXÍLIO E LUZ! EM TEMPOS DE CUIDADO E DOR, REFÚGIO TENHO EM MEU SENHOR. VENCENDO O ARDIL E A TENTAÇÃO, BENDIGO A HORA DE ORAÇÃO. BENDITA A HORA DE ORAÇÃO, QUANDO A FERVENTE PETIÇÃO SOBE AO BENIGNO SALVADOR, QUE ATENDE A VOZ DO MEU CLAMOR! JESUS ME ORDENA A RECORRER AO SEU AMOR, AO SEU PODER. CONTENTE E SEM PERTURBAÇÃO EU BUSCO A HORA DE ORAÇÃO. BENDITA A HORA DE ORAÇÃO, DE SANTA PAZ E COMUNHÃO! DESEJO ENQUANTO AQUI ME ACHAR, COM FÉ CONSTANTE HUMILDE ORAR. E ENFIM, NO RESPLENDOR DE DEUS, NA GLÓRIA DOS MAIS ALTOS CÉUS, LEMBRAR-ME-EI COM GRATIDÃO DAS HORAS SUAVES DE ORAÇÃO.  AMÉM', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('95', '95 - BUSCAR-ME-EIS', 'BUSCAR-ME-EIS, E ME ACHAREIS, \nQUANDO ME BUSCARDES, \nQUANDO ME BUSCARDES, \nDE TODO O CORAÇÃO.\nBIS\n\nCORO\nINVOCAR-ME-EIS, E ORAREIS A MIM,\nE EU VOS OUVIREI.\nINVOCAR-ME-EIS, E ORAREIS A MIM,\nE EU VOS OUVIREI.\n\nINSTRUMENTOS\n\nBUSCAR-ME-EIS, E ME ACHAREIS, \nQUANDO ME BUSCARDES, \nQUANDO ME BUSCARDES, \nDE TODO O CORAÇÃO.\n\nCORO\nINVOCAR-ME-EIS, E ORAREIS A MIM,\nE EU VOS OUVIREI.\nINVOCAR-ME-EIS, E ORAREIS A MIM,\nE EU VOS OUVIREI.\n\nFINAL: ASSIM DIZ O SENHOR.', 'BUSCAR-ME-EIS, E ME ACHAREIS, QUANDO ME BUSCARDES, QUANDO ME BUSCARDES, DE TODO O CORAÇÃO. CORO INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INSTRUMENTOS CORO INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI.  ASSIM DIZ O SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('96', '96 - COM A MINHA VOZ CLAMO AO SENHOR', 'CORO\nCOM A MINHA VOZ CLAMO AO SENHOR,\nCOM A MINHA VOZ AO SENHOR SUPLICO.\nDIANTE DELE A PROSTRAR-ME EU ESTOU,\nDIANTE DELE EXPONHO A MINHA AFLIÇÃO.\n\nQUANDO AQUI DENTRO DE MIM\nESMORECE O MEU ESPÍRITO,\nTU ENTÃO CONHECES MINHA VEREDA.\nOLHO À MÃO DIREITA E VEJO\nNÃO HÁ QUEM ME CONHEÇA,\nNÃO HÁ LUGAR ONDE ME REFUGIAR.\n\nCORO\nCOM A MINHA VOZ CLAMO AO SENHOR,\nCOM A MINHA VOZ AO SENHOR SUPLICO.\nDIANTE DELE A PROSTRAR-ME EU ESTOU,\nDIANTE DELE EXPONHO A MINHA AFLIÇÃO.\n\nÓ SENHOR A TI CLAMEI\nPOIS TU ÉS O MEU REFÚGIO,\nE O MEU TESOURO ENTRE OS VIVENTES.\nVEM, ATENDE AO MEU CLAMOR,\nESTOU MUITO ABATIDO.\nLIVRAR-ME VEM DO FORTE TENTADOR.\n\nCORO\nCOM A MINHA VOZ CLAMO AO SENHOR,\nCOM A MINHA VOZ AO SENHOR SUPLICO.\nDIANTE DELE A PROSTRAR-ME EU ESTOU,\nDIANTE DELE EXPONHO A MINHA AFLIÇÃO.\n\nFINAL:\nTIRA-ME DESTA PRISÃO\nE ASSIM LOUVAREI TEU NOME,\nE ENTÃO OS JUSTOS ME CERCARÃO.\nMEU SENHOR EU CLAMO A TI:\nÓ, VEM LIVRAR MINH’ALMA,\nE CANTAREI QUE ME FIZESTE BEM.', 'CORO COM A MINHA VOZ CLAMO AO SENHOR, COM A MINHA VOZ AO SENHOR SUPLICO. DIANTE DELE A PROSTRAR-ME EU ESTOU, DIANTE DELE EXPONHO A MINHA AFLIÇÃO. QUANDO AQUI DENTRO DE MIM ESMORECE O MEU ESPÍRITO, TU ENTÃO CONHECES MINHA VEREDA. OLHO À MÃO DIREITA E VEJO NÃO HÁ QUEM ME CONHEÇA, NÃO HÁ LUGAR ONDE ME REFUGIAR. Ó SENHOR A TI CLAMEI POIS TU ÉS O MEU REFÚGIO, E O MEU TESOURO ENTRE OS VIVENTES. VEM, ATENDE AO MEU CLAMOR, ESTOU MUITO ABATIDO. LIVRAR-ME VEM DO FORTE TENTADOR. TIRA-ME DESTA PRISÃO E ASSIM LOUVAREI TEU NOME, E ENTÃO OS JUSTOS ME CERCARÃO. MEU SENHOR EU CLAMO A TI: Ó, VEM LIVRAR MINH’ALMA, E CANTAREI QUE ME FIZESTE BEM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('97', '97 - BUSCOU-ME COM TERNURA', 'BUSCOU-ME COM TERNURA\nJESUS, O BOM PASTOR;\nACHOU-ME NA MISÉRIA,\nSALVOU-ME COM AMOR;\nNO CÉU CANTARAM DE ALEGRIA\nOS ANJOS, SIM, EM HARMONIA.\n\nCORO\nOH! QUE AMOR GLORIOSO!\nPREÇO TÃO GRANDIOSO,\nQUE JESUS POR MIM NA CRUZ PAGOU;\nINAUDITA GRAÇA ME MOSTROU!\n\nFERIDO, ABANDONADO, \nJESUS ME SOCORREU; \nE SEGREDOU-ME: “ACHEI-TE;\nDE AGORA EM DIANTE ÉS MEU”.\nTÃO MEIGA VOZ JAMAIS OUVI, \nPRAZER MAIOR JAMAIS SENTI.\n\nCORO\nOH! QUE AMOR GLORIOSO!\nPREÇO TÃO GRANDIOSO,\nQUE JESUS POR MIM NA CRUZ PAGOU;\nINAUDITA GRAÇA ME MOSTROU!\n\nJESUS MOSTROU-ME AS CHAGAS \nQUE EM MEU LUGAR SOFREU, COROA, \nMAS DE ESPINHOS, A CRUZ QUE PADECEU.\nQUE PODERIA EM MIM ACHAR,\nPRA TAIS AFRONTAS SUPORTAR?\n\nCORO\nOH! QUE AMOR GLORIOSO!\nPREÇO TÃO GRANDIOSO,\nQUE JESUS POR MIM NA CRUZ PAGOU;\nINAUDITA GRAÇA ME MOSTROU!\n\nENQUANTO AS HORAS PASSAM, \nEU TENHO GOZO E PAZ, E AGUARDO \nMEU BOM MESTRE, QUE TÃO FELIZ ME FAZ!\nJESUS A MIM VIRÁ BUSCAR,\nE ENTÃO PRA SEMPRE IREI GOZAR!', 'BUSCOU-ME COM TERNURA JESUS, O BOM PASTOR; ACHOU-ME NA MISÉRIA, SALVOU-ME COM AMOR; NO CÉU CANTARAM DE ALEGRIA OS ANJOS, SIM, EM HARMONIA. CORO OH! QUE AMOR GLORIOSO! PREÇO TÃO GRANDIOSO, QUE JESUS POR MIM NA CRUZ PAGOU; INAUDITA GRAÇA ME MOSTROU! FERIDO, ABANDONADO, JESUS ME SOCORREU; E SEGREDOU-ME: ACHEI-TE; DE AGORA EM DIANTE ÉS MEU. TÃO MEIGA VOZ JAMAIS OUVI, PRAZER MAIOR JAMAIS SENTI. JESUS MOSTROU-ME AS CHAGAS QUE EM MEU LUGAR SOFREU, COROA, MAS DE ESPINHOS, A CRUZ QUE PADECEU. QUE PODERIA EM MIM ACHAR, PRA TAIS AFRONTAS SUPORTAR? ENQUANTO AS HORAS PASSAM, EU TENHO GOZO E PAZ, E AGUARDO MEU BOM MESTRE, QUE TÃO FELIZ ME FAZ! JESUS A MIM VIRÁ BUSCAR, E ENTÃO PRA SEMPRE IREI GOZAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('98', '98 - ONDE ESTARIA EU?', 'ONDE ESTARIA EU,\nNÃO FOSSE A MÃO DO SENHOR?\nSOZINHO, SOLITÁRIO NO MUNDO,\nJESUS ME ALCANÇOU.\n\nCORO\nQUERO, CADA DIA MAIS,\nVIVER PRA TI, Ó MEU SENHOR,\nQUERO, CADA DIA MAIS,\nOUVIR A TUA VOZ DE AMOR.\n\nHOJE ME SINTO EM PAZ,\nSEGURO EM TUA MÃO ESTOU;\nCAMINHO PARA A ETERNIDADE,\nCANTANDO PARA O CÉU EU VOU.\n\nCORO (2X)\nQUERO, CADA DIA MAIS,\nVIVER PRA TI, Ó MEU SENHOR,\nQUERO, CADA DIA MAIS,\nOUVIR A TUA VOZ DE AMOR. (3X)', 'ONDE ESTARIA EU, NÃO FOSSE A MÃO DO SENHOR? SOZINHO, SOLITÁRIO NO MUNDO, JESUS ME ALCANÇOU. CORO QUERO, CADA DIA MAIS, VIVER PRA TI, Ó MEU SENHOR, QUERO, CADA DIA MAIS, OUVIR A TUA VOZ DE AMOR. HOJE ME SINTO EM PAZ, SEGURO EM TUA MÃO ESTOU; CAMINHO PARA A ETERNIDADE, CANTANDO PARA O CÉU EU VOU. QUERO, CADA DIA MAIS, VIVER PRA TI, Ó MEU SENHOR, QUERO, CADA DIA MAIS, OUVIR A TUA VOZ DE AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('99', '99 - UM DIA JESUS ACHOU-ME', 'UM DIA JESUS ACHOU-ME\nTÃO LONGE DO MEU LAR,\nPERDIDO JÁ NO MUNDO\nSEM FORÇAS PRA VOLTAR;\nERGUENDO-ME COM OS BRAÇOS,\nLEVANTA AS MINHAS FORÇAS,\nE AGORA ANDAMOS JUNTOS\nVOLTANDO PARA O LAR.\n\nCORO \nA PRESENÇA DE JESUS\nENCHE O CORAÇÃO DE LUZ!\nMUI PRECIOSA FICA,\nCADA VEZ MAIS RICA,\nMINHA VIDA COM JESUS.\n\nPASSANDO PELOS VALES,\nPRA MINHA FÉ PROVAR;\nQUÃO DENSAS SÃO AS TREVAS\nQUE IMPEDEM, POIS, O ANDAR;\nNÃO HÁ NENHUM PERIGO,\nPOIS TENHO JÁ COMIGO,\nUM MUI PRECIOSO AMIGO\nVOLTANDO PARA O LAR.\n\nCORO \nA PRESENÇA DE JESUS\nENCHE O CORAÇÃO DE LUZ!\nMUI PRECIOSA FICA,\nCADA VEZ MAIS RICA,\nMINHA VIDA COM JESUS.\n\nENTRANDO NA ATMOSFERA\nQUE CERCA AQUELE LAR,\nALI MEU PAI ME ESPERA,\nTÃO LONGE HEI DE MORAR;\nNÃO HÁ NO MUNDO INTEIRO\nUM OUTRO COMPANHEIRO\nQUE MOSTRE TANTO ZELO\nVOLTANDO PARA O LAR.\n\nCORO \nA PRESENÇA DE JESUS\nENCHE O CORAÇÃO DE LUZ!\nMUI PRECIOSA FICA,\nCADA VEZ MAIS RICA,\nMINHA VIDA COM JESUS.', 'UM DIA JESUS ACHOU-ME TÃO LONGE DO MEU LAR, PERDIDO JÁ NO MUNDO SEM FORÇAS PRA VOLTAR; ERGUENDO-ME COM OS BRAÇOS, LEVANTA AS MINHAS FORÇAS, E AGORA ANDAMOS JUNTOS VOLTANDO PARA O LAR. CORO A PRESENÇA DE JESUS ENCHE O CORAÇÃO DE LUZ! MUI PRECIOSA FICA, CADA VEZ MAIS RICA, MINHA VIDA COM JESUS. PASSANDO PELOS VALES, PRA MINHA FÉ PROVAR; QUÃO DENSAS SÃO AS TREVAS QUE IMPEDEM, POIS, O ANDAR; NÃO HÁ NENHUM PERIGO, POIS TENHO JÁ COMIGO, UM MUI PRECIOSO AMIGO VOLTANDO PARA O LAR. ENTRANDO NA ATMOSFERA QUE CERCA AQUELE LAR, ALI MEU PAI ME ESPERA, TÃO LONGE HEI DE MORAR; NÃO HÁ NO MUNDO INTEIRO UM OUTRO COMPANHEIRO QUE MOSTRE TANTO ZELO VOLTANDO PARA O LAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('100', '100 - EU ME LEMBRO QUANDO JESUS ME TOCOU', 'EU ME LEMBRO QUANDO JESUS ME TOCOU, \nFOI UM TOQUE DE ESPERANÇA E DE AMOR;\nCRISTO TRANSFORMOU A MORTE NO VIVER \nQUANDO A ELE ENTREGUEI TODO O MEU SER.\n\nCORO\nDEUS AMOU O MUNDO DE TAL MANEIRA\nQUE ENVIOU SEU FILHO JESUS, UNIGÊNITO,\nA MORRER NA CRUZ PRA NOS SALVAR E LIBERTAR-NOS.\nCRISTO VIVE EM MIM, \nPOSSO DIZER, O SEU TOQUE FEZ \nMINH’ALMA RENASCER.\nBIS\n\nEU ME LEMBRO O QUE ELE FEZ POR MIM,\nFOI SEU BRAÇO FORTE QUE ME FEZ ASSIM,\nA CONHECER A ETERNA REDENÇÃO,\nDE BÊNÇÃOS CRISTO ENCHEU MEU CORAÇÃO.', 'EU ME LEMBRO QUANDO JESUS ME TOCOU, FOI UM TOQUE DE ESPERANÇA E DE AMOR; CRISTO TRANSFORMOU A MORTE NO VIVER QUANDO A ELE ENTREGUEI TODO O MEU SER. CORO DEUS AMOU O MUNDO DE TAL MANEIRA QUE ENVIOU SEU FILHO JESUS, UNIGÊNITO, A MORRER NA CRUZ PRA NOS SALVAR E LIBERTAR-NOS. CRISTO VIVE EM MIM, POSSO DIZER, O SEU TOQUE FEZ MINH’ALMA RENASCER. EU ME LEMBRO O QUE ELE FEZ POR MIM, FOI SEU BRAÇO FORTE QUE ME FEZ ASSIM, A CONHECER A ETERNA REDENÇÃO, DE BÊNÇÃOS CRISTO ENCHEU MEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('101', '101 - QUEM ERA EU ANTES DE CONHECER', 'QUEM ERA EU ANTES DE CONHECER\nA OBRA QUE DEUS OPEROU EM MEU SER?\nVIDA SEM DEUS, SEM A LUZ, SEM AMOR,\nANDAVA SEM RUMO, TÃO TRISTE E NA DOR.\nJESUS ME ENCONTROU E COM GRANDE AMOR, \nTOMOU-ME EM SEUS BRAÇOS E ME CARREGOU.\n\nVIVO FELIZ POIS JESUS ME MUDOU,\nA OBRA EM MIM O SENHOR REALIZOU;\nNOVA CRIATURA EU SOU EM JESUS,\nNÃO VIVO MAIS EU, VIVE EM MIM JESUS.\nJESUS É A MAIOR EXPRESSÃO DE AMOR, \nGLÓRIA, ALELUIA, É O NOSSO LOUVOR.\nBIS', 'QUEM ERA EU ANTES DE CONHECER A OBRA QUE DEUS OPEROU EM MEU SER? VIDA SEM DEUS, SEM A LUZ, SEM AMOR, ANDAVA SEM RUMO, TÃO TRISTE E NA DOR. JESUS ME ENCONTROU E COM GRANDE AMOR, TOMOU-ME EM SEUS BRAÇOS E ME CARREGOU. VIVO FELIZ POIS JESUS ME MUDOU, A OBRA EM MIM O SENHOR REALIZOU; NOVA CRIATURA EU SOU EM JESUS, NÃO VIVO MAIS EU, VIVE EM MIM JESUS. JESUS É A MAIOR EXPRESSÃO DE AMOR, GLÓRIA, ALELUIA, É O NOSSO LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('102', '102 - MARAVILHOSO E SUBLIME PRA MIM', 'MARAVILHOSO E SUBLIME PRA MIM,\nSIM, NUNCA ME ESQUECEREI,\nDIA GLORIOSO EM QUE A CRISTO EU VI\nE O CORAÇÃO LHE ENTREGUEI;\nOH! QUÃO PRECIOSO AMIGO ELE É,\nSALVOU-ME DA PERDIÇÃO,\nTIRANDO AS CULPAS E AS TREVAS,\nLIVRANDO E TRAZENDO-ME \nPLENO PERDÃO.\n\nCORO\nA PAZ DO CÉU ENCHEU MEU CORAÇÃO,\nQUANDO JESUS ME DEU A SALVAÇÃO;\nMINH’ALMA ENTÃO LAVOU\nE A LUZ EM MIM RAIOU;\nA PAZ DO CÉU ENCHEU \nMEU CORAÇÃO.\n\nGRANDE ESPERANÇA JESUS JÁ ME DEU,\nQUE NÃO DESVANECERÁ,\nHÁ UMA GLORIOSA MORADA NO CÉU,\nQUE BREVE MINHA SERÁ;\nTUDO PORQUE NESSE DIA FELIZ\nO MEU SENHOR ACEITEI,\nGRANDES RIQUEZAS E BÊNÇÃOS \nCELESTES DAS MÃOS \nDIVINAIS ALCANCEI.\n\nCORO\nA PAZ DO CÉU ENCHEU MEU CORAÇÃO,\nQUANDO JESUS ME DEU A SALVAÇÃO;\nMINH’ALMA ENTÃO LAVOU\nE A LUZ EM MIM RAIOU;\nA PAZ DO CÉU ENCHEU \nMEU CORAÇÃO.', 'MARAVILHOSO E SUBLIME PRA MIM, SIM, NUNCA ME ESQUECEREI, DIA GLORIOSO EM QUE A CRISTO EU VI E O CORAÇÃO LHE ENTREGUEI; OH! QUÃO PRECIOSO AMIGO ELE É, SALVOU-ME DA PERDIÇÃO, TIRANDO AS CULPAS E AS TREVAS, LIVRANDO E TRAZENDO-ME PLENO PERDÃO. CORO A PAZ DO CÉU ENCHEU MEU CORAÇÃO, QUANDO JESUS ME DEU A SALVAÇÃO; MINH’ALMA ENTÃO LAVOU E A LUZ EM MIM RAIOU; A PAZ DO CÉU ENCHEU MEU CORAÇÃO. GRANDE ESPERANÇA JESUS JÁ ME DEU, QUE NÃO DESVANECERÁ, HÁ UMA GLORIOSA MORADA NO CÉU, QUE BREVE MINHA SERÁ; TUDO PORQUE NESSE DIA FELIZ O MEU SENHOR ACEITEI, GRANDES RIQUEZAS E BÊNÇÃOS CELESTES DAS MÃOS DIVINAIS ALCANCEI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('103', '103 - A MINH’ALMA ESTAVA LONGE  DO CAMINHO DO CÉU', 'A MINH’ALMA ESTAVA LONGE\nDO CAMINHO DO CÉU,\nEU ERA POBRE, DESPREZÍVEL PECADOR;\nMAS JESUS JÁ TRANSFORMOU\nMINHAS TREVAS EM LUZ, \nQUANDO ESTENDEU \nA SUA MÃO PARA MIM.\n\nCORO\nQUANDO JESUS ESTENDEU A SUA MÃO,\nQUANDO ESTENDEU A SUA MÃO PARA MIM, \nEU ERA POBRE, PERDIDO,\nSEM DEUS, SEM JESUS, \nQUANDO ESTENDEU \nA SUA MÃO PARA MIM.\n\nAGORA ME REGOZIJO DESDE QUE O \nACEITEI, E NA TEMPESTADE EU POSSO SOSSEGAR,\nPOIS COM ELE ESTOU LIBERTO DO \nPERIGO E DO MAL, DESDE QUE ESTENDEU\nA SUA MÃO PARA MIM.\n\nCORO\nQUANDO JESUS ESTENDEU A SUA MÃO,\nQUANDO ESTENDEU A SUA MÃO PARA MIM, \nEU ERA POBRE, PERDIDO,\nSEM DEUS, SEM JESUS, \nQUANDO ESTENDEU \nA SUA MÃO PARA MIM.', 'A MINH’ALMA ESTAVA LONGE DO CAMINHO DO CÉU, EU ERA POBRE, DESPREZÍVEL PECADOR; MAS JESUS JÁ TRANSFORMOU MINHAS TREVAS EM LUZ, QUANDO ESTENDEU A SUA MÃO PARA MIM. CORO QUANDO JESUS ESTENDEU A SUA MÃO, QUANDO ESTENDEU A SUA MÃO PARA MIM, EU ERA POBRE, PERDIDO, SEM DEUS, SEM JESUS, QUANDO ESTENDEU A SUA MÃO PARA MIM. AGORA ME REGOZIJO DESDE QUE O ACEITEI, E NA TEMPESTADE EU POSSO SOSSEGAR, POIS COM ELE ESTOU LIBERTO DO PERIGO E DO MAL, DESDE QUE ESTENDEU A SUA MÃO PARA MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('104', '104 - VIVI TÃO LONGE DO SENHOR', 'VIVI TÃO LONGE DO SENHOR,\nASSIM EU QUIS ANDAR;\nATÉ QUE EU ENCONTREI O AMOR\nEM SEU GLORIOSO OLHAR.\n\nCORO\nSEU MARAVILHOSO OLHAR, (BIS)\nTRANSFORMOU MEU SER,\nTODO O MEU VIVER,\nSEU MARAVILHOSO OLHAR.\n\nSEU CORPO VI NA RUDE CRUZ\nSOFRENDO ASSIM POR MIM;\nE OUVI A VOZ DO MEU JESUS:\n“POR TI SOFRI ASSIM”.\n\nCORO\nSEU MARAVILHOSO OLHAR, (BIS)\nTRANSFORMOU MEU SER,\nTODO O MEU VIVER,\nSEU MARAVILHOSO OLHAR.\n\nEM CONTRIÇÃO, ENTÃO,\nVOLTEI À FONTE DESSE AMOR;\nPERDÃO E PAZ EM CRISTO ACHEI,\nPERTENÇO AO SALVADOR.\n\nCORO\nSEU MARAVILHOSO OLHAR, (BIS)\nTRANSFORMOU MEU SER,\nTODO O MEU VIVER,\nSEU MARAVILHOSO OLHAR.', 'VIVI TÃO LONGE DO SENHOR, ASSIM EU QUIS ANDAR; ATÉ QUE EU ENCONTREI O AMOR EM SEU GLORIOSO OLHAR. CORO SEU MARAVILHOSO OLHAR,  TRANSFORMOU MEU SER, TODO O MEU VIVER, SEU MARAVILHOSO OLHAR. SEU CORPO VI NA RUDE CRUZ SOFRENDO ASSIM POR MIM; E OUVI A VOZ DO MEU JESUS: POR TI SOFRI ASSIM. EM CONTRIÇÃO, ENTÃO, VOLTEI À FONTE DESSE AMOR; PERDÃO E PAZ EM CRISTO ACHEI, PERTENÇO AO SALVADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('105', '105 - PERDIDO FOI QUE ELE ME ENCONTROU', 'PERDIDO FOI QUE ELE ME ENCONTROU\nNESTE MUNDO VIL;\nTOMOU-ME E COM SEU SANGUE \nME LAVOU; HOJE SOU FELIZ.\n\nCORO\nHOJE SOU FELIZ, FOI-SE O MEU TEMOR,\nVOU PERMANECER JUNTO AO SALVADOR;\nFOI-SE A ESCURIDÃO, RAIA A LUZ DO SOL,\nHOJE EU SEI QUE SOU DO MEU SENHOR\nE ELE É MEU.\n\nSE NEGRA NOITE ASSUSTAR-ME VEM,\nOUÇO A SUA VOZ;\nAS TREVAS TORNAM-SE NA LUZ DO SOL\nCOM MEU SALVADOR.\n\nCORO\nHOJE SOU FELIZ, FOI-SE O MEU TEMOR,\nVOU PERMANECER JUNTO AO SALVADOR;\nFOI-SE A ESCURIDÃO, RAIA A LUZ DO SOL,\nHOJE EU SEI QUE SOU DO MEU SENHOR\nE ELE É MEU.\n\nSE MUITAS TENTAÇÕES ME SOBREVÊM, \nPARA ELE EU VOU;\nRECEBO FORÇAS QUE A MIM CONVÉM, \nSE COM ELE ESTOU.\n\nCORO\nHOJE SOU FELIZ, FOI-SE O MEU TEMOR,\nVOU PERMANECER JUNTO AO SALVADOR;\nFOI-SE A ESCURIDÃO, RAIA A LUZ DO SOL,\nHOJE EU SEI QUE SOU DO MEU SENHOR\nE ELE É MEU.', 'PERDIDO FOI QUE ELE ME ENCONTROU NESTE MUNDO VIL; TOMOU-ME E COM SEU SANGUE ME LAVOU; HOJE SOU FELIZ. CORO HOJE SOU FELIZ, FOI-SE O MEU TEMOR, VOU PERMANECER JUNTO AO SALVADOR; FOI-SE A ESCURIDÃO, RAIA A LUZ DO SOL, HOJE EU SEI QUE SOU DO MEU SENHOR E ELE É MEU. SE NEGRA NOITE ASSUSTAR-ME VEM, OUÇO A SUA VOZ; AS TREVAS TORNAM-SE NA LUZ DO SOL COM MEU SALVADOR. SE MUITAS TENTAÇÕES ME SOBREVÊM, PARA ELE EU VOU; RECEBO FORÇAS QUE A MIM CONVÉM, SE COM ELE ESTOU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('106', '106 - PELA FÉ SOMOS SALVOS', 'PELA FÉ SOMOS SALVOS, \nPELA GRAÇA DE DEUS REVELADA EM JESUS;\nPELA FÉ NO SEU SANGUE VAMOS ALÉM DO VÉU, \nONDE BRILHA A DIVINA LUZ.\n\nCORO\nESTE É O FIRME FUNDAMENTO \nDA IGREJA, A FÉ, SOMENTE A FÉ!\nO INVISÍVEL NÓS JÁ VEMOS, \nPELA FÉ CHEGAREMOS À PÁTRIA \nCELESTIAL, PELA FÉ!\n\nPELA FÉ ENTENDEMOS OS MISTÉRIOS \nDE DEUS E ESCUTAMOS SUA VOZ;\nPELA FÉ A PALAVRA SE REVELA \nA NÓS, PELA FÉ, SOMENTE A FÉ!\n\nCORO\nESTE É O FIRME FUNDAMENTO \nDA IGREJA, A FÉ, SOMENTE A FÉ!\nO INVISÍVEL NÓS JÁ VEMOS, \nPELA FÉ CHEGAREMOS À PÁTRIA \nCELESTIAL, PELA FÉ!', 'PELA FÉ SOMOS SALVOS, PELA GRAÇA DE DEUS REVELADA EM JESUS; PELA FÉ NO SEU SANGUE VAMOS ALÉM DO VÉU, ONDE BRILHA A DIVINA LUZ. CORO ESTE É O FIRME FUNDAMENTO DA IGREJA, A FÉ, SOMENTE A FÉ! O INVISÍVEL NÓS JÁ VEMOS, PELA FÉ CHEGAREMOS À PÁTRIA CELESTIAL, PELA FÉ! PELA FÉ ENTENDEMOS OS MISTÉRIOS DE DEUS E ESCUTAMOS SUA VOZ; PELA FÉ A PALAVRA SE REVELA A NÓS, PELA FÉ, SOMENTE A FÉ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('107', '107 - QUANDO ANDAVA NO MUNDO ESCRAVIZADO', 'QUANDO ANDAVA NO MUNDO ESCRAVIZADO, \nEU VIVIA PRESO NO PECADO, \nMAS UM HOMEM CERTO DIA \nME COMPROU E UM ALTO PREÇO \nPAGOU, E HOJE LIBERTO ESTOU.\n\nCORO\nHOJE EU NÃO SOU MAIS ESCRAVIZADO,\nUM HOMEM CHAMADO JESUS\nTEM ME LIBERTADO;\nLIBERDADE JESUS TEM PROCLAMADO,\nE VIDA NOVA TEM DADO,\nSOU FELIZ COM JESUS AO MEU LADO.\n\nERA ESCRAVO E VIVIA SEMPRE CANSADO,\nNA ANGÚSTIA E NA TRISTEZA SUBJUGADO,\nLIBERDADE EU NÃO TINHA, CRISTO DEU,\nNA CRUZ POR MIM JÁ MORREU\nE VIDA NOVA ME DEU.\nBIS\n\nCORO\nHOJE EU NÃO SOU MAIS ESCRAVIZADO,\nUM HOMEM CHAMADO JESUS\nTEM ME LIBERTADO;\nLIBERDADE JESUS TEM PROCLAMADO,\nE VIDA NOVA TEM DADO,\nSOU FELIZ COM JESUS AO MEU LADO.', 'QUANDO ANDAVA NO MUNDO ESCRAVIZADO, EU VIVIA PRESO NO PECADO, MAS UM HOMEM CERTO DIA ME COMPROU E UM ALTO PREÇO PAGOU, E HOJE LIBERTO ESTOU. CORO HOJE EU NÃO SOU MAIS ESCRAVIZADO, UM HOMEM CHAMADO JESUS TEM ME LIBERTADO; LIBERDADE JESUS TEM PROCLAMADO, E VIDA NOVA TEM DADO, SOU FELIZ COM JESUS AO MEU LADO. ERA ESCRAVO E VIVIA SEMPRE CANSADO, NA ANGÚSTIA E NA TRISTEZA SUBJUGADO, LIBERDADE EU NÃO TINHA, CRISTO DEU, NA CRUZ POR MIM JÁ MORREU E VIDA NOVA ME DEU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('108', '108 - ISRAEL SAIU DO EGITO', 'ISRAEL SAIU DO EGITO,\nFOI ORDEM QUE DEUS LHE DEU,\nCHEGANDO AO MAR VERMELHO,\nTODO O ISRAEL TEMEU;\nMONTES ALTOS DE TODOS OS LADOS,\nMOISÉS LHES RESPONDEU:\nNÃO TEMAS, Ó ISRAEL,\nSÓ O SENHOR É DEUS.\n\nCORO\nSÓ O SENHOR É DEUS! (BIS)\nCANTEMOS COM MUITA ALEGRIA,\nDIZENDO QUE SÓ O SENHOR É DEUS.\n\nMOISÉS ORDENOU ÀS ÁGUAS DO MAR, \nQUE LHE OBEDECERAM, \nPOR TERRA SECA PASSARAM, \nPELO PODER DE DEUS;\nO EXÉRCITO DE FARAÓ, PORÉM, \nNO MAR PERECEU; MIRIÃ CANTOU DIZENDO:\nSÓ O SENHOR É DEUS.\n\nCORO\nSÓ O SENHOR É DEUS! (BIS)\nCANTEMOS COM MUITA ALEGRIA,\nDIZENDO QUE SÓ O SENHOR É DEUS.\n\nNO DESERTO FALTOU ÁGUA, \nISRAEL SE ENTRISTECEU, \nE TODOS SE REVOLTARAM\nCONTRA O SERVO DE DEUS;\nMOISÉS FEZ A ORAÇÃO, \nJEOVÁ LHE APARECEU, \nDA ROCHA BROTOU ÁGUA\nPELO PODER DE DEUS.\n\nCORO\nSÓ O SENHOR É DEUS! (BIS)\nCANTEMOS COM MUITA ALEGRIA,\nDIZENDO QUE SÓ O SENHOR É DEUS.\n\nOS PROFETAS DE BAAL, \nEM HOLOCAUSTOS SEUS, \nCLAMARAM ATÉ O MEIO DIA,\nBAAL NÃO LHES RESPONDEU;\nELIAS FEZ A ORAÇÃO, \nO FOGO DO CÉU DESCEU, \nQUEIMANDO TODO O HOLOCAUSTO, \nCOM O PODER DE DEUS.', 'ISRAEL SAIU DO EGITO, FOI ORDEM QUE DEUS LHE DEU, CHEGANDO AO MAR VERMELHO, TODO O ISRAEL TEMEU; MONTES ALTOS DE TODOS OS LADOS, MOISÉS LHES RESPONDEU: NÃO TEMAS, Ó ISRAEL, SÓ O SENHOR É DEUS. CORO SÓ O SENHOR É DEUS!  CANTEMOS COM MUITA ALEGRIA, DIZENDO QUE SÓ O SENHOR É DEUS. MOISÉS ORDENOU ÀS ÁGUAS DO MAR, QUE LHE OBEDECERAM, POR TERRA SECA PASSARAM, PELO PODER DE DEUS; O EXÉRCITO DE FARAÓ, PORÉM, NO MAR PERECEU; MIRIÃ CANTOU DIZENDO: SÓ O SENHOR É DEUS. NO DESERTO FALTOU ÁGUA, ISRAEL SE ENTRISTECEU, E TODOS SE REVOLTARAM CONTRA O SERVO DE DEUS; MOISÉS FEZ A ORAÇÃO, JEOVÁ LHE APARECEU, DA ROCHA BROTOU ÁGUA PELO PODER DE DEUS. OS PROFETAS DE BAAL, EM HOLOCAUSTOS SEUS, CLAMARAM ATÉ O MEIO DIA, BAAL NÃO LHES RESPONDEU; ELIAS FEZ A ORAÇÃO, O FOGO DO CÉU DESCEU, QUEIMANDO TODO O HOLOCAUSTO, COM O PODER DE DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('109', '109 - A MINHA VIDA TÃO VAZIA', 'A MINHA VIDA TÃO VAZIA\nJESUS CRISTO TRANSFORMOU,\nNÃO TINHA PAZ NEM ALEGRIA\nTÉ QUE O SALVADOR ME ACHOU;\nTAL COMO A FLOR QUE NO JARDIM\nDESPONTA PARA A LUZ DO SOL,\nASSIM BRILHOU UM DIA EM MIM, JESUS.\nNASCI DE NOVO PRA ENTENDER\nO AMOR QUE CRISTO TEM POR MIM;\nJESUS ACHOU-ME, AGORA SOU FELIZ.\n\nPRA ME ENCONTRAR, PERDEU SUA VIDA,\nMEU JESUS, O BOM PASTOR;\nA MÃO QUE FOI NA CRUZ FERIDA\nME SALVOU, TÃO PECADOR.\nA TI, JESUS, CORDEIRO ETERNO,\nCONFESSO QUE ÉS MEU SALVADOR,\nJÁ ME LAVOU TEU SANGUE CARMESIM;\nTÃO FRÁGIL, TÃO PEQUENO SOU, \nCUIDA DE MIM COM TEU AMOR, \nCOMO UMA FLOR PEQUENA \nEM TEU JARDIM.', 'A MINHA VIDA TÃO VAZIA JESUS CRISTO TRANSFORMOU, NÃO TINHA PAZ NEM ALEGRIA TÉ QUE O SALVADOR ME ACHOU; TAL COMO A FLOR QUE NO JARDIM DESPONTA PARA A LUZ DO SOL, ASSIM BRILHOU UM DIA EM MIM, JESUS. NASCI DE NOVO PRA ENTENDER O AMOR QUE CRISTO TEM POR MIM; JESUS ACHOU-ME, AGORA SOU FELIZ. PRA ME ENCONTRAR, PERDEU SUA VIDA, MEU JESUS, O BOM PASTOR; A MÃO QUE FOI NA CRUZ FERIDA ME SALVOU, TÃO PECADOR. A TI, JESUS, CORDEIRO ETERNO, CONFESSO QUE ÉS MEU SALVADOR, JÁ ME LAVOU TEU SANGUE CARMESIM; TÃO FRÁGIL, TÃO PEQUENO SOU, CUIDA DE MIM COM TEU AMOR, COMO UMA FLOR PEQUENA EM TEU JARDIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('110', '110 - OH! QUÃO CEGO ANDEI E PERDIDO VAGUEI', 'OH! QUÃO CEGO ANDEI E PERDIDO VAGUEI\nLONGE, LONGE DO MEU SALVADOR;\nMAS DO CÉU ELE DESCEU E SEU SANGUE VERTEU\nPRA SALVAR UM TÃO POBRE PECADOR.\n\nCORO\nFOI NA CRUZ, FOI NA CRUZ ONDE UM DIA \nEU VI MEU PECADO CASTIGADO EM JESUS;\nFOI ALI PELA FÉ QUE MEUS OLHOS ABRI\nE AGORA ME ALEGRO EM SUA LUZ.\n\nEU OUVIA FALAR DESSA GRAÇA SEM PAR\nQUE DO CÉU TROUXE O NOSSO JESUS,\nMAS EU SURDO ME FIZ, \nCONVERTER-ME NÃO QUIS AO SENHOR \nQUE POR MIM MORREU NA CRUZ.\n\nCORO\nFOI NA CRUZ, FOI NA CRUZ ONDE UM DIA \nEU VI MEU PECADO CASTIGADO EM JESUS;\nFOI ALI PELA FÉ QUE MEUS OLHOS ABRI\nE AGORA ME ALEGRO EM SUA LUZ.\n\nMAS UM DIA SENTI MEU PECADO E VI\nSOBRE MIM A ESPADA DA LEI;\nAPRESSADO FUGI, \nEM JESUS ME ESCONDI E ABRIGO \nSEGURO NELE ACHEI.\n\nCORO\nFOI NA CRUZ, FOI NA CRUZ ONDE UM DIA \nEU VI MEU PECADO CASTIGADO EM JESUS;\nFOI ALI PELA FÉ QUE MEUS OLHOS ABRI\nE AGORA ME ALEGRO EM SUA LUZ.\n\nQUÃO DITOSO, ENTÃO, \nESSE MEU CORAÇÃO CONHECENDO \nO EXCELSO AMOR\nQUE LEVOU MEU JESUS A SOFRER \nLÁ NA CRUZ PRA SALVAR \nUM TÃO POBRE PECADOR.', 'OH! QUÃO CEGO ANDEI E PERDIDO VAGUEI LONGE, LONGE DO MEU SALVADOR; MAS DO CÉU ELE DESCEU E SEU SANGUE VERTEU PRA SALVAR UM TÃO POBRE PECADOR. CORO FOI NA CRUZ, FOI NA CRUZ ONDE UM DIA EU VI MEU PECADO CASTIGADO EM JESUS; FOI ALI PELA FÉ QUE MEUS OLHOS ABRI E AGORA ME ALEGRO EM SUA LUZ. EU OUVIA FALAR DESSA GRAÇA SEM PAR QUE DO CÉU TROUXE O NOSSO JESUS, MAS EU SURDO ME FIZ, CONVERTER-ME NÃO QUIS AO SENHOR QUE POR MIM MORREU NA CRUZ. MAS UM DIA SENTI MEU PECADO E VI SOBRE MIM A ESPADA DA LEI; APRESSADO FUGI, EM JESUS ME ESCONDI E ABRIGO SEGURO NELE ACHEI. QUÃO DITOSO, ENTÃO, ESSE MEU CORAÇÃO CONHECENDO O EXCELSO AMOR QUE LEVOU MEU JESUS A SOFRER LÁ NA CRUZ PRA SALVAR UM TÃO POBRE PECADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('111', '111 - O POVO QUE ANDAVA EM TREVAS', 'O POVO QUE ANDAVA EM TREVAS VIU\nUMA GRANDE LUZ, E SOBRE OS QUE ANDAVAM \nNA SOMBRA DA MORTE,\nRESPLANDECEU A LUZ;\nPORQUE UM MENINO NOS NASCEU,\nUM FILHO SE NOS DEU;\nO PRINCIPADO ESTÁ SOBRE OS SEUS OMBROS,\nELE É JESUS, JESUS, A LUZ DO MUNDO.\nINSTRUMENTOS\n\nMARAVILHOSO CONSELHEIRO, \nSEU NOME SERÁ,\nDEUS FORTE, PAI DA ETERNIDADE,\nPRÍNCIPE DA PAZ;\nDO PRINCIPADO E DA PAZ \nNÃO HAVERÁ FIM\nE SOBRE O TRONO DE DAVI REINARÁ JESUS,\nJESUS, A LUZ DO MUNDO.', 'O POVO QUE ANDAVA EM TREVAS VIU UMA GRANDE LUZ, E SOBRE OS QUE ANDAVAM NA SOMBRA DA MORTE, RESPLANDECEU A LUZ; PORQUE UM MENINO NOS NASCEU, UM FILHO SE NOS DEU; O PRINCIPADO ESTÁ SOBRE OS SEUS OMBROS, ELE É JESUS, JESUS, A LUZ DO MUNDO. INSTRUMENTOS MARAVILHOSO CONSELHEIRO, SEU NOME SERÁ, DEUS FORTE, PAI DA ETERNIDADE, PRÍNCIPE DA PAZ; DO PRINCIPADO E DA PAZ NÃO HAVERÁ FIM E SOBRE O TRONO DE DAVI REINARÁ JESUS, JESUS, A LUZ DO MUNDO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('112', '112 - UMA LUZ BRILHOU EM MEU CAMINHO', 'UMA LUZ BRILHOU EM MEU CAMINHO,\nQUANDO EU IA TRISTE E SOZINHO;\nQUANDO EU OLHEI PRA CRUZ,\nNELA EU VI JESUS,\nFOI UM MINUTO SÓ DO SEU OLHAR.\n\nCORO\nFOI UM MINUTO SÓ, UM MINUTO SÓ,\nFOI UM MINUTO SÓ DO SEU OLHAR;\nTUDO EM MIM MUDOU, TUDO EM MIM CANTOU,\nFOI UM MINUTO SÓ DO SEU OLHAR.\n\nJESUS MARCOU A MINHA VIDA,\nNUNCA MAIS EU SEREI O MESMO;\nFOI SEU DIVINO OLHAR\nQUE ME ENSINOU A AMAR,\nFOI UM MINUTO SÓ DO SEU OLHAR.\n\nCORO\nFOI UM MINUTO SÓ, UM MINUTO SÓ,\nFOI UM MINUTO SÓ DO SEU OLHAR;\nTUDO EM MIM MUDOU, TUDO EM MIM CANTOU,\nFOI UM MINUTO SÓ DO SEU OLHAR.', 'UMA LUZ BRILHOU EM MEU CAMINHO, QUANDO EU IA TRISTE E SOZINHO; QUANDO EU OLHEI PRA CRUZ, NELA EU VI JESUS, FOI UM MINUTO SÓ DO SEU OLHAR. CORO FOI UM MINUTO SÓ, UM MINUTO SÓ, FOI UM MINUTO SÓ DO SEU OLHAR; TUDO EM MIM MUDOU, TUDO EM MIM CANTOU, FOI UM MINUTO SÓ DO SEU OLHAR. JESUS MARCOU A MINHA VIDA, NUNCA MAIS EU SEREI O MESMO; FOI SEU DIVINO OLHAR QUE ME ENSINOU A AMAR, FOI UM MINUTO SÓ DO SEU OLHAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('113', '113 - JESUS, A LUZ DIVINA', 'JESUS, A LUZ DIVINA\nQUE NÃO CESSA DE BRILHAR;\nDE DIA E DE NOITE E SEMPRE \nRELUZ SEM APAGAR.\n\nSÓ ELE É A LUZ QUE BRILHA,\nE PERMANECE SOBRE O ALTAR,\nE CONTINUA SEMPRE A ILUMINAR.\nBIS\n\nJESUS, A LUZ QUE BRILHA\nEM NOSSOS CORAÇÕES,\nTIRANDO DAS NOSSAS MENTES\nAS NOSSAS ILUSÕES.\n\nSÓ ELE É A LUZ QUE BRILHA,\nEM TODO CÉU, NA TERRA E MAR,\nE CONTINUA SEMPRE A ILUMINAR.\nBIS', 'JESUS, A LUZ DIVINA QUE NÃO CESSA DE BRILHAR; DE DIA E DE NOITE E SEMPRE RELUZ SEM APAGAR. SÓ ELE É A LUZ QUE BRILHA, E PERMANECE SOBRE O ALTAR, E CONTINUA SEMPRE A ILUMINAR. JESUS, A LUZ QUE BRILHA EM NOSSOS CORAÇÕES, TIRANDO DAS NOSSAS MENTES AS NOSSAS ILUSÕES. SÓ ELE É A LUZ QUE BRILHA, EM TODO CÉU, NA TERRA E MAR, E CONTINUA SEMPRE A ILUMINAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('114', '114 - AS RIQUEZAS DO MUNDO', 'AS RIQUEZAS DO MUNDO NADA VALEM PRA MIM,\nPOIS, JESUS, NO TEU REINO DÁS-ME VIDA SEM FIM;\nE NO LIVRO DA VIDA QUE CONSERVAS AÍ,\nCERTO ESTOU QUE MEU NOME FOI ESCRITO POR TI.\n\nCORO\nFOI ESCRITO POR TI O MEU NOME NO CÉU;\nSIM, NO LIVRO DA VIDA, \nFOI ESCRITO POR TI.\n\nMEUS PECADOS SÃO MUITOS, COMO A AREIA DO MAR;\nMAS JESUS ME REVELA SUA GRAÇA SEM PAR;\nVEIO PARA SALVAR-ME, O SEU SANGUE VERTEU,\nE AS DELÍCIAS ETERNAS ELE ME PROMETEU.\n\nCORO\nFOI ESCRITO POR TI O MEU NOME NO CÉU;\nSIM, NO LIVRO DA VIDA, \nFOI ESCRITO POR TI.\n\nÓ CIDADE FESTIVA, REFULGENTE DE LUZ,\nÉS MORADA DOS SANTOS E FIÉIS DE JESUS!\nSÓ VERÁ TUA GLÓRIA O QUE CRER NO SENHOR, \nCUJA VIDA FOI SALVA PELO SEU REDENTOR.\n\nCORO\nFOI ESCRITO POR TI O MEU NOME NO CÉU;\nSIM, NO LIVRO DA VIDA, \nFOI ESCRITO POR TI.', 'AS RIQUEZAS DO MUNDO NADA VALEM PRA MIM, POIS, JESUS, NO TEU REINO DÁS-ME VIDA SEM FIM; E NO LIVRO DA VIDA QUE CONSERVAS AÍ, CERTO ESTOU QUE MEU NOME FOI ESCRITO POR TI. CORO FOI ESCRITO POR TI O MEU NOME NO CÉU; SIM, NO LIVRO DA VIDA, FOI ESCRITO POR TI. MEUS PECADOS SÃO MUITOS, COMO A AREIA DO MAR; MAS JESUS ME REVELA SUA GRAÇA SEM PAR; VEIO PARA SALVAR-ME, O SEU SANGUE VERTEU, E AS DELÍCIAS ETERNAS ELE ME PROMETEU. Ó CIDADE FESTIVA, REFULGENTE DE LUZ, ÉS MORADA DOS SANTOS E FIÉIS DE JESUS! SÓ VERÁ TUA GLÓRIA O QUE CRER NO SENHOR, CUJA VIDA FOI SALVA PELO SEU REDENTOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('115', '115 - GRAÇAS DOU, Ó SENHOR', 'GRAÇAS DOU, Ó SENHOR, \nAINDA QUE TE IRASTE CONTRA MIM, \nTUA IRA SE RETIROU, TU ME CONSOLASTE.\nEIS QUE DEUS É SALVAÇÃO, \nCONFIAREI, E NÃO TEMEREI;\nO SENHOR, MINHA FORÇA E O MEU LOUVOR;\nSE TORNOU MINHA SALVAÇÃO.\n\nCORO (4X)\nCOM ALEGRIA TIRAREIS ÁGUAS\nDAS FONTES DA SALVAÇÃO.\n\nINSTRUMENTOS\nFINAL: GRAÇAS DOU, Ó SENHOR.', 'GRAÇAS DOU, Ó SENHOR, AINDA QUE TE IRASTE CONTRA MIM, TUA IRA SE RETIROU, TU ME CONSOLASTE. EIS QUE DEUS É SALVAÇÃO, CONFIAREI, E NÃO TEMEREI; O SENHOR, MINHA FORÇA E O MEU LOUVOR; SE TORNOU MINHA SALVAÇÃO. CORO  COM ALEGRIA TIRAREIS ÁGUAS DAS FONTES DA SALVAÇÃO. INSTRUMENTOS  GRAÇAS DOU, Ó SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('116', '116 - ACHEI SALVAÇÃO', 'ACHEI SALVAÇÃO EM JESUS, \nMEU SENHOR,\nPERDÃO, LIBERDADE ENCONTREI;\nSUA ALMA RESIDE EM MEU CORAÇÃO\nE NO SEU AMOR TENHO PAZ.\n\nCORO\nPAZ DO SENHOR, CÉLICA PAZ,\nPAZ QUE ESTE MUNDO NÃO DÁ;\nQUANDO PENSO NA LUZ \nQUE CENTELHA DA CRUZ,\nTENHO PAZ, UMA CÉLICA PAZ.\n\nA CRISTO, MEU REI, EU DAREI UMA \nCANÇÃO TALENTO, SERVIÇO E LOUVOR;\nCONTENTE AOS SEUS PÉS, \nMEU FIEL CORAÇÃO DIRÁ: \nTENHO PAZ NO SENHOR.\n\nCORO\nPAZ DO SENHOR, CÉLICA PAZ,\nPAZ QUE ESTE MUNDO NÃO DÁ;\nQUANDO PENSO NA LUZ \nQUE CENTELHA DA CRUZ,\nTENHO PAZ, UMA CÉLICA PAZ.', 'ACHEI SALVAÇÃO EM JESUS, MEU SENHOR, PERDÃO, LIBERDADE ENCONTREI; SUA ALMA RESIDE EM MEU CORAÇÃO E NO SEU AMOR TENHO PAZ. CORO PAZ DO SENHOR, CÉLICA PAZ, PAZ QUE ESTE MUNDO NÃO DÁ; QUANDO PENSO NA LUZ QUE CENTELHA DA CRUZ, TENHO PAZ, UMA CÉLICA PAZ. A CRISTO, MEU REI, EU DAREI UMA CANÇÃO TALENTO, SERVIÇO E LOUVOR; CONTENTE AOS SEUS PÉS, MEU FIEL CORAÇÃO DIRÁ: TENHO PAZ NO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('117', '117 - COMO DESISTIR DESTE GRANDE AMOR?', 'COMO DESISTIR DESTE GRANDE AMOR?\nSE NÃO HÁ OUTRO AMOR IGUAL;\nNÃO DESISTIREI, PRA ONDE IREI?\n\nNÃO POSSO PARAR NEM DESANIMAR,\nTENS ME DADO FORÇAS PRA VENCER;\nNÃO DESISTIREI, PRA ONDE IREI?\n\nSENHOR, TU ÉS A PORÇÃO\nDA MINHA HERANÇA, DO MEU CÁLICE;\nSENHOR, ÉS MEU AMOR,\nPRA ONDE IREI, SE SÓ TU ÉS MEU SALVADOR?\n\nINSTRUMENTOS\n\nCOMO DESISTIR DESTE GRANDE AMOR?\nSE NÃO HÁ OUTRO AMOR IGUAL;\nNÃO DESISTIREI, PRA ONDE IREI?\n\nNÃO POSSO PARAR NEM DESANIMAR,\nTENS ME DADO FORÇAS PRA VENCER;\nNÃO DESISTIREI, PRA ONDE IREI?\n\nSENHOR, TU ÉS A PORÇÃO\nDA MINHA HERANÇA, DO MEU CÁLICE;\nSENHOR, ÉS MEU AMOR,\nPRA ONDE IREI, SE SÓ TU ÉS MEU SALVADOR?\n\nFINAL: ÉS MINHA HERANÇA, SENHOR.', 'COMO DESISTIR DESTE GRANDE AMOR? SE NÃO HÁ OUTRO AMOR IGUAL; NÃO DESISTIREI, PRA ONDE IREI? NÃO POSSO PARAR NEM DESANIMAR, TENS ME DADO FORÇAS PRA VENCER; NÃO DESISTIREI, PRA ONDE IREI? SENHOR, TU ÉS A PORÇÃO DA MINHA HERANÇA, DO MEU CÁLICE; SENHOR, ÉS MEU AMOR, PRA ONDE IREI, SE SÓ TU ÉS MEU SALVADOR? INSTRUMENTOS SENHOR, TU ÉS A PORÇÃO DA MINHA HERANÇA, DO MEU CÁLICE; SENHOR, ÉS MEU AMOR, PRA ONDE IREI, SE SÓ TU ÉS MEU SALVADOR?  ÉS MINHA HERANÇA, SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('118', '118 - Ó SENHOR, DEUS DE ISRAEL  (EDIFIQUEI UMA CASA)', 'Ó SENHOR, DEUS DE ISRAEL!\nNO CÉU E NA TERRA (NÃO HÁ DEUS)\nNÃO HÁ DEUS COMO TU QUE GUARDAS \nA ALIANÇA E A MISERICÓRDIA\nAOS TEUS SERVOS QUE ANDAM DIANTE DE TI.\n\nCORO\nEDIFIQUEI UMA CASA \nPARA TUA MORADA, \nUM LUGAR PARA A TUA \nETERNA HABITAÇÃO.\n\nOUVE, POIS, DESDE O CÉU,\nA CADA ORAÇÃO\nE PERDOA (E PERDOA) \nO TEU POVO QUANDO, ARREPENDIDO, \nCONFESSAR O TEU NOME\nE BUSCAR TUA FACE NESTE LUGAR.\n\nCORO\nEDIFIQUEI UMA CASA \nPARA TUA MORADA, \nUM LUGAR PARA A TUA \nETERNA HABITAÇÃO.\n\nVEM, SENHOR, ACEITA E APERFEIÇOA\nA ADORAÇÃO QUE O TEU POVO\nSINCERAMENTE FAZ,\nCOMO OFERTA SUAVE DE LOUVOR.\n\nINSTRUMENTOS\n\nCOMO OFERTA SUAVE DE LOUVOR,\nCOMO OFERTA SUAVE DE LOUVOR.\n(COMO OFERTA DE LOUVOR)', 'Ó SENHOR, DEUS DE ISRAEL! NO CÉU E NA TERRA (NÃO HÁ DEUS) NÃO HÁ DEUS COMO TU QUE GUARDAS A ALIANÇA E A MISERICÓRDIA AOS TEUS SERVOS QUE ANDAM DIANTE DE TI. CORO EDIFIQUEI UMA CASA PARA TUA MORADA, UM LUGAR PARA A TUA ETERNA HABITAÇÃO. OUVE, POIS, DESDE O CÉU, A CADA ORAÇÃO E PERDOA (E PERDOA) O TEU POVO QUANDO, ARREPENDIDO, CONFESSAR O TEU NOME E BUSCAR TUA FACE NESTE LUGAR. VEM, SENHOR, ACEITA E APERFEIÇOA A ADORAÇÃO QUE O TEU POVO SINCERAMENTE FAZ, COMO OFERTA SUAVE DE LOUVOR. INSTRUMENTOS COMO OFERTA SUAVE DE LOUVOR, COMO OFERTA SUAVE DE LOUVOR. (COMO OFERTA DE LOUVOR)', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('119', '119 - JESUS, NOME PRECIOSO', 'JESUS, NOME PRECIOSO,\nJESUS, REI E SENHOR,\nJESUS, POR ESTE NOME\nSOU MAIS QUE VENCEDOR!\n\nJESUS, LÍRIO DOS VALES,\nJESUS, FLOR DO JARDIM,\nJESUS, COM TEU AMOR VEM\nHOJE TOCAR EM MIM!\n\nUMA PALAVRA SÓ BASTA, SENHOR,\nE TODO O MAL SE VAI E TODA A DOR;\nUMA PALAVRA SÓ DO TEU PODER\nE EM NOME DE JESUS EU VOU VENCER!\n\nJESUS, NOME DE VIDA,\nJESUS, FILHO DE DEUS,\nA TI TUDO SE INCLINA\nNA TERRA E NOS CÉUS! (BIS)', 'JESUS, NOME PRECIOSO, JESUS, REI E SENHOR, JESUS, POR ESTE NOME SOU MAIS QUE VENCEDOR! JESUS, LÍRIO DOS VALES, JESUS, FLOR DO JARDIM, JESUS, COM TEU AMOR VEM HOJE TOCAR EM MIM! UMA PALAVRA SÓ BASTA, SENHOR, E TODO O MAL SE VAI E TODA A DOR; UMA PALAVRA SÓ DO TEU PODER E EM NOME DE JESUS EU VOU VENCER! JESUS, NOME DE VIDA, JESUS, FILHO DE DEUS, A TI TUDO SE INCLINA NA TERRA E NOS CÉUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('120', '120 - JESUS CRISTO MUDOU MEU VIVER', 'JESUS CRISTO MUDOU MEU VIVER (BIS)\nÉ A LUZ QUE ILUMINA MEU SER,\nSIM, JESUS CRISTO MUDOU MEU VIVER.\n\nDIFERENTE HOJE É O MEU CORAÇÃO (BIS)\nCRISTO DEU-ME PAZ E PERDÃO,\nSIM, DIFERENTE HOJE É O MEU CORAÇÃO.\n\nO AMOR QUE CONHECI EM CANÇÕES\nME FALAVA DE ILUSÕES;\nHOJE TUDO É DIFERENTE,\nISSO FALO A TODA A GENTE,\nQUE CRISTO SALVA O PECADOR.\n\nJESUS CRISTO MUDOU MEU VIVER (BIS)\nÉ A LUZ QUE ILUMINA MEU SER,\nSIM, JESUS CRISTO MUDOU MEU VIVER.', 'JESUS CRISTO MUDOU MEU VIVER  É A LUZ QUE ILUMINA MEU SER, SIM, JESUS CRISTO MUDOU MEU VIVER. DIFERENTE HOJE É O MEU CORAÇÃO  CRISTO DEU-ME PAZ E PERDÃO, SIM, DIFERENTE HOJE É O MEU CORAÇÃO. O AMOR QUE CONHECI EM CANÇÕES ME FALAVA DE ILUSÕES; HOJE TUDO É DIFERENTE, ISSO FALO A TODA A GENTE, QUE CRISTO SALVA O PECADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('121', '121 - TUDO, Ó CRISTO, A TI ENTREGO', 'TUDO, Ó CRISTO, A TI ENTREGO,\nTUDO, SIM, POR TI DAREI;\nRESOLUTO, MAS SUBMISSO,\nSEMPRE, SEMPRE SEGUIREI.\n\nCORO\nTUDO ENTREGAREI, \nTUDO ENTREGAREI;\nSIM, POR TI, JESUS BENDITO, \nTUDO DEIXAREI.\n\nTUDO, Ó CRISTO, A TI ENTREGO,\nCORPO E ALMA, EIS-ME AQUI;\nESTE MUNDO MAU RENEGO,\nÓ JESUS, ACEITA A MIM.\n\nCORO\nTUDO ENTREGAREI, \nTUDO ENTREGAREI;\nSIM, POR TI, JESUS BENDITO, \nTUDO DEIXAREI.\n\nTUDO, Ó CRISTO, A TI ENTREGO,\nOH! QUE GOZO MEU SENHOR!\nPAZ PERFEITA, PAZ COMPLETA,\nGLÓRIA, GLÓRIA AO SALVADOR.\n\nCORO\nTUDO ENTREGAREI, \nTUDO ENTREGAREI;\nSIM, POR TI, JESUS BENDITO, \nTUDO DEIXAREI.', 'TUDO, Ó CRISTO, A TI ENTREGO, TUDO, SIM, POR TI DAREI; RESOLUTO, MAS SUBMISSO, SEMPRE, SEMPRE SEGUIREI. CORO TUDO ENTREGAREI, TUDO ENTREGAREI; SIM, POR TI, JESUS BENDITO, TUDO DEIXAREI. TUDO, Ó CRISTO, A TI ENTREGO, CORPO E ALMA, EIS-ME AQUI; ESTE MUNDO MAU RENEGO, Ó JESUS, ACEITA A MIM. TUDO, Ó CRISTO, A TI ENTREGO, OH! QUE GOZO MEU SENHOR! PAZ PERFEITA, PAZ COMPLETA, GLÓRIA, GLÓRIA AO SALVADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('122', '122 - AMO AO SENHOR PORQUE ELE ME OUVIU', 'AMO AO SENHOR PORQUE ELE ME OUVIU,\nINCLINOU OS SEUS OUVIDOS E ASSIM ME ATENDEU;\nO SENHOR, QUE É JUSTO E PIEDOSO,\nPOSSO NELE CONFIAR, CERTAMENTE ME GUIARÁ.\n\nANDAREI PERANTE A SUA FACE,\nSOU SEU SERVO, POIS ME LIBERTOU;\nLOUVÁ-LO-EI ETERNAMENTE \nNOS ÁTRIOS DA SUA CASA, \nFAREI A DEUS SACRIFÍCIOS DE LOUVOR.\nBIS', 'AMO AO SENHOR PORQUE ELE ME OUVIU, INCLINOU OS SEUS OUVIDOS E ASSIM ME ATENDEU; O SENHOR, QUE É JUSTO E PIEDOSO, POSSO NELE CONFIAR, CERTAMENTE ME GUIARÁ. ANDAREI PERANTE A SUA FACE, SOU SEU SERVO, POIS ME LIBERTOU; LOUVÁ-LO-EI ETERNAMENTE NOS ÁTRIOS DA SUA CASA, FAREI A DEUS SACRIFÍCIOS DE LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('123', '123 - SEGUIR-TE-EI', 'SEGUIR-TE-EI POR ONDE \nQUER QUE EU VÁ,\nLOUVAR-TE-EI DE TODO O CORAÇÃO;\nÉS PRECIOSO, AMADO E BOM JESUS, \nÉS MINHA VIDA, ÉS MINHA LUZ.\nBIS\n\nTEU GRANDE AMOR POR MIM \nNÃO POSSO COMPARAR, \nPOR ISSO EU TE LOUVO,\nSOFRESTE EM MEU LUGAR, POR ISSO \nEU TE LOUVO, MORRESTE PRA ME SALVAR.\n\nINSTRUMENTOS\n\nSEGUIR-TE-EI POR ONDE \nQUER QUE EU VÁ\nLOUVAR-TE-EI DE TODO O CORAÇÃO;\nÉS PRECIOSO, AMADO E BOM JESUS,\nÉS MINHA VIDA, ÉS MINHA LUZ.', 'SEGUIR-TE-EI POR ONDE QUER QUE EU VÁ, LOUVAR-TE-EI DE TODO O CORAÇÃO; ÉS PRECIOSO, AMADO E BOM JESUS, ÉS MINHA VIDA, ÉS MINHA LUZ. TEU GRANDE AMOR POR MIM NÃO POSSO COMPARAR, POR ISSO EU TE LOUVO, SOFRESTE EM MEU LUGAR, POR ISSO EU TE LOUVO, MORRESTE PRA ME SALVAR. INSTRUMENTOS SEGUIR-TE-EI POR ONDE QUER QUE EU VÁ LOUVAR-TE-EI DE TODO O CORAÇÃO; ÉS PRECIOSO, AMADO E BOM JESUS, ÉS MINHA VIDA, ÉS MINHA LUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('124', '124 - PEREGRINANDO POR SOBRE OS MONTES', 'PEREGRINANDO POR SOBRE OS MONTES\nE PELOS VALES, SEMPRE NA LUZ;\nCRISTO PROMETE NUNCA DEIXAR-ME:\n“EIS-ME CONVOSCO”, DISSE JESUS.\n\nCORO\nBRILHO CELESTE, BRILHO CELESTE,\nENCHE A MINH’ALMA A GLÓRIA DO CÉU!\nALELUIA! SIGO CANTANDO,\nDANDO LOUVORES, CRISTO É MEU.\n\nSOMBRAS À RODA, SOMBRAS EM CIMA,\nO SALVADOR NÃO HÃO DE OCULTAR;\nELE É A LUZ QUE NUNCA SE APAGA,\nJUNTO AO SEU LADO SEMPRE HEI DE ANDAR.\n\nCORO\nBRILHO CELESTE, BRILHO CELESTE,\nENCHE A MINH’ALMA A GLÓRIA DO CÉU!\nALELUIA! SIGO CANTANDO,\nDANDO LOUVORES, CRISTO É MEU.\n\nA LUZ BENDITA ME VAI CERCANDO,\nPASSOS AVANTE PARA A MANSÃO!\nMAIS E MAIS PERTO SEGUINDO O MESTRE,\nPOSSUO O GOZO DA SALVAÇÃO.\n\nCORO\nBRILHO CELESTE, BRILHO CELESTE,\nENCHE A MINH’ALMA A GLÓRIA DO CÉU!\nALELUIA! SIGO CANTANDO,\nDANDO LOUVORES, CRISTO É MEU.', 'PEREGRINANDO POR SOBRE OS MONTES E PELOS VALES, SEMPRE NA LUZ; CRISTO PROMETE NUNCA DEIXAR-ME: EIS-ME CONVOSCO, DISSE JESUS. CORO BRILHO CELESTE, BRILHO CELESTE, ENCHE A MINH’ALMA A GLÓRIA DO CÉU! ALELUIA! SIGO CANTANDO, DANDO LOUVORES, CRISTO É MEU. SOMBRAS À RODA, SOMBRAS EM CIMA, O SALVADOR NÃO HÃO DE OCULTAR; ELE É A LUZ QUE NUNCA SE APAGA, JUNTO AO SEU LADO SEMPRE HEI DE ANDAR. A LUZ BENDITA ME VAI CERCANDO, PASSOS AVANTE PARA A MANSÃO! MAIS E MAIS PERTO SEGUINDO O MESTRE, POSSUO O GOZO DA SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('125', '125 - PELA FÉ EU CAMINHO', 'PELA FÉ EU CAMINHO OLHANDO SÓ PRA JESUS,\nMINHA MÃO PUS NA SUA E ASSIM SEGURO VOU.\nCOMO A LUZ DA AURORA, MINHA VEREDA VAI BRILHANDO MAIS E MAIS, ATÉ O DIA, \nDIA PERFEITO, CHEGAR.\nBIS', 'PELA FÉ EU CAMINHO OLHANDO SÓ PRA JESUS, MINHA MÃO PUS NA SUA E ASSIM SEGURO VOU. COMO A LUZ DA AURORA, MINHA VEREDA VAI BRILHANDO MAIS E MAIS, ATÉ O DIA, DIA PERFEITO, CHEGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('126', '126 - QUERO PROSSEGUIR', 'QUERO PROSSEGUIR NESTA JORNADA,\nEU FUI CHAMADO,\nCOM LAÇOS DE AMOR ABENÇOADO,\nTENHO MEU JESUS SEMPRE AO MEU LADO.\n\nNÃO TEMER, PROSSEGUIR, E VENCER TODA LUTA, \nENFIM; MEU DEUS ESTÁ COMIGO, \nSINTO SUA PRESENÇA; DEU-ME CONFIANÇA,\nESTE É MEU SENHOR!\nAMIGO VERDADEIRO,\nSINCERO E AMOROSO,\nLEAL E COMPANHEIRO,\nJESUS, TENS MEU AMOR.\nBIS', 'QUERO PROSSEGUIR NESTA JORNADA, EU FUI CHAMADO, COM LAÇOS DE AMOR ABENÇOADO, TENHO MEU JESUS SEMPRE AO MEU LADO. NÃO TEMER, PROSSEGUIR, E VENCER TODA LUTA, ENFIM; MEU DEUS ESTÁ COMIGO, SINTO SUA PRESENÇA; DEU-ME CONFIANÇA, ESTE É MEU SENHOR! AMIGO VERDADEIRO, SINCERO E AMOROSO, LEAL E COMPANHEIRO, JESUS, TENS MEU AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('127', '127 - ESTOU A CAMINHAR', 'ESTOU A CAMINHAR PARA O MEU LAR NO CÉU;\nSOU PEREGRINO AQUI, O VIVER ETERNO É MEU.\nDE GRAÇA RECEBI DE DEUS A SALVAÇÃO,\nBENDITO O SANGUE QUE ME LAVOU,\nCREIO EM MEU CORAÇÃO.', 'ESTOU A CAMINHAR PARA O MEU LAR NO CÉU; SOU PEREGRINO AQUI, O VIVER ETERNO É MEU. DE GRAÇA RECEBI DE DEUS A SALVAÇÃO, BENDITO O SANGUE QUE ME LAVOU, CREIO EM MEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('128', '128 - QUANDO A MÃO DE DEUS EM MIM VEIO TOCAR', 'QUANDO A MÃO DE DEUS EM MIM VEIO TOCAR,\nALEGRIA TRANSBORDOU MEU VIVER;\nSUA VOZ SUAVE E MEIGA EM MIM SOOU, \nPUDE ENTÃO SENTIR O SEU IMENSO AMOR.\n\nCORO (2X) \nSOU FELIZ,\nSOU FELIZ EM JESUS,\nSIM, EU SOU FELIZ.\n\nQUANDO O TOQUE DO ESPÍRITO SENTI,\nDEU-ME PAZ E TRANSFORMOU A MINHA VIDA;\nCOMO O SOL NO SEU ALTO ESPLENDOR,\nCRISTO INUNDOU-ME COM O SEU AMOR.\n\nCORO  (2X)\nSOU FELIZ,\nSOU FELIZ EM JESUS, \nSIM, EU SOU FELIZ.\n\nFINAL: \nSOU FELIZ EM JESUS,\nPOSSO ENTÃO DIZER: \nFELIZ, SOU FELIZ.', 'QUANDO A MÃO DE DEUS EM MIM VEIO TOCAR, ALEGRIA TRANSBORDOU MEU VIVER; SUA VOZ SUAVE E MEIGA EM MIM SOOU, PUDE ENTÃO SENTIR O SEU IMENSO AMOR. SOU FELIZ, SOU FELIZ EM JESUS, SIM, EU SOU FELIZ. QUANDO O TOQUE DO ESPÍRITO SENTI, DEU-ME PAZ E TRANSFORMOU A MINHA VIDA; COMO O SOL NO SEU ALTO ESPLENDOR, CRISTO INUNDOU-ME COM O SEU AMOR. SOU FELIZ, SOU FELIZ EM JESUS, SIM, EU SOU FELIZ.  SOU FELIZ EM JESUS, POSSO ENTÃO DIZER: FELIZ, SOU FELIZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('129', '129 - FELICIDADE É TER CRISTO AO MEU LADO', 'FELICIDADE É TER CRISTO AO MEU \nLADO E POR ELE SER GUARDADO,\nTER MEU NOME ESCRITO NO CÉU;\nMEU NOME ESTÁ ESCRITO\nNAS MÃOS DO MEU JESUS,\nQUANDO MORREU ALI NA CRUZ,\nE ASSIM MUI FELIZ SOU EU.\n\nCORO\nFELICIDADE É POSSUIR O MEU JESUS\nE TODO DIA LEVAR A CRUZ\nATÉ O DIA EM QUE ELE VOLTAR;\nFELICIDADE É SER LAVADO\nNO SANGUE DE JESUS,\nE SEMPRE ANDAR NA SUA LUZ,\nATÉ O DIA EM QUE ELE VOLTAR.\n\nFELICIDADE É IR MORAR NUMA CIDADE,\nONDE HÁ PAZ E VERDADE,\nÉ JESUS QUEM MORA LÁ!\nEU SOU FELIZ E EM JESUS EU TENHO ALGO,\nE POR ELE JÁ SOU SALVO,\nELE PODE ME GUARDAR!\n\nCORO\nFELICIDADE É POSSUIR O MEU JESUS\nE TODO DIA LEVAR A CRUZ\nATÉ O DIA EM QUE ELE VOLTAR;\nFELICIDADE É SER LAVADO\nNO SANGUE DE JESUS,\nE SEMPRE ANDAR NA SUA LUZ,\nATÉ O DIA EM QUE ELE VOLTAR.', 'FELICIDADE É TER CRISTO AO MEU LADO E POR ELE SER GUARDADO, TER MEU NOME ESCRITO NO CÉU; MEU NOME ESTÁ ESCRITO NAS MÃOS DO MEU JESUS, QUANDO MORREU ALI NA CRUZ, E ASSIM MUI FELIZ SOU EU. CORO FELICIDADE É POSSUIR O MEU JESUS E TODO DIA LEVAR A CRUZ ATÉ O DIA EM QUE ELE VOLTAR; FELICIDADE É SER LAVADO NO SANGUE DE JESUS, E SEMPRE ANDAR NA SUA LUZ, ATÉ O DIA EM QUE ELE VOLTAR. FELICIDADE É IR MORAR NUMA CIDADE, ONDE HÁ PAZ E VERDADE, É JESUS QUEM MORA LÁ! EU SOU FELIZ E EM JESUS EU TENHO ALGO, E POR ELE JÁ SOU SALVO, ELE PODE ME GUARDAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('130', '130 - TENHO GRANDE GOZO', 'TENHO GRANDE GOZO DESDE QUE REPOUSO\nNESTE CRISTO AMADO QUE POR MIM FOI DADO;\nQUE DESCEU DA GLÓRIA,\nSUPORTOU POBREZA\nPRA ME DAR VITÓRIA E ME \nDAR RIQUEZA.\nBIS\n\nQUANTA DOR SENTIA LÁ NA CRUZ SOMBRIA\nQUANDO PENDURADO PELO MEU PECADO;\nNESSA CRUZ ERGUIDA,\nOH! QUE AMOR PROFUNDO!\nENTREGOU SUA VIDA\nPRA DAR VIDA AO MUNDO.\nBIS\n\nMEU DESEJO AGORA, PELA VIDA AFORA,\nÉ SEGUIR AO LADO DESSE CRISTO AMADO;\nQUERO, DIA A DIA, MAIS E MAIS AMÁ-LO\nE, COM ALEGRIA, PARA SEMPRE HONRÁ-LO.\nBIS', 'TENHO GRANDE GOZO DESDE QUE REPOUSO NESTE CRISTO AMADO QUE POR MIM FOI DADO; QUE DESCEU DA GLÓRIA, SUPORTOU POBREZA PRA ME DAR VITÓRIA E ME DAR RIQUEZA. QUANTA DOR SENTIA LÁ NA CRUZ SOMBRIA QUANDO PENDURADO PELO MEU PECADO; NESSA CRUZ ERGUIDA, OH! QUE AMOR PROFUNDO! ENTREGOU SUA VIDA PRA DAR VIDA AO MUNDO. MEU DESEJO AGORA, PELA VIDA AFORA, É SEGUIR AO LADO DESSE CRISTO AMADO; QUERO, DIA A DIA, MAIS E MAIS AMÁ-LO E, COM ALEGRIA, PARA SEMPRE HONRÁ-LO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('131', '131 - ÀS VEZES, ALGUÉM ME PERGUNTA', 'ÀS VEZES, ALGUÉM ME PERGUNTA \nPORQUE VIVO ALEGRE E SEMPRE A CANTAR;\nRESPONDO: SOU MUITO FELIZ \nPORQUE JESUS CRISTO \nVEIO EM MIM MORAR.\n\nCORO\nACHEI A FELICIDADE,\nEU NÃO ESPERAVA QUE A FOSSE ENCONTRAR;\nMAS UM DIA ENCONTREI COM JESUS\nE A FELICIDADE VEIO EM MIM MORAR.\n\nÀS VEZES, O MEU CORAÇÃO\nFICA PERTURBADO, PERDENDO A PAZ;\nAOS PÉS DO MEU SALVADOR,\nACHEI O SEGREDO DA PERFEITA PAZ.', 'ÀS VEZES, ALGUÉM ME PERGUNTA PORQUE VIVO ALEGRE E SEMPRE A CANTAR; RESPONDO: SOU MUITO FELIZ PORQUE JESUS CRISTO VEIO EM MIM MORAR. CORO ACHEI A FELICIDADE, EU NÃO ESPERAVA QUE A FOSSE ENCONTRAR; MAS UM DIA ENCONTREI COM JESUS E A FELICIDADE VEIO EM MIM MORAR. ÀS VEZES, O MEU CORAÇÃO FICA PERTURBADO, PERDENDO A PAZ; AOS PÉS DO MEU SALVADOR, ACHEI O SEGREDO DA PERFEITA PAZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('132', '132 - JÁ NÃO ESTOU SOZINHO', 'JÁ NÃO ESTOU SOZINHO, AGORA SOU FELIZ\nCOM CRISTO NO MEU CORAÇÃO;\nELE É MEU GRANDE AMIGO, NÃO TEMO O PERIGO,\nCOM CRISTO NO MEU CORAÇÃO.\n\nCORO\nOH! EU SOU FELIZ, AGORA VOU CANTAR,\nPOIS CONTENTE ESTOU COM MEU SALVADOR,\nFELIZ AGORA ENTÃO EU VIVEREI.\n\nJESUS, HÁ MUITO TEMPO MORREU EM UMA CRUZ,\nMORREU SÓ PARA ME SALVAR;\nSEU SANGUE DERRAMOU,\nLAVANDO OS MEUS PECADOS,\nE DESTA FORMA AGORA SALVO ESTOU.\n\nCORO\nOH! EU SOU FELIZ, AGORA VOU CANTAR,\nPOIS CONTENTE ESTOU COM MEU SALVADOR,\nFELIZ AGORA ENTÃO EU VIVEREI. (BIS)', 'JÁ NÃO ESTOU SOZINHO, AGORA SOU FELIZ COM CRISTO NO MEU CORAÇÃO; ELE É MEU GRANDE AMIGO, NÃO TEMO O PERIGO, COM CRISTO NO MEU CORAÇÃO. CORO OH! EU SOU FELIZ, AGORA VOU CANTAR, POIS CONTENTE ESTOU COM MEU SALVADOR, FELIZ AGORA ENTÃO EU VIVEREI. JESUS, HÁ MUITO TEMPO MORREU EM UMA CRUZ, MORREU SÓ PARA ME SALVAR; SEU SANGUE DERRAMOU, LAVANDO OS MEUS PECADOS, E DESTA FORMA AGORA SALVO ESTOU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('133', '133 - EU ESTAVA SÓ, SEM O SENHOR', 'EU ESTAVA SÓ, SEM O SENHOR,\nSEM PAZ E SEM NENHUM VALOR\nE NEM MESMO QUERIA COMPREENDER\nO TEU GRANDE AMOR; \nERA ASSIM O MEU VIVER.\n\nMAS UM DIA TUA VOZ OUVI,\nTUA PAZ EU PUDE SENTIR,\nO TEU AMOR ME TRANSFORMOU,\nNOVA CRIATURA SOU.\n\nINSTRUMENTOS\n\nMINHA VIDA ESTÁ NO SENHOR,\nPOIS DE TI DEPENDENTE SOU,\nANTES MESMO DE TUDO EXISTIR\nESCOLHESTE A MIM, \nE ASSIM ME ENTREGO A TI.\n\nTUA VOZ NÃO ESTRANHAREI,\nTÃO SUAVE EU A OUVIREI,\nPOIS DE TI SOU E TU ÉS MEU,\nALELUIA! GLÓRIA A DEUS!', 'EU ESTAVA SÓ, SEM O SENHOR, SEM PAZ E SEM NENHUM VALOR E NEM MESMO QUERIA COMPREENDER O TEU GRANDE AMOR; ERA ASSIM O MEU VIVER. MAS UM DIA TUA VOZ OUVI, TUA PAZ EU PUDE SENTIR, O TEU AMOR ME TRANSFORMOU, NOVA CRIATURA SOU. INSTRUMENTOS MINHA VIDA ESTÁ NO SENHOR, POIS DE TI DEPENDENTE SOU, ANTES MESMO DE TUDO EXISTIR ESCOLHESTE A MIM, E ASSIM ME ENTREGO A TI. TUA VOZ NÃO ESTRANHAREI, TÃO SUAVE EU A OUVIREI, POIS DE TI SOU E TU ÉS MEU, ALELUIA! GLÓRIA A DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('134', '134 - QUE SEGURANÇA TENHO EM JESUS', 'QUE SEGURANÇA TENHO EM JESUS,\nHÁ NELE GOZO, PAZ, VIDA E LUZ;\nCOM CRISTO HERDEIRO, DEUS ME ACEITOU\nMEDIANTE O FILHO QUE ME SALVOU.\n\nCORO (2X)\nCONTO ESTA HISTÓRIA \nCANTANDO ASSIM: NA CRUZ \nFOI CRISTO MORTO POR MIM.\n\nINTEIRAMENTE ME SUBMETI,\nPLENA ALEGRIA NELE SENTI;\nANJOS DESCENDO TRAZEM DOS CÉUS\nPROVAS DA GRAÇA QUE VEM DE DEUS.\n\nCORO (2X)\nCONTO ESTA HISTÓRIA \nCANTANDO ASSIM: NA CRUZ \nFOI CRISTO MORTO POR MIM.\n\nFIRMADO EM CRISTO, NO SEU AMOR, \nESTOU CONTENTE EM MEU SALVADOR; \nESPERANÇOSO HEI DE VIVER \nPOR JESUS CRISTO, POR SEU PODER! \n\nCORO (2X)\nCONTO ESTA HISTÓRIA \nCANTANDO ASSIM: NA CRUZ \nFOI CRISTO MORTO POR MIM.', 'QUE SEGURANÇA TENHO EM JESUS, HÁ NELE GOZO, PAZ, VIDA E LUZ; COM CRISTO HERDEIRO, DEUS ME ACEITOU MEDIANTE O FILHO QUE ME SALVOU.  CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM. INTEIRAMENTE ME SUBMETI, PLENA ALEGRIA NELE SENTI; ANJOS DESCENDO TRAZEM DOS CÉUS PROVAS DA GRAÇA QUE VEM DE DEUS.  CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM. FIRMADO EM CRISTO, NO SEU AMOR, ESTOU CONTENTE EM MEU SALVADOR; ESPERANÇOSO HEI DE VIVER POR JESUS CRISTO, POR SEU PODER!  CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('135', '135 - EM MEU CORAÇÃO', 'EM MEU CORAÇÃO REINA UMA DOCE PAZ\nDE ALGUÉM QUE POR MIM MORREU\nPRA ME DAR A SALVAÇÃO.\n\nESSE ALGUÉM, EU SEI, É JESUS, O REI,\nQUE SE FEZ PEQUENO EM MEU LUGAR\nPARA A VIDA ETERNA ME DAR.\n\nINSTRUMENTOS\n\nGLÓRIA E ALELUIA A JESUS CANTAREI\nCOM GRATIDÃO E LOUVOR,\nPARA TODO O SEMPRE, AMÉM!\nBIS', 'EM MEU CORAÇÃO REINA UMA DOCE PAZ DE ALGUÉM QUE POR MIM MORREU PRA ME DAR A SALVAÇÃO. ESSE ALGUÉM, EU SEI, É JESUS, O REI, QUE SE FEZ PEQUENO EM MEU LUGAR PARA A VIDA ETERNA ME DAR. INSTRUMENTOS GLÓRIA E ALELUIA A JESUS CANTAREI COM GRATIDÃO E LOUVOR, PARA TODO O SEMPRE, AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('136', '136 - ESTA PAZ QUE SINTO EM MINH’ALMA', 'ESTA PAZ QUE SINTO EM MINH’ALMA\nNÃO É PORQUE TUDO EM MIM VAI BEM;\nESTA PAZ QUE SINTO EM MINH’ALMA\nÉ PORQUE EU AMO AO MEU SENHOR.\n\nCORO\nNÃO OLHO AS CIRCUNSTÂNCIAS,\n(NÃO, NÃO, NÃO) VARÕES\nSÓ OLHO SEU AMOR \n(SEU GRANDE AMOR) VARÕES\nNÃO ME GUIO POR VISTA,\nALEGRE ESTOU.\nBIS\n\nESTE GOZO QUE SINTO EM MINH’ALMA\nNÃO É PORQUE OLHO AO MEU REDOR;\nESTE GOZO QUE SINTO EM MINH’ALMA\nÉ PORQUE EU AMO AO MEU SENHOR.\n\nCORO\nNÃO OLHO AS CIRCUNSTÂNCIAS,\n(NÃO, NÃO, NÃO) VARÕES\nSÓ OLHO SEU AMOR \n(SEU GRANDE AMOR) VARÕES\nNÃO ME GUIO POR VISTA,\nALEGRE ESTOU.\nBIS\n\nAINDA QUE A TERRA NÃO FLORESÇA,\nE A VIDE NÃO DÊ O SEU FRUTO;\nAINDA QUE OS MONTES SE LANCEM AO MAR, \nE A TERRA TREMA, HEI DE CONFIAR.\n\nCORO\nNÃO OLHO AS CIRCUNSTÂNCIAS,\n(NÃO, NÃO, NÃO) VARÕES\nSÓ OLHO SEU AMOR \n(SEU GRANDE AMOR) VARÕES\nNÃO ME GUIO POR VISTA,\nALEGRE ESTOU.\nBIS\n\nALELUIA, OH! GLÓRIA, ALELUIA,\nALELUIA DOU AO MEU SENHOR. \nALELUIA, OH! GLÓRIA, ALELUIA,\nALELUIA DOU AO MEU SENHOR.\n\nCORO\nNÃO OLHO AS CIRCUNSTÂNCIAS,\n(NÃO, NÃO, NÃO) VARÕES\nSÓ OLHO SEU AMOR \n(SEU GRANDE AMOR) VARÕES\nNÃO ME GUIO POR VISTA,\nALEGRE ESTOU.\nBIS', 'ESTA PAZ QUE SINTO EM MINH’ALMA NÃO É PORQUE TUDO EM MIM VAI BEM; ESTA PAZ QUE SINTO EM MINH’ALMA É PORQUE EU AMO AO MEU SENHOR. CORO NÃO OLHO AS CIRCUNSTÂNCIAS, (NÃO, NÃO, NÃO) VARÕES SÓ OLHO SEU AMOR (SEU GRANDE AMOR) VARÕES NÃO ME GUIO POR VISTA, ALEGRE ESTOU. ESTE GOZO QUE SINTO EM MINH’ALMA NÃO É PORQUE OLHO AO MEU REDOR; ESTE GOZO QUE SINTO EM MINH’ALMA É PORQUE EU AMO AO MEU SENHOR. AINDA QUE A TERRA NÃO FLORESÇA, E A VIDE NÃO DÊ O SEU FRUTO; AINDA QUE OS MONTES SE LANCEM AO MAR, E A TERRA TREMA, HEI DE CONFIAR. ALELUIA, OH! GLÓRIA, ALELUIA, ALELUIA DOU AO MEU SENHOR. ALELUIA, OH! GLÓRIA, ALELUIA, ALELUIA DOU AO MEU SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('137', '137 - QUE DOCE VOZ TEM MEU SENHOR', 'QUE DOCE VOZ TEM MEU SENHOR!\nVOZ DE AMOR, TÃO TERNA E GRACIOSA,\nQUE ENCHE O CORAÇÃO, DÁ CONSOLAÇÃO \nQUE SÓ O CRENTE GOZA.\n\nCORO\nQUAL MAIOR PRAZER QUE LHE OUVIR DIZER: \n“VEM, MEU FILHO, VEM ESCUTAR\nO QUE EU FIZ POR TI, TUDO O QUE SOFRI\nNA CRUZ PRA TE RESGATAR”?\n\nCHAMOU-ME NÃO SÓ UMA VEZ,\nTANTAS TÉ QUE EU TRISTE, HUMILHADO,\nPUDE A VOZ OUVIR, PUDE ENTÃO SAIR\nDAS GARRAS DO PECADO.\n\nCORO\nQUAL MAIOR PRAZER QUE LHE OUVIR DIZER: \n“VEM, MEU FILHO, VEM ESCUTAR\nO QUE EU FIZ POR TI, TUDO O QUE SOFRI\nNA CRUZ PRA TE RESGATAR”?\n\nJESUS NÃO ME DEIXA SOFRER,\nSUA VOZ ME ENSINA O CAMINHO\nDE VENCER O MAL, COM FIRMEZA TAL,\nQUE NUNCA ESTOU SOZINHO.', 'QUE DOCE VOZ TEM MEU SENHOR! VOZ DE AMOR, TÃO TERNA E GRACIOSA, QUE ENCHE O CORAÇÃO, DÁ CONSOLAÇÃO QUE SÓ O CRENTE GOZA. CORO QUAL MAIOR PRAZER QUE LHE OUVIR DIZER: VEM, MEU FILHO, VEM ESCUTAR O QUE EU FIZ POR TI, TUDO O QUE SOFRI NA CRUZ PRA TE RESGATAR? CHAMOU-ME NÃO SÓ UMA VEZ, TANTAS TÉ QUE EU TRISTE, HUMILHADO, PUDE A VOZ OUVIR, PUDE ENTÃO SAIR DAS GARRAS DO PECADO. JESUS NÃO ME DEIXA SOFRER, SUA VOZ ME ENSINA O CAMINHO DE VENCER O MAL, COM FIRMEZA TAL, QUE NUNCA ESTOU SOZINHO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('138', '138 - HÁ SEMPRE ALGUÉM QUE NÃO PROVOU', 'HÁ SEMPRE ALGUÉM QUE NÃO PROVOU\nO GRANDE AMOR DO MEU SENHOR;\nPORÉM EU SEI, BEM CERTO ESTOU,\nQUE ELE ESTÁ SEMPRE AO MEU REDOR\nPRA ME GUIAR.\n\nCORO\nAO MEU REDOR DEUS SEMPRE ESTÁ,\nAO MEU REDOR, AO MEU REDOR, \nPRA ME GUIAR;\nCOM SEU AMOR TÃO PURO E BOM,\nELE ESTÁ SEMPRE \nAO MEU REDOR PRA ME GUIAR.\n\nPOSSO PERDER TODOS OS MEUS BENS\nE UM BOM AMIGO ME FALTAR,\nPODE MEU PAI ME ABANDONAR,\nQUE ELE ESTÁ SEMPRE AO MEU REDOR\nPRA ME GUIAR.\n\nCORO\nAO MEU REDOR DEUS SEMPRE ESTÁ,\nAO MEU REDOR, AO MEU REDOR, \nPRA ME GUIAR;\nCOM SEU AMOR TÃO PURO E BOM,\nELE ESTÁ SEMPRE \nAO MEU REDOR PRA ME GUIAR.\n\nNÃO SEI CONTAR O QUE SENTI\nQUANDO JESUS ME REDIMIU;\nDESDE ENTÃO EU DESCOBRI\nQUE ELE ESTARÁ AO MEU REDOR \nQUANDO EU PARTIR.\n\nCORO\nAO MEU REDOR DEUS SEMPRE ESTÁ,\nAO MEU REDOR, AO MEU REDOR, \nPRA ME GUIAR;\nCOM SEU AMOR TÃO PURO E BOM,\nELE ESTÁ SEMPRE \nAO MEU REDOR PRA ME GUIAR.', 'HÁ SEMPRE ALGUÉM QUE NÃO PROVOU O GRANDE AMOR DO MEU SENHOR; PORÉM EU SEI, BEM CERTO ESTOU, QUE ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR. CORO AO MEU REDOR DEUS SEMPRE ESTÁ, AO MEU REDOR, AO MEU REDOR, PRA ME GUIAR; COM SEU AMOR TÃO PURO E BOM, ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR. POSSO PERDER TODOS OS MEUS BENS E UM BOM AMIGO ME FALTAR, PODE MEU PAI ME ABANDONAR, QUE ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR. NÃO SEI CONTAR O QUE SENTI QUANDO JESUS ME REDIMIU; DESDE ENTÃO EU DESCOBRI QUE ELE ESTARÁ AO MEU REDOR QUANDO EU PARTIR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('139', '139 - NÃO SEI POR QUE DE DEUS O AMOR', 'NÃO SEI POR QUE DE DEUS O AMOR\nA MIM SE REVELOU,\nPORQUE, A MIM, O SALVADOR,\nPRA SI ME RESGATOU.\n\nCORO\nMAS EU SEI EM QUEM TENHO CRIDO\nE ESTOU BEM CERTO QUE É PODEROSO\nPRA GUARDAR O MEU TESOURO\nATÉ O DIA FINAL.\n\nIGNORO COMO O ESPÍRITO\nNOS CONVENCE DO MAL,\nREVELA A CRISTO, VERBO SEU,\nCONSOLADOR REAL.\n\nCORO\nMAS EU SEI EM QUEM TENHO CRIDO\nE ESTOU BEM CERTO QUE É PODEROSO\nPRA GUARDAR O MEU TESOURO\nATÉ O DIA FINAL.\n\nNÃO SEI O QUE DE MAL OU \nBEM É DESTINADO A MIM,\nSE MAUS OU ÁUREOS DIAS \nVÊM, ATÉ DA VIDA O FIM?\n\nCORO\nMAS EU SEI EM QUEM TENHO CRIDO\nE ESTOU BEM CERTO QUE É PODEROSO\nPRA GUARDAR O MEU TESOURO\nATÉ O DIA FINAL.\n\nE QUANDO VEM JESUS, NÃO SEI,\nSE BREVE OU TARDE VEM,\nMAS SEI QUE MEU SENHOR VIRÁ\nNA GLÓRIA QUE ELE TEM.\n\nCORO\nMAS EU SEI EM QUEM TENHO CRIDO\nE ESTOU BEM CERTO QUE É PODEROSO\nPRA GUARDAR O MEU TESOURO\nATÉ O DIA FINAL.', 'NÃO SEI POR QUE DE DEUS O AMOR A MIM SE REVELOU, PORQUE, A MIM, O SALVADOR, PRA SI ME RESGATOU. CORO MAS EU SEI EM QUEM TENHO CRIDO E ESTOU BEM CERTO QUE É PODEROSO PRA GUARDAR O MEU TESOURO ATÉ O DIA FINAL. IGNORO COMO O ESPÍRITO NOS CONVENCE DO MAL, REVELA A CRISTO, VERBO SEU, CONSOLADOR REAL. NÃO SEI O QUE DE MAL OU BEM É DESTINADO A MIM, SE MAUS OU ÁUREOS DIAS VÊM, ATÉ DA VIDA O FIM? E QUANDO VEM JESUS, NÃO SEI, SE BREVE OU TARDE VEM, MAS SEI QUE MEU SENHOR VIRÁ NA GLÓRIA QUE ELE TEM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('140', '140 - AS MUITAS ÁGUAS', 'AS MUITAS ÁGUAS NÃO \nPODEM APAGAR O AMOR,\nO AMOR COM QUE TE AMEI;\nNEM RIOS PROFUNDOS PODEM AFOGAR\nO AMOR COM QUE ME AMOU.\n\nCORO \nCOMO ENTÃO PODERIA ESQUECER\nESSE AMOR QUE DEU VIDA AO MEU SER?\n\nMEU AMOR POR TI NÃO PODE MUDAR,\nA TI ENTREGUEI TODO O MEU SER;\nHOJE VIVES DENTRO DO MEU CORAÇÃO,\nÉS MINHA VIDA, MINHA PORÇÃO.\n\nCORO \nCOMO ENTÃO PODERIA ESQUECER\nESSE AMOR QUE DEU VIDA AO MEU SER?\n\nFINAL: \nEM TUA PRESENÇA EU SOU TÃO FELIZ,\nTEU SACRIFÍCIO ME UNIU A TI;\nSEM O TEU AMOR O QUE SERIA EU?\nÉS MINHA VIDA, MEU JESUS,\nÉS MINHA VIDA, Ó MEU JESUS.', 'AS MUITAS ÁGUAS NÃO PODEM APAGAR O AMOR, O AMOR COM QUE TE AMEI; NEM RIOS PROFUNDOS PODEM AFOGAR O AMOR COM QUE ME AMOU. CORO COMO ENTÃO PODERIA ESQUECER ESSE AMOR QUE DEU VIDA AO MEU SER? MEU AMOR POR TI NÃO PODE MUDAR, A TI ENTREGUEI TODO O MEU SER; HOJE VIVES DENTRO DO MEU CORAÇÃO, ÉS MINHA VIDA, MINHA PORÇÃO. CORO COMO ENTÃO PODERIA ESQUECER ESSE AMOR QUE DEU VIDA AO MEU SER?  EM TUA PRESENÇA EU SOU TÃO FELIZ, TEU SACRIFÍCIO ME UNIU A TI; SEM O TEU AMOR O QUE SERIA EU? ÉS MINHA VIDA, MEU JESUS, ÉS MINHA VIDA, Ó MEU JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('141', '141 - O AMOR DE DEUS POR NÓS', 'O AMOR DE DEUS POR NÓS\nÉ COMO O SOL QUE BRILHA EM SEU PODER;\nEM JESUS MOSTROU TODO \nO SEU AMOR REVELANDO \nASSIM O SEU QUERER.\n\nINSTRUMENTOS\nBIS\n\nQUANDO DEUS TOCOU MEU SER,\nEU LHE ENTREGUEI TODO O MEU CORAÇÃO;\nSUA PAZ SENTI INUNDAR MEU SER\nE O SEU AMOR ME TRANSFORMAR.\nBIS', 'O AMOR DE DEUS POR NÓS É COMO O SOL QUE BRILHA EM SEU PODER; EM JESUS MOSTROU TODO O SEU AMOR REVELANDO ASSIM O SEU QUERER. INSTRUMENTOS QUANDO DEUS TOCOU MEU SER, EU LHE ENTREGUEI TODO O MEU CORAÇÃO; SUA PAZ SENTI INUNDAR MEU SER E O SEU AMOR ME TRANSFORMAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('142', '142 - CRISTO, MEU MESTRE, SEMPRE HÁ DE AMAR-ME', 'CRISTO, MEU MESTRE, SEMPRE HÁ DE AMAR-ME\nE DELE O MAL NÃO PODE AFASTAR-ME;\nDEU SUA VIDA PRA ME SALVAR\nE AGORA DELE SOU.\n\nCORO\nSOU DE JESUS, MEU MESTRE,\nCRISTO JESUS É MEU;\nNÃO SÓ NOS DIAS QUE SE VÃO,\nMAS, PARA SEMPRE, AMÉM.\n\nINSTRUMENTOS\n\nPERDIDO, AFEITO AO MAL, DEGRADADO,\nME CONCEDEU PERDÃO DO PECADO;\nNÃO ANDO TRISTE, JÁ SOU FELIZ\nE AGORA DELE SOU.\n\nCORO\nSOU DE JESUS, MEU MESTRE,\nCRISTO JESUS É MEU;\nNÃO SÓ NOS DIAS QUE SE VÃO,\nMAS, PARA SEMPRE, AMÉM.\n\nINSTRUMENTOS\n\nEU CANTO DE ALEGRIA DE SALVO,\nMEU CORAÇÃO JESUS DEIXOU ALVO,\nPOIS O SEU SANGUE ME PURIFICOU\nE AGORA DELE SOU.', 'CRISTO, MEU MESTRE, SEMPRE HÁ DE AMAR-ME E DELE O MAL NÃO PODE AFASTAR-ME; DEU SUA VIDA PRA ME SALVAR E AGORA DELE SOU. CORO SOU DE JESUS, MEU MESTRE, CRISTO JESUS É MEU; NÃO SÓ NOS DIAS QUE SE VÃO, MAS, PARA SEMPRE, AMÉM. INSTRUMENTOS PERDIDO, AFEITO AO MAL, DEGRADADO, ME CONCEDEU PERDÃO DO PECADO; NÃO ANDO TRISTE, JÁ SOU FELIZ E AGORA DELE SOU. EU CANTO DE ALEGRIA DE SALVO, MEU CORAÇÃO JESUS DEIXOU ALVO, POIS O SEU SANGUE ME PURIFICOU E AGORA DELE SOU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('143', '143 - SE NÃO FOSSE O AMOR', 'SE NÃO FOSSE O AMOR QUE DEUS POR MIM REVELOU,\nEU NÃO ESTARIA AQUI; ELE INCLINOU SEUS OLHOS \nE PÔDE CONTEMPLAR TODO O SOFRIMENTO QUE PASSEI.\nSE NÃO FOSSE O AMOR QUE DEUS POR MIM MOSTROU, JAMAIS CONSEGUIRIA ENTENDER\nO GRANDE SACRIFÍCIO DE JESUS POR MIM E POR TI.\nBIS\n\nA PAZ QUE TENHO EM JESUS\nNÃO SE PODE COMPARAR\nA TESOUROS DESTA VIDA  AQUI,\nÀ PAZ QUE FOI COMPRADA\nCOM SANGUE E COM DOR;\nE HOJE É DADA  A MIM E A TI,\nA MIM E A TI POR CRISTO JESUS.\nBIS', 'SE NÃO FOSSE O AMOR QUE DEUS POR MIM REVELOU, EU NÃO ESTARIA AQUI; ELE INCLINOU SEUS OLHOS E PÔDE CONTEMPLAR TODO O SOFRIMENTO QUE PASSEI. SE NÃO FOSSE O AMOR QUE DEUS POR MIM MOSTROU, JAMAIS CONSEGUIRIA ENTENDER O GRANDE SACRIFÍCIO DE JESUS POR MIM E POR TI. A PAZ QUE TENHO EM JESUS NÃO SE PODE COMPARAR A TESOUROS DESTA VIDA AQUI, À PAZ QUE FOI COMPRADA COM SANGUE E COM DOR; E HOJE É DADA A MIM E A TI, A MIM E A TI POR CRISTO JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('144', '144 - JESUS, AGORA EU BEM SEI', 'JESUS, AGORA EU BEM SEI\nQUÃO GRANDE É TEU AMOR,\nPOIS SALVAÇÃO EM TI ACHEI,\nA CRISTO DOU LOUVOR.\n\nCORO\nÉ CRISTO, SÓ, MEU SALVADOR,\nPOR ELE EU TENHO PAZ;\nJESUS, A TI LOUVOR DAREI,\nPOIS TUDO TU ME DÁS.\n\nDESCANSO NUNCA CONHECI,\nINÚTIL SEMPRE SOU;\nMAS CRISTO SE LEMBROU DE MIM,\nSUA GRAÇA ME CHAMOU.\n\nCORO\nÉ CRISTO, SÓ, MEU SALVADOR,\nPOR ELE EU TENHO PAZ;\nJESUS, A TI LOUVOR DAREI,\nPOIS TUDO TU ME DÁS.\n\nCOMIGO, CRENTES, EXALTAI\nO GRANDE SALVADOR,\nPOIS TUDO CRISTO ME SUPRIU\nEMBORA PECADOR.\n\nCORO\nÉ CRISTO, SÓ, MEU SALVADOR,\nPOR ELE EU TENHO PAZ;\nJESUS, A TI LOUVOR DAREI,\nPOIS TUDO TU ME DÁS.\n\nLOUVOR, LOUVOR A TI DAREI,\nÓ CRISTO, MEU SENHOR!\nPROFETA, SACERDOTE, REI,\nDO MUNDO, O SALVADOR.\n\nCORO\nÉ CRISTO, SÓ, MEU SALVADOR,\nPOR ELE EU TENHO PAZ;\nJESUS, A TI LOUVOR DAREI,\nPOIS TUDO TU ME DÁS.', 'JESUS, AGORA EU BEM SEI QUÃO GRANDE É TEU AMOR, POIS SALVAÇÃO EM TI ACHEI, A CRISTO DOU LOUVOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS. DESCANSO NUNCA CONHECI, INÚTIL SEMPRE SOU; MAS CRISTO SE LEMBROU DE MIM, SUA GRAÇA ME CHAMOU. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS. COMIGO, CRENTES, EXALTAI O GRANDE SALVADOR, POIS TUDO CRISTO ME SUPRIU EMBORA PECADOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS. LOUVOR, LOUVOR A TI DAREI, Ó CRISTO, MEU SENHOR! PROFETA, SACERDOTE, REI, DO MUNDO, O SALVADOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('145', '145 - É O AMOR DE DEUS', 'É O AMOR DE DEUS A CAUSA \nDE NÃO SERMOS CONSUMIDOS\nPOIS AINDA QUE FAÇAMOS PRA MERECER,\nNUNCA MERECEMOS ESSE AMOR\nQUE É LINDO E PURO, \nÉ O AMOR QUE DEUS \nTE OFERECE AGORA.\nBIS\n\nÉ O AMOR DE DEUS QUE TE LIVRA\nDE TODA CILADA E INVESTIDA,\nESTÁ SEMPRE AO NOSSO LADO\nPRA CUIDAR DE NÓS EMBORA \nNÃO MEREÇAMOS ESSE AMOR.\nINSTRUMENTOS\n\nELE NOS GUARDA EM TODOS \nOS MOMENTOS DE NOSSAS VIDAS\nE ÀS VEZES SOMOS INGRATOS,\nMAS ELE NOS AMA \nE É POR CAUSA DESSE AMOR \nQUE NÃO SOMOS CONSUMIDOS.\nBIS', 'É O AMOR DE DEUS A CAUSA DE NÃO SERMOS CONSUMIDOS POIS AINDA QUE FAÇAMOS PRA MERECER, NUNCA MERECEMOS ESSE AMOR QUE É LINDO E PURO, É O AMOR QUE DEUS TE OFERECE AGORA. É O AMOR DE DEUS QUE TE LIVRA DE TODA CILADA E INVESTIDA, ESTÁ SEMPRE AO NOSSO LADO PRA CUIDAR DE NÓS EMBORA NÃO MEREÇAMOS ESSE AMOR. INSTRUMENTOS ELE NOS GUARDA EM TODOS OS MOMENTOS DE NOSSAS VIDAS E ÀS VEZES SOMOS INGRATOS, MAS ELE NOS AMA E É POR CAUSA DESSE AMOR QUE NÃO SOMOS CONSUMIDOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('146', '146 - EXISTE UM ALGUÉM QUE CUIDA DE MIM', 'EXISTE UM ALGUÉM QUE CUIDA DE MIM, \nTAMBÉM DE VOCÊ; SEU NOME É DEUS FORTE, \nREAL CONSELHEIRO, ELE É O PODER;\nELE É FIEL, EMANUEL, UM BRILHO \nDE LUZ, TRANSMITE A PAZ, \nTAMBÉM SATISFAZ, SEU NOME É JESUS.\n\nJESUS É A VIDA,\nA FLOR MAIS QUERIDA;\nNA HORA DA DOR,\nJESUS É AMOR,\nA FONTE DA VIDA.\nBIS\n\nEXISTE UM ALGUÉM QUE CUIDA DE \nMIM, TAMBÉM DE VOCÊ;\nSEU NOME É DEUS FORTE, \nREAL CONSELHEIRO, ELE É O PODER;\nELE É FIEL, EMANUEL, UM BRILHO \nDE LUZ, TRANSMITE A PAZ, \nTAMBÉM SATISFAZ, \nSEU NOME É JESUS.\n\nJESUS É A VIDA,\nA FLOR MAIS QUERIDA;\nNA HORA DA DOR,\nJESUS É AMOR,\nA FONTE DA VIDA.\n\nFINAL: NA HORA DA DOR,\n           JESUS É AMOR,\n           A FONTE DA VIDA.\nBIS', 'EXISTE UM ALGUÉM QUE CUIDA DE MIM, TAMBÉM DE VOCÊ; SEU NOME É DEUS FORTE, REAL CONSELHEIRO, ELE É O PODER; ELE É FIEL, EMANUEL, UM BRILHO DE LUZ, TRANSMITE A PAZ, TAMBÉM SATISFAZ, SEU NOME É JESUS. JESUS É A VIDA, A FLOR MAIS QUERIDA; NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA. JESUS É A VIDA, A FLOR MAIS QUERIDA; NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA.  NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('147', '147 - CRISTO É TUDO PARA MIM', 'CORO\nCRISTO É TUDO PARA MIM,\nAMOR E PAZ, PERDÃO SEM FIM;\nSEM ELE A VIDA É TÃO RUIM,\nCRISTO JESUS É TUDO PARA MIM.\n\nCEDINHO SINTO O SEU AMOR,\nDE NOITE SINTO TAMBÉM;\nCOM CRISTO ANDO SEM TEMOR,\nAS TREVAS NUNCA ME VÊM.\n\nCORO\nCRISTO É TUDO PARA MIM,\nAMOR E PAZ, PERDÃO SEM FIM;\nSEM ELE A VIDA É TÃO RUIM,\nCRISTO JESUS É TUDO PARA MIM.\n\nO MUNDO NÃO ME ATRAI,\nJESUS, TEU SER, JÁ ME ATRAIU;\nSEGURANÇA TENHO NO PAI,\nVIDA NESTE MUNDO VIL.\n\nCORO\nCRISTO É TUDO PARA MIM,\nAMOR E PAZ, PERDÃO SEM FIM;\nSEM ELE A VIDA É TÃO RUIM,\nCRISTO JESUS É TUDO PARA MIM.', 'CORO CRISTO É TUDO PARA MIM, AMOR E PAZ, PERDÃO SEM FIM; SEM ELE A VIDA É TÃO RUIM, CRISTO JESUS É TUDO PARA MIM. CEDINHO SINTO O SEU AMOR, DE NOITE SINTO TAMBÉM; COM CRISTO ANDO SEM TEMOR, AS TREVAS NUNCA ME VÊM. O MUNDO NÃO ME ATRAI, JESUS, TEU SER, JÁ ME ATRAIU; SEGURANÇA TENHO NO PAI, VIDA NESTE MUNDO VIL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('148', '148 - UMA CERTA VEZ, JESUS CRISTO', 'UMA CERTA VEZ, JESUS CRISTO, \nPARA UMA GRANDE MULTIDÃO \nNA PRAIA DA GALILÉIA, \nFEZ UMA MULTIPLICAÇÃO:\nCOM DOIS PEIXES E CINCO PÃES,\nTODOS JULGAVAM QUE NÃO;\nA MAIS DE CINCO MIL PESSOAS \nDEU COMIDA BOA, \nDEU PEIXE E DEU PÃO.\n\nCORO\nESTE QUE É DEUS, DEUS PODEROSO,\nDO CELESTE LAR; ESTE QUE É DEUS, \nQUE VENDO O POVO TODO A PECAR MANDOU SEU FILHO PARA QUE O MUNDO VIESSE SALVAR, ESTE DEUS MARAVILHOSO\nDÁ PAZ E DÁ GOZO A QUEM O ACEITAR.\n\nJESUS ORDENOU AO POVO \nQUE SE ASSENTASSE NO CHÃO,\nE RECEBENDO UMA CESTA \nCOM DOIS PEIXES E CINCO PÃES, \nDEU GRAÇAS AO PAI DIVINO;\nDISSE: “TENDE COMPAIXÃO”.\nOS DISCÍPULOS, NAQUELE MOMENTO,\nTIVERAM ALIMENTO PARA A MULTIDÃO.\n\nCORO\nESTE QUE É DEUS, DEUS PODEROSO,\nDO CELESTE LAR; ESTE QUE É DEUS, \nQUE VENDO O POVO TODO A PECAR MANDOU SEU FILHO PARA QUE O MUNDO VIESSE SALVAR, ESTE DEUS MARAVILHOSO\nDÁ PAZ E DÁ GOZO A QUEM O ACEITAR.\n\nPARA QUE NADA PERDESSE,\nMANDOU AS SOBRAS JUNTAR:\nDOZE CESTOS DE PEDAÇOS\nFOI O QUE VEIO A SOBRAR;\nJESUS AINDA É O MESMO,\nOH! QUE ALEGRIA SEM PAR;\nESTE JESUS PODEROSO,\nLÁ DO CÉU DE GOZO\nESTÁ A NOS CONTEMPLAR.\n\nCORO\nESTE QUE É DEUS, DEUS PODEROSO,\nDO CELESTE LAR; ESTE QUE É DEUS, \nQUE VENDO O POVO TODO A PECAR MANDOU SEU FILHO PARA QUE O MUNDO VIESSE SALVAR, ESTE DEUS MARAVILHOSO\nDÁ PAZ E DÁ GOZO A QUEM O ACEITAR.', 'UMA CERTA VEZ, JESUS CRISTO, PARA UMA GRANDE MULTIDÃO NA PRAIA DA GALILÉIA, FEZ UMA MULTIPLICAÇÃO: COM DOIS PEIXES E CINCO PÃES, TODOS JULGAVAM QUE NÃO; A MAIS DE CINCO MIL PESSOAS DEU COMIDA BOA, DEU PEIXE E DEU PÃO. CORO ESTE QUE É DEUS, DEUS PODEROSO, DO CELESTE LAR; ESTE QUE É DEUS, QUE VENDO O POVO TODO A PECAR MANDOU SEU FILHO PARA QUE O MUNDO VIESSE SALVAR, ESTE DEUS MARAVILHOSO DÁ PAZ E DÁ GOZO A QUEM O ACEITAR. JESUS ORDENOU AO POVO QUE SE ASSENTASSE NO CHÃO, E RECEBENDO UMA CESTA COM DOIS PEIXES E CINCO PÃES, DEU GRAÇAS AO PAI DIVINO; DISSE: TENDE COMPAIXÃO. OS DISCÍPULOS, NAQUELE MOMENTO, TIVERAM ALIMENTO PARA A MULTIDÃO. PARA QUE NADA PERDESSE, MANDOU AS SOBRAS JUNTAR: DOZE CESTOS DE PEDAÇOS FOI O QUE VEIO A SOBRAR; JESUS AINDA É O MESMO, OH! QUE ALEGRIA SEM PAR; ESTE JESUS PODEROSO, LÁ DO CÉU DE GOZO ESTÁ A NOS CONTEMPLAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('149', '149 - JESUS FEZ A PROMESSA', 'JESUS FEZ A PROMESSA,\nCONOSCO ESTARIA ATÉ O FIM;\nE OS SINAIS SEGUIRIAM A TODOS \nQUE CRESSEM, ELE FALOU ASSIM.\nCAIAM OS MONTES E OS HOMENS,\nMAS SUA PROMESSA ESTÁ DE PÉ,\nELE NOS TEM PROVADO \nQUE NÃO MUDOU, \nPODEROSO ELE É!\n\nCORO\nPODEROSO ELE É\nE EM NADA MUDOU;\nNELE ESTÁ NOSSA FÉ,\nPODEROSO ELE É!\n\nELE DEU VISTA AOS CEGOS \nE OS OPRIMIDOS ELE LIBERTOU;\nCINCO PÃES E DOIS PEIXES, A MAIS \nDE DOIS MIL ELE ALIMENTOU.\nOPEROU NA JUDÉIA, \nEM CAFARNAUM E EM NAZARÉ;\nCONTINUA OPERANDO AQUI EM \nNOSSO MEIO, PODEROSO ELE É!\n\nCORO (2X)\nPODEROSO ELE É\nE EM NADA MUDOU;\nNELE ESTÁ NOSSA FÉ,\nPODEROSO ELE É!', 'JESUS FEZ A PROMESSA, CONOSCO ESTARIA ATÉ O FIM; E OS SINAIS SEGUIRIAM A TODOS QUE CRESSEM, ELE FALOU ASSIM. CAIAM OS MONTES E OS HOMENS, MAS SUA PROMESSA ESTÁ DE PÉ, ELE NOS TEM PROVADO QUE NÃO MUDOU, PODEROSO ELE É! CORO PODEROSO ELE É E EM NADA MUDOU; NELE ESTÁ NOSSA FÉ, PODEROSO ELE É! ELE DEU VISTA AOS CEGOS E OS OPRIMIDOS ELE LIBERTOU; CINCO PÃES E DOIS PEIXES, A MAIS DE DOIS MIL ELE ALIMENTOU. OPEROU NA JUDÉIA, EM CAFARNAUM E EM NAZARÉ; CONTINUA OPERANDO AQUI EM NOSSO MEIO, PODEROSO ELE É! PODEROSO ELE É E EM NADA MUDOU; NELE ESTÁ NOSSA FÉ, PODEROSO ELE É!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('150', '150 - VOU CONTAR-VOS O QUE PENSO DO MEU MESTRE', 'VOU CONTAR-VOS O QUE PENSO DO MEU MESTRE,\nCOMO DELE RECEBI A LUZ E A PAZ;\nELE MUDOU-ME, EU BEM SEI, COMPLETAMENTE, \nCOMO CRISTO NENHUM OUTRO SATISFAZ.\n\nCORO\nSEMPRE CUIDARÁ DE MIM, MEU MESTRE,\nCOM DESVELO E COMPAIXÃO SEM FIM;\nNENHUM OUTRO TIRA A CULPA DO PECADO. \nOH! COMO ELE AMA A MIM!\n\nCOM A VIDA TODA CHEIA DE PECADO,\nNA MISÉRIA E COM DOR NO CORAÇÃO,\nELE ERGUEU-ME COM BRAÇOS FORTES DE TERNURA, \nDEU-ME GOZO, DEU-ME PAZ, CONSOLAÇÃO.\n\nCORO\nSEMPRE CUIDARÁ DE MIM, MEU MESTRE,\nCOM DESVELO E COMPAIXÃO SEM FIM;\nNENHUM OUTRO TIRA A CULPA DO PECADO. \nOH! COMO ELE AMA A MIM!\n\nCADA DIA ELE VEM COM SEGURANÇA,\nMAIS E MAIS A REVELAR O SEU AMOR;\nCOMO ANELO COMPREENDER COMPLETAMENTE \nESTE AMOR TÃO DIVINAL, RESTAURADOR!\n\nCORO\nSEMPRE CUIDARÁ DE MIM, MEU MESTRE,\nCOM DESVELO E COMPAIXÃO SEM FIM;\nNENHUM OUTRO TIRA A CULPA DO PECADO. \nOH! COMO ELE AMA A MIM!\n\nMEU MAIOR DESEJO AGORA É AMÁ-LO,\nPROCLAMAR O QUE ELE FEZ PRA ME SALVAR;\nE CANTANDO ESSE AMOR INIGUALÁVEL,\nESPERO EM DEUS A MINHA VIDA TERMINAR.', 'VOU CONTAR-VOS O QUE PENSO DO MEU MESTRE, COMO DELE RECEBI A LUZ E A PAZ; ELE MUDOU-ME, EU BEM SEI, COMPLETAMENTE, COMO CRISTO NENHUM OUTRO SATISFAZ. CORO SEMPRE CUIDARÁ DE MIM, MEU MESTRE, COM DESVELO E COMPAIXÃO SEM FIM; NENHUM OUTRO TIRA A CULPA DO PECADO. OH! COMO ELE AMA A MIM! COM A VIDA TODA CHEIA DE PECADO, NA MISÉRIA E COM DOR NO CORAÇÃO, ELE ERGUEU-ME COM BRAÇOS FORTES DE TERNURA, DEU-ME GOZO, DEU-ME PAZ, CONSOLAÇÃO. CADA DIA ELE VEM COM SEGURANÇA, MAIS E MAIS A REVELAR O SEU AMOR; COMO ANELO COMPREENDER COMPLETAMENTE ESTE AMOR TÃO DIVINAL, RESTAURADOR! MEU MAIOR DESEJO AGORA É AMÁ-LO, PROCLAMAR O QUE ELE FEZ PRA ME SALVAR; E CANTANDO ESSE AMOR INIGUALÁVEL, ESPERO EM DEUS A MINHA VIDA TERMINAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('151', '151 - CRISTO JESUS É O MEU BOM SALVADOR', 'CRISTO JESUS É O MEU BOM SALVADOR;\nDESEJO AMÁ-LO MAIS,\nTODOS OS DIAS É MEU SENHOR.\n\nCORO\nA CRISTO EU AMO, OH! QUANTO \nEU AMO, POIS POR MIM MORREU;\nCOM O SEU SANGUE, JESUS SALVOU-ME,\nEM GRATIDÃO SOU SEU.\n\nINSTRUMENTOS\n\nBEM CEDO AO CÉU ELE ME LEVARÁ,\nDESEJO AMÁ-LO MAIS,\nETERNAMENTE ME GUIARÁ.', 'CRISTO JESUS É O MEU BOM SALVADOR; DESEJO AMÁ-LO MAIS, TODOS OS DIAS É MEU SENHOR. CORO A CRISTO EU AMO, OH! QUANTO EU AMO, POIS POR MIM MORREU; COM O SEU SANGUE, JESUS SALVOU-ME, EM GRATIDÃO SOU SEU. INSTRUMENTOS BEM CEDO AO CÉU ELE ME LEVARÁ, DESEJO AMÁ-LO MAIS, ETERNAMENTE ME GUIARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('152', '152 - MAIS PERTO DE JESUS', 'MAIS PERTO DE JESUS PROCURO SEMPRE EU CHEGAR, \nMAIS BELO DO QUE O OURO DO SOL NADO É A TI MIRAR;\nEM PENSAMENTOS, SONHOS, TANTA GLÓRIA NUNCA VI!\nPOIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI!\n\nCORO\nNÃO POSSO EXPLICAR QUÃO MEIGO \nÉS JESUS; MAS FACE A FACE NO TEU LAR,\nEU TE VEREI, JESUS!\n\nA ESTRELA RESPLENDENTE, DA MANHÃ, \nÉ MINHA LUZ, O LÍRIO DOS VALES \nÉ O BOM SENHOR JESUS;\nSUAVE E DOCE É O CHEIRO \nQUE SÓ VEM DE SI, \nPOIS ELE É MAIS BELO \nDO QUE EU JAMAIS PREVI!\n\nCORO\nNÃO POSSO EXPLICAR QUÃO MEIGO \nÉS JESUS; MAS FACE A FACE NO TEU LAR,\nEU TE VEREI, JESUS!\n\nSE MÁGOAS VÊM ME PERTURBAR, \nO BÁLSAMO ELE TEM, \nME TOMA NOS SEUS BRAÇOS \nE ASSIM DESCANSO BEM;\nNA CRUZ LEVOU JESUS \nO MEU PECADO SOBRE SI, \nPOIS ELE É MAIS BELO \nDO QUE EU JAMAIS PREVI!\n\nCORO\nNÃO POSSO EXPLICAR QUÃO MEIGO \nÉS JESUS; MAS FACE A FACE NO TEU LAR,\nEU TE VEREI, JESUS!', 'MAIS PERTO DE JESUS PROCURO SEMPRE EU CHEGAR, MAIS BELO DO QUE O OURO DO SOL NADO É A TI MIRAR; EM PENSAMENTOS, SONHOS, TANTA GLÓRIA NUNCA VI! POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI! CORO NÃO POSSO EXPLICAR QUÃO MEIGO ÉS JESUS; MAS FACE A FACE NO TEU LAR, EU TE VEREI, JESUS! A ESTRELA RESPLENDENTE, DA MANHÃ, É MINHA LUZ, O LÍRIO DOS VALES É O BOM SENHOR JESUS; SUAVE E DOCE É O CHEIRO QUE SÓ VEM DE SI, POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI! SE MÁGOAS VÊM ME PERTURBAR, O BÁLSAMO ELE TEM, ME TOMA NOS SEUS BRAÇOS E ASSIM DESCANSO BEM; NA CRUZ LEVOU JESUS O MEU PECADO SOBRE SI, POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('153', '153 - CRISTO, NOME MAIS ALTO', 'CRISTO, NOME MAIS ALTO,\nSALVADOR NOSSO, GLORIOSO SENHOR;\nEMANUEL É DEUS CONOSCO,\nNOME DE VIDA, DIVINO SENHOR.\n\nEMANUEL, EMANUEL,\nSEU NOME É EMANUEL!\nELE ME AMOU,\nME TRANSFORMOU\nE VIVE EM MIM, EMANUEL.\nBIS', 'CRISTO, NOME MAIS ALTO, SALVADOR NOSSO, GLORIOSO SENHOR; EMANUEL É DEUS CONOSCO, NOME DE VIDA, DIVINO SENHOR. EMANUEL, EMANUEL, SEU NOME É EMANUEL! ELE ME AMOU, ME TRANSFORMOU E VIVE EM MIM, EMANUEL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('154', '154 - Ó TU QUE HABITAS ENTRE OS QUERUBINS', 'Ó TU QUE HABITAS ENTRE OS QUERUBINS,\nRESPLANDECE TUA LUZ EM MIM;\nÓ DEUS EXCELSO, ETERNO CRIADOR,\nVEM TU, MEU DEUS, ME ENCHER DE AMOR.\n\nCORO\nÓ DEUS DE PAZ, Ó DEUS DE AMOR,\n(Ó DEUS DE AMOR) SERVAS\nÉS TU MEU DEUS, MEU SALVADOR;\nA MINHA VIDA ENTREGO A TI,\n(ENTREGO A TI) SERVAS\nDEIXA O ESPÍRITO EM MIM FLUIR.\n\nÓ TU QUE HABITAS NO MAIS ALTO CÉU,\nNO CALVÁRIO TU RASGASTE O VÉU\nDANDO-ME PAZ E COMUNHÃO,\nA TI ENTREGO MEU CORAÇÃO.\n\nCORO\nÓ DEUS DE PAZ, Ó DEUS DE AMOR,\n(Ó DEUS DE AMOR) SERVAS\nÉS TU MEU DEUS, MEU SALVADOR;\nA MINHA VIDA ENTREGO A TI,\n(ENTREGO A TI) SERVAS\nDEIXA O ESPÍRITO EM MIM FLUIR.\n\nÓ DEUS ETERNO, COMO EU TE AMO,\nPOIS TU MUDASTE A MINHA SORTE,\nPOR ISSO EU DIGO QUE ÉS SOBERANO\nPOIS ME AJUDASTE A VENCER A MORTE.\n\nCORO\nÓ DEUS DE PAZ, Ó DEUS DE AMOR,\n(Ó DEUS DE AMOR) SERVAS\nÉS TU MEU DEUS, MEU SALVADOR;\nA MINHA VIDA ENTREGO A TI,\n(ENTREGO A TI) SERVAS\nDEIXA O ESPÍRITO EM MIM FLUIR.', 'Ó TU QUE HABITAS ENTRE OS QUERUBINS, RESPLANDECE TUA LUZ EM MIM; Ó DEUS EXCELSO, ETERNO CRIADOR, VEM TU, MEU DEUS, ME ENCHER DE AMOR. CORO Ó DEUS DE PAZ, Ó DEUS DE AMOR, (Ó DEUS DE AMOR) SERVAS ÉS TU MEU DEUS, MEU SALVADOR; A MINHA VIDA ENTREGO A TI, (ENTREGO A TI) SERVAS DEIXA O ESPÍRITO EM MIM FLUIR. Ó TU QUE HABITAS NO MAIS ALTO CÉU, NO CALVÁRIO TU RASGASTE O VÉU DANDO-ME PAZ E COMUNHÃO, A TI ENTREGO MEU CORAÇÃO. Ó DEUS ETERNO, COMO EU TE AMO, POIS TU MUDASTE A MINHA SORTE, POR ISSO EU DIGO QUE ÉS SOBERANO POIS ME AJUDASTE A VENCER A MORTE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('155', '155 - HÁ UM SER QUE VIVE EM MIM', 'HÁ UM SER QUE VIVE EM MIM\nE SEMPRE ESTÁ COMIGO, SIM,\nPROPORCIONANDO PAZ E AMOR,\nALIVIANDO TODA A MINHA DOR.\n\nUM DIA MORREU PARA ME SALVAR,\nRESSUSCITOU E VAI VOLTAR,\nUM DIA ENTÃO VAI ME LEVAR\nPARA COM ELE ETERNAMENTE MORAR.\n\nO NOME DELE É SIMPLES\nE POR ISSO É SUBLIME,\nÉ O FILHO DE DEUS,\nELE É JESUS, ELE É JESUS (BIS)', 'HÁ UM SER QUE VIVE EM MIM E SEMPRE ESTÁ COMIGO, SIM, PROPORCIONANDO PAZ E AMOR, ALIVIANDO TODA A MINHA DOR. UM DIA MORREU PARA ME SALVAR, RESSUSCITOU E VAI VOLTAR, UM DIA ENTÃO VAI ME LEVAR PARA COM ELE ETERNAMENTE MORAR. O NOME DELE É SIMPLES E POR ISSO É SUBLIME, É O FILHO DE DEUS, ELE É JESUS, ELE É JESUS', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('156', '156 - TOTALMENTE DESEJÁVEL É MEU AMADO', 'TOTALMENTE DESEJÁVEL É O MEU AMADO,\nPRECIOSO PARA MIM ELE É;\nO SEU FALAR É MUITÍSSIMO SUAVE,\nASSIM ELE É, JESUS, O AMADO SALVADOR.\n\nTODO O SEU SER É DELEITÁVEL \nÀ MINH’ALMA, DELE QUERO \nSEMPRE MAIS CONHECER;\nNÃO HÁ PRAZER MAIS REAL\nQUE SUA PRESENÇA,\nTOTALMENTE DESEJÁVEL É JESUS.\nBIS', 'TOTALMENTE DESEJÁVEL É O MEU AMADO, PRECIOSO PARA MIM ELE É; O SEU FALAR É MUITÍSSIMO SUAVE, ASSIM ELE É, JESUS, O AMADO SALVADOR. TODO O SEU SER É DELEITÁVEL À MINH’ALMA, DELE QUERO SEMPRE MAIS CONHECER; NÃO HÁ PRAZER MAIS REAL QUE SUA PRESENÇA, TOTALMENTE DESEJÁVEL É JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('157', '157 - CRISTO É A ROSA MAIS BELA', 'CRISTO É A ROSA MAIS BELA,\nO MAIS PURO LÍRIO DO VALE,\nCRISTO É A BRILHANTE ESTRELA\nQUE ANUNCIA A LUZ DA MANHÃ.\n\nCRISTO É A ROCHA FERIDA\nBROTANDO ÁGUAS DE SALVAÇÃO,\nCRISTO É A FONTE DA VIDA,\nDANDO A PAZ, A CURA E O PERDÃO.\n\nCRISTO É SUAVE ALEGRIA,\nDOCE AMOR ENCHENDO O MEU SER;\nCRISTO É O AMADO QUE UM DIA\nVEM NAS ASAS DO AMANHECER.\nBIS', 'CRISTO É A ROSA MAIS BELA, O MAIS PURO LÍRIO DO VALE, CRISTO É A BRILHANTE ESTRELA QUE ANUNCIA A LUZ DA MANHÃ. CRISTO É A ROCHA FERIDA BROTANDO ÁGUAS DE SALVAÇÃO, CRISTO É A FONTE DA VIDA, DANDO A PAZ, A CURA E O PERDÃO. CRISTO É SUAVE ALEGRIA, DOCE AMOR ENCHENDO O MEU SER; CRISTO É O AMADO QUE UM DIA VEM NAS ASAS DO AMANHECER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('158', '158 - JESUS, O LÍRIO DOS VALES', 'JESUS, O LÍRIO DOS VALES,\nJESUS, O GRANDE AMOR,\nJESUS, O AMOR SUBLIME,\nJESUS, O NOSSO SENHOR.\n\nELE É A PUREZA,\nÉ O AMOR MAIOR,\nÉ VIDA EM ABUNDÂNCIA,\nÉ A PAZ SEM FIM.\n\nJESUS, A ESTRELA DA MANHÃ,\nA ROSA PERFUMADA,\nUM RIO QUE TRANSBORDA\nDE AMOR INFINITO.', 'JESUS, O LÍRIO DOS VALES, JESUS, O GRANDE AMOR, JESUS, O AMOR SUBLIME, JESUS, O NOSSO SENHOR. ELE É A PUREZA, É O AMOR MAIOR, É VIDA EM ABUNDÂNCIA, É A PAZ SEM FIM. JESUS, A ESTRELA DA MANHÃ, A ROSA PERFUMADA, UM RIO QUE TRANSBORDA DE AMOR INFINITO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('159', '159 - ELE É A ROSA DE SAROM', 'ELE É A ROSA DE SAROM,\nSEU FRUTO É DOCE AO PALADAR;\nELE É O LÍRIO DOS VALES,\nÀ SUA SOMBRA QUERO DESCANSAR.\nLEVOU-ME À SALA DO BANQUETE,\nSEU ESTANDARTE ERA EM MIM O AMOR;\nÓ SUSTENTA-ME, CONFORTA-ME\nE ME AMPARE TUA MÃO, SENHOR.\n\nESTA É A VOZ DO AMADO MEU,\nQUE ME ORDENA A LEVANTAR,\nEI-LO QUE VEM POR SOBRE OS MONTES,\nELE TEM PRESSA DE AQUI CHEGAR;\nSURGEM AS FLORES SOBRE A TERRA,\nCHEGOU O TEMPO AGORA DE CANTAR;\nO INVERNO PASSA, A CHUVA CESSOU \nE A VOZ DA ROLA POSSO ESCUTAR.\nBIS', 'ELE É A ROSA DE SAROM, SEU FRUTO É DOCE AO PALADAR; ELE É O LÍRIO DOS VALES, À SUA SOMBRA QUERO DESCANSAR. LEVOU-ME À SALA DO BANQUETE, SEU ESTANDARTE ERA EM MIM O AMOR; Ó SUSTENTA-ME, CONFORTA-ME E ME AMPARE TUA MÃO, SENHOR. ESTA É A VOZ DO AMADO MEU, QUE ME ORDENA A LEVANTAR, EI-LO QUE VEM POR SOBRE OS MONTES, ELE TEM PRESSA DE AQUI CHEGAR; SURGEM AS FLORES SOBRE A TERRA, CHEGOU O TEMPO AGORA DE CANTAR; O INVERNO PASSA, A CHUVA CESSOU E A VOZ DA ROLA POSSO ESCUTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('160', '160 - EU SOU DO MEU AMADO E ELE ME TEM AFEIÇÃO', 'EU SOU DO MEU AMADO\nE ELE ME TEM AFEIÇÃO.\nVEM, Ó MEU AMADO, SAIAMOS AO CAMPO,\nPASSEMOS AS NOITES NAS ALDEIAS,\nLEVANTEMO-NOS DE MANHÃ PARA IR ÀS VINHAS, \nVEJAMOS SE FLORESCEM AS VIDES,\nSE SE ABRE A FLOR, SE JÁ BROTAM \nAS ROMEIRAS, ALI TE DAREI \nO MEU GRANDE AMOR.\nBIS\n\nAS MANDRÁGORAS DÃO CHEIRO\nE ÀS NOSSAS PORTAS HÁ TODA SORTE \nDE EXCELENTES FRUTOS\nNOVOS E VELHOS, NOVOS E VELHOS,\nÓ AMADO MEU, \nEU OS GUARDEI PARA TI.\nBIS\nBIS', 'EU SOU DO MEU AMADO E ELE ME TEM AFEIÇÃO. VEM, Ó MEU AMADO, SAIAMOS AO CAMPO, PASSEMOS AS NOITES NAS ALDEIAS, LEVANTEMO-NOS DE MANHÃ PARA IR ÀS VINHAS, VEJAMOS SE FLORESCEM AS VIDES, SE SE ABRE A FLOR, SE JÁ BROTAM AS ROMEIRAS, ALI TE DAREI O MEU GRANDE AMOR. AS MANDRÁGORAS DÃO CHEIRO E ÀS NOSSAS PORTAS HÁ TODA SORTE DE EXCELENTES FRUTOS NOVOS E VELHOS, NOVOS E VELHOS, Ó AMADO MEU, EU OS GUARDEI PARA TI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('161', '161 - ERA UMA OLIVEIRA', 'ERA UMA OLIVEIRA COM OS RAMOS QUEBRADOS,\nE UM ZAMBUJEIRO ALI FOI ENXERTADO;\nESSA OLIVEIRA É O MEU JESUS,\nFUI NELE ENXERTADO, HOJE TENHO LUZ.\n\nCORO\nOLIVEIRA VERDADEIRA, JESUS CRISTO É,\nNELE FUI ENXERTADO ATRAVÉS DA FÉ,\nPOSSO EU DAR FRUTO ENXERTADO ASSIM;\nCRISTO É OLIVEIRA, NÃO MAIS VIVO EU\nMAS CRISTO VIVE EM MIM.\n\nZAMBUJEIRO BRAVO NÃO DÁ FRUTO BOM,\nASSIM ERA EU NO MEU CORAÇÃO,\nMAS NUM BELO DIA TUDO TRANSFORMOU,\nEU FUI ENXERTADO NO MEU SALVADOR.\n\nCORO\nOLIVEIRA VERDADEIRA, JESUS CRISTO É,\nNELE FUI ENXERTADO ATRAVÉS DA FÉ,\nPOSSO EU DAR FRUTO ENXERTADO ASSIM;\nCRISTO É OLIVEIRA, NÃO MAIS VIVO EU\nMAS CRISTO VIVE EM MIM.\n\nCRISTO É OLIVEIRA, NELE SOU FELIZ,\nNELE ESTOU LIGADO NO TRONCO E NA RAIZ;\nFIRMADO NA OLIVEIRA DE TODO O CORAÇÃO, \nPOSSO EU DAR FRUTO E DAR FRUTO BOM.\n\nCORO\nOLIVEIRA VERDADEIRA, JESUS CRISTO É,\nNELE FUI ENXERTADO ATRAVÉS DA FÉ,\nPOSSO EU DAR FRUTO ENXERTADO ASSIM;\nCRISTO É OLIVEIRA, NÃO MAIS VIVO EU\nMAS CRISTO VIVE EM MIM.', 'ERA UMA OLIVEIRA COM OS RAMOS QUEBRADOS, E UM ZAMBUJEIRO ALI FOI ENXERTADO; ESSA OLIVEIRA É O MEU JESUS, FUI NELE ENXERTADO, HOJE TENHO LUZ. CORO OLIVEIRA VERDADEIRA, JESUS CRISTO É, NELE FUI ENXERTADO ATRAVÉS DA FÉ, POSSO EU DAR FRUTO ENXERTADO ASSIM; CRISTO É OLIVEIRA, NÃO MAIS VIVO EU MAS CRISTO VIVE EM MIM. ZAMBUJEIRO BRAVO NÃO DÁ FRUTO BOM, ASSIM ERA EU NO MEU CORAÇÃO, MAS NUM BELO DIA TUDO TRANSFORMOU, EU FUI ENXERTADO NO MEU SALVADOR. CRISTO É OLIVEIRA, NELE SOU FELIZ, NELE ESTOU LIGADO NO TRONCO E NA RAIZ; FIRMADO NA OLIVEIRA DE TODO O CORAÇÃO, POSSO EU DAR FRUTO E DAR FRUTO BOM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('162', '162 - GRANDIOSO PARA MIM', 'GRANDIOSO PARA MIM\nÉ JESUS, MEU SALVADOR,\nSUA GRAÇA NÃO TEM FIM,\nCOM SEU DIVINAL FULGOR;\nSUA GRAÇA SE ESTENDEU,\nDANDO-ME A SALVAÇÃO;\nGRANDE PAZ E ALEGRIA\nEU DESFRUTAREI UM DIA\nNA CELESTIAL SIÃO.\n\nO TEU NOME É AMOR,\nEU CONFESSO A TI, SENHOR;\nO TEU NOME É PURO E BOM,\nPERDOADO AGORA ESTOU;\nNESTE MUNDO DE ILUSÃO,\nENQUANTO EU AQUI VIVER,\nMINHA BOCA ENTOARÁ\nALELUIA, SEM CESSAR\nEXALTANDO O TEU PODER.\nBIS', 'GRANDIOSO PARA MIM É JESUS, MEU SALVADOR, SUA GRAÇA NÃO TEM FIM, COM SEU DIVINAL FULGOR; SUA GRAÇA SE ESTENDEU, DANDO-ME A SALVAÇÃO; GRANDE PAZ E ALEGRIA EU DESFRUTAREI UM DIA NA CELESTIAL SIÃO. O TEU NOME É AMOR, EU CONFESSO A TI, SENHOR; O TEU NOME É PURO E BOM, PERDOADO AGORA ESTOU; NESTE MUNDO DE ILUSÃO, ENQUANTO EU AQUI VIVER, MINHA BOCA ENTOARÁ ALELUIA, SEM CESSAR EXALTANDO O TEU PODER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('163', '163 - TENHO UMA PEDRA VIVA', 'TENHO UMA PEDRA VIVA,\nPEDRA DE ESQUINA, \nPEDRA ANGULAR;\nCRISTO É ROCHA DURA,\nTODA CRIATURA PODE SEGURAR.\nESTA ROCHA É CRISTO \nTODO IMPREVISTO PODE RESOLVER,\nPORÉM TEUS PECADOS SERÃO \nPERDOADOS, CRISTO TEM PODER.\nBIS\n\nCORO\nCRISTO TEM PODER E SABE LUTAR,\nELE É PODEROSO,\nELE É O LEÃO DA TRIBO DE JUDÁ;\nCRISTO TEM PODER E SABE LUTAR,\nELE É PODEROSO,\nELE É O LEÃO DA TRIBO DE JUDÁ.\n\nESTA ROCHA É ÁGUA \nE QUEM TEM SUAS MÁGOAS ELE VAI TIRAR;\nNUNCA ESTOU SOZINHO, \nCRISTO É MEU CAMINHO, NELE TENHO PAZ.\nTEM PAZ VERDADEIRA QUEM \nNESTA ROCHA FIRMADO ESTÁ;\nCRISTO É A VERDADE E, SE \nCRERES NELE, TE LIBERTARÁ.\nBIS\n\nCORO\nCRISTO TEM PODER E SABE LUTAR,\nELE É PODEROSO,\nELE É O LEÃO DA TRIBO DE JUDÁ;\nCRISTO TEM PODER E SABE LUTAR,\nELE É PODEROSO,\nELE É O LEÃO DA TRIBO DE JUDÁ.\n\nFINAL: \nELE É PODEROSO, \nELE É O LEÃO \nDA TRIBO DE JUDÁ.', 'TENHO UMA PEDRA VIVA, PEDRA DE ESQUINA, PEDRA ANGULAR; CRISTO É ROCHA DURA, TODA CRIATURA PODE SEGURAR. ESTA ROCHA É CRISTO TODO IMPREVISTO PODE RESOLVER, PORÉM TEUS PECADOS SERÃO PERDOADOS, CRISTO TEM PODER. CORO CRISTO TEM PODER E SABE LUTAR, ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ; CRISTO TEM PODER E SABE LUTAR, ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ. ESTA ROCHA É ÁGUA E QUEM TEM SUAS MÁGOAS ELE VAI TIRAR; NUNCA ESTOU SOZINHO, CRISTO É MEU CAMINHO, NELE TENHO PAZ. TEM PAZ VERDADEIRA QUEM NESTA ROCHA FIRMADO ESTÁ; CRISTO É A VERDADE E, SE CRERES NELE, TE LIBERTARÁ. ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('164', '164 - É BASTANTE PARA MIM A TUA GRAÇA', 'É BASTANTE PARA MIM A TUA GRAÇA,\nRECEBER A SALVAÇÃO E TEU PERDÃO;\nÉ MARAVILHOSO SABER\nQUE, PARA SEMPRE, COM JESUS VOU VIVER;\nÉ BASTANTE, PARA MIM, A TUA GRAÇA.\n\nA TUA GRAÇA É MELHOR QUE A VIDA,\nA TUA GRAÇA É MELHOR QUE A VIDA;\nMEUS LÁBIOS TE LOUVAM,\nE ASSIM BENDIREI, EM TEU \nNOME MINHAS MÃOS \nLEVANTAREI.\nBIS\n\nA TUA PRESENÇA ENCHE O MEU CORAÇÃO,\nA TUA PRESENÇA ENCHE O MEU CORAÇÃO;\nMEUS LÁBIOS TE LOUVAM,\nE ASSIM BENDIREI, \nEM TEU NOME MINHAS MÃOS \nLEVANTAREI.\nBIS', 'É BASTANTE PARA MIM A TUA GRAÇA, RECEBER A SALVAÇÃO E TEU PERDÃO; É MARAVILHOSO SABER QUE, PARA SEMPRE, COM JESUS VOU VIVER; É BASTANTE, PARA MIM, A TUA GRAÇA. A TUA GRAÇA É MELHOR QUE A VIDA, A TUA GRAÇA É MELHOR QUE A VIDA; MEUS LÁBIOS TE LOUVAM, E ASSIM BENDIREI, EM TEU NOME MINHAS MÃOS LEVANTAREI. A TUA PRESENÇA ENCHE O MEU CORAÇÃO, A TUA PRESENÇA ENCHE O MEU CORAÇÃO; MEUS LÁBIOS TE LOUVAM, E ASSIM BENDIREI, EM TEU NOME MINHAS MÃOS LEVANTAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('165', '165 - MARAVILHOSA GRAÇA', 'MARAVILHOSA GRAÇA,\nMAIOR QUE O MEU PECAR,\nCOMO PODER CANTÁ-LA,\nCOMO HEI DE COMEÇAR?\nPOIS ALIVIA A MINH’ALMA\nE VIVO EM TODA A CALMA\nPELA MARAVILHOSA GRAÇA DE JESUS.\n\nCORO\nGRAÇA, QUÃO MARAVILHOSA A GRAÇA DE JESUS,\nALTA COMO O FIRMAMENTO, ALTA E SEM FIM,\nGRAÇA, QUÃO MARAVILHOSA, SUFICIENTE A MIM;\nÉ MAIOR QUE A MINHA VIDA INÚTIL,\nÉ MAIOR QUE O MEU PECADO VIL,\nO NOME DE JESUS ENGRANDECEI \nE GLÓRIA DAI!\n\nMARAVILHOSA GRAÇA,\nQUÃO RICAS BÊNÇÃOS TRAZ,\nO SEU PODER TRANSFORMA \nO PECADOR FALAZ;\nESTOU SALVO COM A VERDADE\nPOR TODA A ETERNIDADE,\nPELA MARAVILHOSA GRAÇA DE JESUS.\n\nCORO\nGRAÇA, QUÃO MARAVILHOSA A GRAÇA DE JESUS,\nALTA COMO O FIRMAMENTO, ALTA E SEM FIM,\nGRAÇA, QUÃO MARAVILHOSA, SUFICIENTE A MIM;\nÉ MAIOR QUE A MINHA VIDA INÚTIL,\nÉ MAIOR QUE O MEU PECADO VIL,\nO NOME DE JESUS ENGRANDECEI \nE GLÓRIA DAI!', 'MARAVILHOSA GRAÇA, MAIOR QUE O MEU PECAR, COMO PODER CANTÁ-LA, COMO HEI DE COMEÇAR? POIS ALIVIA A MINH’ALMA E VIVO EM TODA A CALMA PELA MARAVILHOSA GRAÇA DE JESUS. CORO GRAÇA, QUÃO MARAVILHOSA A GRAÇA DE JESUS, ALTA COMO O FIRMAMENTO, ALTA E SEM FIM, GRAÇA, QUÃO MARAVILHOSA, SUFICIENTE A MIM; É MAIOR QUE A MINHA VIDA INÚTIL, É MAIOR QUE O MEU PECADO VIL, O NOME DE JESUS ENGRANDECEI E GLÓRIA DAI! MARAVILHOSA GRAÇA, QUÃO RICAS BÊNÇÃOS TRAZ, O SEU PODER TRANSFORMA O PECADOR FALAZ; ESTOU SALVO COM A VERDADE POR TODA A ETERNIDADE, PELA MARAVILHOSA GRAÇA DE JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('166', '166 - Ó AMADO DE MINH´ALMA', 'Ó AMADO DE MINH’ALMA,\nONDE APASCENTAS O TEU REBANHO?\nENTRE OS LÍRIOS APASCENTAS, \nEU SOU TEU E TU ÉS MEU, \nÓ AMADO MEU.\n\nCORO\nVEM, AMADO, AO TEU JARDIM,\nENTRE OS LÍRIOS, PASTOREAR;\nÓ VEM, SIM, VEM,\nHÁ UM REBANHO A TE ESPERAR.\n\nÓ AMADO DE MINH’ALMA,\nOUÇO TUA VOZ, QUE É TÃO SUAVE,\nQUE ME DIZ: “LEVANTA E VEM”;\nLEVANTAR-ME-EI E IREI, Ó AMADO MEU.\n\nCORO\nVEM, AMADO, AO TEU JARDIM,\nENTRE OS LÍRIOS, PASTOREAR;\nÓ VEM, SIM, VEM,\nHÁ UM REBANHO A TE ESPERAR.\n\nOUÇO A VOZ DO MEU AMADO, \nEI-LO GALGANDO POR SOBRE OS MONTES;\nQUERO ESTAR COM O MEU AMADO\nNO JARDIM DE FRUTOS BONS, \nÓ AMADO MEU.\n\nCORO\nVEM, AMADO, AO TEU JARDIM,\nENTRE OS LÍRIOS, PASTOREAR;\nÓ VEM, SIM, VEM,\nHÁ UM REBANHO A TE ESPERAR.\nBIS', 'Ó AMADO DE MINH’ALMA, ONDE APASCENTAS O TEU REBANHO? ENTRE OS LÍRIOS APASCENTAS, EU SOU TEU E TU ÉS MEU, Ó AMADO MEU. CORO VEM, AMADO, AO TEU JARDIM, ENTRE OS LÍRIOS, PASTOREAR; Ó VEM, SIM, VEM, HÁ UM REBANHO A TE ESPERAR. Ó AMADO DE MINH’ALMA, OUÇO TUA VOZ, QUE É TÃO SUAVE, QUE ME DIZ: LEVANTA E VEM; LEVANTAR-ME-EI E IREI, Ó AMADO MEU. OUÇO A VOZ DO MEU AMADO, EI-LO GALGANDO POR SOBRE OS MONTES; QUERO ESTAR COM O MEU AMADO NO JARDIM DE FRUTOS BONS, Ó AMADO MEU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('167', '167 - AMO AO MEU SENHOR', 'AMO AO MEU SENHOR\nPORQUE ELE OUVE A MINHA VOZ\nE O MEU CLAMOR, REFÚGIO MEU;\nNAS DIFICULDADES DESTA VIDA\nSEI QUE ME OUVIRÁ;\nO SENHOR ME GUARDARÁ.\n\nPORQUE DA MORTE LIVRASTE \nA MINH’ALMA,\nREFÚGIO EM TI ENCONTREI,\nQUERO SEGUIR-TE, \nMEU SENHOR E REI,\nQUERO SEGUIR-TE, \nMEU SENHOR E REI.\n\nINSTRUMENTOS\n\nQUERO OUVIR TUA VOZ, \nQUE É SOBERANA, \nAUXILIANDO O MEU ANDAR,\nPARA O ALVO ALCANÇAR;\nMESMO QUE HAJA PROVAS E DIAS DIFÍCEIS,\nÓ SENHOR, SEI QUE TU ME CONDUZIRÁS.\nPORQUE DA MORTE LIVRASTE A MINH’ALMA,\nDANDO-ME ASSIM SALVAÇÃO\nE BENDIREI TEU NOME SEMPRE, AMÉM,\nE BENDIREI TEU NOME SEMPRE, AMÉM.', 'AMO AO MEU SENHOR PORQUE ELE OUVE A MINHA VOZ E O MEU CLAMOR, REFÚGIO MEU; NAS DIFICULDADES DESTA VIDA SEI QUE ME OUVIRÁ; O SENHOR ME GUARDARÁ. PORQUE DA MORTE LIVRASTE A MINH’ALMA, REFÚGIO EM TI ENCONTREI, QUERO SEGUIR-TE, MEU SENHOR E REI, QUERO SEGUIR-TE, MEU SENHOR E REI. INSTRUMENTOS QUERO OUVIR TUA VOZ, QUE É SOBERANA, AUXILIANDO O MEU ANDAR, PARA O ALVO ALCANÇAR; MESMO QUE HAJA PROVAS E DIAS DIFÍCEIS, Ó SENHOR, SEI QUE TU ME CONDUZIRÁS. PORQUE DA MORTE LIVRASTE A MINH’ALMA, DANDO-ME ASSIM SALVAÇÃO E BENDIREI TEU NOME SEMPRE, AMÉM, E BENDIREI TEU NOME SEMPRE, AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('168', '168 - TU ÉS FIEL, SENHOR', 'TU ÉS FIEL, SENHOR, MEU PAI CELESTE,\nPLENO PODER AOS TEUS FILHOS DARÁS;\nNUNCA MUDASTE, TU NUNCA FALTASTE:\nTAL COMO ERAS, TU SEMPRE SERÁS.\n\nCORO\nTU ÉS FIEL, SENHOR, TU ÉS FIEL, SENHOR,\nDIA APÓS DIA, COM BÊNÇÃOS SEM FIM;\nTUA MERCÊ ME SUSTENTA E ME GUARDA,\nTU ÉS FIEL, SENHOR, FIEL A MIM.\n\nFLORES E FRUTOS, MONTANHAS E MARES,\nSOL, LUA, ESTRELAS NO CÉU A BRILHAR,\nTUDO CRIASTE NA TERRA E NOS ARES,\nTODO O UNIVERSO VEM, POIS, TE LOUVAR!\n\nCORO\nTU ÉS FIEL, SENHOR, TU ÉS FIEL, SENHOR,\nDIA APÓS DIA, COM BÊNÇÃOS SEM FIM;\nTUA MERCÊ ME SUSTENTA E ME GUARDA,\nTU ÉS FIEL, SENHOR, FIEL A MIM.\n\nPLENO PERDÃO TU DÁS, PAZ, SEGURANÇA,\nCADA MOMENTO ME GUIAS, SENHOR;\nE NO PORVIR, OH! QUE DOCE ESPERANÇA,\nDESFRUTAREI DO TEU RICO FAVOR!\n\nCORO\nTU ÉS FIEL, SENHOR, TU ÉS FIEL, SENHOR,\nDIA APÓS DIA, COM BÊNÇÃOS SEM FIM;\nTUA MERCÊ ME SUSTENTA E ME GUARDA,\nTU ÉS FIEL, SENHOR, FIEL A MIM.', 'TU ÉS FIEL, SENHOR, MEU PAI CELESTE, PLENO PODER AOS TEUS FILHOS DARÁS; NUNCA MUDASTE, TU NUNCA FALTASTE: TAL COMO ERAS, TU SEMPRE SERÁS. CORO TU ÉS FIEL, SENHOR, TU ÉS FIEL, SENHOR, DIA APÓS DIA, COM BÊNÇÃOS SEM FIM; TUA MERCÊ ME SUSTENTA E ME GUARDA, TU ÉS FIEL, SENHOR, FIEL A MIM. FLORES E FRUTOS, MONTANHAS E MARES, SOL, LUA, ESTRELAS NO CÉU A BRILHAR, TUDO CRIASTE NA TERRA E NOS ARES, TODO O UNIVERSO VEM, POIS, TE LOUVAR! PLENO PERDÃO TU DÁS, PAZ, SEGURANÇA, CADA MOMENTO ME GUIAS, SENHOR; E NO PORVIR, OH! QUE DOCE ESPERANÇA, DESFRUTAREI DO TEU RICO FAVOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('169', '169 - COMO A FLOR DO CAMPO', 'COMO A FLOR DO CAMPO QUE FLORESCE,\nSOPRA O VENTO E JÁ DESAPARECE,\nCOMO A NÉVOA QUE SE DESVANECE,\nASSIM BREVES SÃO OS NOSSOS DIAS.\n\nMAS DE ETERNIDADE A ETERNIDADE\nÉ, Ó SENHOR DEUS, TUA BONDADE,\nTEU AMOR E TUA FIDELIDADE\nTU ESTENDES SOBRE OS QUE TE TEMEM.\n\nCORO (2X)\nTU ÉS FIEL, SENHOR,\nSIM, TU ÉS FIEL.\n\nCÉUS E TERRA ENVELHECERÃO,\nAS COISAS CRIADAS TODAS PASSARÃO,\nCOMO ROUPAS VELHAS ELAS CAIRÃO,\nPELO TEU PODER AS MUDARÁS.\n\nCORO (2X)\nTU ÉS FIEL, SENHOR,\nSIM, TU ÉS FIEL.', 'COMO A FLOR DO CAMPO QUE FLORESCE, SOPRA O VENTO E JÁ DESAPARECE, COMO A NÉVOA QUE SE DESVANECE, ASSIM BREVES SÃO OS NOSSOS DIAS. MAS DE ETERNIDADE A ETERNIDADE É, Ó SENHOR DEUS, TUA BONDADE, TEU AMOR E TUA FIDELIDADE TU ESTENDES SOBRE OS QUE TE TEMEM.  TU ÉS FIEL, SENHOR, SIM, TU ÉS FIEL. CÉUS E TERRA ENVELHECERÃO, AS COISAS CRIADAS TODAS PASSARÃO, COMO ROUPAS VELHAS ELAS CAIRÃO, PELO TEU PODER AS MUDARÁS.  TU ÉS FIEL, SENHOR, SIM, TU ÉS FIEL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('170', '170 - EU SOU TÃO FELIZ COM O MEU JESUS', 'EU SOU TÃO FELIZ COM O MEU JESUS,\nELE MORREU NUMA CRUZ\nPRA SALVAR O PECADOR;\nELE É O CAMINHO, \nA VERDADE E A VIDA\nELE É JESUS, O SENHOR, \nNOSSO ETERNO REDENTOR!\n\nCORO (2X)\nNOVA CRIATURA SOU, \nPELO SANGUE DE JESUS, \nREDIMIDO PARA HERDAR \nA VIDA ETERNA!\n\nEU SOU TÃO FELIZ \nCOM O MEU JESUS,\nELE MORREU NUMA CRUZ \nPRA SALVAR O PECADOR.\n\nCORO (2X)\nNOVA CRIATURA SOU, \nPELO SANGUE DE JESUS,\nREDIMIDO PARA HERDAR \nA VIDA ETERNA!\n\nFINAL:\nREDIMIDO PARA HERDAR \nA VIDA ETERNA.', 'EU SOU TÃO FELIZ COM O MEU JESUS, ELE MORREU NUMA CRUZ PRA SALVAR O PECADOR; ELE É O CAMINHO, A VERDADE E A VIDA ELE É JESUS, O SENHOR, NOSSO ETERNO REDENTOR! NOVA CRIATURA SOU, PELO SANGUE DE JESUS, REDIMIDO PARA HERDAR A VIDA ETERNA! EU SOU TÃO FELIZ COM O MEU JESUS, ELE MORREU NUMA CRUZ PRA SALVAR O PECADOR. NOVA CRIATURA SOU, PELO SANGUE DE JESUS, REDIMIDO PARA HERDAR A VIDA ETERNA!  REDIMIDO PARA HERDAR A VIDA ETERNA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('171', '171 - O AMOR DE JESUS É GRANDE', 'O AMOR DE JESUS É GRANDE,\nIMENSURÁVEL ESSE AMOR,\nNÃO SE PODE COMPARAR, NEM MEDIR,\nO INFINITO AMOR DE DEUS.\nBIS\n\nDEMONSTRADO LÁ NA CRUZ\nPOR JESUS, MEU REI,\nPRA SALVAR O POBRE PECADOR,\nOH! QUE GRANDE AMOR.\nINSTRUMENTOS\n\nO AMOR DE JESUS É GRANDE,\nIMENSURÁVEL ESSE AMOR,\nNÃO SE PODE COMPARAR, NEM MEDIR,\nO INFINITO AMOR DE DEUS.\n\nDEMONSTRADO LÁ NA CRUZ\nPOR JESUS, MEU REI,\nPRA SALVAR O POBRE PECADOR,\nOH! QUE GRANDE AMOR.\n\nFINAL: (2X) \nOH! QUE GRANDE AMOR.\nBIS', 'O AMOR DE JESUS É GRANDE, IMENSURÁVEL ESSE AMOR, NÃO SE PODE COMPARAR, NEM MEDIR, O INFINITO AMOR DE DEUS. DEMONSTRADO LÁ NA CRUZ POR JESUS, MEU REI, PRA SALVAR O POBRE PECADOR, OH! QUE GRANDE AMOR. INSTRUMENTOS DEMONSTRADO LÁ NA CRUZ POR JESUS, MEU REI, PRA SALVAR O POBRE PECADOR, OH! QUE GRANDE AMOR.   OH! QUE GRANDE AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('172', '172 - SENHOR, TE AMO, TE AMO', 'CORO\nSENHOR, TE AMO, TE AMO,\nPORQUE SOFRESTE POR MIM,\nNA CRUZ MORRESTE, MEU MESTRE,\nEU TE AMO, TE AMO, JESUS.\n\nA DOR QUE ERA PARA MIM\nLEVOU JESUS, MEU SENHOR;\nCOMO É BOM TER CRISTO AO MEU LADO,\nOH! QUANTO AMO JESUS, MEU SENHOR.\n\nCORO\nSENHOR, TE AMO, TE AMO,\nPORQUE SOFRESTE POR MIM,\nNA CRUZ MORRESTE, MEU MESTRE,\nEU TE AMO, TE AMO, JESUS.\n\nA CRISTO ENTREGO MEU VIVER\nPORQUE EU SEI QUE CUIDA DE MIM;\nELE É O AMADO DE MINH’ALMA,\nOH! QUANTO AMO JESUS, MEU SENHOR.\n\nCORO\nSENHOR, TE AMO, TE AMO,\nPORQUE SOFRESTE POR MIM,\nNA CRUZ MORRESTE, MEU MESTRE,\nEU TE AMO, TE AMO, JESUS.', 'CORO SENHOR, TE AMO, TE AMO, PORQUE SOFRESTE POR MIM, NA CRUZ MORRESTE, MEU MESTRE, EU TE AMO, TE AMO, JESUS. A DOR QUE ERA PARA MIM LEVOU JESUS, MEU SENHOR; COMO É BOM TER CRISTO AO MEU LADO, OH! QUANTO AMO JESUS, MEU SENHOR. A CRISTO ENTREGO MEU VIVER PORQUE EU SEI QUE CUIDA DE MIM; ELE É O AMADO DE MINH’ALMA, OH! QUANTO AMO JESUS, MEU SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('173', '173 - QUANDO JESUS UM DIA', 'QUANDO JESUS UM DIA\nMEU PECADO CARREGOU,\nPELO SANGUE DERRAMADO\nMINHA VIDA ELE LAVOU.\n\nALGO NOVO ACONTECEU,\nTÃO SUBLIME E TÃO REAL;\nCHORAR EU NÃO SABIA,\nSÓ DE AGONIA E DOR.\n\nCHORO AGORA DE ALEGRIA,\nALELUIA AO MEU SENHOR;\nMEU PECADO RESGATOU,\nMINHA VIDA TRANSFORMOU.\n\nALGO NOVO ACONTECEU,\nTÃO SUBLIME E TÃO REAL;\nCHORO AGORA DE ALEGRIA,\nALELUIA AO MEU SENHOR.', 'QUANDO JESUS UM DIA MEU PECADO CARREGOU, PELO SANGUE DERRAMADO MINHA VIDA ELE LAVOU. ALGO NOVO ACONTECEU, TÃO SUBLIME E TÃO REAL; CHORAR EU NÃO SABIA, SÓ DE AGONIA E DOR. CHORO AGORA DE ALEGRIA, ALELUIA AO MEU SENHOR; MEU PECADO RESGATOU, MINHA VIDA TRANSFORMOU. ALGO NOVO ACONTECEU, TÃO SUBLIME E TÃO REAL; CHORO AGORA DE ALEGRIA, ALELUIA AO MEU SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('174', '174 - A MINHA HERANÇA É O SENHOR', 'A MINHA HERANÇA É O SENHOR (É O SENHOR),\nNÃO TENHO OUTRO BEM MAIOR;\nAINDA QUE ME OFEREÇAM.\n\nTODA RIQUEZA DESTE MUNDO, \nNÃO TROCAREI A MINHA HERANÇA, \nPOIS CUSTOU UM ALTO PREÇO \nE ESTE PREÇO FOI O SANGUE DE JESUS.\n\nA MINHA HERANÇA É O SENHOR (É O SENHOR),\nNÃO TENHO OUTRO BEM MAIOR;\nAINDA QUE ME OFEREÇAM.\n\nTODA RIQUEZA DESTE MUNDO, \nNÃO TROCAREI A MINHA HERANÇA, \nPOIS CUSTOU UM ALTO PREÇO \nE ESTE PREÇO FOI O SANGUE DE JESUS.\n\nFINAL: (2X)\nE ESTE ALTO PREÇO \nFOI SANGUE DE JESUS', 'A MINHA HERANÇA É O SENHOR (É O SENHOR), NÃO TENHO OUTRO BEM MAIOR; AINDA QUE ME OFEREÇAM. TODA RIQUEZA DESTE MUNDO, NÃO TROCAREI A MINHA HERANÇA, POIS CUSTOU UM ALTO PREÇO E ESTE PREÇO FOI O SANGUE DE JESUS. E ESTE ALTO PREÇO FOI SANGUE DE JESUS', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('175', '175 - DESDE O DIA EM QUE TE CONHECI', 'DESDE O DIA EM QUE TE CONHECI,\nTODA A MINHA VIDA DEI A TI;\nOPERASTE EM MEU SER ALGO QUE NÃO SEI DIZER.\nEU SÓ SEI QUE TU ÉS MEU BEM QUERER.\n\nCORO\nMARANATA! VEM, JESUS!\nMARANATA! VEM, SENHOR JESUS!\nMEU DESEJO É TE VER, \nFACE A FACE TE ADORAR. \nORA VEM, SENHOR, ME BUSCAR.\n\nORA VEM, SENHOR, ME BUSCAR.\nMUITAS LUTAS EU PASSEI PRA TE SERVIR,\nMAS TEU CONSOLO E TEU AMOR ME SUSTENTARAM, \nAS PROMESSAS SE CUMPRIRAM, \nRENOVANDO MEU VIGOR.\nCOMO POSSO TE ESQUECER, \nÓ MEU SENHOR?\n\nCORO\nMARANATA! VEM, JESUS!\nMARANATA! VEM, SENHOR JESUS!\nMEU DESEJO É TE VER, \nFACE A FACE TE ADORAR. \nORA VEM, SENHOR, ME BUSCAR.\n\nTE SERVIR É TUDO QUE EU QUERO, \nSENHOR,TE EXALTAR, ENGRANDECER, \nTE BENDIZER;\nTU ME AMASTE, MEU SENHOR.\nMEU JESUS, MEU SALVADOR,\nMEU DESEJO É TE VER NA ETERNIDADE.\n\nCORO\nMARANATA! VEM, JESUS!\nMARANATA! VEM, SENHOR JESUS!\nMEU DESEJO É TE VER, \nFACE A FACE TE ADORAR. \nORA VEM, SENHOR, ME BUSCAR.\n\nFINAL:  \nMEU DESEJO É TE VER,\nFACE A FACE TE ADORAR,\nEU ESTOU A TE ESPERAR.', 'DESDE O DIA EM QUE TE CONHECI, TODA A MINHA VIDA DEI A TI; OPERASTE EM MEU SER ALGO QUE NÃO SEI DIZER. EU SÓ SEI QUE TU ÉS MEU BEM QUERER. CORO MARANATA! VEM, JESUS! MARANATA! VEM, SENHOR JESUS! MEU DESEJO É TE VER, FACE A FACE TE ADORAR. ORA VEM, SENHOR, ME BUSCAR. ORA VEM, SENHOR, ME BUSCAR. MUITAS LUTAS EU PASSEI PRA TE SERVIR, MAS TEU CONSOLO E TEU AMOR ME SUSTENTARAM, AS PROMESSAS SE CUMPRIRAM, RENOVANDO MEU VIGOR. COMO POSSO TE ESQUECER, Ó MEU SENHOR? TE SERVIR É TUDO QUE EU QUERO, SENHOR,TE EXALTAR, ENGRANDECER, TE BENDIZER; TU ME AMASTE, MEU SENHOR. MEU JESUS, MEU SALVADOR, MEU DESEJO É TE VER NA ETERNIDADE. MEU DESEJO É TE VER, FACE A FACE TE ADORAR, EU ESTOU A TE ESPERAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('176', '176 - NÃO EXISTE OUTRO AMOR IGUAL AO TEU, SENHOR', 'NÃO EXISTE OUTRO AMOR IGUAL AO TEU, SENHOR,\nNÃO EXISTE COMPAIXÃO IGUAL À TUA COMPAIXÃO,\nNÃO HÁ OUTRO CAMINHO QUE NOS CONDUZ AO CÉU\nNÃO EXISTE OUTRO NOME QUE POSSA NOS SALVAR.\n\nJESUS, TU ÉS O ÚNICO SENHOR,\nÉS A LUZ, ÉS O CAMINHO, ÉS MEU AMOR;\nO TEU NOME É SOBRE OS NOMES.\nÉS JESUS, ÉS A RAZÃO DO MEU VIVER.\n\nNÃO EXISTE OUTRO AMOR IGUAL AO \nTEU SENHOR, NÃO EXISTE COMPAIXÃO \nIGUAL À TUA COMPAIXÃO,\nNÃO HÁ OUTRO CAMINHO \nQUE NOS CONDUZ AO CÉU \nNÃO EXISTE OUTRO NOME \nQUE POSSA NOS SALVAR.\n\nJESUS, TU ÉS O ÚNICO SENHOR,\nÉS A LUZ, ÉS O CAMINHO, \nÉS MEU AMOR;\nO TEU NOME É SOBRE OS NOMES.\nÉS JESUS, ÉS A RAZÃO DO MEU VIVER.\n\nFINAL: DO MEU VIVER.\nBIS', 'NÃO EXISTE OUTRO AMOR IGUAL AO TEU, SENHOR, NÃO EXISTE COMPAIXÃO IGUAL À TUA COMPAIXÃO, NÃO HÁ OUTRO CAMINHO QUE NOS CONDUZ AO CÉU NÃO EXISTE OUTRO NOME QUE POSSA NOS SALVAR. JESUS, TU ÉS O ÚNICO SENHOR, ÉS A LUZ, ÉS O CAMINHO, ÉS MEU AMOR; O TEU NOME É SOBRE OS NOMES. ÉS JESUS, ÉS A RAZÃO DO MEU VIVER. NÃO EXISTE OUTRO AMOR IGUAL AO TEU SENHOR, NÃO EXISTE COMPAIXÃO IGUAL À TUA COMPAIXÃO, NÃO HÁ OUTRO CAMINHO QUE NOS CONDUZ AO CÉU NÃO EXISTE OUTRO NOME QUE POSSA NOS SALVAR. JESUS, TU ÉS O ÚNICO SENHOR, ÉS A LUZ, ÉS O CAMINHO, ÉS MEU AMOR; O TEU NOME É SOBRE OS NOMES. ÉS JESUS, ÉS A RAZÃO DO MEU VIVER.  DO MEU VIVER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('177', '177 - SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM', 'SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM,\nNÃO ESQUECEREI TEU GRANDE AMOR POR MIM.\nSEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM,\nNÃO ESQUECEREI TEU GRANDE AMOR POR MIM.\n\nCORO \nNÃO FOI EM VÃO \nO TEU SANGUE VERTIDO NA CRUZ, \nNÃO FOI EM VÃO, \nEU TE LOUVO PRA SEMPRE, JESUS.\n\nINSTRUMENTOS\n\nÉ TEU MEU LOUVOR, \nMINHA ADORAÇÃO, \nÉS TU MEU PASTOR, \nÉ TEU TODO O MEU CORAÇÃO.\n\nCORO \nNÃO FOI EM VÃO \nO TEU SANGUE VERTIDO NA CRUZ, \nNÃO FOI EM VÃO, \nEU TE LOUVO PRA SEMPRE, JESUS.\n\nFINAL: \nEU TE LOUVO PRA SEMPRE, JESUS,\nEU TE LOUVO PRA SEMPRE, JESUS.', 'SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM, NÃO ESQUECEREI TEU GRANDE AMOR POR MIM. SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM, NÃO ESQUECEREI TEU GRANDE AMOR POR MIM. CORO NÃO FOI EM VÃO O TEU SANGUE VERTIDO NA CRUZ, NÃO FOI EM VÃO, EU TE LOUVO PRA SEMPRE, JESUS. INSTRUMENTOS É TEU MEU LOUVOR, MINHA ADORAÇÃO, ÉS TU MEU PASTOR, É TEU TODO O MEU CORAÇÃO. CORO NÃO FOI EM VÃO O TEU SANGUE VERTIDO NA CRUZ, NÃO FOI EM VÃO, EU TE LOUVO PRA SEMPRE, JESUS.  EU TE LOUVO PRA SEMPRE, JESUS, EU TE LOUVO PRA SEMPRE, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('178', '178 - O DEUS A QUEM SERVIMOS', 'O DEUS A QUEM SERVIMOS \nÉ DEUS FORTE NA BATALHA, É DEUS FIEL;\nO DEUS A QUEM SERVIMOS \nNÃO NOS DESAMPARA JAMAIS.\nO DEUS A QUEM SERVIMOS \nÉ UM DEUS PERDOADOR, \nÉ DEUS DE AMOR;\nELE É O NOSSO GUARDADOR, \nÉ O BOM PASTOR.\n\nCORO\nNÓS PERTENCEMOS A TI, SENHOR,\nSOMOS TEUS SERVOS,\nDE QUEM TENS CUIDADO COM AMOR;\nNÓS PERTENCEMOS A TI, SENHOR,\nE PRA TEU LOUVOR ESTAMOS AQUI.\n\nO DEUS A QUEM SERVIMOS \nPÕE EM NOSSOS LÁBIOS UM NOVO LOUVOR,\nO DEUS A QUEM SERVIMOS \nNOS DÁ SEMPRE A VITÓRIA.\nO DEUS A QUEM SERVIMOS ESTÁ \nAQUI PRESENTE ENTRE NÓS.\nPODEMOS SENTIR SUA PRESENÇA,\nPODEMOS OUVIR SUA DOCE VOZ,\nPODEMOS CONTEMPLAR \nAS MARAVILHAS DAS SUAS MÃOS.\n\nCORO\nNÓS PERTENCEMOS A TI, SENHOR,\nSOMOS TEUS SERVOS,\nDE QUEM TENS CUIDADO COM AMOR;\nNÓS PERTENCEMOS A TI, SENHOR,\nE PRA TEU LOUVOR ESTAMOS AQUI.', 'O DEUS A QUEM SERVIMOS É DEUS FORTE NA BATALHA, É DEUS FIEL; O DEUS A QUEM SERVIMOS NÃO NOS DESAMPARA JAMAIS. O DEUS A QUEM SERVIMOS É UM DEUS PERDOADOR, É DEUS DE AMOR; ELE É O NOSSO GUARDADOR, É O BOM PASTOR. CORO NÓS PERTENCEMOS A TI, SENHOR, SOMOS TEUS SERVOS, DE QUEM TENS CUIDADO COM AMOR; NÓS PERTENCEMOS A TI, SENHOR, E PRA TEU LOUVOR ESTAMOS AQUI. O DEUS A QUEM SERVIMOS PÕE EM NOSSOS LÁBIOS UM NOVO LOUVOR, O DEUS A QUEM SERVIMOS NOS DÁ SEMPRE A VITÓRIA. O DEUS A QUEM SERVIMOS ESTÁ AQUI PRESENTE ENTRE NÓS. PODEMOS SENTIR SUA PRESENÇA, PODEMOS OUVIR SUA DOCE VOZ, PODEMOS CONTEMPLAR AS MARAVILHAS DAS SUAS MÃOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('179', '179 - PELA FÉ EU POSSO CONTEMPLAR JESUS', 'PELA FÉ EU POSSO CONTEMPLAR JESUS,\n(PELA FÉ EU POSSO VER)\nPELA FÉ EU VEJO O BRILHO DA SUA LUZ,\nPELA FÉ EU POSSO VER OS ANJOS CANTANDO, \nEXALTANDO O NOSSO REI, O SALVADOR.\n\nPELA FÉ EU SINTO NO MEU CORAÇÃO\n(PELA FÉ POSSO SENTIR)\nA PRESENÇA DO ESPÍRITO DE DEUS,\nPELA FÉ EU SINTO SUA MÃO TOCAR EM MIM\nPELA FÉ POSSO SENTIR SEU GRANDE AMOR.\n\nE PELA FÉ VOU CAMINHANDO\nATÉ CHEGAR À TERRA PROMETIDA,\nE PELA FÉ JÁ OUÇO O CANTO DE VITÓRIA: \nCRISTO REINANDO COM OS FIÉIS NA SUA GLÓRIA.\nBIS', 'PELA FÉ EU POSSO CONTEMPLAR JESUS, (PELA FÉ EU POSSO VER) PELA FÉ EU VEJO O BRILHO DA SUA LUZ, PELA FÉ EU POSSO VER OS ANJOS CANTANDO, EXALTANDO O NOSSO REI, O SALVADOR. PELA FÉ EU SINTO NO MEU CORAÇÃO (PELA FÉ POSSO SENTIR) A PRESENÇA DO ESPÍRITO DE DEUS, PELA FÉ EU SINTO SUA MÃO TOCAR EM MIM PELA FÉ POSSO SENTIR SEU GRANDE AMOR. E PELA FÉ VOU CAMINHANDO ATÉ CHEGAR À TERRA PROMETIDA, E PELA FÉ JÁ OUÇO O CANTO DE VITÓRIA: CRISTO REINANDO COM OS FIÉIS NA SUA GLÓRIA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('180', '180 - ENCONTREI O GRANDE AMOR', 'ENCONTREI O GRANDE AMOR, \nAMOR QUE SARA A DOR;\nQUE CURA AS FERIDAS. \nE TRAZ SENTIDO À VIDA;\nAMOR QUE AQUECE A ALMA, \nCONFORTA E ACALMA O CORAÇÃO \nAFLITO E O FAZ ALCANÇAR A PAZ.\n\nAMOR VERDADEIRO ENCONTREI EM TI, JESUS, \nQUE FOI DEMONSTRADO ALI NA CRUZ;\nAMOR LINDO E PURO, AMOR TÃO SINCERO, \nQUE NÃO SE MEDE, NEM SE PODE COMPARAR.\n\nAMOR INCOMPARÁVEL \nQUE FEZ MEU JESUS \nMORRER INOCENTE NAQUELA CRUZ.\nAMOR QUE É MAIS FORTE \nQUE A PRÓPRIA MORTE, \nQUE ME SALVOU;\n\nCOMO AGRADECER ESSE TÃO \nGRANDE AMOR?\nE COMO EXPRESSAR COM \nPALAVRAS, SENHOR,\nTEU SACRIFÍCIO MARCADO \nDE DOR, POR MIM PECADOR?\nBIS', 'ENCONTREI O GRANDE AMOR, AMOR QUE SARA A DOR; QUE CURA AS FERIDAS. E TRAZ SENTIDO À VIDA; AMOR QUE AQUECE A ALMA, CONFORTA E ACALMA O CORAÇÃO AFLITO E O FAZ ALCANÇAR A PAZ. AMOR VERDADEIRO ENCONTREI EM TI, JESUS, QUE FOI DEMONSTRADO ALI NA CRUZ; AMOR LINDO E PURO, AMOR TÃO SINCERO, QUE NÃO SE MEDE, NEM SE PODE COMPARAR. AMOR INCOMPARÁVEL QUE FEZ MEU JESUS MORRER INOCENTE NAQUELA CRUZ. AMOR QUE É MAIS FORTE QUE A PRÓPRIA MORTE, QUE ME SALVOU; COMO AGRADECER ESSE TÃO GRANDE AMOR? E COMO EXPRESSAR COM PALAVRAS, SENHOR, TEU SACRIFÍCIO MARCADO DE DOR, POR MIM PECADOR?', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('181', '181 - EM TI EU ENCONTRO, Ó DEUS, TODA A PAZ', 'EM TI EU ENCONTRO, Ó DEUS, \nTODA A PAZ, E A VIDA QUE EU QUERO VIVER;\nMINHA VIDA TRANSBORDA DE GOZO E DE PAZ, \nJESUS ME TRANSPORTA AO CÉU.\nSENHOR, TE ADORO, ME ACHEGO A TI, \nEM TI ME ALEGRO, JESUS.\n\nCORO\nTU ÉS MINHA PAZ, A FONTE DA VIDA.\nSENHOR, SOU TEU, TU ÉS MINHA VIDA. \n\nINSTRUMENTOS\n\nJESUS ME TRANSPORTA ÀS MANSÕES CELESTIAIS, \nMINHA VIDA TRANSBORDA DE PAZ.\nME FAZ DESCANSAR EM TEU LINDO JARDIM \nENTRE OS LÍRIOS TE ADORO, SENHOR.\n\nPASSEIO ENTRE OS LÍRIOS, \nEM TEU LINDO JARDIM.\nJESUS ME TRANSPORTA AO CÉU;\nRENOVA MINHA FORÇA, \nSE FRACO ESTOU.\nEM TI REJUBILO, SENHOR.\n\nCORO (2X)\nTU ÉS MINHA PAZ, A FONTE DA VIDA.\nSENHOR, SOU TEU, TU ÉS MINHA VIDA.', 'EM TI EU ENCONTRO, Ó DEUS, TODA A PAZ, E A VIDA QUE EU QUERO VIVER; MINHA VIDA TRANSBORDA DE GOZO E DE PAZ, JESUS ME TRANSPORTA AO CÉU. SENHOR, TE ADORO, ME ACHEGO A TI, EM TI ME ALEGRO, JESUS. CORO TU ÉS MINHA PAZ, A FONTE DA VIDA. SENHOR, SOU TEU, TU ÉS MINHA VIDA. INSTRUMENTOS JESUS ME TRANSPORTA ÀS MANSÕES CELESTIAIS, MINHA VIDA TRANSBORDA DE PAZ. ME FAZ DESCANSAR EM TEU LINDO JARDIM ENTRE OS LÍRIOS TE ADORO, SENHOR. PASSEIO ENTRE OS LÍRIOS, EM TEU LINDO JARDIM. JESUS ME TRANSPORTA AO CÉU; RENOVA MINHA FORÇA, SE FRACO ESTOU. EM TI REJUBILO, SENHOR. TU ÉS MINHA PAZ, A FONTE DA VIDA. SENHOR, SOU TEU, TU ÉS MINHA VIDA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('182', '182 - COMO É BOM SERVIR AO SENHOR', 'COMO É BOM SERVIR AO SENHOR,\nOUVIR SUA VOZ A ORIENTAR:\n“VAI, MEU SERVO, FALAR DO MEU AMOR”\nE PODER DIZER DE TODO O CORAÇÃO: \nEIS-ME AQUI.\nBIS\n\nE MELHOR AINDA É SABER\nQUE NÃO VOU SOZINHO.\nO SENHOR VAI ADIANTE, \nME DANDO CONDIÇÃO\nPARA A SUA OBRA REALIZAR.\n\nFINAL: \nPARA A SUA OBRA REALIZAR.\nBIS', 'COMO É BOM SERVIR AO SENHOR, OUVIR SUA VOZ A ORIENTAR: VAI, MEU SERVO, FALAR DO MEU AMOR E PODER DIZER DE TODO O CORAÇÃO: EIS-ME AQUI. E MELHOR AINDA É SABER QUE NÃO VOU SOZINHO. O SENHOR VAI ADIANTE, ME DANDO CONDIÇÃO PARA A SUA OBRA REALIZAR.  PARA A SUA OBRA REALIZAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('183', '183 - A MINHA ALEGRIA', 'A MINHA ALEGRIA É PERTENCER \nAO GRANDE SALVADOR;\nSABER QUE MUITO EM BREVE O VEREI;\nSABER QUE COM OS ANJOS EU O LOUVAREI.\nA MINHA ALEGRIA É SABER \nQUE PARA SEMPRE \nCOM CRISTO VIVEREI.\n\nCORO\nEXISTE UM LUGAR DE PAZ CELESTIAL (CELESTIAL).\nQUANDO OS MEUS PÉS ALI TOCAREM,\nSEI QUE AS LUTAS VÃO CESSAR, POIS ALI \nÉ LUGAR SANTO, MORADA DO SENHOR.\nE AQUELE QUE VENCER DE JESUS IRÁ OUVIR: \n''VINDE, BENDITOS DE MEU PAI''.\n\nA FORÇA PARA PROSSEGUIR \nEU SEMPRE ENCONTRO QUANDO BUSCO A TI;\nSABER QUE ESTÁS COMIGO A ME GUARDAR\nSABER QUE TUDO TU FARÁS PRA ME PRESERVAR.\nA FORÇA PARA PROSSEGUIR É SABER \nQUE PARA SEMPRE CONTIGO VIVEREI.\n\nCORO (2X)\nEXISTE UM LUGAR DE PAZ CELESTIAL (CELESTIAL).\nQUANDO OS MEUS PÉS ALI TOCAREM,\nSEI QUE AS LUTAS VÃO CESSAR, POIS ALI \nÉ LUGAR SANTO, MORADA DO SENHOR.\nE AQUELE QUE VENCER DE JESUS IRÁ OUVIR: \n''VINDE, BENDITOS DE MEU PAI''.', 'A MINHA ALEGRIA É PERTENCER AO GRANDE SALVADOR; SABER QUE MUITO EM BREVE O VEREI; SABER QUE COM OS ANJOS EU O LOUVAREI. A MINHA ALEGRIA É SABER QUE PARA SEMPRE COM CRISTO VIVEREI. CORO EXISTE UM LUGAR DE PAZ CELESTIAL (CELESTIAL). QUANDO OS MEUS PÉS ALI TOCAREM, SEI QUE AS LUTAS VÃO CESSAR, POIS ALI É LUGAR SANTO, MORADA DO SENHOR. E AQUELE QUE VENCER DE JESUS IRÁ OUVIR: VINDE, BENDITOS DE MEU PAI. A FORÇA PARA PROSSEGUIR EU SEMPRE ENCONTRO QUANDO BUSCO A TI; SABER QUE ESTÁS COMIGO A ME GUARDAR SABER QUE TUDO TU FARÁS PRA ME PRESERVAR. A FORÇA PARA PROSSEGUIR É SABER QUE PARA SEMPRE CONTIGO VIVEREI. EXISTE UM LUGAR DE PAZ CELESTIAL (CELESTIAL). QUANDO OS MEUS PÉS ALI TOCAREM, SEI QUE AS LUTAS VÃO CESSAR, POIS ALI É LUGAR SANTO, MORADA DO SENHOR. E AQUELE QUE VENCER DE JESUS IRÁ OUVIR: VINDE, BENDITOS DE MEU PAI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('184', '184 - REDENTOR ONIPOTENTE', 'REDENTOR ONIPOTENTE,\nPODEROSO SALVADOR,\nREI DOS REIS, ONIPRESENTE\nÉ JESUS, MEU BOM SENHOR.\n\nCORO\nOH! REFÚGIO DA MINH''ALMA,\nTU ÉS TUDO PARA MIM!\nTUDO QUANTO EU CAREÇO,\nACHO, JESUS, SÓ EM TI.\n\nÁGUA VIVA, PÃO DA VIDA!\nDOCE SOMBRA NO CALOR.\nE AO DESCANSO NOS CONVIDA\nÉ JESUS, MEU SALVADOR.\n\nCORO\nOH! REFÚGIO DA MINH''ALMA,\nTU ÉS TUDO PARA MIM!\nTUDO QUANTO EU CAREÇO,\nACHO, JESUS, SÓ EM TI.\n\nO CORDEIRO IMACULADO,\nQUE SEU SANGUE DERRAMOU,\nMEUS PECADOS EXPIANDO,\nA MINH''ALMA RESGATOU.\n\nCORO\nOH! REFÚGIO DA MINH''ALMA,\nTU ÉS TUDO PARA MIM!\nTUDO QUANTO EU CAREÇO,\nACHO, JESUS, SÓ EM TI.\n\nO CAMINHO QUE, SEGURO,\nSEMPRE PARA O CÉU CONDUZ\nQUEM A CRISTO PRONTO SEGUE,\nQUEM TOMAR A SUA CRUZ.\n\nCORO\nOH! REFÚGIO DA MINH''ALMA,\nTU ÉS TUDO PARA MIM!\nTUDO QUANTO EU CAREÇO,\nACHO, JESUS, SÓ EM TI.', 'REDENTOR ONIPOTENTE, PODEROSO SALVADOR, REI DOS REIS, ONIPRESENTE É JESUS, MEU BOM SENHOR. CORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. ÁGUA VIVA, PÃO DA VIDA! DOCE SOMBRA NO CALOR. E AO DESCANSO NOS CONVIDA É JESUS, MEU SALVADOR. CORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. O CORDEIRO IMACULADO, QUE SEU SANGUE DERRAMOU, MEUS PECADOS EXPIANDO, A MINH''ALMA RESGATOU. CORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. O CAMINHO QUE, SEGURO, SEMPRE PARA O CÉU CONDUZ QUEM A CRISTO PRONTO SEGUE, QUEM TOMAR A SUA CRUZ. CORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('185', '185 - JESUS, SENHOR, ME CHEGO A TI  (EU VENHO COMO ESTOU)', 'JESUS, SENHOR, ME CHEGO A TI,\nPERDÃO SUPLICO AGORA A TI,\nOH! DÁ-ME ALÍVIO MESMO AQUI,\nACEITA UM PECADOR!\n\nCORO\nEU VENHO COMO ESTOU;\nEU VENHO COMO ESTOU;\nPORQUE JESUS POR MIM MORREU;\nEU VENHO COMO ESTOU.\n\nEU NADA POSSO MERECER,\nTU VÊS-ME PRESTES A MORRER,\nOH! NÃO ME DEIXES PERECER,\nACEITA UM PECADOR!\n\nCORO\nEU VENHO COMO ESTOU;\nEU VENHO COMO ESTOU;\nPORQUE JESUS POR MIM MORREU;\nEU VENHO COMO ESTOU.\n\nOH! SEM DEMORA, SALVADOR,\nSOCORRE-ME POR TEU AMOR,\nPOIS TU, Ó CRISTO, ÉS MEU SENHOR,\nACEITA UM PECADOR!\n\nCORO\nEU VENHO COMO ESTOU;\nEU VENHO COMO ESTOU;\nPORQUE JESUS POR MIM MORREU;\nEU VENHO COMO ESTOU.\n\nNADA DE BOM SE ACHA EM MIM,\nDE MEUS ESFORÇOS BREVE HÁ FIM;\nSE NÃO ME ACEITAS, AI DE MIM,\nACEITA UM PECADOR!\n\nCORO\nEU VENHO COMO ESTOU;\nEU VENHO COMO ESTOU;\nPORQUE JESUS POR MIM MORREU;\nEU VENHO COMO ESTOU.', 'JESUS, SENHOR, ME CHEGO A TI, PERDÃO SUPLICO AGORA A TI, OH! DÁ-ME ALÍVIO MESMO AQUI, ACEITA UM PECADOR! CORO EU VENHO COMO ESTOU; EU VENHO COMO ESTOU; PORQUE JESUS POR MIM MORREU; EU VENHO COMO ESTOU. EU NADA POSSO MERECER, TU VÊS-ME PRESTES A MORRER, OH! NÃO ME DEIXES PERECER, ACEITA UM PECADOR! CORO EU VENHO COMO ESTOU; EU VENHO COMO ESTOU; PORQUE JESUS POR MIM MORREU; EU VENHO COMO ESTOU. OH! SEM DEMORA, SALVADOR, SOCORRE-ME POR TEU AMOR, POIS TU, Ó CRISTO, ÉS MEU SENHOR, ACEITA UM PECADOR! CORO EU VENHO COMO ESTOU; EU VENHO COMO ESTOU; PORQUE JESUS POR MIM MORREU; EU VENHO COMO ESTOU. NADA DE BOM SE ACHA EM MIM, DE MEUS ESFORÇOS BREVE HÁ FIM; SE NÃO ME ACEITAS, AI DE MIM, ACEITA UM PECADOR! CORO EU VENHO COMO ESTOU; EU VENHO COMO ESTOU; PORQUE JESUS POR MIM MORREU; EU VENHO COMO ESTOU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('186', '186 - PARA QUEM IREMOS NÓS?', 'PARA QUEM IREMOS NÓS?\nSÓ TU, SENHOR, \nTENS AS PALAVRAS DE VIDA ETERNA.\n\nNÃO HÁ OUTRO AMOR MAIOR\nQUE O TEU AMOR, JESUS,\nDEMONSTRADO ALI NA CRUZ\nPARA NOS SALVAR.\n\nINSTRUMENTOS\nBIS\n\nMEU CORAÇÃO A TI ENTREGO,\nA MINHA VIDA \nDEPOSITO EM TEU ALTAR.\n\nTODA  A MINHA ADORAÇÃO\nQUERO TE OFERTAR,\nEM SACRIFÍCIO (EM SACRIFÍCIO) \nVIVO EM TEU ALTAR (EM TEU ALTAR)\n\nEM SACRIFÍCIO (EM SACRIFÍCIO)\nVIVO EM TEU ALTAR.\nBIS', 'PARA QUEM IREMOS NÓS? SÓ TU, SENHOR, TENS AS PALAVRAS DE VIDA ETERNA. NÃO HÁ OUTRO AMOR MAIOR QUE O TEU AMOR, JESUS, DEMONSTRADO ALI NA CRUZ PARA NOS SALVAR. INSTRUMENTOS MEU CORAÇÃO A TI ENTREGO, A MINHA VIDA DEPOSITO EM TEU ALTAR. TODA A MINHA ADORAÇÃO QUERO TE OFERTAR, EM SACRIFÍCIO (EM SACRIFÍCIO) VIVO EM TEU ALTAR (EM TEU ALTAR) EM SACRIFÍCIO (EM SACRIFÍCIO) VIVO EM TEU ALTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('187', '187 - MINH´ALMA TEM SEDE DE TI', 'MINH’ALMA TEM SEDE DE TI,\nTE QUERO COM TODO O MEU SER!\nNUMA TERRA SECA E SEM PAZ,\nMINHA VOZ CLAMA POR DEUS,\nSÓ TUA PRESENÇA, SENHOR,\nME SATISFAZ!\n\nPOR ISSO VENHO, SENHOR,\nAO TEU SANTUÁRIO\nTUA GLÓRIA CONTEMPLAR!\nTE DOU MEU LOUVOR,\nTE DOU MEU AMOR,\nMINHA VIDA PONHO EM TEU ALTAR,\nEU TE AMO, MEU SENHOR!\n\nFINAL: \nSENHOR, EU VENHO TE ADORAR!\nBIS', 'MINH’ALMA TEM SEDE DE TI, TE QUERO COM TODO O MEU SER! NUMA TERRA SECA E SEM PAZ, MINHA VOZ CLAMA POR DEUS, SÓ TUA PRESENÇA, SENHOR, ME SATISFAZ! POR ISSO VENHO, SENHOR, AO TEU SANTUÁRIO TUA GLÓRIA CONTEMPLAR! TE DOU MEU LOUVOR, TE DOU MEU AMOR, MINHA VIDA PONHO EM TEU ALTAR, EU TE AMO, MEU SENHOR!  SENHOR, EU VENHO TE ADORAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('188', '188 - VENHO, SENHOR, MINHA VIDA OFERECER', 'VENHO, SENHOR, \nMINHA VIDA OFERECER\nCOMO OFERTA DE AMOR \nE SACRIFÍCIO;\nQUERO MINHA VIDA A TI ENTREGAR\nCOMO OFERTA VIVA \nEM TEU ALTAR.\nBIS\n\nPOIS, PRA TE ADORAR FOI QUE EU NASCI;\nCUMPRE EM MIM O TEU QUERER,\nFAÇA O QUE ESTÁ EM TEU CORAÇÃO,\nE QUE A CADA DIA EU QUEIRA MAIS \nE MAIS ESTAR AO TEU LADO, SENHOR\n\nE QUE A CADA DIA\nEU QUEIRA MAIS E MAIS\nESTAR AO TEU LADO, SENHOR.\n(ESTAR AO TEU LADO) SENHOR.\nBIS', 'VENHO, SENHOR, MINHA VIDA OFERECER COMO OFERTA DE AMOR E SACRIFÍCIO; QUERO MINHA VIDA A TI ENTREGAR COMO OFERTA VIVA EM TEU ALTAR. POIS, PRA TE ADORAR FOI QUE EU NASCI; CUMPRE EM MIM O TEU QUERER, FAÇA O QUE ESTÁ EM TEU CORAÇÃO, E QUE A CADA DIA EU QUEIRA MAIS E MAIS ESTAR AO TEU LADO, SENHOR E QUE A CADA DIA EU QUEIRA MAIS E MAIS ESTAR AO TEU LADO, SENHOR. (ESTAR AO TEU LADO) SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('189', '189 - DEPENDO DE TI, SENHOR', 'DEPENDO DE TI, SENHOR,\nQUE SEJAS ASSIM EM MEU VIVER:\nTOMA O MEU CORAÇÃO,\nTOTALMENTE DOMINA, Ó SENHOR!\n\nNÃO QUERO ANDAR INDEPENDENTE, \nSENHOR, POIS TUA VONTADE \nÉ VIDA PARA O MEU CORAÇÃO.\n\nDEPENDO DE TI, SENHOR,\nNÃO POSSO ANDAR SEM TEU BRAÇO FORTE;\nSEM TUA VONTADE HAVERÁ AGONIA,\nTRISTEZA E MORTE.\n\nFINAL: (2X)\nDEPENDO DE TI, SENHOR,\nDEPENDO DE TI,\nDEPENDO DE TI, AMÉM.', 'DEPENDO DE TI, SENHOR, QUE SEJAS ASSIM EM MEU VIVER: TOMA O MEU CORAÇÃO, TOTALMENTE DOMINA, Ó SENHOR! NÃO QUERO ANDAR INDEPENDENTE, SENHOR, POIS TUA VONTADE É VIDA PARA O MEU CORAÇÃO. DEPENDO DE TI, SENHOR, NÃO POSSO ANDAR SEM TEU BRAÇO FORTE; SEM TUA VONTADE HAVERÁ AGONIA, TRISTEZA E MORTE.   DEPENDO DE TI, SENHOR, DEPENDO DE TI, DEPENDO DE TI, AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('190', '190 - TODO O MEU SER TE AMA', 'TODO O MEU SER TE AMA\nE MINH’ALMA TE ADORA,\nTU ÉS MEU PASTOR, SALVADOR, \nÉS O MEU VIVER\nE TE AMO MUITO \nMAIS QUE POSSA DIZER.\nBIS\n\nTU ÉS REI ACIMA DE TODOS OS REIS,\nTUA MORADA É ETERNA, SENHOR;\nTU HABITAS DENTRO EM MIM,\nBEM MAIOR NÃO POSSO TER\nPOIS TU ÉS SANTO, ÉS PODEROSO,\nTU ÉS JESUS, SENHOR E REI.\n\nTODO O MEU SER TE AMA E MINH’ALMA \nTE ADORA, TU ÉS MEU PASTOR SALVADOR, \nÉS O MEU VIVER\nE TE AMO MUITO MAIS QUE POSSA DIZER.', 'TODO O MEU SER TE AMA E MINH’ALMA TE ADORA, TU ÉS MEU PASTOR, SALVADOR, ÉS O MEU VIVER E TE AMO MUITO MAIS QUE POSSA DIZER. TU ÉS REI ACIMA DE TODOS OS REIS, TUA MORADA É ETERNA, SENHOR; TU HABITAS DENTRO EM MIM, BEM MAIOR NÃO POSSO TER POIS TU ÉS SANTO, ÉS PODEROSO, TU ÉS JESUS, SENHOR E REI. TODO O MEU SER TE AMA E MINH’ALMA TE ADORA, TU ÉS MEU PASTOR SALVADOR, ÉS O MEU VIVER E TE AMO MUITO MAIS QUE POSSA DIZER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('191', '191 - EU QUERO SENTIR', 'EU QUERO SENTIR MAIS UMA VEZ\nO AMOR DE DEUS AGINDO EM MINHA VIDA.\nVIM PRA DIZER: ME ARREPENDI,\nME PERDOA, SÓ AO TEU LADO \nEU SEI QUE POSSO SER FELIZ.\n\nME LEMBRO DE QUANDO OUVI A TUA VOZ\n(OUVI A TUA VOZ) \nFALANDO AO MEU CORAÇÃO \nPELA PRIMEIRA VEZ, FOI TÃO FORTE \n(FOI TÃO FORTE) \nMARAVILHOSO, SIM! QUEM UM DIA \nO CONHECEU NUNCA \nPODE SE ESQUECER.\n\nCORO\nVOLTEI PRA TI MEU PAI PROS BRAÇOS TEUS\nCOM O CORAÇÃO CARENTE E FERIDO;\nEU RECONHEÇO QUE SEM TI NADA SOU.\n\nINSTRUMENTOS\n\nME LEMBRO DE QUANDO OUVI A TUA VOZ\n(OUVI A TUA VOZ) \nFALANDO AO MEU CORAÇÃO \nPELA PRIMEIRA VEZ, FOI TÃO FORTE \n(FOI TÃO FORTE) \nMARAVILHOSO, SIM! QUEM UM DIA \nO CONHECEU NUNCA \nPODE SE ESQUECER.\n\nCORO\nVOLTEI PRA TI MEU PAI PROS BRAÇOS TEUS\nCOM O CORAÇÃO CARENTE E FERIDO;\nEU RECONHEÇO QUE SEM TI NADA SOU.', 'EU QUERO SENTIR MAIS UMA VEZ O AMOR DE DEUS AGINDO EM MINHA VIDA. VIM PRA DIZER: ME ARREPENDI, ME PERDOA, SÓ AO TEU LADO EU SEI QUE POSSO SER FELIZ. ME LEMBRO DE QUANDO OUVI A TUA VOZ (OUVI A TUA VOZ) FALANDO AO MEU CORAÇÃO PELA PRIMEIRA VEZ, FOI TÃO FORTE (FOI TÃO FORTE) MARAVILHOSO, SIM! QUEM UM DIA O CONHECEU NUNCA PODE SE ESQUECER. CORO VOLTEI PRA TI MEU PAI PROS BRAÇOS TEUS COM O CORAÇÃO CARENTE E FERIDO; EU RECONHEÇO QUE SEM TI NADA SOU. INSTRUMENTOS CORO VOLTEI PRA TI MEU PAI PROS BRAÇOS TEUS COM O CORAÇÃO CARENTE E FERIDO; EU RECONHEÇO QUE SEM TI NADA SOU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('192', '192 - JESUS, JESUS, EU TE AMO', 'JESUS, JESUS, EU TE AMO,\nÓ MEU SALVADOR;\nEU TE QUERO, EU TE ESPERO, \nSENHOR, DEUS DE AMOR!\nBIS', 'JESUS, JESUS, EU TE AMO, Ó MEU SALVADOR; EU TE QUERO, EU TE ESPERO, SENHOR, DEUS DE AMOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('193', '193 - AS MUITAS ÁGUAS NÃO PODERIAM APAGAR', 'AS MUITAS ÁGUAS NÃO PODERIAM \nAPAGAR ESTE AMOR,\nNEM OS RIOS O AFOGARÃO;\nAINDA QUE ALGUÉM \nDESSE OS BENS DE SUA CASA,\nPOR ESTE AMOR, \nOS DESPREZARIA.\n\nCORO (2X)\nE É ASSIM O AMOR DE DEUS,\nINDESCRITÍVEL, INSONDÁVEL \nÉ ESTE AMOR.\n\nINSTRUMENTOS\n\nAS MUITAS ÁGUAS NÃO PODERIAM \nAPAGAR ESTE AMOR,\nNEM OS RIOS O AFOGARÃO;\nAINDA QUE ALGUÉM \nDESSE OS BENS DE SUA CASA,\nPOR ESTE AMOR, \nOS DESPREZARIA.\n\nCORO (2X)\nE É ASSIM O AMOR DE DEUS,\nINDESCRITÍVEL, INSONDÁVEL \nÉ ESTE AMOR.\n\nFINAL: DO MEU JESUS.', 'AS MUITAS ÁGUAS NÃO PODERIAM APAGAR ESTE AMOR, NEM OS RIOS O AFOGARÃO; AINDA QUE ALGUÉM DESSE OS BENS DE SUA CASA, POR ESTE AMOR, OS DESPREZARIA. E É ASSIM O AMOR DE DEUS, INDESCRITÍVEL, INSONDÁVEL É ESTE AMOR. INSTRUMENTOS E É ASSIM O AMOR DE DEUS, INDESCRITÍVEL, INSONDÁVEL É ESTE AMOR.  DO MEU JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('194', '194 - QUERO ANDAR EM TEU CAMINHO, SENHOR', 'QUERO ANDAR EM TEU CAMINHO, \nSENHOR, MAIS E MAIS TE CONHECER\nE FAZER TUA VONTADE, SENHOR;\nTUA VOZ OBEDECER E VIVER SOMENTE \nPRA TEU LOUVOR, DESFRUTANDO \nDO TEU GRANDE AMOR;\nQUERO SER FIEL, QUERO SER FIEL\nA TI, SENHOR.\n\nCORO\nTE AGRADEÇO POR PRIMEIRO ME AMAR,\nTE AGRADEÇO POR ME ESCOLHER,\nTE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) \nTE AGRADEÇO, POIS NÃO SOU MERECEDOR.\n\nFAZE EM MIM (FAZE EM MIM) \nO TEU QUERER, Ó SENHOR (TEU QUERER) \nQUERO SER UM VENCEDOR (VENCEDOR) \nPRA QUE EU POSSA DIZER (PRA QUE EU POSSA DIZER) \nQUE TU VIVES EM MIM\nE DA MINHA VIDA TU ÉS O SENHOR.\n\nCORO\nTE AGRADEÇO POR PRIMEIRO ME AMAR,\nTE AGRADEÇO POR ME ESCOLHER,\nTE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) \nTE AGRADEÇO, POIS NÃO SOU MERECEDOR.\n\nFINAL: \nTE AGRADEÇO \n(TE AGRADEÇO, MEU SENHOR) \nTE AGRADEÇO, POIS NÃO SOU MERECEDOR.', 'QUERO ANDAR EM TEU CAMINHO, SENHOR, MAIS E MAIS TE CONHECER E FAZER TUA VONTADE, SENHOR; TUA VOZ OBEDECER E VIVER SOMENTE PRA TEU LOUVOR, DESFRUTANDO DO TEU GRANDE AMOR; QUERO SER FIEL, QUERO SER FIEL A TI, SENHOR. CORO TE AGRADEÇO POR PRIMEIRO ME AMAR, TE AGRADEÇO POR ME ESCOLHER, TE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) TE AGRADEÇO, POIS NÃO SOU MERECEDOR. FAZE EM MIM (FAZE EM MIM) O TEU QUERER, Ó SENHOR (TEU QUERER) QUERO SER UM VENCEDOR (VENCEDOR) PRA QUE EU POSSA DIZER (PRA QUE EU POSSA DIZER) QUE TU VIVES EM MIM E DA MINHA VIDA TU ÉS O SENHOR. TE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) TE AGRADEÇO, POIS NÃO SOU MERECEDOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('195', '195 - GRANDE AMOR, Ó GRANDE AMOR', 'GRANDE AMOR, Ó GRANDE AMOR,\nÉ O AMOR DE DEUS.\nESTE AMOR QUE HABITA EM MIM,\nPELA FÉ EM DEUS.\nCOMO POSSO COMPREENDER\nA GRANDEZA DO AMOR DE DEUS.\nQUE PROFUNDO É ESSE GRANDE AMOR\nQUE PREENCHE TODO MEU SER.\n\nCORO\nGRANDE AMOR, É O AMOR DE DEUS\nQUE EXCEDE TODO SABER\nOH! QUE SUBLIME!\nNÃO POSSO COMPREENDER,\nPORQUE ME AMOU SEM MERECER.\n\nQUANDO TUDO PARECE ACABAR\nE A ESPERANÇA DESVANECER.\nQUANDO A FÉ PARECE NAUFRAGAR\nE O DIA ESCURECER.\nMESMO ASSIM CONFIO NESTE AMOR,\nQUE É MAIS FORTE QUE A PRÓPRIA MORTE.\nAS MUITAS ÁGUAS NÃO PODEM APAGAR,\nNEM OS RIOS O AFOGARÃO.\n\nCORO\nGRANDE AMOR, É O AMOR DE DEUS\nQUE EXCEDE TODO SABER\nOH! QUE SUBLIME!\nNÃO POSSO COMPREENDER,\nPORQUE ME AMOU SEM MERECER.\n\nFINAL: GRANDE AMOR DE DEUS.', 'GRANDE AMOR, Ó GRANDE AMOR, É O AMOR DE DEUS. ESTE AMOR QUE HABITA EM MIM, PELA FÉ EM DEUS. COMO POSSO COMPREENDER A GRANDEZA DO AMOR DE DEUS. QUE PROFUNDO É ESSE GRANDE AMOR QUE PREENCHE TODO MEU SER. CORO GRANDE AMOR, É O AMOR DE DEUS QUE EXCEDE TODO SABER OH! QUE SUBLIME! NÃO POSSO COMPREENDER, PORQUE ME AMOU SEM MERECER. QUANDO TUDO PARECE ACABAR E A ESPERANÇA DESVANECER. QUANDO A FÉ PARECE NAUFRAGAR E O DIA ESCURECER. MESMO ASSIM CONFIO NESTE AMOR, QUE É MAIS FORTE QUE A PRÓPRIA MORTE. AS MUITAS ÁGUAS NÃO PODEM APAGAR, NEM OS RIOS O AFOGARÃO. CORO GRANDE AMOR, É O AMOR DE DEUS QUE EXCEDE TODO SABER OH! QUE SUBLIME! NÃO POSSO COMPREENDER, PORQUE ME AMOU SEM MERECER.  GRANDE AMOR DE DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('196', '196 - O GRANDE AMOR DO MEU SENHOR', 'O GRANDE AMOR DO MEU SENHOR (M)\nO REI DE TODO UNIVERSO! (H)\nJESUS, O SOBERANO DEUS,\nRESPLANDECENTE ESTRELA DA MANHÃ!\n\nCOMO ENTENDER QUE MEU JESUS,\nUM DIA OLHOU PRA MIM,\nE SEM QUERER NADA EM TROCA,\nTÃO GRANDE AMOR ME DEU, \nO MEU SENHOR VIDA ETERNA DEU, \nMEU SALVADOR!\n\nCORO\nABENÇOADOR, DEUS DE PAZ,\nCONSOLADOR, MEU REI E SENHOR.\nHOMEM SE FEZ, MORREU EM MEU LUGAR\nMEU REDENTOR, MINHA ESPERANÇA.\nMINHA ESPERANÇA É!\n\nCOMO ENTENDER QUE MEU JESUS\nUM DIA OLHOU PRA MIM, \nE SEM QUERER NADA EM TROCA, \nTÃO GRANDE AMOR ME DEU O MEU SENHOR!\nVIDA ETERNA DEU, MEU SALVADOR.\n\nCORO\nABENÇOADOR, DEUS DE PAZ,\nCONSOLADOR, MEU REI E SENHOR.\nHOMEM SE FEZ, MORREU EM MEU LUGAR\nMEU REDENTOR, MINHA ESPERANÇA.\nMINHA ESPERANÇA É!\n\nA ROCHA FIRME, (A ROCHA FIRME)\nREFÚGIO SEGURO, (REFÚGIO SEGURO)\nA MINHA ALEGRIA, MEU LOUVOR\nÉ SÓ PRA TI MEU GRANDE EU SOU!\n\nCORO\nABENÇOADOR, DEUS DE PAZ,\nCONSOLADOR, MEU REI E SENHOR.\nHOMEM SE FEZ, MORREU EM MEU LUGAR\nMEU REDENTOR, MINHA ESPERANÇA.\nMINHA ESPERANÇA É!\n\nFINAL:\nO REI DO UNIVERSO,\n(UM DIA OLHOU PRA MIM)\nO MEU GRANDE EU SOU, JESUS.', 'O GRANDE AMOR DO MEU SENHOR  O REI DE TODO UNIVERSO!  JESUS, O SOBERANO DEUS, RESPLANDECENTE ESTRELA DA MANHÃ! COMO ENTENDER QUE MEU JESUS, UM DIA OLHOU PRA MIM, E SEM QUERER NADA EM TROCA, TÃO GRANDE AMOR ME DEU, O MEU SENHOR VIDA ETERNA DEU, MEU SALVADOR! CORO ABENÇOADOR, DEUS DE PAZ, CONSOLADOR, MEU REI E SENHOR. HOMEM SE FEZ, MORREU EM MEU LUGAR MEU REDENTOR, MINHA ESPERANÇA. MINHA ESPERANÇA É! COMO ENTENDER QUE MEU JESUS UM DIA OLHOU PRA MIM, E SEM QUERER NADA EM TROCA, TÃO GRANDE AMOR ME DEU O MEU SENHOR! VIDA ETERNA DEU, MEU SALVADOR. A ROCHA FIRME, (A ROCHA FIRME) REFÚGIO SEGURO, (REFÚGIO SEGURO) A MINHA ALEGRIA, MEU LOUVOR É SÓ PRA TI MEU GRANDE EU SOU! O REI DO UNIVERSO, (UM DIA OLHOU PRA MIM) O MEU GRANDE EU SOU, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('197', '197 - HOJE QUERO SOMENTE AGRADECER   (SOMENTE AGRADECER)', 'HOJE QUERO SOMENTE AGRADECER A TI.\nHOJE NADA QUERO TE PEDIR.\nRECEBE O MEU LOUVOR, MINHA GRATIDÃO.\n\nESTOU AQUI, PAI (SÓ TU ME CONHECES).\nQUERO SERVIR-TE MAIS E MAIS.\nESTOU AQUI, PAI (SÓ TU ME CONHECES).\nQUERO SERVIR-TE MAIS E MAIS.\nGUIA-ME, SENHOR (GUIA-ME, SENHOR)\nGUIA-ME, SENHOR (GUIA-ME, SENHOR)\n\nHOJE QUERO SOMENTE AGRADECER A TI.\nHOJE NADA QUERO TE PEDIR.\nRECEBE O MEU LOUVOR, MINHA GRATIDÃO.\n\nESTOU AQUI, PAI (SÓ TU ME CONHECES).\nQUERO SERVIR-TE MAIS E MAIS.\nESTOU AQUI, PAI (SÓ TU ME CONHECES).\nQUERO SERVIR-TE MAIS E MAIS.\nGUIA-ME, SENHOR (GUIA-ME, SENHOR)\nGUIA-ME, SENHOR (GUIA-ME, SENHOR)\n\nPELA FÉ EU VOU SEGURO EM TUAS MÃOS,\nPELA FÉ ALCANÇAREI A SALVAÇÃO.\nMOSTRA-ME A DIREÇÃO.\n\nESTOU AQUI, PAI (SÓ TU ME CONHECES).\nQUERO SERVIR-TE MAIS E MAIS\nPRECISO DE TI, PAI (DIRIGE MEUS PASSOS).\nESTE MOMENTO É PRA TUA ADORAÇÃO\n\nFINAL: TUA ADORAÇÃO.', 'HOJE QUERO SOMENTE AGRADECER A TI. HOJE NADA QUERO TE PEDIR. RECEBE O MEU LOUVOR, MINHA GRATIDÃO. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS. GUIA-ME, SENHOR (GUIA-ME, SENHOR) GUIA-ME, SENHOR (GUIA-ME, SENHOR) PELA FÉ EU VOU SEGURO EM TUAS MÃOS, PELA FÉ ALCANÇAREI A SALVAÇÃO. MOSTRA-ME A DIREÇÃO. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS PRECISO DE TI, PAI (DIRIGE MEUS PASSOS). ESTE MOMENTO É PRA TUA ADORAÇÃO  TUA ADORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('198', '198 - ELE É A MINHA ALEGRIA', 'HOJE QUERO SOMENTE AGRADECER A TI.\nHOJE NADA QUERO TE PEDIR.\nRECEBE O MEU LOUVOR, MINHA GRATIDÃO.\n\n197 - HOJE QUERO SOMENTE AGRADECER  \n(SOMENTE AGRADECER)\n\nELE É A MINHA ALEGRIA,\nMESMO EM MOMENTOS DIFÍCEIS,\nELE SEGURA MINHA MÃO.\nELE É A MINHA SEGURANÇA,\nMESMO EM TEMPOS DE GUERRA,\nELE É QUEM ME DÁ A PAZ.\n\nSENHOR, TU ÉS A MINHA FORTALEZA.\nJESUS, TU ÉS A MINHA DEFESA,\nÉS MEU ESCUDO E PROTEÇÃO.\nSENHOR, TU ÉS A ROCHA FERIDA,\nTU ÉS A FONTE DA VIDA,\nBROTANDO ÁGUAS DA SALVAÇÃO.\nSENHOR, TU ÉS O MEU ALIMENTO,\nDE TI PROVÉM TODO SUSTENTO,\nÉS MINHA ÁGUA, ÉS MEU PÃO.\n\nINSTRUMENTOS\n\nELE É A MINHA ALEGRIA,\nMESMO EM MOMENTOS DIFÍCEIS,\nELE SEGURA MINHA MÃO.\nELE É A MINHA SEGURANÇA,\nMESMO EM TEMPOS DE GUERRA,\nELE É QUEM ME DÁ A PAZ.\n\nSENHOR, TU ÉS A MINHA FORTALEZA.\nJESUS, TU ÉS A MINHA DEFESA,\nÉS MEU ESCUDO E PROTEÇÃO.\nSENHOR, TU ÉS A ROCHA FERIDA,\nTU ÉS A FONTE DA VIDA,\nBROTANDO ÁGUAS DA SALVAÇÃO.\nSENHOR, TU ÉS O MEU ALIMENTO,\nDE TI PROVÉM TODO SUSTENTO,\nÉS MINHA ÁGUA, ÉS MEU PÃO.\n\nFINAL: \nSÓ TU ÉS, SENHOR, A VIDA DO TEU POVO.\nEM MEIO A TREVAS, A LUZ,\nÉS O CAMINHO, JESUS (BIS)', 'HOJE QUERO SOMENTE AGRADECER A TI. HOJE NADA QUERO TE PEDIR. RECEBE O MEU LOUVOR, MINHA GRATIDÃO. 197 - HOJE QUERO SOMENTE AGRADECER (SOMENTE AGRADECER) ELE É A MINHA ALEGRIA, MESMO EM MOMENTOS DIFÍCEIS, ELE SEGURA MINHA MÃO. ELE É A MINHA SEGURANÇA, MESMO EM TEMPOS DE GUERRA, ELE É QUEM ME DÁ A PAZ. SENHOR, TU ÉS A MINHA FORTALEZA. JESUS, TU ÉS A MINHA DEFESA, ÉS MEU ESCUDO E PROTEÇÃO. SENHOR, TU ÉS A ROCHA FERIDA, TU ÉS A FONTE DA VIDA, BROTANDO ÁGUAS DA SALVAÇÃO. SENHOR, TU ÉS O MEU ALIMENTO, DE TI PROVÉM TODO SUSTENTO, ÉS MINHA ÁGUA, ÉS MEU PÃO. INSTRUMENTOS SENHOR, TU ÉS A MINHA FORTALEZA. JESUS, TU ÉS A MINHA DEFESA, ÉS MEU ESCUDO E PROTEÇÃO. SENHOR, TU ÉS A ROCHA FERIDA, TU ÉS A FONTE DA VIDA, BROTANDO ÁGUAS DA SALVAÇÃO. SENHOR, TU ÉS O MEU ALIMENTO, DE TI PROVÉM TODO SUSTENTO, ÉS MINHA ÁGUA, ÉS MEU PÃO. SÓ TU ÉS, SENHOR, A VIDA DO TEU POVO. EM MEIO A TREVAS, A LUZ, ÉS O CAMINHO, JESUS', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('199', '199 - COMO É BOM OUVIR TUA VOZ', 'COMO É BOM OUVIR TUA VOZ, SENHOR, \nCOMO É BOM SENTIR TUA PRESENÇA. \nO TEU AMOR NOS CONSOLA, \nNOS RENOVA A CADA DIA, \nSENHOR, GLORIOSO ÉS TU!\nBIS\n\nCORO\nNÓS TE LOUVAMOS, SENHOR JESUS,\nPOR TEU AMOR DEMONSTRADO NA CRUZ.\nAMOR GRANDIOSO, PODEROSO,\nE QUE NOS DARÁ A VIDA ETERNA.\n\nINSTRUMENTOS\n\nCORO\nNÓS TE LOUVAMOS, SENHOR JESUS,\nPOR TEU AMOR DEMONSTRADO NA CRUZ.\nAMOR GRANDIOSO, PODEROSO,\nE QUE NOS DARÁ A VIDA ETERNA.\n\nFINAL: VIDA ETERNA.\nBIS', 'COMO É BOM OUVIR TUA VOZ, SENHOR, COMO É BOM SENTIR TUA PRESENÇA. O TEU AMOR NOS CONSOLA, NOS RENOVA A CADA DIA, SENHOR, GLORIOSO ÉS TU! CORO NÓS TE LOUVAMOS, SENHOR JESUS, POR TEU AMOR DEMONSTRADO NA CRUZ. AMOR GRANDIOSO, PODEROSO, E QUE NOS DARÁ A VIDA ETERNA. INSTRUMENTOS CORO NÓS TE LOUVAMOS, SENHOR JESUS, POR TEU AMOR DEMONSTRADO NA CRUZ. AMOR GRANDIOSO, PODEROSO, E QUE NOS DARÁ A VIDA ETERNA.  VIDA ETERNA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('200', '200 - EU SIRVO UM DEUS', 'EU SIRVO UM DEUS QUE É TÃO BOM PRA MIM,\nEU SIRVO UM DEUS QUE É MEU TUDO ENFIM.\nEU SIRVO UM DEUS QUE AO MEU LADO ESTÁ,\nEU SIRVO UM DEUS QUE AO CÉU ME LEVARÁ.\n\nCORO\nOH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO,\nQUE NOS LIVRA DOS PERIGOS,E NOS FAZ ASSIM CANTAR. OH! ALELUIA, \nNÓS SERVIMOS A UM DEUS VIVO, ELE É REI, ELE É AMIGO, COMO NOSSO DEUS NÃO HÁ.\n\nEU SIRVO UM DEUS QUE ABRIU O MAR VERMELHO,\nEU SIRVO UM DEUS QUE AMOU O MUNDO INTEIRO.\nEU SIRVO UM DEUS QUE ENVIOU JESUS,\nEU SIRVO UM DEUS QUE É GRAÇA E LUZ.\n\nCORO\nOH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO,\nQUE NOS LIVRA DOS PERIGOS,E NOS FAZ ASSIM CANTAR. OH! ALELUIA, \nNÓS SERVIMOS A UM DEUS VIVO, ELE É REI, ELE É AMIGO, COMO NOSSO DEUS NÃO HÁ.\n\nFINAL: NÃO HÁ, NÃO HÁ.', 'EU SIRVO UM DEUS QUE É TÃO BOM PRA MIM, EU SIRVO UM DEUS QUE É MEU TUDO ENFIM. EU SIRVO UM DEUS QUE AO MEU LADO ESTÁ, EU SIRVO UM DEUS QUE AO CÉU ME LEVARÁ. CORO OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, QUE NOS LIVRA DOS PERIGOS,E NOS FAZ ASSIM CANTAR. OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, ELE É REI, ELE É AMIGO, COMO NOSSO DEUS NÃO HÁ. EU SIRVO UM DEUS QUE ABRIU O MAR VERMELHO, EU SIRVO UM DEUS QUE AMOU O MUNDO INTEIRO. EU SIRVO UM DEUS QUE ENVIOU JESUS, EU SIRVO UM DEUS QUE É GRAÇA E LUZ. CORO OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, QUE NOS LIVRA DOS PERIGOS,E NOS FAZ ASSIM CANTAR. OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, ELE É REI, ELE É AMIGO, COMO NOSSO DEUS NÃO HÁ.  NÃO HÁ, NÃO HÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('201', '201 - SEU CORPO CANSADO PELAS CAMINHADAS', 'SEU CORPO CANSADO PELAS CAMINHADAS, PELAS MADRUGADAS PROSTRADO EM ORAÇÃO, MEU JESUS!\nCOM AMOR CURANDO, VIDAS LIBERTANDO, \nDANDO O PÃO DO CÉU À MULTIDÃO!\n\nCORO\nE SEM TER UM LUGAR PARA DESCANSAR,\nPERSEGUIDO COMO VIL MALFEITOR;\nPOR AMOR FOI À CRUZ SEM SE DEFENDER,\nPARA SER MEU SALVADOR!\n\nNÃO TINHA BELEZA PRA SER DESEJADO\nE FOI DESPREZADO POR UM POVO QUE ERA SEU, \nMEU JESUS!\nPOR DEUS AFLIGIDO, SOFREU O CASTIGO,\nPOR PECADO QUE NÃO COMETEU!\n\nCORO\nE SEM TER UM LUGAR PARA DESCANSAR,\nPERSEGUIDO COMO VIL MALFEITOR;\nPOR AMOR FOI À CRUZ SEM SE DEFENDER,\nPARA SER MEU SALVADOR!\n\nSEM TEMER A MORTE, SOB A DOR FOI FORTE, \nE BEBEU O CÁLICE QUE O PAI LHE PREPAROU, \nMEU JESUS!\nCOMO OVELHA MUDA FOI SACRIFICADO,\nSEU PRECIOSO SANGUE DERRAMOU!\n\nCORO\nE SEM TER UM LUGAR PARA DESCANSAR,\nPERSEGUIDO COMO VIL MALFEITOR;\nPOR AMOR FOI À CRUZ SEM SE DEFENDER,\nPARA SER MEU SALVADOR!', 'SEU CORPO CANSADO PELAS CAMINHADAS, PELAS MADRUGADAS PROSTRADO EM ORAÇÃO, MEU JESUS! COM AMOR CURANDO, VIDAS LIBERTANDO, DANDO O PÃO DO CÉU À MULTIDÃO! CORO E SEM TER UM LUGAR PARA DESCANSAR, PERSEGUIDO COMO VIL MALFEITOR; POR AMOR FOI À CRUZ SEM SE DEFENDER, PARA SER MEU SALVADOR! NÃO TINHA BELEZA PRA SER DESEJADO E FOI DESPREZADO POR UM POVO QUE ERA SEU, MEU JESUS! POR DEUS AFLIGIDO, SOFREU O CASTIGO, POR PECADO QUE NÃO COMETEU! SEM TEMER A MORTE, SOB A DOR FOI FORTE, E BEBEU O CÁLICE QUE O PAI LHE PREPAROU, MEU JESUS! COMO OVELHA MUDA FOI SACRIFICADO, SEU PRECIOSO SANGUE DERRAMOU!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('202', '202 - HÁ UMA FONTE ABERTA NO CALVÁRIO', 'HÁ UMA FONTE ABERTA NO CALVÁRIO\nCONTRA A IMPUREZA E O PECADO;\nÁGUAS DE PERDÃO, PURIFICAÇÃO,\nFLUEM DE JESUS CRUCIFICADO.\n\nHÁ UMA FONTE ABERTA NO CALVÁRIO,\nONDE MEU SENHOR FOI TRASPASSADO,\nFONTE DE PODER PRA TODO O QUE CRER,\nPOR SUAS PISADURAS, SER CURADO.\n\nCORO\nTE AGRADEÇO, JESUS,\nPORQUE FOSTE À CRUZ\nPARA ABRIR ESTA FONTE DE VIDA E LUZ.\n\nPARA QUE EU VIVESSE, TU MORRESTE,\nPARA ME CURARES, ENFERMASTE!\nCORREM DO TEU LADO, Ó CRUCIFICADO,\nÁGUAS VIVAS COM QUE ME SALVASTE.', 'HÁ UMA FONTE ABERTA NO CALVÁRIO CONTRA A IMPUREZA E O PECADO; ÁGUAS DE PERDÃO, PURIFICAÇÃO, FLUEM DE JESUS CRUCIFICADO. HÁ UMA FONTE ABERTA NO CALVÁRIO, ONDE MEU SENHOR FOI TRASPASSADO, FONTE DE PODER PRA TODO O QUE CRER, POR SUAS PISADURAS, SER CURADO. CORO TE AGRADEÇO, JESUS, PORQUE FOSTE À CRUZ PARA ABRIR ESTA FONTE DE VIDA E LUZ. PARA QUE EU VIVESSE, TU MORRESTE, PARA ME CURARES, ENFERMASTE! CORREM DO TEU LADO, Ó CRUCIFICADO, ÁGUAS VIVAS COM QUE ME SALVASTE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('203', '203 - JESUS IA SUBINDO O MONTE', 'JESUS IA SUBINDO O MONTE,\nCARREGANDO A NOSSA CRUZ;\nNOSSO FARDO DE PECADO, \nSOBRE SI ELE LEVOU.\nELE NÃO TINHA PECADO,\nMAS POR NÓS JESUS MORREU,\nPRA NOS DAR A LIBERDADE, \nSUA VIDA ELE DEU.\n\nPOR NÓS JESUS MORREU, \nFOI POR AMOR\nQUE ELE SE ENTREGOU, \nAS NOSSAS DORES LEVOU\nE O PECADO PERDOOU.\n\nTODA LUZ SE ESCURECEU,\nO DIA EM TREVAS SE TORNOU,\nPOIS NAQUELA DURA CRUZ\nMORREU O FILHO DE DEUS.\n\nMAS ELE RESSUSCITOU,\nVENCEU A MORTE E A CRUZ, \nVENCEU O MAL, \nME LIBERTOU DO PESO\nQUE ANTES CARREGUEI,\nLEVOU AS NOSSAS DORES\nE A SALVAÇÃO NOS DEU.\nBIS\n\nPOR NÓS JESUS MORREU, \nPOR NÓS RESSUSCITOU,\nO PREÇO DA LIBERDADE,\nCOM SEU SANGUE ELE PAGOU;\nPOR NÓS JESUS MORREU, \nPOR NÓS RESSUSCITOU,\nO PREÇO DA LIBERDADE,\nCOM SEU SANGUE ELE PAGOU.\nAMÉM! AMÉM!', 'JESUS IA SUBINDO O MONTE, CARREGANDO A NOSSA CRUZ; NOSSO FARDO DE PECADO, SOBRE SI ELE LEVOU. ELE NÃO TINHA PECADO, MAS POR NÓS JESUS MORREU, PRA NOS DAR A LIBERDADE, SUA VIDA ELE DEU. POR NÓS JESUS MORREU, FOI POR AMOR QUE ELE SE ENTREGOU, AS NOSSAS DORES LEVOU E O PECADO PERDOOU. TODA LUZ SE ESCURECEU, O DIA EM TREVAS SE TORNOU, POIS NAQUELA DURA CRUZ MORREU O FILHO DE DEUS. MAS ELE RESSUSCITOU, VENCEU A MORTE E A CRUZ, VENCEU O MAL, ME LIBERTOU DO PESO QUE ANTES CARREGUEI, LEVOU AS NOSSAS DORES E A SALVAÇÃO NOS DEU. POR NÓS JESUS MORREU, POR NÓS RESSUSCITOU, O PREÇO DA LIBERDADE, COM SEU SANGUE ELE PAGOU; POR NÓS JESUS MORREU, POR NÓS RESSUSCITOU, O PREÇO DA LIBERDADE, COM SEU SANGUE ELE PAGOU. AMÉM! AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('204', '204 - MORRI NA CRUZ POR TI', 'MORRI NA CRUZ POR TI,\nMORRI PRA TE LIVRAR;\nMEU SANGUE, SIM, VERTI\nE POSSO TE SALVAR.\n\nCORO (2X)\nMORRI, MORRI NA CRUZ POR TI, \nQUE FAZES TU POR MIM?\n\nVIVI ASSIM POR TI,\nCOM DOR, COM DISSABOR;\nSIM, TUDO FIZ AQUI\nPRA SER TEU SALVADOR.\n\nCORO (2X)\nMORRI, MORRI NA CRUZ POR TI, \nQUE FAZES TU POR MIM?\n\nEU TROUXE A SALVAÇÃO,\nDOS ALTOS CÉUS LOUVOR,\nÉ LIVRE MEU PERDÃO,\nÉ GRANDE MEU AMOR.\n\nCORO (2X)\nMORRI, MORRI NA CRUZ POR TI,\nQUE FAZES TU POR MIM', 'MORRI NA CRUZ POR TI, MORRI PRA TE LIVRAR; MEU SANGUE, SIM, VERTI E POSSO TE SALVAR.  MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM? VIVI ASSIM POR TI, COM DOR, COM DISSABOR; SIM, TUDO FIZ AQUI PRA SER TEU SALVADOR.  MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM? EU TROUXE A SALVAÇÃO, DOS ALTOS CÉUS LOUVOR, É LIVRE MEU PERDÃO, É GRANDE MEU AMOR.  MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('205', '205 - JESUS IA SEGUINDO', 'JESUS IA SEGUINDO PELA ESTRADA DE JERUSALÉM,\nELE IA SUBIR AO MONTE PARA AO PAI ORAR,\nQUANDO PRESTES A TOMAR NA CRUZ \nOS MEUS PECADOS, E AS MINHAS CULPAS \nSOBRE SI LEVAR.\n\nINSTRUMENTOS\n\nDO SEU ROSTO LÁGRIMAS COMEÇAM A DESCER,\nMOSTRANDO A TRISTEZA QUE VEM DO CORAÇÃO;\nTRISTEZA ESSA QUE REVELA TODO O SEU SOFRER,\nESTE FOI O PREÇO PELO MEU PERDÃO.\n\nO SOL SE PÕE E A NOITE \nVEM TRAZENDO A SOLIDÃO,\nENVOLVENDO TUDO \nNO SEU DENSO E ESCURO VÉU;\nNAS MÃOS DO PAI TUDO ENTREGOU, \nDIZENDO, ENTÃO:\n“SEJA FEITA A TUA VONTADE AQUI, \nCOMO NO CÉU”.\n\nDEPOIS QUE ELE CUMPRIU A SUA PARTE,\nDEPOIS QUE ELE ASSUMIU TUDO O \nQUE ERA MEU, MOSTRANDO O CAMINHO\nQUE ME LEVA ATÉ DEUS PAI,\nQUE POSSO EU FAZER,\nALÉM DA MINHA VIDA LHE ENTREGAR?\n\nINSTRUMENTOS\n\nDEPOIS QUE ELE CUMPRIU A SUA PARTE,\nDEPOIS QUE ELE ASSUMIU TUDO O \nQUE ERA MEU, MOSTRANDO O CAMINHO\nQUE ME LEVA ATÉ DEUS PAI,\nQUE POSSO EU FAZER,\nALÉM DA MINHA VIDA LHE ENTREGAR?', 'JESUS IA SEGUINDO PELA ESTRADA DE JERUSALÉM, ELE IA SUBIR AO MONTE PARA AO PAI ORAR, QUANDO PRESTES A TOMAR NA CRUZ OS MEUS PECADOS, E AS MINHAS CULPAS SOBRE SI LEVAR. INSTRUMENTOS DO SEU ROSTO LÁGRIMAS COMEÇAM A DESCER, MOSTRANDO A TRISTEZA QUE VEM DO CORAÇÃO; TRISTEZA ESSA QUE REVELA TODO O SEU SOFRER, ESTE FOI O PREÇO PELO MEU PERDÃO. O SOL SE PÕE E A NOITE VEM TRAZENDO A SOLIDÃO, ENVOLVENDO TUDO NO SEU DENSO E ESCURO VÉU; NAS MÃOS DO PAI TUDO ENTREGOU, DIZENDO, ENTÃO: SEJA FEITA A TUA VONTADE AQUI, COMO NO CÉU. DEPOIS QUE ELE CUMPRIU A SUA PARTE, DEPOIS QUE ELE ASSUMIU TUDO O QUE ERA MEU, MOSTRANDO O CAMINHO QUE ME LEVA ATÉ DEUS PAI, QUE POSSO EU FAZER, ALÉM DA MINHA VIDA LHE ENTREGAR? INSTRUMENTOS DEPOIS QUE ELE CUMPRIU A SUA PARTE, DEPOIS QUE ELE ASSUMIU TUDO O QUE ERA MEU, MOSTRANDO O CAMINHO QUE ME LEVA ATÉ DEUS PAI, QUE POSSO EU FAZER, ALÉM DA MINHA VIDA LHE ENTREGAR?', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('206', '206 - PAI, A TI ENTREGO O MEU ESPÍRITO', '“PAI, A TI ENTREGO O MEU ESPÍRITO,\nTRAGO CONSOLO AO PECADOR\nPARA UMA VIDA MUITO ALÉM,\nMUITO ALÉM DESTE MUNDO MAU.\n\nSEI QUE É DIFÍCIL ESTE MEU SOFRER,\nMAS POR AMOR VOU ATÉ MORRER,\nLEVANDO AS MARCAS DO MOMENTO\nE DO SOFRIMENTO QUE AQUI PASSEI;\nPAI, A TI ENTREGO O MEU ESPÍRITO.\n\nMAS SEI QUE VOU RESSURGIR \nEM UM CORPO GLORIOSO\nE SEI QUE VOLTAREI \nPRA BUSCAR A MINHA IGREJA GLORIOSA, \nRESPLANDECENTE DE LUZ. \n\nFINAL: GLORIOSA, RESPLANDECENTE DE LUZ.”\nBIS', 'PAI, A TI ENTREGO O MEU ESPÍRITO, TRAGO CONSOLO AO PECADOR PARA UMA VIDA MUITO ALÉM, MUITO ALÉM DESTE MUNDO MAU. SEI QUE É DIFÍCIL ESTE MEU SOFRER, MAS POR AMOR VOU ATÉ MORRER, LEVANDO AS MARCAS DO MOMENTO E DO SOFRIMENTO QUE AQUI PASSEI; PAI, A TI ENTREGO O MEU ESPÍRITO. MAS SEI QUE VOU RESSURGIR EM UM CORPO GLORIOSO E SEI QUE VOLTAREI PRA BUSCAR A MINHA IGREJA GLORIOSA, RESPLANDECENTE DE LUZ.  GLORIOSA, RESPLANDECENTE DE LUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('207', '207 - DEUS ENVIOU SEU FILHO AMADO', 'DEUS ENVIOU SEU FILHO AMADO\nPARA MORRER EM MEU LUGAR;\nNA CRUZ SOFREU POR MEUS PECADOS,\nMAS O SEPULCRO VAZIO ESTÁ\nPORQUE ELE VIVE.\n\nCORO\nPORQUE ELE VIVE, \nPOSSO CRER NO AMANHÃ; \nPORQUE ELE VIVE, \nTEMOR NÃO HÁ. MAS EU BEM SEI (EU SEI)\nQUE A MINHA VIDA \nESTÁ NAS MÃOS DO MEU JESUS, \nQUE VIVO ESTÁ.\n\nE QUANDO, ENFIM, CHEGAR A HORA\nEM QUE A MORTE ENFRENTAREI;\nSEM MEDO, ENTÃO, TEREI VITÓRIA, VEREI \nNA GLÓRIA O MEU JESUS QUE VIVO ESTÁ.\n\nCORO\nPORQUE ELE VIVE, \nPOSSO CRER NO AMANHÃ; \nPORQUE ELE VIVE, \nTEMOR NÃO HÁ. MAS EU BEM SEI (EU SEI)\nQUE A MINHA VIDA \nESTÁ NAS MÃOS DO MEU JESUS, \nQUE VIVO ESTÁ.', 'DEUS ENVIOU SEU FILHO AMADO PARA MORRER EM MEU LUGAR; NA CRUZ SOFREU POR MEUS PECADOS, MAS O SEPULCRO VAZIO ESTÁ PORQUE ELE VIVE. CORO PORQUE ELE VIVE, POSSO CRER NO AMANHÃ; PORQUE ELE VIVE, TEMOR NÃO HÁ. MAS EU BEM SEI (EU SEI) QUE A MINHA VIDA ESTÁ NAS MÃOS DO MEU JESUS, QUE VIVO ESTÁ. E QUANDO, ENFIM, CHEGAR A HORA EM QUE A MORTE ENFRENTAREI; SEM MEDO, ENTÃO, TEREI VITÓRIA, VEREI NA GLÓRIA O MEU JESUS QUE VIVO ESTÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('208', '208 - DEUS AMOU O MUNDO DE TAL MANEIRA', 'DEUS AMOU O MUNDO DE TAL MANEIRA, \nQUE SEU FILHO JESUS ENVIOU. \nVEIO PRA LIVRAR-NOS DA CONDENAÇÃO,\nVIDA ETERNA E TAMBÉM PAZ NOS PROPORCIONOU;\n\nJESUS, MESMO SENDO DEUS, OBEDECEU,\nME AMOU, TE AMOU, POR NÓS PADECEU;\nE DERRAMOU SEU SANGUE EM PURIFICAÇÃO, \nCUMPRIU O DIVINO PLANO DA REDENÇÃO.\n\nASSIM PODEMOS TER DIRETO ACESSO AO PAI, \nEM COMUNHÃO VIVER ATÉ JESUS VOLTAR. \nEXAMINA A TI MESMO:\nTENS GOZADO DESTA SALVAÇÃO?\nOU AINDA ESTÁS PRESO\nEM MEIO À TUA PRÓPRIA ILUSÃO?\nINSTRUMENTOS\n\nRENDE AGORA A TUA VIDA \nE ENTREGA SOBRE O SEU ALTAR; \nCADA HORA É DECISIVA, \nJESUS DISSE: “LOGO VOU VOLTAR”. \nINSTRUMENTOS\nFINAL: ELE DISSE: “LOGO VOU VOLTAR”.\nBIS', 'DEUS AMOU O MUNDO DE TAL MANEIRA, QUE SEU FILHO JESUS ENVIOU. VEIO PRA LIVRAR-NOS DA CONDENAÇÃO, VIDA ETERNA E TAMBÉM PAZ NOS PROPORCIONOU; JESUS, MESMO SENDO DEUS, OBEDECEU, ME AMOU, TE AMOU, POR NÓS PADECEU; E DERRAMOU SEU SANGUE EM PURIFICAÇÃO, CUMPRIU O DIVINO PLANO DA REDENÇÃO. ASSIM PODEMOS TER DIRETO ACESSO AO PAI, EM COMUNHÃO VIVER ATÉ JESUS VOLTAR. EXAMINA A TI MESMO: TENS GOZADO DESTA SALVAÇÃO? OU AINDA ESTÁS PRESO EM MEIO À TUA PRÓPRIA ILUSÃO? INSTRUMENTOS RENDE AGORA A TUA VIDA E ENTREGA SOBRE O SEU ALTAR; CADA HORA É DECISIVA, JESUS DISSE: LOGO VOU VOLTAR. INSTRUMENTOS  ELE DISSE: LOGO VOU VOLTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('209', '209 - DEUS O SEU FILHO ENVIOU', 'DEUS O SEU FILHO ENVIOU\nPARA MORRER NUMA CRUZ;\nNUM DESEJO PROFUNDO\nDO SEU CORAÇÃO,\nQUIS O HOMEM SALVAR.\nBIS\n\nNESTE CAMINHO DA VIDA, ACONTECE \nQUE, SE DE DEUS, LONGE ESTOU,\nTENHO QUE IR A JESUS;\nLEMBRO QUE ELE SOFREU LÁ NA CRUZ.\n\nPARA RESGATAR MINHA VIDA,\nPARA TRANSFORMAR MEU CORAÇÃO,\nDANDO-ME PERDÃO, VIDA ETERNA, \nSEU AMOR.\n\nFINAL: SEU AMOR.\nBIS', 'DEUS O SEU FILHO ENVIOU PARA MORRER NUMA CRUZ; NUM DESEJO PROFUNDO DO SEU CORAÇÃO, QUIS O HOMEM SALVAR. NESTE CAMINHO DA VIDA, ACONTECE QUE, SE DE DEUS, LONGE ESTOU, TENHO QUE IR A JESUS; LEMBRO QUE ELE SOFREU LÁ NA CRUZ. PARA RESGATAR MINHA VIDA, PARA TRANSFORMAR MEU CORAÇÃO, DANDO-ME PERDÃO, VIDA ETERNA, SEU AMOR.  SEU AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('210', '210 - UM DIA JESUS DESCEU DA SUA GLÓRIA', 'UM DIA JESUS DESCEU DA SUA GLÓRIA\nE AQUI VIVEU, ENTRE NÓS, COM AMOR,\nFEZ MARAVILHAS, NOS DEU A VITÓRIA,\nE COM SEU SANGUE NOS PURIFICOU.\n\nHOSANA, HOSANA AO REI NAS ALTURAS, \nHOSANA, HOSANA A JESUS, \nO SENHOR.\nBIS\n\nMORREU NA CRUZ PELOS NOSSOS PECADOS,\nSOFREU DEMAIS, SÓ PARA NOS SALVAR,\nE, NA TRISTEZA, NOS TEM CONSOLADO,\nSUBIU AO PAI, MAS VIRÁ NOS BUSCAR,\nSUBIU AO PAI, MAS VIRÁ NOS BUSCAR.\n\nHOSANA, HOSANA AO REI NAS ALTURAS, \nHOSANA, HOSANA A JESUS, \nO SENHOR.\nBIS', 'UM DIA JESUS DESCEU DA SUA GLÓRIA E AQUI VIVEU, ENTRE NÓS, COM AMOR, FEZ MARAVILHAS, NOS DEU A VITÓRIA, E COM SEU SANGUE NOS PURIFICOU. HOSANA, HOSANA AO REI NAS ALTURAS, HOSANA, HOSANA A JESUS, O SENHOR. MORREU NA CRUZ PELOS NOSSOS PECADOS, SOFREU DEMAIS, SÓ PARA NOS SALVAR, E, NA TRISTEZA, NOS TEM CONSOLADO, SUBIU AO PAI, MAS VIRÁ NOS BUSCAR, SUBIU AO PAI, MAS VIRÁ NOS BUSCAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('211', '211 - CORTARAM O MADEIRO', 'CORTARAM O MADEIRO, FIZERAM UMA CRUZ \nPARA O MEU SALVADOR; MADEIRO LAVRADO, \nCOM PREGOS CRAVADOS, PESADA FICOU.\nELE CARREGOU A CRUZ, NO CAMINHO CAIU, \nMAS DEUS DEU-LHE GRAÇA\nMORRENDO NA CRUZ POR TI E POR MIM.\n\nCORO\nFOI FEITA ASSIM A CRUZ DO SALVADOR;\nMADEIRO LAVRADO, COM PREGOS CRAVADOS, \nPESADA FICOU.\n\nPERANTE PILATOS JESUS FOI LEVADO\nCOMO UM MALFEITOR;\nCHEGANDO A ELE, OLHANDO O MESTRE,\nASSIM PERGUNTOU: ÉS O REI DOS JUDEUS? \nDISSE JESUS: “NA VERDADE EU SOU, \nO MEU REINO É ETERNO, \nNÃO É DESTE MUNDO, \nDAQUI EU NÃO SOU”.\n\nCORO\nFOI FEITA ASSIM A CRUZ DO SALVADOR;\nMADEIRO LAVRADO, COM PREGOS CRAVADOS, \nPESADA FICOU.\n\nCHEGANDO AO CALVÁRIO, \nPREGARAM NA CRUZ O MEU SALVADOR;\nUMA COROA DE ESPINHOS, \nNA FRONTE SANGRANDO, AO PAI SUPLICOU;\nTRANSPASSADO DE DOR FICOU O MEU SALVADOR, \nÀS TRÊS HORAS DA TARDE,\nINCLINOU A CABEÇA E ALI EXPIROU.\n\nCORO\nFOI FEITA ASSIM A CRUZ DO SALVADOR;\nMADEIRO LAVRADO, COM PREGOS CRAVADOS, \nPESADA FICOU.', 'CORTARAM O MADEIRO, FIZERAM UMA CRUZ PARA O MEU SALVADOR; MADEIRO LAVRADO, COM PREGOS CRAVADOS, PESADA FICOU. ELE CARREGOU A CRUZ, NO CAMINHO CAIU, MAS DEUS DEU-LHE GRAÇA MORRENDO NA CRUZ POR TI E POR MIM. CORO FOI FEITA ASSIM A CRUZ DO SALVADOR; MADEIRO LAVRADO, COM PREGOS CRAVADOS, PESADA FICOU. PERANTE PILATOS JESUS FOI LEVADO COMO UM MALFEITOR; CHEGANDO A ELE, OLHANDO O MESTRE, ASSIM PERGUNTOU: ÉS O REI DOS JUDEUS? DISSE JESUS: NA VERDADE EU SOU, O MEU REINO É ETERNO, NÃO É DESTE MUNDO, DAQUI EU NÃO SOU. CHEGANDO AO CALVÁRIO, PREGARAM NA CRUZ O MEU SALVADOR; UMA COROA DE ESPINHOS, NA FRONTE SANGRANDO, AO PAI SUPLICOU; TRANSPASSADO DE DOR FICOU O MEU SALVADOR, ÀS TRÊS HORAS DA TARDE, INCLINOU A CABEÇA E ALI EXPIROU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('212', '212 - RUDE CRUZ', 'RUDE CRUZ SE ERIGIU, DELA O DIA FUGIU,\nCOMO EMBLEMA DE VERGONHA E DOR;\nMAS EU AMO ESSA CRUZ PORQUE NELA, \nJESUS, DEU A VIDA POR MIM, PECADOR.\n\nCORO\nSIM, EU AMO A MENSAGEM DA CRUZ,\nTÉ MORRER EU A VOU PROCLAMAR;\nLEVAREI EU TAMBÉM MINHA CRUZ,\nTÉ POR UMA COROA TROCAR.\n\nDESDE A GLÓRIA DOS CÉUS, \nO CORDEIRO DE DEUS \nAO CALVÁRIO HUMILHANTE BAIXOU;\nTEM A CRUZ PARA MIM ATRATIVOS SEM FIM, \nPORQUE NELA JESUS ME SALVOU.\n\nCORO\nSIM, EU AMO A MENSAGEM DA CRUZ,\nTÉ MORRER EU A VOU PROCLAMAR;\nLEVAREI EU TAMBÉM MINHA CRUZ,\nTÉ POR UMA COROA TROCAR.\n\nLÁ NA CRUZ PADECEU, \nDESPREZADO MORREU MEU JESUS \nPARA DAR-ME O PERDÃO;\nDELA AGORA PROVÉM PARA MIM TODO \nO BEM, TENHO NELA REAL SALVAÇÃO.\n\nCORO\nSIM, EU AMO A MENSAGEM DA CRUZ,\nTÉ MORRER EU A VOU PROCLAMAR;\nLEVAREI EU TAMBÉM MINHA CRUZ,\nTÉ POR UMA COROA TROCAR.\n\nEU AQUI COM JESUS, A VERGONHA DA CRUZ \nQUERO SEMPRE LEVAR E SOFRER;\nQUANDO CRISTO VOLTAR, \nPARA AQUI ME BUSCAR, \nSUA GLÓRIA EU IREI RECEBER.\n\nCORO\nSIM, EU AMO A MENSAGEM DA CRUZ,\nTÉ MORRER EU A VOU PROCLAMAR;\nLEVAREI EU TAMBÉM MINHA CRUZ,\nTÉ POR UMA COROA TROCAR.', 'RUDE CRUZ SE ERIGIU, DELA O DIA FUGIU, COMO EMBLEMA DE VERGONHA E DOR; MAS EU AMO ESSA CRUZ PORQUE NELA, JESUS, DEU A VIDA POR MIM, PECADOR. CORO SIM, EU AMO A MENSAGEM DA CRUZ, TÉ MORRER EU A VOU PROCLAMAR; LEVAREI EU TAMBÉM MINHA CRUZ, TÉ POR UMA COROA TROCAR. DESDE A GLÓRIA DOS CÉUS, O CORDEIRO DE DEUS AO CALVÁRIO HUMILHANTE BAIXOU; TEM A CRUZ PARA MIM ATRATIVOS SEM FIM, PORQUE NELA JESUS ME SALVOU. LÁ NA CRUZ PADECEU, DESPREZADO MORREU MEU JESUS PARA DAR-ME O PERDÃO; DELA AGORA PROVÉM PARA MIM TODO O BEM, TENHO NELA REAL SALVAÇÃO. EU AQUI COM JESUS, A VERGONHA DA CRUZ QUERO SEMPRE LEVAR E SOFRER; QUANDO CRISTO VOLTAR, PARA AQUI ME BUSCAR, SUA GLÓRIA EU IREI RECEBER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('213', '213 - CRISTO CAMINHAVA EM DIREÇÃO À CRUZ', 'CRISTO CAMINHAVA EM DIREÇÃO À CRUZ,\nSABENDO QUE ESTAVA PRA MORRER,\nENFERMO E CANSADO, SOZINHO E DESPREZADO, CARREGAVA A CRUZ.\n\nNOSSOS PECADOS ELE SUPORTOU,\nSENTINDO SOLIDÃO AO PAI CLAMOU:\n“NÃO SABEM O QUE FAZEM,\nPERDOA-LHES, SENHOR, EU VIM POR AMOR.”\n\nNO CALVÁRIO PREGARAM SUAS MÃOS E PÉS,\nSENDO REI, COROADO DE ESPINHOS;\nO CÉU SE ESCURECEU, \nPRA BEBER LHE DERAM FEL, \nRASGOU-SE O VÉU E EXPIROU.\n\nAO TERCEIRO DIA ELE RESSURGIU\nDANDO-NOS VITÓRIA, \nO SEU SANGUE É GARANTIA!\nGESTO DE AMOR, GESTO DO SENHOR,\nMORRENDO EM UMA CRUZ,\nSENDO NOSSO SALVADOR.\nINSTRUMENTOS\n\nGESTO DE AMOR, \nGESTO DO SENHOR,\nMORRENDO EM UMA CRUZ, \nSENDO NOSSO SALVADOR.\n\nFINAL: GESTO DE AMOR!\nBIS', 'CRISTO CAMINHAVA EM DIREÇÃO À CRUZ, SABENDO QUE ESTAVA PRA MORRER, ENFERMO E CANSADO, SOZINHO E DESPREZADO, CARREGAVA A CRUZ. NOSSOS PECADOS ELE SUPORTOU, SENTINDO SOLIDÃO AO PAI CLAMOU: NÃO SABEM O QUE FAZEM, PERDOA-LHES, SENHOR, EU VIM POR AMOR. NO CALVÁRIO PREGARAM SUAS MÃOS E PÉS, SENDO REI, COROADO DE ESPINHOS; O CÉU SE ESCURECEU, PRA BEBER LHE DERAM FEL, RASGOU-SE O VÉU E EXPIROU. AO TERCEIRO DIA ELE RESSURGIU DANDO-NOS VITÓRIA, O SEU SANGUE É GARANTIA! GESTO DE AMOR, GESTO DO SENHOR, MORRENDO EM UMA CRUZ, SENDO NOSSO SALVADOR. INSTRUMENTOS GESTO DE AMOR, GESTO DO SENHOR, MORRENDO EM UMA CRUZ, SENDO NOSSO SALVADOR.  GESTO DE AMOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('214', '214 - DOIS DISCÍPULOS NO CAMINHO DE EMAÚS', 'DOIS DISCÍPULOS NO CAMINHO DE EMAÚS\nIAM FALANDO A RESPEITO DE JESUS;\nUM VIAJANTE DELES SE APROXIMOU,\nNÃO PERCEBERAM QUE ERA O MEIGO SALVADOR.\n\nCORO\nFICA CONOSCO, NOSSO AMADO SALVADOR,\nFICA CONOSCO PELO TEU IMENSO AMOR,\nCOMO OS DISCÍPULOS TE PEDIRAM \nNO CAMINHO DE EMAÚS, FICA CONOSCO, \nNOSSO AMADO E BOM JESUS.\n\nELES FALAVAM A RESPEITO DE JESUS,\nVARÃO PROFETA QUE FOI PODEROSO EM OBRAS, \nDIANTE DE DEUS E TODO O POVO, ALELUIA, \nESTE JESUS QUE AINDA HOJE NOS CONDUZ.\n\nCORO\nFICA CONOSCO, NOSSO AMADO SALVADOR,\nFICA CONOSCO PELO TEU IMENSO AMOR,\nCOMO OS DISCÍPULOS TE PEDIRAM \nNO CAMINHO DE EMAÚS, FICA CONOSCO, \nNOSSO AMADO E BOM JESUS.\n\nELES FALAVAM A RESPEITO DE JESUS,\nVARÃO PROFETA QUE FOI PODEROSO EM OBRAS, \nDIANTE DE DEUS E TODO O POVO, ALELUIA, \nESTE JESUS QUE AINDA HOJE NOS CONDUZ.\n\nCORO\nFICA CONOSCO, NOSSO AMADO SALVADOR,\nFICA CONOSCO PELO TEU IMENSO AMOR,\nCOMO OS DISCÍPULOS TE PEDIRAM \nNO CAMINHO DE EMAÚS, FICA CONOSCO, \nNOSSO AMADO E BOM JESUS.\n\nSENTADO À MESA, TOMANDO O PÃO, \nABENÇOOU, PARTIU E DEU, \nELES ENTÃO RECONHECERAM;\nCHEIOS DE GOZO EM VER \nO FILHO DE DEUS NO MESMO INSTANTE \nELE DESAPARECEU.\n\nCORO\nFICA CONOSCO, NOSSO AMADO SALVADOR,\nFICA CONOSCO PELO TEU IMENSO AMOR,\nCOMO OS DISCÍPULOS TE PEDIRAM \nNO CAMINHO DE EMAÚS, FICA CONOSCO, \nNOSSO AMADO E BOM JESUS.', 'DOIS DISCÍPULOS NO CAMINHO DE EMAÚS IAM FALANDO A RESPEITO DE JESUS; UM VIAJANTE DELES SE APROXIMOU, NÃO PERCEBERAM QUE ERA O MEIGO SALVADOR. CORO FICA CONOSCO, NOSSO AMADO SALVADOR, FICA CONOSCO PELO TEU IMENSO AMOR, COMO OS DISCÍPULOS TE PEDIRAM NO CAMINHO DE EMAÚS, FICA CONOSCO, NOSSO AMADO E BOM JESUS. ELES FALAVAM A RESPEITO DE JESUS, VARÃO PROFETA QUE FOI PODEROSO EM OBRAS, DIANTE DE DEUS E TODO O POVO, ALELUIA, ESTE JESUS QUE AINDA HOJE NOS CONDUZ. SENTADO À MESA, TOMANDO O PÃO, ABENÇOOU, PARTIU E DEU, ELES ENTÃO RECONHECERAM; CHEIOS DE GOZO EM VER O FILHO DE DEUS NO MESMO INSTANTE ELE DESAPARECEU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('215', '215 - CERTO HOMEM TÃO NOBRE', 'CERTO HOMEM TÃO NOBRE NESTE MUNDO DESCEU, CARREGOU MEUS PECADOS SÓ PORQUE ME AMOU;\nO SEU SANGUE TÃO PURO NA CRUZ DERRAMOU, \nESTE HOMEM TÃO NOBRE É JESUS.\n\nCORO (2X)\nNOBRE E REI, ELE É NOBRE E REI,\nESTE HOMEM TÃO NOBRE É JESUS.\n\nRESSURGIU DENTRE OS MORTOS\nPORQUE É VENCEDOR, \nHOJE À DESTRA DO PAI ESTÁ, CRISTO, \nMEU SALVADOR;\nPROMETEU QUE UM DIA VIRÁ ME BUSCAR, \nPARA SEMPRE O SEU NOME VOU LOUVAR.\n\nCORO (2X)\nNOBRE E REI, ELE É NOBRE E REI,\nESTE HOMEM TÃO NOBRE É JESUS.\n\nFINAL: \nESTE HOMEM TÃO NOBRE É JESUS.', 'CERTO HOMEM TÃO NOBRE NESTE MUNDO DESCEU, CARREGOU MEUS PECADOS SÓ PORQUE ME AMOU; O SEU SANGUE TÃO PURO NA CRUZ DERRAMOU, ESTE HOMEM TÃO NOBRE É JESUS. NOBRE E REI, ELE É NOBRE E REI, ESTE HOMEM TÃO NOBRE É JESUS. RESSURGIU DENTRE OS MORTOS PORQUE É VENCEDOR, HOJE À DESTRA DO PAI ESTÁ, CRISTO, MEU SALVADOR; PROMETEU QUE UM DIA VIRÁ ME BUSCAR, PARA SEMPRE O SEU NOME VOU LOUVAR. NOBRE E REI, ELE É NOBRE E REI, ESTE HOMEM TÃO NOBRE É JESUS.  ESTE HOMEM TÃO NOBRE É JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('216', '216 - SOMOS TEU POVO', 'SOMOS TEU POVO, TUA IGREJA,\nMORRESTE POR NÓS, SENDO REI, SENHOR!\nQUISESTE SALVAR O MUNDO,\nMAS O MUNDO NÃO QUIS.\n\nA TUA IGREJA TE AMA\nE FAZ DO TEU SANGUE ESCUDO FORTE,\nA TUA PALAVRA É ALIMENTO FORTE\nQUE FAZ NOSSA VIDA FELIZ!\n\nA TUA IGREJA TE ESPERA, NÃO SE APRESSA, \nPORQUE TU, JESUS, VOLTARÁS E A LEVARÁS \nPARA UM LAR DE PAZ, ONDE REINA O AMOR.\nBIS', 'SOMOS TEU POVO, TUA IGREJA, MORRESTE POR NÓS, SENDO REI, SENHOR! QUISESTE SALVAR O MUNDO, MAS O MUNDO NÃO QUIS. A TUA IGREJA TE AMA E FAZ DO TEU SANGUE ESCUDO FORTE, A TUA PALAVRA É ALIMENTO FORTE QUE FAZ NOSSA VIDA FELIZ! A TUA IGREJA TE ESPERA, NÃO SE APRESSA, PORQUE TU, JESUS, VOLTARÁS E A LEVARÁS PARA UM LAR DE PAZ, ONDE REINA O AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('217', '217 - DEIXOU O ESPLENDOR DE SUA GLÓRIA', 'DEIXOU O ESPLENDOR DE SUA GLÓRIA\nSABENDO O DESTINO AQUI;\nESTAVA SÓ E FERIDO NO GÓLGOTA\nPARA DAR SUA VIDA POR MIM.\n\nCORO\nSE ISTO NÃO FOR AMOR, O OCEANO SECOU, \nNÃO HÁ ESTRELAS NO CÉU,\nAS ANDORINHAS NÃO VOAM MAIS;\nSE ISTO NÃO FOR AMOR, O CÉU NÃO \nÉ REAL, TUDO PERDE O VALOR \nSE ISTO NÃO FOR AMOR.\n\nMESMO NA MORTE LEMBROU-SE\nDE UM LADRÃO QUE A SEU LADO ESTAVA;\nCOM AMOR E TERNURA FALOU-LHE:\n“AO PARAÍSO COMIGO IRÁS”.\n\nCORO\nSE ISTO NÃO FOR AMOR, O OCEANO SECOU, \nNÃO HÁ ESTRELAS NO CÉU,\nAS ANDORINHAS NÃO VOAM MAIS;\nSE ISTO NÃO FOR AMOR, O CÉU NÃO \nÉ REAL, TUDO PERDE O VALOR \nSE ISTO NÃO FOR AMOR.', 'DEIXOU O ESPLENDOR DE SUA GLÓRIA SABENDO O DESTINO AQUI; ESTAVA SÓ E FERIDO NO GÓLGOTA PARA DAR SUA VIDA POR MIM. CORO SE ISTO NÃO FOR AMOR, O OCEANO SECOU, NÃO HÁ ESTRELAS NO CÉU, AS ANDORINHAS NÃO VOAM MAIS; SE ISTO NÃO FOR AMOR, O CÉU NÃO É REAL, TUDO PERDE O VALOR SE ISTO NÃO FOR AMOR. MESMO NA MORTE LEMBROU-SE DE UM LADRÃO QUE A SEU LADO ESTAVA; COM AMOR E TERNURA FALOU-LHE: AO PARAÍSO COMIGO IRÁS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('218', '218 - POR AMOR JESUS MORREU', 'POR AMOR JESUS MORREU,\nVEIO PARA ME SALVAR,\nPOR AMOR SEU SANGUE VERTEU,\nNAQUELA AMARGA CRUZ,\nPOR AMOR SE FEZ MEU PRESENTE,\nTORNANDO-SE AMIGO \nME FEZ SEU PARENTE.\nBIS\n\nPOR AMOR SE FEZ MEU PRESENTE,\nTORNANDO-SE AMIGO\nME FEZ SEU PARENTE.\nVIVA O REI, (JESUS) \nVIVA O REI, (VIVA O REI) (BIS)\nVIVA O REI.', 'POR AMOR JESUS MORREU, VEIO PARA ME SALVAR, POR AMOR SEU SANGUE VERTEU, NAQUELA AMARGA CRUZ, POR AMOR SE FEZ MEU PRESENTE, TORNANDO-SE AMIGO ME FEZ SEU PARENTE. POR AMOR SE FEZ MEU PRESENTE, TORNANDO-SE AMIGO ME FEZ SEU PARENTE. VIVA O REI, (JESUS) VIVA O REI, (VIVA O REI)  VIVA O REI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('219', '219 - POR AMOR', 'POR AMOR, POR AMOR JESUS ME SALVOU,\nFOI NA CRUZ, FOI NA CRUZ QUE O PREÇO PAGOU,\nNÃO COM PRATA, NEM OURO, MAS \nCOM SANGUE MOSTROU O INFINITO \nAMOR COM QUE  AO MUNDO AMOU.\n\nCORO\nTÃO GRANDE AMOR ME TRANSFORMOU;\nBASTA CRER E ACEITAR, POR SEU SANGUE CLAMAR\nE O AMOR DE MEU DEUS VAI TUA VIDA INUNDAR.\nINSTRUMENTOS\n\nDO IMENSO AMOR NINGUÉM VAI SEPARAR,\nÉ MAIS FORTE QUE A MORTE,\nARDE EM MIM SEM CESSAR;\nTUDO CRÊ E SUPORTA ATÉ O DIA CHEGAR,\nE O AMOR DE MEU DEUS LÁ NO CÉU VOU GOZAR.\n\nCORO\nTÃO GRANDE AMOR ME TRANSFORMOU;\nBASTA CRER E ACEITAR, POR SEU SANGUE CLAMAR\nE O AMOR DE MEU DEUS VAI TUA VIDA INUNDAR.', 'POR AMOR, POR AMOR JESUS ME SALVOU, FOI NA CRUZ, FOI NA CRUZ QUE O PREÇO PAGOU, NÃO COM PRATA, NEM OURO, MAS COM SANGUE MOSTROU O INFINITO AMOR COM QUE AO MUNDO AMOU. CORO TÃO GRANDE AMOR ME TRANSFORMOU; BASTA CRER E ACEITAR, POR SEU SANGUE CLAMAR E O AMOR DE MEU DEUS VAI TUA VIDA INUNDAR. INSTRUMENTOS DO IMENSO AMOR NINGUÉM VAI SEPARAR, É MAIS FORTE QUE A MORTE, ARDE EM MIM SEM CESSAR; TUDO CRÊ E SUPORTA ATÉ O DIA CHEGAR, E O AMOR DE MEU DEUS LÁ NO CÉU VOU GOZAR. CORO TÃO GRANDE AMOR ME TRANSFORMOU; BASTA CRER E ACEITAR, POR SEU SANGUE CLAMAR E O AMOR DE MEU DEUS VAI TUA VIDA INUNDAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('220', '220 - NÃO HÁ AMOR IGUAL', 'NÃO HÁ AMOR IGUAL AO AMOR DE JESUS,\nPOR MIM MORREU NAQUELA CRUZ;\nDEIXOU A SUA GLÓRIA, SOFREU HUMILHAÇÃO\nSÓ PRA ME DAR A SALVAÇÃO.\n\nCORO\nPOR ISSO DEI-LHE O MEU CORAÇÃO\nE LHE ENTREGUEI TODO O MEU SER\nE UM TRONO LEVANTEI PARA O MEU JESUS \nSER REI EM TODO O MEU VIVER.\n\nO QUE ELE VIU EM MIM \nPARA SE DAR ASSIM POR MIM, \nTÃO POBRE PECADOR?\nDESCEU DA SUA GLÓRIA, \nSOFREU HUMILHAÇÃO \nPARA ME DAR A SALVAÇÃO.', 'NÃO HÁ AMOR IGUAL AO AMOR DE JESUS, POR MIM MORREU NAQUELA CRUZ; DEIXOU A SUA GLÓRIA, SOFREU HUMILHAÇÃO SÓ PRA ME DAR A SALVAÇÃO. CORO POR ISSO DEI-LHE O MEU CORAÇÃO E LHE ENTREGUEI TODO O MEU SER E UM TRONO LEVANTEI PARA O MEU JESUS SER REI EM TODO O MEU VIVER. O QUE ELE VIU EM MIM PARA SE DAR ASSIM POR MIM, TÃO POBRE PECADOR? DESCEU DA SUA GLÓRIA, SOFREU HUMILHAÇÃO PARA ME DAR A SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('221', '221 - JESUS, EM TUA PRESENÇA', 'JESUS, EM TUA PRESENÇA,\nREUNIMO-NOS AQUI,\nCONTEMPLAMOS TUA FACE\nE RENDEMO-NOS A TI;\nPOIS UM DIA A TUA MORTE\nTROUXE VIDA A TODOS NÓS\nE NOS DEU COMPLETO ACESSO\nAO CORAÇÃO DO PAI.\nINSTRUMENTOS\n\nJESUS, EM TUA PRESENÇA,\nREUNIMO-NOS AQUI,\nCONTEMPLAMOS TUA FACE\nE RENDEMO-NOS A TI;\nPOIS UM DIA A TUA MORTE\nTROUXE VIDA A TODOS NÓS\nE NOS DEU COMPLETO ACESSO\nAO CORAÇÃO DO PAI.\n\nO VÉU QUE SEPARAVA JÁ NÃO SEPARA MAIS,\nA LUZ, QUE OUTRORA APAGADA, AGORA BRILHA\nE CADA DIA BRILHA MAIS.\n\nSÓ PRA TE ADORAR\nE FAZER TEU NOME GRANDE\nE TE DAR O LOUVOR QUE É \nDEVIDO, ESTAMOS NÓS AQUI.\nBIS', 'JESUS, EM TUA PRESENÇA, REUNIMO-NOS AQUI, CONTEMPLAMOS TUA FACE E RENDEMO-NOS A TI; POIS UM DIA A TUA MORTE TROUXE VIDA A TODOS NÓS E NOS DEU COMPLETO ACESSO AO CORAÇÃO DO PAI. INSTRUMENTOS JESUS, EM TUA PRESENÇA, REUNIMO-NOS AQUI, CONTEMPLAMOS TUA FACE E RENDEMO-NOS A TI; POIS UM DIA A TUA MORTE TROUXE VIDA A TODOS NÓS E NOS DEU COMPLETO ACESSO AO CORAÇÃO DO PAI. O VÉU QUE SEPARAVA JÁ NÃO SEPARA MAIS, A LUZ, QUE OUTRORA APAGADA, AGORA BRILHA E CADA DIA BRILHA MAIS. SÓ PRA TE ADORAR E FAZER TEU NOME GRANDE E TE DAR O LOUVOR QUE É DEVIDO, ESTAMOS NÓS AQUI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('222', '222 - EIS QUE ESTOU À PORTA', 'EIS QUE ESTOU À PORTA E BATO,\nSE ALGUÉM OUVIR MINHA VOZ\nE ABRIR A PORTA,\nENTRAREI EM SEU CORAÇÃO.\n\nE COM ELE CEAREI E ELE COMIGO;\nBASTA ABRIR A PORTA,\nENTRAREI EM SEU CORAÇÃO.\nBIS\nBIS', 'EIS QUE ESTOU À PORTA E BATO, SE ALGUÉM OUVIR MINHA VOZ E ABRIR A PORTA, ENTRAREI EM SEU CORAÇÃO. E COM ELE CEAREI E ELE COMIGO; BASTA ABRIR A PORTA, ENTRAREI EM SEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('223', '223 - ONDE ESTÁ O CORDEIRO?', 'ONDE ESTÁ O CORDEIRO?\nA PERGUNTA DE TODAS AS NAÇÕES\nECOA NO MUNDO INTEIRO;\nONDE ESTÁ O CORDEIRO\nQUE FOI MORTO EM MEU LUGAR\nPARA A VIDA ETERNA ME DAR?\nINSTRUMENTOS\n\nEIS AQUI O CORDEIRO DE DEUS,\nTROUXE A MIM VIDA, PAZ E LUZ,\nREVELOU-ME O PLANO ETERNO,\nO CORDEIRO SE CHAMA JESUS,\n(ONDE ESTÁ O CORDEIRO?)\nO CORDEIRO SE CHAMA JESUS. \nINSTRUMENTOS\n\nOUVI UMA VOZ LÁ DO CÉU\nA CHAMAR: “POVO MEU, SOBE AQUI”;\nVI UM TRONO CELESTE \nE O CORDEIRO ASSENTADO ALI;\nEM CORO OS ANJOS CANTAVAM:\nGLÓRIA, GLÓRIA AO CORDEIRO, AMÉM.\n\nGLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA)\nGLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA)\nGLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA)\nAMÉM!', 'ONDE ESTÁ O CORDEIRO? A PERGUNTA DE TODAS AS NAÇÕES ECOA NO MUNDO INTEIRO; ONDE ESTÁ O CORDEIRO QUE FOI MORTO EM MEU LUGAR PARA A VIDA ETERNA ME DAR? INSTRUMENTOS EIS AQUI O CORDEIRO DE DEUS, TROUXE A MIM VIDA, PAZ E LUZ, REVELOU-ME O PLANO ETERNO, O CORDEIRO SE CHAMA JESUS, (ONDE ESTÁ O CORDEIRO?) O CORDEIRO SE CHAMA JESUS. INSTRUMENTOS OUVI UMA VOZ LÁ DO CÉU A CHAMAR: POVO MEU, SOBE AQUI; VI UM TRONO CELESTE E O CORDEIRO ASSENTADO ALI; EM CORO OS ANJOS CANTAVAM: GLÓRIA, GLÓRIA AO CORDEIRO, AMÉM. GLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) GLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) GLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('224', '224 - ERA UM PECADOR, ANDAVA SEM JESUS', 'ERA UM PECADOR, ANDAVA SEM JESUS,\nNÃO TINHA ESPERANÇA NEM DIVINA LUZ,\nHOJE SOU REMIDO, CRISTO ME SALVOU,\nCOM SEU SANGUE ME LAVOU.\n\nCORO\nQUE AMOR ME CONCEDEU JESUS,\nGOZO SANTO E CELESTE LUZ;\nCRISTO EM BREVE DO CÉU DESCERÁ\nE CONSIGO, ENTÃO, ME LEVARÁ.\n\nO CONSOLADOR JÁ VEIO EM MIM MORAR,\nA PALAVRA SANTA VEIO ILUMINAR;\nQUERO SER GUIADO POR TÃO CLARA LUZ\nQUE A CRISTO ME CONDUZ.\n\nCORO\nQUE AMOR ME CONCEDEU JESUS,\nGOZO SANTO E CELESTE LUZ;\nCRISTO EM BREVE DO CÉU DESCERÁ\nE CONSIGO, ENTÃO, ME LEVARÁ.\n\nVEM, JESUS AMADO, VEM SEM DEMORAR,\nEU ESTOU ANSIOSO PRA NO CÉU ENTRAR,\nVEM SEM MAIS TARDANÇA, FAZ RAIAR A LUZ; \n“ORA VEM, SENHOR JESUS”.\n\nCORO\nQUE AMOR ME CONCEDEU JESUS,\nGOZO SANTO E CELESTE LUZ;\nCRISTO EM BREVE DO CÉU DESCERÁ\nE CONSIGO, ENTÃO, ME LEVARÁ.\n\nQUERES, PECADOR, GOZAR A SALVAÇÃO?\nVEM A CRISTO HOJE RECEBER PERDÃO;\nCRISTO TE ACEITA, POBRE PECADOR,\nNOS SEUS BRAÇOS DE AMOR.\n\nCORO\nQUE AMOR ME CONCEDEU JESUS,\nGOZO SANTO E CELESTE LUZ;\nCRISTO EM BREVE DO CÉU DESCERÁ\nE CONSIGO, ENTÃO, ME LEVARÁ.', 'ERA UM PECADOR, ANDAVA SEM JESUS, NÃO TINHA ESPERANÇA NEM DIVINA LUZ, HOJE SOU REMIDO, CRISTO ME SALVOU, COM SEU SANGUE ME LAVOU. CORO QUE AMOR ME CONCEDEU JESUS, GOZO SANTO E CELESTE LUZ; CRISTO EM BREVE DO CÉU DESCERÁ E CONSIGO, ENTÃO, ME LEVARÁ. O CONSOLADOR JÁ VEIO EM MIM MORAR, A PALAVRA SANTA VEIO ILUMINAR; QUERO SER GUIADO POR TÃO CLARA LUZ QUE A CRISTO ME CONDUZ. VEM, JESUS AMADO, VEM SEM DEMORAR, EU ESTOU ANSIOSO PRA NO CÉU ENTRAR, VEM SEM MAIS TARDANÇA, FAZ RAIAR A LUZ; ORA VEM, SENHOR JESUS. QUERES, PECADOR, GOZAR A SALVAÇÃO? VEM A CRISTO HOJE RECEBER PERDÃO; CRISTO TE ACEITA, POBRE PECADOR, NOS SEUS BRAÇOS DE AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('225', '225 - JESUS É MELHOR, SIM, QUE OURO E BENS', 'JESUS É MELHOR, SIM, QUE OURO E BENS,\nJESUS É MELHOR DO QUE TUDO QUE TENS,\nMELHOR QUE RIQUEZAS E POSIÇÕES,\nMELHOR, MUITO MAIS DO QUE MILHÕES.\n\nCORO\nPODE SER UM REI COM PODER NAS \nMÃOS, MAS DO MAL, ESCRAVO, SIM;\nMIL VEZES PREFIRO O MEU JESUS\nE SERVI-LO ATÉ O FIM.\n\nJESUS É MELHOR QUE QUALQUER VALOR,\nAMIGO LEAL NO PRAZER E NA DOR,\nMELHOR DO QUE TUDO ELE É PRA MIM,\nMELHOR QUE QUALQUER BOM AMIGO ENFIM.\n\nCORO\nPODE SER UM REI COM PODER NAS \nMÃOS, MAS DO MAL, ESCRAVO, SIM;\nMIL VEZES PREFIRO O MEU JESUS\nE SERVI-LO ATÉ O FIM.\n\nJESUS É MAIS PURO QUE A LINDA FLOR,\nJESUS É MELHOR, ELE, SIM, SATISFAZ,\nJESUS É MELHOR, SIM, ELE É AMOR,\nCAMINHO, LUZ, VERDADE E PAZ.\n\nCORO\nPODE SER UM REI COM PODER NAS \nMÃOS, MAS DO MAL, ESCRAVO, SIM;\nMIL VEZES PREFIRO O MEU JESUS\nE SERVI-LO ATÉ O FIM.', 'JESUS É MELHOR, SIM, QUE OURO E BENS, JESUS É MELHOR DO QUE TUDO QUE TENS, MELHOR QUE RIQUEZAS E POSIÇÕES, MELHOR, MUITO MAIS DO QUE MILHÕES. CORO PODE SER UM REI COM PODER NAS MÃOS, MAS DO MAL, ESCRAVO, SIM; MIL VEZES PREFIRO O MEU JESUS E SERVI-LO ATÉ O FIM. JESUS É MELHOR QUE QUALQUER VALOR, AMIGO LEAL NO PRAZER E NA DOR, MELHOR DO QUE TUDO ELE É PRA MIM, MELHOR QUE QUALQUER BOM AMIGO ENFIM. JESUS É MAIS PURO QUE A LINDA FLOR, JESUS É MELHOR, ELE, SIM, SATISFAZ, JESUS É MELHOR, SIM, ELE É AMOR, CAMINHO, LUZ, VERDADE E PAZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('226', '226 - O SENTIDO DE VIVER', 'O SENTIDO DE VIVER ENCONTRAMOS \nEM JESUS, ELE É A SALVAÇÃO QUE \nHOJE PODES TER;\nÉ SÓ ABRIR TEU CORAÇÃO, \nDEIXAR CRISTO HABITAR, \nA PAZ E A ALEGRIA\nEM TI IRÃO REINAR.\n\nCORO (2X)\nJESUS, O SENTIDO DE VIVER,\n(DE VIVER SÓ PRA JESUS) VARÕES\nJESUS, O AMOR, A PAZ REAL,\nNÃO HÁ OUTRO AMOR IGUAL.\n\nJESUS CRISTO VAI VOLTAR, \nELE UM DIA PROMETEU, \nE SEREMOS TRANSFORMADOS \nPARA O ENCONTRO COM O SENHOR;\nESTAREMOS PARA SEMPRE COM JESUS, \nO REDENTOR, \nONDE SÓ HAVERÁ PAZ, \nNUNCA HAVERÁ DOR.\n\nCORO (2X)\nJESUS, O SENTIDO DE VIVER,\n(DE VIVER SÓ PRA JESUS) VARÕES\nJESUS, O AMOR, A PAZ REAL,\nNÃO HÁ OUTRO AMOR IGUAL. \n\nFINAL: JESUS.', 'O SENTIDO DE VIVER ENCONTRAMOS EM JESUS, ELE É A SALVAÇÃO QUE HOJE PODES TER; É SÓ ABRIR TEU CORAÇÃO, DEIXAR CRISTO HABITAR, A PAZ E A ALEGRIA EM TI IRÃO REINAR. JESUS, O SENTIDO DE VIVER, (DE VIVER SÓ PRA JESUS) VARÕES JESUS, O AMOR, A PAZ REAL, NÃO HÁ OUTRO AMOR IGUAL. JESUS CRISTO VAI VOLTAR, ELE UM DIA PROMETEU, E SEREMOS TRANSFORMADOS PARA O ENCONTRO COM O SENHOR; ESTAREMOS PARA SEMPRE COM JESUS, O REDENTOR, ONDE SÓ HAVERÁ PAZ, NUNCA HAVERÁ DOR. JESUS, O SENTIDO DE VIVER, (DE VIVER SÓ PRA JESUS) VARÕES JESUS, O AMOR, A PAZ REAL, NÃO HÁ OUTRO AMOR IGUAL.  JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('227', '227 - SÓ JESUS TE PODE DAR O QUE PROCURAS', 'SÓ JESUS TE PODE DAR O QUE PROCURAS,\nELE É A PORTA QUE SE ABRE PARA O CÉU;\nBATE AGORA E A PORTA SE ABRIRÁ PRA TI,\nE SABERÁS QUE ELE SEMPRE TE ESPEROU.\n\nCORO\nENTRA E VÊ A GLÓRIA, ENTRA E VÊ O AMOR,\nA FARTURA QUE HÁ NA CASA DO SENHOR;\nNADA SE COMPARA AO QUE VÊS AQUI,\nTUDO ISTO DEUS PREPAROU PARA TI.\n\nTANTAS VEZES PERDESTE O RUMO EM TEU VIVER,\nPORTAS FECHADAS QUE NÃO PUDESTE ABRIR;\nMAS JESUS É A PORTA QUE ABRE PARA TI\nUM NOVO DIA PARA TU SERES FELIZ.\n\nCORO\nENTRA E VÊ A GLÓRIA, ENTRA E VÊ O AMOR,\nA FARTURA QUE HÁ NA CASA DO SENHOR;\nNADA SE COMPARA AO QUE VÊS AQUI,\nTUDO ISTO DEUS PREPAROU PARA TI.\n\nFINAL: PREPAROU PARA TI.', 'SÓ JESUS TE PODE DAR O QUE PROCURAS, ELE É A PORTA QUE SE ABRE PARA O CÉU; BATE AGORA E A PORTA SE ABRIRÁ PRA TI, E SABERÁS QUE ELE SEMPRE TE ESPEROU. CORO ENTRA E VÊ A GLÓRIA, ENTRA E VÊ O AMOR, A FARTURA QUE HÁ NA CASA DO SENHOR; NADA SE COMPARA AO QUE VÊS AQUI, TUDO ISTO DEUS PREPAROU PARA TI. TANTAS VEZES PERDESTE O RUMO EM TEU VIVER, PORTAS FECHADAS QUE NÃO PUDESTE ABRIR; MAS JESUS É A PORTA QUE ABRE PARA TI UM NOVO DIA PARA TU SERES FELIZ. CORO ENTRA E VÊ A GLÓRIA, ENTRA E VÊ O AMOR, A FARTURA QUE HÁ NA CASA DO SENHOR; NADA SE COMPARA AO QUE VÊS AQUI, TUDO ISTO DEUS PREPAROU PARA TI.  PREPAROU PARA TI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('228', '228 - ENTREGA O TEU CAMINHO AO SENHOR', 'ENTREGA O TEU CAMINHO AO SENHOR \nE O MAIS ELE FARÁ, FARÁ SOBRESSAIR A SUA JUSTIÇA \nCOMO A LUZ E O SEU JUÍZO; \nCUMPRIRÁ O TEU DESEJO, \nELE TE MOSTRARÁ O SEU PERDÃO, A SALVAÇÃO,\nA REVELAÇÃO DA PALAVRA DE JESUS (EM TEU SER).\n\nENTREGA O TEU CAMINHO AO \nSENHOR E O MAIS ELE FARÁ,\nFARÁ SOBRESSAIR A SUA JUSTIÇA \nCOMO A LUZ E O SEU JUÍZO, \nO SEU PERDÃO, A SALVAÇÃO,\nA REVELAÇÃO DA PALAVRA DE JESUS (EM TEU SER).\n\nENTREGA O TEU CAMINHO AO SENHOR,\n(ENTREGA O TEU CAMINHO AO SENHOR)\nAO SENHOR.\nBIS', 'ENTREGA O TEU CAMINHO AO SENHOR E O MAIS ELE FARÁ, FARÁ SOBRESSAIR A SUA JUSTIÇA COMO A LUZ E O SEU JUÍZO; CUMPRIRÁ O TEU DESEJO, ELE TE MOSTRARÁ O SEU PERDÃO, A SALVAÇÃO, A REVELAÇÃO DA PALAVRA DE JESUS (EM TEU SER). ENTREGA O TEU CAMINHO AO SENHOR E O MAIS ELE FARÁ, FARÁ SOBRESSAIR A SUA JUSTIÇA COMO A LUZ E O SEU JUÍZO, O SEU PERDÃO, A SALVAÇÃO, A REVELAÇÃO DA PALAVRA DE JESUS (EM TEU SER). ENTREGA O TEU CAMINHO AO SENHOR, (ENTREGA O TEU CAMINHO AO SENHOR) AO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('229', '229 - AO POÇO DE JACÓ CHEGOU JESUS', 'AO POÇO DE JACÓ CHEGOU JESUS,\nESTAVA CANSADO, ALI CHEGOU PRA DESCANSAR;\nCHEGOU TAMBÉM ALI UMA MULHER,\nCOM SEU CÂNTARO ÁGUA FOI BUSCAR.\nJESUS PEDIU-LHE ÁGUA PRA BEBER, MAS A MULHER FEZ EXCEÇÃO E NÃO LHE DEU, \nPOR SER SAMARITANA, JESUS ERA JUDEU, \nMAS DA ÁGUA QUE JESUS DIZIA, ELA BEBEU.\n\nCORO\nSE TU CONHECERAS O DOM DE DEUS,\nTU PEDIRIAS, ELE DARIA PRA BEBER\nDA ÁGUA MUI SUPERNA,\nQUE SALTA PARA A VIDA ETERNA,\nDESTA ÁGUA SÓ JESUS TEM PARA DAR.\n\nJESUS É MAIS QUE O POÇO DE JACÓ,\nELE É A FONTE DE BASTANTE ÁGUA, É SÓ TOMAR;\nTODOS QUE TÊM PECADO É SÓ CHEGAR,\nESTA ÁGUA TEM PODER PRA LIBERTAR.\nA ÁGUA QUE JESUS DÁ, ELA É SUAVE,\nACONSELHÁVEL, E JESUS MANDA BEBER;\nQUEM TOMA DESTA ÁGUA VIDA ETERNA TEM,\nVIVER COM CRISTO, AQUI E NO ALÉM.\n\nCORO\nSE TU CONHECERAS O DOM DE DEUS,\nTU PEDIRIAS, ELE DARIA PRA BEBER\nDA ÁGUA MUI SUPERNA,\nQUE SALTA PARA A VIDA ETERNA,\nDESTA ÁGUA SÓ JESUS TEM PARA DAR.', 'AO POÇO DE JACÓ CHEGOU JESUS, ESTAVA CANSADO, ALI CHEGOU PRA DESCANSAR; CHEGOU TAMBÉM ALI UMA MULHER, COM SEU CÂNTARO ÁGUA FOI BUSCAR. JESUS PEDIU-LHE ÁGUA PRA BEBER, MAS A MULHER FEZ EXCEÇÃO E NÃO LHE DEU, POR SER SAMARITANA, JESUS ERA JUDEU, MAS DA ÁGUA QUE JESUS DIZIA, ELA BEBEU. CORO SE TU CONHECERAS O DOM DE DEUS, TU PEDIRIAS, ELE DARIA PRA BEBER DA ÁGUA MUI SUPERNA, QUE SALTA PARA A VIDA ETERNA, DESTA ÁGUA SÓ JESUS TEM PARA DAR. JESUS É MAIS QUE O POÇO DE JACÓ, ELE É A FONTE DE BASTANTE ÁGUA, É SÓ TOMAR; TODOS QUE TÊM PECADO É SÓ CHEGAR, ESTA ÁGUA TEM PODER PRA LIBERTAR. A ÁGUA QUE JESUS DÁ, ELA É SUAVE, ACONSELHÁVEL, E JESUS MANDA BEBER; QUEM TOMA DESTA ÁGUA VIDA ETERNA TEM, VIVER COM CRISTO, AQUI E NO ALÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('230', '230 - FONTE DE ÁGUAS VIVAS', 'FONTE DE ÁGUAS VIVAS,\nCRISTO JESUS NOS CONCEDE A PAZ,\nPAZ CELESTIAL;\nSACIA A NOSSA SEDE,\nCONSOLA A ALMA, LIVRA-NOS DO MAL.\n\nQUEM TIVER SEDE, SEDE DE ÁGUAS VIVAS,\n(DE GRAÇA LHE DAREI DA FONTE DA VIDA)\nDE GRAÇA LHE DAREI DA FONTE DA VIDA\n(FONTE DA VIDA).\n\nO CAMINHO DO SENHOR\nÉ VERDADEIRO, RETO E PURO,\nJUSTO E PERFEITO.\nGUARDA OS SEUS SERVOS,\nCOM SEGURANÇA ESTENDE SUA MÃO.\n\nNA HORA DA ANGÚSTIA OU MESMO NA TRISTEZA,\n(JESUS NOSSO REFÚGIO, NOSSA FORTALEZA)\nJESUS NOSSO REFÚGIO, NOSSA FORTALEZA\n(FORTALEZA).\n\nSABEMOS QUE EM BREVE (ALELUIA)\nJESUS VOLTARÁ PARA A SUA GLÓRIA \nA IGREJA LEVAR;\nCOM ELE HABITAREMOS (ALELUIA),\nSEREMOS SEU POVO E ELE O NOSSO DEUS.\n\nNÃO HAVERÁ MAIS MORTE,\nNEM PRANTO OU CLAMOR,\nVIVENDO PARA SEMPRE \nCOM O SALVADOR.\n\nNÃO HAVERÁ MAIS MORTE, \nNEM PRANTO OU CLAMOR\n(VIVENDO PARA SEMPRE COM O SALVADOR)\nVIVENDO PARA SEMPRE COM O SALVADOR\n(COM O SALVADOR).', 'FONTE DE ÁGUAS VIVAS, CRISTO JESUS NOS CONCEDE A PAZ, PAZ CELESTIAL; SACIA A NOSSA SEDE, CONSOLA A ALMA, LIVRA-NOS DO MAL. QUEM TIVER SEDE, SEDE DE ÁGUAS VIVAS, (DE GRAÇA LHE DAREI DA FONTE DA VIDA) DE GRAÇA LHE DAREI DA FONTE DA VIDA (FONTE DA VIDA). O CAMINHO DO SENHOR É VERDADEIRO, RETO E PURO, JUSTO E PERFEITO. GUARDA OS SEUS SERVOS, COM SEGURANÇA ESTENDE SUA MÃO. NA HORA DA ANGÚSTIA OU MESMO NA TRISTEZA, (JESUS NOSSO REFÚGIO, NOSSA FORTALEZA) JESUS NOSSO REFÚGIO, NOSSA FORTALEZA (FORTALEZA). SABEMOS QUE EM BREVE (ALELUIA) JESUS VOLTARÁ PARA A SUA GLÓRIA A IGREJA LEVAR; COM ELE HABITAREMOS (ALELUIA), SEREMOS SEU POVO E ELE O NOSSO DEUS. NÃO HAVERÁ MAIS MORTE, NEM PRANTO OU CLAMOR, VIVENDO PARA SEMPRE COM O SALVADOR. NÃO HAVERÁ MAIS MORTE, NEM PRANTO OU CLAMOR (VIVENDO PARA SEMPRE COM O SALVADOR) VIVENDO PARA SEMPRE COM O SALVADOR (COM O SALVADOR).', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('231', '231 - QUEM ME DÁ ÁGUA', 'QUEM ME DÁ ÁGUA QUANDO ESTOU SEDENTO,\nE TRAZ A PAZ QUANDO ME ENCONTRO AFLITO,\nTIRANDO A ANGÚSTIA DENTRO DA MINH’ALMA,\nME ALEGRANDO COM O SEU PRECIOSO AMOR?\n\nELE É JESUS, O REI, \n(O REI) VARÕES\nNELE ENCONTRO PAZ, \n(A PAZ) VARÕES\nELE É MEU SALVADOR,\n(MEU REDENTOR) VARÕES\nDEUS FORTE, REDENTOR.\nBIS\n\nQUERES DESCANSO DENTRO DO TEU SER?\nCONFIA EM CRISTO, ELE TEM PODER,\nVENCEU A MORTE LÁ NA CRUZ.\nVIDA É JESUS, VIDA É JESUS.', 'QUEM ME DÁ ÁGUA QUANDO ESTOU SEDENTO, E TRAZ A PAZ QUANDO ME ENCONTRO AFLITO, TIRANDO A ANGÚSTIA DENTRO DA MINH’ALMA, ME ALEGRANDO COM O SEU PRECIOSO AMOR? ELE É JESUS, O REI, (O REI) VARÕES NELE ENCONTRO PAZ, (A PAZ) VARÕES ELE É MEU SALVADOR, (MEU REDENTOR) VARÕES DEUS FORTE, REDENTOR. QUERES DESCANSO DENTRO DO TEU SER? CONFIA EM CRISTO, ELE TEM PODER, VENCEU A MORTE LÁ NA CRUZ. VIDA É JESUS, VIDA É JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('232', '232 - QUEM BEBER DA ÁGUA QUE EU LHE DER', '“QUEM BEBER DA ÁGUA QUE EU LHE DER, \nJAMAIS TERÁ SEDE DE NOVO,\nPOIS DA ÁGUA QUE EU LHE DER\nSE FARÁ UMA FONTE PRA VIDA ETERNA”.\nBIS\n\nO SENHOR ESTÁ JUNTO A TI,\nPODEROSO PARA TE SALVAR,\nSE HOJE OUVIRES SUA DOCE VOZ,\nNÃO ENDUREÇAS O TEU CORAÇÃO,\nNÃO DIGAS NÃO.\n\n“MAS VEM A MIM,\nÓ TU QUE ESTÁS CANSADO,\nE DESCANSO EU TE DAREI,\nPOIS DA ÁGUA QUE EU TE DER\nSE FARÁ UMA FONTE PRA VIDA ETERNA”.\nBIS', 'QUEM BEBER DA ÁGUA QUE EU LHE DER, JAMAIS TERÁ SEDE DE NOVO, POIS DA ÁGUA QUE EU LHE DER SE FARÁ UMA FONTE PRA VIDA ETERNA. O SENHOR ESTÁ JUNTO A TI, PODEROSO PARA TE SALVAR, SE HOJE OUVIRES SUA DOCE VOZ, NÃO ENDUREÇAS O TEU CORAÇÃO, NÃO DIGAS NÃO. MAS VEM A MIM, Ó TU QUE ESTÁS CANSADO, E DESCANSO EU TE DAREI, POIS DA ÁGUA QUE EU TE DER SE FARÁ UMA FONTE PRA VIDA ETERNA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('233', '233 - VOU GUIAR-TE A UMA RICA FONTE', 'VOU GUIAR-TE A UMA RICA FONTE\nDE ÁGUAS CRISTALINAS,\nMATARÁS A TUA SEDE,\nSUBIRÁS MUITAS COLINAS,\nVOU LEVAR-TE AOS ALTOS MONTES.\n\nSOU O SENHOR, SOU A ROCHA \nFONTE QUE JORRA O AMOR,\nSOU A FÉ QUE DESABROCHA\nNAQUELE QUE DESSEDENTO.\nSOU REAL, SOU O SENHOR!\n\nVENHAM, Ó FILHOS QUERIDOS,\nMORAR ALÉM DO HORIZONTE,\nSALTAR PARA A ETERNIDADE,\nBEBER DA ÁGUA DA VIDA. \n\nONDE EU, JESUS, SOU A FONTE \nONDE EU, JESUS, SOU A FONTE\nONDE EU, JESUS, SOU A FONTE\nBIS', 'VOU GUIAR-TE A UMA RICA FONTE DE ÁGUAS CRISTALINAS, MATARÁS A TUA SEDE, SUBIRÁS MUITAS COLINAS, VOU LEVAR-TE AOS ALTOS MONTES. SOU O SENHOR, SOU A ROCHA FONTE QUE JORRA O AMOR, SOU A FÉ QUE DESABROCHA NAQUELE QUE DESSEDENTO. SOU REAL, SOU O SENHOR! VENHAM, Ó FILHOS QUERIDOS, MORAR ALÉM DO HORIZONTE, SALTAR PARA A ETERNIDADE, BEBER DA ÁGUA DA VIDA. ONDE EU, JESUS, SOU A FONTE ONDE EU, JESUS, SOU A FONTE ONDE EU, JESUS, SOU A FONTE', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('234', '234 - DEUS HOJE QUER TE FALAR', 'DEUS HOJE QUER TE FALAR,\nELE BATE AO TEU CORAÇÃO;\nSE TU ABRIRES, ELE VAI ENTRAR\nE TE DARÁ SALVAÇÃO.\n\nVOZ TÃO SUAVE ELE TEM,\nFALANDO COM TANTO AMOR;\nPOR TI JESUS MORREU NA CRUZ,\nELE É O TEU SALVADOR.\n\nSE HOJE OUVIRES SUA VOZ,\nABRE-LHE TEU CORAÇÃO;\nENTÃO TERÁS AMOR E PAZ\nQUE JAMAIS TERMINARÃO.\nBIS', 'DEUS HOJE QUER TE FALAR, ELE BATE AO TEU CORAÇÃO; SE TU ABRIRES, ELE VAI ENTRAR E TE DARÁ SALVAÇÃO. VOZ TÃO SUAVE ELE TEM, FALANDO COM TANTO AMOR; POR TI JESUS MORREU NA CRUZ, ELE É O TEU SALVADOR. SE HOJE OUVIRES SUA VOZ, ABRE-LHE TEU CORAÇÃO; ENTÃO TERÁS AMOR E PAZ QUE JAMAIS TERMINARÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('235', '235 - MANSO E SUAVE JESUS ESTÁ CHAMANDO', 'MANSO E SUAVE JESUS ESTÁ CHAMANDO,\nCHAMA POR TI E POR MIM;\nEIS QUE À PORTA ESPERA VELANDO,\nVELA POR TI E POR MIM.\n\nCORO\nVEM JÁ, VEM JÁ! \nESTÁS CANSADO, VEM JÁ!\nMANSO E SUAVE JESUS ESTÁ CHAMANDO;\nCHAMA: “Ó PECADOR, VEM!”\n\nO TEMPO CORRE, AS HORAS SE PASSAM,\nPASSAM PRA TI E PRA MIM;\nMORTE E LEITO DE DOR PRESTO CHAMAM,\nCHAMAM POR TI E POR MIM.\n\nCORO\nVEM JÁ, VEM JÁ! \nESTÁS CANSADO, VEM JÁ!\nMANSO E SUAVE JESUS ESTÁ CHAMANDO;\nCHAMA: “Ó PECADOR, VEM!”\n\nQUE ESPERAMOS? JESUS \nCONVIDANDO,\nCONVIDA A TI E A MIM.\nPOR QUE DESPREZAS MERCÊ \nQUE ESTÁ DANDO, \nDANDO A TI E A MIM?\n\nCORO\nVEM JÁ, VEM JÁ! \nESTÁS CANSADO, VEM JÁ!\nMANSO E SUAVE JESUS ESTÁ CHAMANDO;\nCHAMA: “Ó PECADOR, VEM!”\n\nOH! QUE AMOR QUE JESUS NOS TEM DADO,\nDADO PRA TI E PRA MIM!\nMORREU PRA SALVAR-NOS DO VIL PECADO, \nSALVAR A TI E A MIM.\n\nCORO\nVEM JÁ, VEM JÁ! \nESTÁS CANSADO, VEM JÁ!\nMANSO E SUAVE JESUS ESTÁ CHAMANDO;\nCHAMA: “Ó PECADOR, VEM!”', 'MANSO E SUAVE JESUS ESTÁ CHAMANDO, CHAMA POR TI E POR MIM; EIS QUE À PORTA ESPERA VELANDO, VELA POR TI E POR MIM. CORO VEM JÁ, VEM JÁ! ESTÁS CANSADO, VEM JÁ! MANSO E SUAVE JESUS ESTÁ CHAMANDO; CHAMA: Ó PECADOR, VEM! O TEMPO CORRE, AS HORAS SE PASSAM, PASSAM PRA TI E PRA MIM; MORTE E LEITO DE DOR PRESTO CHAMAM, CHAMAM POR TI E POR MIM. QUE ESPERAMOS? JESUS CONVIDANDO, CONVIDA A TI E A MIM. POR QUE DESPREZAS MERCÊ QUE ESTÁ DANDO, DANDO A TI E A MIM? OH! QUE AMOR QUE JESUS NOS TEM DADO, DADO PRA TI E PRA MIM! MORREU PRA SALVAR-NOS DO VIL PECADO, SALVAR A TI E A MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('236', '236 - OUVE A VOZ DE JESUS', 'OUVE A VOZ DE JESUS\nQUE É TÃO LINDA E TÃO CALMA\nE ESTÁ CHAMANDO: “VEM JÁ!”\nSE ESTÁS CANSADO, DISSE ELE ASSIM:\n“MEU FILHO, VEM HOJE A MIM!”\nINSTRUMENTOS\n\nSE ESTÁS CANSADO, DISSE ELE ASSIM:\n“MEU FILHO, VEM HOJE A MIM!”\n\nMANSO E SUAVE É JESUS,\nQUÃO DOCE E MEIGO É OUVI-LO!\nVEM JÁ OUVIR SUA VOZ;\nSE ESTÁS CANSADO, DISSE JESUS: \n“EU SOU O CAMINHO E A LUZ.”\nBIS', 'OUVE A VOZ DE JESUS QUE É TÃO LINDA E TÃO CALMA E ESTÁ CHAMANDO: VEM JÁ! SE ESTÁS CANSADO, DISSE ELE ASSIM: MEU FILHO, VEM HOJE A MIM! INSTRUMENTOS SE ESTÁS CANSADO, DISSE ELE ASSIM: MEU FILHO, VEM HOJE A MIM! MANSO E SUAVE É JESUS, QUÃO DOCE E MEIGO É OUVI-LO! VEM JÁ OUVIR SUA VOZ; SE ESTÁS CANSADO, DISSE JESUS: EU SOU O CAMINHO E A LUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('237', '237 - EIS MENSAGEM DO SENHOR', 'EIS MENSAGEM DO SENHOR; ALELUIA!\nPALAVRAS DO BOM DEUS DE AMOR!\nCRISTO SALVA O PECADOR; ALELUIA!\nSALVA-O ATÉ POR MEIO DE UM OLHAR.\n\nCORO\nOH! OLHAI, IRMÃOS, OLHAI,\nOH! OLHAI SÓ PRA JESUS.\nELE SALVA O PECADOR; ALELUIA!\nSALVA-O ATÉ POR MEIO DE UM OLHAR!\n\nVOSSA DÍVIDA PAGOU; ALELUIA!\nJESUS A SATISFEZ NA CRUZ.\nSUA VIDA ENTREGOU; ALELUIA!\nPARA VOS APRESENTAR A DEUS!\n\nCORO\nOH! OLHAI, IRMÃOS, OLHAI,\nOH! OLHAI SÓ PRA JESUS.\nELE SALVA O PECADOR; ALELUIA!\nSALVA-O ATÉ POR MEIO DE UM OLHAR!\n\nESTA OFERTA É FEITA A VÓS; ALELUIA!\nETERNA VIDA LÁ NOS CÉUS!\nOH! OLHAI PRA CRISTO SÓ; ALELUIA!\nCONVERTEI-VOS JÁ AO VOSSO DEUS.\n\nCORO\nOH! OLHAI, IRMÃOS, OLHAI,\nOH! OLHAI SÓ PRA JESUS.\nELE SALVA O PECADOR; ALELUIA!\nSALVA-O ATÉ POR MEIO DE UM OLHAR!\n\nACEITAI A SALVAÇÃO; ALELUIA!\nSEGUI NOS PASSOS DO SENHOR;\nPUBLICAI O SEU PERDÃO; ALELUIA!\nPROCLAMAI O GRANDE REDENTOR!\n\nCORO\nOH! OLHAI, IRMÃOS, OLHAI,\nOH! OLHAI SÓ PRA JESUS.\nELE SALVA O PECADOR; ALELUIA!\nSALVA-O ATÉ POR MEIO DE UM OLHAR!', 'EIS MENSAGEM DO SENHOR; ALELUIA! PALAVRAS DO BOM DEUS DE AMOR! CRISTO SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR. CORO OH! OLHAI, IRMÃOS, OLHAI, OH! OLHAI SÓ PRA JESUS. ELE SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR! VOSSA DÍVIDA PAGOU; ALELUIA! JESUS A SATISFEZ NA CRUZ. SUA VIDA ENTREGOU; ALELUIA! PARA VOS APRESENTAR A DEUS! ESTA OFERTA É FEITA A VÓS; ALELUIA! ETERNA VIDA LÁ NOS CÉUS! OH! OLHAI PRA CRISTO SÓ; ALELUIA! CONVERTEI-VOS JÁ AO VOSSO DEUS. ACEITAI A SALVAÇÃO; ALELUIA! SEGUI NOS PASSOS DO SENHOR; PUBLICAI O SEU PERDÃO; ALELUIA! PROCLAMAI O GRANDE REDENTOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('238', '238 - TANTO TEMPO JÁ FAZ QUE O CHAMADO DIVINAL', 'TANTO TEMPO JÁ FAZ QUE O CHAMADO DIVINAL\nTEM CHEGADO AO VIL PECADOR;\n“SE ESTÁS OPRIMIDO, VEM A MIM SEM TARDAR,\nPOIS EU TE AMO, REPOUSO TE DAREI”.\n\nCORO\nEU PERTENÇO AO REI DO UNIVERSO,\nPARTICIPO DA HERANÇA REAL;\nAO FINDAR O LABUTAR,\nTÉ O FIM DA NOSSA HISTÓRIA\nVIVEREI AOS CUIDADOS DO REI.\n\nPROCURAVA ACHAR UM LUGAR PARA \nMIM, ALMEJAVA JESUS ENCONTRAR;\nTÃO LOGO ELE ACHOU-ME E DEU-ME UM LAR,\nUM NOVO HINO AGORA VOU CANTAR.\n\nCORO\nEU PERTENÇO AO REI DO UNIVERSO,\nPARTICIPO DA HERANÇA REAL;\nAO FINDAR O LABUTAR,\nTÉ O FIM DA NOSSA HISTÓRIA\nVIVEREI AOS CUIDADOS DO REI. (2X)', 'TANTO TEMPO JÁ FAZ QUE O CHAMADO DIVINAL TEM CHEGADO AO VIL PECADOR; SE ESTÁS OPRIMIDO, VEM A MIM SEM TARDAR, POIS EU TE AMO, REPOUSO TE DAREI. CORO EU PERTENÇO AO REI DO UNIVERSO, PARTICIPO DA HERANÇA REAL; AO FINDAR O LABUTAR, TÉ O FIM DA NOSSA HISTÓRIA VIVEREI AOS CUIDADOS DO REI. PROCURAVA ACHAR UM LUGAR PARA MIM, ALMEJAVA JESUS ENCONTRAR; TÃO LOGO ELE ACHOU-ME E DEU-ME UM LAR, UM NOVO HINO AGORA VOU CANTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('239', '239 - DEIXA DEUS FALAR AO TEU CORAÇÃO', 'DEIXA DEUS FALAR AO TEU CORAÇÃO,\nELE QUER SE REVELAR A TI,\nSUA VOZ APRENDE A ESCUTAR,\nDEIXA DEUS TE FALAR;\nBUSCA MAIS SUA PRESENÇA,\nVIGIANDO EM ORAÇÃO,\nSUA VOZ IRÁS, ENTÃO, OUVIR\nFALANDO AO TEU CORAÇÃO.\n\nTEUS PROBLEMAS DEUS PODE RESOLVER,\nDÁ-LHE TUA VIDA SEM TEMER,\nSE TEUS PASSOS DEIXAS DEUS GUIAR,\nPAZ IRÁS EXPERIMENTAR;\nELE TEM UM PLANO PARA TI\nE TE GUIA COM SUA MÃO,\nSIM, ESCUTA O QUE ELE AGORA DIZ,\nFALANDO AO TEU CORAÇÃO. (BIS)', 'DEIXA DEUS FALAR AO TEU CORAÇÃO, ELE QUER SE REVELAR A TI, SUA VOZ APRENDE A ESCUTAR, DEIXA DEUS TE FALAR; BUSCA MAIS SUA PRESENÇA, VIGIANDO EM ORAÇÃO, SUA VOZ IRÁS, ENTÃO, OUVIR FALANDO AO TEU CORAÇÃO. TEUS PROBLEMAS DEUS PODE RESOLVER, DÁ-LHE TUA VIDA SEM TEMER, SE TEUS PASSOS DEIXAS DEUS GUIAR, PAZ IRÁS EXPERIMENTAR; ELE TEM UM PLANO PARA TI E TE GUIA COM SUA MÃO, SIM, ESCUTA O QUE ELE AGORA DIZ, FALANDO AO TEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('240', '240 - SE CANSADO TU ESTÁS', 'SE CANSADO TU ESTÁS, \nSOBRECARREGADO SIM, \nDEIXA TUDO AGORA, DIGO: \nVEM A MIM!\nEU TE ALIVIAREI, PÕE TEU FARDO \nSOBRE MIM, DEIXA TUDO AGORA, \nDIGO: VEM A MIM!\n\nVEM A MIM, Ó PECADOR,\nQUERO SER TEU SALVADOR,\nPOIS SOU MANSO \nE HUMILDE DE CORAÇÃO;\nVEM A MIM, DESCANSARÁS,\nTUA ALMA REPOUSARÁ,\nQUE SUAVE É MEU JUGO \nE LEVE O MEU FARDO.\nBIS', 'SE CANSADO TU ESTÁS, SOBRECARREGADO SIM, DEIXA TUDO AGORA, DIGO: VEM A MIM! EU TE ALIVIAREI, PÕE TEU FARDO SOBRE MIM, DEIXA TUDO AGORA, DIGO: VEM A MIM! VEM A MIM, Ó PECADOR, QUERO SER TEU SALVADOR, POIS SOU MANSO E HUMILDE DE CORAÇÃO; VEM A MIM, DESCANSARÁS, TUA ALMA REPOUSARÁ, QUE SUAVE É MEU JUGO E LEVE O MEU FARDO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('241', '241 - MEU AMADO FILHO', 'MEU AMADO FILHO, TE ESCOLHI,\nNÃO TEMAS, MAS ESFORÇA-TE,\nASSIM DIZ O SENHOR;\nMINHA MÃO ESTENDIDA PARA TI, \nPOR ONDE QUER QUE FORES, \nNÃO TE DEIXAREI.\n\nCORO (2X)\nMINHA PAZ EU TE DOU,\nNÃO TE DEIXAREI, DIZ O SENHOR.\n\nA TERRA PROMETIDA TE DAREI,\nA ÁRVORE DA VIDA TE CONCEDEREI;\nRIOS DE ÁGUA VIVA FLUIRÃO\nE O MEU IMENSO AMOR NO TEU CORAÇÃO.\n\nCORO (2X)\nMINHA PAZ EU TE DOU,\nNÃO TE DEIXAREI, DIZ O SENHOR.\n\nNÃO TE DEIXAREI, DIZ O SENHOR. (3X)', 'MEU AMADO FILHO, TE ESCOLHI, NÃO TEMAS, MAS ESFORÇA-TE, ASSIM DIZ O SENHOR; MINHA MÃO ESTENDIDA PARA TI, POR ONDE QUER QUE FORES, NÃO TE DEIXAREI. MINHA PAZ EU TE DOU, NÃO TE DEIXAREI, DIZ O SENHOR. A TERRA PROMETIDA TE DAREI, A ÁRVORE DA VIDA TE CONCEDEREI; RIOS DE ÁGUA VIVA FLUIRÃO E O MEU IMENSO AMOR NO TEU CORAÇÃO. MINHA PAZ EU TE DOU, NÃO TE DEIXAREI, DIZ O SENHOR. NÃO TE DEIXAREI, DIZ O SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('242', '242 - SE ESTA NOITE DEUS PEDIR TUA ALMA', 'SE ESTA NOITE DEUS PEDIR TUA ALMA,\nO QUE TENS PREPARADO, PRA QUEM SERÁ?\nDE QUE SERVE CIÊNCIA OU RIQUEZA\nSE TUA ALMA PRA SEMPRE PERDERÁS?\n\nCORO\nVEM A JESUS, HOJE, SEM TARDAR,\nDOS FARDOS TEUS ELE QUER TE LIBERTAR;\nSE ESTA NOITE DEUS PEDIR TUA ALMA,\nPODES CRER: O CÉU SERÁ TEU LAR.\n\nINSTRUMENTOS\n\nDE QUE VALE TER TUDO NESTA VIDA\nE NÃO TER A CERTEZA DA SALVAÇÃO?\nTU PRECISAS VIR HOJE AO SALVADOR,\nPELA FÉ NO SEU SANGUE, TER PERDÃO.\n\nCORO\nVEM A JESUS, HOJE, SEM TARDAR,\nDOS FARDOS TEUS ELE QUER TE LIBERTAR;\nSE ESTA NOITE DEUS PEDIR TUA ALMA,\nPODES CRER: O CÉU SERÁ TEU LAR.', 'SE ESTA NOITE DEUS PEDIR TUA ALMA, O QUE TENS PREPARADO, PRA QUEM SERÁ? DE QUE SERVE CIÊNCIA OU RIQUEZA SE TUA ALMA PRA SEMPRE PERDERÁS? CORO VEM A JESUS, HOJE, SEM TARDAR, DOS FARDOS TEUS ELE QUER TE LIBERTAR; SE ESTA NOITE DEUS PEDIR TUA ALMA, PODES CRER: O CÉU SERÁ TEU LAR. INSTRUMENTOS DE QUE VALE TER TUDO NESTA VIDA E NÃO TER A CERTEZA DA SALVAÇÃO? TU PRECISAS VIR HOJE AO SALVADOR, PELA FÉ NO SEU SANGUE, TER PERDÃO. CORO VEM A JESUS, HOJE, SEM TARDAR, DOS FARDOS TEUS ELE QUER TE LIBERTAR; SE ESTA NOITE DEUS PEDIR TUA ALMA, PODES CRER: O CÉU SERÁ TEU LAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('243', '243 - JÁ ACHEI UMA FLOR GLORIOSA', 'JÁ ACHEI UMA FLOR GLORIOSA\nE QUEM DESEJA A MESMA TERÁ;\nA ROSA DE SAROM PRECIOSA\nENTRE MIL A MAIS BELA SERÁ;\nNO VALE DE SOMBRA E MORTE,\nNAS ALTURAS DE GLÓRIA E DE LUZ,\nESTA ROSA SERÁ A MINHA SORTE,\nPRECIOSO PRA MIM É JESUS!\n\nCORO\nPRECIOSO PRA MIM É JESUS! (BIS)\nEU CONFESSO NA VIDA E NA MORTE\nQUE TUDO PRA MIM É JESUS!\n\nJÁ DE MUITOS FOI ACHADA A ROSA\nE PROVADO O EXCELENTE ODOR\nE O PODER DESTA FLOR GLORIOSA\nQUE DÁ VIDA AO VIL PECADOR;\nMUI ZELOSO PELA LEI FOI SAULO,\nPERSEGUIA O POVO DE DEUS,\nMAS TRANSFORMADO FOI EM UM PAULO\nPOIS ACHOU ELE A ROSA DOS CÉUS.\n\nCORO\nPRECIOSO PRA MIM É JESUS! (BIS)\nEU CONFESSO NA VIDA E NA MORTE\nQUE TUDO PRA MIM É JESUS!\n\nVAI BUSCAR A JESUS PRECIOSO,\nVAI DEPRESSA, A NOITE JÁ VEM,\nE SE PERDES O AMOR GLORIOSO,\nSERÁ TRISTE PRA TI O ALÉM;\nESTA FLOR HOJE É OFERTADA\nA QUEM HUMILDEMENTE A BUSCAR,\nSERÁ LOGO DA TERRA TIRADA\nPARA BRILHAR EM OUTRO LUGAR.\n\nCORO\nPRECIOSO PRA MIM É JESUS! (BIS)\nEU CONFESSO NA VIDA E NA MORTE\nQUE TUDO PRA MIM É JESUS!', 'JÁ ACHEI UMA FLOR GLORIOSA E QUEM DESEJA A MESMA TERÁ; A ROSA DE SAROM PRECIOSA ENTRE MIL A MAIS BELA SERÁ; NO VALE DE SOMBRA E MORTE, NAS ALTURAS DE GLÓRIA E DE LUZ, ESTA ROSA SERÁ A MINHA SORTE, PRECIOSO PRA MIM É JESUS! CORO PRECIOSO PRA MIM É JESUS!  EU CONFESSO NA VIDA E NA MORTE QUE TUDO PRA MIM É JESUS! JÁ DE MUITOS FOI ACHADA A ROSA E PROVADO O EXCELENTE ODOR E O PODER DESTA FLOR GLORIOSA QUE DÁ VIDA AO VIL PECADOR; MUI ZELOSO PELA LEI FOI SAULO, PERSEGUIA O POVO DE DEUS, MAS TRANSFORMADO FOI EM UM PAULO POIS ACHOU ELE A ROSA DOS CÉUS. VAI BUSCAR A JESUS PRECIOSO, VAI DEPRESSA, A NOITE JÁ VEM, E SE PERDES O AMOR GLORIOSO, SERÁ TRISTE PRA TI O ALÉM; ESTA FLOR HOJE É OFERTADA A QUEM HUMILDEMENTE A BUSCAR, SERÁ LOGO DA TERRA TIRADA PARA BRILHAR EM OUTRO LUGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('244', '244 - EU DESCI PARA O VALE DE BÊNÇÃO E PAZ', 'EU DESCI PARA O VALE DE BÊNÇÃO E PAZ\nE SINTO COMIGO JESUS;\nSEU SANGUE, AOS HUMILDES, \nGARANTE O PERDÃO,\nSEU ESPÍRITO OS ENCHE DE LUZ.\n\nCORO\nENTRAI NESSE VALE DE BÊNÇÃO E \nPAZ ONDE CRISTO REVELA AFEIÇÃO;\nACEITAI, ABRAÇAI, CONFESSAI-O,\nPUBLICAI QUE NELE HÁ SALVAÇÃO.\n\nHÁ FESTIM NESSE VALE DE BÊNÇÃO \nE PAZ E RIQUEZA DE INCRÍVEL VALOR;\nMAS SOMENTE OS FIÉIS \nÉ QUE PODEM CONTAR \nCOM A GRAÇA DO BOM SALVADOR.\n\nCORO\nENTRAI NESSE VALE DE BÊNÇÃO E \nPAZ ONDE CRISTO REVELA AFEIÇÃO;\nACEITAI, ABRAÇAI, CONFESSAI-O,\nPUBLICAI QUE NELE HÁ SALVAÇÃO.\n\nHÁ SALMOS NO VALE DE BÊNÇÃO \nE PAZ E OS ANJOS DESEJAM \nSE UNIR A CANTAR COM OS HOMENS \nO EXCELSO LOUVOR DE JESUS \nQUE NOS VEIO REMIR.\n\nCORO\nENTRAI NESSE VALE DE BÊNÇÃO E \nPAZ ONDE CRISTO REVELA AFEIÇÃO;\nACEITAI, ABRAÇAI, CONFESSAI-O,\nPUBLICAI QUE NELE HÁ SALVAÇÃO.', 'EU DESCI PARA O VALE DE BÊNÇÃO E PAZ E SINTO COMIGO JESUS; SEU SANGUE, AOS HUMILDES, GARANTE O PERDÃO, SEU ESPÍRITO OS ENCHE DE LUZ. CORO ENTRAI NESSE VALE DE BÊNÇÃO E PAZ ONDE CRISTO REVELA AFEIÇÃO; ACEITAI, ABRAÇAI, CONFESSAI-O, PUBLICAI QUE NELE HÁ SALVAÇÃO. HÁ FESTIM NESSE VALE DE BÊNÇÃO E PAZ E RIQUEZA DE INCRÍVEL VALOR; MAS SOMENTE OS FIÉIS É QUE PODEM CONTAR COM A GRAÇA DO BOM SALVADOR. HÁ SALMOS NO VALE DE BÊNÇÃO E PAZ E OS ANJOS DESEJAM SE UNIR A CANTAR COM OS HOMENS O EXCELSO LOUVOR DE JESUS QUE NOS VEIO REMIR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('245', '245 - POR ONDE QUER QUE EU VÁ', 'POR ONDE QUER QUE EU VÁ\nCOM CRISTO ESTAREI,\nNÃO IMPORTA O QUE VIER,\nELE ME AJUDARÁ;\nMESMO QUE MINHA VIDA\nNO MUNDO CHEGUE AO FIM,\nTEREI UMA NOVA VIDA,\nETERNA, COM JESUS.\n\nCORO \nQUERES SER FELIZ? \nCANTA COMO EU, SABE QUE JESUS POR TI MORREU.\nQUERES NESTE MUNDO SER UM VENCEDOR?\nENTREGA-TE A JESUS, O SALVADOR.\n\nINSTRUMENTOS\n\nJESUS DISSE: “VINDE A MIM\nVÓS QUE ESTAIS CANSADOS,\nQUE EU VOS ALIVIAREI\nE TAMBÉM VOS SALVAREI”.\nJESUS, EU QUERO OUVIR\nO TOQUE DA TROMBETA,\nNESSE DIA EU QUERO IR\nAOS CÉUS TE ENCONTRAR.\n\nCORO (2X)\nQUERES SER FELIZ? \nCANTA COMO EU, SABE QUE JESUS POR TI MORREU.\nQUERES NESTE MUNDO SER UM VENCEDOR?\nENTREGA-TE A JESUS, O SALVADOR.', 'POR ONDE QUER QUE EU VÁ COM CRISTO ESTAREI, NÃO IMPORTA O QUE VIER, ELE ME AJUDARÁ; MESMO QUE MINHA VIDA NO MUNDO CHEGUE AO FIM, TEREI UMA NOVA VIDA, ETERNA, COM JESUS. CORO QUERES SER FELIZ? CANTA COMO EU, SABE QUE JESUS POR TI MORREU. QUERES NESTE MUNDO SER UM VENCEDOR? ENTREGA-TE A JESUS, O SALVADOR. INSTRUMENTOS JESUS DISSE: VINDE A MIM VÓS QUE ESTAIS CANSADOS, QUE EU VOS ALIVIAREI E TAMBÉM VOS SALVAREI. JESUS, EU QUERO OUVIR O TOQUE DA TROMBETA, NESSE DIA EU QUERO IR AOS CÉUS TE ENCONTRAR. QUERES SER FELIZ? CANTA COMO EU, SABE QUE JESUS POR TI MORREU. QUERES NESTE MUNDO SER UM VENCEDOR? ENTREGA-TE A JESUS, O SALVADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('246', '246 - SE EM TEU VIVER', 'SE EM TEU VIVER EXISTE UM VAZIO,\nSE TU NÃO PODES CANTAR\nCOM OS QUE CANTAM PRA JESUS,\nNÃO TE DETENHAS, NÃO CHORES, \nMEU AMIGO,\nCRISTO TRANSFORMA AS TREVAS \nEM PLENA LUZ.\n\nCORO\nFELIZ SERÁS, JAMAIS VERÁS\nTUA VIDA EM PRANTO SE FINDAR;\nFELIZ SERÁS SE AO SENHOR\nTEU CORAÇÃO ENTREGAR.\n\nCONTEMPLA AGORA AS BÊNÇÃOS DE CRISTO, \nQUANDO, EM SORRISOS, \nTUAS LÁGRIMAS ELE VAI TRANSFORMAR,\nPRA SEMPRE GUIARÁ OS TEUS CAMINHOS\nE COM AMOR AO CÉU TE LEVARÁ.\n\nCORO\nFELIZ SERÁS, JAMAIS VERÁS\nTUA VIDA EM PRANTO SE FINDAR;\nFELIZ SERÁS SE AO SENHOR\nTEU CORAÇÃO ENTREGAR.', 'SE EM TEU VIVER EXISTE UM VAZIO, SE TU NÃO PODES CANTAR COM OS QUE CANTAM PRA JESUS, NÃO TE DETENHAS, NÃO CHORES, MEU AMIGO, CRISTO TRANSFORMA AS TREVAS EM PLENA LUZ. CORO FELIZ SERÁS, JAMAIS VERÁS TUA VIDA EM PRANTO SE FINDAR; FELIZ SERÁS SE AO SENHOR TEU CORAÇÃO ENTREGAR. CONTEMPLA AGORA AS BÊNÇÃOS DE CRISTO, QUANDO, EM SORRISOS, TUAS LÁGRIMAS ELE VAI TRANSFORMAR, PRA SEMPRE GUIARÁ OS TEUS CAMINHOS E COM AMOR AO CÉU TE LEVARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('247', '247 - SE ANDA TRISTE O TEU VIVER', 'SE ANDA TRISTE O TEU VIVER,\nSE ESTÁ VAZIO O TEU CORAÇÃO,\nEXISTE ALGUÉM QUE PODE DAR\nA PAZ QUE TANTO VENS A PROCURAR.\n\nCORO \nJESUS, ESSE ALGUÉM!\nJESUS, PODE DAR ALGO QUE VENS A PROCURAR,\nJESUS, BOM AMIGO QUE NÃO PODE FALTAR.\n\nSE ANDAS SEM A SOLUÇÃO,\nANGÚSTIA INVADE O TEU CORAÇÃO,\nEXISTE ALGUÉM QUE PODE DAR\nA PAZ QUE TANTO VENS A PROCURAR.\n\nCORO (2X)\nJESUS, ESSE ALGUÉM!\nJESUS, PODE DAR ALGO QUE VENS A PROCURAR,\nJESUS, BOM AMIGO QUE NÃO PODE FALTAR.', 'SE ANDA TRISTE O TEU VIVER, SE ESTÁ VAZIO O TEU CORAÇÃO, EXISTE ALGUÉM QUE PODE DAR A PAZ QUE TANTO VENS A PROCURAR. CORO JESUS, ESSE ALGUÉM! JESUS, PODE DAR ALGO QUE VENS A PROCURAR, JESUS, BOM AMIGO QUE NÃO PODE FALTAR. SE ANDAS SEM A SOLUÇÃO, ANGÚSTIA INVADE O TEU CORAÇÃO, EXISTE ALGUÉM QUE PODE DAR A PAZ QUE TANTO VENS A PROCURAR. JESUS, ESSE ALGUÉM! JESUS, PODE DAR ALGO QUE VENS A PROCURAR, JESUS, BOM AMIGO QUE NÃO PODE FALTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('248', '248 - SE VOCÊ QUISER SABER', 'SE VOCÊ QUISER SABER COMO PODE SER FELIZ,\nBASTA ABRIR SEU CORAÇÃO E ACEITAR JESUS.\n\nSE VOCÊ QUER CONHECER\nESTE DEUS MARAVILHOSO\nQUE DE BÊNÇÃOS VAI ENCHER SEU CORAÇÃO.\n\nCORO\nNELE HÁ TANTA VIDA,\nCERTEZAS E ESPERANÇAS,\nVIDA ETERNA E PAZ SEM FIM;\nNELE HÁ SALVAÇÃO.\n\nSE VOCÊ QUISER GOZAR\nDESTA GRANDE SALVAÇÃO,\nVENHA HOJE A JESUS SE ENTREGAR.\n\nCORO\nNELE HÁ TANTA VIDA,\nCERTEZAS E ESPERANÇAS,\nVIDA ETERNA E PAZ SEM FIM;\nNELE HÁ SALVAÇÃO.\n\nFINAL: \nSE VOCÊ QUISER SABER \nCOMO PODE SER FELIZ,\nBASTA ABRIR SEU CORAÇÃO \nE ACEITAR JESUS.', 'SE VOCÊ QUISER SABER COMO PODE SER FELIZ, BASTA ABRIR SEU CORAÇÃO E ACEITAR JESUS. SE VOCÊ QUER CONHECER ESTE DEUS MARAVILHOSO QUE DE BÊNÇÃOS VAI ENCHER SEU CORAÇÃO. CORO NELE HÁ TANTA VIDA, CERTEZAS E ESPERANÇAS, VIDA ETERNA E PAZ SEM FIM; NELE HÁ SALVAÇÃO. SE VOCÊ QUISER GOZAR DESTA GRANDE SALVAÇÃO, VENHA HOJE A JESUS SE ENTREGAR. CORO NELE HÁ TANTA VIDA, CERTEZAS E ESPERANÇAS, VIDA ETERNA E PAZ SEM FIM; NELE HÁ SALVAÇÃO. SE VOCÊ QUISER SABER COMO PODE SER FELIZ, BASTA ABRIR SEU CORAÇÃO E ACEITAR JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('249', '249 - SE EM TUA VIDA FALTA PAZ', 'SE EM TUA VIDA FALTA PAZ,\nSE EM TEU VIVER NÃO EXISTE AMOR,\nQUERO TE FALAR DO MEU SENHOR,\nE UMA VIDA NOVA ENCONTRARÁS.\n\nCORO\nCRISTO É A VIDA, ELE É A PAZ,\nFONTE DE ÁGUA VIVA\nQUE A TODOS SATISFAZ.\n\nSÊ UMA NOVA CRIATURA,\nENTREGA OS TEUS PECADOS A JESUS,\nARREPENDE-TE E COMEÇA A VIVER,\nANUNCIANDO QUE JESUS É A SALVAÇÃO.\n\nCORO\nCRISTO É A VIDA, ELE É A PAZ,\nFONTE DE ÁGUA VIVA\nQUE A TODOS SATISFAZ.', 'SE EM TUA VIDA FALTA PAZ, SE EM TEU VIVER NÃO EXISTE AMOR, QUERO TE FALAR DO MEU SENHOR, E UMA VIDA NOVA ENCONTRARÁS. CORO CRISTO É A VIDA, ELE É A PAZ, FONTE DE ÁGUA VIVA QUE A TODOS SATISFAZ. SÊ UMA NOVA CRIATURA, ENTREGA OS TEUS PECADOS A JESUS, ARREPENDE-TE E COMEÇA A VIVER, ANUNCIANDO QUE JESUS É A SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('250', '250 - SE TU PROCURAS PELA PAZ', 'SE TU PROCURAS PELA PAZ,\nSE VAZIO ESTÁ TEU SER,\nNUNCA O MUNDO BUSCARÁS,\nMAS DEVES A JESUS TEMER;\nSE PROCURARES MAIS A CRISTO,\nSÓ VERDADE ENCONTRARÁS,\nPOIS SÓ MEU CRISTO \nA ALMA SATISFAZ.\n\nCORO\nCRISTO SATISFAZ,\nSÓ CRISTO A ALMA SATISFAZ,\nE ELE PODE COMPLETAR TEU CORAÇÃO;\nDARÁ A PAZ, TÃO DOCE AMOR,\nENFIM NOS CÉUS HABITARÁS,\nPOIS SÓ MEU CRISTO \nA ALMA SATISFAZ.\n\nSE FAMA E BENS ALMEJAS TER,\nTÃO EM VÃO É O TEU VIVER,\nPOIS A CRISTO NÃO TERÁS,\nA REDENÇÃO SÓ ELE DÁ;\nJAMAIS EM BENS CONFIARÁS,\nSALVAÇÃO ALI NÃO HÁ,\nPOIS SÓ MEU CRISTO \nA ALMA SATISFAZ.\n\nCORO\nCRISTO SATISFAZ,\nSÓ CRISTO A ALMA SATISFAZ,\nE ELE PODE COMPLETAR TEU CORAÇÃO;\nDARÁ A PAZ, TÃO DOCE AMOR,\nENFIM NOS CÉUS HABITARÁS,\nPOIS SÓ MEU CRISTO \nA ALMA SATISFAZ.\n\nFINAL: CRISTO SATISFAZ!', 'SE TU PROCURAS PELA PAZ, SE VAZIO ESTÁ TEU SER, NUNCA O MUNDO BUSCARÁS, MAS DEVES A JESUS TEMER; SE PROCURARES MAIS A CRISTO, SÓ VERDADE ENCONTRARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ. CORO CRISTO SATISFAZ, SÓ CRISTO A ALMA SATISFAZ, E ELE PODE COMPLETAR TEU CORAÇÃO; DARÁ A PAZ, TÃO DOCE AMOR, ENFIM NOS CÉUS HABITARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ. SE FAMA E BENS ALMEJAS TER, TÃO EM VÃO É O TEU VIVER, POIS A CRISTO NÃO TERÁS, A REDENÇÃO SÓ ELE DÁ; JAMAIS EM BENS CONFIARÁS, SALVAÇÃO ALI NÃO HÁ, POIS SÓ MEU CRISTO A ALMA SATISFAZ. CORO CRISTO SATISFAZ, SÓ CRISTO A ALMA SATISFAZ, E ELE PODE COMPLETAR TEU CORAÇÃO; DARÁ A PAZ, TÃO DOCE AMOR, ENFIM NOS CÉUS HABITARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ.  CRISTO SATISFAZ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('251', '251 - A PAZ QUE TU PROCURAS', 'A PAZ QUE TU PROCURAS, \nA ALEGRIA QUE ALMEJAS\nHOJE PODES ENCONTRAR,\nAQUELE QUE POR TI MORREU\nHOJE AQUI ESTÁ E QUER TE SALVAR.\n\nCORO \nLOUVAI A JESUS\nE JUNTOS EXALTEMOS O SEU NOME,\nPROCLAMAI, (MARANATA!) \nPROCLAMAI: ELE VOLTARÁ! \n\nINSTRUMENTOS\n\nELE ESTÁ A TE CONTEMPLAR\nE COM SUA DOCE VOZ IRÁ FALAR\nAO TEU CORAÇÃO:\n“VEM, FILHO MEU, RECEBE A VIDA\nQUE ESTAVAS A PROCURAR.”\n\nCORO (2X)\nLOUVAI A JESUS\nE JUNTOS EXALTEMOS O SEU NOME,\nPROCLAMAI, (MARANATA!) \nPROCLAMAI: ELE VOLTARÁ! \n\nFINAL: \nPROCLAMAI (MARANATA!) \nPROCLAMAI: ELE VOLTARÁ!', 'A PAZ QUE TU PROCURAS, A ALEGRIA QUE ALMEJAS HOJE PODES ENCONTRAR, AQUELE QUE POR TI MORREU HOJE AQUI ESTÁ E QUER TE SALVAR. CORO LOUVAI A JESUS E JUNTOS EXALTEMOS O SEU NOME, PROCLAMAI, (MARANATA!) PROCLAMAI: ELE VOLTARÁ! INSTRUMENTOS ELE ESTÁ A TE CONTEMPLAR E COM SUA DOCE VOZ IRÁ FALAR AO TEU CORAÇÃO: VEM, FILHO MEU, RECEBE A VIDA QUE ESTAVAS A PROCURAR. LOUVAI A JESUS E JUNTOS EXALTEMOS O SEU NOME, PROCLAMAI, (MARANATA!) PROCLAMAI: ELE VOLTARÁ!  PROCLAMAI (MARANATA!) PROCLAMAI: ELE VOLTARÁ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('252', '252 - SE DISPUSERES O TEU CORAÇÃO  (JÓ 11:13-19)', 'SE DISPUSERES O TEU CORAÇÃO\nE ESTENDERES AS MÃOS A DEUS,\nSE LANÇARES LONGE DE TUAS MÃOS \nTODA A INIQÜIDADE,\nSE NÃO PERMITIRES HABITAR\nEM TUA TENDA A INJUSTIÇA.\n\nCORO\nENTÃO LEVANTARÁS \nO TEU ROSTO SEM MÁCULA,\nESTARÁS SEGURO E NÃO TEMERÁS,\nPOIS TE ESQUECERÁS \nDOS TEUS SOFRIMENTOS,\nDELES TE LEMBRARÁS\nCOMO ÁGUAS QUE PASSARAM.\n\nTUA VIDA SERÁ MAIS CLARA QUE O MEIO DIA,\nAINDA QUE HAJA TREVAS, SERÁ COMO A MANHÃ,\nSENTIR-TE-ÁS SEGURO PORQUE HAVERÁ ESPERANÇA.\n\nCORO\nENTÃO LEVANTARÁS \nO TEU ROSTO SEM MÁCULA,\nESTARÁS SEGURO E NÃO TEMERÁS,\nPOIS TE ESQUECERÁS \nDOS TEUS SOFRIMENTOS,\nDELES TE LEMBRARÁS\nCOMO ÁGUAS QUE PASSARAM.\n\nOLHARÁS AO REDOR E DORMIRÁS TRANQUILO,\nDEITAR-TE-ÁS E NINGUÉM TE ESPANTARÁ,\nMUITOS PROCURARÃO OBTER O TEU FAVOR.\n\nCORO\nENTÃO LEVANTARÁS \nO TEU ROSTO SEM MÁCULA,\nESTARÁS SEGURO E NÃO TEMERÁS,\nPOIS TE ESQUECERÁS \nDOS TEUS SOFRIMENTOS,\nDELES TE LEMBRARÁS\nCOMO ÁGUAS QUE PASSARAM.\nSE DISPUSERES O TEU CORAÇÃO. (BIS)', 'SE DISPUSERES O TEU CORAÇÃO E ESTENDERES AS MÃOS A DEUS, SE LANÇARES LONGE DE TUAS MÃOS TODA A INIQÜIDADE, SE NÃO PERMITIRES HABITAR EM TUA TENDA A INJUSTIÇA. CORO ENTÃO LEVANTARÁS O TEU ROSTO SEM MÁCULA, ESTARÁS SEGURO E NÃO TEMERÁS, POIS TE ESQUECERÁS DOS TEUS SOFRIMENTOS, DELES TE LEMBRARÁS COMO ÁGUAS QUE PASSARAM. TUA VIDA SERÁ MAIS CLARA QUE O MEIO DIA, AINDA QUE HAJA TREVAS, SERÁ COMO A MANHÃ, SENTIR-TE-ÁS SEGURO PORQUE HAVERÁ ESPERANÇA. OLHARÁS AO REDOR E DORMIRÁS TRANQUILO, DEITAR-TE-ÁS E NINGUÉM TE ESPANTARÁ, MUITOS PROCURARÃO OBTER O TEU FAVOR. CORO ENTÃO LEVANTARÁS O TEU ROSTO SEM MÁCULA, ESTARÁS SEGURO E NÃO TEMERÁS, POIS TE ESQUECERÁS DOS TEUS SOFRIMENTOS, DELES TE LEMBRARÁS COMO ÁGUAS QUE PASSARAM. SE DISPUSERES O TEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('253', '253 - SE DISPUSERES O TEU CORAÇÃO', 'SE DISPUSERES O TEU CORAÇÃO\nE ESTENDERES TUA MÃO PRA DEUS,\nSE LANÇARES PARA LONGE\nA INIQUIDADE DE TUA MÃO,\nESTARÁS SEGURO E NÃO TEMERÁS.\n\nSE TU NÃO PERMITIRES QUE A INJUSTIÇA,\nEM TUA TENDA FAÇA HABITAÇÃO,\nSE LANÇARES PARA LONGE\nA INIQUIDADE DE TUA MÃO,\nESTARÁS SEGURO E NÃO TEMERÁS.\n\nTUA VIDA ENTÃO SERÁ MAIS CLARA QUE A MANHÃ,\nOLHARÁS EM VOLTA E DORMIRÁS TRANQUILO,\nMAS OS OLHOS DOS PERVERSOS DESFALECERÃO,\nSEU REFÚGIO PERECERÁ.', 'SE DISPUSERES O TEU CORAÇÃO E ESTENDERES TUA MÃO PRA DEUS, SE LANÇARES PARA LONGE A INIQUIDADE DE TUA MÃO, ESTARÁS SEGURO E NÃO TEMERÁS. SE TU NÃO PERMITIRES QUE A INJUSTIÇA, EM TUA TENDA FAÇA HABITAÇÃO, SE LANÇARES PARA LONGE A INIQUIDADE DE TUA MÃO, ESTARÁS SEGURO E NÃO TEMERÁS. TUA VIDA ENTÃO SERÁ MAIS CLARA QUE A MANHÃ, OLHARÁS EM VOLTA E DORMIRÁS TRANQUILO, MAS OS OLHOS DOS PERVERSOS DESFALECERÃO, SEU REFÚGIO PERECERÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('254', '254 - OLHAI PARA O ALTO', 'CORO (2X)\nOLHAI PARA O ALTO\nDE ONDE VEM A SALVAÇÃO,\nSEGUI PARA O ALVO, À ETERNA VOCAÇÃO.\n\nTODO JUSTO PELA FÉ VIVERÁ,\nSEU OLHAR ESTÁ FIRME NO SENHOR,\nESPERANDO ENCONTRÁ-LO EM RESPLENDOR, \nPORQUE BREVE A TROMBETA SOARÁ. \nÓ OLHAI!\n\nCORO (2X)\nOLHAI PARA O ALTO\nDE ONDE VEM A SALVAÇÃO,\nSEGUI PARA O ALVO, À ETERNA VOCAÇÃO.\n\nESQUECENDO O QUE FICOU PARA TRÁS,\nSEGUE O JUSTO SEU CAMINHO COM JESUS,\nDIA A DIA CARREGANDO A SUA CRUZ,\nPORQUE BREVE JESUS CRISTO VOLTARÁ. \nÓ OLHAI!\n\nCORO (2X)\nOLHAI PARA O ALTO\nDE ONDE VEM A SALVAÇÃO,\nSEGUI PARA O ALVO, À ETERNA VOCAÇÃO.', 'OLHAI PARA O ALTO DE ONDE VEM A SALVAÇÃO, SEGUI PARA O ALVO, À ETERNA VOCAÇÃO. TODO JUSTO PELA FÉ VIVERÁ, SEU OLHAR ESTÁ FIRME NO SENHOR, ESPERANDO ENCONTRÁ-LO EM RESPLENDOR, PORQUE BREVE A TROMBETA SOARÁ. Ó OLHAI! ESQUECENDO O QUE FICOU PARA TRÁS, SEGUE O JUSTO SEU CAMINHO COM JESUS, DIA A DIA CARREGANDO A SUA CRUZ, PORQUE BREVE JESUS CRISTO VOLTARÁ. Ó OLHAI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('255', '255 - MEU CORAÇÃO ENGRANDECE  (CÂNTICO DE VITÓRIA)', 'MEU CORAÇÃO ENGRANDECE AO SENHOR PORQUE ELE MUDOU \nTODO O MEU SER,\nFEZ-ME ANDAR EM VEREDAS\nQUE EU NÃO CONHECIA.\n\nILUMINOU OS MEUS PÉS\nPARA QUE EU NÃO TROPEÇASSE,\nMEU CORAÇÃO JUBILOU DE ALEGRIA\nQUANDO EM MIM VEIO MORAR,\nFEZ EM MIM HABITAÇÃO PRO SEU ESPÍRITO.\nCOLOCOU UM CÂNTICO NOVO EM MEUS LÁBIOS, \nAQUELE QUE EU NÃO CONHECIA,\nPELO SEU ESPÍRITO, ME FEZ CONHECER.\n\n“DESPERTA,\n(DESPERTA) SERVAS\nTU QUE DORMES,  \n(TU QUE DORMES) SERVAS\nDEIXA O SOL              \n(DEIXA O SOL) SERVAS\nRESPLANDECER SOBRE TUA VIDA.”\nINSTRUMENTOS\n\n“O SOL DESPONTA SOBRE A CIDADE\nQUANDO OS CONVOCO A ME ADORAR;\nJERUSALÉM, MORADA DO DEUS VIVO,\nCRIADOR DO UNIVERSO.\nMINHA MÃO ESTÁ SOBRE O MEU POVO,\nPOIS EU O TENHO ESCOLHIDO,\nCOM MINHAS MÃOS PODEROSAS\nFAÇO MORADA EM SEUS CORAÇÕES.”\n\n“REJUBILA, POVO MEU! \n(REJUBILA) SERVAS\nPORQUE EM BREVE VIRÁ \nMINHA GLÓRIA E VÓS \nPODEREIS LEVANTAR UM ESTANDARTE \nENTRE AS NAÇÕES \nE PROCLAMAR QUE SOU REI.”\nBIS', 'MEU CORAÇÃO ENGRANDECE AO SENHOR PORQUE ELE MUDOU TODO O MEU SER, FEZ-ME ANDAR EM VEREDAS QUE EU NÃO CONHECIA. ILUMINOU OS MEUS PÉS PARA QUE EU NÃO TROPEÇASSE, MEU CORAÇÃO JUBILOU DE ALEGRIA QUANDO EM MIM VEIO MORAR, FEZ EM MIM HABITAÇÃO PRO SEU ESPÍRITO. COLOCOU UM CÂNTICO NOVO EM MEUS LÁBIOS, AQUELE QUE EU NÃO CONHECIA, PELO SEU ESPÍRITO, ME FEZ CONHECER. DESPERTA, (DESPERTA) SERVAS TU QUE DORMES, (TU QUE DORMES) SERVAS DEIXA O SOL (DEIXA O SOL) SERVAS RESPLANDECER SOBRE TUA VIDA. INSTRUMENTOS O SOL DESPONTA SOBRE A CIDADE QUANDO OS CONVOCO A ME ADORAR; JERUSALÉM, MORADA DO DEUS VIVO, CRIADOR DO UNIVERSO. MINHA MÃO ESTÁ SOBRE O MEU POVO, POIS EU O TENHO ESCOLHIDO, COM MINHAS MÃOS PODEROSAS FAÇO MORADA EM SEUS CORAÇÕES. REJUBILA, POVO MEU! (REJUBILA) SERVAS PORQUE EM BREVE VIRÁ MINHA GLÓRIA E VÓS PODEREIS LEVANTAR UM ESTANDARTE ENTRE AS NAÇÕES E PROCLAMAR QUE SOU REI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('256', '256 - NÃO MAIS VIVO EU MAS CRISTO VIVE EM MIM', 'NÃO MAIS VIVO EU,\nMAS CRISTO VIVE EM MIM\nDESDE O DIA EM QUE ACEITEI\nA JESUS, O SALVADOR;\nA MINHA VIDA SE TRANSFORMOU\nE O SEU AMOR A MIM SE REVELOU;\n\nBUSCAI A DEUS ENQUANTO SE PODE ACHAR\nE INVOCAI SEU NOME POIS ELE ESTÁ PERTO;\nJESUS VIVE EM MIM E QUER VIVER EM TI\nE REVELAR AS MARAVILHAS DO SEU ESPÍRITO.\n\nCORO\nSÃO TANTAS BÊNÇÃOS, SENHOR!\nSÃO TANTAS BÊNÇÃOS, SENHOR! \n(SÃO TANTAS BÊNÇÃOS) VARÕES\nBÊNÇÃOS QUE NÃO SE PODEM CONTAR;\n(NÃO SE PODEM CONTAR) VARÕES\nCANTEMOS GLÓRIA AO SENHOR,\nHONRA E PODER AO REI DOS REIS POIS \nELE VOLTARÁ PRA NOS LEVAR E REINARÁ.\n\nDEUS QUER TE DAR PAZ,\nBÊNÇÃOS E O GOZO CELESTIAL,\nSE ASSIM QUISERES, ELE O FARÁ.\n\nCORO\nSÃO TANTAS BÊNÇÃOS, SENHOR!\nSÃO TANTAS BÊNÇÃOS, SENHOR! \n(SÃO TANTAS BÊNÇÃOS) VARÕES\nBÊNÇÃOS QUE NÃO SE PODEM CONTAR;\n(NÃO SE PODEM CONTAR) VARÕES\nCANTEMOS GLÓRIA AO SENHOR,\nHONRA E PODER AO REI DOS REIS POIS \nELE VOLTARÁ PRA NOS LEVAR E REINARÁ.', 'NÃO MAIS VIVO EU, MAS CRISTO VIVE EM MIM DESDE O DIA EM QUE ACEITEI A JESUS, O SALVADOR; A MINHA VIDA SE TRANSFORMOU E O SEU AMOR A MIM SE REVELOU; BUSCAI A DEUS ENQUANTO SE PODE ACHAR E INVOCAI SEU NOME POIS ELE ESTÁ PERTO; JESUS VIVE EM MIM E QUER VIVER EM TI E REVELAR AS MARAVILHAS DO SEU ESPÍRITO. CORO SÃO TANTAS BÊNÇÃOS, SENHOR! SÃO TANTAS BÊNÇÃOS, SENHOR! (SÃO TANTAS BÊNÇÃOS) VARÕES BÊNÇÃOS QUE NÃO SE PODEM CONTAR; (NÃO SE PODEM CONTAR) VARÕES CANTEMOS GLÓRIA AO SENHOR, HONRA E PODER AO REI DOS REIS POIS ELE VOLTARÁ PRA NOS LEVAR E REINARÁ. DEUS QUER TE DAR PAZ, BÊNÇÃOS E O GOZO CELESTIAL, SE ASSIM QUISERES, ELE O FARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('257', '257 - TUDO SE FEZ NOVO', 'TUDO SE FEZ NOVO\nE AS COISAS VELHAS JÁ SE PASSARAM\nPOIS O SENHOR FARÁ CONTIGO\nUMA ALIANÇA ETERNA QUE JAMAIS SE ACABARÁ.\n\nDOS TEUS PECADOS DEUS\nNÃO MAIS SE LEMBRARÁ,\nTUA VIDA ELE VAI TRANSFORMAR,\nO TEU FARDO SOBRE SI ELE VAI LEVAR\nE AS TUAS LÁGRIMAS ELE VAI ENXUGAR.\n\nCORO (2X)\nNÃO SOU EU MAIS QUEM VIVE\nMAS CRISTO VIVE EM MIM,\nE A VIDA QUE AGORA VIVO\nEU A VIVO PELA FÉ.\n\nESTE CHAMADO VEM DE DEUS,\nNÃO VEM DO HOMEM MAS VEM DO SENHOR; \nNÃO TEMAS POIS FOI DEUS QUEM TE ESCOLHEU, \nNÃO TEMAS POIS É ELE QUEM DÁ FORÇAS PRA VENCER; NÃO TEMAS PORQUE ELE NÃO TE DEIXARÁ, \nNÃO TEMAS POIS O SEU AMOR É GRANDE.\n\nCORO (2X)\nNÃO SOU EU MAIS QUEM VIVE\nMAS CRISTO VIVE EM MIM,\nE A VIDA QUE AGORA VIVO\nEU A VIVO PELA FÉ. (2X)', 'TUDO SE FEZ NOVO E AS COISAS VELHAS JÁ SE PASSARAM POIS O SENHOR FARÁ CONTIGO UMA ALIANÇA ETERNA QUE JAMAIS SE ACABARÁ. DOS TEUS PECADOS DEUS NÃO MAIS SE LEMBRARÁ, TUA VIDA ELE VAI TRANSFORMAR, O TEU FARDO SOBRE SI ELE VAI LEVAR E AS TUAS LÁGRIMAS ELE VAI ENXUGAR. NÃO SOU EU MAIS QUEM VIVE MAS CRISTO VIVE EM MIM, E A VIDA QUE AGORA VIVO EU A VIVO PELA FÉ. ESTE CHAMADO VEM DE DEUS, NÃO VEM DO HOMEM MAS VEM DO SENHOR; NÃO TEMAS POIS FOI DEUS QUEM TE ESCOLHEU, NÃO TEMAS POIS É ELE QUEM DÁ FORÇAS PRA VENCER; NÃO TEMAS PORQUE ELE NÃO TE DEIXARÁ, NÃO TEMAS POIS O SEU AMOR É GRANDE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('258', '258 - SE TU ESTÁS TÃO LONGE DO SENHOR', 'SE TU ESTÁS TÃO LONGE DO SENHOR,\nSE ESQUECESTE DE TUDO O QUE ELE FEZ POR TI,\nAINDA HÁ TEMPO PARA VOLTARES\nAOS BRAÇOS DAQUELE QUE TE AMA.\n\nCORO\n“VOLTA, FILHO MEU, \nPARA OS BRAÇOS MEUS \nPORQUE JAMAIS ME ESQUECI DE TI, \nO TEU NOME TENHO GRAVADO \nEM MINHAS MÃOS.”\n\n“EU SOU JESUS QUEM FALA CONTIGO,\nO TEU CONSOLO QUANDO ESTÁS ABATIDO,\nO REFÚGIO EM MOMENTOS DIFÍCEIS,\nEU TE LEVANTO QUANDO ESTÁS CAÍDO.”\n\nCORO (2X)\n“VOLTA, FILHO MEU, \nPARA OS BRAÇOS MEUS \nPORQUE JAMAIS ME ESQUECI DE TI, \nO TEU NOME TENHO GRAVADO \nEM MINHAS MÃOS.”\n\nFINAL: “EM MINHAS MÃOS.”', 'SE TU ESTÁS TÃO LONGE DO SENHOR, SE ESQUECESTE DE TUDO O QUE ELE FEZ POR TI, AINDA HÁ TEMPO PARA VOLTARES AOS BRAÇOS DAQUELE QUE TE AMA. CORO VOLTA, FILHO MEU, PARA OS BRAÇOS MEUS PORQUE JAMAIS ME ESQUECI DE TI, O TEU NOME TENHO GRAVADO EM MINHAS MÃOS. EU SOU JESUS QUEM FALA CONTIGO, O TEU CONSOLO QUANDO ESTÁS ABATIDO, O REFÚGIO EM MOMENTOS DIFÍCEIS, EU TE LEVANTO QUANDO ESTÁS CAÍDO. VOLTA, FILHO MEU, PARA OS BRAÇOS MEUS PORQUE JAMAIS ME ESQUECI DE TI, O TEU NOME TENHO GRAVADO EM MINHAS MÃOS.  EM MINHAS MÃOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('259', '259 - COMO UM BOM PASTOR', 'COMO UM BOM PASTOR \nPROCURA UMA OVELHA PERDIDA, \nJESUS ME BUSCOU E DEU LUZ À MINHA VIDA;\nCOM TANTO AMOR FALOU AO MEU CORAÇÃO,\nLIVROU-ME DO MAL,                  \nCUROU MINHAS CHAGAS,\nDEU-ME SALVAÇÃO!\nBIS\n\nEM SEUS FORTES BRAÇOS \nDESCANSO E REFÚGIO ACHEI,\nELE É MEU PASTOR E CUIDA DE MIM, EU SEI!\nCOMO UMA OVELHA, NO PASTO SEU, CAMINHO;\nSEGUINDO A JESUS, \nOUVINDO SUA VOZ,\nSENTINDO SEU AMOR!\nBIS\n\nAMIGO, POR QUE, SEM RUMO, VIVER NESTE MUNDO?\nPOR QUE TANTA DOR, SE EXISTE \nESSE AMOR PROFUNDO?\nÓ VEM A JESUS, TU ÉS UMA OVELHA PERDIDA;\nSÓ CRISTO TE PODE DAR SALVAÇÃO,\nENTREGA-LHE TUA VIDA!\nBIS', 'COMO UM BOM PASTOR PROCURA UMA OVELHA PERDIDA, JESUS ME BUSCOU E DEU LUZ À MINHA VIDA; COM TANTO AMOR FALOU AO MEU CORAÇÃO, LIVROU-ME DO MAL, CUROU MINHAS CHAGAS, DEU-ME SALVAÇÃO! EM SEUS FORTES BRAÇOS DESCANSO E REFÚGIO ACHEI, ELE É MEU PASTOR E CUIDA DE MIM, EU SEI! COMO UMA OVELHA, NO PASTO SEU, CAMINHO; SEGUINDO A JESUS, OUVINDO SUA VOZ, SENTINDO SEU AMOR! AMIGO, POR QUE, SEM RUMO, VIVER NESTE MUNDO? POR QUE TANTA DOR, SE EXISTE ESSE AMOR PROFUNDO? Ó VEM A JESUS, TU ÉS UMA OVELHA PERDIDA; SÓ CRISTO TE PODE DAR SALVAÇÃO, ENTREGA-LHE TUA VIDA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('260', '260 - DEUS ESTÁ PRESENTE AQUI NESTE LUGAR', 'DEUS ESTÁ PRESENTE AQUI NESTE LUGAR,\nELE QUER O SEU AMOR NOS REVELAR,\nTUDO ELE CONHECE, TUDO ELE PODE,\nMESMO TRANSFORMAR A TREVA EM LUZ.\n\nABRE O TEU CORAÇÃO A JESUS,\nDEIXA SOBRE TI BRILHAR SUA LUZ,\nTUDO ELE CONHECE, TUDO ELE PODE,\nDEUS ESTÁ AQUI NESTE LUGAR.\n\nINSTRUMENTOS\n\nABRE O TEU CORAÇÃO A JESUS,\nDEIXA SOBRE TI BRILHAR SUA LUZ,\nTUDO ELE CONHECE, TUDO ELE PODE,\nDEUS ESTÁ AQUI NESTE LUGAR.\n\nFINAL: DEUS ESTÁ AQUI NESTE LUGAR.', 'DEUS ESTÁ PRESENTE AQUI NESTE LUGAR, ELE QUER O SEU AMOR NOS REVELAR, TUDO ELE CONHECE, TUDO ELE PODE, MESMO TRANSFORMAR A TREVA EM LUZ. ABRE O TEU CORAÇÃO A JESUS, DEIXA SOBRE TI BRILHAR SUA LUZ, TUDO ELE CONHECE, TUDO ELE PODE, DEUS ESTÁ AQUI NESTE LUGAR. INSTRUMENTOS ABRE O TEU CORAÇÃO A JESUS, DEIXA SOBRE TI BRILHAR SUA LUZ, TUDO ELE CONHECE, TUDO ELE PODE, DEUS ESTÁ AQUI NESTE LUGAR.  DEUS ESTÁ AQUI NESTE LUGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('261', '261 - CRISTO CAMINHAVA LENTAMENTE', 'CRISTO CAMINHAVA LENTAMENTE,\nPASSO A PASSO COM DECISÃO;\nDE REPENTE ELE FALOU: “ALGUÉM TOCOU-ME, \nALGUÉM NO MEIO DA MULTIDÃO”.\n“MESTRE, A MULTIDÃO TE OPRIME\nDE TODOS OS LADOS, ASSIM”;\nCRISTO DISSE: “ALGUÉM TOCOU-ME DIFERENTE, \nPOIS EU SENTI SAIR VIRTUDE DE MIM”.\n\nCORO\nTOCA PELA FÉ AGORA EM JESUS\nPOIS ELE VAI TE CONCEDER A LUZ,\nESTENDE A MÃO, JESUS VAI TE TOCAR;\nTOCA PELA FÉ AGORA, É SÓ PEDIR,\nQUE DE JESUS VIRTUDE VAI SAIR, \nESTENDE A MÃO, JESUS VAI TE TOCAR. \nINSTRUMENTOS\n\nENTÃO UMA MULHER APROXIMOU-SE\nE DISSE: “FUI EU, SENHOR, QUEM TE TOCOU”;\nCRISTO ALI FALOU-LHE MUI SUAVEMENTE:\n“VAI EM PAZ, A TUA FÉ TE SALVOU.”\nSE QUERES A ALEGRIA EM TUA VIDA,\nSE QUERES A VITÓRIA EM TEU LAR,\nSENTE AGORA A MORTE TRANSFORMAR-SE \nEM VIDA QUANDO A MÃO \nDE DEUS TE TOCAR.\n\nCORO\nTOCA PELA FÉ AGORA EM JESUS\nPOIS ELE VAI TE CONCEDER A LUZ,\nESTENDE A MÃO, JESUS VAI TE TOCAR;\nTOCA PELA FÉ AGORA, É SÓ PEDIR,\nQUE DE JESUS VIRTUDE VAI SAIR, \nESTENDE A MÃO, JESUS VAI TE TOCAR.\nBIS', 'CRISTO CAMINHAVA LENTAMENTE, PASSO A PASSO COM DECISÃO; DE REPENTE ELE FALOU: ALGUÉM TOCOU-ME, ALGUÉM NO MEIO DA MULTIDÃO. MESTRE, A MULTIDÃO TE OPRIME DE TODOS OS LADOS, ASSIM; CRISTO DISSE: ALGUÉM TOCOU-ME DIFERENTE, POIS EU SENTI SAIR VIRTUDE DE MIM. CORO TOCA PELA FÉ AGORA EM JESUS POIS ELE VAI TE CONCEDER A LUZ, ESTENDE A MÃO, JESUS VAI TE TOCAR; TOCA PELA FÉ AGORA, É SÓ PEDIR, QUE DE JESUS VIRTUDE VAI SAIR, ESTENDE A MÃO, JESUS VAI TE TOCAR. INSTRUMENTOS ENTÃO UMA MULHER APROXIMOU-SE E DISSE: FUI EU, SENHOR, QUEM TE TOCOU; CRISTO ALI FALOU-LHE MUI SUAVEMENTE: VAI EM PAZ, A TUA FÉ TE SALVOU. SE QUERES A ALEGRIA EM TUA VIDA, SE QUERES A VITÓRIA EM TEU LAR, SENTE AGORA A MORTE TRANSFORMAR-SE EM VIDA QUANDO A MÃO DE DEUS TE TOCAR. CORO TOCA PELA FÉ AGORA EM JESUS POIS ELE VAI TE CONCEDER A LUZ, ESTENDE A MÃO, JESUS VAI TE TOCAR; TOCA PELA FÉ AGORA, É SÓ PEDIR, QUE DE JESUS VIRTUDE VAI SAIR, ESTENDE A MÃO, JESUS VAI TE TOCAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('262', '262 - QUERO QUE VOCÊ CONHEÇA', 'QUERO QUE VOCÊ CONHEÇA\n(QUERO QUE VOCÊ CONHEÇA) VARÕES\nJESUS, O AUTOR DA NOSSA FÉ;\nQUERO QUE VOCÊ CONHEÇA\n(QUERO QUE VOCÊ CONHEÇA) VARÕES\nTODO O SEU AMOR QUE TEM.\n\nCORO\nELE SALVA, ELE CURA, \nELE DÁ VIDA ETERNA,\nE TUDO O QUE ELE FAZ É POR AMOR.\nABRE A BOCA DO MUDO, \n(DÁ VISTA AO CEGO) VARÕES\nDÁ PALAVRA AO UNGIDO.\nBIS\n\nOH! JESUS MARAVILHOSO,\n(OH! JESUS MARAVILHOSO) VARÕES\nJESUS, O AUTOR DA NOSSA FÉ\nE TUDO QUE ELE FAZ É POR AMOR.\n\nCORO\nELE SALVA, ELE CURA, \nELE DÁ VIDA ETERNA,\nE TUDO O QUE ELE FAZ É POR AMOR.\nABRE A BOCA DO MUDO, \n(DÁ VISTA AO CEGO) VARÕES\nDÁ PALAVRA AO UNGIDO.\nBIS\n\nFINAL: (3X)\nOH! JESUS MARAVILHOSO, \n(OH! JESUS MARAVILHOSO) VARÕES\nJESUS, O AUTOR DA NOSSA FÉ.', 'QUERO QUE VOCÊ CONHEÇA (QUERO QUE VOCÊ CONHEÇA) VARÕES JESUS, O AUTOR DA NOSSA FÉ; QUERO QUE VOCÊ CONHEÇA (QUERO QUE VOCÊ CONHEÇA) VARÕES TODO O SEU AMOR QUE TEM. CORO ELE SALVA, ELE CURA, ELE DÁ VIDA ETERNA, E TUDO O QUE ELE FAZ É POR AMOR. ABRE A BOCA DO MUDO, (DÁ VISTA AO CEGO) VARÕES DÁ PALAVRA AO UNGIDO. OH! JESUS MARAVILHOSO, (OH! JESUS MARAVILHOSO) VARÕES JESUS, O AUTOR DA NOSSA FÉ E TUDO QUE ELE FAZ É POR AMOR. OH! JESUS MARAVILHOSO, (OH! JESUS MARAVILHOSO) VARÕES JESUS, O AUTOR DA NOSSA FÉ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('263', '263 - QUERES IR PARA GLÓRIA?', 'QUERES IR PRA GLÓRIA? PÁRA UM MOMENTO,\nESCUTA ESTA HISTÓRIA, VEM HOJE MESMO,\nQUE, DO NAZARENO, FAÇO A DESCRIÇÃO;\nCRISTO SEM PECADO, \nFOI TRASPASSADO PRA TE DAR PERDÃO, \nVEM A CRISTO AGORA, NESTA MESMA HORA, \nTEM A SALVAÇÃO.\nBIS\n\nCORO\nQUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO,\nCRÊ EM CRISTO, AGORA, ELE É A SALVAÇÃO!\nQUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, \nCRÊ EM CRISTO, AGORA,\nELE É A CONDUÇÃO!\nINSTRUMENTOS\n\nMEU DISTINTO AMIGO,HÁ UM GRANDE PERIGO,\nQUEM NÃO ESTÁ NA LUZ, PARA UM POUQUINHO,\nENTRA NO CAMINHO, ENTREGA-TE A JESUS;\nSE ESTÁS CANSADO, CRISTO JÁ SOFREU \nPOR TI NA CRUZ, VEM A CRISTO AGORA \nE TERÁS VITÓRIA\nBIS\nBIS\n\nCORO\nQUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO,\nCRÊ EM CRISTO, AGORA, ELE É A SALVAÇÃO!\nQUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, \nCRÊ EM CRISTO, AGORA,\nELE É A CONDUÇÃO!', 'QUERES IR PRA GLÓRIA? PÁRA UM MOMENTO, ESCUTA ESTA HISTÓRIA, VEM HOJE MESMO, QUE, DO NAZARENO, FAÇO A DESCRIÇÃO; CRISTO SEM PECADO, FOI TRASPASSADO PRA TE DAR PERDÃO, VEM A CRISTO AGORA, NESTA MESMA HORA, TEM A SALVAÇÃO. CORO QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A SALVAÇÃO! QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A CONDUÇÃO! INSTRUMENTOS MEU DISTINTO AMIGO,HÁ UM GRANDE PERIGO, QUEM NÃO ESTÁ NA LUZ, PARA UM POUQUINHO, ENTRA NO CAMINHO, ENTREGA-TE A JESUS; SE ESTÁS CANSADO, CRISTO JÁ SOFREU POR TI NA CRUZ, VEM A CRISTO AGORA E TERÁS VITÓRIA CORO QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A SALVAÇÃO! QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A CONDUÇÃO!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('264', '264 - OH! QUE BELOS HINOS HOJE LÁ NO CÉU', 'OH! QUE BELOS HINOS HOJE LÁ NO CÉU,\nJÁ DO MUNDO O PECADOR VOLTOU;\nVEDE NO CAMINHO O PAI ABRAÇAR\nESSE FILHO QUE ELE TANTO AMOU.\n\nCORO\nGLÓRIA, GLÓRIA, OS ANJOS CANTAM LÁ!\nGLORIA, GLÓRIA, AS HARPAS TOCAM JÁ!\nÉ O SANTO CORO DANDO GLÓRIA A DEUS\nPOR MAIS UM REMIDO ENTRAR NOS CÉUS.\n\nOH! QUE BELOS HINOS HOJE LÁ NO CÉU,\nÉ QUE JÁ SE RECONCILIOU \nA ALMA ARREPENDIDA QUE, \nRENDIDA A DEUS,\nRENASCIDA PARA O LAR VOLTOU.\n\nCORO\nGLÓRIA, GLÓRIA, OS ANJOS CANTAM LÁ!\nGLORIA, GLÓRIA, AS HARPAS TOCAM JÁ!\nÉ O SANTO CORO DANDO GLÓRIA A DEUS\nPOR MAIS UM REMIDO ENTRAR NOS CÉUS.\n\nESSE ARREPENDIDO, VINDE, FESTEJAR\nCOMO OS ANJOS FAZEM COM FERVOR;\nIDE, POIS, ALEGRES, IDE, ANUNCIAI,\nQUE SE RESGATOU UM PECADOR.', 'OH! QUE BELOS HINOS HOJE LÁ NO CÉU, JÁ DO MUNDO O PECADOR VOLTOU; VEDE NO CAMINHO O PAI ABRAÇAR ESSE FILHO QUE ELE TANTO AMOU. CORO GLÓRIA, GLÓRIA, OS ANJOS CANTAM LÁ! GLORIA, GLÓRIA, AS HARPAS TOCAM JÁ! É O SANTO CORO DANDO GLÓRIA A DEUS POR MAIS UM REMIDO ENTRAR NOS CÉUS. OH! QUE BELOS HINOS HOJE LÁ NO CÉU, É QUE JÁ SE RECONCILIOU A ALMA ARREPENDIDA QUE, RENDIDA A DEUS, RENASCIDA PARA O LAR VOLTOU. ESSE ARREPENDIDO, VINDE, FESTEJAR COMO OS ANJOS FAZEM COM FERVOR; IDE, POIS, ALEGRES, IDE, ANUNCIAI, QUE SE RESGATOU UM PECADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('265', '265 - LEVA TU CONTIGO O NOME', 'LEVA TU CONTIGO O NOME\nDE JESUS, O SALVADOR;\nESTE NOME DÁ CONFORTO\nSEMPRE, SEJA ONDE FOR.\n\nCORO (2X)\nNOME BOM, DOCE À FÉ,\nA ESPERANÇA DO PORVIR.\n\nESSE NOME LEVA SEMPRE\nPARA BEM TE DEFENDER,\nELE É A ARMA AO TEU ALCANCE\nQUANDO O MAL TE APARECER.\n\nCORO (2X)\nNOME BOM, DOCE À FÉ,\nA ESPERANÇA DO PORVIR.\n\nOH! QUE NOME PRECIOSO!\nGOZO TRAZ AO CORAÇÃO;\nSENDO POR JESUS ACEITO,\nTU TERÁS O SEU PERDÃO.\n\nCORO (2X)\nNOME BOM, DOCE À FÉ,\nA ESPERANÇA DO PORVIR.\n\nSANTO NOME, ADORÁVEL,\nTEM JESUS, O AMADO TEU;\n“REI DOS REIS, SENHOR ETERNO”,\nTU O ACLAMARÁS NO CÉU.\n\nCORO (2X)\nNOME BOM, DOCE À FÉ,\nA ESPERANÇA DO PORVIR.', 'LEVA TU CONTIGO O NOME DE JESUS, O SALVADOR; ESTE NOME DÁ CONFORTO SEMPRE, SEJA ONDE FOR.  NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR. ESSE NOME LEVA SEMPRE PARA BEM TE DEFENDER, ELE É A ARMA AO TEU ALCANCE QUANDO O MAL TE APARECER.  NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR. OH! QUE NOME PRECIOSO! GOZO TRAZ AO CORAÇÃO; SENDO POR JESUS ACEITO, TU TERÁS O SEU PERDÃO.  NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR. SANTO NOME, ADORÁVEL, TEM JESUS, O AMADO TEU; REI DOS REIS, SENHOR ETERNO, TU O ACLAMARÁS NO CÉU.  NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('266', '266 - TODOS OS POVOS', 'TODOS OS POVOS SABERÃO \nQUE O SENHOR JESUS É O ÚNICO SALVADOR,\nTODAS AS NAÇÕES SE PROSTRARÃO DIANTE DELE \nE CONFESSARÃO QUE JESUS CRISTO É O SENHOR.\n\nCORO\nJESUS É O CORDEIRO DE DEUS\nQUE TIRA O PECADO DO MUNDO,\nELE TE TROUXE AQUI PRA TE REVELAR\nA VIDA ETERNA QUE NELE HÁ.\nINSTRUMENTOS\n\nTU QUE PROCURAS ENCONTRAR A DOCE PAZ,\nSABE QUE JESUS É O PRÍNCIPE DA PAZ;\nTU QUE ESTÁS SEM ALEGRIA NO VIVER,\nEM CRISTO ENCONTRARÁS\nTUDO O QUE PRECISAS EM TEU SER.\n\nCORO (2X)\nJESUS É O CORDEIRO DE DEUS\nQUE TIRA O PECADO DO MUNDO,\nELE TE TROUXE AQUI PRA TE REVELAR\nA VIDA ETERNA QUE NELE HÁ.\n\nFINAL: A VIDA ETERNA QUE NELE HÁ.', 'TODOS OS POVOS SABERÃO QUE O SENHOR JESUS É O ÚNICO SALVADOR, TODAS AS NAÇÕES SE PROSTRARÃO DIANTE DELE E CONFESSARÃO QUE JESUS CRISTO É O SENHOR. CORO JESUS É O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, ELE TE TROUXE AQUI PRA TE REVELAR A VIDA ETERNA QUE NELE HÁ. INSTRUMENTOS TU QUE PROCURAS ENCONTRAR A DOCE PAZ, SABE QUE JESUS É O PRÍNCIPE DA PAZ; TU QUE ESTÁS SEM ALEGRIA NO VIVER, EM CRISTO ENCONTRARÁS TUDO O QUE PRECISAS EM TEU SER. JESUS É O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, ELE TE TROUXE AQUI PRA TE REVELAR A VIDA ETERNA QUE NELE HÁ.  A VIDA ETERNA QUE NELE HÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('267', '267 - QUANDO JESUS SEU SANGUE VERTEU', 'QUANDO JESUS SEU SANGUE VERTEU,\nTUDO SE FEZ NOVO,\nO VÉU SE RASGOU, A TERRA TREMEU,\nMORREU NA CRUZ O FILHO DE DEUS.\n\nINSTRUMENTOS\n\nELE RESTAUROU O MEU CORAÇÃO,\nDEU-ME UMA NOVA VIDA;\nOH! QUANTO ME AMOU, \nMEU MESTRE E SENHOR,\nPERTENCE A ELE TODO MEU LOUVOR.\n\nINSTRUMENTOS\n\nFOI POR AMAR-ME QUE JESUS SOFREU,\nTUDO DEVO A CRISTO;\nO QUE DAREI AO MEU SALVADOR?\nLOUVÁ-LO-EI POR SEU INFINITO AMOR.', 'QUANDO JESUS SEU SANGUE VERTEU, TUDO SE FEZ NOVO, O VÉU SE RASGOU, A TERRA TREMEU, MORREU NA CRUZ O FILHO DE DEUS. INSTRUMENTOS ELE RESTAUROU O MEU CORAÇÃO, DEU-ME UMA NOVA VIDA; OH! QUANTO ME AMOU, MEU MESTRE E SENHOR, PERTENCE A ELE TODO MEU LOUVOR. INSTRUMENTOS FOI POR AMAR-ME QUE JESUS SOFREU, TUDO DEVO A CRISTO; O QUE DAREI AO MEU SALVADOR? LOUVÁ-LO-EI POR SEU INFINITO AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('268', '268 - JESUS É O CORDEIRO', 'JESUS É O CORDEIRO ETERNO E IMACULADO\nQUE FOI SACRIFICADO PARA NOS SALVAR,\nO SUMO SACERDOTE QUE DEUS CONSTITUIU,\nLEVOU A NOSSA CRUZ PROVANDO O SEU AMOR.\n\nCORO\nJESUS RESSUSCITOU TRAZENDO VIDA ETERNA,\nNÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR,\nJESUS É O AMIGO FIEL, CONSOLADOR,\nENTREGA A TUA VIDA A QUEM TANTO TE AMA.\n\nO FARDO QUE CARREGAS TE TRAZ DESILUSÃO,\nO MUNDO QUE TU VIVES SÓ TEM DECEPÇÃO?\nA VIDA EM JESUS É LEVE E SUAVE,\nDESCANSO PARA ALMA NELE ENCONTRARÁS.\n\nCORO\nJESUS RESSUSCITOU TRAZENDO VIDA ETERNA,\nNÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR,\nJESUS É O AMIGO FIEL, CONSOLADOR,\nENTREGA A TUA VIDA A QUEM TANTO TE AMA.\n\nSEU NOME É CONSELHEIRO, \nPAI DA ETERNIDADE,\nMARAVILHOSO, \nELE É DEUS FORTE.\n\nCORO\nJESUS RESSUSCITOU TRAZENDO VIDA ETERNA,\nNÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR,\nJESUS É O AMIGO FIEL, CONSOLADOR,\nENTREGA A TUA VIDA A QUEM TANTO TE AMA.\n\nFINAL: JESUS TE AMA.', 'JESUS É O CORDEIRO ETERNO E IMACULADO QUE FOI SACRIFICADO PARA NOS SALVAR, O SUMO SACERDOTE QUE DEUS CONSTITUIU, LEVOU A NOSSA CRUZ PROVANDO O SEU AMOR. CORO JESUS RESSUSCITOU TRAZENDO VIDA ETERNA, NÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR, JESUS É O AMIGO FIEL, CONSOLADOR, ENTREGA A TUA VIDA A QUEM TANTO TE AMA. O FARDO QUE CARREGAS TE TRAZ DESILUSÃO, O MUNDO QUE TU VIVES SÓ TEM DECEPÇÃO? A VIDA EM JESUS É LEVE E SUAVE, DESCANSO PARA ALMA NELE ENCONTRARÁS. SEU NOME É CONSELHEIRO, PAI DA ETERNIDADE, MARAVILHOSO, ELE É DEUS FORTE. CORO JESUS RESSUSCITOU TRAZENDO VIDA ETERNA, NÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR, JESUS É O AMIGO FIEL, CONSOLADOR, ENTREGA A TUA VIDA A QUEM TANTO TE AMA.  JESUS TE AMA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('269', '269 - NÃO SEI SE OUVISTE FALAR', 'NÃO SEI SE OUVISTE FALAR DE UM REI\nQUE MORREU PRA TE SALVAR;\nO SEU SANGUE PURO DERRAMOU,\nCOMO HOLOCAUSTO FOI ENTREGUE POR AMOR.\n\nCORO\nELE VIVE E QUER TE SALVAR,\nENTREGA A TUA VIDA E ELA IRÁ MUDAR;\nELE É JESUS, NOSSO ETERNO REDENTOR,\nELE É O REI DA GLÓRIA, \nELE É O MEU SENHOR.\n\nSE PROCURAS A ALEGRIA \nE NÃO CONSEGUES ENCONTRAR, \nSÓ JESUS PODE DAR A ALEGRIA \nQUE NUNCA CESSARÁ;\nSE SERVIRES A JESUS \nUM NOVO DIA IRÁ SURGIR,\nELE TE CONDUZIRÁ AO SANTO \nE ETERNO LAR.\n\nCORO\nELE VIVE E QUER TE SALVAR,\nENTREGA A TUA VIDA E ELA IRÁ MUDAR;\nELE É JESUS, NOSSO ETERNO REDENTOR,\nELE É O REI DA GLÓRIA, \nELE É O MEU SENHOR.\n\nINSTRUMENTOS\n\nCORO\nELE VIVE E QUER TE SALVAR,\nENTREGA A TUA VIDA E ELA IRÁ MUDAR;\nELE É JESUS, NOSSO ETERNO REDENTOR,\nELE É O REI DA GLÓRIA, \nELE É O MEU SENHOR.', 'NÃO SEI SE OUVISTE FALAR DE UM REI QUE MORREU PRA TE SALVAR; O SEU SANGUE PURO DERRAMOU, COMO HOLOCAUSTO FOI ENTREGUE POR AMOR. CORO ELE VIVE E QUER TE SALVAR, ENTREGA A TUA VIDA E ELA IRÁ MUDAR; ELE É JESUS, NOSSO ETERNO REDENTOR, ELE É O REI DA GLÓRIA, ELE É O MEU SENHOR. SE PROCURAS A ALEGRIA E NÃO CONSEGUES ENCONTRAR, SÓ JESUS PODE DAR A ALEGRIA QUE NUNCA CESSARÁ; SE SERVIRES A JESUS UM NOVO DIA IRÁ SURGIR, ELE TE CONDUZIRÁ AO SANTO E ETERNO LAR. CORO ELE VIVE E QUER TE SALVAR, ENTREGA A TUA VIDA E ELA IRÁ MUDAR; ELE É JESUS, NOSSO ETERNO REDENTOR, ELE É O REI DA GLÓRIA, ELE É O MEU SENHOR. INSTRUMENTOS', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('270', '270 - SE TU PEDISTE UM SINAL', 'CORO\nSE TU PEDISTE UM SINAL, \nDEUS OUVIU TUA VOZ \nE TE TROUXE AQUI PARA ABENÇOAR.\nSE TUA VIDA É VAZIA, DEUS PODE MUDAR\nELE TE TROUXE AQUI PARA ABENÇOAR.\n\nENTREGA A TUA VIDA A JESUS\nE A SALVAÇÃO TERÁS;\nELE DIZ: “EU SOU O SENHOR,\nO REI DOS REIS, O REI DAS NAÇÕES”.\n\nINSTRUMENTOS\n\nCORO\nSE TU PEDISTE UM SINAL, \nDEUS OUVIU TUA VOZ \nE TE TROUXE AQUI PARA ABENÇOAR.\nSE TUA VIDA É VAZIA, DEUS PODE MUDAR\nELE TE TROUXE AQUI PARA ABENÇOAR.\n\nFINAL:\nENTREGA A TUA VIDA A JESUS,\nELE VAI TE TRANSFORMAR.\nELE DIZ: “EU SOU O SENHOR,\nO REI DOS REIS, O REI DAS NAÇÕES ”.\nBIS', 'CORO SE TU PEDISTE UM SINAL, DEUS OUVIU TUA VOZ E TE TROUXE AQUI PARA ABENÇOAR. SE TUA VIDA É VAZIA, DEUS PODE MUDAR ELE TE TROUXE AQUI PARA ABENÇOAR. ENTREGA A TUA VIDA A JESUS E A SALVAÇÃO TERÁS; ELE DIZ: EU SOU O SENHOR, O REI DOS REIS, O REI DAS NAÇÕES. INSTRUMENTOS ENTREGA A TUA VIDA A JESUS, ELE VAI TE TRANSFORMAR. ELE DIZ: EU SOU O SENHOR, O REI DOS REIS, O REI DAS NAÇÕES .', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('271', '271 - O CAMINHO QUE NOS LEVA AO CÉU É JESUS', 'O CAMINHO QUE NOS LEVA AO CÉU É JESUS,\nO ÚNICO MEDIADOR ENTRE DEUS E OS HOMENS.\n\nCORO\nFOI ELE QUEM SE DEU A MORRER \nNA CRUZ, ELE É O NOSSO SALVADOR\nO SEU NOME É MARAVILHOSO,\nCONSELHEIRO, NOBRE, PRÍNCIPE DA PAZ,\nELE ERA, É, SEMPRE SERÁ\nO AUTOR DA NOSSA REDENÇÃO.\n\nO AMIGO QUE NUNCA FALHA É JESUS,\nAQUELE QUE NOS GARANTE A VIDA ETERNA.\n\nCORO\nFOI ELE QUEM SE DEU A MORRER \nNA CRUZ, ELE É O NOSSO SALVADOR\nO SEU NOME É MARAVILHOSO,\nCONSELHEIRO, NOBRE, PRÍNCIPE DA PAZ,\nELE ERA, É, SEMPRE SERÁ\nO AUTOR DA NOSSA REDENÇÃO.\n\nFINAL: (2X)\n(O AUTOR DA NOSSA REDENÇÃO)\nO AUTOR DA NOSSA REDENÇÃO.', 'O CAMINHO QUE NOS LEVA AO CÉU É JESUS, O ÚNICO MEDIADOR ENTRE DEUS E OS HOMENS. CORO FOI ELE QUEM SE DEU A MORRER NA CRUZ, ELE É O NOSSO SALVADOR O SEU NOME É MARAVILHOSO, CONSELHEIRO, NOBRE, PRÍNCIPE DA PAZ, ELE ERA, É, SEMPRE SERÁ O AUTOR DA NOSSA REDENÇÃO. O AMIGO QUE NUNCA FALHA É JESUS, AQUELE QUE NOS GARANTE A VIDA ETERNA. (O AUTOR DA NOSSA REDENÇÃO) O AUTOR DA NOSSA REDENÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('272', '272 - NOVO CAMINHO', 'NOVO CAMINHO É O MEU JESUS,\nOUTRO CAMINHO NÃO PODES ENCONTRAR.\nPODES ENCONTRAR A PAZ, VIDA EM JESUS.\nSEGUE ESTE CAMINHO E TERÁS A LUZ.\n\nCORO\nPAZ, PAZ, VIDA, AMOR E LUZ,\nSE NÃO HÁ PAZ, PEDE A JESUS.\n\nDEIXA CRISTO SE REVELAR EM TEU CORAÇÃO,\nELE TE DARÁ A PAZ, VIDA E SALVAÇÃO.\n\nCORO\nPAZ, PAZ, VIDA, AMOR E LUZ,\nSE NÃO HÁ PAZ, PEDE A JESUS.\nPAZ, PAZ, VIDA, AMOR E LUZ,\nSE NÃO HÁ PAZ, PEDE A JESUS.', 'NOVO CAMINHO É O MEU JESUS, OUTRO CAMINHO NÃO PODES ENCONTRAR. PODES ENCONTRAR A PAZ, VIDA EM JESUS. SEGUE ESTE CAMINHO E TERÁS A LUZ. CORO PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS. DEIXA CRISTO SE REVELAR EM TEU CORAÇÃO, ELE TE DARÁ A PAZ, VIDA E SALVAÇÃO. CORO PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS. PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('273', '273 - O SENHOR É A ALEGRIA', 'O SENHOR É A ALEGRIA,\nÉ A PAZ E A REDENÇÃO,\nELE É O AUTOR DA SALVAÇÃO;\nO SENHOR É A FORTALEZA\nDOS QUE CRÊEM NELE ASSIM;\nE A VOCÊ ESTÁ CHAMANDO: \n“VENHA A MIM”.\nPODE TUDO CONFIAR A \nJESUS SE ENTREGAR SUA \nVIDA ELE PODERÁ MUDAR.\nBIS\nBIS\n\n“VINDE A MIM”, DISSE JESUS,\n“VÓS TODOS QUE ESTAIS CANSADOS, \nÓ VINDE A MIM”.\n\nFINAL: DISSE JESUS.\nBIS', 'O SENHOR É A ALEGRIA, É A PAZ E A REDENÇÃO, ELE É O AUTOR DA SALVAÇÃO; O SENHOR É A FORTALEZA DOS QUE CRÊEM NELE ASSIM; E A VOCÊ ESTÁ CHAMANDO: VENHA A MIM. PODE TUDO CONFIAR A JESUS SE ENTREGAR SUA VIDA ELE PODERÁ MUDAR. VINDE A MIM, DISSE JESUS, VÓS TODOS QUE ESTAIS CANSADOS, Ó VINDE A MIM.  DISSE JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('274', '274 - SE EM TUA VIDA NÃO ENCONTRAS PAZ E LUZ', 'SE EM TUA VIDA NÃO ENCONTRAS PAZ E LUZ,\nSE O CAMINHO É DIFÍCIL E DE DOR,\nABRE O TEU CORAÇÃO HOJE A JESUS,\nELE VAI TE REVELAR O SEU AMOR;\n\nSE NO MUNDO TU ENFRENTAS MUITAS LUTAS,\nE AS LÁGRIMAS JÁ TOMAM O TEU SER,\nVEM A CRISTO, ELE JÁ VENCEU O MUNDO\nE COM ELE TAMBÉM HOJE VAIS VENCER.\n\nCORO\nLIMPA TUAS LÁGRIMAS, ALEGRIA E PAZ TE DÁ,\nVEM PARA CRISTO E A VITÓRIA TERÁS;\nEXISTE ESPERANÇA, DEUS PROVERÁ,\nENTREGA O TEU CAMINHO \nE TUDO ELE FARÁ.\n\nINSTRUMENTOS\n\nELE UM DIA TRANSFORMOU O MEU VIVER\nE PASSOU A HABITAR MEU CORAÇÃO,\nHOJE POSSO CONTEMPLAR O SEU PODER\nE JESUS SEGURA FORTE A MINHA MÃO;\n\nNESTE MUNDO ENFRENTAMOS MUITAS LUTAS,\nMAS COM CRISTO É MAIS FÁCIL DE VENCER.\nÉ SÓ ENTREGAR A ELE OS TEUS PROBLEMAS\nE UMA NOVA VIDA HOJE VAIS VIVER.\n\nCORO (2X)\nLIMPA TUAS LÁGRIMAS, ALEGRIA E PAZ TE DÁ,\nVEM PARA CRISTO E A VITÓRIA TERÁS;\nEXISTE ESPERANÇA, DEUS PROVERÁ,\nENTREGA O TEU CAMINHO \nE TUDO ELE FARÁ.\n\nFINAL: \nENTREGA O TEU CAMINHO \nE TUDO ELE FARÁ.', 'SE EM TUA VIDA NÃO ENCONTRAS PAZ E LUZ, SE O CAMINHO É DIFÍCIL E DE DOR, ABRE O TEU CORAÇÃO HOJE A JESUS, ELE VAI TE REVELAR O SEU AMOR; SE NO MUNDO TU ENFRENTAS MUITAS LUTAS, E AS LÁGRIMAS JÁ TOMAM O TEU SER, VEM A CRISTO, ELE JÁ VENCEU O MUNDO E COM ELE TAMBÉM HOJE VAIS VENCER. CORO LIMPA TUAS LÁGRIMAS, ALEGRIA E PAZ TE DÁ, VEM PARA CRISTO E A VITÓRIA TERÁS; EXISTE ESPERANÇA, DEUS PROVERÁ, ENTREGA O TEU CAMINHO E TUDO ELE FARÁ. INSTRUMENTOS ELE UM DIA TRANSFORMOU O MEU VIVER E PASSOU A HABITAR MEU CORAÇÃO, HOJE POSSO CONTEMPLAR O SEU PODER E JESUS SEGURA FORTE A MINHA MÃO; NESTE MUNDO ENFRENTAMOS MUITAS LUTAS, MAS COM CRISTO É MAIS FÁCIL DE VENCER. É SÓ ENTREGAR A ELE OS TEUS PROBLEMAS E UMA NOVA VIDA HOJE VAIS VIVER. LIMPA TUAS LÁGRIMAS, ALEGRIA E PAZ TE DÁ, VEM PARA CRISTO E A VITÓRIA TERÁS; EXISTE ESPERANÇA, DEUS PROVERÁ, ENTREGA O TEU CAMINHO E TUDO ELE FARÁ.  ENTREGA O TEU CAMINHO E TUDO ELE FARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('275', '275 - UM MILAGRE É', 'UM MILAGRE É A MINHA VIDA AQUI,\nNESTE LUGAR, SEMPRE A TEUS PÉS.\nDESDE AQUELE DIA EM QUE ME\nALCANÇOU, EU VIVO PARA\nAGRADECER O TEU GRANDE AMOR.\n\nCORO\nESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, \nJESUS, DANDO-ME A VIDA,\nCURANDO TODAS AS MINHAS FERIDAS.\n\nHOJE POSSO OUVIR A TUA VOZ EM MIM,\nEU POSSO TER A TUA DIREÇÃO.\nSE VOCÊ QUISER TAMBÉM O PODE TER,\nÉ SÓ ABRIR SEU CORAÇÃO E O RECEBER.\n\nCORO\nESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, \nJESUS, DANDO-ME A VIDA,\nCURANDO TODAS AS MINHAS FERIDAS.\n\nINSTRUMENTOS\n\nESSE MILAGRE É, \nA TUA MORTE LÁ NA CRUZ, \nJESUS, DANDO-ME A VIDA,\nCURANDO TODAS AS MINHAS FERIDAS.\n\nFINAL: \nDANDO-ME A VIDA, \nCURANDO TODAS AS MINHAS FERIDAS.', 'UM MILAGRE É A MINHA VIDA AQUI, NESTE LUGAR, SEMPRE A TEUS PÉS. DESDE AQUELE DIA EM QUE ME ALCANÇOU, EU VIVO PARA AGRADECER O TEU GRANDE AMOR. CORO ESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS. HOJE POSSO OUVIR A TUA VOZ EM MIM, EU POSSO TER A TUA DIREÇÃO. SE VOCÊ QUISER TAMBÉM O PODE TER, É SÓ ABRIR SEU CORAÇÃO E O RECEBER. CORO ESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS. INSTRUMENTOS ESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS.  DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('276', '276 - AINDA QUE OS SEUS SONHOS', 'AINDA QUE OS SEUS SONHOS CAIAM PELO CHÃO\nE NÃO ENCONTRE UMA SAÍDA, UMA SOLUÇÃO,\nA VIDA PODE SER DIFÍCIL DE RESOLVER,\nE NESTA HORA QUE VOCÊ PRECISA RECONHECER.\n\nINSTRUMENTOS\n\nO MUNDO NÃO O CONHECE, \nPOR ISSO ELE PERECE, \nJESUS QUER SE DAR A CONHECER.\nVENHA A VERDADE RECEBER.\n\nCORO\nO FILHO DO DEUS ETERNO É JESUS.\nELE VEIO TE RESGATAR DAS TREVAS \nPARA A LUZ, DAS TREVAS PARA A LUZ.\nABRA AGORA O TEU CORAÇÃO A JESUS.\n\nINSTRUMENTOS\n\nO MUNDO NÃO O CONHECE, \nPOR ISSO ELE PERECE, \nJESUS QUER SE DAR A CONHECER.\nVENHA A VERDADE RECEBER.\n\nCORO (2X)\nO FILHO DO DEUS ETERNO É JESUS.\nELE VEIO TE RESGATAR DAS TREVAS \nPARA A LUZ, DAS TREVAS PARA A LUZ.\nABRA AGORA O TEU CORAÇÃO A JESUS.', 'AINDA QUE OS SEUS SONHOS CAIAM PELO CHÃO E NÃO ENCONTRE UMA SAÍDA, UMA SOLUÇÃO, A VIDA PODE SER DIFÍCIL DE RESOLVER, E NESTA HORA QUE VOCÊ PRECISA RECONHECER. INSTRUMENTOS O MUNDO NÃO O CONHECE, POR ISSO ELE PERECE, JESUS QUER SE DAR A CONHECER. VENHA A VERDADE RECEBER. CORO O FILHO DO DEUS ETERNO É JESUS. ELE VEIO TE RESGATAR DAS TREVAS PARA A LUZ, DAS TREVAS PARA A LUZ. ABRA AGORA O TEU CORAÇÃO A JESUS. INSTRUMENTOS O FILHO DO DEUS ETERNO É JESUS. ELE VEIO TE RESGATAR DAS TREVAS PARA A LUZ, DAS TREVAS PARA A LUZ. ABRA AGORA O TEU CORAÇÃO A JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('277', '277 - ROCHA ETERNA', 'ROCHA ETERNA, FOI NA CRUZ\nQUE MORRESTE TU, JESUS;\nVEM DE TI UM SANGUE TAL\nQUE ME LIMPA TODO O MAL.\nTRAZ AS BÊNÇÃOS DO PERDÃO:\nGOZO, PAZ E SALVAÇÃO.\n\nNEM TRABALHO, NEM PENAR\nPODE O PECADOR SALVAR;\nSÓ TU PODES, BOM JESUS,\nDAR-ME VIDA, PAZ E LUZ.\nPEÇO-TE PERDÃO, SENHOR,\nPOIS CONFIO EM TEU AMOR.\n\nEIS QUE VEM A MORTE ATRÁS\nDESTA VIDA TÃO FUGAZ.\nQUANDO EU AO MEU LAR SUBIR,\nE TEU ROSTO EM GLÓRIA VIR,\nROCHA ETERNA, QUE PRAZER\nEU TEREI DE EM TI VIVER!', 'ROCHA ETERNA, FOI NA CRUZ QUE MORRESTE TU, JESUS; VEM DE TI UM SANGUE TAL QUE ME LIMPA TODO O MAL. TRAZ AS BÊNÇÃOS DO PERDÃO: GOZO, PAZ E SALVAÇÃO. NEM TRABALHO, NEM PENAR PODE O PECADOR SALVAR; SÓ TU PODES, BOM JESUS, DAR-ME VIDA, PAZ E LUZ. PEÇO-TE PERDÃO, SENHOR, POIS CONFIO EM TEU AMOR. EIS QUE VEM A MORTE ATRÁS DESTA VIDA TÃO FUGAZ. QUANDO EU AO MEU LAR SUBIR, E TEU ROSTO EM GLÓRIA VIR, ROCHA ETERNA, QUE PRAZER EU TEREI DE EM TI VIVER!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('278', '278 - A NOVA DO EVANGELHO', 'A NOVA DO EVANGELHO\nJÁ SE FEZ OUVIR AQUI;\nBOAS NOVAS, TÃO ALEGRES,\nELAS SÃO PRA QUEM OUVIR:\nASSIM DEUS NOS AMOU,\nSIM, A CADA PECADOR,\nQUE NOS DEU SEU FILHO AMADO\nPRA SOFRER A NOSSA DOR.\n\nCORO\nSANTA PAZ (SANTA PAZ)\nE PERDÃO! (E PERDÃO!)\nSÃO AS NOVAS LÁ DOS CÉUS!\nSANTA PAZ (SANTA PAZ) \nE PERDÃO! (E PERDÃO!)\nÉ BENDITO O NOSSO DEUS!\n\nA NOVA DO EVANGELHO\nDÁ-NOS TODOS A SABER\nQUE FARTURA HÁ PARA TODOS,\nVEM, DE GRAÇA, RECEBER.\nDA VIDA O PÃO EU SOU; \nSATISFEITO FICARÁS;\nTEUS PECADOS E TUA ALMA\nLAVAREI, E PAZ TERÁS.\n\nCORO\nSANTA PAZ (SANTA PAZ)\nE PERDÃO! (E PERDÃO!)\nSÃO AS NOVAS LÁ DOS CÉUS!\nSANTA PAZ (SANTA PAZ) \nE PERDÃO! (E PERDÃO!)\nÉ BENDITO O NOSSO DEUS!\n\nA NOVA DO EVANGELHO\nORA VEM NOS AVISAR\nDO PERIGO GRANDE E GRAVE,\nPARA QUEM SE DESCUIDAR:\nSALVAI-VOS DESDE JÁ;\nNÃO VOS DEMOREIS, ENTÃO,\nNÃO VIREIS OS VOSSOS OLHOS,\nOH! FUGI DA PERDIÇÃO!\n\nCORO\nSANTA PAZ (SANTA PAZ)\nE PERDÃO! (E PERDÃO!)\nSÃO AS NOVAS LÁ DOS CÉUS!\nSANTA PAZ (SANTA PAZ) \nE PERDÃO! (E PERDÃO!)\nÉ BENDITO O NOSSO DEUS!\n\nA NOVA DO EVANGELHO \nJUBILOSO SOM NOS É,\nPOIS O AMOR DE JESUS CRISTO\nDÁ PERDÃO MEDIANTE A FÉ.\nAS NOVAS SE VOS DÃO\nQUE JÁ VEIO O SALVADOR,\nTÃO BONDOSO E PODEROSO,\nQUE PERDOA AO PECADOR.\n\nCORO\nSANTA PAZ (SANTA PAZ)\nE PERDÃO! (E PERDÃO!)\nSÃO AS NOVAS LÁ DOS CÉUS!\nSANTA PAZ (SANTA PAZ) \nE PERDÃO! (E PERDÃO!)\nÉ BENDITO O NOSSO DEUS!', 'A NOVA DO EVANGELHO JÁ SE FEZ OUVIR AQUI; BOAS NOVAS, TÃO ALEGRES, ELAS SÃO PRA QUEM OUVIR: ASSIM DEUS NOS AMOU, SIM, A CADA PECADOR, QUE NOS DEU SEU FILHO AMADO PRA SOFRER A NOSSA DOR. CORO SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) SÃO AS NOVAS LÁ DOS CÉUS! SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) É BENDITO O NOSSO DEUS! A NOVA DO EVANGELHO DÁ-NOS TODOS A SABER QUE FARTURA HÁ PARA TODOS, VEM, DE GRAÇA, RECEBER. DA VIDA O PÃO EU SOU; SATISFEITO FICARÁS; TEUS PECADOS E TUA ALMA LAVAREI, E PAZ TERÁS. A NOVA DO EVANGELHO ORA VEM NOS AVISAR DO PERIGO GRANDE E GRAVE, PARA QUEM SE DESCUIDAR: SALVAI-VOS DESDE JÁ; NÃO VOS DEMOREIS, ENTÃO, NÃO VIREIS OS VOSSOS OLHOS, OH! FUGI DA PERDIÇÃO! A NOVA DO EVANGELHO JUBILOSO SOM NOS É, POIS O AMOR DE JESUS CRISTO DÁ PERDÃO MEDIANTE A FÉ. AS NOVAS SE VOS DÃO QUE JÁ VEIO O SALVADOR, TÃO BONDOSO E PODEROSO, QUE PERDOA AO PECADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('279', '279 - QUERO ESTAR AO PÉ DA CRUZ', 'QUERO ESTAR AO PÉ DA CRUZ,\nDE ONDE RICA FONTE\nCORRE FRANCA, SALUTAR,\nDO CALVÁRIO, MONTE.\n\nCORO\nSIM, NA CRUZ, SIM, NA CRUZ,\nSEMPRE ME GLORIO E ENFIM \nVOU DESCANSAR SALVO, \nALÉM DO RIO.\n\nA TREMER AO PÉ DA CRUZ, \nGRAÇA ETERNA ACHOU-ME;\nMATUTINA ESTRELA ALI RAIOS \nSEUS MANDOU-ME.\n\nCORO\nSIM, NA CRUZ, SIM, NA CRUZ,\nSEMPRE ME GLORIO E ENFIM \nVOU DESCANSAR SALVO, \nALÉM DO RIO.\n\nSEMPRE A CRUZ, JESUS, MEU DEUS,\nQUEIRAS RECORDAR-ME;\nDELA À SOMBRA, SALVADOR,\nQUEIRAS ABRIGAR-ME.\n\nCORO\nSIM, NA CRUZ, SIM, NA CRUZ,\nSEMPRE ME GLORIO E ENFIM \nVOU DESCANSAR SALVO, \nALÉM DO RIO.\n\nJUNTO À CRUZ, ARDENDO EM FÉ,\nSEM TEMOR VIGIO, \nPOIS À TERRA SANTA IREI, \nSALVO, ALÉM DO RIO.', 'QUERO ESTAR AO PÉ DA CRUZ, DE ONDE RICA FONTE CORRE FRANCA, SALUTAR, DO CALVÁRIO, MONTE. CORO SIM, NA CRUZ, SIM, NA CRUZ, SEMPRE ME GLORIO E ENFIM VOU DESCANSAR SALVO, ALÉM DO RIO. A TREMER AO PÉ DA CRUZ, GRAÇA ETERNA ACHOU-ME; MATUTINA ESTRELA ALI RAIOS SEUS MANDOU-ME. SEMPRE A CRUZ, JESUS, MEU DEUS, QUEIRAS RECORDAR-ME; DELA À SOMBRA, SALVADOR, QUEIRAS ABRIGAR-ME. JUNTO À CRUZ, ARDENDO EM FÉ, SEM TEMOR VIGIO, POIS À TERRA SANTA IREI, SALVO, ALÉM DO RIO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('280', '280 - HÁ HOJE ALGUÉM ESPERANDO', 'HÁ HOJE ALGUÉM ESPERANDO\nPARA JESUS ENCONTRAR?\nVENHA, SEM MAIS DEMORAR-SE,\nCRISTO VAI HOJE PASSAR!\nEI-LO DE MÃOS ESTENDIDAS,\nCHEIO DE GRAÇA SEM PAR.\nOH! QUE VENTURA INAUDITA,\nCRISTO VAI HOJE PASSAR!\n\nCORO\nCRISTO VAI HOJE PASSAR, PASSAR, PASSAR!\nCRISTO VAI HOJE PASSAR, \nPASSAR, VAI HOJE PASSAR!\nPASSA DE AMOR TRANSBORDANDO,\nTODOS A SI CONVIDANDO.\nO MESTRE VAI HOJE PASSAR (PASSAR) VARÕES\nSIM, HOJE ELE VAI PASSAR!\n\nVARÕES\n\nHÁ INDA ALGUÉM DUVIDANDO\nDO SEU PODER DE SALVAR?\nPOIS VENHA EXPERIMENTÁ-LO,\nCRISTO VAI HOJE PASSAR!\nO SEU PODER É DIVINO,\nO SEU AMOR É SEM PAR.\nÓ CORAÇÃO QUEBRANTADO!\nCRISTO VAI HOJE PASSAR!\n\nCORO\nCRISTO VAI HOJE PASSAR, PASSAR, PASSAR!\nCRISTO VAI HOJE PASSAR, \nPASSAR, VAI HOJE PASSAR!\nPASSA DE AMOR TRANSBORDANDO,\nTODOS A SI CONVIDANDO.\nO MESTRE VAI HOJE PASSAR (PASSAR) VARÕES\nSIM, HOJE ELE VAI PASSAR!\n\nVARÕES\n\nHÁ INDA ALGUÉM DEMORANDO\nPARA JESUS ACEITAR?\nEIS QUE O SENHOR ESTÁ PERTO,\nELE VAI HOJE PASSAR!\nÓ PECADOR DESDITOSO,\nNÃO CESSES POIS DE CLAMAR!\nVEM TUAS CULPAS CHORANDO;\nCRISTO VAI HOJE PASSAR!\n\nCORO\nCRISTO VAI HOJE PASSAR, PASSAR, PASSAR!\nCRISTO VAI HOJE PASSAR, \nPASSAR, VAI HOJE PASSAR!\nPASSA DE AMOR TRANSBORDANDO,\nTODOS A SI CONVIDANDO.\nO MESTRE VAI HOJE PASSAR (PASSAR) VARÕES\nSIM, HOJE ELE VAI PASSAR!\n\nVARÕES', 'HÁ HOJE ALGUÉM ESPERANDO PARA JESUS ENCONTRAR? VENHA, SEM MAIS DEMORAR-SE, CRISTO VAI HOJE PASSAR! EI-LO DE MÃOS ESTENDIDAS, CHEIO DE GRAÇA SEM PAR. OH! QUE VENTURA INAUDITA, CRISTO VAI HOJE PASSAR! CORO CRISTO VAI HOJE PASSAR, PASSAR, PASSAR! CRISTO VAI HOJE PASSAR, PASSAR, VAI HOJE PASSAR! PASSA DE AMOR TRANSBORDANDO, TODOS A SI CONVIDANDO. O MESTRE VAI HOJE PASSAR (PASSAR) VARÕES SIM, HOJE ELE VAI PASSAR! HÁ INDA ALGUÉM DUVIDANDO DO SEU PODER DE SALVAR? POIS VENHA EXPERIMENTÁ-LO, CRISTO VAI HOJE PASSAR! O SEU PODER É DIVINO, O SEU AMOR É SEM PAR. Ó CORAÇÃO QUEBRANTADO! CRISTO VAI HOJE PASSAR! HÁ INDA ALGUÉM DEMORANDO PARA JESUS ACEITAR? EIS QUE O SENHOR ESTÁ PERTO, ELE VAI HOJE PASSAR! Ó PECADOR DESDITOSO, NÃO CESSES POIS DE CLAMAR! VEM TUAS CULPAS CHORANDO; CRISTO VAI HOJE PASSAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('281', '281 - ESTE MUNDO NÃO TEM A SUA PAZ', 'ESTE MUNDO NÃO TEM A SUA PAZ,\nESTE MUNDO NÃO CONHECE O QUE É AMOR,\nESTE MUNDO NÃO SABE O QUE É SER FELIZ,\nEU TAMBÉM NÃO SABIA,\nMAS JESUS UM DIA ME ENCONTROU.\n\nJESUS BUSCOU-ME COM SEUS BRAÇOS \nDE TERNURA, FALOU-ME DE UM AMOR \nQUE JAMAIS CONHECI,\nFEZ-ME ENTENDER O QUE É A SALVAÇÃO,\nENTÃO, ABRI O MEU CORAÇÃO.\n\nCORO \nELE É JESUS (ELE É JESUS) (BIS)\nO AMOR VERDADEIRO \nQUE O HOMEM PODE TER.\n\nINSTRUMENTOS\n\nJESUS BUSCOU-ME COM SEUS BRAÇOS \nDE TERNURA, FALOU-ME DE UM AMOR \nQUE JAMAIS CONHECI,\nFEZ-ME ENTENDER O QUE É A SALVAÇÃO,\nENTÃO, ABRI O MEU CORAÇÃO.\n\nCORO (2X)\nELE É JESUS (ELE É JESUS) (BIS)\nO AMOR VERDADEIRO \nQUE O HOMEM PODE TER.', 'ESTE MUNDO NÃO TEM A SUA PAZ, ESTE MUNDO NÃO CONHECE O QUE É AMOR, ESTE MUNDO NÃO SABE O QUE É SER FELIZ, EU TAMBÉM NÃO SABIA, MAS JESUS UM DIA ME ENCONTROU. JESUS BUSCOU-ME COM SEUS BRAÇOS DE TERNURA, FALOU-ME DE UM AMOR QUE JAMAIS CONHECI, FEZ-ME ENTENDER O QUE É A SALVAÇÃO, ENTÃO, ABRI O MEU CORAÇÃO. CORO ELE É JESUS (ELE É JESUS)  O AMOR VERDADEIRO QUE O HOMEM PODE TER. INSTRUMENTOS ELE É JESUS (ELE É JESUS)  O AMOR VERDADEIRO QUE O HOMEM PODE TER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('282', '282 - QUEM É QUE DÁ ÁGUA AO SEDENTO?', 'QUEM É QUE DÁ ÁGUA AO SEDENTO \nE UMA SOMBRA FRESCA?\nQUEM É QUE RETIRA DO HOMEM TODO ARDOR?\nQUEM É QUE ABRIU NO CALVÁRIO \nUM NOVO CAMINHO?\nQUEM É A FONTE DA VIDA, \nO GRANDE AMOR?\n\nCORO\nÉ O FILHO DE DEUS, É JESUS, O SENHOR,\nÉ O GRANDE EU SOU, \nELE É O CORDEIRO DE DEUS,\nO AUTOR DA ETERNA VIDA, ELE É JESUS.\n\nINSTRUMENTOS\n\nQUEM É QUE DÁ ÁGUA AO SEDENTO\nNUMA TERRA SECA?\nQUEM É A FLOR MAIS QUERIDA \nDO LINDO JARDIM?\nQUEM É QUE TRAZ PAZ \nE CONSOLO DENTRO DA ALMA?\nQUEM É O AMIGO EXCELENTE, \nO SALVADOR?\n\nCORO\nÉ O FILHO DE DEUS, É JESUS, O SENHOR,\nÉ O GRANDE EU SOU, \nELE É O CORDEIRO DE DEUS,\nO AUTOR DA ETERNA VIDA, ELE É JESUS.\n\nINSTRUMENTOS\n\nCORO\nÉ O FILHO DE DEUS, É JESUS, O SENHOR,\nÉ O GRANDE EU SOU, \nELE É O CORDEIRO DE DEUS,\nO AUTOR DA ETERNA VIDA, ELE É JESUS.', 'QUEM É QUE DÁ ÁGUA AO SEDENTO E UMA SOMBRA FRESCA? QUEM É QUE RETIRA DO HOMEM TODO ARDOR? QUEM É QUE ABRIU NO CALVÁRIO UM NOVO CAMINHO? QUEM É A FONTE DA VIDA, O GRANDE AMOR? CORO É O FILHO DE DEUS, É JESUS, O SENHOR, É O GRANDE EU SOU, ELE É O CORDEIRO DE DEUS, O AUTOR DA ETERNA VIDA, ELE É JESUS. INSTRUMENTOS QUEM É QUE DÁ ÁGUA AO SEDENTO NUMA TERRA SECA? QUEM É A FLOR MAIS QUERIDA DO LINDO JARDIM? QUEM É QUE TRAZ PAZ E CONSOLO DENTRO DA ALMA? QUEM É O AMIGO EXCELENTE, O SALVADOR? CORO É O FILHO DE DEUS, É JESUS, O SENHOR, É O GRANDE EU SOU, ELE É O CORDEIRO DE DEUS, O AUTOR DA ETERNA VIDA, ELE É JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('283', '283 - O MUNDO NÃO SABE', 'O MUNDO NÃO SABE A HISTÓRIA DE JESUS \nE PENSA QUE TUDO TERMINOU NA CRUZ,\nMAS EU SEI QUE A HISTÓRIA NÃO CHEGOU AO FIM, \nJESUS ESTÁ VIVO PARA MIM!\n\nNAQUELA MANHÃ QUANDO O SOL RAIOU,\nA PORTA DA SEPULTURA SE ABRIU\nE O MESTRE AOS DISCÍPULOS SE APRESENTOU,\nO CORDEIRO QUE FOI MORTO RESSURGIU!\n\nCORO (2X)\nALELUIA, RESSUSCITOU! \nCRISTO RESSUSCITOU\nE ELE VIVO HOJE ESTÁ, \nE BREVE VOLTARÁ!\n\nO MUNDO NÃO MAIS O VIU NEM IRÁ VER\nMAS HOJE À SUA IGREJA DÁ-SE A CONHECER \nCOM MUITOS SINAIS E PROVAS DE AMOR, \nJESUS CRISTO ESTÁ VIVO E É SENHOR!\n\nCORO (2X)\nALELUIA, RESSUSCITOU! \nCRISTO RESSUSCITOU\nE ELE VIVO HOJE ESTÁ, \nE BREVE VOLTARÁ!', 'O MUNDO NÃO SABE A HISTÓRIA DE JESUS E PENSA QUE TUDO TERMINOU NA CRUZ, MAS EU SEI QUE A HISTÓRIA NÃO CHEGOU AO FIM, JESUS ESTÁ VIVO PARA MIM! NAQUELA MANHÃ QUANDO O SOL RAIOU, A PORTA DA SEPULTURA SE ABRIU E O MESTRE AOS DISCÍPULOS SE APRESENTOU, O CORDEIRO QUE FOI MORTO RESSURGIU! ALELUIA, RESSUSCITOU! CRISTO RESSUSCITOU E ELE VIVO HOJE ESTÁ, E BREVE VOLTARÁ! O MUNDO NÃO MAIS O VIU NEM IRÁ VER MAS HOJE À SUA IGREJA DÁ-SE A CONHECER COM MUITOS SINAIS E PROVAS DE AMOR, JESUS CRISTO ESTÁ VIVO E É SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('284', '284 - O AMOR DE DEUS É GRANDE', 'O AMOR DE DEUS É GRANDE,\nMANDOU SEU FILHO À CRUZ\nA MORRER POR MIM E POR TI\nE TAMBÉM PARA VENCER,\nVENCER A MORTE, VENCER A DOR;\nELE É JESUS, UNIGÊNITO FILHO,\nREI, SENHOR, SALVADOR.\n\nCORO\nTE CHAMA PRA VIDA, \nTE CHAMA PRA LUZ,\nVENCEU A MORTE, ELE É JESUS,\nQUER TE SALVAR, VIDA TE DAR,\nVEM A JESUS.\n\nUM ALTO PREÇO ELE PAGOU\nSÓ POR AMOR AO PECADOR,\nALI NA CRUZ JESUS MORREU\nE DEPOIS RESSUSCITOU,\nVENCEU A MORTE, VENCEU A DOR;\nELE É JESUS, UNIGÊNITO FILHO,\nREI, SENHOR, SALVADOR.\n\nCORO\nTE CHAMA PRA VIDA, \nTE CHAMA PRA LUZ,\nVENCEU A MORTE, ELE É JESUS,\nQUER TE SALVAR, VIDA TE DAR,\nVEM A JESUS.\n\nFINAL:\nTE CHAMA PRA VIDA (TE CHAMA PRA VIDA) \nTE CHAMA PRA LUZ (TE CHAMA PRA LUZ) \nVENCEU A MORTE(ELE VENCEU) \nELE É JESUS (ELE É JESUS) \nQUER TE SALVAR (QUER TE SALVAR) \nVIDA TE DAR, \nVEM A JESUS. (BIS)', 'O AMOR DE DEUS É GRANDE, MANDOU SEU FILHO À CRUZ A MORRER POR MIM E POR TI E TAMBÉM PARA VENCER, VENCER A MORTE, VENCER A DOR; ELE É JESUS, UNIGÊNITO FILHO, REI, SENHOR, SALVADOR. CORO TE CHAMA PRA VIDA, TE CHAMA PRA LUZ, VENCEU A MORTE, ELE É JESUS, QUER TE SALVAR, VIDA TE DAR, VEM A JESUS. UM ALTO PREÇO ELE PAGOU SÓ POR AMOR AO PECADOR, ALI NA CRUZ JESUS MORREU E DEPOIS RESSUSCITOU, VENCEU A MORTE, VENCEU A DOR; ELE É JESUS, UNIGÊNITO FILHO, REI, SENHOR, SALVADOR. TE CHAMA PRA VIDA (TE CHAMA PRA VIDA) TE CHAMA PRA LUZ (TE CHAMA PRA LUZ) VENCEU A MORTE(ELE VENCEU) ELE É JESUS (ELE É JESUS) QUER TE SALVAR (QUER TE SALVAR) VIDA TE DAR, VEM A JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('285', '285 - TU QUE ANDAS SÓ E TRISTE  SEM SABER O CAMINHO', 'TU QUE ANDAS SÓ E TRISTE SEM SABER \nO CAMINHO, EXISTE ALGUÉM \nQUE PODE TUA FOME E TUA SEDE SACIAR;\nELE ESTÁ À PORTA DO TEU CORAÇÃO\nE INSISTENTE BATE AGORA, DEIXA-O ENTRAR, \nDEIXA-O FLUIR EM TEU SER.\n\nQUEM É JESUS? \n“EU SOU AQUELE QUE NA CRUZ MORRI POR TI, \nMEU SANGUE ALI VERTI PRA TE SALVAR, (PRA TE SALVAR) \nPOR TE AMAR, (POR TE AMAR) \nE QUERO TUA VIDA MUDAR.”\n\nINSTRUMENTOS\n\nTU QUE ANDAS SÓ E TRISTE SEM SABER \nO CAMINHO, EXISTE ALGUÉM \nQUE PODE TE AJUDAR E UMA VIDA NOVA TE DAR;\nELE ESTÁ À PORTA DO TEU CORAÇÃO\nE INSISTENTE BATE AGORA, DEIXA-O ENTRAR, \nDEIXA-O FLUIR EM TEU SER.\n\nQUERO JESUS! (SERVAS)\nPOIS SEI QUE NUMA CRUZ \nPOR MIM MORREU,\nSEU SANGUE ALI VERTEU\nPRA ME SALVAR, \n(PRA ME SALVAR) SERVAS\nPOR ME AMAR, \n(POR ME AMAR) SERVAS\nE PODE MINHA VIDA MUDAR.\n\nQUERO JESUS!\nE RENDEREI A ELE O MEU LOUVOR\nPOR TUDO QUE A MIM FEZ, \nQUERO SEMPRE A TI, JESUS, SER FIEL.', 'TU QUE ANDAS SÓ E TRISTE SEM SABER O CAMINHO, EXISTE ALGUÉM QUE PODE TUA FOME E TUA SEDE SACIAR; ELE ESTÁ À PORTA DO TEU CORAÇÃO E INSISTENTE BATE AGORA, DEIXA-O ENTRAR, DEIXA-O FLUIR EM TEU SER. QUEM É JESUS? EU SOU AQUELE QUE NA CRUZ MORRI POR TI, MEU SANGUE ALI VERTI PRA TE SALVAR, (PRA TE SALVAR) POR TE AMAR, (POR TE AMAR) E QUERO TUA VIDA MUDAR. INSTRUMENTOS TU QUE ANDAS SÓ E TRISTE SEM SABER O CAMINHO, EXISTE ALGUÉM QUE PODE TE AJUDAR E UMA VIDA NOVA TE DAR; ELE ESTÁ À PORTA DO TEU CORAÇÃO E INSISTENTE BATE AGORA, DEIXA-O ENTRAR, DEIXA-O FLUIR EM TEU SER. QUERO JESUS!  POIS SEI QUE NUMA CRUZ POR MIM MORREU, SEU SANGUE ALI VERTEU PRA ME SALVAR, (PRA ME SALVAR) SERVAS POR ME AMAR, (POR ME AMAR) SERVAS E PODE MINHA VIDA MUDAR. QUERO JESUS! E RENDEREI A ELE O MEU LOUVOR POR TUDO QUE A MIM FEZ, QUERO SEMPRE A TI, JESUS, SER FIEL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('286', '286 - UM DIA EU QUIS TE DEIXAR', 'UM DIA EU QUIS TE DEIXAR,\nPARTIR E SER LIVRE PARA VIVER;\nABANDONEI-TE, TÃO CEGO NÃO VI\nCOMO ENTÃO FERI TEU CORAÇÃO DE PAI.\nBEM CEDO TUDO PERDI,\nA SOLIDÃO TOMOU CONTA DE MIM;\nENTÃO LEMBREI-ME DA CASA DO PAI\nE CONTRITO VIM, VOLTANDO PARA TI.\n\nCORO\nVOLTEI, MEU PAI,\nAO TEU AMOR INCONDICIONAL!\nÉS MINHA HERANÇA,\nCONTIGO SOU TÃO FELIZ, MEU DEUS!\n\nDE LONGE VISTE CHEGAR\nE ME ABRAÇASTE COM TANTO AMOR,\nE HOUVE CÂNTICOS, FESTA NOS CÉUS,\nPOIS VOLTOU À CASA UM POBRE PECADOR.\n\nFINAL: TÃO FELIZ!', 'UM DIA EU QUIS TE DEIXAR, PARTIR E SER LIVRE PARA VIVER; ABANDONEI-TE, TÃO CEGO NÃO VI COMO ENTÃO FERI TEU CORAÇÃO DE PAI. BEM CEDO TUDO PERDI, A SOLIDÃO TOMOU CONTA DE MIM; ENTÃO LEMBREI-ME DA CASA DO PAI E CONTRITO VIM, VOLTANDO PARA TI. CORO VOLTEI, MEU PAI, AO TEU AMOR INCONDICIONAL! ÉS MINHA HERANÇA, CONTIGO SOU TÃO FELIZ, MEU DEUS! DE LONGE VISTE CHEGAR E ME ABRAÇASTE COM TANTO AMOR, E HOUVE CÂNTICOS, FESTA NOS CÉUS, POIS VOLTOU À CASA UM POBRE PECADOR.  TÃO FELIZ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('287', '287 - NÃO TINHA PARECER', 'NÃO TINHA PARECER NEM FORMOSURA,\nERA DESPREZADO ENTRE OS HOMENS,\nCOMO OVELHA MUDA FOI SACRIFICADO,\nELE VENCEU O MUNDO, SIM,\nELE VENCEU A MORTE.\n\nCORO\nLÁ NA CRUZ, LÁ NA CRUZ\nO SEU SANGUE DERRAMOU,\nLÁ NA CRUZ, LÁ NA CRUZ\nSUA VIDA ENTREGOU;\nÉ POR ISSO QUE CANTO ALELUIA,\nÉ POR ISSO QUE AMO A JESUS,\nNÃO POSSO VIVER SEM O SEU AMOR,\nJESUS É TUDO PRA MIM.\n\nINSTRUMENTOS\n\nJESUS MORREU AQUI POR NOSSAS VIDAS,\nSEU AMOR É INCOMPARÁVEL,\nFOI SUBINDO COMO UM RENOVO\nE RAIZ DE UMA TERRA SECA,\nCONCEDENDO-NOS SEU PERDÃO,\nCONCEDENDO-NOS SALVAÇÃO.\n\nCORO\nLÁ NA CRUZ, LÁ NA CRUZ\nO SEU SANGUE DERRAMOU,\nLÁ NA CRUZ, LÁ NA CRUZ\nSUA VIDA ENTREGOU;\nÉ POR ISSO QUE CANTO ALELUIA,\nÉ POR ISSO QUE AMO A JESUS,\nNÃO POSSO VIVER SEM O SEU AMOR,\nJESUS É TUDO PRA MIM.', 'NÃO TINHA PARECER NEM FORMOSURA, ERA DESPREZADO ENTRE OS HOMENS, COMO OVELHA MUDA FOI SACRIFICADO, ELE VENCEU O MUNDO, SIM, ELE VENCEU A MORTE. CORO LÁ NA CRUZ, LÁ NA CRUZ O SEU SANGUE DERRAMOU, LÁ NA CRUZ, LÁ NA CRUZ SUA VIDA ENTREGOU; É POR ISSO QUE CANTO ALELUIA, É POR ISSO QUE AMO A JESUS, NÃO POSSO VIVER SEM O SEU AMOR, JESUS É TUDO PRA MIM. INSTRUMENTOS JESUS MORREU AQUI POR NOSSAS VIDAS, SEU AMOR É INCOMPARÁVEL, FOI SUBINDO COMO UM RENOVO E RAIZ DE UMA TERRA SECA, CONCEDENDO-NOS SEU PERDÃO, CONCEDENDO-NOS SALVAÇÃO. CORO LÁ NA CRUZ, LÁ NA CRUZ O SEU SANGUE DERRAMOU, LÁ NA CRUZ, LÁ NA CRUZ SUA VIDA ENTREGOU; É POR ISSO QUE CANTO ALELUIA, É POR ISSO QUE AMO A JESUS, NÃO POSSO VIVER SEM O SEU AMOR, JESUS É TUDO PRA MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('288', '288 - BASTA DIZERES O QUE QUERES', 'BASTA DIZERES O QUE QUERES\nJESUS PRONTO ESTÁ A TE OUVIR,\nBASTA COM O SENHOR SERES SINCERO \nE ELE TE RESPONDERÁ.\n\nA ALEGRIA QUE NO MUNDO \nJAMAIS PUDESTE ENCONTRAR, \nEM CRISTO TERÁS O PRAZER, \nSIM, DELA GOZAR.\n\nCORO\nSENTE OS BRAÇOS DE JESUS\nQUERENDO TE SOCORRER,\nOUVE A VOZ SUAVE DO FILHO DE DEUS\nDIZENDO: “TE AMO E QUERO TE SALVAR.”\n\nCRISTO QUER QUE ESTEJAS CONOSCO\nQUANDO VIER NOS BUSCAR\nPROCLAMANDO: \n“VINDE BENDITOS DE MEU PAI \nCOMIGO NO CÉU MORAR”.\n\nCORO\nSENTE OS BRAÇOS DE JESUS\nQUERENDO TE SOCORRER,\nOUVE A VOZ SUAVE DO FILHO DE DEUS\nDIZENDO: “TE AMO E QUERO TE SALVAR.”\n\n“TE AMO E QUERO TE SALVAR.”', 'BASTA DIZERES O QUE QUERES JESUS PRONTO ESTÁ A TE OUVIR, BASTA COM O SENHOR SERES SINCERO E ELE TE RESPONDERÁ. A ALEGRIA QUE NO MUNDO JAMAIS PUDESTE ENCONTRAR, EM CRISTO TERÁS O PRAZER, SIM, DELA GOZAR. CORO SENTE OS BRAÇOS DE JESUS QUERENDO TE SOCORRER, OUVE A VOZ SUAVE DO FILHO DE DEUS DIZENDO: TE AMO E QUERO TE SALVAR. CRISTO QUER QUE ESTEJAS CONOSCO QUANDO VIER NOS BUSCAR PROCLAMANDO: VINDE BENDITOS DE MEU PAI COMIGO NO CÉU MORAR. CORO SENTE OS BRAÇOS DE JESUS QUERENDO TE SOCORRER, OUVE A VOZ SUAVE DO FILHO DE DEUS DIZENDO: TE AMO E QUERO TE SALVAR. TE AMO E QUERO TE SALVAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('289', '289 - ESTENDE TUA MÃO', 'ESTENDE TUA MÃO AO DEUS QUE TE AMA,\nABRE O CORAÇÃO AO DEUS QUE TE GUARDA;\nSÓ ELE SABERÁ TEUS PROBLEMAS RESOLVER,\nÓ ABRE O TEU CORAÇÃO A DEUS.\nBIS\n\nELE SERÁ TEU PASTOR, SÓ ELE TE GUIARÁ\nJUNTO ÀS ÁGUAS TRANQÜILAS,\nREFRIGERA TUA VIDA,\nELE TE MOSTRARÁ A SALVAÇÃO.\n\nSÓ ESTENDE TUA MÃO AO DEUS QUE \nTE AMA, ABRE O CORAÇÃO AO DEUS QUE  TE GUARDA;\nSÓ ELE SABERÁ TEUS PROBLEMAS RESOLVER\nÓ ABRE O TEU CORAÇÃO A DEUS. (2X)', 'ESTENDE TUA MÃO AO DEUS QUE TE AMA, ABRE O CORAÇÃO AO DEUS QUE TE GUARDA; SÓ ELE SABERÁ TEUS PROBLEMAS RESOLVER, Ó ABRE O TEU CORAÇÃO A DEUS. ELE SERÁ TEU PASTOR, SÓ ELE TE GUIARÁ JUNTO ÀS ÁGUAS TRANQÜILAS, REFRIGERA TUA VIDA, ELE TE MOSTRARÁ A SALVAÇÃO. SÓ ESTENDE TUA MÃO AO DEUS QUE TE AMA, ABRE O CORAÇÃO AO DEUS QUE TE GUARDA; SÓ ELE SABERÁ TEUS PROBLEMAS RESOLVER Ó ABRE O TEU CORAÇÃO A DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('290', '290 - O QUE OS OLHOS NUNCA VIRAM', 'O QUE OS OLHOS NUNCA VIRAM,\nE O CORAÇÃO NUNCA SENTIU,\nDEUS PREPAROU EM SEU FAVOR,\nPARA O ABENÇOAR\nABRA SEU CORAÇÃO E DEIXE QUE A LUZ,\nVENHA BRILHAR EM SEU VIVER, \nETERNAMENTE.\n\nCORO\nENTÃO COMECE A GLORIFICAR\nPELO QUE AINDA NÃO VIU.\nSE DEUS PROMETEU, VAI SE CUMPRIR\nSUA BÊNÇÃO VAI CHEGAR.\n\nABRA SEU CORAÇÃO \nE DEIXE QUE A LUZ,\nVENHA BRILHAR EM SEU VIVER, \nETERNAMENTE\n\nCORO (2X)\nENTÃO COMECE A GLORIFICAR\nPELO QUE AINDA NÃO VIU.\nSE DEUS PROMETEU, VAI SE CUMPRIR\nSUA BÊNÇÃO VAI CHEGAR.\n\nFINAL: (2X)\nDEUS TEM SEMPRE O MELHOR, \nMESMO QUE A LUZ \nNÃO BRILHE EM SEUS OLHOS. \nVÊ, DEUS AMA VOCÊ! \nJÁ DECRETOU A SUA VITÓRIA. \n\nBASTA CRER, BASTA CRER.', 'O QUE OS OLHOS NUNCA VIRAM, E O CORAÇÃO NUNCA SENTIU, DEUS PREPAROU EM SEU FAVOR, PARA O ABENÇOAR ABRA SEU CORAÇÃO E DEIXE QUE A LUZ, VENHA BRILHAR EM SEU VIVER, ETERNAMENTE. CORO ENTÃO COMECE A GLORIFICAR PELO QUE AINDA NÃO VIU. SE DEUS PROMETEU, VAI SE CUMPRIR SUA BÊNÇÃO VAI CHEGAR. ABRA SEU CORAÇÃO E DEIXE QUE A LUZ, VENHA BRILHAR EM SEU VIVER, ETERNAMENTE ENTÃO COMECE A GLORIFICAR PELO QUE AINDA NÃO VIU. SE DEUS PROMETEU, VAI SE CUMPRIR SUA BÊNÇÃO VAI CHEGAR. DEUS TEM SEMPRE O MELHOR, MESMO QUE A LUZ NÃO BRILHE EM SEUS OLHOS. VÊ, DEUS AMA VOCÊ! JÁ DECRETOU A SUA VITÓRIA. BASTA CRER, BASTA CRER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('291', '291 - SEMPRE QUE QUISERES FALAREI CONTIGO', 'SEMPRE QUE QUISERES FALAREI CONTIGO, \nSEMPRE QUE PRECISARES, \nESTAREI À DISPOSIÇÃO \nPRA MOSTRAR A TI O MEU AMOR \nE DAR-TE A MINHA SALVAÇÃO.\nBIS\n\nO MEU AMOR POR TI, Ó FILHO É MUITO GRANDE \nTÃO GRANDE QUE NÃO PODES NEM IMAGINAR \nPOR VOCÊ EU DEI MEU ÚNICO FILHO, \nPRA MORRER NAQUELA CRUZ, \nPRA QUE VOCÊ TENHA VIDA EM ABUNDÂNCIA.\nPRA QUE VOCÊ TENHA VIDA ETERNA.\n\nO MEU AMOR POR TI, Ó FILHO É MUITO GRANDE \nTÃO GRANDE QUE NÃO PODES NEM IMAGINAR \nPOR VOCÊ EU DEI MEU ÚNICO FILHO, \nPRA MORRER NAQUELA CRUZ, \nPRA QUE VOCÊ TENHA VIDA EM ABUNDÂNCIA.\nPRA QUE VOCÊ TENHA VIDA ETERNA.', 'SEMPRE QUE QUISERES FALAREI CONTIGO, SEMPRE QUE PRECISARES, ESTAREI À DISPOSIÇÃO PRA MOSTRAR A TI O MEU AMOR E DAR-TE A MINHA SALVAÇÃO. O MEU AMOR POR TI, Ó FILHO É MUITO GRANDE TÃO GRANDE QUE NÃO PODES NEM IMAGINAR POR VOCÊ EU DEI MEU ÚNICO FILHO, PRA MORRER NAQUELA CRUZ, PRA QUE VOCÊ TENHA VIDA EM ABUNDÂNCIA. PRA QUE VOCÊ TENHA VIDA ETERNA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('292', '292 - AO SENTIR O MUNDO AO MEU REDOR', 'AO SENTIR O MUNDO AO MEU REDOR,\nNADA VI QUE PUDESSE SER REAL.\nPERCEBI QUE TODOS BUSCAM PAZ, PORÉM EM VÃO,\nPOIS NAQUILO QUE PROCURAM NÃO HÁ SOLUÇÃO.\nNÃO HÁ SOLUÇÃO.\n\nSÓ EM JESUS, A PAZ REAL EU PUDE ENCONTRAR.\nO SEU AMOR PUDE EXPERIMENTAR.\nME ENTREGUEI A CRISTO,\nE A VIDA ETERNA VOU GOZAR.\n\nINSTRUMENTOS\n\nPOSSO VER QUE VOCÊ NÃO É FELIZ,\nVOU DIZER QUE NÃO PODE SER FELIZ,\nSE CONTINUAR A PROCURAR EM VÃO,\nPOR CAMINHOS QUE NÃO TRAZEM SOLUÇÃO.\nNÃO TRAZEM SOLUÇÃO.\n\nSÓ EM JESUS, A PAZ REAL VOCÊ VAI ENCONTRAR.\nO SEU AMOR VAI EXPERIMENTAR.\nVENHA A JESUS CRISTO,\nE A VIDA ETERNA VAI GOZAR.\n\nINSTRUMENTOS\n\nSÓ EM JESUS, A PAZ REAL VOCÊ VAI ENCONTRAR.\nO SEU AMOR VAI EXPERIMENTAR.\nVENHA A JESUS CRISTO,\nE A VIDA ETERNA VAI GOZAR.', 'AO SENTIR O MUNDO AO MEU REDOR, NADA VI QUE PUDESSE SER REAL. PERCEBI QUE TODOS BUSCAM PAZ, PORÉM EM VÃO, POIS NAQUILO QUE PROCURAM NÃO HÁ SOLUÇÃO. NÃO HÁ SOLUÇÃO. SÓ EM JESUS, A PAZ REAL EU PUDE ENCONTRAR. O SEU AMOR PUDE EXPERIMENTAR. ME ENTREGUEI A CRISTO, E A VIDA ETERNA VOU GOZAR. INSTRUMENTOS POSSO VER QUE VOCÊ NÃO É FELIZ, VOU DIZER QUE NÃO PODE SER FELIZ, SE CONTINUAR A PROCURAR EM VÃO, POR CAMINHOS QUE NÃO TRAZEM SOLUÇÃO. NÃO TRAZEM SOLUÇÃO. SÓ EM JESUS, A PAZ REAL VOCÊ VAI ENCONTRAR. O SEU AMOR VAI EXPERIMENTAR. VENHA A JESUS CRISTO, E A VIDA ETERNA VAI GOZAR. INSTRUMENTOS SÓ EM JESUS, A PAZ REAL VOCÊ VAI ENCONTRAR. O SEU AMOR VAI EXPERIMENTAR. VENHA A JESUS CRISTO, E A VIDA ETERNA VAI GOZAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('293', '293 - ALMA CANSADA', 'ALMA CANSADA, NÃO DESESPERES, ESPERA EM DEUS.\nQUE ELE VEM, QUE ELE VEM, TE SOCORRER\nALMA CANSADA, NÃO DESESPERES, ESPERA EM DEUS,\nESPERA EM DEUS, ESPERA EM DEUS\nE NO SEU PODER.\n\nCORO\nÓ ALMA CANSADA, ESPERA EM DEUS\nE NO SEU AMOR.\nVEM, ALMA CANSADA, ESPERA EM DEUS,\nCONFIA SOMENTE NO SEU AMOR.\n\nINSTRUMENTOS\n\nALMA CANSADA, O TEU LAMENTO NÃO RESOLVERÁ.\nA HORA É TRISTE, COM FÉ INSISTE, DEUS TE OUVIRÁ.\nSE TEUS MOMENTOS TRAZEM TORMENTOS\nQUE FAZEM CHORAR, DEUS TE CHAMA AGORA,\nÓ VEM, SEM DEMORA EM PAZ DESCANSAR.\n\nCORO\nÓ ALMA CANSADA, ESPERA EM DEUS\nE NO SEU AMOR.\nVEM, ALMA CANSADA, ESPERA EM DEUS,\nCONFIA SOMENTE NO SEU AMOR.', 'ALMA CANSADA, NÃO DESESPERES, ESPERA EM DEUS. QUE ELE VEM, QUE ELE VEM, TE SOCORRER ALMA CANSADA, NÃO DESESPERES, ESPERA EM DEUS, ESPERA EM DEUS, ESPERA EM DEUS E NO SEU PODER. CORO Ó ALMA CANSADA, ESPERA EM DEUS E NO SEU AMOR. VEM, ALMA CANSADA, ESPERA EM DEUS, CONFIA SOMENTE NO SEU AMOR. INSTRUMENTOS ALMA CANSADA, O TEU LAMENTO NÃO RESOLVERÁ. A HORA É TRISTE, COM FÉ INSISTE, DEUS TE OUVIRÁ. SE TEUS MOMENTOS TRAZEM TORMENTOS QUE FAZEM CHORAR, DEUS TE CHAMA AGORA, Ó VEM, SEM DEMORA EM PAZ DESCANSAR. CORO Ó ALMA CANSADA, ESPERA EM DEUS E NO SEU AMOR. VEM, ALMA CANSADA, ESPERA EM DEUS, CONFIA SOMENTE NO SEU AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('294', '294 - JESUS IA CAMINHANDO (CANTA MEU POVO)', 'JESUS IA CAMINHANDO,\nSUBINDO PRA JERUSALÉM.\nCOM ELE IAM OS SEUS DISCÍPULOS\nE OS FARISEUS TAMBÉM.\nOS DISCÍPULOS SE ALEGRAVAM,\nCANTAVAM E DAVAM LOUVOR,\nDIZENDO: BENDITO É O REI\nQUE VEM EM NOME DO SENHOR.\n\nCORO\nCANTA MEU POVO, ALEGRA MEU POVO,\nQUE A FESTA NÃO VAI ACABAR\nQUANDO FINDAR NA TERRA, \nNO CÉU VAI CONTINUAR.\n\nOS FARISEUS MURMURAVAM,\nFALAVAM COM ANSIEDADE\nMANDE QUE O POVO SE CALE,\nNÃO PERTURBE A CIDADE.\nJESUS OLHANDO PARA ELES,\nLHES DISSE COM SATISFAÇÃO:\nSE ELES SE CALAREM,\nLOGO AS PEDRAS CLAMARÃO.\n\nCORO\nCANTA MEU POVO, ALEGRA MEU POVO,\nQUE A FESTA NÃO VAI ACABAR\nQUANDO FINDAR NA TERRA, \nNO CÉU VAI CONTINUAR.\n\nE NÓS QUE SABEMOS ISTO,\nQUE FOI JESUS QUEM FALOU,\nANTES QUE AS PEDRAS CLAMEM,\nNÓS CLAMAMOS AO SENHOR.\nJESUS DESCEU DA SUA GLÓRIA\nPRA NOS TRAZER SALVAÇÃO,\nNÃO HÁ NINGUÉM QUE TIRE\nO GOZO DO MEU CORAÇÃO.\n\nCORO (2X)\nCANTA MEU POVO, ALEGRA MEU POVO,\nQUE A FESTA NÃO VAI ACABAR\nQUANDO FINDAR NA TERRA, \nNO CÉU VAI CONTINUAR.', 'JESUS IA CAMINHANDO, SUBINDO PRA JERUSALÉM. COM ELE IAM OS SEUS DISCÍPULOS E OS FARISEUS TAMBÉM. OS DISCÍPULOS SE ALEGRAVAM, CANTAVAM E DAVAM LOUVOR, DIZENDO: BENDITO É O REI QUE VEM EM NOME DO SENHOR. CORO CANTA MEU POVO, ALEGRA MEU POVO, QUE A FESTA NÃO VAI ACABAR QUANDO FINDAR NA TERRA, NO CÉU VAI CONTINUAR. OS FARISEUS MURMURAVAM, FALAVAM COM ANSIEDADE MANDE QUE O POVO SE CALE, NÃO PERTURBE A CIDADE. JESUS OLHANDO PARA ELES, LHES DISSE COM SATISFAÇÃO: SE ELES SE CALAREM, LOGO AS PEDRAS CLAMARÃO. E NÓS QUE SABEMOS ISTO, QUE FOI JESUS QUEM FALOU, ANTES QUE AS PEDRAS CLAMEM, NÓS CLAMAMOS AO SENHOR. JESUS DESCEU DA SUA GLÓRIA PRA NOS TRAZER SALVAÇÃO, NÃO HÁ NINGUÉM QUE TIRE O GOZO DO MEU CORAÇÃO. CANTA MEU POVO, ALEGRA MEU POVO, QUE A FESTA NÃO VAI ACABAR QUANDO FINDAR NA TERRA, NO CÉU VAI CONTINUAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('295', '295 - DAS PROFUNDEZAS A TI CLAMO, Ó SENHOR', 'DAS PROFUNDEZAS A TI CLAMO, Ó SENHOR, ESCUTA A MINHA VOZ;\nSEJAM OS TEUS OUVIDOS ATENTOS \nÀ VOZ DAS MINHAS SÚPLICAS; SE TU, SENHOR,\nOBSERVARES AS INIQÜIDADES, \nSENHOR, QUEM SUBSISTIRÁ?\nMAS CONTIGO ESTÁ O PERDÃO \nPARA QUE SEJAS TEMIDO, \nAGUARDO O SENHOR,\nA MINHA ALMA O AGUARDA.\n\nE ESPERO NA SUA PALAVRA,\nA MINH’ALMA ANSEIA PELO SENHOR\nMAIS DO QUE OS GUARDAS\nPELO ROMPER DA MANHÃ,\nSIM, DO QUE AQUELES\nQUE ESPERAM PELA MANHÃ.\n\nESPERA, ISRAEL, NO SENHOR,\nPORQUE NO SENHOR HÁ MISERICÓRDIA\nE NELE HÁ ABUNDANTE REDENÇÃO,\nE ELE REMIRÁ A ISRAEL\nDE TODAS AS SUAS INIQÜIDADES.\nBIS', 'DAS PROFUNDEZAS A TI CLAMO, Ó SENHOR, ESCUTA A MINHA VOZ; SEJAM OS TEUS OUVIDOS ATENTOS À VOZ DAS MINHAS SÚPLICAS; SE TU, SENHOR, OBSERVARES AS INIQÜIDADES, SENHOR, QUEM SUBSISTIRÁ? MAS CONTIGO ESTÁ O PERDÃO PARA QUE SEJAS TEMIDO, AGUARDO O SENHOR, A MINHA ALMA O AGUARDA. E ESPERO NA SUA PALAVRA, A MINH’ALMA ANSEIA PELO SENHOR MAIS DO QUE OS GUARDAS PELO ROMPER DA MANHÃ, SIM, DO QUE AQUELES QUE ESPERAM PELA MANHÃ. ESPERA, ISRAEL, NO SENHOR, PORQUE NO SENHOR HÁ MISERICÓRDIA E NELE HÁ ABUNDANTE REDENÇÃO, E ELE REMIRÁ A ISRAEL DE TODAS AS SUAS INIQÜIDADES.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('296', '296 - TODA A MINHA DOR JESUS LEVOU', 'TODA A MINHA DOR JESUS LEVOU\nE A TRISTEZA E O PRANTO ELE ENXUGOU,\nHOJE SOU FELIZ PORQUE O SENHOR\nDE TODO O PECADO ME LAVOU.\n\nCORO\nDEIXA-ME CHORAR A TEUS PÉS, SENHOR,\nQUERO AGRADECER TEU GRANDE AMOR;\nE JUNTO DA CRUZ QUERO ESTAR,\nA TEUS PÉS, JESUS, DEIXA CHORAR.\n\nO SEU SANGUE PURO DERRAMOU,\nTODO O MAL DO MUNDO ELE VENCEU;\nPOR SEU DIVINAL E EXCELSO AMOR\nA TODOS PERDÃO QUIS CONCEDER.\n\nCORO\nDEIXA-ME CHORAR A TEUS PÉS, SENHOR,\nQUERO AGRADECER TEU GRANDE AMOR;\nE JUNTO DA CRUZ QUERO ESTAR,\nA TEUS PÉS, JESUS, DEIXA CHORAR.\n\nPARA TI JESUS ESTÁ OLHANDO,\nCOM MAIOR TERNURA ESTÁ PEDINDO:\n“DÁ-ME, FILHO MEU, TEU CORAÇÃO\nE UM DIA TERÁS UM GALARDÃO!”\n\nCORO\nDEIXA-ME CHORAR A TEUS PÉS, SENHOR,\nQUERO AGRADECER TEU GRANDE AMOR;\nE JUNTO DA CRUZ QUERO ESTAR,\nA TEUS PÉS, JESUS, DEIXA CHORAR.', 'TODA A MINHA DOR JESUS LEVOU E A TRISTEZA E O PRANTO ELE ENXUGOU, HOJE SOU FELIZ PORQUE O SENHOR DE TODO O PECADO ME LAVOU. CORO DEIXA-ME CHORAR A TEUS PÉS, SENHOR, QUERO AGRADECER TEU GRANDE AMOR; E JUNTO DA CRUZ QUERO ESTAR, A TEUS PÉS, JESUS, DEIXA CHORAR. O SEU SANGUE PURO DERRAMOU, TODO O MAL DO MUNDO ELE VENCEU; POR SEU DIVINAL E EXCELSO AMOR A TODOS PERDÃO QUIS CONCEDER. PARA TI JESUS ESTÁ OLHANDO, COM MAIOR TERNURA ESTÁ PEDINDO: DÁ-ME, FILHO MEU, TEU CORAÇÃO E UM DIA TERÁS UM GALARDÃO!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('297', '297 - EM ME VINDO O TEMOR', 'EM ME VINDO O TEMOR HEI DE CONFIAR EM TI,\nEM ME VINDO A DOR DESCANSAREI EM TI;\nPOR QUE TEMER SE COMIGO ESTÁS?\nTE SINTO TÃO PERTO, SEGURO ME FAZES.\n\nTU ME ENSINASTE A EM TI CONFIAR,\nTU ME AJUDASTE A EM TI ESPERAR;\nDÁ-ME MAIS FÉ PARA PROSSEGUIR\nE NUNCA TEMER O QUE HÁ DE VIR.\n\nEM TI EU SOU FORTE, CONHEÇO TUA PAZ,\nCONTIGO AO MEU LADO O TEMOR SE DESFAZ;\nPOR QUE TEMER SE COMIGO ESTÁS?\nTE SINTO TÃO PERTO, SEGURO ME FAZES.', 'EM ME VINDO O TEMOR HEI DE CONFIAR EM TI, EM ME VINDO A DOR DESCANSAREI EM TI; POR QUE TEMER SE COMIGO ESTÁS? TE SINTO TÃO PERTO, SEGURO ME FAZES. TU ME ENSINASTE A EM TI CONFIAR, TU ME AJUDASTE A EM TI ESPERAR; DÁ-ME MAIS FÉ PARA PROSSEGUIR E NUNCA TEMER O QUE HÁ DE VIR. EM TI EU SOU FORTE, CONHEÇO TUA PAZ, CONTIGO AO MEU LADO O TEMOR SE DESFAZ; POR QUE TEMER SE COMIGO ESTÁS? TE SINTO TÃO PERTO, SEGURO ME FAZES.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('298', '298 - NEM SEMPRE ENTENDEMOS', 'NEM SEMPRE ENTENDEMOS \nPORQUE DEUS AGE ASSIM,\nO ESPINHO QUE A BELA ROSA TEM;\nMAS PELA FÉ SABEMOS \nQUE TUDO QUE ELE FAZ É SEMPRE, \nSEMPRE PARA O NOSSO BEM.\n\nCORO\nNADA ME SEPARA DESSE AMOR,\nSEMPRE CONFIAREI NO MEU SENHOR;\nSEI QUE CUIDARÁ DE MIM, \nCOM DESVELO ATÉ O FIM, \nNADA ME SEPARARÁ DO AMOR DE DEUS.\n\nA DOR E O SOFRIMENTO, \nA MORTE TÃO CRUEL,\nVENCEU NA CRUZ O NOSSO EMANUEL;\nDE CINZAS AS VESTES TROCA \nPOR VESTES DE LOUVOR \nE ÓLEO DE ALEGRIA DÁ-NOS O SENHOR.\n\nCORO\nNADA ME SEPARA DESSE AMOR,\nSEMPRE CONFIAREI NO MEU SENHOR;\nSEI QUE CUIDARÁ DE MIM, \nCOM DESVELO ATÉ O FIM, \nNADA ME SEPARARÁ DO AMOR DE DEUS. (BIS)', 'NEM SEMPRE ENTENDEMOS PORQUE DEUS AGE ASSIM, O ESPINHO QUE A BELA ROSA TEM; MAS PELA FÉ SABEMOS QUE TUDO QUE ELE FAZ É SEMPRE, SEMPRE PARA O NOSSO BEM. CORO NADA ME SEPARA DESSE AMOR, SEMPRE CONFIAREI NO MEU SENHOR; SEI QUE CUIDARÁ DE MIM, COM DESVELO ATÉ O FIM, NADA ME SEPARARÁ DO AMOR DE DEUS. A DOR E O SOFRIMENTO, A MORTE TÃO CRUEL, VENCEU NA CRUZ O NOSSO EMANUEL; DE CINZAS AS VESTES TROCA POR VESTES DE LOUVOR E ÓLEO DE ALEGRIA DÁ-NOS O SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('299', '299 - HÁ MOMENTOS QUE AS PALAVRAS NÃO RESOLVEM', 'HÁ MOMENTOS QUE AS PALAVRAS \nNÃO RESOLVEM,\nMAS O GESTO DE JESUS, NA CRUZ, \nDEMONSTRA AMOR POR NÓS.\nBIS\n\nCORO (2X)\nFOI NO CALVÁRIO\nQUE ELE SEM FALAR,\nMOSTROU AO MUNDO \nINTEIRO O QUE É AMAR.\n\nAQUI NO MUNDO \nAS DESILUSÕES SÃO TANTAS,\nMAS EXISTE UMA ESPERANÇA:\nQUE CRISTO VAI VOLTAR.\nBIS\n\nCORO (2X)\nFOI NO CALVÁRIO\nQUE ELE SEM FALAR,\nMOSTROU AO MUNDO \nINTEIRO O QUE É AMAR.', 'HÁ MOMENTOS QUE AS PALAVRAS NÃO RESOLVEM, MAS O GESTO DE JESUS, NA CRUZ, DEMONSTRA AMOR POR NÓS. FOI NO CALVÁRIO QUE ELE SEM FALAR, MOSTROU AO MUNDO INTEIRO O QUE É AMAR. AQUI NO MUNDO AS DESILUSÕES SÃO TANTAS, MAS EXISTE UMA ESPERANÇA: QUE CRISTO VAI VOLTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('300', '300 - QUANDO PARECE QUE TUDO ACABOU', 'QUANDO PARECE QUE TUDO ACABOU\nE NÃO HÁ MAIS ESPERANÇA EM TEU VIVER,\nERGUE TEU OLHAR E CLAMA A JESUS,\nCRÊ SOMENTE: ELE TEM PODER.\n\nCORO\nOH! NÃO TEMAS, MAS CONFIA EM JESUS,\nTEUS PROBLEMAS DEUS CONHECE E TUDO VÊ;\nNÃO CHORES MAIS, JESUS TE AMA,\nNÃO TEMAS, SOMENTE CRÊ.\n\nNÃO TEMAS, CRÊ SOMENTE EM JESUS,\nIMPOSSÍVEIS NÃO HÁ PARA O SENHOR,\nPEDE EM ORAÇÃO E TÃO SOMENTE CRÊ,\nGRANDE É, POR TI, O SEU AMOR.\n\nCORO\nOH! NÃO TEMAS, MAS CONFIA EM JESUS,\nTEUS PROBLEMAS DEUS CONHECE E TUDO VÊ;\nNÃO CHORES MAIS, JESUS TE AMA,\nNÃO TEMAS, SOMENTE CRÊ.\n\nFINAL: \nNÃO CHORES MAIS, JESUS TE AMA,\nNÃO TEMAS, SOMENTE CRÊ.', 'QUANDO PARECE QUE TUDO ACABOU E NÃO HÁ MAIS ESPERANÇA EM TEU VIVER, ERGUE TEU OLHAR E CLAMA A JESUS, CRÊ SOMENTE: ELE TEM PODER. CORO OH! NÃO TEMAS, MAS CONFIA EM JESUS, TEUS PROBLEMAS DEUS CONHECE E TUDO VÊ; NÃO CHORES MAIS, JESUS TE AMA, NÃO TEMAS, SOMENTE CRÊ. NÃO TEMAS, CRÊ SOMENTE EM JESUS, IMPOSSÍVEIS NÃO HÁ PARA O SENHOR, PEDE EM ORAÇÃO E TÃO SOMENTE CRÊ, GRANDE É, POR TI, O SEU AMOR. NÃO CHORES MAIS, JESUS TE AMA, NÃO TEMAS, SOMENTE CRÊ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('301', '301 - QUANDO A TRISTEZA SURGIR', 'QUANDO A TRISTEZA SURGIR\nE O MUNDO TE ENGANAR,\nLEMBRA-TE QUE JESUS CRISTO\nCOM SEU SANGUE TE PODE SALVAR.\n\nCORO (2X)\nCANTA FELIZ E ORA COM FERVOR,\nO SANGUE DE JESUS\nMOSTROU-NOS SEU AMOR.\n\nQUANDO A ALEGRIA VOLTAR\nE A PAZ INVADIR O TEU SER,\nDIZE ALELUIA A JESUS\nPOIS SEU SANGUE TEM ESSE PODER.\n\nCORO (2X)\nCANTA FELIZ E ORA COM FERVOR,\nO SANGUE DE JESUS\nMOSTROU-NOS SEU AMOR.', 'QUANDO A TRISTEZA SURGIR E O MUNDO TE ENGANAR, LEMBRA-TE QUE JESUS CRISTO COM SEU SANGUE TE PODE SALVAR. CANTA FELIZ E ORA COM FERVOR, O SANGUE DE JESUS MOSTROU-NOS SEU AMOR. QUANDO A ALEGRIA VOLTAR E A PAZ INVADIR O TEU SER, DIZE ALELUIA A JESUS POIS SEU SANGUE TEM ESSE PODER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('302', '302 - SE A VIDA PARECE PESADA', 'SE A VIDA PARECE PESADA\nE ACHAS QUE CHEGOU AO FIM;\nÓ VEM A JESUS SEM DEMORA,\nPORQUE ELE DISSE ASSIM:\n\nCORO\n“VINDE A MIM, CANSADOS, \nSOBRECARREGADOS, E EU VOS ALIVIAREI;\nVINDE A MIM, ENTREGAI O VOSSO CORAÇÃO, \nE VIDA ETERNA VOS DAREI.”\n\nTÃO CEDO A TRISTEZA TE ABATE\nE ENTRA EM TEU CORAÇÃO;\nNÃO TEMAS, CONFIA EM CRISTO,\nTERÁS PLENA PAZ, SALVAÇÃO.\n\nCORO (2X)\n“VINDE A MIM, CANSADOS, \nSOBRECARREGADOS, E EU VOS ALIVIAREI;\nVINDE A MIM, ENTREGAI O VOSSO CORAÇÃO, \nE VIDA ETERNA VOS DAREI.”\n\nFINAL: “E VIDA ETERNA VOS DAREI.”', 'SE A VIDA PARECE PESADA E ACHAS QUE CHEGOU AO FIM; Ó VEM A JESUS SEM DEMORA, PORQUE ELE DISSE ASSIM: CORO VINDE A MIM, CANSADOS, SOBRECARREGADOS, E EU VOS ALIVIAREI; VINDE A MIM, ENTREGAI O VOSSO CORAÇÃO, E VIDA ETERNA VOS DAREI. TÃO CEDO A TRISTEZA TE ABATE E ENTRA EM TEU CORAÇÃO; NÃO TEMAS, CONFIA EM CRISTO, TERÁS PLENA PAZ, SALVAÇÃO. VINDE A MIM, CANSADOS, SOBRECARREGADOS, E EU VOS ALIVIAREI; VINDE A MIM, ENTREGAI O VOSSO CORAÇÃO, E VIDA ETERNA VOS DAREI.  E VIDA ETERNA VOS DAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('303', '303 - SE NÃO PUDERES MAIS', 'SE NÃO PUDERES MAIS\nSENTIR O TOQUE DO SENHOR JESUS,\nÉ PORQUE TUA ALMA NÃO SE ABRIU,\nTEU CORAÇÃO NÃO SENTIU\nA PRESENÇA DO SENHOR JESUS.\n\nCORO \nSE HOJE OUVIRDES \nA SUA VOZ NÃO ENDUREÇAIS \nOS VOSSOS CORAÇÕES A JESUS.\n\nINSTRUMENTOS\n\nQUANDO A LUTA SE TRAVAR, \nDO CÉU DEUS OUVIRÁ E ENVIARÁ \nUM ANJO PRA TE LIVRAR;\nCONFIA SOMENTE NO SENHOR, \nO MESMO DEUS DE ONTEM \nE HOJE JAMAIS TE ABANDONARÁ.\n\nCORO (2X)\nSE HOJE OUVIRDES \nA SUA VOZ NÃO ENDUREÇAIS \nOS VOSSOS CORAÇÕES A JESUS.', 'SE NÃO PUDERES MAIS SENTIR O TOQUE DO SENHOR JESUS, É PORQUE TUA ALMA NÃO SE ABRIU, TEU CORAÇÃO NÃO SENTIU A PRESENÇA DO SENHOR JESUS. CORO SE HOJE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES A JESUS. INSTRUMENTOS QUANDO A LUTA SE TRAVAR, DO CÉU DEUS OUVIRÁ E ENVIARÁ UM ANJO PRA TE LIVRAR; CONFIA SOMENTE NO SENHOR, O MESMO DEUS DE ONTEM E HOJE JAMAIS TE ABANDONARÁ. SE HOJE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES A JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('304', '304 - BASTA QUE ME TOQUES, SENHOR', 'BASTA QUE ME TOQUES, SENHOR,\nE MINH’ALMA PROVADA SERÁ.\n\nSE A NOITE ESCURA ESTÁ,\nTUA LUZ ME GUIARÁ,\nBASTA QUE ME TOQUES, SENHOR.\nBIS', 'BASTA QUE ME TOQUES, SENHOR, E MINH’ALMA PROVADA SERÁ. SE A NOITE ESCURA ESTÁ, TUA LUZ ME GUIARÁ, BASTA QUE ME TOQUES, SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('305', '305 - O SENHOR É A MINHA LUZ', 'O SENHOR É A MINHA LUZ, A MINHA SALVAÇÃO, \nA QUEM TEMEREI, A QUEM TEMEREI?\nO SENHOR É O MEU REFÚGIO E PAZ,\nA QUEM ENTÃO EU PRECISO TEMER?\nO SENHOR É A MINHA LUZ, A MINHA SALVAÇÃO,\nA QUEM TEMEREI, A QUEM TEMEREI?\nBIS', 'O SENHOR É A MINHA LUZ, A MINHA SALVAÇÃO, A QUEM TEMEREI, A QUEM TEMEREI? O SENHOR É O MEU REFÚGIO E PAZ, A QUEM ENTÃO EU PRECISO TEMER? O SENHOR É A MINHA LUZ, A MINHA SALVAÇÃO, A QUEM TEMEREI, A QUEM TEMEREI?', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('306', '306 - MEDO TENS QUE O TENTADOR TE VÁ VENCER?', 'MEDO TENS QUE O TENTADOR TE VÁ VENCER?\nLUZ TE FALTA E ONDE ESTÁS NÃO PODES VER?\nABRE O CORAÇÃO E DEIXA CRISTO ENTRAR \nE O SOL EM TI RAIAR.\n\nCORO\nDEIXA A LUZ DO CÉU ENTRAR,\nDEIXA O SOL EM TI NASCER;\nABRE O CORAÇÃO E DEIXA \nCRISTO ENTRAR E O SOL EM TI NASCER.\n\nFRACA ESTÁ A TUA FÉ NO SALVADOR?\nDEUS NÃO OUVE AS TUAS PRECES COM FAVOR?\nABRE O CORAÇÃO E DEIXA CRISTO ENTRAR \nE O SOL EM TI RAIAR.\n\nCORO\nDEIXA A LUZ DO CÉU ENTRAR,\nDEIXA O SOL EM TI NASCER;\nABRE O CORAÇÃO E DEIXA \nCRISTO ENTRAR E O SOL EM TI NASCER.\n\nQUERES IR ANDANDO ALEGRE PARA O CÉU,\nIGNORANDO TODO O NEGRO E DENSO VÉU?\nABRE O CORAÇÃO E DEIXA CRISTO ENTRAR\nE O SOL EM TI RAIAR.\n\nCORO\nDEIXA A LUZ DO CÉU ENTRAR,\nDEIXA O SOL EM TI NASCER;\nABRE O CORAÇÃO E DEIXA \nCRISTO ENTRAR E O SOL EM TI NASCER.', 'MEDO TENS QUE O TENTADOR TE VÁ VENCER? LUZ TE FALTA E ONDE ESTÁS NÃO PODES VER? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR. CORO DEIXA A LUZ DO CÉU ENTRAR, DEIXA O SOL EM TI NASCER; ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI NASCER. FRACA ESTÁ A TUA FÉ NO SALVADOR? DEUS NÃO OUVE AS TUAS PRECES COM FAVOR? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR. QUERES IR ANDANDO ALEGRE PARA O CÉU, IGNORANDO TODO O NEGRO E DENSO VÉU? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('307', '307 - PELO VALE ESCURO SEGUIREI JESUS', 'PELO VALE ESCURO SEGUIREI JESUS,\nMAS POR TI SEGURO, VENDO A TUA LUZ;\nMEUS PASSOS INCERTOS TU DIRIGIRÁS\nE AO SENTIR-TE PERTO NUNCA PERCO A PAZ.\n\nCORO\nBREVE A NOITE DESCE, NOITE DE EMAÚS,\nE MEU SER CARECE DE TE VER, JESUS,\nCOMPANHEIRO AMIGO, \nAO MEU LADO VEM FICA, \nÓ DEUS, COMIGO, INFINITO BEM.\n\nOS ESPINHOS, TANTOS, \nQUE VEM NOS SANGRAR \nSÃO REMÉDIOS SANTOS PARA NOS CURAR;\nONDE EXISTE A GRAÇA DO BONDOSO DEUS \nTUDO O QUE SE PASSA NOS CONDUZ  AOS CÉUS.\n\nCORO\nBREVE A NOITE DESCE, NOITE DE EMAÚS,\nE MEU SER CARECE DE TE VER, JESUS,\nCOMPANHEIRO AMIGO, \nAO MEU LADO VEM FICA, \nÓ DEUS, COMIGO, INFINITO BEM.\n\nNÃO HÁ DOR QUE SEJA SEM \nDIVINO FIM, FAZE, Ó DEUS,\nQUE A IGREJA COMPREENDA ASSIM;\nE, APESAR DAS TREVAS, POSSA VER, SENHOR, \nQUE TU MESMO A LEVAS COM IMENSO AMOR.\n\nCORO\nBREVE A NOITE DESCE, NOITE DE EMAÚS,\nE MEU SER CARECE DE TE VER, JESUS,\nCOMPANHEIRO AMIGO, \nAO MEU LADO VEM FICA, \nÓ DEUS, COMIGO, INFINITO BEM.', 'PELO VALE ESCURO SEGUIREI JESUS, MAS POR TI SEGURO, VENDO A TUA LUZ; MEUS PASSOS INCERTOS TU DIRIGIRÁS E AO SENTIR-TE PERTO NUNCA PERCO A PAZ. CORO BREVE A NOITE DESCE, NOITE DE EMAÚS, E MEU SER CARECE DE TE VER, JESUS, COMPANHEIRO AMIGO, AO MEU LADO VEM FICA, Ó DEUS, COMIGO, INFINITO BEM. OS ESPINHOS, TANTOS, QUE VEM NOS SANGRAR SÃO REMÉDIOS SANTOS PARA NOS CURAR; ONDE EXISTE A GRAÇA DO BONDOSO DEUS TUDO O QUE SE PASSA NOS CONDUZ AOS CÉUS. NÃO HÁ DOR QUE SEJA SEM DIVINO FIM, FAZE, Ó DEUS, QUE A IGREJA COMPREENDA ASSIM; E, APESAR DAS TREVAS, POSSA VER, SENHOR, QUE TU MESMO A LEVAS COM IMENSO AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('308', '308 - PROFUNDOS VALES EU TEREI DE PASSAR', 'PROFUNDOS VALES EU TEREI DE PASSAR\nE MUITAS LUTAS EU TEREI DE VENCER,\nMAS SEI QUE SÓ EU NÃO ESTOU,\nCOMIGO ESTÁS AO MEU REDOR,\nA TUA GRAÇA ME SATISFAZ.\n\nSE UM DIA, ACASO, TRISTE EU ME ENCONTRAR, \nSE SOLITÁRIO NO DESERTO A CAMINHAR, \nENTÃO ALI ME FALARÁS\nE A TUA MÃO ME ESTENDERÁS;\nMOSTRAR-ME-ÁS A CELESTE MANSÃO.\n\nA TUA GRAÇA ME SUSTERÁ POIS FRACO SOU, \nSEM TI NÃO POSSO CAMINHAR;\nREVELA-ME O TEU AMOR, COM \nTUA MÃO VEM ME GUIAR POIS \nJUNTO A TI SEMPRE QUERO ESTAR.\nBIS', 'PROFUNDOS VALES EU TEREI DE PASSAR E MUITAS LUTAS EU TEREI DE VENCER, MAS SEI QUE SÓ EU NÃO ESTOU, COMIGO ESTÁS AO MEU REDOR, A TUA GRAÇA ME SATISFAZ. SE UM DIA, ACASO, TRISTE EU ME ENCONTRAR, SE SOLITÁRIO NO DESERTO A CAMINHAR, ENTÃO ALI ME FALARÁS E A TUA MÃO ME ESTENDERÁS; MOSTRAR-ME-ÁS A CELESTE MANSÃO. A TUA GRAÇA ME SUSTERÁ POIS FRACO SOU, SEM TI NÃO POSSO CAMINHAR; REVELA-ME O TEU AMOR, COM TUA MÃO VEM ME GUIAR POIS JUNTO A TI SEMPRE QUERO ESTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('309', '309 - OH! POR QUE DUVIDAR?', 'OH! POR QUE DUVIDAR SOBRE AS ONDAS DO MAR\nQUANDO CRISTO CAMINHO ABRIU?\nQUANDO FORÇADO ÉS CONTRA AS ONDAS LUTAR,\nSEU AMOR A TI QUER REVELAR.\n\nCORO\nSOLTA O CABO DA NAU,\nTOMA O REMO NAS MÃOS\nE NAVEGA COM FÉ EM JESUS;\nE ENTÃO TU VERÁS QUE BONANÇA SE FAZ\nPOIS COM ELE SEGURO SERÁS.\n\nTREVAS VEM TE ASSUSTAR,\nTEMPESTADES NO MAR?\nDA MONTANHA O MESTRE TE VÊ,\nE NA TRIBULAÇÃO ELE VEM SOCORRER,\nSUA MÃO BEM TE PODE SUSTER.\n\nCORO\nSOLTA O CABO DA NAU,\nTOMA O REMO NAS MÃOS\nE NAVEGA COM FÉ EM JESUS;\nE ENTÃO TU VERÁS QUE BONANÇA SE FAZ\nPOIS COM ELE SEGURO SERÁS.\n\nPODES TU RECORDAR, MARAVILHAS SEM PAR?\nNO DESERTO AO POVO FARTOU;\nE O MESMO PODER ELE SEMPRE TERÁ\nPOIS NÃO MUDA E NÃO FALHARÁ.\n\nCORO\nSOLTA O CABO DA NAU,\nTOMA O REMO NAS MÃOS\nE NAVEGA COM FÉ EM JESUS;\nE ENTÃO TU VERÁS QUE BONANÇA SE FAZ\nPOIS COM ELE SEGURO SERÁS.\n\nQUANDO PEDES MAIS FÉ, ELE OUVE, \nOH! CRÊ! MESMO SENDO EM TRIBULAÇÃO;\nQUANDO A MÃO DE PODER \nO TEU EGO TIRAR SOBRE AS ONDAS \nPODERÁS ANDAR.\n\nCORO\nSOLTA O CABO DA NAU,\nTOMA O REMO NAS MÃOS\nE NAVEGA COM FÉ EM JESUS;\nE ENTÃO TU VERÁS QUE BONANÇA SE FAZ\nPOIS COM ELE SEGURO SERÁS.', 'OH! POR QUE DUVIDAR SOBRE AS ONDAS DO MAR QUANDO CRISTO CAMINHO ABRIU? QUANDO FORÇADO ÉS CONTRA AS ONDAS LUTAR, SEU AMOR A TI QUER REVELAR. CORO SOLTA O CABO DA NAU, TOMA O REMO NAS MÃOS E NAVEGA COM FÉ EM JESUS; E ENTÃO TU VERÁS QUE BONANÇA SE FAZ POIS COM ELE SEGURO SERÁS. TREVAS VEM TE ASSUSTAR, TEMPESTADES NO MAR? DA MONTANHA O MESTRE TE VÊ, E NA TRIBULAÇÃO ELE VEM SOCORRER, SUA MÃO BEM TE PODE SUSTER. PODES TU RECORDAR, MARAVILHAS SEM PAR? NO DESERTO AO POVO FARTOU; E O MESMO PODER ELE SEMPRE TERÁ POIS NÃO MUDA E NÃO FALHARÁ. QUANDO PEDES MAIS FÉ, ELE OUVE, OH! CRÊ! MESMO SENDO EM TRIBULAÇÃO; QUANDO A MÃO DE PODER O TEU EGO TIRAR SOBRE AS ONDAS PODERÁS ANDAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('310', '310 - MESTRE O MAR SE REVOLTA', 'MESTRE, O MAR SE REVOLTA,\nAS ONDAS NOS DÃO PAVOR!\nO CÉU SE REVESTE DE TREVAS,\nNÃO TEMOS UM SALVADOR!\nNÃO SE TE DÁ QUE MORRAMOS?\nPODES ASSIM DORMIR?\nSE A CADA MOMENTO NOS VEMOS\nJÁ PRESTES A SUBMERGIR?\n\nCORO\n“AS ONDAS ATENDEM AO MEU MANDAR: SOSSEGAI!”\nSEJA O ENCAPELADO MAR, A IRA DOS HOMENS, \nO GÊNIO DO MAL, TAIS ÁGUAS NÃO PODEM \nA NAU TRAGAR QUE LEVA O MESTRE DO CÉU E MAR!\n“POIS TODOS OUVEM O MEU MANDAR:\nSOSSEGAI! SOSSEGAI!\nCONVOSCO ESTOU \nPARA VOS SALVAR: SOSSEGAI!”\n\nMESTRE, TÃO GRANDE TRISTEZA\nME QUER HOJE CONSUMIR!\nE A DOR QUE PERTURBA MINH’ALMA\nTE IMPLORA: VEM ME ACUDIR!\nDE ONDAS DO MAL QUE ME ENCOBREM\nQUEM ME FARÁ SAIR?\nPEREÇO, PEREÇO, Ó MESTRE,\nTE ROGO, VEM ME ACUDIR!\n\nCORO\n“AS ONDAS ATENDEM AO MEU MANDAR: SOSSEGAI!”\nSEJA O ENCAPELADO MAR, A IRA DOS HOMENS, \nO GÊNIO DO MAL, TAIS ÁGUAS NÃO PODEM \nA NAU TRAGAR QUE LEVA O MESTRE DO CÉU E MAR!\n“POIS TODOS OUVEM O MEU MANDAR:\nSOSSEGAI! SOSSEGAI!\nCONVOSCO ESTOU \nPARA VOS SALVAR: SOSSEGAI!”\n\nMESTRE, CHEGOU A BONANÇA,\nEM PAZ VEJO O CÉU E O MAR,\nO MEU CORAÇÃO GOZA CALMA\nQUE NÃO PODERÁ FINDAR;\nFICA COMIGO, Ó MESTRE, \nDONO DA TERRA E CÉU,\nE ASSIM CHEGAREI BEM SEGURO,\nAO PORTO, DESTINO MEU.\n\nCORO\n“AS ONDAS ATENDEM AO MEU MANDAR: SOSSEGAI!”\nSEJA O ENCAPELADO MAR, A IRA DOS HOMENS, \nO GÊNIO DO MAL, TAIS ÁGUAS NÃO PODEM \nA NAU TRAGAR QUE LEVA O MESTRE DO CÉU E MAR!\n“POIS TODOS OUVEM O MEU MANDAR:\nSOSSEGAI! SOSSEGAI!\nCONVOSCO ESTOU \nPARA VOS SALVAR: SOSSEGAI!”', 'MESTRE, O MAR SE REVOLTA, AS ONDAS NOS DÃO PAVOR! O CÉU SE REVESTE DE TREVAS, NÃO TEMOS UM SALVADOR! NÃO SE TE DÁ QUE MORRAMOS? PODES ASSIM DORMIR? SE A CADA MOMENTO NOS VEMOS JÁ PRESTES A SUBMERGIR? CORO AS ONDAS ATENDEM AO MEU MANDAR: SOSSEGAI! SEJA O ENCAPELADO MAR, A IRA DOS HOMENS, O GÊNIO DO MAL, TAIS ÁGUAS NÃO PODEM A NAU TRAGAR QUE LEVA O MESTRE DO CÉU E MAR! POIS TODOS OUVEM O MEU MANDAR: SOSSEGAI! SOSSEGAI! CONVOSCO ESTOU PARA VOS SALVAR: SOSSEGAI! MESTRE, TÃO GRANDE TRISTEZA ME QUER HOJE CONSUMIR! E A DOR QUE PERTURBA MINH’ALMA TE IMPLORA: VEM ME ACUDIR! DE ONDAS DO MAL QUE ME ENCOBREM QUEM ME FARÁ SAIR? PEREÇO, PEREÇO, Ó MESTRE, TE ROGO, VEM ME ACUDIR! MESTRE, CHEGOU A BONANÇA, EM PAZ VEJO O CÉU E O MAR, O MEU CORAÇÃO GOZA CALMA QUE NÃO PODERÁ FINDAR; FICA COMIGO, Ó MESTRE, DONO DA TERRA E CÉU, E ASSIM CHEGAREI BEM SEGURO, AO PORTO, DESTINO MEU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('311', '311 - SÓ UMA VOZ ME BASTA', 'SÓ UMA VOZ ME BASTA\nNA HORA DA TRISTEZA E DOR;\nSÓ UMA VOZ ME CONSOLA,\nÉ A VOZ DO MEU SENHOR.\n\nELE TIRA A ESCURIDÃO,\nELE FAZ NASCER A LUZ, \nELE DÁ A SOLUÇÃO\nREVELADA EM JESUS.\n\nSÓ UMA VOZ ME BASTA\nNA HORA DA TRISTEZA E DOR;\nSÓ UMA VOZ ME CONSOLA,\nÉ A VOZ DO MEU SENHOR.\n\nELE TIRA A ESCURIDÃO, \nELE FAZ NASCER A LUZ,\nELE DÁ A SOLUÇÃO\nREVELADA EM JESUS.\nBIS', 'SÓ UMA VOZ ME BASTA NA HORA DA TRISTEZA E DOR; SÓ UMA VOZ ME CONSOLA, É A VOZ DO MEU SENHOR. ELE TIRA A ESCURIDÃO, ELE FAZ NASCER A LUZ, ELE DÁ A SOLUÇÃO REVELADA EM JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('312', '312 - SE, DA VIDA, AS VAGAS PROCELOSAS SÃO', 'SE, DA VIDA, AS VAGAS PROCELOSAS SÃO,\nSE, COM DESALENTO, JULGAS TUDO VÃO,\nCONTA AS MUITAS BÊNÇÃOS, DIZE-AS DE UMA VEZ,\nVERÁS, COM SURPRESA, QUANTO DEUS JÁ FEZ.\n\nCORO\nCONTA AS BÊNÇÃOS, CONTA QUANTAS \nSÃO RECEBIDAS DA DIVINA MÃO;\nVEM DIZÊ-LAS, TODAS DE UMA VEZ,\nE VERÁS SURPRESO QUANTO DEUS JÁ FEZ.\n\nTENS ACASO MÁGOA? TRISTE É O TEU LIDAR?\nÉ A CRUZ PESADA QUE TENS DE LEVAR?\nCONTA AS MUITAS BÊNÇÃOS, NÃO DUVIDARÁS,\nE EM CANTO ALEGRE OS DIAS PASSARÁS.\n\nCORO\nCONTA AS BÊNÇÃOS, CONTA QUANTAS \nSÃO RECEBIDAS DA DIVINA MÃO;\nVEM DIZÊ-LAS, TODAS DE UMA VEZ,\nE VERÁS SURPRESO QUANTO DEUS JÁ FEZ.\n\nQUANDO VIRES OUTROS COM SEU OURO E BENS, \nLEMBRA QUE TESOUROS PROMETIDOS TENS;\nNUNCA OS BENS DA TERRA PODERÃO COMPRAR\nA MANSÃO CELESTE QUE VAIS HABITAR.\n\nCORO\nCONTA AS BÊNÇÃOS, CONTA QUANTAS \nSÃO RECEBIDAS DA DIVINA MÃO;\nVEM DIZÊ-LAS, TODAS DE UMA VEZ,\nE VERÁS SURPRESO QUANTO DEUS JÁ FEZ.', 'SE, DA VIDA, AS VAGAS PROCELOSAS SÃO, SE, COM DESALENTO, JULGAS TUDO VÃO, CONTA AS MUITAS BÊNÇÃOS, DIZE-AS DE UMA VEZ, VERÁS, COM SURPRESA, QUANTO DEUS JÁ FEZ. CORO CONTA AS BÊNÇÃOS, CONTA QUANTAS SÃO RECEBIDAS DA DIVINA MÃO; VEM DIZÊ-LAS, TODAS DE UMA VEZ, E VERÁS SURPRESO QUANTO DEUS JÁ FEZ. TENS ACASO MÁGOA? TRISTE É O TEU LIDAR? É A CRUZ PESADA QUE TENS DE LEVAR? CONTA AS MUITAS BÊNÇÃOS, NÃO DUVIDARÁS, E EM CANTO ALEGRE OS DIAS PASSARÁS. QUANDO VIRES OUTROS COM SEU OURO E BENS, LEMBRA QUE TESOUROS PROMETIDOS TENS; NUNCA OS BENS DA TERRA PODERÃO COMPRAR A MANSÃO CELESTE QUE VAIS HABITAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('313', '313 - O QUE HABITA NO ABRIGO DE DEUS', 'O QUE HABITA NO ABRIGO DE DEUS \nMORARÁ SOB SOMBRAS DE AMOR,\nSOBRE ELE NÃO VIRÁ NENHUM MAL, \nEM SUAS ASAS FELIZ VIVERÁ.\n\nOH! EU QUERO HABITAR NO ABRIGO DE DEUS, \nSÓ ALI ACHAREI PAZ E PROFUNDO AMOR;\nMEU DESEJO É COM ELE COMUNHÃO \nDESFRUTAR E PRA SEMPRE O SEU NOME LOUVAR.', 'O QUE HABITA NO ABRIGO DE DEUS MORARÁ SOB SOMBRAS DE AMOR, SOBRE ELE NÃO VIRÁ NENHUM MAL, EM SUAS ASAS FELIZ VIVERÁ. OH! EU QUERO HABITAR NO ABRIGO DE DEUS, SÓ ALI ACHAREI PAZ E PROFUNDO AMOR; MEU DESEJO É COM ELE COMUNHÃO DESFRUTAR E PRA SEMPRE O SEU NOME LOUVAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('314', '314 - QUE IMPORTA', 'QUE IMPORTA SE O SOL NÃO BRILHAR,\nQUE IMPORTA SE DAS ESTRELAS O BRILHO CESSAR;\nO QUE IMPORTA PARA MIM, É DE JESUS NÃO ESQUECER;\nELE É A RAZÃO DE TODO O MEU VIVER.\n\nÉ JESUS MINHA PAZ, É JESUS MINHA FORÇA,\nÉ JESUS MEU AMIGO, É JESUS A MINHA ALEGRIA,\nÉ JESUS MEU COMPANHEIRO,\nÉ JESUS MEU AMOR VERDADEIRO,\nELE É A RAZÃO DE TODO O MEU VIVER.', 'QUE IMPORTA SE O SOL NÃO BRILHAR, QUE IMPORTA SE DAS ESTRELAS O BRILHO CESSAR; O QUE IMPORTA PARA MIM, É DE JESUS NÃO ESQUECER; ELE É A RAZÃO DE TODO O MEU VIVER. É JESUS MINHA PAZ, É JESUS MINHA FORÇA, É JESUS MEU AMIGO, É JESUS A MINHA ALEGRIA, É JESUS MEU COMPANHEIRO, É JESUS MEU AMOR VERDADEIRO, ELE É A RAZÃO DE TODO O MEU VIVER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('315', '315 - SE EM PROFUNDO ABISMO TE ENCONTRAS', 'SE EM PROFUNDO ABISMO TE ENCONTRAS \nE CAMINHAS SEM TERES DIREÇÃO,\nNA ESTRADA NÃO TENS O AUXÍLIO,\nNÃO ENCONTRAS QUEM TE DÊ A MÃO;\nSE A TRISTEZA SEMPRE TE DOMINA,\nSE O TEU FARDO É PESADO, ENTÃO,\nMESMO QUE TUDO VENHA A PERECER,\nNÃO, NÃO TEMAS, DEUS TE DÁ A MÃO.\n\nCORO\nDEUS SEGURA EM TUA MÃO (BIS)\nMESMO QUE, COM LUTAS, NA ESTRADA,\nDEUS SEGURA EM TUA MÃO.\n\nINSTRUMENTOS\nBIS\n\nSE O SOL PARA TI JÁ NÃO BRILHA\nE OS SEUS RAIOS NÃO REFLETEM MAIS;\nSE O AMOR EM TUA VIDA NÃO HÁ\nE CAMINHAS EM BUSCA DE PAZ;\nSE A TRISTEZA SEMPRE TE DOMINA,\nSE O TEU FARDO É PESADO, ENTÃO,\nMESMO QUE TUDO VENHA A PERECER,\nNÃO, NÃO TEMAS, DEUS TE DÁ A MÃO.\n\nCORO\nDEUS SEGURA EM TUA MÃO (BIS)\nMESMO QUE, COM LUTAS, NA ESTRADA,\nDEUS SEGURA EM TUA MÃO.\nBIS', 'SE EM PROFUNDO ABISMO TE ENCONTRAS E CAMINHAS SEM TERES DIREÇÃO, NA ESTRADA NÃO TENS O AUXÍLIO, NÃO ENCONTRAS QUEM TE DÊ A MÃO; SE A TRISTEZA SEMPRE TE DOMINA, SE O TEU FARDO É PESADO, ENTÃO, MESMO QUE TUDO VENHA A PERECER, NÃO, NÃO TEMAS, DEUS TE DÁ A MÃO. CORO DEUS SEGURA EM TUA MÃO  MESMO QUE, COM LUTAS, NA ESTRADA, DEUS SEGURA EM TUA MÃO. INSTRUMENTOS SE O SOL PARA TI JÁ NÃO BRILHA E OS SEUS RAIOS NÃO REFLETEM MAIS; SE O AMOR EM TUA VIDA NÃO HÁ E CAMINHAS EM BUSCA DE PAZ; SE A TRISTEZA SEMPRE TE DOMINA, SE O TEU FARDO É PESADO, ENTÃO, MESMO QUE TUDO VENHA A PERECER, NÃO, NÃO TEMAS, DEUS TE DÁ A MÃO. CORO DEUS SEGURA EM TUA MÃO  MESMO QUE, COM LUTAS, NA ESTRADA, DEUS SEGURA EM TUA MÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('316', '316 - A VIDA NO MUNDO É CRUEL', 'A VIDA NO MUNDO É CRUEL,\nTORMENTOS E LUTAS SEM FIM;\nCAMINHO ALMEJANDO O CÉU;\nJESUS, TU TE LEMBRAS DE MIM!\n\nCORO\nJESUS, TU TE LEMBRAS DE MIM! (BIS)\nNA VIDA, NA MORTE E NA DOR:\nJESUS, TU TE LEMBRAS DE MIM!\n\nNAVEGANDO VAI MINHA NAU\nNO MAR DESTA VIDA AQUI;\nABRIGA-ME NO TEMPORAL;\nJESUS, TU TE LEMBRAS DE MIM!\n\nCORO\nJESUS, TU TE LEMBRAS DE MIM! (BIS)\nNA VIDA, NA MORTE E NA DOR:\nJESUS, TU TE LEMBRAS DE MIM!\n\nJESUS, O QUE HEI DE FAZER\nSE O MUNDO ESTÁ CONTRA MIM?\nENVIA-ME O TEU PODER;\nJESUS, TU TE LEMBRAS DE MIM!', 'A VIDA NO MUNDO É CRUEL, TORMENTOS E LUTAS SEM FIM; CAMINHO ALMEJANDO O CÉU; JESUS, TU TE LEMBRAS DE MIM! CORO JESUS, TU TE LEMBRAS DE MIM!  NA VIDA, NA MORTE E NA DOR: JESUS, TU TE LEMBRAS DE MIM! NAVEGANDO VAI MINHA NAU NO MAR DESTA VIDA AQUI; ABRIGA-ME NO TEMPORAL; JESUS, TU TE LEMBRAS DE MIM! CORO JESUS, TU TE LEMBRAS DE MIM!  NA VIDA, NA MORTE E NA DOR: JESUS, TU TE LEMBRAS DE MIM! JESUS, O QUE HEI DE FAZER SE O MUNDO ESTÁ CONTRA MIM? ENVIA-ME O TEU PODER; JESUS, TU TE LEMBRAS DE MIM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('317', '317 - QUANDO VEM NOS OLHOS UMA LÁGRIMA', 'QUANDO VEM NOS OLHOS UMA LÁGRIMA,\nQUANDO NÃO CONSIGO MAIS SORRIR,\nDÁ-ME, MEU SENHOR, O TEU OLHAR,\nDÁ-ME, MEU SENHOR, O TEU PERDÃO.\n\nCORO (2X)\nMARANATA, ORA VEM, MEU SENHOR,\nMARANATA, VEM VENCER A MINHA DOR.\n\nSINTO ENTÃO A TUA MÃO ME TOCAR\nAPLACANDO O FUROR DA TRIBULAÇÃO,\nMAIS UM POUCO E NÃO MAIS CHORAREI,\nMAIS UM POUCO E NAS NUVENS TE VEREI.', 'QUANDO VEM NOS OLHOS UMA LÁGRIMA, QUANDO NÃO CONSIGO MAIS SORRIR, DÁ-ME, MEU SENHOR, O TEU OLHAR, DÁ-ME, MEU SENHOR, O TEU PERDÃO. MARANATA, ORA VEM, MEU SENHOR, MARANATA, VEM VENCER A MINHA DOR. SINTO ENTÃO A TUA MÃO ME TOCAR APLACANDO O FUROR DA TRIBULAÇÃO, MAIS UM POUCO E NÃO MAIS CHORAREI, MAIS UM POUCO E NAS NUVENS TE VEREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('318', '318 - QUANDO A ANGÚSTIA VEM', 'QUANDO A ANGÚSTIA VEM E NOS OLHOS LÁGRIMAS, PARECE, ENTÃO, QUE TU NÃO PODES SUPORTAR;\nJESUS TE AJUDARÁ E TUDO ELE VERÁ,\nAS LÁGRIMAS FALAM, DEUS OUVIRÁ.\n\nCORO\nDEUS VÊ NOS OLHOS O AFLITO CORAÇÃO,\nÉ O SENHOR QUEM VÊ AS LÁGRIMAS EM ORAÇÃO; \nCOM O HOMEM ELE VIVERÁ,\nPELAS MÃOS O TOMARÁ.\nAS LÁGRIMAS FALAM, DEUS OUVIRÁ.\n\nQUANDO NÃO PUDERES MAIS SONHO ALGUM \nREALIZAR, E TUDO, ENFIM, DIANTE DE TI \nDESMORONAR, MEU DEUS NÃO TE ESQUECERÁ, \nPROMESSAS CUMPRIRÁ, \nAS LÁGRIMAS FALAM, DEUS OUVIRÁ.\n\nCORO\nDEUS VÊ NOS OLHOS O AFLITO CORAÇÃO,\nÉ O SENHOR QUEM VÊ AS LÁGRIMAS EM ORAÇÃO; \nCOM O HOMEM ELE VIVERÁ,\nPELAS MÃOS O TOMARÁ.\nAS LÁGRIMAS FALAM, DEUS OUVIRÁ.', 'QUANDO A ANGÚSTIA VEM E NOS OLHOS LÁGRIMAS, PARECE, ENTÃO, QUE TU NÃO PODES SUPORTAR; JESUS TE AJUDARÁ E TUDO ELE VERÁ, AS LÁGRIMAS FALAM, DEUS OUVIRÁ. CORO DEUS VÊ NOS OLHOS O AFLITO CORAÇÃO, É O SENHOR QUEM VÊ AS LÁGRIMAS EM ORAÇÃO; COM O HOMEM ELE VIVERÁ, PELAS MÃOS O TOMARÁ. AS LÁGRIMAS FALAM, DEUS OUVIRÁ. QUANDO NÃO PUDERES MAIS SONHO ALGUM REALIZAR, E TUDO, ENFIM, DIANTE DE TI DESMORONAR, MEU DEUS NÃO TE ESQUECERÁ, PROMESSAS CUMPRIRÁ, AS LÁGRIMAS FALAM, DEUS OUVIRÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('319', '319 - MAIS PERTO QUERO ESTAR', 'MAIS PERTO QUERO ESTAR, MEU DEUS DE TI,\nINDA QUE SEJA A DOR QUE ME UNA A TI!\nSEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, \nMAIS PERTO QUERO ESTAR, MEU DEUS, DE TI!\n\nANDANDO TRISTE AQUI, NA SOLIDÃO,\nPAZ E DESCANSO A MIM TEUS BRAÇOS DÃO.\nSEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, \nMAIS PERTO QUERO ESTAR, MEU DEUS, DE TI!\n\nMINH’ALMA CANTARÁ A TI, SENHOR,\nCHEIA DE GRATIDÃO POR TEU AMOR.\nSEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, \nMAIS PERTO QUERO ESTAR, MEU DEUS, DE TI!\n\nE QUANDO CRISTO, ENFIM, ME VIER CHAMAR,\nNOS CÉUS COM O SENHOR IREI MORAR.\nENTÃO ME ALEGRAREI, PERTO DE TI, MEU REI,\nPERTO DE TI, MEU REI, MEU DEUS, DE TI!', 'MAIS PERTO QUERO ESTAR, MEU DEUS DE TI, INDA QUE SEJA A DOR QUE ME UNA A TI! SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI! ANDANDO TRISTE AQUI, NA SOLIDÃO, PAZ E DESCANSO A MIM TEUS BRAÇOS DÃO. SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI! MINH’ALMA CANTARÁ A TI, SENHOR, CHEIA DE GRATIDÃO POR TEU AMOR. SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI! E QUANDO CRISTO, ENFIM, ME VIER CHAMAR, NOS CÉUS COM O SENHOR IREI MORAR. ENTÃO ME ALEGRAREI, PERTO DE TI, MEU REI, PERTO DE TI, MEU REI, MEU DEUS, DE TI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('320', '320 - BOM É O SENHOR', 'BOM É O SENHOR PARA AQUELE QUE O BUSCA \nE QUE ESPERA EM SILÊNCIO A SUA SALVAÇÃO.\n\nCORO \nPORQUE O SENHOR NÃO REJEITARÁ PRA SEMPRE,\nNEM TEM PRAZER NA NOSSA DOR;\nBOM É O SENHOR.\nBIS\n\nPOIS AINDA QUE ENTRISTEÇA\nUSARÁ DE COMPAIXÃO, \nSEGUNDO A SUA MISERICÓRDIA.\n\nCORO \nPORQUE O SENHOR NÃO REJEITARÁ PRA SEMPRE,\nNEM TEM PRAZER NA NOSSA DOR;\nBOM É O SENHOR (3X)\nBIS', 'BOM É O SENHOR PARA AQUELE QUE O BUSCA E QUE ESPERA EM SILÊNCIO A SUA SALVAÇÃO. CORO PORQUE O SENHOR NÃO REJEITARÁ PRA SEMPRE, NEM TEM PRAZER NA NOSSA DOR; BOM É O SENHOR. POIS AINDA QUE ENTRISTEÇA USARÁ DE COMPAIXÃO, SEGUNDO A SUA MISERICÓRDIA. CORO PORQUE O SENHOR NÃO REJEITARÁ PRA SEMPRE, NEM TEM PRAZER NA NOSSA DOR; BOM É O SENHOR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('321', '321 - QUÃO BONDOSO AMIGO É CRISTO', 'QUÃO BONDOSO AMIGO É CRISTO!\nCARREGOU A NOSSA DOR\nE NOS MANDA QUE LEVEMOS\nOS CUIDADOS AO SENHOR;\nFALTA AO CORAÇÃO DORIDO\nGOZO, PAZ, CONSOLAÇÃO?\nISSO É PORQUE NÃO LEVAMOS\nTUDO A DEUS EM ORAÇÃO.\n\nESTÁS FRACO E CARREGADO\nDE CUIDADOS E TEMOR?\nA JESUS, REFÚGIO ETERNO,\nVAI COM FÉ TEU MAL EXPOR.\nTEUS AMIGOS TE DESPREZAM?\nCONTA-LHE ISSO EM ORAÇÃO\nE COM SEU AMOR TÃO TERNO\nPAZ TERÁS NO CORAÇÃO.\n\nCRISTO É VERDADEIRO AMIGO,\nDISTO PROVA NOS MOSTROU\nQUANDO, PRA LEVAR CONSIGO,\nAO CULPADO, ENCARNOU;\nDERRAMOU SEU SANGUE PURO,\nNOSSA MANCHA PRA LAVAR,\nGOZO EM VIDA E NO FUTURO\nNELE PODEMOS ALCANÇAR.', 'QUÃO BONDOSO AMIGO É CRISTO! CARREGOU A NOSSA DOR E NOS MANDA QUE LEVEMOS OS CUIDADOS AO SENHOR; FALTA AO CORAÇÃO DORIDO GOZO, PAZ, CONSOLAÇÃO? ISSO É PORQUE NÃO LEVAMOS TUDO A DEUS EM ORAÇÃO. ESTÁS FRACO E CARREGADO DE CUIDADOS E TEMOR? A JESUS, REFÚGIO ETERNO, VAI COM FÉ TEU MAL EXPOR. TEUS AMIGOS TE DESPREZAM? CONTA-LHE ISSO EM ORAÇÃO E COM SEU AMOR TÃO TERNO PAZ TERÁS NO CORAÇÃO. CRISTO É VERDADEIRO AMIGO, DISTO PROVA NOS MOSTROU QUANDO, PRA LEVAR CONSIGO, AO CULPADO, ENCARNOU; DERRAMOU SEU SANGUE PURO, NOSSA MANCHA PRA LAVAR, GOZO EM VIDA E NO FUTURO NELE PODEMOS ALCANÇAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('322', '322 - HÁ UM AMIGO SEMPRE JUNTO', 'HÁ UM AMIGO SEMPRE JUNTO A MIM,\nHÁ UM AMIGO QUE ME OUVE,\nHÁ UM AMIGO TÃO QUERIDO, SIM,\nPRECIOSO ELE É.\n\nCORO\nQUANDO OUÇO A VOZ DO VENTO,\nQUANDO TODA A AFLIÇÃO ME SOBREVEM, ELE DIZ: \n“ESTOU CONTIGO”, PRECIOSO É JESUS.\n\nSUAS PROMESSAS ELE CUMPRE,\nNÃO HÁ COMO ELE FALHAR;\nMINHA PARTE QUERO EU FAZER,\nPRECIOSO ELE É.', 'HÁ UM AMIGO SEMPRE JUNTO A MIM, HÁ UM AMIGO QUE ME OUVE, HÁ UM AMIGO TÃO QUERIDO, SIM, PRECIOSO ELE É. CORO QUANDO OUÇO A VOZ DO VENTO, QUANDO TODA A AFLIÇÃO ME SOBREVEM, ELE DIZ: ESTOU CONTIGO, PRECIOSO É JESUS. SUAS PROMESSAS ELE CUMPRE, NÃO HÁ COMO ELE FALHAR; MINHA PARTE QUERO EU FAZER, PRECIOSO ELE É.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('323', '323 - DEUS ESTÁ PRESENTE  SEMPRE PRONTO A NOS AJUDAR', 'DEUS ESTÁ PRESENTE SEMPRE PRONTO A NOS AJUDAR, \nCOM SEUS OUVIDOSSEMPRE VOLTADOS A NOS ESCUTAR;\nBUSCA A JESUS PARA O TEU CORAÇÃO ALEGRAR, \nPARA ENCHER TEU CORAÇÃO DE GOZO E PAZ.\n\nCORO\nAGORA SOMOS POVO TRANSFORMADO\nPELA GRAÇA DE CRISTO JESUS,\nPELO SEU GRANDIOSO AMOR,\nSOMOS POVO DE CRISTO JESUS, O SENHOR. \n\nINSTRUMENTOS\n\nESTAMOS CONSCIENTES \nDE SUA SANTA PRESENÇA AQUI,\nCOM SEU PODER E COM GRAÇA ELE VAI OPERAR;\nNÃO FICARÁ QUALQUER LUTA \nOU TRISTEZA AQUI POIS O SENHOR \nNOS LEVARÁ A SENTIR O SEU AMOR.\n\nCORO\nAGORA SOMOS POVO TRANSFORMADO\nPELA GRAÇA DE CRISTO JESUS,\nPELO SEU GRANDIOSO AMOR,\nSOMOS POVO DE CRISTO JESUS, O SENHOR. \n\nFINAL: \nSOMOS POVO DE CRISTO JESUS, O SENHOR.', 'DEUS ESTÁ PRESENTE SEMPRE PRONTO A NOS AJUDAR, COM SEUS OUVIDOSSEMPRE VOLTADOS A NOS ESCUTAR; BUSCA A JESUS PARA O TEU CORAÇÃO ALEGRAR, PARA ENCHER TEU CORAÇÃO DE GOZO E PAZ. CORO AGORA SOMOS POVO TRANSFORMADO PELA GRAÇA DE CRISTO JESUS, PELO SEU GRANDIOSO AMOR, SOMOS POVO DE CRISTO JESUS, O SENHOR. INSTRUMENTOS ESTAMOS CONSCIENTES DE SUA SANTA PRESENÇA AQUI, COM SEU PODER E COM GRAÇA ELE VAI OPERAR; NÃO FICARÁ QUALQUER LUTA OU TRISTEZA AQUI POIS O SENHOR NOS LEVARÁ A SENTIR O SEU AMOR. CORO AGORA SOMOS POVO TRANSFORMADO PELA GRAÇA DE CRISTO JESUS, PELO SEU GRANDIOSO AMOR, SOMOS POVO DE CRISTO JESUS, O SENHOR.  SOMOS POVO DE CRISTO JESUS, O SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('324', '324 - EU SOU POBRE E NECESSITADO', 'EU SOU POBRE E NECESSITADO\nMAS O SENHOR CUIDA DE MIM;\nEU SOU POBRE E NECESSITADO\nMAS O SENHOR CUIDA DE MIM.\n\nTU ÉS MEU AUXÍLIO \nE O MEU LIBERTADOR!\nNÃO TE DETENHAS, Ó MEU DEUS! (BIS)', 'EU SOU POBRE E NECESSITADO MAS O SENHOR CUIDA DE MIM; EU SOU POBRE E NECESSITADO MAS O SENHOR CUIDA DE MIM. TU ÉS MEU AUXÍLIO E O MEU LIBERTADOR! NÃO TE DETENHAS, Ó MEU DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('325', '325 - SE HOJE ESCUTARES', 'SE HOJE ESCUTARES\nA VOZ MANSA E SUAVE DE DEUS,\nO TEU CORAÇÃO ABRE A ELE SEM HESITAÇÃO.\n\nCORO\nE DIZE: EIS-ME AQUI, SENHOR, COMO ESTOU, \nTÃO NECESSITADO DE TI;\nE PODES CONFIAR, DEUS VAI OPERAR\nMUITO ACIMA DO QUE PODES PENSAR.\n\nINSTRUMENTOS\n\nDEPENDE DE TI ELE AGORA \nEM TUA VIDA ENTRAR;\nESCUTA SUA VOZ E ABRE A PORTA HOJE, \nSEM HESITAR.\n\nCORO\nE DIZE: EIS-ME AQUI, SENHOR, COMO ESTOU, \nTÃO NECESSITADO DE TI;\nE PODES CONFIAR, DEUS VAI OPERAR\nMUITO ACIMA DO QUE PODES PENSAR', 'SE HOJE ESCUTARES A VOZ MANSA E SUAVE DE DEUS, O TEU CORAÇÃO ABRE A ELE SEM HESITAÇÃO. CORO E DIZE: EIS-ME AQUI, SENHOR, COMO ESTOU, TÃO NECESSITADO DE TI; E PODES CONFIAR, DEUS VAI OPERAR MUITO ACIMA DO QUE PODES PENSAR. INSTRUMENTOS DEPENDE DE TI ELE AGORA EM TUA VIDA ENTRAR; ESCUTA SUA VOZ E ABRE A PORTA HOJE, SEM HESITAR. CORO E DIZE: EIS-ME AQUI, SENHOR, COMO ESTOU, TÃO NECESSITADO DE TI; E PODES CONFIAR, DEUS VAI OPERAR MUITO ACIMA DO QUE PODES PENSAR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('326', '326 - EU NÃO SABERIA CAMINHAR', 'EU NÃO SABERIA CAMINHAR SEM TI, SENHOR,\nPORQUE TU ÉS O MEU AUXÍLIO E FORTALEZA;\nREFÚGIO NA TRIBULAÇÃO\nQUANDO OS MEUS JOELHOS\nDOBRAM EM ORAÇÃO.\nBIS\n\nSENHOR, TU ÉS O MEU AUXÍLIO NA TRIBULAÇÃO, \nAMIGO BEM PRESENTE QUE NÃO FALHA;\nTENHO DESEJADO A TUA \nSALVAÇÃO, Ó SENHOR,\nO TEU QUERER É TODO O \nMEU PRAZER.\n\nREFÚGIO NA TRIBULAÇÃO\nQUANDO OS MEUS JOELHOS\nDOBRAM EM ORAÇÃO.\nBIS\nBIS', 'EU NÃO SABERIA CAMINHAR SEM TI, SENHOR, PORQUE TU ÉS O MEU AUXÍLIO E FORTALEZA; REFÚGIO NA TRIBULAÇÃO QUANDO OS MEUS JOELHOS DOBRAM EM ORAÇÃO. SENHOR, TU ÉS O MEU AUXÍLIO NA TRIBULAÇÃO, AMIGO BEM PRESENTE QUE NÃO FALHA; TENHO DESEJADO A TUA SALVAÇÃO, Ó SENHOR, O TEU QUERER É TODO O MEU PRAZER. REFÚGIO NA TRIBULAÇÃO QUANDO OS MEUS JOELHOS DOBRAM EM ORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('327', '327 - DEUS É CONTIGO', 'DEUS É CONTIGO, É SÓ A ELE ORAR,\nDEUS É CONTIGO, DO MAL TE LIVRARÁ;\nMISERICÓRDIA A TI VIRÁ,\nDEUS É CONTIGO, SE A ELE ORAR.\n\nDEUS É CONTIGO, TUDO ELE VÊ DOS CÉUS,\nE TE PROTEGE DOS INIMIGOS TEUS;\nA CADA DIA RENOVA O AMOR,\nDEUS É CONTIGO, ELE É O SENHOR.\n\nDEUS É CONTIGO, TUDO ELE VÊ DOS CÉUS,\nE TE PROTEGE DOS INIMIGOS TEUS;\nA CADA DIA RENOVA O AMOR,\nDEUS É CONTIGO, ELE É O SENHOR.\n\nDEUS É CONTIGO, \nDEUS É CONTIGO ATÉ O FIM.', 'DEUS É CONTIGO, É SÓ A ELE ORAR, DEUS É CONTIGO, DO MAL TE LIVRARÁ; MISERICÓRDIA A TI VIRÁ, DEUS É CONTIGO, SE A ELE ORAR. DEUS É CONTIGO, TUDO ELE VÊ DOS CÉUS, E TE PROTEGE DOS INIMIGOS TEUS; A CADA DIA RENOVA O AMOR, DEUS É CONTIGO, ELE É O SENHOR. DEUS É CONTIGO, TUDO ELE VÊ DOS CÉUS, E TE PROTEGE DOS INIMIGOS TEUS; A CADA DIA RENOVA O AMOR, DEUS É CONTIGO, ELE É O SENHOR. DEUS É CONTIGO, DEUS É CONTIGO ATÉ O FIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('328', '328 - CONSOLAI, CONSOLAI O MEU POVO', 'CORO\n“CONSOLAI, CONSOLAI O MEU POVO,”\nDIZ O VOSSO DEUS;\n“PREPARAI, PREPARAI O CAMINHO DO SENHOR.”\n\nFALAI BENIGNAMENTE A JERUSALÉM:\nSUA INIQÜIDADE PERDOADA ESTÁ\nE JÁ RECEBEU EM DOBRO DO SENHOR\nPOR TODOS OS SEUS PECADOS.\n\nCORO\n“CONSOLAI, CONSOLAI O MEU POVO,”\nDIZ O VOSSO DEUS;\n“PREPARAI, PREPARAI O CAMINHO DO SENHOR.”\n\nENDIREITAI NO ERMO VEREDA \nAO NOSSO DEUS E A GLÓRIA \nDO SENHOR SE MANIFESTARÁ, \nLEVANTA A TUA VOZ, \nPROCLAMA FORTEMENTE: \nEIS AQUI ESTÁ O VOSSO DEUS.\n\nCORO\n“CONSOLAI, CONSOLAI O MEU POVO,”\nDIZ O VOSSO DEUS;\n“PREPARAI, PREPARAI O CAMINHO DO SENHOR.”', 'CORO CONSOLAI, CONSOLAI O MEU POVO, DIZ O VOSSO DEUS; PREPARAI, PREPARAI O CAMINHO DO SENHOR. FALAI BENIGNAMENTE A JERUSALÉM: SUA INIQÜIDADE PERDOADA ESTÁ E JÁ RECEBEU EM DOBRO DO SENHOR POR TODOS OS SEUS PECADOS. ENDIREITAI NO ERMO VEREDA AO NOSSO DEUS E A GLÓRIA DO SENHOR SE MANIFESTARÁ, LEVANTA A TUA VOZ, PROCLAMA FORTEMENTE: EIS AQUI ESTÁ O VOSSO DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('329', '329 - ATUA EM NOSSO MEIO', 'ATUA EM NOSSO MEIO, Ó SENHOR,\nMANIFESTA O TEU PODER CONSOLADOR, \nCONFORTA OS CORAÇÕES AFLITOS \nE NECESSITADOS DA TUA SALVAÇÃO.\nBIS\n\nQUEREMOS SEMPRE TE LOUVAR, \nÓ SENHOR, REI DOS EXÉRCITOS, \nMOSTRAR O TEU AMOR\nATRAVÉS DA NOSSA ADORAÇÃO.\n\nINSTRUMENTOS\nBIS\n\nATUA EM NOSSO MEIO, Ó SENHOR,\n(GLÓRIA AO SENHOR NAS ALTURAS) VARÕES\nMANIFESTA O TEU PODER CONSOLADOR,\n(GLÓRIA AO SENHOR) VARÕES\nGLÓRIA AO SENHOR NAS ALTURAS!', 'ATUA EM NOSSO MEIO, Ó SENHOR, MANIFESTA O TEU PODER CONSOLADOR, CONFORTA OS CORAÇÕES AFLITOS E NECESSITADOS DA TUA SALVAÇÃO. QUEREMOS SEMPRE TE LOUVAR, Ó SENHOR, REI DOS EXÉRCITOS, MOSTRAR O TEU AMOR ATRAVÉS DA NOSSA ADORAÇÃO. INSTRUMENTOS ATUA EM NOSSO MEIO, Ó SENHOR, (GLÓRIA AO SENHOR NAS ALTURAS) VARÕES MANIFESTA O TEU PODER CONSOLADOR, (GLÓRIA AO SENHOR) VARÕES GLÓRIA AO SENHOR NAS ALTURAS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('330', '330 - EIS AÍ O DIA ACEITÁVEL', 'EIS AÍ O DIA ACEITÁVEL DO SENHOR!\nELE VEIO CONSOLAR TODOS \nOS QUE CHORAM E OS CONTRITOS DE CORAÇÃO;\nELE VAI TE DAR VESTES DE LOUVOR\nAO INVÉS DE ESPÍRITO ANGUSTIADO,\nÓLEO DE ALEGRIA AO INVÉS DE PRANTO.\n\nCORO (2X)\nOUVI A VOZ DO SENHOR,\nEU VI OS ANJOS ANUNCIAREM,\nSENTI A SUA MÃO A ME TOCAR,\nSEU AMOR SOBRE O MEU SER.\n\nAINDA QUE OS TEUS PECADOS \nFOSSEM COMO CARMESIM,\nO SENHOR PODE OS TORNAR ALVOS COMO A BRANCA LÃ;\nE COMO O NOIVO QUE SE ALEGRA DA NOIVA, \nO SENHOR SE ALEGRARÁ DE TI,\nELE VAI TE DAR UMA COROA AO INVÉS DE PRANTO.\n\nCORO (2X)\nOUVI A VOZ DO SENHOR,\nEU VI OS ANJOS ANUNCIAREM,\nSENTI A SUA MÃO A ME TOCAR,\nSEU AMOR SOBRE O MEU SER.', 'EIS AÍ O DIA ACEITÁVEL DO SENHOR! ELE VEIO CONSOLAR TODOS OS QUE CHORAM E OS CONTRITOS DE CORAÇÃO; ELE VAI TE DAR VESTES DE LOUVOR AO INVÉS DE ESPÍRITO ANGUSTIADO, ÓLEO DE ALEGRIA AO INVÉS DE PRANTO. OUVI A VOZ DO SENHOR, EU VI OS ANJOS ANUNCIAREM, SENTI A SUA MÃO A ME TOCAR, SEU AMOR SOBRE O MEU SER. AINDA QUE OS TEUS PECADOS FOSSEM COMO CARMESIM, O SENHOR PODE OS TORNAR ALVOS COMO A BRANCA LÃ; E COMO O NOIVO QUE SE ALEGRA DA NOIVA, O SENHOR SE ALEGRARÁ DE TI, ELE VAI TE DAR UMA COROA AO INVÉS DE PRANTO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('331', '331 - SE DEUS É POR NÓS', 'SE DEUS É POR NÓS, QUEM SERÁ CONTRA NÓS?\nAQUELE QUE NEM MESMO O SEU FILHO POUPOU, \nANTES O ENTREGOU À CRUZ.\n\nSE O SENHOR CONOSCO ESTÁ,\nQUEM NOS CONDENARÁ?\nAQUELE QUE NOS ESCOLHEU\nÉ QUEM NOS JUSTIFICA.\n\nCORO\nJESUS RESSUSCITOU, RESSUSCITOU,\nVIVO ESTÁ AQUI ENTRE NÓS;\nQUEM IRÁ NOS SEPARAR DO AMOR DE DEUS?\nNEM A MORTE OU TRIBULAÇÃO,\nNEM ALTURA OU PROFUNDIDADE\nPODEM NOS SEPARAR DO AMOR DE DEUS,\nQUE ESTÁ EM CRISTO JESUS, CRISTO JESUS, \nNOSSO SENHOR, CRISTO JESUS.\n\nMAS EM TODAS ESTAS COISAS\nSOMOS MAIS DO QUE VENCEDORES\nPOR AQUELE QUE NOS AMOU\nE A JESUS NOS REVELOU.\n\nCORO\nJESUS RESSUSCITOU, RESSUSCITOU,\nVIVO ESTÁ AQUI ENTRE NÓS;\nQUEM IRÁ NOS SEPARAR DO AMOR DE DEUS?\nNEM A MORTE OU TRIBULAÇÃO,\nNEM ALTURA OU PROFUNDIDADE\nPODEM NOS SEPARAR DO AMOR DE DEUS,\nQUE ESTÁ EM CRISTO JESUS, CRISTO JESUS, \nNOSSO SENHOR, CRISTO JESUS.\n\nFINAL: \nNOSSO SALVADOR, \nCRISTO JESUS,\nNOSSO SALVADOR.', 'SE DEUS É POR NÓS, QUEM SERÁ CONTRA NÓS? AQUELE QUE NEM MESMO O SEU FILHO POUPOU, ANTES O ENTREGOU À CRUZ. SE O SENHOR CONOSCO ESTÁ, QUEM NOS CONDENARÁ? AQUELE QUE NOS ESCOLHEU É QUEM NOS JUSTIFICA. CORO JESUS RESSUSCITOU, RESSUSCITOU, VIVO ESTÁ AQUI ENTRE NÓS; QUEM IRÁ NOS SEPARAR DO AMOR DE DEUS? NEM A MORTE OU TRIBULAÇÃO, NEM ALTURA OU PROFUNDIDADE PODEM NOS SEPARAR DO AMOR DE DEUS, QUE ESTÁ EM CRISTO JESUS, CRISTO JESUS, NOSSO SENHOR, CRISTO JESUS. MAS EM TODAS ESTAS COISAS SOMOS MAIS DO QUE VENCEDORES POR AQUELE QUE NOS AMOU E A JESUS NOS REVELOU. NOSSO SALVADOR, CRISTO JESUS, NOSSO SALVADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('332', '332 - O REI E EU', 'O REI E EU TRILHAMOS NA ESTRADA \nDA VIDA ONDE MUITAS ALMAS PASSANDO VÃO;\nO GRANDE SER E EU, UM POBRE MENDIGO, \nANDAMOS JUNTOS, O REI E EU.\n\nCORO\nPORQUE ELE CUIDA DE MIM É UM MISTÉRIO,\nELE TEM O MUNDO EM SUAS MÃOS,\nMAS QUEM SOU EU?\nASSIM MINH’ALMA MUITO SE MARAVILHA\nDE ESTARMOS JUNTOS, O REI E EU.\n\nO REI E EU, ERA MUITO ESTRANHO,\nEU ANDAVA SÓ SEM SABER POR QUE;\nATÉ QUE UM DIA SUAS MÃOS ME ABRAÇARAM,\nNÃO MAIS ESTRANHOS, JESUS E EU.\n\nCORO\nPORQUE ELE CUIDA DE MIM É UM MISTÉRIO,\nELE TEM O MUNDO EM SUAS MÃOS,\nMAS QUEM SOU EU?\nASSIM MINH’ALMA MUITO SE MARAVILHA\nDE ESTARMOS JUNTOS, O REI E EU.', 'O REI E EU TRILHAMOS NA ESTRADA DA VIDA ONDE MUITAS ALMAS PASSANDO VÃO; O GRANDE SER E EU, UM POBRE MENDIGO, ANDAMOS JUNTOS, O REI E EU. CORO PORQUE ELE CUIDA DE MIM É UM MISTÉRIO, ELE TEM O MUNDO EM SUAS MÃOS, MAS QUEM SOU EU? ASSIM MINH’ALMA MUITO SE MARAVILHA DE ESTARMOS JUNTOS, O REI E EU. O REI E EU, ERA MUITO ESTRANHO, EU ANDAVA SÓ SEM SABER POR QUE; ATÉ QUE UM DIA SUAS MÃOS ME ABRAÇARAM, NÃO MAIS ESTRANHOS, JESUS E EU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('333', '333 - SE TE ENCONTRARES TRISTE', 'SE TE ENCONTRARES TRISTE E CANSADO\nE SE VACILARES NO TEU CAMINHAR, \nNÃO TE DETENHAS, POIS NOS TEUS PASSOS, \nEM TEUS CAMINHOS, DEUS PROVERÁ;\nNÃO DESANIMES, À TUA VOLTA ELE ESTARÁ, \nDEUS PROVERÁ.\n\nCORO\nCONFIA E ANDA, \nSEMPRE ORANDO,\nSEMPRE CANTANDO, \nDEUS PROVERÁ.\nBIS\n\nNÃO TE DETENHAS, (DEUS PROVERÁ) VARÕES\nPOIS NOS TEUS PASSOS, (DEUS PROVERÁ) VARÕES\nEM TEUS CAMINHOS, DEUS PROVERÁ;\nNÃO DESANIMES, (DEUS PROVERÁ) VARÕES\nÀ TUA VOLTA (DEUS PROVERÁ) VARÕES\nELE ESTARÁ, DEUS PROVERÁ.\n\nINSTRUMENTOS\n\nCORO\nCONFIA E ANDA, \nSEMPRE ORANDO,\nSEMPRE CANTANDO, \nDEUS PROVERÁ.\nBIS', 'SE TE ENCONTRARES TRISTE E CANSADO E SE VACILARES NO TEU CAMINHAR, NÃO TE DETENHAS, POIS NOS TEUS PASSOS, EM TEUS CAMINHOS, DEUS PROVERÁ; NÃO DESANIMES, À TUA VOLTA ELE ESTARÁ, DEUS PROVERÁ. CORO CONFIA E ANDA, SEMPRE ORANDO, SEMPRE CANTANDO, DEUS PROVERÁ. NÃO TE DETENHAS, (DEUS PROVERÁ) VARÕES POIS NOS TEUS PASSOS, (DEUS PROVERÁ) VARÕES EM TEUS CAMINHOS, DEUS PROVERÁ; NÃO DESANIMES, (DEUS PROVERÁ) VARÕES À TUA VOLTA (DEUS PROVERÁ) VARÕES ELE ESTARÁ, DEUS PROVERÁ. INSTRUMENTOS', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('334', '334 - DEUS CUIDA DE TI', 'DEUS CUIDA DE TI, (BIS)\nQUANDO TUDO PARECE FALHAR,\nOUVE A VOZ DO SENHOR TE FALAR:\n“NÃO FALHAREI, POIS EU TE AMEI,\nE SEMPRE CUIDO DE TI,\nCUIDO DE TI”.\nBIS', 'DEUS CUIDA DE TI,  QUANDO TUDO PARECE FALHAR, OUVE A VOZ DO SENHOR TE FALAR: NÃO FALHAREI, POIS EU TE AMEI, E SEMPRE CUIDO DE TI, CUIDO DE TI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('335', '335 - NÃO DESANIMES! DEUS PROVERÁ', 'NÃO DESANIMES! DEUS PROVERÁ,\nDEUS VELARÁ POR TI;\nSOB SUAS ASAS TE ACOLHERÁ,\nDEUS VELARÁ POR TI.\n\nCORO\nDEUS CUIDARÁ DE TI,\nNA TUA DOR, COM TODO AMOR;\nJAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI.\n\nSE NO TEU PEITO VIBRAR A DOR,\nDEUS VELARÁ POR TI;\nTU JÁ PROVASTE SEU GRANDE AMOR,\nDEUS VELARÁ POR TI.\n\nCORO\nDEUS CUIDARÁ DE TI,\nNA TUA DOR, COM TODO AMOR;\nJAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI.\n\nNOS DESALENTOS, NAS PROVAÇÕES,\nDEUS VELARÁ POR TI;\nNAS DESVENTURAS, NAS TENTAÇÕES,\nDEUS VELARÁ POR TI.\n\nCORO\nDEUS CUIDARÁ DE TI,\nNA TUA DOR, COM TODO AMOR;\nJAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI.\n\nCOMO ESTIVERES, NÃO TEMAS, VEM!\nDEUS VELARÁ POR TI;\nTEU PAI BONDOSO TE ESPERA ALÉM,\nDEUS VELARÁ POR TI.\n\nCORO\nDEUS CUIDARÁ DE TI,\nNA TUA DOR, COM TODO AMOR;\nJAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI.', 'NÃO DESANIMES! DEUS PROVERÁ, DEUS VELARÁ POR TI; SOB SUAS ASAS TE ACOLHERÁ, DEUS VELARÁ POR TI. CORO DEUS CUIDARÁ DE TI, NA TUA DOR, COM TODO AMOR; JAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI. SE NO TEU PEITO VIBRAR A DOR, DEUS VELARÁ POR TI; TU JÁ PROVASTE SEU GRANDE AMOR, DEUS VELARÁ POR TI. NOS DESALENTOS, NAS PROVAÇÕES, DEUS VELARÁ POR TI; NAS DESVENTURAS, NAS TENTAÇÕES, DEUS VELARÁ POR TI. COMO ESTIVERES, NÃO TEMAS, VEM! DEUS VELARÁ POR TI; TEU PAI BONDOSO TE ESPERA ALÉM, DEUS VELARÁ POR TI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('336', '336 - HÁ ALGUÉM QUE CUIDA DE TI', 'CORO\nHÁ ALGUÉM QUE CUIDA DE TI, (BIS)\nE QUANDO SENTES SOLIDÃO\nE UM VAZIO NO CORAÇÃO,\nLEMBRA QUE ALGUÉM CUIDA DE TI.\n\nSE AS NUVENS TE ENCOBREM E NÃO QUEREM PASSAR, \nSE O TEU BARCO SE AGITA COM AS ONDAS DO MAR,\nNÃO TE ESQUEÇAS:\nALGUÉM CUIDA DE TI SEM CESSAR,\nCOM SEU AMOR ELE VEM TE AJUDAR.\n\nCORO\nHÁ ALGUÉM QUE CUIDA DE TI, (BIS)\nE QUANDO SENTES SOLIDÃO\nE UM VAZIO NO CORAÇÃO,\nLEMBRA QUE ALGUÉM CUIDA DE TI.\n\nQUANDO LEVAS TUDO A ELE EM ORAÇÃO\nE COM LÁGRIMAS SINCERAS NO CORAÇÃO,\nCRISTO VÊ E SE ALEGRA DE TE AJUDAR\nE SUA PAZ ELE VEM DERRAMAR.\n\nCORO\nHÁ ALGUÉM QUE CUIDA DE TI, (BIS)\nE QUANDO SENTES SOLIDÃO\nE UM VAZIO NO CORAÇÃO,\nLEMBRA QUE ALGUÉM CUIDA DE TI.', 'CORO HÁ ALGUÉM QUE CUIDA DE TI,  E QUANDO SENTES SOLIDÃO E UM VAZIO NO CORAÇÃO, LEMBRA QUE ALGUÉM CUIDA DE TI. SE AS NUVENS TE ENCOBREM E NÃO QUEREM PASSAR, SE O TEU BARCO SE AGITA COM AS ONDAS DO MAR, NÃO TE ESQUEÇAS: ALGUÉM CUIDA DE TI SEM CESSAR, COM SEU AMOR ELE VEM TE AJUDAR. QUANDO LEVAS TUDO A ELE EM ORAÇÃO E COM LÁGRIMAS SINCERAS NO CORAÇÃO, CRISTO VÊ E SE ALEGRA DE TE AJUDAR E SUA PAZ ELE VEM DERRAMAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('337', '337 - O MESMO DEUS', 'O MESMO DEUS QUE ONTEM DE MIM CUIDOU, \nVIVO ESTÁ HOJE EM MEU VIVER, MISERICÓRDIAS ME SEGUIRÃO CADA DIA DO MEU VIVER;\nNADA TENHO QUE TEMER POIS JESUS ME GUARDARÁ, \nNADA TENHO QUE TEMER POIS JESUS ME GUIARÁ;\nE ME LEVARÁ À FONTE DA VIDA \nE NO CÉU ME RECEBERÁ.\n\nO MESMO DEUS QUE ONTEM DE MIM CUIDOU, \nVIVO ESTÁ HOJE EM MEU VIVER, MISERICÓRDIAS ME SEGUIRÃO CADA DIA DO MEU VIVER;\nNADA TENHO QUE TEMER POIS JESUS ME GUARDARÁ, \nNADA TENHO QUE TEMER POIS JESUS ME GUIARÁ;\nE ME LEVARÁ À FONTE DA VIDA \nE NO CÉU ME RECEBERÁ (3X NO FINAL)', 'O MESMO DEUS QUE ONTEM DE MIM CUIDOU, VIVO ESTÁ HOJE EM MEU VIVER, MISERICÓRDIAS ME SEGUIRÃO CADA DIA DO MEU VIVER; NADA TENHO QUE TEMER POIS JESUS ME GUARDARÁ, NADA TENHO QUE TEMER POIS JESUS ME GUIARÁ; E ME LEVARÁ À FONTE DA VIDA E NO CÉU ME RECEBERÁ. O MESMO DEUS QUE ONTEM DE MIM CUIDOU, VIVO ESTÁ HOJE EM MEU VIVER, MISERICÓRDIAS ME SEGUIRÃO CADA DIA DO MEU VIVER; NADA TENHO QUE TEMER POIS JESUS ME GUARDARÁ, NADA TENHO QUE TEMER POIS JESUS ME GUIARÁ; E ME LEVARÁ À FONTE DA VIDA E NO CÉU ME RECEBERÁ ( NO FINAL)', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('338', '338 - DIVINO COMPANHEIRO', 'DIVINO COMPANHEIRO NO CAMINHO,\nTUA PRESENÇA SINTO LOGO AO TRANSITAR,\nEIS QUE DISSIPASTE TODA SOMBRA,\nJÁ TENHO LUZ, A LUZ BENDITA DO AMOR.\n\nCORO\nFICA, SENHOR, JÁ SE FAZ TARDE,\nTENS MEU CORAÇÃO PARA POUSAR;\nFAZE EM MIM MORADA PERMANENTE,\nFICA, SENHOR, FICA, SENHOR, MEU SALVADOR.\n\nA SOMBRA DA NOITE SE APROXIMA,\nE NELA O TENTADOR PODE ATACAR;\nNÃO, NÃO ME DEIXES SÓ, NO CAMINHO,\nAJUDA-ME, AJUDA-ME AO CÉU CHEGAR.\n\nCORO\nFICA, SENHOR, JÁ SE FAZ TARDE,\nTENS MEU CORAÇÃO PARA POUSAR;\nFAZE EM MIM MORADA PERMANENTE,\nFICA, SENHOR, FICA, SENHOR, MEU SALVADOR.', 'DIVINO COMPANHEIRO NO CAMINHO, TUA PRESENÇA SINTO LOGO AO TRANSITAR, EIS QUE DISSIPASTE TODA SOMBRA, JÁ TENHO LUZ, A LUZ BENDITA DO AMOR. CORO FICA, SENHOR, JÁ SE FAZ TARDE, TENS MEU CORAÇÃO PARA POUSAR; FAZE EM MIM MORADA PERMANENTE, FICA, SENHOR, FICA, SENHOR, MEU SALVADOR. A SOMBRA DA NOITE SE APROXIMA, E NELA O TENTADOR PODE ATACAR; NÃO, NÃO ME DEIXES SÓ, NO CAMINHO, AJUDA-ME, AJUDA-ME AO CÉU CHEGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('339', '339 - PARA OS MONTES OLHAREI', 'PARA OS MONTES OLHAREI,\nDE ONDE VEM A SALVAÇÃO?\nMEU SOCORRO VEM DE DEUS,\nO SENHOR DA CRIAÇÃO.\n\nCORO\nO SENHOR É QUEM TE GUARDA,\nGUARDARÁ DE TODO MAL;\nTUA ENTRADA E SAÍDA,\nDESDE AGORA ATÉ O FINAL.\n\nO SENHOR É QUEM TE GUARDA,\nSEMPRE À TUA DESTRA ESTÁ;\nNEM DE DIA, NEM DE NOITE,\nMAL ALGUM TE OFENDERÁ.\n\nCORO\nO SENHOR É QUEM TE GUARDA,\nGUARDARÁ DE TODO MAL;\nTUA ENTRADA E SAÍDA,\nDESDE AGORA ATÉ O FINAL.\n\nO SENHOR É QUEM TE GUARDA,\nNÃO TE DEIXARÁ CAIR;\nCOMO A ISRAEL GUARDAVA,\nNÃO DORMIU NEM VAI DORMIR.', 'PARA OS MONTES OLHAREI, DE ONDE VEM A SALVAÇÃO? MEU SOCORRO VEM DE DEUS, O SENHOR DA CRIAÇÃO. CORO O SENHOR É QUEM TE GUARDA, GUARDARÁ DE TODO MAL; TUA ENTRADA E SAÍDA, DESDE AGORA ATÉ O FINAL. O SENHOR É QUEM TE GUARDA, SEMPRE À TUA DESTRA ESTÁ; NEM DE DIA, NEM DE NOITE, MAL ALGUM TE OFENDERÁ. CORO O SENHOR É QUEM TE GUARDA, GUARDARÁ DE TODO MAL; TUA ENTRADA E SAÍDA, DESDE AGORA ATÉ O FINAL. O SENHOR É QUEM TE GUARDA, NÃO TE DEIXARÁ CAIR; COMO A ISRAEL GUARDAVA, NÃO DORMIU NEM VAI DORMIR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('340', '340 - DEUS TE GUARDARÁ', 'DEUS TE GUARDARÁ, DEUS TE GUARDARÁ,\nELE PROMETE E NUNCA FALHARÁ,\nDEUS TE GUARDARÁ.\n\nDEUS TE GUIARÁ, DEUS TE ABENÇOARÁ\nE SOBRE TI O SEU ROSTO BRILHARÁ;\nDEUS TE GUARDARÁ.\n\nDEUS TE GUARDARÁ, DEUS TE GUARDARÁ,\nELE PROMETE E NUNCA FALHARÁ,\nDEUS TE GUARDARÁ.\n\nDEUS TE GUIARÁ, DEUS TE ABENÇOARÁ\nE SOBRE TI O SEU ROSTO BRILHARÁ;\nDEUS TE GUARDARÁ.\n\nFINAL: DEUS TE GUIARÁ,\n            DEUS TE ABENÇOARÁ.', 'DEUS TE GUARDARÁ, DEUS TE GUARDARÁ, ELE PROMETE E NUNCA FALHARÁ, DEUS TE GUARDARÁ. DEUS TE GUIARÁ, DEUS TE ABENÇOARÁ E SOBRE TI O SEU ROSTO BRILHARÁ; DEUS TE GUARDARÁ. DEUS TE GUARDARÁ, DEUS TE GUARDARÁ, ELE PROMETE E NUNCA FALHARÁ, DEUS TE GUARDARÁ. DEUS TE GUIARÁ, DEUS TE ABENÇOARÁ E SOBRE TI O SEU ROSTO BRILHARÁ; DEUS TE GUARDARÁ.  DEUS TE GUIARÁ, DEUS TE ABENÇOARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('341', '341 - QUE CONSOLAÇÃO', 'QUE CONSOLAÇÃO TEM MEU CORAÇÃO,\nDESCANSANDO NO PODER DE DEUS;\nELE TEM PRAZER EM ME PROTEGER,\nDESCANSANDO NO PODER DE DEUS.\n\nCORO\nDESCANSANDO\nNOS ETERNOS BRAÇOS DO MEU DEUS;\nVOU SEGURO,\nDESCANSANDO NO PODER DE DEUS.\n\nSEMPRE AVANTE VOU, BEM CONTENTE ESTOU,\nDESCANSANDO NO PODER DE DEUS;\nTUDO HEI DE VENCER PELO SEU PODER,\nDESCANSANDO NO PODER DE DEUS.\n\nCORO\nDESCANSANDO\nNOS ETERNOS BRAÇOS DO MEU DEUS;\nVOU SEGURO,\nDESCANSANDO NO PODER DE DEUS.\n\nNÃO RECEAREI, NADA TEMEREI,\nDESCANSANDO NO PODER DE DEUS;\nGOZO, PAZ E AMOR JUNTO A MEU SENHOR\nDESCANSANDO NO PODER DE DEUS.', 'QUE CONSOLAÇÃO TEM MEU CORAÇÃO, DESCANSANDO NO PODER DE DEUS; ELE TEM PRAZER EM ME PROTEGER, DESCANSANDO NO PODER DE DEUS. CORO DESCANSANDO NOS ETERNOS BRAÇOS DO MEU DEUS; VOU SEGURO, DESCANSANDO NO PODER DE DEUS. SEMPRE AVANTE VOU, BEM CONTENTE ESTOU, DESCANSANDO NO PODER DE DEUS; TUDO HEI DE VENCER PELO SEU PODER, DESCANSANDO NO PODER DE DEUS. CORO DESCANSANDO NOS ETERNOS BRAÇOS DO MEU DEUS; VOU SEGURO, DESCANSANDO NO PODER DE DEUS. NÃO RECEAREI, NADA TEMEREI, DESCANSANDO NO PODER DE DEUS; GOZO, PAZ E AMOR JUNTO A MEU SENHOR DESCANSANDO NO PODER DE DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('342', '342 - ROCHA MINHA', 'ROCHA MINHA, LIBERTADOR MEU,\nEM TI EU CONFIAREI;\nO PODER NA TERRA E NO CÉU\nSÓ A TI PERTENCE, MEU DEUS E REI.\n\nCORO\nESPERO EM TI, Ó MEU DEUS,\nTUDO ESTÁ EM TUAS MÃOS!\nROCHA MINHA, LIBERTADOR MEU,\nSÓ TU ÉS, SENHOR, MINHA SALVAÇÃO\n\nCOM LOUVOR EM TUA CASA ENTRAREI\nE CANTAREI AO SENHOR,\nNA ALEGRIA OU TRISTEZA, EU SEI,\nSEMPRE ESTÁS COMIGO, MEU GRANDE AMOR.\n\nCORO\nESPERO EM TI, Ó MEU DEUS,\nTUDO ESTÁ EM TUAS MÃOS!\nROCHA MINHA, LIBERTADOR MEU,\nSÓ TU ÉS, SENHOR, MINHA SALVAÇÃO', 'ROCHA MINHA, LIBERTADOR MEU, EM TI EU CONFIAREI; O PODER NA TERRA E NO CÉU SÓ A TI PERTENCE, MEU DEUS E REI. CORO ESPERO EM TI, Ó MEU DEUS, TUDO ESTÁ EM TUAS MÃOS! ROCHA MINHA, LIBERTADOR MEU, SÓ TU ÉS, SENHOR, MINHA SALVAÇÃO COM LOUVOR EM TUA CASA ENTRAREI E CANTAREI AO SENHOR, NA ALEGRIA OU TRISTEZA, EU SEI, SEMPRE ESTÁS COMIGO, MEU GRANDE AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('343', '343 - OS QUE CONFIAM NO SENHOR', 'OS QUE CONFIAM NO SENHOR\nSERÃO FORTES E INABALÁVEIS,\nNÃO SERÃO CONFUNDIDOS\nE EM SEUS BRAÇOS DESCANSARÃO.\n\nO SENHOR CUMPRIRÁ SEUS DESEJOS,\nOUVIRÁ SEU CLAMOR E OS SALVARÁ,\nELE SERÁ SEU AUXÍLIO\nE EM SEUS BRAÇOS DESCANSARÃO.\nBIS', 'OS QUE CONFIAM NO SENHOR SERÃO FORTES E INABALÁVEIS, NÃO SERÃO CONFUNDIDOS E EM SEUS BRAÇOS DESCANSARÃO. O SENHOR CUMPRIRÁ SEUS DESEJOS, OUVIRÁ SEU CLAMOR E OS SALVARÁ, ELE SERÁ SEU AUXÍLIO E EM SEUS BRAÇOS DESCANSARÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('344', '344 - CONFIO EM DEUS', 'CONFIO EM DEUS AONDE QUER QUE EU VÁ,\nEM TERRA FIRME OU SOBRE O IRADO MAR;\nE SEJA POIS, POR ONDE FOR, \nCONFIO EM DEUS E EM SEU EXCELSO AMOR.\n\nCORO\nCONFIO EM DEUS, EU SEI QUE ME AMARÁ\nE PELA SUA MÃO ME GUIARÁ;\nE SEJA, POIS, POR ONDE FOR, \nCONFIO EM DEUS E EM SEU EXCELSO AMOR.\n\nA LINDA FLOR RECEBE O SEU CUIDAR,\nELE GUIA OS ANJOS NO ESPAÇOSO AR;\nSE ELE ASSIM, CUIDA DE MIM,\nCONFIO EM DEUS E EM SEU EXCELSO AMOR.\n\nCORO\nCONFIO EM DEUS, EU SEI QUE ME AMARÁ\nE PELA SUA MÃO ME GUIARÁ;\nE SEJA, POIS, POR ONDE FOR, \nCONFIO EM DEUS E EM SEU EXCELSO AMOR.\n\nPOR VALES OU DESERTO ABRASADOR\nCOM ELE IREI E SEM NENHUM TEMOR;\nE PELA MÃO ME CONDUZIRÁ,\nCONFIO EM DEUS E EM SEU EXCELSO AMOR.', 'CONFIO EM DEUS AONDE QUER QUE EU VÁ, EM TERRA FIRME OU SOBRE O IRADO MAR; E SEJA POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR. CORO CONFIO EM DEUS, EU SEI QUE ME AMARÁ E PELA SUA MÃO ME GUIARÁ; E SEJA, POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR. A LINDA FLOR RECEBE O SEU CUIDAR, ELE GUIA OS ANJOS NO ESPAÇOSO AR; SE ELE ASSIM, CUIDA DE MIM, CONFIO EM DEUS E EM SEU EXCELSO AMOR. CORO CONFIO EM DEUS, EU SEI QUE ME AMARÁ E PELA SUA MÃO ME GUIARÁ; E SEJA, POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR. POR VALES OU DESERTO ABRASADOR COM ELE IREI E SEM NENHUM TEMOR; E PELA MÃO ME CONDUZIRÁ, CONFIO EM DEUS E EM SEU EXCELSO AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('345', '345 - EM NADA PONHO A MINHA FÉ', 'EM NADA PONHO A MINHA FÉ,\nSENÃO NA GRAÇA DE JESUS;\nNO SACRIFÍCIO REMIDOR,\nNO SANGUE DO BOM REDENTOR.\nCORO\nA MINHA FÉ E O MEU AMOR\nESTÃO FIRMADOS NO SENHOR. (BIS)\n\nSE NÃO LHE POSSO A FACE VER,\nNA SUA GRAÇA VOU VIVER;\nA CADA DIA, SEM FALHAR,\nSEMPRE HEI DE NELE CONFIAR.\nCORO\nA MINHA FÉ E O MEU AMOR\nESTÃO FIRMADOS NO SENHOR. (BIS)\n\nSEU JURAMENTO É MUI LEAL,\nABRIGA-ME NO TEMPORAL;\nAO VIR CERCAR-ME A TENTAÇÃO\nÉ CRISTO A MINHA SALVAÇÃO.\nCORO\nA MINHA FÉ E O MEU AMOR\nESTÃO FIRMADOS NO SENHOR. (BIS)\n\nASSIM QUE O SEU CLARIM SOAR,\nIREI COM ELE ME ENCONTRAR\nE GOZAREI DA REDENÇÃO\nCOM TODOS QUE NO CÉU ESTÃO.\nCORO\nA MINHA FÉ E O MEU AMOR\nESTÃO FIRMADOS NO SENHOR. (BIS)', 'EM NADA PONHO A MINHA FÉ, SENÃO NA GRAÇA DE JESUS; NO SACRIFÍCIO REMIDOR, NO SANGUE DO BOM REDENTOR. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. SE NÃO LHE POSSO A FACE VER, NA SUA GRAÇA VOU VIVER; A CADA DIA, SEM FALHAR, SEMPRE HEI DE NELE CONFIAR. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. SEU JURAMENTO É MUI LEAL, ABRIGA-ME NO TEMPORAL; AO VIR CERCAR-ME A TENTAÇÃO É CRISTO A MINHA SALVAÇÃO. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. ASSIM QUE O SEU CLARIM SOAR, IREI COM ELE ME ENCONTRAR E GOZAREI DA REDENÇÃO COM TODOS QUE NO CÉU ESTÃO. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('346', '346 - SE A FÉ POR VEZES FALTA', 'SE A FÉ POR VEZES FALTA,\nQUANDO O SOL NÃO POSSO VER,\nA JESUS EU DIGO HUMILDE:\nÓ VEM MEU PILOTO SER.\n\nCORO\n“Ó NÃO TEMAS, SOU CONTIGO,\nTEU PILOTO ATÉ O FIM!\nNÃO TE IMPORTES COM O PERIGO,\nEIS A MÃO, CONFIA EM MIM.”\n\nQUANDO A TENTAÇÃO ME ENVOLVE\nE NÃO POSSO A CRISTO VER,\nEIS QUE EM MEIO À TEMPESTADE,\nOUÇO O SALVADOR DIZER:\n\nCORO\n“Ó NÃO TEMAS, SOU CONTIGO,\nTEU PILOTO ATÉ O FIM!\nNÃO TE IMPORTES COM O PERIGO,\nEIS A MÃO, CONFIA EM MIM.”\n\nQUANDO A ALMA SE ABATE,\nQUANDO OS DIAS LONGOS SÃO,\nVOLVO O OLHAR AO MEU PILOTO\nE ESSE CANTO ESCUTO ENTÃO:\n\nCORO\n“Ó NÃO TEMAS, SOU CONTIGO,\nTEU PILOTO ATÉ O FIM!\nNÃO TE IMPORTES COM O PERIGO,\nEIS A MÃO, CONFIA EM MIM.”', 'SE A FÉ POR VEZES FALTA, QUANDO O SOL NÃO POSSO VER, A JESUS EU DIGO HUMILDE: Ó VEM MEU PILOTO SER. CORO Ó NÃO TEMAS, SOU CONTIGO, TEU PILOTO ATÉ O FIM! NÃO TE IMPORTES COM O PERIGO, EIS A MÃO, CONFIA EM MIM. QUANDO A TENTAÇÃO ME ENVOLVE E NÃO POSSO A CRISTO VER, EIS QUE EM MEIO À TEMPESTADE, OUÇO O SALVADOR DIZER: QUANDO A ALMA SE ABATE, QUANDO OS DIAS LONGOS SÃO, VOLVO O OLHAR AO MEU PILOTO E ESSE CANTO ESCUTO ENTÃO:', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('347', '347 - FIRME NAS PROMESSAS', 'FIRME NAS PROMESSAS DO MEU SALVADOR,\nCANTAREI LOUVORES AO MEU CRIADOR,\nFICO NA DISPENSAÇÃO DO SEU AMOR,\nFIRME NAS PROMESSAS DE JESUS.\n\nCORO\nFIRME, FIRME, FIRME \nNAS PROMESSAS DE JESUS, MEU MESTRE;\nFIRME, FIRME, SIM,\nFIRME NAS PROMESSAS DE JESUS.\n\nFIRME NAS PROMESSAS, NÃO IREI FALHAR\nQUANDO AS TEMPESTADES VÊM ME ASSOLAR,\nPELO VERBO VIVO HEI DE BATALHAR,\nFIRME NAS PROMESSAS DE JESUS.\n\nCORO\nFIRME, FIRME, FIRME \nNAS PROMESSAS DE JESUS, MEU MESTRE;\nFIRME, FIRME, SIM,\nFIRME NAS PROMESSAS DE JESUS.\n\nFIRME NAS PROMESSAS, SEMPRE VEJO ASSIM,\nPURIFICAÇÃO NO SANGUE PARA MIM,\nPLENA LIBERDADE EM JESUS, SEM FIM,\nFIRME NAS PROMESSAS DE JESUS.\n\nCORO\nFIRME, FIRME, FIRME \nNAS PROMESSAS DE JESUS, MEU MESTRE;\nFIRME, FIRME, SIM,\nFIRME NAS PROMESSAS DE JESUS.\n\nFIRME NAS PROMESSAS DO SENHOR JESUS\nEM AMOR LIGADO COM A SUA CRUZ,\nCADA DIA MAIS ALEGRO-ME NA LUZ,\nFIRME NAS PROMESSAS DE JESUS.\n\nCORO\nFIRME, FIRME, FIRME \nNAS PROMESSAS DE JESUS, MEU MESTRE;\nFIRME, FIRME, SIM,\nFIRME NAS PROMESSAS DE JESUS.', 'FIRME NAS PROMESSAS DO MEU SALVADOR, CANTAREI LOUVORES AO MEU CRIADOR, FICO NA DISPENSAÇÃO DO SEU AMOR, FIRME NAS PROMESSAS DE JESUS. CORO FIRME, FIRME, FIRME NAS PROMESSAS DE JESUS, MEU MESTRE; FIRME, FIRME, SIM, FIRME NAS PROMESSAS DE JESUS. FIRME NAS PROMESSAS, NÃO IREI FALHAR QUANDO AS TEMPESTADES VÊM ME ASSOLAR, PELO VERBO VIVO HEI DE BATALHAR, FIRME NAS PROMESSAS DE JESUS. FIRME NAS PROMESSAS, SEMPRE VEJO ASSIM, PURIFICAÇÃO NO SANGUE PARA MIM, PLENA LIBERDADE EM JESUS, SEM FIM, FIRME NAS PROMESSAS DE JESUS. FIRME NAS PROMESSAS DO SENHOR JESUS EM AMOR LIGADO COM A SUA CRUZ, CADA DIA MAIS ALEGRO-ME NA LUZ, FIRME NAS PROMESSAS DE JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('348', '348 - ESPERA NO SENHOR, ANIMA-TE', 'ESPERA NO SENHOR, ANIMA-TE,\nE ELE FORTALECERÁ TEU CORAÇÃO;\nO SENHOR É A FORÇA \nDO SEU POVO,\nO SENHOR É A FORÇA \nDO SEU POVO.\nBIS\n\nCONHECES MINHA ALMA NAS ANGÚSTIAS\nE AS ÂNSIAS DO MEU CORAÇÃO;\nEM TI PONHO MINHA CONFIANÇA\nPOIS TU, SENHOR, \nÉS A FORÇA DO TEU POVO.\n\nO SENHOR É A FORÇA \nDO SEU POVO.\n\n4x', 'ESPERA NO SENHOR, ANIMA-TE, E ELE FORTALECERÁ TEU CORAÇÃO; O SENHOR É A FORÇA DO SEU POVO, O SENHOR É A FORÇA DO SEU POVO. CONHECES MINHA ALMA NAS ANGÚSTIAS E AS ÂNSIAS DO MEU CORAÇÃO; EM TI PONHO MINHA CONFIANÇA POIS TU, SENHOR, ÉS A FORÇA DO TEU POVO. O SENHOR É A FORÇA DO SEU POVO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('349', '349 - SE NÃO FOSSE O SENHOR', 'SE NÃO FOSSE O SENHOR\n(SE NÃO FOSSE O SENHOR) VARÕES\nQUE ESTIVESSE AO NOSSO LADO\nQUANDO OS HOMENS SE LEVANTARAM,\nSE LEVANTARAM CONTRA NÓS.\n\nO SOCORRO ESTÁ NO SENHOR\nQUE FEZ O CÉU E A TERRA.\nOS LAÇOS SÃO QUEBRADOS\nE NÓS ESCAPAMOS.\nBENDITO SEJA O SENHOR\nQUE FEZ O CÉU E A TERRA.\n\nINSTRUMENTOS\n\nSE NÃO FOSSE O SENHOR\n(SE NÃO FOSSE O SENHOR) VARÕES\nQUE ESTIVESSE AO NOSSO LADO\nQUANDO OS HOMENS SE LEVANTARAM,\nSE LEVANTARAM CONTRA NÓS.\n\nO SOCORRO ESTÁ NO SENHOR\nQUE FEZ O CÉU E A TERRA.\nOS LAÇOS SÃO QUEBRADOS\nE NÓS ESCAPAMOS.\n\nBENDITO SEJA O SENHOR\nQUE FEZ O CÉU E A TERRA.\n\nINSTRUMENTOS\n\nFINAL: \nSE NÃO FOSSE O SENHOR,\n(SE NÃO FOSSE O SENHOR)\n(SE NÃO FOSSE O SENHOR)\nO SENHOR!\nBIS', 'SE NÃO FOSSE O SENHOR (SE NÃO FOSSE O SENHOR) VARÕES QUE ESTIVESSE AO NOSSO LADO QUANDO OS HOMENS SE LEVANTARAM, SE LEVANTARAM CONTRA NÓS. O SOCORRO ESTÁ NO SENHOR QUE FEZ O CÉU E A TERRA. OS LAÇOS SÃO QUEBRADOS E NÓS ESCAPAMOS. BENDITO SEJA O SENHOR QUE FEZ O CÉU E A TERRA. INSTRUMENTOS SE NÃO FOSSE O SENHOR (SE NÃO FOSSE O SENHOR) VARÕES QUE ESTIVESSE AO NOSSO LADO QUANDO OS HOMENS SE LEVANTARAM, SE LEVANTARAM CONTRA NÓS. O SOCORRO ESTÁ NO SENHOR QUE FEZ O CÉU E A TERRA. OS LAÇOS SÃO QUEBRADOS E NÓS ESCAPAMOS. BENDITO SEJA O SENHOR QUE FEZ O CÉU E A TERRA. INSTRUMENTOS  SE NÃO FOSSE O SENHOR, (SE NÃO FOSSE O SENHOR) (SE NÃO FOSSE O SENHOR) O SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('350', '350 - FAZ-ME INABALÁVEL', 'FAZ-ME INABALÁVEL,\nSENHOR, EM TEU CAMINHO,\nFIRMA OS MEUS PASSOS\nE NÃO TRIUNFE O INIMIGO;\nOUVE A MINHA ORAÇÃO \nE SEJA EU SOCORRIDO.\n\nPOR QUE TE ABATES DENTRO EM MIM,\nÓ MINH’ALMA? ESPERA EM TEU DEUS,\nELE É TUA FORÇA E GLÓRIA;\nNÃO TE DESAMPAROU,\nSEMPRE ESTEVE AO TEU LADO.\n\nA DEUS TODA A GLÓRIA,\nELE NOS FAZ TRIUNFANTES,\nSUA GRAÇA É FORTE\nE SUA PALAVRA É VERDADE, AMÉM!', 'FAZ-ME INABALÁVEL, SENHOR, EM TEU CAMINHO, FIRMA OS MEUS PASSOS E NÃO TRIUNFE O INIMIGO; OUVE A MINHA ORAÇÃO E SEJA EU SOCORRIDO. POR QUE TE ABATES DENTRO EM MIM, Ó MINH’ALMA? ESPERA EM TEU DEUS, ELE É TUA FORÇA E GLÓRIA; NÃO TE DESAMPAROU, SEMPRE ESTEVE AO TEU LADO. A DEUS TODA A GLÓRIA, ELE NOS FAZ TRIUNFANTES, SUA GRAÇA É FORTE E SUA PALAVRA É VERDADE, AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('351', '351 - TU ÉS MEU REI', 'TU ÉS MEU REI, Ó DEUS, \nORDENA SALVAÇÃO.\nPOIS, POR TI, VENCEREMOS\nTODOS OS INIMIGOS MEUS;\nPELO TEU NOME VENCEDOR SEREI.\n\nPOIS NÃO CONFIAREI \nNO MEU ARCO NEM \nNA MINHA ESPADA.\nBIS', 'TU ÉS MEU REI, Ó DEUS, ORDENA SALVAÇÃO. POIS, POR TI, VENCEREMOS TODOS OS INIMIGOS MEUS; PELO TEU NOME VENCEDOR SEREI. POIS NÃO CONFIAREI NO MEU ARCO NEM NA MINHA ESPADA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('352', '352 - QUERES NESTE MUNDO SER UM VENCEDOR', 'QUERES NESTE MUNDO SER UM VENCEDOR?\nQUERES TU CANTAR NAS LUTAS E NA DOR?\nQUERES SER ALEGRE, QUAL BOM LUTADOR?\nGUARDA O CONTATO COM TEU SALVADOR.\n\nCORO\nGUARDA O CONTATO COM TEU SALVADOR\nE A NUVEM DO MAL NÃO TE COBRIRÁ;\nPELA SENDA ALEGRE TU CAMINHARÁS\nINDO EM CONTATO COM TEU SALVADOR.\n\nNESTE MUNDO VIVEM MUITOS A PENAR,\nCUJOS CORAÇÕES TRANSBORDAM DE PESAR, \nDÁ-LHES A MENSAGEM DE AMOR SEM PAR, \nCOM DEUS O CONTATO DEVES TU GUARDAR.\n\nCORO\nGUARDA O CONTATO COM TEU SALVADOR\nE A NUVEM DO MAL NÃO TE COBRIRÁ;\nPELA SENDA ALEGRE TU CAMINHARÁS\nINDO EM CONTATO COM TEU SALVADOR.\n\nQUERES TU COM DEUS A COMUNHÃO OBTER?\nSUA GLÓRIA EM TI SEMPRE PERMANECER?\nQUE O MUNDO POSSA CRISTO EM TI VER?\nGUARDA O CONTATO COM O SUPREMO SER.\n\nCORO\nGUARDA O CONTATO COM TEU SALVADOR\nE A NUVEM DO MAL NÃO TE COBRIRÁ;\nPELA SENDA ALEGRE TU CAMINHARÁS\nINDO EM CONTATO COM TEU SALVADOR.\n\nDEIXA QUE O ESPÍRITO IMPLANTE \nEM TEU SER O AMOR DE CRISTO, DIVINAL PRAZER;\nQUERES NESTE MUNDO TODO O MAL VENCER?\nGUARDA O CONTATO E TERÁS PODER.\n\nCORO\nGUARDA O CONTATO COM TEU SALVADOR\nE A NUVEM DO MAL NÃO TE COBRIRÁ;\nPELA SENDA ALEGRE TU CAMINHARÁS\nINDO EM CONTATO COM TEU SALVADOR.', 'QUERES NESTE MUNDO SER UM VENCEDOR? QUERES TU CANTAR NAS LUTAS E NA DOR? QUERES SER ALEGRE, QUAL BOM LUTADOR? GUARDA O CONTATO COM TEU SALVADOR. CORO GUARDA O CONTATO COM TEU SALVADOR E A NUVEM DO MAL NÃO TE COBRIRÁ; PELA SENDA ALEGRE TU CAMINHARÁS INDO EM CONTATO COM TEU SALVADOR. NESTE MUNDO VIVEM MUITOS A PENAR, CUJOS CORAÇÕES TRANSBORDAM DE PESAR, DÁ-LHES A MENSAGEM DE AMOR SEM PAR, COM DEUS O CONTATO DEVES TU GUARDAR. QUERES TU COM DEUS A COMUNHÃO OBTER? SUA GLÓRIA EM TI SEMPRE PERMANECER? QUE O MUNDO POSSA CRISTO EM TI VER? GUARDA O CONTATO COM O SUPREMO SER. DEIXA QUE O ESPÍRITO IMPLANTE EM TEU SER O AMOR DE CRISTO, DIVINAL PRAZER; QUERES NESTE MUNDO TODO O MAL VENCER? GUARDA O CONTATO E TERÁS PODER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('353', '353 - DEIXA-ME CHORAR', 'DEIXA-ME CHORAR AOS TEUS PÉS,\nDEIXA-ME SENTIR O TEU AMOR,\nSENHOR, SENHOR.\n\nSEM O TEU AMOR NÃO SEI VIVER,\nSEM A TUA GRAÇA EU NÃO SOU\nFELIZ, FELIZ.\n\nTE AMO, Ó SENHOR,\nAUTOR DA MINHA FÉ.\nPOIS CONSOLAS MINH’ALMA,\nO TEU SER ME ACALMA, Ó SENHOR.\n\nDEIXA-ME CHORAR AOS TEUS PÉS,\nDEIXA-ME SENTIR O TEU AMOR,\nSENHOR, SENHOR.\n\nSEM O TEU AMOR NÃO SEI VIVER,\nSEM A TUA GRAÇA EU NÃO SOU\nFELIZ, FELIZ.\n\nTE AMO, Ó SENHOR,\nAUTOR DA MINHA FÉ.\nPOIS CONSOLAS MINH’ALMA,\nO TEU SER ME ACALMA, Ó SENHOR.\n\nFINAL: \nDEIXA-ME CHORAR AOS TEUS PÉS.', 'DEIXA-ME CHORAR AOS TEUS PÉS, DEIXA-ME SENTIR O TEU AMOR, SENHOR, SENHOR. SEM O TEU AMOR NÃO SEI VIVER, SEM A TUA GRAÇA EU NÃO SOU FELIZ, FELIZ. TE AMO, Ó SENHOR, AUTOR DA MINHA FÉ. POIS CONSOLAS MINH’ALMA, O TEU SER ME ACALMA, Ó SENHOR. TE AMO, Ó SENHOR, AUTOR DA MINHA FÉ. POIS CONSOLAS MINH’ALMA, O TEU SER ME ACALMA, Ó SENHOR.  DEIXA-ME CHORAR AOS TEUS PÉS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('354', '354 - CRISTO ME DARÁ', 'CRISTO ME DARÁ A SUA PAZ,\nSIM, GUARDARÁ MEU CAMINHO;\nPOR ONDE QUER QUE EU ANDAR, \nSENHOR, A TUA LUZ ME GUIARÁ.\n\nCORO\nSÓ EM JESUS (SÓ EM JESUS)\nEU POSSO CONFIAR, (EU POSSO CONFIAR)\nETERNAMENTE E EXALTAR (E EXALTAR)\nCOM ALEGRIA SEU SANTO NOME, (GLORIOSO JESUS)\nCOMO É BOM VIVER O AMOR DE DEUS,\nQUE PRECIOSO É TER JESUS EM MIM.\n\nJESUS ME DARÁ O SEU AMOR,\nMESMO EM LUTAS OU PROVAÇÕES,\nCOM SEU ESPÍRITO EM MINHA VIDA\nNADA TEMEREI, SEMPRE O LOUVAREI, \nATÉ O FIM.\n\nCORO\nSÓ EM JESUS (SÓ EM JESUS)\nEU POSSO CONFIAR, (EU POSSO CONFIAR)\nETERNAMENTE E EXALTAR (E EXALTAR)\nCOM ALEGRIA SEU SANTO NOME, (GLORIOSO JESUS)\nCOMO É BOM VIVER O AMOR DE DEUS,\nQUE PRECIOSO É TER JESUS EM MIM.\n\nFINAL: \nAMO AO SENHOR, \nLOUVO A JESUS! \nCRISTO ESTÁ EM MIM!', 'CRISTO ME DARÁ A SUA PAZ, SIM, GUARDARÁ MEU CAMINHO; POR ONDE QUER QUE EU ANDAR, SENHOR, A TUA LUZ ME GUIARÁ. CORO SÓ EM JESUS (SÓ EM JESUS) EU POSSO CONFIAR, (EU POSSO CONFIAR) ETERNAMENTE E EXALTAR (E EXALTAR) COM ALEGRIA SEU SANTO NOME, (GLORIOSO JESUS) COMO É BOM VIVER O AMOR DE DEUS, QUE PRECIOSO É TER JESUS EM MIM. JESUS ME DARÁ O SEU AMOR, MESMO EM LUTAS OU PROVAÇÕES, COM SEU ESPÍRITO EM MINHA VIDA NADA TEMEREI, SEMPRE O LOUVAREI, ATÉ O FIM. AMO AO SENHOR, LOUVO A JESUS! CRISTO ESTÁ EM MIM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('355', '355 - VENCER, VENCER', 'VENCER, VENCER, EU FUI CHAMADO PRA VENCER;\nVENCER O MAL, VENCER O MUNDO\nPOR AMOR AO MEU DEUS. (BIS)\n\nPORQUE SEREI,  \nSEREI COMO OS ANJOS QUE LOUVAM\nA CRISTO JESUS, O MEU REI, E ESTAREI NAS LINDAS MANSÕES CELESTIAIS,\nPELAS RUAS DE OURO ANDAREI.\n\nVENCER, VENCER, \nEU FUI CHAMADO PRA VENCER\nATÉ CHEGAR O LINDO DIA\nONDE O MAL CESSARÁ,\nONDE A PAZ REINARÁ.\n\nPORQUE JESUS \nJÁ VENCEU A MORTE NA CRUZ\nE DEU SUA GRAÇA, SUA LUZ;\nPORQUE SEREI, \nSEREI COMO OS ANJOS QUE LOUVAM \nA CRISTO JESUS, O MEU REI.\n\nVENCER, VENCER, \nEU FUI CHAMADO PRA VENCER,\nVENCER O MAL, VENCER O MUNDO\nPOR AMOR AO MEU DEUS. (3X)', 'VENCER, VENCER, EU FUI CHAMADO PRA VENCER; VENCER O MAL, VENCER O MUNDO POR AMOR AO MEU DEUS. PORQUE SEREI, SEREI COMO OS ANJOS QUE LOUVAM A CRISTO JESUS, O MEU REI, E ESTAREI NAS LINDAS MANSÕES CELESTIAIS, PELAS RUAS DE OURO ANDAREI. VENCER, VENCER, EU FUI CHAMADO PRA VENCER ATÉ CHEGAR O LINDO DIA ONDE O MAL CESSARÁ, ONDE A PAZ REINARÁ. PORQUE JESUS JÁ VENCEU A MORTE NA CRUZ E DEU SUA GRAÇA, SUA LUZ; PORQUE SEREI, SEREI COMO OS ANJOS QUE LOUVAM A CRISTO JESUS, O MEU REI. VENCER, VENCER, EU FUI CHAMADO PRA VENCER, VENCER O MAL, VENCER O MUNDO POR AMOR AO MEU DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('356', '356 - O SENHOR É O MEU PASTOR  (REFRIGERA A MINHA ALMA)', 'O SENHOR É O MEU PASTOR, \nNADA ME FALTARÁ,\nDEITAR-ME FAZ EM VERDES PASTOS,\nE ÀS ÁGUAS, MANSAMENTE,\nMINHA ALMA GUIARÁ.\n\nCORO\nREFRIGERA A MINHA ALMA, (BIS)\nÓ SENHOR, TEU SER ME ACALMA,\nREFRIGERA A MINHA ALMA;\nGUIA-ME PELAS VEREDAS,\nAS VEREDAS DA JUSTIÇA\nE SENTIR SOMENTE O AMOR,\nO AMOR DE DEUS.\n\nSE SOZINHO EU ESTOU\nE NÃO POSSO MAIS LUTAR,\nO SENHOR ESTÁ COMIGO,\nCOM SUA VARA E SEU CAJADO\nNÃO VAI ME DESAMPARAR.\n\nCORO\nREFRIGERA A MINHA ALMA, (BIS)\nÓ SENHOR, TEU SER ME ACALMA,\nREFRIGERA A MINHA ALMA;\nGUIA-ME PELAS VEREDAS,\nAS VEREDAS DA JUSTIÇA\nE SENTIR SOMENTE O AMOR,\nO AMOR DE DEUS.', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, DEITAR-ME FAZ EM VERDES PASTOS, E ÀS ÁGUAS, MANSAMENTE, MINHA ALMA GUIARÁ. CORO REFRIGERA A MINHA ALMA,  Ó SENHOR, TEU SER ME ACALMA, REFRIGERA A MINHA ALMA; GUIA-ME PELAS VEREDAS, AS VEREDAS DA JUSTIÇA E SENTIR SOMENTE O AMOR, O AMOR DE DEUS. SE SOZINHO EU ESTOU E NÃO POSSO MAIS LUTAR, O SENHOR ESTÁ COMIGO, COM SUA VARA E SEU CAJADO NÃO VAI ME DESAMPARAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('357', '357 - O SENHOR É O MEU PASTOR  (BONDADE E MISERICÓRDIA)', 'O SENHOR É O MEU PASTOR, \nNADA ME FALTARÁ,\nPOR VERDES CAMPOS ME CONDUZIRÁ,\nEM PLANAS CAMPINAS,\nJUNTO ÀS ÁGUAS TRANQÜILAS\nDO SEU AMOR ELE ME DARÁ.\n\nCORO\nBONDADE E MISERICÓRDIA\nCERTAMENTE ME SEGUIRÃO\nE HABITAREI COM MEU SENHOR\nPRA TODO O SEMPRE;\nAINDA QUE EU ANDASSE\nPELO VALE DA SOMBRA DA MORTE,\nNÃO TEMERIA MAL \nALGUM PORQUE COMIGO ESTÁS.\n\nME UNGES COM ÓLEO DA ALEGRIA,\nEM TUA PRESENÇA ME ALEGRO, SENHOR;\nUMA MESA FARTA TU ME DARÁS,\nTUA DESTRA EM VERDADE ME SUSTENTARÁ.\n\nCORO\nBONDADE E MISERICÓRDIA\nCERTAMENTE ME SEGUIRÃO\nE HABITAREI COM MEU SENHOR\nPRA TODO O SEMPRE;\nAINDA QUE EU ANDASSE\nPELO VALE DA SOMBRA DA MORTE,\nNÃO TEMERIA MAL \nALGUM PORQUE COMIGO ESTÁS.\n\nFINAL: \nO SENHOR É O MEU PASTOR,\nNADA ME FALTARÁ,\nPOR VERDES CAMPOS ME CONDUZIRÁ.', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, POR VERDES CAMPOS ME CONDUZIRÁ, EM PLANAS CAMPINAS, JUNTO ÀS ÁGUAS TRANQÜILAS DO SEU AMOR ELE ME DARÁ. CORO BONDADE E MISERICÓRDIA CERTAMENTE ME SEGUIRÃO E HABITAREI COM MEU SENHOR PRA TODO O SEMPRE; AINDA QUE EU ANDASSE PELO VALE DA SOMBRA DA MORTE, NÃO TEMERIA MAL ALGUM PORQUE COMIGO ESTÁS. ME UNGES COM ÓLEO DA ALEGRIA, EM TUA PRESENÇA ME ALEGRO, SENHOR; UMA MESA FARTA TU ME DARÁS, TUA DESTRA EM VERDADE ME SUSTENTARÁ. O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, POR VERDES CAMPOS ME CONDUZIRÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('358', '358 - O SENHOR É O MEU PASTOR (SALMO 23)', 'O SENHOR É O MEU PASTOR,\nNADA ME FALTARÁ;\nPELOS PASTOS VERDEJANTES\nELE ME CONDUZIRÁ.\nBIS\n\nREFRIGERA A MINHA ALMA\nCOM SUA PAZ E SEU AMOR,\nSEU CAJADO ME CONSOLA,\nELE É O MEU PASTOR.\n\nQUANDO NO VALE DA MORTE\nMAL ALGUM EU TEMEREI,\nO SENHOR É O MEU PASTOR,\nEM SUA CASA HABITAREI.\nBIS', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ; PELOS PASTOS VERDEJANTES ELE ME CONDUZIRÁ. REFRIGERA A MINHA ALMA COM SUA PAZ E SEU AMOR, SEU CAJADO ME CONSOLA, ELE É O MEU PASTOR. QUANDO NO VALE DA MORTE MAL ALGUM EU TEMEREI, O SENHOR É O MEU PASTOR, EM SUA CASA HABITAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('359', '359 - DEUS É NOSSO AMPARO, NOSSA FORTALEZA', 'DEUS É NOSSO AMPARO, NOSSA FORTALEZA,\nNOSSO PRONTO AUXÍLIO NA TRIBULAÇÃO,\nAINDA QUE SE MUDEM OS MONTES PARA O MAR,\nAINDA QUE A TERRA TREMA,\nTEREMOS QUE CONFIAR.\nBIS\n\nCÉUS E TERRA PODERÃO PASSAR,\nMAS SUA PALAVRA NÃO PASSARÁ.\n\nNÃO, NÃO, NÃO PASSARÁ. (BIS)\n\nSANTO, SANTO, SANTO, DIZ O SERAFIM,\nSANTO, SANTO, SANTO, É O SENHOR JEOVÁ,\nSANTO, SANTO, SANTO, É O DEUS QUE NOS REDIME,\nPORQUE TRÊS VEZES SANTO,\nA TERRA TREME ONDE DEUS ESTÁ.\nBIS\nBIS\n\nCÉUS E TERRA PODERÃO PASSAR,\nMAS SUA PALAVRA NÃO PASSARÁ.\n\nNÃO, NÃO, NÃO PASSARÁ. (BIS)\nBIS', 'DEUS É NOSSO AMPARO, NOSSA FORTALEZA, NOSSO PRONTO AUXÍLIO NA TRIBULAÇÃO, AINDA QUE SE MUDEM OS MONTES PARA O MAR, AINDA QUE A TERRA TREMA, TEREMOS QUE CONFIAR. CÉUS E TERRA PODERÃO PASSAR, MAS SUA PALAVRA NÃO PASSARÁ. NÃO, NÃO, NÃO PASSARÁ.  SANTO, SANTO, SANTO, DIZ O SERAFIM, SANTO, SANTO, SANTO, É O SENHOR JEOVÁ, SANTO, SANTO, SANTO, É O DEUS QUE NOS REDIME, PORQUE TRÊS VEZES SANTO, A TERRA TREME ONDE DEUS ESTÁ. CÉUS E TERRA PODERÃO PASSAR, MAS SUA PALAVRA NÃO PASSARÁ. NÃO, NÃO, NÃO PASSARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('360', '360 - ROCHEDO FORTE É O SENHOR', 'ROCHEDO FORTE É O SENHOR,\nREFÚGIO NA TRIBULAÇÃO!\nCONSTANTE E FIRME, AMPARADOR,\nREFÚGIO NA TRIBULAÇÃO!\n\nCORO\nOH! CRISTO É NOSSO ABRIGO NO TEMPORAL, \nNO TEMPORAL, NO TEMPORAL!\nOH! CRISTO É NOSSO ABRIGO \nNO TEMPORAL! REFÚGIO NA TRIBULAÇÃO.\n\nLUGAR DE SOMBRA NO VERÃO,\nDESCANSO NA TRIBULAÇÃO!\nVIGIA FIEL NA ESCURIDÃO,\nDESCANSO NA TRIBULAÇÃO!\n\nCORO\nOH! CRISTO É NOSSO ABRIGO NO TEMPORAL, \nNO TEMPORAL, NO TEMPORAL!\nOH! CRISTO É NOSSO ABRIGO \nNO TEMPORAL! REFÚGIO NA TRIBULAÇÃO.\n\nPILOTO BOM NO BRAVO MAR,\nCONSOLO NA TRIBULAÇÃO!\nANCORADOURO SINGULAR,\nCONSOLO NA TRIBULAÇÃO!\n\nCORO\nOH! CRISTO É NOSSO ABRIGO NO TEMPORAL, \nNO TEMPORAL, NO TEMPORAL!\nOH! CRISTO É NOSSO ABRIGO \nNO TEMPORAL! REFÚGIO NA TRIBULAÇÃO.\n\nJESUS É O NOSSO BENFEITOR,\nAUXÍLIO NA TRIBULAÇÃO!\nPRESENTE, ETERNO SALVADOR,\nAUXÍLIO NA TRIBULAÇÃO!\n\nCORO\nOH! CRISTO É NOSSO ABRIGO NO TEMPORAL, \nNO TEMPORAL, NO TEMPORAL!\nOH! CRISTO É NOSSO ABRIGO \nNO TEMPORAL! REFÚGIO NA TRIBULAÇÃO.', 'ROCHEDO FORTE É O SENHOR, REFÚGIO NA TRIBULAÇÃO! CONSTANTE E FIRME, AMPARADOR, REFÚGIO NA TRIBULAÇÃO! CORO OH! CRISTO É NOSSO ABRIGO NO TEMPORAL, NO TEMPORAL, NO TEMPORAL! OH! CRISTO É NOSSO ABRIGO NO TEMPORAL! REFÚGIO NA TRIBULAÇÃO. LUGAR DE SOMBRA NO VERÃO, DESCANSO NA TRIBULAÇÃO! VIGIA FIEL NA ESCURIDÃO, DESCANSO NA TRIBULAÇÃO! PILOTO BOM NO BRAVO MAR, CONSOLO NA TRIBULAÇÃO! ANCORADOURO SINGULAR, CONSOLO NA TRIBULAÇÃO! JESUS É O NOSSO BENFEITOR, AUXÍLIO NA TRIBULAÇÃO! PRESENTE, ETERNO SALVADOR, AUXÍLIO NA TRIBULAÇÃO!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('361', '361 - NÃO HÁ DEUS ACIMA DO SENHOR', 'NÃO HÁ DEUS ACIMA DO SENHOR,\nNÃO HÁ ROCHA COMO NOSSO DEUS;\nÉ FORÇA E PROTEÇÃO, \nELE É A TORRE DE LIBERTAÇÃO\nAOS QUE CONFIAM NO SENHOR.\nBIS\n\nNÃO HÁ DEUS ACIMA DO SENHOR,\nNÃO HÁ ROCHA COMO NOSSO DEUS;\nME TREINA PRA LUTAR,\nME MOSTRA ONDE DEVO EU ANDAR,\nGLÓRIA AO NOME DE JESUS.\n\nME TREINA PRA LUTAR,\nME MOSTRA ONDE DEVO EU ANDAR,\nGLÓRIA AO NOME DE JESUS.\nGLÓRIA AO NOME DE JESUS.', 'NÃO HÁ DEUS ACIMA DO SENHOR, NÃO HÁ ROCHA COMO NOSSO DEUS; É FORÇA E PROTEÇÃO, ELE É A TORRE DE LIBERTAÇÃO AOS QUE CONFIAM NO SENHOR. NÃO HÁ DEUS ACIMA DO SENHOR, NÃO HÁ ROCHA COMO NOSSO DEUS; ME TREINA PRA LUTAR, ME MOSTRA ONDE DEVO EU ANDAR, GLÓRIA AO NOME DE JESUS. ME TREINA PRA LUTAR, ME MOSTRA ONDE DEVO EU ANDAR, GLÓRIA AO NOME DE JESUS. GLÓRIA AO NOME DE JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('362', '362 - DEUS PROVERÁ', 'CORO (2X)\nDEUS PROVERÁ TUDO PARA O SEU POVO,\nPAZ, ALEGRIA, CONSOLO E GOZO,\nCANTANDO E LOUVANDO AO DEUS CRIADOR,\nAQUELE QUE REINA E QUE É O SENHOR,\nMESMO NAS LUTAS E AFLIÇÕES,\nDEUS PROVERÁ PARA NÓS A VITÓRIA.\n\n“QUANDO PASSARES PELAS ÁGUAS\nESTAREI CONTIGO,\nQUANDO PASSARES PELOS \nRIOS ELES NÃO TE COBRIRÃO\nQUANDO PASSARES PELO FOGO\nELE NÃO TE QUEIMARÁ,\nPORQUE EU SOU O TEU \nDEUS, O SANTO DE ISRAEL\nO TEU SALVADOR”.\n\nVARÕES\n\nVARÕES\n\nCORO\nDEUS PROVERÁ TUDO PARA O SEU POVO,\nPAZ, ALEGRIA, CONSOLO E GOZO,\nCANTANDO E LOUVANDO AO DEUS CRIADOR,\nAQUELE QUE REINA E QUE É O SENHOR,\nMESMO NAS LUTAS E AFLIÇÕES,\nDEUS PROVERÁ PARA NÓS A VITÓRIA.\n\n“QUANDO PASSARES PELAS ÁGUAS\nESTAREI CONTIGO,\nQUANDO PASSARES PELOS \nRIOS ELES NÃO TE COBRIRÃO\nACHASTE GRAÇA AOS MEUS OLHOS,\nDIGNO DE HONRA E EU TE AMEI,\nNÃO TEMAS, EU SOU CONTIGO,\nO SANTO DE ISRAEL,\nO TEU SALVADOR”.\n\nVARÕES\n\nVARÕES\n\nCORO\nDEUS PROVERÁ TUDO PARA O SEU POVO,\nPAZ, ALEGRIA, CONSOLO E GOZO,\nCANTANDO E LOUVANDO AO DEUS CRIADOR,\nAQUELE QUE REINA E QUE É O SENHOR,\nMESMO NAS LUTAS E AFLIÇÕES,\nDEUS PROVERÁ PARA NÓS A VITÓRIA.\nBIS', 'DEUS PROVERÁ TUDO PARA O SEU POVO, PAZ, ALEGRIA, CONSOLO E GOZO, CANTANDO E LOUVANDO AO DEUS CRIADOR, AQUELE QUE REINA E QUE É O SENHOR, MESMO NAS LUTAS E AFLIÇÕES, DEUS PROVERÁ PARA NÓS A VITÓRIA. QUANDO PASSARES PELAS ÁGUAS ESTAREI CONTIGO, QUANDO PASSARES PELOS RIOS ELES NÃO TE COBRIRÃO QUANDO PASSARES PELO FOGO ELE NÃO TE QUEIMARÁ, PORQUE EU SOU O TEU DEUS, O SANTO DE ISRAEL O TEU SALVADOR. CORO DEUS PROVERÁ TUDO PARA O SEU POVO, PAZ, ALEGRIA, CONSOLO E GOZO, CANTANDO E LOUVANDO AO DEUS CRIADOR, AQUELE QUE REINA E QUE É O SENHOR, MESMO NAS LUTAS E AFLIÇÕES, DEUS PROVERÁ PARA NÓS A VITÓRIA. QUANDO PASSARES PELAS ÁGUAS ESTAREI CONTIGO, QUANDO PASSARES PELOS RIOS ELES NÃO TE COBRIRÃO ACHASTE GRAÇA AOS MEUS OLHOS, DIGNO DE HONRA E EU TE AMEI, NÃO TEMAS, EU SOU CONTIGO, O SANTO DE ISRAEL, O TEU SALVADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('363', '363 - QUANDO AS LUTAS ME CERCAM', 'QUANDO AS LUTAS ME CERCAM,\nME ENTRISTECEM E ME FAZEM CHORAR,\nEU CORRO PARA O TEU ALTAR.\n\nCORO (2X)\nDE JOELHOS COMEÇO A ORAR\nSINTO A MÃO DE JESUS ME TOCAR. \n\nINSTRUMENTOS\n\nO RENOVO ASSIM VEM,\nTEU ESPÍRITO A ME CONSOLAR.\nDE ALEGRIA, QUERO TE LOUVAR.\n\nCORO (2X)\nDE JOELHOS COMEÇO A ORAR\nSINTO A MÃO DE JESUS ME TOCAR. \n\nFINAL: \nDE ALEGRIA, \nQUERO TE LOUVAR.', 'QUANDO AS LUTAS ME CERCAM, ME ENTRISTECEM E ME FAZEM CHORAR, EU CORRO PARA O TEU ALTAR. DE JOELHOS COMEÇO A ORAR SINTO A MÃO DE JESUS ME TOCAR. INSTRUMENTOS O RENOVO ASSIM VEM, TEU ESPÍRITO A ME CONSOLAR. DE ALEGRIA, QUERO TE LOUVAR. DE JOELHOS COMEÇO A ORAR SINTO A MÃO DE JESUS ME TOCAR.  DE ALEGRIA, QUERO TE LOUVAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('364', '364 - DÁ-ME MAIS FÉ', 'DÁ-ME MAIS FÉ PRA QUE O MEU CHORO\nSOMENTE ESTA NOITE POSSA DURAR\nE A TUA ALEGRIA, QUE É A MINHA FORÇA, \nBEM CEDO POSSA CHEGAR;\n\nDÁ-ME MAIS FÉ E A CERTEZA \nDE QUE EU POSSO ESPERAR \nBÊNÇÃOS QUE HOJE EU NÃO POSSO VER, \nMAS EM TI ESPERAREI,\nSEI QUE UM DIA VÃO CHEGAR \nE PELA FÉ EU VENCEREI.\n\nCORO\nO IMPOSSÍVEL NÃO DEVO TEMER,\nTUDO É POSSÍVEL AO HOMEM QUE CRÊ;\nDEUS TEM PRAZER NO HOMEM DE FÉ\nQUE CRÊ QUE DEUS NUNCA FALHA,\nQUE MILAGRES E MARAVILHAS OPERARÁ, \nPOR AMOR ÀQUELE QUE DE DEUS \nNÃO DUVIDAR.\n\nDÁ-ME MAIS FÉ E A FORÇA DE LUTAR\nSEM, DO CAMINHO, EU ME DESVIAR,\nMESMO TENDO AFLIÇÕES NÃO \nQUERO JAMAIS PECAR,\nÓ VEM, SENHOR, ME SUSTENTAR;\n\nDÁ-ME MAIS FÉ PARA ENTENDER\nQUE O QUE ME ACONTECER\nÉ SOMENTE PRA EU CRESCER,\nE SEGUINDO SEMPRE EM FRENTE,\nCOM A TUA SALVAÇÃO\nREPOUSO O MEU CORAÇÃO.\n\nCORO\nO IMPOSSÍVEL NÃO DEVO TEMER,\nTUDO É POSSÍVEL AO HOMEM QUE CRÊ;\nDEUS TEM PRAZER NO HOMEM DE FÉ\nQUE CRÊ QUE DEUS NUNCA FALHA,\nQUE MILAGRES E MARAVILHAS OPERARÁ, \nPOR AMOR ÀQUELE QUE DE DEUS \nNÃO DUVIDAR.\nINSTRUMENTOS\n\nCORO\nO IMPOSSÍVEL NÃO DEVO TEMER,\nTUDO É POSSÍVEL AO HOMEM QUE CRÊ;\nDEUS TEM PRAZER NO HOMEM DE FÉ\nQUE CRÊ QUE DEUS NUNCA FALHA,\nQUE MILAGRES E MARAVILHAS OPERARÁ, \nPOR AMOR ÀQUELE QUE DE DEUS \nNÃO DUVIDAR.\n\nFINAL: DÁ-ME MAIS FÉ', 'DÁ-ME MAIS FÉ PRA QUE O MEU CHORO SOMENTE ESTA NOITE POSSA DURAR E A TUA ALEGRIA, QUE É A MINHA FORÇA, BEM CEDO POSSA CHEGAR; DÁ-ME MAIS FÉ E A CERTEZA DE QUE EU POSSO ESPERAR BÊNÇÃOS QUE HOJE EU NÃO POSSO VER, MAS EM TI ESPERAREI, SEI QUE UM DIA VÃO CHEGAR E PELA FÉ EU VENCEREI. CORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR. DÁ-ME MAIS FÉ E A FORÇA DE LUTAR SEM, DO CAMINHO, EU ME DESVIAR, MESMO TENDO AFLIÇÕES NÃO QUERO JAMAIS PECAR, Ó VEM, SENHOR, ME SUSTENTAR; DÁ-ME MAIS FÉ PARA ENTENDER QUE O QUE ME ACONTECER É SOMENTE PRA EU CRESCER, E SEGUINDO SEMPRE EM FRENTE, COM A TUA SALVAÇÃO REPOUSO O MEU CORAÇÃO. CORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR. INSTRUMENTOS CORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR.  DÁ-ME MAIS FÉ', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('365', '365 - NÃO TEMAS, POIS TE ESCOLHI  (QUANDO PELAS ÁGUAS)', '“NÃO TEMAS, POIS TE ESCOLHI, TE REMI,\nNÃO TEMAS, POIS, PORQUE CONTIGO ESTOU;\nAS COISAS PASSADAS TU NÃO DEVES LEMBRAR,\nPOIS NOVO CAMINHO O TEU DEUS ABRIRÁ.\n\nCORO\nQUANDO PELAS ÁGUAS PASSARES\nCONTIGO ESTAREI,\nQUANDO PELO FOGO TU PASSARES\nNÃO TE QUEIMARÁS,\nEU SOU TEU DEUS, TEU SENHOR, \nO SALVADOR.\n\nINSTRUMENTOS\n\nEU ANUNCIEI E SALVEI, FIZ OUVIR\nQUE NÃO HÁ DEUS SALVADOR FORA DE MIM;\nFOSTE PRECIOSO AOS MEUS OLHOS, EU TE AMEI.\nQUANDO O TEU DEUS OPERAR, QUEM IMPEDIRÁ?\n\nCORO\nQUANDO PELAS ÁGUAS PASSARES\nCONTIGO ESTAREI,\nQUANDO PELO FOGO TU PASSARES\nNÃO TE QUEIMARÁS,\nEU SOU TEU DEUS, TEU SENHOR, \nO SALVADOR.\n\nFINAL: \nQUANDO PELAS ÁGUAS \n(O SALVADOR)\nESTAREI CONTIGO \n(O SALVADOR).”', 'NÃO TEMAS, POIS TE ESCOLHI, TE REMI, NÃO TEMAS, POIS, PORQUE CONTIGO ESTOU; AS COISAS PASSADAS TU NÃO DEVES LEMBRAR, POIS NOVO CAMINHO O TEU DEUS ABRIRÁ. CORO QUANDO PELAS ÁGUAS PASSARES CONTIGO ESTAREI, QUANDO PELO FOGO TU PASSARES NÃO TE QUEIMARÁS, EU SOU TEU DEUS, TEU SENHOR, O SALVADOR. INSTRUMENTOS EU ANUNCIEI E SALVEI, FIZ OUVIR QUE NÃO HÁ DEUS SALVADOR FORA DE MIM; FOSTE PRECIOSO AOS MEUS OLHOS, EU TE AMEI. QUANDO O TEU DEUS OPERAR, QUEM IMPEDIRÁ? CORO QUANDO PELAS ÁGUAS PASSARES CONTIGO ESTAREI, QUANDO PELO FOGO TU PASSARES NÃO TE QUEIMARÁS, EU SOU TEU DEUS, TEU SENHOR, O SALVADOR. QUANDO PELAS ÁGUAS (O SALVADOR) ESTAREI CONTIGO (O SALVADOR).', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('366', '366 - AS LÁGRIMAS FALAM, JESUS TE ESCUTA', 'AS LÁGRIMAS FALAM, JESUS TE ESCUTA\nAS LÁGRIMAS FALAM PRA JESUS A TUA DOR.\n“BEM-AVENTURADO É O HOMEM QUE CHORA, \nSERÁ CONSOLADO”, \nPOIS ASSIM DIZ O SENHOR.\n\nDOBRA OS TEUS JOELHOS PERANTE \nO ALTAR DO SENHOR E A VITÓRIA VIRÁ.\nENTREGA TUA ALMA, \nCONFIA EM CRISTO JESUS \nE AS TUAS LÁGRIMAS \nJESUS ENXUGARÁ.\n\nINSTRUMENTOS\n\nAS LÁGRIMAS FALAM, JESUS TE ESCUTA\nAS LÁGRIMAS FALAM PRA JESUS A TUA DOR.\n“BEM-AVENTURADO É O HOMEM QUE CHORA, \nSERÁ CONSOLADO”, \nPOIS ASSIM DIZ O SENHOR.\n\nDOBRA OS TEUS JOELHOS \nPERANTE O ALTAR DO SENHOR E A VITÓRIA VIRÁ.\nENTREGA TUA ALMA, CONFIA EM CRISTO JESUS \nE AS TUAS LÁGRIMAS JESUS ENXUGARÁ.\n\nFINAL: \n(ENXUGARÁ)\nE AS TUAS LÁGRIMAS \nJESUS ENXUGARÁ.', 'AS LÁGRIMAS FALAM, JESUS TE ESCUTA AS LÁGRIMAS FALAM PRA JESUS A TUA DOR. BEM-AVENTURADO É O HOMEM QUE CHORA, SERÁ CONSOLADO, POIS ASSIM DIZ O SENHOR. DOBRA OS TEUS JOELHOS PERANTE O ALTAR DO SENHOR E A VITÓRIA VIRÁ. ENTREGA TUA ALMA, CONFIA EM CRISTO JESUS E AS TUAS LÁGRIMAS JESUS ENXUGARÁ. INSTRUMENTOS DOBRA OS TEUS JOELHOS PERANTE O ALTAR DO SENHOR E A VITÓRIA VIRÁ. ENTREGA TUA ALMA, CONFIA EM CRISTO JESUS E AS TUAS LÁGRIMAS JESUS ENXUGARÁ.  (ENXUGARÁ) E AS TUAS LÁGRIMAS JESUS ENXUGARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('367', '367 - SE AS LUTAS DA VIDA', 'SE AS LUTAS DA VIDA TE FAZEM CHORAR \nE ÀS VEZES TE FALTAM FORÇAS PRA VENCER,\nEXISTE UM DEUS QUE AO TEU LADO ESTÁ,\nELE É DEUS FORTE, POR TI VENCERÁ;\nEXISTE UM DEUS QUE AO TEU LADO ESTÁ, \nELE É FIEL E NÃO FALHARÁ.\n\nDEUS CUIDA DE TI (BIS)\nÉ TEU AMIGO, ESTÁ CONTIGO, \nDEUS CUIDA DE TI.\n\nSÓ EXISTE UM DEUS E AO TEU LADO ESTÁ, \nELE É DEUS FORTE, POR TI VENCERÁ;\nSIM, EXISTE UM DEUS E AO TEU LADO ESTÁ,\nELE É FIEL E NÃO FALHARÁ. (BIS)', 'SE AS LUTAS DA VIDA TE FAZEM CHORAR E ÀS VEZES TE FALTAM FORÇAS PRA VENCER, EXISTE UM DEUS QUE AO TEU LADO ESTÁ, ELE É DEUS FORTE, POR TI VENCERÁ; EXISTE UM DEUS QUE AO TEU LADO ESTÁ, ELE É FIEL E NÃO FALHARÁ. DEUS CUIDA DE TI  É TEU AMIGO, ESTÁ CONTIGO, DEUS CUIDA DE TI. SÓ EXISTE UM DEUS E AO TEU LADO ESTÁ, ELE É DEUS FORTE, POR TI VENCERÁ; SIM, EXISTE UM DEUS E AO TEU LADO ESTÁ, ELE É FIEL E NÃO FALHARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('368', '368 - ESFORÇA-TE, TEM BOM ÂNIMO!', '“ESFORÇA-TE, TEM BOM ÂNIMO!”\nASSIM DIZ O SENHOR TEU DEUS,\nO DEUS DE ISRAEL, O DEUS QUE TE AMA,\nO DEUS QUE CONTIGO SEMPRE ESTÁ;\nTÃO-SOMENTE ESFORÇA-TE.\n\nCORO (2X)\nELE CONTIGO IRÁ\nPOR ONDE QUER QUE VÁS,\nSUA BOA MÃO TE SUSTERÁ;\nO SENHOR CONTIGO ESTARÁ.\n\nNÃO TEMAS, NÃO TE APAVORES\nQUANDO A LUTA SURGIR,\nLEMBRA QUE O TEU DEUS\nÉ MAIS FORTE E PODE TE AJUDAR;\nTÃO-SOMENTE ESFORÇA-TE.\n\nCORO (2X)\nELE CONTIGO IRÁ\nPOR ONDE QUER QUE VÁS,\nSUA BOA MÃO TE SUSTERÁ;\nO SENHOR CONTIGO ESTARÁ.\n\nFINAL: \nTÃO-SOMENTE ESFORÇA-TE.', 'ESFORÇA-TE, TEM BOM ÂNIMO! ASSIM DIZ O SENHOR TEU DEUS, O DEUS DE ISRAEL, O DEUS QUE TE AMA, O DEUS QUE CONTIGO SEMPRE ESTÁ; TÃO-SOMENTE ESFORÇA-TE. ELE CONTIGO IRÁ POR ONDE QUER QUE VÁS, SUA BOA MÃO TE SUSTERÁ; O SENHOR CONTIGO ESTARÁ. NÃO TEMAS, NÃO TE APAVORES QUANDO A LUTA SURGIR, LEMBRA QUE O TEU DEUS É MAIS FORTE E PODE TE AJUDAR; TÃO-SOMENTE ESFORÇA-TE. ELE CONTIGO IRÁ POR ONDE QUER QUE VÁS, SUA BOA MÃO TE SUSTERÁ; O SENHOR CONTIGO ESTARÁ.  TÃO-SOMENTE ESFORÇA-TE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('369', '369 - QUANDO MAIS NINGUÉM TE COMPREENDE', 'QUANDO MAIS NINGUÉM TE COMPREENDE,\nTEUS AMIGOS TE ABANDONARAM,\nJESUS TE ABRAÇA E DIZ:\n“ESTOU AQUI PRA TE OUVIR”.\n\nCORO\n“FALA, FILHO MEU,\nCONTA PARA MIM OS TEUS PROBLEMAS,\nCLAMA A MIM EM ORAÇÃO, \nCOM SINCERIDADE E RESPONDER-TE-EI.\nFALAREI CONTIGO, SOU O DEUS DA PROVISÃO, \nSOU JESUS, A TUA SOLUÇÃO”.\n\nINSTRUMENTOS\n\n“QUANDO INVOCARES O MEU NOME,\nDE MADRUGADA ME BUSCARES\nE DE JOELHOS TU ORARES, \nESTAREI ATENTO A TE OUVIR.”\n\nCORO (2X)\n“FALA, FILHO MEU,\nCONTA PARA MIM OS TEUS PROBLEMAS,\nCLAMA A MIM EM ORAÇÃO, \nCOM SINCERIDADE E RESPONDER-TE-EI.\nFALAREI CONTIGO, SOU O DEUS DA PROVISÃO, \nSOU JESUS, A TUA SOLUÇÃO”.\n\nFINAL: \n“SOU JESUS, A TUA SOLUÇÃO”.', 'QUANDO MAIS NINGUÉM TE COMPREENDE, TEUS AMIGOS TE ABANDONARAM, JESUS TE ABRAÇA E DIZ: ESTOU AQUI PRA TE OUVIR. CORO FALA, FILHO MEU, CONTA PARA MIM OS TEUS PROBLEMAS, CLAMA A MIM EM ORAÇÃO, COM SINCERIDADE E RESPONDER-TE-EI. FALAREI CONTIGO, SOU O DEUS DA PROVISÃO, SOU JESUS, A TUA SOLUÇÃO. INSTRUMENTOS QUANDO INVOCARES O MEU NOME, DE MADRUGADA ME BUSCARES E DE JOELHOS TU ORARES, ESTAREI ATENTO A TE OUVIR. FALA, FILHO MEU, CONTA PARA MIM OS TEUS PROBLEMAS, CLAMA A MIM EM ORAÇÃO, COM SINCERIDADE E RESPONDER-TE-EI. FALAREI CONTIGO, SOU O DEUS DA PROVISÃO, SOU JESUS, A TUA SOLUÇÃO.  SOU JESUS, A TUA SOLUÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('370', '370 - JESUS É A ROCHA INABALÁVEL', 'JESUS É A ROCHA INABALÁVEL,\nREFÚGIO E FORTALEZA DOS FIÉIS;\nMESMO QUE TEU CHORO \nDURE TODA A NOITE, \nMAS AO AMANHECER A ALEGRIA\nENCHERÁ TEU CORAÇÃO, \nPORQUE JESUS TE FALARÁ: \n“NÃO CHORES, ÉS MEU FILHO \nMUITO AMADO”.\n\nAINDA QUE AS PROVAS SEJAM MUITAS,\nNÃO TEMAS, CONFIA EM JESUS;\nMESMO QUE TEU PRANTO SE CONVERTA EM DOR,\nMAS AO ROMPER O DIA A FELICIDADE\nENCHERÁ TEU CORAÇÃO,\nPORQUE JESUS TE FALARÁ: \n“NÃO CHORES, ÉS MEU FILHO \nMUITO AMADO”.\n\nJESUS É A ROCHA INABALÁVEL,\nREFÚGIO E FORTALEZA DOS FIÉIS;\nAINDA QUE AS PROVAS SEJAM MUITAS,\nNÃO TEMAS, CONFIA EM JESUS. \n\nFINAL: NÃO TEMAS, CONFIA EM JESUS.', 'JESUS É A ROCHA INABALÁVEL, REFÚGIO E FORTALEZA DOS FIÉIS; MESMO QUE TEU CHORO DURE TODA A NOITE, MAS AO AMANHECER A ALEGRIA ENCHERÁ TEU CORAÇÃO, PORQUE JESUS TE FALARÁ: NÃO CHORES, ÉS MEU FILHO MUITO AMADO. AINDA QUE AS PROVAS SEJAM MUITAS, NÃO TEMAS, CONFIA EM JESUS; MESMO QUE TEU PRANTO SE CONVERTA EM DOR, MAS AO ROMPER O DIA A FELICIDADE ENCHERÁ TEU CORAÇÃO, PORQUE JESUS TE FALARÁ: NÃO CHORES, ÉS MEU FILHO MUITO AMADO. JESUS É A ROCHA INABALÁVEL, REFÚGIO E FORTALEZA DOS FIÉIS; AINDA QUE AS PROVAS SEJAM MUITAS, NÃO TEMAS, CONFIA EM JESUS.  NÃO TEMAS, CONFIA EM JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('371', '371 - TODA PROMESSA', 'TODA PROMESSA, TODA PALAVRA QUE O SENHOR FALOU \nSE CUMPRIRÁ, EM BREVE VIRÁ O MEU SALVADOR; \nNAS NUVENS DESCENDO, COM PODER E GRAÇA \nVEREI JESUS, MOLDADO O MEU CORPO, \nCOMO O DOS ANJOS SERÁ.\nEM BREVE ESTAREMOS NO CÉU, COM OS ANJOS E O SALVADOR E JUNTOS UNIDOS, \nLOUVAREMOS AO SENHOR.\n\nCORO\nESPERA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) \nE AS TUAS FORÇAS SE RENOVARÃO (SIM, RENOVARÃO)\nCONFIA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL)\nE SERÁS COMO O MONTE DE SIÃO\nQUE NÃO SE ABALA.\n\nA SUA PALAVRA É DIGNA DE TODA ACEITAÇÃO \nE NÃO VOLTARÁ VAZIA, ANTES TUDO CUMPRIRÁ, \nPOIS O SENHOR DISSE E FARÁ.\nBEM-AVENTURADOS VÓS QUE AGORA CHORAIS, \nPOIS É GRANDE A ALEGRIA\nQUE O SENHOR HÁ DE VOS DAR;\nBEM-AVENTURADOS VÓS QUE AGORA \nSOFREIS POIS SEREIS CONSOLADOS\nPELO GRANDE AMOR DO DEUS DE ISRAEL.\n\nCORO (2X)\nESPERA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) \nE AS TUAS FORÇAS SE RENOVARÃO (SIM, RENOVARÃO)\nCONFIA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL)\nE SERÁS COMO O MONTE DE SIÃO\nQUE NÃO SE ABALA.\n\nFINAL: \nCOMO O MONTE DE SIÃO \nQUE NÃO SE ABALA.', 'TODA PROMESSA, TODA PALAVRA QUE O SENHOR FALOU SE CUMPRIRÁ, EM BREVE VIRÁ O MEU SALVADOR; NAS NUVENS DESCENDO, COM PODER E GRAÇA VEREI JESUS, MOLDADO O MEU CORPO, COMO O DOS ANJOS SERÁ. EM BREVE ESTAREMOS NO CÉU, COM OS ANJOS E O SALVADOR E JUNTOS UNIDOS, LOUVAREMOS AO SENHOR. CORO ESPERA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E AS TUAS FORÇAS SE RENOVARÃO (SIM, RENOVARÃO) CONFIA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E SERÁS COMO O MONTE DE SIÃO QUE NÃO SE ABALA. A SUA PALAVRA É DIGNA DE TODA ACEITAÇÃO E NÃO VOLTARÁ VAZIA, ANTES TUDO CUMPRIRÁ, POIS O SENHOR DISSE E FARÁ. BEM-AVENTURADOS VÓS QUE AGORA CHORAIS, POIS É GRANDE A ALEGRIA QUE O SENHOR HÁ DE VOS DAR; BEM-AVENTURADOS VÓS QUE AGORA SOFREIS POIS SEREIS CONSOLADOS PELO GRANDE AMOR DO DEUS DE ISRAEL. ESPERA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E AS TUAS FORÇAS SE RENOVARÃO (SIM, RENOVARÃO) CONFIA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E SERÁS COMO O MONTE DE SIÃO QUE NÃO SE ABALA.  COMO O MONTE DE SIÃO QUE NÃO SE ABALA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('372', '372 - MUITOS SÃO OS QUE CONFIAM', 'MUITOS SÃO OS QUE CONFIAM EM SUA FORÇA \nE SEU SABER E POR ISSO SE ENCURVAM, \nCAEM SEM PODER VENCER;\nMAS EU FAÇO MENÇÃO DO TEU NOME,\nSÓ EM TI CONFIAREI, E NO DIA DA PELEJA \nE ANGÚSTIA COMIGO ESTARÁS, MEU REI!\n\nLEMBRA-TE DA MINHA OFERTA,\nOUVE A MINHA PETIÇÃO\nE EU ME ALEGRAREI COM JÚBILO\nPELA TUA SALVAÇÃO;\nEM TEU NOME ARVORAREI BANDEIRAS, \nPOIS COMIGO ESTÁS, SENHOR, EM TEU \nNOME SALTAREI BARREIRAS\nE SEREI UM VENCEDOR!\nBIS', 'MUITOS SÃO OS QUE CONFIAM EM SUA FORÇA E SEU SABER E POR ISSO SE ENCURVAM, CAEM SEM PODER VENCER; MAS EU FAÇO MENÇÃO DO TEU NOME, SÓ EM TI CONFIAREI, E NO DIA DA PELEJA E ANGÚSTIA COMIGO ESTARÁS, MEU REI! LEMBRA-TE DA MINHA OFERTA, OUVE A MINHA PETIÇÃO E EU ME ALEGRAREI COM JÚBILO PELA TUA SALVAÇÃO; EM TEU NOME ARVORAREI BANDEIRAS, POIS COMIGO ESTÁS, SENHOR, EM TEU NOME SALTAREI BARREIRAS E SEREI UM VENCEDOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('373', '373 - SE UM DIA DEUS PROMETEU', 'SE UM DIA DEUS PROMETEU \nA VITÓRIA TE CONCEDER, \nSE IMPOSSÍVEL PARECE \nSER TUA BÊNÇÃO ACONTECER,\nSABE QUE O QUE ELE PROMETEU \nÉ PODEROSO PRA CUMPRIR, \nO TEMPO DE DEUS VAI CHEGAR, \nELE NÃO FALHARÁ.\n\nCORO\nSOMENTE CRER (SOMENTE CRER)\nE DESCANSAR (E DESCANSAR)\nCONFIA EM DEUS E TUDO MAIS (E TUDO MAIS)\nELE FARÁ, NÃO TARDARÁ.\n\nSE NÃO PODES IMAGINAR \nCOMO DEUS PODE OPERAR, \nDEIXA DIANTE DO ALTAR\nPRA QUE DEUS POSSA TE PROVAR \nQUE NÃO HÁ NADA IMPOSSÍVEL \nQUE DEUS NÃO POSSA RESOLVER, \nO SEU ANJO ENVIARÁ \nE A VITÓRIA ELE TRARÁ.\n\nCORO\nSOMENTE CRER (SOMENTE CRER)\nE DESCANSAR (E DESCANSAR)\nCONFIA EM DEUS E TUDO MAIS (E TUDO MAIS)\nELE FARÁ, NÃO TARDARÁ.\n\nFINAL: ELE FARÁ.', 'SE UM DIA DEUS PROMETEU A VITÓRIA TE CONCEDER, SE IMPOSSÍVEL PARECE SER TUA BÊNÇÃO ACONTECER, SABE QUE O QUE ELE PROMETEU É PODEROSO PRA CUMPRIR, O TEMPO DE DEUS VAI CHEGAR, ELE NÃO FALHARÁ. CORO SOMENTE CRER (SOMENTE CRER) E DESCANSAR (E DESCANSAR) CONFIA EM DEUS E TUDO MAIS (E TUDO MAIS) ELE FARÁ, NÃO TARDARÁ. SE NÃO PODES IMAGINAR COMO DEUS PODE OPERAR, DEIXA DIANTE DO ALTAR PRA QUE DEUS POSSA TE PROVAR QUE NÃO HÁ NADA IMPOSSÍVEL QUE DEUS NÃO POSSA RESOLVER, O SEU ANJO ENVIARÁ E A VITÓRIA ELE TRARÁ. CORO SOMENTE CRER (SOMENTE CRER) E DESCANSAR (E DESCANSAR) CONFIA EM DEUS E TUDO MAIS (E TUDO MAIS) ELE FARÁ, NÃO TARDARÁ.  ELE FARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('374', '374 - QUERO TOCAR EM TUAS VESTES, SENHOR', 'QUERO TOCAR EM TUAS VESTES, SENHOR,\nCOMO AQUELA MULHER TOCOU.\nE QUE SAIA, SENHOR, VIRTUDE,\nPRA QUE EU VEJA O TEU PODER.\nBIS\n\nCORO\nCREIO, SENHOR (CREIO, SENHOR)\nQUE ESTÁS AQUI E NÃO HÁ IMPOSSÍVEL PRA TI,\n(IMPOSSÍVEL NÃO HÁ)\nBASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ)\nPRA QUE EU VEJA O TEU PODER.\n\nINSTRUMENTOS\n\nSE VOCÊ ESTÁ EM AFLIÇÕES\nE VÊ SEUS PROBLEMAS SEM SOLUÇÃO,\nO SENHOR AGORA AO SEU LADO ESTÁ,\nBASTA EM SUAS VESTES TOCAR.\n\nCORO (2X)\nCREIO, SENHOR (CREIO, SENHOR)\nQUE ESTÁS AQUI E NÃO HÁ IMPOSSÍVEL PRA TI,\n(IMPOSSÍVEL NÃO HÁ)\nBASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ)\nPRA QUE EU VEJA O TEU PODER.\n\nFINAL: \nBASTA, SENHOR, \nUMA PALAVRA DIZER (UMA SÓ) \nPRA QUE EU VEJA O TEU PODER.', 'QUERO TOCAR EM TUAS VESTES, SENHOR, COMO AQUELA MULHER TOCOU. E QUE SAIA, SENHOR, VIRTUDE, PRA QUE EU VEJA O TEU PODER. CORO CREIO, SENHOR (CREIO, SENHOR) QUE ESTÁS AQUI E NÃO HÁ IMPOSSÍVEL PRA TI, (IMPOSSÍVEL NÃO HÁ) BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER. INSTRUMENTOS SE VOCÊ ESTÁ EM AFLIÇÕES E VÊ SEUS PROBLEMAS SEM SOLUÇÃO, O SENHOR AGORA AO SEU LADO ESTÁ, BASTA EM SUAS VESTES TOCAR. CREIO, SENHOR (CREIO, SENHOR) QUE ESTÁS AQUI E NÃO HÁ IMPOSSÍVEL PRA TI, (IMPOSSÍVEL NÃO HÁ) BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER. BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('375', '375 - OS SEGREDOS DO TEU CORAÇÃO', 'OS SEGREDOS DO TEU CORAÇÃO\nDEUS CONHECE, DEUS CONHECE;\nTUA DOR, TUA INDAGAÇÃO DEUS CONHECE, \nSIM, DEUS CONHECE.\n\nCORO\nCRISTO QUER SEU AMOR REVELAR,\nOPERAR MARAVILHAS NESTE LUGAR;\nVIVO ESTÁ JESUS, PRONTO A AJUDAR\nAQUELE QUE QUER SUA LUZ.\n\nO QUE TU NÃO CONSEGUES FAZER\nCRISTO PODE, TUDO PODE;\nIMPOSSÍVEIS NÃO HÁ PRA QUEM CRER,\nCRISTO PODE, SIM, TUDO PODE.\n\nCORO\nCRISTO QUER SEU AMOR REVELAR,\nOPERAR MARAVILHAS NESTE LUGAR;\nVIVO ESTÁ JESUS, PRONTO A AJUDAR\nAQUELE QUE QUER SUA LUZ.', 'OS SEGREDOS DO TEU CORAÇÃO DEUS CONHECE, DEUS CONHECE; TUA DOR, TUA INDAGAÇÃO DEUS CONHECE, SIM, DEUS CONHECE. CORO CRISTO QUER SEU AMOR REVELAR, OPERAR MARAVILHAS NESTE LUGAR; VIVO ESTÁ JESUS, PRONTO A AJUDAR AQUELE QUE QUER SUA LUZ. O QUE TU NÃO CONSEGUES FAZER CRISTO PODE, TUDO PODE; IMPOSSÍVEIS NÃO HÁ PRA QUEM CRER, CRISTO PODE, SIM, TUDO PODE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('376', '376 - O SENHOR TE GUIARÁ', 'O SENHOR TE GUIARÁ CONTINUAMENTE \nE FARTARÁ TUA  ALMA EM LUGARES \nSECOS E FORTIFICARÁ TEUS OSSOS.\n\nE SERÁS COMO UM JARDIM REGADO\nE COMO UM MANANCIAL CUJAS ÁGUAS \nNUNCA FALTAM, NUNCA FALTAM,\nE SERÁS COMO UM MANANCIAL\nCUJAS ÁGUAS NUNCA FALTAM.\n\nO SENHOR TE GUIARÁ CONTINUAMENTE \nE FARTARÁ TUA  ALMA EM LUGARES \nSECOS E FORTIFICARÁ TEUS OSSOS.\n\nE SERÁS COMO UM JARDIM REGADO\nE COMO UM MANANCIAL CUJAS ÁGUAS \nNUNCA FALTAM, NUNCA FALTAM,\nE SERÁS COMO UM MANANCIAL\nCUJAS ÁGUAS NUNCA FALTAM.', 'O SENHOR TE GUIARÁ CONTINUAMENTE E FARTARÁ TUA ALMA EM LUGARES SECOS E FORTIFICARÁ TEUS OSSOS. E SERÁS COMO UM JARDIM REGADO E COMO UM MANANCIAL CUJAS ÁGUAS NUNCA FALTAM, NUNCA FALTAM, E SERÁS COMO UM MANANCIAL CUJAS ÁGUAS NUNCA FALTAM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('377', '377 - DEUS É MINHA ROCHA', 'DEUS É MINHA ROCHA, MEU REFÚGIO E FORTALEZA.\nDO ALTÍSSIMO SE OUVIU UMA GRANDE VOZ.\nFLECHAS DISPARADAS E O INIMIGO EM RETIRADA.\nESTENDEU DO ALTO A SUA PODEROSA MÃO.\nPOIS QUEM É DEUS, SENÃO NOSSO DEUS?\nPOIS QUEM É DEUS, SENÃO O SENHOR?\n\nCONDUZIU-ME PARA UM LUGAR MAIS ESPAÇOSO,\nE RETRIBUI-ME SEGUNDO A SUA JUSTIÇA.\nCELEBRAREI, POIS, ENTRE AS NAÇÕES Ó SENHOR,\nE CANTAREI LOUVOR AO TEU NOME.\nPOIS QUEM É DEUS, SENÃO NOSSO DEUS?\nPOIS QUEM É DEUS, SENÃO O SENHOR?\n\nROCHA ETERNA (ROCHA ETERNA) \nQUE FIRMA MEUS PASSOS (TU ÉS) \nPEDRA DE ESQUINA, ABRIGO SEGURO.\nBIS', 'DEUS É MINHA ROCHA, MEU REFÚGIO E FORTALEZA. DO ALTÍSSIMO SE OUVIU UMA GRANDE VOZ. FLECHAS DISPARADAS E O INIMIGO EM RETIRADA. ESTENDEU DO ALTO A SUA PODEROSA MÃO. POIS QUEM É DEUS, SENÃO NOSSO DEUS? POIS QUEM É DEUS, SENÃO O SENHOR? CONDUZIU-ME PARA UM LUGAR MAIS ESPAÇOSO, E RETRIBUI-ME SEGUNDO A SUA JUSTIÇA. CELEBRAREI, POIS, ENTRE AS NAÇÕES Ó SENHOR, E CANTAREI LOUVOR AO TEU NOME. POIS QUEM É DEUS, SENÃO NOSSO DEUS? POIS QUEM É DEUS, SENÃO O SENHOR? ROCHA ETERNA (ROCHA ETERNA) QUE FIRMA MEUS PASSOS (TU ÉS) PEDRA DE ESQUINA, ABRIGO SEGURO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('378', '378 - CONFIA E ORA', 'CONFIA E ORA, CERTAMENTE\nO SENHOR TE OUVIRÁ.\nENTREGA AGORA, EM SUAS MÃOS,\nE TUDO VAI PASSAR, TUDO ELE MUDARÁ.\nE TUDO VAI PASSAR, TUDO ELE MUDARÁ.\n\nO TEU DEUS PODEROSO É\nEM SUAS MÃOS VOCÊ AGORA ESTÁ.\nMESMO EM TEMPESTADES,\nOU NA FÚRIA DO MAR,\nO SENHOR TE ESCONDERÁ.\n\nO TEU DEUS ESTARÁ CONTIGO \nONDE QUER QUE FOR\nELE É FIEL E NÃO FALHARÁ. \nCONFIA NO SENHOR,\nELE É FIEL E NÃO FALHARÁ,\nCONFIA NO SENHOR.\n\nINSTRUMENTOS\n\nFINAL: \nCONFIA E ORA,\nCERTAMENTE O SENHOR TE OUVIRÁ.\nENTREGA AGORA, EM SUAS MÃOS,\nE TUDO VAI PASSAR, \nTUDO ELE MUDARÁ. \nE TUDO VAI PASSAR.\nBIS', 'CONFIA E ORA, CERTAMENTE O SENHOR TE OUVIRÁ. ENTREGA AGORA, EM SUAS MÃOS, E TUDO VAI PASSAR, TUDO ELE MUDARÁ. E TUDO VAI PASSAR, TUDO ELE MUDARÁ. O TEU DEUS PODEROSO É EM SUAS MÃOS VOCÊ AGORA ESTÁ. MESMO EM TEMPESTADES, OU NA FÚRIA DO MAR, O SENHOR TE ESCONDERÁ. O TEU DEUS ESTARÁ CONTIGO ONDE QUER QUE FOR ELE É FIEL E NÃO FALHARÁ. CONFIA NO SENHOR, ELE É FIEL E NÃO FALHARÁ, CONFIA NO SENHOR. INSTRUMENTOS CONFIA E ORA, CERTAMENTE O SENHOR TE OUVIRÁ. ENTREGA AGORA, EM SUAS MÃOS, E TUDO VAI PASSAR, TUDO ELE MUDARÁ. E TUDO VAI PASSAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('379', '379 - ALGEMADO POR UM PESO', 'ALGEMADO POR UM PESO,\nOH! QUÃO TRISTE EU ANDEI,\nTÉ SENTIR A MÃO DE CRISTO,\nNÃO SOU MAIS COMO ERA, EU SEI.\n\nCORO\nTOCOU-ME, JESUS, TOCOU-ME,\nDE PAZ ELE ENCHEU MEU CORAÇÃO.\nQUANDO O SENHOR JESUS ME TOCOU,\nLIVROU-ME DA ESCURIDÃO.\n\nDESDE QUE ENCONTREI A CRISTO,\nE SENTI SEU TERNO AMOR,\nTENHO ACHADO PAZ E VIDA,\nPRA SEMPRE CANTAREI EM SEU LOUVOR.\n\nCORO\nTOCOU-ME, JESUS, TOCOU-ME,\nDE PAZ ELE ENCHEU MEU CORAÇÃO.\nQUANDO O SENHOR JESUS ME TOCOU,\nLIVROU-ME DA ESCURIDÃO.', 'ALGEMADO POR UM PESO, OH! QUÃO TRISTE EU ANDEI, TÉ SENTIR A MÃO DE CRISTO, NÃO SOU MAIS COMO ERA, EU SEI. CORO TOCOU-ME, JESUS, TOCOU-ME, DE PAZ ELE ENCHEU MEU CORAÇÃO. QUANDO O SENHOR JESUS ME TOCOU, LIVROU-ME DA ESCURIDÃO. DESDE QUE ENCONTREI A CRISTO, E SENTI SEU TERNO AMOR, TENHO ACHADO PAZ E VIDA, PRA SEMPRE CANTAREI EM SEU LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('380', '380 - POSSO SENTIR O SEU TOQUE EM MIM  (GLORIFICA)', 'POSSO SENTIR O SEU TOQUE EM MIM,\nSINTO O SEU CALOR AQUECENDO O CORAÇÃO.\nPOSSO OUVIR SUA VOZ A SOAR:\n“MEU SERVO, NÃO TEMAS!\nVOU TE AJUDAR. QUANDO AS LUTAS CHEGAREM, \nNÃO OLHES PARA TRÁS OLHA PARA O ALTO E A VITÓRIA ALCANÇARÁS!”\n\nCORO \nÉ DO ALTO QUE VÊM NOSSAS VITÓRIAS,\nCRISTO ESTÁ PRONTO A TE ESCUTAR.\nNÃO ACHES QUE TUDO TERMINOU,\nPOIS DEUS TRAVA UMA BATALHA EM TEU FAVOR!\nGLORIFICA, SÓ GLORIFICA!\nERGUE A VOZ E AGRADECE AO REI SENHOR.\nGLORIFICA, SÓ GLORIFICA!\nERGUE A VOZ E AGRADECE AO REI SENHOR.\n\nCORO \nÉ DO ALTO QUE VÊM NOSSAS VITÓRIAS,\nCRISTO ESTÁ PRONTO A TE ESCUTAR.\nNÃO ACHES QUE TUDO TERMINOU,\nPOIS DEUS TRAVA UMA BATALHA EM TEU FAVOR!\nGLORIFICA, SÓ GLORIFICA!\nERGUE A VOZ E AGRADECE AO REI SENHOR.\nGLORIFICA, SÓ GLORIFICA!\nERGUE A VOZ E AGRADECE AO REI SENHOR.\n\nFINAL: GLORIFICA!', 'POSSO SENTIR O SEU TOQUE EM MIM, SINTO O SEU CALOR AQUECENDO O CORAÇÃO. POSSO OUVIR SUA VOZ A SOAR: MEU SERVO, NÃO TEMAS! VOU TE AJUDAR. QUANDO AS LUTAS CHEGAREM, NÃO OLHES PARA TRÁS OLHA PARA O ALTO E A VITÓRIA ALCANÇARÁS! CORO É DO ALTO QUE VÊM NOSSAS VITÓRIAS, CRISTO ESTÁ PRONTO A TE ESCUTAR. NÃO ACHES QUE TUDO TERMINOU, POIS DEUS TRAVA UMA BATALHA EM TEU FAVOR! GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. CORO É DO ALTO QUE VÊM NOSSAS VITÓRIAS, CRISTO ESTÁ PRONTO A TE ESCUTAR. NÃO ACHES QUE TUDO TERMINOU, POIS DEUS TRAVA UMA BATALHA EM TEU FAVOR! GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR.  GLORIFICA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('381', '381 - MEU DEUS VENCE BATALHAS', 'MEU DEUS VENCE BATALHAS,\nMEU DEUS QUEBRA MURALHAS,\nE QUANDO EU ESTOU A FRAQUEJAR,\nSUAS MÃOS ESTÃO A ME SUSTENTAR.\n\nMEU DEUS É MUITO FORTE,\nO SEU NOME É EMANUEL,\nE QUANDO TUDO PARECE MAL,\nA VITÓRIA EU SEI QUE ELE ME DARÁ.\n\nCORO\nCLAMEI NA ANGÚSTIA,\nE OUVIU O MEU CLAMOR.\nMEU DEUS ESTÁ COMIGO,\nNÃO TEMO O PERIGO,\nELE É O MEU SALVADOR.\nNÃO SE COMPARA A PRATA,\nNEM SE COMPARA O OURO,\nJESUS É O MEU TESOURO,\nCOM ELE VOU VENCER.\n\nMEU DEUS VENCE BATALHAS,\nMEU DEUS QUEBRA MURALHAS,\nE QUANDO EU ESTOU A FRAQUEJAR,\nSUAS MÃOS ESTÃO A ME SUSTENTAR.\n\nMEU DEUS É MUITO FORTE,\nO SEU NOME É EMANUEL,\nE QUANDO TUDO PARECE MAL,\nA VITÓRIA EU SEI QUE ELE ME DARÁ.\n\nCORO \nCLAMEI NA ANGÚSTIA,\nE OUVIU O MEU CLAMOR.\nMEU DEUS ESTÁ COMIGO,\nNÃO TEMO O PERIGO,\nELE É O MEU SALVADOR.\nNÃO SE COMPARA A PRATA,\nNEM SE COMPARA O OURO,\nJESUS É O MEU TESOURO,\nCOM ELE VOU VENCER.\n\nCORO \nCLAMEI NA ANGÚSTIA, E OUVIU O MEU CLAMOR.\nMEU DEUS ESTÁ COMIGO, NÃO TEMO O PERIGO,\nELE É O MEU SALVADOR.\nNÃO SE COMPARA A PRATA, NEM SE COMPARA O OURO,\nJESUS É O MEU TESOURO, COM ELE VOU VENCER.\n\nFINAL: \nMEU DEUS VENCE BATALHAS.', 'MEU DEUS VENCE BATALHAS, MEU DEUS QUEBRA MURALHAS, E QUANDO EU ESTOU A FRAQUEJAR, SUAS MÃOS ESTÃO A ME SUSTENTAR. MEU DEUS É MUITO FORTE, O SEU NOME É EMANUEL, E QUANDO TUDO PARECE MAL, A VITÓRIA EU SEI QUE ELE ME DARÁ. CORO CLAMEI NA ANGÚSTIA, E OUVIU O MEU CLAMOR. MEU DEUS ESTÁ COMIGO, NÃO TEMO O PERIGO, ELE É O MEU SALVADOR. NÃO SE COMPARA A PRATA, NEM SE COMPARA O OURO, JESUS É O MEU TESOURO, COM ELE VOU VENCER. CORO CLAMEI NA ANGÚSTIA, E OUVIU O MEU CLAMOR. MEU DEUS ESTÁ COMIGO, NÃO TEMO O PERIGO, ELE É O MEU SALVADOR. NÃO SE COMPARA A PRATA, NEM SE COMPARA O OURO, JESUS É O MEU TESOURO, COM ELE VOU VENCER.  MEU DEUS VENCE BATALHAS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('382', '382 - JÁ CHEGUEI ATÉ AQUI (VITÓRIAS)', 'JÁ CHEGUEI ATÉ AQUI, EU NÃO POSSO DESISTIR,\nDEVO ACHAR FORÇAS PRA ANDAR.\nAS LUTAS VIERAM, MAS TAMBÉM PASSARAM,\nVENDAVAIS TENTARAM ME DERRUBAR.\n\nMAS BUSQUEI A CRISTO, MEU FIEL AMIGO,\nQUE ESTEVE COMIGO TODO DIA\nMESMO QUE CHORANDO, \nMESMO QUE SOFRENDO, JÁ ESTAVA CERTO\nQUE A VITÓRIA GANHARIA NO AMANHÃ.\n\nINSTRUMENTOS\n\nJÁ CHEGAMOS ATÉ AQUI, \nNÃO PODEMOS DESISTIR,\nVAMOS ACHAR FORÇAS PRA ANDAR.\nAS LUTAS VIERAM, MAS TAMBÉM PASSARAM,\nVENDAVAIS TENTARAM NOS DERRUBAR.\n\nNADA NOS FALTOU, TUDO DEUS PROVEU, \nNOS DEU COROA PRA GUARDAR. \nE FEZ TUDO NOVO COM AMOR GLORIOSO, \nE NOS DEU VITÓRIA ONTEM, \nHOJE E NOS DARÁ NO AMANHÃ.\nBIS', 'JÁ CHEGUEI ATÉ AQUI, EU NÃO POSSO DESISTIR, DEVO ACHAR FORÇAS PRA ANDAR. AS LUTAS VIERAM, MAS TAMBÉM PASSARAM, VENDAVAIS TENTARAM ME DERRUBAR. MAS BUSQUEI A CRISTO, MEU FIEL AMIGO, QUE ESTEVE COMIGO TODO DIA MESMO QUE CHORANDO, MESMO QUE SOFRENDO, JÁ ESTAVA CERTO QUE A VITÓRIA GANHARIA NO AMANHÃ. INSTRUMENTOS JÁ CHEGAMOS ATÉ AQUI, NÃO PODEMOS DESISTIR, VAMOS ACHAR FORÇAS PRA ANDAR. AS LUTAS VIERAM, MAS TAMBÉM PASSARAM, VENDAVAIS TENTARAM NOS DERRUBAR. NADA NOS FALTOU, TUDO DEUS PROVEU, NOS DEU COROA PRA GUARDAR. E FEZ TUDO NOVO COM AMOR GLORIOSO, E NOS DEU VITÓRIA ONTEM, HOJE E NOS DARÁ NO AMANHÃ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('383', '383 - LÁ ONDE EU NÃO POSSO VER (A ORAÇÃO)', 'LÁ ONDE EU NÃO POSSO VER, \nONDE NÃO POSSO ESTAR, \nTEM PESSOAS QUE EU NÃO \nPOSSO AJUDAR, \n\nSEUS PROBLEMAS RESOLVER, \nMAS EU SEI QUE POSSO ORAR.\n\nM\n\nT\n\nMEUS TEMORES E AFLIÇÕES, \nMINHA DOR, DECEPÇÕES. \n\nAPRENDI A COLOCAR \nMINHA VIDA SOBRE O ALTAR, \nENTÃO SEI QUE POSSO ORAR \nE EM DEUS POSSO CONFIAR.\n\nH\n\nT\n\nCORO\nA ORAÇÃO VAI AONDE EU NÃO POSSO IR,\nDEUS PROMETEU ME SOCORRER \nQUANDO EU PEDIR.\nA ORAÇÃO DOS QUE AMAM A DEUS\nPODE MUITO EM SEU PODER,\nE QUANDO ESTOU EM ORAÇÃO,\nTUDO PODE ACONTECER.\n\nSOU LAVADO E REMIDO, \nNÃO PRECISO TEMER \n\nPOIS O SANGUE DE JESUS \nÉ QUE TEM O PODER, \nDE ME FAZER ENTRAR \nNA PRESENÇA DE DEUS,\nNÃO HÁ MAIS IMPEDIMENTO\nDE FICAR MAIS PERTO DE DEUS.\n\nM\n\nH\n\nT\n\nCORO (2X)\nA ORAÇÃO VAI AONDE EU NÃO POSSO IR,\nDEUS PROMETEU ME SOCORRER \nQUANDO EU PEDIR.\nA ORAÇÃO DOS QUE AMAM A DEUS\nPODE MUITO EM SEU PODER,\nE QUANDO ESTOU EM ORAÇÃO,\nTUDO PODE ACONTECER.', 'LÁ ONDE EU NÃO POSSO VER, ONDE NÃO POSSO ESTAR, TEM PESSOAS QUE EU NÃO POSSO AJUDAR, SEUS PROBLEMAS RESOLVER, MAS EU SEI QUE POSSO ORAR. MEUS TEMORES E AFLIÇÕES, MINHA DOR, DECEPÇÕES. APRENDI A COLOCAR MINHA VIDA SOBRE O ALTAR, ENTÃO SEI QUE POSSO ORAR E EM DEUS POSSO CONFIAR. CORO A ORAÇÃO VAI AONDE EU NÃO POSSO IR, DEUS PROMETEU ME SOCORRER QUANDO EU PEDIR. A ORAÇÃO DOS QUE AMAM A DEUS PODE MUITO EM SEU PODER, E QUANDO ESTOU EM ORAÇÃO, TUDO PODE ACONTECER. SOU LAVADO E REMIDO, NÃO PRECISO TEMER POIS O SANGUE DE JESUS É QUE TEM O PODER, DE ME FAZER ENTRAR NA PRESENÇA DE DEUS, NÃO HÁ MAIS IMPEDIMENTO DE FICAR MAIS PERTO DE DEUS. A ORAÇÃO VAI AONDE EU NÃO POSSO IR, DEUS PROMETEU ME SOCORRER QUANDO EU PEDIR. A ORAÇÃO DOS QUE AMAM A DEUS PODE MUITO EM SEU PODER, E QUANDO ESTOU EM ORAÇÃO, TUDO PODE ACONTECER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('384', '384 - BEM SEI QUE TUDO PODES', 'BEM SEI QUE TUDO PODES, MEU AMADO.\nNENHUM DOS TEUS PLANOS É FRUSTRADO.\nAINDA QUE COM PERDAS E COM DOR\nEU SEMPRE SEGUIREI A TI, SENHOR.\n\nOS TEUS CAMINHOS POSSO\nNÃO ENTENDER, SENHOR,\nMAS SEI QUE TUDO\nÉ VISANDO O MEU CRESCER.\nSE LUTAS E TRIBULAÇÕES\nEU TENHO QUE PASSAR,\nTE PEÇO FORÇAS PRA CONTINUAR.\n\nCONTINUAR A CRER E A CONFIAR,\nNO GRANDE AMOR QUE TENS, \nMEU PAI, POR MIM.\nO MEU DESEJO ETERNO É TE ADORAR,\nE AOS TEUS PÉS ME DERRAMAR SEM FIM.\n\nBEM SEI QUE TUDO PODES, MEU AMADO.\nNENHUM DOS TEUS PLANOS É FRUSTRADO.\nAINDA QUE COM PERDAS E COM DOR\nEU SEMPRE SEGUIREI A TI, SENHOR.\n\nOS TEUS CAMINHOS POSSO\nNÃO ENTENDER, SENHOR,\nMAS SEI QUE TUDO\nÉ VISANDO O MEU CRESCER.\nSE LUTAS E TRIBULAÇÕES\nEU TENHO QUE PASSAR,\nTE PEÇO FORÇAS PRA CONTINUAR.\n\nCONTINUAR A CRER E A CONFIAR,\nNO GRANDE AMOR QUE TENS, \nMEU PAI, POR MIM.\nO MEU DESEJO ETERNO É TE ADORAR,\nE AOS TEUS PÉS ME DERRAMAR SEM FIM.\n\nFINAL: \nE AOS TEUS PÉS ME DERRAMAR SEM FIM.', 'BEM SEI QUE TUDO PODES, MEU AMADO. NENHUM DOS TEUS PLANOS É FRUSTRADO. AINDA QUE COM PERDAS E COM DOR EU SEMPRE SEGUIREI A TI, SENHOR. OS TEUS CAMINHOS POSSO NÃO ENTENDER, SENHOR, MAS SEI QUE TUDO É VISANDO O MEU CRESCER. SE LUTAS E TRIBULAÇÕES EU TENHO QUE PASSAR, TE PEÇO FORÇAS PRA CONTINUAR. CONTINUAR A CRER E A CONFIAR, NO GRANDE AMOR QUE TENS, MEU PAI, POR MIM. O MEU DESEJO ETERNO É TE ADORAR, E AOS TEUS PÉS ME DERRAMAR SEM FIM. CONTINUAR A CRER E A CONFIAR, NO GRANDE AMOR QUE TENS, MEU PAI, POR MIM. O MEU DESEJO ETERNO É TE ADORAR, E AOS TEUS PÉS ME DERRAMAR SEM FIM.  E AOS TEUS PÉS ME DERRAMAR SEM FIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('385', '385 - QUÃO DIFÍCIL É, SENHOR  (FAZE-ME UM VENCEDOR)', 'QUÃO DIFÍCIL É, SENHOR,\nVIVER NESTE MUNDO DE AFLIÇÕES.\nÉ TEU AMOR QUE ME SUSTÉM\nE PELO TEU PODER.\n\nCORO (BIS)\nFAZE-ME UM VENCEDOR,\nFAZE-ME, SENHOR,\nUM INSTRUMENTO PRA TEU LOUVOR.\n\nE ADORAÇÃO ÀQUELE \nQUE É MAIS QUE VENCEDOR!\n\nINSTRUMENTOS\n\nSE PROVADO EU ESTOU\nE ESTAS PROVAÇÕES \nTENHO QUE PASSAR;\nEIS-ME AQUI, AJUDA-ME,\nAPROVADO QUERO SER.\n\nCORO (BIS)\nFAZE-ME UM VENCEDOR,\nFAZE-ME, SENHOR,\nUM INSTRUMENTO PRA TEU LOUVOR.\n\nFINAL: \nAQUELE QUE É MAIS \nQUE VENCEDOR!', 'QUÃO DIFÍCIL É, SENHOR, VIVER NESTE MUNDO DE AFLIÇÕES. É TEU AMOR QUE ME SUSTÉM E PELO TEU PODER. CORO  FAZE-ME UM VENCEDOR, FAZE-ME, SENHOR, UM INSTRUMENTO PRA TEU LOUVOR. E ADORAÇÃO ÀQUELE QUE É MAIS QUE VENCEDOR! INSTRUMENTOS SE PROVADO EU ESTOU E ESTAS PROVAÇÕES TENHO QUE PASSAR; EIS-ME AQUI, AJUDA-ME, APROVADO QUERO SER. CORO  FAZE-ME UM VENCEDOR, FAZE-ME, SENHOR, UM INSTRUMENTO PRA TEU LOUVOR.  AQUELE QUE É MAIS QUE VENCEDOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('386', '386 - QUAL O ADORNO DESTA VIDA?', 'QUAL O ADORNO DESTA VIDA?\nÉ O AMOR, É O AMOR;\nALEGRIA É CONCEDIDA\nPELO AMOR, PELO AMOR;\nÉ BENIGNO, É PACIENTE,\nNÃO SE TORNA MALDIZENTE, (BIS)\nESTE MEIGO AMOR.\n\nCOM SUSPEITAS NÃO SE ALCANÇA\nDOCE AMOR, DOCE AMOR;\nONDE HOUVER DESCONFIANÇA,\nAI DO AMOR, AI DO AMOR;\nPOIS AMEMOS A VERDADE,\nONDE REINA A FALSIDADE, (BIS)\nNÃO PERDURA O AMOR.\n\nNÃO TE IRRITES, MAS TOLERA\nCOM AMOR, COM AMOR;\nTUDO SOFRE, TUDO ESPERA\nPELO AMOR, PELO AMOR;\nPOIS MOSTREMOS TOLERÂNCIA,\nMUITAS VEZES A ARROGÂNCIA, (BIS)\nMURCHA E MATA O AMOR.\n\nÓ CRISTÃO, AO TEU VIZINHO\nMOSTRA AMOR, MOSTRA AMOR;\nO VALOR NÃO É MESQUINHO\nDESTE AMOR, DESTE AMOR;\nO SUPREMO DEUS NOS AMA;\nCRISTO PARA O CÉU NOS CHAMA, (BIS)\nONDE REINA O AMOR!', 'QUAL O ADORNO DESTA VIDA? É O AMOR, É O AMOR; ALEGRIA É CONCEDIDA PELO AMOR, PELO AMOR; É BENIGNO, É PACIENTE, NÃO SE TORNA MALDIZENTE,  ESTE MEIGO AMOR. COM SUSPEITAS NÃO SE ALCANÇA DOCE AMOR, DOCE AMOR; ONDE HOUVER DESCONFIANÇA, AI DO AMOR, AI DO AMOR; POIS AMEMOS A VERDADE, ONDE REINA A FALSIDADE,  NÃO PERDURA O AMOR. NÃO TE IRRITES, MAS TOLERA COM AMOR, COM AMOR; TUDO SOFRE, TUDO ESPERA PELO AMOR, PELO AMOR; POIS MOSTREMOS TOLERÂNCIA, MUITAS VEZES A ARROGÂNCIA,  MURCHA E MATA O AMOR. Ó CRISTÃO, AO TEU VIZINHO MOSTRA AMOR, MOSTRA AMOR; O VALOR NÃO É MESQUINHO DESTE AMOR, DESTE AMOR; O SUPREMO DEUS NOS AMA; CRISTO PARA O CÉU NOS CHAMA,  ONDE REINA O AMOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('387', '387 - SONDA-ME, Ó DEUS', 'SONDA-ME, Ó DEUS, \nPOIS VÊS MEU CORAÇÃO, \nPROVA-ME, Ó PAI, \nTE PEÇO EM ORAÇÃO;\nDE TODO O MAL LIBERTA-ME, \nSENHOR,TAMBÉM DA TRANSGRESSÃO \nQUE OCULTA FOR.\n\nTODO O MEU SER QUE JÁ NÃO CHAMO MEU, \nQUERO GASTÁ-LO NO SERVIÇO TEU;\nMINHAS PAIXÕES TU PODES DOMINAR,\nEU ME SUBMETO, EM MIM VEM SEMPRE ESTAR.\n\nLÁ DO ALTO CÉU O AVIVAMENTO VEM\nA COMEÇAR EM MIM E INDO ALÉM;\nO TEU PODER, TUAS BÊNÇÃOS, TEU FAVOR,\nCONCEDE AOS FILHOS TEUS, \nÓ PAI DE AMOR.', 'SONDA-ME, Ó DEUS, POIS VÊS MEU CORAÇÃO, PROVA-ME, Ó PAI, TE PEÇO EM ORAÇÃO; DE TODO O MAL LIBERTA-ME, SENHOR,TAMBÉM DA TRANSGRESSÃO QUE OCULTA FOR. TODO O MEU SER QUE JÁ NÃO CHAMO MEU, QUERO GASTÁ-LO NO SERVIÇO TEU; MINHAS PAIXÕES TU PODES DOMINAR, EU ME SUBMETO, EM MIM VEM SEMPRE ESTAR. LÁ DO ALTO CÉU O AVIVAMENTO VEM A COMEÇAR EM MIM E INDO ALÉM; O TEU PODER, TUAS BÊNÇÃOS, TEU FAVOR, CONCEDE AOS FILHOS TEUS, Ó PAI DE AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('388', '388 - SÓ TU ÉS, SENHOR', 'SÓ TU ÉS, SENHOR, SÓ TU ÉS, SENHOR,\nA RAZÃO DO MEU VIVER.\n\nSONDA, Ó PAI, TODO O MEU CORAÇÃO,\nFAZ-ME OUVIR TUA DOCE VOZ, SENHOR.\nBIS\n\nCORO\nDO TEU SANTO ESPÍRITO EM MIM,\nÓ VEM TRANSBORDAR,\nVEM EM MIM REALIZAR\nTODO O TEU QUERER.\n\nVEM, Ó JESUS, \nDERRAMAR TUA PAZ,\nENCHE-ME DE GOZO, \nTUA LUZ ME SATISFAZ.\n\nCORO\nDO TEU SANTO ESPÍRITO EM MIM,\nÓ VEM TRANSBORDAR,\nVEM EM MIM REALIZAR\nTODO O TEU QUERER.\n\nFINAL: (2X)\nSEM TEU AMOR NÃO SEI VIVER, SENHOR;\nSEM TUA GRAÇA EU NÃO SOU FELIZ,\nPOIS TU ÉS TODA A RAZÃO\nE O SENTIDO DE VIVER.\n\nINSTRUMENTOS\n\nSÓ TU ÉS, SENHOR, \nSÓ TU ÉS, SENHOR,\nA RAZÃO DO MEU VIVER.\nBIS\nBIS', 'SÓ TU ÉS, SENHOR, SÓ TU ÉS, SENHOR, A RAZÃO DO MEU VIVER. SONDA, Ó PAI, TODO O MEU CORAÇÃO, FAZ-ME OUVIR TUA DOCE VOZ, SENHOR. CORO DO TEU SANTO ESPÍRITO EM MIM, Ó VEM TRANSBORDAR, VEM EM MIM REALIZAR TODO O TEU QUERER. VEM, Ó JESUS, DERRAMAR TUA PAZ, ENCHE-ME DE GOZO, TUA LUZ ME SATISFAZ. SEM TEU AMOR NÃO SEI VIVER, SENHOR; SEM TUA GRAÇA EU NÃO SOU FELIZ, POIS TU ÉS TODA A RAZÃO E O SENTIDO DE VIVER. INSTRUMENTOS SÓ TU ÉS, SENHOR, SÓ TU ÉS, SENHOR, A RAZÃO DO MEU VIVER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('389', '389 - SENHOR, O QUE É A NOSSA VIDA?', 'SENHOR, O QUE É A NOSSA VIDA?\nÉ COMO A VIGÍLIA DA NOITE:\nCOMO A RELVA, CEDO FLORESCE,\nMAS À TARDE MURCHA E PERECE, SENHOR!\n\nINSTRUMENTOS\n\nSENHOR, NOSSOS DIAS SE PASSAM,\nNOSSOS ANOS LOGO SE ACABAM;\nSÃO COMO BREVE PENSAMENTO,\nASSIM SÃO OS HOMENS NO TEMPO, SENHOR!\n\nINSTRUMENTOS\n\nCORO\nENSINA-NOS A CONTAR \nOS NOSSOS DIAS \nPARA QUE UM SÁBIO CORAÇÃO\nPOSSAMOS TER.\n\nINSTRUMENTOS\n\nSENHOR, DÁ-NOS UM NOVO DIA,\nSACIA-NOS DA ETERNA VIDA\nPARA QUE CANTEMOS DE JÚBILO;\nÓ PAI, ÉS O ETERNO REFÚGIO, SENHOR!\n\nINSTRUMENTOS\n\nCORO (2X)\nENSINA-NOS A CONTAR \nOS NOSSOS DIAS \nPARA QUE UM SÁBIO CORAÇÃO\nPOSSAMOS TER.\n\nINSTRUMENTOS\n\nFINAL: SENHOR!', 'SENHOR, O QUE É A NOSSA VIDA? É COMO A VIGÍLIA DA NOITE: COMO A RELVA, CEDO FLORESCE, MAS À TARDE MURCHA E PERECE, SENHOR! INSTRUMENTOS SENHOR, NOSSOS DIAS SE PASSAM, NOSSOS ANOS LOGO SE ACABAM; SÃO COMO BREVE PENSAMENTO, ASSIM SÃO OS HOMENS NO TEMPO, SENHOR! INSTRUMENTOS CORO ENSINA-NOS A CONTAR OS NOSSOS DIAS PARA QUE UM SÁBIO CORAÇÃO POSSAMOS TER. INSTRUMENTOS SENHOR, DÁ-NOS UM NOVO DIA, SACIA-NOS DA ETERNA VIDA PARA QUE CANTEMOS DE JÚBILO; Ó PAI, ÉS O ETERNO REFÚGIO, SENHOR! INSTRUMENTOS ENSINA-NOS A CONTAR OS NOSSOS DIAS PARA QUE UM SÁBIO CORAÇÃO POSSAMOS TER. INSTRUMENTOS  SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('390', '390 - CANTAREI A MISERICÓRDIA', 'CANTAREI A MISERICÓRDIA E O JUÍZO A TI, \nSENHOR, CANTAREI;\nANDAREI NO CAMINHO RETO,\nQUANDO VIRÁS A MIM, Ó SENHOR?\n\nCORO\nNÃO POREI COISA MÁ DIANTE DE MIM,\nNEM A MENTIRA NEM O ENGANO SEGUIREI;\nSEI QUE NÃO SUPORTARÁS TODO O SOBERBO,\nGUARDA-ME, POIS, EM TI, SENHOR.\n\nO QUE ANDA RETAMENTE\nTE SERVIRÁ, OS FIÉIS TE PROCURARÃO;\nE ESTARÃO JUNTO DE TI\nEM TUA CASA, SENHOR, ETERNAMENTE.\n\nCORO\nNÃO POREI COISA MÁ DIANTE DE MIM,\nNEM A MENTIRA NEM O ENGANO SEGUIREI;\nSEI QUE NÃO SUPORTARÁS TODO O SOBERBO,\nGUARDA-ME, POIS, EM TI, SENHOR.', 'CANTAREI A MISERICÓRDIA E O JUÍZO A TI, SENHOR, CANTAREI; ANDAREI NO CAMINHO RETO, QUANDO VIRÁS A MIM, Ó SENHOR? CORO NÃO POREI COISA MÁ DIANTE DE MIM, NEM A MENTIRA NEM O ENGANO SEGUIREI; SEI QUE NÃO SUPORTARÁS TODO O SOBERBO, GUARDA-ME, POIS, EM TI, SENHOR. O QUE ANDA RETAMENTE TE SERVIRÁ, OS FIÉIS TE PROCURARÃO; E ESTARÃO JUNTO DE TI EM TUA CASA, SENHOR, ETERNAMENTE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('391', '391 - CANTAREI A BONDADE E A JUSTIÇA', 'CANTAREI A BONDADE E A JUSTIÇA, \nA TI, SENHOR,\nCANTAREI, CANTAREI, CANTAREI,\nCANTAREI, CANTAREI.\n\nATENTAREI SABIAMENTE\nAO TEU CAMINHO, SENHOR,\nATENTAREI, ATENTAREI;\nÓ, QUANDO VIRÁS TER COMIGO, SENHOR?\nÓ, QUANDO, SENHOR?\nMAS CANTAREI, CANTAREI\nCANTAREI, CANTAREI.\nBIS\nBIS NO FINAL', 'CANTAREI A BONDADE E A JUSTIÇA, A TI, SENHOR, CANTAREI, CANTAREI, CANTAREI, CANTAREI, CANTAREI. ATENTAREI SABIAMENTE AO TEU CAMINHO, SENHOR, ATENTAREI, ATENTAREI; Ó, QUANDO VIRÁS TER COMIGO, SENHOR? Ó, QUANDO, SENHOR? MAS CANTAREI, CANTAREI CANTAREI, CANTAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('392', '392 - EU SÓ QUERO SER SENHOR', 'EU SÓ QUERO SER, SENHOR,\nUM REMIDO DO TEU GRANDE AMOR;\nQUERO SÓ ANDAR NA LUZ\nE NO CÉU A CONTEMPLAR JESUS;\nPOIS NA CRUZ ELE MORREU\nE O SEU SANGUE ASSIM VERTEU POR MIM;\nQUANTO EU AMO AO MEU SENHOR,\nMEU ETERNO SALVADOR, JESUS.\n\nEU SÓ QUERO TER\nUM LUGAR NO CÉU, SENHOR;\nESTENDE A TUA MÃO,\nDERRAMA O TEU PODER EM \nMIM; OUVIR A TUA VOZ\nALMEJA O MEU CORAÇÃO,\nEU SÓ QUERO SERVIR A TI.\n\nEU SÓ QUERO SER, SENHOR,\nUM REMIDO DO TEU GRANDE AMOR;\nQUERO SÓ ANDAR NA LUZ\nE NO CÉU A CONTEMPLAR JESUS;\nPOIS NA CRUZ ELE MORREU\nE O SEU SANGUE ASSIM VERTEU POR MIM;\nQUANTO EU AMO AO MEU SENHOR,\nMEU ETERNO SALVADOR, JESUS.\n\nEU SÓ QUERO TER\nUM LUGAR NO CÉU, SENHOR;\nESTENDE A TUA MÃO,\nDERRAMA O TEU PODER EM \nMIM; OUVIR A TUA VOZ\nALMEJA O MEU CORAÇÃO,\nEU SÓ QUERO SERVIR A TI.\n\nEU SÓ QUERO TER\nUM LUGAR NO CÉU, SENHOR;\nESTENDE A TUA MÃO,\nDERRAMA O TEU PODER EM \nMIM; OUVIR A TUA VOZ\nALMEJA O MEU CORAÇÃO,\nEU SÓ QUERO SERVIR A TI.\n\nFINAL: EU SÓ QUERO SERVIR A TI.', 'EU SÓ QUERO SER, SENHOR, UM REMIDO DO TEU GRANDE AMOR; QUERO SÓ ANDAR NA LUZ E NO CÉU A CONTEMPLAR JESUS; POIS NA CRUZ ELE MORREU E O SEU SANGUE ASSIM VERTEU POR MIM; QUANTO EU AMO AO MEU SENHOR, MEU ETERNO SALVADOR, JESUS. EU SÓ QUERO TER UM LUGAR NO CÉU, SENHOR; ESTENDE A TUA MÃO, DERRAMA O TEU PODER EM MIM; OUVIR A TUA VOZ ALMEJA O MEU CORAÇÃO, EU SÓ QUERO SERVIR A TI. EU SÓ QUERO TER UM LUGAR NO CÉU, SENHOR; ESTENDE A TUA MÃO, DERRAMA O TEU PODER EM MIM; OUVIR A TUA VOZ ALMEJA O MEU CORAÇÃO, EU SÓ QUERO SERVIR A TI.  EU SÓ QUERO SERVIR A TI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('393', '393 - QUERO SER FIEL AO MEU SENHOR', 'QUERO SER FIEL AO MEU SENHOR,\nSEGUIR COMO ENSINOU ESTE CAMINHO,\nLEVAR ESTA MENSAGEM DE AMOR,\nSEGUIR AO SALVADOR.\n\nCORO\nQUERO TER A PAZ QUE CRISTO DÁ,\nSENTIR O SEU AMOR POR MIM ASSIM,\nNÃO TEMER A LUTA QUE VIER,\nSENHOR, AJUDA-ME.\n\nE QUANDO ME ENCONTRAR AFLITO ASSIM, \nLEMBRAR QUE MEU JESUS\nTANTO SOFREU POR MIM,\nE VIVO ANSIOSO A ESPERAR\nMORAR LÁ NO PORVIR.\n\nCORO (2X)\nQUERO TER A PAZ QUE CRISTO DÁ,\nSENTIR O SEU AMOR POR MIM ASSIM,\nNÃO TEMER A LUTA QUE VIER,\nSENHOR, AJUDA-ME.\n\nFINAL: SENHOR, AJUDA-ME.', 'QUERO SER FIEL AO MEU SENHOR, SEGUIR COMO ENSINOU ESTE CAMINHO, LEVAR ESTA MENSAGEM DE AMOR, SEGUIR AO SALVADOR. CORO QUERO TER A PAZ QUE CRISTO DÁ, SENTIR O SEU AMOR POR MIM ASSIM, NÃO TEMER A LUTA QUE VIER, SENHOR, AJUDA-ME. E QUANDO ME ENCONTRAR AFLITO ASSIM, LEMBRAR QUE MEU JESUS TANTO SOFREU POR MIM, E VIVO ANSIOSO A ESPERAR MORAR LÁ NO PORVIR. QUERO TER A PAZ QUE CRISTO DÁ, SENTIR O SEU AMOR POR MIM ASSIM, NÃO TEMER A LUTA QUE VIER, SENHOR, AJUDA-ME.  SENHOR, AJUDA-ME.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('394', '394 - UMA VOZ ME DISSE LÁ DO CÉU', 'UMA VOZ ME DISSE LÁ DO CÉU:\n“CANTA, CANTA, CANTA PARA MIM;\nORA, ORA, ORA UMA VEZ MAIS,\nSEGUE, SEGUE, SEGUE APÓS MIM;\nÓ VEM SER FIEL ATÉ À MORTE,\nE EU SEREI CONTIGO ATÉ O FIM;\nLOGO ESTARÁS AQUI NO CÉU\nONDE TENS UMA COROA\nPREPARADA PARA TI”.\nBIS', 'UMA VOZ ME DISSE LÁ DO CÉU: CANTA, CANTA, CANTA PARA MIM; ORA, ORA, ORA UMA VEZ MAIS, SEGUE, SEGUE, SEGUE APÓS MIM; Ó VEM SER FIEL ATÉ À MORTE, E EU SEREI CONTIGO ATÉ O FIM; LOGO ESTARÁS AQUI NO CÉU ONDE TENS UMA COROA PREPARADA PARA TI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('395', '395 - FALA, JESUS QUERIDO', 'FALA, JESUS QUERIDO, FALA-ME, HOJE, SIM!\nFALA COM TUA BONDADE, FICA BEM JUNTO A MIM;\nMEU CORAÇÃO ABERTO ESTÁ PRA TUA VOZ OUVIR,\nENCHE-ME DE LOUVORES E GOZO PRA TE SERVIR.\n\nCORO\nFALA-ME SUAVEMENTE! FALA COM MUITO AMOR!\nVENCEDOR PARA SEMPRE, LIVRE TE HEI DE PÔR;\nFALA-ME CADA DIA, SEMPRE EM TERNO TOM;\nOUVIR TUA VOZ EU QUERO E NESTE MESMO SOM.\n\nPARA TEUS FILHOS FALA, E, NO CAMINHO BOM,\nPELA BONDADE OS GUIA A PEDIR O SANTO DOM;\nQUERERÃO CONSAGRAR-SE PARA SUAS VIDAS DAR,\nOBEDECENDO A CRISTO E COM FERVOR O AMAR.\n\nCORO\nFALA-ME SUAVEMENTE! FALA COM MUITO AMOR!\nVENCEDOR PARA SEMPRE, LIVRE TE HEI DE PÔR;\nFALA-ME CADA DIA, SEMPRE EM TERNO TOM;\nOUVIR TUA VOZ EU QUERO E NESTE MESMO SOM.\n\nCOMO NO TEMPO ANTIGO, TU REVELASTE A LEI,\nMOSTRA-ME TUA VONTADE E À TUA SANTA GREI;\nDEIXA-ME GLORIAR-TE, QUERO A TI LOUVAR,\nCANTAR ALEGREMENTE E SEMPRE TE HONRAR.\n\nCORO\nFALA-ME SUAVEMENTE! FALA COM MUITO AMOR!\nVENCEDOR PARA SEMPRE, LIVRE TE HEI DE PÔR;\nFALA-ME CADA DIA, SEMPRE EM TERNO TOM;\nOUVIR TUA VOZ EU QUERO E NESTE MESMO SOM.', 'FALA, JESUS QUERIDO, FALA-ME, HOJE, SIM! FALA COM TUA BONDADE, FICA BEM JUNTO A MIM; MEU CORAÇÃO ABERTO ESTÁ PRA TUA VOZ OUVIR, ENCHE-ME DE LOUVORES E GOZO PRA TE SERVIR. CORO FALA-ME SUAVEMENTE! FALA COM MUITO AMOR! VENCEDOR PARA SEMPRE, LIVRE TE HEI DE PÔR; FALA-ME CADA DIA, SEMPRE EM TERNO TOM; OUVIR TUA VOZ EU QUERO E NESTE MESMO SOM. PARA TEUS FILHOS FALA, E, NO CAMINHO BOM, PELA BONDADE OS GUIA A PEDIR O SANTO DOM; QUERERÃO CONSAGRAR-SE PARA SUAS VIDAS DAR, OBEDECENDO A CRISTO E COM FERVOR O AMAR. COMO NO TEMPO ANTIGO, TU REVELASTE A LEI, MOSTRA-ME TUA VONTADE E À TUA SANTA GREI; DEIXA-ME GLORIAR-TE, QUERO A TI LOUVAR, CANTAR ALEGREMENTE E SEMPRE TE HONRAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('396', '396 - ABBA PAI', 'ABBA PAI, \nFAZE-ME SER SOMENTE TEU,\nMEUS DESEJOS POSSAM \nSER MAIS E MAIS OS TEUS.\nNUNCA A MINHA VIDA ESFRIE,\nNUNCA ME DEIXES CAIR!\nABBA PAI, FAZE-ME SER\nSOMENTE TEU.', 'ABBA PAI, FAZE-ME SER SOMENTE TEU, MEUS DESEJOS POSSAM SER MAIS E MAIS OS TEUS. NUNCA A MINHA VIDA ESFRIE, NUNCA ME DEIXES CAIR! ABBA PAI, FAZE-ME SER SOMENTE TEU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('397', '397 - HÁ UMA LUZ A BRILHAR', 'HÁ UMA LUZ A BRILHAR,\nUMA ESTRELA A CINTILAR\nGUIANDO O CRENTE EM JESUS;\nHÁ UMA VOZ A SOAR\nPELOS SÉCULOS SEM CESSAR,\nQUE À VIDA ETERNA CONDUZ.\n\nCORO\nOH! EU QUERO ANDAR NESSA LUZ DIVINAL, \nNA REVELAÇÃO QUE VEM DESDE OS CÉUS;\nSÓ ASSIM VIVEREI, NÃO NO TEMPO MORTAL, \nMAS NA ETERNIDADE DE DEUS.\n\nNÃO MAIS PELA RAZÃO QUE DITAR MEU CORAÇÃO, \nMAS POR TUA PALAVRA IREI;\nSEJA MEU TEU QUERER, MINHA MENTE A DE JESUS, \nNO TEMPO DE DEUS VIVEREI.\n\nCORO\nOH! EU QUERO ANDAR NESSA LUZ DIVINAL, \nNA REVELAÇÃO QUE VEM DESDE OS CÉUS;\nSÓ ASSIM VIVEREI, NÃO NO TEMPO MORTAL, \nMAS NA ETERNIDADE DE DEUS.\n\nFINAL: \nSÓ ASSIM VIVEREI,\nNÃO NO TEMPO MORTAL,\nMAS NA ETERNIDADE DE DEUS.', 'HÁ UMA LUZ A BRILHAR, UMA ESTRELA A CINTILAR GUIANDO O CRENTE EM JESUS; HÁ UMA VOZ A SOAR PELOS SÉCULOS SEM CESSAR, QUE À VIDA ETERNA CONDUZ. CORO OH! EU QUERO ANDAR NESSA LUZ DIVINAL, NA REVELAÇÃO QUE VEM DESDE OS CÉUS; SÓ ASSIM VIVEREI, NÃO NO TEMPO MORTAL, MAS NA ETERNIDADE DE DEUS. NÃO MAIS PELA RAZÃO QUE DITAR MEU CORAÇÃO, MAS POR TUA PALAVRA IREI; SEJA MEU TEU QUERER, MINHA MENTE A DE JESUS, NO TEMPO DE DEUS VIVEREI. SÓ ASSIM VIVEREI, NÃO NO TEMPO MORTAL, MAS NA ETERNIDADE DE DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('398', '398 - ENTREI NO TEMPO DE DEUS', 'ENTREI NO TEMPO DE DEUS,\nJÁ VIVO A ETERNIDADE,\nVOCÊ TAMBÉM PODE ENTRAR, (PODE ENTRAR)\nSER PARTICIPANTE COMO EU,\nO PAI LHE REVELARÁ A ETERNIDADE.\nBIS\n\nO MEU TEMPO ERA CONTADO \nPARA A MORTE, MAS APROUVE \nAO PAI ENVIAR SEU FILHO\nE HOJE EU VIVO JÁ A ETERNIDADE.\n\nFINAL: A ETERNIDADE.\nBIS', 'ENTREI NO TEMPO DE DEUS, JÁ VIVO A ETERNIDADE, VOCÊ TAMBÉM PODE ENTRAR, (PODE ENTRAR) SER PARTICIPANTE COMO EU, O PAI LHE REVELARÁ A ETERNIDADE. O MEU TEMPO ERA CONTADO PARA A MORTE, MAS APROUVE AO PAI ENVIAR SEU FILHO E HOJE EU VIVO JÁ A ETERNIDADE.  A ETERNIDADE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('399', '399 - QUAL MADEIRA', 'QUAL MADEIRA INÚTIL ASSIM, FUI PLANTADO,\nNO DESERTO DO MUNDO CRUEL FUI DEIXADO;\nMAS JESUS ME ENCONTROU E ME LIBERTOU \nDE UMA VIDA VAZIA, SEM PAZ, SEM AMOR.\n\nINSTRUMENTOS\n\nDE MINH’ALMA TIROU O MEU FARDO TÃO PESADO,\nQUAL A CASCA NA DURA MADEIRA, MEU PECADO.\nTRABALHOU-ME POR INTEIRO,\nCOMO UM BOM CARPINTEIRO,\nFEZ-ME VASO DE HONRA,\nDE GLÓRIA E LOUVOR.\nBIS', 'QUAL MADEIRA INÚTIL ASSIM, FUI PLANTADO, NO DESERTO DO MUNDO CRUEL FUI DEIXADO; MAS JESUS ME ENCONTROU E ME LIBERTOU DE UMA VIDA VAZIA, SEM PAZ, SEM AMOR. INSTRUMENTOS DE MINH’ALMA TIROU O MEU FARDO TÃO PESADO, QUAL A CASCA NA DURA MADEIRA, MEU PECADO. TRABALHOU-ME POR INTEIRO, COMO UM BOM CARPINTEIRO, FEZ-ME VASO DE HONRA, DE GLÓRIA E LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('400', '400 - EU QUERO SER, SENHOR AMADO', 'EU QUERO SER, SENHOR AMADO,\nCOMO UM VASO NAS MÃOS DO OLEIRO;\nQUEBRA A MINHA VIDA E FAZE-A DE NOVO, \nEU QUERO SER, EU QUERO SER,\nUM VASO NOVO.', 'EU QUERO SER, SENHOR AMADO, COMO UM VASO NAS MÃOS DO OLEIRO; QUEBRA A MINHA VIDA E FAZE-A DE NOVO, EU QUERO SER, EU QUERO SER, UM VASO NOVO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('401', '401 - COMO TU QUERES', 'COMO TU QUERES, SENHOR, SOU TEU\nTU ÉS OLEIRO, VASO SOU EU;\nQUEBRA E TRANSFORMA ATÉ QUE, \nENFIM TUA VONTADE SE CUMPRA EM MIM.', 'COMO TU QUERES, SENHOR, SOU TEU TU ÉS OLEIRO, VASO SOU EU; QUEBRA E TRANSFORMA ATÉ QUE, ENFIM TUA VONTADE SE CUMPRA EM MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('402', '402 - A LEI DO SENHOR É PERFEITA', 'A LEI DO SENHOR É PERFEITA\nE RESTAURA A ALMA;\nO TESTEMUNHO DO SENHOR É FIEL\nE DÁ SABEDORIA AOS SÍMPLICES.\n\nCORO\nSÃO MAIS DESEJÁVEIS\nDO QUE O OURO DEPURADO,\nSÃO MAIS DOCES DO QUE O MEL\nE O DESTILAR DOS FAVOS.\n\nOS PRECEITOS DO SENHOR SÃO RETOS\nE ALEGRAM O CORAÇÃO;\nO MANDAMENTO DO SENHOR É PURO\nE ILUMINA OS OLHOS.\n\nCORO\nSÃO MAIS DESEJÁVEIS\nDO QUE O OURO DEPURADO,\nSÃO MAIS DOCES DO QUE O MEL\nE O DESTILAR DOS FAVOS.\n\nO TEMOR DO SENHOR É LÍMPIDO\nE PERMANECE PARA SEMPRE;\nOS JUÍZOS DO SENHOR SÃO VERDADEIROS\nE TODOS IGUALMENTE JUSTOS.\n\nCORO\nSÃO MAIS DESEJÁVEIS\nDO QUE O OURO DEPURADO,\nSÃO MAIS DOCES DO QUE O MEL\nE O DESTILAR DOS FAVOS.', 'A LEI DO SENHOR É PERFEITA E RESTAURA A ALMA; O TESTEMUNHO DO SENHOR É FIEL E DÁ SABEDORIA AOS SÍMPLICES. CORO SÃO MAIS DESEJÁVEIS DO QUE O OURO DEPURADO, SÃO MAIS DOCES DO QUE O MEL E O DESTILAR DOS FAVOS. OS PRECEITOS DO SENHOR SÃO RETOS E ALEGRAM O CORAÇÃO; O MANDAMENTO DO SENHOR É PURO E ILUMINA OS OLHOS. O TEMOR DO SENHOR É LÍMPIDO E PERMANECE PARA SEMPRE; OS JUÍZOS DO SENHOR SÃO VERDADEIROS E TODOS IGUALMENTE JUSTOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('403', '403 - AQUELE QUE TEM OS MEUS MANDAMENTOS', 'AQUELE QUE TEM OS MEUS MANDAMENTOS \nE OS GUARDA, ESSE É O QUE ME AMA. \nAQUELE QUE TEM OS MEUS MANDAMENTOS \nE OS GUARDA, ESSE É O QUE ME AMA.\n\nE AQUELE QUE ME AMA\nSERÁ AMADO POR MEU PAI,\nE EU TAMBÉM O AMAREI,\nE ME MANIFESTAREI A ELE.\nAQUELE QUE TEM OS MEUS MANDAMENTOS \nE OS GUARDA, ESSE É O QUE ME AMA.\n\nINSTRUMENTOS\n\nE AQUELE QUE ME AMA\nSERÁ AMADO POR MEU PAI,\nE EU TAMBÉM O AMAREI,\nE ME MANIFESTAREI A ELE.\nAQUELE QUE TEM OS MEUS MANDAMENTOS \nE OS GUARDA, ESSE É O QUE ME AMA.\n\nAQUELE QUE TEM OS MEUS MANDAMENTOS \nE OS GUARDA, ESSE É O QUE ME AMA.\nAQUELE QUE TEM OS MEUS MANDAMENTOS \nE OS GUARDA, ESSE É O QUE ME AMA. \n\nFINAL: ESSE É O QUE ME AMA. (BIS)', 'AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. E AQUELE QUE ME AMA SERÁ AMADO POR MEU PAI, E EU TAMBÉM O AMAREI, E ME MANIFESTAREI A ELE. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. INSTRUMENTOS E AQUELE QUE ME AMA SERÁ AMADO POR MEU PAI, E EU TAMBÉM O AMAREI, E ME MANIFESTAREI A ELE. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA.  ESSE É O QUE ME AMA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('404', '404 - JESUS, QUERO AMAR-TE', 'JESUS, QUERO AMAR-TE ATÉ MORRER\nQUERO SEGUIR-TE EM MEU VIVER,\nPARA O CÉU DE AMOR GANHAR. \n\nCORO (2X)\nOH! ALELUIA, AMÉM, AMÉM! (3X)\nSENHOR!\nBIS\n\nJESUS, TODO O MEU SER QUERO TE DAR,\nSE FOR PRECISO ATÉ MORRER \nPARA O CÉU DE AMOR GANHAR;\nPARA TE DEDICAR AQUILO \nQUE ME CONCEDESTE \nE A TUA OBRA REALIZAR.\n\nCORO (2X)\nOH! ALELUIA, AMÉM, AMÉM! (3X)\nSENHOR!', 'JESUS, QUERO AMAR-TE ATÉ MORRER QUERO SEGUIR-TE EM MEU VIVER, PARA O CÉU DE AMOR GANHAR.  OH! ALELUIA, AMÉM, AMÉM!  SENHOR! JESUS, TODO O MEU SER QUERO TE DAR, SE FOR PRECISO ATÉ MORRER PARA O CÉU DE AMOR GANHAR; PARA TE DEDICAR AQUILO QUE ME CONCEDESTE E A TUA OBRA REALIZAR.  OH! ALELUIA, AMÉM, AMÉM!  SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('405', '405 - AMO AO SENHOR DE TODO O MEU CORAÇÃO', 'AMO AO SENHOR DE TODO O MEU CORAÇÃO;\nNA PRESENÇA DE TODOS OS POVOS\nA TI, SENHOR, LOUVAREI.\n\nA TI SERVIREI, SIM, SERVIREI,\nENQUANTO EU VIVER, SENHOR,\nA TI SERVIREI.\n\nNÃO ME DEIXES CAIR,\nSEGURA EM MINHAS MÃOS,\nFAZE DE MIM UM VENCEDOR\nPRA QUE EU POSSA TE LOUVAR.\n\nA TI LOUVAREI, SIM, LOUVAREI,\nENQUANTO EU VIVER, SENHOR,\nA TI LOUVAREI;\nA TI SERVIREI, SIM, LOUVAREI,\nENQUANTO EU VIVER, SENHOR,\nA TI SERVIREI, SERVIREI.\n\nINSTRUMENTOS\n\nA TI LOUVAREI, SIM, LOUVAREI,\nENQUANTO EU VIVER, SENHOR,\nA TI LOUVAREI;\nA TI SERVIREI, SIM, LOUVAREI,\nENQUANTO EU VIVER, SENHOR,\nA TI SERVIREI, SERVIREI.', 'AMO AO SENHOR DE TODO O MEU CORAÇÃO; NA PRESENÇA DE TODOS OS POVOS A TI, SENHOR, LOUVAREI. A TI SERVIREI, SIM, SERVIREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI. NÃO ME DEIXES CAIR, SEGURA EM MINHAS MÃOS, FAZE DE MIM UM VENCEDOR PRA QUE EU POSSA TE LOUVAR. A TI LOUVAREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI LOUVAREI; A TI SERVIREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI, SERVIREI. INSTRUMENTOS A TI LOUVAREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI LOUVAREI; A TI SERVIREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI, SERVIREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('406', '406 - JESUS, O QUE MAIS QUERO NO MUNDO', 'JESUS, O QUE EU MAIS QUERO \nNO MUNDO É VIVER PARA TI, \nTE SERVIR POIS FIZESTE JÁ TUDO POR MIM;\nMINHA VIDA MUDASTE E TEU AMOR ME MOSTRASTE, \nMUITAS COISAS EU PUDE, ENTÃO, VER, \nGRANDE AMOR INUNDOU O MEU SER, \nNÃO REJEITASTE O MEU CORAÇÃO \nQUANDO CURVEI-ME A TI EM ORAÇÃO.\n\nCORO\nTUDO NA VIDA DEPENDE DE FÉ,\nDE CONFIARMOS EM TI, COMO ÉS;\nSEMPRE ME GUIAS, ÉS DEUS DE AMOR;\nCRISTO, ÉS TUDO PARA MIM.\n\nINSTRUMENTOS\n\nCORO\nTUDO NA VIDA DEPENDE DE FÉ,\nDE CONFIARMOS EM TI, COMO ÉS;\nSEMPRE ME GUIAS, ÉS DEUS DE AMOR;\nCRISTO, ÉS TUDO PARA MIM.\n\nFINAL: CRISTO, ÉS TUDO PARA MIM.', 'JESUS, O QUE EU MAIS QUERO NO MUNDO É VIVER PARA TI, TE SERVIR POIS FIZESTE JÁ TUDO POR MIM; MINHA VIDA MUDASTE E TEU AMOR ME MOSTRASTE, MUITAS COISAS EU PUDE, ENTÃO, VER, GRANDE AMOR INUNDOU O MEU SER, NÃO REJEITASTE O MEU CORAÇÃO QUANDO CURVEI-ME A TI EM ORAÇÃO. CORO TUDO NA VIDA DEPENDE DE FÉ, DE CONFIARMOS EM TI, COMO ÉS; SEMPRE ME GUIAS, ÉS DEUS DE AMOR; CRISTO, ÉS TUDO PARA MIM. INSTRUMENTOS CORO TUDO NA VIDA DEPENDE DE FÉ, DE CONFIARMOS EM TI, COMO ÉS; SEMPRE ME GUIAS, ÉS DEUS DE AMOR; CRISTO, ÉS TUDO PARA MIM.  CRISTO, ÉS TUDO PARA MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('407', '407 - EU VI O SENHOR', 'EU VI O SENHOR NUM ALTO E SUBLIME TRONO\nE AS ABAS DE SUAS VESTES ENCHIAM O TEMPLO;\nOS ANJOS VOAVAM POR SOBRE O SEU TRONO, COBRIAM O ROSTO E OS PÉS,\nUNS AOS OUTROS CLAMAVAM:\n\nCORO (2X)\nSANTO, SANTO, SANTO,\nÉ O SENHOR DOS EXÉRCITOS,\nTODA A TERRA ESTÁ CHEIA \nDA SUA GLÓRIA!\n\nINSTRUMENTOS\n\nENTÃO DISSE EU: SOU UM HOMEM IMPURO \nE OS MEUS OLHOS VIRAM O REI, \nO SENHOR DOS EXÉRCITOS!\nCOM UMA BRASA DO ALTAR\nOS MEUS LÁBIOS FORAM TOCADOS,\nMEU PECADO FOI PERDOADO \nE OUVI A VOZ DO SENHOR:\nBIS\n\n“A QUEM ENVIAREI, \nE QUEM HÁ DE IR POR NÓS?”\nDISSE EU: EIS-ME AQUI, \nENVIA-ME A MIM.\n\nCORO (2X)\nSANTO, SANTO, SANTO,\nÉ O SENHOR DOS EXÉRCITOS,\nTODA A TERRA ESTÁ CHEIA \nDA SUA GLÓRIA!', 'EU VI O SENHOR NUM ALTO E SUBLIME TRONO E AS ABAS DE SUAS VESTES ENCHIAM O TEMPLO; OS ANJOS VOAVAM POR SOBRE O SEU TRONO, COBRIAM O ROSTO E OS PÉS, UNS AOS OUTROS CLAMAVAM: SANTO, SANTO, SANTO, É O SENHOR DOS EXÉRCITOS, TODA A TERRA ESTÁ CHEIA DA SUA GLÓRIA! INSTRUMENTOS ENTÃO DISSE EU: SOU UM HOMEM IMPURO E OS MEUS OLHOS VIRAM O REI, O SENHOR DOS EXÉRCITOS! COM UMA BRASA DO ALTAR OS MEUS LÁBIOS FORAM TOCADOS, MEU PECADO FOI PERDOADO E OUVI A VOZ DO SENHOR: A QUEM ENVIAREI, E QUEM HÁ DE IR POR NÓS? DISSE EU: EIS-ME AQUI, ENVIA-ME A MIM. SANTO, SANTO, SANTO, É O SENHOR DOS EXÉRCITOS, TODA A TERRA ESTÁ CHEIA DA SUA GLÓRIA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('408', '408 - EIS QUE SURGE UM POVO FORTE', 'EIS QUE SURGE UM POVO FORTE,\nREVESTIDO DE PODER;\nE NÃO TEME NEM A MORTE,\nQUEM A ELE PERTENCER,\nE TERÁ SUBLIME SORTE,\nPOIS COM CRISTO AO CÉU VAI,\nPODES TU DIZER TAMBÉM:\nSOU UM DOS TAIS?\n\nCORO (2X)\nUM DOS TAIS, UM DOS TAIS,\nPODES TU TAMBÉM DIZER:\nSOU UM DOS TAIS?\n\nNO CENÁCULO REUNIDOS, \nO PODER BUSCAVAM ENTÃO, \nPELO AMOR DE DEUS UNIDOS \nA CLAMAR EM ORAÇÃO;\nEIS QUE UM VENTO É DESCIDO \nE O FOGO DO CÉU CAI; \nPODES TU DIZER TAMBÉM:\nSOU UM DOS TAIS?\n\nCORO (2X)\nUM DOS TAIS, UM DOS TAIS,\nPODES TU TAMBÉM DIZER:\nSOU UM DOS TAIS?\n\nESTE POVO DESTEMIDO, \nSÃO DISCÍPULOS DE JESUS \nPELO MUNDO PERSEGUIDO, \nPOR LEVAR A SUA CRUZ,\nE AGORA REVESTIDO COM PODER,\nAO MUNDO SAI; PODES TU DIZER TAMBÉM: \nSOU UM DOS TAIS?\n\nCORO (2X)\nUM DOS TAIS, UM DOS TAIS,\nPODES TU TAMBÉM DIZER:\nSOU UM DOS TAIS?\n\nOH! NÃO SEJAS DESCUIDOSO \nPRA BUSCAR O DOM DE DEUS, \nDOM QUE TE FARÁ DITOSO, \nDAR-TE-Á VISÕES DO CÉU,\nE JESUS MARAVILHOSO \nPROCLAMANDO AOS OUTROS VAI; \nPODERÁS ENTÃO DIZER: \nSOU UM DOS TAIS.\n\nCORO (2X)\nUM DOS TAIS, UM DOS TAIS,\nPODES TU TAMBÉM DIZER:\nSOU UM DOS TAIS?', 'EIS QUE SURGE UM POVO FORTE, REVESTIDO DE PODER; E NÃO TEME NEM A MORTE, QUEM A ELE PERTENCER, E TERÁ SUBLIME SORTE, POIS COM CRISTO AO CÉU VAI, PODES TU DIZER TAMBÉM: SOU UM DOS TAIS? UM DOS TAIS, UM DOS TAIS, PODES TU TAMBÉM DIZER: SOU UM DOS TAIS? NO CENÁCULO REUNIDOS, O PODER BUSCAVAM ENTÃO, PELO AMOR DE DEUS UNIDOS A CLAMAR EM ORAÇÃO; EIS QUE UM VENTO É DESCIDO E O FOGO DO CÉU CAI; PODES TU DIZER TAMBÉM: SOU UM DOS TAIS? ESTE POVO DESTEMIDO, SÃO DISCÍPULOS DE JESUS PELO MUNDO PERSEGUIDO, POR LEVAR A SUA CRUZ, E AGORA REVESTIDO COM PODER, AO MUNDO SAI; PODES TU DIZER TAMBÉM: SOU UM DOS TAIS? OH! NÃO SEJAS DESCUIDOSO PRA BUSCAR O DOM DE DEUS, DOM QUE TE FARÁ DITOSO, DAR-TE-Á VISÕES DO CÉU, E JESUS MARAVILHOSO PROCLAMANDO AOS OUTROS VAI; PODERÁS ENTÃO DIZER: SOU UM DOS TAIS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('409', '409 - EIS MARCHAMOS PARA AQUELE BOM PAÍS', 'EIS MARCHAMOS PARA AQUELE BOM PAÍS, \nONDE O CRENTE, SIM, \nÉ CRISTO QUEM O DIZ COM SEU SALVADOR, \nPRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR;\n\nTRABALHEMOS POIS, COM ZELO E COM VIGOR, CONSTRANGIDOS PELO SEU IMENSO AMOR;\nTRABALHEMOS PELO NOSSO SALVADOR,\nEIS QUE A VIDA VAI FINDAR!\n\nCORO\nACORDAI! ACORDAI! DESPERTAI! DESPERTAI!\nE CANTAI! SIM, CANTAI! O SENHOR NÃO TARDARÁ!\nEIS MARCHAMOS PARA AQUELE BOM PAÍS,\nONDE O CRENTE SIM, É CRISTO QUEM O DIZ, \nCOM SEU SALVADOR, PRA SEMPRE ALI FELIZ, \nVAI COM ELE DESCANSAR.\n\nEIS CONOSCO NOSSO INSIGNE CAPITÃO\nQUE NOS ASSEGURA ETERNA SALVAÇÃO,\nEIS DA SANTA FÉ O INVICTO PAVILHÃO,\nVAMOS, VAMOS TRABALHAR!\nEIA, AVANTE! NADA TEMOS QUE TEMER,\nPOR JESUS HAVEMOS SEMPRE DE VENCER;\nTRABALHEMOS, POIS, ATÉ O AMANHECER\nE O TRABALHO AQUI FINDAR!\n\nCORO\nACORDAI! ACORDAI! DESPERTAI! DESPERTAI!\nE CANTAI! SIM, CANTAI! O SENHOR NÃO TARDARÁ!\nEIS MARCHAMOS PARA AQUELE BOM PAÍS,\nONDE O CRENTE SIM, É CRISTO QUEM O DIZ, \nCOM SEU SALVADOR, PRA SEMPRE ALI FELIZ, \nVAI COM ELE DESCANSAR.\n\nREVESTIDOS DA CONFIANÇA DE JESUS,\nCOMO SERVOS SEUS E FILHOS, SIM, DA LUZ,\nGLORIANDO-NOS EM CRISTO E SUA CRUZ,\nVAMOS, VAMOS TRABALHAR!\nOS PERDIDOS, VAMOS COM AMOR BUSCAR,\nAOS DESESPERADOS VAMOS DECLARAR\nQUE JESUS ESTÁ PRONTO TODOS A SALVAR!\nOH! SIM, VAMOS TRABALHAR!\n\nCORO\nACORDAI! ACORDAI! DESPERTAI! DESPERTAI!\nE CANTAI! SIM, CANTAI! O SENHOR NÃO TARDARÁ!\nEIS MARCHAMOS PARA AQUELE BOM PAÍS,\nONDE O CRENTE SIM, É CRISTO QUEM O DIZ, \nCOM SEU SALVADOR, PRA SEMPRE ALI FELIZ, \nVAI COM ELE DESCANSAR.', 'EIS MARCHAMOS PARA AQUELE BOM PAÍS, ONDE O CRENTE, SIM, É CRISTO QUEM O DIZ COM SEU SALVADOR, PRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR; TRABALHEMOS POIS, COM ZELO E COM VIGOR, CONSTRANGIDOS PELO SEU IMENSO AMOR; TRABALHEMOS PELO NOSSO SALVADOR, EIS QUE A VIDA VAI FINDAR! CORO ACORDAI! ACORDAI! DESPERTAI! DESPERTAI! E CANTAI! SIM, CANTAI! O SENHOR NÃO TARDARÁ! EIS MARCHAMOS PARA AQUELE BOM PAÍS, ONDE O CRENTE SIM, É CRISTO QUEM O DIZ, COM SEU SALVADOR, PRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR. EIS CONOSCO NOSSO INSIGNE CAPITÃO QUE NOS ASSEGURA ETERNA SALVAÇÃO, EIS DA SANTA FÉ O INVICTO PAVILHÃO, VAMOS, VAMOS TRABALHAR! EIA, AVANTE! NADA TEMOS QUE TEMER, POR JESUS HAVEMOS SEMPRE DE VENCER; TRABALHEMOS, POIS, ATÉ O AMANHECER E O TRABALHO AQUI FINDAR! REVESTIDOS DA CONFIANÇA DE JESUS, COMO SERVOS SEUS E FILHOS, SIM, DA LUZ, GLORIANDO-NOS EM CRISTO E SUA CRUZ, VAMOS, VAMOS TRABALHAR! OS PERDIDOS, VAMOS COM AMOR BUSCAR, AOS DESESPERADOS VAMOS DECLARAR QUE JESUS ESTÁ PRONTO TODOS A SALVAR! OH! SIM, VAMOS TRABALHAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('410', '410 - UM PENDÃO REAL VOS ENTREGOU O REI', 'UM PENDÃO REAL VOS ENTREGOU \nO REI A VÓS, SOLDADOS SEUS;\nCORAJOSOS, POIS, EM TUDO O DEFENDEI\nMARCHANDO PARA OS CÉUS.\n\nCORO\nCOM VALOR! SEM TEMOR!\nPOR CRISTO PRONTOS A SOFRER;\nBEM ALTO ERGUEI O SEU PENDÃO,\nFIRMES SEMPRE ATÉ MORRER!\n\nEIS FORMADOS JÁ OS NEGROS BATALHÕES\nDO GRANDE USURPADOR;\nDECLARAI-VOS, HOJE, \nBRAVOS CAMPEÕES, \nAVANTE, SEM TEMOR!\n\nCORO\nCOM VALOR! SEM TEMOR!\nPOR CRISTO PRONTOS A SOFRER;\nBEM ALTO ERGUEI O SEU PENDÃO,\nFIRMES SEMPRE ATÉ MORRER!\n\nQUEM RECEIO SENTE NO SEU CORAÇÃO \nE FRACO SE MOSTRAR,\nNÃO RECEBERÁ O ETERNO \nGALARDÃO QUE CRISTO TEM PRA DAR.\n\nCORO\nCOM VALOR! SEM TEMOR!\nPOR CRISTO PRONTOS A SOFRER;\nBEM ALTO ERGUEI O SEU PENDÃO,\nFIRMES SEMPRE ATÉ MORRER!\n\nPOIS SEJAMOS TODOS A JESUS \nLEAIS E AO SEU REAL PENDÃO;\nOS QUE NA BATALHA SEMPRE \nSÃO FIÉIS COM ELE REINARÃO.', 'UM PENDÃO REAL VOS ENTREGOU O REI A VÓS, SOLDADOS SEUS; CORAJOSOS, POIS, EM TUDO O DEFENDEI MARCHANDO PARA OS CÉUS. CORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! EIS FORMADOS JÁ OS NEGROS BATALHÕES DO GRANDE USURPADOR; DECLARAI-VOS, HOJE, BRAVOS CAMPEÕES, AVANTE, SEM TEMOR! CORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! QUEM RECEIO SENTE NO SEU CORAÇÃO E FRACO SE MOSTRAR, NÃO RECEBERÁ O ETERNO GALARDÃO QUE CRISTO TEM PRA DAR. CORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! POIS SEJAMOS TODOS A JESUS LEAIS E AO SEU REAL PENDÃO; OS QUE NA BATALHA SEMPRE SÃO FIÉIS COM ELE REINARÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('411', '411 - O SENHOR DA CEIFA ESTÁ CHAMANDO', 'O SENHOR DA CEIFA ESTÁ CHAMANDO:\n“QUEM QUER IR POR MIM A PROCURAR\nALMAS QUE NO MUNDO VÃO CHORANDO\nSEM DA SALVAÇÃO PARTICIPAR?”\n\nCORO\nFALA DEUS! FALA DEUS!\nTOCA-ME COM BRASAS DO ALTAR;\nFALA DEUS! FALA DEUS!\nSIM, ALEGRE ATENDO AO TEU MANDAR.\n\nMUITOS SÃO OS QUE VÃO EXPIRANDO\nSEM TER ESPERANÇA DE VER DEUS;\nVAI, DEPRESSA, LHES ANUNCIANDO\nQUE JESUS OS LEVA PARA OS CÉUS.\n\nCORO\nFALA DEUS! FALA DEUS!	\nTOCA-ME COM BRASAS DO ALTAR;\nFALA DEUS! FALA DEUS!\nSIM, ALEGRE ATENDO AO TEU MANDAR.\n\nO PROFETA A DEUS SE APROXIMANDO,\nCONSIDERA-SE UM PECADOR,\nMAS, O FOGO SANTO O QUEIMANDO,\nTORNA-O ÚTIL PARA SEU SENHOR.\n\nCORO\nFALA DEUS! FALA DEUS!	\nTOCA-ME COM BRASAS DO ALTAR;\nFALA DEUS! FALA DEUS!\nSIM, ALEGRE ATENDO AO TEU MANDAR.\n\nBREVE OS TRABALHOS SERÃO FINDOS,\nBÊNÇÃOS VÃO OS SERVOS DESFRUTAR,\nE JESUS OS SAUDARÁ: “BEM-VINDOS”,\nESTA GLÓRIA ESPERO ALCANÇAR!', 'O SENHOR DA CEIFA ESTÁ CHAMANDO: QUEM QUER IR POR MIM A PROCURAR ALMAS QUE NO MUNDO VÃO CHORANDO SEM DA SALVAÇÃO PARTICIPAR? CORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. MUITOS SÃO OS QUE VÃO EXPIRANDO SEM TER ESPERANÇA DE VER DEUS; VAI, DEPRESSA, LHES ANUNCIANDO QUE JESUS OS LEVA PARA OS CÉUS. CORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. O PROFETA A DEUS SE APROXIMANDO, CONSIDERA-SE UM PECADOR, MAS, O FOGO SANTO O QUEIMANDO, TORNA-O ÚTIL PARA SEU SENHOR. CORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. BREVE OS TRABALHOS SERÃO FINDOS, BÊNÇÃOS VÃO OS SERVOS DESFRUTAR, E JESUS OS SAUDARÁ: BEM-VINDOS, ESTA GLÓRIA ESPERO ALCANÇAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('412', '412 - SOU FORASTEIRO AQUI', 'SOU FORASTEIRO AQUI, \nEM TERRA ESTRANHA ESTOU, \nCELESTE PÁTRIA, \nSIM,  É PARA ONDE VOU;\nEMBAIXADOR, POR DEUS, \nDE REINOS D’ALÉM CÉUS, \nVENHO A SERVIÇO DO MEU REI.\n\nCORO\nEIS A MENSAGEM QUE ME DEU, \nQUE OS ANJOS CANTAM LÁ NO CÉU:\n“RECONCILIAI-VOS JÁ”, DIZ O SENHOR, \nREI MEU, “RECONCILIAI-VOS JÁ COM DEUS”.\n\nPOR DEUS MANDADO ESTÁ QUE O HOMEM PECADOR, ARREPENDIDO JÁ, SE CHEGUE AO SALVADOR;\nPOIS QUEM O RECEBER NO REINO VAI VIVER, \nVENHO A SERVIÇO DO MEU REI.\n\nCORO\nEIS A MENSAGEM QUE ME DEU, \nQUE OS ANJOS CANTAM LÁ NO CÉU:\n“RECONCILIAI-VOS JÁ”, DIZ O SENHOR, \nREI MEU, “RECONCILIAI-VOS JÁ COM DEUS”.\n\nMAIS BELO QUE O ROSAL, \nO LAR CELESTE TEM A BÊNÇÃO PRO MORTAL, \nO GOZO ETERNO ALÉM;\nALI SÓ HÁ PRAZER, VOS MANDA O REI DIZER;\nVENHO A SERVIÇO DO MEU REI.\n\nCORO\nEIS A MENSAGEM QUE ME DEU, \nQUE OS ANJOS CANTAM LÁ NO CÉU:\n“RECONCILIAI-VOS JÁ”, DIZ O SENHOR, \nREI MEU, “RECONCILIAI-VOS JÁ COM DEUS”.', 'SOU FORASTEIRO AQUI, EM TERRA ESTRANHA ESTOU, CELESTE PÁTRIA, SIM, É PARA ONDE VOU; EMBAIXADOR, POR DEUS, DE REINOS D’ALÉM CÉUS, VENHO A SERVIÇO DO MEU REI. CORO EIS A MENSAGEM QUE ME DEU, QUE OS ANJOS CANTAM LÁ NO CÉU: RECONCILIAI-VOS JÁ, DIZ O SENHOR, REI MEU, RECONCILIAI-VOS JÁ COM DEUS. POR DEUS MANDADO ESTÁ QUE O HOMEM PECADOR, ARREPENDIDO JÁ, SE CHEGUE AO SALVADOR; POIS QUEM O RECEBER NO REINO VAI VIVER, VENHO A SERVIÇO DO MEU REI. MAIS BELO QUE O ROSAL, O LAR CELESTE TEM A BÊNÇÃO PRO MORTAL, O GOZO ETERNO ALÉM; ALI SÓ HÁ PRAZER, VOS MANDA O REI DIZER; VENHO A SERVIÇO DO MEU REI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('413', '413 - OUVI, Ó CÉUS, E FALAREI', 'OUVI, Ó CÉUS, E FALAREI E OUÇA \nA TERRA A MINHA VOZ, ESCUTAI, POVO MEU!\n\nGOTEJE A MINHA DOUTRINA COMO A CHUVA,\nDESTILE A MINHA PALAVRA COMO O ORVALHO;\nTAL COMO SUAVE CHUVISCO SOBRE \nA RELVA E COMO AS GOTAS \nDE ÁGUA SOBRE A ERVA.\n\nCORO\nENTÃO PROCLAMAREI O NOME DO SENHOR! \nENGRANDECEI O NOSSO DEUS!\n\nINSTRUMENTOS\n\nDEUS É MINHA ROCHA, SUA OBRA É PERFEITA, \nPORQUE TODOS OS SEUS CAMINHOS SÃO JUÍZO;\nDEUS É A VERDADE, NÃO HÁ NELE INJUSTIÇA, \nÉ JUSTO E RETO O SENHOR MEU, ALELUIA!\n\nCORO (2X)\nENTÃO PROCLAMAREI O NOME DO SENHOR!\nENGRANDECEI O NOSSO DEUS!\n\nFINAL:\nENGRANDECEI O NOSSO DEUS!', 'OUVI, Ó CÉUS, E FALAREI E OUÇA A TERRA A MINHA VOZ, ESCUTAI, POVO MEU! GOTEJE A MINHA DOUTRINA COMO A CHUVA, DESTILE A MINHA PALAVRA COMO O ORVALHO; TAL COMO SUAVE CHUVISCO SOBRE A RELVA E COMO AS GOTAS DE ÁGUA SOBRE A ERVA. CORO ENTÃO PROCLAMAREI O NOME DO SENHOR! ENGRANDECEI O NOSSO DEUS! INSTRUMENTOS DEUS É MINHA ROCHA, SUA OBRA É PERFEITA, PORQUE TODOS OS SEUS CAMINHOS SÃO JUÍZO; DEUS É A VERDADE, NÃO HÁ NELE INJUSTIÇA, É JUSTO E RETO O SENHOR MEU, ALELUIA! ENTÃO PROCLAMAREI O NOME DO SENHOR! ENGRANDECEI O NOSSO DEUS!  ENGRANDECEI O NOSSO DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('414', '414 - MEU DEUS, MEU BOM PASTOR', 'MEU DEUS, MEU BOM PASTOR,\nGUIAR-ME VEM PELA MÃO\nPORQUE SOU FRACO, SENHOR;\nDÁ-ME TUA PROTEÇÃO!\nJESUS, MEU SALVADOR,\nTRANSBORDA-ME DE AMOR\nE EU SEREI TEU FIEL SERVIDOR!\n\nDE MANHÃ, LOGO CEDO, \nTUA FACE BUSCAR QUERO, \nSENHOR, E EM SEGREDO,\nMINHA VIDA TE DAR; E QUANDO \nO DIA FINDAR POR TEU SANGUE \nVOU CLAMAR, QUERO TORNAR-ME \nUM FIEL SERVIDOR!\n\nSENDO AGORA TEU SOMENTE,\nVEM DE PODER E AMOR\nENCHER-ME, Ó ONIPOTENTE,\nE GLÓRIA TE DOU, MEU SENHOR;\nMEU DEUS, MEU GUARDADOR,\nGLÓRIA E ALELUIA COM ARDOR,\nEIS-ME TORNADO UM FIEL SERVIDOR!\nGLÓRIA E ALELUIA COM ARDOR,\nEIS-ME TORNADO UM FIEL SERVIDOR!', 'MEU DEUS, MEU BOM PASTOR, GUIAR-ME VEM PELA MÃO PORQUE SOU FRACO, SENHOR; DÁ-ME TUA PROTEÇÃO! JESUS, MEU SALVADOR, TRANSBORDA-ME DE AMOR E EU SEREI TEU FIEL SERVIDOR! DE MANHÃ, LOGO CEDO, TUA FACE BUSCAR QUERO, SENHOR, E EM SEGREDO, MINHA VIDA TE DAR; E QUANDO O DIA FINDAR POR TEU SANGUE VOU CLAMAR, QUERO TORNAR-ME UM FIEL SERVIDOR! SENDO AGORA TEU SOMENTE, VEM DE PODER E AMOR ENCHER-ME, Ó ONIPOTENTE, E GLÓRIA TE DOU, MEU SENHOR; MEU DEUS, MEU GUARDADOR, GLÓRIA E ALELUIA COM ARDOR, EIS-ME TORNADO UM FIEL SERVIDOR! GLÓRIA E ALELUIA COM ARDOR, EIS-ME TORNADO UM FIEL SERVIDOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('415', '415 - QUERO SER UM VASO DE BÊNÇÃO', 'QUERO SER UM VASO DE BÊNÇÃO,\nSIM, UM VASO ESCOLHIDO DE DEUS,\nPARA AS NOVAS LEVAR AOS PERDIDOS,\nBOAS NOVAS QUE VÊM LÁ DOS CÉUS.\n\nCORO\nFAZE-ME VASO DE BÊNÇÃO, SENHOR,\nVASO QUE LEVE A MENSAGEM DE AMOR!\nEIS-ME SUBMISSO AO TEU SERVIÇO;\nEU ME CONSAGRO, BENDITO SENHOR.\n\nQUERO SER UM VASO DE BÊNÇÃO\nPARA TODOS OS DIAS FAZER,\nAOS CULPADOS QUE VIVEM NAS TREVAS,\nO PERDÃO DE JESUS CONHECER.\n\nCORO\nFAZE-ME VASO DE BÊNÇÃO, SENHOR,\nVASO QUE LEVE A MENSAGEM DE AMOR!\nEIS-ME SUBMISSO AO TEU SERVIÇO;\nEU ME CONSAGRO, BENDITO SENHOR.\n\nQUERO SER UM VASO DE BÊNÇÃO,\nSIM, UM VASO DE BÊNÇÃO SEM PAR,\nANUNCIANDO QUE OS CRENTES EM CRISTO\nJUBILOSOS NO CÉU HÃO DE ENTRAR.\n\nCORO\nFAZE-ME VASO DE BÊNÇÃO, SENHOR,\nVASO QUE LEVE A MENSAGEM DE AMOR!\nEIS-ME SUBMISSO AO TEU SERVIÇO;\nEU ME CONSAGRO, BENDITO SENHOR.', 'QUERO SER UM VASO DE BÊNÇÃO, SIM, UM VASO ESCOLHIDO DE DEUS, PARA AS NOVAS LEVAR AOS PERDIDOS, BOAS NOVAS QUE VÊM LÁ DOS CÉUS. CORO FAZE-ME VASO DE BÊNÇÃO, SENHOR, VASO QUE LEVE A MENSAGEM DE AMOR! EIS-ME SUBMISSO AO TEU SERVIÇO; EU ME CONSAGRO, BENDITO SENHOR. QUERO SER UM VASO DE BÊNÇÃO PARA TODOS OS DIAS FAZER, AOS CULPADOS QUE VIVEM NAS TREVAS, O PERDÃO DE JESUS CONHECER. QUERO SER UM VASO DE BÊNÇÃO, SIM, UM VASO DE BÊNÇÃO SEM PAR, ANUNCIANDO QUE OS CRENTES EM CRISTO JUBILOSOS NO CÉU HÃO DE ENTRAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('416', '416 - PELAS PRAIAS GALILÉIAS VEM JESUS', 'PELAS PRAIAS GALILÉIAS VEM JESUS, (BIS)\nJUNTO ÀS REDES, OS PESCADORES,\nPRA O SEGUIREM VAI CHAMAR\nE SEGUIRAM A JESUS SEM VACILAR.\n\nTODA A NOITE DE VIGÍLIA TUDO EM VÃO, (BIS)\nDIZ JESUS: “LANÇAI ALÉM VOSSAS REDES \nE ACHAREIS, E DE PEIXES ENCHEREIS \nLOGO OS BATÉIS”.\n\nASSENTADOS, MUI CANSADOS E FAMINTOS, (BIS)\nCRISTO TOMA O PÃO E O PEIXE, \nORA AO PAI E PARTE O PÃO, E ALIMENTA, \nCOM SOBEJO, A MULTIDÃO.\n\nEU TAMBÉM SEGUIR-TE QUERO, MEU SENHOR, (BIS)\nSÓ EM TI PODE ENCONTRAR,\nMEU FAMINTO CORAÇÃO,\nALEGRIA COM REAL SATISFAÇÃO.', 'PELAS PRAIAS GALILÉIAS VEM JESUS,  JUNTO ÀS REDES, OS PESCADORES, PRA O SEGUIREM VAI CHAMAR E SEGUIRAM A JESUS SEM VACILAR. TODA A NOITE DE VIGÍLIA TUDO EM VÃO,  DIZ JESUS: LANÇAI ALÉM VOSSAS REDES E ACHAREIS, E DE PEIXES ENCHEREIS LOGO OS BATÉIS. ASSENTADOS, MUI CANSADOS E FAMINTOS,  CRISTO TOMA O PÃO E O PEIXE, ORA AO PAI E PARTE O PÃO, E ALIMENTA, COM SOBEJO, A MULTIDÃO. EU TAMBÉM SEGUIR-TE QUERO, MEU SENHOR,  SÓ EM TI PODE ENCONTRAR, MEU FAMINTO CORAÇÃO, ALEGRIA COM REAL SATISFAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('417', '417 - MINHA DEDICAÇÃO', 'MINHA DEDICAÇÃO EU VIM TE OFERECER,\nTU ÉS O MEU AMADO SALVADOR;\nPEDISTE MAIS DE MIM EIS-ME AQUI SENHOR\nTENS A MINHA VIDA E MEU LOUVOR.\n\nVIVER SÓ PARA TI COM TODO O MEU AMOR, \nOBEDECER TUA VOZ EM TODO \nO MEU VIVER, SEGUINDO OS TEUS PASSOS\nOBSERVANDO OS MEUS, \nEM TUDO AGRADANDO A TI, \nMEU TERNO DEUS.\n\nÓ VINDE, ADOREMOS AO SENHOR,\nO GRANDE REI,\nJUSTO E PODEROSO, EXCELSO DEUS,\nMEU REDENTOR,\nTEMEI DIANTE DELE TODA A TERRA,\nADORAI AO REI.\nALELUIA, ALELUIA, ALELUIA, ALELUIA (BIS)\nTEMEI DIANTE DELE TODA A TERRA,\nADORAI AO REI.', 'MINHA DEDICAÇÃO EU VIM TE OFERECER, TU ÉS O MEU AMADO SALVADOR; PEDISTE MAIS DE MIM EIS-ME AQUI SENHOR TENS A MINHA VIDA E MEU LOUVOR. VIVER SÓ PARA TI COM TODO O MEU AMOR, OBEDECER TUA VOZ EM TODO O MEU VIVER, SEGUINDO OS TEUS PASSOS OBSERVANDO OS MEUS, EM TUDO AGRADANDO A TI, MEU TERNO DEUS. Ó VINDE, ADOREMOS AO SENHOR, O GRANDE REI, JUSTO E PODEROSO, EXCELSO DEUS, MEU REDENTOR, TEMEI DIANTE DELE TODA A TERRA, ADORAI AO REI. ALELUIA, ALELUIA, ALELUIA, ALELUIA  TEMEI DIANTE DELE TODA A TERRA, ADORAI AO REI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('418', '418 - HÁ UM POVO QUE TE ADORA', 'HÁ UM POVO QUE TE ADORA EM ESPÍRITO,\nATENDENDO À TUA VOZ, SENHOR,\nQUE TEM UMA NOVA FORMA DE VIDA,\nLUTA COM AS ARMAS CELESTIAIS\nE VENCE PELO PODER DE JESUS.\n\nCORO\nO SENHOR É QUEM DIRIGE,\nO SENHOR É QUEM CONDUZ,\nDEPENDENTE SOU DO SEU AMOR\nQUERO VIVER UMA NOVA FORMA DE VIDA\nNO AMOR, NO AMOR DE JESUS.\n\nVARÕES\n\nNO PROJETO DE DEUS ESTOU\nLUTANDO COM AS ARMAS CELESTIAIS,\nVENCENDO PELO PODER DO SENHOR JESUS, \nCOM SEU PERDÃO, NA SALVAÇÃO, \nNA REVELAÇÃO DE JESUS.\n\nCORO\nO SENHOR É QUEM DIRIGE,\nO SENHOR É QUEM CONDUZ,\nDEPENDENTE SOU DO SEU AMOR\nQUERO VIVER UMA NOVA FORMA DE VIDA\nNO AMOR, NO AMOR DE JESUS.\n\nINSTRUMENTOS\n\nVARÕES\n\nFINAL:\nQUERO VIVER UMA NOVA FORMA DE VIDA \nNO AMOR, NO AMOR DE JESUS.', 'HÁ UM POVO QUE TE ADORA EM ESPÍRITO, ATENDENDO À TUA VOZ, SENHOR, QUE TEM UMA NOVA FORMA DE VIDA, LUTA COM AS ARMAS CELESTIAIS E VENCE PELO PODER DE JESUS. CORO O SENHOR É QUEM DIRIGE, O SENHOR É QUEM CONDUZ, DEPENDENTE SOU DO SEU AMOR QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS. NO PROJETO DE DEUS ESTOU LUTANDO COM AS ARMAS CELESTIAIS, VENCENDO PELO PODER DO SENHOR JESUS, COM SEU PERDÃO, NA SALVAÇÃO, NA REVELAÇÃO DE JESUS. CORO O SENHOR É QUEM DIRIGE, O SENHOR É QUEM CONDUZ, DEPENDENTE SOU DO SEU AMOR QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS. INSTRUMENTOS QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('419', '419 - HÁ UMA OBRA QUE DEUS QUER FAZER', 'HÁ UMA OBRA QUE DEUS QUER FAZER,\nOBRA DE SANTIDADE E DE PODER;\nQUEM ESTÁ DISPOSTO A SE CONSAGRAR \nE A ARCA DE DEUS AOS OMBROS CARREGAR?\n\nCORO\nO ESPÍRITO DE DEUS QUER A TI,\nCOMO ELE UM DIA POSSUIU DAVI, SIM,\nHÁ UMA OBRA A REALIZAR\nANTES QUE VENHA O FIM.\n\nDESCE AO RIBEIRO E ENCHE O CORAÇÃO,\nTERÁS VITÓRIA NA REVELAÇÃO;\nCOM ÓLEO DE PODER DEUS TE UNGIRÁ,\nJAMAIS VAZIA A ESPADA VOLTARÁ.\n\nCORO\nO ESPÍRITO DE DEUS QUER A TI,\nCOMO ELE UM DIA POSSUIU DAVI, SIM,\nHÁ UMA OBRA A REALIZAR\nANTES QUE VENHA O FIM.', 'HÁ UMA OBRA QUE DEUS QUER FAZER, OBRA DE SANTIDADE E DE PODER; QUEM ESTÁ DISPOSTO A SE CONSAGRAR E A ARCA DE DEUS AOS OMBROS CARREGAR? CORO O ESPÍRITO DE DEUS QUER A TI, COMO ELE UM DIA POSSUIU DAVI, SIM, HÁ UMA OBRA A REALIZAR ANTES QUE VENHA O FIM. DESCE AO RIBEIRO E ENCHE O CORAÇÃO, TERÁS VITÓRIA NA REVELAÇÃO; COM ÓLEO DE PODER DEUS TE UNGIRÁ, JAMAIS VAZIA A ESPADA VOLTARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('420', '420 - SOMOS TEU POVO E QUEREMOS', 'SOMOS TEU POVO E QUEREMOS\nTUA VONTADE FAZER;\nLIVRA-NOS DO VELHO HOMEM\nE CONCEDE-NOS UM NOVO VIVER,\nPARA QUE POSSAMOS SEMPRE TE OBEDECER.\nE DEFINIR NOSSAS VIDAS EM \nTI E SERMOS TUA IGREJA FIEL.\nBIS\n\nESTA É UMA OBRA ETERNA\nQUE O TEU ESPÍRITO NOS TRAZ,\nTODOS EM UM SÓ LOUVOR CANTARÃO: MARANATA! JESUS VOLTARÁ!\nBIS', 'SOMOS TEU POVO E QUEREMOS TUA VONTADE FAZER; LIVRA-NOS DO VELHO HOMEM E CONCEDE-NOS UM NOVO VIVER, PARA QUE POSSAMOS SEMPRE TE OBEDECER. E DEFINIR NOSSAS VIDAS EM TI E SERMOS TUA IGREJA FIEL. ESTA É UMA OBRA ETERNA QUE O TEU ESPÍRITO NOS TRAZ, TODOS EM UM SÓ LOUVOR CANTARÃO: MARANATA! JESUS VOLTARÁ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('421', '421 - A OBRA DO SENHOR É PERFEITA', 'A OBRA DO SENHOR É PERFEITA,\nÉ COMO A LUZ DO SOL\nEM SEU ESPLENDOR,\nÉ CHUVA SOBRE A TERRA,\nSEMENTE QUE PROSPERA,\nA OBRA DO SENHOR. (BIS)\n\nA OBRA DO SENHOR É PERFEITA,\nÉ COMO A LUZ DO SOL\nEM SEU ESPLENDOR,\nÉ CHUVA SOBRE A TERRA,\nSEMENTE QUE PROSPERA,\nA OBRA DO SENHOR.\nA OBRA DO SENHOR É PERFEITA,\nA OBRA DO SENHOR É PRECIOSA.', 'A OBRA DO SENHOR É PERFEITA, É COMO A LUZ DO SOL EM SEU ESPLENDOR, É CHUVA SOBRE A TERRA, SEMENTE QUE PROSPERA, A OBRA DO SENHOR. A OBRA DO SENHOR É PERFEITA, É COMO A LUZ DO SOL EM SEU ESPLENDOR, É CHUVA SOBRE A TERRA, SEMENTE QUE PROSPERA, A OBRA DO SENHOR. A OBRA DO SENHOR É PERFEITA, A OBRA DO SENHOR É PRECIOSA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('422', '422 - A MINHA HERANÇA', 'A MINHA HERANÇA ÉS TU, SENHOR,\nE TUA OBRA É PARA MIM MEU BEM MAIOR;\nEM CADA DIA TE CONHECER E DESCOBRIR \nTEU PLANO PARA O MEU VIVER.\n\nCORO\nDAS MÃOS DO MEU JESUS HERANÇA RECEBI, \nESTA VINHA QUE ME DEU PARA GUARDAR;\nPOR OUTRO BEM QUALQUER DO \nMUNDO AQUI JAMAIS EU PODEREI \nMINHA HERANÇA TROCAR.\n\nE SE VIER A TENTAÇÃO RESISTIREI \nFIRMADO EM TUA SALVAÇÃO;\nA VIDA AQUI POSSO PERDER \nMAS UMA HERANÇA ETERNA \nVOU NO CÉU RECEBER.\n\nCORO\nDAS MÃOS DO MEU JESUS HERANÇA RECEBI, \nESTA VINHA QUE ME DEU PARA GUARDAR;\nPOR OUTRO BEM QUALQUER DO \nMUNDO AQUI JAMAIS EU PODEREI \nMINHA HERANÇA TROCAR.', 'A MINHA HERANÇA ÉS TU, SENHOR, E TUA OBRA É PARA MIM MEU BEM MAIOR; EM CADA DIA TE CONHECER E DESCOBRIR TEU PLANO PARA O MEU VIVER. CORO DAS MÃOS DO MEU JESUS HERANÇA RECEBI, ESTA VINHA QUE ME DEU PARA GUARDAR; POR OUTRO BEM QUALQUER DO MUNDO AQUI JAMAIS EU PODEREI MINHA HERANÇA TROCAR. E SE VIER A TENTAÇÃO RESISTIREI FIRMADO EM TUA SALVAÇÃO; A VIDA AQUI POSSO PERDER MAS UMA HERANÇA ETERNA VOU NO CÉU RECEBER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('423', '423 - Ó PASTOR DE ISRAEL', 'Ó PASTOR DE ISRAEL, QUE GUIAS TEU POVO\nE TE ASSENTAS ENTRE OS QUERUBINS, \nRESPLANDECE!\n\nCORO\nVEM, VISITA A TUA VINHA QUE PLANTASTE \nCOM TUA DESTRA, RESPLANDECE \nSOBRE NÓS O TEU ROSTO; \nDOS QUE A QUEREM DEVORAR, \nTUA VINHA VEM GUARDAR, \nRESPLANDECE SOBRE NÓS O TEU ROSTO!\n\nÓ PASTOR DE ISRAEL, \nFAZE BRILHAR TUA GLÓRIA \nE DESPERTA O TEU PODER \nNO MEIO DO TEU POVO.\n\nCORO\nVEM, VISITA A TUA VINHA QUE PLANTASTE \nCOM TUA DESTRA, RESPLANDECE \nSOBRE NÓS O TEU ROSTO; \nDOS QUE A QUEREM DEVORAR, \nTUA VINHA VEM GUARDAR, \nRESPLANDECE SOBRE NÓS O TEU ROSTO!\n\nINSTRUMENTOS\n\nCORO\nVEM, VISITA A TUA VINHA QUE PLANTASTE \nCOM TUA DESTRA, RESPLANDECE \nSOBRE NÓS O TEU ROSTO; \nDOS QUE A QUEREM DEVORAR, \nTUA VINHA VEM GUARDAR, \nRESPLANDECE SOBRE NÓS O TEU ROSTO!\n\nFINAL: O TEU ROSTO!', 'Ó PASTOR DE ISRAEL, QUE GUIAS TEU POVO E TE ASSENTAS ENTRE OS QUERUBINS, RESPLANDECE! CORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO! Ó PASTOR DE ISRAEL, FAZE BRILHAR TUA GLÓRIA E DESPERTA O TEU PODER NO MEIO DO TEU POVO. CORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO! INSTRUMENTOS CORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO!  O TEU ROSTO!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('424', '424 - O MEU AMADO DESCEU AO SEU JARDIM', 'O MEU AMADO DESCEU AO \nSEU JARDIM, AOS CANTEIROS DE BÁLSAMO.\n\nPARA PASTOREAR NO JARDIM\nPARA COLHER OS LÍRIOS.\nBIS\nBIS\n\nEU SOU DO MEU AMADO\nE O MEU AMADO É MEU;\nELE PASTOREIA ENTRE OS LÍRIOS, \nELE PASTOREIA ENTRE OS LÍRIOS.\nBIS\nBIS', 'O MEU AMADO DESCEU AO SEU JARDIM, AOS CANTEIROS DE BÁLSAMO. PARA PASTOREAR NO JARDIM PARA COLHER OS LÍRIOS. EU SOU DO MEU AMADO E O MEU AMADO É MEU; ELE PASTOREIA ENTRE OS LÍRIOS, ELE PASTOREIA ENTRE OS LÍRIOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('425', '425 - QUE VISTA AMÁVEL É (SALMO 132)', 'QUE VISTA AMÁVEL É\nQUANDO, COM SANTO AMOR,\nIRMÃOS UNIDOS PELA FÉ,\nADORAM O SENHOR.\n\nO MUNDO OBSERVARÁ\nAQUELA SANTA PAZ,\nCOMO UM PERFUME SENTIRÁ\nO GOZO QUE ELA TRAZ.\n\nENVIA-NOS, JESUS,\nDO TEU MONTE SIÃO,\nO SANTO ESPÍRITO QUE PRODUZ\nAQUELA DOCE UNIÃO.', 'QUE VISTA AMÁVEL É QUANDO, COM SANTO AMOR, IRMÃOS UNIDOS PELA FÉ, ADORAM O SENHOR. O MUNDO OBSERVARÁ AQUELA SANTA PAZ, COMO UM PERFUME SENTIRÁ O GOZO QUE ELA TRAZ. ENVIA-NOS, JESUS, DO TEU MONTE SIÃO, O SANTO ESPÍRITO QUE PRODUZ AQUELA DOCE UNIÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('426', '426 - UM CORPO SÓ, UM SÓ ESPÍRITO', 'UM CORPO SÓ, UM SÓ ESPÍRITO,\nUMA ESPERANÇA, UM SÓ SENHOR, UMA FÉ,\nUM SÓ BATISMO E UM SÓ DEUS E PAI,\nUM CORPO SÓ, UM SÓ ESPÍRITO. (BIS)\n\nCRISTO É A CABEÇA, O CORPO É A SUA IGREJA, \nCOM SEU PODER NOS BATIZOU O SENHOR;\nNÃO HÁ NO MUNDO UM POVO UNIDO ASSIM,\nUM CORPO SÓ, UM SÓ ESPÍRITO. (BIS)\n\nDE SUA GRAÇA CRISTO DERRAMOU,\nEM MINISTÉRIOS, DONS E MUITO AMOR,\nMAS SOMOS TODOS UM SÓ EM JESUS,\nUM CORPO SÓ, UM SÓ ESPÍRITO. (BIS)', 'UM CORPO SÓ, UM SÓ ESPÍRITO, UMA ESPERANÇA, UM SÓ SENHOR, UMA FÉ, UM SÓ BATISMO E UM SÓ DEUS E PAI, UM CORPO SÓ, UM SÓ ESPÍRITO. CRISTO É A CABEÇA, O CORPO É A SUA IGREJA, COM SEU PODER NOS BATIZOU O SENHOR; NÃO HÁ NO MUNDO UM POVO UNIDO ASSIM, UM CORPO SÓ, UM SÓ ESPÍRITO. DE SUA GRAÇA CRISTO DERRAMOU, EM MINISTÉRIOS, DONS E MUITO AMOR, MAS SOMOS TODOS UM SÓ EM JESUS, UM CORPO SÓ, UM SÓ ESPÍRITO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('427', '427 - OH! QUÃO BOM E QUÃO SUAVE É', 'OH! QUÃO BOM E QUÃO SUAVE \nÉ A UNIÃO ENTRE OS IRMÃOS,\nÉ COMO ÓLEO PRECIOSO\nSOBRE A CABEÇA DE ARÃO,\nÉ COMO ÓLEO PRECIOSO\nSOBRE O CORPO DE ARÃO.\n\nCOMO O ORVALHO DE HERMOM\nQUE DESCE SOBRE OS MONTES \nDE SIÃO PORQUE ALI O SENHOR\nORDENA A BÊNÇÃO PARA SEMPRE, \nORDENA A VIDA PARA SEMPRE. \n\nINSTRUMENTOS\nBIS\n\nOH! QUÃO BOM E QUÃO SUAVE \nÉ A UNIÃO ENTRE OS IRMÃOS,\nÉ COMO ÓLEO PRECIOSO\nSOBRE A CABEÇA DE ARÃO,\nÉ COMO ÓLEO PRECIOSO\nSOBRE O CORPO DE ARÃO.\n\nCOMO O ORVALHO DE HERMOM\nQUE DESCE SOBRE OS MONTES \nDE SIÃO PORQUE ALI O SENHOR\nORDENA A BÊNÇÃO PARA SEMPRE, \nORDENA A VIDA PARA SEMPRE.\nBIS', 'OH! QUÃO BOM E QUÃO SUAVE É A UNIÃO ENTRE OS IRMÃOS, É COMO ÓLEO PRECIOSO SOBRE A CABEÇA DE ARÃO, É COMO ÓLEO PRECIOSO SOBRE O CORPO DE ARÃO. COMO O ORVALHO DE HERMOM QUE DESCE SOBRE OS MONTES DE SIÃO PORQUE ALI O SENHOR ORDENA A BÊNÇÃO PARA SEMPRE, ORDENA A VIDA PARA SEMPRE. INSTRUMENTOS COMO O ORVALHO DE HERMOM QUE DESCE SOBRE OS MONTES DE SIÃO PORQUE ALI O SENHOR ORDENA A BÊNÇÃO PARA SEMPRE, ORDENA A VIDA PARA SEMPRE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('428', '428 - VEM, Ó CRISTO, ENCHER MEU CÁLICE', 'VEM, Ó CRISTO, ENCHER MEU CÁLICE, \nSACIAR A SEDE DO MEU CORAÇÃO;\nPÃO DA VIDA, VEM ALIMENTAR MEU SER, \nVEM MEU CÁLICE DE GOZO TRANSBORDAR.', 'VEM, Ó CRISTO, ENCHER MEU CÁLICE, SACIAR A SEDE DO MEU CORAÇÃO; PÃO DA VIDA, VEM ALIMENTAR MEU SER, VEM MEU CÁLICE DE GOZO TRANSBORDAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('429', '429 - ÉS O PÃO DA VIDA', 'ÉS O PÃO DA VIDA, CELESTIAL MANÁ,\nDELE, QUEM COMER, PRA SEMPRE VIVERÁ;\nEM TEU CORPO REPARTIDO, \nSOMOS UM SÓ POVO UNIDO \nPELOS LAÇOS DE AMOR \nNO TEU SANTO ESPÍRITO.\n\nÉS JESUS, DA VIDE, O DOCE FRUTO,\nVIDA ABUNDANTE QUE NOS SATISFAZ;\nPELO SANGUE DA TUA CRUZ\nALCANÇAMOS VIDA E LUZ,\nCÁLICE TRANSBORDANTE DE \nALEGRIA E DE PAZ.\nBIS', 'ÉS O PÃO DA VIDA, CELESTIAL MANÁ, DELE, QUEM COMER, PRA SEMPRE VIVERÁ; EM TEU CORPO REPARTIDO, SOMOS UM SÓ POVO UNIDO PELOS LAÇOS DE AMOR NO TEU SANTO ESPÍRITO. ÉS JESUS, DA VIDE, O DOCE FRUTO, VIDA ABUNDANTE QUE NOS SATISFAZ; PELO SANGUE DA TUA CRUZ ALCANÇAMOS VIDA E LUZ, CÁLICE TRANSBORDANTE DE ALEGRIA E DE PAZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('430', '430 - COMO CRISTO ENSINOU', 'COMO CRISTO ENSINOU \nPARTILHAMOS DESTE PÃO, \nO SEU CORPO REPARTIDO \nEM COMUNHÃO; E DO CÁLICE BEBAMOS,\nQUE É SEU SANGUE DERRAMADO,\nPREÇO PAGO PELA NOSSA SALVAÇÃO;\n\nE LEMBRAMOS ESSE DIA, \nESSA NOITE TÃO SOMBRIA \nEM QUE ELE NO JARDIM \nAO PAI CLAMOU; \nE TRAÍDO E ABANDONADO\nFOI NA CRUZ SACRIFICADO,\nO CORDEIRO NOSSA CULPA EXPIOU.\n\nDO SEU LADO TRASPASSADO PELO NOSSO VIL PECADO, \nNOS GEROU PRA SERMOS SUA POSSESSÃO;\nUM SÓ CORPO FEZ DE NÓS, UM SÓ POVO, \nUM SÓ PÃO BATIZOU-NOS COM A MESMA SANTA UNÇÃO;\nNO LUGAR DA SUA DOR HÁ PRA NÓS GOZO E LOUVOR, \nNOSSO CÁLICE TRANSBORDA DE ALEGRIA;\nPOIS A VIDA NOS FOI DADA, \nA VITÓRIA ALCANÇADA POR JESUS, \nO NOSSO AMADO SALVADOR.\n\nPOR ESPELHO NÓS JÁ VEMOS, \nPOR ENIGMA PERCEBEMOS \nMARAVILHAS QUE ELE VAI NOS REVELAR, \nAS MORADAS ETERNAIS \nNOS LUGARES CELESTIAIS \nQUE JESUS JUNTO \nDO PAI FOI PREPARAR.', 'COMO CRISTO ENSINOU PARTILHAMOS DESTE PÃO, O SEU CORPO REPARTIDO EM COMUNHÃO; E DO CÁLICE BEBAMOS, QUE É SEU SANGUE DERRAMADO, PREÇO PAGO PELA NOSSA SALVAÇÃO; E LEMBRAMOS ESSE DIA, ESSA NOITE TÃO SOMBRIA EM QUE ELE NO JARDIM AO PAI CLAMOU; E TRAÍDO E ABANDONADO FOI NA CRUZ SACRIFICADO, O CORDEIRO NOSSA CULPA EXPIOU. DO SEU LADO TRASPASSADO PELO NOSSO VIL PECADO, NOS GEROU PRA SERMOS SUA POSSESSÃO; UM SÓ CORPO FEZ DE NÓS, UM SÓ POVO, UM SÓ PÃO BATIZOU-NOS COM A MESMA SANTA UNÇÃO; NO LUGAR DA SUA DOR HÁ PRA NÓS GOZO E LOUVOR, NOSSO CÁLICE TRANSBORDA DE ALEGRIA; POIS A VIDA NOS FOI DADA, A VITÓRIA ALCANÇADA POR JESUS, O NOSSO AMADO SALVADOR. POR ESPELHO NÓS JÁ VEMOS, POR ENIGMA PERCEBEMOS MARAVILHAS QUE ELE VAI NOS REVELAR, AS MORADAS ETERNAIS NOS LUGARES CELESTIAIS QUE JESUS JUNTO DO PAI FOI PREPARAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('431', '431 - CRISTO JÁ NOS PREPAROU', 'CRISTO JÁ NOS PREPAROU\nUM MANJAR QUE NOS COMPROU\nE AGORA NOS CONVIDA A CEAR;\nCOM CELESTIAL MANÁ\nQUE DE GRAÇA DEUS TE DÁ,\nVEM FAMINTO, TUA ALMA SACIAR.\n\nCORO\n“VEM CEAR”, O MESTRE CHAMA: “VEM CEAR”, \nMESMO HOJE TU TE PODES SACIAR;\nPOUCOS PÃES MULTIPLICOU, \nÁGUA EM VINHO TRANSFORMOU, \nVEM, FAMINTO, A JESUS, “VEM CEAR!”\n\nEIS DISCÍPULOS A VOLTAR, \nSEM OS PEIXES APANHAR, \nMAS JESUS OS MANDA OUTRA VEZ PARTIR; \nAO TORNAR À PRAIA, ENTÃO, \nVÊEM NO FOGO PEIXE E PÃO \nE JESUS QUE OS CONVIDA À CEIA VIR.\n\nCORO\n“VEM CEAR”, O MESTRE CHAMA: “VEM CEAR”, \nMESMO HOJE TU TE PODES SACIAR;\nPOUCOS PÃES MULTIPLICOU, \nÁGUA EM VINHO TRANSFORMOU, \nVEM, FAMINTO, A JESUS, “VEM CEAR!”\n\nQUEM SEDENTO SE ACHAR, \nVENHA A CRISTO SEM TARDAR POIS UM VINHO \nSEM MISTURA ELE DÁ, E TAMBÉM DA VIDA, INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('432', '432 - OUÇO A VOZ DO MEU AMADO ME CHAMAR', 'CORO\nOUÇO A VOZ DO MEU AMADO ME CHAMAR\n“FILHO, VEM COMIGO À MESA A CEAR;\nTUDO FIZ, NA CRUZ MORRI POR AMOR,\nPRA DAR VIDA AO PERDIDO PECADOR”.\n\n“ESTE PÃO QUE EU VOS DOU REPRESENTA \nO MEU CORPO QUE ENTREGUEI \nNA CRUZ POR AMOR”.\n\nCORO\nOUÇO A VOZ DO MEU AMADO ME CHAMAR\n“FILHO, VEM COMIGO À MESA A CEAR;\nTUDO FIZ, NA CRUZ MORRI POR AMOR,\nPRA DAR VIDA AO PERDIDO PECADOR”.\n\n“ESTE CÁLICE VOS DOU, REPRESENTA O \nMEU SANGUE QUE DERRAMEI NA CRUZ POR AMOR”.\n\nFINAL:\nOUÇO A VOZ DO MEU AMADO ME CHAMAR\n“FILHO, VEM COMIGO À MESA A CEAR”.', 'CORO OUÇO A VOZ DO MEU AMADO ME CHAMAR FILHO, VEM COMIGO À MESA A CEAR; TUDO FIZ, NA CRUZ MORRI POR AMOR, PRA DAR VIDA AO PERDIDO PECADOR. ESTE PÃO QUE EU VOS DOU REPRESENTA O MEU CORPO QUE ENTREGUEI NA CRUZ POR AMOR. ESTE CÁLICE VOS DOU, REPRESENTA O MEU SANGUE QUE DERRAMEI NA CRUZ POR AMOR.  OUÇO A VOZ DO MEU AMADO ME CHAMAR FILHO, VEM COMIGO À MESA A CEAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('433', '433 - TOMAI AGORA O PÃO', 'TOMAI AGORA O PÃO, EU O PARTI POR VÓS, \nE AO FAZERDES, LEMBRAI-VOS DE MIM;\nFAZEI ISTO E COMEI-O, QUANDO O FIZERDES, \nLEMBRAI-VOS DE MIM.\nESTE PÃO É O MEU CORPO, \nPARTIDO SOMENTE POR VÓS, \nTOMAI-O, COMEI-O, \nFAZEI-O EM MEMÓRIA DE MIM.\n\nTOMAI O CÁLICE, EU O ENCHI POR VÓS,\nE AO FAZERDES, LEMBRAI-VOS DE MIM;\nFAZEI ISTO E BEBEI-O, QUANDO O FIZERDES, \nLEMBRAI-VOS DE MIM.\nESTE É O NOVO CONCERTO \nQUE FAÇO SOMENTE POR VÓS, \nTOMAI-O, BEBEI-O, FAZEI-O,\nEM MEMÓRIA DE MIM. (3X)', 'TOMAI AGORA O PÃO, EU O PARTI POR VÓS, E AO FAZERDES, LEMBRAI-VOS DE MIM; FAZEI ISTO E COMEI-O, QUANDO O FIZERDES, LEMBRAI-VOS DE MIM. ESTE PÃO É O MEU CORPO, PARTIDO SOMENTE POR VÓS, TOMAI-O, COMEI-O, FAZEI-O EM MEMÓRIA DE MIM. TOMAI O CÁLICE, EU O ENCHI POR VÓS, E AO FAZERDES, LEMBRAI-VOS DE MIM; FAZEI ISTO E BEBEI-O, QUANDO O FIZERDES, LEMBRAI-VOS DE MIM. ESTE É O NOVO CONCERTO QUE FAÇO SOMENTE POR VÓS, TOMAI-O, BEBEI-O, FAZEI-O, EM MEMÓRIA DE MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('434', '434 - HÁ UM DOCE ESPÍRITO AQUI', 'HÁ UM DOCE ESPÍRITO AQUI\nE EU SEI QUE É O ESPÍRITO DE DEUS;\nJÁ PODEMOS TODOS PERCEBER\nA PRESENÇA DE JESUS, O SALVADOR.\n\nCORO\nDOCE PRESENÇA, PRESENÇA SANTA, \nVEM SOBRE NÓS, ENCHENDO-NOS \nDO TEU PODER;\nTE ADORAREMOS POR TUA PRESENÇA \nAQUI E QUE RENOVARÁ \nOS NOSSOS CORAÇÕES, \nLOUVADO SEJA O SENHOR\n\nSE TE ACHAS SALVO, E AO INVÉS,\nESTÁS DÉBIL, OPRIMIDO E SEM VIGOR,\nRENDE-TE À PRESENÇA DE JESUS\nE O ESPÍRITO TE ENCHERÁ DE AMOR.\n\nCORO\nDOCE PRESENÇA, PRESENÇA SANTA, \nVEM SOBRE NÓS, ENCHENDO-NOS \nDO TEU PODER;\nTE ADORAREMOS POR TUA PRESENÇA \nAQUI E QUE RENOVARÁ \nOS NOSSOS CORAÇÕES, \nLOUVADO SEJA O SENHOR (BIS)', 'HÁ UM DOCE ESPÍRITO AQUI E EU SEI QUE É O ESPÍRITO DE DEUS; JÁ PODEMOS TODOS PERCEBER A PRESENÇA DE JESUS, O SALVADOR. CORO DOCE PRESENÇA, PRESENÇA SANTA, VEM SOBRE NÓS, ENCHENDO-NOS DO TEU PODER; TE ADORAREMOS POR TUA PRESENÇA AQUI E QUE RENOVARÁ OS NOSSOS CORAÇÕES, LOUVADO SEJA O SENHOR SE TE ACHAS SALVO, E AO INVÉS, ESTÁS DÉBIL, OPRIMIDO E SEM VIGOR, RENDE-TE À PRESENÇA DE JESUS E O ESPÍRITO TE ENCHERÁ DE AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('435', '435 - FLUIRÁ COMO UM RIO', 'FLUIRÁ COMO UM RIO, COMO A CHUVA CAIRÁ, \nCOMO ORVALHO, SOBRE OS MONTES E VALES COBRIRÁ!\nE A GLÓRIA DO SENHOR TODA TERRA ENCHERÁ \nE O ESPÍRITO DE DEUS VIRÁ!\nE O ESPÍRITO DE DEUS, E O ESPÍRITO DE DEUS, \nE O ESPÍRITO DE DEUS VIRÁ!', 'FLUIRÁ COMO UM RIO, COMO A CHUVA CAIRÁ, COMO ORVALHO, SOBRE OS MONTES E VALES COBRIRÁ! E A GLÓRIA DO SENHOR TODA TERRA ENCHERÁ E O ESPÍRITO DE DEUS VIRÁ! E O ESPÍRITO DE DEUS, E O ESPÍRITO DE DEUS, E O ESPÍRITO DE DEUS VIRÁ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('436', '436 - ESPÍRITO DO TRINO DEUS', 'ESPÍRITO DO TRINO DEUS, VEM SOBRE MIM,\nESPÍRITO DO TRINO DEUS, VEM SOBRE MIM.\nQUEBRANTA-ME, CONSOLA-ME,\nTRANSFORMA-ME, TRANSBORDA-ME;\nESPÍRITO DO TRINO DEUS VEM SOBRE MIM.\n\nREPETE O LOUVOR', 'ESPÍRITO DO TRINO DEUS, VEM SOBRE MIM, ESPÍRITO DO TRINO DEUS, VEM SOBRE MIM. QUEBRANTA-ME, CONSOLA-ME, TRANSFORMA-ME, TRANSBORDA-ME; ESPÍRITO DO TRINO DEUS VEM SOBRE MIM. REPETE O LOUVOR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('437', '437 - VEM SOBRE MIM, ESPÍRITO DE DEUS', 'VEM SOBRE MIM, ESPÍRITO DE DEUS,\nA TI ME RENDO EM COMPLETA SUBMISSÃO\nVEM COM PODER, COM GRAÇA DOS CÉUS,\nVEM HOJE ENCHER MEU CORAÇÃO.\n\nCORO\nVEM, SIM, VEM, COMO A BRISA DA \nMANHÃ TRAZENDO O AROMA \nDOS JARDINS CELESTIAIS;\nVEM, SIM, VEM, COMO UM FORTE VENTO \nVEM, DE TI EU QUERO MAIS E MAIS.\n\nSEM TEU PODER NÃO PODEREI VIVER,\nVEM SOBRE MIM, Ó ESPÍRITO DE DEUS;\nSEM TUA UNÇÃO NÃO PODEREI VENCER,\nVEM HOJE ENCHER MEU CORAÇÃO.\n\nCORO\nVEM, SIM, VEM, COMO A BRISA DA \nMANHÃ TRAZENDO O AROMA \nDOS JARDINS CELESTIAIS;\nVEM, SIM, VEM, COMO UM FORTE VENTO \nVEM, DE TI EU QUERO MAIS E MAIS.\n\nFINAL: DE TI EU QUERO MAIS E MAIS.', 'VEM SOBRE MIM, ESPÍRITO DE DEUS, A TI ME RENDO EM COMPLETA SUBMISSÃO VEM COM PODER, COM GRAÇA DOS CÉUS, VEM HOJE ENCHER MEU CORAÇÃO. CORO VEM, SIM, VEM, COMO A BRISA DA MANHÃ TRAZENDO O AROMA DOS JARDINS CELESTIAIS; VEM, SIM, VEM, COMO UM FORTE VENTO VEM, DE TI EU QUERO MAIS E MAIS. SEM TEU PODER NÃO PODEREI VIVER, VEM SOBRE MIM, Ó ESPÍRITO DE DEUS; SEM TUA UNÇÃO NÃO PODEREI VENCER, VEM HOJE ENCHER MEU CORAÇÃO. CORO VEM, SIM, VEM, COMO A BRISA DA MANHÃ TRAZENDO O AROMA DOS JARDINS CELESTIAIS; VEM, SIM, VEM, COMO UM FORTE VENTO VEM, DE TI EU QUERO MAIS E MAIS.  DE TI EU QUERO MAIS E MAIS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('438', '438 - O ESPÍRITO DO SENHOR ESTÁ  SENDO DERRAMADO', 'O ESPÍRITO DO SENHOR \nESTÁ SENDO DERRAMADO,\nPREPARANDO A NOIVA AMADA \nPARA  AS BODAS DO CORDEIRO!\nMARANATA! MARANATA!\nORA VEM, JESUS!\nMARANATA! MARANATA!\nORA VEM, JESUS!', 'O ESPÍRITO DO SENHOR ESTÁ SENDO DERRAMADO, PREPARANDO A NOIVA AMADA PARA AS BODAS DO CORDEIRO! MARANATA! MARANATA! ORA VEM, JESUS! MARANATA! MARANATA! ORA VEM, JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('439', '439 - Ó VEM DERRAMAR', 'Ó VEM DERRAMAR, MEU SENHOR,\nA PORÇÃO DO TEU SANTO ESPÍRITO\nSOBRE MIM, SOBRE MIM.\n\nCORO\nPERMITE ENTRAR NA TUA GLÓRIA,\nSENTIR O TOQUE DE JESUS\nE EM MISTÉRIO ENTÃO FALAR,\nLOUVAR O NOME DE JESUS.\n\nQUERO EM COMUNHÃO TE ADORAR,\nTE EXALTAR, Ó TRIUNO DEUS,\nTEU AMOR PROCLAMAR.\n\nCORO (2X)\nPERMITE ENTRAR NA TUA GLÓRIA,\nSENTIR O TOQUE DE JESUS\nE EM MISTÉRIO ENTÃO FALAR,\nLOUVAR O NOME DE JESUS.\n\nFINAL: LOUVAR O NOME DE JESUS. (2X)', 'Ó VEM DERRAMAR, MEU SENHOR, A PORÇÃO DO TEU SANTO ESPÍRITO SOBRE MIM, SOBRE MIM. CORO PERMITE ENTRAR NA TUA GLÓRIA, SENTIR O TOQUE DE JESUS E EM MISTÉRIO ENTÃO FALAR, LOUVAR O NOME DE JESUS. QUERO EM COMUNHÃO TE ADORAR, TE EXALTAR, Ó TRIUNO DEUS, TEU AMOR PROCLAMAR.  PERMITE ENTRAR NA TUA GLÓRIA, SENTIR O TOQUE DE JESUS E EM MISTÉRIO ENTÃO FALAR, LOUVAR O NOME DE JESUS.  LOUVAR O NOME DE JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('440', '440 - DERRAMAREI MEU ESPÍRITO', 'DERRAMAREI MEU ESPÍRITO\nSOBRE TODA A CARNE \nE VOSSOS FILHOS E VOSSAS \nFILHAS PROFETIZARÃO, \nOS VOSSOS VELHOS TERÃO SONHOS,\nVOSSOS MANCEBOS, VISÕES.\n\nCORO (2X)\nPOIS DERRAMAREI MEU ESPÍRITO\nSOBRE TODA A CARNE.\n\nESPERAI, POIS, A PROMESSA\nQUE DE MIM OUVISTES:\nJOÃO BATIZOU COM ÁGUA\nMAS EU VOS BATIZO\nCOM MEU ESPÍRITO, COM MEU PODER,\nDE MIM SEREIS TESTEMUNHAS.', 'DERRAMAREI MEU ESPÍRITO SOBRE TODA A CARNE E VOSSOS FILHOS E VOSSAS FILHAS PROFETIZARÃO, OS VOSSOS VELHOS TERÃO SONHOS, VOSSOS MANCEBOS, VISÕES. POIS DERRAMAREI MEU ESPÍRITO SOBRE TODA A CARNE. ESPERAI, POIS, A PROMESSA QUE DE MIM OUVISTES: JOÃO BATIZOU COM ÁGUA MAS EU VOS BATIZO COM MEU ESPÍRITO, COM MEU PODER, DE MIM SEREIS TESTEMUNHAS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('441', '441 - ESPÍRITO SANTO, Ó CONSOLADOR', 'ESPÍRITO SANTO, Ó CONSOLADOR,\nVISITA O TEU POVO COM GRAÇA E AMOR;\nÓ VENTO QUE SOPRAS NOS JARDINS DE DEUS, \nDERRAMA PODER,\nESPALHA ENTRE NÓS O AROMA DOS CÉUS!\n\nESPÍRITO SANTO, Ó CONSOLADOR,\nVISITA O TEU POVO COM GRAÇA E AMOR;\nÓ VENTO QUE SOPRAS NOS JARDINS DE DEUS, \nDERRAMA PODER,\nESPALHA ENTRE NÓS O AROMA DOS CÉUS!\n\n\nFINAL: \nDERRAMA PODER, ESPALHA ENTRE NÓS \nO AROMA DOS CÉUS.', 'ESPÍRITO SANTO, Ó CONSOLADOR, VISITA O TEU POVO COM GRAÇA E AMOR; Ó VENTO QUE SOPRAS NOS JARDINS DE DEUS, DERRAMA PODER, ESPALHA ENTRE NÓS O AROMA DOS CÉUS! ESPÍRITO SANTO, Ó CONSOLADOR, VISITA O TEU POVO COM GRAÇA E AMOR; Ó VENTO QUE SOPRAS NOS JARDINS DE DEUS, DERRAMA PODER, ESPALHA ENTRE NÓS O AROMA DOS CÉUS!  DERRAMA PODER, ESPALHA ENTRE NÓS O AROMA DOS CÉUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('442', '442 - MAIS DO TEU SANTO ESPÍRITO', 'MAIS DO TEU SANTO ESPÍRITO, DÁ-NOS, SENHOR,\nMAIS DOS TESOUROS ESCONDIDOS DO TEU AMOR;\nMAIS, MUITO MAIS TU TENS PARA NOS \nDAR SE TEU POVO TE BUSCAR, MAIS, \nMUITO MAIS, TU QUERES ABENÇOAR.\n\nCORO\nPOR ISSO TE ROGAMOS, COM FÉ ORAMOS, \nMANIFESTA TUA GRAÇA HOJE AQUI; \nDÁ-NOS PORÇÃO DOBRADA \nDO TEU SANTO ESPÍRITO,\nPARA QUE MAIS, MUITO MAIS,\nPOSSAMOS TE SERVIR.\nBIS\n\nVEM REVELAR A ESTA GERAÇÃO\nAS MARAVILHAS E SINAIS DA SALVAÇÃO;\nTAL COMO A NOSSOS PAIS MOSTRASTE \nABUNDANTE GRAÇA E AMOR, MAIS, \nMUITO MAIS, CONCEDE-NOS, SENHOR.\n\nCORO\nPOR ISSO TE ROGAMOS, COM FÉ ORAMOS, \nMANIFESTA TUA GRAÇA HOJE AQUI; \nDÁ-NOS PORÇÃO DOBRADA \nDO TEU SANTO ESPÍRITO,\nPARA QUE MAIS, MUITO MAIS,\nPOSSAMOS TE SERVIR.\nBIS', 'MAIS DO TEU SANTO ESPÍRITO, DÁ-NOS, SENHOR, MAIS DOS TESOUROS ESCONDIDOS DO TEU AMOR; MAIS, MUITO MAIS TU TENS PARA NOS DAR SE TEU POVO TE BUSCAR, MAIS, MUITO MAIS, TU QUERES ABENÇOAR. CORO POR ISSO TE ROGAMOS, COM FÉ ORAMOS, MANIFESTA TUA GRAÇA HOJE AQUI; DÁ-NOS PORÇÃO DOBRADA DO TEU SANTO ESPÍRITO, PARA QUE MAIS, MUITO MAIS, POSSAMOS TE SERVIR. VEM REVELAR A ESTA GERAÇÃO AS MARAVILHAS E SINAIS DA SALVAÇÃO; TAL COMO A NOSSOS PAIS MOSTRASTE ABUNDANTE GRAÇA E AMOR, MAIS, MUITO MAIS, CONCEDE-NOS, SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('443', '443 - HÁ UM RIO DE ÁGUAS VIVAS', 'HÁ UM RIO DE ÁGUAS VIVAS\nQUE PROCEDEM DO TRONO DO SENHOR,\nONDE PASSAM NASCE A VIDA, \nDOS DESERTOS FAZEM JARDINS DE AMOR.\n\nCORO\nOH! EU QUERO ESTAR ONDE CORREM\nESSAS ÁGUAS DE PROFUNDA PAZ.\n\nESSE RIO É O ESPÍRITO\nDERRAMADO SOBRE TODO O QUE CRÊ;\nELE PASSA HOJE POR AQUI\nE TE QUER ENCHER DE GOZO E PODER.\n\nCORO\nOH! EU QUERO ESTAR ONDE CORREM\nESSAS ÁGUAS DE PROFUNDA PAZ.\n\nSE QUERES JUBILAR POR TUA SALVAÇÃO,\nENTRA NESSE RIO E VEM SEM TEMOR;\nDEIXA SUBMERGIR TODO O TEU SER\nNAS PROFUNDAS ÁGUAS DO SEU AMOR.', 'HÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDEM DO TRONO DO SENHOR, ONDE PASSAM NASCE A VIDA, DOS DESERTOS FAZEM JARDINS DE AMOR. CORO OH! EU QUERO ESTAR ONDE CORREM ESSAS ÁGUAS DE PROFUNDA PAZ. ESSE RIO É O ESPÍRITO DERRAMADO SOBRE TODO O QUE CRÊ; ELE PASSA HOJE POR AQUI E TE QUER ENCHER DE GOZO E PODER. CORO OH! EU QUERO ESTAR ONDE CORREM ESSAS ÁGUAS DE PROFUNDA PAZ. SE QUERES JUBILAR POR TUA SALVAÇÃO, ENTRA NESSE RIO E VEM SEM TEMOR; DEIXA SUBMERGIR TODO O TEU SER NAS PROFUNDAS ÁGUAS DO SEU AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('444', '444 - DESEJEI ARDENTEMENTE', 'DESEJEI ARDENTEMENTE\nCONVOSCO CEAR, POVO MEU.\n\nTOMAI AGORA O PÃO QUE REPRESENTA\nO MEU CORPO QUE É DADO POR VÓS (BIS)\nBIS\n\nCORO\nALEGRAI-VOS\nREGOZIJAI-VOS\nVÓS QUE ESTAIS À MESA.\n\nINSTRUMENTOS\n\nDESEJEI ARDENTEMENTE\nCONVOSCO CEAR, POVO MEU\nBIS\nBIS\n\nTOMAI O CÁLICE QUE REPRESENTA\nO MEU SANGUE VERTIDO POR VÓS (BIS)\n\nCORO\nALEGRAI-VOS \nREGOZIJAI-VOS \nVÓS QUE ESTAIS À MESA.\n\nINSTRUMENTOS\n\nFINAL: \nALEGRAI-VOS, VÓS QUE ESTAIS À MESA.\nBIS', 'DESEJEI ARDENTEMENTE CONVOSCO CEAR, POVO MEU. TOMAI AGORA O PÃO QUE REPRESENTA O MEU CORPO QUE É DADO POR VÓS CORO ALEGRAI-VOS REGOZIJAI-VOS VÓS QUE ESTAIS À MESA. INSTRUMENTOS DESEJEI ARDENTEMENTE CONVOSCO CEAR, POVO MEU TOMAI O CÁLICE QUE REPRESENTA O MEU SANGUE VERTIDO POR VÓS  CORO ALEGRAI-VOS REGOZIJAI-VOS VÓS QUE ESTAIS À MESA. INSTRUMENTOS  ALEGRAI-VOS, VÓS QUE ESTAIS À MESA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('445', '445 - DIRIGE-ME, SENHOR', 'DIRIGE-ME, SENHOR, FAZ ESTA CANDEIA ILUMINAR, \nQUE A ENCHAS COM TEU ÓLEO PARA NUNCA APAGAR;\nNO TEU TABERNÁCULO EU TE ENCONTRO, AGORA, \nALÉM DO VÉU RASGADO PRA MIM, TE ENCONTRAREI\nETERNAMENTE, DA TARDE ATÉ O AMANHECER,\nCUIDANDO DA CANDEIA QUE É O MEU SER,\nNA TUA PRESENÇA EU QUERO VIVER.\nBIS', 'DIRIGE-ME, SENHOR, FAZ ESTA CANDEIA ILUMINAR, QUE A ENCHAS COM TEU ÓLEO PARA NUNCA APAGAR; NO TEU TABERNÁCULO EU TE ENCONTRO, AGORA, ALÉM DO VÉU RASGADO PRA MIM, TE ENCONTRAREI ETERNAMENTE, DA TARDE ATÉ O AMANHECER, CUIDANDO DA CANDEIA QUE É O MEU SER, NA TUA PRESENÇA EU QUERO VIVER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('446', '446 - A TUA PALAVRA', 'A TUA PALAVRA É TESOURO ESCONDIDO\nQUE ALGUÉM ENCONTROU;\nMISTÉRIOS SELADOS QUE O ESPÍRITO SANTO \nA NÓS REVELOU. \nA TUA PALAVRA É TRIGO GUARDADO,\nMALHADO NO LAGAR DE DEUS,\nNOSSO PÃO DO CÉU!\n\nCORO\nBENDITA PALAVRA \nQUE CURA E SALVA\nA QUEM NELA CRÊ, \nPALAVRA DE DEUS!\n\nA TUA PALAVRA É ABRIGO SEGURO \nQUE VIDA NOS TRAZ, \nA ROCHA DOS SÉCULOS, \nBEM FIRME ALICERCE, ETERNA E VERAZ; \nFELIZ QUEM SOBRE ELA EDIFICA SUA CASA,\nTORMENTAS ELA VENCERÁ, NÃO VACILARÁ!\n\nCORO\nBENDITA PALAVRA \nQUE CURA E SALVA\nA QUEM NELA CRÊ, \nPALAVRA DE DEUS!\n\nFINAL: PALAVRA DE DEUS!', 'A TUA PALAVRA É TESOURO ESCONDIDO QUE ALGUÉM ENCONTROU; MISTÉRIOS SELADOS QUE O ESPÍRITO SANTO A NÓS REVELOU. A TUA PALAVRA É TRIGO GUARDADO, MALHADO NO LAGAR DE DEUS, NOSSO PÃO DO CÉU! CORO BENDITA PALAVRA QUE CURA E SALVA A QUEM NELA CRÊ, PALAVRA DE DEUS! A TUA PALAVRA É ABRIGO SEGURO QUE VIDA NOS TRAZ, A ROCHA DOS SÉCULOS, BEM FIRME ALICERCE, ETERNA E VERAZ; FELIZ QUEM SOBRE ELA EDIFICA SUA CASA, TORMENTAS ELA VENCERÁ, NÃO VACILARÁ! CORO BENDITA PALAVRA QUE CURA E SALVA A QUEM NELA CRÊ, PALAVRA DE DEUS!  PALAVRA DE DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('447', '447 - SENHOR, GUIA-NOS', 'SENHOR, GUIA-NOS À TUA PRESENÇA \nONDE O TEU SANGUE PURIFICA;\nFAZE-NOS UM, UM SÓ CORPO, \nUMA SÓ MENTE, UM CORAÇÃO.\n\nCORO\nSOMOS TEUS, REUNIDOS PRA LOUVAR TEU SANTO \nNOME, ADORAMOS SÓ A TI; GUIA-NOS À TUA GLÓRIA, \nTEU ESPÍRITO DERRAMADO REVELANDO O TEU QUERER;\nGUIA-NOS À TUA GLÓRIA, NÃO HÁ NADA \nMAIS PRECIOSO, NADA QUE QUEIRAMOS MAIS, \nGUIA-NOS.\n\nJÁ SOMOS UM, UM SÓ CORPO, \nUMA SÓ MENTE, UM CORAÇÃO, \nPRA TE OFERTAR TÃO SOMENTE \nNOSSO AMOR E GRATIDÃO.\n\nCORO\nSOMOS TEUS, REUNIDOS PRA LOUVAR TEU SANTO \nNOME, ADORAMOS SÓ A TI; GUIA-NOS À TUA GLÓRIA, \nTEU ESPÍRITO DERRAMADO REVELANDO O TEU QUERER;\nGUIA-NOS À TUA GLÓRIA, NÃO HÁ NADA \nMAIS PRECIOSO, NADA QUE QUEIRAMOS MAIS, \nGUIA-NOS. \n\nFINAL: GUIA-NOS.', 'SENHOR, GUIA-NOS À TUA PRESENÇA ONDE O TEU SANGUE PURIFICA; FAZE-NOS UM, UM SÓ CORPO, UMA SÓ MENTE, UM CORAÇÃO. CORO SOMOS TEUS, REUNIDOS PRA LOUVAR TEU SANTO NOME, ADORAMOS SÓ A TI; GUIA-NOS À TUA GLÓRIA, TEU ESPÍRITO DERRAMADO REVELANDO O TEU QUERER; GUIA-NOS À TUA GLÓRIA, NÃO HÁ NADA MAIS PRECIOSO, NADA QUE QUEIRAMOS MAIS, GUIA-NOS. JÁ SOMOS UM, UM SÓ CORPO, UMA SÓ MENTE, UM CORAÇÃO, PRA TE OFERTAR TÃO SOMENTE NOSSO AMOR E GRATIDÃO. CORO SOMOS TEUS, REUNIDOS PRA LOUVAR TEU SANTO NOME, ADORAMOS SÓ A TI; GUIA-NOS À TUA GLÓRIA, TEU ESPÍRITO DERRAMADO REVELANDO O TEU QUERER; GUIA-NOS À TUA GLÓRIA, NÃO HÁ NADA MAIS PRECIOSO, NADA QUE QUEIRAMOS MAIS, GUIA-NOS.  GUIA-NOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('448', '448 - SENHOR, EU QUERO SERVIR-TE', 'SENHOR, EU QUERO SERVIR-TE,\nSERVIR-TE COM TODO O MEU SER,\nCANTANDO, SEMPRE CANTANDO,\nLOUVANDO COM TODO O PRAZER.\n\nCORO\nJESUS, JESUS,\nFIRMADO EM TI QUERO ESTAR,\nJESUS, JESUS,\nNÃO QUERO, NÃO QUERO FALHAR.\n\nPERMITE QUE EU FALE SOMENTE\nDA GRAÇA E DO AMOR DIVINAL,\nPROCLAME AS TUAS VERDADES\nATÉ A VITÓRIA FINAL.', 'SENHOR, EU QUERO SERVIR-TE, SERVIR-TE COM TODO O MEU SER, CANTANDO, SEMPRE CANTANDO, LOUVANDO COM TODO O PRAZER. CORO JESUS, JESUS, FIRMADO EM TI QUERO ESTAR, JESUS, JESUS, NÃO QUERO, NÃO QUERO FALHAR. PERMITE QUE EU FALE SOMENTE DA GRAÇA E DO AMOR DIVINAL, PROCLAME AS TUAS VERDADES ATÉ A VITÓRIA FINAL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('449', '449 - A MINHA VIDA ENTREGO', 'A MINHA VIDA ENTREGO EM TUAS MÃOS, \nSENHOR, TUDO O QUE SOU A TI EU DOU,\nTODO O MEU SER; O QUE NA ETERNIDADE \nPLANEJASTE PRA MIM DESEJO EXPERIMENTAR EM MEU VIVER.\n\nNÃO PEÇO PARA MIM RIQUEZAS SEM VALOR, \nSÓ PEÇO CADA DIA A LUZ DO TEU AMOR, \nBEM SEI QUE ÉS FIEL, DE MIM TU CUIDARÁS, \nA TEMPESTADE ACALMAS, DÁ-ME PAZ.\n\nCORO\n“EU CUIDAREI DE TI \n(EU CUIDAREI DE TI) VARÕES\nNÃO FALHARÁ O MEU AMOR”,\nOS DIAS PASSARÃO MAS PERMANECERÁ\nEM TI A BÊNÇÃO DO SENHOR.\n\nCOM TEU OLHAR DIRIGES OS PASSOS MEUS, \nSENHOR, AJUDA-ME A GUARDAR \nA HERANÇA, BEM MAIOR, \nPOIS QUERO TE SERVIR, \nTUA OBRA REALIZAR;\nÓ PAI BENDITO, ENTREGO-ME NO ALTAR.\n\nCORO\n“EU CUIDAREI DE TI \n(EU CUIDAREI DE TI) VARÕES\nNÃO FALHARÁ O MEU AMOR”,\nOS DIAS PASSARÃO MAS PERMANECERÁ\nEM TI A BÊNÇÃO DO SENHOR.', 'A MINHA VIDA ENTREGO EM TUAS MÃOS, SENHOR, TUDO O QUE SOU A TI EU DOU, TODO O MEU SER; O QUE NA ETERNIDADE PLANEJASTE PRA MIM DESEJO EXPERIMENTAR EM MEU VIVER. NÃO PEÇO PARA MIM RIQUEZAS SEM VALOR, SÓ PEÇO CADA DIA A LUZ DO TEU AMOR, BEM SEI QUE ÉS FIEL, DE MIM TU CUIDARÁS, A TEMPESTADE ACALMAS, DÁ-ME PAZ. CORO EU CUIDAREI DE TI (EU CUIDAREI DE TI) VARÕES NÃO FALHARÁ O MEU AMOR, OS DIAS PASSARÃO MAS PERMANECERÁ EM TI A BÊNÇÃO DO SENHOR. COM TEU OLHAR DIRIGES OS PASSOS MEUS, SENHOR, AJUDA-ME A GUARDAR A HERANÇA, BEM MAIOR, POIS QUERO TE SERVIR, TUA OBRA REALIZAR; Ó PAI BENDITO, ENTREGO-ME NO ALTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('450', '450 - A TUA JUSTIÇA', 'A TUA JUSTIÇA, Ó DEUS,\nESTÁ MUITO ACIMA DOS CÉUS,\nPOIS FIZESTE GRANDES COISAS Ó DEUS,\nQUEM É SEMELHANTE A TI?\n\n3X\n\nTU QUE ME TENS FEITO VER\nMUITOS MALES E ANGÚSTIAS,\nME DARÁS AINDA A VIDA\nE ME LIVRARÁS DOS ABISMOS DA TERRA, \nAUMENTARÁS A MINHA GRANDEZA,\nNOVAMENTE ME CONSOLARÁS,\nNOVAMENTE ME CONSOLARÁS.\n\nTAMBÉM EU TE LOUVAREI \nCOM  O SALTÉRIO,\nBEM COMO À TUA VERDADE, \nÓ MEU DEUS,\nCANTAR-TE-EI COM HARPA,\nÓ SANTO DE ISRAEL, \n(SANTO DE ISRAEL) VARÕES\nÓ SANTO DE ISRAEL,\nÓ SANTO DE ISRAEL, SENHOR.\n\nOS MEUS LÁBIOS EXULTARÃO \nQUANDO A TI CANTAR, \nASSIM COMO A MINHA ALMA\nQUE TU REMISTE, SENHOR;\nA MINHA LÍNGUA FALARÁ \nDA TUA JUSTIÇA TODO DIA.\nPOIS CONFUNDIDOS ESTÃO \nMEUS INIMIGOS.\nBIS\n\nA TUA JUSTIÇA, Ó DEUS,\nESTÁ MUITO ACIMA DOS CÉUS,\nPOIS FIZESTE GRANDES COISAS Ó DEUS,\nQUEM É SEMELHANTE A TI?\n\nTU QUE ME TENS FEITO VER\nMUITOS MALES E ANGÚSTIAS,\nME DARÁS AINDA A VIDA \nE ME LIVRARÁS DOS ABISMOS DA TERRA,\nAUMENTARÁS A MINHA GRANDEZA,\nNOVAMENTE ME CONSOLARÁS,\nNOVAMENTE ME CONSOLARÁS.\n\nFINAL: NOVAMENTE ME CONSOLARÁS.', 'A TUA JUSTIÇA, Ó DEUS, ESTÁ MUITO ACIMA DOS CÉUS, POIS FIZESTE GRANDES COISAS Ó DEUS, QUEM É SEMELHANTE A TI? TU QUE ME TENS FEITO VER MUITOS MALES E ANGÚSTIAS, ME DARÁS AINDA A VIDA E ME LIVRARÁS DOS ABISMOS DA TERRA, AUMENTARÁS A MINHA GRANDEZA, NOVAMENTE ME CONSOLARÁS, NOVAMENTE ME CONSOLARÁS. TAMBÉM EU TE LOUVAREI COM O SALTÉRIO, BEM COMO À TUA VERDADE, Ó MEU DEUS, CANTAR-TE-EI COM HARPA, Ó SANTO DE ISRAEL, (SANTO DE ISRAEL) VARÕES Ó SANTO DE ISRAEL, Ó SANTO DE ISRAEL, SENHOR. OS MEUS LÁBIOS EXULTARÃO QUANDO A TI CANTAR, ASSIM COMO A MINHA ALMA QUE TU REMISTE, SENHOR; A MINHA LÍNGUA FALARÁ DA TUA JUSTIÇA TODO DIA. POIS CONFUNDIDOS ESTÃO MEUS INIMIGOS. TU QUE ME TENS FEITO VER MUITOS MALES E ANGÚSTIAS, ME DARÁS AINDA A VIDA E ME LIVRARÁS DOS ABISMOS DA TERRA, AUMENTARÁS A MINHA GRANDEZA, NOVAMENTE ME CONSOLARÁS, NOVAMENTE ME CONSOLARÁS.  NOVAMENTE ME CONSOLARÁS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('451', '451 - NO SERVIÇO DO MEU REI EU SOU FELIZ', 'NO SERVIÇO DO MEU REI EU SOU FELIZ,\nSATISFEITO, ABENÇOADO,\nPROCLAMANDO DO MEU REI \nA SALVAÇÃO,\nNO SERVIÇO DO MEU REI.\n\nCORO\nNO SERVIÇO DO MEU REI\nMINHA VIDA ENTREGAREI,\nGOZO, PAZ, FELICIDADE\nTEM QUEM SERVE A MEU BOM REI.\n\nNO SERVIÇO DO MEU REI EU SOU FELIZ,\nOBEDIENTE E CORAJOSO,\nNA TRISTEZA OU NA ALEGRIA SEI SORRIR, \nNO SERVIÇO DO MEU REI.\n\nCORO\nNO SERVIÇO DO MEU REI\nMINHA VIDA ENTREGAREI,\nGOZO, PAZ, FELICIDADE\nTEM QUEM SERVE A MEU BOM REI.\n\nNO SERVIÇO DO MEU REI EU SOU FELIZ,\nJUBILOSO E CONSAGRADO,\nAO SEU LADO VENCEREI TODO MAL,\nNO SERVIÇO DO MEU REI.\n\nCORO\nNO SERVIÇO DO MEU REI\nMINHA VIDA ENTREGAREI,\nGOZO, PAZ, FELICIDADE\nTEM QUEM SERVE A MEU BOM REI.\n\nNO SERVIÇO DO MEU REI EU SOU FELIZ, \nVENTUROSO E DECIDIDO,\nMINHA VIDA AO SERVIÇO ENTREGAREI, \nNO SERVIÇO DO MEU REI.\n\nCORO\nNO SERVIÇO DO MEU REI\nMINHA VIDA ENTREGAREI,\nGOZO, PAZ, FELICIDADE\nTEM QUEM SERVE A MEU BOM REI.', 'NO SERVIÇO DO MEU REI EU SOU FELIZ, SATISFEITO, ABENÇOADO, PROCLAMANDO DO MEU REI A SALVAÇÃO, NO SERVIÇO DO MEU REI. CORO NO SERVIÇO DO MEU REI MINHA VIDA ENTREGAREI, GOZO, PAZ, FELICIDADE TEM QUEM SERVE A MEU BOM REI. NO SERVIÇO DO MEU REI EU SOU FELIZ, OBEDIENTE E CORAJOSO, NA TRISTEZA OU NA ALEGRIA SEI SORRIR, NO SERVIÇO DO MEU REI. CORO NO SERVIÇO DO MEU REI MINHA VIDA ENTREGAREI, GOZO, PAZ, FELICIDADE TEM QUEM SERVE A MEU BOM REI. NO SERVIÇO DO MEU REI EU SOU FELIZ, JUBILOSO E CONSAGRADO, AO SEU LADO VENCEREI TODO MAL, NO SERVIÇO DO MEU REI. CORO NO SERVIÇO DO MEU REI MINHA VIDA ENTREGAREI, GOZO, PAZ, FELICIDADE TEM QUEM SERVE A MEU BOM REI. NO SERVIÇO DO MEU REI EU SOU FELIZ, VENTUROSO E DECIDIDO, MINHA VIDA AO SERVIÇO ENTREGAREI, NO SERVIÇO DO MEU REI. CORO NO SERVIÇO DO MEU REI MINHA VIDA ENTREGAREI, GOZO, PAZ, FELICIDADE TEM QUEM SERVE A MEU BOM REI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('452', '452 - QUERO QUE ME TOQUES, SENHOR', 'QUERO QUE ME TOQUES, SENHOR,\nCOM O TOQUE DO TEU SANTO ESPÍRITO.\n\nCORO\nEIS-ME AQUI, SENHOR (2X)\nFALA-ME, SENHOR, USA-ME, SENHOR.\nBIS\n\nQUERO QUE ME FALES, SENHOR,\nO QUE DEVO FAZER PRA TUA OBRA REALIZAR.\nESTOU PRONTO A TE OUVIR VEM, \nSENHOR JESUS, MINHA VIDA USAR.\n\nCORO\nEIS-ME AQUI, SENHOR (2X)\nFALA-ME, SENHOR, USA-ME, SENHOR.\n\nFINAL: \nESTOU PRONTO A TE OUVIR\nVEM, SENHOR JESUS, \nMINHA VIDA USAR.\nBIS', 'QUERO QUE ME TOQUES, SENHOR, COM O TOQUE DO TEU SANTO ESPÍRITO. CORO EIS-ME AQUI, SENHOR  FALA-ME, SENHOR, USA-ME, SENHOR. QUERO QUE ME FALES, SENHOR, O QUE DEVO FAZER PRA TUA OBRA REALIZAR. ESTOU PRONTO A TE OUVIR VEM, SENHOR JESUS, MINHA VIDA USAR. CORO EIS-ME AQUI, SENHOR  FALA-ME, SENHOR, USA-ME, SENHOR.  ESTOU PRONTO A TE OUVIR VEM, SENHOR JESUS, MINHA VIDA USAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('453', '453 - QUANDO DOBRO OS MEUS JOELHOS EM ORAÇÃO', 'QUANDO DOBRO OS MEUS JOELHOS EM ORAÇÃO, VEJO QUÃO PEQUENO SOU DIANTE DO TEU PODER,\nSEI QUE POSSO DERRAMAR MEU CORAÇÃO EM TEU ALTAR POIS DE TI VEM A RESPOSTA, VEM O PERDÃO.\n\nCORO\nEU QUERO VOLTAR, VOLTAR AO PRIMEIRO AMOR.\nEU QUERO TE SERVIR, SENHOR,\nCOM TODO O MEU VIGOR POIS \nSE CHEGUEI ATÉ AQUI NÃO POSSO MAIS OLHAR \nPRA TRÁS EU QUERO PROSSEGUIR \nCONTIGO ATÉ O FIM.\n\nQUANDO VEJO O QUE FIZESTE EM MEU VIVER, \nTANTAS BÊNÇÃOS RECEBIDAS SEM MERECER,\nO MEU CORAÇÃO TRANSBORDA DE GRATIDÃO; \nQUE DAREI A TI, SENHOR, SENÃO O MEU LOUVOR?\n\nCORO\nEU QUERO VOLTAR, VOLTAR AO PRIMEIRO AMOR.\nEU QUERO TE SERVIR, SENHOR,\nCOM TODO O MEU VIGOR POIS \nSE CHEGUEI ATÉ AQUI NÃO POSSO MAIS OLHAR \nPRA TRÁS EU QUERO PROSSEGUIR \nCONTIGO ATÉ O FIM.\n\nFINAL: \nEU QUERO PROSSEGUIR \nCONTIGO ATÉ O FIM.\nBIS', 'QUANDO DOBRO OS MEUS JOELHOS EM ORAÇÃO, VEJO QUÃO PEQUENO SOU DIANTE DO TEU PODER, SEI QUE POSSO DERRAMAR MEU CORAÇÃO EM TEU ALTAR POIS DE TI VEM A RESPOSTA, VEM O PERDÃO. CORO EU QUERO VOLTAR, VOLTAR AO PRIMEIRO AMOR. EU QUERO TE SERVIR, SENHOR, COM TODO O MEU VIGOR POIS SE CHEGUEI ATÉ AQUI NÃO POSSO MAIS OLHAR PRA TRÁS EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM. QUANDO VEJO O QUE FIZESTE EM MEU VIVER, TANTAS BÊNÇÃOS RECEBIDAS SEM MERECER, O MEU CORAÇÃO TRANSBORDA DE GRATIDÃO; QUE DAREI A TI, SENHOR, SENÃO O MEU LOUVOR? CORO EU QUERO VOLTAR, VOLTAR AO PRIMEIRO AMOR. EU QUERO TE SERVIR, SENHOR, COM TODO O MEU VIGOR POIS SE CHEGUEI ATÉ AQUI NÃO POSSO MAIS OLHAR PRA TRÁS EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM.  EU QUERO PROSSEGUIR CONTIGO ATÉ O FIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('454', '454 - SENHOR, MEU DEUS, O QUE DIREI DE TI?  (SAÚDE ESPIRITUAL)', 'SENHOR, MEU DEUS, O QUE DIREI DE TI?\nTU ÉS AQUELE QUE ME AMOU PRIMEIRO!\nMAS HOJE FRACO ESTOU, SINTO SAUDADES DE TI, \nSENHOR, E VIM CHORAR DIANTE AOS TEUS PÉS, \nEM ORAÇÃO.\n\nRENOVA EM MIM VALORES TEUS,\nORDENA OS PENSAMENTOS MEUS\nE DÁ-ME, Ó PAI, SAÚDE ESPIRITUAL;\nPRA COM PRIMEIRO AMOR TE AMAR,\nO AMOR QUE UM DIA CONHECI \nQUANDO TOCASTE EM MIM \nPELA PRIMEIRA VEZ.\n\nINSTRUMENTOS\n\nSENHOR, MEU DEUS, O QUE DIREI DE TI?\nTU ÉS AQUELE QUE ME AMOU PRIMEIRO!\nMAS HOJE FRACO ESTOU, SINTO SAUDADES DE TI, \nSENHOR, E VIM CHORAR DIANTE AOS TEUS PÉS, \nEM ORAÇÃO.\n\nRENOVA EM MIM VALORES TEUS, \nORDENA OS PENSAMENTOS MEUS E DÁ-ME, \nÓ PAI, SAÚDE ESPIRITUAL;\nPRA COM PRIMEIRO AMOR TE AMAR, \nO AMOR QUE UM DIA CONHECI \nQUANDO TOCASTE EM MIM \nPELA PRIMEIRA VEZ.\nBIS', 'SENHOR, MEU DEUS, O QUE DIREI DE TI? TU ÉS AQUELE QUE ME AMOU PRIMEIRO! MAS HOJE FRACO ESTOU, SINTO SAUDADES DE TI, SENHOR, E VIM CHORAR DIANTE AOS TEUS PÉS, EM ORAÇÃO. RENOVA EM MIM VALORES TEUS, ORDENA OS PENSAMENTOS MEUS E DÁ-ME, Ó PAI, SAÚDE ESPIRITUAL; PRA COM PRIMEIRO AMOR TE AMAR, O AMOR QUE UM DIA CONHECI QUANDO TOCASTE EM MIM PELA PRIMEIRA VEZ. INSTRUMENTOS RENOVA EM MIM VALORES TEUS, ORDENA OS PENSAMENTOS MEUS E DÁ-ME, Ó PAI, SAÚDE ESPIRITUAL; PRA COM PRIMEIRO AMOR TE AMAR, O AMOR QUE UM DIA CONHECI QUANDO TOCASTE EM MIM PELA PRIMEIRA VEZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('455', '455 - QUANTO MAIS EU TE CONHEÇO  (CAMINHO FELIZ)', 'QUANTO MAIS EU TE CONHEÇO,\nMAIS EU QUERO TE SERVIR;\nQUANDO FALAS, OBEDEÇO,\nA BÊNÇÃO ESTÁ EM TE OUVIR.\nTU ÉS FOGO QUE CONSOME,\nBRISA SUAVE DE AMOR, TU ÉS \nDEUS QUE MUDA O HOMEM,\nÉS O MEU AJUDADOR!\nBIS\n\nCORO\nQUE MARAVILHA, SENHOR, É ESTAR\nNESTE CAMINHO QUE LEVA AO CÉU!\nCAMINHO FELIZ, INDO PRA TE ENCONTRAR \nE VER O TEU ROSTO SERÁ COMO UM SONHO,\nPELA FÉ EU JÁ SEI, VAI SE REALIZAR.\n\nO QUE GUARDA A TUA LEI\nNÃO SE ARREPENDERÁ;\nSE A GLÓRIA DE DEUS EU BUSCO,\nELA EU IREI ALCANÇAR;\nSE ESPINHOS NO CAMINHO\nEU TIVER DE ENFRENTAR,\nEU NÃO LIGO, MAS PROSSIGO,\nPARA CRISTO VOU OLHAR!\n\nCORO (2X)\nQUE MARAVILHA, SENHOR, É ESTAR\nNESTE CAMINHO QUE LEVA AO CÉU!\nCAMINHO FELIZ, INDO PRA TE ENCONTRAR \nE VER O TEU ROSTO SERÁ COMO UM SONHO,\nPELA FÉ EU JÁ SEI, VAI SE REALIZAR.\n\nFINAL: VAI SE REALIZAR.', 'QUANTO MAIS EU TE CONHEÇO, MAIS EU QUERO TE SERVIR; QUANDO FALAS, OBEDEÇO, A BÊNÇÃO ESTÁ EM TE OUVIR. TU ÉS FOGO QUE CONSOME, BRISA SUAVE DE AMOR, TU ÉS DEUS QUE MUDA O HOMEM, ÉS O MEU AJUDADOR! CORO QUE MARAVILHA, SENHOR, É ESTAR NESTE CAMINHO QUE LEVA AO CÉU! CAMINHO FELIZ, INDO PRA TE ENCONTRAR E VER O TEU ROSTO SERÁ COMO UM SONHO, PELA FÉ EU JÁ SEI, VAI SE REALIZAR. O QUE GUARDA A TUA LEI NÃO SE ARREPENDERÁ; SE A GLÓRIA DE DEUS EU BUSCO, ELA EU IREI ALCANÇAR; SE ESPINHOS NO CAMINHO EU TIVER DE ENFRENTAR, EU NÃO LIGO, MAS PROSSIGO, PARA CRISTO VOU OLHAR! QUE MARAVILHA, SENHOR, É ESTAR NESTE CAMINHO QUE LEVA AO CÉU! CAMINHO FELIZ, INDO PRA TE ENCONTRAR E VER O TEU ROSTO SERÁ COMO UM SONHO, PELA FÉ EU JÁ SEI, VAI SE REALIZAR.  VAI SE REALIZAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('456', '456 - CHUVAS DE BÊNÇÃOS TEREMOS', 'CHUVAS DE BÊNÇÃOS TEREMOS;\nÉ A PROMESSA DE DEUS.\nTEMPOS BENDITOS TRAZENDO,\nCHUVAS DE BÊNÇÃOS DOS CÉUS.\n\nCORO\nCHUVAS DE BÊNÇÃOS,\nCHUVAS DE BÊNÇÃOS DOS CÉUS;\nGOTAS BENDITAS JÁ TEMOS,\nCHUVAS ROGAMOS A DEUS.\n\nCHUVAS DE BÊNÇÃOS TEREMOS,\nDE VIDA PAZ E PERDÃO.\nOS PECADORES INDIGNOS\nGRAÇA DOS CÉUS OBTERÃO.\n\nSERVAS\n\nCORO\nCHUVAS DE BÊNÇÃOS,\nCHUVAS DE BÊNÇÃOS DOS CÉUS;\nGOTAS BENDITAS JÁ TEMOS,\nCHUVAS ROGAMOS A DEUS.\n\nCHUVAS DE BÊNÇÃOS TEREMOS,\nMANDA-NOS JÁ, Ó SENHOR!\nDÁ-NOS O GOZO DOS FRUTOS\nDOS TEUS ENSINOS DE AMOR.\n\nVARÕES\n\nCORO\nCHUVAS DE BÊNÇÃOS,\nCHUVAS DE BÊNÇÃOS DOS CÉUS;\nGOTAS BENDITAS JÁ TEMOS,\nCHUVAS ROGAMOS A DEUS.\n\nFINAL: AMÉM!', 'CHUVAS DE BÊNÇÃOS TEREMOS; É A PROMESSA DE DEUS. TEMPOS BENDITOS TRAZENDO, CHUVAS DE BÊNÇÃOS DOS CÉUS. CORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS. CHUVAS DE BÊNÇÃOS TEREMOS, DE VIDA PAZ E PERDÃO. OS PECADORES INDIGNOS GRAÇA DOS CÉUS OBTERÃO. CORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS. CHUVAS DE BÊNÇÃOS TEREMOS, MANDA-NOS JÁ, Ó SENHOR! DÁ-NOS O GOZO DOS FRUTOS DOS TEUS ENSINOS DE AMOR. CORO CHUVAS DE BÊNÇÃOS, CHUVAS DE BÊNÇÃOS DOS CÉUS; GOTAS BENDITAS JÁ TEMOS, CHUVAS ROGAMOS A DEUS.  AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('457', '457 - INSTRUMENTO EM TUAS MÃOS', 'INSTRUMENTO EM TUAS MÃOS\nQUERO SER, PARA TEU LOUVOR,\nUM VASO DE HONRA E BÊNÇÃOS\nPARA TU USARES, SENHOR. BREVE OU LONGA, \nA MINHA VIDASÓ TERÁ SENTIDO ASSIM, \nPOR ISSO PEÇO: FAZE DE MIM \nINSTRUMENTO EM TUAS MÃOS.\n\nCORO\nUM VASO PARA TI,FRÁGIL BARRO EM TUAS MÃOS,\nPARA TU MOLDARES E REVESTIRES \nDO OURO MAIS PRECIOSO. UM INSTRUMENTO TEU, SEM VALOR, MAS TEU SENHOR, \nSEPARADO PARA TI, \nPRA TE SERVIR CONFORME \nTE APROUVER EM TEU LOUVOR.\n\nCOM A ÁGUA DA PALAVRA\nMINHA MENTE VEM LIMPAR\nE PURIFICA A MINHA VIDA\nCOM O SANGUE DE JESUS.\nCOM O ÓLEO DO ESPÍRITO,\nME REVESTE DE PODER, PRA SER UM VASO \nDE HONRA E GLÓRIA EM TUAS MÃOS, \nÓ MEU JESUS.\n\nCORO\nUM VASO PARA TI,FRÁGIL BARRO EM TUAS MÃOS,\nPARA TU MOLDARES E REVESTIRES \nDO OURO MAIS PRECIOSO. UM INSTRUMENTO TEU, SEM VALOR, MAS TEU SENHOR, \nSEPARADO PARA TI, \nPRA TE SERVIR CONFORME \nTE APROUVER EM TEU LOUVOR.\n\nFINAL: TEU LOUVOR', 'INSTRUMENTO EM TUAS MÃOS QUERO SER, PARA TEU LOUVOR, UM VASO DE HONRA E BÊNÇÃOS PARA TU USARES, SENHOR. BREVE OU LONGA, A MINHA VIDASÓ TERÁ SENTIDO ASSIM, POR ISSO PEÇO: FAZE DE MIM INSTRUMENTO EM TUAS MÃOS. CORO UM VASO PARA TI,FRÁGIL BARRO EM TUAS MÃOS, PARA TU MOLDARES E REVESTIRES DO OURO MAIS PRECIOSO. UM INSTRUMENTO TEU, SEM VALOR, MAS TEU SENHOR, SEPARADO PARA TI, PRA TE SERVIR CONFORME TE APROUVER EM TEU LOUVOR. COM A ÁGUA DA PALAVRA MINHA MENTE VEM LIMPAR E PURIFICA A MINHA VIDA COM O SANGUE DE JESUS. COM O ÓLEO DO ESPÍRITO, ME REVESTE DE PODER, PRA SER UM VASO DE HONRA E GLÓRIA EM TUAS MÃOS, Ó MEU JESUS. CORO UM VASO PARA TI,FRÁGIL BARRO EM TUAS MÃOS, PARA TU MOLDARES E REVESTIRES DO OURO MAIS PRECIOSO. UM INSTRUMENTO TEU, SEM VALOR, MAS TEU SENHOR, SEPARADO PARA TI, PRA TE SERVIR CONFORME TE APROUVER EM TEU LOUVOR.  TEU LOUVOR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('458', '458 - Ó CRISTO! PÃO DA VIDA', 'Ó CRISTO! PÃO DA VIDA, \nDESCIDO LÁ DO CÉU.\nO PÃO DE NOSSAS ALMAS,\nQUE O PAI DE AMOR NOS DEU!\nEM TI NOS ALEGRAMOS, \nPROVANDO MESMO AQUI\nDO ALENTO E DA DOÇURA,\nQUE ACHAMOS SEMPRE EM TI.\n\nDA ETERNA E SANTA VIDA,\nDA QUAL TU ÉS O AUTOR,\nSUSTENTO E FORTALEZA \nÉS TU TAMBÉM, SENHOR.\nSEM TI NÃO NOS ASSISTEM\nNEM FORÇAS, NEM PODER;\nDE TI, NOSSO ALIMENTO, \nQUEREMOS NÓS VIVER.\n\nÓ CRISTO! PÃO DA VIDA! \nA TI LOUVAMOS NÓS,\nE AO PAI TAMBÉM ERGUEMOS \nA NOSSA ALEGRE VOZ.\nAGRADECEMOS SEMPRE \nO AMOR QUE AQUI NOS DEU,\nSUSTENTO VERDADEIRO \nNO SANTO PÃO DO CÉU.\nAMÉM.', 'Ó CRISTO! PÃO DA VIDA, DESCIDO LÁ DO CÉU. O PÃO DE NOSSAS ALMAS, QUE O PAI DE AMOR NOS DEU! EM TI NOS ALEGRAMOS, PROVANDO MESMO AQUI DO ALENTO E DA DOÇURA, QUE ACHAMOS SEMPRE EM TI. DA ETERNA E SANTA VIDA, DA QUAL TU ÉS O AUTOR, SUSTENTO E FORTALEZA ÉS TU TAMBÉM, SENHOR. SEM TI NÃO NOS ASSISTEM NEM FORÇAS, NEM PODER; DE TI, NOSSO ALIMENTO, QUEREMOS NÓS VIVER. Ó CRISTO! PÃO DA VIDA! A TI LOUVAMOS NÓS, E AO PAI TAMBÉM ERGUEMOS A NOSSA ALEGRE VOZ. AGRADECEMOS SEMPRE O AMOR QUE AQUI NOS DEU, SUSTENTO VERDADEIRO NO SANTO PÃO DO CÉU. AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('459', '459 - É TEMPO, É TEMPO', 'É TEMPO, É TEMPO, \nO MESTRE ESTÁ CHAMANDO JÁ!\nMARCHAR, MARCHAR, \nCONFIANDO EM SEU AMOR!\nPARTIR, PARTIR, A SALVAÇÃO \nA PROCLAMAR, COM A PALAVRA \nSANTA DO BOM SALVADOR.\n\nCORO\nMARCHAR, (MARCHAR, MARCHAR)\nSIM, AVANTE! (SIM, AVANTE!)\nMARCHAR, ERGUENDO O PENDÃO REAL!\n(MARCHAR, ERGUENDO O PENDÃO REAL!)\nAVANTE! (AVANTE, SIM)\nSIM, AVANTE, (SEMPRE, SIM)\nUNIDOS, FIRMES SEMPRE AO AVANÇAR.\n\nGLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! \nCONSAGRANDO TODO O VOSSO CORAÇÃO, \nPRA A JESUS OBEDECER, SEU QUERER EXECUTAR,\nENTOAI, LOUVORES ALTOS! AVANÇAR!\n\n“QUEREMOS LUZ”, É O GRITO DAS NAÇÕES PAGÃS, QUE VEM ATRAVESSANDO O IMENSO MAR.\nIR JÁ, SIM, JÁ, LEVANDO NOVAS DE AMOR, \nSEM ESQUECER TAMBÉM AQUI DE SEMEAR.\n\nCORO\nMARCHAR, (MARCHAR, MARCHAR)\nSIM, AVANTE! (SIM, AVANTE!)\nMARCHAR, ERGUENDO O PENDÃO REAL!\n(MARCHAR, ERGUENDO O PENDÃO REAL!)\nAVANTE! (AVANTE, SIM)\nSIM, AVANTE, (SEMPRE, SIM)\nUNIDOS, FIRMES SEMPRE AO AVANÇAR.\n\nGLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! \nCONSAGRANDO TODO O VOSSO CORAÇÃO, \nPRA A JESUS OBEDECER, SEU QUERER EXECUTAR,\nENTOAI, LOUVORES ALTOS! AVANÇAR!\n\nDESPERTA, IGREJA!\nO TEU PODER VEM EXERCER,\nA TODOS FAZE CRISTO CONHECER;\nA TUA MÃO ESTENDE COM PACIENTE AMOR; ESFORÇA-TE DA MORTE ETERNA A OS DETER.\n\nCORO\nMARCHAR, (MARCHAR, MARCHAR)\nSIM, AVANTE! (SIM, AVANTE!)\nMARCHAR, ERGUENDO O PENDÃO REAL!\n(MARCHAR, ERGUENDO O PENDÃO REAL!)\nAVANTE! (AVANTE, SIM)\nSIM, AVANTE, (SEMPRE, SIM)\nUNIDOS, FIRMES SEMPRE AO AVANÇAR.\n\nGLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! \nCONSAGRANDO TODO O VOSSO CORAÇÃO, \nPRA A JESUS OBEDECER, SEU QUERER EXECUTAR,\nENTOAI, LOUVORES ALTOS! AVANÇAR!\n\nIGREJA, ALERTA!\nO DIA PROMETIDO VEM,\nQUANDO ACLAMADO O SALVADOR SERÁ;\nPOR TODA A PARTE O BEM AMADO REDENTOR ETERNA GLÓRIA, HONRA E LOUVOR TERÁ.\n\nCORO\nMARCHAR, (MARCHAR, MARCHAR)\nSIM, AVANTE! (SIM, AVANTE!)\nMARCHAR, ERGUENDO O PENDÃO REAL!\n(MARCHAR, ERGUENDO O PENDÃO REAL!)\nAVANTE! (AVANTE, SIM)\nSIM, AVANTE, (SEMPRE, SIM)\nUNIDOS, FIRMES SEMPRE AO AVANÇAR.\n\nGLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! \nCONSAGRANDO TODO O VOSSO CORAÇÃO, \nPRA A JESUS OBEDECER, SEU QUERER EXECUTAR,\nENTOAI, LOUVORES ALTOS! AVANÇAR!\n\nCORO\nMARCHAR, (MARCHAR, MARCHAR)\nSIM, AVANTE! (SIM, AVANTE!)\nMARCHAR, ERGUENDO O PENDÃO REAL!\n(MARCHAR, ERGUENDO O PENDÃO REAL!)\nAVANTE! (AVANTE, SIM)\nSIM, AVANTE, (SEMPRE, SIM)\nUNIDOS, FIRMES SEMPRE AO AVANÇAR.', 'É TEMPO, É TEMPO, O MESTRE ESTÁ CHAMANDO JÁ! MARCHAR, MARCHAR, CONFIANDO EM SEU AMOR! PARTIR, PARTIR, A SALVAÇÃO A PROCLAMAR, COM A PALAVRA SANTA DO BOM SALVADOR. CORO MARCHAR, (MARCHAR, MARCHAR) SIM, AVANTE! (SIM, AVANTE!) MARCHAR, ERGUENDO O PENDÃO REAL! (MARCHAR, ERGUENDO O PENDÃO REAL!) AVANTE! (AVANTE, SIM) SIM, AVANTE, (SEMPRE, SIM) UNIDOS, FIRMES SEMPRE AO AVANÇAR. GLÓRIA! GLÓRIA! EIS QUE CANTA A MULTIDÃO! CONSAGRANDO TODO O VOSSO CORAÇÃO, PRA A JESUS OBEDECER, SEU QUERER EXECUTAR, ENTOAI, LOUVORES ALTOS! AVANÇAR! QUEREMOS LUZ, É O GRITO DAS NAÇÕES PAGÃS, QUE VEM ATRAVESSANDO O IMENSO MAR. IR JÁ, SIM, JÁ, LEVANDO NOVAS DE AMOR, SEM ESQUECER TAMBÉM AQUI DE SEMEAR. DESPERTA, IGREJA! O TEU PODER VEM EXERCER, A TODOS FAZE CRISTO CONHECER; A TUA MÃO ESTENDE COM PACIENTE AMOR; ESFORÇA-TE DA MORTE ETERNA A OS DETER. IGREJA, ALERTA! O DIA PROMETIDO VEM, QUANDO ACLAMADO O SALVADOR SERÁ; POR TODA A PARTE O BEM AMADO REDENTOR ETERNA GLÓRIA, HONRA E LOUVOR TERÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('460', '460 - Ó JESUS! Ó VERA PÁSCOA', 'Ó JESUS! Ó VERA PÁSCOA!\nSUSPIRADA DOS ANTIGOS!\nÓ CORDEIRO ETERNO E MEIGO,\nDIGNA-TE ASSISTIR AQUI!\n\nBOM JESUS, Ó PÃO DIVINO!\nPELA FÉ TE RECEBEMOS;\nÉS NAS ALMAS O ALIMENTO\nQUE SUSTENTA O NOSSO AMOR.\n\nBOM JESUS, Ó VINHO PURO!\nDE PERENE GOZO A FONTE!\nQUE NOSSA ALMA VIVA SEMPRE\nPARA TI, DE TI, EM TI. AMÉM.', 'Ó JESUS! Ó VERA PÁSCOA! SUSPIRADA DOS ANTIGOS! Ó CORDEIRO ETERNO E MEIGO, DIGNA-TE ASSISTIR AQUI! BOM JESUS, Ó PÃO DIVINO! PELA FÉ TE RECEBEMOS; ÉS NAS ALMAS O ALIMENTO QUE SUSTENTA O NOSSO AMOR. BOM JESUS, Ó VINHO PURO! DE PERENE GOZO A FONTE! QUE NOSSA ALMA VIVA SEMPRE PARA TI, DE TI, EM TI. AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('461', '461 - TU, Ó SENHOR, TU ÉS TÃO PURO  (AVIVA A TUA OBRA)', 'TU, Ó SENHOR, TU ÉS TÃO PURO DE OLHOS \nE NÃO PODES VER O MAL,\nNEM INJUSTIÇA PODES CONTEMPLAR.\nESCUTA, SENHOR, A NOSSA ORAÇÃO VEM OUVIR\nE AVIVA, SENHOR, A TUA OBRA!\n\nA TUA VOZ ESCUTAMOS COM TEMOR,\nPOIS SABEMOS QUE O TEU\nJUÍZO BREVE VEM A TODA TERRA.\nNA IRA, Ó DEUS, VEM TER MISERICÓRDIA \nDE NÓS E AVIVA, SENHOR, A TUA OBRA!\n\nSENHOR, NA TUA IRA, Ó DEUS,\nVEM TER MISERICÓRDIA DE NÓS\nE AVIVA, SENHOR, A TUA OBRA!\nE AVIVA, SENHOR, A TUA OBRA!', 'TU, Ó SENHOR, TU ÉS TÃO PURO DE OLHOS E NÃO PODES VER O MAL, NEM INJUSTIÇA PODES CONTEMPLAR. ESCUTA, SENHOR, A NOSSA ORAÇÃO VEM OUVIR E AVIVA, SENHOR, A TUA OBRA! A TUA VOZ ESCUTAMOS COM TEMOR, POIS SABEMOS QUE O TEU JUÍZO BREVE VEM A TODA TERRA. NA IRA, Ó DEUS, VEM TER MISERICÓRDIA DE NÓS E AVIVA, SENHOR, A TUA OBRA! SENHOR, NA TUA IRA, Ó DEUS, VEM TER MISERICÓRDIA DE NÓS E AVIVA, SENHOR, A TUA OBRA! E AVIVA, SENHOR, A TUA OBRA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('462', '462 - QUANTOS FIÉIS, SENHOR', 'QUANTOS FIÉIS, SENHOR, \nMÁRTIRES E HERÓIS DA FÉ, \nDERAM SUA VIDA, POR TUA VINHA, \nTOMBARAM, MAS DE PÉ.\nOUVIU-SE NAS ARENAS SEU CANTO DE LOUVOR; \nMAIS QUE AS FOGUEIRAS,\nNELES ARDIA O AMOR DO SALVADOR.\n\nCORO\nGRAÇAS DOU POR TANTOS FIÉIS, SENHOR\nQUE FORAM MAIS FORTES DO QUE A MORTE \nE NOS LEGARAM DA FÉ A HERANÇA,\nFAZ DE NÓS VALENTES EM TUAS MÃOS \nE AVIVA TUA OBRA, Ó DEUS, \nREVELA O TEU PODER NESTA GERAÇÃO.\n\nRAMOS DE MIRRA FOSTE PRA TEUS FIÉIS, JESUS; \nFAMÍLIA, AMIGOS, BENS, MESMO A VIDA, \nTROCARAM PELA CRUZ;\nNUVENS DE TESTEMUNHAS QUE O CÉU \nVAI RECEBER COM PALMAS DE OURO, \nCOROA E GLÓRIA A TODO O QUE VENCER.\n\nCORO\nGRAÇAS DOU POR TANTOS FIÉIS, SENHOR\nQUE FORAM MAIS FORTES DO QUE A MORTE \nE NOS LEGARAM DA FÉ A HERANÇA,\nFAZ DE NÓS VALENTES EM TUAS MÃOS \nE AVIVA TUA OBRA, Ó DEUS, \nREVELA O TEU PODER NESTA GERAÇÃO.', 'QUANTOS FIÉIS, SENHOR, MÁRTIRES E HERÓIS DA FÉ, DERAM SUA VIDA, POR TUA VINHA, TOMBARAM, MAS DE PÉ. OUVIU-SE NAS ARENAS SEU CANTO DE LOUVOR; MAIS QUE AS FOGUEIRAS, NELES ARDIA O AMOR DO SALVADOR. CORO GRAÇAS DOU POR TANTOS FIÉIS, SENHOR QUE FORAM MAIS FORTES DO QUE A MORTE E NOS LEGARAM DA FÉ A HERANÇA, FAZ DE NÓS VALENTES EM TUAS MÃOS E AVIVA TUA OBRA, Ó DEUS, REVELA O TEU PODER NESTA GERAÇÃO. RAMOS DE MIRRA FOSTE PRA TEUS FIÉIS, JESUS; FAMÍLIA, AMIGOS, BENS, MESMO A VIDA, TROCARAM PELA CRUZ; NUVENS DE TESTEMUNHAS QUE O CÉU VAI RECEBER COM PALMAS DE OURO, COROA E GLÓRIA A TODO O QUE VENCER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('463', '463 - VEM, SENHOR, VISITA TUA VINHA', 'VEM, SENHOR, VISITA TUA VINHA,\nQUE PLANTASTE EM NOSSAS VIDAS\nCOM TUA DESTRA.\nVEM, SENHOR, VISITA TUA VINHA,\nQUE COMPRASTE COM TEU \nPRECIOSO SANGUE.\n\nCORO\nVEM, VISITA-ME; Ó VEM, PURIFICA-ME,\nSONDA O MEU CORAÇÃO\nE DÁ-ME O TEU SANTO PERDÃO\nE FAZE-ME REPOUSAR EM TI, SENHOR.\n\nQUERO SER, SENHOR, \nTEU SERVO AMADO POIS CONFIO \nEM TEU PODER Ó MEU JESUS.\nPODER QUE PARA MIM É INFINITO E \nÉ DADO A TODO AQUELE QUE EM TI CRER.\n\nCORO\nVEM, VISITA-ME; Ó VEM, PURIFICA-ME,\nSONDA O MEU CORAÇÃO\nE DÁ-ME O TEU SANTO PERDÃO\nE FAZE-ME REPOUSAR EM TI, SENHOR.\n\nFINAL:\nE FAZE-ME REPOUSAR EM TI, SENHOR.', 'VEM, SENHOR, VISITA TUA VINHA, QUE PLANTASTE EM NOSSAS VIDAS COM TUA DESTRA. VEM, SENHOR, VISITA TUA VINHA, QUE COMPRASTE COM TEU PRECIOSO SANGUE. CORO VEM, VISITA-ME; Ó VEM, PURIFICA-ME, SONDA O MEU CORAÇÃO E DÁ-ME O TEU SANTO PERDÃO E FAZE-ME REPOUSAR EM TI, SENHOR. QUERO SER, SENHOR, TEU SERVO AMADO POIS CONFIO EM TEU PODER Ó MEU JESUS. PODER QUE PARA MIM É INFINITO E É DADO A TODO AQUELE QUE EM TI CRER. CORO VEM, VISITA-ME; Ó VEM, PURIFICA-ME, SONDA O MEU CORAÇÃO E DÁ-ME O TEU SANTO PERDÃO E FAZE-ME REPOUSAR EM TI, SENHOR.  E FAZE-ME REPOUSAR EM TI, SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('464', '464 - TU QUE SOBRE A AMARGA CRUZ', 'TU, QUE SOBRE A AMARGA CRUZ\nREVELASTE TEU AMOR;\nTU, QUE VIVES, Ó JESUS,\nVIVIFICA-NOS, SENHOR!\n\nCORO\nVEM, Ó VEM, JESUS, SENHOR,\nNOSSAS ALMAS DESPERTAR!\nCOM TEU SANTO E PURO AMOR\nVEM, SENHOR, NOS INFLAMAR,\nOH! VEM, OH! VEM, \nNOSSAS ALMAS INFLAMAR!\n\nEIS O MUNDO TENTADOR\nA QUERER NOS ATRAIR;\nSEM TEU FOGO ABRASADOR\nNÃO PODEMOS RESISTIR.\n\nCORO\nVEM, Ó VEM, JESUS, SENHOR,\nNOSSAS ALMAS DESPERTAR!\nCOM TEU SANTO E PURO AMOR\nVEM, SENHOR, NOS INFLAMAR,\nOH! VEM, OH! VEM, \nNOSSAS ALMAS INFLAMAR!\n\nQUANTOS QUE CORRIAM BEM\nDE TI LONGE AGORA VÃO!\nOUTROS SEGUEM, MAS TAMBÉM\nSEM FERVOR VIVENDO ESTÃO.\n\nCORO\nVEM, Ó VEM, JESUS, SENHOR,\nNOSSAS ALMAS DESPERTAR!\nCOM TEU SANTO E PURO AMOR\nVEM, SENHOR, NOS INFLAMAR,\nOH! VEM, OH! VEM, \nNOSSAS ALMAS INFLAMAR!\n\nVEM AGORA CONSUMIR\nTUDO QUANTO, Ó SALVADOR,\nQUER, ALTIVO, RESISTIR\nAO TEU BRANDO E SANTO AMOR.\n\nCORO\nVEM, Ó VEM, JESUS, SENHOR,\nNOSSAS ALMAS DESPERTAR!\nCOM TEU SANTO E PURO AMOR\nVEM, SENHOR, NOS INFLAMAR,\nOH! VEM, OH! VEM, \nNOSSAS ALMAS INFLAMAR!', 'TU, QUE SOBRE A AMARGA CRUZ REVELASTE TEU AMOR; TU, QUE VIVES, Ó JESUS, VIVIFICA-NOS, SENHOR! CORO VEM, Ó VEM, JESUS, SENHOR, NOSSAS ALMAS DESPERTAR! COM TEU SANTO E PURO AMOR VEM, SENHOR, NOS INFLAMAR, OH! VEM, OH! VEM, NOSSAS ALMAS INFLAMAR! EIS O MUNDO TENTADOR A QUERER NOS ATRAIR; SEM TEU FOGO ABRASADOR NÃO PODEMOS RESISTIR. QUANTOS QUE CORRIAM BEM DE TI LONGE AGORA VÃO! OUTROS SEGUEM, MAS TAMBÉM SEM FERVOR VIVENDO ESTÃO. VEM AGORA CONSUMIR TUDO QUANTO, Ó SALVADOR, QUER, ALTIVO, RESISTIR AO TEU BRANDO E SANTO AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('465', '465 - O ESTANDARTE DESTA IGREJA', 'O ESTANDARTE DESTA IGREJA\nLEVANTEMOS SEM TEMOR!\nELA É A MUITO AMADA ESPOSA\nDO BENDITO SALVADOR;\nÉ JESUS O COMANDANTE\nVERDADEIRO QUE A CONDUZ,\nSOMOS, NÓS, OS SEUS SOLDADOS\nNESTA IGREJA DE JESUS.\n\nCORO\nRESOLUTOS, AVANÇAI, \nTRABALHANDO POR JESUS! \nO ESTANDARTE LEVANTAI,\nESPALHANDO A SUA LUZ.\n\nÓ IGREJA DEDIQUEMOS \nNOSSOS CORPOS AO SENHOR! \nNÃO DEVEMOS SER ESCRAVOS\nDO SAGAZ ENGANADOR.\nAS RIQUEZAS NOS SÃO DADAS \nPELA TERNA MÃO REAL, \nE O SENHOR DO CÉU OBSERVA\nSE FAZEMOS BEM OU MAL.\n\nCORO\nRESOLUTOS, AVANÇAI, \nTRABALHANDO POR JESUS! \nO ESTANDARTE LEVANTAI,\nESPALHANDO A SUA LUZ.\n\nGRAÇA E GLÓRIA A TI PERTENCEM, \nÓ ESPOSA DO SENHOR! SÊ, ENTÃO, \nUM INSTRUMENTO DE SALVAR O PECADOR;\nPOIS ATÉ OS FINS DO MUNDO CRISTO \nMESMO REINARÁ E O DOMÍNIO \nDO EVANGELHO TODA A TERRA ABRANGERÁ.\n\nCORO\nRESOLUTOS, AVANÇAI, \nTRABALHANDO POR JESUS! \nO ESTANDARTE LEVANTAI,\nESPALHANDO A SUA LUZ.', 'O ESTANDARTE DESTA IGREJA LEVANTEMOS SEM TEMOR! ELA É A MUITO AMADA ESPOSA DO BENDITO SALVADOR; É JESUS O COMANDANTE VERDADEIRO QUE A CONDUZ, SOMOS, NÓS, OS SEUS SOLDADOS NESTA IGREJA DE JESUS. CORO RESOLUTOS, AVANÇAI, TRABALHANDO POR JESUS! O ESTANDARTE LEVANTAI, ESPALHANDO A SUA LUZ. Ó IGREJA DEDIQUEMOS NOSSOS CORPOS AO SENHOR! NÃO DEVEMOS SER ESCRAVOS DO SAGAZ ENGANADOR. AS RIQUEZAS NOS SÃO DADAS PELA TERNA MÃO REAL, E O SENHOR DO CÉU OBSERVA SE FAZEMOS BEM OU MAL. GRAÇA E GLÓRIA A TI PERTENCEM, Ó ESPOSA DO SENHOR! SÊ, ENTÃO, UM INSTRUMENTO DE SALVAR O PECADOR; POIS ATÉ OS FINS DO MUNDO CRISTO MESMO REINARÁ E O DOMÍNIO DO EVANGELHO TODA A TERRA ABRANGERÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('466', '466 - EIS O ESTANDARTE', 'EIS O ESTANDARTE TREMULANDO À LUZ,\nEIS A SUA DIVISA: C’ROA SOBRE CRUZ!\nPARA A SANTA GUERRA ELE VOS CONDUZ;\nQUEM QUER ALISTAR-SE SOB O REI JESUS?\n\nCORO\nEIS O ESTANDARTE TREMULANDO À LUZ;\nEIS A SUA DIVISA: C’ROA SOBRE CRUZ!\n\nGUERRA CONTRA AS TREVAS! \nGUERRA CONTRA O MAL!\nE CONTRA O PECADO! GUERRA DIVINAL!\nGUERRA CONTRA O MUNDO! \nNELA QUEM ENTRAR HÁ DE, \nSEM RESERVAS, TUDO ABANDONAR.\n\nCORO\nEIS O ESTANDARTE TREMULANDO À LUZ;\nEIS A SUA DIVISA: C’ROA SOBRE CRUZ!\n\nTUDO! SOA DURO? RECEAIS A CRUZ?\nNÃO VOS ENVERGONHE A GRAÇA DE JESUS!\nÓ IRMÃOS, LEMBRAI-VOS, \nQUEM POR ELE SOFRER A COROA \nDA SUA MÃO HÁ DE RECEBER!\n\nCORO\nEIS O ESTANDARTE TREMULANDO À LUZ;\nEIS A SUA DIVISA: C’ROA SOBRE CRUZ!\n\nNESTA SANTA GUERRA DESEJAIS LUTAR\nE A C’ROA DE GLÓRIA LÁ NO CÉU GANHAR?\nA QUEM QUER SEGUI-LO, \nEIS QUE DIZ JESUS: “NEGUE-SE A SI \nMESMO E TOME A SUA CRUZ”.\n\nCORO\nEIS O ESTANDARTE TREMULANDO À LUZ;\nEIS A SUA DIVISA: C’ROA SOBRE CRUZ!\n\nSALVADOR, EU HOJE VENHO ME RENDER,\nSÓ POR TI VENCIDO PODEREI VENCER;\nSÓ CONTIGO MORTO SEMPRE VIVEREI,\nTOMO AGORA MINHA CRUZ, \nMEU BONDOSO REI!\n\nCORO\nEIS O ESTANDARTE TREMULANDO À LUZ;\nEIS A SUA DIVISA: C’ROA SOBRE CRUZ!\n\nSOB TEU ESTANDARTE MARCHAREI, JESUS,\nTUA DIVISA É MINHA: C’ROA SOBRE CRUZ!', 'EIS O ESTANDARTE TREMULANDO À LUZ, EIS A SUA DIVISA: C’ROA SOBRE CRUZ! PARA A SANTA GUERRA ELE VOS CONDUZ; QUEM QUER ALISTAR-SE SOB O REI JESUS? CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! GUERRA CONTRA AS TREVAS! GUERRA CONTRA O MAL! E CONTRA O PECADO! GUERRA DIVINAL! GUERRA CONTRA O MUNDO! NELA QUEM ENTRAR HÁ DE, SEM RESERVAS, TUDO ABANDONAR. CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! TUDO! SOA DURO? RECEAIS A CRUZ? NÃO VOS ENVERGONHE A GRAÇA DE JESUS! Ó IRMÃOS, LEMBRAI-VOS, QUEM POR ELE SOFRER A COROA DA SUA MÃO HÁ DE RECEBER! CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! NESTA SANTA GUERRA DESEJAIS LUTAR E A C’ROA DE GLÓRIA LÁ NO CÉU GANHAR? A QUEM QUER SEGUI-LO, EIS QUE DIZ JESUS: NEGUE-SE A SI MESMO E TOME A SUA CRUZ. CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! SALVADOR, EU HOJE VENHO ME RENDER, SÓ POR TI VENCIDO PODEREI VENCER; SÓ CONTIGO MORTO SEMPRE VIVEREI, TOMO AGORA MINHA CRUZ, MEU BONDOSO REI! CORO EIS O ESTANDARTE TREMULANDO À LUZ; EIS A SUA DIVISA: C’ROA SOBRE CRUZ! SOB TEU ESTANDARTE MARCHAREI, JESUS, TUA DIVISA É MINHA: C’ROA SOBRE CRUZ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('467', '467 - OS GUERREIROS SE PREPARAM', 'OS GUERREIROS SE PREPARAM\nPARA A GRANDE LUTA,\nÉ JESUS O CAPITÃO QUE AVANTE OS LEVARÁ;\nA MILÍCIA DOS REMIDOS MARCHA IMPOLUTA\nCERTA QUE A VITÓRIA ALCANÇARÁ.\n\nCORO\nEU QUERO ESTAR COM CRISTO\nONDE A LUTA SE TRAVAR,\nNO LANCE IMPREVISTO\nNA FRENTE ME ENCONTRAR;\nATÉ QUE O POSSA VER NA GLÓRIA,\nME ALEGRANDO DA VITÓRIA\nONDE DEUS VAI ME COROAR.\n\nEIS OS BATALHÕES DE CRISTO\nPROSSEGUINDO AVANTE!\nNÃO OS VÊS COM QUE VALOR\nCOMBATEM CONTRA O MAL?\nPODES TU FICAR DORMINDO, \nMESMO VACILANTE,\nQUANDO ATACAM OUTROS A BELIAL?\n\nCORO\nEU QUERO ESTAR COM CRISTO\nONDE A LUTA SE TRAVAR,\nNO LANCE IMPREVISTO\nNA FRENTE ME ENCONTRAR;\nATÉ QUE O POSSA VER NA GLÓRIA,\nME ALEGRANDO DA VITÓRIA\nONDE DEUS VAI ME COROAR.\n\nDÁ-TE PRESSA, NÃO VACILES,\nHOJE DEUS TE CHAMA\nPARA VIRES PELEJAR AO LADO DO SENHOR;\nENTRA NA BATALHA ONDE MAIS O FOGO \nINFLAMA E PELEJA CONTRA O VIL TENTADOR.\n\nCORO\nEU QUERO ESTAR COM CRISTO\nONDE A LUTA SE TRAVAR,\nNO LANCE IMPREVISTO\nNA FRENTE ME ENCONTRAR;\nATÉ QUE O POSSA VER NA GLÓRIA,\nME ALEGRANDO DA VITÓRIA\nONDE DEUS VAI ME COROAR.\n\nA PELEJA É TREMENDA, TORNA-SE RENHIDA,\nMAS SÃO POUCOS OS SOLDADOS PARA BATALHAR;\nÓ VEM LIBERTAR AS POBRES ALMAS OPRIMIDAS\nDE QUEM, FURIOSO, AS QUER TRAGAR.\n\nCORO\nEU QUERO ESTAR COM CRISTO\nONDE A LUTA SE TRAVAR,\nNO LANCE IMPREVISTO\nNA FRENTE ME ENCONTRAR;\nATÉ QUE O POSSA VER NA GLÓRIA,\nME ALEGRANDO DA VITÓRIA\nONDE DEUS VAI ME COROAR.', 'OS GUERREIROS SE PREPARAM PARA A GRANDE LUTA, É JESUS O CAPITÃO QUE AVANTE OS LEVARÁ; A MILÍCIA DOS REMIDOS MARCHA IMPOLUTA CERTA QUE A VITÓRIA ALCANÇARÁ. CORO EU QUERO ESTAR COM CRISTO ONDE A LUTA SE TRAVAR, NO LANCE IMPREVISTO NA FRENTE ME ENCONTRAR; ATÉ QUE O POSSA VER NA GLÓRIA, ME ALEGRANDO DA VITÓRIA ONDE DEUS VAI ME COROAR. EIS OS BATALHÕES DE CRISTO PROSSEGUINDO AVANTE! NÃO OS VÊS COM QUE VALOR COMBATEM CONTRA O MAL? PODES TU FICAR DORMINDO, MESMO VACILANTE, QUANDO ATACAM OUTROS A BELIAL? DÁ-TE PRESSA, NÃO VACILES, HOJE DEUS TE CHAMA PARA VIRES PELEJAR AO LADO DO SENHOR; ENTRA NA BATALHA ONDE MAIS O FOGO INFLAMA E PELEJA CONTRA O VIL TENTADOR. A PELEJA É TREMENDA, TORNA-SE RENHIDA, MAS SÃO POUCOS OS SOLDADOS PARA BATALHAR; Ó VEM LIBERTAR AS POBRES ALMAS OPRIMIDAS DE QUEM, FURIOSO, AS QUER TRAGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('468', '468 - HÁ TANTA TERRA', 'HÁ TANTA TERRA PRA POSSUIR,\nMAS NA BATALHA NÃO ESTAMOS SÓS;\nMUITAS PROMESSAS SE VÃO CUMPRIR\nSE O SENHOR SE AGRADAR DE NÓS.\n\nCORO (2X)\nASSIM NÓS VAMOS COM FÉ \nNO TEU PODER, SE FORES CONOSCO, \nSENHOR, IREMOS VENCER!\n\nINSTRUMENTOS\n\nCERCA A CIDADE COM ORAÇÃO,\nLEVANTA, IGREJA, A TUA VOZ;\nDIANTE DE TI MUROS RUIRÃO\nSE O SENHOR SE AGRADAR DE NÓS.\n\nCORO (2X)\nASSIM NÓS VAMOS COM FÉ \nNO TEU PODER, SE FORES CONOSCO, \nSENHOR, IREMOS VENCER!', 'HÁ TANTA TERRA PRA POSSUIR, MAS NA BATALHA NÃO ESTAMOS SÓS; MUITAS PROMESSAS SE VÃO CUMPRIR SE O SENHOR SE AGRADAR DE NÓS. ASSIM NÓS VAMOS COM FÉ NO TEU PODER, SE FORES CONOSCO, SENHOR, IREMOS VENCER! INSTRUMENTOS CERCA A CIDADE COM ORAÇÃO, LEVANTA, IGREJA, A TUA VOZ; DIANTE DE TI MUROS RUIRÃO SE O SENHOR SE AGRADAR DE NÓS. ASSIM NÓS VAMOS COM FÉ NO TEU PODER, SE FORES CONOSCO, SENHOR, IREMOS VENCER!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('469', '469 - CASTELO FORTE', 'CASTELO FORTE É O NOSSO DEUS,\nESPADA E BOM ESCUDO;\nCOM SEU PODER DEFENDE OS SEUS,\nSOCORRO TRAZ E PROTEÇÃO.\nSABEIS QUEM É JESUS?\nO QUE VENCEU NA CRUZ,\nSENHOR DOS ALTOS CÉUS\nE SENDO O PRÓPRIO DEUS,\nTRIUNFA NA BATALHA.\n\nA NOSSA FORÇA NADA FAZ,\nESTAMOS CONVENCIDOS;\nMAS NOSSO DEUS SOCORRO TRAZ\nE SOMOS PROTEGIDOS.\nSE TEMOS DE PERDER\nRIQUEZAS, BENS QUE HOUVER,\nEMBORA A VIDA VÁ,\nPOR NÓS JESUS ESTÁ\nE DAR-NOS-Á SEU REINO.\n\nSIM, QUE A PALAVRA FICARÁ\nSABEMOS COM CERTEZA;\nE NADA NOS ASSUSTARÁ\nCOM CRISTO POR DEFESA.\nSABEIS QUEM É JESUS?\nO QUE VENCEU NA CRUZ,\nSENHOR DOS ALTOS CÉUS\nE SENDO O PRÓPRIO DEUS,\nTRIUNFA NA BATALHA.', 'CASTELO FORTE É O NOSSO DEUS, ESPADA E BOM ESCUDO; COM SEU PODER DEFENDE OS SEUS, SOCORRO TRAZ E PROTEÇÃO. SABEIS QUEM É JESUS? O QUE VENCEU NA CRUZ, SENHOR DOS ALTOS CÉUS E SENDO O PRÓPRIO DEUS, TRIUNFA NA BATALHA. A NOSSA FORÇA NADA FAZ, ESTAMOS CONVENCIDOS; MAS NOSSO DEUS SOCORRO TRAZ E SOMOS PROTEGIDOS. SE TEMOS DE PERDER RIQUEZAS, BENS QUE HOUVER, EMBORA A VIDA VÁ, POR NÓS JESUS ESTÁ E DAR-NOS-Á SEU REINO. SIM, QUE A PALAVRA FICARÁ SABEMOS COM CERTEZA; E NADA NOS ASSUSTARÁ COM CRISTO POR DEFESA. SABEIS QUEM É JESUS? O QUE VENCEU NA CRUZ, SENHOR DOS ALTOS CÉUS E SENDO O PRÓPRIO DEUS, TRIUNFA NA BATALHA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('470', '470 - NO GETSÊMANI', 'NO GETSÊMANI JESUS OROU: \n“PASSA DE MIM ESTE CÁLICE”, \nMAS O ALTAR ESTAVA PRONTO PARA O RECEBER;\nO CUTELO, A LENHA E O FOGO \nAGUARDAVAM O CORDEIRO\nQUE DARIA A SUA VIDA PELO MUNDO.\n\nCORO\nAO LAGAR DESCEU O FRUTO \nDA VIDEIRA E CORREU DA COR \nDO SANGUE O SEU MOSTO\nPRA QUE EU PUDESSE ENCHER \nMEU CÁLICE DE SALVAÇÃO.\n\nINSTRUMENTOS\nBIS\n\nHOJE AQUI NÓS CELEBRAMOS \nESTA PÁSCOA VERDADEIRA \nPOIS DO JUGO LIBERTADOS \nTEMOS PAZ COM DEUS;\nCOMO MEMBROS DE UM SÓ CORPO\nPARTILHAMOS DE UM MESMO PÃO\nE BEBEMOS DESTE CÁLICE \nDO SEU SANGUE.\n\nCORO\nAO LAGAR DESCEU O FRUTO \nDA VIDEIRA E CORREU DA COR \nDO SANGUE O SEU MOSTO\nPRA QUE EU PUDESSE ENCHER \nMEU CÁLICE DE SALVAÇÃO.\nBIS', 'NO GETSÊMANI JESUS OROU: PASSA DE MIM ESTE CÁLICE, MAS O ALTAR ESTAVA PRONTO PARA O RECEBER; O CUTELO, A LENHA E O FOGO AGUARDAVAM O CORDEIRO QUE DARIA A SUA VIDA PELO MUNDO. CORO AO LAGAR DESCEU O FRUTO DA VIDEIRA E CORREU DA COR DO SANGUE O SEU MOSTO PRA QUE EU PUDESSE ENCHER MEU CÁLICE DE SALVAÇÃO. INSTRUMENTOS HOJE AQUI NÓS CELEBRAMOS ESTA PÁSCOA VERDADEIRA POIS DO JUGO LIBERTADOS TEMOS PAZ COM DEUS; COMO MEMBROS DE UM SÓ CORPO PARTILHAMOS DE UM MESMO PÃO E BEBEMOS DESTE CÁLICE DO SEU SANGUE. CORO AO LAGAR DESCEU O FRUTO DA VIDEIRA E CORREU DA COR DO SANGUE O SEU MOSTO PRA QUE EU PUDESSE ENCHER MEU CÁLICE DE SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('471', '471 - VEM, ESPÍRITO DE DEUS', 'VEM, ESPÍRITO DE DEUS,\nEU PRECISO DE TI. \n\nCORO\nDE JESUS SERÁ A GLÓRIA E DELE SERÁ \nO LOUVOR, VEM, ESPÍRITO DE DEUS, \nEU PRECISO DE TI.\n\nMESMO SENDO PEQUENINO \nEU PRECISO DE TI.\nBIS\nBIS', 'VEM, ESPÍRITO DE DEUS, EU PRECISO DE TI. CORO DE JESUS SERÁ A GLÓRIA E DELE SERÁ O LOUVOR, VEM, ESPÍRITO DE DEUS, EU PRECISO DE TI. MESMO SENDO PEQUENINO EU PRECISO DE TI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('472', '472 - VINDE, CANTAI E ENTOAI LOUVORES A JESUS', 'VINDE, CANTAI E ENTOAI LOUVORES A JESUS,\nQUE PARA A NOSSA SALVAÇÃO\nMORREU NA AMARGA CRUZ;\nSEU SANGUE DERRAMOU, \nDE TUDO ME LAVOU, MAIS ALVO \nDO QUE A NEVE ME TORNOU.\n\nCORO\nO SANGUE DE JESUS ME LAVOU, ME LAVOU,\nO SANGUE DE JESUS ME LAVOU, ME LAVOU,\nALEGRE CANTAREI LOUVORES AO MEU REI,\nA MEU SENHOR JESUS QUE ME SALVOU.\n\nVINDE CONOSCO VOS UNIR NA \nGUERRA CONTRA O MAL\nE COM O NOSSO SALVADOR EM MARCHA TRIUNFAL,\nA TODOS PROCLAMAR SUA GRAÇA E SEU PODER,\nSEU SANGUE DERRAMOU PRA NOS SALVAR.\n\nCORO\nO SANGUE DE JESUS ME LAVOU, ME LAVOU,\nO SANGUE DE JESUS ME LAVOU, ME LAVOU,\nALEGRE CANTAREI LOUVORES AO MEU REI,\nA MEU SENHOR JESUS QUE ME SALVOU.\n\nO CAPITÃO DA SALVAÇÃO É CRISTO, O SALVADOR;\nO REI DOS REIS, O REDENTOR, JESUS, NOSSO SENHOR;\nELE TUDO VENCERÁ, VITÓRIA NOS DARÁ,\nÀ GLÓRIA, SALVOS, NOS CONDUZIRÁ.\n\nCORO\nO SANGUE DE JESUS ME LAVOU, ME LAVOU,\nO SANGUE DE JESUS ME LAVOU, ME LAVOU,\nALEGRE CANTAREI LOUVORES AO MEU REI,\nA MEU SENHOR JESUS QUE ME SALVOU.', 'VINDE, CANTAI E ENTOAI LOUVORES A JESUS, QUE PARA A NOSSA SALVAÇÃO MORREU NA AMARGA CRUZ; SEU SANGUE DERRAMOU, DE TUDO ME LAVOU, MAIS ALVO DO QUE A NEVE ME TORNOU. CORO O SANGUE DE JESUS ME LAVOU, ME LAVOU, O SANGUE DE JESUS ME LAVOU, ME LAVOU, ALEGRE CANTAREI LOUVORES AO MEU REI, A MEU SENHOR JESUS QUE ME SALVOU. VINDE CONOSCO VOS UNIR NA GUERRA CONTRA O MAL E COM O NOSSO SALVADOR EM MARCHA TRIUNFAL, A TODOS PROCLAMAR SUA GRAÇA E SEU PODER, SEU SANGUE DERRAMOU PRA NOS SALVAR. O CAPITÃO DA SALVAÇÃO É CRISTO, O SALVADOR; O REI DOS REIS, O REDENTOR, JESUS, NOSSO SENHOR; ELE TUDO VENCERÁ, VITÓRIA NOS DARÁ, À GLÓRIA, SALVOS, NOS CONDUZIRÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('473', '473 - MINHA PORÇÃO', 'MINHA PORÇÃO ÉS TU, SENHOR,\nE TODO MEU SER TE DAREI COM AMOR.\nFOI NO TEU FALAR QUE DESCOBRI,\nQUE ÉS TUDO, TUDO PRA MIM!\n\nCORO\nÉS MEU AUXÍLIO, MEU GRANDE AMOR,\nE O MEU TESOURO, ÉS TU MEU SENHOR.\nNA TUA OBRA VOU ME DEDICAR, \nE SEMPRE TE AMAR.\n\nQUERO OUVIR TUA VOZ EM MEU VIVER,\nPOIS QUANDO TU FALAS \nSINTO GOZO EM MEU SER.\nTUA DESTRA É A LUZ QUE ME CONDUZ,\nAOS TEUS BRAÇOS, MEU PAI E SENHOR.\n\nCORO (BIS)\nÉS MEU AUXÍLIO, MEU GRANDE AMOR,\nE O MEU TESOURO, ÉS TU MEU SENHOR.\nNA TUA OBRA VOU ME DEDICAR, \nE SEMPRE TE AMAR.\n\nFINAL: E SEMPRE TE AMAR.', 'MINHA PORÇÃO ÉS TU, SENHOR, E TODO MEU SER TE DAREI COM AMOR. FOI NO TEU FALAR QUE DESCOBRI, QUE ÉS TUDO, TUDO PRA MIM! CORO ÉS MEU AUXÍLIO, MEU GRANDE AMOR, E O MEU TESOURO, ÉS TU MEU SENHOR. NA TUA OBRA VOU ME DEDICAR, E SEMPRE TE AMAR. QUERO OUVIR TUA VOZ EM MEU VIVER, POIS QUANDO TU FALAS SINTO GOZO EM MEU SER. TUA DESTRA É A LUZ QUE ME CONDUZ, AOS TEUS BRAÇOS, MEU PAI E SENHOR. CORO  ÉS MEU AUXÍLIO, MEU GRANDE AMOR, E O MEU TESOURO, ÉS TU MEU SENHOR. NA TUA OBRA VOU ME DEDICAR, E SEMPRE TE AMAR.  E SEMPRE TE AMAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('474', '474 - QUEM SOU EU, SENHOR?', 'QUEM SOU EU, SENHOR?\nPRA TE LEMBRARES DE MIM,\nPRA ME TRAZERES ATÉ AQUI\nE ME DIZER COISAS TÃO LINDAS.\n\nTU SABES QUEM EU SOU,\nO MENOR DE MINHA CASA,\nE NO TEU AMOR, SENHOR, \nPROFETIZASTE A MIM\nUM CAMINHO NOVO A TRILHAR.\n\nCORO (2X)\nME TOMASTE, SENHOR, DA TUA MALHADA,\nDE TRÁS DAS TUAS OVELHAS ME TIRASTE Ó DEUS,\nPOR ONDE QUER QUE EU FOR, PARA O TEU LOUVOR,\nA TUA PRESENÇA COMIGO IRÁ.\n\nFINAL: \nSE MINHA VIDA ABENÇOARES,\nPARA SEMPRE ABENÇOADA SERÁ.', 'QUEM SOU EU, SENHOR? PRA TE LEMBRARES DE MIM, PRA ME TRAZERES ATÉ AQUI E ME DIZER COISAS TÃO LINDAS. TU SABES QUEM EU SOU, O MENOR DE MINHA CASA, E NO TEU AMOR, SENHOR, PROFETIZASTE A MIM UM CAMINHO NOVO A TRILHAR. ME TOMASTE, SENHOR, DA TUA MALHADA, DE TRÁS DAS TUAS OVELHAS ME TIRASTE Ó DEUS, POR ONDE QUER QUE EU FOR, PARA O TEU LOUVOR, A TUA PRESENÇA COMIGO IRÁ.  SE MINHA VIDA ABENÇOARES, PARA SEMPRE ABENÇOADA SERÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('475', '475 - AINDA UMA VEZ (AGEU)', '“AINDA UMA VEZ, DAQUI A POUCO,\nE FAREI TREMER OS CÉUS,\nE TODA A TERRA, TODO MAR E A TERRA SECA,\nFAREI TREMER NAÇÕES,\nE ENFIM VIRÁ O DESEJADO DAS NAÇÕES.”\n\nCORO\n“E EU ENCHEREI ASSIM ESTE LUGAR, (EU ENCHEREI)\nCOM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR),”\nDIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR),\nDIZ O SENHOR.\n\n“MINHA É A PRATA E MEU O OURO”,\nASSIM DIZ O SENHOR,\n“E A GLÓRIA DESTA CASA ENFIM EXCEDERÁ,\nA GLÓRIA DA PRIMEIRA,\nE NESTE LUGAR, DAREI A MINHA PAZ.”\n\nCORO\n“E EU ENCHEREI ASSIM ESTE LUGAR, (EU ENCHEREI)\nCOM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR),”\nDIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR),\nDIZ O SENHOR DOS EXÉRCITOS!\n\nFINAL:\n“SIM ESTE LUGAR (EU ENCHEREI)\nCOM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR)”\nDIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR),\nDIZ O SENHOR JESUS.', 'AINDA UMA VEZ, DAQUI A POUCO, E FAREI TREMER OS CÉUS, E TODA A TERRA, TODO MAR E A TERRA SECA, FAREI TREMER NAÇÕES, E ENFIM VIRÁ O DESEJADO DAS NAÇÕES. CORO E EU ENCHEREI ASSIM ESTE LUGAR, (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR), DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR. MINHA É A PRATA E MEU O OURO, ASSIM DIZ O SENHOR, E A GLÓRIA DESTA CASA ENFIM EXCEDERÁ, A GLÓRIA DA PRIMEIRA, E NESTE LUGAR, DAREI A MINHA PAZ. CORO E EU ENCHEREI ASSIM ESTE LUGAR, (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR), DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR DOS EXÉRCITOS! SIM ESTE LUGAR (EU ENCHEREI) COM MINHA GLÓRIA ENCHEREI ESTE LUGAR (ESTE LUGAR) DIZ O SENHOR DOS EXÉRCITOS (DIZ O SENHOR), DIZ O SENHOR JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('476', '476 - ESTA OBRA É DE DEUS', 'ESTA OBRA É DE DEUS, \nNINGUÉM A PODE IMPEDIR, \nFOI JESUS QUEM CONVOCOU \nESTÁ NA HORA DE AGIR.\nBIS\n\nVAMOS TODOS! VAMOS TODOS!\nTRABALHAR PARA A GLÓRIA DE JESUS.\nALELUIA! ALELUIA!\nLOUVANDO AQUELE QUE POR NÓS\nMORREU NA CRUZ, GLÓRIA A JESUS!\nALELUIA! ALELUIA!\nGLORIFICADO SEJA O NOME DO SENHOR.\n\nVIVO CONTENTE, VIVO ALEGRE, \nPORQUE JESUS JÁ ME LIBERTOU.\nVIVO CONTENTE, VIVO ALEGRE, \nPORQUE JESUS JÁ ME LIBERTOU.', 'ESTA OBRA É DE DEUS, NINGUÉM A PODE IMPEDIR, FOI JESUS QUEM CONVOCOU ESTÁ NA HORA DE AGIR. VAMOS TODOS! VAMOS TODOS! TRABALHAR PARA A GLÓRIA DE JESUS. ALELUIA! ALELUIA! LOUVANDO AQUELE QUE POR NÓS MORREU NA CRUZ, GLÓRIA A JESUS! ALELUIA! ALELUIA! GLORIFICADO SEJA O NOME DO SENHOR. VIVO CONTENTE, VIVO ALEGRE, PORQUE JESUS JÁ ME LIBERTOU. VIVO CONTENTE, VIVO ALEGRE, PORQUE JESUS JÁ ME LIBERTOU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('477', '477 - SEMPRE VOU LEMBRAR', 'SEMPRE VOU LEMBRAR DA CORAGEM E DO AMOR, \nDA FIRMEZA E VONTADE, DA FÉ E DEVOÇÃO. \nDA IGREJA DO TEMPO DE PAULO, DE PEDRO E JOÃO. \nENFRENTARAM A FÚRIA DE ROMA,\nMAS NUNCA NEGARAM SUA FÉ DE CRISTÃO \nÉ UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO.\n\nSEMPRE VOU LEMBRAR DA BRAVURA DOS SANTOS,\nEM PLENA ARENA ENFRENTANDO OS LEÕES. \nQUANTO MAIS A FOGUEIRA QUEIMAVA, \nMAIS SE OUVIA O LOUVOR DOS NOSSOS IRMÃOS.\nSOBRE O SANGUE TOMBAVAM NAS RUAS,\nCHEGAVAM NOS CÉUS COM A VITÓRIA NAS MÃOS. \nÉ UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO.\n\nCORO\nÓ MEU DEUS, REAVIVA A TUA IGREJA DE NOVO.\nFAZ A CHAMA ARDER NESTE POVO,\nCOMO FORAM OS PRIMEIROS CRISTÃOS.\nÓ MEU DEUS, REAVIVA TUA IGREJA DE NOVO.  \nFAZ A CHAMA ARDER NESTE POVO, \nCOMEÇANDO EM MEU CORAÇÃO.\n\nINSTRUMENTOS\n\nSEMPRE VOU LEMBRAR DA BRAVURA DOS SANTOS,\nEM PLENA ARENA ENFRENTANDO OS LEÕES. \nQUANTO MAIS A FOGUEIRA QUEIMAVA, \nMAIS SE OUVIA O LOUVOR DOS NOSSOS IRMÃOS.\nSOBRE O SANGUE TOMBAVAM NAS RUAS,\nCHEGAVAM NOS CÉUS COM A VITÓRIA NAS MÃOS. \nÉ UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO.\n\nCORO\nÓ MEU DEUS, REAVIVA A TUA IGREJA DE NOVO.\nFAZ A CHAMA ARDER NESTE POVO,\nCOMO FORAM OS PRIMEIROS CRISTÃOS.\nÓ MEU DEUS, REAVIVA TUA IGREJA DE NOVO.  \nFAZ A CHAMA ARDER NESTE POVO, \nCOMEÇANDO EM MEU CORAÇÃO.\n\nFINAL:\nÓ MEU DEUS, REAVIVA TUA IGREJA DE NOVO.  \nFAZ A CHAMA ARDER NESTE POVO, \nCOMEÇANDO EM MEU CORAÇÃO.', 'SEMPRE VOU LEMBRAR DA CORAGEM E DO AMOR, DA FIRMEZA E VONTADE, DA FÉ E DEVOÇÃO. DA IGREJA DO TEMPO DE PAULO, DE PEDRO E JOÃO. ENFRENTARAM A FÚRIA DE ROMA, MAS NUNCA NEGARAM SUA FÉ DE CRISTÃO É UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO. SEMPRE VOU LEMBRAR DA BRAVURA DOS SANTOS, EM PLENA ARENA ENFRENTANDO OS LEÕES. QUANTO MAIS A FOGUEIRA QUEIMAVA, MAIS SE OUVIA O LOUVOR DOS NOSSOS IRMÃOS. SOBRE O SANGUE TOMBAVAM NAS RUAS, CHEGAVAM NOS CÉUS COM A VITÓRIA NAS MÃOS. É UM EXEMPLO PRA MIM, VERDADEIRA LIÇÃO. CORO Ó MEU DEUS, REAVIVA A TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMO FORAM OS PRIMEIROS CRISTÃOS. Ó MEU DEUS, REAVIVA TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMEÇANDO EM MEU CORAÇÃO. INSTRUMENTOS CORO Ó MEU DEUS, REAVIVA A TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMO FORAM OS PRIMEIROS CRISTÃOS. Ó MEU DEUS, REAVIVA TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMEÇANDO EM MEU CORAÇÃO. Ó MEU DEUS, REAVIVA TUA IGREJA DE NOVO. FAZ A CHAMA ARDER NESTE POVO, COMEÇANDO EM MEU CORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('478', '478 - VI UM NOVO CÉU', 'VI UM NOVO CÉU, UMA NOVA TERRA, \nEU VI A NOVA JERUSALÉM.\n\nVI UM TRONO BRANCO E SOBRE \nELE UM SER,\nCUJO ROSTO EU NÃO PUDE VER\nMAS EU SEI QUE ERA O MEU JESUS.\nBIS\n\nVI UM CORO CELESTIAL, \nVI OS ANJOS DE DEUS\nQUE CANTAVAM EM UMA SÓ VOZ:\nVIVA O REI JESUS!\n\nINSTRUMENTOS\n\nEM BREVE ESTAREMOS NESSE NOVO LAR, \nCANTANDO: VIVA O REI JESUS!\nEM RUAS DE OURO NÓS VAMOS ANDAR,\nDA ÁRVORE DA VIDA DESFRUTAR.\nBIS\n\nE COM JESUS PARA SEMPRE MORAR, \n(VOU MORAR) (VARÕES)\nE COM JESUS PARA SEMPRE MORAR, \n(VOU LOUVAR) (VARÕES)\nE COM JESUS PARA SEMPRE MORAR.', 'VI UM NOVO CÉU, UMA NOVA TERRA, EU VI A NOVA JERUSALÉM. VI UM TRONO BRANCO E SOBRE ELE UM SER, CUJO ROSTO EU NÃO PUDE VER MAS EU SEI QUE ERA O MEU JESUS. VI UM CORO CELESTIAL, VI OS ANJOS DE DEUS QUE CANTAVAM EM UMA SÓ VOZ: VIVA O REI JESUS! INSTRUMENTOS EM BREVE ESTAREMOS NESSE NOVO LAR, CANTANDO: VIVA O REI JESUS! EM RUAS DE OURO NÓS VAMOS ANDAR, DA ÁRVORE DA VIDA DESFRUTAR. E COM JESUS PARA SEMPRE MORAR, (VOU MORAR)  E COM JESUS PARA SEMPRE MORAR, (VOU LOUVAR)  E COM JESUS PARA SEMPRE MORAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('479', '479 - QUEM ME DERA', 'QUEM ME DERA FOSSEM AGORA\nESCRITAS AS MINHAS PALAVRAS,\nCOM PENA DE FERRO GRAVADAS,\nPRA SEMPRE ESCULPIDAS NA ROCHA.\n\nCORO\nEU SEI QUE VIVE MEU REDENTOR\nE POR FIM SOBRE A TERRA VIRÁ;\nO MEU CORPO SE LEVANTARÁ\nREVESTIDO DE GLÓRIA E LOUVOR. \n\nINSTRUMENTOS\n\nFACE A FACE VEREI MEU SENHOR,\nOS MEUS OLHOS O CONTEMPLARÃO;\nDE SAUDADE PULSA O CORAÇÃO,\nMINH’ALMA CLAMA: VEM, REDENTOR!\n\nCORO (2X)\nEU SEI QUE VIVE MEU REDENTOR\nE POR FIM SOBRE A TERRA VIRÁ;\nO MEU CORPO SE LEVANTARÁ\nREVESTIDO DE GLÓRIA E LOUVOR. \n\nREVESTIDO DE GLÓRIA E LOUVOR.\nREVESTIDO DE GLÓRIA E LOUVOR.', 'QUEM ME DERA FOSSEM AGORA ESCRITAS AS MINHAS PALAVRAS, COM PENA DE FERRO GRAVADAS, PRA SEMPRE ESCULPIDAS NA ROCHA. CORO EU SEI QUE VIVE MEU REDENTOR E POR FIM SOBRE A TERRA VIRÁ; O MEU CORPO SE LEVANTARÁ REVESTIDO DE GLÓRIA E LOUVOR. INSTRUMENTOS FACE A FACE VEREI MEU SENHOR, OS MEUS OLHOS O CONTEMPLARÃO; DE SAUDADE PULSA O CORAÇÃO, MINH’ALMA CLAMA: VEM, REDENTOR! EU SEI QUE VIVE MEU REDENTOR E POR FIM SOBRE A TERRA VIRÁ; O MEU CORPO SE LEVANTARÁ REVESTIDO DE GLÓRIA E LOUVOR. REVESTIDO DE GLÓRIA E LOUVOR. REVESTIDO DE GLÓRIA E LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('480', '480 - EU ESPERO, ANSIOSO, POR MEU DEUS', 'EU ESPERO, ANSIOSO, POR MEU DEUS\nEU ESPERO, ANSIOSO, POR MEU DEUS\nDE, COM ELE, ESTAR PARA SEMPRE LÁ NO CÉU,\nCOM MEU DEUS QUERO ESTAR PARA SEMPRE.\n\nMUITAS LUTAS EU TEREI DE ENFRENTAR,\nMUITAS LUTAS EU TEREI DE SUPORTAR,\nMAS SEI QUE O MEU SENHOR \nNUNCA MAIS VAI ME DEIXAR, \nCOM MEU DEUS QUERO ESTAR PARA SEMPRE.\n\nCOM MEU DEUS EU SOU MUITO FELIZ (2X)\nPOIS SEI QUE O MEU SENHOR \nNUNCA MAIS VAI ME DEIXAR, \nCOM MEU DEUS QUERO ESTAR PARA SEMPRE, \nPARA SEMPRE VOU ESTAR.', 'EU ESPERO, ANSIOSO, POR MEU DEUS EU ESPERO, ANSIOSO, POR MEU DEUS DE, COM ELE, ESTAR PARA SEMPRE LÁ NO CÉU, COM MEU DEUS QUERO ESTAR PARA SEMPRE. MUITAS LUTAS EU TEREI DE ENFRENTAR, MUITAS LUTAS EU TEREI DE SUPORTAR, MAS SEI QUE O MEU SENHOR NUNCA MAIS VAI ME DEIXAR, COM MEU DEUS QUERO ESTAR PARA SEMPRE. COM MEU DEUS EU SOU MUITO FELIZ  POIS SEI QUE O MEU SENHOR NUNCA MAIS VAI ME DEIXAR, COM MEU DEUS QUERO ESTAR PARA SEMPRE, PARA SEMPRE VOU ESTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('481', '481 - ASSIM COMO A NOITE ESPERA PELO DIA', 'ASSIM COMO A NOITE ESPERA PELO DIA,\nASSIM COMO A PRAIA ESPERA PELO MAR\nASSIM COMO AS NUVENS ESPERAM PELO VENTO, \nCOM MAIS ANSIEDADE EU ESTOU A TE ESPERAR.\n\nCORO\nTE ESPERO, CHORANDO, TE ESPERO, \nMESTRE QUERIDO, ESTOU ME ENCURVANDO \nANTE O PESO DA CRUZ;\nME AJUDA A CHEGAR AO MEU LAR PROMETIDO, \nSEM TI NÃO CONSIGO VIVER, MEU JESUS.\n\nBEM MAIS QUE AS FLORES ESPERAM PELO ORVALHO,\nBEM MAIS QUE O PERDIDO ESPERA SE ENCONTRAR,\nBEM MAIS QUE O FILHO ESPERA A MÃE QUERIDA, \nCOM MAIS ANSIEDADE EU ESTOU A TE ESPERAR.\n\nCORO\nTE ESPERO, CHORANDO, TE ESPERO, \nMESTRE QUERIDO, ESTOU ME ENCURVANDO \nANTE O PESO DA CRUZ;\nME AJUDA A CHEGAR AO MEU LAR PROMETIDO, \nSEM TI NÃO CONSIGO VIVER, MEU JESUS.\n\nFINAL: Ó, MEU JESUS.', 'ASSIM COMO A NOITE ESPERA PELO DIA, ASSIM COMO A PRAIA ESPERA PELO MAR ASSIM COMO AS NUVENS ESPERAM PELO VENTO, COM MAIS ANSIEDADE EU ESTOU A TE ESPERAR. CORO TE ESPERO, CHORANDO, TE ESPERO, MESTRE QUERIDO, ESTOU ME ENCURVANDO ANTE O PESO DA CRUZ; ME AJUDA A CHEGAR AO MEU LAR PROMETIDO, SEM TI NÃO CONSIGO VIVER, MEU JESUS. BEM MAIS QUE AS FLORES ESPERAM PELO ORVALHO, BEM MAIS QUE O PERDIDO ESPERA SE ENCONTRAR, BEM MAIS QUE O FILHO ESPERA A MÃE QUERIDA, COM MAIS ANSIEDADE EU ESTOU A TE ESPERAR. CORO TE ESPERO, CHORANDO, TE ESPERO, MESTRE QUERIDO, ESTOU ME ENCURVANDO ANTE O PESO DA CRUZ; ME AJUDA A CHEGAR AO MEU LAR PROMETIDO, SEM TI NÃO CONSIGO VIVER, MEU JESUS.  Ó, MEU JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('482', '482 - OS SINAIS DO FIM SE CUMPREM', 'OS SINAIS DO FIM SE CUMPREM EM NOSSA GERAÇÃO,\nTODA A TERRA SE ANGUSTIA, GEME A CRIAÇÃO;\nTREVAS CAEM SOBRE O MUNDO, \nSÓ UM POVO TEM A LUZ,\nQUEM TEM SEDE VENHA E BEBA, ÁGUA VIVA É JESUS.\n\nCORO\nDEUS É POR NÓS, \nQUEM SERÁ CONTRA NÓS?\nQUEM PODERÁ SEPARAR-NOS \nDO AMOR QUE HÁ EM JESUS?\n\nNÓS TAMBÉM JUNTOS GEMEMOS ANSIANDO A PARTIDA, \nNÃO GEMEMOS PARA A MORTE MAS PELA ETERNA VIDA,\nPOIS JÁ A LUZ A VISTA ALCANÇA, NOVO DIA VAI RAIAR, \nCUMPRE-SE NOSSA ESPERANÇA, \nJESUS CRISTO VAI VOLTAR!\n\nCORO\nDEUS É POR NÓS, \nQUEM SERÁ CONTRA NÓS?\nQUEM PODERÁ SEPARAR-NOS\nDO AMOR QUE HÁ EM JESUS?', 'OS SINAIS DO FIM SE CUMPREM EM NOSSA GERAÇÃO, TODA A TERRA SE ANGUSTIA, GEME A CRIAÇÃO; TREVAS CAEM SOBRE O MUNDO, SÓ UM POVO TEM A LUZ, QUEM TEM SEDE VENHA E BEBA, ÁGUA VIVA É JESUS. CORO DEUS É POR NÓS, QUEM SERÁ CONTRA NÓS? QUEM PODERÁ SEPARAR-NOS DO AMOR QUE HÁ EM JESUS? NÓS TAMBÉM JUNTOS GEMEMOS ANSIANDO A PARTIDA, NÃO GEMEMOS PARA A MORTE MAS PELA ETERNA VIDA, POIS JÁ A LUZ A VISTA ALCANÇA, NOVO DIA VAI RAIAR, CUMPRE-SE NOSSA ESPERANÇA, JESUS CRISTO VAI VOLTAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('483', '483 - CHEGOU O TEMPO DE PENSAR', 'CHEGOU O TEMPO DE PENSAR\nNAS COISAS QUE SÃO LÁ DO CÉU,\nDEIXANDO ESTE MUNDO MAU,\nCOM TODOS OS PECADOS SEUS;\nPENSAI NAS COISAS LÁ DE CIMA,\nQUE SE APROXIMA O REINO DE DEUS.\nBIS\n\nCORO\nÓ BUSCAI AS BÊNÇÃOS DO SENHOR,\nCONFIAI NO SEU IMENSO AMOR;\nOS SINAIS ESTÃO NOS DECLARANDO\nQUE VAI SE PASSANDO O QUE JESUS FALOU;\nVEM A CRISTO QUE ELE TE REVESTE  \nCOM O DOM CELESTE DO CONSOLADOR.\nBIS\n\nCOMO FOI NOS DIAS DE NOÉ,\nCOM O DILÚVIO TUDO SE ACABOU,\nTAMBÉM NOS DIAS DE GOMORRA\nO FOGO TUDO DEVOROU;\nPORÉM NÃO HOUVE AGONIA\nCOMO NESSE DIA, DIA DO SENHOR.\nBIS\n\nCORO\nÓ BUSCAI AS BÊNÇÃOS DO SENHOR,\nCONFIAI NO SEU IMENSO AMOR;\nOS SINAIS ESTÃO NOS DECLARANDO\nQUE VAI SE PASSANDO O QUE JESUS FALOU;\nVEM A CRISTO QUE ELE TE REVESTE  \nCOM O DOM CELESTE DO CONSOLADOR.\nBIS', 'CHEGOU O TEMPO DE PENSAR NAS COISAS QUE SÃO LÁ DO CÉU, DEIXANDO ESTE MUNDO MAU, COM TODOS OS PECADOS SEUS; PENSAI NAS COISAS LÁ DE CIMA, QUE SE APROXIMA O REINO DE DEUS. CORO Ó BUSCAI AS BÊNÇÃOS DO SENHOR, CONFIAI NO SEU IMENSO AMOR; OS SINAIS ESTÃO NOS DECLARANDO QUE VAI SE PASSANDO O QUE JESUS FALOU; VEM A CRISTO QUE ELE TE REVESTE COM O DOM CELESTE DO CONSOLADOR. COMO FOI NOS DIAS DE NOÉ, COM O DILÚVIO TUDO SE ACABOU, TAMBÉM NOS DIAS DE GOMORRA O FOGO TUDO DEVOROU; PORÉM NÃO HOUVE AGONIA COMO NESSE DIA, DIA DO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('484', '484 - TEMEI A DEUS', 'TEMEI A DEUS E DAI-LHE GLÓRIA\nPOIS É CHEGADA A HORA DO SEU JUÍZO;\nE ADORAI AQUELE QUE FEZ O CÉU E A TERRA\nE O MAR E AS FONTES DAS ÁGUAS.\n\nTUA IGREJA TE ESPERA, \nTODOS OS SINAIS SÃO CUMPRIDOS,\nDO FIRMAMENTO TU NOS OUVES\nVEM JÁ, JESUS!\n\nFINAL: VEM JÁ, JESUS!\nBIS', 'TEMEI A DEUS E DAI-LHE GLÓRIA POIS É CHEGADA A HORA DO SEU JUÍZO; E ADORAI AQUELE QUE FEZ O CÉU E A TERRA E O MAR E AS FONTES DAS ÁGUAS. TUA IGREJA TE ESPERA, TODOS OS SINAIS SÃO CUMPRIDOS, DO FIRMAMENTO TU NOS OUVES VEM JÁ, JESUS!  VEM JÁ, JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('485', '485 - SETE CASTIÇAIS', 'SETE CASTIÇAIS O ESPÍRITO ACENDEU\nE DO MEIO DA IGREJA NOS FALOU;\nTODO O MUNDO OUVIU A VOZ DO CORDEIRO QUE VENCEU,\nVAI CUMPRIR-SE AGORA O QUE ELE PROMETEU: CRISTO VAI VOLTAR!\n\nO QUE É DIGNO JÁ OS SELOS DESATOU,\nA TROMBETA DA VITÓRIA VAI SOAR;\nA VIDEIRA FLORESCEU, A FIGUEIRA JÁ BROTOU, \nVAI CUMPRIR-SE O QUE ELE UM DIA NOS FALOU: \nCRISTO VAI VOLTAR!\n\nCORO\nMARANATA! CRISTO VAI VOLTAR!\nO ESPÍRITO E A ESPOSA DIZEM:\nVEM! ORA VEM, JESUS!\n\nHÁ SINAIS NO CÉU, NA TERRA E NO MAR,\nO JUÍZO DO SENHOR NÃO VAI TARDAR;\nESTÁS PRONTO MEU IRMÃO, SE DEUS HOJE TE CHAMAR? SÓ NO SANGUE DO CORDEIRO HÁ SALVAÇÃO, CRISTO VAI VOLTAR!\n\nCORO (2X)\nMARANATA! CRISTO VAI VOLTAR!\nO ESPÍRITO E A ESPOSA DIZEM:\nVEM! ORA VEM, JESUS!', 'SETE CASTIÇAIS O ESPÍRITO ACENDEU E DO MEIO DA IGREJA NOS FALOU; TODO O MUNDO OUVIU A VOZ DO CORDEIRO QUE VENCEU, VAI CUMPRIR-SE AGORA O QUE ELE PROMETEU: CRISTO VAI VOLTAR! O QUE É DIGNO JÁ OS SELOS DESATOU, A TROMBETA DA VITÓRIA VAI SOAR; A VIDEIRA FLORESCEU, A FIGUEIRA JÁ BROTOU, VAI CUMPRIR-SE O QUE ELE UM DIA NOS FALOU: CRISTO VAI VOLTAR! CORO MARANATA! CRISTO VAI VOLTAR! O ESPÍRITO E A ESPOSA DIZEM: VEM! ORA VEM, JESUS! HÁ SINAIS NO CÉU, NA TERRA E NO MAR, O JUÍZO DO SENHOR NÃO VAI TARDAR; ESTÁS PRONTO MEU IRMÃO, SE DEUS HOJE TE CHAMAR? SÓ NO SANGUE DO CORDEIRO HÁ SALVAÇÃO, CRISTO VAI VOLTAR! MARANATA! CRISTO VAI VOLTAR! O ESPÍRITO E A ESPOSA DIZEM: VEM! ORA VEM, JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('486', '486 - DO NORTE VEM O ÁUREO ESPLENDOR', 'DO NORTE VEM O ÁUREO ESPLENDOR,\nO AZUL DO CÉU TAMBÉM ME FAZ SONHAR \nCOM UMA NOVA TERRA, UM NOVO CÉU, \nONDE OS JUSTOS VIVERÃO EM PLENA PAZ E PARA SEMPRE, ONDE UNIDOS VIVERÃO \nE PARA SEMPRE ESTARÃO \nNA PRESENÇA DE JESUS.\n\nINSTRUMENTOS\n\nSEI QUE UM DIA OS PRÉDIOS CAIRÃO\nE OS MOINHOS NÃO MAIS ROLARÃO;\nAPENAS FICARÁ DE PÉ O QUE NA ROCHA \nSE FIRMOU, O QUE ANDOU NA CLARA LUZ, \nO QUE OUVINDO UM DIA CREU \nE QUE OUVINDO OBEDECEU \nA PALAVRA DE JESUS.\n\nINSTRUMENTOS\n\nBREVE A VIDA FINDA O SEU LABOR E SABEREI \nQUE NADA FOI EM VÃO, SE EU SORRI, LUTEI, \nCHOREI, SE CONSEGUI, PERDI, ACHEI, \nO QUE SENTI, TUDO PASSOU;\nIMPORTA AGORA, SIM, ESTAR,\nJUNTO A QUEM POR QUEM VIVI\nJUNTO A TI, Ó MEU SENHOR.\nBIS', 'DO NORTE VEM O ÁUREO ESPLENDOR, O AZUL DO CÉU TAMBÉM ME FAZ SONHAR COM UMA NOVA TERRA, UM NOVO CÉU, ONDE OS JUSTOS VIVERÃO EM PLENA PAZ E PARA SEMPRE, ONDE UNIDOS VIVERÃO E PARA SEMPRE ESTARÃO NA PRESENÇA DE JESUS. INSTRUMENTOS SEI QUE UM DIA OS PRÉDIOS CAIRÃO E OS MOINHOS NÃO MAIS ROLARÃO; APENAS FICARÁ DE PÉ O QUE NA ROCHA SE FIRMOU, O QUE ANDOU NA CLARA LUZ, O QUE OUVINDO UM DIA CREU E QUE OUVINDO OBEDECEU A PALAVRA DE JESUS. INSTRUMENTOS BREVE A VIDA FINDA O SEU LABOR E SABEREI QUE NADA FOI EM VÃO, SE EU SORRI, LUTEI, CHOREI, SE CONSEGUI, PERDI, ACHEI, O QUE SENTI, TUDO PASSOU; IMPORTA AGORA, SIM, ESTAR, JUNTO A QUEM POR QUEM VIVI JUNTO A TI, Ó MEU SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('487', '487 - VIGIAI E SEMPRE ORAI, Ó CRENTES', 'VIGIAI E SEMPRE ORAI, Ó CRENTES,\nBREVE CRISTO VOLTARÁ;\nPARA O CÉU, O SEU DESCANSO ETERNO, \nOS REMIDOS LEVARÁ.\n\nCORO\nVIGIAI! É ORDEM SANTA,\nVIGIAI EM ORAÇÃO!\nBREVE CRISTO VOLTARÁ,\nPARA O CÉU NOS LEVARÁ!\n\nVIGIAI, GUARDANDO SEMPRE LIMPO,\nDO PECADO, O CORAÇÃO;\nCONFIANDO NO DIVINO MESTRE,\nVENCEREIS A TENTAÇÃO.\n\nCORO\nVIGIAI! É ORDEM SANTA,\nVIGIAI EM ORAÇÃO!\nBREVE CRISTO VOLTARÁ,\nPARA O CÉU NOS LEVARÁ!\n\nVIGIANDO E SEMPRE TRABALHANDO,\nNESTE MUNDO VIL LUTAI,\nAGUARDANDO A VOZ DE JESUS CRISTO: \n“NO MEU DOCE LAR ENTRAI!”', 'VIGIAI E SEMPRE ORAI, Ó CRENTES, BREVE CRISTO VOLTARÁ; PARA O CÉU, O SEU DESCANSO ETERNO, OS REMIDOS LEVARÁ. CORO VIGIAI! É ORDEM SANTA, VIGIAI EM ORAÇÃO! BREVE CRISTO VOLTARÁ, PARA O CÉU NOS LEVARÁ! VIGIAI, GUARDANDO SEMPRE LIMPO, DO PECADO, O CORAÇÃO; CONFIANDO NO DIVINO MESTRE, VENCEREIS A TENTAÇÃO. CORO VIGIAI! É ORDEM SANTA, VIGIAI EM ORAÇÃO! BREVE CRISTO VOLTARÁ, PARA O CÉU NOS LEVARÁ! VIGIANDO E SEMPRE TRABALHANDO, NESTE MUNDO VIL LUTAI, AGUARDANDO A VOZ DE JESUS CRISTO: NO MEU DOCE LAR ENTRAI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('488', '488 - CERTA VEZ, AO ORAR', 'CERTA VEZ, AO ORAR, \nPENSEI QUANTO FALTARÁ \nPARA REGRESSAR O NOSSO REI?\nSUA VOZ OUÇO, ENTÃO, \nREVELANDO AO CORAÇÃO: \n“MINHA IGREJA, BREVE VOLTAREI!”\n\nCORO\n“BREVE VENHO VOS BUSCAR \nPARA TER-VOS JUNTO A MIM \nSEDE FORTES E FIÉIS ATÉ O FIM!\nQUE ALEGRIA SINTO, ENTÃO, \nEM MEU CORAÇÃO EU SEI, \nBREVE VIRÁ JESUS, MEU REI!\n\nQUE RIQUEZAS SEM PAR ELE FOI NOS PREPARAR, \nLÁ NO CÉU ONDE NÃO HÁ MAIS DOR; \nMEU DESEJO É PARTIR, PARA SUA VOZ OUVIR: \n“ENTRA NO GOZO DO TEU SENHOR”\n\nCORO\n“BREVE VENHO VOS BUSCAR \nPARA TER-VOS JUNTO A MIM \nSEDE FORTES E FIÉIS ATÉ O FIM!\nQUE ALEGRIA SINTO, ENTÃO, \nEM MEU CORAÇÃO EU SEI, \nBREVE VIRÁ JESUS, MEU REI!', 'CERTA VEZ, AO ORAR, PENSEI QUANTO FALTARÁ PARA REGRESSAR O NOSSO REI? SUA VOZ OUÇO, ENTÃO, REVELANDO AO CORAÇÃO: MINHA IGREJA, BREVE VOLTAREI! CORO BREVE VENHO VOS BUSCAR PARA TER-VOS JUNTO A MIM SEDE FORTES E FIÉIS ATÉ O FIM! QUE ALEGRIA SINTO, ENTÃO, EM MEU CORAÇÃO EU SEI, BREVE VIRÁ JESUS, MEU REI! QUE RIQUEZAS SEM PAR ELE FOI NOS PREPARAR, LÁ NO CÉU ONDE NÃO HÁ MAIS DOR; MEU DESEJO É PARTIR, PARA SUA VOZ OUVIR: ENTRA NO GOZO DO TEU SENHOR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('489', '489 - EM BREVE ESCUTAREI', 'EM BREVE ESCUTAREI O TOQUE TRIUNFAL\nDA ÚLTIMA TROMBETA CELESTIAL,\nE TODOS OS FIÉIS DO SONO ACORDARÃO\nE AO CÉU CONOSCO JUNTOS SUBIRÃO.\n\nCORO\nALI JESUS VEREI, DE JOELHOS CAIREI\nAOS PÉS DO AMADO SALVADOR!\nE ENTRE A MULTIDÃO CANTAREI A CANÇÃO\nDO MAIS SUBLIME E BELO AMOR!\n\nQUE IMPORTA A DOR AQUI, A BREVE PROVAÇÃO, \nSE VÊ-LO ALI SERÁ MEU GALARDÃO?\nSUA VOZ EU OUVIREI: “BENDITOS DE MEU PAI, \nNO LAR QUE PREPAREI AGORA ENTRAI!”\n\nCORO\nALI JESUS VEREI, DE JOELHOS CAIREI\nAOS PÉS DO AMADO SALVADOR!\nE ENTRE A MULTIDÃO CANTAREI A CANÇÃO\nDO MAIS SUBLIME E BELO AMOR!', 'EM BREVE ESCUTAREI O TOQUE TRIUNFAL DA ÚLTIMA TROMBETA CELESTIAL, E TODOS OS FIÉIS DO SONO ACORDARÃO E AO CÉU CONOSCO JUNTOS SUBIRÃO. CORO ALI JESUS VEREI, DE JOELHOS CAIREI AOS PÉS DO AMADO SALVADOR! E ENTRE A MULTIDÃO CANTAREI A CANÇÃO DO MAIS SUBLIME E BELO AMOR! QUE IMPORTA A DOR AQUI, A BREVE PROVAÇÃO, SE VÊ-LO ALI SERÁ MEU GALARDÃO? SUA VOZ EU OUVIREI: BENDITOS DE MEU PAI, NO LAR QUE PREPAREI AGORA ENTRAI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('490', '490 - ELE VOLTARÁ PRA BUSCAR A SUA IGREJA', 'ELE VOLTARÁ PRA BUSCAR A SUA IGREJA,\nQUE AQUI NO MUNDO FOI FIEL;\nELE VOLTARÁ E MUITOS NÃO ACREDITAM,\nESSES TAIS FICARÃO,\nSOFRENDO, SEM TER SALVAÇÃO.\n\nCORO\nELE PODE VOLTAR AGORA, \nNINGUÉM SABE O DIA NEM A HORA QUE ELE VOLTARÁ;\nELE PODE VOLTAR HOJE MESMO,\nPREPARA-TE, IRMÃO, PRA NÃO FICAR.\n\nELE VOLTARÁ, POIS ASSIM NOS AFIRMA \nA BÍBLIA, MAIS PERTO DE DEUS DEVEMOS FICAR;\nELE VOLTARÁ, ACERTA A TUA VIDA,\nDIANTE DE DEUS, DE SEU SANTO ALTAR.\n\nCORO\nELE PODE VOLTAR AGORA, \nNINGUÉM SABE O DIA NEM A HORA \nQUE ELE VOLTARÁ;\nELE PODE VOLTAR HOJE MESMO,\nPREPARA-TE, IRMÃO, PRA NÃO FICAR.\n\nFINAL: PREPARA-TE IRMÃO PRA NÃO FICAR.', 'ELE VOLTARÁ PRA BUSCAR A SUA IGREJA, QUE AQUI NO MUNDO FOI FIEL; ELE VOLTARÁ E MUITOS NÃO ACREDITAM, ESSES TAIS FICARÃO, SOFRENDO, SEM TER SALVAÇÃO. CORO ELE PODE VOLTAR AGORA, NINGUÉM SABE O DIA NEM A HORA QUE ELE VOLTARÁ; ELE PODE VOLTAR HOJE MESMO, PREPARA-TE, IRMÃO, PRA NÃO FICAR. ELE VOLTARÁ, POIS ASSIM NOS AFIRMA A BÍBLIA, MAIS PERTO DE DEUS DEVEMOS FICAR; ELE VOLTARÁ, ACERTA A TUA VIDA, DIANTE DE DEUS, DE SEU SANTO ALTAR. CORO ELE PODE VOLTAR AGORA, NINGUÉM SABE O DIA NEM A HORA QUE ELE VOLTARÁ; ELE PODE VOLTAR HOJE MESMO, PREPARA-TE, IRMÃO, PRA NÃO FICAR.  PREPARA-TE IRMÃO PRA NÃO FICAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('491', '491 - ELE UM DIA VOLTARÁ', 'ELE UM DIA VOLTARÁ,\nVEM BUSCAR A SUA IGREJA,\nMEU JESUS, MEU JESUS VOLTARÁ;\nTODA A TERRA ESTREMECEU\nNO DIA DA SUA PARTIDA,\nMEU JESUS, MEU JESUS, \nMINHA VIDA.\n\nNOSSO FARDO ELE CARREGOU,\nTODO O SEU AMOR NOS DEU,\nNOSSAS DORES CONSIGO LEVOU,\nVIDA NOVA NOS OFERECEU;\nCOMO É BOM SERVIR AO MEU JESUS,\nSEU NOME GLORIFICAR;\nSUA GLÓRIA NOS CONDUZ\nA ANDAR NA SUA LUZ, \nA JESUS QUERO LOUVAR!\n\nFINAL: LOUVAR, LOUVAR.\nBIS', 'ELE UM DIA VOLTARÁ, VEM BUSCAR A SUA IGREJA, MEU JESUS, MEU JESUS VOLTARÁ; TODA A TERRA ESTREMECEU NO DIA DA SUA PARTIDA, MEU JESUS, MEU JESUS, MINHA VIDA. NOSSO FARDO ELE CARREGOU, TODO O SEU AMOR NOS DEU, NOSSAS DORES CONSIGO LEVOU, VIDA NOVA NOS OFERECEU; COMO É BOM SERVIR AO MEU JESUS, SEU NOME GLORIFICAR; SUA GLÓRIA NOS CONDUZ A ANDAR NA SUA LUZ, A JESUS QUERO LOUVAR!  LOUVAR, LOUVAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('492', '492 - ELE VOLTARÁ, SIM, ELE VOLTARÁ', 'ELE VOLTARÁ \n(SIM, ELE VOLTARÁ) SERVAS\nELE VOLTARÁ\n(SIM, ELE VOLTARÁ) SERVAS\nPARA BUSCAR UMA IGREJA \nA  QUAL ESTÁ PREPARADA.\n\nELE VOLTARÁ \n(SIM, ELE VOLTARÁ) SERVAS\nELE VOLTARÁ\n(SIM, ELE VOLTARÁ) SERVAS\nPOVO ESCOLHIDO LUTANDO \nCOM CRISTO NESTA BATALHA.\n\nMAS ESTA LUTA AGORA JÁ É O FIM,\nSOMOS FELIZES PORQUE \nNÃO ESTAREMOS AQUI;\nAS TROMBETAS IRÃO SOAR\nE CRISTO EM BREVE VEM NOS BUSCAR\nPARA A GLÓRIA ETERNA HERDAR.\n\nINSTRUMENTOS\n\nELE VOLTARÁ \n(SIM, ELE VOLTARÁ) SERVAS\nELE VOLTARÁ\n(SIM, ELE VOLTARÁ) SERVAS\nPARA BUSCAR UMA IGREJA \nA  QUAL ESTÁ PREPARADA.\n\nELE VOLTARÁ \n(SIM, ELE VOLTARÁ) SERVAS\nELE VOLTARÁ\n(SIM, ELE VOLTARÁ) SERVAS\nPOVO ESCOLHIDO LUTANDO \nCOM CRISTO NESTA BATALHA.\n\nMAS ESTA LUTA AGORA JÁ É O FIM,\nSOMOS FELIZES PORQUE \nNÃO ESTAREMOS AQUI;\nAS TROMBETAS IRÃO SOAR \nE  CRISTO EM BREVE VEM \nNOS BUSCAR PARA A \nGLÓRIA ETERNA HERDAR.\nBIS', 'ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS PARA BUSCAR UMA IGREJA A QUAL ESTÁ PREPARADA. ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS ELE VOLTARÁ (SIM, ELE VOLTARÁ) SERVAS POVO ESCOLHIDO LUTANDO COM CRISTO NESTA BATALHA. MAS ESTA LUTA AGORA JÁ É O FIM, SOMOS FELIZES PORQUE NÃO ESTAREMOS AQUI; AS TROMBETAS IRÃO SOAR E CRISTO EM BREVE VEM NOS BUSCAR PARA A GLÓRIA ETERNA HERDAR. INSTRUMENTOS MAS ESTA LUTA AGORA JÁ É O FIM, SOMOS FELIZES PORQUE NÃO ESTAREMOS AQUI; AS TROMBETAS IRÃO SOAR E CRISTO EM BREVE VEM NOS BUSCAR PARA A GLÓRIA ETERNA HERDAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('493', '493 - COMO FOI PARA O CÉU, JESUS CRISTO HÁ DE VIR', 'COMO FOI PARA O CÉU, JESUS CRISTO HÁ DE VIR,\nQUANDO O SOM DA TROMBETA ECOAR;\nQUANDO A VOZ DE UM ARCANJO NO \nCÉU ESTRUGIR EU IREI \nCOM JESUS ME ENCONTRAR.\n\nCORO\nOH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER!\nQUANDO O SOM DA TROMBETA ECOAR;\nQUANDO CRISTO, NAS NUVENS, TIVER \nDE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!\n\nNESSE DIA DE GLÓRIA O MEU CORPO MORTAL, \nSEMELHANTE AO DE CRISTO HÁ DE SER;\nE JÁ LIVRE DA MORTE E JÁ LIVRE DO MAL,\nO MILÊNIO DE CRISTO HEI DE VER.\n\nCORO\nOH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER!\nQUANDO O SOM DA TROMBETA ECOAR;\nQUANDO CRISTO, NAS NUVENS, TIVER \nDE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!\n\nEU AQUI, PELA CRUZ, PARA O MUNDO MORRI, \nMUITA DOR INDA AQUI SOFREREI;\nMINHA VIDA COM CRISTO, \nEM MEU DEUS ESCONDI \nE COM CRISTO EU AQUI REINAREI.\n\nCORO\nOH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER!\nQUANDO O SOM DA TROMBETA ECOAR;\nQUANDO CRISTO, NAS NUVENS, TIVER \nDE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!\n\nVEM, JESUS, Ó SENHOR, \nVEM DEPRESSA REINAR, \nVEM A PAZ E A JUSTIÇA TRAZER;\nCRIAÇÃO, POVO TEU, \nTUDO ALMEJA O RAIAR \nDESSE DIA DE GLÓRIA E PODER.\n\nCORO\nOH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER!\nQUANDO O SOM DA TROMBETA ECOAR;\nQUANDO CRISTO, NAS NUVENS, TIVER \nDE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!\n\nESTE IMPÉRIO DO MAL, VEM, SENHOR, \nDESTRUIR, VEM ESPOSO CELESTE, REINAR!\nVEM Ó SOL DA JUSTIÇA NO MUNDO LUZIR\nÓ MEU REI, VEM MEU PRANTO ESTANCAR!\n\nCORO\nOH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER!\nQUANDO O SOM DA TROMBETA ECOAR;\nQUANDO CRISTO, NAS NUVENS, TIVER \nDE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR!', 'COMO FOI PARA O CÉU, JESUS CRISTO HÁ DE VIR, QUANDO O SOM DA TROMBETA ECOAR; QUANDO A VOZ DE UM ARCANJO NO CÉU ESTRUGIR EU IREI COM JESUS ME ENCONTRAR. CORO OH! QUE DIA FAUSTOSO ESSE DIA HÁ DE SER! QUANDO O SOM DA TROMBETA ECOAR; QUANDO CRISTO, NAS NUVENS, TIVER DE DESCER, TRIUNFANTE, ENTRE NÓS, HABITAR! NESSE DIA DE GLÓRIA O MEU CORPO MORTAL, SEMELHANTE AO DE CRISTO HÁ DE SER; E JÁ LIVRE DA MORTE E JÁ LIVRE DO MAL, O MILÊNIO DE CRISTO HEI DE VER. EU AQUI, PELA CRUZ, PARA O MUNDO MORRI, MUITA DOR INDA AQUI SOFREREI; MINHA VIDA COM CRISTO, EM MEU DEUS ESCONDI E COM CRISTO EU AQUI REINAREI. VEM, JESUS, Ó SENHOR, VEM DEPRESSA REINAR, VEM A PAZ E A JUSTIÇA TRAZER; CRIAÇÃO, POVO TEU, TUDO ALMEJA O RAIAR DESSE DIA DE GLÓRIA E PODER. ESTE IMPÉRIO DO MAL, VEM, SENHOR, DESTRUIR, VEM ESPOSO CELESTE, REINAR! VEM Ó SOL DA JUSTIÇA NO MUNDO LUZIR Ó MEU REI, VEM MEU PRANTO ESTANCAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('494', '494 - NÃO SEI QUANDO CRISTO JESUS HÁ DE VIR', 'NÃO SEI QUANDO CRISTO JESUS \nHÁ DE VIR E NEM QUAL O DIA \nEM QUE EU HEI DE PARTIR, \nMAS EU SEI QUANDO A SUA PRESENÇA LUZIR\nSERÁ GRANDE GLÓRIA PRA MIM (3X)\nMAS EU SEI QUANDO A SUA PRESENÇA \nLUZIR, SERÁ GRANDE GLÓRIA PRA MIM.\n\nNÃO SEI QUE MORADA JESUS VAI ME DAR, \nE NEM QUAL O NOME QUE EU HEI \nDE GANHAR, MAS EU SEI QUE O BEM \nVINDO DELE HEI DE ESCUTAR,\nSÓ ISSO SERÁ CÉU PRA MIM (3X)\nMAS EU SEI QUE O BEM-VINDO \nDELE HEI DE ESCUTAR, \nSÓ ISSO SERÁ CÉU PRA MIM.\n\nO CANTO DOS ANJOS AINDA NÃO SEI \nNEM QUE SINFONIAS EU LÁ OUVIREI, \nMAS EU SEI QUE O FALAR DE JESUS, \nO MEU REI \nSERÁ SINFONIA PRA MIM (3X)\nMAS EU SEI QUE O FALAR DE JESUS, \nO MEU REI, SERÁ SINFONIA PRA MIM.', 'NÃO SEI QUANDO CRISTO JESUS HÁ DE VIR E NEM QUAL O DIA EM QUE EU HEI DE PARTIR, MAS EU SEI QUANDO A SUA PRESENÇA LUZIR SERÁ GRANDE GLÓRIA PRA MIM  MAS EU SEI QUANDO A SUA PRESENÇA LUZIR, SERÁ GRANDE GLÓRIA PRA MIM. NÃO SEI QUE MORADA JESUS VAI ME DAR, E NEM QUAL O NOME QUE EU HEI DE GANHAR, MAS EU SEI QUE O BEM VINDO DELE HEI DE ESCUTAR, SÓ ISSO SERÁ CÉU PRA MIM  MAS EU SEI QUE O BEM-VINDO DELE HEI DE ESCUTAR, SÓ ISSO SERÁ CÉU PRA MIM. O CANTO DOS ANJOS AINDA NÃO SEI NEM QUE SINFONIAS EU LÁ OUVIREI, MAS EU SEI QUE O FALAR DE JESUS, O MEU REI SERÁ SINFONIA PRA MIM  MAS EU SEI QUE O FALAR DE JESUS, O MEU REI, SERÁ SINFONIA PRA MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('495', '495 - O QUE HÁ DE VIR, VIRÁ', 'O QUE HÁ DE VIR, VIRÁ, (BIS)\nNÃO TARDARÁ, NÃO TARDARÁ,\nJESUS COM PODER VIRÁ.\nINSTRUMENTOS\n\nSÓ OS FIÉIS SUBIRÃO (BIS)\nNÃO TARDARÁ, NÃO TARDARÁ,\nJESUS COM PODER VIRÁ.\n\nINSTRUMENTOS\n\nO QUE HÁ DE VIR, VIRÁ, (2X)\nNÃO TARDARÁ, NÃO TARDARÁ,\nJESUS COM PODER VIRÁ.\n\nINSTRUMENTOS\n\nFINAL: O QUE HÁ DE VIR, VIRÁ (3X)', 'O QUE HÁ DE VIR, VIRÁ,  NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS SÓ OS FIÉIS SUBIRÃO  NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS O QUE HÁ DE VIR, VIRÁ,  NÃO TARDARÁ, NÃO TARDARÁ, JESUS COM PODER VIRÁ. INSTRUMENTOS  O QUE HÁ DE VIR, VIRÁ', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('496', '496 - NÃO VAI TARDAR', 'NÃO VAI TARDAR E CRISTO HÁ DE VOLTAR;\nNÃO VAI TARDAR E VEREMOS JESUS.\nEU QUERO ME PREPARAR,\nPOIS QUERO LOGO SUBIR;\nE QUERO JUNTO A VOCÊ, LÁ NO CÉU,\nCONVERSAR COM JESUS.\nBIS', 'NÃO VAI TARDAR E CRISTO HÁ DE VOLTAR; NÃO VAI TARDAR E VEREMOS JESUS. EU QUERO ME PREPARAR, POIS QUERO LOGO SUBIR; E QUERO JUNTO A VOCÊ, LÁ NO CÉU, CONVERSAR COM JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('497', '497 - JESUS, SIM, VEM, DO CÉU EM GLÓRIA ELE VEM', 'JESUS, SIM, VEM, DO CÉU EM GLÓRIA ELE VEM, \nECOA A NOVA PELO MUNDO ALÉM!\nOH! ESPERANÇA QUE A SUA IGREJA TEM!\nDAI GLÓRIA A DEUS JESUS EM BREVE VEM\n\nCORO (2X)\nNOSSA ESPERANÇA É SUA VINDA,\nO REI DOS REIS VEM NOS BUSCAR;\nNÓS AGUARDAMOS JESUS AINDA,\nTÉ A LUZ DA MANHÃ RAIAR.\n\nJESUS SIM, VEM, OS MORTOS \nESPERANDO ESTÃO O GRAN \nMOMENTO DA RESSURREIÇÃO \nE DO SEPULCRO EM BREVE \nSE LEVANTARÃO DAI GLÓRIA A DEUS, \nJESUS EM BREVE VEM!\n\nCORO (2X)\nNOSSA ESPERANÇA É SUA VINDA,\nO REI DOS REIS VEM NOS BUSCAR;\nNÓS AGUARDAMOS JESUS AINDA,\nTÉ A LUZ DA MANHÃ RAIAR.\n\nJESUS, SIM, VEM, DO CÉU CERCADO DE ESPLENDOR, \nANIQUILANDO A CORRUPÇÃO E A DOR, \nQUEBRANDO OS LAÇOS DO ASTUTO USURPADOR!\nDAI GLÓRIA A DEUS, JESUS EM BREVE VEM!\n\nCORO (2X)\nNOSSA ESPERANÇA É SUA VINDA,\nO REI DOS REIS VEM NOS BUSCAR;\nNÓS AGUARDAMOS JESUS AINDA,\nTÉ A LUZ DA MANHÃ RAIAR.\n\nJESUS, SIM, VEM, COMPLETAMENTE RESTAURAR \nO MUNDO QUE SE ARRUÍNA SEM PARAR SIM, \nTODAS AS COISAS VEM DEPRESSA TRANSFORMAR!\nDAI GLÓRIA A DEUS JESUS EM BREVE VEM!\n\nCORO (2X)\nNOSSA ESPERANÇA É SUA VINDA,\nO REI DOS REIS VEM NOS BUSCAR;\nNÓS AGUARDAMOS JESUS AINDA,\nTÉ A LUZ DA MANHÃ RAIAR.', 'JESUS, SIM, VEM, DO CÉU EM GLÓRIA ELE VEM, ECOA A NOVA PELO MUNDO ALÉM! OH! ESPERANÇA QUE A SUA IGREJA TEM! DAI GLÓRIA A DEUS JESUS EM BREVE VEM NOSSA ESPERANÇA É SUA VINDA, O REI DOS REIS VEM NOS BUSCAR; NÓS AGUARDAMOS JESUS AINDA, TÉ A LUZ DA MANHÃ RAIAR. JESUS SIM, VEM, OS MORTOS ESPERANDO ESTÃO O GRAN MOMENTO DA RESSURREIÇÃO E DO SEPULCRO EM BREVE SE LEVANTARÃO DAI GLÓRIA A DEUS, JESUS EM BREVE VEM! JESUS, SIM, VEM, DO CÉU CERCADO DE ESPLENDOR, ANIQUILANDO A CORRUPÇÃO E A DOR, QUEBRANDO OS LAÇOS DO ASTUTO USURPADOR! DAI GLÓRIA A DEUS, JESUS EM BREVE VEM! JESUS, SIM, VEM, COMPLETAMENTE RESTAURAR O MUNDO QUE SE ARRUÍNA SEM PARAR SIM, TODAS AS COISAS VEM DEPRESSA TRANSFORMAR! DAI GLÓRIA A DEUS JESUS EM BREVE VEM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('498', '498 - CRISTO VEM ME BUSCAR', 'CRISTO VEM ME BUSCAR,\nPARA O CÉU ME LEVARÁ,\nO CORDEIRO PROMETIDO VOLTARÁ.\nBIS\n\nCORO\nOH! GLÓRIA, ALELUIA! MARANATA, \nVEM JESUS! ESTOU COBERTO \nPELO SANGUE DESSA CRUZ \nTENHO O CONSOLADOR SUA GLÓRIA \nE ESPLENDOR SOU LIBERTO\nPELO PODER DO SENHOR.\n\nELE MANDA ATENTO ESTAR,\nVIGIAR E SEMPRE ORAR \nPARA O TOQUE DA TROMBETA ESCUTAR.\nBIS\n\nCORO\nOH! GLÓRIA, ALELUIA! MARANATA, \nVEM JESUS! ESTOU COBERTO \nPELO SANGUE DESSA CRUZ \nTENHO O CONSOLADOR SUA GLÓRIA \nE ESPLENDOR SOU LIBERTO\nPELO PODER DO SENHOR.\n\nOH! QUE GOZO VOU SENTIR\nCOM OS ANJOS A CANTAR,\nPOIS COM CRISTO\nPARA SEMPRE VOU MORAR.\nBIS\n\nCORO\nOH! GLÓRIA, ALELUIA! MARANATA, \nVEM JESUS! ESTOU COBERTO \nPELO SANGUE DESSA CRUZ \nTENHO O CONSOLADOR SUA GLÓRIA \nE ESPLENDOR SOU LIBERTO\nPELO PODER DO SENHOR.', 'CRISTO VEM ME BUSCAR, PARA O CÉU ME LEVARÁ, O CORDEIRO PROMETIDO VOLTARÁ. CORO OH! GLÓRIA, ALELUIA! MARANATA, VEM JESUS! ESTOU COBERTO PELO SANGUE DESSA CRUZ TENHO O CONSOLADOR SUA GLÓRIA E ESPLENDOR SOU LIBERTO PELO PODER DO SENHOR. ELE MANDA ATENTO ESTAR, VIGIAR E SEMPRE ORAR PARA O TOQUE DA TROMBETA ESCUTAR. OH! QUE GOZO VOU SENTIR COM OS ANJOS A CANTAR, POIS COM CRISTO PARA SEMPRE VOU MORAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('499', '499 - OUÇO UMA VOZ TÃO SUAVE', 'OUÇO UMA VOZ TÃO SUAVE,\nUMA VOZ COMO DAS ÁGUAS\nQUE SAI DO TRONO DO SENHOR\nDIZENDO ASSIM COM AMOR:\n\nCORO\n“IGREJA, BREVEMENTE VOLTAREI,\nTUAS LÁGRIMAS EU ENXUGAREI,\nTENHO OUVIDO O TEU CLAMOR,\nTENHO ACEITO O TEU LOUVOR”;\nCOMO EU TE AMO, TU ÉS O MEU AMOR.\n\nINSTRUMENTOS\n\n“ESTOU À DESTRA DE MEU PAI\nINTERCEDENDO POR TODOS VÓS”;\nE QUERO OUVI-LO DIZER:\n“EIS O MOMENTO DA IGREJA SUBIR.”\n\nCORO\n“IGREJA, BREVEMENTE VOLTAREI,\nTUAS LÁGRIMAS EU ENXUGAREI,\nTENHO OUVIDO O TEU CLAMOR,\nTENHO ACEITO O TEU LOUVOR”;\nCOMO EU TE AMO, TU ÉS O MEU AMOR.\n\nFINAL: \nCOMO EU TE AMO, TU ÉS O MEU AMOR.', 'OUÇO UMA VOZ TÃO SUAVE, UMA VOZ COMO DAS ÁGUAS QUE SAI DO TRONO DO SENHOR DIZENDO ASSIM COM AMOR: CORO IGREJA, BREVEMENTE VOLTAREI, TUAS LÁGRIMAS EU ENXUGAREI, TENHO OUVIDO O TEU CLAMOR, TENHO ACEITO O TEU LOUVOR; COMO EU TE AMO, TU ÉS O MEU AMOR. INSTRUMENTOS ESTOU À DESTRA DE MEU PAI INTERCEDENDO POR TODOS VÓS; E QUERO OUVI-LO DIZER: EIS O MOMENTO DA IGREJA SUBIR. CORO IGREJA, BREVEMENTE VOLTAREI, TUAS LÁGRIMAS EU ENXUGAREI, TENHO OUVIDO O TEU CLAMOR, TENHO ACEITO O TEU LOUVOR; COMO EU TE AMO, TU ÉS O MEU AMOR.  COMO EU TE AMO, TU ÉS O MEU AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('500', '500 - SEI QUE O SENHOR', 'SEI QUE O SENHOR AO MEU LADO ESTÁ,\nSEI QUE UM DIA ENCONTRÁ-LO-EI.\n\nCORO\nNESSE DIA ALEGRAR-ME-EI, \nCOM OS ANJOS CANTAREI \nLOUVOR E ADORAREI \nPROSTRADO DIANTE DO SENHOR.\n\nSEI QUE ESTA VIDA LOGO PASSARÁ,\nSEI QUE O SENHOR TAMBÉM ME LEVARÁ,\nSEI QUE AMANHÃ HÁ UM NOVO ALVORECER\nQUE APAGARÁ AS DORES NO AMANHECER.\n\nCORO\nNESSE DIA ALEGRAR-ME-EI, \nCOM OS ANJOS CANTAREI \nLOUVOR E ADORAREI \nPROSTRADO DIANTE DO SENHOR.\n\nSEI QUE A ALEGRIA LOGO VOLTARÁ, \nO AMOR DO SENHOR EM MIM FLORESCERÁ.\n\nCORO\nNESSE DIA ALEGRAR-ME-EI, \nCOM OS ANJOS CANTAREI \nLOUVOR E ADORAREI \nPROSTRADO DIANTE DO SENHOR.\n\nINSTRUMENTOS\n\nALEGRAR-ME-EI, COM OS ANJOS \nCANTAREI LOUVOR E ADORAREI \nPROSTRADO DIANTE DO SENHOR.', 'SEI QUE O SENHOR AO MEU LADO ESTÁ, SEI QUE UM DIA ENCONTRÁ-LO-EI. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR. SEI QUE ESTA VIDA LOGO PASSARÁ, SEI QUE O SENHOR TAMBÉM ME LEVARÁ, SEI QUE AMANHÃ HÁ UM NOVO ALVORECER QUE APAGARÁ AS DORES NO AMANHECER. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR. SEI QUE A ALEGRIA LOGO VOLTARÁ, O AMOR DO SENHOR EM MIM FLORESCERÁ. CORO NESSE DIA ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR. INSTRUMENTOS ALEGRAR-ME-EI, COM OS ANJOS CANTAREI LOUVOR E ADORAREI PROSTRADO DIANTE DO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('501', '501 - O ESPÍRITO DE DEUS FALA À SUA IGREJA', 'O ESPÍRITO DE DEUS FALA À SUA IGREJA:\n“VIREI BUSCAR OS REMIDOS QUE FORAM LAVADOS \nNO SANGUE DO CORDEIRO, \nNO SANGUE DO FILHO JESUS;\n\nINSTRUMENTOS\n\nCEDO VENHO MUITO EM BREVE, \nEIS QUE ESTOU À PORTA E BATO, \nSE ABRIRES ENTRAREI, \nCUMPRIREI MINHA PROMESSA \nE UM REMIDO TU SERÁS”.\n\nCORO\nEIS O CANTO DE VITÓRIA (DE VITÓRIA)\nVEREMOS O REI DA GLÓRIA (O REI DA GLÓRIA)\nVEREMOS O REI JESUS (O REI JESUS)\nQUE FOI MORTO ALI NA CRUZ;\nRESSUSCITOU DANDO VIDA AO PECADOR.\n\n“BREVE CRISTO VOLTARÁ,\nQUEM VENCER TUDO HERDARÁ\nE EU SEREI SEU DEUS E ELE SERÁ MEU FILHO,\nESSAS PALAVRAS SÃO FIÉIS”.\n\nCORO\nEIS O CANTO DE VITÓRIA (DE VITÓRIA)\nVEREMOS O REI DA GLÓRIA (O REI DA GLÓRIA)\nVEREMOS O REI JESUS (O REI JESUS)\nQUE FOI MORTO ALI NA CRUZ;\nRESSUSCITOU DANDO VIDA AO PECADOR.\n\nFINAL: \nO ESPÍRITO DE DEUS FALA À SUA IGREJA: \n“JESUS VIRÁ, O CORDEIRO VIVO,\nJESUS EM BREVE VIRÁ”.', 'O ESPÍRITO DE DEUS FALA À SUA IGREJA: VIREI BUSCAR OS REMIDOS QUE FORAM LAVADOS NO SANGUE DO CORDEIRO, NO SANGUE DO FILHO JESUS; INSTRUMENTOS CEDO VENHO MUITO EM BREVE, EIS QUE ESTOU À PORTA E BATO, SE ABRIRES ENTRAREI, CUMPRIREI MINHA PROMESSA E UM REMIDO TU SERÁS. CORO EIS O CANTO DE VITÓRIA (DE VITÓRIA) VEREMOS O REI DA GLÓRIA (O REI DA GLÓRIA) VEREMOS O REI JESUS (O REI JESUS) QUE FOI MORTO ALI NA CRUZ; RESSUSCITOU DANDO VIDA AO PECADOR. BREVE CRISTO VOLTARÁ, QUEM VENCER TUDO HERDARÁ E EU SEREI SEU DEUS E ELE SERÁ MEU FILHO, ESSAS PALAVRAS SÃO FIÉIS. O ESPÍRITO DE DEUS FALA À SUA IGREJA: JESUS VIRÁ, O CORDEIRO VIVO, JESUS EM BREVE VIRÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('502', '502 - QUANDO LÁ DO CÉU DESCENDO', 'QUANDO LÁ DO CÉU DESCENDO,\nPARA OS SEUS, JESUS VOLTAR,\nE O CLARIM DE DEUS A TODOS PROCLAMAR QUE CHEGOU O GRANDE DIA DA VITÓRIA DO MEU REI, EU, POR SUA IMENSA GRAÇA, LÁ ESTAREI.\n\nCORO\nQUANDO, ENFIM, CHEGAR O DIA\nDA VITÓRIA DO MEU REI,\nQUANDO, ENFIM, CHEGAR O DIA,\nPELA GRAÇA DE JESUS, EU LÁ ESTAREI.\n\nNESSE DIA, QUANDO OS MORTOS \nHÃO DE A VOZ DE CRISTO OUVIR, \nE DE SEUS SEPULCROS HÃO DE RESSURGIR, \nOS REMIDOS, TRANSFORMADOS, \nLOGO ACLAMARÃO SEU REI \nE, POR SUA IMENSA GRAÇA, LÁ ESTAREI.\n\nCORO\nQUANDO, ENFIM, CHEGAR O DIA\nDA VITÓRIA DO MEU REI,\nQUANDO, ENFIM, CHEGAR O DIA,\nPELA GRAÇA DE JESUS, EU LÁ ESTAREI.\n\nPELO MUNDO, REJEITADO, FOI JESUS, \nMEU SALVADOR;  DESPREZARAM, INSULTARAM \nMEU SENHOR,  MAS, FAUSTOSO VEM O DIA \nDA VITÓRIA DO MEU REI E, POR SUA \nIMENSA GRAÇA, LÁ ESTAREI.\n\nCORO\nQUANDO, ENFIM, CHEGAR O DIA\nDA VITÓRIA DO MEU REI,\nQUANDO, ENFIM, CHEGAR O DIA,\nPELA GRAÇA DE JESUS, EU LÁ ESTAREI.\n\nEM MIM MESMO, NADA TENHO EM QUE POSSA CONFIAR, MAS JESUS MORREU NA CRUZ \nPRA ME SALVAR; TÃO-SOMENTE NELE ESPERO, SIM, E SEMPRE ESPERAREI POIS, POR SUA \nIMENSA GRAÇA, LÁ ESTAREI.\n\nCORO\nQUANDO, ENFIM, CHEGAR O DIA\nDA VITÓRIA DO MEU REI,\nQUANDO, ENFIM, CHEGAR O DIA,\nPELA GRAÇA DE JESUS, EU LÁ ESTAREI.', 'QUANDO LÁ DO CÉU DESCENDO, PARA OS SEUS, JESUS VOLTAR, E O CLARIM DE DEUS A TODOS PROCLAMAR QUE CHEGOU O GRANDE DIA DA VITÓRIA DO MEU REI, EU, POR SUA IMENSA GRAÇA, LÁ ESTAREI. CORO QUANDO, ENFIM, CHEGAR O DIA DA VITÓRIA DO MEU REI, QUANDO, ENFIM, CHEGAR O DIA, PELA GRAÇA DE JESUS, EU LÁ ESTAREI. NESSE DIA, QUANDO OS MORTOS HÃO DE A VOZ DE CRISTO OUVIR, E DE SEUS SEPULCROS HÃO DE RESSURGIR, OS REMIDOS, TRANSFORMADOS, LOGO ACLAMARÃO SEU REI E, POR SUA IMENSA GRAÇA, LÁ ESTAREI. PELO MUNDO, REJEITADO, FOI JESUS, MEU SALVADOR; DESPREZARAM, INSULTARAM MEU SENHOR, MAS, FAUSTOSO VEM O DIA DA VITÓRIA DO MEU REI E, POR SUA IMENSA GRAÇA, LÁ ESTAREI. EM MIM MESMO, NADA TENHO EM QUE POSSA CONFIAR, MAS JESUS MORREU NA CRUZ PRA ME SALVAR; TÃO-SOMENTE NELE ESPERO, SIM, E SEMPRE ESPERAREI POIS, POR SUA IMENSA GRAÇA, LÁ ESTAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('503', '503 - TENHO SAUDADES DE JESUS, MEU MESTRE', 'TENHO SAUDADES DE JESUS, MEU MESTRE,\nTENHO SAUDADES DE JESUS, MEU REI,\nMAS AO FINDAR O MEU LABOR TERRESTRE\nME ENCONTRAREI COM ELE, SIM, EU SEI.\n\nCORO\nSAUDADES, TENHO SAUDADES\nDE TI, JESUS, MEU SALVADOR E REI;\nQUERO SENTIR A TUA PRESENÇA,\nPOIS SEI QUE SÓ ASSIM FELIZ SEREI.\n\nTENHO VONTADE DE VIVER PRA CRISTO,\nDE NOS SEUS BRAÇOS SEMPRE ME ABRIGAR;\nSIM, QUERO A ELE SEMPRE SER SUBMISSO\nE CONFIAR NO SEU AMOR SEM PAR.\n\nCORO\nSAUDADES, TENHO SAUDADES\nDE TI, JESUS, MEU SALVADOR E REI;\nQUERO SENTIR A TUA PRESENÇA,\nPOIS SEI QUE SÓ ASSIM FELIZ SEREI.\n\nNÃO QUERES TU TAMBÉM VIVER COM CRISTO \nE NELE SEMPRE CONFIAR?\nSUA PRESENÇA TE DARÁ CONFORTO,\nE SEU AMOR TE LEVARÁ AO LAR.\n\nCORO\nSAUDADES, TENHO SAUDADES\nDE TI, JESUS, MEU SALVADOR E REI;\nQUERO SENTIR A TUA PRESENÇA,\nPOIS SEI QUE SÓ ASSIM FELIZ SEREI.', 'TENHO SAUDADES DE JESUS, MEU MESTRE, TENHO SAUDADES DE JESUS, MEU REI, MAS AO FINDAR O MEU LABOR TERRESTRE ME ENCONTRAREI COM ELE, SIM, EU SEI. CORO SAUDADES, TENHO SAUDADES DE TI, JESUS, MEU SALVADOR E REI; QUERO SENTIR A TUA PRESENÇA, POIS SEI QUE SÓ ASSIM FELIZ SEREI. TENHO VONTADE DE VIVER PRA CRISTO, DE NOS SEUS BRAÇOS SEMPRE ME ABRIGAR; SIM, QUERO A ELE SEMPRE SER SUBMISSO E CONFIAR NO SEU AMOR SEM PAR. NÃO QUERES TU TAMBÉM VIVER COM CRISTO E NELE SEMPRE CONFIAR? SUA PRESENÇA TE DARÁ CONFORTO, E SEU AMOR TE LEVARÁ AO LAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('504', '504 - EU SEI QUE JESUS', 'EU SEI QUE JESUS NÃO TARDA EM VIR,\nO MEU CORAÇÃO PREPARADO ESTÁ.\n\nINSTRUMENTOS\n\nEM CRISTO JESUS, A GLÓRIA POR VIR,\nJESUS É A ESPERANÇA DO MEU CORAÇÃO.\n\nCORO\nJESUS, TU ÉS O MEU REI, \nJESUS É A ESPERANÇA DA GLÓRIA POR VIR,\nPOR VIR, DA GLÓRIA POR VIR,\nJESUS É A ESPERANÇA DO MEU CORAÇÃO.\n\nINSTRUMENTOS\n\nEM BREVE HÁ DE VIR, JESUS, O SENHOR,\nETERNA ESPERANÇA DO MEU CORAÇÃO,\nA GLÓRIA POR VIR, REVELADA, ENTÃO,\nIREI COM JESUS PARA SEMPRE MORAR.\n\nCORO\nJESUS, TU ÉS O MEU REI, \nJESUS É A ESPERANÇA DA GLÓRIA POR VIR,\nPOR VIR, DA GLÓRIA POR VIR,\nJESUS É A ESPERANÇA DO MEU CORAÇÃO.\n\nFINAL: \nJESUS É A ESPERANÇA \nDO MEU CORAÇÃO', 'EU SEI QUE JESUS NÃO TARDA EM VIR, O MEU CORAÇÃO PREPARADO ESTÁ. INSTRUMENTOS EM CRISTO JESUS, A GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO. CORO JESUS, TU ÉS O MEU REI, JESUS É A ESPERANÇA DA GLÓRIA POR VIR, POR VIR, DA GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO. INSTRUMENTOS EM BREVE HÁ DE VIR, JESUS, O SENHOR, ETERNA ESPERANÇA DO MEU CORAÇÃO, A GLÓRIA POR VIR, REVELADA, ENTÃO, IREI COM JESUS PARA SEMPRE MORAR. CORO JESUS, TU ÉS O MEU REI, JESUS É A ESPERANÇA DA GLÓRIA POR VIR, POR VIR, DA GLÓRIA POR VIR, JESUS É A ESPERANÇA DO MEU CORAÇÃO.  JESUS É A ESPERANÇA DO MEU CORAÇÃO', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('505', '505 - OS SEUS CABELOS', 'OS SEUS CABELOS BRANCOS \nSÃO COMO A NEVE E A BRANCA LÃ, \nE OS SEUS OLHOS COMO UMA CHAMA \nFORTE NO ROSTO A BRILHAR;\nRUMOR DE MUITAS ÁGUAS É SUA VOZ \nTAMBÉM, COMO UMA ESPADA AGUDA \nÉ O SEU FALAR.\n\nCORO\nJESUS! JESUS! SENHOR DOS SENHORES!\nPRINCÍPIO E FIM, O QUE ERA E É,\nE O QUE HÁ DE VIR!\nSEU NOME É JESUS! SEU NOME É JESUS!\n\nELE É O CORDEIRO DIGNO DE ABRIR \nO LIVRO POIS NA CRUZ MORREU,\nE COM SEU SANGUE HOMENS DE TODA \nA TRIBO PARA O PAI COMPROU;\nDIGNO É DE RECEBER TODA A ETERNIDADE, \nAÇÕES DE GRAÇAS, \nGLÓRIA E TODO O PODER.\n\nCORO\nJESUS! JESUS! SENHOR DOS SENHORES!\nPRINCÍPIO E FIM, O QUE ERA E É,\nE O QUE HÁ DE VIR!\nSEU NOME É JESUS! SEU NOME É JESUS!\n\nELE É O QUE VEM NAS NUVENS\nBUSCAR A ESPOSA, PREPARADA JÁ,\nCOM SUAS VESTES BRANCAS,\nCANDEIA ACESA E CHEIA DE AMOR;\nELE É A RESPLANDECENTE ESTRELA DA MANHÃ.\n“CEDO VIREI!” VEM JÁ, Ó SENHOR JESUS!\n\nCORO\nJESUS! JESUS! SENHOR DOS SENHORES!\nPRINCÍPIO E FIM, O QUE ERA E É,\nE O QUE HÁ DE VIR!\nSEU NOME É JESUS! SEU NOME É JESUS!', 'OS SEUS CABELOS BRANCOS SÃO COMO A NEVE E A BRANCA LÃ, E OS SEUS OLHOS COMO UMA CHAMA FORTE NO ROSTO A BRILHAR; RUMOR DE MUITAS ÁGUAS É SUA VOZ TAMBÉM, COMO UMA ESPADA AGUDA É O SEU FALAR. CORO JESUS! JESUS! SENHOR DOS SENHORES! PRINCÍPIO E FIM, O QUE ERA E É, E O QUE HÁ DE VIR! SEU NOME É JESUS! SEU NOME É JESUS! ELE É O CORDEIRO DIGNO DE ABRIR O LIVRO POIS NA CRUZ MORREU, E COM SEU SANGUE HOMENS DE TODA A TRIBO PARA O PAI COMPROU; DIGNO É DE RECEBER TODA A ETERNIDADE, AÇÕES DE GRAÇAS, GLÓRIA E TODO O PODER. ELE É O QUE VEM NAS NUVENS BUSCAR A ESPOSA, PREPARADA JÁ, COM SUAS VESTES BRANCAS, CANDEIA ACESA E CHEIA DE AMOR; ELE É A RESPLANDECENTE ESTRELA DA MANHÃ. CEDO VIREI! VEM JÁ, Ó SENHOR JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('506', '506 - TU QUE ESTÁS ASSENTADO', 'TU QUE ESTÁS ASSENTADO NO TRONO,\nSEMPRE REINANDO, SOBERANO;\nANJOS CANTANDO, HOMENS LOUVANDO,\nDEUS REUNIDO COM SEU POVO.\n\nÓ, ALFA, ÔMEGA, CRISTO, FILHO,\nÓ VEM, Ó VEM, Ó VEM SENHOR JESUS!\n\nANSIOSO ESPERO A TUA VOLTA,\nNO GRANDE DIA EM QUE TU VIRÁS;\nENTÃO SUBIREMOS, COM CRISTO ESTAREMOS \nPARA SEMPRE, ALELUIA!\n\nMARANATA, CRISTO, FILHO, \nMESTRE,Ó VEM, Ó VEM, Ó VEM,\nSENHOR JESUS!\nBIS', 'TU QUE ESTÁS ASSENTADO NO TRONO, SEMPRE REINANDO, SOBERANO; ANJOS CANTANDO, HOMENS LOUVANDO, DEUS REUNIDO COM SEU POVO. Ó, ALFA, ÔMEGA, CRISTO, FILHO, Ó VEM, Ó VEM, Ó VEM SENHOR JESUS! ANSIOSO ESPERO A TUA VOLTA, NO GRANDE DIA EM QUE TU VIRÁS; ENTÃO SUBIREMOS, COM CRISTO ESTAREMOS PARA SEMPRE, ALELUIA! MARANATA, CRISTO, FILHO, MESTRE,Ó VEM, Ó VEM, Ó VEM, SENHOR JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('507', '507 - O SOL ESCURECERÁ', 'O SOL ESCURECERÁ, A LUA NÃO MAIS BRILHARÁ \nE APARECERÁ NO CÉU COM PODER E GLÓRIA \nO FILHO DO HOMEM.\n\nDIA DE DORES, DIA DE TREVAS, O JUÍZO \nDO SENHOR VIRÁ SOBRE A TERRA;\nDIA GLORIOSO, MARAVILHOSO, \nA IGREJA REINARÁ COM CRISTO NA GLÓRIA.\n\nCANTAREMOS AO SENHOR \nLOUVORES DE VITÓRIA\nPOIS A IGREJA VENCERÁ,\nJESUS VOLTARÁ.\n\nFINAL: JESUS, JESUS VOLTARÁ.\nBIS', 'O SOL ESCURECERÁ, A LUA NÃO MAIS BRILHARÁ E APARECERÁ NO CÉU COM PODER E GLÓRIA O FILHO DO HOMEM. DIA DE DORES, DIA DE TREVAS, O JUÍZO DO SENHOR VIRÁ SOBRE A TERRA; DIA GLORIOSO, MARAVILHOSO, A IGREJA REINARÁ COM CRISTO NA GLÓRIA. CANTAREMOS AO SENHOR LOUVORES DE VITÓRIA POIS A IGREJA VENCERÁ, JESUS VOLTARÁ.  JESUS, JESUS VOLTARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('508', '508 - BREVE NO CÉU JESUS HÁ DE APARECER', 'BREVE NO CÉU JESUS HÁ DE APARECER,\nEM GLORIOSA LUZ TODOS O HÃO DE VER;\nNAQUELE DIA, ENTÃO, EU HEI DE RECEBER\nDE CRISTO O GALARDÃO OH QUE PRAZER!\n\nCORO\nBREVE VEREI O BOM JESUS\nE VIVEREI EM PLENA LUZ;\nNO LINDO CÉU, EU GOZAREI, \nDE TODA A DOR, POR DEUS, \nLIVRE SEREI.\n\nNA VINDA DO SENHOR DESFRUTAREI PRAZER, \nQUANDO MEU SALVADOR EM GLÓRIA APARECER;\nEIS QUE ELE BREVE VEM, OS SANTOS \nLEVARÁ PARA A MANSÃO DE ALÉM, \nDONDE VIRÁ.\n\nCORO\nBREVE VEREI O BOM JESUS\nE VIVEREI EM PLENA LUZ;\nNO LINDO CÉU, EU GOZAREI, \nDE TODA A DOR, POR DEUS, \nLIVRE SEREI.', 'BREVE NO CÉU JESUS HÁ DE APARECER, EM GLORIOSA LUZ TODOS O HÃO DE VER; NAQUELE DIA, ENTÃO, EU HEI DE RECEBER DE CRISTO O GALARDÃO OH QUE PRAZER! CORO BREVE VEREI O BOM JESUS E VIVEREI EM PLENA LUZ; NO LINDO CÉU, EU GOZAREI, DE TODA A DOR, POR DEUS, LIVRE SEREI. NA VINDA DO SENHOR DESFRUTAREI PRAZER, QUANDO MEU SALVADOR EM GLÓRIA APARECER; EIS QUE ELE BREVE VEM, OS SANTOS LEVARÁ PARA A MANSÃO DE ALÉM, DONDE VIRÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('509', '509 - EM BREVE, MUI BREVE', 'EM BREVE, MUI BREVE IREMOS VER\nAS PORTAS DO CÉU SE ABRIREM,\nO CORDEIRO DESCENDO EM GLÓRIA\nNO MONTE DAS OLIVEIRAS.\nCORO (2X)\nHOSANA, HOSANA,\nHOSANA AO FILHO DE DAVI\n\nJUNTOS COM OS ANJOS\nLOUVAREMOS O SEU SANTO NOME,\nTODOS OS SEUS REMIDOS\nEXALTARÃO O REI NA SUA GLÓRIA.\nCORO (2X)\nHOSANA, HOSANA,\nHOSANA AO FILHO DE DAVI.\n\nFINAL: HOSANA AO FILHO DE DAVI.', 'EM BREVE, MUI BREVE IREMOS VER AS PORTAS DO CÉU SE ABRIREM, O CORDEIRO DESCENDO EM GLÓRIA NO MONTE DAS OLIVEIRAS.  HOSANA, HOSANA, HOSANA AO FILHO DE DAVI JUNTOS COM OS ANJOS LOUVAREMOS O SEU SANTO NOME, TODOS OS SEUS REMIDOS EXALTARÃO O REI NA SUA GLÓRIA.  HOSANA, HOSANA, HOSANA AO FILHO DE DAVI.  HOSANA AO FILHO DE DAVI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('510', '510 - ALERTA POVO, ESTÁ CHEGANDO A NOSSA HORA', 'ALERTA POVO, ESTÁ CHEGANDO A NOSSA HORA \nDA VITÓRIA QUE JESUS TEM PRA NOS DAR;\nELE VEM COM PODER E GRANDE GLÓRIA,\nDESCENDO AO MUNDO PARA NOS ARREBATAR.\n\nCORO\nE NUM ABRIR E FECHAR DE OLHOS\nNÓS ESTAREMOS NAS MANSÕES CELESTIAIS, \nE SÓ SE OUVE: GLÓRIA A DEUS E ALELUIA! \nDOS REMIDOS QUE NO CÉU TÊM SEU LUGAR.\n\nALI NÃO HÁ MAIS PRANTO NEM DORES,\nAS NOSSAS LÁGRIMAS JESUS VAI ENXUGAR, \nE BEM NO MEIO ESTÁ A ÁRVORE DA VIDA \nQUE TEM SEUS FRUTOS PARA NOS ALIMENTAR.\n\nCORO\nE NUM ABRIR E FECHAR DE OLHOS\nNÓS ESTAREMOS NAS MANSÕES CELESTIAIS, \nE SÓ SE OUVE: GLÓRIA A DEUS E ALELUIA! \nDOS REMIDOS QUE NO CÉU TÊM SEU LUGAR.', 'ALERTA POVO, ESTÁ CHEGANDO A NOSSA HORA DA VITÓRIA QUE JESUS TEM PRA NOS DAR; ELE VEM COM PODER E GRANDE GLÓRIA, DESCENDO AO MUNDO PARA NOS ARREBATAR. CORO E NUM ABRIR E FECHAR DE OLHOS NÓS ESTAREMOS NAS MANSÕES CELESTIAIS, E SÓ SE OUVE: GLÓRIA A DEUS E ALELUIA! DOS REMIDOS QUE NO CÉU TÊM SEU LUGAR. ALI NÃO HÁ MAIS PRANTO NEM DORES, AS NOSSAS LÁGRIMAS JESUS VAI ENXUGAR, E BEM NO MEIO ESTÁ A ÁRVORE DA VIDA QUE TEM SEUS FRUTOS PARA NOS ALIMENTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('511', '511 - JESUS CRISTO VOLTARÁ COM  SEU GRANDE ESPLENDOR', 'JESUS CRISTO VOLTARÁ COM SEU \nGRANDE ESPLENDOR, A IGREJA SUBIRÁ AO ENCONTRO DO SENHOR.\n\nCORO\nMARANATA, MARANATA, VEM, JESUS!\nMARANATA, MARANATA, VEM, JESUS!\n\nESSE DIA CHEGARÁ, ESTAREMOS \nNAS MANSÕES, TUDO AQUI HÁ DE FINDAR, \nJESUS CRISTO VOLTARÁ.\n\nCORO\nMARANATA, MARANATA, VEM, JESUS!\nMARANATA, MARANATA, VEM, JESUS!\n\nCANTAREMOS LOUVORES JUNTO \nCOM OS ANJOS A JESUS, O REI.\nTODOS OS SEUS REMIDOS NO CÉU \nESTARÃO LOUVANDO A CRISTO, O REI.\nCORO\nMARANATA, MARANATA, VEM, JESUS!\nMARANATA, MARANATA, VEM, JESUS!\nFINAL: \nMARANATA, MARANATA, VEM, JESUS!', 'JESUS CRISTO VOLTARÁ COM SEU GRANDE ESPLENDOR, A IGREJA SUBIRÁ AO ENCONTRO DO SENHOR. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS! ESSE DIA CHEGARÁ, ESTAREMOS NAS MANSÕES, TUDO AQUI HÁ DE FINDAR, JESUS CRISTO VOLTARÁ. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS! CANTAREMOS LOUVORES JUNTO COM OS ANJOS A JESUS, O REI. TODOS OS SEUS REMIDOS NO CÉU ESTARÃO LOUVANDO A CRISTO, O REI. CORO MARANATA, MARANATA, VEM, JESUS! MARANATA, MARANATA, VEM, JESUS!  MARANATA, MARANATA, VEM, JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('512', '512 - O MEU AMADO FALA E ME DIZ', 'O MEU AMADO FALA E ME DIZ:\n“LEVANTA-TE QUERIDA MINHA FORMOSA \nMINHA E VEM, E VEM, VEM, VEM”;\nPORQUE EIS QUE PASSOU O INVERNO,\nCESSOU A CHUVA E SE FOI,\nAPARECEM AS FLORES NA TERRA,\nCHEGOU O TEMPO DE CANTAREM AS AVES.\n\nA VOZ DA ROLA OUVE-SE EM NOSSA TERRA\nA FIGUEIRA COMEÇOU A DAR SEUS FIGOS\nE AS VIDES EM FLOR EXALAM SEU AROMA;\nLEVANTA-TE, QUERIDA MINHA,\nFORMOSA MINHA E VEM.\n\nPOMBA MINHA QUE ANDAS PELAS FENDAS \nDOS PENHASCOS, NO ESCONDERIJO DAS ROCHAS \nESCARPADAS,  MOSTRA-ME O TEU ROSTO, \nFAZE-ME OUVIR A TUA VOZ,\nA TUA VOZ É DOCE E TEU ROSTO AMÁVEL.\n\nA VOZ DA ROLA OUVE-SE EM NOSSA TERRA\nA FIGUEIRA COMEÇOU A DAR SEUS FIGOS\nE AS VIDES EM FLOR EXALAM SEU AROMA;\nLEVANTA-TE, QUERIDA MINHA,\nFORMOSA MINHA E VEM.\n\nPOMBA MINHA QUE ANDAS PELAS FENDAS \nDOS PENHASCOS, NO ESCONDERIJO DAS ROCHAS \nESCARPADAS,  MOSTRA-ME O TEU ROSTO, \nFAZE-ME OUVIR A TUA VOZ,\nA TUA VOZ É DOCE E TEU ROSTO AMÁVEL. (3X)', 'O MEU AMADO FALA E ME DIZ: LEVANTA-TE QUERIDA MINHA FORMOSA MINHA E VEM, E VEM, VEM, VEM; PORQUE EIS QUE PASSOU O INVERNO, CESSOU A CHUVA E SE FOI, APARECEM AS FLORES NA TERRA, CHEGOU O TEMPO DE CANTAREM AS AVES. A VOZ DA ROLA OUVE-SE EM NOSSA TERRA A FIGUEIRA COMEÇOU A DAR SEUS FIGOS E AS VIDES EM FLOR EXALAM SEU AROMA; LEVANTA-TE, QUERIDA MINHA, FORMOSA MINHA E VEM. POMBA MINHA QUE ANDAS PELAS FENDAS DOS PENHASCOS, NO ESCONDERIJO DAS ROCHAS ESCARPADAS, MOSTRA-ME O TEU ROSTO, FAZE-ME OUVIR A TUA VOZ, A TUA VOZ É DOCE E TEU ROSTO AMÁVEL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('513', '513 - QUEM É ESTA QUE APARECE', 'QUEM É ESTA QUE APARECE COMO A ALVA DO DIA,\nFORMOSA COMO A LUA, PURA COMO O SOL, \nFORMIDÁVEL COMO UM EXÉRCITO COM BANDEIRAS!\n\nDESCI AO JARDIM DAS NOGUEIRAS,\nPARA MIRAR OS RENOVOS DO VALE,\nPARA VER SE BROTAVAM AS VIDES,\nSE FLORESCIAM AS ROMEIRAS.\nNÃO SEI COMO, IMAGINEI-ME NO CARRO \nDO MEU NOBRE POVO!\n\nVOLTA, VOLTA, Ó, SULAMITA,\nVOLTA, VOLTA\nPRA QUE NÓS TE CONTEMPLEMOS;\nPOR QUE QUEREIS CONTEMPLAR A SULAMITA,\nNA DANÇA DE MAANAIM? (BIS)\nBIS', 'QUEM É ESTA QUE APARECE COMO A ALVA DO DIA, FORMOSA COMO A LUA, PURA COMO O SOL, FORMIDÁVEL COMO UM EXÉRCITO COM BANDEIRAS! DESCI AO JARDIM DAS NOGUEIRAS, PARA MIRAR OS RENOVOS DO VALE, PARA VER SE BROTAVAM AS VIDES, SE FLORESCIAM AS ROMEIRAS. NÃO SEI COMO, IMAGINEI-ME NO CARRO DO MEU NOBRE POVO! VOLTA, VOLTA, Ó, SULAMITA, VOLTA, VOLTA PRA QUE NÓS TE CONTEMPLEMOS; POR QUE QUEREIS CONTEMPLAR A SULAMITA, NA DANÇA DE MAANAIM?', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('514', '514 - ESTA É A VOZ, A VOZ DO MEU AMADO', 'ESTA É A VOZ, A VOZ DO MEU AMADO,\nÉS PARA MIM RAMALHETE DE MIRRA;\nTEU PERFUME, UNGUENTO DERRAMADO,\nTU ÉS GENTIL, AGRADÁVEL É O TEU AROMA.\n\nCORO\nVEM COMIGO, (VEM COMIGO) SERVAS\nVEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS\nÓ, VEM! (Ó, VEM!) SERVAS\nMEU AMADO!\n\nTU ÉS TODA FORMOSA, AMIGA MINHA,\nEM TI NÃO HÁ MANCHAS, ESPOSA MINHA;\nFAVOS DE MEL MANAM DOS TEUS LÁBIOS, \nOS TEUS VESTIDOS TÊM CHEIRO AROMÁTICO.\n\nCORO\nVEM COMIGO, (VEM COMIGO) SERVAS\nVEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS\nÓ, VEM! (Ó, VEM!) SERVAS\nMEU AMADO!\n\nTU ÉS FORMOSA, FORMOSA ENTRE AS MULHERES,\nOS TEUS OLHOS SÃO COMO OS DAS POMBAS;\nQUE BELOS SÃO OS TEUS AMORES,\nBEM DE MANHÃ IREMOS ÀS VINHAS.\n\nCORO (2X)\nVEM COMIGO, (VEM COMIGO) SERVAS\nVEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS\nÓ, VEM! (Ó, VEM!) SERVAS\nMEU AMADO!\n\nFINAL: Ó, VEM! (Ó, VEM!) SERVAS\n            MEU AMADO!', 'ESTA É A VOZ, A VOZ DO MEU AMADO, ÉS PARA MIM RAMALHETE DE MIRRA; TEU PERFUME, UNGUENTO DERRAMADO, TU ÉS GENTIL, AGRADÁVEL É O TEU AROMA. CORO VEM COMIGO, (VEM COMIGO) SERVAS VEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO! TU ÉS TODA FORMOSA, AMIGA MINHA, EM TI NÃO HÁ MANCHAS, ESPOSA MINHA; FAVOS DE MEL MANAM DOS TEUS LÁBIOS, OS TEUS VESTIDOS TÊM CHEIRO AROMÁTICO. TU ÉS FORMOSA, FORMOSA ENTRE AS MULHERES, OS TEUS OLHOS SÃO COMO OS DAS POMBAS; QUE BELOS SÃO OS TEUS AMORES, BEM DE MANHÃ IREMOS ÀS VINHAS. VEM COMIGO, (VEM COMIGO) SERVAS VEM COMIGO AO CAMPO, (SIM, AO CAMPO) SERVAS Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO!  Ó, VEM! (Ó, VEM!) SERVAS MEU AMADO!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('515', '515 - QUANDO A ÚLTIMA TROMBETA ECOAR', 'QUANDO A ÚLTIMA TROMBETA ECOAR\nO MEU NOME OUVIREI JESUS CHAMAR,\nREVESTIDO EM NOVO CORPO ELE ME TRANSFORMARÁ, \nPODEREI ENTÃO SEU ROSTO CONTEMPLAR.\n\nCORO\nALELUIA, ALELUIA, ALELUIA, \nELE ME TRANSFORMARÁ;\nALELUIA, ALELUIA, \nPODEREI ENTÃO SEU ROSTO CONTEMPLAR.\n\nINSTRUMENTOS\n\nQUANDO A ÚLTIMA TROMBETA ECOAR\nO MEU NOME OUVIREI JESUS CHAMAR,\nREVESTIDO EM NOVO CORPO \nELE ME TRANSFORMARÁ, \nPODEREI ENTÃO SEU ROSTO CONTEMPLAR.\n\nCORO\nALELUIA, ALELUIA, ALELUIA, \nELE ME TRANSFORMARÁ;\nALELUIA, ALELUIA, \nPODEREI ENTÃO SEU ROSTO CONTEMPLAR.\n\nFINAL: \nPODEREI ENTÃO SEU ROSTO CONTEMPLAR.', 'QUANDO A ÚLTIMA TROMBETA ECOAR O MEU NOME OUVIREI JESUS CHAMAR, REVESTIDO EM NOVO CORPO ELE ME TRANSFORMARÁ, PODEREI ENTÃO SEU ROSTO CONTEMPLAR. CORO ALELUIA, ALELUIA, ALELUIA, ELE ME TRANSFORMARÁ; ALELUIA, ALELUIA, PODEREI ENTÃO SEU ROSTO CONTEMPLAR. INSTRUMENTOS CORO ALELUIA, ALELUIA, ALELUIA, ELE ME TRANSFORMARÁ; ALELUIA, ALELUIA, PODEREI ENTÃO SEU ROSTO CONTEMPLAR.  PODEREI ENTÃO SEU ROSTO CONTEMPLAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('516', '516 - QUANDO A ANGELICAL TROMBETA', 'QUANDO A ANGELICAL TROMBETA \nNESTE MUNDO ESTRUGIR, \nO MEU NOME OUVIREI JESUS CHAMAR;\nPOIS EU CREIO NA PROMESSA \nE QUE DEUS A VAI CUMPRIR, QUANDO\nOUVIR JESUS MEU NOME PROCLAMAR.\n\nCORO\nGLÓRIA, GLÓRIA, ALELUIA!\nO MEU NOME OUVIREI JESUS CHAMAR;\nGLÓRIA, GLÓRIA, ALELUIA!\nEU ESPERO OUVIR JESUS A ME CHAMAR.\n\nQUANDO O CÉU FOR ENROLADO \nE O SOL NÃO DER MAIS LUZ, \nO MEU NOME OUVIREI JESUS CHAMAR;\nPASSARÃO A TERRA, O MAR, \nMAS PERMANECERÁ JESUS, QUE MEU\nNOME VAI, NA GLÓRIA, PRONUNCIAR.\n\nCORO\nGLÓRIA, GLÓRIA, ALELUIA!\nO MEU NOME OUVIREI JESUS CHAMAR;\nGLÓRIA, GLÓRIA, ALELUIA!\nEU ESPERO OUVIR JESUS A ME CHAMAR.\n\nOH! QUE MÚSICA SUAVE LÁ NO CÉU IREI OUVIR;\nO MEU NOME JESUS CRISTO ANUNCIAR;\nOH! QUE GOZO VAI MINH’ALMA \nLÁ NOS ALTOS CÉUS FLUIR, QUANDO CRISTO\nO MEU NOME PROCLAMAR.\n\nCORO\nGLÓRIA, GLÓRIA, ALELUIA!\nO MEU NOME OUVIREI JESUS CHAMAR;\nGLÓRIA, GLÓRIA, ALELUIA!\nEU ESPERO OUVIR JESUS A ME CHAMAR.', 'QUANDO A ANGELICAL TROMBETA NESTE MUNDO ESTRUGIR, O MEU NOME OUVIREI JESUS CHAMAR; POIS EU CREIO NA PROMESSA E QUE DEUS A VAI CUMPRIR, QUANDO OUVIR JESUS MEU NOME PROCLAMAR. CORO GLÓRIA, GLÓRIA, ALELUIA! O MEU NOME OUVIREI JESUS CHAMAR; GLÓRIA, GLÓRIA, ALELUIA! EU ESPERO OUVIR JESUS A ME CHAMAR. QUANDO O CÉU FOR ENROLADO E O SOL NÃO DER MAIS LUZ, O MEU NOME OUVIREI JESUS CHAMAR; PASSARÃO A TERRA, O MAR, MAS PERMANECERÁ JESUS, QUE MEU NOME VAI, NA GLÓRIA, PRONUNCIAR. OH! QUE MÚSICA SUAVE LÁ NO CÉU IREI OUVIR; O MEU NOME JESUS CRISTO ANUNCIAR; OH! QUE GOZO VAI MINH’ALMA LÁ NOS ALTOS CÉUS FLUIR, QUANDO CRISTO O MEU NOME PROCLAMAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('517', '517 - JÁ NÃO TARDA O AMANHECER', 'JÁ NÃO TARDA O AMANHECER,\nA ALVORADA DE UM NOVO SER;\nPELOS MONTES DE LUZ JÁ VEM O MEU JESUS!\nELE MANDA ME LEVANTAR,\nHÁ UMA FESTA A CELEBRAR; COM AMOR EU IREI CANTAR LOUVORES AO REI!\nALELUIA, ALELUIA, ALELUIA,\nNÃO TARDA O AMANHECER!\n\nA FIGUEIRA SEUS FRUTOS DÁ,\nA VIDEIRA EM FLOR ESTÁ,\nO INVERNO PASSOU E A CHUVA JÁ CESSOU; \nTODA A TERRA RENASCE EM FLOR,\nPRIMAVERA, CANTANDO AMOR; \nESTÁ TÃO PERTO DE NÓS, JÁ OUÇO A SUA VOZ!\nALELUIA, ALELUIA, ALELUIA,\nJÁ OUÇO A SUA VOZ.\n\nSOPRA O VENTO NO MEU JARDIM,\nQUERO A TEMPO TER FLOR EM MIM,\nSEU PERFUME SEM PAR DESEJO DERRAMAR;\nSE DESCESSES DO ALTO CÉU\nE VIESSES, AMADO MEU, E COMESSES DO FRUTO\nQUE TE QUERO DAR!\nALELUIA, ALELUIA, ALELUIA,\nO MEU AMADO VEM!', 'JÁ NÃO TARDA O AMANHECER, A ALVORADA DE UM NOVO SER; PELOS MONTES DE LUZ JÁ VEM O MEU JESUS! ELE MANDA ME LEVANTAR, HÁ UMA FESTA A CELEBRAR; COM AMOR EU IREI CANTAR LOUVORES AO REI! ALELUIA, ALELUIA, ALELUIA, NÃO TARDA O AMANHECER! A FIGUEIRA SEUS FRUTOS DÁ, A VIDEIRA EM FLOR ESTÁ, O INVERNO PASSOU E A CHUVA JÁ CESSOU; TODA A TERRA RENASCE EM FLOR, PRIMAVERA, CANTANDO AMOR; ESTÁ TÃO PERTO DE NÓS, JÁ OUÇO A SUA VOZ! ALELUIA, ALELUIA, ALELUIA, JÁ OUÇO A SUA VOZ. SOPRA O VENTO NO MEU JARDIM, QUERO A TEMPO TER FLOR EM MIM, SEU PERFUME SEM PAR DESEJO DERRAMAR; SE DESCESSES DO ALTO CÉU E VIESSES, AMADO MEU, E COMESSES DO FRUTO QUE TE QUERO DAR! ALELUIA, ALELUIA, ALELUIA, O MEU AMADO VEM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('518', '518 - NÃO TEREI MAIS TRISTEZAS', 'NÃO TEREI MAIS TRISTEZAS\nNEM MAIS FARDOS EU LEVAREI,\nNEM MAIS MORTE NEM MAIS DOR,\nEU JAMAIS ME SEPARAREI\nDE MEU MESTRE, MEU SENHOR;\nPARA SEMPRE ALI ESTAREI\nE FELIZ, COM JESUS, EU SEREI.\n\nCORO\nEU VOU COM CRISTO PARA O CÉU \nEM GLÓRIA ANJOS CANTANDO AO MEU REDOR, \nQUERO, COM CRISTO, SUBIR LÁ PRA GLÓRIA, \nCOM CRISTO FELIZ EU SEREI.\n\nOH! QUE DIA TÃO LINDO DO ENCONTRO \nCOM CRISTO E LOUVORES A ELE DAREI.\nOH! QUE ALEGRIA O DIA QUE TEREI A COROA, \nCOM CRISTO FELIZ EU SEREI.\n\nCORO\nEU VOU COM CRISTO PARA O CÉU \nEM GLÓRIA ANJOS CANTANDO AO MEU REDOR, \nQUERO, COM CRISTO, SUBIR LÁ PRA GLÓRIA, \nCOM CRISTO FELIZ EU SEREI.\n\nFINAL: COM CRISTO FELIZ EU SEREI.', 'NÃO TEREI MAIS TRISTEZAS NEM MAIS FARDOS EU LEVAREI, NEM MAIS MORTE NEM MAIS DOR, EU JAMAIS ME SEPARAREI DE MEU MESTRE, MEU SENHOR; PARA SEMPRE ALI ESTAREI E FELIZ, COM JESUS, EU SEREI. CORO EU VOU COM CRISTO PARA O CÉU EM GLÓRIA ANJOS CANTANDO AO MEU REDOR, QUERO, COM CRISTO, SUBIR LÁ PRA GLÓRIA, COM CRISTO FELIZ EU SEREI. OH! QUE DIA TÃO LINDO DO ENCONTRO COM CRISTO E LOUVORES A ELE DAREI. OH! QUE ALEGRIA O DIA QUE TEREI A COROA, COM CRISTO FELIZ EU SEREI. CORO EU VOU COM CRISTO PARA O CÉU EM GLÓRIA ANJOS CANTANDO AO MEU REDOR, QUERO, COM CRISTO, SUBIR LÁ PRA GLÓRIA, COM CRISTO FELIZ EU SEREI.  COM CRISTO FELIZ EU SEREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('519', '519 - UM NOVO LAR ME ESPERA', 'UM NOVO LAR ME ESPERA,\nVOU COM JESUS MORAR;\nQUE ALEGRIA SEM PAR,\nVOU PRA GLÓRIA, É A VITÓRIA.\nANDAR PELAS RUAS DE OURO\nCOM OS ANJOS E O REI JESUS,\nA QUEM ENTREGUEI A MINHA VIDA\nPOIS POR MIM MORREU NA CRUZ.\nBIS', 'UM NOVO LAR ME ESPERA, VOU COM JESUS MORAR; QUE ALEGRIA SEM PAR, VOU PRA GLÓRIA, É A VITÓRIA. ANDAR PELAS RUAS DE OURO COM OS ANJOS E O REI JESUS, A QUEM ENTREGUEI A MINHA VIDA POIS POR MIM MORREU NA CRUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('520', '520 - NO TRONO DA CIDADE DE DEUS', 'NO TRONO DA CIDADE DE DEUS, JERUSALÉM,\nOS ANJOS CANTAM ALELUIA,\nLOUVANDO A JESUS, REI DOS REIS,\nO SALVADOR, O LEÃO DE JUDÁ.\n\nCORO\nGLÓRIA AO QUE VENCEU E VIVO ESTÁ ENTRE NÓS, \nGLÓRIA, MILHARES DE VOZES NO CÉU \nPROCLAMAM EM UMA SÓ VOZ: SANTO, SANTO.\n\nINSTRUMENTOS\n\nEM BREVE O MEU JESUS VOLTARÁ\nPARA LEVAR A IGREJA FIEL\nE TODOS JUNTOS NA ETERNIDADE,\nCOM O SALVADOR NA ETERNA SIÃO!\n\nCORO\nGLÓRIA AO QUE VENCEU E VIVO ESTÁ ENTRE NÓS, \nGLÓRIA, MILHARES DE VOZES NO CÉU \nPROCLAMAM EM UMA SÓ VOZ: SANTO, SANTO.\n\nFINAL: \n(SANTO) SANTO, SANTO,\n(SANTO) SANTO, SANTO.', 'NO TRONO DA CIDADE DE DEUS, JERUSALÉM, OS ANJOS CANTAM ALELUIA, LOUVANDO A JESUS, REI DOS REIS, O SALVADOR, O LEÃO DE JUDÁ. CORO GLÓRIA AO QUE VENCEU E VIVO ESTÁ ENTRE NÓS, GLÓRIA, MILHARES DE VOZES NO CÉU PROCLAMAM EM UMA SÓ VOZ: SANTO, SANTO. INSTRUMENTOS EM BREVE O MEU JESUS VOLTARÁ PARA LEVAR A IGREJA FIEL E TODOS JUNTOS NA ETERNIDADE, COM O SALVADOR NA ETERNA SIÃO! CORO GLÓRIA AO QUE VENCEU E VIVO ESTÁ ENTRE NÓS, GLÓRIA, MILHARES DE VOZES NO CÉU PROCLAMAM EM UMA SÓ VOZ: SANTO, SANTO.  (SANTO) SANTO, SANTO, (SANTO) SANTO, SANTO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('521', '521 - AO CHEGAR AO CÉU GOZO EU TEREI', 'AO CHEGAR AO CÉU GOZO EU TEREI,\nNAS RUAS DE OURO SEI QUE ANDAREI;\nÁGUAS CRISTALINAS LÁ DESFRUTAREI\nE UM CORO CELESTIAL OUVIREI.\n\nCORO\nSANTO, SANTO, SANTO, SANTO, SANTO ÉS;\nSANTO, SANTO, SANTO, SANTO, SANTO ÉS, JESUS.\n\nNÃO HAVERÁ MAIS DOR NA BELA SIÃO,\nOS SALVOS DO SENHOR REINARÃO;\nSÓ EXISTE A PAZ E O GRANDE AMOR \nDE DEUS QUE PREPAROU \nPARA OS SEUS FIÉIS.\n\nCORO \nSANTO, SANTO, SANTO, SANTO, SANTO ÉS;\nSANTO, SANTO, SANTO, SANTO, SANTO ÉS, JESUS.\n\nNOVA JERUSALÉM, CIDADE ETERNAL,\nRESPLANDECE A LUZ DO MEU REI;\nAO MEU TRIUNO DEUS PRA SEMPRE ADORAR\nCOM OS ANJOS EU TAMBÉM LOUVAREI.\n\nCORO (2X)\nSANTO, SANTO, SANTO, SANTO, SANTO ÉS;\nSANTO, SANTO, SANTO, SANTO, SANTO ÉS, JESUS.', 'AO CHEGAR AO CÉU GOZO EU TEREI, NAS RUAS DE OURO SEI QUE ANDAREI; ÁGUAS CRISTALINAS LÁ DESFRUTAREI E UM CORO CELESTIAL OUVIREI. CORO SANTO, SANTO, SANTO, SANTO, SANTO ÉS; SANTO, SANTO, SANTO, SANTO, SANTO ÉS, JESUS. NÃO HAVERÁ MAIS DOR NA BELA SIÃO, OS SALVOS DO SENHOR REINARÃO; SÓ EXISTE A PAZ E O GRANDE AMOR DE DEUS QUE PREPAROU PARA OS SEUS FIÉIS. CORO SANTO, SANTO, SANTO, SANTO, SANTO ÉS; SANTO, SANTO, SANTO, SANTO, SANTO ÉS, JESUS. NOVA JERUSALÉM, CIDADE ETERNAL, RESPLANDECE A LUZ DO MEU REI; AO MEU TRIUNO DEUS PRA SEMPRE ADORAR COM OS ANJOS EU TAMBÉM LOUVAREI. SANTO, SANTO, SANTO, SANTO, SANTO ÉS; SANTO, SANTO, SANTO, SANTO, SANTO ÉS, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('522', '522 - EXISTE UM LAR', 'EXISTE UM LAR QUE COM GRANDE AMOR \nFOI PREPARADO,DELE DESFRUTARÁ \nTODO AQUELE QUE A JESUS SE ENTREGOU.\nCORO\nNOVO CÉU, NOVA TERRA, NOVO LAR;\nDE FILHO, O MEU DEUS ME CHAMARÁ.\n\nMORTE, NEM LUTO,\nPRANTO, NEM DOR, \nSÓ PORTAS ABERTAS, \nLUZ E AMOR. \n\nCORO\nNOVO CÉU, NOVA TERRA, NOVO LAR;\nDE FILHO, O MEU DEUS ME CHAMARÁ.\nBIS', 'EXISTE UM LAR QUE COM GRANDE AMOR FOI PREPARADO,DELE DESFRUTARÁ TODO AQUELE QUE A JESUS SE ENTREGOU. CORO NOVO CÉU, NOVA TERRA, NOVO LAR; DE FILHO, O MEU DEUS ME CHAMARÁ. MORTE, NEM LUTO, PRANTO, NEM DOR, SÓ PORTAS ABERTAS, LUZ E AMOR. CORO NOVO CÉU, NOVA TERRA, NOVO LAR; DE FILHO, O MEU DEUS ME CHAMARÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('523', '523 - LÁ NO CÉU OS ANJOS CANTAM', 'LÁ NO CÉU OS ANJOS CANTAM:\nVIVA, VIVA O REI;\nLINDO CORO CELESTIAL\nNA BELA CIDADE ETERNAL.\n\nCORO \nEM BREVE TAMBÉM ESTAREI\nNA SANTA CIDADE DE LUZ,\nLOUVANDO SEMPRE O MEU REI,\nO MEU PRECIOSO JESUS. \n\nLINDO RIO PURO DE ÁGUA VIVA\nQUE VEM DE DEUS PAI;\nLÁ NÃO HÁ TRISTEZA E DOR,\nSÓ PAZ, ALEGRIA E AMOR.\n\nCORO (2X)\nEM BREVE TAMBÉM ESTAREI\nNA SANTA CIDADE DE LUZ,\nLOUVANDO SEMPRE O MEU REI,\nO MEU PRECIOSO JESUS. (2X NO FINAL)', 'LÁ NO CÉU OS ANJOS CANTAM: VIVA, VIVA O REI; LINDO CORO CELESTIAL NA BELA CIDADE ETERNAL. CORO EM BREVE TAMBÉM ESTAREI NA SANTA CIDADE DE LUZ, LOUVANDO SEMPRE O MEU REI, O MEU PRECIOSO JESUS. LINDO RIO PURO DE ÁGUA VIVA QUE VEM DE DEUS PAI; LÁ NÃO HÁ TRISTEZA E DOR, SÓ PAZ, ALEGRIA E AMOR. EM BREVE TAMBÉM ESTAREI NA SANTA CIDADE DE LUZ, LOUVANDO SEMPRE O MEU REI, O MEU PRECIOSO JESUS. ( NO FINAL)', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('524', '524 - SE EU TIVESSE NA ALVORADA', 'SE EU ESTIVESSE NA ALVORADA\nCOM AS ASAS ALVAS PARA VOAR,\nEU, MUI CONTENTE, ME TRANSLADAVA\nPARA AS FRONTEIRAS DE CANAÃ.\n\nCORO\nANJOS DE BRANCO ME LEVARIAM\nJUNTO À PRESENÇA DO MEU SENHOR,\nE EU, COM JÚBILO, CANTARIA\nCOM OS JÁ SALVOS POR SEU AMOR.\nLÁ NÃO HÁ PRANTO, NEM AMARGURA,\nLÁ NÃO SE SABE O QUE É DOR,\nALI É GOZO E ALEGRIA,\nALI É TUDO PURO AMOR.\n\nVESTIDOS BRANCOS, PALMA E COROA\nCADA UM DELES RECEBERÁ;\nE SE AGORA A CRISTO EU SIGO,\nJUNTO A ELE HEI DE VENCER.\n\nCORO\nANJOS DE BRANCO ME LEVARIAM\nJUNTO À PRESENÇA DO MEU SENHOR,\nE EU, COM JÚBILO, CANTARIA\nCOM OS JÁ SALVOS POR SEU AMOR.\nLÁ NÃO HÁ PRANTO, NEM AMARGURA,\nLÁ NÃO SE SABE O QUE É DOR,\nALI É GOZO E ALEGRIA,\nALI É TUDO PURO AMOR.', 'SE EU ESTIVESSE NA ALVORADA COM AS ASAS ALVAS PARA VOAR, EU, MUI CONTENTE, ME TRANSLADAVA PARA AS FRONTEIRAS DE CANAÃ. CORO ANJOS DE BRANCO ME LEVARIAM JUNTO À PRESENÇA DO MEU SENHOR, E EU, COM JÚBILO, CANTARIA COM OS JÁ SALVOS POR SEU AMOR. LÁ NÃO HÁ PRANTO, NEM AMARGURA, LÁ NÃO SE SABE O QUE É DOR, ALI É GOZO E ALEGRIA, ALI É TUDO PURO AMOR. VESTIDOS BRANCOS, PALMA E COROA CADA UM DELES RECEBERÁ; E SE AGORA A CRISTO EU SIGO, JUNTO A ELE HEI DE VENCER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('525', '525 - HÁ UM LINDO PAÍS', 'HÁ UM LINDO PAÍS\nONDE OS ANJOS NÃO CESSAM\nDE ENTOAR LOUVORES AO GRANDE REI.\n\nCORO\nUM CORO CELESTIAL ÀQUELE \nQUE É DIGNO DE ABRIR O LIVRO,\nA JESUS, O LEÃO DA TRIBO DE JUDÁ.\n\nNO MEU NOVO LAR AS LÁGRIMAS \nJÁ NÃO CORREM POIS O SENHOR\nCOM SEU AMOR ENXUGOU.\n\nCORO\nUM CORO CELESTIAL ÀQUELE \nQUE É DIGNO DE ABRIR O LIVRO,\nA JESUS, O LEÃO DA TRIBO DE JUDÁ.\n\nNO MEU NOVO LAR AS LÁGRIMAS \nJÁ NÃO CORREM POIS O SENHOR\nCOM SEU AMOR ENXUGOU.\n\nCORO\nUM CORO CELESTIAL ÀQUELE \nQUE É DIGNO DE ABRIR O LIVRO,\nA JESUS, O LEÃO DA TRIBO DE JUDÁ.\n\nFINAL: (2X)\n(UM CORO CELESTIAL)\nA JESUS, O LEÃO DA TRIBO DE JUDÁ', 'HÁ UM LINDO PAÍS ONDE OS ANJOS NÃO CESSAM DE ENTOAR LOUVORES AO GRANDE REI. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ. NO MEU NOVO LAR AS LÁGRIMAS JÁ NÃO CORREM POIS O SENHOR COM SEU AMOR ENXUGOU. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ. NO MEU NOVO LAR AS LÁGRIMAS JÁ NÃO CORREM POIS O SENHOR COM SEU AMOR ENXUGOU. CORO UM CORO CELESTIAL ÀQUELE QUE É DIGNO DE ABRIR O LIVRO, A JESUS, O LEÃO DA TRIBO DE JUDÁ.   (UM CORO CELESTIAL) A JESUS, O LEÃO DA TRIBO DE JUDÁ', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('526', '526 - LINDO! LINDO! LINDO!', 'CORO (2X)\nLINDO! LINDO! LINDO! \nÉ NOVA JERUSALÉM.\n\nCIDADE SANTA DO MEU REI E SENHOR,\nANJOS E ARCANJOS HABITAM ALI;\nA MORTE E A DOR JAMAIS ENTRARÃO\nNESSA CIDADE ETERNA DE GOZO E DE PAZ.\n\nCORO (2X)\nLINDO! LINDO! LINDO! \nÉ NOVA JERUSALÉM.\n\nINSTRUMENTOS\n\nOH! QUANTO ALMEJO ESTAR ALI\nNESSA CIDADE DE BRILHO ETERNAL,\nPARA CONTEMPLAR O REI JESUS,\nAQUELE QUE REVELOU A MIM SUA LUZ.\n\nCORO (2X)\nLINDO! LINDO! LINDO! \nÉ NOVA JERUSALÉM.', 'LINDO! LINDO! LINDO! É NOVA JERUSALÉM. CIDADE SANTA DO MEU REI E SENHOR, ANJOS E ARCANJOS HABITAM ALI; A MORTE E A DOR JAMAIS ENTRARÃO NESSA CIDADE ETERNA DE GOZO E DE PAZ.  LINDO! LINDO! LINDO! É NOVA JERUSALÉM. INSTRUMENTOS OH! QUANTO ALMEJO ESTAR ALI NESSA CIDADE DE BRILHO ETERNAL, PARA CONTEMPLAR O REI JESUS, AQUELE QUE REVELOU A MIM SUA LUZ.  LINDO! LINDO! LINDO! É NOVA JERUSALÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('527', '527 - REGOZIJAI-VOS', '“REGOZIJAI-VOS, Ó MEUS AMADOS,\nESPERAI UM POUCO MAIS,\nO MEU FILHO VIRÁ EM BREVE,\nSOBRE AS NUVENS ELE VIRÁ,\nJESUS VIRÁ!”\n\nCORO\nDESPERTAI (DESPERTAI) \nÓ FILHOS DE SIÃO, CONFIAI (CONFIAI)\nNO DEUS DE ABRAÃO,\nDEUS PROVERÁ, DEUS DE ISRAEL; EXULTAI (EXULTAI)\nNA VOSSA SALVAÇÃO POIS JESUS VIRÁ!\n\nINSTRUMENTOS\n\n“SANTIFICAI-VOS COMO EU SOU SANTO, \nRENOVAI VOSSAS CANDEIAS\nCOM O ÓLEO DO MEU ESPÍRITO;\nPREPARAI-VOS, ELE VIRÁ,\nJESUS VIRÁ!”\n\nCORO (2X)\nDESPERTAI (DESPERTAI) \nÓ FILHOS DE SIÃO, CONFIAI (CONFIAI)\nNO DEUS DE ABRAÃO,\nDEUS PROVERÁ, DEUS DE ISRAEL; EXULTAI (EXULTAI)\nNA VOSSA SALVAÇÃO POIS JESUS VIRÁ!\n\nFINAL: ELE VIRÁ, JESUS VIRÁ!', 'REGOZIJAI-VOS, Ó MEUS AMADOS, ESPERAI UM POUCO MAIS, O MEU FILHO VIRÁ EM BREVE, SOBRE AS NUVENS ELE VIRÁ, JESUS VIRÁ! CORO DESPERTAI (DESPERTAI) Ó FILHOS DE SIÃO, CONFIAI (CONFIAI) NO DEUS DE ABRAÃO, DEUS PROVERÁ, DEUS DE ISRAEL; EXULTAI (EXULTAI) NA VOSSA SALVAÇÃO POIS JESUS VIRÁ! INSTRUMENTOS SANTIFICAI-VOS COMO EU SOU SANTO, RENOVAI VOSSAS CANDEIAS COM O ÓLEO DO MEU ESPÍRITO; PREPARAI-VOS, ELE VIRÁ, JESUS VIRÁ! DESPERTAI (DESPERTAI) Ó FILHOS DE SIÃO, CONFIAI (CONFIAI) NO DEUS DE ABRAÃO, DEUS PROVERÁ, DEUS DE ISRAEL; EXULTAI (EXULTAI) NA VOSSA SALVAÇÃO POIS JESUS VIRÁ!  ELE VIRÁ, JESUS VIRÁ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('528', '528 - EU AVISTO UMA TERRA FELIZ', 'EU AVISTO UMA TERRA FELIZ,\nONDE IREI PARA SEMPRE MORAR;\nHÁ MANSÕES NESSE LINDO PAÍS\nQUE JESUS FOI AO CÉU PREPARAR.\n\nVOU MORAR, VOU MORAR\nNESSA TERRA CELESTE PORVIR.\nBIS\n\nCANTAREI NESSE LINDO PAÍS,\nBELOS HINOS AO MEU SALVADOR\nPOIS ALI VIVEREI BEM FELIZ,\nSEM TRISTEZAS, VEXAMES NEM DOR.\n\nVOU CANTAR, VOU CANTAR\nNESSA TERRA CELESTE PORVIR.\nBIS\n\nDEIXAREI ESTE MUNDO AFINAL,\nPARA IR A JESUS ADORAR;\nNESSA BELA CIDADE FESTAL,\nMIL VENTURAS SEM FIM VOU GOZAR.\n\nVOU GOZAR, VOU GOZAR\nNESSA TERRA CELESTE PORVIR.\nBIS', 'EU AVISTO UMA TERRA FELIZ, ONDE IREI PARA SEMPRE MORAR; HÁ MANSÕES NESSE LINDO PAÍS QUE JESUS FOI AO CÉU PREPARAR. VOU MORAR, VOU MORAR NESSA TERRA CELESTE PORVIR. CANTAREI NESSE LINDO PAÍS, BELOS HINOS AO MEU SALVADOR POIS ALI VIVEREI BEM FELIZ, SEM TRISTEZAS, VEXAMES NEM DOR. VOU CANTAR, VOU CANTAR NESSA TERRA CELESTE PORVIR. DEIXAREI ESTE MUNDO AFINAL, PARA IR A JESUS ADORAR; NESSA BELA CIDADE FESTAL, MIL VENTURAS SEM FIM VOU GOZAR. VOU GOZAR, VOU GOZAR NESSA TERRA CELESTE PORVIR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('529', '529 - EU OUVI DA FORMOSA CIDADE', 'EU OUVI DA FORMOSA CIDADE,\nMUITO ACIMA DE TUDO NO MUNDO,\nEU SENTI UM DESEJO PROFUNDO\nDE UM DIA ESTAR COM JESUS.\nALELUIA, EM VOZ TRIUNFANTE,\nJÁ ESCUTO O CORO CELESTE,\nTERMINADA A MÁGOA TERRESTRE\nESTAREI COM JESUS LÁ NO CÉU.\n\nEU OUVI DE UM VESTIDO TÃO ALVO,\nDA COROA ORNADA DE ESTRELAS,\nDOS IRMÃOS, DA FELIZ PARENTELA,\nQUE UM DIA VEREI EU ALI.\nALELUIA, ALI TUDO É GOZO\nCADA DÚVIDA FOI DISSIPADA,\nPARA SEMPRE A MALÍCIA É TIRADA,\nESTOU NO ALVO, \nNO CÉU COM JESUS.\n\nEU OUVI DE UM PAÍS TÃO DISTANTE,\nONDE NÃO HÁ DOENÇAS NEM DORES,\nNÃO HÁ MAIS ESTES VIS TENTADORES,\nQUE PERTURBAM A VIDA AQUI.\nALELUIA, FELIZ ESPERANÇA,\nJÁ ESCUTO O CANTO CELESTE,\nTERMINADA A MÁGOA TERRESTRE\nESTAREI COM JESUS LÁ NO CÉU.', 'EU OUVI DA FORMOSA CIDADE, MUITO ACIMA DE TUDO NO MUNDO, EU SENTI UM DESEJO PROFUNDO DE UM DIA ESTAR COM JESUS. ALELUIA, EM VOZ TRIUNFANTE, JÁ ESCUTO O CORO CELESTE, TERMINADA A MÁGOA TERRESTRE ESTAREI COM JESUS LÁ NO CÉU. EU OUVI DE UM VESTIDO TÃO ALVO, DA COROA ORNADA DE ESTRELAS, DOS IRMÃOS, DA FELIZ PARENTELA, QUE UM DIA VEREI EU ALI. ALELUIA, ALI TUDO É GOZO CADA DÚVIDA FOI DISSIPADA, PARA SEMPRE A MALÍCIA É TIRADA, ESTOU NO ALVO, NO CÉU COM JESUS. EU OUVI DE UM PAÍS TÃO DISTANTE, ONDE NÃO HÁ DOENÇAS NEM DORES, NÃO HÁ MAIS ESTES VIS TENTADORES, QUE PERTURBAM A VIDA AQUI. ALELUIA, FELIZ ESPERANÇA, JÁ ESCUTO O CANTO CELESTE, TERMINADA A MÁGOA TERRESTRE ESTAREI COM JESUS LÁ NO CÉU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('530', '530 - OH! QUE SAUDOSA LEMBRANÇA', 'OH! QUE SAUDOSA LEMBRANÇA\nTENHO DE TI, Ó SIÃO,\nTERRA QUE EU TANTO AMO\nPOIS ÉS DO MEU CORAÇÃO;\nEU PARA TI VOAREI\nQUANDO O SENHOR MEU VOLTAR\nPOIS ELE FOI PARA O CÉU\nE BREVE VEM ME BUSCAR.\n\nCORO\nSIM, EU PORFIAREI POR ESSA TERRA DE ALÉM\nE LÁ TERMINAREI AS MUITAS LUTAS DE AQUÉM;\nLÁ ESTÁ MEU BOM SENHOR, AO QUAL EU DESEJO VER,\nELE É TUDO PRA MIM,\nE SEM ELE EU NÃO POSSO VIVER.\n\nBELA, MUI BELA, É A ESPERANÇA\nDOS QUE VIGIAM POR TI,\nPOIS ELES RECEBEM FORÇAS\nQUE SÓ SE ENCONTRAM ALI;\nOS QUE PROCURAM CHEGAR\nAO TEU REGAÇO, Ó SIÃO,\nLIVRES SERÃO DE PECAR\nE DE TODA A TENTAÇÃO.\n\nCORO\nSIM, EU PORFIAREI POR ESSA TERRA DE ALÉM\nE LÁ TERMINAREI AS MUITAS LUTAS DE AQUÉM;\nLÁ ESTÁ MEU BOM SENHOR, AO QUAL EU DESEJO VER,\nELE É TUDO PRA MIM,\nE SEM ELE EU NÃO POSSO VIVER.\n\nDIZ A SAGRADA ESCRITURA\nQUE SÃO FORMOSOS OS PÉS\nDAQUELES QUE BOAS NOVAS\nLEVAM PARA OS INFIÉIS;\nE SE TÃO BELO É FALAR\nDESSAS GRANDEZAS AQUI,\nQUE NÃO SERÁ O GOZAR,\nA GRAÇA QUE EXISTE ALI?\n\nCORO\nSIM, EU PORFIAREI POR ESSA TERRA DE ALÉM\nE LÁ TERMINAREI AS MUITAS LUTAS DE AQUÉM;\nLÁ ESTÁ MEU BOM SENHOR, AO QUAL EU DESEJO VER,\nELE É TUDO PRA MIM,\nE SEM ELE EU NÃO POSSO VIVER.', 'OH! QUE SAUDOSA LEMBRANÇA TENHO DE TI, Ó SIÃO, TERRA QUE EU TANTO AMO POIS ÉS DO MEU CORAÇÃO; EU PARA TI VOAREI QUANDO O SENHOR MEU VOLTAR POIS ELE FOI PARA O CÉU E BREVE VEM ME BUSCAR. CORO SIM, EU PORFIAREI POR ESSA TERRA DE ALÉM E LÁ TERMINAREI AS MUITAS LUTAS DE AQUÉM; LÁ ESTÁ MEU BOM SENHOR, AO QUAL EU DESEJO VER, ELE É TUDO PRA MIM, E SEM ELE EU NÃO POSSO VIVER. BELA, MUI BELA, É A ESPERANÇA DOS QUE VIGIAM POR TI, POIS ELES RECEBEM FORÇAS QUE SÓ SE ENCONTRAM ALI; OS QUE PROCURAM CHEGAR AO TEU REGAÇO, Ó SIÃO, LIVRES SERÃO DE PECAR E DE TODA A TENTAÇÃO. DIZ A SAGRADA ESCRITURA QUE SÃO FORMOSOS OS PÉS DAQUELES QUE BOAS NOVAS LEVAM PARA OS INFIÉIS; E SE TÃO BELO É FALAR DESSAS GRANDEZAS AQUI, QUE NÃO SERÁ O GOZAR, A GRAÇA QUE EXISTE ALI?', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('531', '531 - JERUSALÉM, JERUSALÉM', 'JERUSALÉM, JERUSALÉM,\nDO POVO SANTO DO SENHOR,\nCOM FÉ UM DIA LÁ ESTAREI\nRENDENDO GLÓRIA \nAO MEU SALVADOR.\n\nCORO\nALELUIA, GLÓRIA A DEUS, \nALELUIA, AMÉM, AMÉM, \nCOM OS ANJOS CANTAREI TODO O LOUVOR;\nALELUIA, GLÓRIA A DEUS, \nALELUIA, AMÉM, AMÉM, \nJERUSALÉM DO POVO DO SENHOR.\n\nJERUSALÉM, CIDADE SANTA,\nILUMINADA COM A GLÓRIA DE DEUS,\nEM SUAS RUAS EU ANDAREI \nCOM O CORDEIRO E OS REMIDOS SEUS.\n\nCORO\nALELUIA, GLÓRIA A DEUS, \nALELUIA, AMÉM, AMÉM, \nCOM OS ANJOS CANTAREI TODO O LOUVOR;\nALELUIA, GLÓRIA A DEUS, \nALELUIA, AMÉM, AMÉM, \nJERUSALÉM DO POVO DO SENHOR.', 'JERUSALÉM, JERUSALÉM, DO POVO SANTO DO SENHOR, COM FÉ UM DIA LÁ ESTAREI RENDENDO GLÓRIA AO MEU SALVADOR. CORO ALELUIA, GLÓRIA A DEUS, ALELUIA, AMÉM, AMÉM, COM OS ANJOS CANTAREI TODO O LOUVOR; ALELUIA, GLÓRIA A DEUS, ALELUIA, AMÉM, AMÉM, JERUSALÉM DO POVO DO SENHOR. JERUSALÉM, CIDADE SANTA, ILUMINADA COM A GLÓRIA DE DEUS, EM SUAS RUAS EU ANDAREI COM O CORDEIRO E OS REMIDOS SEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('532', '532 - QUANDO O MUNDO AQUI DEIXAR', 'QUANDO O MUNDO AQUI DEIXAR\nTRISTEZAS NÃO TEREI, POIS COMO \nOS ANJOS SEREI, NÃO HÁ DOR, \nANGÚSTIAS NEM AFLIÇÕES, \nNOS BRAÇOS DO SALVADOR \nDESCANSAREI LÁ NO CÉU.\n\nCORO\nJERUSALÉM ANSEIO ENCONTRAR,\n(JERUSALÉM ANSEIO ENCONTRAR) \nRUAS DE OURO EM MEU NOVO LAR,\n(RUAS DE OURO EM MEU NOVO LAR) \nOH! QUE PRAZER, (OH! QUE PRAZER) \nVEREI MEU REI, (VEREI MEU REI) \nPRA SEMPRE ESTAR COM JESUS.\n\nRIO DE ÁGUA VIVA, CLARO COMO CRISTAL, \nNO LINDO CÉU GOZAREI,\nNÃO HÁ DOR, ANGÚSTIAS NEM AFLIÇÕES, \nNOS BRAÇOS DO SALVADOR \nDESCANSAREI LÁ NO CÉU.\n\nCORO (2X)\nJERUSALÉM ANSEIO ENCONTRAR,\n(JERUSALÉM ANSEIO ENCONTRAR) \nRUAS DE OURO EM MEU NOVO LAR,\n(RUAS DE OURO EM MEU NOVO LAR) \nOH! QUE PRAZER, (OH! QUE PRAZER) \nVEREI MEU REI, (VEREI MEU REI) \nPRA SEMPRE ESTAR COM JESUS\n\nPRA SEMPRE ESTAR COM JESUS (2X)', 'QUANDO O MUNDO AQUI DEIXAR TRISTEZAS NÃO TEREI, POIS COMO OS ANJOS SEREI, NÃO HÁ DOR, ANGÚSTIAS NEM AFLIÇÕES, NOS BRAÇOS DO SALVADOR DESCANSAREI LÁ NO CÉU. CORO JERUSALÉM ANSEIO ENCONTRAR, (JERUSALÉM ANSEIO ENCONTRAR) RUAS DE OURO EM MEU NOVO LAR, (RUAS DE OURO EM MEU NOVO LAR) OH! QUE PRAZER, (OH! QUE PRAZER) VEREI MEU REI, (VEREI MEU REI) PRA SEMPRE ESTAR COM JESUS. RIO DE ÁGUA VIVA, CLARO COMO CRISTAL, NO LINDO CÉU GOZAREI, NÃO HÁ DOR, ANGÚSTIAS NEM AFLIÇÕES, NOS BRAÇOS DO SALVADOR DESCANSAREI LÁ NO CÉU. JERUSALÉM ANSEIO ENCONTRAR, (JERUSALÉM ANSEIO ENCONTRAR) RUAS DE OURO EM MEU NOVO LAR, (RUAS DE OURO EM MEU NOVO LAR) OH! QUE PRAZER, (OH! QUE PRAZER) VEREI MEU REI, (VEREI MEU REI) PRA SEMPRE ESTAR COM JESUS PRA SEMPRE ESTAR COM JESUS', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('533', '533 - MINHA ALMA, POIS, ALEGRE CANTA', 'MINHA ALMA, POIS, \nALEGRE CANTA:\nVOU PRA JERUSALÉM;\nÉ UMA CIDADE SANTA, \nNOVA JERUSALÉM.\n\nCORO\nSE VOCÊ QUISER A VIDA\nE CAMINHAR TAMBÉM,\nSEGURA NA MÃO DE CRISTO \nE VAI PRA NOVA JERUSALÉM. \n\nINSTRUMENTOS\n\nLÁ NÃO HÁ PRANTO,\nTRISTEZAS E NEM DOR,\nSÓ HÁ A PAZ DE CRISTO,\nALEGRIA E MUITO AMOR.\nBIS', 'MINHA ALMA, POIS, ALEGRE CANTA: VOU PRA JERUSALÉM; É UMA CIDADE SANTA, NOVA JERUSALÉM. CORO SE VOCÊ QUISER A VIDA E CAMINHAR TAMBÉM, SEGURA NA MÃO DE CRISTO E VAI PRA NOVA JERUSALÉM. INSTRUMENTOS LÁ NÃO HÁ PRANTO, TRISTEZAS E NEM DOR, SÓ HÁ A PAZ DE CRISTO, ALEGRIA E MUITO AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('534', '534 - BREVE VOLTARÁ O MESSIAS', 'BREVE VOLTARÁ O MESSIAS, \nO UNGIDO DE DEUS, O REI, O SENHOR,\nO QUE É DIGNO DE TODO O LOUVOR.\n\nO SOL PERDERÁ O SEU BRILHO E A LUA \nNÃO DARÁ MAIS A SUA LUZ\nQUANDO O FILHO DO HOMEM VOLTAR.\n\nTODOS OS POVOS DA TERRA O VERÃO\nVINDO SOBRE AS NUVENS DO CÉU,\nCOM GLÓRIA, COM GLÓRIA,\nCOM GLÓRIA JESUS VOLTARÁ!\nBIS', 'BREVE VOLTARÁ O MESSIAS, O UNGIDO DE DEUS, O REI, O SENHOR, O QUE É DIGNO DE TODO O LOUVOR. O SOL PERDERÁ O SEU BRILHO E A LUA NÃO DARÁ MAIS A SUA LUZ QUANDO O FILHO DO HOMEM VOLTAR. TODOS OS POVOS DA TERRA O VERÃO VINDO SOBRE AS NUVENS DO CÉU, COM GLÓRIA, COM GLÓRIA, COM GLÓRIA JESUS VOLTARÁ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('535', '535 - ALÉM DO CÉU AZUL', 'ALÉM DO CÉU AZUL FOI JESUS PREPARAR \nUM LAR PRA DAR A QUEM \nA VITÓRIA ALCANÇAR;\nANELO CONSEGUIR A VIDA NO PORVIR, \nCOM FÉ NO MEU SENHOR JESUS.\n\nBEM SEI QUE EU, DE MIM,\nNADA TENHO PRA DAR,\nMAS SEI QUE MEU JESUS\nJÁ ME VEIO SALVAR;\nAGORA QUERO EU TER \nFÉ NO CORAÇÃO,\nATÉ SEU ROSTO VER ALÉM.', 'ALÉM DO CÉU AZUL FOI JESUS PREPARAR UM LAR PRA DAR A QUEM A VITÓRIA ALCANÇAR; ANELO CONSEGUIR A VIDA NO PORVIR, COM FÉ NO MEU SENHOR JESUS. BEM SEI QUE EU, DE MIM, NADA TENHO PRA DAR, MAS SEI QUE MEU JESUS JÁ ME VEIO SALVAR; AGORA QUERO EU TER FÉ NO CORAÇÃO, ATÉ SEU ROSTO VER ALÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('536', '536 - FINDA A LIDA TERREAL', 'FINDA A LIDA TERREAL, \nQUANDO JÁ DO RIO ALÉM, \nNESSA VIDA GLORIOSA ME ENCONTRAR;\nSEI QUE LÁ MEU REDENTOR SORRIDENTE \nHEI DE VER, ENTRE A TURBA, \nO PRIMEIRO A ME CHAMAR.\n\nCORO\nHEI DE VER MEU REDENTOR,\nREDIMIDO AO SEU LADO HEI DE ESTAR;\nHEI DE VER MEU SALVADOR,\nEM GLORIOSA E SANTA LUZ CELESTIAL.\n\nOH! DA ALMA MEU ENLEVO É SEU ROSTO \nCONTEMPLAR NESSA AURORA DO DIA ETERNAL;\nCOMO ENTÃO MEU CORAÇÃO O NÃO \nHÁ DE ALI LOUVAR PELA GRAÇA E FAVOR CELESTIAL?\n\nCORO\nHEI DE VER MEU REDENTOR,\nREDIMIDO AO SEU LADO HEI DE ESTAR;\nHEI DE VER MEU SALVADOR,\nEM GLORIOSA E SANTA LUZ CELESTIAL.\n\nNESSA PÁTRIA RESPLENDENTE HEI DE AMIGOS \nENCONTRAR, SIM, AMIGOS \nMAIS PREZADOS HEI DE TER;\nMAS PRIMEIRO QUE TUDO, \nQUANDO EU ALI CHEGAR, \nMEU JESUS É QUEM EU MAIS ANSEIO VER.\n\nCORO\nHEI DE VER MEU REDENTOR,\nREDIMIDO AO SEU LADO HEI DE ESTAR;\nHEI DE VER MEU SALVADOR,\nEM GLORIOSA E SANTA LUZ CELESTIAL.\n\nPELAS PORTAS DA CIDADE, EM VESTES \nA ALVEJAR, ALI, ONDE NOITE E PRANTO NÃO ESTARÃO;\nENTRE CANTO ANGELICAL HÁ MEUS PASSOS DE GUIAR, \nPERTO, SIM, MUI PERTO O HEI DE VER, ENTÃO.\n\nCORO\nHEI DE VER MEU REDENTOR,\nREDIMIDO AO SEU LADO HEI DE ESTAR;\nHEI DE VER MEU SALVADOR,\nEM GLORIOSA E SANTA LUZ CELESTIAL.', 'FINDA A LIDA TERREAL, QUANDO JÁ DO RIO ALÉM, NESSA VIDA GLORIOSA ME ENCONTRAR; SEI QUE LÁ MEU REDENTOR SORRIDENTE HEI DE VER, ENTRE A TURBA, O PRIMEIRO A ME CHAMAR. CORO HEI DE VER MEU REDENTOR, REDIMIDO AO SEU LADO HEI DE ESTAR; HEI DE VER MEU SALVADOR, EM GLORIOSA E SANTA LUZ CELESTIAL. OH! DA ALMA MEU ENLEVO É SEU ROSTO CONTEMPLAR NESSA AURORA DO DIA ETERNAL; COMO ENTÃO MEU CORAÇÃO O NÃO HÁ DE ALI LOUVAR PELA GRAÇA E FAVOR CELESTIAL? NESSA PÁTRIA RESPLENDENTE HEI DE AMIGOS ENCONTRAR, SIM, AMIGOS MAIS PREZADOS HEI DE TER; MAS PRIMEIRO QUE TUDO, QUANDO EU ALI CHEGAR, MEU JESUS É QUEM EU MAIS ANSEIO VER. PELAS PORTAS DA CIDADE, EM VESTES A ALVEJAR, ALI, ONDE NOITE E PRANTO NÃO ESTARÃO; ENTRE CANTO ANGELICAL HÁ MEUS PASSOS DE GUIAR, PERTO, SIM, MUI PERTO O HEI DE VER, ENTÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('537', '537 - QUANDO O JORDÃO PASSARMOS UNIDOS', 'QUANDO O JORDÃO PASSARMOS UNIDOS\nE ENTRARMOS NO CÉU, VEREMOS LÁ,\nCOMO A AREIA DA PRAIA, OS REMIDOS,\nOH! QUE GLORIOSA VISTA SERÁ!\n\nCORO\nTANTOS COMO A AREIA DA PRAIA!\nTANTOS COMO A AREIA DO MAR!\nQUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ,\nSIM,TANTOS COMO A AREIA DA PRAIA!\n\nQUANDO VIRMOS OS SALVOS DO MUNDO\nQUE A MORTE JAMAIS ALCANÇARÁ,\nSE SAUDAREM COM GOZO PROFUNDO,\nOH! QUE GLORIOSA VISTA SERÁ!\n\nCORO\nTANTOS COMO A AREIA DA PRAIA!\nTANTOS COMO A AREIA DO MAR!\nQUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ,\nSIM,TANTOS COMO A AREIA DA PRAIA!\n\nLÁ NA MARGEM DO RIO DA VIDA\nONDE PAZ E JUSTIÇA HAVERÁ,\nNÓS VEREMOS A TERRA PROMETIDA,\nOH! QUE GLORIOSA VISTA SERÁ!\n\nCORO\nTANTOS COMO A AREIA DA PRAIA!\nTANTOS COMO A AREIA DO MAR!\nQUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ,\nSIM,TANTOS COMO A AREIA DA PRAIA!\n\nQUANDO CRISTO JESUS CONTEMPLARMOS\nCOROADO NO CÉU, COMO ESTÁ, \nE PROSTRADOS AOS SEUS PÉS ADORARMOS\nOH! QUE GLORIOSA VISTA SERÁ!\n\nCORO\nTANTOS COMO A AREIA DA PRAIA!\nTANTOS COMO A AREIA DO MAR!\nQUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ,\nSIM,TANTOS COMO A AREIA DA PRAIA!', 'QUANDO O JORDÃO PASSARMOS UNIDOS E ENTRARMOS NO CÉU, VEREMOS LÁ, COMO A AREIA DA PRAIA, OS REMIDOS, OH! QUE GLORIOSA VISTA SERÁ! CORO TANTOS COMO A AREIA DA PRAIA! TANTOS COMO A AREIA DO MAR! QUE GOZO SENTIRÁ TODO O SALVO, POIS VERÁ, SIM,TANTOS COMO A AREIA DA PRAIA! QUANDO VIRMOS OS SALVOS DO MUNDO QUE A MORTE JAMAIS ALCANÇARÁ, SE SAUDAREM COM GOZO PROFUNDO, OH! QUE GLORIOSA VISTA SERÁ! LÁ NA MARGEM DO RIO DA VIDA ONDE PAZ E JUSTIÇA HAVERÁ, NÓS VEREMOS A TERRA PROMETIDA, OH! QUE GLORIOSA VISTA SERÁ! QUANDO CRISTO JESUS CONTEMPLARMOS COROADO NO CÉU, COMO ESTÁ, E PROSTRADOS AOS SEUS PÉS ADORARMOS OH! QUE GLORIOSA VISTA SERÁ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('538', '538 - HÁ UM LUGAR PRA MIM', 'HÁ UM LUGAR PRA MIM\nCHEIO DE AMOR,\nCOM O MEU JESUS MORAR,\nCOM OS ANJOS HABITAR.\n\nCORO\nLÁ NÃO TEREI MAIS DOR, (LÁ NÃO TEREI MAIS DOR) \nSÓ GOZO E ALEGRIA, (VEREI MEU SALVADOR) \nESPERO ANSIOSO (ESPERO ANSIOSO) \nPOR VER JESUS UM DIA.\n\nNESSE LUGAR PRA MIM,\nDE ETERNA REDENÇÃO,\nENCONTRO PAZ SEM FIM\nEM MEU CORAÇÃO.\n\nCORO (2X)\nLÁ NÃO TEREI MAIS DOR, (LÁ NÃO TEREI MAIS DOR) \nSÓ GOZO E ALEGRIA, (VEREI MEU SALVADOR) \nESPERO ANSIOSO (ESPERO ANSIOSO) \nPOR VER JESUS UM DIA.\n\nFINAL: \nESPERO ANSIOSO (ESPERO ANSIOSO) \nPOR VER JESUS UM DIA.', 'HÁ UM LUGAR PRA MIM CHEIO DE AMOR, COM O MEU JESUS MORAR, COM OS ANJOS HABITAR. CORO LÁ NÃO TEREI MAIS DOR, (LÁ NÃO TEREI MAIS DOR) SÓ GOZO E ALEGRIA, (VEREI MEU SALVADOR) ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA. NESSE LUGAR PRA MIM, DE ETERNA REDENÇÃO, ENCONTRO PAZ SEM FIM EM MEU CORAÇÃO. LÁ NÃO TEREI MAIS DOR, (LÁ NÃO TEREI MAIS DOR) SÓ GOZO E ALEGRIA, (VEREI MEU SALVADOR) ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA.  ESPERO ANSIOSO (ESPERO ANSIOSO) POR VER JESUS UM DIA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('539', '539 - JUNTO AO TRONO DE DEUS, PREPARADO', 'JUNTO AO TRONO DE DEUS PREPARADO\nHÁ, CRISTÃO, UM LUGAR PARA TI;\nHÁ ALEGRIAS, HÁ GOZO EXALTADO,\nHÁ DELÍCIAS PROFUSAS ALI;\nSIM, ALI, SIM, ALI, DE SEUS ANJOS FIÉIS RODEADO, \nNUMA ESFERA DE GLÓRIA E DE LUZ, \nJUNTO A DEUS NOS ESPERA JESUS.\n\nOS ENCANTOS DA TERRA NÃO PODEM\nDAR IDÉIA DO GOZO DALI,\nSE NA TERRA OS PRAZERES ACODEM,\nSÃO PRAZERES QUE FINDAM AQUI;\nMAS DALI, MAS DALI,\nAS VENTURAS ETERNAS CONCORREM\nCOM O BRILHO PERPÉTUO DA LUZ,\nA TORNAR-TE FELIZ COM JESUS.\n\nCONSERVEMOS EM NOSSA LEMBRANÇA\nAS RIQUEZAS DO LINDO PAÍS,\nE GUARDEMOS CONOSCO A ESPERANÇA\nDUMA VIDA MELHOR, MAIS FELIZ;\nPOIS DALI, POIS DALI,\nUMA VOZ VERDADEIRA NÃO CANSA\nDE FALAR-NOS DO REINO DA LUZ\nE DO AMOR PROTETOR DE JESUS.\n\nSE QUISERMOS GOZAR DA VENTURA\nQUE NO BELO PAÍS HAVERÁ,\nÉ SOMENTE PEDIR DE ALMA PURA\nE ESSA GRAÇA JESUS NOS DARÁ;\nPOIS DALI, POIS DALI,\nTODO CHEIO DE AMOR, DE TERNURA,\nDESSE AMOR QUE MOSTROU LÁ NA CRUZ, \nNOS ATENDE, NOS OUVE, JESUS.', 'JUNTO AO TRONO DE DEUS PREPARADO HÁ, CRISTÃO, UM LUGAR PARA TI; HÁ ALEGRIAS, HÁ GOZO EXALTADO, HÁ DELÍCIAS PROFUSAS ALI; SIM, ALI, SIM, ALI, DE SEUS ANJOS FIÉIS RODEADO, NUMA ESFERA DE GLÓRIA E DE LUZ, JUNTO A DEUS NOS ESPERA JESUS. OS ENCANTOS DA TERRA NÃO PODEM DAR IDÉIA DO GOZO DALI, SE NA TERRA OS PRAZERES ACODEM, SÃO PRAZERES QUE FINDAM AQUI; MAS DALI, MAS DALI, AS VENTURAS ETERNAS CONCORREM COM O BRILHO PERPÉTUO DA LUZ, A TORNAR-TE FELIZ COM JESUS. CONSERVEMOS EM NOSSA LEMBRANÇA AS RIQUEZAS DO LINDO PAÍS, E GUARDEMOS CONOSCO A ESPERANÇA DUMA VIDA MELHOR, MAIS FELIZ; POIS DALI, POIS DALI, UMA VOZ VERDADEIRA NÃO CANSA DE FALAR-NOS DO REINO DA LUZ E DO AMOR PROTETOR DE JESUS. SE QUISERMOS GOZAR DA VENTURA QUE NO BELO PAÍS HAVERÁ, É SOMENTE PEDIR DE ALMA PURA E ESSA GRAÇA JESUS NOS DARÁ; POIS DALI, POIS DALI, TODO CHEIO DE AMOR, DE TERNURA, DESSE AMOR QUE MOSTROU LÁ NA CRUZ, NOS ATENDE, NOS OUVE, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('540', '540 - OH! PENSAI NESSE LAR LÁ DO CÉU', 'OH! PENSAI NESSE LAR LÁ DO CÉU,\nBEM AO LADO DO RIO DE LUZ,\nONDE OS SANTOS PRA SEMPRE JÁ GOZAM \nDA PRESENÇA DO NOSSO JESUS.\n\nCORO (2X)\nLÁ DO CÉU! LÁ DO CÉU!\nOH! PENSAI NESSE LAR LÁ DO CÉU!\n\nOH! PENSAI NOS AMIGOS DO CÉU\nQUE A JORNADA JÁ TÊM ACABADO\nE NOS CANTOS QUE SOAM NOS ARES,\nNO PALÁCIO POR DEUS PREPARADO.\n\nCORO (2X)\nLÁ DO CÉU! LÁ DO CÉU!\nOH! PENSAI NESSE LAR LÁ DO CÉU!\n \nHEI DE VER LÁ NO CÉU MEU JESUS,\nFACE A FACE SEU ROSTO MIRAR;\nLONGE, LONGE, CUIDADOS TRISTEZAS!\nCOM JESUS VOU PRA SEMPRE MORAR.\n\nCORO (2X)\nLÁ DO CÉU! LÁ DO CÉU!\nOH! PENSAI NESSE LAR LÁ DO CÉU!\n\nCEDO, CEDO NO CÉU LÁ ESTAREI,\nVEJO O FIM DA JORNADA CHEGAR;\nMEU JESUS ALI ESTÁ ME ESPERANDO,\nÉ MELHOR ESTAR ALI QUE AQUI ESTAR.\n\nCORO (2X)\nLÁ DO CÉU! LÁ DO CÉU!\nOH! PENSAI NESSE LAR LÁ DO CÉU!', 'OH! PENSAI NESSE LAR LÁ DO CÉU, BEM AO LADO DO RIO DE LUZ, ONDE OS SANTOS PRA SEMPRE JÁ GOZAM DA PRESENÇA DO NOSSO JESUS. LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! OH! PENSAI NOS AMIGOS DO CÉU QUE A JORNADA JÁ TÊM ACABADO E NOS CANTOS QUE SOAM NOS ARES, NO PALÁCIO POR DEUS PREPARADO. LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! HEI DE VER LÁ NO CÉU MEU JESUS, FACE A FACE SEU ROSTO MIRAR; LONGE, LONGE, CUIDADOS TRISTEZAS! COM JESUS VOU PRA SEMPRE MORAR. LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU! CEDO, CEDO NO CÉU LÁ ESTAREI, VEJO O FIM DA JORNADA CHEGAR; MEU JESUS ALI ESTÁ ME ESPERANDO, É MELHOR ESTAR ALI QUE AQUI ESTAR. LÁ DO CÉU! LÁ DO CÉU! OH! PENSAI NESSE LAR LÁ DO CÉU!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('541', '541 - ARREBATADO FUI NO DIA DO SENHOR', 'ARREBATADO FUI NO DIA DO SENHOR,\nOUVI UMA GRANDE VOZ, A VOZ DO MEU JESUS;\nE AOS SEUS PÉS CAÍ, AOS PÉS DO SALVADOR, \nAQUELE QUE POR MIM MORREU NA CRUZ.\n\nCORO\nE VI UM NOVO CÉU, UMA NOVA TERRA VI,\nUMA GRANDE VOZ NO CÉU SE FEZ ENTÃO OUVIR;\nNÃO HAVERÁ MAIS DOR, NEM PRANTO E NEM CLAMOR, \nSÓ HAVERÁ DE DEUS UM GRANDE AMOR.\n\nONIPOTENTE DEUS, A QUEM VENCER, \nDARÁ TUDO O QUE É SEU, NO CÉU,\nTUDO HERDARÁ DE DEUS;\n“SEREI TEU ETERNO PAI, SERÁS MEU FILHO, \nENTÃO, POIS QUEM VENCER \nALCANÇARÁ PERDÃO”.\n\nCORO\nE VI UM NOVO CÉU, UMA NOVA TERRA VI,\nUMA GRANDE VOZ NO CÉU SE FEZ ENTÃO OUVIR;\nNÃO HAVERÁ MAIS DOR, NEM PRANTO E NEM CLAMOR, \nSÓ HAVERÁ DE DEUS UM GRANDE AMOR.', 'ARREBATADO FUI NO DIA DO SENHOR, OUVI UMA GRANDE VOZ, A VOZ DO MEU JESUS; E AOS SEUS PÉS CAÍ, AOS PÉS DO SALVADOR, AQUELE QUE POR MIM MORREU NA CRUZ. CORO E VI UM NOVO CÉU, UMA NOVA TERRA VI, UMA GRANDE VOZ NO CÉU SE FEZ ENTÃO OUVIR; NÃO HAVERÁ MAIS DOR, NEM PRANTO E NEM CLAMOR, SÓ HAVERÁ DE DEUS UM GRANDE AMOR. ONIPOTENTE DEUS, A QUEM VENCER, DARÁ TUDO O QUE É SEU, NO CÉU, TUDO HERDARÁ DE DEUS; SEREI TEU ETERNO PAI, SERÁS MEU FILHO, ENTÃO, POIS QUEM VENCER ALCANÇARÁ PERDÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('542', '542 - AO LADO DE JESUS QUERO ESTAR', 'AO LADO DE JESUS EU QUERO ESTAR\nE O SEU AMOR AO MUNDO ANUNCIAR;\nEM GRAÇA, ATINGIR A GLÓRIA DO PORVIR;\nAO LADO DE JESUS EU QUERO ESTAR.\n\nCOROA MUI BRILHANTE EU HEI DE TER\nE A GLÓRIA DA CIDADE EU HEI DE VER,\nE LÁ HEI DE CANTAR UM HINO TRIUNFAL;\nAO LADO DE JESUS EU QUERO ESTAR.\n\nCANSADO, MUITAS VEZES, DE LUTAR\nE DE UMA CRUZ PESADA CARREGAR,\nUM DIA A DEIXAREI, COROA GANHAREI;\nAO LADO DE JESUS EU QUERO ESTAR.\n\nO ROSTO DE JESUS VOU CONTEMPLAR,\nPRA SEMPRE LÁ NO CÉU IREI MORAR,\nCOM ANJOS, QUERUBINS, TAMBÉM OS SERAFINS;\nAO LADO DE JESUS EU QUERO ESTAR.\n\nCOM CRISTO EU ESTAREI LÁ NA MANSÃO,\nA ELE EU ENTREGUEI MEU CORAÇÃO,\nELE É MEU SALVADOR, O ETERNO REDENTOR;\nAO LADO DE JESUS EU QUERO ESTAR.', 'AO LADO DE JESUS EU QUERO ESTAR E O SEU AMOR AO MUNDO ANUNCIAR; EM GRAÇA, ATINGIR A GLÓRIA DO PORVIR; AO LADO DE JESUS EU QUERO ESTAR. COROA MUI BRILHANTE EU HEI DE TER E A GLÓRIA DA CIDADE EU HEI DE VER, E LÁ HEI DE CANTAR UM HINO TRIUNFAL; AO LADO DE JESUS EU QUERO ESTAR. CANSADO, MUITAS VEZES, DE LUTAR E DE UMA CRUZ PESADA CARREGAR, UM DIA A DEIXAREI, COROA GANHAREI; AO LADO DE JESUS EU QUERO ESTAR. O ROSTO DE JESUS VOU CONTEMPLAR, PRA SEMPRE LÁ NO CÉU IREI MORAR, COM ANJOS, QUERUBINS, TAMBÉM OS SERAFINS; AO LADO DE JESUS EU QUERO ESTAR. COM CRISTO EU ESTAREI LÁ NA MANSÃO, A ELE EU ENTREGUEI MEU CORAÇÃO, ELE É MEU SALVADOR, O ETERNO REDENTOR; AO LADO DE JESUS EU QUERO ESTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('543', '543 - AO LADO DE DEUS', 'AO LADO DE DEUS QUERO ESTAR,\nNAS MANSÕES QUE ELE MESMO PREPAROU,\nVER, FACE A FACE, O MEU SENHOR JESUS, \nREI DOS REIS;\nNAS RUAS DE OURO SEI QUE ANDAREI,\nNO TOQUE DA TROMBETA SUBIREI.\n\nCORO (2X)\nOUVIREI (OUVIREI) \nJESUS CHAMAR (PELO MEU NOME) \nE COM OS ANJOS (CANTAREI) \nVIVA O REI (VIVA O REI)\nJESUS!\n\nINSTRUMENTOS\n\nNA ETERNIDADE EU VOU MORAR,\nPARA SEMPRE COM OS ANJOS VOU CANTAR;\nCOM QUERUBINS E SERAFINS A JESUS ADORAREI;\nNAS RUAS DE OURO SEI QUE ANDAREI,\nNO TOQUE DA TROMBETA SUBIREI.\n\nCORO (2X)\nOUVIREI (OUVIREI) \nJESUS CHAMAR (PELO MEU NOME) \nE COM OS ANJOS (CANTAREI) \nVIVA O REI (VIVA O REI)\nJESUS!\n\nINSTRUMENTOS\n\nFINAL:\nVIVA O REI JESUS!\n\nINSTRUMENTOS\n\nJESUS!', 'AO LADO DE DEUS QUERO ESTAR, NAS MANSÕES QUE ELE MESMO PREPAROU, VER, FACE A FACE, O MEU SENHOR JESUS, REI DOS REIS; NAS RUAS DE OURO SEI QUE ANDAREI, NO TOQUE DA TROMBETA SUBIREI. OUVIREI (OUVIREI) JESUS CHAMAR (PELO MEU NOME) E COM OS ANJOS (CANTAREI) VIVA O REI (VIVA O REI) JESUS! INSTRUMENTOS NA ETERNIDADE EU VOU MORAR, PARA SEMPRE COM OS ANJOS VOU CANTAR; COM QUERUBINS E SERAFINS A JESUS ADORAREI; NAS RUAS DE OURO SEI QUE ANDAREI, NO TOQUE DA TROMBETA SUBIREI. VIVA O REI JESUS! INSTRUMENTOS JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('544', '544 - EM BREVE O DIA CHEGARÁ', 'EM BREVE O DIA CHEGARÁ,\nJESUS NAS NUVENS VIRÁ\nE NOS LEVARÁ PARA UM NOVO LAR, \nA NOVA JERUSALÉM.\n\nCORO\nE NOS MOSTRARÁ UM RIO DE ÁGUAS VIVAS \nQUE PROCEDE DO TRONO DO SENHOR \nE PARA SEMPRE LOUVAREMOS\nO CORDEIRO QUE VENCEU,\nO ALFA E O ÔMEGA, O NOSSO REI.\n\nINSTRUMENTOS\n\nTODA DOR CESSARÁ, O DIA E NOITE NÃO HAVERÁ \nPORQUE O SENHOR SERÁ A NOSSA LUZ \nE PARA SEMPRE REINAREMOS COM JESUS.\n\nCORO (2X)\nE NOS MOSTRARÁ UM RIO DE ÁGUAS VIVAS \nQUE PROCEDE DO TRONO DO SENHOR \nE PARA SEMPRE LOUVAREMOS\nO CORDEIRO QUE VENCEU,\nO ALFA E O ÔMEGA, O NOSSO REI.', 'EM BREVE O DIA CHEGARÁ, JESUS NAS NUVENS VIRÁ E NOS LEVARÁ PARA UM NOVO LAR, A NOVA JERUSALÉM. CORO E NOS MOSTRARÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDE DO TRONO DO SENHOR E PARA SEMPRE LOUVAREMOS O CORDEIRO QUE VENCEU, O ALFA E O ÔMEGA, O NOSSO REI. INSTRUMENTOS TODA DOR CESSARÁ, O DIA E NOITE NÃO HAVERÁ PORQUE O SENHOR SERÁ A NOSSA LUZ E PARA SEMPRE REINAREMOS COM JESUS. E NOS MOSTRARÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDE DO TRONO DO SENHOR E PARA SEMPRE LOUVAREMOS O CORDEIRO QUE VENCEU, O ALFA E O ÔMEGA, O NOSSO REI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('545', '545 - JESUS ESTÁ ÀS PORTAS', 'JESUS ESTÁ ÀS PORTAS,\nJESUS EM BREVE VEM\nNAS NUVENS COM OS ANJOS,\nEM GLÓRIA E ESPLENDOR.\nBIS\n\nVEM (VEM), AMADO JESUS,\nTUA IGREJA ESPERA\nANSIOSA POR VER \nTUA FACE EM ESPLENDOR,\nESTAMOS A TE ESPERAR (JESUS)\n \nFINAL: \n(JESUS) ESTAMOS A TE ESPERAR. (BIS)\nBIS', 'JESUS ESTÁ ÀS PORTAS, JESUS EM BREVE VEM NAS NUVENS COM OS ANJOS, EM GLÓRIA E ESPLENDOR. VEM (VEM), AMADO JESUS, TUA IGREJA ESPERA ANSIOSA POR VER TUA FACE EM ESPLENDOR, ESTAMOS A TE ESPERAR (JESUS)  (JESUS) ESTAMOS A TE ESPERAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('546', '546 - OH! QUANTO ALMEJO ESTAR', 'OH! QUANTO ALMEJO ESTAR PERANTE A TUA FACE\nE CONTEMPLAR A TUA MAJESTADE;\nSEI QUE A VIDA AQUI LOGO VAI PASSAR\nE TODA LÁGRIMA DEUS ENXUGARÁ.\n\nCORO\nJERUSALÉM, CIDADE CELESTIAL (JERUSALÉM)\nOH! QUANTO ANSEIO ESTAR\nJUNTO AO MEU AMIGO E REI,\nQUE POR AMOR VERTEU SEU SANGUE\nPARA NOS SALVAR.\nMARANATA! (MARANATA)\nORA VEM, SENHOR JESUS!\n\nINSTRUMENTOS\n\nEM BREVE IREI MORAR NA BELA CIDADE, \nEM RUAS DE OURO SEI QUE VOU ANDAR;\nJERUSALÉM, ONDE CRISTO REINARÁ\nE PARA SEMPRE COM ELE IREI ESTAR.\n\nCORO\nJERUSALÉM, CIDADE CELESTIAL (JERUSALÉM)\nOH! QUANTO ANSEIO ESTAR JUNTO \nAO MEU AMIGO E REI, QUE POR AMOR \nVERTEU SEU SANGUE PARA NOS SALVAR.\nMARANATA! (MARANATA)\nORA VEM, SENHOR JESUS!\n\n\nFINAL: MARANATA! ORA VEM SENHOR JESUS!', 'OH! QUANTO ALMEJO ESTAR PERANTE A TUA FACE E CONTEMPLAR A TUA MAJESTADE; SEI QUE A VIDA AQUI LOGO VAI PASSAR E TODA LÁGRIMA DEUS ENXUGARÁ. CORO JERUSALÉM, CIDADE CELESTIAL (JERUSALÉM) OH! QUANTO ANSEIO ESTAR JUNTO AO MEU AMIGO E REI, QUE POR AMOR VERTEU SEU SANGUE PARA NOS SALVAR. MARANATA! (MARANATA) ORA VEM, SENHOR JESUS! INSTRUMENTOS EM BREVE IREI MORAR NA BELA CIDADE, EM RUAS DE OURO SEI QUE VOU ANDAR; JERUSALÉM, ONDE CRISTO REINARÁ E PARA SEMPRE COM ELE IREI ESTAR. CORO JERUSALÉM, CIDADE CELESTIAL (JERUSALÉM) OH! QUANTO ANSEIO ESTAR JUNTO AO MEU AMIGO E REI, QUE POR AMOR VERTEU SEU SANGUE PARA NOS SALVAR. MARANATA! (MARANATA) ORA VEM, SENHOR JESUS!  MARANATA! ORA VEM SENHOR JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('547', '547 - Ó VEM LOGO, JESUS', 'Ó VEM LOGO, JESUS!\nÓ VEM LOGO, JESUS!\nA MINH’ALMA TE ESPERA,\nÓ VEM LOGO, JESUS!\n\nINSTRUMENTOS\n\nQUANTO ALMEJO TE VER, \nMEU AMADO JESUS, \nO TEU ROSTO TÃO LINDO\nBRILHANDO TUA LUZ. \nORA VEM (VEM) \nVEM (VEM), JESUS!\n\nINSTRUMENTOS\n\nA MINH’ALMA TE ESPERA, \nGLORIOSO JESUS, \nCOROADO DE GLÓRIA,\nBRILHANDO TUA LUZ. \nORA VEM (VEM) \nVEM (VEM), JESUS!\n\nINSTRUMENTOS\n\nÓ VEM LOGO, JESUS!\nÓ VEM LOGO, JESUS!\nA MINH’ALMA TE ESPERA,\nÓ VEM LOGO, JESUS!\n\nÓ VEM LOGO, JESUS!\nVEM, VEM, VEM!\nÓ VEM LOGO, JESUS!\nVEM, VEM, VEM!\nA MINH’ALMA TE ESPERA,\nVEM, VEM, VEM!\nÓ VEM LOGO, JESUS!\n\nFINAL: VEM JESUS!\n\n3X', 'Ó VEM LOGO, JESUS! Ó VEM LOGO, JESUS! A MINH’ALMA TE ESPERA, Ó VEM LOGO, JESUS! INSTRUMENTOS QUANTO ALMEJO TE VER, MEU AMADO JESUS, O TEU ROSTO TÃO LINDO BRILHANDO TUA LUZ. ORA VEM (VEM) VEM (VEM), JESUS! INSTRUMENTOS A MINH’ALMA TE ESPERA, GLORIOSO JESUS, COROADO DE GLÓRIA, BRILHANDO TUA LUZ. ORA VEM (VEM) VEM (VEM), JESUS! INSTRUMENTOS Ó VEM LOGO, JESUS! Ó VEM LOGO, JESUS! A MINH’ALMA TE ESPERA, Ó VEM LOGO, JESUS! Ó VEM LOGO, JESUS! VEM, VEM, VEM! Ó VEM LOGO, JESUS! VEM, VEM, VEM! A MINH’ALMA TE ESPERA, VEM, VEM, VEM! Ó VEM LOGO, JESUS!  VEM JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('548', '548 - NAS LINDAS MORADAS', 'NAS LINDAS MORADAS EM BREVE ESTAREI \nLOUVANDO PRA SEMPRE \nA JESUS, MEU GRANDE REI;\nNAS RUAS DE OURO SEI QUE VOU \nANDAR E JUNTO COM OS ANJOS LOUVAR.\n\nCORO\nA JESUS, O REI, \nO LEÃO DA TRIBO DE JUDÁ, \nQUE VENCEU E O LIVRO ABRIRÁ.\n\nO SEU ROSTO LINDO NO CÉU HEI DE VER, \nNO SEU MEIGO OLHAR SENTIREI QUANTO ME AMOU;\nSUAS MARAVILHAS IREI CONTEMPLAR\nE JUNTO COM OS ANJOS LOUVAR.\n\nCORO (2X)\nA JESUS, O REI, \nO LEÃO DA TRIBO DE JUDÁ, \nQUE VENCEU E O LIVRO ABRIRÁ.\n\nFINAL: \nE O LIVRO ABRIRÁ, \nE O LIVRO ABRIRÁ.', 'NAS LINDAS MORADAS EM BREVE ESTAREI LOUVANDO PRA SEMPRE A JESUS, MEU GRANDE REI; NAS RUAS DE OURO SEI QUE VOU ANDAR E JUNTO COM OS ANJOS LOUVAR. CORO A JESUS, O REI, O LEÃO DA TRIBO DE JUDÁ, QUE VENCEU E O LIVRO ABRIRÁ. O SEU ROSTO LINDO NO CÉU HEI DE VER, NO SEU MEIGO OLHAR SENTIREI QUANTO ME AMOU; SUAS MARAVILHAS IREI CONTEMPLAR E JUNTO COM OS ANJOS LOUVAR. A JESUS, O REI, O LEÃO DA TRIBO DE JUDÁ, QUE VENCEU E O LIVRO ABRIRÁ.  E O LIVRO ABRIRÁ, E O LIVRO ABRIRÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('549', '549 - MARAVILHOSO SERÁ', 'MARAVILHOSO SERÁ \nQUANDO JESUS VOLTAR, \nMARAVILHOSO SERÁ.\n\nINSTRUMENTOS\n\nOH! QUE ALEGRIA SERÁ QUANDO JESUS VOLTAR, \nANJOS LOUVANDO SEU NOME,\nOS CÉUS SE ABRINDO EM ESPLENDOR.\n\nCORO (2X)\nMARAVILHOSO SERÁ \n(MARAVILHOSO)\nQUE DIA GLORIOSO SERÁ.\n\nTODA LUTA CESSARÁ, TODO PRANTO, \nTODA DOR, A IGREJA SUBIRÁ LOUVANDO \nAO SENHOR.\n\nCORO (2X)\nMARAVILHOSO SERÁ \n(MARAVILHOSO)\nQUE DIA GLORIOSO SERÁ.\n\nORA VEM, ORA VEM, SENHOR JESUS,\nTUA IGREJA TE ESPERA, TUA IGREJA PROCLAMA:\nMARANATA! ORA VEM, SENHOR JESUS.\n\nCORO (2X)\nMARAVILHOSO SERÁ \n(MARAVILHOSO)\nQUE DIA GLORIOSO SERÁ.\n\nFINAL: \nMARANATA! ORA VEM,\n(MARANATA! ORA VEM)\nMARANATA! ORA VEM,\n(MARANATA! ORA VEM)\nVEM, JESUS!', 'MARAVILHOSO SERÁ QUANDO JESUS VOLTAR, MARAVILHOSO SERÁ. INSTRUMENTOS OH! QUE ALEGRIA SERÁ QUANDO JESUS VOLTAR, ANJOS LOUVANDO SEU NOME, OS CÉUS SE ABRINDO EM ESPLENDOR. MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ. TODA LUTA CESSARÁ, TODO PRANTO, TODA DOR, A IGREJA SUBIRÁ LOUVANDO AO SENHOR. MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ. ORA VEM, ORA VEM, SENHOR JESUS, TUA IGREJA TE ESPERA, TUA IGREJA PROCLAMA: MARANATA! ORA VEM, SENHOR JESUS. MARAVILHOSO SERÁ (MARAVILHOSO) QUE DIA GLORIOSO SERÁ.  MARANATA! ORA VEM, (MARANATA! ORA VEM) MARANATA! ORA VEM, (MARANATA! ORA VEM) VEM, JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('550', '550 - CANTAI, Ó IGREJA', 'CANTAI, Ó IGREJA \n(CANTAI, Ó IGREJA),\nO DIA CHEGARÁ, \nO NOIVO ENTRE AS NUVENS \nA TI RECEBERÁ.\n\nCORO (2X)\nALEGRAI-VOS, Ó FILHOS (ALEGRAI-VOS, IRMÃOS),\nALEGRAI-VOS, IRMÃOS, EM BREVE A IGREJA \nCANTARÁ EM SIÃO.\n\nMESMO EM LUTAS \n(MESMO EM LUTAS)\nQUE PROVAM NOSSO AMOR, \nA IGREJA, TRIUNFANTE, \nESPERA NO SENHOR.\n\nCORO (2X)\nALEGRAI-VOS, Ó FILHOS (ALEGRAI-VOS, IRMÃOS),\nALEGRAI-VOS, IRMÃOS, EM BREVE A IGREJA \nCANTARÁ EM SIÃO.\n\nFINAL: ALEGRAI-VOS.', 'CANTAI, Ó IGREJA (CANTAI, Ó IGREJA), O DIA CHEGARÁ, O NOIVO ENTRE AS NUVENS A TI RECEBERÁ. ALEGRAI-VOS, Ó FILHOS (ALEGRAI-VOS, IRMÃOS), ALEGRAI-VOS, IRMÃOS, EM BREVE A IGREJA CANTARÁ EM SIÃO. MESMO EM LUTAS (MESMO EM LUTAS) QUE PROVAM NOSSO AMOR, A IGREJA, TRIUNFANTE, ESPERA NO SENHOR. ALEGRAI-VOS, Ó FILHOS (ALEGRAI-VOS, IRMÃOS), ALEGRAI-VOS, IRMÃOS, EM BREVE A IGREJA CANTARÁ EM SIÃO.  ALEGRAI-VOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('551', '551 - TENHO LIDO DA BELA CIDADE  (A BELA CIDADE)', 'TENHO LIDO DA BELA CIDADE,\nCONSTRUÍDA POR CRISTO NOS CÉUS;\nÉ MURADA DE JASPE LUZENTE\nE JUNCADA COM ÁUREOS TROFÉUS.\nE NO MEIO DA PRAÇA EIS O RIO\nDO VIGOR E DA VIDA ETERNAL;\nMAS METADE DA GLÓRIA CELESTE\nJAMAIS SE CONTOU AO MORTAL.\n\nCORO\nJAMAIS SE CONTOU AO MORTAL (BIS)\nMETADE DA GLÓRIA CELESTE\nJAMAIS SE CONTOU AO MORTAL.\n\nTENHO LIDO DAS BELAS MORADAS\nQUE JESUS FOI NO CÉU PREPARAR,\nONDE OS CRENTES FIÉIS PARA SEMPRE,\nMUI FELIZES, IRÃO HABITAR;\nNEM TRISTEZA, NEM DOR, NEM GEMIDOS\nENTRARÃO NA MANSÃO PATERNAL;\nMAS METADE DO GOZO CELESTE\nJAMAIS SE CONTOU AO MORTAL.\n\nCORO\nJAMAIS SE CONTOU AO MORTAL (BIS)\nMETADE DA GLÓRIA CELESTE\nJAMAIS SE CONTOU AO MORTAL.\n\nTENHO LIDO DAS VESTES BRILHANTES,\nDAS COROAS QUE OS SANTOS TERÃO\nQUANDO O PAI OS CHAMAR E DISSER-LHES:\n“RECEBEI O ETERNAL GALARDÃO”.\nTENHO LIDO QUE OS SANTOS NA GLÓRIA\nPISARÃO RUAS DE OURO E CRISTAL;\nMAS METADE DA GLÓRIA CELESTE\nJAMAIS SE CONTOU AO MORTAL.\n\nCORO\nJAMAIS SE CONTOU AO MORTAL (BIS)\nMETADE DA GLÓRIA CELESTE\nJAMAIS SE CONTOU AO MORTAL.', 'TENHO LIDO DA BELA CIDADE, CONSTRUÍDA POR CRISTO NOS CÉUS; É MURADA DE JASPE LUZENTE E JUNCADA COM ÁUREOS TROFÉUS. E NO MEIO DA PRAÇA EIS O RIO DO VIGOR E DA VIDA ETERNAL; MAS METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL. CORO JAMAIS SE CONTOU AO MORTAL  METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL. TENHO LIDO DAS BELAS MORADAS QUE JESUS FOI NO CÉU PREPARAR, ONDE OS CRENTES FIÉIS PARA SEMPRE, MUI FELIZES, IRÃO HABITAR; NEM TRISTEZA, NEM DOR, NEM GEMIDOS ENTRARÃO NA MANSÃO PATERNAL; MAS METADE DO GOZO CELESTE JAMAIS SE CONTOU AO MORTAL. TENHO LIDO DAS VESTES BRILHANTES, DAS COROAS QUE OS SANTOS TERÃO QUANDO O PAI OS CHAMAR E DISSER-LHES: RECEBEI O ETERNAL GALARDÃO. TENHO LIDO QUE OS SANTOS NA GLÓRIA PISARÃO RUAS DE OURO E CRISTAL; MAS METADE DA GLÓRIA CELESTE JAMAIS SE CONTOU AO MORTAL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('552', '552 - PÁTRIA MINHA', 'PÁTRIA MINHA, POR TI SUSPIRO \nQUANDO NO TEU BOM DESCANSO CHEGAREI?\nOS PATRIARCAS, DE DEUS AMIGOS,\nE OS BONS PROFETAS, FIÉIS, ANTIGOS,\nJÁ ENTRARAM NA TUA GLÓRIA,\nCONTEMPLANDO, EM ESPLENDOR, \nO GRANDE REI.\n\nOS REMIDOS, TÃO PERSEGUIDOS,\nPELO SANGUE JÁ VENCERAM O DRAGÃO;\nPOR JESUS CRISTO SÃO VENCEDORES,\nE AGORA CANTAM OS SEUS LOUVORES;\nPÁTRIA SANTA, DESEJO VER-TE,\nVER COM CRISTO A REDIMIDA MULTIDÃO.\n\nLÁ O RIO DAS ÁGUAS VIVAS \nSAI DO TRONO DO CORDEIRO E DO SENHOR;\nÉ LUMINOSO DESDE A NASCENTE,\nCOMO CRISTAL É RESPLANDECENTE;\nPELA MARGEM DAQUELE RIO ANDAM \nOS REMIDOS COM O SALVADOR.\n\nINSTRUMENTOS\n\nNÃO HÁ PRANTO NA MINHA PÁTRIA,\nNELA NUNCA SE DARÁ SEPARAÇÃO.\nALI O TRONO DE DEUS DESCANSA,\nALI TEREMOS REAL BONANÇA;\nOS REMIDOS DA MINHA PÁTRIA\nCOM JESUS ETERNAMENTE REINARÃO.', 'PÁTRIA MINHA, POR TI SUSPIRO QUANDO NO TEU BOM DESCANSO CHEGAREI? OS PATRIARCAS, DE DEUS AMIGOS, E OS BONS PROFETAS, FIÉIS, ANTIGOS, JÁ ENTRARAM NA TUA GLÓRIA, CONTEMPLANDO, EM ESPLENDOR, O GRANDE REI. OS REMIDOS, TÃO PERSEGUIDOS, PELO SANGUE JÁ VENCERAM O DRAGÃO; POR JESUS CRISTO SÃO VENCEDORES, E AGORA CANTAM OS SEUS LOUVORES; PÁTRIA SANTA, DESEJO VER-TE, VER COM CRISTO A REDIMIDA MULTIDÃO. LÁ O RIO DAS ÁGUAS VIVAS SAI DO TRONO DO CORDEIRO E DO SENHOR; É LUMINOSO DESDE A NASCENTE, COMO CRISTAL É RESPLANDECENTE; PELA MARGEM DAQUELE RIO ANDAM OS REMIDOS COM O SALVADOR. INSTRUMENTOS NÃO HÁ PRANTO NA MINHA PÁTRIA, NELA NUNCA SE DARÁ SEPARAÇÃO. ALI O TRONO DE DEUS DESCANSA, ALI TEREMOS REAL BONANÇA; OS REMIDOS DA MINHA PÁTRIA COM JESUS ETERNAMENTE REINARÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('553', '553 - VEM! MARANATA! JESUS VEM!', 'VEM! MARANATA! JESUS VEM!\nESTE DIA CHEGARÁ, SUA IGREJA SUBIRÁ.\nVAMOS MORAR NA CIDADE ETERNAL,\nNA NOVA JERUSALÉM, \nONDE AS RUAS SÃO DE OURO.\n\nLÁ VAMOS CANTAR, \nVAMOS LOUVAR A JESUS CRISTO.\nLÁ VAMOS CANTAR, \nVAMOS LOUVAR (SÓ ELE É DIGNO)\nJUNTOS COM OS ANJOS NO CÉU.\n\nVEM! MARANATA! JESUS VEM!\nESTE DIA CHEGARÁ, SUA IGREJA SUBIRÁ.\nVAMOS MORAR NA CIDADE ETERNAL,\nNA NOVA JERUSALÉM, \nONDE AS RUAS SÃO DE OURO.\n\nLÁ VAMOS CANTAR, \nVAMOS LOUVAR A JESUS CRISTO.\nLÁ VAMOS CANTAR, \nVAMOS LOUVAR (SÓ ELE É DIGNO)\nJUNTOS COM OS ANJOS NO CÉU.\n\nFINAL: \nTODOS LOUVANDO A JESUS, \nNO CÉU, NO CÉU.', 'VEM! MARANATA! JESUS VEM! ESTE DIA CHEGARÁ, SUA IGREJA SUBIRÁ. VAMOS MORAR NA CIDADE ETERNAL, NA NOVA JERUSALÉM, ONDE AS RUAS SÃO DE OURO. LÁ VAMOS CANTAR, VAMOS LOUVAR A JESUS CRISTO. LÁ VAMOS CANTAR, VAMOS LOUVAR (SÓ ELE É DIGNO) JUNTOS COM OS ANJOS NO CÉU. LÁ VAMOS CANTAR, VAMOS LOUVAR A JESUS CRISTO. LÁ VAMOS CANTAR, VAMOS LOUVAR (SÓ ELE É DIGNO) JUNTOS COM OS ANJOS NO CÉU.  TODOS LOUVANDO A JESUS, NO CÉU, NO CÉU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('554', '554 - JERUSALÉM (SUAS RUAS DE OURO)', 'JERUSALÉM, JERUSALÉM, \nSUAS RUAS DE OURO, MEU GRANDE BEM.\nJERUSALÉM, JERUSALÉM,\nCIDADE TÃO LINDA, IGUAL NÃO TEM.\n\nINSTRUMENTOS\n\nDAS LINDAS MORADAS, Ó PÁTRIA QUERIDA, \nDO PERFUME DAS FLORES, \nESSÊNCIA DA VIDA, A MINH’ALMA ESPERA, \nESPERA POR TI, Ó JERUSALÉM!\n\nJERUSALÉM, JERUSALÉM, SUAS RUAS \nDE OURO, MEU GRANDE BEM;\nJERUSALÉM, JERUSALÉM,\nCIDADE TÃO LINDA, IGUAL NÃO TEM.\n\nINSTRUMENTOS\n\nDAS LINDAS MORADAS, Ó PÁTRIA QUERIDA, \nDO PERFUME DAS FLORES, \nESSÊNCIA DA VIDA, A MINH’ALMA ESPERA, \nESPERA POR TI, Ó JERUSALÉM!\n\nJERUSALÉM (JERUSALÉM)\nJERUSALÉM (JERUSALÉM)\nSUAS RUAS DE OURO, MEU GRANDE BEM;\nJERUSALÉM (JERUSALÉM)\nJERUSALÉM (JERUSALÉM)\nCIDADE TÃO LINDA, IGUAL NÃO TEM.', 'JERUSALÉM, JERUSALÉM, SUAS RUAS DE OURO, MEU GRANDE BEM. JERUSALÉM, JERUSALÉM, CIDADE TÃO LINDA, IGUAL NÃO TEM. INSTRUMENTOS DAS LINDAS MORADAS, Ó PÁTRIA QUERIDA, DO PERFUME DAS FLORES, ESSÊNCIA DA VIDA, A MINH’ALMA ESPERA, ESPERA POR TI, Ó JERUSALÉM! JERUSALÉM, JERUSALÉM, SUAS RUAS DE OURO, MEU GRANDE BEM; JERUSALÉM, JERUSALÉM, CIDADE TÃO LINDA, IGUAL NÃO TEM. INSTRUMENTOS JERUSALÉM (JERUSALÉM) JERUSALÉM (JERUSALÉM) SUAS RUAS DE OURO, MEU GRANDE BEM; JERUSALÉM (JERUSALÉM) JERUSALÉM (JERUSALÉM) CIDADE TÃO LINDA, IGUAL NÃO TEM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('555', '555 - DA LINDA PÁTRIA', 'DA LINDA PÁTRIA ESTOU MUI LONGE,\nTRISTE EU ESTOU;\nEU TENHO DE JESUS SAUDADE;\nQUANDO SERÁ QUE VOU?\nPASSARINHOS, BELAS FLORES,\nQUEREM ME ENCANTAR,\nOH! VÃOS TERRESTRES ESPLENDORES,\nNÃO QUERO AQUI FICAR!\n\nJESUS ME DEU FIEL PROMESSA,\nVEM ME BUSCAR;\nMEU CORAÇÃO ESTÁ COM PRESSA,\nEU QUERO AO CÉU VOAR.\nMEUS PECADOS SÃO MUI GRANDES\nE CULPADO SOU;\nMAS O SEU SANGUE PÕE-ME LIMPO\nE PARA A PÁTRIA VOU.\n\nQUAL FILHO DO SEU LAR SAUDOSO,\nEU QUERO IR;\nQUAL PASSARINHO PARA O NINHO,\nEU QUERO AO CÉU SUBIR.\nSUA VINDA AO MUNDO É CERTA,\nQUANDO NÃO O SEI,\nMAS ELE ME ACHARÁ ALERTA\nE PARA O CÉU IREI.', 'DA LINDA PÁTRIA ESTOU MUI LONGE, TRISTE EU ESTOU; EU TENHO DE JESUS SAUDADE; QUANDO SERÁ QUE VOU? PASSARINHOS, BELAS FLORES, QUEREM ME ENCANTAR, OH! VÃOS TERRESTRES ESPLENDORES, NÃO QUERO AQUI FICAR! JESUS ME DEU FIEL PROMESSA, VEM ME BUSCAR; MEU CORAÇÃO ESTÁ COM PRESSA, EU QUERO AO CÉU VOAR. MEUS PECADOS SÃO MUI GRANDES E CULPADO SOU; MAS O SEU SANGUE PÕE-ME LIMPO E PARA A PÁTRIA VOU. QUAL FILHO DO SEU LAR SAUDOSO, EU QUERO IR; QUAL PASSARINHO PARA O NINHO, EU QUERO AO CÉU SUBIR. SUA VINDA AO MUNDO É CERTA, QUANDO NÃO O SEI, MAS ELE ME ACHARÁ ALERTA E PARA O CÉU IREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('556', '556 - NA NOVA JERUSALÉM IREI MORAR', 'NA NOVA JERUSALÉM IREI MORAR,\nONDE MORA O MEU JESUS, LÁ ESTAREI,\nNA CIDADE ETERNAL IREI GOZAR,\nNAS RUAS DE OURO SEI QUE ANDAREI.\n\nCORO\nLÁ CANTAREI LOUVORES A JESUS,\nLÁ LOUVAREI COM OS ANJOS AO MEU REI\nNA CIDADE DE LUZ EM BREVE ESTAREI.\n\nINSTRUMENTOS\n\nAQUI NÃO FICAREI, NÃO É MEU LUGAR,\nPRA SEMPRE COM JESUS IREI MORAR, \nCOM OS ANJOS, QUERUBINS, \nTAMBÉM SERAFINS A FACE DE JESUS \nVOU CONTEMPLAR.\n\nCORO (2X)\nLÁ CANTAREI LOUVORES A JESUS,\nLÁ LOUVAREI COM OS ANJOS AO MEU REI,\nNA CIDADE DE LUZ EM BREVE ESTAREI.\n\nFINAL: \nNA CIDADE DE LUZ EM BREVE ESTAREI.', 'NA NOVA JERUSALÉM IREI MORAR, ONDE MORA O MEU JESUS, LÁ ESTAREI, NA CIDADE ETERNAL IREI GOZAR, NAS RUAS DE OURO SEI QUE ANDAREI. CORO LÁ CANTAREI LOUVORES A JESUS, LÁ LOUVAREI COM OS ANJOS AO MEU REI NA CIDADE DE LUZ EM BREVE ESTAREI. INSTRUMENTOS AQUI NÃO FICAREI, NÃO É MEU LUGAR, PRA SEMPRE COM JESUS IREI MORAR, COM OS ANJOS, QUERUBINS, TAMBÉM SERAFINS A FACE DE JESUS VOU CONTEMPLAR. LÁ CANTAREI LOUVORES A JESUS, LÁ LOUVAREI COM OS ANJOS AO MEU REI, NA CIDADE DE LUZ EM BREVE ESTAREI.  NA CIDADE DE LUZ EM BREVE ESTAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('557', '557 - ESTE MUNDO JAMAIS PODE ME SEPARAR', 'ESTE MUNDO JAMAIS\nPODE ME SEPARAR\nDOS VALORES CELESTIAIS\nQUE EU VOU RECEBER;\nMEU TESOURO E ESPERANÇA\nESTÃO NO MEU NOVO LAR,\nSOU HERDEIRO COM CRISTO,\nVOU COM ELE MORAR.\n\nCORO\nCÉU, LINDO CÉU! CÉU, LINDO CÉU!\nHÁ MANSÕES CELESTIAIS,\nTODAS FEITAS POR DEUS!\nCÉU, LINDO CÉU! CÉU, LINDO CÉU!\nEU VOU PRO CÉU, LINDO CÉU,\nCOM CRISTO VOU MORAR \nNO LINDO CÉU!\n\nJÁ ESTANDO ALÉM RIO,\nMINHA LUTA FINDOU,\nEU NÃO TEMO MAIS PECADO\nPOIS JESUS ME LAVOU;\nOH! QUE GLÓRIA NA SUBIDA\nPRAS ALTURAS DOS CÉUS,\nCANTAREI A LINDA HISTÓRIA\nDO CORDEIRO DE DEUS!\n\nCORO\nCÉU, LINDO CÉU! CÉU, LINDO CÉU!\nHÁ MANSÕES CELESTIAIS,\nTODAS FEITAS POR DEUS!\nCÉU, LINDO CÉU! CÉU, LINDO CÉU!\nEU VOU PRO CÉU, LINDO CÉU,\nCOM CRISTO VOU MORAR \nNO LINDO CÉU!', 'ESTE MUNDO JAMAIS PODE ME SEPARAR DOS VALORES CELESTIAIS QUE EU VOU RECEBER; MEU TESOURO E ESPERANÇA ESTÃO NO MEU NOVO LAR, SOU HERDEIRO COM CRISTO, VOU COM ELE MORAR. CORO CÉU, LINDO CÉU! CÉU, LINDO CÉU! HÁ MANSÕES CELESTIAIS, TODAS FEITAS POR DEUS! CÉU, LINDO CÉU! CÉU, LINDO CÉU! EU VOU PRO CÉU, LINDO CÉU, COM CRISTO VOU MORAR NO LINDO CÉU! JÁ ESTANDO ALÉM RIO, MINHA LUTA FINDOU, EU NÃO TEMO MAIS PECADO POIS JESUS ME LAVOU; OH! QUE GLÓRIA NA SUBIDA PRAS ALTURAS DOS CÉUS, CANTAREI A LINDA HISTÓRIA DO CORDEIRO DE DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('558', '558 - VEM, JESUS CRISTO JÁ VEM', 'VEM, JESUS CRISTO JÁ VEM, \nEM BREVE ELE VEM, VEM NOS BUSCAR,\nQUE DIA GLORIOSO SERÁ\nQUANDO OS SALVOS SUBIREM\nPRA COM ELE ESTAR.\n\nEM BREVE JESUS CRISTO VEM,\nIREMOS COM ELE SUBIR, \nVEREMOS NOSSO SENHOR.\n\nVARÕES\n\nEM BREVE JESUS CRISTO VEM, (DE GLÓRIA)\nIREMOS COM ELE SUBIR, (EM GLÓRIA)\nVEREMOS NOSSO REDENTOR.\n\nINSTRUMENTOS\n\nVEM, JESUS CRISTO JÁ VEM, \nEM BREVE ELE VEM, VEM NOS BUSCAR,\nQUE DIA GLORIOSO SERÁ\nQUANDO OS SALVOS SUBIREM\nPRA COM ELE ESTAR.\n\nEM BREVE JESUS CRISTO VEM,\nIREMOS COM ELE SUBIR, \nVEREMOS NOSSO SENHOR.\n\nVARÕES\n\nEM BREVE JESUS CRISTO VEM, (DE GLÓRIA)\nIREMOS COM ELE SUBIR, (EM GLÓRIA)\nVEREMOS NOSSO REDENTOR.\n\nINSTRUMENTOS\n\nVEM, \n(SIM, JESUS VEM)\nVEM, \n(SIM, JESUS VEM)\nVEM, SIM, JESUS VEM.', 'VEM, JESUS CRISTO JÁ VEM, EM BREVE ELE VEM, VEM NOS BUSCAR, QUE DIA GLORIOSO SERÁ QUANDO OS SALVOS SUBIREM PRA COM ELE ESTAR. EM BREVE JESUS CRISTO VEM, IREMOS COM ELE SUBIR, VEREMOS NOSSO SENHOR. EM BREVE JESUS CRISTO VEM, (DE GLÓRIA) IREMOS COM ELE SUBIR, (EM GLÓRIA) VEREMOS NOSSO REDENTOR. INSTRUMENTOS VEM, (SIM, JESUS VEM) VEM, (SIM, JESUS VEM) VEM, SIM, JESUS VEM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('559', '559 - A ÚLTIMA TROMBETA SOARÁ', 'A ÚLTIMA TROMBETA SOARÁ,\nJESUS ENTÃO VIRÁ;\nQUÃO TERRÍVEL DIA SERÁ,\nMAS ASSIM APROUVE A DEUS REALIZAR.\n\nAMADOS, PREPARAI VOSSOS CAMINHOS,\nCONVERTEI-VOS A DEUS, \nO ÚNICO SALVADOR E DOMINADOR, \nPARA QUE NÃO SOFRAIS \nNO GRANDE DIA DO SENHOR\n\nCORO\nOUVI, Ó CÉUS,\nOUVI, Ó MORADORES DA TERRA\nO QUE O ESPÍRITO SANTO DIZ:\n“JESUS, O DESEJADO DAS NAÇÕES\nVEM BUSCAR SUA IGREJA FIEL”.\n\nINSTRUMENTOS\n\n“EU, JESUS, ENVIEI O MEU ANJO \nPARA VOS TESTIFICAR ESTAS COISAS NAS IGREJAS,\nEU SOU A RAIZ E A GERAÇÃO DE DAVI,\nA RESPLANDECENTE ESTRELA DA MANHÃ”.\n\nO ESPÍRITO E A ESPOSA DIZEM: VEM!\nE QUEM OUVE DIGA: VEM!\nQUEM TEM SEDE VENHA E QUEM QUISER\nBEBA DE GRAÇA DA ÁGUA DA VIDA.\n\nCORO (2X)\nOUVI, Ó CÉUS,\nOUVI, Ó MORADORES DA TERRA\nO QUE O ESPÍRITO SANTO DIZ:\n“JESUS, O DESEJADO DAS NAÇÕES\nVEM BUSCAR SUA IGREJA FIEL”.\n\nFINAL: \nVEM BUSCAR SUA IGREJA FIEL.', 'A ÚLTIMA TROMBETA SOARÁ, JESUS ENTÃO VIRÁ; QUÃO TERRÍVEL DIA SERÁ, MAS ASSIM APROUVE A DEUS REALIZAR. AMADOS, PREPARAI VOSSOS CAMINHOS, CONVERTEI-VOS A DEUS, O ÚNICO SALVADOR E DOMINADOR, PARA QUE NÃO SOFRAIS NO GRANDE DIA DO SENHOR CORO OUVI, Ó CÉUS, OUVI, Ó MORADORES DA TERRA O QUE O ESPÍRITO SANTO DIZ: JESUS, O DESEJADO DAS NAÇÕES VEM BUSCAR SUA IGREJA FIEL. INSTRUMENTOS EU, JESUS, ENVIEI O MEU ANJO PARA VOS TESTIFICAR ESTAS COISAS NAS IGREJAS, EU SOU A RAIZ E A GERAÇÃO DE DAVI, A RESPLANDECENTE ESTRELA DA MANHÃ. O ESPÍRITO E A ESPOSA DIZEM: VEM! E QUEM OUVE DIGA: VEM! QUEM TEM SEDE VENHA E QUEM QUISER BEBA DE GRAÇA DA ÁGUA DA VIDA. OUVI, Ó CÉUS, OUVI, Ó MORADORES DA TERRA O QUE O ESPÍRITO SANTO DIZ: JESUS, O DESEJADO DAS NAÇÕES VEM BUSCAR SUA IGREJA FIEL.  VEM BUSCAR SUA IGREJA FIEL.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('560', '560 - EIS QUE ESTOU A PORTA E BATO', '“EIS QUE ESTOU À PORTA E BATO,\nSE ABRIRES, ENTRAREI E CONTIGO VIVEREI,\nABRE A PORTA DO TEU CORAÇÃO.\n\nO MEU NOME É JESUS,\nO AUTOR DA PRÓPRIA VIDA,\nSOU O TEU LIBERTADOR,\nO CORDEIRO QUE RESSUSCITOU.\n\nEIS QUE VENHO SOBRE AS NUVENS,\nEIS QUE VENHO VENCEDOR,\nNADA MAIS ME DETERÁ,\nO MEU POVO VOU ARREBATAR,\nA TROMBETA BREVE SOARÁ;\nTUDO NOVO SE FARÁ\nNO MEU REINO DE JUSTIÇA E PAZ”.\nMARANATA, JESUS VEM!\n\nINSTRUMENTOS\n\n“ESTE DIA ESTÁ CHEGANDO,\nA FIGUEIRA JÁ BROTOU E A IGREJA FIEL\nORA E VIGIA SEM CESSAR.\n\nO MEU NOME É JESUS,\nSOU A FONTE DE ÁGUA VIVA,\nQUEM TEM SEDE VEM A MIM,\nVEM BEBER DE GRAÇA DESTA ÁGUA.\n\nEIS QUE VENHO SOBRE AS NUVENS,\nEIS QUE VENHO VENCEDOR,\nNADA MAIS ME DETERÁ,\nO MEU POVO VOU ARREBATAR,\nA TROMBETA BREVE SOARÁ;\nTUDO NOVO SE FARÁ\nNO MEU REINO DE JUSTIÇA E PAZ”.\nMARANATA, JESUS VEM!\nBIS\n\nFINAL: \n(MARANATA, JESUS)\nMARANATA, JESUS VEM!', 'EIS QUE ESTOU À PORTA E BATO, SE ABRIRES, ENTRAREI E CONTIGO VIVEREI, ABRE A PORTA DO TEU CORAÇÃO. O MEU NOME É JESUS, O AUTOR DA PRÓPRIA VIDA, SOU O TEU LIBERTADOR, O CORDEIRO QUE RESSUSCITOU. EIS QUE VENHO SOBRE AS NUVENS, EIS QUE VENHO VENCEDOR, NADA MAIS ME DETERÁ, O MEU POVO VOU ARREBATAR, A TROMBETA BREVE SOARÁ; TUDO NOVO SE FARÁ NO MEU REINO DE JUSTIÇA E PAZ. MARANATA, JESUS VEM! INSTRUMENTOS ESTE DIA ESTÁ CHEGANDO, A FIGUEIRA JÁ BROTOU E A IGREJA FIEL ORA E VIGIA SEM CESSAR. O MEU NOME É JESUS, SOU A FONTE DE ÁGUA VIVA, QUEM TEM SEDE VEM A MIM, VEM BEBER DE GRAÇA DESTA ÁGUA. EIS QUE VENHO SOBRE AS NUVENS, EIS QUE VENHO VENCEDOR, NADA MAIS ME DETERÁ, O MEU POVO VOU ARREBATAR, A TROMBETA BREVE SOARÁ; TUDO NOVO SE FARÁ NO MEU REINO DE JUSTIÇA E PAZ. MARANATA, JESUS VEM! (MARANATA, JESUS) MARANATA, JESUS VEM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('561', '561 - OUVI COMO QUE A VOZ DE  UMA GRANDE MULTIDÃO', 'OUVI COMO QUE A VOZ \nDE UMA GRANDE MULTIDÃO, \nCOMO VOZ DE MUITAS ÁGUAS, \nCOMO VOZ DE GRANDES TROVÕES;\nQUE DIZIA: ALELUIA!\nO SENHOR TODO PODEROSO \nREINA, REINA, REINA!\n\nREGOZIJEMO-NOS, ALEGREMO-NOS,\nDEMOS-LHE GLÓRIA!\nPORQUE VINDAS SÃO AS BODAS \nDO CORDEIRO E SUA ESPOSA SE PREPAROU.\n\nINSTRUMENTOS\n\nOUVI COMO QUE A VOZ \nDE UMA GRANDE MULTIDÃO, \nCOMO VOZ DE MUITAS ÁGUAS,\nCOMO VOZ DE GRANDES TROVÕES;\nQUE DIZIA: ALELUIA!\nO SENHOR TODO PODEROSO \nREINA, REINA, REINA!\n\nFINAL: AMÉM.', 'OUVI COMO QUE A VOZ DE UMA GRANDE MULTIDÃO, COMO VOZ DE MUITAS ÁGUAS, COMO VOZ DE GRANDES TROVÕES; QUE DIZIA: ALELUIA! O SENHOR TODO PODEROSO REINA, REINA, REINA! REGOZIJEMO-NOS, ALEGREMO-NOS, DEMOS-LHE GLÓRIA! PORQUE VINDAS SÃO AS BODAS DO CORDEIRO E SUA ESPOSA SE PREPAROU. INSTRUMENTOS OUVI COMO QUE A VOZ DE UMA GRANDE MULTIDÃO, COMO VOZ DE MUITAS ÁGUAS, COMO VOZ DE GRANDES TROVÕES; QUE DIZIA: ALELUIA! O SENHOR TODO PODEROSO REINA, REINA, REINA!  AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('562', '562 - QUANDO MEU TEMPO DE LUTA PASSAR', 'QUANDO MEU TEMPO DE LUTA PASSAR,\nQUANDO MEU DEUS PARA SI ME CHAMAR,\nGRATO, PERANTE JESUS HEI DE ESTAR;\nGLÓRIA PERENE SERÁ PARA MIM!\n\nCORO\nSIM, HÁ DE SER GLÓRIA PRA MIM,\nGLÓRIA PRA MIM, GLÓRIA PRA MIM;\nQUANDO PUDER O SEU ROSTO MIRAR,\nOH! HÁ DE SER GRANDE GLÓRIA PRA MIM!\n\nQUANDO POR GRAÇA DO SEU GRANDE AMOR \nEU ALCANÇAR O INFINITO FAVOR,\nDE IR PARA PERTO DO MEU SALVADOR,\nGLÓRIA PERENE SERÁ PARA MIM!\n\nCORO\nSIM, HÁ DE SER GLÓRIA PRA MIM,\nGLÓRIA PRA MIM, GLÓRIA PRA MIM;\nQUANDO PUDER O SEU ROSTO MIRAR,\nOH! HÁ DE SER GRANDE GLÓRIA PRA MIM!\n\nMUITOS AMIGOS ALI HEI DE ACHAR,\nPAZ, A ALEGRIA ETERNAL, BEM ESTAR;\nMAS QUANDO MEU SALVADOR ME SAUDAR, \nGLÓRIA PERENE SERÁ PARA MIM!\n\nCORO\nSIM, HÁ DE SER GLÓRIA PRA MIM,\nGLÓRIA PRA MIM, GLÓRIA PRA MIM;\nQUANDO PUDER O SEU ROSTO MIRAR,\nOH! HÁ DE SER GRANDE GLÓRIA PRA MIM!', 'QUANDO MEU TEMPO DE LUTA PASSAR, QUANDO MEU DEUS PARA SI ME CHAMAR, GRATO, PERANTE JESUS HEI DE ESTAR; GLÓRIA PERENE SERÁ PARA MIM! CORO SIM, HÁ DE SER GLÓRIA PRA MIM, GLÓRIA PRA MIM, GLÓRIA PRA MIM; QUANDO PUDER O SEU ROSTO MIRAR, OH! HÁ DE SER GRANDE GLÓRIA PRA MIM! QUANDO POR GRAÇA DO SEU GRANDE AMOR EU ALCANÇAR O INFINITO FAVOR, DE IR PARA PERTO DO MEU SALVADOR, GLÓRIA PERENE SERÁ PARA MIM! MUITOS AMIGOS ALI HEI DE ACHAR, PAZ, A ALEGRIA ETERNAL, BEM ESTAR; MAS QUANDO MEU SALVADOR ME SAUDAR, GLÓRIA PERENE SERÁ PARA MIM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('563', '563 - NÃO SE TURBEM OS VOSSOS CORAÇÕES', 'NÃO SE TURBEM OS VOSSOS CORAÇÕES,\nBREVE SE CUMPRIRÁ A PROMESSA DE JESUS;\nTODO PRANTO CESSARÁ E OS SANTOS SUBIRÃO \nLEVADOS PELOS ANJOS AO SENHOR.\n\nCORO\nERGUEI VOSSAS CABEÇAS E CONTEMPLAI\nOS ANJOS LOUVANDO A DEUS, O PAI;\nO DIA DA VITÓRIA BREVE VAI CHEGAR,\nREGOZIJAI-VOS Ó ESCOLHIDOS DO SENHOR.\n\nANTES MESMO QUE NASCESSEIS VOS ESCOLHI \nE VOS CONSAGREI, PARA ANUNCIARDES \nAS MINHAS PALAVRAS PORQUE VOLTAREI \nE SOMENTE OS FIÉIS LEVAREI.\n\nCORO\nERGUEI VOSSAS CABEÇAS E CONTEMPLAI\nOS ANJOS LOUVANDO A DEUS, O PAI;\nO DIA DA VITÓRIA BREVE VAI CHEGAR,\nREGOZIJAI-VOS Ó ESCOLHIDOS DO SENHOR.\n\nFINAL: \nREGOZIJAI-VOS, \nÓ ESCOLHIDOS DO SENHOR.', 'NÃO SE TURBEM OS VOSSOS CORAÇÕES, BREVE SE CUMPRIRÁ A PROMESSA DE JESUS; TODO PRANTO CESSARÁ E OS SANTOS SUBIRÃO LEVADOS PELOS ANJOS AO SENHOR. CORO ERGUEI VOSSAS CABEÇAS E CONTEMPLAI OS ANJOS LOUVANDO A DEUS, O PAI; O DIA DA VITÓRIA BREVE VAI CHEGAR, REGOZIJAI-VOS Ó ESCOLHIDOS DO SENHOR. ANTES MESMO QUE NASCESSEIS VOS ESCOLHI E VOS CONSAGREI, PARA ANUNCIARDES AS MINHAS PALAVRAS PORQUE VOLTAREI E SOMENTE OS FIÉIS LEVAREI. CORO ERGUEI VOSSAS CABEÇAS E CONTEMPLAI OS ANJOS LOUVANDO A DEUS, O PAI; O DIA DA VITÓRIA BREVE VAI CHEGAR, REGOZIJAI-VOS Ó ESCOLHIDOS DO SENHOR.  REGOZIJAI-VOS, Ó ESCOLHIDOS DO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('564', '564 - DISPÕE-TE, RESPLANDECE', 'DISPÕE-TE, RESPLANDECE, \nPORQUE VEM A TUA LUZ E A GLÓRIA \nDO SENHOR JESUS NASCE SOBRE TI.\n\nPORQUE EIS QUE AS TREVAS COBREM A TERRA \nE A ESCURIDÃO OS POVOS MAS SOBRE TI APARECE RESPLENDENTE O SENHOR JESUS E A SUA GLÓRIA \nSE VÊ SOBRE TI, NOVA JERUSALÉM.\n\nAS NAÇÕES SE ENCAMINHAM PARA A TUA LUZ, \nE OS REIS PARA O RESPLENDOR\nQUE TE NASCEU.\n\nLEVANTA EM REDOR OS TEUS OLHOS E VÊ, TODOS ESTES SE AJUNTAM E VÊM TER CONTIGO, TEUS FILHOS CHEGAM DE LONGE E TUAS FILHAS SÃO TRAZIDAS NOS BRAÇOS, NOVA JERUSALÉM.\n\nENTÃO O VERÁS E SERÁS\nRADIANTE DE ALEGRIA,\nO TEU CORAÇÃO ESTREMECERÁ\nE SE DILATARÁ DE JÚBILO.\n\nPORQUE A ABUNDÂNCIA DO MAR\nSE TORNARÁ A TI\nE AS RIQUEZAS DAS NAÇÕES\nVIRÃO A TER CONTIGO,\nNOVA JERUSALÉM, NOVA JERUSALÉM.', 'DISPÕE-TE, RESPLANDECE, PORQUE VEM A TUA LUZ E A GLÓRIA DO SENHOR JESUS NASCE SOBRE TI. PORQUE EIS QUE AS TREVAS COBREM A TERRA E A ESCURIDÃO OS POVOS MAS SOBRE TI APARECE RESPLENDENTE O SENHOR JESUS E A SUA GLÓRIA SE VÊ SOBRE TI, NOVA JERUSALÉM. AS NAÇÕES SE ENCAMINHAM PARA A TUA LUZ, E OS REIS PARA O RESPLENDOR QUE TE NASCEU. LEVANTA EM REDOR OS TEUS OLHOS E VÊ, TODOS ESTES SE AJUNTAM E VÊM TER CONTIGO, TEUS FILHOS CHEGAM DE LONGE E TUAS FILHAS SÃO TRAZIDAS NOS BRAÇOS, NOVA JERUSALÉM. ENTÃO O VERÁS E SERÁS RADIANTE DE ALEGRIA, O TEU CORAÇÃO ESTREMECERÁ E SE DILATARÁ DE JÚBILO. PORQUE A ABUNDÂNCIA DO MAR SE TORNARÁ A TI E AS RIQUEZAS DAS NAÇÕES VIRÃO A TER CONTIGO, NOVA JERUSALÉM, NOVA JERUSALÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('565', '565 - LÁ ESTÁ O MEU TESOURO', 'LÁ ESTÁ O MEU TESOURO,\nLÁ ONDE NÃO HÁ CHORO,\nONDE TODOS CANTAREMOS JUNTOS,\nHINOS DE LOUVOR.\n\nALELUIA, ALELUIA, ALELUIA, ALELUIA,\nHINOS DE LOUVOR AO SENHOR.\n\nINSTRUMENTOS\n\nALELUIA, ALELUIA, ALELUIA, ALELUIA,\nHINOS DE LOUVOR AO SENHOR.', 'LÁ ESTÁ O MEU TESOURO, LÁ ONDE NÃO HÁ CHORO, ONDE TODOS CANTAREMOS JUNTOS, HINOS DE LOUVOR. ALELUIA, ALELUIA, ALELUIA, ALELUIA, HINOS DE LOUVOR AO SENHOR. INSTRUMENTOS ALELUIA, ALELUIA, ALELUIA, ALELUIA, HINOS DE LOUVOR AO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('566', '566 - O SENHOR É O MEU PASTOR  (MARANATA, JESUS VEM)', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ,\nJESUS CRISTO É O MEU SENHOR,\nNA SUA GLÓRIA VOU ESTAR.\nLOUVORES AO SEU NOME,\nE ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ),\nNA SUA GLÓRIA VOU ESTAR.\n\nO SENHOR É O MEU PASTOR, NADA ME FALTARÁ,\nJESUS CRISTO É O MEU SENHOR,\nNA SUA GLÓRIA VOU ESTAR.\nLOUVORES AO SEU NOME,\nE ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ),\nTODA A IGREJA CANTARÁ.\n\nCORO \nMARANATA, JESUS VEM!\nMARANATA, VEM BUSCAR!\nMARANATA, TODOS SEUS,\nCOM O SENHOR VÃO MORAR.\n\nINSTRUMENTOS\n\nDE LUZ (DE LUZ).\n\nO SENHOR É O MEU PASTOR, NADA ME FALTARÁ,\nJESUS CRISTO É O MEU SENHOR,\nNA SUA GLÓRIA VOU ESTAR.\nLOUVORES AO SEU NOME,\nE ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ),\nTODA A IGREJA CANTARÁ.\n\nCORO (2X) \nMARANATA, JESUS VEM!\nMARANATA, VEM BUSCAR!\nMARANATA, TODOS SEUS,\nCOM O SENHOR VÃO MORAR.', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, JESUS CRISTO É O MEU SENHOR, NA SUA GLÓRIA VOU ESTAR. LOUVORES AO SEU NOME, E ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ), NA SUA GLÓRIA VOU ESTAR. O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, JESUS CRISTO É O MEU SENHOR, NA SUA GLÓRIA VOU ESTAR. LOUVORES AO SEU NOME, E ME GUIA EM SEUS PASSOS DE LUZ (DE LUZ), TODA A IGREJA CANTARÁ. CORO MARANATA, JESUS VEM! MARANATA, VEM BUSCAR! MARANATA, TODOS SEUS, COM O SENHOR VÃO MORAR. INSTRUMENTOS DE LUZ (DE LUZ). MARANATA, JESUS VEM! MARANATA, VEM BUSCAR! MARANATA, TODOS SEUS, COM O SENHOR VÃO MORAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('567', '567 - QUANDO SERÁ O GRANDE DIA', 'QUANDO SERÁ O GRANDE DIA,\nEM QUE O SENHOR VIRÁ NOS BUSCAR?\nNINGUÉM SABE O DIA, NINGUÉM SABE A HORA,\nMAS A TROMBETA ESTÁ PRA SOAR.\n\nEIS QUE OS SINAIS JÁ SE CUMPRIRAM,\nEM TODA TERRA, RIOS E MARES.\nA SUA IGREJA OUVE A VOZ DO ESPÍRITO:\nMARANATA! JESUS VOLTARÁ! SIM, VOLTARÁ!\n\nCORO\nMARANATA, MARANATA,\nA IGREJA CLAMA, MARANATA!\nO SENHOR VEM! O SENHOR VEM!\n\nINSTRUMENTOS\n\nDIAS DIFÍCEIS, DIAS DE DORES,\nDIAS DE LUTAS, ESTÃO SE PASSANDO.\nA SUA IGREJA OLHA PRO ALTO,\nMARANATA! JESUS VOLTARÁ! SIM, VOLTARÁ!\n\nCORO\nMARANATA, MARANATA,\nA IGREJA CLAMA, MARANATA!\nO SENHOR VEM! O SENHOR VEM!', 'QUANDO SERÁ O GRANDE DIA, EM QUE O SENHOR VIRÁ NOS BUSCAR? NINGUÉM SABE O DIA, NINGUÉM SABE A HORA, MAS A TROMBETA ESTÁ PRA SOAR. EIS QUE OS SINAIS JÁ SE CUMPRIRAM, EM TODA TERRA, RIOS E MARES. A SUA IGREJA OUVE A VOZ DO ESPÍRITO: MARANATA! JESUS VOLTARÁ! SIM, VOLTARÁ! CORO MARANATA, MARANATA, A IGREJA CLAMA, MARANATA! O SENHOR VEM! O SENHOR VEM! INSTRUMENTOS DIAS DIFÍCEIS, DIAS DE DORES, DIAS DE LUTAS, ESTÃO SE PASSANDO. A SUA IGREJA OLHA PRO ALTO, MARANATA! JESUS VOLTARÁ! SIM, VOLTARÁ! CORO MARANATA, MARANATA, A IGREJA CLAMA, MARANATA! O SENHOR VEM! O SENHOR VEM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('568', '568 - SENHOR, QUEM SÃO AQUELES', 'SENHOR, QUEM SÃO AQUELES,\nQUE VEM SUBINDO PELO CAMINHO?\nCANTANDO ALEGREMENTE COMO AS AVES\nQUE VÃO PARA O NINHO.\n\nCORO\nSENHOR, QUEM SÃO AQUELES,\nQUE EM VESTES BRANCAS VÃO PROSSEGUINDO?\nSENHOR, EU QUERO SEGUI-LOS,\nTU NÃO ME DEIXES FICAR SOZINHO.\n\nO MUNDO JÁ NÃO ME SERVE,\nA MINHA ALMA ESTÁ SOLUÇANDO.\nSENHOR, TU NÃO ME DEIXES,\nFICAR NO MUNDO SEMPRE SOFRENDO.\n\nCORO\nSENHOR, QUEM SÃO AQUELES,\nQUE EM VESTES BRANCAS VÃO PROSSEGUINDO?\nSENHOR, EU QUERO SEGUI-LOS,\nTU NÃO ME DEIXES FICAR SOZINHO.\n\nEU QUERO TAMBÉM SER UM DELES,\nTER FELICIDADE NO CORAÇÃO.\nSEGUIR AQUELE CAMINHO,\nSEM SOFRIMENTO E DE SALVAÇÃO.\n\nCORO\nSENHOR, QUEM SÃO AQUELES,\nQUE EM VESTES BRANCAS VÃO PROSSEGUINDO?\nSENHOR, EU QUERO SEGUI-LOS,\nTU NÃO ME DEIXES FICAR SOZINHO.\n\nFINAL: (2X)\nTU NÃO ME DEIXES FICAR SOZINHO.', 'SENHOR, QUEM SÃO AQUELES, QUE VEM SUBINDO PELO CAMINHO? CANTANDO ALEGREMENTE COMO AS AVES QUE VÃO PARA O NINHO. CORO SENHOR, QUEM SÃO AQUELES, QUE EM VESTES BRANCAS VÃO PROSSEGUINDO? SENHOR, EU QUERO SEGUI-LOS, TU NÃO ME DEIXES FICAR SOZINHO. O MUNDO JÁ NÃO ME SERVE, A MINHA ALMA ESTÁ SOLUÇANDO. SENHOR, TU NÃO ME DEIXES, FICAR NO MUNDO SEMPRE SOFRENDO. EU QUERO TAMBÉM SER UM DELES, TER FELICIDADE NO CORAÇÃO. SEGUIR AQUELE CAMINHO, SEM SOFRIMENTO E DE SALVAÇÃO. CORO SENHOR, QUEM SÃO AQUELES, QUE EM VESTES BRANCAS VÃO PROSSEGUINDO? SENHOR, EU QUERO SEGUI-LOS, TU NÃO ME DEIXES FICAR SOZINHO.   TU NÃO ME DEIXES FICAR SOZINHO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('569', '569 - JESUS VEM, VEM NOS BUSCAR', 'CORO\nJESUS VEM! VEM NOS BUSCAR!\nLEVAR-NOS PARA A ETERNIDADE.\nJESUS VEM! VEM NOS BUSCAR!\nCOM MAJESTADE E PODER.\n\nO SEU PODER É DIVINO,\nA SUA GRAÇA É SEM PAR;\nTODOS OS SEUS FIÉIS HÃO DE SUBIR,\nVAMOS PRA SEMPRE MORAR,\nNA ETERNIDADE, \nJUNTOS COM O SALVADOR.\n\nCORO\nJESUS VEM! VEM NOS BUSCAR!\nLEVAR-NOS PARA A ETERNIDADE.\nJESUS VEM! VEM NOS BUSCAR!\nCOM MAJESTADE E PODER.\n\nOS SINAIS ESTÃO SE CUMPRINDO,\nA SUA PALAVRA É FIEL;\nPREPARA-TE IGREJA,\nCINJA OS TEUS LOMBOS!\nMANTENHA ACESA A CANDEIA!\nPARA O ENCONTRO COM O NOIVO,\nNAS BODAS DO CORDEIRO.\n\nCORO\nJESUS VEM! VEM NOS BUSCAR!\nLEVAR-NOS PARA A ETERNIDADE.\nJESUS VEM! VEM NOS BUSCAR!\nCOM MAJESTADE E PODER.\n\nFINAL: \nJESUS VEM! DA SUA GLÓRIA!\nJESUS VEM! COM PODER E GLÓRIA!\nCOM MAJESTADE E PODER;\nVEM, VEM, VEM, JESUS!', 'CORO JESUS VEM! VEM NOS BUSCAR! LEVAR-NOS PARA A ETERNIDADE. JESUS VEM! VEM NOS BUSCAR! COM MAJESTADE E PODER. O SEU PODER É DIVINO, A SUA GRAÇA É SEM PAR; TODOS OS SEUS FIÉIS HÃO DE SUBIR, VAMOS PRA SEMPRE MORAR, NA ETERNIDADE, JUNTOS COM O SALVADOR. OS SINAIS ESTÃO SE CUMPRINDO, A SUA PALAVRA É FIEL; PREPARA-TE IGREJA, CINJA OS TEUS LOMBOS! MANTENHA ACESA A CANDEIA! PARA O ENCONTRO COM O NOIVO, NAS BODAS DO CORDEIRO. JESUS VEM! DA SUA GLÓRIA! JESUS VEM! COM PODER E GLÓRIA! COM MAJESTADE E PODER; VEM, VEM, VEM, JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('570', '570 - JÁ REFULGE A GLÓRIA ETERNA (VENCENDO VEM JESUS)', 'JÁ REFULGE A GLÓRIA ETERNA DE JESUS,\nO REI DOS REIS.\nBREVE OS REINOS DESTE MUNDO\nSEGUIRÃO AS SUAS LEIS!\nOS SINAIS DA SUA VINDA\nMAIS SE MOSTRAM CADA VEZ.\nVENCENDO VEM JESUS!\n\nCORO\nGLÓRIA, GLÓRIA ALELUIA!\nGLÓRIA, GLÓRIA ALELUIA! \nGLÓRIA, GLÓRIA ALELUIA!  \nVENCENDO VEM JESUS!\n\nO CLARIM QUE CHAMA OS CRENTES\nÀ BATALHA, JÁ SOOU.\nCRISTO, À FRENTE DO SEU POVO,\nMULTIDÕES JÁ CONQUISTOU.\nO INIMIGO EM RETIRADA\nSEU FUROR PATENTEOU.\nVENCENDO VEM JESUS!\n\nCORO\nGLÓRIA, GLÓRIA ALELUIA!\nGLÓRIA, GLÓRIA ALELUIA! \nGLÓRIA, GLÓRIA ALELUIA!  \nVENCENDO VEM JESUS!\n\nEIS QUE EM GLÓRIA REFULGENTE\nSOBRE AS NUVENS DESCERÁ.\nE AS NAÇÕES E OS REIS DA TERRA\nCOM PODER GOVERNARÁ.\nSIM, EM PAZ E SANTIDADE\nTODA A TERRA REGERÁ.\nVENCENDO VEM JESUS!\n\nCORO\nGLÓRIA, GLÓRIA ALELUIA!\nGLÓRIA, GLÓRIA ALELUIA! \nGLÓRIA, GLÓRIA ALELUIA!  \nVENCENDO VEM JESUS!\n\nE, POR FIM ENTRONIZADO\nAS NAÇÕES HÁ DE JULGAR.\nTODOS, GRANDES E PEQUENOS,\nO JUIZ, HÃO DE ENCARAR.\nE OS REMIDOS TRIUNFANTES,\nEM FULGOR HÃO DE CANTAR.\nVENCIDO TEM JESUS!\n\nCORO\nGLÓRIA, GLÓRIA ALELUIA!\nGLÓRIA, GLÓRIA ALELUIA! \nGLÓRIA, GLÓRIA ALELUIA!  \nVENCIDO TEM JESUS!', 'JÁ REFULGE A GLÓRIA ETERNA DE JESUS, O REI DOS REIS. BREVE OS REINOS DESTE MUNDO SEGUIRÃO AS SUAS LEIS! OS SINAIS DA SUA VINDA MAIS SE MOSTRAM CADA VEZ. VENCENDO VEM JESUS! CORO GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! VENCENDO VEM JESUS! O CLARIM QUE CHAMA OS CRENTES À BATALHA, JÁ SOOU. CRISTO, À FRENTE DO SEU POVO, MULTIDÕES JÁ CONQUISTOU. O INIMIGO EM RETIRADA SEU FUROR PATENTEOU. VENCENDO VEM JESUS! EIS QUE EM GLÓRIA REFULGENTE SOBRE AS NUVENS DESCERÁ. E AS NAÇÕES E OS REIS DA TERRA COM PODER GOVERNARÁ. SIM, EM PAZ E SANTIDADE TODA A TERRA REGERÁ. VENCENDO VEM JESUS! E, POR FIM ENTRONIZADO AS NAÇÕES HÁ DE JULGAR. TODOS, GRANDES E PEQUENOS, O JUIZ, HÃO DE ENCARAR. E OS REMIDOS TRIUNFANTES, EM FULGOR HÃO DE CANTAR. VENCIDO TEM JESUS! CORO GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! GLÓRIA, GLÓRIA ALELUIA! VENCIDO TEM JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('571', '571 - O GRANDE DIA ESTÁ CHEGANDO', 'O GRANDE DIA ESTÁ CHEGANDO, FALTA TÃO POUCO\nPARA O ENCONTRO COM MEU REI.\nPOSSO ATÉ IMAGINAR O QUE VOU SENTIR\nQUANDO EU PASSAR PELOS PORTAIS CELESTIAIS\nOUVINDO A MAIS BELA CANÇÃO DE LOUVOR!\nLÁ PODEREI DIZER ASSIM:\n\nCORO\nJERUSALÉM, COMO ÉS LINDA!\nJERUSALÉM, QUANTO TEMPO,\nEU ESPEREI PARA VER A TUA BELEZA!\nJERUSALÉM, NA CIDADE DO MEU REI EU ESTOU!\nPOSSO AGORA MEU MESTRE JESUS ABRAÇAR!\n\nO GRANDE DIA ESTÁ CHEGANDO, FALTA TÃO POUCO\nPARA O ENCONTRO COM MEU REI.\nPOSSO ATÉ IMAGINAR O QUE VOU SENTIR\nQUANDO EU PASSAR PELOS PORTAIS CELESTIAIS\nOUVINDO A MAIS BELA CANÇÃO DE LOUVOR!\nLÁ PODEREI DIZER ASSIM:\n\nCORO (BIS)\nJERUSALÉM, COMO ÉS LINDA!\nJERUSALÉM, QUANTO TEMPO,\nEU ESPEREI PARA VER A TUA BELEZA!\nJERUSALÉM, NA CIDADE DO MEU REI EU ESTOU!\nPOSSO AGORA MEU MESTRE JESUS ABRAÇAR!\n\nFINAL: \nPOSSO AGORA MEU MESTRE JESUS ABRAÇAR!', 'O GRANDE DIA ESTÁ CHEGANDO, FALTA TÃO POUCO PARA O ENCONTRO COM MEU REI. POSSO ATÉ IMAGINAR O QUE VOU SENTIR QUANDO EU PASSAR PELOS PORTAIS CELESTIAIS OUVINDO A MAIS BELA CANÇÃO DE LOUVOR! LÁ PODEREI DIZER ASSIM: CORO JERUSALÉM, COMO ÉS LINDA! JERUSALÉM, QUANTO TEMPO, EU ESPEREI PARA VER A TUA BELEZA! JERUSALÉM, NA CIDADE DO MEU REI EU ESTOU! POSSO AGORA MEU MESTRE JESUS ABRAÇAR! CORO  JERUSALÉM, COMO ÉS LINDA! JERUSALÉM, QUANTO TEMPO, EU ESPEREI PARA VER A TUA BELEZA! JERUSALÉM, NA CIDADE DO MEU REI EU ESTOU! POSSO AGORA MEU MESTRE JESUS ABRAÇAR!  POSSO AGORA MEU MESTRE JESUS ABRAÇAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('572', '572 - CHEGUEMOS AO ALTAR DE DEUS', 'CHEGUEMOS AO ALTAR DE DEUS\nCOM A NOSSA ADORAÇÃO;\nNA SUA MÃO HÁ FORÇA E PODER.\n\nCORO (2X)\nADOREMOS, ADOREMOS E CANTEMOS \nAO AUTOR DA NOSSA SALVAÇÃO. \n\nINSTRUMENTOS\n\nCHEGUEMOS AO ALTAR DE DEUS\nCOM A NOSSA ADORAÇÃO;\nTUA É, SENHOR, A HONRA E A MAJESTADE, \nPORQUE TEU É TUDO QUANTO \nHÁ NOS CÉUS E NA TERRA;\nTEU, SENHOR, É O REINO E TU TE EXALTASTE;\nNA TUA MÃO HÁ FORÇA E PODER. (BIS)\n\nCORO (2X)\nADOREMOS, ADOREMOS E CANTEMOS \nAO AUTOR DA NOSSA SALVAÇÃO. \n\nINSTRUMENTOS\n\nFINAL: \nE CANTEMOS AO AUTOR\nDA NOSSA SALVAÇÃO.', 'CHEGUEMOS AO ALTAR DE DEUS COM A NOSSA ADORAÇÃO; NA SUA MÃO HÁ FORÇA E PODER.  ADOREMOS, ADOREMOS E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO. INSTRUMENTOS CHEGUEMOS AO ALTAR DE DEUS COM A NOSSA ADORAÇÃO; TUA É, SENHOR, A HONRA E A MAJESTADE, PORQUE TEU É TUDO QUANTO HÁ NOS CÉUS E NA TERRA; TEU, SENHOR, É O REINO E TU TE EXALTASTE; NA TUA MÃO HÁ FORÇA E PODER. ADOREMOS, ADOREMOS E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO. INSTRUMENTOS  E CANTEMOS AO AUTOR DA NOSSA SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('573', '573 - COMO É BOM TE ADORAR', 'COMO É BOM TE ADORAR SENHOR JESUS\nCOMO É BOM ESTAR NA TUA PRESENÇA,\nCANTAR LOUVORES E BENDIZER\nDE TODO O MEU CORAÇÃO.\n\nCORO\nOH! SENHOR, \n(OH! SENHOR) (VARÕES)\nGRANDIOSO É O TEU NOME;\nOH! SENHOR, (VARÕES)\n(OH! SENHOR) (SERVAS)\nTEU ESPÍRITO SANTO \nNOS TEM CONSOLADO.\n\nTENS ABENÇOADO O TEU POVO,\nTENS ABENÇOADO, Ó DEUS,\nTENS ALEGRADO OS CORAÇÕES,\nTENS NOS DADO A LIBERTAÇÃO.\n\nCORO\nOH! SENHOR, \n(OH! SENHOR) (VARÕES)\nGRANDIOSO É O TEU NOME;\nOH! SENHOR, (VARÕES)\n(OH! SENHOR) (SERVAS)\nTEU ESPÍRITO SANTO \nNOS TEM CONSOLADO.\n\nFINAL: (2X)\nLOUVAREI AO SENHOR, (LOUVAREI)\nCOM GRANDE ALEGRIA, (LOUVAREI) \nLOUVAREI AO SENHOR,\nLOUVAREI AO SENHOR JESUS.\nLOUVAREI AO SENHOR JESUS.', 'COMO É BOM TE ADORAR SENHOR JESUS COMO É BOM ESTAR NA TUA PRESENÇA, CANTAR LOUVORES E BENDIZER DE TODO O MEU CORAÇÃO. CORO OH! SENHOR, (OH! SENHOR)  GRANDIOSO É O TEU NOME; OH! SENHOR,  (OH! SENHOR)  TEU ESPÍRITO SANTO NOS TEM CONSOLADO. TENS ABENÇOADO O TEU POVO, TENS ABENÇOADO, Ó DEUS, TENS ALEGRADO OS CORAÇÕES, TENS NOS DADO A LIBERTAÇÃO. LOUVAREI AO SENHOR, (LOUVAREI) COM GRANDE ALEGRIA, (LOUVAREI) LOUVAREI AO SENHOR, LOUVAREI AO SENHOR JESUS. LOUVAREI AO SENHOR JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('574', '574 - ADORAREI', 'ADORAREI O GRANDE EU SOU,\nDEUS SEM PRINCÍPIO, DEUS SEM FIM\nEXALTAREI O REI QUE VIVE EM MIM.\n\nA DEUS DE PRATA E OURO\nJAMAIS EU ME ENCURVAREI,\nSÓ TU ÉS MEU TESOURO,\nA TI SOMENTE SERVIREI,\nACEITA O MEU SACRIFÍCIO DE LOUVOR.\nBIS\nBIS', 'ADORAREI O GRANDE EU SOU, DEUS SEM PRINCÍPIO, DEUS SEM FIM EXALTAREI O REI QUE VIVE EM MIM. A DEUS DE PRATA E OURO JAMAIS EU ME ENCURVAREI, SÓ TU ÉS MEU TESOURO, A TI SOMENTE SERVIREI, ACEITA O MEU SACRIFÍCIO DE LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('575', '575 - TE ADORAMOS', 'TE ADORAMOS, Ó SENHOR,\nPELAS GRANDES MARAVILHAS\nQUE TENS FEITO;\nNOSSA VIDA TENS GUARDADO,\nCADA DIA NOS REVELAS TEU AMOR.\n\nCORO\nÉS O DEUS EM QUEM PODEMOS CONFIAR,\nDEUS FIEL E FORTE QUE NÃO VAI MUDAR;\nCOM O NOSSO CORAÇÃO CHEIO DE AMOR\nTE OFERTAMOS NOSSA ADORAÇÃO, \nNOSSO LOUVOR.\n\nINSTRUMENTOS\n\nÉS REFÚGIO BEM SEGURO \nNOSSO ABRIGO CONTRA \nA TEMPESTADE E O VENTO;\nCOMO SOMBRA DE UMA ROCHA \nE CORRENTES DE ÁGUA \nEM LUGAR SEDENTO.\n\nCORO\nÉS O DEUS EM QUEM PODEMOS CONFIAR,\nDEUS FIEL E FORTE QUE NÃO VAI MUDAR;\nCOM O NOSSO CORAÇÃO CHEIO DE AMOR\nTE OFERTAMOS NOSSA ADORAÇÃO, \nNOSSO LOUVOR.\n\nFINAL:  \nNOSSA ADORAÇÃO, NOSSO LOUVOR', 'TE ADORAMOS, Ó SENHOR, PELAS GRANDES MARAVILHAS QUE TENS FEITO; NOSSA VIDA TENS GUARDADO, CADA DIA NOS REVELAS TEU AMOR. CORO ÉS O DEUS EM QUEM PODEMOS CONFIAR, DEUS FIEL E FORTE QUE NÃO VAI MUDAR; COM O NOSSO CORAÇÃO CHEIO DE AMOR TE OFERTAMOS NOSSA ADORAÇÃO, NOSSO LOUVOR. INSTRUMENTOS ÉS REFÚGIO BEM SEGURO NOSSO ABRIGO CONTRA A TEMPESTADE E O VENTO; COMO SOMBRA DE UMA ROCHA E CORRENTES DE ÁGUA EM LUGAR SEDENTO. CORO ÉS O DEUS EM QUEM PODEMOS CONFIAR, DEUS FIEL E FORTE QUE NÃO VAI MUDAR; COM O NOSSO CORAÇÃO CHEIO DE AMOR TE OFERTAMOS NOSSA ADORAÇÃO, NOSSO LOUVOR.  NOSSA ADORAÇÃO, NOSSO LOUVOR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('576', '576 - REUNIMO-NOS AQUI PRA TE ADORAR', 'REUNIMO-NOS AQUI PRA TE ADORAR, SENHOR,\nPARA TE OFERTAR O NOSSO CORAÇÃO,\nTODO O NOSSO SER SE RENDE A TI;\nNOSSA ALMA EXALTA O TEU AMOR\nEM PERFEITA E PLENA COMUNHÃO.\n\nHONRA AO PAI DE LUZ, \nAO SENHOR JESUS E AO ESPÍRITO SANTO; \nTODA GLÓRIA, PODER, VITÓRIA, \nHOJE E ETERNAMENTE.\n\nINSTRUMENTOS\n\nREUNIMO-NOS AQUI PRA TE ADORAR, \nSENHOR, PARA TE OFERTAR \nO NOSSO CORAÇÃO, \nTODO O NOSSO SER SE RENDE A TI;\nNOSSA ALMA EXALTA O TEU AMOR \nEM PERFEITA E PLENA COMUNHÃO.\n\nHONRA AO PAI DE LUZ,\nAO SENHOR JESUS\nE AO ESPÍRITO SANTO;\nTODA GLÓRIA, PODER, VITÓRIA,\nHOJE E ETERNAMENTE.', 'REUNIMO-NOS AQUI PRA TE ADORAR, SENHOR, PARA TE OFERTAR O NOSSO CORAÇÃO, TODO O NOSSO SER SE RENDE A TI; NOSSA ALMA EXALTA O TEU AMOR EM PERFEITA E PLENA COMUNHÃO. HONRA AO PAI DE LUZ, AO SENHOR JESUS E AO ESPÍRITO SANTO; TODA GLÓRIA, PODER, VITÓRIA, HOJE E ETERNAMENTE. INSTRUMENTOS HONRA AO PAI DE LUZ, AO SENHOR JESUS E AO ESPÍRITO SANTO; TODA GLÓRIA, PODER, VITÓRIA, HOJE E ETERNAMENTE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('577', '577 - GLÓRIA E ALELUIA', 'GLÓRIA E ALELUIA NÓS TE DAMOS SENHOR\nBENDITO SEJA O TEU SANTO NOME (VARÕES)\nGRAÇAS E LOUVORES TE RENDEMOS SENHOR\nEXALTADA SEJA A TUA SALVAÇÃO. (VARÕES)\n\nPOIS O SENHOR É MINHA ROCHA \nE MINHA FORÇA E O MEU ALTO REFÚGIO;\nA ELE CLAMAREI E ELE ME SALVARÁ,\nÉ ELE QUEM ME TEM DADO A VITÓRIA.\n\nGLÓRIA E ALELUIA NÓS TE DAMOS SENHOR\nBENDITO SEJA O TEU SANTO NOME (VARÕES)\nGRAÇAS E LOUVORES TE RENDEMOS SENHOR\nEXALTADA SEJA A TUA SALVAÇÃO. (VARÕES)\n\nPOIS O SENHOR É MINHA ROCHA \nE MINHA FORÇA E O MEU ALTO REFÚGIO;\nA ELE CLAMAREI E ELE ME SALVARÁ,\nÉ ELE QUEM ME TEM DADO A VITÓRIA.\n\nGLÓRIA E ALELUIA NÓS TE DAMOS SENHOR\nBENDITO SEJA O TEU SANTO NOME (VARÕES)\nGRAÇAS E LOUVORES TE RENDEMOS SENHOR\nEXALTADA SEJA A TUA SALVAÇÃO. (VARÕES)\n\nFINAL: \nEXALTADA SEJA A TUA SALVAÇÃO.', 'GLÓRIA E ALELUIA NÓS TE DAMOS SENHOR BENDITO SEJA O TEU SANTO NOME  GRAÇAS E LOUVORES TE RENDEMOS SENHOR EXALTADA SEJA A TUA SALVAÇÃO. POIS O SENHOR É MINHA ROCHA E MINHA FORÇA E O MEU ALTO REFÚGIO; A ELE CLAMAREI E ELE ME SALVARÁ, É ELE QUEM ME TEM DADO A VITÓRIA. GLÓRIA E ALELUIA NÓS TE DAMOS SENHOR BENDITO SEJA O TEU SANTO NOME  GRAÇAS E LOUVORES TE RENDEMOS SENHOR EXALTADA SEJA A TUA SALVAÇÃO.   EXALTADA SEJA A TUA SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('578', '578 - HOJE O SENHOR DERRAMOU', 'HOJE O SENHOR DERRAMOU \nDO SEU PODER SOBRE NÓS,\nCOMO A CADA MANHÃ E ENTARDECER \nSENTIMOS SEU AMOR;\nPOR ISSO DAMOS GRAÇAS \nPOR TUDO O QUE TEM FEITO A NÓS, \nLOUVORES E HONRAS \nAO GRANDE SALVADOR.\n\nCORO\nMEU SENHOR, A TI LOUVAMOS \nPOR TUA MÃO QUE NOS SUSTÉM, \nE APESAR DAS LUTAS E PROVAS SENHOR \nSABEMOS QUE CONOSCO SEMPRE ESTARÁS.\n\nO QUE TE DAR, MEU SENHOR, \nPOR TANTAS BÊNÇÃOS CELESTIAIS?\nEM SANTIDADE Ó SENHOR TE OFERECEMOS \nCORAÇÕES FIÉIS, PURIFICADOS EM JESUS, \nBUSCANDO A SALVAÇÃO POIS BEM SABEMOS \nQUE O SANGUE DE CRISTO TRAZ PERDÃO.\n\nCORO (2X)\nMEU SENHOR, A TI LOUVAMOS \nPOR TUA MÃO QUE NOS SUSTÉM, \nE APESAR DAS LUTAS E PROVAS SENHOR \nSABEMOS QUE CONOSCO SEMPRE ESTARÁS.\n\nFINAL: \nCONOSCO SEMPRE ESTARÁS.', 'HOJE O SENHOR DERRAMOU DO SEU PODER SOBRE NÓS, COMO A CADA MANHÃ E ENTARDECER SENTIMOS SEU AMOR; POR ISSO DAMOS GRAÇAS POR TUDO O QUE TEM FEITO A NÓS, LOUVORES E HONRAS AO GRANDE SALVADOR. CORO MEU SENHOR, A TI LOUVAMOS POR TUA MÃO QUE NOS SUSTÉM, E APESAR DAS LUTAS E PROVAS SENHOR SABEMOS QUE CONOSCO SEMPRE ESTARÁS. O QUE TE DAR, MEU SENHOR, POR TANTAS BÊNÇÃOS CELESTIAIS? EM SANTIDADE Ó SENHOR TE OFERECEMOS CORAÇÕES FIÉIS, PURIFICADOS EM JESUS, BUSCANDO A SALVAÇÃO POIS BEM SABEMOS QUE O SANGUE DE CRISTO TRAZ PERDÃO. MEU SENHOR, A TI LOUVAMOS POR TUA MÃO QUE NOS SUSTÉM, E APESAR DAS LUTAS E PROVAS SENHOR SABEMOS QUE CONOSCO SEMPRE ESTARÁS.  CONOSCO SEMPRE ESTARÁS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('579', '579 - SENHOR, EU NEM SEI CONTAR', 'SENHOR, EU NEM SEI CONTAR\nAS GRANDES BÊNÇÃOS\nQUE TENS DADO AO MEU CORAÇÃO,\nPOIS O TEU AMOR ME FAZ TÃO FELIZ\nE A TUA GRAÇA ME SATISFAZ\nEM SABER QUE A CADA MANHÃ\nEU POSSO SENTIR JESUS\nTÃO PERTO DE MIM.\nBIS\n\nCORO\nEU QUERO AGRADECER (VARÕES)\nAO MEU SENHOR,\nEU QUERO AGRADECER (VARÕES)\nPOR SEU AMOR,\nQUE ME FAZ SER VENCEDOR E ME FAZ FELIZ\nQUE ME FAZ SER UM SERVO DO SENHOR.\n\nINSTRUMENTOS\n\nTÃO PERTO DE MIM...\n\nCORO\nEU QUERO AGRADECER (VARÕES)\nAO MEU SENHOR,\nEU QUERO AGRADECER (VARÕES)\nPOR SEU AMOR,\nQUE ME FAZ SER VENCEDOR E ME FAZ FELIZ\nQUE ME FAZ SER UM SERVO DO SENHOR.\n\nFINAL:	\nQUE ME FAZ SER UM SERVO DO SENHOR.', 'SENHOR, EU NEM SEI CONTAR AS GRANDES BÊNÇÃOS QUE TENS DADO AO MEU CORAÇÃO, POIS O TEU AMOR ME FAZ TÃO FELIZ E A TUA GRAÇA ME SATISFAZ EM SABER QUE A CADA MANHÃ EU POSSO SENTIR JESUS TÃO PERTO DE MIM. CORO EU QUERO AGRADECER  AO MEU SENHOR, EU QUERO AGRADECER  POR SEU AMOR, QUE ME FAZ SER VENCEDOR E ME FAZ FELIZ QUE ME FAZ SER UM SERVO DO SENHOR. INSTRUMENTOS TÃO PERTO DE MIM... CORO EU QUERO AGRADECER  AO MEU SENHOR, EU QUERO AGRADECER  POR SEU AMOR, QUE ME FAZ SER VENCEDOR E ME FAZ FELIZ QUE ME FAZ SER UM SERVO DO SENHOR.  QUE ME FAZ SER UM SERVO DO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('580', '580 - NOS PROSTEMOS AOS SEUS PÉS', 'NOS PROSTREMOS AOS SEUS PÉS\nADORANDO A JESUS,\nEXALTANDO A SUA GLÓRIA E MAJESTADE.\nBIS\n\nCORO\nCOROAI A JESUS MINISTRANDO \nO SEU LOUVOR EM SUA CASA, \nCOROAI A JESUS\nBENDIZENDO E LOUVANDO O SEU NOME.\n\nINSTRUMENTOS\n\nENTRONIZADO ESTÁS ENTRE OS LOUVORES,\nASSENTADO NO TEU TRONO,\nE TODOS OS ANJOS DE DEUS TE ADORANDO.\nBIS\n\nCORO (2X)\nCOROAI A JESUS MINISTRANDO \nO SEU LOUVOR EM SUA CASA,\nCOROAI A JESUS BENDIZENDO \nE LOUVANDO O SEU NOME.\n\nFINAL: (2X) \nO SEU NOME (VARÕES)\nBENDIZENDO E LOUVANDO O SEU NOME.', 'NOS PROSTREMOS AOS SEUS PÉS ADORANDO A JESUS, EXALTANDO A SUA GLÓRIA E MAJESTADE. CORO COROAI A JESUS MINISTRANDO O SEU LOUVOR EM SUA CASA, COROAI A JESUS BENDIZENDO E LOUVANDO O SEU NOME. INSTRUMENTOS ENTRONIZADO ESTÁS ENTRE OS LOUVORES, ASSENTADO NO TEU TRONO, E TODOS OS ANJOS DE DEUS TE ADORANDO. COROAI A JESUS MINISTRANDO O SEU LOUVOR EM SUA CASA, COROAI A JESUS BENDIZENDO E LOUVANDO O SEU NOME.   O SEU NOME  BENDIZENDO E LOUVANDO O SEU NOME.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('581', '581 - PAI DE AMOR, GOSTO TANTO DE TI', 'PAI DE AMOR, GOSTO TANTO DE TI!\nPAI DE AMOR, GOSTO TANTO DE TI!\nTE AMO, TE QUERO E PROSTRADO TE ADORO!\nPAI DE AMOR, GOSTO TANTO DE TI!\n\nMEU JESUS, AMOROSO TU ÉS! (BIS)\nMINH’ALMA LIMPASTE\nE O ESPÍRITO ENVIASTE!\nMEU JESUS, AMOROSO TU ÉS!\n\nSANTO ESPÍRITO CONSOLADOR! (BIS)\nTU NOS SANTIFICAS\nE EM NÓS SEMPRE HABITAS!\nSANTO ESPÍRITO CONSOLADOR!', 'PAI DE AMOR, GOSTO TANTO DE TI! PAI DE AMOR, GOSTO TANTO DE TI! TE AMO, TE QUERO E PROSTRADO TE ADORO! PAI DE AMOR, GOSTO TANTO DE TI! MEU JESUS, AMOROSO TU ÉS!  MINH’ALMA LIMPASTE E O ESPÍRITO ENVIASTE! MEU JESUS, AMOROSO TU ÉS! SANTO ESPÍRITO CONSOLADOR!  TU NOS SANTIFICAS E EM NÓS SEMPRE HABITAS! SANTO ESPÍRITO CONSOLADOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('582', '582 - CANTAREI AO AMOR DOS AMORES', 'CANTAREI AO AMOR DOS AMORES,\nO AMOR, DOCE AMOR, O AMOR DE DEUS.\nNÃO HÁ MAIS DÚVIDAS EM MIM,\nTODO O MEU MUNDO MUDOU,\nAO AMOR DOS AMORES CANTAREI.\nBIS\n\nO INVERNO PASSOU, SE FOI A CHUVA,\nE COM LÍRIOS A TERRA SE COBRIU.\nÉ TEMPO DE NOVA CANÇÃO,\nA FIGUEIRA REVERDECEU,\nAO AMOR DOS AMORES CANTAREI.\nBIS\n\nMINHA VIDA HOJE É DIFERENTE,\nDE ALEGRIA CANTA E BATE O CORAÇÃO.\nILUMINOU TODO O MEU SER,\nQUERO DE NOVO VIVER,\nAO AMOR DOS AMORES CANTAREI.\nBIS', 'CANTAREI AO AMOR DOS AMORES, O AMOR, DOCE AMOR, O AMOR DE DEUS. NÃO HÁ MAIS DÚVIDAS EM MIM, TODO O MEU MUNDO MUDOU, AO AMOR DOS AMORES CANTAREI. O INVERNO PASSOU, SE FOI A CHUVA, E COM LÍRIOS A TERRA SE COBRIU. É TEMPO DE NOVA CANÇÃO, A FIGUEIRA REVERDECEU, AO AMOR DOS AMORES CANTAREI. MINHA VIDA HOJE É DIFERENTE, DE ALEGRIA CANTA E BATE O CORAÇÃO. ILUMINOU TODO O MEU SER, QUERO DE NOVO VIVER, AO AMOR DOS AMORES CANTAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('583', '583 - Ó GRANDE DEUS', 'Ó GRANDE DEUS, A TUA GRAÇA\nENCHE O MEU CORAÇÃO;\nTEU É O CÉU E NA TERRA TU ÉS SALVAÇÃO\nTEU É O MAR E AS FONTES DAS ÁGUAS TAMBÉM, \nSÓ TU, SENHOR, ÉS A LUZ, ÉS A VIDA TAMBÉM.\n\nCORO\nNÃO SE ENCONTRA ENTRE OS POVOS \nDA TERRA TÃO GRANDE AMOR,\nNEM TAMBÉM NAS ESCARPAS \nDAS SERRAS TÃO RICO AMOR,\nNEM NAS NUVENS QUE PASSAM \nNOS ARES HÁ TANTO AMOR, \nMAS NO TRONO DA GLÓRIA \nSE ENCONTRA ESTE SANTO AMOR.\n\nÓ GRANDE DEUS, ME AJUDA NA TRIBULAÇÃO,\nDÁ-ME PODER PRA PREGAR E FAZER ORAÇÃO,\nDÁ-ME SABER PARA AQUI NESTA VIDA TRILHAR\nE COMPREENDER TEU AMOR, \nTUA GRAÇA SEM PAR.\n\nCORO\nNÃO SE ENCONTRA ENTRE OS POVOS \nDA TERRA TÃO GRANDE AMOR,\nNEM TAMBÉM NAS ESCARPAS \nDAS SERRAS TÃO RICO AMOR,\nNEM NAS NUVENS QUE PASSAM \nNOS ARES HÁ TANTO AMOR, \nMAS NO TRONO DA GLÓRIA \nSE ENCONTRA ESTE SANTO AMOR.', 'Ó GRANDE DEUS, A TUA GRAÇA ENCHE O MEU CORAÇÃO; TEU É O CÉU E NA TERRA TU ÉS SALVAÇÃO TEU É O MAR E AS FONTES DAS ÁGUAS TAMBÉM, SÓ TU, SENHOR, ÉS A LUZ, ÉS A VIDA TAMBÉM. CORO NÃO SE ENCONTRA ENTRE OS POVOS DA TERRA TÃO GRANDE AMOR, NEM TAMBÉM NAS ESCARPAS DAS SERRAS TÃO RICO AMOR, NEM NAS NUVENS QUE PASSAM NOS ARES HÁ TANTO AMOR, MAS NO TRONO DA GLÓRIA SE ENCONTRA ESTE SANTO AMOR. Ó GRANDE DEUS, ME AJUDA NA TRIBULAÇÃO, DÁ-ME PODER PRA PREGAR E FAZER ORAÇÃO, DÁ-ME SABER PARA AQUI NESTA VIDA TRILHAR E COMPREENDER TEU AMOR, TUA GRAÇA SEM PAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('584', '584 - EU TE AMO, MEU JESUS', 'EU TE AMO, MEU JESUS,\nPOIS TU ÉS O REMIDOR,\nRESGATASTE A MINHA ALMA\nDA TRISTEZA E DA DOR,\nEU TE AMO, MEU JESUS,\nCOMO É FORTE O MEU AMOR,\nALEGRASTE A MINHA ALMA\nCOM TUA PAZ E O TEU AMOR.\n\nCORO\nEU TE AMO, MEU JESUS, POIS ABRISTE PARA MIM\nA PORTA DO JARDIM DA VIDA QUE É SEM FIM;\nA MINH’ALMA CANTA AGORA O AMOR \nQUE VIVE EM MIM, POIS MUDASTE O MEU SER,\nNÃO MAIS EU, CRISTO EM MIM.\n\nINSTRUMENTOS\n\nEU TE AMO, MEU JESUS,\nPOIS TU ÉS O MEU PASTOR,\nLEVA-ME COM ALEGRIA\nAO BANQUETE DO AMOR;\nA MINH’ALMA REJUBILA\nPOIS ÉS TUDO PARA MIM,\nSOU HERDEIRO DE UM REINO,\nDE UM REINO QUE É SEM FIM.\n\nCORO\nEU TE AMO, MEU JESUS, POIS ABRISTE PARA MIM\nA PORTA DO JARDIM DA VIDA QUE É SEM FIM;\nA MINH’ALMA CANTA AGORA O AMOR \nQUE VIVE EM MIM, POIS MUDASTE O MEU SER,\nNÃO MAIS EU, CRISTO EM MIM. (3X)', 'EU TE AMO, MEU JESUS, POIS TU ÉS O REMIDOR, RESGATASTE A MINHA ALMA DA TRISTEZA E DA DOR, EU TE AMO, MEU JESUS, COMO É FORTE O MEU AMOR, ALEGRASTE A MINHA ALMA COM TUA PAZ E O TEU AMOR. CORO EU TE AMO, MEU JESUS, POIS ABRISTE PARA MIM A PORTA DO JARDIM DA VIDA QUE É SEM FIM; A MINH’ALMA CANTA AGORA O AMOR QUE VIVE EM MIM, POIS MUDASTE O MEU SER, NÃO MAIS EU, CRISTO EM MIM. INSTRUMENTOS EU TE AMO, MEU JESUS, POIS TU ÉS O MEU PASTOR, LEVA-ME COM ALEGRIA AO BANQUETE DO AMOR; A MINH’ALMA REJUBILA POIS ÉS TUDO PARA MIM, SOU HERDEIRO DE UM REINO, DE UM REINO QUE É SEM FIM. CORO EU TE AMO, MEU JESUS, POIS ABRISTE PARA MIM A PORTA DO JARDIM DA VIDA QUE É SEM FIM; A MINH’ALMA CANTA AGORA O AMOR QUE VIVE EM MIM, POIS MUDASTE O MEU SER, NÃO MAIS EU, CRISTO EM MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('585', '585 - FONTE DA VIDA', 'FONTE DA VIDA, ESTRELA DA MANHÃ,\nJESUS, O REI DOS REIS,\nSENHOR, EMANUEL!\nCORDEIRO DE DEUS, O LEÃO DE JUDÁ,\nCRISTO JESUS.\n\nTE ADORAMOS POIS TU ESTÁS AQUI,\nOS NOSSOS CORAÇÕES ESTÃO CHEIOS DE LOUVOR;\nTU ÉS TUDO PRA NÓS, TU ÉS O SENHOR,\nCRISTO JESUS. CRISTO JESUS.\nBIS', 'FONTE DA VIDA, ESTRELA DA MANHÃ, JESUS, O REI DOS REIS, SENHOR, EMANUEL! CORDEIRO DE DEUS, O LEÃO DE JUDÁ, CRISTO JESUS. TE ADORAMOS POIS TU ESTÁS AQUI, OS NOSSOS CORAÇÕES ESTÃO CHEIOS DE LOUVOR; TU ÉS TUDO PRA NÓS, TU ÉS O SENHOR, CRISTO JESUS. CRISTO JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('586', '586 - Ó PROFUNDIDADE DAS RIQUEZAS', 'Ó PROFUNDIDADE DAS RIQUEZAS, \nTANTO DA SABEDORIA \nCOMO DO CONHECIMENTO DE DEUS, \nQUÃO INSONDÁVEIS SÃO SEUS JUÍZOS, \nQUÃO INESCRUTÁVEIS OS SEUS CAMINHOS!\n\nINSTRUMENTOS\n\nAH! POR QUE QUEM COMPREENDEU\nOS INTENTOS DO SENHOR?\nOU QUEM FOI SEU CONSELHEIRO?\nOU QUEM LHE DEU PRIMEIRO A ELE?\nPRA QUE LHE SEJA RECOMPENSADO.\n\nPORQUE DELE E POR ELE \nE PARA ELE SÃO TODAS AS COISAS;\nGLÓRIA, GLÓRIA, GLÓRIA POIS \nA ELE ETERNAMENTE, AMÉM.\n\nFINAL: AMÉM!\nBIS', 'Ó PROFUNDIDADE DAS RIQUEZAS, TANTO DA SABEDORIA COMO DO CONHECIMENTO DE DEUS, QUÃO INSONDÁVEIS SÃO SEUS JUÍZOS, QUÃO INESCRUTÁVEIS OS SEUS CAMINHOS! INSTRUMENTOS AH! POR QUE QUEM COMPREENDEU OS INTENTOS DO SENHOR? OU QUEM FOI SEU CONSELHEIRO? OU QUEM LHE DEU PRIMEIRO A ELE? PRA QUE LHE SEJA RECOMPENSADO. PORQUE DELE E POR ELE E PARA ELE SÃO TODAS AS COISAS; GLÓRIA, GLÓRIA, GLÓRIA POIS A ELE ETERNAMENTE, AMÉM.  AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('587', '587 - Ó JESUS, EXALTAR-TE-EI', 'Ó JESUS, EXALTAR-TE-EI!\nÓ JESUS, EXALTAR-TE-EI!\n\nA MINH’ALMA TE ENALTECE,\nA MINH’ALMA E TODO O MEU SER\nTE GLORIFICAM.\n\nÓ JESUS, EXALTAR-TE-EI!\nÓ JESUS, EXALTAR-TE-EI!\n\nA MINH’ALMA TE ENALTECE,\nA MINH’ALMA E TODO O MEU \nSER TE GLORIFICAM.\nBIS', 'Ó JESUS, EXALTAR-TE-EI! Ó JESUS, EXALTAR-TE-EI! A MINH’ALMA TE ENALTECE, A MINH’ALMA E TODO O MEU SER TE GLORIFICAM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('588', '588 - HÁ UM NOME QUE RELUZ', 'HÁ UM NOME QUE RELUZ\nMAIS QUE MIL SÓIS EM SEU FULGOR,\nSERES CELESTIAIS SE INCLINAM \nDIANTE DELE COM TEMOR;\nNEM MIL ANOS BASTARIAM\nEM MIL CANTOS DE LOUVOR\nPARA CELEBRAR A GLÓRIA DE JESUS!\n\nFOI NA CRUZ QUE POR SEU SANGUE\nPRINCIPADOS DESPOJOU, \nFOI NA CRUZ QUE A MALDIÇÃO DA LEI \nPOR NÓS ALI CRAVOU;\nAO TERCEIRO DIA A MORTE \nPARA SEMPRE CONQUISTOU, \nOH! QUE GLÓRIA \nTEM O NOME DE JESUS!\n\nCORO\nNOME SANTO DE JESUS, \nO MISTÉRIO REVELADO LÁ NA CRUZ, \nO QUE PRÍNCIPES E SÁBIOS \nNÃO PUDERAM ENTENDER\nTEMOS PELA FÉ NO NOME DE JESUS!\n\nAO SEU NOME OBEDECE TUDO \nO QUE HÁ NA CRIAÇÃO, \nA UMA SÓ PALAVRA SUA\nCESSA TODA A OPOSIÇÃO;\nAQUI HOJE PROCLAMAMOS \nA ETERNA SALVAÇÃO, \nHAJA PAZ E CURA EM NOME DE JESUS!\n\nCORO\nNOME SANTO DE JESUS, \nO MISTÉRIO REVELADO LÁ NA CRUZ, \nO QUE PRÍNCIPES E SÁBIOS \nNÃO PUDERAM ENTENDER\nTEMOS PELA FÉ NO NOME DE JESUS!', 'HÁ UM NOME QUE RELUZ MAIS QUE MIL SÓIS EM SEU FULGOR, SERES CELESTIAIS SE INCLINAM DIANTE DELE COM TEMOR; NEM MIL ANOS BASTARIAM EM MIL CANTOS DE LOUVOR PARA CELEBRAR A GLÓRIA DE JESUS! FOI NA CRUZ QUE POR SEU SANGUE PRINCIPADOS DESPOJOU, FOI NA CRUZ QUE A MALDIÇÃO DA LEI POR NÓS ALI CRAVOU; AO TERCEIRO DIA A MORTE PARA SEMPRE CONQUISTOU, OH! QUE GLÓRIA TEM O NOME DE JESUS! CORO NOME SANTO DE JESUS, O MISTÉRIO REVELADO LÁ NA CRUZ, O QUE PRÍNCIPES E SÁBIOS NÃO PUDERAM ENTENDER TEMOS PELA FÉ NO NOME DE JESUS! AO SEU NOME OBEDECE TUDO O QUE HÁ NA CRIAÇÃO, A UMA SÓ PALAVRA SUA CESSA TODA A OPOSIÇÃO; AQUI HOJE PROCLAMAMOS A ETERNA SALVAÇÃO, HAJA PAZ E CURA EM NOME DE JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('589', '589 - JESUS TÃO BELO', 'JESUS TÃO BELO, Ó REI DA TERRA,\nNASCESTE IGUAL A DEUS SENHOR,\nÉS DE MINH’ALMA, SOL, ALEGRIA,\nCOROA, GLÓRIA, LUZ, AMOR.\n\nDOS CÉUS A GLÓRIA, A MAJESTADE,\nEM TI, SENHOR, EU POSSO VER,\nDE MIM MAIS PERTO OU MAIS QUERIDO,\nÓ MEU JESUS, NINGUÉM VOU TER.\n\nJESUS AMADO, Ó REI GLORIOSO,\nAO PECADOR SÓ QUERES BEM,\nPOIS SEJA SEMPRE, \nLOUVOR E GLÓRIA A TI, \nÓ SALVADOR, AMÉM.', 'JESUS TÃO BELO, Ó REI DA TERRA, NASCESTE IGUAL A DEUS SENHOR, ÉS DE MINH’ALMA, SOL, ALEGRIA, COROA, GLÓRIA, LUZ, AMOR. DOS CÉUS A GLÓRIA, A MAJESTADE, EM TI, SENHOR, EU POSSO VER, DE MIM MAIS PERTO OU MAIS QUERIDO, Ó MEU JESUS, NINGUÉM VOU TER. JESUS AMADO, Ó REI GLORIOSO, AO PECADOR SÓ QUERES BEM, POIS SEJA SEMPRE, LOUVOR E GLÓRIA A TI, Ó SALVADOR, AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('590', '590 - AO CORDEIRO', 'AO CORDEIRO QUE FOI MORTO\nE DA MORTE É VENCEDOR,\nAO QUE É SANTO, SANTO, SANTO,\nTRIUNFANTE REDENTOR,\nTODA HONRA, TODA GLÓRIA,\nPARA TODO O SEMPRE DAI,\nHOMENS E ANJOS, ADORAI O REI!\nADORAI!\n\nINSTRUMENTOS\n\nAO CORDEIRO QUE FOI MORTO\nE DA MORTE É VENCEDOR,\nAO QUE É SANTO, SANTO, SANTO,\nTRIUNFANTE REDENTOR,\nTODA HONRA, TODA GLÓRIA,\nPARA TODO O SEMPRE DAI,\nHOMENS E ANJOS, ADORAI O REI!\nADORAI!', 'AO CORDEIRO QUE FOI MORTO E DA MORTE É VENCEDOR, AO QUE É SANTO, SANTO, SANTO, TRIUNFANTE REDENTOR, TODA HONRA, TODA GLÓRIA, PARA TODO O SEMPRE DAI, HOMENS E ANJOS, ADORAI O REI! ADORAI! INSTRUMENTOS AO CORDEIRO QUE FOI MORTO E DA MORTE É VENCEDOR, AO QUE É SANTO, SANTO, SANTO, TRIUNFANTE REDENTOR, TODA HONRA, TODA GLÓRIA, PARA TODO O SEMPRE DAI, HOMENS E ANJOS, ADORAI O REI! ADORAI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('591', '591 - GLÓRIA PRA SEMPRE AO CORDEIRO DE DEUS', 'GLÓRIA PRA SEMPRE \nAO CORDEIRO DE DEUS, \nA JESUS, O SENHOR, \nAO LEÃO DE JUDÁ,\nÀ RAIZ DE DAVI QUE VENCEU\nE O LIVRO ABRIRÁ.\n\nO CÉU, A TERRA E O MAR,\nE TUDO O QUE NELES HÁ,\nO ADORARÃO E CONFESSARÃO:\nJESUS CRISTO É O SENHOR.\n\nELE É O SENHOR, ELE É O SENHOR;\nRESSURRETO DENTRE OS MORTOS,\nELE É O SENHOR;\nTODO JOELHO SE DOBRARÁ,\nTODA LÍNGUA CONFESSARÁ\nQUE JESUS CRISTO É O SENHOR.\n\nGLÓRIA PRA SEMPRE AO CORDEIRO \nDE DEUS, A JESUS, O SENHOR, \nAO LEÃO DE JUDÁ,\nÀ RAIZ DE DAVI QUE VENCEU\nE O LIVRO ABRIRÁ.\n\nO CÉU, A TERRA E O MAR,\nE TUDO O QUE NELES HÁ,\nO ADORARÃO E CONFESSARÃO:\nJESUS CRISTO É O SENHOR.', 'GLÓRIA PRA SEMPRE AO CORDEIRO DE DEUS, A JESUS, O SENHOR, AO LEÃO DE JUDÁ, À RAIZ DE DAVI QUE VENCEU E O LIVRO ABRIRÁ. O CÉU, A TERRA E O MAR, E TUDO O QUE NELES HÁ, O ADORARÃO E CONFESSARÃO: JESUS CRISTO É O SENHOR. ELE É O SENHOR, ELE É O SENHOR; RESSURRETO DENTRE OS MORTOS, ELE É O SENHOR; TODO JOELHO SE DOBRARÁ, TODA LÍNGUA CONFESSARÁ QUE JESUS CRISTO É O SENHOR. GLÓRIA PRA SEMPRE AO CORDEIRO DE DEUS, A JESUS, O SENHOR, AO LEÃO DE JUDÁ, À RAIZ DE DAVI QUE VENCEU E O LIVRO ABRIRÁ. O CÉU, A TERRA E O MAR, E TUDO O QUE NELES HÁ, O ADORARÃO E CONFESSARÃO: JESUS CRISTO É O SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('592', '592 - ALELUIA! MUITAS VOZES DE ANJOS', 'ALELUIA!\nMUITAS VOZES DE ANJOS EXALTAM \nO NOME DO CORDEIRO VIVO, JESUS;\nAQUELE QUE UM DIA NA AMARGA \nCRUZ MORREU COMO PROVA DE AMOR.\n\nCORO\nE SÓ ELE É DIGNO DE TODO O LOUVOR!\nGLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA,\nGLÓRIA AO REDENTOR!\nGLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA,\nGLÓRIA AO SALVADOR!\n\nINSTRUMENTOS\n\nALELUIA! NÓS TAMBÉM ESTAREMOS \nNAS LINDAS MANSÕES \nLOUVANDO PRA SEMPRE AO SENHOR;\nE COM GRANDE ALEGRIA VEREMOS JESUS, \nQUE DIA TÃO LINDO SERÁ.\n\nCORO\nE SÓ ELE É DIGNO DE TODO O LOUVOR!\nGLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA,\nGLÓRIA AO REDENTOR!\nGLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA,\nGLÓRIA AO SALVADOR! (2X)', 'ALELUIA! MUITAS VOZES DE ANJOS EXALTAM O NOME DO CORDEIRO VIVO, JESUS; AQUELE QUE UM DIA NA AMARGA CRUZ MORREU COMO PROVA DE AMOR. CORO E SÓ ELE É DIGNO DE TODO O LOUVOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO REDENTOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO SALVADOR! INSTRUMENTOS ALELUIA! NÓS TAMBÉM ESTAREMOS NAS LINDAS MANSÕES LOUVANDO PRA SEMPRE AO SENHOR; E COM GRANDE ALEGRIA VEREMOS JESUS, QUE DIA TÃO LINDO SERÁ. CORO E SÓ ELE É DIGNO DE TODO O LOUVOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO REDENTOR! GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA AO SALVADOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('593', '593 - A CRISTO COROAI', 'A CRISTO COROAI, CORDEIRO VENCEDOR!\nOUVI, DAS HOSTES CELESTIAIS\nDOS ANJOS, O LOUVOR!\nELEVA A TUA VOZ\nE ENTOA, CORAÇÃO,\nLOUVANDO AQUELE QUE MORREU\nE DEU-TE A SALVAÇÃO.\n\nA CRISTO COROAI, SEU LADO E MÃOS OLHAI!\nDAS SUAS CHAGAS O ESPLENDOR\nE A GLÓRIA CONTEMPLAI;\nNEM ANJOS LÁ DO CÉU\nO PODEM SUPORTAR,\nPERANTE O EXCELSO ESPLENDOR\nABAIXAM SEU OLHAR.\n\nA CRISTO COROAI, A VIDA NOS DOOU!\nE, A FIM DE DAR-NOS SALVAÇÃO,\nDA MORTE TRIUNFOU.\nCANTEMOS SEU PODER;\nMORREU, MAS RESSURGIU,\nA VIDA ETERNA ALI NOS DEU\nE A MORTE DESTRUIU.\n\nA CRISTO COROAI, DAS ERAS, O SENHOR!\nDOS MUNDOS E ASTROS DA AMPLIDÃO\nETERNO CRIADOR!\nAO GRANDE REDENTOR,\nQUE NOS DEU SALVAÇÃO,\nETERNAMENTE TRIBUTAI\nLOUVOR E ADORAÇÃO.', 'A CRISTO COROAI, CORDEIRO VENCEDOR! OUVI, DAS HOSTES CELESTIAIS DOS ANJOS, O LOUVOR! ELEVA A TUA VOZ E ENTOA, CORAÇÃO, LOUVANDO AQUELE QUE MORREU E DEU-TE A SALVAÇÃO. A CRISTO COROAI, SEU LADO E MÃOS OLHAI! DAS SUAS CHAGAS O ESPLENDOR E A GLÓRIA CONTEMPLAI; NEM ANJOS LÁ DO CÉU O PODEM SUPORTAR, PERANTE O EXCELSO ESPLENDOR ABAIXAM SEU OLHAR. A CRISTO COROAI, A VIDA NOS DOOU! E, A FIM DE DAR-NOS SALVAÇÃO, DA MORTE TRIUNFOU. CANTEMOS SEU PODER; MORREU, MAS RESSURGIU, A VIDA ETERNA ALI NOS DEU E A MORTE DESTRUIU. A CRISTO COROAI, DAS ERAS, O SENHOR! DOS MUNDOS E ASTROS DA AMPLIDÃO ETERNO CRIADOR! AO GRANDE REDENTOR, QUE NOS DEU SALVAÇÃO, ETERNAMENTE TRIBUTAI LOUVOR E ADORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('594', '594 - NA EXPRESSÃO DESTE LOUVOR', 'NA EXPRESSÃO DESTE LOUVOR\nQUERO DAR AO MEU SENHOR\nTODA A MINHA GRATIDÃO\nPOR SER MUI FELIZ.\n\nDEVO MUITO AO SALVADOR,\nNELE TENHO O PERDÃO,\nNELE TENHO A SALVAÇÃO,\nEU SOU TÃO FELIZ.\n\nCORO\nNÓS TE ADORAMOS, MEU SENHOR,\nPORQUE VENCESTE NA CRUZ,\nNÓS TE ADORAMOS, MEU SENHOR,\nPORQUE TU HÁS DE VOLTAR;\nNÓS TE ADORAMOS, MEU SENHOR,\nPOIS TU ÉS A RAZÃO\nE O MOTIVO DA NOSSA ADORAÇÃO.\n\nLÁ NA CRUZ MORREU JESUS,\nO SEU SANGUE DERRAMOU,\nO MEU FARDO ELE LEVOU\nE HOJE LIVRE SOU.\n\nCORO (2X)\nNÓS TE ADORAMOS, MEU SENHOR,\nPORQUE VENCESTE NA CRUZ,\nNÓS TE ADORAMOS, MEU SENHOR,\nPORQUE TU HÁS DE VOLTAR;\nNÓS TE ADORAMOS, MEU SENHOR,\nPOIS TU ÉS A RAZÃO\nE O MOTIVO DA NOSSA ADORAÇÃO.', 'NA EXPRESSÃO DESTE LOUVOR QUERO DAR AO MEU SENHOR TODA A MINHA GRATIDÃO POR SER MUI FELIZ. DEVO MUITO AO SALVADOR, NELE TENHO O PERDÃO, NELE TENHO A SALVAÇÃO, EU SOU TÃO FELIZ. CORO NÓS TE ADORAMOS, MEU SENHOR, PORQUE VENCESTE NA CRUZ, NÓS TE ADORAMOS, MEU SENHOR, PORQUE TU HÁS DE VOLTAR; NÓS TE ADORAMOS, MEU SENHOR, POIS TU ÉS A RAZÃO E O MOTIVO DA NOSSA ADORAÇÃO. LÁ NA CRUZ MORREU JESUS, O SEU SANGUE DERRAMOU, O MEU FARDO ELE LEVOU E HOJE LIVRE SOU. NÓS TE ADORAMOS, MEU SENHOR, PORQUE VENCESTE NA CRUZ, NÓS TE ADORAMOS, MEU SENHOR, PORQUE TU HÁS DE VOLTAR; NÓS TE ADORAMOS, MEU SENHOR, POIS TU ÉS A RAZÃO E O MOTIVO DA NOSSA ADORAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('595', '595 - MAJESTADE, GLÓRIA E MAJESTADE', 'MAJESTADE, GLÓRIA E MAJESTADE,\nJESUS CRISTO MERECE TODO LOUVOR;\nMAJESTADE, GLÓRIA E MAJESTADE, \nSEU É O LOUVOR, \nSEU É O PODER GLÓRIA A ELE!\n\nEXALTAI, ENGRANDECEI SEU SANTO NOME!\nADORAI, MAGNIFICAI A CRISTO REI!\nMAJESTADE, GLÓRIA E MAJESTADE, \nCRISTO MORREU, \nRESSUSCITOU E HOJE É SENHOR.', 'MAJESTADE, GLÓRIA E MAJESTADE, JESUS CRISTO MERECE TODO LOUVOR; MAJESTADE, GLÓRIA E MAJESTADE, SEU É O LOUVOR, SEU É O PODER GLÓRIA A ELE! EXALTAI, ENGRANDECEI SEU SANTO NOME! ADORAI, MAGNIFICAI A CRISTO REI! MAJESTADE, GLÓRIA E MAJESTADE, CRISTO MORREU, RESSUSCITOU E HOJE É SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('596', '596 - ÉS MAJESTADE', 'ÉS MAJESTADE, Ó REI DA GLÓRIA,\nÉS O SENHOR, O REI DOS REIS;\nTUA É SENHOR A MAGNIFICÊNCIA\nE O PODER E A HONRA E A VITÓRIA.\n\nCORO (2X)\nJESUS, JESUS, ÉS O SENHOR,\nO REI DOS REIS.\n\nDIGNO ÉS DE ABRIR O LIVRO DA VIDA,\nDIGNO É O CORDEIRO QUE FOI MORTO\nDE RECEBER PODER E RIQUEZAS,\nSABEDORIA, GLÓRIA E LOUVOR.\n\nCORO (2X)\nJESUS, JESUS, ÉS O SENHOR,\nO REI DOS REIS.', 'ÉS MAJESTADE, Ó REI DA GLÓRIA, ÉS O SENHOR, O REI DOS REIS; TUA É SENHOR A MAGNIFICÊNCIA E O PODER E A HONRA E A VITÓRIA.  JESUS, JESUS, ÉS O SENHOR, O REI DOS REIS. DIGNO ÉS DE ABRIR O LIVRO DA VIDA, DIGNO É O CORDEIRO QUE FOI MORTO DE RECEBER PODER E RIQUEZAS, SABEDORIA, GLÓRIA E LOUVOR.  JESUS, JESUS, ÉS O SENHOR, O REI DOS REIS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('597', '597 - CANTAREI, CANTAREI HOSANA (VIVA O REI)', 'CANTAREI, CANTAREI HOSANA,\nCANTAREI HOSANA AO SALVADOR;\nLOUVAREI SEU SANTO NOME \nE COM TODA ALEGRIA NO CÉU OUVIREI:\n\nCORO\nAS VOZES DE MILHÕES DE ANJOS CANTANDO,\nCOM TODOS OS FIÉIS FALAREI:\nVIVA O REI, VIVA O REI, \nVIVA O REI JESUS!\n\nENTRAREI NO SANTO DOS SANTOS\nCOM UM CÂNTICO NOVO DE GRATIDÃO;\nVIVEREI PRA SEMPRE NA GLÓRIA,\nLOUVANDO A JESUS E LÁ OUVIREI:\n\nCORO \nAS VOZES DE MILHÕES DE ANJOS CANTANDO,\nCOM TODOS OS FIÉIS FALAREI: \nVIVA O REI, VIVA O REI, \nVIVA O REI JESUS!\n\nGOZO, PAZ, PERFEITO AMOR,\nPLENA ALEGRIA NO MEU SENHOR;\nESTE MUNDO DEIXAREI, NA LINDA PÁTRIA \nBREVE ESTAREI E OUVIREI:\n\nCORO (2X)\nAS VOZES DE MILHÕES DE ANJOS CANTANDO,\nCOM TODOS OS FIÉIS FALAREI: \nVIVA O REI, VIVA O REI, \nVIVA O REI JESUS!', 'CANTAREI, CANTAREI HOSANA, CANTAREI HOSANA AO SALVADOR; LOUVAREI SEU SANTO NOME E COM TODA ALEGRIA NO CÉU OUVIREI: CORO AS VOZES DE MILHÕES DE ANJOS CANTANDO, COM TODOS OS FIÉIS FALAREI: VIVA O REI, VIVA O REI, VIVA O REI JESUS! ENTRAREI NO SANTO DOS SANTOS COM UM CÂNTICO NOVO DE GRATIDÃO; VIVEREI PRA SEMPRE NA GLÓRIA, LOUVANDO A JESUS E LÁ OUVIREI: GOZO, PAZ, PERFEITO AMOR, PLENA ALEGRIA NO MEU SENHOR; ESTE MUNDO DEIXAREI, NA LINDA PÁTRIA BREVE ESTAREI E OUVIREI: AS VOZES DE MILHÕES DE ANJOS CANTANDO, COM TODOS OS FIÉIS FALAREI: VIVA O REI, VIVA O REI, VIVA O REI JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('598', '598 - HÁ UM SÓ QUE É DIGNO', 'HÁ UM SÓ QUE É DIGNO \nDE SOBRE NÓS REINAR \nE NO TRONO DE DAVI COM GLÓRIA SE SENTAR;\nO QUE ELE FALA PRA NÓS É SANTA LEI,\nA ELE ACLAMAMOS: VIVA O NOSSO REI!\n\nCORO\nVIVA O REI! VIVA O REI! \nO FILHO DE DAVI, O LEÃO DE JUDÁ,\nO REI DOS REIS QUE BREVE VOLTARÁ.\n\nMUITOS SE LEVANTAM QUERENDO SE IMPOR, \nCOM SEUS VÃOS PROJETOS, \nAO POVO DO SENHOR;\nMAS DEUS TEM SUA OBRA E NELA SERVIREI; \nFRACO SOU, MAS CLAMO: \nVIVA O NOSSO REI!\n\nCORO\nVIVA O REI! VIVA O REI! \nO FILHO DE DAVI, O LEÃO DE JUDÁ,\nO REI DOS REIS QUE BREVE VOLTARÁ.\n\nEIS AQUI QUEM REINA, \nMAIOR QUE SALOMÃO, COMO ESPADA AGUDA \nSÃO OS CONSELHOS SEUS;\nE A NÓS, IGREJA, DEU GRAÇA E SALVAÇÃO\nFEZ-NOS SEUS VALENTES, EXÉRCITO DE DEUS.\n\nCORO\nVIVA O REI! VIVA O REI! \nO FILHO DE DAVI, O LEÃO DE JUDÁ,\nO REI DOS REIS QUE BREVE VOLTARÁ.', 'HÁ UM SÓ QUE É DIGNO DE SOBRE NÓS REINAR E NO TRONO DE DAVI COM GLÓRIA SE SENTAR; O QUE ELE FALA PRA NÓS É SANTA LEI, A ELE ACLAMAMOS: VIVA O NOSSO REI! CORO VIVA O REI! VIVA O REI! O FILHO DE DAVI, O LEÃO DE JUDÁ, O REI DOS REIS QUE BREVE VOLTARÁ. MUITOS SE LEVANTAM QUERENDO SE IMPOR, COM SEUS VÃOS PROJETOS, AO POVO DO SENHOR; MAS DEUS TEM SUA OBRA E NELA SERVIREI; FRACO SOU, MAS CLAMO: VIVA O NOSSO REI! EIS AQUI QUEM REINA, MAIOR QUE SALOMÃO, COMO ESPADA AGUDA SÃO OS CONSELHOS SEUS; E A NÓS, IGREJA, DEU GRAÇA E SALVAÇÃO FEZ-NOS SEUS VALENTES, EXÉRCITO DE DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('599', '599 - JESUS, TE ENTRONIZAMOS AQUI ENTRE NÓS', 'JESUS, TE ENTRONIZAMOS AQUI ENTRE NÓS \nPARA TRIBUTARMOS NOSSA ADORAÇÃO;\nA TI RENDEMOS GLÓRIA, HONRA E LOUVOR, EXPRESSÃO DE TODA A GRATIDÃO.\n\nCORO\nCOROAMOS A CRISTO,\n(COROAMOS A CRISTO NOSSO REI) VARÕES\nELE É O NOSSO REI, SIM, O NOSSO REI;\nERGUEMOS AS VOZES EM CORO,\n(HOSANA A TI, JESUS) VARÕES\nCANTANDO HOSANA A JESUS,\nE OS ANJOS PROCLAMAM NO CÉU: VIVA O REI.\n\nINSTRUMENTOS\n\nTU ÉS DEUS FILHO, O VERBO CRIADOR,\nESTÁS REVESTIDO DE GLÓRIA E ESPLENDOR;\nSANTO, SANTO, SANTO, ÉS TU, JESUS,\nTODA HONRA SEJA PARA TI.\n\nCORO (2X)\nCOROAMOS A CRISTO,\n(COROAMOS A CRISTO NOSSO REI) VARÕES\nELE É O NOSSO REI, SIM, O NOSSO REI;\nERGUEMOS AS VOZES EM CORO,\n(HOSANA A TI, JESUS) VARÕES\nCANTANDO HOSANA A JESUS,\nE OS ANJOS PROCLAMAM NO CÉU: VIVA O REI.\n\nFINAL: VIVA O REI.', 'JESUS, TE ENTRONIZAMOS AQUI ENTRE NÓS PARA TRIBUTARMOS NOSSA ADORAÇÃO; A TI RENDEMOS GLÓRIA, HONRA E LOUVOR, EXPRESSÃO DE TODA A GRATIDÃO. CORO COROAMOS A CRISTO, (COROAMOS A CRISTO NOSSO REI) VARÕES ELE É O NOSSO REI, SIM, O NOSSO REI; ERGUEMOS AS VOZES EM CORO, (HOSANA A TI, JESUS) VARÕES CANTANDO HOSANA A JESUS, E OS ANJOS PROCLAMAM NO CÉU: VIVA O REI. INSTRUMENTOS TU ÉS DEUS FILHO, O VERBO CRIADOR, ESTÁS REVESTIDO DE GLÓRIA E ESPLENDOR; SANTO, SANTO, SANTO, ÉS TU, JESUS, TODA HONRA SEJA PARA TI. COROAMOS A CRISTO, (COROAMOS A CRISTO NOSSO REI) VARÕES ELE É O NOSSO REI, SIM, O NOSSO REI; ERGUEMOS AS VOZES EM CORO, (HOSANA A TI, JESUS) VARÕES CANTANDO HOSANA A JESUS, E OS ANJOS PROCLAMAM NO CÉU: VIVA O REI.  VIVA O REI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('600', '600 - JESUS, TE ENTRONIZAMOS', 'JESUS, TE ENTRONIZAMOS,\nDECLARAMOS QUE ÉS REI,\nTU ESTÁS NO MEIO DE NÓS,\nTE EXALTAMOS COM LOUVORES.\n\nA TI FAZEMOS UM TRONO DE LOUVORES (3X)\nÓ VEM, JESUS, E TOMA O TEU LUGAR,\nÓ VEM, JESUS, E TOMA O TEU LUGAR.\n\nINSTRUMENTOS\n\nJESUS, TE ENTRONIZAMOS,\nDECLARAMOS QUE ÉS REI,\nTU ESTÁS NO MEIO DE NÓS,\nTE EXALTAMOS COM LOUVORES.\n\nA TI FAZEMOS UM TRONO DE LOUVORES, (3X)\nÓ VEM, JESUS, E TOMA O TEU LUGAR,\nÓ VEM, JESUS, E TOMA O TEU LUGAR.\n\nFINAL: \nÓ VEM, JESUS, E TOMA O TEU LUGAR.', 'JESUS, TE ENTRONIZAMOS, DECLARAMOS QUE ÉS REI, TU ESTÁS NO MEIO DE NÓS, TE EXALTAMOS COM LOUVORES. A TI FAZEMOS UM TRONO DE LOUVORES  Ó VEM, JESUS, E TOMA O TEU LUGAR, Ó VEM, JESUS, E TOMA O TEU LUGAR. INSTRUMENTOS A TI FAZEMOS UM TRONO DE LOUVORES,  Ó VEM, JESUS, E TOMA O TEU LUGAR, Ó VEM, JESUS, E TOMA O TEU LUGAR.  Ó VEM, JESUS, E TOMA O TEU LUGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('601', '601 - AO QUE ESTÁ NO TRONO', 'AO QUE ESTÁ NO TRONO ENTRE OS QUERUBINS, \nAO QUE RESPLANDECE E É FOGO CONSUMIDOR;\nDIANTE DELE VOAM ANJOS, SERAFINS \nQUE SEUS ROSTOS COBREM PERANTE \nA FACE DO SENHOR.\n\nCORO\nGLÓRIA, FORÇA, GRAÇA E PODER,\nSABEDORIA, HONRA E LOUVOR\n\nAO CORDEIRO MORTO QUE RESSUSCITOU \nE QUE AO CÉU SUBIU,\nAO REI QUE TÃO BREVE VEM;\nÀ PALAVRA VIVA QUE SE REVELOU,\nÀ LUZ QUE ILUMINA A NOVA JERUSALÉM.\n\nCORO (2X)\nGLÓRIA, FORÇA, GRAÇA E PODER,\nSABEDORIA, HONRA E LOUVOR\n\nFINAL: \nSANTO É O SENHOR! (3X)', 'AO QUE ESTÁ NO TRONO ENTRE OS QUERUBINS, AO QUE RESPLANDECE E É FOGO CONSUMIDOR; DIANTE DELE VOAM ANJOS, SERAFINS QUE SEUS ROSTOS COBREM PERANTE A FACE DO SENHOR. CORO GLÓRIA, FORÇA, GRAÇA E PODER, SABEDORIA, HONRA E LOUVOR AO CORDEIRO MORTO QUE RESSUSCITOU E QUE AO CÉU SUBIU, AO REI QUE TÃO BREVE VEM; À PALAVRA VIVA QUE SE REVELOU, À LUZ QUE ILUMINA A NOVA JERUSALÉM. GLÓRIA, FORÇA, GRAÇA E PODER, SABEDORIA, HONRA E LOUVOR  SANTO É O SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('602', '602 - SANTO É O QUE ESTÁ NO TRONO', 'SANTO, SANTO, SANTO\nÉ O QUE ESTÁ NO TRONO,\nO QUE É REI E SENHOR;\nDIGNO, SÓ ELE É DIGNO\nDE RECEBER A GLÓRIA E LOUVOR!\n\nQUEM É O QUE BRILHA MAIS QUE O SOL?\nÉ O SENHOR! (SERVAS)\nQUEM É O QUE BRAMA COMO O LEÃO? \nÉ O SENHOR! (SERVAS)\nQUEM É O QUE PASSEIA \nENTRE OS SETE CASTIÇAIS? \nÉ O CORDEIRO, É O LEÃO DE JUDÁ!\n\nQUEM É O QUE A MORTE CONQUISTOU? \nÉ O SENHOR! (VARÕES)\nQUEM É O QUE O MUNDO VAI JULGAR? \nÉ O SENHOR! (VARÕES)\nQUEM É O QUE VIRÁ A ESPOSA ARREBATAR? \nÉ O CORDEIRO, É O LEÃO DE JUDÁ.\n\nSANTO, SANTO, SANTO\nÉ O QUE ESTÁ NO TRONO,\nO QUE É REI E SENHOR;\nDIGNO, SÓ ELE É DIGNO\nDE RECEBER A GLÓRIA E LOUVOR! (3X)', 'SANTO, SANTO, SANTO É O QUE ESTÁ NO TRONO, O QUE É REI E SENHOR; DIGNO, SÓ ELE É DIGNO DE RECEBER A GLÓRIA E LOUVOR! QUEM É O QUE BRILHA MAIS QUE O SOL? É O SENHOR!  QUEM É O QUE BRAMA COMO O LEÃO? É O SENHOR!  QUEM É O QUE PASSEIA ENTRE OS SETE CASTIÇAIS? É O CORDEIRO, É O LEÃO DE JUDÁ! QUEM É O QUE A MORTE CONQUISTOU? É O SENHOR!  QUEM É O QUE O MUNDO VAI JULGAR? É O SENHOR!  QUEM É O QUE VIRÁ A ESPOSA ARREBATAR? É O CORDEIRO, É O LEÃO DE JUDÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('603', '603 - LOUVAREI AO MEU SENHOR', 'LOUVAREI AO MEU SENHOR ENQUANTO EU VIVER,\nSEU LOUVOR PRA SEMPRE ESTARÁ COMIGO;\nSUA GRAÇA ME SUSTÉM, SEU AMOR É TÃO PROFUNDO, \nSEU ESPÍRITO ME DÁ FORÇAS PRA LUTAR.\n\nCORO (2X)\nSIM, LOUVAREI AO MEU SENHOR,\nPOIS É DIGNO DE LOUVOR,\nSUA GRAÇA ME SUSTÉM,\nLOUVAREI PRA SEMPRE, AMÉM.\n\nLOUVAREI AO MEU SENHOR,\nSUA GRAÇA ME SALVOU,\n(SIM, LOUVAREI)\nPÔS UM NOVO CÂNTICO EM MEUS LÁBIOS.\n(SIM, LOUVAREI)\nSUA GRAÇA ME SUSTÉM,\nSEU AMOR É TÃO PROFUNDO,\n(SIM, LOUVAREI)\nSEU ESPÍRITO ME DÁ FORÇAS PRA LUTAR.\n\nCORO (2X)\nSIM, LOUVAREI AO MEU SENHOR,\nPOIS É DIGNO DE LOUVOR,\nSUA GRAÇA ME SUSTÉM,\nLOUVAREI PRA SEMPRE, AMÉM.\n\nFINAL: SIM, LOUVAREI.', 'LOUVAREI AO MEU SENHOR ENQUANTO EU VIVER, SEU LOUVOR PRA SEMPRE ESTARÁ COMIGO; SUA GRAÇA ME SUSTÉM, SEU AMOR É TÃO PROFUNDO, SEU ESPÍRITO ME DÁ FORÇAS PRA LUTAR. SIM, LOUVAREI AO MEU SENHOR, POIS É DIGNO DE LOUVOR, SUA GRAÇA ME SUSTÉM, LOUVAREI PRA SEMPRE, AMÉM. LOUVAREI AO MEU SENHOR, SUA GRAÇA ME SALVOU, (SIM, LOUVAREI) PÔS UM NOVO CÂNTICO EM MEUS LÁBIOS. (SIM, LOUVAREI) SUA GRAÇA ME SUSTÉM, SEU AMOR É TÃO PROFUNDO, (SIM, LOUVAREI) SEU ESPÍRITO ME DÁ FORÇAS PRA LUTAR. SIM, LOUVAREI AO MEU SENHOR, POIS É DIGNO DE LOUVOR, SUA GRAÇA ME SUSTÉM, LOUVAREI PRA SEMPRE, AMÉM.  SIM, LOUVAREI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('604', '604 - O MEU LOUVOR É FRUTO', 'CORO (2X)\nO MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME;\nÉ FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO \nEM TI E DO TEU ESPÍRITO QUE HABITA EM MIM,\nQUE HABITA EM MIM.\n\nAINDA QUE AS TREVAS VENHAM \nME CERCAR AINDA QUE OS MONTES \nDESABEM SOBRE MIM, \nMEUS LÁBIOS NÃO SE FECHARÃO \nPRA SEMPRE HEI DE TE LOUVAR!\n\nCORO\nO MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME;\nÉ FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO \nEM TI E DO TEU ESPÍRITO QUE HABITA EM MIM,\nQUE HABITA EM MIM.\n\nAINDA QUE CADEIAS VENHAM \nME PRENDER AINDA QUE OS HOMENS \nSE LEVANTEM CONTRA MIM, \nMEUS LÁBIOS NÃO SE FECHARÃO,\nPRA SEMPRE HEI DE TE LOUVAR!\n\nCORO\nO MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME;\nÉ FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO \nEM TI E DO TEU ESPÍRITO QUE HABITA EM MIM,\nQUE HABITA EM MIM.', 'O MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME; É FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO EM TI E DO TEU ESPÍRITO QUE HABITA EM MIM, QUE HABITA EM MIM. AINDA QUE AS TREVAS VENHAM ME CERCAR AINDA QUE OS MONTES DESABEM SOBRE MIM, MEUS LÁBIOS NÃO SE FECHARÃO PRA SEMPRE HEI DE TE LOUVAR! CORO O MEU LOUVOR É FRUTO DO MEU AMOR POR TI, JESUS, DE LÁBIOS QUE CONFESSAM O TEU NOME; É FRUTO DE TUA GRAÇA E DA PAZ QUE ENCONTRO EM TI E DO TEU ESPÍRITO QUE HABITA EM MIM, QUE HABITA EM MIM. AINDA QUE CADEIAS VENHAM ME PRENDER AINDA QUE OS HOMENS SE LEVANTEM CONTRA MIM, MEUS LÁBIOS NÃO SE FECHARÃO, PRA SEMPRE HEI DE TE LOUVAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('605', '605 - NOSSO DEUS É SOBERANO', 'NOSSO DEUS É SOBERANO, \nELE REINA ANTES DA FUNDAÇÃO DO MUNDO. \nNOSSO DEUS É SOBERANO, \nELE REINA ANTES DA FUNDAÇÃO DO MUNDO.\n\nA TERRA ERA SEM FORMA E VAZIA\nE O ESPÍRITO DO NOSSO DEUS\nSE MOVIA SOBRE A FACE DAS ÁGUAS;\nFOI ELE QUEM CRIOU O CÉU DOS CÉUS \nE FEZ SEPARAÇÃO DAS ÁGUAS DA TERRA SECA;\nFOI ELE QUEM CRIOU OS LUMINARES,\nCRIOU A NATUREZA \nE FORMOU O HOMEM.\n\nGLÓRIA A DEUS POR SUAS MARAVILHAS,\nPELA SUA GRANDEZA, GLÓRIA A DEUS!\nGLÓRIA A DEUS POR SUAS MARAVILHAS,\nPELA SUA GRANDEZA, GLÓRIA A DEUS!\nGLÓRIA A DEUS!', 'NOSSO DEUS É SOBERANO, ELE REINA ANTES DA FUNDAÇÃO DO MUNDO. NOSSO DEUS É SOBERANO, ELE REINA ANTES DA FUNDAÇÃO DO MUNDO. A TERRA ERA SEM FORMA E VAZIA E O ESPÍRITO DO NOSSO DEUS SE MOVIA SOBRE A FACE DAS ÁGUAS; FOI ELE QUEM CRIOU O CÉU DOS CÉUS E FEZ SEPARAÇÃO DAS ÁGUAS DA TERRA SECA; FOI ELE QUEM CRIOU OS LUMINARES, CRIOU A NATUREZA E FORMOU O HOMEM. GLÓRIA A DEUS POR SUAS MARAVILHAS, PELA SUA GRANDEZA, GLÓRIA A DEUS! GLÓRIA A DEUS POR SUAS MARAVILHAS, PELA SUA GRANDEZA, GLÓRIA A DEUS! GLÓRIA A DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('606', '606 - O CRIADOR DO UNIVERSO', 'O CRIADOR DO UNIVERSO,\nQUE FEZ DO NADA TUDO NASCER,\nO QUE SUSTENTA TODAS AS COISAS\nPELA PALAVRA DO SEU PODER;\nO VERBO ETERNO QUE SE FEZ CARNE,\nO DEUS EM HOMEM REVELADO,\nO DEUS QUE É PAI, FILHO E ESPÍRITO,\nPARA SEMPRE SEJA ADORADO.\n\nCORO\nSEJA ADORADO, SEJA EXALTADO,\nSEJA LOUVADO O TEU NOME, Ó DEUS!\n\nO PAI, A QUEM ABANDONAMOS,\nMAS QUE JAMAIS NOS ESQUECEU,\nATÉ QUE UM DIA REGRESSAMOS\nE COM PERDÃO NOS RECEBEU;\nO FILHO QUE NA CRUZ FOI DADO,\nO QUE HOJE VIVE E ORA POR NÓS,\nO ESPÍRITO QUE NÃO NOS DEIXA SÓS;\nPARA SEMPRE SEJA ADORADO.\n\nCORO\nSEJA ADORADO, SEJA EXALTADO,\nSEJA LOUVADO O TEU NOME, Ó DEUS!\n\nFINAL: \nSEJA ADORADO, SEJA EXALTADO,\nSEJA LOUVADO O TEU NOME, \nÓ DEUS! SEJA EXALTADO O TEU NOME, \nÓ DEUS!', 'O CRIADOR DO UNIVERSO, QUE FEZ DO NADA TUDO NASCER, O QUE SUSTENTA TODAS AS COISAS PELA PALAVRA DO SEU PODER; O VERBO ETERNO QUE SE FEZ CARNE, O DEUS EM HOMEM REVELADO, O DEUS QUE É PAI, FILHO E ESPÍRITO, PARA SEMPRE SEJA ADORADO. CORO SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS! O PAI, A QUEM ABANDONAMOS, MAS QUE JAMAIS NOS ESQUECEU, ATÉ QUE UM DIA REGRESSAMOS E COM PERDÃO NOS RECEBEU; O FILHO QUE NA CRUZ FOI DADO, O QUE HOJE VIVE E ORA POR NÓS, O ESPÍRITO QUE NÃO NOS DEIXA SÓS; PARA SEMPRE SEJA ADORADO. CORO SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS!  SEJA ADORADO, SEJA EXALTADO, SEJA LOUVADO O TEU NOME, Ó DEUS! SEJA EXALTADO O TEU NOME, Ó DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('607', '607 - A DEUS SEJA O LOUVOR', 'A DEUS SEJA O LOUVOR\nPOR TUDO O QUE ELE É,\nPERFEIÇÃO ABSOLUTA\nEM SANTIDADE E AMOR!\nE DAI AO FILHO A GLÓRIA\nPOIS ALCANÇOU VITÓRIA,\nELE É VENCEDOR, \nDE TUDO É O SENHOR!\n\nPOR TODO O SEMPRE SE FALARÁ DAS MARAVILHAS \nDO SEU AMOR E TODO SER SE ENCURVARÁ \nPRA O ADORAR!\nO REI QUE SERVO SE TORNOU, \nO DEUS QUE ÀQUELA CRUZ BAIXOU PARA, \nPOR SEU SANGUE, O PECADOR RESGATAR.\n\nÀQUELA CRUZ BAIXOU PARA, \nPOR SEU SANGUE, \nO PECADOR RESGATAR.', 'A DEUS SEJA O LOUVOR POR TUDO O QUE ELE É, PERFEIÇÃO ABSOLUTA EM SANTIDADE E AMOR! E DAI AO FILHO A GLÓRIA POIS ALCANÇOU VITÓRIA, ELE É VENCEDOR, DE TUDO É O SENHOR! POR TODO O SEMPRE SE FALARÁ DAS MARAVILHAS DO SEU AMOR E TODO SER SE ENCURVARÁ PRA O ADORAR! O REI QUE SERVO SE TORNOU, O DEUS QUE ÀQUELA CRUZ BAIXOU PARA, POR SEU SANGUE, O PECADOR RESGATAR. ÀQUELA CRUZ BAIXOU PARA, POR SEU SANGUE, O PECADOR RESGATAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('608', '608 - DEUS DOS EXÉRCITOS DE ISRAEL', 'DEUS DOS EXÉRCITOS DE ISRAEL,\nCRIADOR DA TERRA E DO CÉU,\nTU ÉS DEUS DOS PROFETAS,\nSOBERANO CRIADOR;\nTU ÉS DEUS E DA VIDA ÉS AUTOR.\nÉS CRIADOR! ÉS CRIADOR!\n\nJESUS, ETERNO SALVADOR,\nNA CRUZ DEMONSTRASTE TEU AMOR,\nCOM GRAÇA E VERDADE,\nCOM TEU SANGUE ME ALCANÇASTE;\nJESUS, TU ÉS DEUS, TU ÉS SENHOR.\nTU ÉS SENHOR! TU ÉS SENHOR!\n\nÓ ESPÍRITO SANTO CONSOLADOR,\nEM TEUS SERVOS OPERAS COM FAVOR,\nTU ESTÁS SEMPRE CONOSCO,\nÉS O NOSSO INTERCESSOR;\nTU ÉS DEUS, Ó ESPÍRITO DE AMOR.\nCONSOLADOR! CONSOLADOR!', 'DEUS DOS EXÉRCITOS DE ISRAEL, CRIADOR DA TERRA E DO CÉU, TU ÉS DEUS DOS PROFETAS, SOBERANO CRIADOR; TU ÉS DEUS E DA VIDA ÉS AUTOR. ÉS CRIADOR! ÉS CRIADOR! JESUS, ETERNO SALVADOR, NA CRUZ DEMONSTRASTE TEU AMOR, COM GRAÇA E VERDADE, COM TEU SANGUE ME ALCANÇASTE; JESUS, TU ÉS DEUS, TU ÉS SENHOR. TU ÉS SENHOR! TU ÉS SENHOR! Ó ESPÍRITO SANTO CONSOLADOR, EM TEUS SERVOS OPERAS COM FAVOR, TU ESTÁS SEMPRE CONOSCO, ÉS O NOSSO INTERCESSOR; TU ÉS DEUS, Ó ESPÍRITO DE AMOR. CONSOLADOR! CONSOLADOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('609', '609 - SENHOR, MEU DEUS, QUANDO EU MARAVILHADO', 'SENHOR, MEU DEUS, \nQUANDO EU MARAVILHADO \nFICO A PENSAR NAS OBRAS DE TUAS MÃOS,\nO CÉU AZUL DE ESTRELAS PONTILHADO,\nO TEU PODER MOSTRANDO A CRIAÇÃO.\n\nCORO (2X)\nENTÃO MINH’ALMA CANTA A TI, \nSENHOR: QUÃO GRANDE ÉS TU,\nQUÃO GRANDE ÉS TU!\n\nQUANDO A VAGAR NAS MATAS E FLORESTAS, \nO PASSAREDO ALEGRE OUÇO A CANTAR;\nOLHANDO OS MONTES VALES E CAMPINAS\nEM TUDO VEJO O TEU PODER SEM PAR.\n\nCORO (2X)\nENTÃO MINH’ALMA CANTA A TI, \nSENHOR: QUÃO GRANDE ÉS TU,\nQUÃO GRANDE ÉS TU!\n\nQUANDO EU MEDITO EM TEU AMOR TÃO GRANDE, \nTEU FILHO DANDO AO MUNDO PRA SALVAR;\nNA CRUZ VERTENDO O SEU PRECIOSO SANGUE,\nMINH’ALMA PÔDE ASSIM PURIFICAR.\n\nCORO (2X)\nENTÃO MINH’ALMA CANTA A TI, \nSENHOR: QUÃO GRANDE ÉS TU,\nQUÃO GRANDE ÉS TU!\n\nE QUANDO ENFIM JESUS VIER EM GLÓRIA\nE AO LAR CELESTE ENTÃO ME TRANSPORTAR, \nADORAREI, PROSTRADO E PARA SEMPRE, \nQUÃO GRANDE ÉS TU, MEU DEUS, HEI DE CANTAR.\n\nCORO (2X)\nENTÃO MINH’ALMA CANTA A TI, \nSENHOR: QUÃO GRANDE ÉS TU,\nQUÃO GRANDE ÉS TU!', 'SENHOR, MEU DEUS, QUANDO EU MARAVILHADO FICO A PENSAR NAS OBRAS DE TUAS MÃOS, O CÉU AZUL DE ESTRELAS PONTILHADO, O TEU PODER MOSTRANDO A CRIAÇÃO. ENTÃO MINH’ALMA CANTA A TI, SENHOR: QUÃO GRANDE ÉS TU, QUÃO GRANDE ÉS TU! QUANDO A VAGAR NAS MATAS E FLORESTAS, O PASSAREDO ALEGRE OUÇO A CANTAR; OLHANDO OS MONTES VALES E CAMPINAS EM TUDO VEJO O TEU PODER SEM PAR. QUANDO EU MEDITO EM TEU AMOR TÃO GRANDE, TEU FILHO DANDO AO MUNDO PRA SALVAR; NA CRUZ VERTENDO O SEU PRECIOSO SANGUE, MINH’ALMA PÔDE ASSIM PURIFICAR. E QUANDO ENFIM JESUS VIER EM GLÓRIA E AO LAR CELESTE ENTÃO ME TRANSPORTAR, ADORAREI, PROSTRADO E PARA SEMPRE, QUÃO GRANDE ÉS TU, MEU DEUS, HEI DE CANTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('610', '610 - SANTO! SANTO! SANTO! DEUS ONIPOTENTE!', 'SANTO! SANTO! SANTO! DEUS ONIPOTENTE!\nCEDO DE MANHÃ CANTAREMOS TEU LOUVOR!\nSANTO! SANTO! SANTO! DEUS, JEOVÁ, TRIUNO,\nÉS UM SÓ DEUS, EXCELSO CRIADOR!\n\nSANTO! SANTO! SANTO! \nTODOS OS REMIDOS, JUNTOS COM OS ANJOS, \nPROCLAMAM TEU LOUVOR; ANTES DE \nFORMAR-SE O FIRMAMENTO E A TERRA, \nERAS E SEMPRE ÉS E HÁS DE SER, SENHOR!\n\nSANTO! SANTO! SANTO! NÓS OS PECADORES \nNÃO PODEMOS VER TUA GLÓRIA SEM TREMOR;\nTU SOMENTE ÉS SANTO, NÃO HÁ NENHUM \nOUTRO PURO E PERFEITO, EXCELSO BENFEITOR!\n\nINSTRUMENTOS\n\nSANTO! SANTO! SANTO! DEUS ONIPOTENTE!\nTUAS OBRAS LOUVAM TEU NOME COM FERVOR;\nSANTO! SANTO! SANTO! JUSTO E COMPASSIVO!\nÉS UM SÓ DEUS, SUPREMO CRIADOR.', 'SANTO! SANTO! SANTO! DEUS ONIPOTENTE! CEDO DE MANHÃ CANTAREMOS TEU LOUVOR! SANTO! SANTO! SANTO! DEUS, JEOVÁ, TRIUNO, ÉS UM SÓ DEUS, EXCELSO CRIADOR! SANTO! SANTO! SANTO! TODOS OS REMIDOS, JUNTOS COM OS ANJOS, PROCLAMAM TEU LOUVOR; ANTES DE FORMAR-SE O FIRMAMENTO E A TERRA, ERAS E SEMPRE ÉS E HÁS DE SER, SENHOR! SANTO! SANTO! SANTO! NÓS OS PECADORES NÃO PODEMOS VER TUA GLÓRIA SEM TREMOR; TU SOMENTE ÉS SANTO, NÃO HÁ NENHUM OUTRO PURO E PERFEITO, EXCELSO BENFEITOR! INSTRUMENTOS SANTO! SANTO! SANTO! DEUS ONIPOTENTE! TUAS OBRAS LOUVAM TEU NOME COM FERVOR; SANTO! SANTO! SANTO! JUSTO E COMPASSIVO! ÉS UM SÓ DEUS, SUPREMO CRIADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('611', '611 - JESUS DEUS SANTO, SANTO, SANTO!', 'JESUS, DEUS SANTO, SANTO, SANTO!\nDEUS DE PERDÃO E AMOR,\nDEUS DE PAZ, EMANUEL!\nÓ FILHO DE DAVI, ESTRELA DA MANHÃ,\nREI DOS REIS, EU TE ADORO, LOUVO,\nTE ESPERO, AMADO MEU, MEU JESUS!\nBIS', 'JESUS, DEUS SANTO, SANTO, SANTO! DEUS DE PERDÃO E AMOR, DEUS DE PAZ, EMANUEL! Ó FILHO DE DAVI, ESTRELA DA MANHÃ, REI DOS REIS, EU TE ADORO, LOUVO, TE ESPERO, AMADO MEU, MEU JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('612', '612 - SANTO, SANTO, SANTO,  Ó DEUS DE GLÓRIA E PODER', 'SANTO, SANTO, SANTO,\nÓ DEUS DE GLÓRIA E PODER;\nCÉUS E TERRA ESTÃO PLENOS DE TI,\nHOSANA, HOSANA,\nHOSANA AO NOSSO DEUS.\n\nBENDITO AQUELE QUE VEM\nEM NOME DO SENHOR,\nHOSANA, HOSANA,\nHOSANA AO NOSSO DEUS.', 'SANTO, SANTO, SANTO, Ó DEUS DE GLÓRIA E PODER; CÉUS E TERRA ESTÃO PLENOS DE TI, HOSANA, HOSANA, HOSANA AO NOSSO DEUS. BENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA, HOSANA AO NOSSO DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('613', '613 - DAI GLÓRIA A DEUS', 'DAI GLÓRIA A DEUS!\nQUEM NÃO O TEMERÁ?\nGLÓRIA E PODER SEM FIM\nDIANTE DELE HÁ!\n\nEIS O LEÃO\nDA TRIBO DE JUDÁ,\nAQUELE QUE ERA E É,\nE QUE VOLTARÁ!\n\nANJOS E QUERUBINS,\nARCANJOS E SERAFINS,\nDIA E NOITE ADORAM O QUE FOI MORTO \nMAS HOJE VIVE E É REI DOS REIS!\n\nDAI GLÓRIA A DEUS,\nDAI GLÓRIA A DEUS PAI,\nAO FILHO ADORAI,\nDAI GLÓRIA A DEUS! (3X)', 'DAI GLÓRIA A DEUS! QUEM NÃO O TEMERÁ? GLÓRIA E PODER SEM FIM DIANTE DELE HÁ! EIS O LEÃO DA TRIBO DE JUDÁ, AQUELE QUE ERA E É, E QUE VOLTARÁ! ANJOS E QUERUBINS, ARCANJOS E SERAFINS, DIA E NOITE ADORAM O QUE FOI MORTO MAS HOJE VIVE E É REI DOS REIS! DAI GLÓRIA A DEUS, DAI GLÓRIA A DEUS PAI, AO FILHO ADORAI, DAI GLÓRIA A DEUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('614', '614 - A DEUS DEMOS GLÓRIA', 'A DEUS DEMOS GLÓRIA COM GRANDE FERVOR, \nSEU FILHO BENDITO POR NÓS TODOS DEU;\nA GRAÇA CONCEDE A QUALQUER PECADOR \nABRINDO-LHE A PORTA DE ENTRADA NO CÉU.\n\nCORO\nEXULTAI! EXULTAI! VINDE TODOS LOUVAR\nA JESUS SALVADOR, A JESUS REDENTOR!\nA DEUS DEMOS GLÓRIA PORQUANTO DO CÉU\nSEU FILHO BENDITO POR NÓS TODOS DEU.\n\nOH! GRAÇA REAL! FOI ASSIM QUE JESUS\nMORRENDO, SEU SANGUE POR NÓS DERRAMOU; \nHERANÇA NOS CÉUS, COM OS SANTOS EM LUZ, \nCOMPROU-NOS, JESUS, POIS O PREÇO PAGOU.\n\nCORO\nEXULTAI! EXULTAI! VINDE TODOS LOUVAR\nA JESUS SALVADOR, A JESUS REDENTOR!\nA DEUS DEMOS GLÓRIA PORQUANTO DO CÉU\nSEU FILHO BENDITO POR NÓS TODOS DEU.\n\nA CRER NOS CONVIDA TAL PROVA DE AMOR, \nNOS MERECIMENTOS DO FILHO DE DEUS; E QUEM, \nPOIS, CONFIA EM JESUS, SALVADOR, \nVAI VÊ-LO EXALTADO NA GLÓRIA DOS CÉUS.\n\nCORO\nEXULTAI! EXULTAI! VINDE TODOS LOUVAR\nA JESUS SALVADOR, A JESUS REDENTOR!\nA DEUS DEMOS GLÓRIA PORQUANTO DO CÉU\nSEU FILHO BENDITO POR NÓS TODOS DEU.', 'A DEUS DEMOS GLÓRIA COM GRANDE FERVOR, SEU FILHO BENDITO POR NÓS TODOS DEU; A GRAÇA CONCEDE A QUALQUER PECADOR ABRINDO-LHE A PORTA DE ENTRADA NO CÉU. CORO EXULTAI! EXULTAI! VINDE TODOS LOUVAR A JESUS SALVADOR, A JESUS REDENTOR! A DEUS DEMOS GLÓRIA PORQUANTO DO CÉU SEU FILHO BENDITO POR NÓS TODOS DEU. OH! GRAÇA REAL! FOI ASSIM QUE JESUS MORRENDO, SEU SANGUE POR NÓS DERRAMOU; HERANÇA NOS CÉUS, COM OS SANTOS EM LUZ, COMPROU-NOS, JESUS, POIS O PREÇO PAGOU. A CRER NOS CONVIDA TAL PROVA DE AMOR, NOS MERECIMENTOS DO FILHO DE DEUS; E QUEM, POIS, CONFIA EM JESUS, SALVADOR, VAI VÊ-LO EXALTADO NA GLÓRIA DOS CÉUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('615', '615 - LOUVOR E GLÓRIA', 'LOUVOR E GLÓRIA E SABEDORIA\nE AÇÕES DE GRAÇA E HONRA E PODER\nE FORÇA AO NOSSO DEUS, AO NOSSO DEUS,\nPRA TODO O SEMPRE, AMÉM.\n\nREPETE O LOUVOR', 'LOUVOR E GLÓRIA E SABEDORIA E AÇÕES DE GRAÇA E HONRA E PODER E FORÇA AO NOSSO DEUS, AO NOSSO DEUS, PRA TODO O SEMPRE, AMÉM. REPETE O LOUVOR', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('616', '616 - AO DEUS DE AMOR E DE IMENSA BONDADE', 'AO DEUS DE AMOR E DE IMENSA BONDADE,\nCOM VOZ DE JÚBILO VINDE E ACLAMAI!\nCOM O CORAÇÃO TRANSBORDANTE DE GRAÇAS,\nSEU GRANDE AMOR, TODOS VINDE E LOUVAI!\n\nCORO\nNO CÉU, NA TERRA, QUE MARAVILHAS\nVAI OPERANDO O PODER DO SENHOR!\nMAS SEU AMOR AOS HOMENS PERDIDOS,\nDAS MARAVILHAS É SEMPRE A MAIOR!\n\nJÁ NOSSOS PAIS NOS CONTARAM \nA GLÓRIA DE DEUS, FALANDO COM MUITO PRAZER,\nQUE NAS TRISTEZAS, NOS GRANDES PERIGOS, \nELE OS SALVOU POR SEU GRANDE PODER!\n\nCORO\nNO CÉU, NA TERRA, QUE MARAVILHAS\nVAI OPERANDO O PODER DO SENHOR!\nMAS SEU AMOR AOS HOMENS PERDIDOS,\nDAS MARAVILHAS É SEMPRE A MAIOR!\n\nHOJE TAMBÉM NÓS BEM ALTO CANTAMOS\nQUE AS ORAÇÕES ELE NOS ATENDEU!\nSEU FORTE BRAÇO, QUE É TÃO COMPASSIVO, \nEM NOSSO AUXÍLIO ELE SEMPRE ESTENDEU!\n\nCORO\nNO CÉU, NA TERRA, QUE MARAVILHAS\nVAI OPERANDO O PODER DO SENHOR!\nMAS SEU AMOR AOS HOMENS PERDIDOS,\nDAS MARAVILHAS É SEMPRE A MAIOR!\n\nCOMO ATÉ HOJE E DAQUI PARA SEMPRE\nELE SERÁ NOSSO ETERNO PODER!\nNOSSO CASTELO BEM FORTE E SEGURO\nE NOSSA FONTE DE EXCELSO PRAZER!\n\nCORO\nNO CÉU, NA TERRA, QUE MARAVILHAS\nVAI OPERANDO O PODER DO SENHOR!\nMAS SEU AMOR AOS HOMENS PERDIDOS,\nDAS MARAVILHAS É SEMPRE A MAIOR!', 'AO DEUS DE AMOR E DE IMENSA BONDADE, COM VOZ DE JÚBILO VINDE E ACLAMAI! COM O CORAÇÃO TRANSBORDANTE DE GRAÇAS, SEU GRANDE AMOR, TODOS VINDE E LOUVAI! CORO NO CÉU, NA TERRA, QUE MARAVILHAS VAI OPERANDO O PODER DO SENHOR! MAS SEU AMOR AOS HOMENS PERDIDOS, DAS MARAVILHAS É SEMPRE A MAIOR! JÁ NOSSOS PAIS NOS CONTARAM A GLÓRIA DE DEUS, FALANDO COM MUITO PRAZER, QUE NAS TRISTEZAS, NOS GRANDES PERIGOS, ELE OS SALVOU POR SEU GRANDE PODER! HOJE TAMBÉM NÓS BEM ALTO CANTAMOS QUE AS ORAÇÕES ELE NOS ATENDEU! SEU FORTE BRAÇO, QUE É TÃO COMPASSIVO, EM NOSSO AUXÍLIO ELE SEMPRE ESTENDEU! COMO ATÉ HOJE E DAQUI PARA SEMPRE ELE SERÁ NOSSO ETERNO PODER! NOSSO CASTELO BEM FORTE E SEGURO E NOSSA FONTE DE EXCELSO PRAZER!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('617', '617 - O MEU CORAÇÃO SE REGOZIJA NO SENHOR', 'O MEU CORAÇÃO SE REGOZIJA NO SENHOR, \nO MEU PODER ESTÁ EXALTADO NO SENHOR;\nA MINHA BOCA SE ABRIRÁ EM GLORIFICAÇÃO \nPORQUE ME ALEGRO NA SUA SALVAÇÃO.\n\nCORO\nNÃO HÁ SANTO COMO O SENHOR,\nNÃO HÁ OUTRO ALÉM DE TI;\nROCHA INABALÁVEL É O NOSSO DEUS.\n\nINSTRUMENTOS\n\nO SENHOR É O QUE TIRA A VIDA E A DÁ\nE OS SEUS SANTOS ETERNAMENTE GUARDARÁ;\nELE EXALTARÁ DO SEU UNGIDO O PODER\nPORQUE O SENHOR É O NOSSO DEUS.\n\nCORO (2X)\nNÃO HÁ SANTO COMO O SENHOR,\nNÃO HÁ OUTRO ALÉM DE TI;\nROCHA INABALÁVEL É O NOSSO DEUS.', 'O MEU CORAÇÃO SE REGOZIJA NO SENHOR, O MEU PODER ESTÁ EXALTADO NO SENHOR; A MINHA BOCA SE ABRIRÁ EM GLORIFICAÇÃO PORQUE ME ALEGRO NA SUA SALVAÇÃO. CORO NÃO HÁ SANTO COMO O SENHOR, NÃO HÁ OUTRO ALÉM DE TI; ROCHA INABALÁVEL É O NOSSO DEUS. INSTRUMENTOS O SENHOR É O QUE TIRA A VIDA E A DÁ E OS SEUS SANTOS ETERNAMENTE GUARDARÁ; ELE EXALTARÁ DO SEU UNGIDO O PODER PORQUE O SENHOR É O NOSSO DEUS. NÃO HÁ SANTO COMO O SENHOR, NÃO HÁ OUTRO ALÉM DE TI; ROCHA INABALÁVEL É O NOSSO DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('618', '618 - CANTAREI AO MEU SENHOR ENQUANTO EU VIVER', 'CANTAREI AO MEU SENHOR ENQUANTO EU VIVER,\nCANTAREI LOUVORES ENQUANTO EXISTIR;\nENTÃO ME ALEGRAREI NO MEU SENHOR,\nMUI SUAVE SERÁ A MINHA MEDITAÇÃO.\n\nLOUVA, MINH’ALMA, \nAO SENHOR, BENDIZE-O JÁ.\n\n4X', 'CANTAREI AO MEU SENHOR ENQUANTO EU VIVER, CANTAREI LOUVORES ENQUANTO EXISTIR; ENTÃO ME ALEGRAREI NO MEU SENHOR, MUI SUAVE SERÁ A MINHA MEDITAÇÃO. LOUVA, MINH’ALMA, AO SENHOR, BENDIZE-O JÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('619', '619 - VEM, DERRAMA A PAZ', 'VEM, DERRAMA A PAZ,\nVEM, DERRAMA AS BÊNÇÃOS SOBRE ESTE POVO\nQUE SE CHAMA POVO TEU;\nDÁ-NOS TEU AMOR, DÁ-NOS TUA FORÇA \nPRA QUE TENTAÇÕES NÃO VENHAM \nA NOS DESVIAR.\n\nE A GLÓRIA SEJA DADA A TI PELO \nQUE TENS FEITO, PELO QUE TENS SIDO,\nPELO QUE FARÁS EM NÓS.\n\nINSTRUMENTOS\n\nVEM, DERRAMA A PAZ, VEM, DERRAMA \nAS BÊNÇÃOS SOBRE ESTE POVO \nQUE SE CHAMA POVO TEU;\nDÁ-NOS TEU AMOR, DÁ-NOS TUA FORÇA \nPRA QUE TENTAÇÕES NÃO VENHAM \nA NOS DESVIAR.\n\nE A GLÓRIA SEJA DADA A TI\nPELO QUE TENS FEITO,\nPELO QUE TENS SIDO,\nPELO QUE FARÁS EM NÓS.\n\nFINAL: \nPELO QUE FARÁS EM NÓS.', 'VEM, DERRAMA A PAZ, VEM, DERRAMA AS BÊNÇÃOS SOBRE ESTE POVO QUE SE CHAMA POVO TEU; DÁ-NOS TEU AMOR, DÁ-NOS TUA FORÇA PRA QUE TENTAÇÕES NÃO VENHAM A NOS DESVIAR. E A GLÓRIA SEJA DADA A TI PELO QUE TENS FEITO, PELO QUE TENS SIDO, PELO QUE FARÁS EM NÓS. INSTRUMENTOS E A GLÓRIA SEJA DADA A TI PELO QUE TENS FEITO, PELO QUE TENS SIDO, PELO QUE FARÁS EM NÓS.  PELO QUE FARÁS EM NÓS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('620', '620 - ESTAMOS AQUI, Ó DEUS', 'ESTAMOS AQUI, Ó DEUS, PRA LOUVAR \nO TEU NOME, POIS TU NOS TORNASTE \nSERVOS TEUS. LOUVEMOS A DEUS!\nA CAMINHO ESTAMOS DA NOVA JERUSALÉM, \nCIDADE ETERNAL QUE O PAI NOS PREPAROU.\nMARANATA! ORA VEM, SENHOR JESUS,\nVEM NOS BUSCAR.\n\nINSTRUMENTOS\n\nESTAMOS AQUI, Ó DEUS, PRA LOUVAR \nO TEU NOME, POIS TU NOS TORNASTE \nSERVOS TEUS. LOUVEMOS A DEUS!\nA CAMINHO ESTAMOS DA NOVA JERUSALÉM, \nCIDADE ETERNAL QUE O PAI NOS PREPAROU.\nMARANATA! ORA VEM, SENHOR JESUS,\nVEM NOS BUSCAR.\n\nFINAL: (2X)\nMARANATA! \nLOUVEMOS A DEUS,\nLOUVEMOS A DEUS, \nLOUVEMOS A DEUS.\nMARANATA!', 'ESTAMOS AQUI, Ó DEUS, PRA LOUVAR O TEU NOME, POIS TU NOS TORNASTE SERVOS TEUS. LOUVEMOS A DEUS! A CAMINHO ESTAMOS DA NOVA JERUSALÉM, CIDADE ETERNAL QUE O PAI NOS PREPAROU. MARANATA! ORA VEM, SENHOR JESUS, VEM NOS BUSCAR. INSTRUMENTOS ESTAMOS AQUI, Ó DEUS, PRA LOUVAR O TEU NOME, POIS TU NOS TORNASTE SERVOS TEUS. LOUVEMOS A DEUS! A CAMINHO ESTAMOS DA NOVA JERUSALÉM, CIDADE ETERNAL QUE O PAI NOS PREPAROU. MARANATA! ORA VEM, SENHOR JESUS, VEM NOS BUSCAR. MARANATA! LOUVEMOS A DEUS, LOUVEMOS A DEUS, LOUVEMOS A DEUS. MARANATA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('621', '621 - ADORAR-TE', 'ADORAR-TE É O MEU MAIOR PRAZER,\nSEGUIR-TE É O MEU VIVER.\n\nINSTRUMENTOS\n\nMEU CORAÇÃO TRANSBORDA, SENHOR,\nDA TUA GRAÇA E DO TEU AMOR;\nADORAR-TE É O MEU MAIOR PRAZER,\nSEGUIR-TE É O MEU VIVER.\n\nCORO\nNÃO HÁ DEUS COMO TU, Ó SENHOR,\nDEUS FORTE, PRÍNCIPE DA PAZ;\nTEU DOMÍNIO É ETERNO, SENHOR,\nTU REINAS SOBRE TERRA, CÉU E MAR,\nTU REINAS EM NÓS.\n\nINSTRUMENTOS\n\nNOS TEUS ÁTRIOS ENTRAMOS, SENHOR,\nCOM OS LÁBIOS CHEIOS DE LOUVOR,\nGRAÇAS TE DAMOS PELA PAZ, (PELA PAZ) \nPELAS BÊNÇÃOS,  (PELAS BÊNÇÃOS, SENHOR)\nPOR TÃO GRANDE SALVAÇÃO,\nPOR JESUS ENTRE NÓS.\n\nCORO (2X)\nNÃO HÁ DEUS COMO TU, Ó SENHOR,\nDEUS FORTE, PRÍNCIPE DA PAZ;\nTEU DOMÍNIO É ETERNO, SENHOR,\nTU REINAS SOBRE TERRA, CÉU E MAR,\nTU REINAS EM NÓS.', 'ADORAR-TE É O MEU MAIOR PRAZER, SEGUIR-TE É O MEU VIVER. INSTRUMENTOS MEU CORAÇÃO TRANSBORDA, SENHOR, DA TUA GRAÇA E DO TEU AMOR; ADORAR-TE É O MEU MAIOR PRAZER, SEGUIR-TE É O MEU VIVER. CORO NÃO HÁ DEUS COMO TU, Ó SENHOR, DEUS FORTE, PRÍNCIPE DA PAZ; TEU DOMÍNIO É ETERNO, SENHOR, TU REINAS SOBRE TERRA, CÉU E MAR, TU REINAS EM NÓS. INSTRUMENTOS NOS TEUS ÁTRIOS ENTRAMOS, SENHOR, COM OS LÁBIOS CHEIOS DE LOUVOR, GRAÇAS TE DAMOS PELA PAZ, (PELA PAZ) PELAS BÊNÇÃOS, (PELAS BÊNÇÃOS, SENHOR) POR TÃO GRANDE SALVAÇÃO, POR JESUS ENTRE NÓS. NÃO HÁ DEUS COMO TU, Ó SENHOR, DEUS FORTE, PRÍNCIPE DA PAZ; TEU DOMÍNIO É ETERNO, SENHOR, TU REINAS SOBRE TERRA, CÉU E MAR, TU REINAS EM NÓS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('622', '622 - A BELEZA DA TUA SANTIDADE', 'A BELEZA DA TUA SANTIDADE É MAIOR\nQUE AS MARAVILHAS DA TERRA,\nPOIS AQUI CADA UMA SE ENCERRA;\nTUA BELEZA, PORÉM, \nPOR TODA A ETERNIDADE É.\n\nCÉUS E TERRA PASSARÃO,\nTUA PALAVRA PERMANECERÁ,\nA MATÉRIA ENVELHECERÁ\nMAS TEU AMOR PRA SEMPRE EXISTIRÁ.\n\nA BELEZA DA TUA SANTIDADE É MAIOR\nQUE AS MARAVILHAS DA TERRA,\nPOIS AQUI CADA UMA SE ENCERRA;\nTUA BELEZA, PORÉM, \nPOR TODA A ETERNIDADE É.\n\nCÉUS E TERRA PASSARÃO,\nTUA PALAVRA PERMANECERÁ,\nA MATÉRIA ENVELHECERÁ\nMAS TEU AMOR PRA SEMPRE EXISTIRÁ.\n\nINSTRUMENTOS\n\nCÉUS E TERRA PASSARÃO...\n\nFINAL: \nTEU AMOR PRA SEMPRE EXISTIRÁ.', 'A BELEZA DA TUA SANTIDADE É MAIOR QUE AS MARAVILHAS DA TERRA, POIS AQUI CADA UMA SE ENCERRA; TUA BELEZA, PORÉM, POR TODA A ETERNIDADE É. CÉUS E TERRA PASSARÃO, TUA PALAVRA PERMANECERÁ, A MATÉRIA ENVELHECERÁ MAS TEU AMOR PRA SEMPRE EXISTIRÁ. A BELEZA DA TUA SANTIDADE É MAIOR QUE AS MARAVILHAS DA TERRA, POIS AQUI CADA UMA SE ENCERRA; TUA BELEZA, PORÉM, POR TODA A ETERNIDADE É. CÉUS E TERRA PASSARÃO, TUA PALAVRA PERMANECERÁ, A MATÉRIA ENVELHECERÁ MAS TEU AMOR PRA SEMPRE EXISTIRÁ. INSTRUMENTOS CÉUS E TERRA PASSARÃO...  TEU AMOR PRA SEMPRE EXISTIRÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('623', '623 - EU QUERO CANTAR DE GOZO E DE PAZ', 'EU QUERO CANTAR DE GOZO E DE PAZ,\nSENTIR A ALEGRIA QUE JESUS CRISTO TRAZ;\nEU QUERO SENTIR A PRESENÇA DO AMOR\nEM MEU CORAÇÃO, O SALVADOR.\n\nEU QUERO DIZER PARA O MUNDO\nQUE O SENHOR DEUS ENVIOU\nSEU FILHO A MORRER NUMA CRUZ,\nTUDO PORQUE ME AMOU;\nEU QUERO CHORAR DE ALEGRIA\nE VIVER A CANTAR,\nTUDO PORQUE MEU JESUS\nMORREU PARA ME SALVAR. \n\nINSTRUMENTOS\n\nEU QUERO CANTAR DE GOZO E DE PAZ,\nSENTIR A ALEGRIA QUE JESUS CRISTO TRAZ;\nEU QUERO SENTIR A PRESENÇA DO AMOR\nEM MEU CORAÇÃO, O SALVADOR.\n\nEU QUERO DIZER PARA O MUNDO\nQUE O SENHOR DEUS ENVIOU\nSEU FILHO A MORRER NUMA CRUZ,\nTUDO PORQUE ME AMOU;\nEU QUERO CHORAR DE ALEGRIA\nE VIVER A CANTAR,\nTUDO PORQUE MEU JESUS\nMORREU PARA ME SALVAR.\nBIS', 'EU QUERO CANTAR DE GOZO E DE PAZ, SENTIR A ALEGRIA QUE JESUS CRISTO TRAZ; EU QUERO SENTIR A PRESENÇA DO AMOR EM MEU CORAÇÃO, O SALVADOR. EU QUERO DIZER PARA O MUNDO QUE O SENHOR DEUS ENVIOU SEU FILHO A MORRER NUMA CRUZ, TUDO PORQUE ME AMOU; EU QUERO CHORAR DE ALEGRIA E VIVER A CANTAR, TUDO PORQUE MEU JESUS MORREU PARA ME SALVAR. INSTRUMENTOS EU QUERO DIZER PARA O MUNDO QUE O SENHOR DEUS ENVIOU SEU FILHO A MORRER NUMA CRUZ, TUDO PORQUE ME AMOU; EU QUERO CHORAR DE ALEGRIA E VIVER A CANTAR, TUDO PORQUE MEU JESUS MORREU PARA ME SALVAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('624', '624 - A MINH’ALMA ENGRANDECE', 'A MINH’ALMA ENGRANDECE\nAO MEU SENHOR E REI. \n\nCORO\nA QUEM MAIS ENGRANDECEREI\nDO QUE A JESUS, O SENHOR?\nBIS\nBIS\n\nDEU-ME LÍNGUA NOVA PRA LOUVÁ-LO, \nELE É O SENHOR. \n\nCORO\nA QUEM MAIS ENGRANDECEREI \nDO QUE A JESUS, O SENHOR?\nBIS\nBIS\n\nSEI QUE ELE VOLTA PRA BUSCAR-ME, \nLEVAR-ME AO CÉU. \n\nCORO\nA QUEM MAIS ENGRANDECEREI \nDO QUE A JESUS, O SENHOR?\nBIS\nBIS', 'A MINH’ALMA ENGRANDECE AO MEU SENHOR E REI. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR? DEU-ME LÍNGUA NOVA PRA LOUVÁ-LO, ELE É O SENHOR. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR? SEI QUE ELE VOLTA PRA BUSCAR-ME, LEVAR-ME AO CÉU. CORO A QUEM MAIS ENGRANDECEREI DO QUE A JESUS, O SENHOR?', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('625', '625 - DIGNO É JESUS', 'DIGNO É JESUS DE TODA A GLÓRIA,\nDIGNO É DE TODO O LOUVOR;\nTODA A TERRA EXALTE O SEU NOME\nPOIS ELE É REI DOS REIS E É SENHOR.\nPROSTREMO-NOS DIANTE DO SEU TRONO\nPOIS TODO JOELHO HÁ DE SE DOBRAR\nE TODA LÍNGUA VAI CONFESSAR \nQUE ELE É SENHOR, \nO REI QUE EM TODA A TERRA VAI REINAR.\n\nA TI DAMOS O LOUVOR E A HONRA,\nSENHOR JESUS, SENHOR JESUS!\nNOS PROSTRAMOS DIANTE DA TUA GLÓRIA,\nSENHOR JESUS, SENHOR JESUS!\nSENHOR JESUS, REI DOS REIS,\nO SENHOR!', 'DIGNO É JESUS DE TODA A GLÓRIA, DIGNO É DE TODO O LOUVOR; TODA A TERRA EXALTE O SEU NOME POIS ELE É REI DOS REIS E É SENHOR. PROSTREMO-NOS DIANTE DO SEU TRONO POIS TODO JOELHO HÁ DE SE DOBRAR E TODA LÍNGUA VAI CONFESSAR QUE ELE É SENHOR, O REI QUE EM TODA A TERRA VAI REINAR. A TI DAMOS O LOUVOR E A HONRA, SENHOR JESUS, SENHOR JESUS! NOS PROSTRAMOS DIANTE DA TUA GLÓRIA, SENHOR JESUS, SENHOR JESUS! SENHOR JESUS, REI DOS REIS, O SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('626', '626 - OH! CONTEMPLAI', 'OH! CONTEMPLAI E VEDE ESTE AMOR,\nO AMOR DE JESUS CRISTO POR NÓS;\nSE DEU, SE ENTREGOU POR TI E POR MIM, \nNOS RESGATOU, COM SEU SANGUE \nNOS COMPROU.\n\nDA MORTE ELE É O VENCEDOR,\nDA VIDA ETERNA ELE É SENHOR,\nPALAVRA DE DEUS É O SEU NOME,\nREI DOS REIS, SENHOR DOS SENHORES.\n\nDIGNO É DE RECEBER A GLÓRIA \nE O LOUVOR E AÇÕES DE GRAÇAS;\nDIGNO É DE RECEBER A HONRA \nE O PODER PARA TODO O SEMPRE, JESUS.', 'OH! CONTEMPLAI E VEDE ESTE AMOR, O AMOR DE JESUS CRISTO POR NÓS; SE DEU, SE ENTREGOU POR TI E POR MIM, NOS RESGATOU, COM SEU SANGUE NOS COMPROU. DA MORTE ELE É O VENCEDOR, DA VIDA ETERNA ELE É SENHOR, PALAVRA DE DEUS É O SEU NOME, REI DOS REIS, SENHOR DOS SENHORES. DIGNO É DE RECEBER A GLÓRIA E O LOUVOR E AÇÕES DE GRAÇAS; DIGNO É DE RECEBER A HONRA E O PODER PARA TODO O SEMPRE, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('627', '627 - NÓS TE ADORAMOS, SENHOR', 'NÓS TE ADORAMOS, SENHOR, \nPOIS ÉS DIGNO DE LOUVOR, \nNOSSO REI E SALVADOR, NOSSO SENHOR.\nA TUA GLÓRIA ESTÁ NESTE LUGAR,\nA TUA PRESENÇA FAZ TRANSBORDAR\nO NOSSO CORAÇÃO DE LOUVOR.\nINSTRUMENTOS\n\nCORO \nA TUA GLÓRIA (GLÓRIA)\nSE ESTENDE SOBRE OS CÉUS\n(SE ESTENDE SOBRE OS CÉUS) \nE O TEU LOUVOR (E O TEU LOUVOR) \nAOS CONFINS DA TERRA.\n\nCORO \nA TUA GLÓRIA (GLÓRIA)\nSE ESTENDE SOBRE OS CÉUS\n(SE ESTENDE SOBRE OS CÉUS) \nE O TEU LOUVOR (E O TEU LOUVOR) \nAOS CONFINS DA TERRA.\n(SE FAZ OUVIR)\n\nINSTRUMENTOS\n\nEM SANTIDADE, Ó SENHOR,\nQUEREMOS TE ADORAR,\nEM UMA SÓ VOZ TUA GLÓRIA EXALTAR.\n\nCORO (2X)\nA TUA GLÓRIA (GLÓRIA)\nSE ESTENDE SOBRE OS CÉUS\n(SE ESTENDE SOBRE OS CÉUS) \nE O TEU LOUVOR (E O TEU LOUVOR) \nAOS CONFINS DA TERRA.\n\nFINAL:\nA TUA GLÓRIA ESTÁ NESTE LUGAR,\nA TUA PRESENÇA FAZ TRANSBORDAR\nO NOSSO CORAÇÃO DE LOUVOR.', 'NÓS TE ADORAMOS, SENHOR, POIS ÉS DIGNO DE LOUVOR, NOSSO REI E SALVADOR, NOSSO SENHOR. A TUA GLÓRIA ESTÁ NESTE LUGAR, A TUA PRESENÇA FAZ TRANSBORDAR O NOSSO CORAÇÃO DE LOUVOR. INSTRUMENTOS CORO A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA. CORO A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA. (SE FAZ OUVIR) INSTRUMENTOS EM SANTIDADE, Ó SENHOR, QUEREMOS TE ADORAR, EM UMA SÓ VOZ TUA GLÓRIA EXALTAR. A TUA GLÓRIA (GLÓRIA) SE ESTENDE SOBRE OS CÉUS (SE ESTENDE SOBRE OS CÉUS) E O TEU LOUVOR (E O TEU LOUVOR) AOS CONFINS DA TERRA. A TUA GLÓRIA ESTÁ NESTE LUGAR, A TUA PRESENÇA FAZ TRANSBORDAR O NOSSO CORAÇÃO DE LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('628', '628 - SANTO É SEU NOME', 'SANTO É SEU NOME,\nTAL QUAL ESSE EXCELSO \nNOME JAMAIS OUVI;\nDOCE, PURO E PERFEITO É,\nESSE NOME SE TRADUZ EM LUZ,\nEM CHORAR E EM SORRIR.\n\nCORO\nSANTO NOME DE JESUS,\nMAIS MARAVILHOSO \nQUE OUTRO QUALQUER;\nSANTO NOME DE JESUS,\nDÁ CONSOLAÇÃO E DOCE AMOR\nSEJA NO LUGAR QUE FOR,\nSANTO É O NOME DE JESUS.\n\nINSTRUMENTOS\n\nSANTO É SEU NOME,\nMAIS PRECIOSO QUE RIQUEZAS\nDO MUNDO, SIM;\nSOA EM MIM EM PERFEITO TOM\nTAL QUAL EM CELESTIAL CORO \nQUE NUNCA UM HOMEM \nPÔDE OUVIR.\n\nCORO (2X)\nSANTO NOME DE JESUS,\nMAIS MARAVILHOSO \nQUE OUTRO QUALQUER;\nSANTO NOME DE JESUS,\nDÁ CONSOLAÇÃO E DOCE AMOR\nSEJA NO LUGAR QUE FOR,\nSANTO É O NOME DE JESUS.', 'SANTO É SEU NOME, TAL QUAL ESSE EXCELSO NOME JAMAIS OUVI; DOCE, PURO E PERFEITO É, ESSE NOME SE TRADUZ EM LUZ, EM CHORAR E EM SORRIR. CORO SANTO NOME DE JESUS, MAIS MARAVILHOSO QUE OUTRO QUALQUER; SANTO NOME DE JESUS, DÁ CONSOLAÇÃO E DOCE AMOR SEJA NO LUGAR QUE FOR, SANTO É O NOME DE JESUS. INSTRUMENTOS SANTO É SEU NOME, MAIS PRECIOSO QUE RIQUEZAS DO MUNDO, SIM; SOA EM MIM EM PERFEITO TOM TAL QUAL EM CELESTIAL CORO QUE NUNCA UM HOMEM PÔDE OUVIR. SANTO NOME DE JESUS, MAIS MARAVILHOSO QUE OUTRO QUALQUER; SANTO NOME DE JESUS, DÁ CONSOLAÇÃO E DOCE AMOR SEJA NO LUGAR QUE FOR, SANTO É O NOME DE JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('629', '629 - QUE ALEGRIA', 'QUE ALEGRIA É OUVIR O TEU POVO TODO CANTANDO\nDE FELICIDADE, REGOZIJANDO DIANTE DO ALTAR;\nQUE ALEGRIA VER O TEU POVO TODO ADORANDO \nA TI, Ó GRANDE DEUS,  E AO TEU FILHO JESUS \nQUE NOS VEIO SALVAR.\n\nQUE ALEGRIA É ESTAR \nNA TUA CASA TODOS OS DIAS, \nCOM OS IRMÃOS REUNIDOS, \nÉ A BÊNÇÃO DO ESPÍRITO A TRANSBORDAR;\nQUE ALEGRIA É DEUS OPERANDO NA \nSUA IGREJA SALVANDO E LIBERTANDO,\nORIENTANDO E CURANDO \nTODO O QUE A ELE VEM.\n\nQUE ALEGRIA É ESTAR NA TUA CASA \nTODOS OS DIAS, COM OS IRMÃOS REUNIDOS, \nÉ A BÊNÇÃO DO ESPÍRITO A TRANSBORDAR;\nQUE ALEGRIA É DEUS OPERANDO NA SUA IGREJA\nSALVANDO E LIBERTANDO\nORIENTANDO E CURANDO\nTODO O QUE A ELE VEM.\nBIS', 'QUE ALEGRIA É OUVIR O TEU POVO TODO CANTANDO DE FELICIDADE, REGOZIJANDO DIANTE DO ALTAR; QUE ALEGRIA VER O TEU POVO TODO ADORANDO A TI, Ó GRANDE DEUS, E AO TEU FILHO JESUS QUE NOS VEIO SALVAR. QUE ALEGRIA É ESTAR NA TUA CASA TODOS OS DIAS, COM OS IRMÃOS REUNIDOS, É A BÊNÇÃO DO ESPÍRITO A TRANSBORDAR; QUE ALEGRIA É DEUS OPERANDO NA SUA IGREJA SALVANDO E LIBERTANDO, ORIENTANDO E CURANDO TODO O QUE A ELE VEM. QUE ALEGRIA É ESTAR NA TUA CASA TODOS OS DIAS, COM OS IRMÃOS REUNIDOS, É A BÊNÇÃO DO ESPÍRITO A TRANSBORDAR; QUE ALEGRIA É DEUS OPERANDO NA SUA IGREJA SALVANDO E LIBERTANDO ORIENTANDO E CURANDO TODO O QUE A ELE VEM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('630', '630 - SAUDAI O NOME DE JESUS', 'SAUDAI O NOME DE JESUS!\nARCANJOS VOS PROSTRAI; (BIS)\nAO FILHO DO ETERNO DEUS\nCOM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, \nCOM GLÓRIA COROAI!\n\nÓ ESCOLHIDA GERAÇÃO\nDO BOM E ETERNO PAI; (BIS)\nAO GRANDE AUTOR DA SALVAÇÃO\nCOM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, \nCOM GLÓRIA COROAI!\n\nÓ PERDOADOS, CUJO AMOR\nBEM TRIUNFANTE VAI; (BIS)\nAO DEUS VARÃO, CONQUISTADOR\nCOM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, \nCOM GLÓRIA COROAI!\n\nÓ RAÇAS, TRIBOS E NAÇÕES,\nAO REI DIVINO HONRAI! (BIS)\nA QUEM QUEBROU VOSSOS GRILHÕES,\nCOM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, \nCOM GLÓRIA COROAI!', 'SAUDAI O NOME DE JESUS! ARCANJOS VOS PROSTRAI;  AO FILHO DO ETERNO DEUS COM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, COM GLÓRIA COROAI! Ó ESCOLHIDA GERAÇÃO DO BOM E ETERNO PAI;  AO GRANDE AUTOR DA SALVAÇÃO COM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, COM GLÓRIA COROAI! Ó PERDOADOS, CUJO AMOR BEM TRIUNFANTE VAI;  AO DEUS VARÃO, CONQUISTADOR COM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, COM GLÓRIA COROAI! Ó RAÇAS, TRIBOS E NAÇÕES, AO REI DIVINO HONRAI!  A QUEM QUEBROU VOSSOS GRILHÕES, COM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, COM GLÓRIA COROAI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('631', '631 - SÊ EXALTADO, Ó DEUS', 'CORO\nSÊ EXALTADO, Ó DEUS, SOBRE OS CÉUS,\nSEJA TUA GLÓRIA SOBRE TODA A TERRA.\n\nGRANDE E MAJESTOSO, \nTODA A TERRA E O MAR PERTENCEM SÓ A TI;\nJUSTO, TÃO PODEROSO, MESMO DIANTE \nDE TUA GLÓRIA POSSO \nDIZER QUE TU VIVES EM MIM.\n\nCORO\nSÊ EXALTADO, Ó DEUS, SOBRE OS CÉUS,\nSEJA TUA GLÓRIA SOBRE TODA A TERRA.\n\nINSTRUMENTOS\n\nTU ÉS PERFEITO, SANTO E SOBERANO DEUS, \nDE TODA A TERRA TU ÉS REI;\nETERNO E TRIUNO, SUPREMO CRIADOR,\nA TI PRA SEMPRE CANTAREI;\nLOUVAR-TE-EI, SENHOR, ENTRE OS POVOS, \nCANTAR-TE-EI LOUVORES ENTRE AS NAÇÕES.\n\nCORO\nSÊ EXALTADO, Ó DEUS, SOBRE OS CÉUS,\nSEJA TUA GLÓRIA SOBRE TODA A TERRA.', 'CORO SÊ EXALTADO, Ó DEUS, SOBRE OS CÉUS, SEJA TUA GLÓRIA SOBRE TODA A TERRA. GRANDE E MAJESTOSO, TODA A TERRA E O MAR PERTENCEM SÓ A TI; JUSTO, TÃO PODEROSO, MESMO DIANTE DE TUA GLÓRIA POSSO DIZER QUE TU VIVES EM MIM. CORO SÊ EXALTADO, Ó DEUS, SOBRE OS CÉUS, SEJA TUA GLÓRIA SOBRE TODA A TERRA. INSTRUMENTOS TU ÉS PERFEITO, SANTO E SOBERANO DEUS, DE TODA A TERRA TU ÉS REI; ETERNO E TRIUNO, SUPREMO CRIADOR, A TI PRA SEMPRE CANTAREI; LOUVAR-TE-EI, SENHOR, ENTRE OS POVOS, CANTAR-TE-EI LOUVORES ENTRE AS NAÇÕES.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('632', '632 - MUITOS ANJOS LOUVAM', 'MUITOS ANJOS LOUVAM\nA JESUS, O SALVADOR,\nENTOANDO ALELUIA\nÀQUELE QUE VIVO ESTÁ.\n\nCORO\nSANTO, SANTO, SANTO,\nSANTO ÉS, JESUS.\nINSTRUMENTOS\n\nNAS MANSÕES CELESTIAIS\nONDE OS SALVOS POR JESUS\nCOM UM BRADO DE VITÓRIA\nCANTAM EM UMA SÓ VOZ.\n\nCORO\nSANTO, SANTO, SANTO,\nSANTO ÉS, JESUS.\nINSTRUMENTOS\n\nNADA PODE COMPARAR\nCOM A GLÓRIA DO SENHOR,\nPARA OS SEUS ESCOLHIDOS\nUMA MORADA PREPAROU.\n\nCORO (2X)\nSANTO, SANTO, SANTO,\nSANTO ÉS, JESUS.\n\nFINAL: SANTO ÉS, JESUS.', 'MUITOS ANJOS LOUVAM A JESUS, O SALVADOR, ENTOANDO ALELUIA ÀQUELE QUE VIVO ESTÁ. CORO SANTO, SANTO, SANTO, SANTO ÉS, JESUS. INSTRUMENTOS NAS MANSÕES CELESTIAIS ONDE OS SALVOS POR JESUS COM UM BRADO DE VITÓRIA CANTAM EM UMA SÓ VOZ. CORO SANTO, SANTO, SANTO, SANTO ÉS, JESUS. INSTRUMENTOS NADA PODE COMPARAR COM A GLÓRIA DO SENHOR, PARA OS SEUS ESCOLHIDOS UMA MORADA PREPAROU.  SANTO, SANTO, SANTO, SANTO ÉS, JESUS.  SANTO ÉS, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('633', '633 - TE EXALTAREMOS', 'TE EXALTAREMOS PARA SEMPRE, Ó DEUS,\nETERNAMENTE COM NOSSO LOUVOR;\nAGRADECIDOS PELO QUE TENS FEITO,\nPELOS LIVRAMENTOS E PROVAÇÕES.\n\nCORO\nNÓS TE ADORAMOS E ENTREGAMOS\nEM GRATIDÃO, SENHOR AS NOSSAS VIDAS\nEM SACRIFÍCIO VIVO DE LOUVOR\nOFERECEMOS SEMPRE A TI, SENHOR.\n\nCOM NOSSOS LÁBIOS EXPRESSAREMOS\nNOSSA GRATIDÃO A TI, SENHOR,\nE A NOSSA ALEGRIA SERÁ RENOVADA\nA CADA INSTANTE POR TEU LOUVOR.\n\nCORO\nNÓS TE ADORAMOS E ENTREGAMOS\nEM GRATIDÃO, SENHOR AS NOSSAS VIDAS\nEM SACRIFÍCIO VIVO DE LOUVOR\nOFERECEMOS SEMPRE A TI, SENHOR.\n\nFINAL:\nNÓS TE ADORAMOS E ENTREGAMOS\nEM GRATIDÃO, SENHOR, AS NOSSAS VIDAS,\nE A NOSSA ALEGRIA SERÁ RENOVADA\nA CADA INSTANTE POR TEU LOUVOR.', 'TE EXALTAREMOS PARA SEMPRE, Ó DEUS, ETERNAMENTE COM NOSSO LOUVOR; AGRADECIDOS PELO QUE TENS FEITO, PELOS LIVRAMENTOS E PROVAÇÕES. CORO NÓS TE ADORAMOS E ENTREGAMOS EM GRATIDÃO, SENHOR AS NOSSAS VIDAS EM SACRIFÍCIO VIVO DE LOUVOR OFERECEMOS SEMPRE A TI, SENHOR. COM NOSSOS LÁBIOS EXPRESSAREMOS NOSSA GRATIDÃO A TI, SENHOR, E A NOSSA ALEGRIA SERÁ RENOVADA A CADA INSTANTE POR TEU LOUVOR. NÓS TE ADORAMOS E ENTREGAMOS EM GRATIDÃO, SENHOR, AS NOSSAS VIDAS, E A NOSSA ALEGRIA SERÁ RENOVADA A CADA INSTANTE POR TEU LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
