INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('635', '635 - ENTRONIZADO ESTÁ ENTRE AS NAÇÕES', 'ENTRONIZADO ESTÁ ENTRE AS NAÇÕES (BIS)\nPROCLAMAI A JESUS, O REI, \nO LEÃO DE JUDÁ \nQUE VENCEU E O LIVRO ABRIRÁ.\n\nCORO (2X)\nAO GRANDE REI, \nJESUS SENHOR, EXALTAI.\n\nINSTRUMENTOS\n\nANUNCIAI QUE JESUS VIRÁ\nE QUEM VENCER TUDO HERDARÁ,\nUM NOVO CÉU, UMA COROA DE GLÓRIA\nE EM RUAS DE OURO ANDAR.\n\nCORO (3X)\nAO GRANDE REI, \nJESUS SENHOR, EXALTAI.\n\nFINAL: \nAO GRANDE REI, EXALTAI.', 'ENTRONIZADO ESTÁ ENTRE AS NAÇÕES  PROCLAMAI A JESUS, O REI, O LEÃO DE JUDÁ QUE VENCEU E O LIVRO ABRIRÁ. AO GRANDE REI, JESUS SENHOR, EXALTAI. INSTRUMENTOS ANUNCIAI QUE JESUS VIRÁ E QUEM VENCER TUDO HERDARÁ, UM NOVO CÉU, UMA COROA DE GLÓRIA E EM RUAS DE OURO ANDAR. CORO  AO GRANDE REI, JESUS SENHOR, EXALTAI.  AO GRANDE REI, EXALTAI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('636', '636 - LOUVAI A JESUS', '“LOUVAI A JESUS, O REI DOS REIS,\nLOUVAI O REI DOS REIS”,\nDIZ O ESPÍRITO DO SENHOR;\nPELAS OBRAS QUE FEZ, \nPELO SEU GRANDE AMOR, CANTAI, CANTAI;\nLOUVAI AO SALVADOR, LOUVAI, LOUVAI,\nBIS\n\nPELOS BENEFÍCIOS, LOUVAI, POR SUAS MARAVILHAS, \nLOUVAI, PELO GRANDE AMOR, LOUVAI, \nPELA SALVAÇÃO, LOUVAI,\nLOUVAI AO SALVADOR, LOUVAI, LOUVAI.\n\n“LOUVAI A JESUS, O REI DOS REIS,\nLOUVAI O REI DOS REIS”,\nDIZ O ESPÍRITO DO SENHOR;\nPELAS OBRAS QUE FEZ, \nPELO SEU GRANDE AMOR, CANTAI, CANTAI;\nLOUVAI AO SALVADOR, LOUVAI, LOUVAI,\nBIS\n\nPELOS BENEFÍCIOS, LOUVAI,\nPOR SUAS MARAVILHAS, LOUVAI,\nPELO GRANDE AMOR, LOUVAI,\nPELA SALVAÇÃO, LOUVAI,\nLOUVAI AO SALVADOR, LOUVAI, LOUVAI.\n\nFINAL: (2X)\nLOUVAI A JESUS, O REI DOS REIS,\nLOUVAI O REI DOS REIS.', 'LOUVAI A JESUS, O REI DOS REIS, LOUVAI O REI DOS REIS, DIZ O ESPÍRITO DO SENHOR; PELAS OBRAS QUE FEZ, PELO SEU GRANDE AMOR, CANTAI, CANTAI; LOUVAI AO SALVADOR, LOUVAI, LOUVAI, PELOS BENEFÍCIOS, LOUVAI, POR SUAS MARAVILHAS, LOUVAI, PELO GRANDE AMOR, LOUVAI, PELA SALVAÇÃO, LOUVAI, LOUVAI AO SALVADOR, LOUVAI, LOUVAI. PELOS BENEFÍCIOS, LOUVAI, POR SUAS MARAVILHAS, LOUVAI, PELO GRANDE AMOR, LOUVAI, PELA SALVAÇÃO, LOUVAI, LOUVAI AO SALVADOR, LOUVAI, LOUVAI.   LOUVAI A JESUS, O REI DOS REIS, LOUVAI O REI DOS REIS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('637', '637 - TODA A TERRA TE EXALTA', 'TODA A TERRA TE EXALTA, \nENGRANDECE O TEU NOME \nPELAS GRANDES MARAVILHAS QUE TENS FEITO;\nCOM PODER DOMINAS O MUNDO, \nREINAS SOBRE CÉU E MAR, \nÉS O CORDEIRO PROMETIDO \nQUE VEIO PRA SALVAR.\n\nCORO (2X) \nENGRANDECIDO SEJA O TEU NOME\nPOIS ÉS GRANDE ENTRE AS NAÇÕES,\nTE EXALTAREI ENQUANTO EU VIVER.\n\nINSTRUMENTOS\n\nOS MEUS LÁBIOS PRONUNCIAM \nTUA VERDADE DIA E NOITE, \nÉS O GRANDE EU SOU, \nETERNO EL-SHADAI;\nPOR AMOR VERTESTE SANGUE \nPRA DAR VIDA AO PECADOR; \nLOUVAREMOS AO TEU NOME, \nPOIS ÉS DIGNO DE LOUVOR.\n\nCORO (2X) \nENGRANDECIDO SEJA O TEU NOME\nPOIS ÉS GRANDE ENTRE AS NAÇÕES,\nTE EXALTAREI ENQUANTO EU VIVER.\n\nTE EXALTAREI ENQUANTO EU VIVER!\nENGRANDECIDO SEJA O TEU NOME, \nSENHOR REI, SENHOR, REDENTOR; \nLOUVAREI PARA SEMPRE O TEU NOME \nENQUANTO EM MIM HOUVER FÔLEGO DE VIDA; ENTRONIZADO ESTÁS ENTRE OS QUERUBINS, (M) \nTE LOUVAREI ATÉ O FIM, (H)\n(SIM, EU LOUVAREI)\nTE LOUVAREI ATÉ O FIM \n(HONRA E LOUVOR)\nTE LOUVAREI, SENHOR, ATÉ O FIM.', 'TODA A TERRA TE EXALTA, ENGRANDECE O TEU NOME PELAS GRANDES MARAVILHAS QUE TENS FEITO; COM PODER DOMINAS O MUNDO, REINAS SOBRE CÉU E MAR, ÉS O CORDEIRO PROMETIDO QUE VEIO PRA SALVAR. ENGRANDECIDO SEJA O TEU NOME POIS ÉS GRANDE ENTRE AS NAÇÕES, TE EXALTAREI ENQUANTO EU VIVER. INSTRUMENTOS OS MEUS LÁBIOS PRONUNCIAM TUA VERDADE DIA E NOITE, ÉS O GRANDE EU SOU, ETERNO EL-SHADAI; POR AMOR VERTESTE SANGUE PRA DAR VIDA AO PECADOR; LOUVAREMOS AO TEU NOME, POIS ÉS DIGNO DE LOUVOR. ENGRANDECIDO SEJA O TEU NOME POIS ÉS GRANDE ENTRE AS NAÇÕES, TE EXALTAREI ENQUANTO EU VIVER. TE EXALTAREI ENQUANTO EU VIVER! ENGRANDECIDO SEJA O TEU NOME, SENHOR REI, SENHOR, REDENTOR; LOUVAREI PARA SEMPRE O TEU NOME ENQUANTO EM MIM HOUVER FÔLEGO DE VIDA; ENTRONIZADO ESTÁS ENTRE OS QUERUBINS,  TE LOUVAREI ATÉ O FIM,  (SIM, EU LOUVAREI) TE LOUVAREI ATÉ O FIM (HONRA E LOUVOR) TE LOUVAREI, SENHOR, ATÉ O FIM