INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('638', '638 - JESUS VEIO E SALVOU-ME', 'JESUS VEIO E SALVOU-ME E ASSIM TRANSFORMOU \nO MEU VIVER, AGORA POSSO ADORÁ-LO\nE CONFIAR POIS É O MEU SALVADOR.\n\nCORO\nDIGNO, SÓ ELE É DIGNO DE RECEBER \nHONRA E LOUVOR, POIS COMIGO ELE ESTÁ, \nNUNCA VAI ME DEIXAR;\nDIGNO É O SENHOR.\n\nINSTRUMENTOS\n\nVITÓRIAS ELE CONCEDE-ME\nE COM BÊNÇÃOS ENVOLVE O MEU SER\nE EM MIM RENOVA A CADA DIA\nA CERTEZA DA ETERNA SALVAÇÃO.\n\nCORO (2X)\nDIGNO, SÓ ELE É DIGNO DE RECEBER \nHONRA E LOUVOR, POIS COMIGO ELE ESTÁ, \nNUNCA VAI ME DEIXAR;\nDIGNO É O SENHOR.\n\nFINAL: DIGNO É O SENHOR. (3X)', 'JESUS VEIO E SALVOU-ME E ASSIM TRANSFORMOU O MEU VIVER, AGORA POSSO ADORÁ-LO E CONFIAR POIS É O MEU SALVADOR. CORO DIGNO, SÓ ELE É DIGNO DE RECEBER HONRA E LOUVOR, POIS COMIGO ELE ESTÁ, NUNCA VAI ME DEIXAR; DIGNO É O SENHOR. INSTRUMENTOS VITÓRIAS ELE CONCEDE-ME E COM BÊNÇÃOS ENVOLVE O MEU SER E EM MIM RENOVA A CADA DIA A CERTEZA DA ETERNA SALVAÇÃO. DIGNO, SÓ ELE É DIGNO DE RECEBER HONRA E LOUVOR, POIS COMIGO ELE ESTÁ, NUNCA VAI ME DEIXAR; DIGNO É O SENHOR.  DIGNO É O SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('639', '639 - MOTIVOS TENHO PRA LOUVAR-TE', 'MOTIVOS TENHO PRA LOUVAR-TE, Ó DEUS,\nMOTIVOS TENHO PRA DIZER: GLÓRIA A DEUS!\nMOTIVOS TENHO PRA DIZER QUE TU NÃO MUDASTE,\nMOTIVOS TENHO PRA FALAR DO TEU AMOR.\n\nPELO QUE TU FIZESTE NA TUA OBRA,\nPELO TEU POVO QUE A TI SE DEDICOU,\nNA EXISTÊNCIA VIVA DO ESPÍRITO.\n\nPOR TUA OBRA E TEU PODER, \nPELO TEU POVO SUSTENTADO AQUI.\nPOR TUA OBRA E TEU PODER, \nPELO TEU POVO SUSTENTADO AQUI.', 'MOTIVOS TENHO PRA LOUVAR-TE, Ó DEUS, MOTIVOS TENHO PRA DIZER: GLÓRIA A DEUS! MOTIVOS TENHO PRA DIZER QUE TU NÃO MUDASTE, MOTIVOS TENHO PRA FALAR DO TEU AMOR. PELO QUE TU FIZESTE NA TUA OBRA, PELO TEU POVO QUE A TI SE DEDICOU, NA EXISTÊNCIA VIVA DO ESPÍRITO. POR TUA OBRA E TEU PODER, PELO TEU POVO SUSTENTADO AQUI. POR TUA OBRA E TEU PODER, PELO TEU POVO SUSTENTADO AQUI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('640', '640 - MEU LOUVOR É SÓ PRA TI', 'MEU LOUVOR É SÓ PRA TI, SENHOR, \nMEU SALVADOR,\nGRANDE AUTOR DA MINHA FÉ;\nTODA A MINHA ADORAÇÃO,\nSENHOR, PERTENCE A TI,\nMEU ETERNO REDENTOR.\n\nPORQUE MUDASTE O MEU VIVER, \nE ME GUIASTE NA TUA LUZ,\nPORQUE COMPRASTE A MINHA VIDA, \nMEU SENHOR, SOU GRATO, SENHOR.\n\nMEU LOUVOR É SÓ PRA TI, SENHOR, \nMEU SALVADOR, GRANDE AUTOR DA MINHA FÉ;\nTODA A MINHA ADORAÇÃO, SENHOR, \nPERTENCE A TI, MEU ETERNO REDENTOR.\n\nPORQUE MUDASTE O MEU VIVER\nE ME GUIASTE NA TUA LUZ,\nPORQUE COMPRASTE A MINHA VIDA, \nMEU SENHOR,\nSOU GRATO, JESUS,\n(SOU GRATO, JESUS) \nSOU GRATO, JESUS,\n(SOU GRATO, JESUS) \nSOU GRATO, JESUS.', 'MEU LOUVOR É SÓ PRA TI, SENHOR, MEU SALVADOR, GRANDE AUTOR DA MINHA FÉ; TODA A MINHA ADORAÇÃO, SENHOR, PERTENCE A TI, MEU ETERNO REDENTOR. PORQUE MUDASTE O MEU VIVER, E ME GUIASTE NA TUA LUZ, PORQUE COMPRASTE A MINHA VIDA, MEU SENHOR, SOU GRATO, SENHOR. PORQUE MUDASTE O MEU VIVER E ME GUIASTE NA TUA LUZ, PORQUE COMPRASTE A MINHA VIDA, MEU SENHOR, SOU GRATO, JESUS, (SOU GRATO, JESUS) SOU GRATO, JESUS, (SOU GRATO, JESUS) SOU GRATO, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('641', '641 - A LUZ DO MUNDO', 'A LUZ DO MUNDO, O LÍRIO DOS VALES, \nA FONTE DE ÁGUAS VIVAS, EMANUEL;\nO VERBO VIVO, RAIZ DE DAVI,\nESTRELA DA MANHÃ, REI DOS REIS.\nCOMO CANTAR O NOSSO AMOR\nE NOSSA GRATIDÃO POR TEU FAVOR?\n\nCORO\nGLÓRIA, HONRA E TODO LOUVOR\nSEJAM A CRISTO, NOSSO SENHOR;\nGLÓRIA, HONRA E TODO LOUVOR\nDAI A CRISTO, ETERNO SENHOR.\n\nVENCEU A MORTE\n(VENCEU A MORTE) VARÕES\nNOS RESGATOU,\n(NOS RESGATOU) VARÕES\nMOSTROU A TODA A TERRA\nQUE É SENHOR; NADA NO MUNDO \n(NADA NO MUNDO) VARÕES\nO IMPEDIRÁ.\n(O IMPEDIRÁ) VARÕES\nJESUS RESSUSCITOU E VAI VOLTAR.\n\nCORO (2X)\nGLÓRIA, HONRA E TODO LOUVOR\nSEJAM A CRISTO, NOSSO SENHOR;\nGLÓRIA, HONRA E TODO LOUVOR\nDAI A CRISTO, ETERNO SENHOR.\n\nFINAL: ETERNO SENHOR.', 'A LUZ DO MUNDO, O LÍRIO DOS VALES, A FONTE DE ÁGUAS VIVAS, EMANUEL; O VERBO VIVO, RAIZ DE DAVI, ESTRELA DA MANHÃ, REI DOS REIS. COMO CANTAR O NOSSO AMOR E NOSSA GRATIDÃO POR TEU FAVOR? CORO GLÓRIA, HONRA E TODO LOUVOR SEJAM A CRISTO, NOSSO SENHOR; GLÓRIA, HONRA E TODO LOUVOR DAI A CRISTO, ETERNO SENHOR. VENCEU A MORTE (VENCEU A MORTE) VARÕES NOS RESGATOU, (NOS RESGATOU) VARÕES MOSTROU A TODA A TERRA QUE É SENHOR; NADA NO MUNDO (NADA NO MUNDO) VARÕES O IMPEDIRÁ. (O IMPEDIRÁ) VARÕES JESUS RESSUSCITOU E VAI VOLTAR. GLÓRIA, HONRA E TODO LOUVOR SEJAM A CRISTO, NOSSO SENHOR; GLÓRIA, HONRA E TODO LOUVOR DAI A CRISTO, ETERNO SENHOR.  ETERNO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('642', '642 - EM NOSSOS LÁBIOS', 'EM NOSSOS LÁBIOS \nSEMPRE HAVERÁ UM CANTO A TI, JESUS, \nPOR TUDO QUE TENS FEITO E POR TUDO \nQUE FARÁS EM NOSSAS VIDAS.\n\nQUEREMOS EXPRESSAR NOSSO AMOR E GRATIDÃO \nA TI, JESUS, PELA TUA SALVAÇÃO E PELO TEU PERDÃO, RESGATANDO-NOS PRA TUA ADORAÇÃO,\nO TEU AMOR NOS SUSTENTA, SENHOR,\nA TUA GRAÇA RENOVA OS CORAÇÕES E NOS BASTA.\n\nCORO\nTODO LOUVOR E ADORAÇÃO\nPERTENCEM A TI ETERNAMENTE,\nNOSSAS VIDAS, Ó SENHOR, \nENTREGAMOS EM TEU ALTAR \nPRA TE ADORAR, JESUS.\n\nINSTRUMENTOS\n\nFAZ DE NÓS, SENHOR, UM VASO\nEM TUAS MÃOS PARA EXPRESSAR.\n\nCORO\nTODO LOUVOR E ADORAÇÃO \nPERTENCEM A TI ETERNAMENTE,\nNOSSAS VIDAS, Ó SENHOR, \nENTREGAMOS EM TEU ALTAR \nPRA TE ADORAR, JESUS.\n\nINSTRUMENTOS\n\nEM NOSSOS LÁBIOS SEMPRE HAVERÁ \nUM CANTO A TI, JESUS, \nPOR TUDO QUE TENS FEITO \nE POR TUDO QUE FARÁS \nEM  NOSSAS VIDAS.', 'EM NOSSOS LÁBIOS SEMPRE HAVERÁ UM CANTO A TI, JESUS, POR TUDO QUE TENS FEITO E POR TUDO QUE FARÁS EM NOSSAS VIDAS. QUEREMOS EXPRESSAR NOSSO AMOR E GRATIDÃO A TI, JESUS, PELA TUA SALVAÇÃO E PELO TEU PERDÃO, RESGATANDO-NOS PRA TUA ADORAÇÃO, O TEU AMOR NOS SUSTENTA, SENHOR, A TUA GRAÇA RENOVA OS CORAÇÕES E NOS BASTA. CORO TODO LOUVOR E ADORAÇÃO PERTENCEM A TI ETERNAMENTE, NOSSAS VIDAS, Ó SENHOR, ENTREGAMOS EM TEU ALTAR PRA TE ADORAR, JESUS. INSTRUMENTOS FAZ DE NÓS, SENHOR, UM VASO EM TUAS MÃOS PARA EXPRESSAR. CORO TODO LOUVOR E ADORAÇÃO PERTENCEM A TI ETERNAMENTE, NOSSAS VIDAS, Ó SENHOR, ENTREGAMOS EM TEU ALTAR PRA TE ADORAR, JESUS. INSTRUMENTOS', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('643', '643 - TODO LOUVOR SEJA DADO A DEUS', 'TODO LOUVOR SEJA DADO A DEUS,\nPOR TUDO QUE TEM FEITO EM NOSSAS VIDAS,\nNOSSA GRATIDÃO A JESUS O REI, \nFOI ELE QUEM NOS SUSTENTOU ATÉ AQUI.\n\nPOR ISSO RENDEMOS UM LOUVOR AO REI JESUS\nE ENTREGAMOS NOSSAS VIDAS AO SEU DISPOR.\n\nCORO\nLOUVEMOS AO SENHOR QUE É JUSTO \nE PODEROSO, É QUEM NOS GUIA,\nA SUA GRAÇA ESTÁ EM NOSSO MEIO,\nA SUA PRESENÇA É VIVA A CADA INSTANTE.\n\nINSTRUMENTOS\n\nTODO LOUVOR SEJA DADO A DEUS, \nPOR TUDO QUE TEM FEITO EM NOSSAS VIDAS,\nNOSSA GRATIDÃO A JESUS O REI, \nFOI ELE QUEM NOS SUSTENTOU ATÉ AQUI.\n\nPOR ISSO RENDEMOS UM LOUVOR AO REI JESUS\nE ENTREGAMOS NOSSAS VIDAS AO SEU DISPOR.\n\nCORO (2X)\nLOUVEMOS AO SENHOR QUE É JUSTO \nE PODEROSO, É QUEM NOS GUIA,\nA SUA GRAÇA ESTÁ EM NOSSO MEIO, \nA SUA PRESENÇA É VIVA A CADA INSTANTE.\n\nFINAL: DE NOSSAS VIDAS.', 'TODO LOUVOR SEJA DADO A DEUS, POR TUDO QUE TEM FEITO EM NOSSAS VIDAS, NOSSA GRATIDÃO A JESUS O REI, FOI ELE QUEM NOS SUSTENTOU ATÉ AQUI. POR ISSO RENDEMOS UM LOUVOR AO REI JESUS E ENTREGAMOS NOSSAS VIDAS AO SEU DISPOR. CORO LOUVEMOS AO SENHOR QUE É JUSTO E PODEROSO, É QUEM NOS GUIA, A SUA GRAÇA ESTÁ EM NOSSO MEIO, A SUA PRESENÇA É VIVA A CADA INSTANTE. INSTRUMENTOS TODO LOUVOR SEJA DADO A DEUS, POR TUDO QUE TEM FEITO EM NOSSAS VIDAS, NOSSA GRATIDÃO A JESUS O REI, FOI ELE QUEM NOS SUSTENTOU ATÉ AQUI. POR ISSO RENDEMOS UM LOUVOR AO REI JESUS E ENTREGAMOS NOSSAS VIDAS AO SEU DISPOR. LOUVEMOS AO SENHOR QUE É JUSTO E PODEROSO, É QUEM NOS GUIA, A SUA GRAÇA ESTÁ EM NOSSO MEIO, A SUA PRESENÇA É VIVA A CADA INSTANTE.  DE NOSSAS VIDAS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('644', '644 - LOUVAMOS-TE, Ó DEUS', 'LOUVAMOS-TE, Ó DEUS, \nPELO DOM DE JESUS,\nQUE POR NÓS, PECADORES, \nFOI MORTO NA CRUZ.\n\nCORO\nALELUIA! TODA A GLÓRIA \nTE RENDEMOS SEM FIM;\nALELUIA! TUA GRAÇA IMPLORAMOS. \nAMÉM.\n\nLOUVAMOS-TE, Ó DEUS,\nPELO ESPÍRITO E SUA LUZ,\nQUE AS TREVAS DISSIPA, \nE A CRISTO CONDUZ\n\nCORO\nALELUIA! TODA A GLÓRIA \nTE RENDEMOS SEM FIM;\nALELUIA! TUA GRAÇA IMPLORAMOS. \nAMÉM.\n\nLOUVAMOS-TE, SENHOR, \nÓ CORDEIRO DE DEUS,\nFOSTE MORTO, MAS VIVES, \nETERNO, NOS CÉUS.\n\nCORO\nALELUIA! TODA A GLÓRIA \nTE RENDEMOS SEM FIM;\nALELUIA! TUA GRAÇA IMPLORAMOS. \nAMÉM.\n\nVEM ENCHER-NOS, Ó DEUS, \nDE CELESTE ARDOR\nE FAZER-NOS SENTIR \nTEU IMENSO AMOR.', 'LOUVAMOS-TE, Ó DEUS, PELO DOM DE JESUS, QUE POR NÓS, PECADORES, FOI MORTO NA CRUZ. CORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. LOUVAMOS-TE, Ó DEUS, PELO ESPÍRITO E SUA LUZ, QUE AS TREVAS DISSIPA, E A CRISTO CONDUZ CORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. LOUVAMOS-TE, SENHOR, Ó CORDEIRO DE DEUS, FOSTE MORTO, MAS VIVES, ETERNO, NOS CÉUS. CORO ALELUIA! TODA A GLÓRIA TE RENDEMOS SEM FIM; ALELUIA! TUA GRAÇA IMPLORAMOS. AMÉM. VEM ENCHER-NOS, Ó DEUS, DE CELESTE ARDOR E FAZER-NOS SENTIR TEU IMENSO AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('645', '645 - COROAI', 'COROAI O QUE É DIGNO E BENDITO SOBRE A TERRA,\nEXALTAI O SEU NOME COM LOUVORES DE VITÓRIA;\nO SEU NOME ENTRE TODAS AS NAÇÕES SERÁ LOUVADO;\n(O CORDEIRO ENTRE TODAS AS NAÇÕES SERÁ LOUVADO)\nOH! JESUS, MEU SENHOR, REINARÁ.\n\nREINARÁ COM PODER E GLÓRIA SOBRE TODA A TERRA,\nTODO HOMEM DIANTE DO SEU TRONO SE ENCURVARÁ;\nCONTEMPLAI O CORDEIRO ETERNO QUE RESSUSCITOU,\n(DAI LOUVORES AO CORDEIRO ETERNO, QUE RESSUSCITOU)\nSOBRE AS NUVENS DESCERÁ E REINARÁ.\n\nINSTRUMENTOS\n\nREINARÁ COM PODER E GLÓRIA SOBRE TODA A TERRA,\nTODO HOMEM DIANTE DO SEU TRONO SE ENCURVARÁ;\nCONTEMPLAI O CORDEIRO ETERNO QUE RESSUSCITOU,\n(DAI LOUVORES AO CORDEIRO ETERNO, QUE RESSUSCITOU)\nSOBRE AS NUVENS DESCERÁ E REINARÁ.\n\nFINAL: COROAI.', 'COROAI O QUE É DIGNO E BENDITO SOBRE A TERRA, EXALTAI O SEU NOME COM LOUVORES DE VITÓRIA; O SEU NOME ENTRE TODAS AS NAÇÕES SERÁ LOUVADO; (O CORDEIRO ENTRE TODAS AS NAÇÕES SERÁ LOUVADO) OH! JESUS, MEU SENHOR, REINARÁ. REINARÁ COM PODER E GLÓRIA SOBRE TODA A TERRA, TODO HOMEM DIANTE DO SEU TRONO SE ENCURVARÁ; CONTEMPLAI O CORDEIRO ETERNO QUE RESSUSCITOU, (DAI LOUVORES AO CORDEIRO ETERNO, QUE RESSUSCITOU) SOBRE AS NUVENS DESCERÁ E REINARÁ. INSTRUMENTOS REINARÁ COM PODER E GLÓRIA SOBRE TODA A TERRA, TODO HOMEM DIANTE DO SEU TRONO SE ENCURVARÁ; CONTEMPLAI O CORDEIRO ETERNO QUE RESSUSCITOU, (DAI LOUVORES AO CORDEIRO ETERNO, QUE RESSUSCITOU) SOBRE AS NUVENS DESCERÁ E REINARÁ.  COROAI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('646', '646 - AGRADECIDOS SOMOS A TI', 'AGRADECIDOS SOMOS A TI, MEU REI JESUS, \nPELA GRAÇA E PELA VIDA QUE HOJE HÁ EM NÓS;\nGRATOS SOMOS POR TÃO GRANDE AMOR\nDEMONSTRADO NO MADEIRO, \nTU ÉS MEU SALVADOR.\n\nALELUIA TE DAMOS, Ó MEU SENHOR PELA PAZ \nQUE HOJE HABITA EM NOSSOS CORAÇÕES;\nPOR NÓS JESUS MORREU, QUE DOR ELE SENTIU, \nMAS AO TERCEIRO DIA ELE RESSURGIU.\n\nALELUIA TE DAMOS, Ó MEU SENHOR,\nPELA PAZ QUE HOJE HABITA \nEM NOSSOS CORAÇÕES;\nE HOJE VIVO ESTÁ À DESTRA \nDE DEUS PAI AGUARDANDO \nA HORA CERTA PARA VIR NOS BUSCAR.\n\nALELUIA TE DAMOS, Ó MEU SENHOR,\nPELA PAZ QUE HOJE HABITA \nEM NOSSOS CORAÇÕES;\nPOR NÓS JESUS MORREU,\nMAS AO TERCEIRO DIA ELE RESSURGIU.\n\nFINAL:\nAGRADECIDOS SOMOS A TI, \nMEU REI JESUS, PELA GRAÇA \nE PELA VIDA QUE HOJE HÁ EM NÓS, \nQUE HOJE HÁ EM NÓS.', 'AGRADECIDOS SOMOS A TI, MEU REI JESUS, PELA GRAÇA E PELA VIDA QUE HOJE HÁ EM NÓS; GRATOS SOMOS POR TÃO GRANDE AMOR DEMONSTRADO NO MADEIRO, TU ÉS MEU SALVADOR. ALELUIA TE DAMOS, Ó MEU SENHOR PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; POR NÓS JESUS MORREU, QUE DOR ELE SENTIU, MAS AO TERCEIRO DIA ELE RESSURGIU. ALELUIA TE DAMOS, Ó MEU SENHOR, PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; E HOJE VIVO ESTÁ À DESTRA DE DEUS PAI AGUARDANDO A HORA CERTA PARA VIR NOS BUSCAR. ALELUIA TE DAMOS, Ó MEU SENHOR, PELA PAZ QUE HOJE HABITA EM NOSSOS CORAÇÕES; POR NÓS JESUS MORREU, MAS AO TERCEIRO DIA ELE RESSURGIU. AGRADECIDOS SOMOS A TI, MEU REI JESUS, PELA GRAÇA E PELA VIDA QUE HOJE HÁ EM NÓS, QUE HOJE HÁ EM NÓS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
