INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('648', '648 - O SENHOR É O MEU PASTOR (MINH’ALMA CANTARÁ)', 'O SENHOR É O MEU PASTOR \nE NADA ME FALTARÁ,  \nO DESERTO OU O VALE \nELE OS TRANSFORMARÁ, \nEM MANANCIAIS DE ÁGUAS,\nMINHA VIDA FARTARÁ.\n\n(M)\n\n(H)\n\nCORO (2X)\nMINH’ALMA CANTARÁ,\nE ANUNCIARÁ O TEU LOUVOR,\nSENHOR, MEU REI!\n\nDAVI OROU, LUTOU, SALMODIOU, VENCEU.\nSENHOR, MEU DEUS, ESTOU AQUI\nPARA TE HONRAR.\n\nCORO (2X)\nMINH’ALMA CANTARÁ,\nE ANUNCIARÁ O TEU LOUVOR,\nSENHOR, MEU REI!\n\nJESUS, FILHO DE DEUS, \nO NOSSO BOM PASTOR,\nNOS RESGATOU PARA HERDARMOS \nA GLÓRIA ETERNA\n\nCORO (2X)\nMINH’ALMA CANTARÁ,\nE ANUNCIARÁ O TEU LOUVOR,\nSENHOR, MEU REI!\n\nFINAL: \nO TEU LOUVOR, SENHOR, MEU REI!', 'O SENHOR É O MEU PASTOR E NADA ME FALTARÁ, O DESERTO OU O VALE ELE OS TRANSFORMARÁ, EM MANANCIAIS DE ÁGUAS, MINHA VIDA FARTARÁ. MINH’ALMA CANTARÁ, E ANUNCIARÁ O TEU LOUVOR, SENHOR, MEU REI! DAVI OROU, LUTOU, SALMODIOU, VENCEU. SENHOR, MEU DEUS, ESTOU AQUI PARA TE HONRAR. MINH’ALMA CANTARÁ, E ANUNCIARÁ O TEU LOUVOR, SENHOR, MEU REI! JESUS, FILHO DE DEUS, O NOSSO BOM PASTOR, NOS RESGATOU PARA HERDARMOS A GLÓRIA ETERNA MINH’ALMA CANTARÁ, E ANUNCIARÁ O TEU LOUVOR, SENHOR, MEU REI!  O TEU LOUVOR, SENHOR, MEU REI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('649', '649 - TE LOUVAMOS, SENHOR', 'TE LOUVAMOS, SENHOR, PELO TEU AMOR\n(TE LOUVAMOS, SENHOR)\nTUA GRAÇA É REAL, NOS ACEITA COMO FILHOS.\nBENDIZEMOS, SENHOR, PELO TEU SANTO NOME\n(BENDIZEMOS, SENHOR)\nNO TEU NOME ESTÁ TODA A TUA GLÓRIA.\n\nCORO\nVAMOS TE ADORAR (TE ADORAR),\nE TE BENDIZER (TE BENDIZER),\nPELAS MUITAS MARAVILHAS QUE TENS FEITO.\nPOR NOS ALCANÇAR (NOS ALCANÇAR),\nLIVRAR-NOS DA MORTE,\nE POR DAR-NOS DE GRAÇA \nUMA VIDA ETERNA.\n\nTE LOUVAMOS, SENHOR, PELO TEU AMOR\n(TE LOUVAMOS, SENHOR)\nTUA GRAÇA É REAL, NOS ACEITA COMO FILHOS.\nBENDIZEMOS, SENHOR, PELO TEU SANTO NOME\n(BENDIZEMOS, SENHOR)\nNO TEU NOME ESTÁ TODA A TUA GLÓRIA.\n\nCORO (2X)\nVAMOS TE ADORAR (TE ADORAR),\nE TE BENDIZER (TE BENDIZER),\nPELAS MUITAS MARAVILHAS QUE TENS FEITO.\nPOR NOS ALCANÇAR (NOS ALCANÇAR),\nLIVRAR-NOS DA MORTE,\nE POR DAR-NOS DE GRAÇA \nUMA VIDA ETERNA.', 'TE LOUVAMOS, SENHOR, PELO TEU AMOR (TE LOUVAMOS, SENHOR) TUA GRAÇA É REAL, NOS ACEITA COMO FILHOS. BENDIZEMOS, SENHOR, PELO TEU SANTO NOME (BENDIZEMOS, SENHOR) NO TEU NOME ESTÁ TODA A TUA GLÓRIA. CORO VAMOS TE ADORAR (TE ADORAR), E TE BENDIZER (TE BENDIZER), PELAS MUITAS MARAVILHAS QUE TENS FEITO. POR NOS ALCANÇAR (NOS ALCANÇAR), LIVRAR-NOS DA MORTE, E POR DAR-NOS DE GRAÇA UMA VIDA ETERNA. VAMOS TE ADORAR (TE ADORAR), E TE BENDIZER (TE BENDIZER), PELAS MUITAS MARAVILHAS QUE TENS FEITO. POR NOS ALCANÇAR (NOS ALCANÇAR), LIVRAR-NOS DA MORTE, E POR DAR-NOS DE GRAÇA UMA VIDA ETERNA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('650', '650 - Ó VINDE E ADOREMOS', 'Ó VINDE E ADOREMOS\nE PROSTREMOS DIANTE DO SENHOR;\nSE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS \nOS VOSSOS CORAÇÕES.\n\nÓ VINDE A JESUS,\nNÃO ENDUREÇAIS OS VOSSOS CORAÇÕES,\nACEITAI A CRISTO, A SALVAÇÃO.\n\nINSTRUMENTOS\nBIS\n\nÓ VINDE E ADOREMOS\nE PROSTREMOS DIANTE DO SENHOR;\nSE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS \nOS VOSSOS CORAÇÕES.\n\nÓ VINDE A JESUS,\nNÃO ENDUREÇAIS OS VOSSOS CORAÇÕES,\nACEITAI A CRISTO, A SALVAÇÃO.\n\nFINAL: JESUS, SÓ JESUS, O SALVADOR, JESUS.\nBIS', 'Ó VINDE E ADOREMOS E PROSTREMOS DIANTE DO SENHOR; SE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES. Ó VINDE A JESUS, NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES, ACEITAI A CRISTO, A SALVAÇÃO. INSTRUMENTOS Ó VINDE A JESUS, NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES, ACEITAI A CRISTO, A SALVAÇÃO.  JESUS, SÓ JESUS, O SALVADOR, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
