INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('653', '653 - ALELUIA (SALMOS 113)', 'CORO\nALELUIA! LOUVAI, SERVOS DO SENHOR!\nLOUVAI O NOME DO SENHOR!\n\nBENDITO SEJA O NOME DO SENHOR\nDESDE AGORA E PARA SEMPRE,\nDO NASCER DO SOL ATÉ O OCASO,\nLOUVADO SEJA O NOME DO SENHOR!\n\nEXCELSO É O NOME DO SENHOR,\nSUA GLÓRIA ESTÁ ACIMA DOS CÉUS,\nQUEM HÁ SEMELHANTE AO SENHOR,\nO NOSSO DEUS?\nNAS ALTURAS ESTÁ SEU TRONO!\n\nCORO\nALELUIA! LOUVAI, SERVOS DO SENHOR!\nLOUVAI O NOME DO SENHOR!\n\nO SENHOR SE INCLINA DESDE O CÉU\nPARA VER O QUE HÁ NA TERRA,\nELE ERGUE DO PÓ O DESVALIDO \nPARA O ASSENTAR COM OS PRÍNCIPES \nDO SEU POVO.\n\nALELUIA! (LOUVAI!) \nLOUVAI, SERVOS DO SENHOR!\nLOUVAI O NOME DO SENHOR!\nALELUIA! (LOUVAI!)', 'CORO ALELUIA! LOUVAI, SERVOS DO SENHOR! LOUVAI O NOME DO SENHOR! BENDITO SEJA O NOME DO SENHOR DESDE AGORA E PARA SEMPRE, DO NASCER DO SOL ATÉ O OCASO, LOUVADO SEJA O NOME DO SENHOR! EXCELSO É O NOME DO SENHOR, SUA GLÓRIA ESTÁ ACIMA DOS CÉUS, QUEM HÁ SEMELHANTE AO SENHOR, O NOSSO DEUS? NAS ALTURAS ESTÁ SEU TRONO! CORO ALELUIA! LOUVAI, SERVOS DO SENHOR! LOUVAI O NOME DO SENHOR! O SENHOR SE INCLINA DESDE O CÉU PARA VER O QUE HÁ NA TERRA, ELE ERGUE DO PÓ O DESVALIDO PARA O ASSENTAR COM OS PRÍNCIPES DO SEU POVO. ALELUIA! (LOUVAI!) LOUVAI, SERVOS DO SENHOR! LOUVAI O NOME DO SENHOR! ALELUIA! (LOUVAI!)', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
