INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('656', '656 - GRANDE É O SENHOR', 'GRANDE É O SENHOR E MUI DIGNO DE LOUVOR, \nNA CIDADE DO NOSSO DEUS, \nSEU SANTO MONTE, ALEGRIA DE TODA A TERRA;\nGRANDE É O SENHOR EM QUEM NÓS TEMOS \nA VITÓRIA, QUE NOS AJUDA CONTRA \nO INIMIGO, POR ISSO DIANTE \nDELE NOS PROSTRAMOS.\n\nQUEREMOS O TEU NOME ENGRANDECER E AGRADECER-TE POR TUA OBRA EM NOSSA VIDA;\nCONFIAMOS EM TEU INFINITO AMOR,\nPOIS SÓ TU ÉS O DEUS ETERNO\nSOBRE TODA A TERRA E CÉU.\nBIS', 'GRANDE É O SENHOR E MUI DIGNO DE LOUVOR, NA CIDADE DO NOSSO DEUS, SEU SANTO MONTE, ALEGRIA DE TODA A TERRA; GRANDE É O SENHOR EM QUEM NÓS TEMOS A VITÓRIA, QUE NOS AJUDA CONTRA O INIMIGO, POR ISSO DIANTE DELE NOS PROSTRAMOS. QUEREMOS O TEU NOME ENGRANDECER E AGRADECER-TE POR TUA OBRA EM NOSSA VIDA; CONFIAMOS EM TEU INFINITO AMOR, POIS SÓ TU ÉS O DEUS ETERNO SOBRE TODA A TERRA E CÉU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('657', '657 - OS CÉUS PROCLAMAM', 'OS CÉUS PROCLAMAM A SUA GLÓRIA \nE O FIRMAMENTO AS OBRAS DAS SUAS MÃOS;\nUM DIA PASSA, O OUTRO VEM,\nÉ ELE QUEM DÁ VITÓRIAS AO SEU POVO;\nDO ALTO VEIO A SUA MÃO\nTOCOU-ME E DEU-ME \nALEGRIA DE VIVER.\nBIS\n\nCORO (2X)\nGLÓRIA AO MEU JESUS,\nPOIS DELE SOU E ELE É MEU.\n\nSE FAZ OUVIR A SUA VOZ POR TODA A TERRA \nE AOS CONFINS DO MUNDO;\nSINTO A PRESENÇA DE DEUS TÃO CALMA\nPOIS QUEM É DEUS SENÃO O NOSSO DEUS?\nCOMIGO ESTÁ, NÃO VOU DEIXAR \nQUE QUALQUER COISA\nQUEBRE ESSA COMUNHÃO.\nBIS\n\nCORO (2X)\nGLÓRIA AO MEU JESUS,\nPOIS DELE SOU E ELE É MEU.', 'OS CÉUS PROCLAMAM A SUA GLÓRIA E O FIRMAMENTO AS OBRAS DAS SUAS MÃOS; UM DIA PASSA, O OUTRO VEM, É ELE QUEM DÁ VITÓRIAS AO SEU POVO; DO ALTO VEIO A SUA MÃO TOCOU-ME E DEU-ME ALEGRIA DE VIVER. GLÓRIA AO MEU JESUS, POIS DELE SOU E ELE É MEU. SE FAZ OUVIR A SUA VOZ POR TODA A TERRA E AOS CONFINS DO MUNDO; SINTO A PRESENÇA DE DEUS TÃO CALMA POIS QUEM É DEUS SENÃO O NOSSO DEUS? COMIGO ESTÁ, NÃO VOU DEIXAR QUE QUALQUER COISA QUEBRE ESSA COMUNHÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('658', '658 - MEDITAREI NAS MARAVILHAS DO SENHOR', 'MEDITAREI NAS MARAVILHAS  DO SENHOR \nE FALAREI DO SEU PODER ENTRE AS NAÇÕES;\nCELEBRAREI A MAJESTADE DO SENHOR, PROCLAMAREI SUA JUSTIÇA SOBRE A TERRA.\n\nPORQUE GRANDES SÃO AS TUAS OBRAS, \nTU ÉS FIEL ETERNAMENTE.\n\nEXALTAR-TE-EI, Ó DEUS,\nPOIS TU ÉS MEU REI,\nGLORIFICAREI TEU NOME\nENQUANTO EU VIVER.\n\nPERTO ESTÁ O SENHOR\nDOS QUE O INVOCAM.\n\n4X\nBIS', 'MEDITAREI NAS MARAVILHAS DO SENHOR E FALAREI DO SEU PODER ENTRE AS NAÇÕES; CELEBRAREI A MAJESTADE DO SENHOR, PROCLAMAREI SUA JUSTIÇA SOBRE A TERRA. PORQUE GRANDES SÃO AS TUAS OBRAS, TU ÉS FIEL ETERNAMENTE. EXALTAR-TE-EI, Ó DEUS, POIS TU ÉS MEU REI, GLORIFICAREI TEU NOME ENQUANTO EU VIVER. PERTO ESTÁ O SENHOR DOS QUE O INVOCAM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('659', '659 - CELEBRAI COM JÚBILO AO SENHOR', 'CELEBRAI COM JÚBILO AO SENHOR\nTODAS AS TERRAS,\nSERVI AO SENHOR COM ALEGRIA;\nAPRESENTAI-VOS DIANTE\nDELE COM CÂNTICOS,\nSABEI QUE O SENHOR É DEUS.\n\nFOI ELE QUEM NOS FEZ E DELE SOMOS, \nSOMOS O SEU POVO\nE REBANHO DO SEU PASTO.\n\nENTRAI EM SUAS PORTAS COM AÇÕES DE GRAÇAS E NOS SEUS ÁTRIOS COM HINOS DE LOUVOR;\nRENDEI-LHE GRAÇAS E BENDIZEI-LHE \nO NOME PORQUE O SENHOR É BOM.\nBIS\n\nSUA MISERICÓRDIA DURA PARA SEMPRE,\nDE GERAÇÃO EM GERAÇÃO,SUA FIDELIDADE.\nSUA MISERICÓRDIA DURA PARA SEMPRE,\nDE GERAÇÃO EM GERAÇÃO, SUA FIDELIDADE.', 'CELEBRAI COM JÚBILO AO SENHOR TODAS AS TERRAS, SERVI AO SENHOR COM ALEGRIA; APRESENTAI-VOS DIANTE DELE COM CÂNTICOS, SABEI QUE O SENHOR É DEUS. FOI ELE QUEM NOS FEZ E DELE SOMOS, SOMOS O SEU POVO E REBANHO DO SEU PASTO. ENTRAI EM SUAS PORTAS COM AÇÕES DE GRAÇAS E NOS SEUS ÁTRIOS COM HINOS DE LOUVOR; RENDEI-LHE GRAÇAS E BENDIZEI-LHE O NOME PORQUE O SENHOR É BOM. SUA MISERICÓRDIA DURA PARA SEMPRE, DE GERAÇÃO EM GERAÇÃO,SUA FIDELIDADE. SUA MISERICÓRDIA DURA PARA SEMPRE, DE GERAÇÃO EM GERAÇÃO, SUA FIDELIDADE.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('660', '660 - O MEU CORAÇÃO', 'O MEU CORAÇÃO FERVE COM PALAVRAS BOAS, \nFALO DO QUE TENHO FEITO NO TOCANTE AO REI;\nA MINHA LÍNGUA É A PENA DE UM DESTRO ESCRITOR, \nTU ÉS MAIS FORMOSO QUE OS FILHOS DOS HOMENS;\n\nA GRAÇA SE DERRAMOU EM TEUS LÁBIOS,\nPOR ISSO DEUS TE ABENÇOOU PARA SEMPRE;\nÀ COXA CINGE A TUA ESPADA, Ó VALENTE,\nCOM A TUA GLÓRIA E TUA MAJESTADE.\n\nINSTRUMENTOS\n\nE NESTE ESPLENDOR TEU CAVALGA PROSPERAMENTE, PELA VERDADE E JUSTIÇA E A MANSIDÃO;\nAS TUAS FLECHAS SÃO AGUDAS NO CORAÇÃO \nDOS INIMIGOS DO REI,\n\nE POR ELAS OS POVOS SE QUEDAM DIANTE DE TI; \nO TEU TRONO, Ó DEUS, É ETERNO E PERPÉTUO, \nO CETRO DO TEU REINO É DE EQUIDADE, \nTU AMAS JUSTIÇA E ODEIAS MALDADE, \nPOR ISSO O TEU DEUS TE UNGIU COM ALEGRIA.', 'O MEU CORAÇÃO FERVE COM PALAVRAS BOAS, FALO DO QUE TENHO FEITO NO TOCANTE AO REI; A MINHA LÍNGUA É A PENA DE UM DESTRO ESCRITOR, TU ÉS MAIS FORMOSO QUE OS FILHOS DOS HOMENS; A GRAÇA SE DERRAMOU EM TEUS LÁBIOS, POR ISSO DEUS TE ABENÇOOU PARA SEMPRE; À COXA CINGE A TUA ESPADA, Ó VALENTE, COM A TUA GLÓRIA E TUA MAJESTADE. INSTRUMENTOS E NESTE ESPLENDOR TEU CAVALGA PROSPERAMENTE, PELA VERDADE E JUSTIÇA E A MANSIDÃO; AS TUAS FLECHAS SÃO AGUDAS NO CORAÇÃO DOS INIMIGOS DO REI, E POR ELAS OS POVOS SE QUEDAM DIANTE DE TI; O TEU TRONO, Ó DEUS, É ETERNO E PERPÉTUO, O CETRO DO TEU REINO É DE EQUIDADE, TU AMAS JUSTIÇA E ODEIAS MALDADE, POR ISSO O TEU DEUS TE UNGIU COM ALEGRIA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('661', '661 - ÉS O MAIS FORMOSO, JESUS', 'ÉS O MAIS FORMOSO, JESUS,\nGRAÇA HÁ EM TEU FALAR,\nCONSAGRO-TE O QUE COMPUS,\nNESTE CANTO TE ADORAR;\nCINGE A TUA ESPADA, HERÓI,\nDE GLÓRIA E MAJESTADE,\nCOM PODER HÁS DE ENSINAR\nJUSTIÇA E VERDADE.\n\nTEUS PERFUMES SE EXALAM:\nCÁSSIA, MIRRA E ALOÉS;\nDE AMOR TEUS OLHOS BRILHAM,\nME CONDUZEM PELA FÉ.\nO TEU TRONO, Ó REI, É PODER\nPOR TODA A ETERNIDADE,\nAMAS A JUSTIÇA, PORÉM,\nODEIAS A IMPIEDADE.\n\n“OUVE, FILHA, VÊ, DÁ ATENÇÃO,\nFALA O TEU SALVADOR,\nPROSTRA-TE ANTE MIM, ENTÃO,\nADORA O DEUS DE AMOR;\nNOIVA MINHA, ALMEJAS O CÉU,\nVESTES DE OURO TE DAREI,\nTÃO FORMOSA E FIEL,\nÀ MINHA GLÓRIA A LEVAREI”.\n\n“TEUS VESTIDOS SÃO BORDADOS\nDE OURO PURO DE OFIR,\nTEU NOME FAREI LEMBRADO,\nEU TE QUERO JUNTO A MIM;\nCOM ALEGRIA E TODA GLÓRIA,\nFIÉIS TE SEGUIRÃO\nE COM CANTO DE VITÓRIA,\nPRA SEMPRE TE LOUVARÃO”.', 'ÉS O MAIS FORMOSO, JESUS, GRAÇA HÁ EM TEU FALAR, CONSAGRO-TE O QUE COMPUS, NESTE CANTO TE ADORAR; CINGE A TUA ESPADA, HERÓI, DE GLÓRIA E MAJESTADE, COM PODER HÁS DE ENSINAR JUSTIÇA E VERDADE. TEUS PERFUMES SE EXALAM: CÁSSIA, MIRRA E ALOÉS; DE AMOR TEUS OLHOS BRILHAM, ME CONDUZEM PELA FÉ. O TEU TRONO, Ó REI, É PODER POR TODA A ETERNIDADE, AMAS A JUSTIÇA, PORÉM, ODEIAS A IMPIEDADE. OUVE, FILHA, VÊ, DÁ ATENÇÃO, FALA O TEU SALVADOR, PROSTRA-TE ANTE MIM, ENTÃO, ADORA O DEUS DE AMOR; NOIVA MINHA, ALMEJAS O CÉU, VESTES DE OURO TE DAREI, TÃO FORMOSA E FIEL, À MINHA GLÓRIA A LEVAREI. TEUS VESTIDOS SÃO BORDADOS DE OURO PURO DE OFIR, TEU NOME FAREI LEMBRADO, EU TE QUERO JUNTO A MIM; COM ALEGRIA E TODA GLÓRIA, FIÉIS TE SEGUIRÃO E COM CANTO DE VITÓRIA, PRA SEMPRE TE LOUVARÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('662', '662 - ONIPOTENTE DEUS', 'ONIPOTENTE DEUS,\nTUAS OBRAS TE EXALTAM, SENHOR;\nONIPOTENTE DEUS,\nCÉUS E TERRA TE RENDEM LOUVOR.\nQUE É O HOMEM QUE DELE TE LEMBRES\nE O FILHO DO HOMEM PARA O VISITAR?\nPORÉM, HONRA DESTE PRA GOVERNAR.\n\nINSTRUMENTOS\n\nSENHOR JESUS CRISTO,\nMINHA VIDA VIESTE SALVAR;\nSENHOR JESUS CRISTO,\nCOM TEU SANGUE ME PURIFICAR.\nCOMO SERVO VIESTE,\nNA CRUZ TE HUMILHASTE,\nMAS VIVO NA GLÓRIA HOJE ESTÁS,\nÉS MEU REI, SENHOR E, SEI, VOLTARÁS.\n\nINSTRUMENTOS\n\nESPÍRITO SANTO, Ó VEM E HABITA EM MEU SER,\nESPÍRITO SANTO, VIVIFICA-ME COM TEU PODER;\nTAL COMO ÁGUA QUE JORRA,\nE O VENTO QUE SOPRA,\nE O FOGO ARDENTE, ENFIM,\nSANTO ESPÍRITO DE \nDEUS VIVE EM MIM.\nBIS', 'ONIPOTENTE DEUS, TUAS OBRAS TE EXALTAM, SENHOR; ONIPOTENTE DEUS, CÉUS E TERRA TE RENDEM LOUVOR. QUE É O HOMEM QUE DELE TE LEMBRES E O FILHO DO HOMEM PARA O VISITAR? PORÉM, HONRA DESTE PRA GOVERNAR. INSTRUMENTOS SENHOR JESUS CRISTO, MINHA VIDA VIESTE SALVAR; SENHOR JESUS CRISTO, COM TEU SANGUE ME PURIFICAR. COMO SERVO VIESTE, NA CRUZ TE HUMILHASTE, MAS VIVO NA GLÓRIA HOJE ESTÁS, ÉS MEU REI, SENHOR E, SEI, VOLTARÁS. INSTRUMENTOS ESPÍRITO SANTO, Ó VEM E HABITA EM MEU SER, ESPÍRITO SANTO, VIVIFICA-ME COM TEU PODER; TAL COMO ÁGUA QUE JORRA, E O VENTO QUE SOPRA, E O FOGO ARDENTE, ENFIM, SANTO ESPÍRITO DE DEUS VIVE EM MIM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('663', '663 - LOUVAI AO SENHOR TODAS AS NAÇÕES', 'LOUVAI AO SENHOR TODAS AS NAÇÕES LOUVAI-O \nTODOS OS POVOS.\nLOUVAI AO SENHOR TODAS AS NAÇÕES LOUVAI-O \nTODOS OS POVOS.\nLOUVAI AO SENHOR, LOUVAI AO SENHOR.\n\nPORQUE A SUA BENIGNIDADE\nÉ GRANDE PARA CONOSCO,\nE A VERDADE DO SENHOR\nDURA PARA SEMPRE.\nLOUVAI AO SENHOR, \nLOUVAI AO SENHOR.', 'LOUVAI AO SENHOR TODAS AS NAÇÕES LOUVAI-O TODOS OS POVOS. LOUVAI AO SENHOR TODAS AS NAÇÕES LOUVAI-O TODOS OS POVOS. LOUVAI AO SENHOR, LOUVAI AO SENHOR. PORQUE A SUA BENIGNIDADE É GRANDE PARA CONOSCO, E A VERDADE DO SENHOR DURA PARA SEMPRE. LOUVAI AO SENHOR, LOUVAI AO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('664', '664 - QUÃO AMÁVEIS SÃO', 'QUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS, \nSENHOR DOS EXÉRCITOS!\nA MINH’ALMA ESTÁ ANELANTE E DESFALECE \nPELOS ÁTRIOS DO SENHOR.\n\nCORO\nO MEU CORAÇÃO E A MINHA CARNE\nCLAMAM PELO DEUS VIVO!\nQUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS \nSENHOR DOS EXÉRCITOS\n\nATÉ O PARDAL ENCONTROU CASA E A ANDORINHA ENCONTROU NINHO PARA SI JUNTO AO TEU ALTAR, \nSENHOR DOS EXÉRCITOS, REI MEU E DEUS MEU.\n\nCORO\nO MEU CORAÇÃO E A MINHA CARNE\nCLAMAM PELO DEUS VIVO!\nQUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS \nSENHOR DOS EXÉRCITOS', 'QUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS, SENHOR DOS EXÉRCITOS! A MINH’ALMA ESTÁ ANELANTE E DESFALECE PELOS ÁTRIOS DO SENHOR. CORO O MEU CORAÇÃO E A MINHA CARNE CLAMAM PELO DEUS VIVO! QUÃO AMÁVEIS SÃO OS TEUS TABERNÁCULOS SENHOR DOS EXÉRCITOS ATÉ O PARDAL ENCONTROU CASA E A ANDORINHA ENCONTROU NINHO PARA SI JUNTO AO TEU ALTAR, SENHOR DOS EXÉRCITOS, REI MEU E DEUS MEU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('665', '665 - BENDIZEI AO SENHOR', 'BENDIZEI AO SENHOR\nVÓS TODOS, SERVOS DO SENHOR,\nQUE ASSISTIS NA CASA DO SENHOR\nNAS HORAS DA NOITE.\n\nERGUEI AS MÃOS NO SANTUÁRIO\nE BENDIZEI AO SENHOR;\nDE SIÃO TE ABENÇOE O SENHOR,\nCRIADOR DO CÉU E DA TERRA.\nBIS', 'BENDIZEI AO SENHOR VÓS TODOS, SERVOS DO SENHOR, QUE ASSISTIS NA CASA DO SENHOR NAS HORAS DA NOITE. ERGUEI AS MÃOS NO SANTUÁRIO E BENDIZEI AO SENHOR; DE SIÃO TE ABENÇOE O SENHOR, CRIADOR DO CÉU E DA TERRA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('666', '666 - TAL QUAL ESTOU', 'TAL QUAL ESTOU, EIS-ME, SENHOR,\nPOIS O TEU SANGUE REMIDOR\nVERTESTE PELO PECADOR;\nÓ SALVADOR, ME ACHEGO A TI!\n\nTAL QUAL ESTOU, SEM ESPERAR\nQUE POSSA A VIDA MELHORAR,\nEM TI SÓ QUERO CONFIAR;\nÓ SALVADOR, ME ACHEGO A TI!\n\nTAL QUAL ESTOU E SEM PODER,\nAS FALTAS PODES PREENCHER\nE TUDO QUANTO ME É MISTER;\nÓ SALVADOR, ME ACHEGO A TI!\n\nTAL QUAL ESTOU ME ACEITARÁS\nE TU, MINH’ALMA LIMPARÁS,\nCOM TEU AMOR ME COBRIRÁS;\nÓ SALVADOR, ME ACHEGO A TI!', 'TAL QUAL ESTOU, EIS-ME, SENHOR, POIS O TEU SANGUE REMIDOR VERTESTE PELO PECADOR; Ó SALVADOR, ME ACHEGO A TI! TAL QUAL ESTOU, SEM ESPERAR QUE POSSA A VIDA MELHORAR, EM TI SÓ QUERO CONFIAR; Ó SALVADOR, ME ACHEGO A TI! TAL QUAL ESTOU E SEM PODER, AS FALTAS PODES PREENCHER E TUDO QUANTO ME É MISTER; Ó SALVADOR, ME ACHEGO A TI! TAL QUAL ESTOU ME ACEITARÁS E TU, MINH’ALMA LIMPARÁS, COM TEU AMOR ME COBRIRÁS; Ó SALVADOR, ME ACHEGO A TI!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('667', '667 - JUSTO ÉS, SENHOR', 'JUSTO ÉS, SENHOR,\nNOS TEUS SANTOS CAMINHOS,\nÉS DIGNO EM TODAS AS TUAS OBRAS.\nJUSTO ÉS, SENHOR,\nNOS TEUS SANTOS CAMINHOS,\nÉS DIGNO EM TODAS AS TUAS OBRAS.\n\nBEM PERTO ESTÁS, SENHOR\nBEM PERTO ESTÁS\nDOS QUE TE INVOCAM,\nDE TODOS QUE TE INVOCAM EM VERDADE.\n\nFINAL: ALELUIA! ALELUIA!\nBIS', 'JUSTO ÉS, SENHOR, NOS TEUS SANTOS CAMINHOS, ÉS DIGNO EM TODAS AS TUAS OBRAS. JUSTO ÉS, SENHOR, NOS TEUS SANTOS CAMINHOS, ÉS DIGNO EM TODAS AS TUAS OBRAS. BEM PERTO ESTÁS, SENHOR BEM PERTO ESTÁS DOS QUE TE INVOCAM, DE TODOS QUE TE INVOCAM EM VERDADE.  ALELUIA! ALELUIA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('668', '668 - UM POBRE CEGO', 'UM POBRE CEGO À BEIRA DE UMA ESTRADA \nENVOLTO EM TRAPOS, TREVAS E DESDÉM;\nPEDIA ESMOLAS DE ALMA ANGUSTIADA,\nVEIO JESUS E TROUXE A LUZ E O BEM.\n\nCORO\nAO VIR JESUS A TEMPESTADE CESSA,\nAO VIR JESUS AS LÁGRIMAS SE VÃO;\nELE TRANSFORMA TODA NOSSA VIDA\nILUMINANDO A NEGRA ESCURIDÃO.\n\nHOJE, TAMBÉM, O PECADOR DEPARA,\nEM CRISTO, LIVRAMENTO DE AFLIÇÃO,\nEM MEIO À TENTAÇÃO QUE O DESTROÇARA,\nVEIO JESUS E TROUXE A SALVAÇÃO.\n\nCORO\nAO VIR JESUS A TEMPESTADE CESSA,\nAO VIR JESUS AS LÁGRIMAS SE VÃO;\nELE TRANSFORMA TODA NOSSA VIDA\nILUMINANDO A NEGRA ESCURIDÃO.', 'UM POBRE CEGO À BEIRA DE UMA ESTRADA ENVOLTO EM TRAPOS, TREVAS E DESDÉM; PEDIA ESMOLAS DE ALMA ANGUSTIADA, VEIO JESUS E TROUXE A LUZ E O BEM. CORO AO VIR JESUS A TEMPESTADE CESSA, AO VIR JESUS AS LÁGRIMAS SE VÃO; ELE TRANSFORMA TODA NOSSA VIDA ILUMINANDO A NEGRA ESCURIDÃO. HOJE, TAMBÉM, O PECADOR DEPARA, EM CRISTO, LIVRAMENTO DE AFLIÇÃO, EM MEIO À TENTAÇÃO QUE O DESTROÇARA, VEIO JESUS E TROUXE A SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('669', '669 - DEUS DOS ANTIGOS', 'DEUS DOS ANTIGOS, CUJA FORTE MÃO\nREGE E SUSTÉM OS ASTROS DA AMPLIDÃO;\nDO CINTILANTE CÉU INSPIRADOR,\nCOM GRATIDÃO CANTAMOS TEU LOUVOR.\n\nJÁ NO PASSADO FOSTE NOSSA LUZ,\nHOJE ÉS FAROL QUE À VIDA NOS CONDUZ;\nSÊ NOSSO ESTEIO, GUIA E PROTEÇÃO,\nTUA PALAVRA É LEI E DIREÇÃO.\n\nTEU POVO, Ó DEUS, RESTAURA EM SEU LABOR, \nTRANSFORMA A NOITE EM DIA DE ESPLENDOR;\nAS NOSSAS VIDAS VEM FORTALECER\nPARA TEU NOME SEMPRE ENGRANDECER. \nAMÉM.', 'DEUS DOS ANTIGOS, CUJA FORTE MÃO REGE E SUSTÉM OS ASTROS DA AMPLIDÃO; DO CINTILANTE CÉU INSPIRADOR, COM GRATIDÃO CANTAMOS TEU LOUVOR. JÁ NO PASSADO FOSTE NOSSA LUZ, HOJE ÉS FAROL QUE À VIDA NOS CONDUZ; SÊ NOSSO ESTEIO, GUIA E PROTEÇÃO, TUA PALAVRA É LEI E DIREÇÃO. TEU POVO, Ó DEUS, RESTAURA EM SEU LABOR, TRANSFORMA A NOITE EM DIA DE ESPLENDOR; AS NOSSAS VIDAS VEM FORTALECER PARA TEU NOME SEMPRE ENGRANDECER. AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('670', '670 - SE PAZ, A MAIS DOCE', 'SE PAZ, A MAIS DOCE ME DERES GOZAR,\nSE DOR A MAIS FORTE SOFRER,\nOH! SEJA O QUE FOR, TU ME FAZES SABER\nQUE FELIZ COM JESUS SEMPRE SOU!\n\nCORO\nSOU FELIZ, (SOU FELIZ)\nCOM JESUS, (COM JESUS) \nSOU FELIZ, COM JESUS, MEU SENHOR!\n\nEMBORA ME ASSALTE O CRUEL TENTADOR\nE ATAQUE COM VIS TENTAÇÕES;\nOH! CERTO ESTOU, APESAR DE AFLIÇÕES,\nQUE FELIZ EU SEREI COM JESUS!\n\nCORO\nSOU FELIZ, (SOU FELIZ)\nCOM JESUS, (COM JESUS) \nSOU FELIZ, COM JESUS, MEU SENHOR!\n\nMEU TRISTE PECADO, POR MEU SALVADOR, \nFOI PAGO DE UM MODO CABAL;\nVALEU-ME O SENHOR, OH! MERCÊ SEM IGUAL! \nSOU FELIZ! GRAÇAS DOU A JESUS!\n\nCORO\nSOU FELIZ, (SOU FELIZ)\nCOM JESUS, (COM JESUS) \nSOU FELIZ, COM JESUS, MEU SENHOR!\n\nA VINDA EU ANSEIO DO MEU SALVADOR;\nEM BREVE VIRÁ ME LEVAR\nAO CÉU, ONDE VOU PARA SEMPRE MORAR\nCOM REMIDOS NA LUZ DO SENHOR!\n\nCORO\nSOU FELIZ, (SOU FELIZ)\nCOM JESUS, (COM JESUS) \nSOU FELIZ, COM JESUS, MEU SENHOR!', 'SE PAZ, A MAIS DOCE ME DERES GOZAR, SE DOR A MAIS FORTE SOFRER, OH! SEJA O QUE FOR, TU ME FAZES SABER QUE FELIZ COM JESUS SEMPRE SOU! CORO SOU FELIZ, (SOU FELIZ) COM JESUS, (COM JESUS) SOU FELIZ, COM JESUS, MEU SENHOR! EMBORA ME ASSALTE O CRUEL TENTADOR E ATAQUE COM VIS TENTAÇÕES; OH! CERTO ESTOU, APESAR DE AFLIÇÕES, QUE FELIZ EU SEREI COM JESUS! MEU TRISTE PECADO, POR MEU SALVADOR, FOI PAGO DE UM MODO CABAL; VALEU-ME O SENHOR, OH! MERCÊ SEM IGUAL! SOU FELIZ! GRAÇAS DOU A JESUS! A VINDA EU ANSEIO DO MEU SALVADOR; EM BREVE VIRÁ ME LEVAR AO CÉU, ONDE VOU PARA SEMPRE MORAR COM REMIDOS NA LUZ DO SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('671', '671 - OUVE, POIS ISRAEL', 'OUVE, POIS, ISRAEL!\nO SENHOR É O TEU DEUS, \nELE É O ÚNICO SENHOR,\n“AMARÁS O SENHOR TEU DEUS\nDE TODA A TUA ALMA,”\nESTAS PALAVRAS TE ORDENO.\n\nATA-AS EM TEU CORAÇÃO,\nAOS TEUS FILHOS FALARÁS\nE SERÁ POR SINAL NAS TUAS MÃOS\nE NOS UMBRAIS DE TUA CASA.\nE ASSIM VOSSAS CASAS \nESTARÃO CHEIAS DE TODO BEM,\nDOS POÇOS TIRAREIS ÁGUAS DE SALVAÇÃO.\n\nCOMEREIS E VOS FARTAREIS DAS VINHAS, \nCOMEREIS E VOS FARTAREIS\nINSTRUMENTOS\n\n\nGUARDA AS MINHAS PALAVRAS \nNÃO ESQUEÇAS DO SENHOR \nTEU DEUS QUE TE TIROU DA SERVIDÃO \nE HOJE LIVRE ESTÁS,\nAO TEU DEUS TEMERÁS \nE SÓ A ELE SERVIRÁS.\nPOIS EM BREVE VIRÁ. (4X)\nBIS', 'OUVE, POIS, ISRAEL! O SENHOR É O TEU DEUS, ELE É O ÚNICO SENHOR, AMARÁS O SENHOR TEU DEUS DE TODA A TUA ALMA, ESTAS PALAVRAS TE ORDENO. ATA-AS EM TEU CORAÇÃO, AOS TEUS FILHOS FALARÁS E SERÁ POR SINAL NAS TUAS MÃOS E NOS UMBRAIS DE TUA CASA. E ASSIM VOSSAS CASAS ESTARÃO CHEIAS DE TODO BEM, DOS POÇOS TIRAREIS ÁGUAS DE SALVAÇÃO. COMEREIS E VOS FARTAREIS DAS VINHAS, COMEREIS E VOS FARTAREIS INSTRUMENTOS GUARDA AS MINHAS PALAVRAS NÃO ESQUEÇAS DO SENHOR TEU DEUS QUE TE TIROU DA SERVIDÃO E HOJE LIVRE ESTÁS, AO TEU DEUS TEMERÁS E SÓ A ELE SERVIRÁS. POIS EM BREVE VIRÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('672', '672 - MEU SERVO, NÃO TEMAS', 'MEU SERVO, NÃO TEMAS,\nNÃO TEMAS POIS TE ESCOLHI\nPRA FAZER A MINHA OBRA,\nSERVO, TU ÉS MEU.\nBIS\n\nQUANDO EU TE ENVIAR, TU FALARÁS,\nQUANDO EU MANDAR, TU CALARÁS,\nE ASSIM FARÁS MINHA OBRA \nE TE ALEGRARÁS.\n\nQUANDO A OBRA FIZERES \nE TUDO SE COMPLETAR,\nTE TRAREI À MINHA PRESENÇA\nE TE ALEGRARÁS.\n\n3X', 'MEU SERVO, NÃO TEMAS, NÃO TEMAS POIS TE ESCOLHI PRA FAZER A MINHA OBRA, SERVO, TU ÉS MEU. QUANDO EU TE ENVIAR, TU FALARÁS, QUANDO EU MANDAR, TU CALARÁS, E ASSIM FARÁS MINHA OBRA E TE ALEGRARÁS. QUANDO A OBRA FIZERES E TUDO SE COMPLETAR, TE TRAREI À MINHA PRESENÇA E TE ALEGRARÁS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('673', '673 - Ó PAI, EU QUERIA TANTO VER (AUTOR DA MINHA FÉ)', 'Ó PAI, EU QUERIA TANTO VER O MEU SENHOR \nDESCER VINDO ME ENCONTRAR,\nEU POSSO ATÉ IMAGINAR A REFULGENTE \nGLÓRIA DO SENHOR JESUS,\nTRANSPONDO AS BRANCAS NUVENS\nNO MAIS PURO AZUL ONDE NEM SUL, \nNEM NORTE EXISTIRÁ.\n\nE EM MEIO A LÁGRIMAS,\nSORRISOS DE ALEGRIA E DE PRAZER,\nEU QUE ERA CEGO AGORA POSSO VER,\nCONTEMPLAR, CONTEMPLAR, ENFIM,\nPOR ISSO EU CANTO GLÓRIA.\nCORO (2X)\nGLÓRIA, GLÓRIA\nAO AUTOR DA MINHA FÉ.\n\nÓ PAI, EU QUERIA TANTO OUVIR\nO SOM QUE VAI ABRIR O ENCONTRO TRIUNFAL,\nREVER AMIGOS QUE UM DIA EM CRISTO \nFORAM FEITOS MEUS IRMÃOS,\nE AGORA, SIM, PODEMOS DAR AS MÃOS\nPOIS TEMOS TODOS UM, SOMENTE UM,\nUM SÓ SENHOR;\n\nE EIS O CONSOLO QUE ENVOLVE A MINHA VIDA, \nO MEU SENHOR JESUS QUE FOI MORTO, SIM, \nNAQUELA CRUZ, VOLTARÁ, VOLTARÁ, ENFIM,\nPOR ISSO EU CANTO GLÓRIA.\nCORO (2X)\nGLÓRIA, GLÓRIA\nAO AUTOR DA MINHA FÉ.\n\nFINAL:\n(ALELUIA) GLÓRIA AO SENHOR, (3X)\n(ALELUIA) O AUTOR DA MINHA FÉ;\n(ALELUIA) GLÓRIA AO SENHOR, (2X)\n(ALELUIA) O AUTOR DA MINHA FÉ.\nBIS', 'Ó PAI, EU QUERIA TANTO VER O MEU SENHOR DESCER VINDO ME ENCONTRAR, EU POSSO ATÉ IMAGINAR A REFULGENTE GLÓRIA DO SENHOR JESUS, TRANSPONDO AS BRANCAS NUVENS NO MAIS PURO AZUL ONDE NEM SUL, NEM NORTE EXISTIRÁ. E EM MEIO A LÁGRIMAS, SORRISOS DE ALEGRIA E DE PRAZER, EU QUE ERA CEGO AGORA POSSO VER, CONTEMPLAR, CONTEMPLAR, ENFIM, POR ISSO EU CANTO GLÓRIA.  GLÓRIA, GLÓRIA AO AUTOR DA MINHA FÉ. Ó PAI, EU QUERIA TANTO OUVIR O SOM QUE VAI ABRIR O ENCONTRO TRIUNFAL, REVER AMIGOS QUE UM DIA EM CRISTO FORAM FEITOS MEUS IRMÃOS, E AGORA, SIM, PODEMOS DAR AS MÃOS POIS TEMOS TODOS UM, SOMENTE UM, UM SÓ SENHOR; E EIS O CONSOLO QUE ENVOLVE A MINHA VIDA, O MEU SENHOR JESUS QUE FOI MORTO, SIM, NAQUELA CRUZ, VOLTARÁ, VOLTARÁ, ENFIM, POR ISSO EU CANTO GLÓRIA.  GLÓRIA, GLÓRIA AO AUTOR DA MINHA FÉ. (ALELUIA) GLÓRIA AO SENHOR,  (ALELUIA) O AUTOR DA MINHA FÉ; (ALELUIA) GLÓRIA AO SENHOR,  (ALELUIA) O AUTOR DA MINHA FÉ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('674', '674 - EU NÃO SEI QUAL SERÁ O LOUVOR', 'EU NÃO SEI QUAL SERÁ MEU LOUVOR\nAO OUVIR A VOZ DO SENHOR:\n“VINDE, BENDITOS DE MEU PAI,\nPOSSUÍ O REINO DE AMOR E PAZ;\nREDIMIDOS DO SENHOR!”\n\nO QUE DEUS TEM PREPARADO PARA NÓS,\nUM LINDO CÉU DE AMOR E PAZ!\nVIVEREMOS PARA SEMPRE COM O SENHOR!\nOLHO HUMANO NUNCA VIU, SOM ALGUM \nJAMAIS SE OUVIU DAS MORADAS DO SENHOR.\n\nINSTRUMENTOS\n\nO QUE DEUS TEM PREPARADO PARA NÓS,\nUM LINDO CÉU DE AMOR E PAZ!\nVIVEREMOS PARA SEMPRE COM O SENHOR!\nOLHO HUMANO NUNCA VIU, SOM ALGUM \nJAMAIS SE OUVIU DAS MORADAS DO SENHOR.\n\nFINAL: \n“VINDE, BENDITOS DE MEU PAI,\nPOSSUÍ O REINO DE AMOR E PAZ”.', 'EU NÃO SEI QUAL SERÁ MEU LOUVOR AO OUVIR A VOZ DO SENHOR: VINDE, BENDITOS DE MEU PAI, POSSUÍ O REINO DE AMOR E PAZ; REDIMIDOS DO SENHOR! O QUE DEUS TEM PREPARADO PARA NÓS, UM LINDO CÉU DE AMOR E PAZ! VIVEREMOS PARA SEMPRE COM O SENHOR! OLHO HUMANO NUNCA VIU, SOM ALGUM JAMAIS SE OUVIU DAS MORADAS DO SENHOR. INSTRUMENTOS O QUE DEUS TEM PREPARADO PARA NÓS, UM LINDO CÉU DE AMOR E PAZ! VIVEREMOS PARA SEMPRE COM O SENHOR! OLHO HUMANO NUNCA VIU, SOM ALGUM JAMAIS SE OUVIU DAS MORADAS DO SENHOR.  VINDE, BENDITOS DE MEU PAI, POSSUÍ O REINO DE AMOR E PAZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('675', '675 - NA ETERNIDADE', 'NA ETERNIDADE, AO LADO DE JESUS,\nANSEIO LOGO ESTAR E ESTE MUNDO DEIXAR;\nQUERO COM OS ANJOS FORMAR UM GRANDE \nCORO E CANTAR LOUVORES \nDE ADORAÇÃO AO SENHOR.\nO CORO DOS ANJOS E AS VOZES \nDOS ARCANJOS JÁ LOUVAM A JESUS.\n\nCORO\nBREVE OS REMIDOS NA GLÓRIA ESTARÃO\nE JUNTO COM OS ANJOS \nE ARCANJOS VÃO PARA SEMPRE LOUVAR,\nLOUVAR A JESUS.\n\nINSTRUMENTOS\n\nO MEU CORAÇÃO TRANSBORDA DE ALEGRIA \nAO IMAGINAR QUE GRANDE DIA SERÁ;\nO CORDEIRO SANTO VIRÁ SOBRE \nAS NUVENS E A SUA IGREJA \nAO SEU ENCONTRO SUBIRÁ.\nSAUDAI A JESUS! ELE É O REI \nDA GLÓRIA QUE VIRÁ NOS BUSCAR.\n\nCORO\nBREVE OS REMIDOS NA GLÓRIA ESTARÃO\nE JUNTO COM OS ANJOS \nE ARCANJOS VÃO PARA SEMPRE LOUVAR,\nLOUVAR A JESUS.\n\nSAUDAI A JESUS! ELE É O REI DA GLÓRIA \nQUE VIRÁ NOS BUSCAR.\nCORO\nBREVE OS REMIDOS NA GLÓRIA ESTARÃO\nE JUNTO COM OS ANJOS \nE ARCANJOS VÃO PARA SEMPRE LOUVAR,  \nLOUVAR A JESUS.\n\nFINAL: LOUVAR A JESUS!', 'NA ETERNIDADE, AO LADO DE JESUS, ANSEIO LOGO ESTAR E ESTE MUNDO DEIXAR; QUERO COM OS ANJOS FORMAR UM GRANDE CORO E CANTAR LOUVORES DE ADORAÇÃO AO SENHOR. O CORO DOS ANJOS E AS VOZES DOS ARCANJOS JÁ LOUVAM A JESUS. CORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS. INSTRUMENTOS O MEU CORAÇÃO TRANSBORDA DE ALEGRIA AO IMAGINAR QUE GRANDE DIA SERÁ; O CORDEIRO SANTO VIRÁ SOBRE AS NUVENS E A SUA IGREJA AO SEU ENCONTRO SUBIRÁ. SAUDAI A JESUS! ELE É O REI DA GLÓRIA QUE VIRÁ NOS BUSCAR. CORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS. SAUDAI A JESUS! ELE É O REI DA GLÓRIA QUE VIRÁ NOS BUSCAR. CORO BREVE OS REMIDOS NA GLÓRIA ESTARÃO E JUNTO COM OS ANJOS E ARCANJOS VÃO PARA SEMPRE LOUVAR, LOUVAR A JESUS.  LOUVAR A JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('676', '676 - Ó VINDE ADORAR', 'Ó VINDE ADORAR AO DEUS CRIADOR,\nDA TERRA E DOS CÉUS, ETERNO SENHOR;\nAO DEUS QUE, HABITANDO NO REINO DA LUZ,\nA NÓS SE REVELA POR CRISTO JESUS.\n\nSEU GRANDE PODER DEVEIS CONTEMPLAR,\nNO CÉU ESTRELADO, NO AMPLO MAR;\nA GOTA DE ORVALHO, A MÍNIMA FLOR\nPROCLAMAM CONSTANTES SER DEUS SEU AUTOR.\n\nÓ VINDE ADORAR AO DEUS SALVADOR,\nQUE EM CRISTO JESUS NOS MOSTRA FAVOR;\nAO DEUS QUE, BENIGNO, COM GRAÇA \nE PERDÃO, PECADOS COBRINDO, \nNOS DÁ REDENÇÃO.', 'Ó VINDE ADORAR AO DEUS CRIADOR, DA TERRA E DOS CÉUS, ETERNO SENHOR; AO DEUS QUE, HABITANDO NO REINO DA LUZ, A NÓS SE REVELA POR CRISTO JESUS. SEU GRANDE PODER DEVEIS CONTEMPLAR, NO CÉU ESTRELADO, NO AMPLO MAR; A GOTA DE ORVALHO, A MÍNIMA FLOR PROCLAMAM CONSTANTES SER DEUS SEU AUTOR. Ó VINDE ADORAR AO DEUS SALVADOR, QUE EM CRISTO JESUS NOS MOSTRA FAVOR; AO DEUS QUE, BENIGNO, COM GRAÇA E PERDÃO, PECADOS COBRINDO, NOS DÁ REDENÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('677', '677 - A TI, QUE HABITAS ENTRE OS QUERUBINS', 'A TI, QUE HABITAS ENTRE OS QUERUBINS,\nE ÉS DEUS DA ETERNIDADE, \nA TI, COMPASSIVO, PAI DE AMOR,\nPROSTRADO EU ADORO.\n\nA TI, QUE ÉS TÃO PURO E PERFEITO, TEU ROSTO NINGUÉM PODE CONTEMPLAR;\nLOUVORES E HONRA, GLÓRIA E PODER,\nPRA SEMPRE, SEMPRE, AMÉM.\n\nA TI, QUE PASSEIAS ENTRE OS CASTIÇAIS,\nVESTIDO COM VESTES BRANCAS,\nA TI, CUJO ROSTO BRILHA COMO O SOL,\nPROSTRADO EU ADORO.\n\nA TI, O PRIMEIRO E O ÚLTIMO,\nA TI, QUE MORRESTE MAS VIVO ESTÁS,\nLOUVORES E HONRA, GLÓRIA E PODER,\nPRA SEMPRE, SEMPRE, AMÉM.', 'A TI, QUE HABITAS ENTRE OS QUERUBINS, E ÉS DEUS DA ETERNIDADE, A TI, COMPASSIVO, PAI DE AMOR, PROSTRADO EU ADORO. A TI, QUE ÉS TÃO PURO E PERFEITO, TEU ROSTO NINGUÉM PODE CONTEMPLAR; LOUVORES E HONRA, GLÓRIA E PODER, PRA SEMPRE, SEMPRE, AMÉM. A TI, QUE PASSEIAS ENTRE OS CASTIÇAIS, VESTIDO COM VESTES BRANCAS, A TI, CUJO ROSTO BRILHA COMO O SOL, PROSTRADO EU ADORO. A TI, O PRIMEIRO E O ÚLTIMO, A TI, QUE MORRESTE MAS VIVO ESTÁS, LOUVORES E HONRA, GLÓRIA E PODER, PRA SEMPRE, SEMPRE, AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('678', '678 - TUA GLÓRIA ENCHE TODA A TERRA', 'TUA GLÓRIA ENCHE TODA A TERRA,\nTEU NOME É SOBRE TODO NOME,\nTU ÉS O DONO DA MINHA VIDA,\nÉ SÓ EM TI QUE TENHO SALVAÇÃO!\nTU ÉS O DONO DO MEU QUERER\nE OS MEUS LÁBIOS\nPRA SEMPRE TE ADORARÃO.\nBIS\n\nE COM OS ANJOS TE ADORAREI\nE AOS TEUS PÉS ME PROSTRAREI;\nTU ÉS MINHA VIDA E O MEU LOUVOR\nE AO TEU NOME EXALTAREI.\n\nTUA GLÓRIA ENCHE TODA A TERRA,\nTEU NOME É SOBRE TODO NOME,\nTU ÉS O DONO DA MINHA VIDA,\nÉ SÓ EM TI QUE TENHO SALVAÇÃO!\nTU ÉS O DONO DO MEU QUERER\nE OS MEUS LÁBIOS\nPRA SEMPRE TE ADORARÃO. (3X)\n\nAQUELE QUE MORREU\nMAS A MORTE VENCEU (BIS)\nAQUELE QUE RESSUSCITOU,\nSOBRE A MORTE TRIUNFOU. (BIS)\n\nE AGORA ASSENTADO ESTÁ \nÀ DIREITA DE DEUS PAI,\nTEM O PODER SOBRE TERRA E CÉU,\nTEM O NOME SOBRE TODO NOME,\nELE É O CORDEIRO, JESUS CRISTO, SENHOR.\nBIS\n\nAQUELE QUE NOS AMA E COM SEU SANGUE \nNOS LIBERTOU DO PECADO\nE NOS CONSTITUIU REINO E SACERDOTES\nPARA NOSSO DEUS E PAI, A ELE A GLÓRIA\nE O DOMÍNIO PELOS SÉCULOS DOS SÉCULOS, AMÉM.\n\nA ELE A GLÓRIA E O DOMÍNIO,\nPELOS SÉCULOS DOS SÉCULOS,\nAMÉM, AMÉM, AMÉM.', 'TUA GLÓRIA ENCHE TODA A TERRA, TEU NOME É SOBRE TODO NOME, TU ÉS O DONO DA MINHA VIDA, É SÓ EM TI QUE TENHO SALVAÇÃO! TU ÉS O DONO DO MEU QUERER E OS MEUS LÁBIOS PRA SEMPRE TE ADORARÃO. E COM OS ANJOS TE ADORAREI E AOS TEUS PÉS ME PROSTRAREI; TU ÉS MINHA VIDA E O MEU LOUVOR E AO TEU NOME EXALTAREI. AQUELE QUE MORREU MAS A MORTE VENCEU  AQUELE QUE RESSUSCITOU, SOBRE A MORTE TRIUNFOU. E AGORA ASSENTADO ESTÁ À DIREITA DE DEUS PAI, TEM O PODER SOBRE TERRA E CÉU, TEM O NOME SOBRE TODO NOME, ELE É O CORDEIRO, JESUS CRISTO, SENHOR. AQUELE QUE NOS AMA E COM SEU SANGUE NOS LIBERTOU DO PECADO E NOS CONSTITUIU REINO E SACERDOTES PARA NOSSO DEUS E PAI, A ELE A GLÓRIA E O DOMÍNIO PELOS SÉCULOS DOS SÉCULOS, AMÉM. A ELE A GLÓRIA E O DOMÍNIO, PELOS SÉCULOS DOS SÉCULOS, AMÉM, AMÉM, AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('679', '679 - EIS A ESCRAVA RESGATADA', 'EIS A ESCRAVA RESGATADA!\nGRANDE PREÇO CRISTO DEU;\nNÃO FOI OURO, NEM FOI PRATA,\nPRÓPRIO SANGUE ELE VERTEU.\n\nCORO\nTANTO FOI O TEU AMOR\nQUE POR MIM ASSIM MOSTRASTE;\nPRA REMIR MEU CATIVEIRO\nTUA VIDA NÃO POUPASTE!\n\nJÁ AGORA QUE SOU TUA,\nSEM JAMAIS A TI PERDER,\nQUERO, MEU SENHOR, SERVIR-TE\nGRATA, E SÓ PARA TI VIVER.\n\nCORO\nTANTO FOI O TEU AMOR\nQUE POR MIM ASSIM MOSTRASTE;\nPRA REMIR MEU CATIVEIRO\nTUA VIDA NÃO POUPASTE!\n\nQUERO RECEBER TEU JUGO,\nE EM TEUS PASSOS CAMINHAR;\nSE POR TI EU SOFRO TUDO,\nVOU CONTIGO EM PAZ REINAR.\n\nCORO\nTANTO FOI O TEU AMOR\nQUE POR MIM ASSIM MOSTRASTE;\nPRA REMIR MEU CATIVEIRO\nTUA VIDA NÃO POUPASTE!\n\nESTÁS NO CÉU! VIVO EU NA TERRA,\nESPERANDO O TEU VOLTAR;\nLEVARÁS ENTÃO A ESCRAVA\nPRA CONTIGO ALI FICAR.\n\nCORO\nTANTO FOI O TEU AMOR\nQUE POR MIM ASSIM MOSTRASTE;\nPRA REMIR MEU CATIVEIRO\nTUA VIDA NÃO POUPASTE!\n\nTODO O AMOR POR MIM SENTIAS,\nPADECENDO A DOR DA CRUZ,\nVESTE-ME DA TUA GLÓRIA!\nVEM, Ó VEM, SENHOR JESUS.\n\nCORO\nTANTO FOI O TEU AMOR\nQUE POR MIM ASSIM MOSTRASTE;\nPRA REMIR MEU CATIVEIRO\nTUA VIDA NÃO POUPASTE!', 'EIS A ESCRAVA RESGATADA! GRANDE PREÇO CRISTO DEU; NÃO FOI OURO, NEM FOI PRATA, PRÓPRIO SANGUE ELE VERTEU. CORO TANTO FOI O TEU AMOR QUE POR MIM ASSIM MOSTRASTE; PRA REMIR MEU CATIVEIRO TUA VIDA NÃO POUPASTE! JÁ AGORA QUE SOU TUA, SEM JAMAIS A TI PERDER, QUERO, MEU SENHOR, SERVIR-TE GRATA, E SÓ PARA TI VIVER. QUERO RECEBER TEU JUGO, E EM TEUS PASSOS CAMINHAR; SE POR TI EU SOFRO TUDO, VOU CONTIGO EM PAZ REINAR. ESTÁS NO CÉU! VIVO EU NA TERRA, ESPERANDO O TEU VOLTAR; LEVARÁS ENTÃO A ESCRAVA PRA CONTIGO ALI FICAR. TODO O AMOR POR MIM SENTIAS, PADECENDO A DOR DA CRUZ, VESTE-ME DA TUA GLÓRIA! VEM, Ó VEM, SENHOR JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('680', '680 - TEU NOME É PRA MIM, SENHOR', 'TEU NOME É PRA MIM, SENHOR \nMARAVILHOSO, DEUS FORTE;\nTEU NOME É PRA MIM, SENHOR \nMARAVILHOSO, PRÍNCIPE DA PAZ.\n\nCORO (2X)\nEMANUEL É DEUS CONOSCO,\nCONOSCO ESTÁ, PRA SEMPRE VIVERÁ.\n\nTEU NOME É PRA MIM, SENHOR \nCONSELHEIRO, PAI DA ETERNIDADE;\nTEU NOME É PRA MIM, SENHOR \nCONSELHEIRO, PAI DA ETERNIDADE.\n\nCORO (3X)\nEMANUEL É DEUS CONOSCO,\nCONOSCO ESTÁ, PRA SEMPRE VIVERÁ.\n \nFINAL: \nTEU NOME É PRA MIM, \nSENHOR, JESUS.', 'TEU NOME É PRA MIM, SENHOR MARAVILHOSO, DEUS FORTE; TEU NOME É PRA MIM, SENHOR MARAVILHOSO, PRÍNCIPE DA PAZ. EMANUEL É DEUS CONOSCO, CONOSCO ESTÁ, PRA SEMPRE VIVERÁ. TEU NOME É PRA MIM, SENHOR CONSELHEIRO, PAI DA ETERNIDADE; TEU NOME É PRA MIM, SENHOR CONSELHEIRO, PAI DA ETERNIDADE. CORO  EMANUEL É DEUS CONOSCO, CONOSCO ESTÁ, PRA SEMPRE VIVERÁ.  TEU NOME É PRA MIM, SENHOR, JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('681', '681 - NOSSO LOUVOR', 'NOSSO LOUVOR VAMOS TE OFERTAR\nPELO QUE ÉS, PELO QUE FARÁS;\nNÃO HÁ PALAVRAS QUE POSSAM \nEXPRESSAR NOSSA ADORAÇÃO A TI, \nSENHOR.\n\nINSTRUMENTOS\n\nCOM GRATIDÃO (COM GRATIDÃO)\nVAMOS TE ADORAR (ADORAR)\nPELO QUE ÉS, PELO QUE FARÁS, (EXPRESSAR)\nNÃO HÁ PALAVRAS QUE POSSAM EXPRESSAR \nNOSSO AMOR A TI, SENHOR.\n\nCORO\nMAJESTOSO (MAJESTOSO)\nPODEROSO ÉS, GRAÇAS TE DAMOS \nPOR TUA MISERICÓRDIA,\nA TI PERTENCE TODO O LOUVOR.\n\nINSTRUMENTOS\n\nNOSSO REFÚGIO E DIREÇÃO, (ÉS TU, SENHOR)\nNOSSO AMPARO E PROTEÇÃO, \nNOSSO ABRIGO NO TEMPORAL ÉS TU, SENHOR.\nCOM GRATIDÃO VAMOS TE ADORAR, \nEM TEU ALTAR VAMOS OFERTAR NOSSO LOUVOR, \nQUE É FRUTO DO TEU AMOR.\n\nCORO\nMAJESTOSO (MAJESTOSO)\nPODEROSO ÉS, GRAÇAS TE DAMOS \nPOR TUA MISERICÓRDIA,\nA TI PERTENCE TODO O LOUVOR.\n\nFINAL: \nMAJESTOSO (MAJESTOSO)\nPODEROSO ÉS, GRAÇAS TE DAMOS\nA TI PERTENCE TODO O LOUVOR. (2X)', 'NOSSO LOUVOR VAMOS TE OFERTAR PELO QUE ÉS, PELO QUE FARÁS; NÃO HÁ PALAVRAS QUE POSSAM EXPRESSAR NOSSA ADORAÇÃO A TI, SENHOR. INSTRUMENTOS COM GRATIDÃO (COM GRATIDÃO) VAMOS TE ADORAR (ADORAR) PELO QUE ÉS, PELO QUE FARÁS, (EXPRESSAR) NÃO HÁ PALAVRAS QUE POSSAM EXPRESSAR NOSSO AMOR A TI, SENHOR. CORO MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS POR TUA MISERICÓRDIA, A TI PERTENCE TODO O LOUVOR. INSTRUMENTOS NOSSO REFÚGIO E DIREÇÃO, (ÉS TU, SENHOR) NOSSO AMPARO E PROTEÇÃO, NOSSO ABRIGO NO TEMPORAL ÉS TU, SENHOR. COM GRATIDÃO VAMOS TE ADORAR, EM TEU ALTAR VAMOS OFERTAR NOSSO LOUVOR, QUE É FRUTO DO TEU AMOR. CORO MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS POR TUA MISERICÓRDIA, A TI PERTENCE TODO O LOUVOR. MAJESTOSO (MAJESTOSO) PODEROSO ÉS, GRAÇAS TE DAMOS A TI PERTENCE TODO O LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('682', '682 - A JESUS SEJA DADA TODA GLÓRIA', 'CORO \nA JESUS SEJA DADA TODA GLÓRIA E LOUVOR, \nA JESUS SEJA DADA\nTODA GLÓRIA, HONRA E LOUVOR.\n\nESTÁ ASSENTADO NO TRONO,\nÀ DIREITA DE DEUS PAI.\nESTÁ ASSENTADO NO TRONO,\nÀ DIREITA DE DEUS PAI.\nESTÁ ASSENTADO NO TRONO,\nÀ DIREITA DE DEUS PAI.\nESTÁ ASSENTADO NO TRONO,\nÀ DIREITA DE DEUS PAI.\n\nCORO \nA JESUS SEJA DADA TODA GLÓRIA E LOUVOR, \nA JESUS SEJA DADA\nTODA GLÓRIA, HONRA E LOUVOR.\n\nQUEREMOS TE ADORAR EM ESPÍRITO \nE EM VERDADE, A VERDADEIRA ADORAÇÃO \nÉ A QUE PROCEDE DO CORAÇÃO, DO CORAÇÃO\nDOS QUE LOUVAM AO SENHOR.\n\nCORO (2X)\nA JESUS SEJA DADA TODA GLÓRIA E LOUVOR, \nA JESUS SEJA DADA\nTODA GLÓRIA, HONRA E LOUVOR.', 'CORO A JESUS SEJA DADA TODA GLÓRIA E LOUVOR, A JESUS SEJA DADA TODA GLÓRIA, HONRA E LOUVOR. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. ESTÁ ASSENTADO NO TRONO, À DIREITA DE DEUS PAI. QUEREMOS TE ADORAR EM ESPÍRITO E EM VERDADE, A VERDADEIRA ADORAÇÃO É A QUE PROCEDE DO CORAÇÃO, DO CORAÇÃO DOS QUE LOUVAM AO SENHOR. A JESUS SEJA DADA TODA GLÓRIA E LOUVOR, A JESUS SEJA DADA TODA GLÓRIA, HONRA E LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('683', '683 - JESUS CRISTO VOLTARÁ', 'JESUS CRISTO VOLTARÁ PRA NOS LEVAR,\nSUA IGREJA FIEL SUBIRÁ PRA GLÓRIA;\nDEIXAREMOS ESTE MUNDO DE TRISTEZA,\nPARA ESTAR COM NOSSO AMADO SALVADOR.\n\nCORO\nLOUVAREMOS A JESUS, O REI,\nCANTAREMOS UM HINO DE VITÓRIA,\nCOM OS ANJOS ESTAREMOS\nNAS MAIS BELAS MANSÕES CELESTIAIS.\n\nALELUIA, VAMOS TODOS ADORAR\nA JESUS, O LEÃO DA TRIBO DE JUDÁ,\nQUE FOI MORTO MAS RESSUSCITOU,\nVAMOS CANTAR (VAMOS CANTAR)\nESTE GRANDE AMOR DO NOSSO SALVADOR.\n\nCORO\nLOUVAREMOS A JESUS, O REI,\nCANTAREMOS UM HINO DE VITÓRIA,\nCOM OS ANJOS ESTAREMOS\nNAS MAIS BELAS MANSÕES CELESTIAIS.\n\nALELUIA, VAMOS TODOS ADORAR\nA JESUS, O LEÃO DA TRIBO DE JUDÁ,\nQUE FOI MORTO MAS RESSUSCITOU,\nVAMOS CANTAR (VAMOS CANTAR)\nESTE GRANDE AMOR DO NOSSO SALVADOR.\n\nCORO\nLOUVAREMOS A JESUS, O REI,\nCANTAREMOS UM HINO DE VITÓRIA,\nCOM OS ANJOS ESTAREMOS\nNAS MAIS BELAS MANSÕES CELESTIAIS.', 'JESUS CRISTO VOLTARÁ PRA NOS LEVAR, SUA IGREJA FIEL SUBIRÁ PRA GLÓRIA; DEIXAREMOS ESTE MUNDO DE TRISTEZA, PARA ESTAR COM NOSSO AMADO SALVADOR. CORO LOUVAREMOS A JESUS, O REI, CANTAREMOS UM HINO DE VITÓRIA, COM OS ANJOS ESTAREMOS NAS MAIS BELAS MANSÕES CELESTIAIS. ALELUIA, VAMOS TODOS ADORAR A JESUS, O LEÃO DA TRIBO DE JUDÁ, QUE FOI MORTO MAS RESSUSCITOU, VAMOS CANTAR (VAMOS CANTAR) ESTE GRANDE AMOR DO NOSSO SALVADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('684', '684 - ELE É O REI DOS REIS', 'ELE É O REI DOS REIS,\nA RAIZ DE DAVI, ELE É JESUS.\n\nINSTRUMENTOS\n\nAO QUE ESTÁ NO TRONO SEJA O LOUVOR,\nAO CORDEIRO MORTO QUE RESSUSCITOU,\nÀ RESPLANDECENTE ESTRELA DA MANHÃ,\nSEJA DADA  A GLÓRIA E TODO LOUVOR,\nSEJA DADA  A GLÓRIA E TODO LOUVOR.\n\nCORO\nO LEÃO DA TRIBO DE JUDÁ ESTÁ NO \nTRONO ELE É O CORDEIRO QUE VENCEU;\nO LEÃO DA TRIBO DE JUDÁ ESTÁ NO \nTRONO ELE É O REI DOS REIS, A RAIZ DE DAVI, \nELE É JESUS.\n\nINSTRUMENTOS\n\nLOUVEMOS A GLÓRIA \n(DO REI DOS REIS)\nDAQUELE QUE É VENCEDOR, \n(JESUS VOLTARÁ)\nPORQUE SÃO CHEGADAS \n(JESUS VOLTARÁ)\nAS BODAS DO CORDEIRO.\n\nCORO\nO LEÃO DA TRIBO DE JUDÁ ESTÁ NO \nTRONO ELE É O CORDEIRO QUE VENCEU;\nO LEÃO DA TRIBO DE JUDÁ ESTÁ NO \nTRONO ELE É O REI DOS REIS, A RAIZ DE DAVI, \nELE É JESUS.\n\nFINAL: \nELE É O REI DOS REIS, \nA RAIZ DE DAVI, ELE É JESUS.', 'ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS. INSTRUMENTOS AO QUE ESTÁ NO TRONO SEJA O LOUVOR, AO CORDEIRO MORTO QUE RESSUSCITOU, À RESPLANDECENTE ESTRELA DA MANHÃ, SEJA DADA A GLÓRIA E TODO LOUVOR, SEJA DADA A GLÓRIA E TODO LOUVOR. CORO O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O CORDEIRO QUE VENCEU; O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS. INSTRUMENTOS LOUVEMOS A GLÓRIA (DO REI DOS REIS) DAQUELE QUE É VENCEDOR, (JESUS VOLTARÁ) PORQUE SÃO CHEGADAS (JESUS VOLTARÁ) AS BODAS DO CORDEIRO. CORO O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O CORDEIRO QUE VENCEU; O LEÃO DA TRIBO DE JUDÁ ESTÁ NO TRONO ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS.  ELE É O REI DOS REIS, A RAIZ DE DAVI, ELE É JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('685', '685 - PERFEITO AMOR EU CONHECI', 'PERFEITO AMOR EU CONHECI, \nNÃO IMAGINEI QUE ELE PODERIA EXISTIR;\nAMOR FIEL E VERDADEIRO AMOR, \nTUDO ELE SUPORTOU, ME AMOU ATÉ O FIM.\n\nFOI, ENTÃO, QUE EU SENTI\nCOMO É FORTE O AMOR QUE RECEBI;\nAMOR FIEL E VERDADEIRO AMOR\nÉ JESUS, O MEU SENHOR.\nTENHO EM MIM O SEU AMOR.\n\nDIFERENTE DO AMOR \nQUE NO MUNDO ESTÁ, AMOR PERFEITO \nÉ DOM DE DEUS E SÓ ELE PODE DAR;\nCOM ESTE AMOR EU POSSO CRER, \nTUDO SUPERAR, SEI QUE EM TODA \nA MINHA VIDA ELE NÃO FALHARÁ.\n\nDIFERENTE DO AMOR (DIFERENTE DO AMOR)\nQUE NO MUNDO ESTÁ, AMOR PERFEITO \nÉ DOM DE DEUS E SÓ ELE PODE DAR;\nCOM ESTE AMOR EU POSSO CRER, \n(COM ESTE AMOR EU POSSO CRER) \nTUDO SUPERAR, SEI QUE EM TODA \nA MINHA VIDA ELE NÃO FALHARÁ.\n\nINSTRUMENTOS\n\nFINAL: \nAMOR FIEL E VERDADEIRO AMOR,\nRECEBI DO MEU SENHOR,\nQUE ME AMOU COM ETERNO AMOR.', 'PERFEITO AMOR EU CONHECI, NÃO IMAGINEI QUE ELE PODERIA EXISTIR; AMOR FIEL E VERDADEIRO AMOR, TUDO ELE SUPORTOU, ME AMOU ATÉ O FIM. FOI, ENTÃO, QUE EU SENTI COMO É FORTE O AMOR QUE RECEBI; AMOR FIEL E VERDADEIRO AMOR É JESUS, O MEU SENHOR. TENHO EM MIM O SEU AMOR. DIFERENTE DO AMOR QUE NO MUNDO ESTÁ, AMOR PERFEITO É DOM DE DEUS E SÓ ELE PODE DAR; COM ESTE AMOR EU POSSO CRER, TUDO SUPERAR, SEI QUE EM TODA A MINHA VIDA ELE NÃO FALHARÁ. DIFERENTE DO AMOR (DIFERENTE DO AMOR) QUE NO MUNDO ESTÁ, AMOR PERFEITO É DOM DE DEUS E SÓ ELE PODE DAR; COM ESTE AMOR EU POSSO CRER, (COM ESTE AMOR EU POSSO CRER) TUDO SUPERAR, SEI QUE EM TODA A MINHA VIDA ELE NÃO FALHARÁ. INSTRUMENTOS AMOR FIEL E VERDADEIRO AMOR, RECEBI DO MEU SENHOR, QUE ME AMOU COM ETERNO AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('686', '686 - QUANDO JESUS PROVOU O SEU AMOR POR MIM', 'QUANDO JESUS PROVOU \nO SEU AMOR POR MIM,\nSOFRENDO NA CRUZ, \nDERRAMANDO SEU SANGUE PURO,\nESTAVA COM ELE O AMOR DO PAI.\nQUÃO IMENSURÁVEL ESTE AMOR!\n\nCORO (2X)\nEMBORA EU NÃO MEREÇA,\nFOI A SUA COMPAIXÃO\nQUE ME DEU ETERNA SALVAÇÃO.\n\nINSTRUMENTOS\n\nTODO O MEU LOUVOR, MINHA ADORAÇÃO,\nENTREGO A CRISTO, DONO DO MEU VIVER;\nGLÓRIA E HONRA AO CORDEIRO ETERNO,\nQUE DEU SUA VIDA POR ME AMAR.\n\nCORO (2X)\nEMBORA EU NÃO MEREÇA,\nFOI A SUA COMPAIXÃO\nQUE ME DEU ETERNA SALVAÇÃO.', 'QUANDO JESUS PROVOU O SEU AMOR POR MIM, SOFRENDO NA CRUZ, DERRAMANDO SEU SANGUE PURO, ESTAVA COM ELE O AMOR DO PAI. QUÃO IMENSURÁVEL ESTE AMOR! EMBORA EU NÃO MEREÇA, FOI A SUA COMPAIXÃO QUE ME DEU ETERNA SALVAÇÃO. INSTRUMENTOS TODO O MEU LOUVOR, MINHA ADORAÇÃO, ENTREGO A CRISTO, DONO DO MEU VIVER; GLÓRIA E HONRA AO CORDEIRO ETERNO, QUE DEU SUA VIDA POR ME AMAR. EMBORA EU NÃO MEREÇA, FOI A SUA COMPAIXÃO QUE ME DEU ETERNA SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('687', '687 - QUEM É COMO TU?', 'QUEM É COMO TU?\nFORTE COMO TU, SENHOR,\nCOM TUA FIDELIDADE AO REDOR DE TI?\nQUEM É COMO TU?\nQUE DOMINAS O ÍMPETO DO MAR;\nQUANDO SUAS ONDAS SE LEVANTAM,\nTU FAZES ACALMAR.\n\nCORO\nTEU É O CÉU, TUA A TERRA, \nO MUNDO E A SUA PLENITUDE;\nNORTE E SUL CRIASTE; MISERICÓRDIA \nE VERDADE VÃO PERANTE TUA FACE, \nOH! SENHOR!\n\nCOM TUA PRESENÇA \nOS MEUS PASSOS SE FIRMARÃO,\nMEU PEQUENO BRAÇO SE FORTALECERÁ\nE OS MEUS PENSAMENTOS\nDESCANSARÃO EM TEU ALTAR;\nNENHUMA TEMPESTADE ME ABALARÁ!\n\nCORO\nTEU É O CÉU, TUA A TERRA, \nO MUNDO E A SUA PLENITUDE;\nNORTE E SUL CRIASTE; MISERICÓRDIA \nE VERDADE VÃO PERANTE TUA FACE, \nOH! SENHOR!\n\nFINAL: \nQUEM É COMO TU? \n(Ó SENHOR)', 'QUEM É COMO TU? FORTE COMO TU, SENHOR, COM TUA FIDELIDADE AO REDOR DE TI? QUEM É COMO TU? QUE DOMINAS O ÍMPETO DO MAR; QUANDO SUAS ONDAS SE LEVANTAM, TU FAZES ACALMAR. CORO TEU É O CÉU, TUA A TERRA, O MUNDO E A SUA PLENITUDE; NORTE E SUL CRIASTE; MISERICÓRDIA E VERDADE VÃO PERANTE TUA FACE, OH! SENHOR! COM TUA PRESENÇA OS MEUS PASSOS SE FIRMARÃO, MEU PEQUENO BRAÇO SE FORTALECERÁ E OS MEUS PENSAMENTOS DESCANSARÃO EM TEU ALTAR; NENHUMA TEMPESTADE ME ABALARÁ! CORO TEU É O CÉU, TUA A TERRA, O MUNDO E A SUA PLENITUDE; NORTE E SUL CRIASTE; MISERICÓRDIA E VERDADE VÃO PERANTE TUA FACE, OH! SENHOR!  QUEM É COMO TU? (Ó SENHOR)', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('688', '688 - CRISTO JÁ RESSUSCITOU', 'CRISTO JÁ RESSUSCITOU: ALELUIA!\nSOBRE A MORTE TRIUNFOU: ALELUIA!\nTUDO CONSUMADO ESTÁ! ALELUIA!\nSALVAÇÃO DE GRAÇA DÁ! ALELUIA!\n\nUMA VEZ NA CRUZ SOFREU: ALELUIA!\nUMA VEZ POR NÓS MORREU: ALELUIA!\nMAS AGORA VIVO ESTÁ, ALELUIA!\nE PRA SEMPRE REINARÁ, ALELUIA!\n\nINSTRUMENTOS\n\nGRATOS HINOS ENTOAI, ALELUIA!\nA JESUS O GRANDE REI, ALELUIA!\nPOIS À MORTE QUIS BAIXAR, ALELUIA!\nPECADORES PRA SALVAR! ALELUIA!', 'CRISTO JÁ RESSUSCITOU: ALELUIA! SOBRE A MORTE TRIUNFOU: ALELUIA! TUDO CONSUMADO ESTÁ! ALELUIA! SALVAÇÃO DE GRAÇA DÁ! ALELUIA! UMA VEZ NA CRUZ SOFREU: ALELUIA! UMA VEZ POR NÓS MORREU: ALELUIA! MAS AGORA VIVO ESTÁ, ALELUIA! E PRA SEMPRE REINARÁ, ALELUIA! INSTRUMENTOS GRATOS HINOS ENTOAI, ALELUIA! A JESUS O GRANDE REI, ALELUIA! POIS À MORTE QUIS BAIXAR, ALELUIA! PECADORES PRA SALVAR! ALELUIA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('689', '689 - AMOR, QUE POR AMOR DESCESTE!', 'AMOR, QUE POR AMOR DESCESTE!\nAMOR, QUE POR AMOR MORRESTE!\nAH! QUANTA DOR NÃO PADECESTE,\nMEU CORAÇÃO PRA CONQUISTAR\nE MEU AMOR GANHAR!\n\nAMOR, QUE COM AMOR SEGUIAS \nA MIM, QUE SEM AMOR TU VIAS!\nOH! QUANTO AMOR POR MIM SENTIAS,\nMEU SALVADOR, MEU BOM JESUS,\nSOFRENDO SOBRE A CRUZ!\n\nINSTRUMENTOS\n\nAMOR, QUE TUDO ME PERDOAS!\nAMOR, QUE ATÉ MESMO ABENÇOAS\nUM RÉU A QUEM TU TE AFEIÇOAS!\nPOR TI VENCIDO, Ó SALVADOR,\nEIS-ME AOS TEUS PÉS, SENHOR.\n\nAMOR QUE NUNCA, NUNCA MUDA,\nQUE EM TUA GRAÇA ME SEGURA,\nCERCANDO-ME DE MIL VENTURAS;\nACEITA AGORA, Ó SALVADOR,\nO MEU HUMILDE AMOR!', 'AMOR, QUE POR AMOR DESCESTE! AMOR, QUE POR AMOR MORRESTE! AH! QUANTA DOR NÃO PADECESTE, MEU CORAÇÃO PRA CONQUISTAR E MEU AMOR GANHAR! AMOR, QUE COM AMOR SEGUIAS A MIM, QUE SEM AMOR TU VIAS! OH! QUANTO AMOR POR MIM SENTIAS, MEU SALVADOR, MEU BOM JESUS, SOFRENDO SOBRE A CRUZ! INSTRUMENTOS AMOR, QUE TUDO ME PERDOAS! AMOR, QUE ATÉ MESMO ABENÇOAS UM RÉU A QUEM TU TE AFEIÇOAS! POR TI VENCIDO, Ó SALVADOR, EIS-ME AOS TEUS PÉS, SENHOR. AMOR QUE NUNCA, NUNCA MUDA, QUE EM TUA GRAÇA ME SEGURA, CERCANDO-ME DE MIL VENTURAS; ACEITA AGORA, Ó SALVADOR, O MEU HUMILDE AMOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('690', '690 - DA IGREJA O FUNDAMENTO  (A PEDRA FUNDAMENTAL)', 'DA IGREJA O FUNDAMENTO É CRISTO, O SALVADOR;\nEM SEU PODER DESCANSA E É FORTE EM SEU AMOR.\nEM CRISTO BEM FIRMADA, SEGURA SEMPRE ESTÁ,\nE, SOBRE A ROCHA ETERNA JAMAIS SE ABALARÁ.\n\nA PEDRA PRECIOSA QUE DEUS PREDESTINOU \nSUSTENTA PEDRAS VIVAS, QUE A GRAÇA TRABALHOU.\nE, QUANDO O MONUMENTO SURGIR EM PLENA LUZ,\nA GLÓRIA DO EDIFÍCIO SERÁ DO REI JESUS!\n\nSENHOR, NÓS TE ROGAMOS QUE, \nERGUIDO POR AMOR, \nO TEMPLO CONSAGRADO \nREDUNDE EM TEU LOUVOR;\nE QUE ALMAS REDIMIDAS AQUI, \nEM COMUNHÃO, SE TORNEM TEMPLO \nSANTO DA TUA HABITAÇÃO. \n\nFINAL: AMÉM!', 'DA IGREJA O FUNDAMENTO É CRISTO, O SALVADOR; EM SEU PODER DESCANSA E É FORTE EM SEU AMOR. EM CRISTO BEM FIRMADA, SEGURA SEMPRE ESTÁ, E, SOBRE A ROCHA ETERNA JAMAIS SE ABALARÁ. A PEDRA PRECIOSA QUE DEUS PREDESTINOU SUSTENTA PEDRAS VIVAS, QUE A GRAÇA TRABALHOU. E, QUANDO O MONUMENTO SURGIR EM PLENA LUZ, A GLÓRIA DO EDIFÍCIO SERÁ DO REI JESUS! SENHOR, NÓS TE ROGAMOS QUE, ERGUIDO POR AMOR, O TEMPLO CONSAGRADO REDUNDE EM TEU LOUVOR; E QUE ALMAS REDIMIDAS AQUI, EM COMUNHÃO, SE TORNEM TEMPLO SANTO DA TUA HABITAÇÃO.  AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('691', '691 - AO DEUS DE ABRAÃO', 'AO DEUS DE ABRAÃO LOUVAI; \nDO VASTO CÉU SENHOR; \nETERNO E PODEROSO PAIE DEUS DE AMOR.\nAUGUSTO DEUS JEOVÁ, \nQUE TERRA E CÉU CRIOU; \nMINH'' ALMA O NOME ABENÇOARÁ\nDO GRANDE EU SOU.\n\nAO DEUS DE ABRAÃO LOUVAI; \nEIS, POR MANDADO SEU, \nMINH'' ALMA DEIXA A TERRA, \n E VAI GOZAR NO CÉU.\nO MUNDO DESPREZEI, \nSEU LUCRO E SEU LOUVOR, \nE DEUS POR MEU SENHOR \nTOMEI E PROTETOR.\n\nMEU GUIA DEUS SERÁ SEU INFINITO \nAMOR FELIZ EM TUDO ME FARÁ \nPOR ONDE EU FOR TOMOU-ME PELA MÃO, \nEM TREVAS DEU-ME LUZ, \nE DÁ-ME ETERNA SALVAÇÃO,\nPOR MEU JESUS.\n\nMEU DEUS POR SI JUROU, E NELE CONFIEI, \nE PARA O CÉU QUE PREPAROU EU SUBIREI.\nSUA FACE EU HEI DE VER, CONFIADO EM SEU AMOR, \nE EU HEI DE SEMPRE ENGRANDECER MEU REDENTOR.', 'AO DEUS DE ABRAÃO LOUVAI; DO VASTO CÉU SENHOR; ETERNO E PODEROSO PAIE DEUS DE AMOR. AUGUSTO DEUS JEOVÁ, QUE TERRA E CÉU CRIOU; MINH'' ALMA O NOME ABENÇOARÁ DO GRANDE EU SOU. AO DEUS DE ABRAÃO LOUVAI; EIS, POR MANDADO SEU, MINH'' ALMA DEIXA A TERRA, E VAI GOZAR NO CÉU. O MUNDO DESPREZEI, SEU LUCRO E SEU LOUVOR, E DEUS POR MEU SENHOR TOMEI E PROTETOR. MEU GUIA DEUS SERÁ SEU INFINITO AMOR FELIZ EM TUDO ME FARÁ POR ONDE EU FOR TOMOU-ME PELA MÃO, EM TREVAS DEU-ME LUZ, E DÁ-ME ETERNA SALVAÇÃO, POR MEU JESUS. MEU DEUS POR SI JUROU, E NELE CONFIEI, E PARA O CÉU QUE PREPAROU EU SUBIREI. SUA FACE EU HEI DE VER, CONFIADO EM SEU AMOR, E EU HEI DE SEMPRE ENGRANDECER MEU REDENTOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('692', '692 - COMIGO HABITA, Ó DEUS!', 'COMIGO HABITA, Ó DEUS!\nA NOITE VEM, AS TREVAS CRESCEM;\nEIS, SENHOR, CONVÉM\nQUE ME SOCORRA A TUA PROTEÇÃO;\nOH! VEM FAZER COMIGO HABITAÇÃO!\n\nVEM REVELAR-TE A MIM, JESUS, SENHOR!\nMESTRE DIVINO! REI! CONSOLADOR!\nÉ QUEM ME GUIA! AMPARO EM PROVAÇÃO!\nVEM, VEM FAZER COMIGO HABITAÇÃO.\n\nPRESENTE ESTÁS NAS TREVAS OU NA LUZ!\nNÃO HÁ PERIGO, ANDANDO COM JESUS!\nPARTIR COM CRISTO, ETERNA REDENÇÃO,\nONDE MEU DEUS FIZER HABITAÇÃO.\n\nQUE ALEGRIA, QUE SATISFAÇÃO\nVER O SENHOR, A GRANDE SALVAÇÃO!\nEXULTAREMOS NA RESSURREIÇÃO;\nTENHO COM DEUS ETERNA HABITAÇÃO.', 'COMIGO HABITA, Ó DEUS! A NOITE VEM, AS TREVAS CRESCEM; EIS, SENHOR, CONVÉM QUE ME SOCORRA A TUA PROTEÇÃO; OH! VEM FAZER COMIGO HABITAÇÃO! VEM REVELAR-TE A MIM, JESUS, SENHOR! MESTRE DIVINO! REI! CONSOLADOR! É QUEM ME GUIA! AMPARO EM PROVAÇÃO! VEM, VEM FAZER COMIGO HABITAÇÃO. PRESENTE ESTÁS NAS TREVAS OU NA LUZ! NÃO HÁ PERIGO, ANDANDO COM JESUS! PARTIR COM CRISTO, ETERNA REDENÇÃO, ONDE MEU DEUS FIZER HABITAÇÃO. QUE ALEGRIA, QUE SATISFAÇÃO VER O SENHOR, A GRANDE SALVAÇÃO! EXULTAREMOS NA RESSURREIÇÃO; TENHO COM DEUS ETERNA HABITAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('693', '693 - DORMINDO NO MEU LEITO(JERUSALÉM, CIDADE SANTA)', 'DORMINDO NO MEU LEITO,\nEM SONHO ENCANTADOR,\nUM DIA EU VI JERUSALÉM\nE O TEMPLO DO SENHOR.\nOUVI CANTAR CRIANÇAS\nE EM MEIO AO SEU CANTAR.\n\nROMPEU A VOZ DOS ANJOS,\nDO CÉU A PROCLAMAR:\n“JERUSALÉM! JERUSALÉM!\nCANTAI, Ó SANTA GREI!\nHOSANA! HOSANA!\nHOSANA AO VOSSO REI!”\n\nINSTRUMENTOS\nBIS\n\nENTÃO O SONHO SE ALTEROU,\nNÃO MAIS O SOM FELIZ\nOUVIA DAS HOSANAS \nDOS COROS INFANTIS.\nO AR EM TORNO SE ESFRIOU,\nDO SOL FALTAVA A LUZ;\n\nE NUM ALTO E TOSCO MONTE \nVI O VULTO DE UMA CRUZ.\n“JERUSALÉM! JERUSALÉM!\n(AOS ANJOS ESCUTEI)\nHOSANA! HOSANA!\nHOSANA AO VOSSO REI!”\n\nINSTRUMENTOS\nBIS\n\nAINDA A CENA SE MUDOU;\nSURGIA EM RESPLENDOR\nA DIVINAL CIDADE, \nMORADA DO SENHOR.\nDA LUA NÃO BRILHAVA A LUZ,\nNEM SOL NASCIA LÁ,\nMAS SÓ FULGIA A LUZ DE DEUS,\nMUI PURA EM SEU BRILHAR,\n\nE TODOS QUE QUERIAM, SIM,\nPODIAM LOGO ENTRAR\nNA MUI FELIZ JERUSALÉM,\nQUE NUNCA PASSARÁ.\n“JERUSALÉM! JERUSALÉM!\nTEU DIA VAI RAIAR!\nHOSANA! HOSANA!\nHOSANA SEM CESSAR!\nHOSANA! HOSANA! \nHOSANA SEM CESSAR!”\nBIS', 'DORMINDO NO MEU LEITO, EM SONHO ENCANTADOR, UM DIA EU VI JERUSALÉM E O TEMPLO DO SENHOR. OUVI CANTAR CRIANÇAS E EM MEIO AO SEU CANTAR. ROMPEU A VOZ DOS ANJOS, DO CÉU A PROCLAMAR: JERUSALÉM! JERUSALÉM! CANTAI, Ó SANTA GREI! HOSANA! HOSANA! HOSANA AO VOSSO REI! INSTRUMENTOS ENTÃO O SONHO SE ALTEROU, NÃO MAIS O SOM FELIZ OUVIA DAS HOSANAS DOS COROS INFANTIS. O AR EM TORNO SE ESFRIOU, DO SOL FALTAVA A LUZ; E NUM ALTO E TOSCO MONTE VI O VULTO DE UMA CRUZ. JERUSALÉM! JERUSALÉM! (AOS ANJOS ESCUTEI) HOSANA! HOSANA! HOSANA AO VOSSO REI! INSTRUMENTOS AINDA A CENA SE MUDOU; SURGIA EM RESPLENDOR A DIVINAL CIDADE, MORADA DO SENHOR. DA LUA NÃO BRILHAVA A LUZ, NEM SOL NASCIA LÁ, MAS SÓ FULGIA A LUZ DE DEUS, MUI PURA EM SEU BRILHAR, E TODOS QUE QUERIAM, SIM, PODIAM LOGO ENTRAR NA MUI FELIZ JERUSALÉM, QUE NUNCA PASSARÁ. JERUSALÉM! JERUSALÉM! TEU DIA VAI RAIAR! HOSANA! HOSANA! HOSANA SEM CESSAR! HOSANA! HOSANA! HOSANA SEM CESSAR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('694', '694 - JERUSALÉM ETERNA', 'JERUSALÉM ETERNA, EM BREVE TU VIRÁS, \nDESCENDO COMO NOIVA\nATAVIADA PARA O SEU MARIDO.\n\nJERUSALÉM, MINHA CIDADE, \nCOMO ANSEIO ENTRAR \nE ANDAR POR TUAS RUAS,\nSAUDAR OS TEUS REMIDOS\nE ADORAR TEU REI.\n\nINSTRUMENTOS\nBIS\n\nJERUSALÉM ETERNA, ESPOSA DO CORDEIRO,\nEM TI HABITARÁ DEUS COM OS HOMENS\nPARA TODO SEMPRE.\nNAÇÕES VIRÃO PARA ADORAR E REIS \nTRARÃO SUA GLÓRIA\nAO FILHO DE DAVI, AO PRÍNCIPE DA PAZ,\nTEU REI, JERUSALÉM. JERUSALÉM.\nBIS', 'JERUSALÉM ETERNA, EM BREVE TU VIRÁS, DESCENDO COMO NOIVA ATAVIADA PARA O SEU MARIDO. JERUSALÉM, MINHA CIDADE, COMO ANSEIO ENTRAR E ANDAR POR TUAS RUAS, SAUDAR OS TEUS REMIDOS E ADORAR TEU REI. INSTRUMENTOS JERUSALÉM ETERNA, ESPOSA DO CORDEIRO, EM TI HABITARÁ DEUS COM OS HOMENS PARA TODO SEMPRE. NAÇÕES VIRÃO PARA ADORAR E REIS TRARÃO SUA GLÓRIA AO FILHO DE DAVI, AO PRÍNCIPE DA PAZ, TEU REI, JERUSALÉM. JERUSALÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('695', '695 - JERUSALÉM EXCELSA', 'JERUSALÉM EXCELSA,\nGLORIAMO-NOS EM TI;\nCONSOLADORA ESPERANÇA \nDO TEU REBANHO AQUI;\nRADIANTE, BELOS MUROS, \nELE JÁ DE LONGE VÊ\nE A GLÓRIA DA CIDADE \nCONTEMPLA PELA FÉ.\n\nÉ A CRUZ QUE TE ALUMIA; \nE AO GRANDE REDENTOR,\nCORDEIRO, TEU ESPOSO, \nTRIBUTAS-LHE LOUVOR.\nQUE GOZO QUE ME INSPIRA, \nETERNA HABITAÇÃO,\nSABER QUE EM TI \nTERMINO A PEREGRINAÇÃO!\n\nÓ DOCE PÁTRIA AMADA, \nTEU GOZO SERÁ MEU!\nÓ PÁTRIA DESEJADA, \nQUE ENCANTO É ESSE TEU;\nEXULTA, TU QUE GEMES \nNA DOR QUE SE DESFAZ;\nCOM DEUS, QUE TE REDIME, \nFELIZ SEMPRE SERÁS.', 'JERUSALÉM EXCELSA, GLORIAMO-NOS EM TI; CONSOLADORA ESPERANÇA DO TEU REBANHO AQUI; RADIANTE, BELOS MUROS, ELE JÁ DE LONGE VÊ E A GLÓRIA DA CIDADE CONTEMPLA PELA FÉ. É A CRUZ QUE TE ALUMIA; E AO GRANDE REDENTOR, CORDEIRO, TEU ESPOSO, TRIBUTAS-LHE LOUVOR. QUE GOZO QUE ME INSPIRA, ETERNA HABITAÇÃO, SABER QUE EM TI TERMINO A PEREGRINAÇÃO! Ó DOCE PÁTRIA AMADA, TEU GOZO SERÁ MEU! Ó PÁTRIA DESEJADA, QUE ENCANTO É ESSE TEU; EXULTA, TU QUE GEMES NA DOR QUE SE DESFAZ; COM DEUS, QUE TE REDIME, FELIZ SEMPRE SERÁS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('696', '696 - NA TERRA ABENÇOADA', 'NA TERRA ABENÇOADA ESTOU;\nE NELA PEREGRINO SOU;\nDELÍCIAS ABUNDANTES SÃO;\nE SÓ DOS CÉUS SAUDADES DÃO.\n\nCORO\nOH! BELA TERRA DE AMOR!\nDO ALTO MONTE ENCANTADOR\nOLHANDO, VEJO ALÉM DO MAR,\n(QUE BREVE HEI DE ATRAVESSAR) \nA PRAIA ÁUREA, ETERNAL,\nQUERIDO LAR CELESTIAL.\n\nCOMIGO ANDA O SALVADOR;\nCONVERSA EM TONS DE SANTO AMOR,\nME GUIA SEMPRE PELA MÃO,\nE RUMO AO CÉU MEUS PASSOS VÃO.\n\nCORO\nOH! BELA TERRA DE AMOR!\nDO ALTO MONTE ENCANTADOR\nOLHANDO, VEJO ALÉM DO MAR,\n(QUE BREVE HEI DE ATRAVESSAR) \nA PRAIA ÁUREA, ETERNAL,\nQUERIDO LAR CELESTIAL.\n\nSENTIR A BRISA, OH! QUE PRAZER,\nDOCE PERFUME VEM TRAZER\nDAS FLORES QUE NÃO MURCHARÃO,\nDOS FRUTOS QUE SUPERNOS SÃO.\n\nCORO\nOH! BELA TERRA DE AMOR!\nDO ALTO MONTE ENCANTADOR\nOLHANDO, VEJO ALÉM DO MAR,\n(QUE BREVE HEI DE ATRAVESSAR) \nA PRAIA ÁUREA, ETERNAL,\nQUERIDO LAR CELESTIAL.\n\nNA DOCE ARAGEM CELESTIAL\nFLUTUA O CANTO ANGELICAL\nDA TRIUNFANTE MULTIDÃO\nQUE ENTOA A GRANDE REDENÇÃO.\n\nCORO\nOH! BELA TERRA DE AMOR!\nDO ALTO MONTE ENCANTADOR\nOLHANDO, VEJO ALÉM DO MAR,\n(QUE BREVE HEI DE ATRAVESSAR) \nA PRAIA ÁUREA, ETERNAL,\nQUERIDO LAR CELESTIAL.', 'NA TERRA ABENÇOADA ESTOU; E NELA PEREGRINO SOU; DELÍCIAS ABUNDANTES SÃO; E SÓ DOS CÉUS SAUDADES DÃO. CORO OH! BELA TERRA DE AMOR! DO ALTO MONTE ENCANTADOR OLHANDO, VEJO ALÉM DO MAR, (QUE BREVE HEI DE ATRAVESSAR) A PRAIA ÁUREA, ETERNAL, QUERIDO LAR CELESTIAL. COMIGO ANDA O SALVADOR; CONVERSA EM TONS DE SANTO AMOR, ME GUIA SEMPRE PELA MÃO, E RUMO AO CÉU MEUS PASSOS VÃO. SENTIR A BRISA, OH! QUE PRAZER, DOCE PERFUME VEM TRAZER DAS FLORES QUE NÃO MURCHARÃO, DOS FRUTOS QUE SUPERNOS SÃO. NA DOCE ARAGEM CELESTIAL FLUTUA O CANTO ANGELICAL DA TRIUNFANTE MULTIDÃO QUE ENTOA A GRANDE REDENÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('697', '697 - OUVI O SALVADOR DIZER', 'OUVI O SALVADOR DIZER:\n“VEM DESCANSAR EM MIM\nE CONFIANTE RECEBER\nCONFORTO E PAZ SEM FIM”.\nFUI A JESUS E LHE ENTREGUEI\nMEU TRISTE CORAÇÃO;\nABRIGO E PAZ E GOZO ACHEI,\nACHEI CONSOLAÇÃO.\n\nOUVI O SALVADOR DIZER:\n“DE GRAÇA É O MEU AMOR.\nAS ÁGUAS VIVAS VEM BEBER;\nDA VIDA A FONTE SOU”.\nFUI A JESUS E ME PROSTREI,\nDA FONTE ENFIM BEBI;\nJAMAIS A SEDE SENTIREI,\nESTANDO SEMPRE ALI.\n\nOUVI O SALVADOR DIZER:\n“DO MUNDO EU SOU A LUZ;\nOH! VEM A MIM, POIS SENTIRÁS\nQUE TE AMO DESDE A CRUZ”.\nFUI A JESUS E NELE ACHEI\nO SOL QUE BRILHA EM MIM;\nE NESSA LUZ EU ANDAREI\nATÉ DA VIDA O FIM. \n\nFINAL: AMÉM.', 'OUVI O SALVADOR DIZER: VEM DESCANSAR EM MIM E CONFIANTE RECEBER CONFORTO E PAZ SEM FIM. FUI A JESUS E LHE ENTREGUEI MEU TRISTE CORAÇÃO; ABRIGO E PAZ E GOZO ACHEI, ACHEI CONSOLAÇÃO. OUVI O SALVADOR DIZER: DE GRAÇA É O MEU AMOR. AS ÁGUAS VIVAS VEM BEBER; DA VIDA A FONTE SOU. FUI A JESUS E ME PROSTREI, DA FONTE ENFIM BEBI; JAMAIS A SEDE SENTIREI, ESTANDO SEMPRE ALI. OUVI O SALVADOR DIZER: DO MUNDO EU SOU A LUZ; OH! VEM A MIM, POIS SENTIRÁS QUE TE AMO DESDE A CRUZ. FUI A JESUS E NELE ACHEI O SOL QUE BRILHA EM MIM; E NESSA LUZ EU ANDAREI ATÉ DA VIDA O FIM.  AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('698', '698 - TUA, Ó DEUS, É TODA A GRANDEZA', 'TUA, Ó DEUS, É TODA A GRANDEZA,\nTUA, Ó DEUS, É TODA A GRANDEZA,\nTUA, Ó DEUS, É TODA A GRANDEZA,\nE O PODER, E A GLÓRIA, E A VITÓRIA,\nE OS LOUVORES. VITÓRIA E LOUVORES.\n\nTUA, Ó DEUS; TUA, Ó DEUS,\nÉ A GRANDEZA, E O PODER,\n(A GRANDEZA E O PODER),\nE A GLÓRIA, E A VITÓRIA, \nE OS LOUVORES ETERNOS;\n\nPOIS TUDO O QUE ESTÁ NO MAR, \nE ESTÁ NO CÉU E NA TERRA É TEU!\nTEU É O IMPÉRIO. \nTEU É O IMPÉRIO, Ó SENHOR!\nE TU ÉS ACIMA DE TODOS OS REIS, \nDE TODOS OS REIS, AMÉM ALELUIA!\n\nTUA, Ó DEUS; TUA, Ó DEUS,\nÉ A GRANDEZA, E O PODER,\n(A GRANDEZA E O PODER),\nE A GLÓRIA, E A VITÓRIA, \nE OS LOUVORES ETERNOS;', 'TUA, Ó DEUS, É TODA A GRANDEZA, TUA, Ó DEUS, É TODA A GRANDEZA, TUA, Ó DEUS, É TODA A GRANDEZA, E O PODER, E A GLÓRIA, E A VITÓRIA, E OS LOUVORES. VITÓRIA E LOUVORES. TUA, Ó DEUS; TUA, Ó DEUS, É A GRANDEZA, E O PODER, (A GRANDEZA E O PODER), E A GLÓRIA, E A VITÓRIA, E OS LOUVORES ETERNOS; POIS TUDO O QUE ESTÁ NO MAR, E ESTÁ NO CÉU E NA TERRA É TEU! TEU É O IMPÉRIO. TEU É O IMPÉRIO, Ó SENHOR! E TU ÉS ACIMA DE TODOS OS REIS, DE TODOS OS REIS, AMÉM ALELUIA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('699', '699 - QUE GRANDE REUNIÃO (JESUS DE NAZARÉ)', 'QUE GRANDE REUNIÃO CONVOCADA ALI,\nDA COLINA SE ACHAVA AO PÉ.\nOLHANDO MELHOR ENTRE ELES \nVI A JESUS DE NAZARÉ.\nVI CEGO EM MOMENTOS TORNAR A VER,\nENTREVADO FICAR DE PÉ, \nE TODOS GOZAVAM REAL \nPRAZER EM JESUS DE NAZARÉ.\n\nCORO\nJESUS CRISTO, MEU MESTRE DIVINO,\nPRA SEMPRE POR TI VIVEREI.\nA VIDA SERÁ QUAL UM HINO\nEM TI, MEU AMADO REI.\n\nINDAGAM OS PRÍNCIPES ENTRE SI, \nTODOS QUEREM SABER \nQUEM É O HOMEM HUMILDE \nQUE ESTÁ ALI. É JESUS DE NAZARÉ.\nO MAR SE AQUIETA AO MANDO SEU,\nPODEROSO E GRANDIOSO ELE É, \nÉ O NOSSO PÃO QUE DESCEU DO CÉU, \nJESUS DE NAZARÉ.\n\nCORO\nJESUS CRISTO, MEU MESTRE DIVINO,\nPRA SEMPRE POR TI VIVEREI.\nA VIDA SERÁ QUAL UM HINO\nEM TI, MEU AMADO REI.\n\nSE GRANDES, QUAIS ONDAS, \nAS LUTAS SÃO, SE JÁ QUASE VACILA \nA FÉ, ACALMA A TORMENTA NO CORAÇÃO, \nJESUS DE NAZARÉ.\nA ÁGUA DA VIDA A TODOS \nDÁ AO CONTRITO ELE SALVARÁ!\nMEU DÚBIO CAMINHO EM SUA \nMÃO ESTÁ, JESUS DE NAZARÉ.\n\nCORO\nJESUS CRISTO, MEU MESTRE DIVINO,\nPRA SEMPRE POR TI VIVEREI.\nA VIDA SERÁ QUAL UM HINO\nEM TI, MEU AMADO REI.', 'QUE GRANDE REUNIÃO CONVOCADA ALI, DA COLINA SE ACHAVA AO PÉ. OLHANDO MELHOR ENTRE ELES VI A JESUS DE NAZARÉ. VI CEGO EM MOMENTOS TORNAR A VER, ENTREVADO FICAR DE PÉ, E TODOS GOZAVAM REAL PRAZER EM JESUS DE NAZARÉ. CORO JESUS CRISTO, MEU MESTRE DIVINO, PRA SEMPRE POR TI VIVEREI. A VIDA SERÁ QUAL UM HINO EM TI, MEU AMADO REI. INDAGAM OS PRÍNCIPES ENTRE SI, TODOS QUEREM SABER QUEM É O HOMEM HUMILDE QUE ESTÁ ALI. É JESUS DE NAZARÉ. O MAR SE AQUIETA AO MANDO SEU, PODEROSO E GRANDIOSO ELE É, É O NOSSO PÃO QUE DESCEU DO CÉU, JESUS DE NAZARÉ. SE GRANDES, QUAIS ONDAS, AS LUTAS SÃO, SE JÁ QUASE VACILA A FÉ, ACALMA A TORMENTA NO CORAÇÃO, JESUS DE NAZARÉ. A ÁGUA DA VIDA A TODOS DÁ AO CONTRITO ELE SALVARÁ! MEU DÚBIO CAMINHO EM SUA MÃO ESTÁ, JESUS DE NAZARÉ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('700', '700 - ESTEVÃO AVISTOU (EU TAMBÉM QUERO VER)', 'ESTEVÃO AVISTOU OS CÉUS ABERTOS:\nO FILHO DO DEUS PAI; VIU BEM DE PERTO, \nCORAL ANGELICAL\nCANTANDO AO REI DOS REIS,\nSALMODIANDO AO GRANDE DEUS!\n\nCORO\nEU TAMBÉM QUERO VER A GLÓRIA DO SENHOR \nQUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! \nEU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) \nA GLÓRIA DO SENHOR!\n\nJOÃO TAMBÉM OLHOU OS CÉUS ABERTOS,\nA GLÓRIA DO SENHOR, SIM, MANIFESTOS:\nUMA CIDADE REAL, UM RIO DE CRISTAL!\nNUNCA, JAMAIS SE VIU BELEZAS MIL!\n\nCORO\nEU TAMBÉM QUERO VER A GLÓRIA DO SENHOR \nQUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! \nEU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) \nA GLÓRIA DO SENHOR!\n\nELIAS CONTEMPLOU, NO FIRMAMENTO:\nCARRO DE FOGO QUE BRILHAVA AO VENTO; \nE PARA O CÉU HOMEM DE DEUS SUBIU \nROMPENDO AS NUVENS CELESTIAIS!\n\nCORO\nEU TAMBÉM QUERO VER A GLÓRIA DO SENHOR \nQUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! \nEU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) \nA GLÓRIA DO SENHOR!\n\nINSTRUMENTOS\n\nCORO\nEU TAMBÉM QUERO VER A GLÓRIA DO SENHOR \nQUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! \nEU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) \nA GLÓRIA DO SENHOR!\n\nFINAL: EU TAMBÉM QUERO VER!', 'ESTEVÃO AVISTOU OS CÉUS ABERTOS: O FILHO DO DEUS PAI; VIU BEM DE PERTO, CORAL ANGELICAL CANTANDO AO REI DOS REIS, SALMODIANDO AO GRANDE DEUS! CORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR! JOÃO TAMBÉM OLHOU OS CÉUS ABERTOS, A GLÓRIA DO SENHOR, SIM, MANIFESTOS: UMA CIDADE REAL, UM RIO DE CRISTAL! NUNCA, JAMAIS SE VIU BELEZAS MIL! ELIAS CONTEMPLOU, NO FIRMAMENTO: CARRO DE FOGO QUE BRILHAVA AO VENTO; E PARA O CÉU HOMEM DE DEUS SUBIU ROMPENDO AS NUVENS CELESTIAIS! CORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR! INSTRUMENTOS CORO EU TAMBÉM QUERO VER A GLÓRIA DO SENHOR QUE PREPARADA ESTÁ PARA OS SANTOS ALI MORAREM! EU TAMBÉM QUERO VER (EU TAMBÉM QUERO VER) A GLÓRIA DO SENHOR!  EU TAMBÉM QUERO VER!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('701', '701 - AS TUAS MÃOS', 'AS TUAS MÃOS DIRIGEM MEU DESTINO\nOH! DEUS DE AMOR! FOLGO QUE SEJA ASSIM! \nTEUS SÃO OS MEUS PODERES, \nMINHA VIDA; EM TUDO, ETERNO PAI, \nDISPÕE DE MIM!\n\nMEUS DIAS SEJAM CURTOS OU COMPRIDOS, \nPASSADOS EM TRISTEZAS OU PRAZER, \nEM SOMBRA OU LUZ DE ACORDO COM TEU PLANO!\nE É TUDO BOM SE VEM DO TEU QUERER.\n\nAS TUAS MÃOS DIRIGEM MEU DESTINO,\nDANTES CRAVADAS NA SANGRENTA CRUZ.\nPOR MEUS PECADOS FORAM TRASPASSADAS\nBEM POSSO NELAS DESCANSAR, JESUS!\n\nNOS CÉUS ERGUIDAS SEMPRE INTERCEDENDO\nAS SANTAS MÃOS NÃO PEDIRÃO EM VÃO!\nAO SEU CUIDADO, EM PLENA CONFIANÇA,\nENTREGO A MINHA ETERNA SALVAÇÃO.\n\nAS TUAS MÃOS DIRIGEM MEU DESTINO;\nO ACASO, PARA MIM, NÃO HAVERÁ!\nO GRANDE PAI VIGIA O MEU CAMINHO,\nE SEM MOTIVO NÃO ME AFLIGIRÁ.\n\nTENHO NO SEU PODER CONSTANTE APOIO,\nFORTE É SEU BRAÇO, INSONE O SEU AMOR;\nE EM BREVE, ENTRANDO NA CIDADE ETERNA \nEU LOUVAREI MEU REI E SALVADOR!', 'AS TUAS MÃOS DIRIGEM MEU DESTINO OH! DEUS DE AMOR! FOLGO QUE SEJA ASSIM! TEUS SÃO OS MEUS PODERES, MINHA VIDA; EM TUDO, ETERNO PAI, DISPÕE DE MIM! MEUS DIAS SEJAM CURTOS OU COMPRIDOS, PASSADOS EM TRISTEZAS OU PRAZER, EM SOMBRA OU LUZ DE ACORDO COM TEU PLANO! E É TUDO BOM SE VEM DO TEU QUERER. AS TUAS MÃOS DIRIGEM MEU DESTINO, DANTES CRAVADAS NA SANGRENTA CRUZ. POR MEUS PECADOS FORAM TRASPASSADAS BEM POSSO NELAS DESCANSAR, JESUS! NOS CÉUS ERGUIDAS SEMPRE INTERCEDENDO AS SANTAS MÃOS NÃO PEDIRÃO EM VÃO! AO SEU CUIDADO, EM PLENA CONFIANÇA, ENTREGO A MINHA ETERNA SALVAÇÃO. AS TUAS MÃOS DIRIGEM MEU DESTINO; O ACASO, PARA MIM, NÃO HAVERÁ! O GRANDE PAI VIGIA O MEU CAMINHO, E SEM MOTIVO NÃO ME AFLIGIRÁ. TENHO NO SEU PODER CONSTANTE APOIO, FORTE É SEU BRAÇO, INSONE O SEU AMOR; E EM BREVE, ENTRANDO NA CIDADE ETERNA EU LOUVAREI MEU REI E SALVADOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('702', '702 - OS CÉUS EM BREVE SE ABRIRÃO', 'OS CÉUS EM BREVE SE ABRIRÃO E TODOS TE VERÃO, \nÓ FILHO DE DAVI; OS REIS VIRÃO A TI,  DE JOELHOS ADORAR AQUELE QUE PRA SEMPRE IRÁ REINAR.\n\nENTÃO TODOS VERÃO QUEM ÉS,\nPROSTRADOS A TEUS PÉS ENTENDERÃO \nO AMOR, O VERDADEIRO AMOR QUE TE LEVOU, \nJESUS, A DAR-ME A TUA VIDA NAQUELA CRUZ.\n\nCORO\nNÓS TE SAUDAMOS;\nHOSANA AO FILHO DE DAVI!\nBENDITO AQUELE QUE VEM \nEM NOME DO SENHOR, \nHOSANA, HOSANA!\n\nOS CÉUS, DE GOZO, TREMERÃO, \nE TODA CRIAÇÃO SE ALEGRARÁ EM TI,\nÓ FILHO DE DAVI, POIS NA VIDA E PAZ\nAO NOSSO MUNDO ENFIM TU NOS TRARÁS.\n\nCORO\nNÓS TE SAUDAMOS;\nHOSANA AO FILHO DE DAVI!\nBENDITO AQUELE QUE VEM \nEM NOME DO SENHOR, \nHOSANA, HOSANA!\n\nFINAL: HOSANA!', 'OS CÉUS EM BREVE SE ABRIRÃO E TODOS TE VERÃO, Ó FILHO DE DAVI; OS REIS VIRÃO A TI, DE JOELHOS ADORAR AQUELE QUE PRA SEMPRE IRÁ REINAR. ENTÃO TODOS VERÃO QUEM ÉS, PROSTRADOS A TEUS PÉS ENTENDERÃO O AMOR, O VERDADEIRO AMOR QUE TE LEVOU, JESUS, A DAR-ME A TUA VIDA NAQUELA CRUZ. CORO NÓS TE SAUDAMOS; HOSANA AO FILHO DE DAVI! BENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA! OS CÉUS, DE GOZO, TREMERÃO, E TODA CRIAÇÃO SE ALEGRARÁ EM TI, Ó FILHO DE DAVI, POIS NA VIDA E PAZ AO NOSSO MUNDO ENFIM TU NOS TRARÁS. CORO NÓS TE SAUDAMOS; HOSANA AO FILHO DE DAVI! BENDITO AQUELE QUE VEM EM NOME DO SENHOR, HOSANA, HOSANA!  HOSANA!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('703', '703 - SERÁ DE MANHÃ', 'SERÁ DE MANHÃ, NO COMEÇO DO DIA?\nSERÁ QUANDO A LUZ PELAS TREVAS PENETRA,\nQUE CRISTO HÁ DE VIR COM OS ANJOS DA GLÓRIA,\nRECEBER DESTE MUNDO OS SEUS?\n\nCORO\nOH! JESUS, SALVADOR, SENHOR!\nQUANDO VAMOS CANTAR:\nCRISTO VOLTA, ALELUIA, ALELUIA, AMÉM!\nALELUIA, AMÉM!\n\nSERÁ NA AURORA, SERÁ PELA TARDE?\nSIM, PODERÁ SER QUE AS TREVAS DA NOITE\nSE TORNEM NA LUZ DESSE BRILHO DE GLÓRIA,\nQUANDO CRISTO OS SEUS RECEBER.\n\nCORO\nOH! JESUS, SALVADOR, SENHOR!\nQUANDO VAMOS CANTAR:\nCRISTO VOLTA, ALELUIA, ALELUIA, AMÉM!\nALELUIA, AMÉM!\n\nOH! GOZO SEM FIM, QUANDO A MORTE VENCIDA,\nJESUS REVESTIR-NOS DE PERPÉTUA VIDA,\nE TODOS NÓS FORMOS MORAR LÁ NA GLÓRIA,\nNESSE LAR QUE JESUS PREPAROU!\n\nCORO\nOH! JESUS, SALVADOR, SENHOR!\nQUANDO VAMOS CANTAR:\nCRISTO VOLTA, ALELUIA, ALELUIA, AMÉM!\nALELUIA, AMÉM!', 'SERÁ DE MANHÃ, NO COMEÇO DO DIA? SERÁ QUANDO A LUZ PELAS TREVAS PENETRA, QUE CRISTO HÁ DE VIR COM OS ANJOS DA GLÓRIA, RECEBER DESTE MUNDO OS SEUS? CORO OH! JESUS, SALVADOR, SENHOR! QUANDO VAMOS CANTAR: CRISTO VOLTA, ALELUIA, ALELUIA, AMÉM! ALELUIA, AMÉM! SERÁ NA AURORA, SERÁ PELA TARDE? SIM, PODERÁ SER QUE AS TREVAS DA NOITE SE TORNEM NA LUZ DESSE BRILHO DE GLÓRIA, QUANDO CRISTO OS SEUS RECEBER. OH! GOZO SEM FIM, QUANDO A MORTE VENCIDA, JESUS REVESTIR-NOS DE PERPÉTUA VIDA, E TODOS NÓS FORMOS MORAR LÁ NA GLÓRIA, NESSE LAR QUE JESUS PREPAROU!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('704', '704 - SENHOR, SE ESTIVESSES AQUI (LÁZARO)', 'SENHOR, SE ESTIVESSES AQUI \nO MEU IRMÃO NÃO TERIA MORRIDO,\nFOI O QUE DISSE AQUELA MULHER, \nLANÇANDO-SE AOS PÉS DE JESUS\nQUE AO OUVIR O CHORO QUE HAVIA NAQUELE LUGAR, MOVEU-SE EM ESPÍRITO, \nJESUS CHOROU.\n\nCORO\nTIRAI A PEDRA DISSE JESUS \nE NÃO PODIAM COMPREENDER O\nQUE IRIA FAZER, ENTÃO A LÁZARO \nELE CHAMOU E O RESSUSCITOU,\nTODOS VIRAM A GLÓRIA DE DEUS.\nELE  TROUXE A ESPERANÇA, \nELE TROUXE A VIDA,\nPOIS ELE É O AUTOR DA VIDA.\nINSTRUMENTOS\nBIS\n\nSE ESTÁS COMO AQUELA MULHER,\nSE PARA O TEU PROBLEMA NÃO TEM SOLUÇÃO,\nCHORA AOS PÉS DE JESUS,\nE ELE ENXUGARÁ AS TUAS LÁGRIMAS.\nVERÁS A GLÓRIA DE DEUS.\n\nCORO (2X)\nTIRAI A PEDRA DISSE JESUS \nE NÃO PODIAM COMPREENDER O\nQUE IRIA FAZER, ENTÃO A LÁZARO \nELE CHAMOU E O RESSUSCITOU,\nTODOS VIRAM A GLÓRIA DE DEUS.\nELE  TROUXE A ESPERANÇA, \nELE TROUXE A VIDA,\nPOIS ELE É O AUTOR DA VIDA.\nBIS', 'SENHOR, SE ESTIVESSES AQUI O MEU IRMÃO NÃO TERIA MORRIDO, FOI O QUE DISSE AQUELA MULHER, LANÇANDO-SE AOS PÉS DE JESUS QUE AO OUVIR O CHORO QUE HAVIA NAQUELE LUGAR, MOVEU-SE EM ESPÍRITO, JESUS CHOROU. CORO TIRAI A PEDRA DISSE JESUS E NÃO PODIAM COMPREENDER O QUE IRIA FAZER, ENTÃO A LÁZARO ELE CHAMOU E O RESSUSCITOU, TODOS VIRAM A GLÓRIA DE DEUS. ELE TROUXE A ESPERANÇA, ELE TROUXE A VIDA, POIS ELE É O AUTOR DA VIDA. INSTRUMENTOS SE ESTÁS COMO AQUELA MULHER, SE PARA O TEU PROBLEMA NÃO TEM SOLUÇÃO, CHORA AOS PÉS DE JESUS, E ELE ENXUGARÁ AS TUAS LÁGRIMAS. VERÁS A GLÓRIA DE DEUS. TIRAI A PEDRA DISSE JESUS E NÃO PODIAM COMPREENDER O QUE IRIA FAZER, ENTÃO A LÁZARO ELE CHAMOU E O RESSUSCITOU, TODOS VIRAM A GLÓRIA DE DEUS. ELE TROUXE A ESPERANÇA, ELE TROUXE A VIDA, POIS ELE É O AUTOR DA VIDA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('705', '705 - GRANDES SÃO, SENHOR,  OS TEUS FEITOS (GRATIDÃO)', 'GRANDES SÃO, SENHOR, OS TEUS FEITOS!\nA TI DEVO TODO O MEU LOUVOR.\nO TEU NOME É PODEROSO,\nE A TUA GRAÇA É TUDO PARA MIM! (PARA MIM)\n\nTU, Ó DEUS, ÉS ONIPOTENTE!\nTUA MORADA É ETERNA, SENHOR\nMINHA VIDA SE ENCHE DE ALEGRIA,\nA MINH’ALMA TE ENGRANDECE POR TE SERVIR.\n\nA TI EU DEVO TODA HONRA \nE TODA GLÓRIA! (TODA GLÓRIA) \nNO TEU NOME ENCONTRO PAZ E VITÓRIA, \nMINHA VIDA É FRUTO DO TEU GRANDE AMOR,  \nPOR ISSO DIGO QUE ÉS \nMEU REI E MEU SENHOR!\n\nINSTRUMENTOS\nBIS\n\nFINAL: \nGRANDES SÃO SENHOR OS TEUS FEITOS!\n(GRANDES SÃO).', 'GRANDES SÃO, SENHOR, OS TEUS FEITOS! A TI DEVO TODO O MEU LOUVOR. O TEU NOME É PODEROSO, E A TUA GRAÇA É TUDO PARA MIM! (PARA MIM) TU, Ó DEUS, ÉS ONIPOTENTE! TUA MORADA É ETERNA, SENHOR MINHA VIDA SE ENCHE DE ALEGRIA, A MINH’ALMA TE ENGRANDECE POR TE SERVIR. A TI EU DEVO TODA HONRA E TODA GLÓRIA! (TODA GLÓRIA) NO TEU NOME ENCONTRO PAZ E VITÓRIA, MINHA VIDA É FRUTO DO TEU GRANDE AMOR, POR ISSO DIGO QUE ÉS MEU REI E MEU SENHOR! INSTRUMENTOS GRANDES SÃO SENHOR OS TEUS FEITOS! (GRANDES SÃO).', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('706', '706 - DESTE MUNDO ESCRAVO FUI (GRANDES COISAS FEZ O SENHOR POR NÓS)', 'DESTE MUNDO ESCRAVO FUI,\nPRESO EM ILUSÕES SEM FIM.\nE ME VIA A PERGUNTAR:\nQUANDO VIRÁ O SENHOR POR MIM?\nCOMO QUANDO EM CATIVEIRO\nO SENHOR TROUXE A SIÃO,\nO SEU POVO AMADO.\n\nFOI COMO UM SONHO,\nNOSSA BOCA SE ENCHEU DE RISO E CANTO. \nE DIZIA ENTRE AS NAÇÕES:\nGRANDES COISAS FEZ O SENHOR!\nGRANDES COISAS FEZ O SENHOR POR NÓS\nGRANDES COISAS FEZ O SENHOR POR NÓS\nJÁ PLANTEI AS MINHAS LÁGRIMAS, \nCOLHEREI OS FRUTOS DE JUSTIÇA.\nBIS\n\nCOMO QUANDO EM CATIVEIRO,\nME TROUXE O SENHOR. FOI COMO UM SONHO, \nMINHA BOCA SE ENCHEU DE RISO E CANTO \nE DIREI ENTRE AS NAÇÕES:\nQUE GRANDES COISAS FEZ O SENHOR!\nGRANDES COISAS FEZ O SENHOR POR NÓS\nGRANDES COISAS FEZ O SENHOR POR NÓS\nVIVO AGORA A ESPERANÇA DE COM ELE\nUM DIA ME ENCONTRAR NO CÉU.\n\nFINAL: \nVIVO AGORA A ESPERANÇA DE COM ELE\nUM DIA ME ENCONTRAR NO CÉU, NO CÉU.', 'DESTE MUNDO ESCRAVO FUI, PRESO EM ILUSÕES SEM FIM. E ME VIA A PERGUNTAR: QUANDO VIRÁ O SENHOR POR MIM? COMO QUANDO EM CATIVEIRO O SENHOR TROUXE A SIÃO, O SEU POVO AMADO. FOI COMO UM SONHO, NOSSA BOCA SE ENCHEU DE RISO E CANTO. E DIZIA ENTRE AS NAÇÕES: GRANDES COISAS FEZ O SENHOR! GRANDES COISAS FEZ O SENHOR POR NÓS GRANDES COISAS FEZ O SENHOR POR NÓS JÁ PLANTEI AS MINHAS LÁGRIMAS, COLHEREI OS FRUTOS DE JUSTIÇA. COMO QUANDO EM CATIVEIRO, ME TROUXE O SENHOR. FOI COMO UM SONHO, MINHA BOCA SE ENCHEU DE RISO E CANTO E DIREI ENTRE AS NAÇÕES: QUE GRANDES COISAS FEZ O SENHOR! GRANDES COISAS FEZ O SENHOR POR NÓS GRANDES COISAS FEZ O SENHOR POR NÓS VIVO AGORA A ESPERANÇA DE COM ELE UM DIA ME ENCONTRAR NO CÉU. VIVO AGORA A ESPERANÇA DE COM ELE UM DIA ME ENCONTRAR NO CÉU, NO CÉU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('707', '707 - EU CONFIO EM TI, SENHOR', 'EU CONFIO EM TI, SENHOR!\nMESMO QUANDO TUDO NÃO VAI BEM,\nE A ESPERANÇA FUGIR, EU OLHAREI SÓ PRA TI.\nACALMA O MEU CORAÇÃO, SEGURA MINHA MÃO\nE ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR.\n\nPORQUE NUNCA SE OUVIU, \nE NUNCA SE VIU UM DEUS ALÉM DE TI. \nE EU APRENDI A CONFIAR NO DEUS \nPROVEDOR QUE NÃO FALHARÁ.\nROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. \nSOCORRO BEM PRESENTE, \nMESMO NA TRISTEZA E DOR.\nEU CONFIO EM TI, SENHOR! JESUS! (BIS)\n\nINSTRUMENTOS\n\nEU CONFIO EM TI, SENHOR!\nMEU CAMINHO PONHO EM TEU ALTAR.\nTUA VOZ EU QUERO OUVIR,\nTUA PRESENÇA, SIM, QUERO SENTIR.\nENSINA-ME A CAMINHAR E A TE HONRAR, \nSENHOR, E ME FAZ UM VENCEDOR,\nSIM, ME FAZ UM VENCEDOR.\n\nPORQUE NUNCA SE OUVIU, \nE NUNCA SE VIU UM DEUS ALÉM DE TI. \nE EU APRENDI A CONFIAR NO DEUS \nPROVEDOR QUE NÃO FALHARÁ.\nROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. \nSOCORRO BEM PRESENTE, \nMESMO NA TRISTEZA E DOR.\nEU CONFIO EM TI, SENHOR! (BIS)\n\nACALMA O MEU CORAÇÃO, \nSEGURA MINHA MÃO E ME FAZ UM VENCEDOR,\nSIM, ME FAZ UM VENCEDOR.\n\nROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. \nSOCORRO BEM PRESENTE, MESMO NA TRISTEZA E DOR. \nEU CONFIO EM TI, SENHOR! (2X)\nJESUS!\n\nFINAL: ABRIGO SEGURO, JESUS!', 'EU CONFIO EM TI, SENHOR! MESMO QUANDO TUDO NÃO VAI BEM, E A ESPERANÇA FUGIR, EU OLHAREI SÓ PRA TI. ACALMA O MEU CORAÇÃO, SEGURA MINHA MÃO E ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR. PORQUE NUNCA SE OUVIU, E NUNCA SE VIU UM DEUS ALÉM DE TI. E EU APRENDI A CONFIAR NO DEUS PROVEDOR QUE NÃO FALHARÁ. ROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. SOCORRO BEM PRESENTE, MESMO NA TRISTEZA E DOR. EU CONFIO EM TI, SENHOR! JESUS!  INSTRUMENTOS EU CONFIO EM TI, SENHOR! MEU CAMINHO PONHO EM TEU ALTAR. TUA VOZ EU QUERO OUVIR, TUA PRESENÇA, SIM, QUERO SENTIR. ENSINA-ME A CAMINHAR E A TE HONRAR, SENHOR, E ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR. PORQUE NUNCA SE OUVIU, E NUNCA SE VIU UM DEUS ALÉM DE TI. E EU APRENDI A CONFIAR NO DEUS PROVEDOR QUE NÃO FALHARÁ. ROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. SOCORRO BEM PRESENTE, MESMO NA TRISTEZA E DOR. EU CONFIO EM TI, SENHOR! ACALMA O MEU CORAÇÃO, SEGURA MINHA MÃO E ME FAZ UM VENCEDOR, SIM, ME FAZ UM VENCEDOR. ROCHA INABALÁVEL, MEU ABRIGO E BOM PASTOR. SOCORRO BEM PRESENTE, MESMO NA TRISTEZA E DOR. EU CONFIO EM TI, SENHOR!  JESUS!  ABRIGO SEGURO, JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('708', '708 - OUVI NO CÉU (BODAS DO CORDEIRO)', 'OUVI NO CÉU A VOZ DE UMA MULTIDÃO\nE VI OS ANCIÃOS E OS SERES QUE SE PROSTRARAM \nEM ADORAÇÃO AO NOSSO DEUS.\nQUE NO TRONO ASSENTADO ESTÁ\nDIZENDO AMÉM, ALELUIA!\n\nALEGREMOS E DEMOS-LHE GLÓRIA, GLÓRIA\nPORQUE SÃO CHEGADAS AS BODAS DO CORDEIRO\nE SUA ESPOSA PRONTA ESTÁ REVESTIDA \nDE UM LINHO FINO QUE RESPLANDECE\nPORQUE O LINHO SÃO AS JUSTIÇAS DOS SANTOS.\n\nINSTRUMENTOS\n\nBEM AVENTURADOS SÃO AQUELES CONVIDADOS\nPORQUE DERAM TUDO O QUE TINHAM A JESUS\nO CORDEIRO, FILHO DE DEUS!\nQUE VIGIARAM E ORARAM E VENCERAM\nESTÃO CHEIOS DO ESPÍRITO \nLOUVANDO AO SENHOR.\nBIS\n\nSÃO PALAVRAS DE DEUS.\n\nINSTRUMENTOS\n \nFINAL: \nSÃO PALAVRAS DE DEUS.', 'OUVI NO CÉU A VOZ DE UMA MULTIDÃO E VI OS ANCIÃOS E OS SERES QUE SE PROSTRARAM EM ADORAÇÃO AO NOSSO DEUS. QUE NO TRONO ASSENTADO ESTÁ DIZENDO AMÉM, ALELUIA! ALEGREMOS E DEMOS-LHE GLÓRIA, GLÓRIA PORQUE SÃO CHEGADAS AS BODAS DO CORDEIRO E SUA ESPOSA PRONTA ESTÁ REVESTIDA DE UM LINHO FINO QUE RESPLANDECE PORQUE O LINHO SÃO AS JUSTIÇAS DOS SANTOS. INSTRUMENTOS BEM AVENTURADOS SÃO AQUELES CONVIDADOS PORQUE DERAM TUDO O QUE TINHAM A JESUS O CORDEIRO, FILHO DE DEUS! QUE VIGIARAM E ORARAM E VENCERAM ESTÃO CHEIOS DO ESPÍRITO LOUVANDO AO SENHOR. SÃO PALAVRAS DE DEUS. INSTRUMENTOS  SÃO PALAVRAS DE DEUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('709', '709 - RESPLANDECE, Ó JERUSALÉM', 'RESPLANDECE Ó JERUSALÉM!\nPORQUE NASCE SOBRE TI A TUA LUZ.\nJERUSALÉM, ÉS ILUMINADA PELA GLÓRIA \nDO SENHOR, AS TUAS RUAS SÃO DE OURO, \nÓ QUANTO ALMEJO NELAS ANDAR.\n\nJERUSALÉM, Ó QUANTO ALMEJO\nESTAR AO LADO DO MEU SENHOR\nPRA SEMPRE O ADORAR.\nJERUSALÉM,  POR  SUAS  PORTAS\nVOU ENTRAR E EM CORO CELESTIAL,\nA JESUS ADORAR.\n\nINSTRUMENTOS\n\nDENTRO DOS TEUS MUROS,\n(Ó JERUSALÉM) \nASSENTA-SE NO TRONO\nÀ DESTRA DE DEUS PAI,\nO NOSSO REI, O CORDEIRO \nQUE VENCEU.\n\nJERUSALÉM, QUERO CONTEMPLAR,\nA TUA GLÓRIA COM A GLÓRIA DO\nSENHOR, QUERO HABITAR DENTRO\nDOS TEUS MUROS.\n\nFINAL:\nÓ JERUSALÉM. (JERUSALÉM)\n\n3X', 'RESPLANDECE Ó JERUSALÉM! PORQUE NASCE SOBRE TI A TUA LUZ. JERUSALÉM, ÉS ILUMINADA PELA GLÓRIA DO SENHOR, AS TUAS RUAS SÃO DE OURO, Ó QUANTO ALMEJO NELAS ANDAR. JERUSALÉM, Ó QUANTO ALMEJO ESTAR AO LADO DO MEU SENHOR PRA SEMPRE O ADORAR. JERUSALÉM, POR SUAS PORTAS VOU ENTRAR E EM CORO CELESTIAL, A JESUS ADORAR. INSTRUMENTOS DENTRO DOS TEUS MUROS, (Ó JERUSALÉM) ASSENTA-SE NO TRONO À DESTRA DE DEUS PAI, O NOSSO REI, O CORDEIRO QUE VENCEU. JERUSALÉM, QUERO CONTEMPLAR, A TUA GLÓRIA COM A GLÓRIA DO SENHOR, QUERO HABITAR DENTRO DOS TEUS MUROS.  Ó JERUSALÉM. (JERUSALÉM)', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('710', '710 - O MEU VIVER É CRISTO', 'O MEU VIVER É CRISTO, MEU SALVADOR,\nPOIS SE ENTREGOU PARA A VIDA ETERNA ME DAR.\nHOJE VENHO A TI PARA EXPRESSAR.\n\nCORO\nTODA GRATIDÃO A TI JESUS,\nPORQUE TU ÉS MARAVILHOSO.\nPRA SEMPRE CANTAREI A TI,\nMEU REI, POIS TU ME FIZESTE BEM.\n\nMEU VERDADEIRO AMIGO SEMPRE FIEL.\nESCONDERIJO MEU, PARA SEMPRE EMANUEL.\nÉS O MEU SENHOR, A RAZÃO DO MEU VIVER!\nHOJE VENHO A TI PARA EXPRESSAR.\n\nCORO (2X)\nTODA GRATIDÃO A TI JESUS,\nPORQUE TU ÉS MARAVILHOSO.\nPRA SEMPRE CANTAREI A TI,\nMEU REI, POIS TU ME FIZESTE BEM.\n\nFINAL: \nPOIS TU ME FIZESTE BEM.', 'O MEU VIVER É CRISTO, MEU SALVADOR, POIS SE ENTREGOU PARA A VIDA ETERNA ME DAR. HOJE VENHO A TI PARA EXPRESSAR. CORO TODA GRATIDÃO A TI JESUS, PORQUE TU ÉS MARAVILHOSO. PRA SEMPRE CANTAREI A TI, MEU REI, POIS TU ME FIZESTE BEM. MEU VERDADEIRO AMIGO SEMPRE FIEL. ESCONDERIJO MEU, PARA SEMPRE EMANUEL. ÉS O MEU SENHOR, A RAZÃO DO MEU VIVER! HOJE VENHO A TI PARA EXPRESSAR. TODA GRATIDÃO A TI JESUS, PORQUE TU ÉS MARAVILHOSO. PRA SEMPRE CANTAREI A TI, MEU REI, POIS TU ME FIZESTE BEM.  POIS TU ME FIZESTE BEM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('711', '711 - TUA GRAÇA ME BASTA, Ó SENHOR', 'TUA GRAÇA ME BASTA, Ó SENHOR,\nESTA GRAÇA QUE UM DIA ME ALCANÇOU.\nTEU ESPÍRITO ALEGRA O MEU CORAÇÃO,\nME FAZENDO VIVER A REVELAÇÃO,\nRENOVANDO A SALVAÇÃO EM MINHA VIDA.\n\nCORO\nDERRAMA SOBRE NÓS A TUA GRAÇA \n(DERRAMA TUA GRAÇA),\nDERRAMA SOBRE NÓS O TEU PODER,\nPREPARANDO NOSSOS CORAÇÕES,\nPARA O GRANDE DIA DO SENHOR,\nMARANATA! ORA VEM, SENHOR JESUS.\n\nTUA GRAÇA NOS LIVRA DO MUNDO,\n(TUA GRAÇA NOS LIVRA DO MUNDO)\nTRAZENDO VIDA NOVA A CADA DIA.\nTEU ESPÍRITO HABITA ENTRE NÓS,\nE NÓS TEMOS A CERTEZA DA SALVAÇÃO,\nANSIANDO EM BREVE ESTAR NA TUA GLÓRIA.\n\nCORO (2X)\nDERRAMA SOBRE NÓS A TUA GRAÇA \n(DERRAMA TUA GRAÇA),\nDERRAMA SOBRE NÓS O TEU PODER,\nPREPARANDO NOSSOS CORAÇÕES,\nPARA O GRANDE DIA DO SENHOR,\nMARANATA! ORA VEM, SENHOR JESUS.\n\nFINAL:\nPREPARANDO NOSSOS CORAÇÕES,\nPARA O GRANDE DIA DO SENHOR,\nMARANATA! ORA VEM, SENHOR JESUS!\n(MARANATA! ORA VEM)\nVEM, JESUS!', 'TUA GRAÇA ME BASTA, Ó SENHOR, ESTA GRAÇA QUE UM DIA ME ALCANÇOU. TEU ESPÍRITO ALEGRA O MEU CORAÇÃO, ME FAZENDO VIVER A REVELAÇÃO, RENOVANDO A SALVAÇÃO EM MINHA VIDA. CORO DERRAMA SOBRE NÓS A TUA GRAÇA (DERRAMA TUA GRAÇA), DERRAMA SOBRE NÓS O TEU PODER, PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS. TUA GRAÇA NOS LIVRA DO MUNDO, (TUA GRAÇA NOS LIVRA DO MUNDO) TRAZENDO VIDA NOVA A CADA DIA. TEU ESPÍRITO HABITA ENTRE NÓS, E NÓS TEMOS A CERTEZA DA SALVAÇÃO, ANSIANDO EM BREVE ESTAR NA TUA GLÓRIA. DERRAMA SOBRE NÓS A TUA GRAÇA (DERRAMA TUA GRAÇA), DERRAMA SOBRE NÓS O TEU PODER, PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS. PREPARANDO NOSSOS CORAÇÕES, PARA O GRANDE DIA DO SENHOR, MARANATA! ORA VEM, SENHOR JESUS! (MARANATA! ORA VEM) VEM, JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('712', '712 - AO FINDAR O LABOR DESTA VIDA  (A ÚLTIMA HORA)', 'AO FINDAR O LABOR DESTA VIDA,\nQUANDO A MORTE A TEU LADO CHEGAR,\nQUE DESTINO HÁ DE TER A TUA ALMA?\nQUAL SERÁ NO FUTURO TEU LAR?\n\nCORO\nMEU AMIGO, HOJE TU TENS A ESCOLHA:\nVIDA OU MORTE QUAL VAIS ACEITAR?\nAMANHÃ PODE SER MUITO TARDE;\nHOJE CRISTO TE QUER LIBERTAR.\n\nTU PROCURAS A PAZ NESTE MUNDO,\nEM PRAZERES QUE PASSAM EM VÃO,\nMAS NA ÚLTIMA HORA DA VIDA,\nELES NUNCA TE SATISFARÃO.\n\nCORO\nMEU AMIGO, HOJE TU TENS A ESCOLHA:\nVIDA OU MORTE QUAL VAIS ACEITAR?\nAMANHÃ PODE SER MUITO TARDE;\nHOJE CRISTO TE QUER LIBERTAR.\n\nCOM TUA ALMA MANCHADA NÃO PODES\nNUNCA VER O SEMBLANTE DE DEUS;\nSÓ OS CRENTES COM CORAÇÃO LIMPO\nPODERÃO TER O GOZO DOS CÉUS.\n\nCORO\nMEU AMIGO, HOJE TU TENS A ESCOLHA:\nVIDA OU MORTE QUAL VAIS ACEITAR?\nAMANHÃ PODE SER MUITO TARDE;\nHOJE CRISTO TE QUER LIBERTAR.\n\nSE TU QUERES DEIXAR TEUS PECADOS,\nENTREGAR TUA VIDA A JESUS,\nTU TERÁS, SIM, NA ÚLTIMA HORA\nUM CAMINHO BRILHANTE DE LUZ.\n\nCORO\nMEU AMIGO, HOJE TU TENS A ESCOLHA:\nVIDA OU MORTE QUAL VAIS ACEITAR?\nAMANHÃ PODE SER MUITO TARDE;\nHOJE CRISTO TE QUER LIBERTAR.', 'AO FINDAR O LABOR DESTA VIDA, QUANDO A MORTE A TEU LADO CHEGAR, QUE DESTINO HÁ DE TER A TUA ALMA? QUAL SERÁ NO FUTURO TEU LAR? CORO MEU AMIGO, HOJE TU TENS A ESCOLHA: VIDA OU MORTE QUAL VAIS ACEITAR? AMANHÃ PODE SER MUITO TARDE; HOJE CRISTO TE QUER LIBERTAR. TU PROCURAS A PAZ NESTE MUNDO, EM PRAZERES QUE PASSAM EM VÃO, MAS NA ÚLTIMA HORA DA VIDA, ELES NUNCA TE SATISFARÃO. COM TUA ALMA MANCHADA NÃO PODES NUNCA VER O SEMBLANTE DE DEUS; SÓ OS CRENTES COM CORAÇÃO LIMPO PODERÃO TER O GOZO DOS CÉUS. SE TU QUERES DEIXAR TEUS PECADOS, ENTREGAR TUA VIDA A JESUS, TU TERÁS, SIM, NA ÚLTIMA HORA UM CAMINHO BRILHANTE DE LUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('713', '713 - CAREÇO DE JESUS!', 'CAREÇO DE JESUS!\nSEMPRE DE TI, SENHOR!\nSOMENTE A TUA VOZ\nTEM PARA MIM VALOR!\n\nCORO\nDE TI, SENHOR, CAREÇO,\nSEMPRE DE TI CAREÇO!\nOH! DÁ-ME A TUA BÊNÇÃO,\nASPIRO A TI!\n\nCAREÇO DE JESUS!\nUNIDO A TI, SENHOR,\nPECADO E TENTAÇÃO\nPERDEM O SEU VIGOR.\n\nCORO\nDE TI, SENHOR, CAREÇO,\nSEMPRE DE TI CAREÇO!\nOH! DÁ-ME A TUA BÊNÇÃO,\nASPIRO A TI!\n\nCAREÇO DE JESUS!\nREGE MEU CORAÇÃO!\nENSINA-ME A VIVER\nEM SANTA RETIDÃO!\n\nCORO\nDE TI, SENHOR, CAREÇO,\nSEMPRE DE TI CAREÇO!\nOH! DÁ-ME A TUA BÊNÇÃO,\nASPIRO A TI!\n\nCAREÇO DE JESUS!\nNAS TREVAS E NA LUZ!\nSEM TI A VIDA É VÃ;\nSOU POBRE SEM JESUS.\n\nCORO\nDE TI, SENHOR, CAREÇO,\nSEMPRE DE TI CAREÇO!\nOH! DÁ-ME A TUA BÊNÇÃO,\nASPIRO A TI!', 'CAREÇO DE JESUS! SEMPRE DE TI, SENHOR! SOMENTE A TUA VOZ TEM PARA MIM VALOR! CORO DE TI, SENHOR, CAREÇO, SEMPRE DE TI CAREÇO! OH! DÁ-ME A TUA BÊNÇÃO, ASPIRO A TI! CAREÇO DE JESUS! UNIDO A TI, SENHOR, PECADO E TENTAÇÃO PERDEM O SEU VIGOR. CAREÇO DE JESUS! REGE MEU CORAÇÃO! ENSINA-ME A VIVER EM SANTA RETIDÃO! CAREÇO DE JESUS! NAS TREVAS E NA LUZ! SEM TI A VIDA É VÃ; SOU POBRE SEM JESUS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('714', '714 - COM TUA MÃO SEGURA BEM A MINHA  (AMPARO DIVINO)', 'COM TUA MÃO SEGURA BEM A MINHA,\nPOIS EU TÃO FRÁGIL SOU, Ó SALVADOR,\nQUE NÃO ME ATREVO A DAR JAMAIS UM PASSO\nSEM TEU AMPARO, CRISTO, MEU SENHOR!\n\nINSTRUMENTOS\n\nCOM TUA MÃO SEGURA BEM A MINHA,\nE PELO MUNDO, ALEGRE, SEGUIREI.\nMESMO ONDE AS SOMBRAS CAEM MAIS ESCURAS,\nTEU ROSTO VENDO, NADA TEMEREI.\n\nINSTRUMENTOS\n\nQUANDO EU CHEGAR À BEIRA DESSE RIO\nQUE TU, POR MIM, QUISESTE ATRAVESSAR,\nCOM TUA MÃO SEGURA BEM A MINHA,\nE SOBRE A MORTE EU HEI DE TRIUNFAR.\n\nINSTRUMENTOS\n\nAO REGRESSARES, LÁ DOS CÉUS DESCENDO,\nA MINHA MÃO SEGURA BEM, SENHOR,\nPOIS, MEU JESUS, EU QUERO ENTRAR CONTIGO\nLÁ ONDE EU GOZE O TEU ETERNO AMOR.', 'COM TUA MÃO SEGURA BEM A MINHA, POIS EU TÃO FRÁGIL SOU, Ó SALVADOR, QUE NÃO ME ATREVO A DAR JAMAIS UM PASSO SEM TEU AMPARO, CRISTO, MEU SENHOR! INSTRUMENTOS COM TUA MÃO SEGURA BEM A MINHA, E PELO MUNDO, ALEGRE, SEGUIREI. MESMO ONDE AS SOMBRAS CAEM MAIS ESCURAS, TEU ROSTO VENDO, NADA TEMEREI. INSTRUMENTOS QUANDO EU CHEGAR À BEIRA DESSE RIO QUE TU, POR MIM, QUISESTE ATRAVESSAR, COM TUA MÃO SEGURA BEM A MINHA, E SOBRE A MORTE EU HEI DE TRIUNFAR. INSTRUMENTOS AO REGRESSARES, LÁ DOS CÉUS DESCENDO, A MINHA MÃO SEGURA BEM, SENHOR, POIS, MEU JESUS, EU QUERO ENTRAR CONTIGO LÁ ONDE EU GOZE O TEU ETERNO AMOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('715', '715 - DEUS VOS GUARDE PELO SEU PODER', 'DEUS VOS GUARDE PELO SEU PODER,\nSEMPRE ESTEJA AO VOSSO LADO;\nVOS DISPENSE O SEU CUIDADO,\nDEUS VOS GUARDE PELO SEU PODER.\n\nCORO\nPELO SEU PODER E NO SEU AMOR,\nTÉ NOS ENCONTRARMOS COM JESUS;\nPELO SEU PODER E NO SEU AMOR,\nOH! QUE DEUS VOS GUARDE EM SUA LUZ.\n\nDEUS VOS GUARDE BEM NO SEU AMOR,\nCONSOLADOS E CONTENTES;\nACHEGADOS PARA OS CRENTES,\nDEUS VOS GUARDE BEM NO SEU AMOR.\n\nCORO\nPELO SEU PODER E NO SEU AMOR,\nTÉ NOS ENCONTRARMOS COM JESUS;\nPELO SEU PODER E NO SEU AMOR,\nOH! QUE DEUS VOS GUARDE EM SUA LUZ.\n\nDEUS VOS GUARDE DO PODER DO MAL,\nDA RUÍNA, DO PECADO;\nDOS MOTINS DE QUALQUER LADO,\nDEUS VOS GUARDE DO PODER DO MAL,\n\nCORO\nPELO SEU PODER E NO SEU AMOR,\nTÉ NOS ENCONTRARMOS COM JESUS;\nPELO SEU PODER E NO SEU AMOR,\nOH! QUE DEUS VOS GUARDE EM SUA LUZ.\n\nDEUS VOS GUARDE PARA SEU LOUVOR,\nPARA O SEU PRESENTE GOZO;\nSEU SERVIÇO GLORIOSO,\nDEUS VOS GUARDE PARA SEU LOUVOR.\n\nCORO\nPELO SEU PODER E NO SEU AMOR,\nTÉ NOS ENCONTRARMOS COM JESUS;\nPELO SEU PODER E NO SEU AMOR,\nOH! QUE DEUS VOS GUARDE EM SUA LUZ.', 'DEUS VOS GUARDE PELO SEU PODER, SEMPRE ESTEJA AO VOSSO LADO; VOS DISPENSE O SEU CUIDADO, DEUS VOS GUARDE PELO SEU PODER. CORO PELO SEU PODER E NO SEU AMOR, TÉ NOS ENCONTRARMOS COM JESUS; PELO SEU PODER E NO SEU AMOR, OH! QUE DEUS VOS GUARDE EM SUA LUZ. DEUS VOS GUARDE BEM NO SEU AMOR, CONSOLADOS E CONTENTES; ACHEGADOS PARA OS CRENTES, DEUS VOS GUARDE BEM NO SEU AMOR. DEUS VOS GUARDE DO PODER DO MAL, DA RUÍNA, DO PECADO; DOS MOTINS DE QUALQUER LADO, DEUS VOS GUARDE DO PODER DO MAL, DEUS VOS GUARDE PARA SEU LOUVOR, PARA O SEU PRESENTE GOZO; SEU SERVIÇO GLORIOSO, DEUS VOS GUARDE PARA SEU LOUVOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('716', '716 - ENQUANTO, Ó SALVADOR, TEU LIVRO LER', 'ENQUANTO, Ó SALVADOR, TEU LIVRO LER,\nMEUS OLHOS VÊM ABRIR, TE QUERO VER;\nDA MERA LETRA ALÉM, A TI, SENHOR,\nEU BUSCO A TI, JESUS, MEU REDENTOR.\n\nÀ BEIRA MAR, JESUS, PARTISTE O PÃO,\nSATISFAZENDO ALI A MULTIDÃO;\nDA VIDA O PÃO ÉS TU; VEM, POIS, ASSIM,\nSATISFAZER, SENHOR, A MIM, A MIM!', 'ENQUANTO, Ó SALVADOR, TEU LIVRO LER, MEUS OLHOS VÊM ABRIR, TE QUERO VER; DA MERA LETRA ALÉM, A TI, SENHOR, EU BUSCO A TI, JESUS, MEU REDENTOR. À BEIRA MAR, JESUS, PARTISTE O PÃO, SATISFAZENDO ALI A MULTIDÃO; DA VIDA O PÃO ÉS TU; VEM, POIS, ASSIM, SATISFAZER, SENHOR, A MIM, A MIM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('717', '717 - A TERRA SEMEAMOS', 'A TERRA SEMEAMOS \nA FIM DE NOS DAR PÃO,\nMAS DEUS É QUEM A NUTRE\nCOM BENFAZEJA MÃO.\nÉ DEUS QUEM MANDA O FRIO,\nA CHUVA E O BOM CALOR\nE FAZ SURGIR DA TERRA \nO FRUTO APÓS A FLOR.\n\nCORO\nDE TODO BEM A FONTE\nÉ NOSSO BOM SENHOR!\nLOUVAI A DEUS, LOUVAI A DEUS\nPOR TODO O SEU AMOR!\n\nO CRIADOR DE TUDO, \nQUE PERTO OU LONGE ESTÁ\nCOLORE A FLOR SILVESTRE\nE À ESTRELA O BRILHO DÁ.\nOS VENTOS LHE OBEDECEM,\nO BRAVO MAR TAMBÉM;\nAS FRÁGEIS AVEZINHAS \nO SEU CUIDADO TÊM!\n\nCORO\nDE TODO BEM A FONTE\nÉ NOSSO BOM SENHOR!\nLOUVAI A DEUS, LOUVAI A DEUS\nPOR TODO O SEU AMOR!\n\nINSTRTUMENTOS\n\nA NÓS, PORÉM, SEUS FILHOS, \nREVELA MAIS AMOR;\nO FILHO ETERNO DEU-NOS,\nJESUS, O SALVADOR,\nA NÓS FAZENDO HERDEIROS\nDE TUDO QUANTO TEM,\nNA REDENÇÃO PERFEITA \nQUE DELE NOS PROVÉM.\n\nCORO\nDE TODO BEM A FONTE\nÉ NOSSO BOM SENHOR!\nLOUVAI A DEUS, LOUVAI A DEUS\nPOR TODO O SEU AMOR!', 'A TERRA SEMEAMOS A FIM DE NOS DAR PÃO, MAS DEUS É QUEM A NUTRE COM BENFAZEJA MÃO. É DEUS QUEM MANDA O FRIO, A CHUVA E O BOM CALOR E FAZ SURGIR DA TERRA O FRUTO APÓS A FLOR. CORO DE TODO BEM A FONTE É NOSSO BOM SENHOR! LOUVAI A DEUS, LOUVAI A DEUS POR TODO O SEU AMOR! O CRIADOR DE TUDO, QUE PERTO OU LONGE ESTÁ COLORE A FLOR SILVESTRE E À ESTRELA O BRILHO DÁ. OS VENTOS LHE OBEDECEM, O BRAVO MAR TAMBÉM; AS FRÁGEIS AVEZINHAS O SEU CUIDADO TÊM! CORO DE TODO BEM A FONTE É NOSSO BOM SENHOR! LOUVAI A DEUS, LOUVAI A DEUS POR TODO O SEU AMOR! INSTRTUMENTOS A NÓS, PORÉM, SEUS FILHOS, REVELA MAIS AMOR; O FILHO ETERNO DEU-NOS, JESUS, O SALVADOR, A NÓS FAZENDO HERDEIROS DE TUDO QUANTO TEM, NA REDENÇÃO PERFEITA QUE DELE NOS PROVÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('718', '718 - GUIA, Ó CRISTO, A MINHA NAU', 'GUIA, Ó CRISTO, A MINHA NAU\nSOBRE O REVOLTOSO MAR.\nTÃO ENFURECIDO E MAU;\nQUER FAZÊ-LA NAUFRAGAR\nVEM JESUS, Ó VEM GUIAR\nMINHA NAU VEM PILOTAR.\n\nSE, NO PORTO QUANDO ENTRAR,\nMAIS O MAR SE ENFURECER,\nQUE ME POSSA DELEITAR\nEM OUVIR JESUS DIZER:\n“ENTRA POBRE VIAJOR\nNO DESCANSO DO SENHOR!”', 'GUIA, Ó CRISTO, A MINHA NAU SOBRE O REVOLTOSO MAR. TÃO ENFURECIDO E MAU; QUER FAZÊ-LA NAUFRAGAR VEM JESUS, Ó VEM GUIAR MINHA NAU VEM PILOTAR. SE, NO PORTO QUANDO ENTRAR, MAIS O MAR SE ENFURECER, QUE ME POSSA DELEITAR EM OUVIR JESUS DIZER: ENTRA POBRE VIAJOR NO DESCANSO DO SENHOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('719', '719 - AVANTE, AVANTE, Ó CRENTES', 'AVANTE, AVANTE, Ó CRENTES,\nSOLDADOS DE JESUS!\nERGUEI SEU ESTANDARTE,\nLUTAI POR SUA CRUZ!\nE CONTRA OS INIMIGOS,\nEM GRANDES MULTIDÕES\nO EXCELSO COMANDANTE\nDIRIGE OS BATALHÕES.\n\nAVANTE, AVANTE, Ó CRENTES,\nPOR CRISTO PELEJAI!\nVESTI SUA ARMADURA,\nEM SEU PODER MARCHAI!\nNO POSTO SEMPRE ACHADOS,\nVELANDO EM ORAÇÃO,\nEM MEIO AOS PERIGOS\nSEGUINDO O CAPITÃO!\n\nINSTRTUMENTOS\n\nAVANTE, AVANTE, Ó CRENTES,\nA PASSO TRIUNFAL!\nAPÓS COMBATE HORRENDO;\nVIRÁ A PAZ FINAL!\nENTÃO, ETERNAMENTE,\nSERÁ O VENCEDOR,\nNO CÉU GLORIFICADO\nCOM CRISTO, O SALVADOR!', 'AVANTE, AVANTE, Ó CRENTES, SOLDADOS DE JESUS! ERGUEI SEU ESTANDARTE, LUTAI POR SUA CRUZ! E CONTRA OS INIMIGOS, EM GRANDES MULTIDÕES O EXCELSO COMANDANTE DIRIGE OS BATALHÕES. AVANTE, AVANTE, Ó CRENTES, POR CRISTO PELEJAI! VESTI SUA ARMADURA, EM SEU PODER MARCHAI! NO POSTO SEMPRE ACHADOS, VELANDO EM ORAÇÃO, EM MEIO AOS PERIGOS SEGUINDO O CAPITÃO! INSTRTUMENTOS AVANTE, AVANTE, Ó CRENTES, A PASSO TRIUNFAL! APÓS COMBATE HORRENDO; VIRÁ A PAZ FINAL! ENTÃO, ETERNAMENTE, SERÁ O VENCEDOR, NO CÉU GLORIFICADO COM CRISTO, O SALVADOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('720', '720 - AS GRUTAS, AS ROCHAS IMENSAS (UM HINO AO SENHOR)', 'AS GRUTAS, AS ROCHAS IMENSAS,\nDOS MUNDOS O GRANDE ESPLENDOR,\nPROCLAMAM, BEM ALTO, CONSTANTES,\nUM HINO AO TEU NOME, SENHOR!\n\nCORO\nNOS CÉUS E NO MAR, E NA TERRA,\nNOS BOSQUES, NOS PRADOS EM FLOR,\nNO FRAGOSO ALCANTIL, \nNA AMPLITUDE CELESTE,\nUM HINO RESSOA AO SENHOR.\n\nNOS CÉU, AS ESTRELAS BRILHANTES,\nDOS MARES O GRANDE FRAGOR,\nE AS BRISAS ENTOAM, RIDENTES,\nUM HINO AO TEU NOME, SENHOR!\n\nCORO\nNOS CÉUS E NO MAR, E NA TERRA,\nNOS BOSQUES, NOS PRADOS EM FLOR,\nNO FRAGOSO ALCANTIL, \nNA AMPLITUDE CELESTE,\nUM HINO RESSOA AO SENHOR.\n\nAS AVES ALEGRES NA MATA,\nPOR ENTRE AS RAMAGENS EM FLOR,\nEXULTAM EM CORO, CANTANDO\nUM HINO AO TEU NOME, SENHOR!\n\nCORO\nNOS CÉUS E NO MAR, E NA TERRA,\nNOS BOSQUES, NOS PRADOS EM FLOR,\nNO FRAGOSO ALCANTIL, \nNA AMPLITUDE CELESTE,\nUM HINO RESSOA AO SENHOR.\n\nINSTRUMENTOS\n\nE TU, PECADOR, QUE VAGUEIAS,\nQUE FAZES AO TEU CRIADOR?\nNÃO ACHAS MOMENTO EM QUE CANTES\nUM HINO DE GLÓRIA AO SENHOR?\n\nCORO\nNOS CÉUS E NO MAR, E NA TERRA,\nNOS BOSQUES, NOS PRADOS EM FLOR,\nNO FRAGOSO ALCANTIL, \nNA AMPLITUDE CELESTE,\nUM HINO RESSOA AO SENHOR.\n\nFINAL: AMÉM!', 'AS GRUTAS, AS ROCHAS IMENSAS, DOS MUNDOS O GRANDE ESPLENDOR, PROCLAMAM, BEM ALTO, CONSTANTES, UM HINO AO TEU NOME, SENHOR! CORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR. NOS CÉU, AS ESTRELAS BRILHANTES, DOS MARES O GRANDE FRAGOR, E AS BRISAS ENTOAM, RIDENTES, UM HINO AO TEU NOME, SENHOR! AS AVES ALEGRES NA MATA, POR ENTRE AS RAMAGENS EM FLOR, EXULTAM EM CORO, CANTANDO UM HINO AO TEU NOME, SENHOR! CORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR. INSTRUMENTOS E TU, PECADOR, QUE VAGUEIAS, QUE FAZES AO TEU CRIADOR? NÃO ACHAS MOMENTO EM QUE CANTES UM HINO DE GLÓRIA AO SENHOR? CORO NOS CÉUS E NO MAR, E NA TERRA, NOS BOSQUES, NOS PRADOS EM FLOR, NO FRAGOSO ALCANTIL, NA AMPLITUDE CELESTE, UM HINO RESSOA AO SENHOR.  AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('721', '721 - PROCLAMAI A TODO O MUNDO QUE O SENHOR É REI', 'CORO\nPROCLAMAI A TODO O MUNDO\nQUE O SENHOR É REI!\nPROCLAMAI! PROCLAMAI!\nPROCLAMAI QUE SUAVE E DOCE\nÉ SUA SANTA LEI!\nPROCLAMAI! PROCLAMAI!\n\nPROCLAMAI COMO ELE SE CHAMA\nO SALVADOR JESUS,\nCOMO ELE POR NÓS MORREU\nNA ENSANGUENTADA CRUZ;\nCOMO ELE, O CORDEIRO, \nSOBRE O TRONO ESTÁ,\nO DEUS DE TODA A GRAÇA\nQUE MORREU EM MEU LUGAR.\n\nCORO\nPROCLAMAI A TODO O MUNDO\nQUE O SENHOR É REI!\nPROCLAMAI! PROCLAMAI!\nPROCLAMAI QUE SUAVE E DOCE\nÉ SUA SANTA LEI!\nPROCLAMAI! PROCLAMAI!\n\nPROCLAMAI QUE REINA EM GRAÇA\nNOSSO SALVADOR!\nQUE POR CETRO DE SEU REINO\nELE TEM O AMOR!\nANUNCIAI AOS TRISTES QUE ELE VIVO ESTÁ,\nE A TODOS OS CANSADOS\nQUE DESCANSO ELE DÁ.\n\nCORO\nPROCLAMAI A TODO O MUNDO\nQUE O SENHOR É REI!\nPROCLAMAI! PROCLAMAI!\nPROCLAMAI QUE SUAVE E DOCE\nÉ SUA SANTA LEI!\nPROCLAMAI! PROCLAMAI!\n\nPROCLAMAI QUE ELE DO CÉU EM BREVE DESCERÁ!\nE COM TODOS OS SEUS SANTOS APARECERÁ!\nQUE SEM DEMORA VENHAM TODOS SE RENDER,\nE COM AMOR, EM TUDO A CRISTO OBEDECER.\n\nCORO\nPROCLAMAI A TODO O MUNDO\nQUE O SENHOR É REI!\nPROCLAMAI! PROCLAMAI!\nPROCLAMAI QUE SUAVE E DOCE\nÉ SUA SANTA LEI!\nPROCLAMAI! PROCLAMAI!', 'CORO PROCLAMAI A TODO O MUNDO QUE O SENHOR É REI! PROCLAMAI! PROCLAMAI! PROCLAMAI QUE SUAVE E DOCE É SUA SANTA LEI! PROCLAMAI! PROCLAMAI! PROCLAMAI COMO ELE SE CHAMA O SALVADOR JESUS, COMO ELE POR NÓS MORREU NA ENSANGUENTADA CRUZ; COMO ELE, O CORDEIRO, SOBRE O TRONO ESTÁ, O DEUS DE TODA A GRAÇA QUE MORREU EM MEU LUGAR. PROCLAMAI QUE REINA EM GRAÇA NOSSO SALVADOR! QUE POR CETRO DE SEU REINO ELE TEM O AMOR! ANUNCIAI AOS TRISTES QUE ELE VIVO ESTÁ, E A TODOS OS CANSADOS QUE DESCANSO ELE DÁ. PROCLAMAI QUE ELE DO CÉU EM BREVE DESCERÁ! E COM TODOS OS SEUS SANTOS APARECERÁ! QUE SEM DEMORA VENHAM TODOS SE RENDER, E COM AMOR, EM TUDO A CRISTO OBEDECER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('722', '722 - A DEUS, SUPREMO BENFEITOR', 'A DEUS, SUPREMO BENFEITOR;\nVÓS ANJOS E HOMENS DAI LOUVOR.\nA DEUS, O FILHO, A DEUS, O PAI\nA DEUS ESPÍRITO, GLÓRIA DAI.', 'A DEUS, SUPREMO BENFEITOR; VÓS ANJOS E HOMENS DAI LOUVOR. A DEUS, O FILHO, A DEUS, O PAI A DEUS ESPÍRITO, GLÓRIA DAI.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('723', '723 - CRISTO PODE HOJE VIR (OH! DIA ALEGRE!)', 'CRISTO PODE HOJE VIR,\nOH! DIA ALEGRE! IREI AMIGOS VER;\nO MAL TERÁ O SEU FIM.\nSE CRISTO DO CÉU DESCER!\n\nCORO\nOH! DIA ALEGRE, DA VINDA DE JESUS!\nJESUS VEM BUSCAR-ME PARA OS CÉUS;\nELE VEM BREVE PARA OS SEUS;\nOH! DIA ALEGRE! DA VINDA DE JESUS!\n\nINSTRUMENTOS\n\nSE HOJE FOR PRA MEU LAR,\nOH! DIA ALEGRE! PARECE-ME OUVIR\nOS SALVOS CANTANDO: “VEM!''\nQUEM DERA PRA O CÉU PARTIR!\n\nCORO\nOH! DIA ALEGRE, DA VINDA DE JESUS!\nJESUS VEM BUSCAR-ME PARA OS CÉUS;\nELE VEM BREVE PARA OS SEUS;\nOH! DIA ALEGRE! DA VINDA DE JESUS!\n\nINSTRUMENTOS\n\nPOR QUE ANSIOSO ESTAR?\nOH! DIA ALEGRE! \nHÁ LUZ NA MARGEM ALÉM;\nTEMPORAL NUNCA HAVERÁ;\nDO CÉU BREVE CRISTO VEM!\n\nCORO\nOH! DIA ALEGRE, DA VINDA DE JESUS!\nJESUS VEM BUSCAR-ME PARA OS CÉUS;\nELE VEM BREVE PARA OS SEUS;\nOH! DIA ALEGRE! DA VINDA DE JESUS!\n\nINSTRUMENTOS\n\nFIEL A JESUS ESTOU,\nOH! DIA ALEGRE! \nLIVRE ESTOU PRA DIZER:\n“CRISTO É TUDO PRA MIM”;\nDO CÉU VIRÁ COM PODER!\n\nCORO\nOH! DIA ALEGRE, DA VINDA DE JESUS!\nJESUS VEM BUSCAR-ME PARA OS CÉUS;\nELE VEM BREVE PARA OS SEUS;\nOH! DIA ALEGRE! DA VINDA DE JESUS!', 'CRISTO PODE HOJE VIR, OH! DIA ALEGRE! IREI AMIGOS VER; O MAL TERÁ O SEU FIM. SE CRISTO DO CÉU DESCER! CORO OH! DIA ALEGRE, DA VINDA DE JESUS! JESUS VEM BUSCAR-ME PARA OS CÉUS; ELE VEM BREVE PARA OS SEUS; OH! DIA ALEGRE! DA VINDA DE JESUS! INSTRUMENTOS SE HOJE FOR PRA MEU LAR, OH! DIA ALEGRE! PARECE-ME OUVIR OS SALVOS CANTANDO: VEM! QUEM DERA PRA O CÉU PARTIR! POR QUE ANSIOSO ESTAR? OH! DIA ALEGRE! HÁ LUZ NA MARGEM ALÉM; TEMPORAL NUNCA HAVERÁ; DO CÉU BREVE CRISTO VEM! FIEL A JESUS ESTOU, OH! DIA ALEGRE! LIVRE ESTOU PRA DIZER: CRISTO É TUDO PRA MIM; DO CÉU VIRÁ COM PODER! CORO OH! DIA ALEGRE, DA VINDA DE JESUS! JESUS VEM BUSCAR-ME PARA OS CÉUS; ELE VEM BREVE PARA OS SEUS; OH! DIA ALEGRE! DA VINDA DE JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('724', '724 - EIS OS MILHÕES', 'EIS OS MILHÕES QUE EM TREVAS TÃO MEDONHAS\nJAZEM PERDIDOS, SEM O SALVADOR!\nOH! QUEM IRÁ AS NOVAS PROCLAMANDO\nQUE DEUS EM CRISTO SALVA O PECADOR?\n\nCORO\nTODO PODER O PAI MEU DEU,\nNA TERRA COMO LÁ NO CÉU!\nIDE, POIS, ANUNCIAI O EVANGELHO,\nE EIS-ME CONVOSCO SEMPRE!\n\nPORTAS ABERTAS EIS POR TODO O MUNDO!\nCRENTES EM CRISTO, SEMPRE AVANTE ANDAI!\nE, NA PELEJA UNINDO AS VOSSAS FORÇAS,\nDA ESCRAVIDÃO OS POVOS LIBERTAI.\n\nCORO\nTODO PODER O PAI MEU DEU,\nNA TERRA COMO LÁ NO CÉU!\nIDE, POIS, ANUNCIAI O EVANGELHO,\nE EIS-ME CONVOSCO SEMPRE!\n\n“OH! VINDE A MIM”, A VOZ DIVINA CHAMA.\n“VINDE”, CLAMAI EM NOME DE JESUS.\nPOR NOS SALVAR DA MALDIÇÃO ETERNA,\nSEU SANGUE DERRAMOU POR NÓS NA CRUZ.\n\nCORO\nTODO PODER O PAI MEU DEU,\nNA TERRA COMO LÁ NO CÉU!\nIDE, POIS, ANUNCIAI O EVANGELHO,\nE EIS-ME CONVOSCO SEMPRE!\n\nÓ DEUS! APRESSA O GLORIOSO DIA\nEM QUE OS REMIDOS TODOS SE UNIRÃO,\nE EM CORO SANTO, EXCELSO, JUBILOSO,\nPOR TODO O SEMPRE GLÓRIA A TI DARÃO!\n\nCORO\nTODO PODER O PAI MEU DEU,\nNA TERRA COMO LÁ NO CÉU!\nIDE, POIS, ANUNCIAI O EVANGELHO,\nE EIS-ME CONVOSCO SEMPRE!', 'EIS OS MILHÕES QUE EM TREVAS TÃO MEDONHAS JAZEM PERDIDOS, SEM O SALVADOR! OH! QUEM IRÁ AS NOVAS PROCLAMANDO QUE DEUS EM CRISTO SALVA O PECADOR? CORO TODO PODER O PAI MEU DEU, NA TERRA COMO LÁ NO CÉU! IDE, POIS, ANUNCIAI O EVANGELHO, E EIS-ME CONVOSCO SEMPRE! PORTAS ABERTAS EIS POR TODO O MUNDO! CRENTES EM CRISTO, SEMPRE AVANTE ANDAI! E, NA PELEJA UNINDO AS VOSSAS FORÇAS, DA ESCRAVIDÃO OS POVOS LIBERTAI. OH! VINDE A MIM, A VOZ DIVINA CHAMA. VINDE, CLAMAI EM NOME DE JESUS. POR NOS SALVAR DA MALDIÇÃO ETERNA, SEU SANGUE DERRAMOU POR NÓS NA CRUZ. Ó DEUS! APRESSA O GLORIOSO DIA EM QUE OS REMIDOS TODOS SE UNIRÃO, E EM CORO SANTO, EXCELSO, JUBILOSO, POR TODO O SEMPRE GLÓRIA A TI DARÃO!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('725', '725 - EU PERDIDO PECADOR', 'EU PERDIDO PECADOR, LONGE DO MEU JESUS,\nJÁ ME ACHAVA SEM VIGOR A PERECER SEM LUZ;\nMEU ESTADO CRISTO VI\n\n\nDANDO-ME SUA MÃO,\nE SALVAR-ME CONSEGUIU DA PERDIÇÃO.\n\nCORO\nCRISTO ME AMOU, E ME LIVROU;\nO SEU IMENSO AMOR ME TRANSFORMOU.\nFOI SEU PODER, O SEU QUERER.\nSIM, CRISTO O SALVADOR ME TRANSFORMOU.\n\nINSTRUMENTOS\n\nMINHA VIDA, TODO O SER, QUERO-LHE CONSAGRAR;\nA SEU LADO VOU VIVER, O SEU AMOR CANTAR;\nA MENSAGEM TRANSMITIR AOS QUE PERDIDOS SÃO. \nVENHAM TODOS JÁ FRUIR A SALVAÇÃO.\n\nCORO\nCRISTO ME AMOU, E ME LIVROU;\nO SEU IMENSO AMOR ME TRANSFORMOU.\nFOI SEU PODER, O SEU QUERER.\nSIM, CRISTO O SALVADOR ME TRANSFORMOU.', 'EU PERDIDO PECADOR, LONGE DO MEU JESUS, JÁ ME ACHAVA SEM VIGOR A PERECER SEM LUZ; MEU ESTADO CRISTO VI DANDO-ME SUA MÃO, E SALVAR-ME CONSEGUIU DA PERDIÇÃO. CORO CRISTO ME AMOU, E ME LIVROU; O SEU IMENSO AMOR ME TRANSFORMOU. FOI SEU PODER, O SEU QUERER. SIM, CRISTO O SALVADOR ME TRANSFORMOU. INSTRUMENTOS MINHA VIDA, TODO O SER, QUERO-LHE CONSAGRAR; A SEU LADO VOU VIVER, O SEU AMOR CANTAR; A MENSAGEM TRANSMITIR AOS QUE PERDIDOS SÃO. VENHAM TODOS JÁ FRUIR A SALVAÇÃO. CORO CRISTO ME AMOU, E ME LIVROU; O SEU IMENSO AMOR ME TRANSFORMOU. FOI SEU PODER, O SEU QUERER. SIM, CRISTO O SALVADOR ME TRANSFORMOU.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('726', '726 - QUANDO A TEMPESTADE RUGE', 'QUANDO A TEMPESTADE RUGE\nCOM O SEU FEROZ BRAMIR,\nQUANDO AS NUVENS SE ACUMULAM,\nRAIOS MIL A DESPEDIR,\nDO TROVÃO O SOM TREMENDO\nOUVE-SE ENTÃO COM PAVOR;\nMAS, NA VOZ DA TEMPESTADE,\nSOA A TUA VOZ, SENHOR!\n\nCORO\nTUA VOZ OUVIMOS NÓS\nA ANIMAR OS QUE ANDAM SÓS,\nMAS SEMPRE EM TI CONFIADOS\nE POR TI SEMPRE A LUTAR,\nNA ARIDEZ DE IMENSAS PLAGAS,\nNAS SOLIDÕES DO VASTO MAR!\n\nQUANDO O MAR VEM MANSAMENTE\nNA PRAIA SE ESPREGUIÇAR,\nQUANDO A BRISA SUSSURRANTE\nNOS SEGREDA AO PERPASSAR,\nSOA DOCE HARMONIA,\nOUVE-SE UM FELIZ RUMOR,\nSOBRE O CORO VEM DAS ONDAS\nTUA DOCE VOZ, SENHOR!\n\nCORO\nTUA VOZ OUVIMOS NÓS\nA ANIMAR OS QUE ANDAM SÓS,\nMAS SEMPRE EM TI CONFIADOS\nE POR TI SEMPRE A LUTAR,\nNA ARIDEZ DE IMENSAS PLAGAS,\nNAS SOLIDÕES DO VASTO MAR!\n\nQUANDO O CORAÇÃO AFLITO\nQUER À DOR, AO MAL FUGIR,\nE SE AGITA E LUTA E RUGE,\nSEM A DOCE PAZ SENTIR,\nENTÃO, QUAL ECO AFASTADO\nNAS QUEBRADAS A ROLAR,\nAO AFLITO E CONTRISTADO\nTUA VOZ VEM CONSOLAR.\n\nCORO\nTUA VOZ OUVIMOS NÓS\nA ANIMAR OS QUE ANDAM SÓS,\nMAS SEMPRE EM TI CONFIADOS\nE POR TI SEMPRE A LUTAR,\nNA ARIDEZ DE IMENSAS PLAGAS,\nNAS SOLIDÕES DO VASTO MAR!', 'QUANDO A TEMPESTADE RUGE COM O SEU FEROZ BRAMIR, QUANDO AS NUVENS SE ACUMULAM, RAIOS MIL A DESPEDIR, DO TROVÃO O SOM TREMENDO OUVE-SE ENTÃO COM PAVOR; MAS, NA VOZ DA TEMPESTADE, SOA A TUA VOZ, SENHOR! CORO TUA VOZ OUVIMOS NÓS A ANIMAR OS QUE ANDAM SÓS, MAS SEMPRE EM TI CONFIADOS E POR TI SEMPRE A LUTAR, NA ARIDEZ DE IMENSAS PLAGAS, NAS SOLIDÕES DO VASTO MAR! QUANDO O MAR VEM MANSAMENTE NA PRAIA SE ESPREGUIÇAR, QUANDO A BRISA SUSSURRANTE NOS SEGREDA AO PERPASSAR, SOA DOCE HARMONIA, OUVE-SE UM FELIZ RUMOR, SOBRE O CORO VEM DAS ONDAS TUA DOCE VOZ, SENHOR! QUANDO O CORAÇÃO AFLITO QUER À DOR, AO MAL FUGIR, E SE AGITA E LUTA E RUGE, SEM A DOCE PAZ SENTIR, ENTÃO, QUAL ECO AFASTADO NAS QUEBRADAS A ROLAR, AO AFLITO E CONTRISTADO TUA VOZ VEM CONSOLAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('727', '727 - EIS QUE ABRO A PORTA', 'EIS QUE ABRO A PORTA \nPRA DIZER AO POVO MEU: \nENTRA E VEM CEAR COM O VOSSO DEUS.\nANTES DAREI-VOS NOVOS CORPOS,\nPOVO QUE TANTO SOFREU.\nSERÃO GLORIFICADOS \nPARA VEREM O VOSSO DEUS.\n\nSERÁ UMA LINDA FESTA, \nELA NUNCA FINDARÁ,\nNUNCA MAIS VAMOS SOFRER, \nNUNCA MAIS VAMOS CHORAR.\nPOIS ALI CONOSCO ESTARÁ \nE O VEREMOS FACE A FACE,\nE PARA SEMPRE VAMOS ADORAR\nO DEUS QUE AQUI TANTO BUSCAMOS.\nINSTRUMENTOS\n\nEIS QUE ABRO A PORTA \nPRA DIZER AO POVO MEU: \nENTRA E VEM CEAR COM O VOSSO DEUS.\nANTES DAREI-VOS NOVOS CORPOS,\nPOVO QUE TANTO SOFREU.\nSERÃO GLORIFICADOS \nPARA VEREM O VOSSO DEUS.\n\nSERÁ UMA LINDA FESTA, \nELA NUNCA FINDARÁ,\nNUNCA MAIS VAMOS SOFRER, \nNUNCA MAIS VAMOS CHORAR.\nPOIS ALI CONOSCO ESTARÁ \nE O VEREMOS FACE A FACE,\nE PARA SEMPRE VAMOS ADORAR\nO DEUS QUE AQUI TANTO BUSCAMOS.\n\nFINAL:\nE PARA SEMPRE VAMOS ADORAR\nO DEUS QUE AQUI TANTO BUSCAMOS.', 'EIS QUE ABRO A PORTA PRA DIZER AO POVO MEU: ENTRA E VEM CEAR COM O VOSSO DEUS. ANTES DAREI-VOS NOVOS CORPOS, POVO QUE TANTO SOFREU. SERÃO GLORIFICADOS PARA VEREM O VOSSO DEUS. SERÁ UMA LINDA FESTA, ELA NUNCA FINDARÁ, NUNCA MAIS VAMOS SOFRER, NUNCA MAIS VAMOS CHORAR. POIS ALI CONOSCO ESTARÁ E O VEREMOS FACE A FACE, E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS. INSTRUMENTOS SERÁ UMA LINDA FESTA, ELA NUNCA FINDARÁ, NUNCA MAIS VAMOS SOFRER, NUNCA MAIS VAMOS CHORAR. POIS ALI CONOSCO ESTARÁ E O VEREMOS FACE A FACE, E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS.  E PARA SEMPRE VAMOS ADORAR O DEUS QUE AQUI TANTO BUSCAMOS.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('728', '728 - NOSSA GRATIDÃO', 'NOSSA GRATIDÃO E NOSSO LOUVOR \nIREMOS DEIXAR NO TEU ALTAR.\nQUE DAREMOS A TI, SENHOR, \nPELOS TEUS FEITOS E POR TUA OBRA\nAQUI NESTE LUGAR?\n\nGLÓRIA, SEMPRE GLÓRIA,\nCOM TEUS ANJOS PARA SEMPRE ADORAR.\nGLÓRIA, SEMPRE GLÓRIA,\nA TEUS PÉS, SENHOR, IREMOS EXALTAR.\n\nO SENHOR É JUSTO, É FIEL, \nSUA MISERICÓRDIA NÃO TEM FIM.\nTODAS AS TUAS OBRAS, SENHOR, TE LOUVARÃO \nE OS TEUS SANTOS TE BENDIRÃO.\n\nGLÓRIA (OS CÉUS MANIFESTAM),	\nSEMPRE GLÓRIA (SUA GLÓRIA E PODER).	\nOS ANJOS TE LOUVAM SEM CESSAR (GLÓRIA).\nGLÓRIA (EM NOSSOS LÁBIOS),\nSEMPRE GLÓRIA (HÁ SEMPRE UM LOUVOR),	\nDE EXALTAÇÃO POR TUA GLÓRIA, SENHOR.\nBIS\n\nFINAL: \nGLÓRIA (GLÓRIA) PARA SEMPRE AO SENHOR,INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('729', '729 - OS TEUS ANJOS TE EXALTAM', 'OS TEUS ANJOS TE EXALTAM, \nMEU SENHOR E REI,\nCOMO ALMEJO ESTAR NA TUA GLÓRIA.\nDEIXAR ESTE MUNDO DE TREVAS, \nCHEIO DE TRIBULAÇÕES,\nE SOMENTE TE LOUVAR, MEU SENHOR.\n\nSENHOR, TU PASSASTE UM DIA POR AFLIÇÕES\nE SABES QUE NÃO É FÁCIL VIVER AQUI.\nMAS, COMO TU VENCESTE O MUNDO,\nEU TAMBÉM POSSO VENCER,\nPOIS, NÃO HÁ NADA QUE SEJA \nIMPOSSÍVEL PARA TI.\n\nSINTO-ME FELIZ QUANDO OUÇO TUA VOZ\nA FALAR A MEU CORAÇÃO: \n“NÃO TARDAREI, EM BREVE VOLTAREI (VOLTAREI),\nPARA VOS LEVAR (SIM, VOLTAREI),\nEM BREVE VOLTAREI.”\n\nFINAL: E NÓS TE ESPERAMOS, SENHOR.', 'OS TEUS ANJOS TE EXALTAM, MEU SENHOR E REI, COMO ALMEJO ESTAR NA TUA GLÓRIA. DEIXAR ESTE MUNDO DE TREVAS, CHEIO DE TRIBULAÇÕES, E SOMENTE TE LOUVAR, MEU SENHOR. SENHOR, TU PASSASTE UM DIA POR AFLIÇÕES E SABES QUE NÃO É FÁCIL VIVER AQUI. MAS, COMO TU VENCESTE O MUNDO, EU TAMBÉM POSSO VENCER, POIS, NÃO HÁ NADA QUE SEJA IMPOSSÍVEL PARA TI. SINTO-ME FELIZ QUANDO OUÇO TUA VOZ A FALAR A MEU CORAÇÃO: NÃO TARDAREI, EM BREVE VOLTAREI (VOLTAREI), PARA VOS LEVAR (SIM, VOLTAREI), EM BREVE VOLTAREI.  E NÓS TE ESPERAMOS, SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('730', '730 - SÓ TU NOS FAZES VENCEDORES', 'SÓ TU NOS FAZES VENCEDORES,\nEM TI TEMOS FORÇAS PRA JORNADA;\nSACIA A FOME COM TEU SUBLIME \nAMOR, DOCE AMOR.\n\nPOIS NÓS TE LOUVAMOS,\nDIZEMOS: ÉS NOSSA ROCHA ETERNA,\nÉS NOSSO PASTOR, NOSSO JESUS, \nNOSSO SENHOR.\n\nSÓ TU NOS FAZES VENCEDORES,\nEM TI TEMOS FORÇAS PRA JORNADA;\nSACIA A FOME COM TEU SUBLIME \nAMOR, DOCE AMOR.\n\nPOIS NÓS TE LOUVAMOS,\nDIZEMOS: ÉS NOSSA ROCHA ETERNA,\nÉS NOSSO PASTOR, NOSSO JESUS, \nNOSSO SENHOR.\nINSTRUMENTOS\n\nNÓS TE LOUVAMOS,\nDIZEMOS: ÉS NOSSA ROCHA ETERNA,\nÉS NOSSO PASTOR, NOSSO JESUS, \nNOSSO SENHOR.\n \nFINAL:\nNOSSO PASTOR, NOSSO JESUS, \nNOSSO SENHOR.', 'SÓ TU NOS FAZES VENCEDORES, EM TI TEMOS FORÇAS PRA JORNADA; SACIA A FOME COM TEU SUBLIME AMOR, DOCE AMOR. POIS NÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR. SÓ TU NOS FAZES VENCEDORES, EM TI TEMOS FORÇAS PRA JORNADA; SACIA A FOME COM TEU SUBLIME AMOR, DOCE AMOR. POIS NÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR. INSTRUMENTOS NÓS TE LOUVAMOS, DIZEMOS: ÉS NOSSA ROCHA ETERNA, ÉS NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR.  NOSSO PASTOR, NOSSO JESUS, NOSSO SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('731', '731 - SEQUÊNCIA DE LOUVORES Nº 1', 'A\nDESDE O DIA EM QUE ACEITEI JESUS,\nA MINHA VIDA SE TRANSFORMOU.\nAGORA SOU FELIZ, AGORA VIVO BEM,\nJESUS SALVOU A MIM E SALVA A TI TAMBÉM;\nAGORA SOU FELIZ, AGORA VIVO BEM\nCOM JESUS E MAIS NINGUÉM.\nBIS\n\nB\nCORO\nA MINH’ALMA ESTÁ CHEIA DE PAZ, (BIS)\nTENHO GOZO CELESTE E PRAZER EM DIZER\nQUE A MINH’ALMA ESTÁ CHEIA DE PAZ.\n\nEU CAMINHANDO VOU, (BIS)\nPARA A CIDADE SANTA\nEU CAMINHANDO VOU.\nBIS\n\nCORO\nA MINH’ALMA ESTÁ CHEIA DE PAZ, (BIS)\nTENHO GOZO CELESTE E PRAZER EM DIZER\nQUE A MINH’ALMA ESTÁ CHEIA DE PAZ.\n\nEU VOU CANTAR NO CÉU, (BIS)\nUM HINO DE VITÓRIA\nEU VOU CANTAR NO CÉU.\nBIS\n\nCORO\nA MINH’ALMA ESTÁ CHEIA DE PAZ, (BIS)\nTENHO GOZO CELESTE E PRAZER EM DIZER\nQUE A MINH’ALMA ESTÁ CHEIA DE PAZ.\nC\nSOU FELIZ PORQUE SOU DE CRISTO (3X)\nALELUIA, GLÓRIA A DEUS.\nALELUIA, AMÉM, AMÉM, (BIS)\nALELUIA, GLÓRIA A DEUS.\n\nD\nNA CASA DE MEU PAI NÃO HÁ TRISTEZA, NÃO,\nNA CASA DE MEU PAI NÃO HÁ TRISTEZA, NÃO,\nÉ UM LUGAR MUI BELO E DE CONSAGRAÇÃO,\nNA CASA DE MEU PAI NÃO HÁ TRISTEZA, NÃO.\n\nE\nÉ ISTO QUE ME FAZ CANTAR, (BIS)\nDOS MEUS PECADOS LIVRE ESTOU\nE PARA O CÉU EU VOU,\nÉ ISTO QUE ME FAZ CANTAR.\n\nAO MUNDO VOU ANUNCIAR, (BIS)\nQUE CRISTO SALVA O POBRE PECADOR,\nPOR SEU AMOR;\nÉ ISTO QUE ME FAZ CANTAR.\n\nF\nGOZO EU TENHO EM JESUS,\nGOZO EU TENHO EM JESUS;\nMEU CORAÇÃO ALEGRE ESTÁ\nPORQUE JESUS ME VALERÁ.\nG\nJESUS É MARAVILHOSO! (BIS)\nELE SALVA, ELE CURA,\nELE FAZ FELIZ,\nSIM, JESUS É MARAVILHOSO!\nBIS', 'A DESDE O DIA EM QUE ACEITEI JESUS, A MINHA VIDA SE TRANSFORMOU. AGORA SOU FELIZ, AGORA VIVO BEM, JESUS SALVOU A MIM E SALVA A TI TAMBÉM; AGORA SOU FELIZ, AGORA VIVO BEM COM JESUS E MAIS NINGUÉM. B CORO A MINH’ALMA ESTÁ CHEIA DE PAZ,  TENHO GOZO CELESTE E PRAZER EM DIZER QUE A MINH’ALMA ESTÁ CHEIA DE PAZ. EU CAMINHANDO VOU,  PARA A CIDADE SANTA EU CAMINHANDO VOU. CORO A MINH’ALMA ESTÁ CHEIA DE PAZ,  TENHO G