INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('732', '732 - SEQUÊNCIA DE LOUVORES Nº 2', 'A\nESTAMOS MUITO ALEGRES,\nJESUS ESTÁ AQUI, SENTIMOS MUITO AMOR,\nSENTIMOS UNIÃO;\nSENTIMOS QUE JESUS\nESTÁ AQUI EM NOSSO MEIO\nPOR ISSO TEMOS PAZ NO CORAÇÃO.\nBIS\n\nB\nEU QUERO ANDAR NAS RUAS DE OURO COM JESUS,\nEU QUERO ANDAR NAS RUAS DE OURO COM JESUS,\nEU QUERO ANDAR NAS RUAS DE OURO (BIS) \nEU QUERO ANDAR NAS RUAS DE OURO COM JESUS.\n\nE LÁ NA NOVA JERUSALÉM (3X)\nEU QUERO ANDAR NAS RUAS DE OURO \nCOM JESUS.\n\nC\nEU TENHO UMA COROA LÁ NO CÉU,\nJESUS FOI PREPARÁ-LA PARA MIM, OUVI A VOZ DE CRISTO QUE DIZIA: “MEU FILHO, NÃO TARDES VIR A MIM”; EU NÃO ENDURECI MEU CORAÇÃO, A VOZ DE CRISTO EU ACEITEI,\nELE ME DISSE: “LEVANTA-TE, MEU FILHO, \nPORQUE OS TEUS PECADOS\nNO MEU SANGUE, EU JÁ LAVEI”.\nBIS\n\nD\nEU SOU FELIZ PORQUE A BÍBLIA DIZ, \nVOU PARA O CÉU O MEU LINDO PAÍS\n\nALELUIA, ALELUIA,\nALELUIA, AMÉM.\nBIS\nBIS\n\nE\nO NOME DE JESUS É DOCE, TRAZ GOZO, \nPAZ E ALEGRIA, CANTANDO ESTA MELODIA: \nJESUS, JESUS, JESUS.\nF\nCOMO É BOM DEIXAR ESTE MUNDO (3X)\nE SEGUIR AO MEU SALVADOR.', 'A ESTAMOS MUITO ALEGRES, JESUS ESTÁ AQUI, SENTIMOS MUITO AMOR, SENTIMOS UNIÃO; SENTIMOS QUE JESUS ESTÁ AQUI EM NOSSO MEIO POR ISSO TEMOS PAZ NO CORAÇÃO. B EU QUERO ANDAR NAS RUAS DE OURO COM JESUS, EU QUERO ANDAR NAS RUAS DE OURO COM JESUS, EU QUERO ANDAR NAS RUAS DE OURO  EU QUERO ANDAR NAS RUAS DE OURO COM JESUS. E LÁ NA NOVA JERUSALÉM  EU QUERO ANDAR NAS RUAS DE OURO COM JESUS. C EU TENHO UMA COROA LÁ NO CÉU, JESUS FOI PREPARÁ-LA PARA MIM, OUVI A VOZ DE CRISTO QUE DIZIA: MEU FILHO, NÃO TARDES VIR A MIM; EU NÃO ENDURECI MEU CORAÇÃO, A VOZ DE CRISTO EU ACEITEI, ELE ME DISSE: LEVANTA-TE, MEU FILHO, PORQUE OS TEUS PECADOS NO MEU SANGUE, EU JÁ LAVEI. D EU SOU FELIZ PORQUE A BÍBLIA DIZ, VOU PARA O CÉU O MEU LINDO PAÍS ALELUIA, ALELUIA, ALELUIA, AMÉM. E O NOME DE JESUS É DOCE, TRAZ GOZO, PAZ E ALEGRIA, CANTANDO ESTA MELODIA: JESUS, JESUS, JESUS. 