INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('733', '733 - SEQUÊNCIA DE LOUVORES Nº 3', 'A\nEU TENHO UM AMIGO QUE ME AMA,\nME AMA, ME AMA,\nEU TENHO UM AMIGO QUE ME AMA,\nSEU NOME É JESUS.\nJESUS, JESUS, SEU NOME É JESUS. (BIS)\n\nB\nA VIDA, A VIDA É JESUS, (BIS)\nEU VOU MORAR LÁ NO CÉU\nPORQUE A VIDA, A VIDA É JESUS.\n\nC\nMEU CORAÇÃO CONTENTE ESTÁ,\nMEU CORAÇÃO CONTENTE ESTÁ\nPORQUE O SENHOR JÁ ME SALVOU.\n\nOH! QUÃO FELIZ, FELIZ, FELIZ\nSE SENTE A ALMA COM JESUS\nCOM A PAZ QUE NENHUM \nOUTRO PODE DAR.\nBIS\nBIS\n\nD\nEU SÓ CONFIO NO SENHOR QUE NÃO VAI\nFALHAR, EU SÓ CONFIO NO SENHOR, SIGO A CANTAR;\nSE O SOL CHEGAR A ESCURECER  E O CÉU\nTOLDAR, EU SÓ CONFIO NO SENHOR\nQUE NÃO VAI FALHAR.\n\nPOSSO CONFIAR, POSSO CONFIAR \nQUE UM LAR NO CÉU CRISTO VAI ME DAR;\nSE O SOL CHEGAR A ESCURECER \nE O CÉU TOLDAR, \nEU SÓ CONFIO NO SENHOR\nQUE NÃO VAI FALHAR.', 'A EU TENHO UM AMIGO QUE ME AMA, ME AMA, ME AMA, EU TENHO UM AMIGO QUE ME AMA, SEU NOME É JESUS. JESUS, JESUS, SEU NOME É JESUS. B A VIDA, A VIDA É JESUS,  EU VOU MORAR LÁ NO CÉU PORQUE A VIDA, A VIDA É JESUS. C MEU CORAÇÃO CONTENTE ESTÁ, MEU CORAÇÃO CONTENTE ESTÁ PORQUE O SENHOR JÁ ME SALVOU. OH! QUÃO FELIZ, FELIZ, FELIZ SE SENTE A ALMA COM JESUS COM A PAZ QUE NENHUM OUTRO PODE DAR. D EU SÓ CONFIO NO SENHOR QUE NÃO VAI FALHAR, EU SÓ CONFIO NO SENHOR, SIGO A CANTAR; SE O SOL CHEGAR A ESCURECER E O CÉU TOLDAR, EU SÓ CONFIO NO SENHOR QUE NÃO VAI FALHAR. POSSO CONFIAR, POSSO CONFIAR QUE UM LAR NO CÉU CRISTO VAI ME DAR; SE O SOL CHEGAR A ESCURECER E O CÉU TOLDAR, EU SÓ CONFIO NO SENHOR QUE NÃO VAI FALHAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
