INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('762', '762 - O CÉU É UM LUGAR MARAVILHOSO', 'O CÉU É UM LUGAR MARAVILHOSO,\nCHEIO DE GLÓRIA E GOZO!\nEU QUERO VER O MEU SALVADOR:\nO CÉU É UM LUGAR MARAVILHOSO.\n\nO CÉU É UM LUGAR MARAVILHOSO,\nCHEIO DE GLÓRIA E GOZO!\nEU QUERO VER O MEU SALVADOR:\nO CÉU É UM LUGAR,  \nLUGAR PRA ONDE VOU;\nO CÉU É UM LUGAR MARAVILHOSO.', 'O CÉU É UM LUGAR MARAVILHOSO, CHEIO DE GLÓRIA E GOZO! EU QUERO VER O MEU SALVADOR: O CÉU É UM LUGAR MARAVILHOSO. O CÉU É UM LUGAR MARAVILHOSO, CHEIO DE GLÓRIA E GOZO! EU QUERO VER O MEU SALVADOR: O CÉU É UM LUGAR, LUGAR PRA ONDE VOU; O CÉU É UM LUGAR MARAVILHOSO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('763', '763 - JESUS É TUDO PRA MIM', 'JESUS É TUDO PRA MIM.	\nELE É TUDO PRA MIM.\nJESUS É O TESOURO QUE EU \nTENHO GUARDADO NO PEITO,\nELE É TUDO PRA MIM.\n\nREPETIR O HINO\nBIS', 'JESUS É TUDO PRA MIM. ELE É TUDO PRA MIM. JESUS É O TESOURO QUE EU TENHO GUARDADO NO PEITO, ELE É TUDO PRA MIM. REPETIR O HINO', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('764', '764 - NÓS VAMOS LÁ, IRMÃOS', 'NÓS VAMOS LÁ IRMÃOS, \nNÓS VAMOS LÁ \nA JERUSALÉM DE DEUS.\nMAS É COM GLÓRIA E ALELUIA\nQUE NÓS VAMOS ALI CHEGAR.\nBIS\n\nMAS É COM GLÓRIA, GLÓRIA,\nGLÓRIA, GLÓRIA, GLÓRIA A JESUS.\nMAS É COM GLÓRIA E ALELUIA\nQUE NÓS VAMOS ALI CHEGAR.\nBIS', 'NÓS VAMOS LÁ IRMÃOS, NÓS VAMOS LÁ A JERUSALÉM DE DEUS. MAS É COM GLÓRIA E ALELUIA QUE NÓS VAMOS ALI CHEGAR. MAS É COM GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA, GLÓRIA A JESUS. MAS É COM GLÓRIA E ALELUIA QUE NÓS VAMOS ALI CHEGAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('765', '765 - LÁ NO CÉU TODOS CANTAM ALELUIA!', 'LÁ NO CÉU TODOS CANTAM ALELUIA!\nLÁ NO CÉU TODOS CANTAM ALELUIA!\nLÁ NO CÉU TODOS CANTAM ALELUIA!\nEU TAMBÉM VOU PRA LÁ CANTAR.\nBIS\n\nALELUIA, VOU PARA O CÉU \nCOM JESUS CANTAR.\nALELUIA, VOU PARA O CÉU \nCOM JESUS CANTAR.\n\nLÁ NO CÉU TODOS CANTAM ALELUIA!\nLÁ NO CÉU TODOS CANTAM ALELUIA!\nLÁ NO CÉU TODOS CANTAM ALELUIA!\nEU TAMBÉM VOU PRA LÁ CANTAR.\nBIS\n\nALELUIA, VOU PARA O CÉU \nCOM JESUS CANTAR.\nALELUIA, VOU PARA O CÉU \nCOM JESUS CANTAR.', 'LÁ NO CÉU TODOS CANTAM ALELUIA! LÁ NO CÉU TODOS CANTAM ALELUIA! LÁ NO CÉU TODOS CANTAM ALELUIA! EU TAMBÉM VOU PRA LÁ CANTAR. ALELUIA, VOU PARA O CÉU COM JESUS CANTAR. ALELUIA, VOU PARA O CÉU COM JESUS CANTAR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('766', '766 - NO DIA DA GRANDE GLÓRIA', 'NO DIA DA GRANDE GLÓRIA\nHAVEMOS DE IR CANTAR LÁ NO CÉU.\nHAVEMOS DE ENCONTRAR-NOS\nALÉM DO JORDÃO, QUE ALEGRIA, \nQUE BOM!\n\nSE TU CHEGARES PRIMEIRO QUE EU\nAVISA QUE EU TAMBÉM CHEGO LÁ.\nHAVEMOS DE ENCONTRAR-NOS\nALÉM DO JORDÃO, \nQUE ALEGRIA SERÁ!\n\nHAVEMOS DE ENCONTRAR-NOS\nALÉM DO JORDÃO, QUE BELO SERÁ!\nHAVEMOS DE ENCONTRAR-NOS\nTAMBÉM COM JESUS \nQUE ALEGRIA SERÁ!\n\nLÁ NÃO VAI HAVER TRISTEZA;\nO PRANTO E A DOR LÁ NÃO TEM LUGAR!\nHAVEMOS DE ENCONTRAR-NOS\nCOM CRISTO NO CÉU \nQUE ALEGRIA SERÁ!', 'NO DIA DA GRANDE GLÓRIA HAVEMOS DE IR CANTAR LÁ NO CÉU. HAVEMOS DE ENCONTRAR-NOS ALÉM DO JORDÃO, QUE ALEGRIA, QUE BOM! SE TU CHEGARES PRIMEIRO QUE EU AVISA QUE EU TAMBÉM CHEGO LÁ. HAVEMOS DE ENCONTRAR-NOS ALÉM DO JORDÃO, QUE ALEGRIA SERÁ! HAVEMOS DE ENCONTRAR-NOS ALÉM DO JORDÃO, QUE BELO SERÁ! HAVEMOS DE ENCONTRAR-NOS TAMBÉM COM JESUS QUE ALEGRIA SERÁ! LÁ NÃO VAI HAVER TRISTEZA; O PRANTO E A DOR LÁ NÃO TEM LUGAR! HAVEMOS DE ENCONTRAR-NOS COM CRISTO NO CÉU QUE ALEGRIA SERÁ!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('767', '767 - CONTA PRA JESUS ONDE É A TUA DOR', 'CONTA PRA JESUS ONDE É A TUA DOR.\nELE TE AJUDA A CARREGAR A CRUZ.\nCOM INSISTÊNCIA ORA, \nQUE TU VAIS VENCER:\nO QUE TU PRECISAS CONTA PRA JESUS!\n\nSE DESANIMARES COM A TUA CRUZ\nLEMBRA QUE MAIOR LEVOU O MEU JESUS.\nCOM INSISTÊNCIA ORA, ELE VAI TE OUVIR:\nO QUE TU PRECISAS CONTA PRA JESUS!\n\nSE EM LÍNGUA ESTRANHA AINDA\nNÃO FALASTE, COM DEUS EM \nMISTÉRIOS PODES CONVERSAR.\nE O ESPÍRITO SANTO, TOMARÁ TEU SER:\nO QUE TU PRECISAS CONTA PRA JESUS!', 'CONTA PRA JESUS ONDE É A TUA DOR. ELE TE AJUDA A CARREGAR A CRUZ. COM INSISTÊNCIA ORA, QUE TU VAIS VENCER: O QUE TU PRECISAS CONTA PRA JESUS! SE DESANIMARES COM A TUA CRUZ LEMBRA QUE MAIOR LEVOU O MEU JESUS. COM INSISTÊNCIA ORA, ELE VAI TE OUVIR: O QUE TU PRECISAS CONTA PRA JESUS! SE EM LÍNGUA ESTRANHA AINDA NÃO FALASTE, COM DEUS EM MISTÉRIOS PODES CONVERSAR. E O ESPÍRITO SANTO, TOMARÁ TEU SER: O QUE TU PRECISAS CONTA PRA JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('768', '768 - A VITÓRIA DESTA IGREJA É ORAÇÃO', 'A VITÓRIA DESTA IGREJA É ORAÇÃO.\nA VITÓRIA DESTA IGREJA É ORAÇÃO.\nJESUS ATENDE TODA NOSSA PETIÇÃO;\nDESPERTA AGORA TU QUE DORMES\nMEU IRMÃO.\n\nVAMOS ORAR TODOS CRENTES  DESTA IGREJA\nPARA QUE JESUS AO NOSSO LADO SEMPRE ESTEJA.\n\nJESUS EM BREVE VEM BUSCAR A SUA IGREJA, \nPOIS QUEM NÃO ORA JÁ ESTÁ FORA DA PELEJA.\nBIS', 'A VITÓRIA DESTA IGREJA É ORAÇÃO. A VITÓRIA DESTA IGREJA É ORAÇÃO. JESUS ATENDE TODA NOSSA PETIÇÃO; DESPERTA AGORA TU QUE DORMES MEU IRMÃO. VAMOS ORAR TODOS CRENTES DESTA IGREJA PARA QUE JESUS AO NOSSO LADO SEMPRE ESTEJA. JESUS EM BREVE VEM BUSCAR A SUA IGREJA, POIS QUEM NÃO ORA JÁ ESTÁ FORA DA PELEJA.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('769', '769 - O GRANDE RELÓGIO', 'O GRANDE RELÓGIO JÁ DEU O SINAL:\nDESPERTA QUE É HORA, \nVAMOS TRABALHAR EM BUSCA \nDAS ALMAS PERDIDAS QUE \nESTÃO FALANDO BEM ALTO \nAO SEU CORAÇÃO.\n\nCORO (2X)\nDESPERTA QUE É HORA, \nO MESTRE JÁ VEM, JÁ VEM \nEM CAMINHO DE JERUSALÉM!\n\nA LUZ SE APAGA O DIA FINAL\nNÃO OLHES ATRÁS, POIS É TEMPO AINDA.\nNÃO TE DETENHAS, POIS JESUS TE CHAMA.\nELE TE QUER E AINDA TE AMA.\n\nCORO (2X)\nDESPERTA QUE É HORA, \nO MESTRE JÁ VEM, JÁ VEM \nEM CAMINHO DE JERUSALÉM!\n\nJESUS É O CAMINHO, A VERDADE E A VIDA.\nNINGUÉM VAI AO PAI A NÃO SER POR JESUS.\nLÁ NO CÉU SÓ ENTRA QUEM FOR\nENCONTRADO, LAVADO NO SANGUE\nVERTIDO NA CRUZ.\n\nCORO (2X)\nDESPERTA QUE É HORA, \nO MESTRE JÁ VEM, JÁ VEM \nEM CAMINHO DE JERUSALÉM!', 'O GRANDE RELÓGIO JÁ DEU O SINAL: DESPERTA QUE É HORA, VAMOS TRABALHAR EM BUSCA DAS ALMAS PERDIDAS QUE ESTÃO FALANDO BEM ALTO AO SEU CORAÇÃO. DESPERTA QUE É HORA, O MESTRE JÁ VEM, JÁ VEM EM CAMINHO DE JERUSALÉM! A LUZ SE APAGA O DIA FINAL NÃO OLHES ATRÁS, POIS É TEMPO AINDA. NÃO TE DETENHAS, POIS JESUS TE CHAMA. ELE TE QUER E AINDA TE AMA. JESUS É O CAMINHO, A VERDADE E A VIDA. NINGUÉM VAI AO PAI A NÃO SER POR JESUS. LÁ NO CÉU SÓ ENTRA QUEM FOR ENCONTRADO, LAVADO NO SANGUE VERTIDO NA CRUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('770', '770 - POR QUE DEIXAR QUE AS ÂNSIAS', 'POR QUE DEIXAR QUE AS ÂNSIAS \nDO FUTURO PERTURBEM HOJE A \nPAZ DO CORAÇÃO?\nO PAI QUE NUTRE OS FRÁGEIS PASSARINHOS, \nGARANTE A TUA PROVISÃO.\n\nPOIS SABE E CUIDA E SEMPRE TE AJUDA;\nO PAI QUE NUTRE OS FRÁGEIS PASSARINHOS, \nGARANTE A TUA PROVISÃO.', 'POR QUE DEIXAR QUE AS ÂNSIAS DO FUTURO PERTURBEM HOJE A PAZ DO CORAÇÃO? O PAI QUE NUTRE OS FRÁGEIS PASSARINHOS, GARANTE A TUA PROVISÃO. POIS SABE E CUIDA E SEMPRE TE AJUDA; O PAI QUE NUTRE OS FRÁGEIS PASSARINHOS, GARANTE A TUA PROVISÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
