INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('773', '773 - ABRA A PORTA, VENHA VER!', 'BIS\n\nABRA A PORTA, VENHA VER\nOS MENSAGEIROS DO SENHOR\nQUE ANUNCIAM A PALAVRA, \nDE DEUS E SEU AMOR!\n\nABRA A PORTA E TAMBÉM O CORAÇÃO,\nPORQUE ENTRE NÓS ESTÁ JESUS,\nQUE OUTORGA A SALVAÇÃO.\nGLÓRIA A JESUS!', 'ABRA A PORTA, VENHA VER OS MENSAGEIROS DO SENHOR QUE ANUNCIAM A PALAVRA, DE DEUS E SEU AMOR! ABRA A PORTA E TAMBÉM O CORAÇÃO, PORQUE ENTRE NÓS ESTÁ JESUS, QUE OUTORGA A SALVAÇÃO. GLÓRIA A JESUS!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('774', '774 - CHEGADO A CRUZ DO MEU SALVADOR', 'CHEGADO A CRUZ DO MEU SALVADOR,\nPROSTRADO AOS PÉS DO MEU REDENTOR.\nELE ATENDEU AO MEU CLAMOR:\nGLÓRIA AO SALVADOR!\n\nCORO\nGLÓRIA AO SALVADOR, \nGLÓRIA AO SALVADOR!\nAGORA SEI QUE ELE ME SALVOU:\nGLÓRIA AO SALVADOR!\n\nQUE MARAVILHA, JESUS ME AMOU!\nTUDO DE GRAÇA ME PERDOOU.\nQUEBROU MEUS LAÇOS E ME \nLIVROU: GLÓRIA AO SALVADOR!', 'CHEGADO A CRUZ DO MEU SALVADOR, PROSTRADO AOS PÉS DO MEU REDENTOR. ELE ATENDEU AO MEU CLAMOR: GLÓRIA AO SALVADOR! CORO GLÓRIA AO SALVADOR, GLÓRIA AO SALVADOR! AGORA SEI QUE ELE ME SALVOU: GLÓRIA AO SALVADOR! QUE MARAVILHA, JESUS ME AMOU! TUDO DE GRAÇA ME PERDOOU. QUEBROU MEUS LAÇOS E ME LIVROU: GLÓRIA AO SALVADOR!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
