INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('779', '779 - ABRE A PORTA DO TEU CORAÇÃO', 'ABRE A PORTA DO TEU CORAÇÃO\nE DEIXA CRISTO ENTRAR, ELE TE DARÁ \nSALVAÇÃO, AO CÉU TE LEVARÁ;\nABRE A PORTA DO TEU CORAÇÃO\nE DEIXA CRISTO EM TI HABITAR,\nABRE A PORTA DO TEU CORAÇÃO\nQUE JESUS TE ABENÇOARÁ.\n\nABRE A PORTA, ABRE A PORTA,\nABRE A PORTA DO TEU CORAÇÃO;\nABRE A PORTA, ABRE A PORTA,\nQUE JESUS TE DARÁ SALVAÇÃO.', 'ABRE A PORTA DO TEU CORAÇÃO E DEIXA CRISTO ENTRAR, ELE TE DARÁ SALVAÇÃO, AO CÉU TE LEVARÁ; ABRE A PORTA DO TEU CORAÇÃO E DEIXA CRISTO EM TI HABITAR, ABRE A PORTA DO TEU CORAÇÃO QUE JESUS TE ABENÇOARÁ. ABRE A PORTA, ABRE A PORTA, ABRE A PORTA DO TEU CORAÇÃO; ABRE A PORTA, ABRE A PORTA, QUE JESUS TE DARÁ SALVAÇÃO.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
