INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('781', '781 - SÓ O PODER DE DEUS', 'SÓ O PODER DE DEUS PODE MUDAR TEU SER,\nA PROVA QUE EU TE DOU: ELE MUDOU O MEU;\nNÃO VÊS QUE SOU FELIZ SEGUINDO AO SENHOR?\nNOVA CRIATURA SOU, NOVA SOU!\nBIS', 'SÓ O PODER DE DEUS PODE MUDAR TEU SER, A PROVA QUE EU TE DOU: ELE MUDOU O MEU; NÃO VÊS QUE SOU FELIZ SEGUINDO AO SENHOR? NOVA CRIATURA SOU, NOVA SOU!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('782', '782 - TENHO NOVA VIDA', 'TENHO NOVA VIDA, AGORA SOU FELIZ,\nNUNCA ESTOU SOZINHO, CRISTO MORA EM MIM;\nCANTO COM AMOR, CANTO COM ALEGRIA,\nLOUVANDO SEMPRE A JESUS QUE VIRÁ UM DIA.\n\nTUDO É NOVO EM MIM, TUDO É TÃO BELO,\nSER IGUAL A CRISTO É O QUE MAIS ANELO;\nCANTO COM ALEGRIA, CANTO COM AMOR,\nLOUVANDO SEMPRE A JESUS, \nO MEU SALVADOR.', 'TENHO NOVA VIDA, AGORA SOU FELIZ, NUNCA ESTOU SOZINHO, CRISTO MORA EM MIM; CANTO COM AMOR, CANTO COM ALEGRIA, LOUVANDO SEMPRE A JESUS QUE VIRÁ UM DIA. TUDO É NOVO EM MIM, TUDO É TÃO BELO, SER IGUAL A CRISTO É O QUE MAIS ANELO; CANTO COM ALEGRIA, CANTO COM AMOR, LOUVANDO SEMPRE A JESUS, O MEU SALVADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('783', '783 - FELIZ, FELIZ EU SOU', 'FELIZ, FELIZ, EU SOU.\nTÃO LONGE DO SENHOR ANDAVA, NÃO TINHA PAZ;\nO MUNDO ALVOROÇAVA AO MEU REDOR\nATÉ AS BELAS FLORES NÃO TINHAM BELEZA PRA MIM, \nVIVIA TRISTE, ERA INFELIZ.(ERA INFELIZ).\n\nMAS UM DIA A LUZ RAIOU,\nJESUS CRISTO ME ALCANÇOU;\nE AGORA, FELIZ,\nAO LADO DO MEU SENHOR,\nQUERO ANDAR SEGURO\nPOR ONDE EU FOR \n(POR ONDE EU FOR).\nBIS', 'FELIZ, FELIZ, EU SOU. TÃO LONGE DO SENHOR ANDAVA, NÃO TINHA PAZ; O MUNDO ALVOROÇAVA AO MEU REDOR ATÉ AS BELAS FLORES NÃO TINHAM BELEZA PRA MIM, VIVIA TRISTE, ERA INFELIZ.(ERA INFELIZ). MAS UM DIA A LUZ RAIOU, JESUS CRISTO ME ALCANÇOU; E AGORA, FELIZ, AO LADO DO MEU SENHOR, QUERO ANDAR SEGURO POR ONDE EU FOR (POR ONDE EU FOR).', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
