INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('785', '785 - ESTOU CONFIANDO', 'ESTOU CONFIANDO, SENHOR, EM TI,\nPORQUE SEI QUE TU, MEU PAI, CUIDAS DE MIM;\nAINDA QUE AS LUTAS AUMENTEM O FUROR,\nESTOU CONFIANDO EM TI, SENHOR.', 'ESTOU CONFIANDO, SENHOR, EM TI, PORQUE SEI QUE TU, MEU PAI, CUIDAS DE MIM; AINDA QUE AS LUTAS AUMENTEM O FUROR, ESTOU CONFIANDO EM TI, SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('786', '786 - QUE AS PALAVRAS DA MINHA BOCA', 'QUE AS PALAVRAS DA MINHA BOCA\nE A MEDITAÇÃO DO MEU CORAÇÃO,\nSEJAM GRATAS DIANTE DE TI, Ó SENHOR.\nBIS\n\nÓ SENHOR, Ó SENHOR!\nÓ SENHOR, Ó SENHOR!\n\nQUE AS PALAVRAS DA MINHA BOCA\nE A MEDITAÇÃO DO MEU CORAÇÃO,\nSEJAM GRATAS DIANTE DE TI, Ó SENHOR.', 'QUE AS PALAVRAS DA MINHA BOCA E A MEDITAÇÃO DO MEU CORAÇÃO, SEJAM GRATAS DIANTE DE TI, Ó SENHOR. Ó SENHOR, Ó SENHOR! Ó SENHOR, Ó SENHOR! QUE AS PALAVRAS DA MINHA BOCA E A MEDITAÇÃO DO MEU CORAÇÃO, SEJAM GRATAS DIANTE DE TI, Ó SENHOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('787', '787 - ARDENDO EM FOGO MINH’ALMA ESTÁ', 'ARDENDO EM FOGO MINH’ALMA ESTÁ \nARDENDO EM FOGO MINH’ALMA ESTÁ\nGLORIOSA CHAMA ME LIMPARÁ.\nOH! ALELUIA! MINH’ALMA ARDENDO ESTÁ!\n\nOH! SENHOR, QUERO QUE ARDAS EM MEU SER,\nE COMO A SARÇA QUERO ARDER COM TEU PODER, \nEM NOVAS LÍNGUAS VOU FALAR COMO SINAL, \nESTOU ARDENDO COM O FOGO CELESTIAL.\n\nQUERO ADORAR-TE E AMAR-TE SÓ A TI\nCOMO SE ADORA EM ESPÍRITO E EM VERDADE;\nOH! SENHOR, QUERO QUE ARDAS EM MEU SER, \nE COMO A SARÇA QUERO ARDER COM TEU PODER.\n\nARDENDO EM FOGO MINH’ALMA ESTÁ\nARDENDO EM FOGO MINH’ALMA ESTÁ\nGLORIOSA CHAMA ME LIMPARÁ.\nOH! ALELUIA! MINH’ALMA ARDENDO ESTÁ!', 'ARDENDO EM FOGO MINH’ALMA ESTÁ ARDENDO EM FOGO MINH’ALMA ESTÁ GLORIOSA CHAMA ME LIMPARÁ. OH! ALELUIA! MINH’ALMA ARDENDO ESTÁ! OH! SENHOR, QUERO QUE ARDAS EM MEU SER, E COMO A SARÇA QUERO ARDER COM TEU PODER, EM NOVAS LÍNGUAS VOU FALAR COMO SINAL, ESTOU ARDENDO COM O FOGO CELESTIAL. QUERO ADORAR-TE E AMAR-TE SÓ A TI COMO SE ADORA EM ESPÍRITO E EM VERDADE; OH! SENHOR, QUERO QUE ARDAS EM MEU SER, E COMO A SARÇA QUERO ARDER COM TEU PODER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('788', '788 - IGREJA, TU VAIS SUBIR', 'IGREJA, TU VAIS SUBIR, (BIS)\nESPERA MAIS UM POUQUINHO,\nQUE O NOIVO HÁ DE VIR.\n\nIGREJA, FOSTE COMPRADA\nCOM O SANGUE DE JESUS;\nIGREJA, FOSTE SELADA\nCOM O SANGUE DESSA CRUZ.\nBIS', 'IGREJA, TU VAIS SUBIR,  ESPERA MAIS UM POUQUINHO, QUE O NOIVO HÁ DE VIR. IGREJA, FOSTE COMPRADA COM O SANGUE DE JESUS; IGREJA, FOSTE SELADA COM O SANGUE DESSA CRUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('789', '789 - NA PRESENÇA DE JESUS', 'NA PRESENÇA DE JESUS\nNÃO HÁ DOR NEM PRANTO:\nFICO CHEIO DE ALEGRIA,\nCHEIO DO ESPÍRITO SANTO!\nBIS\n\nCORO\nCRISTO VEM NA SUA GLÓRIA;\nQUERO IR TAMBÉM, QUERO IR TAMBÉM.\nQUERO PERTENCER À GLÓRIA\nQUE OS REMIDOS TEM.\nQUERO SUBIR COM JESUS\nPARA A MANSÃO DE ALÉM.\nVOU MORAR NA GLÓRIA .\nPARA SEMPRE, AMÉM.\n\nO DEVER DE TODO CRENTE\nÉ VIGIAR E ORAR.\nCRISTO VEM BUSCAR A IGREJA,\nOLHA QUE TU VAIS FICAR.\nBIS\n\nCORO\nCRISTO VEM NA SUA GLÓRIA;\nQUERO IR TAMBÉM, QUERO IR TAMBÉM.\nQUERO PERTENCER À GLÓRIA\nQUE OS REMIDOS TEM.\nQUERO SUBIR COM JESUS\nPARA A MANSÃO DE ALÉM.\nVOU MORAR NA GLÓRIA .\nPARA SEMPRE, AMÉM.\n\nTODO CRENTE QUE VIGIA\nVIVE COM JESUS,\nFICA CHEIO DE ALEGRIA\nQUANDO MANIFESTA A LUZ.\nBIS\n\nCORO\nCRISTO VEM NA SUA GLÓRIA;\nQUERO IR TAMBÉM, QUERO IR TAMBÉM.\nQUERO PERTENCER À GLÓRIA\nQUE OS REMIDOS TEM.\nQUERO SUBIR COM JESUS\nPARA A MANSÃO DE ALÉM.\nVOU MORAR NA GLÓRIA.\nPARA SEMPRE, AMÉM.\nBIS', 'NA PRESENÇA DE JESUS NÃO HÁ DOR NEM PRANTO: FICO CHEIO DE ALEGRIA, CHEIO DO ESPÍRITO SANTO! CORO CRISTO VEM NA SUA GLÓRIA; QUERO IR TAMBÉM, QUERO IR TAMBÉM. QUERO PERTENCER À GLÓRIA QUE OS REMIDOS TEM. QUERO SUBIR COM JESUS PARA A MANSÃO DE ALÉM. VOU MORAR NA GLÓRIA . PARA SEMPRE, AMÉM. O DEVER DE TODO CRENTE É VIGIAR E ORAR. CRISTO VEM BUSCAR A IGREJA, OLHA QUE TU VAIS FICAR. TODO CRENTE QUE VIGIA VIVE COM JESUS, FICA CHEIO DE ALEGRIA QUANDO MANIFESTA A LUZ. CORO CRISTO VEM NA SUA GLÓRIA; QUERO IR TAMBÉM, QUERO IR TAMBÉM. QUERO PERTENCER À GLÓRIA QUE OS REMIDOS TEM. QUERO SUBIR COM JESUS PARA A MANSÃO DE ALÉM. VOU MORAR NA GLÓRIA. PARA SEMPRE, AMÉM.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('790', '790 - AQUELE QUE HABITA NO ESCONDERIJO', 'AQUELE QUE HABITA\nNO ESCONDERIJO DO ALTÍSSIMO\nÀ SOMBRA DO SENHOR, \nONIPOTENTE DESCANSARÁ.\n\nDIREI DO SENHOR, DIREI DO SENHOR,\nDIREI DO SENHOR: ELE É O MEU DEUS.\nDIREI DO SENHOR, DIREI DO SENHOR,\nDIREI DO SENHOR: ELE É O MEU DEUS.\n\nFINAL: AMÉM! AMÉM!', 'AQUELE QUE HABITA NO ESCONDERIJO DO ALTÍSSIMO À SOMBRA DO SENHOR, ONIPOTENTE DESCANSARÁ. DIREI DO SENHOR, DIREI DO SENHOR, DIREI DO SENHOR: ELE É O MEU DEUS. DIREI DO SENHOR, DIREI DO SENHOR, DIREI DO SENHOR: ELE É O MEU DEUS.  AMÉM! AMÉM!', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('791', '791 - EU QUERO MAIS E MAIS DE CRISTO', 'EU QUERO MAIS E MAIS DE CRISTO. \nEU QUERO MAIS DO SEU PODER. \nEU QUERO MAIS DA SUA PRESENÇA. \nEU QUERO MAIS DO SEU VIVER.\nBIS\n\nMAIS, MAIS DE CRISTO,\nMAIS, MAIS DE CRISTO,\nMAIS DO SEU PURO E SANTO AMOR,\nMAIS DE TI MESMO, Ó SALVADOR.', 'EU QUERO MAIS E MAIS DE CRISTO. EU QUERO MAIS DO SEU PODER. EU QUERO MAIS DA SUA PRESENÇA. EU QUERO MAIS DO SEU VIVER. MAIS, MAIS DE CRISTO, MAIS, MAIS DE CRISTO, MAIS DO SEU PURO E SANTO AMOR, MAIS DE TI MESMO, Ó SALVADOR.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('792', '792 - LOUVANDO A DEUS', 'LOUVANDO A DEUS \nE CAINDO NA GRAÇA DE TODO O POVO.\n\nE TODOS OS DIAS ACRESCENTAVA O SENHOR\nÀ IGREJA AQUELES QUE SE HAVIAM DE SALVAR.\n\nREPETIR O HINO\nBIS', 'LOUVANDO A DEUS E CAINDO NA GRAÇA DE TODO O POVO. E TODOS OS DIAS ACRESCENTAVA O SENHOR À IGREJA AQUELES QUE SE HAVIAM DE SALVAR. REPETIR O HINO', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('793', '793 - MESA PREPARADA', 'MESA PREPARADA, CABEÇA UNGIDA,\nCÁLICE TRANSBORDANDO,\nISTO É QUE É VIDA.\n\nCORO (2X)\nISTO É QUE É VIDA, VIDA COM JESUS.\nISTO É QUE É VIDA, CARREGANDO A CRUZ.\n\nJESUS CRISTO SALVA, CURA E BATIZA,\nCOM O ESPÍRITO SANTO,\nISTO É QUE É VIDA.\n\nCORO (2X)\nISTO É QUE É VIDA, VIDA COM JESUS.\nISTO É QUE É VIDA, CARREGANDO A CRUZ.\n\nCORAÇÃO ALEGRE, ESPADA ERGUIDA,\nPOR CRISTO LUTANDO,\nISTO É QUE É VIDA.\n\nCORO (2X)\nISTO É QUE É VIDA, VIDA COM JESUS.\nISTO É QUE É VIDA, CARREGANDO A CRUZ.', 'MESA PREPARADA, CABEÇA UNGIDA, CÁLICE TRANSBORDANDO, ISTO É QUE É VIDA.  ISTO É QUE É VIDA, VIDA COM JESUS. ISTO É QUE É VIDA, CARREGANDO A CRUZ. JESUS CRISTO SALVA, CURA E BATIZA, COM O ESPÍRITO SANTO, ISTO É QUE É VIDA.  ISTO É QUE É VIDA, VIDA COM JESUS. ISTO É QUE É VIDA, CARREGANDO A CRUZ. CORAÇÃO ALEGRE, ESPADA ERGUIDA, POR CRISTO LUTANDO, ISTO É QUE É VIDA.  ISTO É QUE É VIDA, VIDA COM JESUS. ISTO É QUE É VIDA, CARREGANDO A CRUZ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('794', '794 - JESUS LIBERTOU', 'JESUS LIBERTOU MINH’ALMA DO MEU VIL PECADO.\nELE ESTÁ FALANDO A TI, QUER SALVAR, \nQUER TE PERDOAR.\n\nELE SE FEZ CARNE AQUI E NA CRUZ, \nPURO SE ENTREGOU; E VENCEU A MORTE \nPOR NÓS. PIEDOSO, VAI TE LIBERTAR, \nBASTA NO SEU NOME CRER E A ELE RECEBER.', 'JESUS LIBERTOU MINH’ALMA DO MEU VIL PECADO. ELE ESTÁ FALANDO A TI, QUER SALVAR, QUER TE PERDOAR. ELE SE FEZ CARNE AQUI E NA CRUZ, PURO SE ENTREGOU; E VENCEU A MORTE POR NÓS. PIEDOSO, VAI TE LIBERTAR, BASTA NO SEU NOME CRER E A ELE RECEBER.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id, date_insert, date_update) VALUES ('null', 'AQUILO QUE FUI NÃO SOU MAIS', 'AQUILO QUE FUI NÃO SOU MAIS,\nNEM SOU TUDO O QUE DEVO SER;\nJÁ SOU PELA GRAÇA O QUE SOU\nE AO VÊ-LO ESTOU CERTO QUE VOU\nNA GLÓRIA DE CRISTO VIVER\n\nCORO\nMARANATA, MARANATA\nEM BREVE O SENHOR VOLTARÁ;\nMARANATA, MARANATA,\nELE MESMO VIRÁ NOS BUSCAR.\n\nEU MORTO PENSEI QUE VIVIA,\nJÁ VIVO E COM CRISTO MORRI;\nPARA ELE, ESTOU RESSUSCITADO,\nPARA O MUNDO, ESTOU CRUCIFICADO;\nMINHA VIDA EM JESUS ESCONDI.\n\nCORO\nMARANATA, MARANATA\nEM BREVE O SENHOR VOLTARÁ;\nMARANATA, MARANATA,\nELE MESMO VIRÁ NOS BUSCAR.\n\nMINH’ALMA GOZANDO RIQUEZAS,\nTESOUROS QUE O MUNDO NÃO DÁ,\nCAMINHA NA DOCE ESPERANÇA,\nDÁ VIDA EM ETERNA BONANÇA,\nNA GLÓRIA ONDE JESUS ESTÁ.\n\nCORO\nMARANATA, MARANATA\nEM BREVE O SENHOR VOLTARÁ;\nMARANATA, MARANATA,\nELE MESMO VIRÁ NOS BUSCAR.', 'AQUILO QUE FUI NÃO SOU MAIS, NEM SOU TUDO O QUE DEVO SER; JÁ SOU PELA GRAÇA O QUE SOU E AO VÊ-LO ESTOU CERTO QUE VOU NA GLÓRIA DE CRISTO VIVER CORO MARANATA, MARANATA EM BREVE O SENHOR VOLTARÁ; MARANATA, MARANATA, ELE MESMO VIRÁ NOS BUSCAR. EU MORTO PENSEI QUE VIVIA, JÁ VIVO E COM CRISTO MORRI; PARA ELE, ESTOU RESSUSCITADO, PARA O MUNDO, ESTOU CRUCIFICADO; MINHA VIDA EM JESUS ESCONDI. MINH’ALMA GOZANDO RIQUEZAS, TESOUROS QUE O MUNDO NÃO DÁ, CAMINHA NA DOCE ESPERANÇA, DÁ VIDA EM ETERNA BONANÇA, NA GLÓRIA ONDE JESUS ESTÁ.', 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
