INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('17', '17 - EM TUA PRESENÇA ENTRAMOS', 'EM TUA PRESENÇA ENTRAMOS CLAMANDO A TI, Ó SENHOR, LAVA-NOS COM O TEU SANGUE, RENOVA EM NÓS TEU AMOR.\n\nCOM TEU ESPÍRITO SANTO VEM ENTRE NÓS PRESIDIR. SEJA ESTE CULTO UM INCENSO QUE POSSA AOS CÉUS SUBIR.\n\nEM TEU ALTAR ENTREGAMOS AS NOSSAS VIDAS, SENHOR, DE CORAÇÃO OFERTAMOS A TI O NOSSO LOUVOR.\n\nÓ CRISTO VIVO, REVELA TUA PRESENÇA ENTRE NÓS, COM TUA GRAÇA OPERA, FAZE-NOS OUVIR TUA VOZ.\n\nEM TUA PRESENÇA ENTRAMOS CLAMANDO A TI, Ó SENHOR, LAVA-NOS COM O TEU SANGUE, RENOVA EM NÓS TEU AMOR.', 'EM TUA PRESENÇA ENTRAMOS CLAMANDO A TI, Ó SENHOR, LAVA-NOS COM O TEU SANGUE, RENOVA EM NÓS TEU AMOR. COM TEU ESPÍRITO SANTO VEM ENTRE NÓS PRESIDIR. SEJA ESTE CULTO UM INCENSO QUE POSSA AOS CÉUS SUBIR. EM TEU ALTAR ENTREGAMOS AS NOSSAS VIDAS, SENHOR, DE CORAÇÃO OFERTAMOS A TI O NOSSO LOUVOR. Ó CRISTO VIVO, REVELA TUA PRESENÇA ENTRE NÓS, COM TUA GRAÇA OPERA, FAZE-NOS OUVIR TUA VOZ.',  1);
