INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('20', '20 - O TEU PECADO TU QUERES DEIXAR?', 'O TEU PECADO TU QUERES DEIXAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. QUERES DO MAL A VITÓRIA GANHAR? SEU SANGUE TEM ESTE PODER.\n\nCORO HÁ PODER, SIM, FORÇA E VIGOR, NESTE SANGUE DE JESUS; HÁ PODER, SIM, NO BOM SALVADOR, OH! CONFIA NO CRISTO DA CRUZ.\n\nQUERES OS VÍCIOS ABANDONAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. CONFIA EM CRISTO PARA TE CURAR, SEU SANGUE TEM ESTE PODER.\n\nCORO HÁ PODER, SIM, FORÇA E VIGOR, NESTE SANGUE DE JESUS; HÁ PODER, SIM, NO BOM SALVADOR, OH! CONFIA NO CRISTO DA CRUZ.\n\nQUERES PUREZA PARA O TEU CORAÇÃO? NO SANGUE HÁ PODER, SIM, HÁ PODER. MAIS LEALDADE, MAIS CONSAGRAÇÃO, SEU SANGUE TEM ESTE PODER.\n\nCORO HÁ PODER, SIM, FORÇA E VIGOR, NESTE SANGUE DE JESUS; HÁ PODER, SIM, NO BOM SALVADOR, OH! CONFIA NO CRISTO DA CRUZ.\n\nQUERES DE CRISTO A MENSAGEM LEVAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. QUERES COM OS ANJOS NA GLÓRIA CANTAR? SEU SANGUE TEM ESTE PODER.\n\nCORO HÁ PODER, SIM, FORÇA E VIGOR, NESTE SANGUE DE JESUS; HÁ PODER, SIM, NO BOM SALVADOR, OH! CONFIA NO CRISTO DA CRUZ.', 'O TEU PECADO TU QUERES DEIXAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. QUERES DO MAL A VITÓRIA GANHAR? SEU SANGUE TEM ESTE PODER. CORO HÁ PODER, SIM, FORÇA E VIGOR, NESTE SANGUE DE JESUS; HÁ PODER, SIM, NO BOM SALVADOR, OH! CONFIA NO CRISTO DA CRUZ. QUERES OS VÍCIOS ABANDONAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. CONFIA EM CRISTO PARA TE CURAR, SEU SANGUE TEM ESTE PODER. QUERES PUREZA PARA O TEU CORAÇÃO? NO SANGUE HÁ PODER, SIM, HÁ PODER. MAIS LEALDADE, MAIS CONSAGRAÇÃO, SEU SANGUE TEM ESTE PODER. QUERES DE CRISTO A MENSAGEM LEVAR? NO SANGUE HÁ PODER, SIM, HÁ PODER. QUERES COM OS ANJOS NA GLÓRIA CANTAR? SEU SANGUE TEM ESTE PODER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('21', '21 - NESTA HORA DE CLAMOR', 'NESTA HORA DE CLAMOR PELO SANGUE DE JESUS (VARÕES) NECESSITADO, DEPENDENTE, ME COLOCO AOS TEUS PÉS, Ó PAI.\n\nCORO DÁ-ME O PÃO COTIDIANO, ESSE PÃO ÉS TU JESUS; TUA PAZ É O MEU ALIMENTO, DÁ-ME DE TI, SENHOR.\n\nAS RIQUEZAS QUE EU ENCONTRO EM TI, SÃO PRA MIM MEU BEM MAIOR. EM TI ESPERO, EM TI CONFIO. DE TI VEM MINHA SALVAÇÃO.\n\nCORO DÁ-ME O PÃO COTIDIANO, ESSE PÃO ÉS TU JESUS; TUA PAZ É O MEU ALIMENTO, DÁ-ME DE TI, SENHOR. INSTRUMENTOS\n\nNA ETERNIDADE HABITAREI. LÁ VEREI O MEU GRANDE REI! RUAS DE OURO, RIOS DE CRISTAL. GLÓRIA AO TEU SANTO ESPÍRITO!\nBIS', 'NESTA HORA DE CLAMOR PELO SANGUE DE JESUS  NECESSITADO, DEPENDENTE, ME COLOCO AOS TEUS PÉS, Ó PAI. CORO DÁ-ME O PÃO COTIDIANO, ESSE PÃO ÉS TU JESUS; TUA PAZ É O MEU ALIMENTO, DÁ-ME DE TI, SENHOR. AS RIQUEZAS QUE EU ENCONTRO EM TI, SÃO PRA MIM MEU BEM MAIOR. EM TI ESPERO, EM TI CONFIO. DE TI VEM MINHA SALVAÇÃO. CORO DÁ-ME O PÃO COTIDIANO, ESSE PÃO ÉS TU JESUS; TUA PAZ É O MEU ALIMENTO, DÁ-ME DE TI, SENHOR. INSTRUMENTOS NA ETERNIDADE HABITAREI. LÁ VEREI O MEU GRANDE REI! RUAS DE OURO, RIOS DE CRISTAL. GLÓRIA AO TEU SANTO ESPÍRITO!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('22', '22 - BENDITO SEJA O CORDEIRO', 'BENDITO SEJA O CORDEIRO QUE NA CRUZ POR NÓS PADECEU! BENDITO SEJA O SEU SANGUE QUE POR NÓS ALI ELE VERTEU! EIS NESSE SANGUE LAVADOS, COM ROUPAS QUE TÃO ALVAS SÃO, OS PECADORES REMIDOS QUE PERANTE SEU DEUS JÁ ESTÃO!\n\nCORO ALVO MAIS QUE A NEVE! (BIS) SIM, NESTE SANGUE LAVADO, MAIS ALVO QUE A NEVE SEREI!\n\nQUÃO ESPINHOSA COROA QUE JESUS POR NÓS SUPORTOU! OH! QUÃO PROFUNDAS AS CHAGAS QUE NOS PROVAM QUANTO ELE AMOU!\n\nEIS NESSAS CHAGAS PUREZA PARA O MAIOR PECADOR! POIS QUE MAIS ALVOS QUE A NEVE, O TEU SANGUE NOS TORNA, SENHOR!\n\nCORO ALVO MAIS QUE A NEVE! (BIS) SIM, NESTE SANGUE LAVADO, MAIS ALVO QUE A NEVE SEREI!\n\nSE NÓS A TI CONFESSARMOS E SEGUIRMOS NA TUA LUZ, TU NÃO SOMENTE PERDOAS, PURIFICAS TAMBÉM, Ó JESUS!\n\nSIM, E DE TODO O PECADO! QUE MARAVILHA DE AMOR! POIS QUE MAIS ALVOS QUE A NEVE O TEU SANGUE NOS TORNA, SENHOR!\n\nCORO ALVO MAIS QUE A NEVE! (BIS) SIM, NESTE SANGUE LAVADO, MAIS ALVO QUE A NEVE SEREI!', 'BENDITO SEJA O CORDEIRO QUE NA CRUZ POR NÓS PADECEU! BENDITO SEJA O SEU SANGUE QUE POR NÓS ALI ELE VERTEU! EIS NESSE SANGUE LAVADOS, COM ROUPAS QUE TÃO ALVAS SÃO, OS PECADORES REMIDOS QUE PERANTE SEU DEUS JÁ ESTÃO! CORO ALVO MAIS QUE A NEVE!  SIM, NESTE SANGUE LAVADO, MAIS ALVO QUE A NEVE SEREI! QUÃO ESPINHOSA COROA QUE JESUS POR NÓS SUPORTOU! OH! QUÃO PROFUNDAS AS CHAGAS QUE NOS PROVAM QUANTO ELE AMOU! EIS NESSAS CHAGAS PUREZA PARA O MAIOR PECADOR! POIS QUE MAIS ALVOS QUE A NEVE, O TEU SANGUE NOS TORNA, SENHOR! SE NÓS A TI CONFESSARMOS E SEGUIRMOS NA TUA LUZ, TU NÃO SOMENTE PERDOAS, PURIFICAS TAMBÉM, Ó JESUS! SIM, E DE TODO O PECADO! QUE MARAVILHA DE AMOR! POIS QUE MAIS ALVOS QUE A NEVE O TEU SANGUE NOS TORNA, SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('23', '23 - SENHOR, OUVE A MINHA ORAÇÃO', 'SENHOR, OUVE A MINHA ORAÇÃO E DÁ-ME PLENA COMUNHÃO, LIBERTA DO MEU VIL PECADO; SENHOR, OUVE A MINHA ORAÇÃO. TRANSBORDA, INUNDA O MEU SER, TRANSFORMA, FAZE EM MIM TEU QUERER, DERRAMA DO SANGUE DE CRISTO; SENHOR, OUVE A MINHA ORAÇÃO.', 'SENHOR, OUVE A MINHA ORAÇÃO E DÁ-ME PLENA COMUNHÃO, LIBERTA DO MEU VIL PECADO; SENHOR, OUVE A MINHA ORAÇÃO. TRANSBORDA, INUNDA O MEU SER, TRANSFORMA, FAZE EM MIM TEU QUERER, DERRAMA DO SANGUE DE CRISTO; SENHOR, OUVE A MINHA ORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('24', '24 - EIS O CORDEIRO DE DEUS', 'EIS O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, QUE MORREU NO MEU LUGAR, ENTÃO PODE ME PERDOAR.\n\nCORO CLAMO AGORA PEDINDO: VEM PERDOAR, (VEM PERDOAR) PURIFICAR, (PURIFICAR) VEM TRANSFORMAR E RENOVAR, VEM RESTAURAR.\n\nDIANTE DO TEU ALTAR DEIXO A MINHA ANSIEDADE, PELO SANGUE DE JESUS, TENHO VIDA.\n\nCORO CLAMO AGORA PEDINDO: VEM PERDOAR, (VEM PERDOAR) PURIFICAR, (PURIFICAR) VEM TRANSFORMAR E RENOVAR, VEM RESTAURAR. FINAL: VEM RESTAURAR.', 'EIS O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, QUE MORREU NO MEU LUGAR, ENTÃO PODE ME PERDOAR. CORO CLAMO AGORA PEDINDO: VEM PERDOAR, (VEM PERDOAR) PURIFICAR, (PURIFICAR) VEM TRANSFORMAR E RENOVAR, VEM RESTAURAR. DIANTE DO TEU ALTAR DEIXO A MINHA ANSIEDADE, PELO SANGUE DE JESUS, TENHO VIDA. CORO CLAMO AGORA PEDINDO: VEM PERDOAR, (VEM PERDOAR) PURIFICAR, (PURIFICAR) VEM TRANSFORMAR E RENOVAR, VEM RESTAURAR.  VEM RESTAURAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('25', '25 - SENHOR, A TI CLAMO', 'SENHOR, A TI CLAMO, INCLINA OS TEUS OUVIDOS À MINHA VOZ; LAVA A MINH’ALMA COM O SANGUE DE JESUS, SUPLICO, SENHOR!\nBIS\n\nDÁ-ME UM CÂNTICO NOVO, FAZ MEU LOUVOR TOCAR JESUS; Ó DEUS, INCLINA OS TEUS OUVIDOS E OUVE A MINHA VOZ!\nBIS', 'SENHOR, A TI CLAMO, INCLINA OS TEUS OUVIDOS À MINHA VOZ; LAVA A MINH’ALMA COM O SANGUE DE JESUS, SUPLICO, SENHOR! DÁ-ME UM CÂNTICO NOVO, FAZ MEU LOUVOR TOCAR JESUS; Ó DEUS, INCLINA OS TEUS OUVIDOS E OUVE A MINHA VOZ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('26', '26 - SEJA SÓ O SENHOR LOUVADO AQUI', 'SEJA SÓ O SENHOR LOUVADO AQUI, ALELUIA! NESTE ABENÇOADO MAANAIM, ALELUIA! POIS PRESENTE ELE ESTÁ, QUER A TODOS ABENÇOAR, SUA GRAÇA DERRAMAR, ALELUIA!\n\nE PELO TEU SANGUE CLAMAMOS, SENHOR, ALELUIA! TEU PRECIOSO SANGUE REMIDOR, ALELUIA! PÕE TEU SANGUE SOBRE MIM, DE TODO O MAL LIVRA-ME E ASSIM, GOZO ENTÃO TEREI SEM FIM, ALELUIA!', 'SEJA SÓ O SENHOR LOUVADO AQUI, ALELUIA! NESTE ABENÇOADO MAANAIM, ALELUIA! POIS PRESENTE ELE ESTÁ, QUER A TODOS ABENÇOAR, SUA GRAÇA DERRAMAR, ALELUIA! E PELO TEU SANGUE CLAMAMOS, SENHOR, ALELUIA! TEU PRECIOSO SANGUE REMIDOR, ALELUIA! PÕE TEU SANGUE SOBRE MIM, DE TODO O MAL LIVRA-ME E ASSIM, GOZO ENTÃO TEREI SEM FIM, ALELUIA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('27', '27 - CORDEIRO ETERNO', 'CORDEIRO ETERNO, VIESTE PRA ME DAR TUA VIDA, TEU SANGUE, PRA QUE EU PUDESSE TER A VIDA ETERNA E O PERDÃO, A CURA, A PAZ, LIBERTAÇÃO, CORDEIRO ETERNO DE DEUS.\n\nE HOJE TEU SANGUE ESTÁ AGINDO EM MIM, LIMPANDO, CURANDO, MUDANDO O MEU SER; OH! QUE PODER E VIDA HÁ NO SANGUE QUE JESUS VERTEU, CORDEIRO ETERNO DE DEUS. FINAL: CORDEIRO ETERNO DE DEUS.\nBIS', 'CORDEIRO ETERNO, VIESTE PRA ME DAR TUA VIDA, TEU SANGUE, PRA QUE EU PUDESSE TER A VIDA ETERNA E O PERDÃO, A CURA, A PAZ, LIBERTAÇÃO, CORDEIRO ETERNO DE DEUS. E HOJE TEU SANGUE ESTÁ AGINDO EM MIM, LIMPANDO, CURANDO, MUDANDO O MEU SER; OH! QUE PODER E VIDA HÁ NO SANGUE QUE JESUS VERTEU, CORDEIRO ETERNO DE DEUS.  CORDEIRO ETERNO DE DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('28', '28 - O PODER DO SANGUE DE JESUS', 'O PODER DO SANGUE DE JESUS HÁ DE ME PURIFICAR, MEU PECADO PERDOAR; O PODER DO SANGUE DE JESUS DÁ-ME PLENA COMUNHÃO, TRAZ-ME A REVELAÇÃO.\n\nCORO ESTOU LIBERTO NA PRESENÇA DO SENHOR, NOS SEUS ÁTRIOS HEI DE ENTRAR, COM LOUVOR GLORIFICAR; SUA GLÓRIA RESPLANDECE NO ALTAR, SEU ESPÍRITO ME ALCANÇA COM AMOR.\n\nO PODER DO SANGUE DE JESUS FAZ-ME CAMINHAR NA LUZ, BEM SEGURO ME CONDUZ.\n\nCORO ESTOU LIBERTO NA PRESENÇA DO SENHOR, NOS SEUS ÁTRIOS HEI DE ENTRAR, COM LOUVOR GLORIFICAR; SUA GLÓRIA RESPLANDECE NO ALTAR, SEU ESPÍRITO ME ALCANÇA COM AMOR. FINAL: HÁ PODER NO SANGUE DE JESUS (BIS)', 'O PODER DO SANGUE DE JESUS HÁ DE ME PURIFICAR, MEU PECADO PERDOAR; O PODER DO SANGUE DE JESUS DÁ-ME PLENA COMUNHÃO, TRAZ-ME A REVELAÇÃO. CORO ESTOU LIBERTO NA PRESENÇA DO SENHOR, NOS SEUS ÁTRIOS HEI DE ENTRAR, COM LOUVOR GLORIFICAR; SUA GLÓRIA RESPLANDECE NO ALTAR, SEU ESPÍRITO ME ALCANÇA COM AMOR. O PODER DO SANGUE DE JESUS FAZ-ME CAMINHAR NA LUZ, BEM SEGURO ME CONDUZ. CORO ESTOU LIBERTO NA PRESENÇA DO SENHOR, NOS SEUS ÁTRIOS HEI DE ENTRAR, COM LOUVOR GLORIFICAR; SUA GLÓRIA RESPLANDECE NO ALTAR, SEU ESPÍRITO ME ALCANÇA COM AMOR.  HÁ PODER NO SANGUE DE JESUS',  1);
