INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('30', '30 - OH! QUE PRECIOSO SANGUE', 'OH! QUE PRECIOSO SANGUE O SENHOR VERTEU, QUANDO PARA RESGATAR-NOS PADECEU! OH! QUE PRECIOSO SANGUE, SANGUE DE JESUS, QUE POR NÓS FOI DERRAMADO SOBRE A CRUZ!\n\nOH! QUE PRECIOSO SANGUE, SACRIFICIAL, POIS APAGA DE NOSSA ALMA TODO O MAL! OH! QUE PRECIOSO SANGUE, FALA-NOS DE PAZ, TUDO QUANTO A LEI EXIGE SATISFAZ!', 'OH! QUE PRECIOSO SANGUE O SENHOR VERTEU, QUANDO PARA RESGATAR-NOS PADECEU! OH! QUE PRECIOSO SANGUE, SANGUE DE JESUS, QUE POR NÓS FOI DERRAMADO SOBRE A CRUZ! OH! QUE PRECIOSO SANGUE, SACRIFICIAL, POIS APAGA DE NOSSA ALMA TODO O MAL! OH! QUE PRECIOSO SANGUE, FALA-NOS DE PAZ, TUDO QUANTO A LEI EXIGE SATISFAZ!',  1);
