INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('33', '33 - EU ME PROSTRO EM TEU ALTAR', 'EU ME PROSTRO EM TEU ALTAR NESTA HORA DE CLAMOR, LEMBRO-ME DE TUA DOR QUE SOFRESTE POR ME AMAR, O TEU SANGUE TEM PODER PARA ME PURIFICAR.\n\nNESSE SANGUE HÁ COMUNHÃO, CURA E LIBERTAÇÃO; VISITA-ME COM TUA PAZ, TEU ESPÍRITO RELUZ, VIVIFICA-ME, JESUS, E MINH’ALMA SATISFAZ. FINAL: E MINH’ALMA SATISFAZ.\nBIS', 'EU ME PROSTRO EM TEU ALTAR NESTA HORA DE CLAMOR, LEMBRO-ME DE TUA DOR QUE SOFRESTE POR ME AMAR, O TEU SANGUE TEM PODER PARA ME PURIFICAR. NESSE SANGUE HÁ COMUNHÃO, CURA E LIBERTAÇÃO; VISITA-ME COM TUA PAZ, TEU ESPÍRITO RELUZ, VIVIFICA-ME, JESUS, E MINH’ALMA SATISFAZ.  E MINH’ALMA SATISFAZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('34', '34 - QUANDO BUSCAMOS', 'QUANDO BUSCAMOS A TI, SENHOR, E COM FÉ PEDIMOS TEU PERDÃO, CONFESSAMOS NOSSOS PECADOS, VEM COM TEU SANGUE NOS PURIFICAR.\n\nCORO RENOVA A NOSSA COMUNHÃO (NOSSA COMUNHÃO) SERVAS RENOVA A FÉ EM NOSSOS CORAÇÕES PARA QUE SEJAMOS MAIS FIÉIS A TI E ENTREGUEMOS NOSSAS VIDAS (NOSSAS VIDAS) VARÕES CADA VEZ MAIS NO TEU ALTAR.\n\nOS CÉUS SE ABREM E COM PODER OS ANJOS TRAZEM RESPOSTAS DO SENHOR; GLORIFICAMOS AO NOSSO DEUS QUE ESTÁ VIVO E REINA ENTRE NÓS.\n\nCORO RENOVA A NOSSA COMUNHÃO (NOSSA COMUNHÃO) SERVAS RENOVA A FÉ EM NOSSOS CORAÇÕES PARA QUE SEJAMOS MAIS FIÉIS A TI E ENTREGUEMOS NOSSAS VIDAS (NOSSAS VIDAS) VARÕES CADA VEZ MAIS NO TEU ALTAR. FINAL: CADA VEZ MAIS NO TEU ALTAR.', 'QUANDO BUSCAMOS A TI, SENHOR, E COM FÉ PEDIMOS TEU PERDÃO, CONFESSAMOS NOSSOS PECADOS, VEM COM TEU SANGUE NOS PURIFICAR. CORO RENOVA A NOSSA COMUNHÃO (NOSSA COMUNHÃO) SERVAS RENOVA A FÉ EM NOSSOS CORAÇÕES PARA QUE SEJAMOS MAIS FIÉIS A TI E ENTREGUEMOS NOSSAS VIDAS (NOSSAS VIDAS) VARÕES CADA VEZ MAIS NO TEU ALTAR. OS CÉUS SE ABREM E COM PODER OS ANJOS TRAZEM RESPOSTAS DO SENHOR; GLORIFICAMOS AO NOSSO DEUS QUE ESTÁ VIVO E REINA ENTRE NÓS. CORO RENOVA A NOSSA COMUNHÃO (NOSSA COMUNHÃO) SERVAS RENOVA A FÉ EM NOSSOS CORAÇÕES PARA QUE SEJAMOS MAIS FIÉIS A TI E ENTREGUEMOS NOSSAS VIDAS (NOSSAS VIDAS) VARÕES CADA VEZ MAIS NO TEU ALTAR.  CADA VEZ MAIS NO TEU ALTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('35', '35 - A MEU SENHOR', 'A MEU SENHOR E CRISTO JESUS VENHO AGORA CLAMAR; VEM COM TEU SANGUE LAVAR MINHAS VESTES, VEM ME PURIFICAR.\n\nVEM LIBERTAR A MINH’ALMA, JESUS, DE MIM VEM CUIDAR; VEM COM TEU SANGUE LAVAR MINHAS VESTES, VEM ME PURIFICAR. FINAL: VEM ME PURIFICAR.\nBIS', 'A MEU SENHOR E CRISTO JESUS VENHO AGORA CLAMAR; VEM COM TEU SANGUE LAVAR MINHAS VESTES, VEM ME PURIFICAR. VEM LIBERTAR A MINH’ALMA, JESUS, DE MIM VEM CUIDAR; VEM COM TEU SANGUE LAVAR MINHAS VESTES, VEM ME PURIFICAR.  VEM ME PURIFICAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('36', '36 - CRUCIFICADO FOI MEU JESUS', 'CRUCIFICADO FOI MEU JESUS, DOR E OPRÓBRIO SOFREU NA CRUZ, CAMINHO NOVO NOS CONSAGROU AO SANTUÁRIO DO REI SENHOR.\n\nCORO NA SUA PRESENÇA EU ENTRO ASSIM, CRENDO QUE O SANGUE VERTEU POR MIM; VEM SER PROPÍCIO AO PECADOR, JÁ ME ALCANÇASTE POR TEU AMOR.\n\nMEU TRISTE ESTADO ELE MUDOU, EM NOVA VIDA ME TRANSFORMOU, CONFESSO AGORA O SEU PERDÃO, CONFIANDO A ELE MEU CORAÇÃO.\n\nCORO NA SUA PRESENÇA EU ENTRO ASSIM, CRENDO QUE O SANGUE VERTEU POR MIM; VEM SER PROPÍCIO AO PECADOR, JÁ ME ALCANÇASTE POR TEU AMOR.', 'CRUCIFICADO FOI MEU JESUS, DOR E OPRÓBRIO SOFREU NA CRUZ, CAMINHO NOVO NOS CONSAGROU AO SANTUÁRIO DO REI SENHOR. CORO NA SUA PRESENÇA EU ENTRO ASSIM, CRENDO QUE O SANGUE VERTEU POR MIM; VEM SER PROPÍCIO AO PECADOR, JÁ ME ALCANÇASTE POR TEU AMOR. MEU TRISTE ESTADO ELE MUDOU, EM NOVA VIDA ME TRANSFORMOU, CONFESSO AGORA O SEU PERDÃO, CONFIANDO A ELE MEU CORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('37', '37 - NESTA HORA ESQUEÇO O MUNDO', 'NESTA HORA ESQUEÇO O MUNDO, TODA A PREOCUPAÇÃO, ME COLOCO EM TUA PRESENÇA, SOU CARENTE DO TEU PERDÃO.\n\nCONSIDERO O MEU PECADO, RECONHEÇO O TEU AMOR, TEU ESPÍRITO ME TEM LEVADO A ADORAR-TE MEU SALVADOR.\n\nCORO OH! QUE SANGUE REMIDOR QUE RETIRA A MINHA DOR E ME LIVRA DO PECADO, Ó JESUS, TU ÉS AMADO. INSTRUMENTOS\n\nPOR TEU SANGUE ESTOU LIBERTO, POSSO TE GLORIFICAR, DE MEU PAI, SIM, AGORA ESTOU PERTO, ALELUIA, VOU TE LOUVAR.\n\nCORO OH! QUE SANGUE REMIDOR QUE RETIRA A MINHA DOR E ME LIVRA DO PECADO, Ó JESUS, TU ÉS AMADO.\nBIS', 'NESTA HORA ESQUEÇO O MUNDO, TODA A PREOCUPAÇÃO, ME COLOCO EM TUA PRESENÇA, SOU CARENTE DO TEU PERDÃO. CONSIDERO O MEU PECADO, RECONHEÇO O TEU AMOR, TEU ESPÍRITO ME TEM LEVADO A ADORAR-TE MEU SALVADOR. CORO OH! QUE SANGUE REMIDOR QUE RETIRA A MINHA DOR E ME LIVRA DO PECADO, Ó JESUS, TU ÉS AMADO. INSTRUMENTOS POR TEU SANGUE ESTOU LIBERTO, POSSO TE GLORIFICAR, DE MEU PAI, SIM, AGORA ESTOU PERTO, ALELUIA, VOU TE LOUVAR. CORO OH! QUE SANGUE REMIDOR QUE RETIRA A MINHA DOR E ME LIVRA DO PECADO, Ó JESUS, TU ÉS AMADO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('38', '38 - PELO SANGUE DE JESUS IREI CLAMAR', 'PELO SANGUE DE JESUS IREI CLAMAR, MEUS PECADOS NO ALTAR IREI DEIXAR; SEU PERDÃO QUE ME ALCANÇA EU JÁ POSSO CONTEMPLAR. ALELUIA, EU TE AMO, MEU JESUS.\n\nTODA MINHA GRATIDÃO TE QUERO DAR, PELA PAZ E PELA FÉ EM TI, SENHOR; TEU ESPÍRITO TRANSBORDA E MEU SER QUER TE ADORAR COM OS DONS MARAVILHOSOS DE JESUS.', 'PELO SANGUE DE JESUS IREI CLAMAR, MEUS PECADOS NO ALTAR IREI DEIXAR; SEU PERDÃO QUE ME ALCANÇA EU JÁ POSSO CONTEMPLAR. ALELUIA, EU TE AMO, MEU JESUS. TODA MINHA GRATIDÃO TE QUERO DAR, PELA PAZ E PELA FÉ EM TI, SENHOR; TEU ESPÍRITO TRANSBORDA E MEU SER QUER TE ADORAR COM OS DONS MARAVILHOSOS DE JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('39', '39 - JESUS, TU ÉS O MEU DEUS', 'JESUS, TU ÉS O MEU DEUS, A MINH’ALMA TEM SEDE DE TI, EU TE DESEJO, Ó DEUS, NECESSITADO ESTOU.\n\nNO TEU SANGUE HÁ RENOVO, PODER PARA TRANSFORMAR, PARA LIBERTAR E PERDOAR, DERRAMA DESTE SANGUE EM MIM.\n\nE ASSIM TE BENDIREI ENQUANTO EU VIVER, EM TEU NOME FALAREI, OS MEUS LÁBIOS TE LOUVARÃO. ALELUIA, AMÉM (2X)\nBIS', 'JESUS, TU ÉS O MEU DEUS, A MINH’ALMA TEM SEDE DE TI, EU TE DESEJO, Ó DEUS, NECESSITADO ESTOU. NO TEU SANGUE HÁ RENOVO, PODER PARA TRANSFORMAR, PARA LIBERTAR E PERDOAR, DERRAMA DESTE SANGUE EM MIM. E ASSIM TE BENDIREI ENQUANTO EU VIVER, EM TEU NOME FALAREI, OS MEUS LÁBIOS TE LOUVARÃO. ALELUIA, AMÉM',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('40', '40 - OH! QUE SANGUE PODEROSO', 'OH! QUE SANGUE PODEROSO! OH! QUE SANGUE CHEIO DE VIRTUDE, SANGUE QUE POR NÓS FOI DERRAMADO; QUE NOS DÁ PLENO PERDÃO, COMUNHÃO, TOTAL LIBERTAÇÃO, ROMPENDO AS BARREIRAS DO PECADO.\n\nCLAMAMOS A TI, SENHOR, PELO PODER QUE HÁ NO SANGUE DE JESUS PARA PEDIR O TEU PERDÃO E TOTAL LIBERTAÇÃO. FINAL: E TOTAL LIBERTAÇÃO.\nBIS', 'OH! QUE SANGUE PODEROSO! OH! QUE SANGUE CHEIO DE VIRTUDE, SANGUE QUE POR NÓS FOI DERRAMADO; QUE NOS DÁ PLENO PERDÃO, COMUNHÃO, TOTAL LIBERTAÇÃO, ROMPENDO AS BARREIRAS DO PECADO. CLAMAMOS A TI, SENHOR, PELO PODER QUE HÁ NO SANGUE DE JESUS PARA PEDIR O TEU PERDÃO E TOTAL LIBERTAÇÃO.  E TOTAL LIBERTAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('41', '41 - ALÉM DO VÉU EU QUERO ESTAR', 'ALÉM DO VÉU EU QUERO ESTAR, ALÉM DO VÉU COM JESUS, PELO CAMINHO QUE ELE ABRIU NAQUELA CRUZ; COM OUSADIA VOU ENTRAR E POR SEU SANGUE VOU CLAMAR, ALÉM DO VÉU ONDE ELE QUER ME ABENÇOAR.\n\nALÉM DO VÉU A SUA GLÓRIA POSSO AGORA CONTEMPLAR; DO AMOR DE DEUS QUEM PODERÁ ME SEPARAR? COM QUERUBINS E SERAFINS MIL ALELUIAS VOU CANTAR, ALÉM DO VÉU, ONDE PRA SEMPRE QUERO ESTAR.\nBIS', 'ALÉM DO VÉU EU QUERO ESTAR, ALÉM DO VÉU COM JESUS, PELO CAMINHO QUE ELE ABRIU NAQUELA CRUZ; COM OUSADIA VOU ENTRAR E POR SEU SANGUE VOU CLAMAR, ALÉM DO VÉU ONDE ELE QUER ME ABENÇOAR. ALÉM DO VÉU A SUA GLÓRIA POSSO AGORA CONTEMPLAR; DO AMOR DE DEUS QUEM PODERÁ ME SEPARAR? COM QUERUBINS E SERAFINS MIL ALELUIAS VOU CANTAR, ALÉM DO VÉU, ONDE PRA SEMPRE QUERO ESTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('42', '42 - SENHOR, SUPLICO A TI EM ORAÇÃO', 'SENHOR, SUPLICO A TI EM ORAÇÃO: FAZE DE MIM UM VASO NOVO, DERRAMA O SANGUE DE JESUS EM MIM.\n\nCORO PERMITE-ME QUE ENTRE NO SANTO DOS SANTOS, PERDOA-ME DO VIL PECADO, ESTENDE A TUA MÃO.\n\nPRECISO CONTEMPLAR A TUA GLÓRIA, CONCEDE-ME TUA VITÓRIA, DERRAMA O SANGUE DE JESUS EM MIM.\n\nCORO PERMITE-ME QUE ENTRE NO SANTO DOS SANTOS, PERDOA-ME DO VIL PECADO, ESTENDE A TUA MÃO. FINAL: SENHOR (5X)', 'SENHOR, SUPLICO A TI EM ORAÇÃO: FAZE DE MIM UM VASO NOVO, DERRAMA O SANGUE DE JESUS EM MIM. CORO PERMITE-ME QUE ENTRE NO SANTO DOS SANTOS, PERDOA-ME DO VIL PECADO, ESTENDE A TUA MÃO. PRECISO CONTEMPLAR A TUA GLÓRIA, CONCEDE-ME TUA VITÓRIA, DERRAMA O SANGUE DE JESUS EM MIM. CORO PERMITE-ME QUE ENTRE NO SANTO DOS SANTOS, PERDOA-ME DO VIL PECADO, ESTENDE A TUA MÃO.  SENHOR (5X)',  1);
