INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('44', '44 - CONFORME A SUA INFINITA GRAÇA', 'CONFORME A TUA INFINITA GRAÇA, Ó PAI PERDOA A MINHA TRANSGRESSÃO; FAZE-ME SENTIR MAIS UMA VEZ A GRAÇA E DÁ-ME TUA PAZ AO CORAÇÃO.\n\nCORO MAIS UMA VEZ EU SINTO O MEU PECADO QUE É CONTRA TI, Ó PAI, Ó PAI DE AMOR; MINH’ALMA PEDE A TUA ALEGRIA, RENOVA EM MIM O TEU AMOR, Ó MEU SENHOR.\n\nMEU CORAÇÃO A TI EU OFEREÇO, PERDÃO IMPLORO, MEU SENHOR E REI; CONFORME A TUA INFINITA GRAÇA, MISERICÓRDIA ASSIM ALCANÇAREI.\n\nCORO MAIS UMA VEZ EU SINTO O MEU PECADO QUE É CONTRA TI, Ó PAI, Ó PAI DE AMOR; MINH’ALMA PEDE A TUA ALEGRIA, RENOVA EM MIM O TEU AMOR, Ó MEU SENHOR.\n\nDEUS DE BONDADE A TI EU AGRADEÇO TEU PATERNAL CUIDADO SOBRE MIM; TODO MEU SER A TI EU OFEREÇO, LOUVAR-TE QUERO SEMPRE ATÉ O FIM.\n\nCORO MAIS UMA VEZ EU SINTO O MEU PECADO QUE É CONTRA TI, Ó PAI, Ó PAI DE AMOR; MINH’ALMA PEDE A TUA ALEGRIA, RENOVA EM MIM O TEU AMOR, Ó MEU SENHOR.', 'CONFORME A TUA INFINITA GRAÇA, Ó PAI PERDOA A MINHA TRANSGRESSÃO; FAZE-ME SENTIR MAIS UMA VEZ A GRAÇA E DÁ-ME TUA PAZ AO CORAÇÃO. CORO MAIS UMA VEZ EU SINTO O MEU PECADO QUE É CONTRA TI, Ó PAI, Ó PAI DE AMOR; MINH’ALMA PEDE A TUA ALEGRIA, RENOVA EM MIM O TEU AMOR, Ó MEU SENHOR. MEU CORAÇÃO A TI EU OFEREÇO, PERDÃO IMPLORO, MEU SENHOR E REI; CONFORME A TUA INFINITA GRAÇA, MISERICÓRDIA ASSIM ALCANÇAREI. DEUS DE BONDADE A TI EU AGRADEÇO TEU PATERNAL CUIDADO SOBRE MIM; TODO MEU SER A TI EU OFEREÇO, LOUVAR-TE QUERO SEMPRE ATÉ O FIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('45', '45 - NOS ÁTRIOS DO SENHOR ENTRAMOS', 'NOS ÁTRIOS DO SENHOR ENTRAMOS, PURIFICADOS PELO SANGUE DE JESUS. VIMOS LOUVAR-TE E ADORAR-TE, ÉS NOSSO DEUS! VIMOS TRAZER-TE O NOSSO INCENSO, OS NOSSOS HOLOCAUSTOS DE LOUVOR.\n\nCORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS! QUANTA BELEZA EM TI ACHAMOS, AO CONTEMPLARMOS TUA SANTIDADE. Ó DEUS PERFEITO, Ó DEUS TRIUNO, ÉS NOSSO DEUS! E COMO RESPLANDECE A TUA LUZ, INCOMPARÁVEL É TUA GLÓRIA.\n\nCORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS! PERANTE A IMENSIDÃO DO TEU AMOR, QUEBRANTA-SE DE GRATIDÃO O NOSSO SER. Ó, QUANTO AMASTE, QUANTO SOFRESTE, ÉS NOSSO DEUS! TUDO VENCESTE PELO TEU PODER, PRA SEMPRE FIRME ESTÁ TEU TRONO!\n\nCORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS!', 'NOS ÁTRIOS DO SENHOR ENTRAMOS, PURIFICADOS PELO SANGUE DE JESUS. VIMOS LOUVAR-TE E ADORAR-TE, ÉS NOSSO DEUS! VIMOS TRAZER-TE O NOSSO INCENSO, OS NOSSOS HOLOCAUSTOS DE LOUVOR. CORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS! QUANTA BELEZA EM TI ACHAMOS, AO CONTEMPLARMOS TUA SANTIDADE. Ó DEUS PERFEITO, Ó DEUS TRIUNO, ÉS NOSSO DEUS! E COMO RESPLANDECE A TUA LUZ, INCOMPARÁVEL É TUA GLÓRIA. CORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS! PERANTE A IMENSIDÃO DO TEU AMOR, QUEBRANTA-SE DE GRATIDÃO O NOSSO SER. Ó, QUANTO AMASTE, QUANTO SOFRESTE, ÉS NOSSO DEUS! TUDO VENCESTE PELO TEU PODER, PRA SEMPRE FIRME ESTÁ TEU TRONO! CORO SÓ TU ÉS DIGNO DA NOSSA ADORAÇÃO, ÉS NOSSO DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('46', '46 - AO SENTIR O TEU PERDÃO', 'AO SENTIR O TEU PERDÃO POR TEU SANGUE REMIDOR, RECEBI LIBERTAÇÃO, ALEGRIA DO SENHOR. ALELUIA, GLÓRIA A JESUS. INSTRUMENTOS\n\nNESTA HORA DE LOUVOR, QUE TRAZ GOZO AO CORAÇÃO, QUERO DAR-TE, MEU SENHOR, TODA A MINHA GRATIDÃO. ALELUIA, GLÓRIA A JESUS. INSTRUMENTOS\n\nQUERO ENTÃO TE ADORAR, POIS EM COMUNHÃO ESTOU, EM MINH’ALMA TE EXALTAR, ALELUIA, EU TE DOU. ALELUIA, GLÓRIA A JESUS. (BIS)', 'AO SENTIR O TEU PERDÃO POR TEU SANGUE REMIDOR, RECEBI LIBERTAÇÃO, ALEGRIA DO SENHOR. ALELUIA, GLÓRIA A JESUS. INSTRUMENTOS NESTA HORA DE LOUVOR, QUE TRAZ GOZO AO CORAÇÃO, QUERO DAR-TE, MEU SENHOR, TODA A MINHA GRATIDÃO. ALELUIA, GLÓRIA A JESUS. INSTRUMENTOS QUERO ENTÃO TE ADORAR, POIS EM COMUNHÃO ESTOU, EM MINH’ALMA TE EXALTAR, ALELUIA, EU TE DOU. ALELUIA, GLÓRIA A JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('47', '47 - É TEU SANGUE', 'É TEU SANGUE QUE ME LIMPA, É TEU SANGUE QUE VIDA ME DÁ, ESTE SANGUE DO SACRIFÍCIO, DERRAMADO EM MEU LUGAR, E QUE ME TORNA ALVO MAIS QUE A NEVE, MEU JESUS, OFERTA PERFEITA NO ALTAR. INSTRUMENTOS\n\nPOR TEU SANGUE CLAMO AGORA, POR TEU SANGUE EU OUSO ENTRAR NO SANTUÁRIO, ONDE O DEUS VIVO QUER A MIM SE REVELAR. CORDEIRO ETERNO! ÉS O HOLOCAUSTO QUE ME SALVOU! OFERTA PERFEITA NO ALTAR.', 'É TEU SANGUE QUE ME LIMPA, É TEU SANGUE QUE VIDA ME DÁ, ESTE SANGUE DO SACRIFÍCIO, DERRAMADO EM MEU LUGAR, E QUE ME TORNA ALVO MAIS QUE A NEVE, MEU JESUS, OFERTA PERFEITA NO ALTAR. INSTRUMENTOS POR TEU SANGUE CLAMO AGORA, POR TEU SANGUE EU OUSO ENTRAR NO SANTUÁRIO, ONDE O DEUS VIVO QUER A MIM SE REVELAR. CORDEIRO ETERNO! ÉS O HOLOCAUSTO QUE ME SALVOU! OFERTA PERFEITA NO ALTAR.',  1);
