INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('51', '51 - O CORDEIRO QUE NA CRUZ MORREU', 'O CORDEIRO QUE NA CRUZ MORREU VIVO ESTÁ ENTRE NÓS, DERRAMOU SEU SANGUE INOCENTE PARA NOS PURIFICAR.\n\nCORO NÓS CLAMAMOS POR ESTE SANGUE, QUE É ESPÍRITO DE VIDA, É O NOSSO ESCUDO, NOSSA PROTEÇÃO. ESTE SANGUE NOS TRAZ O PERDÃO.\n\nDE ALTO A BAIXO O VÉU SE RASGOU QUANDO JESUS EXPIROU, PERMITINDO A NOSSA ENTRADA NO SANTÍSSIMO LUGAR.\n\nCORO NÓS CLAMAMOS POR ESTE SANGUE, QUE É ESPÍRITO DE VIDA, É O NOSSO ESCUDO, NOSSA PROTEÇÃO. ESTE SANGUE NOS TRAZ O PERDÃO.\n\nFINAL: É O NOSSO ESCUDO, NOSSA PROTEÇÃO. ESTE SANGUE NOS TRAZ COMUNHÃO.', 'O CORDEIRO QUE NA CRUZ MORREU VIVO ESTÁ ENTRE NÓS, DERRAMOU SEU SANGUE INOCENTE PARA NOS PURIFICAR. CORO NÓS CLAMAMOS POR ESTE SANGUE, QUE É ESPÍRITO DE VIDA, É O NOSSO ESCUDO, NOSSA PROTEÇÃO. ESTE SANGUE NOS TRAZ O PERDÃO. DE ALTO A BAIXO O VÉU SE RASGOU QUANDO JESUS EXPIROU, PERMITINDO A NOSSA ENTRADA NO SANTÍSSIMO LUGAR. É O NOSSO ESCUDO, NOSSA PROTEÇÃO. ESTE SANGUE NOS TRAZ COMUNHÃO.',  1);
