INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('57', '57 - EM FERVENTE ORAÇÃO', 'EM FERVENTE ORAÇÃO, VENS O TEU CORAÇÃO NA PRESENÇA DE DEUS DERRAMAR; MAS NÃO PODES FRUIR O QUE ESTÁS A PEDIR, SEM QUE TUDO ABANDONES NO ALTAR.\n\nCORO QUANDO TUDO PERANTE O SENHOR ESTIVER, E TODO O TEU SER ELE CONTROLAR, SÓ ENTÃO HÁS DE VER QUE O SENHOR TEM PODER, QUANDO TUDO DEIXARES NO ALTAR.\n\nMARAVILHAS DE AMOR TE FARÁ O SENHOR, ATENDENDO A ORAÇÃO QUE ACEITAR; SEU IMENSO PODER TE VIRÁ SOCORRER, QUANDO TUDO DEIXARES NO ALTAR.\n\nCORO QUANDO TUDO PERANTE O SENHOR ESTIVER, E TODO O TEU SER ELE CONTROLAR, SÓ ENTÃO HÁS DE VER QUE O SENHOR TEM PODER, QUANDO TUDO DEIXARES NO ALTAR.\n\nSE ORARES, ENTÃO, SEM QUE O TEU CORAÇÃO GOZE A PAZ QUE O SENHOR PODE DAR, É QUE DEUS NÃO SENTIU QUE TUA ALMA SE ABRIU, TUDO, TUDO DEIXANDO NO ALTAR.\n\nCORO QUANDO TUDO PERANTE O SENHOR ESTIVER, E TODO O TEU SER ELE CONTROLAR, SÓ ENTÃO HÁS DE VER QUE O SENHOR TEM PODER, QUANDO TUDO DEIXARES NO ALTAR.', 'EM FERVENTE ORAÇÃO, VENS O TEU CORAÇÃO NA PRESENÇA DE DEUS DERRAMAR; MAS NÃO PODES FRUIR O QUE ESTÁS A PEDIR, SEM QUE TUDO ABANDONES NO ALTAR. CORO QUANDO TUDO PERANTE O SENHOR ESTIVER, E TODO O TEU SER ELE CONTROLAR, SÓ ENTÃO HÁS DE VER QUE O SENHOR TEM PODER, QUANDO TUDO DEIXARES NO ALTAR. MARAVILHAS DE AMOR TE FARÁ O SENHOR, ATENDENDO A ORAÇÃO QUE ACEITAR; SEU IMENSO PODER TE VIRÁ SOCORRER, QUANDO TUDO DEIXARES NO ALTAR. SE ORARES, ENTÃO, SEM QUE O TEU CORAÇÃO GOZE A PAZ QUE O SENHOR PODE DAR, É QUE DEUS NÃO SENTIU QUE TUA ALMA SE ABRIU, TUDO, TUDO DEIXANDO NO ALTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('58', '58 - ATENDE A VOZ DO MEU CLAMOR', 'ATENDE A VOZ DO MEU CLAMOR, Ó REI, DEUS MEU, POIS EU A TI ORAREI COM MAIS FERVOR, DÁ OUVIDOS À VOZ DA MINHA ORAÇÃO, A MINHA MEDITAÇÃO ELEVO A TI.\n\nCORO PELA MANHÃ TU OUVIRÁS A MINHA VOZ, EM LONGAS NOITES EU A TI VIGIAREI; E O TEU REINO, Ó DEUS BENDITO, VENHA A NÓS, DERRAMA AS TUAS BÊNÇÃOS À TUA GREI.\n\nÓ DEUS BENDITO, NÃO TENS PRAZER NA INIQUIDADE; TUA PALAVRA É ESCUDO E BROQUEL; ELA EXTERMINA DESTE MUNDO TODA A MALDADE E CONSERVA EM RETIDÃO QUEM FOR FIEL.\n\nCORO PELA MANHÃ TU OUVIRÁS A MINHA VOZ, EM LONGAS NOITES EU A TI VIGIAREI; E O TEU REINO, Ó DEUS BENDITO, VENHA A NÓS, DERRAMA AS TUAS BÊNÇÃOS À TUA GREI.\n\nEU ENTRAREI EM TUA CASA PELA GRANDEZA, E EM TEU TEMPLO ME INCLINAREI PERANTE TI, GUIA MEUS PASSOS EM TUA JUSTIÇA E REALEZA E VENCE O INIMIGO PARA MIM.', 'ATENDE A VOZ DO MEU CLAMOR, Ó REI, DEUS MEU, POIS EU A TI ORAREI COM MAIS FERVOR, DÁ OUVIDOS À VOZ DA MINHA ORAÇÃO, A MINHA MEDITAÇÃO ELEVO A TI. CORO PELA MANHÃ TU OUVIRÁS A MINHA VOZ, EM LONGAS NOITES EU A TI VIGIAREI; E O TEU REINO, Ó DEUS BENDITO, VENHA A NÓS, DERRAMA AS TUAS BÊNÇÃOS À TUA GREI. Ó DEUS BENDITO, NÃO TENS PRAZER NA INIQUIDADE; TUA PALAVRA É ESCUDO E BROQUEL; ELA EXTERMINA DESTE MUNDO TODA A MALDADE E CONSERVA EM RETIDÃO QUEM FOR FIEL. EU ENTRAREI EM TUA CASA PELA GRANDEZA, E EM TEU TEMPLO ME INCLINAREI PERANTE TI, GUIA MEUS PASSOS EM TUA JUSTIÇA E REALEZA E VENCE O INIMIGO PARA MIM.',  1);
