INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('68', '68 - LÁ NO CIMO DO MONTE', 'LÁ NO CIMO DO MONTE EU ESTOU ME ENCURVANDO, BUSCANDO TUA FACE, MEU DIVINO SENHOR; MEU FARDO É PESADO, ESTOU TÃO CANSADO, MAS SEI QUE TU ÉS AMOR. OH! VEM RENOVAR, EM MEU CORAÇÃO A FÉ, O PODER, ME DÁ TUA MÃO; RENOVA, SENHOR.\n\nCOMO TERRA SEDENTA, A MINH’ALMA ANSEIA POR TEU SANTO ESPÍRITO, QUE DÁ VIDA E PODER, E QUANDO ME ERGUER, EU SEI QUE VOU TER MAIS FORÇA E ALEGRIA; DE GLÓRIA EM GLÓRIA ME VAIS TRANSFORMANDO, QUANDO EU, LÁ NO MONTE, ESTOU ME ENCURVANDO, BUSCANDO-TE, Ó DEUS!', 'LÁ NO CIMO DO MONTE EU ESTOU ME ENCURVANDO, BUSCANDO TUA FACE, MEU DIVINO SENHOR; MEU FARDO É PESADO, ESTOU TÃO CANSADO, MAS SEI QUE TU ÉS AMOR. OH! VEM RENOVAR, EM MEU CORAÇÃO A FÉ, O PODER, ME DÁ TUA MÃO; RENOVA, SENHOR. COMO TERRA SEDENTA, A MINH’ALMA ANSEIA POR TEU SANTO ESPÍRITO, QUE DÁ VIDA E PODER, E QUANDO ME ERGUER, EU SEI QUE VOU TER MAIS FORÇA E ALEGRIA; DE GLÓRIA EM GLÓRIA ME VAIS TRANSFORMANDO, QUANDO EU, LÁ NO MONTE, ESTOU ME ENCURVANDO, BUSCANDO-TE, Ó DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('69', '69 - O ANJO DO SENHOR ESTÁ PASSEANDO', 'O ANJO DO SENHOR ESTÁ PASSEANDO, ESTÁ PASSEANDO AQUI NESTE LUGAR. DÁ LUGAR, IRMÃO, DÁ LUGAR, IRMÃO, E DEIXA O ANJO DO SENHOR TE VISITAR.\nBIS\nBIS', 'O ANJO DO SENHOR ESTÁ PASSEANDO, ESTÁ PASSEANDO AQUI NESTE LUGAR. DÁ LUGAR, IRMÃO, DÁ LUGAR, IRMÃO, E DEIXA O ANJO DO SENHOR TE VISITAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('70', '70 - PRECIOSAS SÃO AS HORAS', 'PRECIOSAS SÃO AS HORAS NA PRESENÇA DE JESUS! COMUNHÃO DELICIOSA DA MINHA ALMA COM A LUZ! OS CUIDADOS DESTE MUNDO NUNCA PODEM ME ABALAR, POIS É ELE O MEU ABRIGO, QUANDO O TENTADOR CHEGAR (BIS)\n\nAO SENTIR-ME RODEADO DE CUIDADOS TERREAIS, IRRITADO, ABATIDO OU EM DÚVIDAS FATAIS, A JESUS EU ME DIRIJO NESTES TEMPOS DE AFLIÇÃO; AS PALAVRAS QUE ELE FALA TRAZEM-ME CONSOLAÇÃO (BIS)\n\nSE CONFESSO MEUS TEMORES, TODA A MINHA IMPERFEIÇÃO, ELE ESCUTA COM PACIÊNCIA ESSA TRISTE CONFISSÃO; COM TERNURA REPREENDE MEU PECADO E TODO MAL, ELE É SEMPRE O MEU AMIGO, O MELHOR E MAIS LEAL (BIS)\n\nSE QUEREIS SABER QUÃO DOCE É A SECRETA COMUNHÃO, PODEREIS MUI BEM PROVÁ-LA E TEREIS COMPENSAÇÃO; PROCURAI ESTAR SOZINHOS EM CONVERSA COM JESUS, PROVAREIS NA VOSSA VIDA O ESPÍRITO DA CRUZ (BIS)', 'PRECIOSAS SÃO AS HORAS NA PRESENÇA DE JESUS! COMUNHÃO DELICIOSA DA MINHA ALMA COM A LUZ! OS CUIDADOS DESTE MUNDO NUNCA PODEM ME ABALAR, POIS É ELE O MEU ABRIGO, QUANDO O TENTADOR CHEGAR AO SENTIR-ME RODEADO DE CUIDADOS TERREAIS, IRRITADO, ABATIDO OU EM DÚVIDAS FATAIS, A JESUS EU ME DIRIJO NESTES TEMPOS DE AFLIÇÃO; AS PALAVRAS QUE ELE FALA TRAZEM-ME CONSOLAÇÃO SE CONFESSO MEUS TEMORES, TODA A MINHA IMPERFEIÇÃO, ELE ESCUTA COM PACIÊNCIA ESSA TRISTE CONFISSÃO; COM TERNURA REPREENDE MEU PECADO E TODO MAL, ELE É SEMPRE O MEU AMIGO, O MELHOR E MAIS LEAL SE QUEREIS SABER QUÃO DOCE É A SECRETA COMUNHÃO, PODEREIS MUI BEM PROVÁ-LA E TEREIS COMPENSAÇÃO; PROCURAI ESTAR SOZINHOS EM CONVERSA COM JESUS, PROVAREIS NA VOSSA VIDA O ESPÍRITO DA CRUZ',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('71', '71 - VEM, AMADO MEU', 'VEM, AMADO MEU, VEM VISITAR A TUA IGREJA; VEM, DEPRESSA, VEM, MINH’ALMA ANSEIA POR TUA PRESENÇA. PERTENÇO A TI, ÉS MEU AMADO, JUNTO A TI QUERO ESTAR PARA SEMPRE, VEM ME BUSCAR. REPETIR O LOUVOR', 'VEM, AMADO MEU, VEM VISITAR A TUA IGREJA; VEM, DEPRESSA, VEM, MINH’ALMA ANSEIA POR TUA PRESENÇA. PERTENÇO A TI, ÉS MEU AMADO, JUNTO A TI QUERO ESTAR PARA SEMPRE, VEM ME BUSCAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('72', '72 - É PRECISO CONFIAR E BUSCAR A DEUS', 'É PRECISO CONFIAR E BUSCAR A DEUS, E O SENHOR DOS EXÉRCITOS TE OUVIRÁ. O SENHOR TE GUIARÁ, É PRECISO CONFIAR, E O SENHOR DOS EXÉRCITOS TE GUIARÁ.\n\nCORO BUSCA A DEUS (BUSCA A DEUS) QUE ELE TE OUVIRÁ. BUSCAI AO SENHOR ENQUANTO SE PODE ACHAR, INVOCAI-O ENQUANTO ESTÁ PERTO. FINAL: É PRECISO CONFIAR, E O SENHOR DOS EXÉRCITOS TE GUIARÁ.\nBIS\nBIS', 'É PRECISO CONFIAR E BUSCAR A DEUS, E O SENHOR DOS EXÉRCITOS TE OUVIRÁ. O SENHOR TE GUIARÁ, É PRECISO CONFIAR, E O SENHOR DOS EXÉRCITOS TE GUIARÁ. CORO BUSCA A DEUS (BUSCA A DEUS) QUE ELE TE OUVIRÁ. BUSCAI AO SENHOR ENQUANTO SE PODE ACHAR, INVOCAI-O ENQUANTO ESTÁ PERTO.  É PRECISO CONFIAR, E O SENHOR DOS EXÉRCITOS TE GUIARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('73', '73 - DEUS ESTÁ NO TEMPLO', 'DEUS ESTÁ NO TEMPLO, PAI ONIPOTENTE, DIANTE DELE NOS PROSTREMOS! REVERENTEMENTE, SERVOS CONSAGRADOS, AO ALTÍSSIMO ADOREMOS! COM FAVOR, COM AMOR, ESPIRITUALMENTE, DEUS ESTÁ NO TEMPLO!\n\nCRISTO ESTÁ NO TEMPLO, SUMO BENEFÍCIO RECEBEMOS DE SEU SANGUE. O CORDEIRO ETERNO FOI O SACRIFÍCIO, QUE NA CRUZ MORREU EXANGUE; ESCOLHEU E SOFREU O MORTAL SUPLÍCIO; CRISTO ESTÁ NO TEMPLO!\n\nVEM, OCUPA O TEMPLO, INSTRUTOR DIVINO, NOSSOS CORAÇÕES HABITA. MESTRE INCOMPARÁVEL, DÁ-NOS TEU ENSINO, ACLARANDO A LEI BENDITA; COM PRAZER E PODER, OH! FAVOR SUPINO! ELE ESTÁ NO TEMPLO!', 'DEUS ESTÁ NO TEMPLO, PAI ONIPOTENTE, DIANTE DELE NOS PROSTREMOS! REVERENTEMENTE, SERVOS CONSAGRADOS, AO ALTÍSSIMO ADOREMOS! COM FAVOR, COM AMOR, ESPIRITUALMENTE, DEUS ESTÁ NO TEMPLO! CRISTO ESTÁ NO TEMPLO, SUMO BENEFÍCIO RECEBEMOS DE SEU SANGUE. O CORDEIRO ETERNO FOI O SACRIFÍCIO, QUE NA CRUZ MORREU EXANGUE; ESCOLHEU E SOFREU O MORTAL SUPLÍCIO; CRISTO ESTÁ NO TEMPLO! VEM, OCUPA O TEMPLO, INSTRUTOR DIVINO, NOSSOS CORAÇÕES HABITA. MESTRE INCOMPARÁVEL, DÁ-NOS TEU ENSINO, ACLARANDO A LEI BENDITA; COM PRAZER E PODER, OH! FAVOR SUPINO! ELE ESTÁ NO TEMPLO!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('74', '74 - SEI, SENHOR', 'SEI, SENHOR, QUE CARINHOSAMENTE ME TROUXESTE AQUI E O MEU CORAÇÃO QUERO TE OFERTAR.\n\nSENTIR TUA PRESENÇA, GOZAR DE COMUNHÃO; VEM, SENHOR, ME ABRAÇA, ME CONFORTA, ÉS AMOR; SOU TEU, SOMENTE TEU, Ó VEM ME ABENÇOAR.\nBIS', 'SEI, SENHOR, QUE CARINHOSAMENTE ME TROUXESTE AQUI E O MEU CORAÇÃO QUERO TE OFERTAR. SENTIR TUA PRESENÇA, GOZAR DE COMUNHÃO; VEM, SENHOR, ME ABRAÇA, ME CONFORTA, ÉS AMOR; SOU TEU, SOMENTE TEU, Ó VEM ME ABENÇOAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('75', '75 - SENTE EM TUA FACE O ROÇAR', 'SENTE EM TUA FACE O ROÇAR DA ORLA DO VESTIDO DE JESUS, SENTE QUE O SENHOR ESTÁ AQUI E QUER OUVIR O TEU LOUVOR. CANTEMOS COM ALEGRIA E OREMOS COM DEVOÇÃO; VEM, SENHOR, MORAR EM MEU CORAÇÃO.\nBIS', 'SENTE EM TUA FACE O ROÇAR DA ORLA DO VESTIDO DE JESUS, SENTE QUE O SENHOR ESTÁ AQUI E QUER OUVIR O TEU LOUVOR. CANTEMOS COM ALEGRIA E OREMOS COM DEVOÇÃO; VEM, SENHOR, MORAR EM MEU CORAÇÃO.',  1);
