INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('78', '78 - UMA VOZ MANSA E DELICADA', 'UMA VOZ MANSA E DELICADA FALA AO MEU CORAÇÃO, VOZ TÃO SUAVE, MARAVILHOSA, TRAZ PAZ E CONSOLAÇÃO. OH! QUE VOZ MANSA E DELICADA, QUE FALA AO MEU CORAÇÃO.\n\nESTA VOZ DOCE QUE EU ESCUTO, É A VOZ DO MEU SENHOR, ELE ESTÁ VIVO, FALA COMIGO, ELE É O MEU CONSOLADOR. OH! QUE VOZ MANSA E DELICADA, QUE FALA AO MEU CORAÇÃO.\nBIS', 'UMA VOZ MANSA E DELICADA FALA AO MEU CORAÇÃO, VOZ TÃO SUAVE, MARAVILHOSA, TRAZ PAZ E CONSOLAÇÃO. OH! QUE VOZ MANSA E DELICADA, QUE FALA AO MEU CORAÇÃO. ESTA VOZ DOCE QUE EU ESCUTO, É A VOZ DO MEU SENHOR, ELE ESTÁ VIVO, FALA COMIGO, ELE É O MEU CONSOLADOR. OH! QUE VOZ MANSA E DELICADA, QUE FALA AO MEU CORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('79', '79 - DEUS ESTÁ PRESENTE ENTRE PECADORES COMO NÓS', 'DEUS ESTÁ PRESENTE (BIS) ENTRE PECADORES COMO NÓS. DEUS ESTÁ PRESENTE, (BIS) DEUS ESTÁ PRESENTE, PRONTO A PERDOAR.\nBIS\n\nDEUS IRÁ OUVIR NOSSA ORAÇÃO, NOSSO FARDO VAI SOBRE SI LEVAR; POIS DEUS ESTÁ PRESENTE, PRONTO PRA SALVAR, PRONTO PRA AJUDAR, DEUS ESTÁ PRESENTE.', 'DEUS ESTÁ PRESENTE  ENTRE PECADORES COMO NÓS. DEUS ESTÁ PRESENTE,  DEUS ESTÁ PRESENTE, PRONTO A PERDOAR. DEUS IRÁ OUVIR NOSSA ORAÇÃO, NOSSO FARDO VAI SOBRE SI LEVAR; POIS DEUS ESTÁ PRESENTE, PRONTO PRA SALVAR, PRONTO PRA AJUDAR, DEUS ESTÁ PRESENTE.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('80', '80 - VIVES MUITO ALÉM DO CÉU', 'VIVES MUITO ALÉM DO CÉU, MUITO ALÉM DO QUE MEUS OLHOS PODEM ALCANÇAR, MAS POSSO PELA FÉ TOCAR TUAS VESTES E RECEBER DE TI GRAÇA E PAZ; POSSO SEMPRE QUE ORO A TI, JESUS POSSO SEMPRE QUE ORO A TI, JESUS\n\nTU DEIXASTE TEU ESPÍRITO CONSOLADOR, TÃO AMIGO, TÃO FIEL, AJUDADOR, TÉ QUE VENHAS COM PODER PRA NOS BUSCAR PRA ENTÃO ETERNAMENTE CONTIGO ESTAR; JUNTO, SEMPRE, ESTAREI DE TI, JESUS, ESTAREI DE TI, JESUS.\nBIS', 'VIVES MUITO ALÉM DO CÉU, MUITO ALÉM DO QUE MEUS OLHOS PODEM ALCANÇAR, MAS POSSO PELA FÉ TOCAR TUAS VESTES E RECEBER DE TI GRAÇA E PAZ; POSSO SEMPRE QUE ORO A TI, JESUS POSSO SEMPRE QUE ORO A TI, JESUS TU DEIXASTE TEU ESPÍRITO CONSOLADOR, TÃO AMIGO, TÃO FIEL, AJUDADOR, TÉ QUE VENHAS COM PODER PRA NOS BUSCAR PRA ENTÃO ETERNAMENTE CONTIGO ESTAR; JUNTO, SEMPRE, ESTAREI DE TI, JESUS, ESTAREI DE TI, JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('81', '81 - GRANDE DEUS, ÚNICO DEUS', 'GRANDE DEUS, ÚNICO DEUS, CRIADOR DE TUDO QUANTO HÁ DE MAIS PROFUNDO, DE MAIS VALOR, O SENHOR, O MEU GRANDE DEUS.\n\nPAI, TU QUE ME OUVES MESMO QUANDO A VOZ NÃO SAI, MEU GRANDE PAI, TU ÉS AMIGO, PREFERIDO, MAIS QUE IRMÃO, MEU GRANDE PAI, TU ÉS AMIGO, MEU PREFERIDO, DÁ-ME A TUA PAZ.\nBIS', 'GRANDE DEUS, ÚNICO DEUS, CRIADOR DE TUDO QUANTO HÁ DE MAIS PROFUNDO, DE MAIS VALOR, O SENHOR, O MEU GRANDE DEUS. PAI, TU QUE ME OUVES MESMO QUANDO A VOZ NÃO SAI, MEU GRANDE PAI, TU ÉS AMIGO, PREFERIDO, MAIS QUE IRMÃO, MEU GRANDE PAI, TU ÉS AMIGO, MEU PREFERIDO, DÁ-ME A TUA PAZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('82', '82 - CADA INSTANTE', 'CADA INSTANTE CONTIGO, SENHOR, QUE PASSO A TEUS PÉS, EU SOU MAIS FELIZ. CADA INSTANTE CONTIGO, JESUS, É PAZ EM MINH’ALMA, SUAVE HARMONIA, NO TEU GRANDE AMOR.\nBIS', 'CADA INSTANTE CONTIGO, SENHOR, QUE PASSO A TEUS PÉS, EU SOU MAIS FELIZ. CADA INSTANTE CONTIGO, JESUS, É PAZ EM MINH’ALMA, SUAVE HARMONIA, NO TEU GRANDE AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('83', '83 - MAIS UMA VEZ', 'MAIS UMA VEZ, ESTAMOS AQUI, SENHOR MAIS UMA VEZ PEDINDO COMUNHÃO PARA TE LOUVAR, TE GLORIFICAR, PARA TE EXALTARMOS, SENHOR JESUS.\n\nMAIS UMA VEZ, ESTAMOS AQUI, SENHOR MAIS UMA VEZ PEDINDO COMUNHÃO PARA TE LOUVAR, TE GLORIFICAR, PARA TE EXALTARMOS, SENHOR JESUS. FINAL: PARA TE EXALTARMOS, SENHOR JESUS.', 'MAIS UMA VEZ, ESTAMOS AQUI, SENHOR MAIS UMA VEZ PEDINDO COMUNHÃO PARA TE LOUVAR, TE GLORIFICAR, PARA TE EXALTARMOS, SENHOR JESUS. MAIS UMA VEZ, ESTAMOS AQUI, SENHOR MAIS UMA VEZ PEDINDO COMUNHÃO PARA TE LOUVAR, TE GLORIFICAR, PARA TE EXALTARMOS, SENHOR JESUS.  PARA TE EXALTARMOS, SENHOR JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('84', '84 - ATENDE, SENHOR, A MINHA ORAÇÃO', 'ATENDE, SENHOR, A MINHA ORAÇÃO, A TI, LEVANTO MINHAS MÃOS; A MINHA ALMA ANSEIA POR TI, DÁ-TE PRESSA EM ME SOCORRER.\n\nFAZE-ME OUVIR NA MANHÃ, DA TUA GRAÇA, ENSINA-ME A FAZER A TUA VONTADE POIS EU SOU TEU E TU ÉS MEU, EM TI CONFIO, SENHOR. FINAL: POIS EU SOU TEU E TU ÉS MEU, EM TI CONFIO, SENHOR (3X)\nBIS', 'ATENDE, SENHOR, A MINHA ORAÇÃO, A TI, LEVANTO MINHAS MÃOS; A MINHA ALMA ANSEIA POR TI, DÁ-TE PRESSA EM ME SOCORRER. FAZE-ME OUVIR NA MANHÃ, DA TUA GRAÇA, ENSINA-ME A FAZER A TUA VONTADE POIS EU SOU TEU E TU ÉS MEU, EM TI CONFIO, SENHOR.  POIS EU SOU TEU E TU ÉS MEU, EM TI CONFIO, SENHOR',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('85', '85 - ENVIA A TUA LUZ', 'ENVIA A TUA LUZ, Ó DEUS, ENVIA A TUA VERDADE, MEU SENHOR, PRA QUE ME GUIEM AO TEU SANTO MONTE, AOS TEUS TABERNÁCULOS, MEU SENHOR, MEU CRIADOR, MEU REI, Ó MEU SENHOR.\n\nENTÃO IREI AO ALTAR DE DEUS, DO DEUS QUE É MINHA GRANDE ALEGRIA. E COM HARPA LOUVAREI A DEUS, DEUS, MEU SENHOR.\nBIS', 'ENVIA A TUA LUZ, Ó DEUS, ENVIA A TUA VERDADE, MEU SENHOR, PRA QUE ME GUIEM AO TEU SANTO MONTE, AOS TEUS TABERNÁCULOS, MEU SENHOR, MEU CRIADOR, MEU REI, Ó MEU SENHOR. ENTÃO IREI AO ALTAR DE DEUS, DO DEUS QUE É MINHA GRANDE ALEGRIA. E COM HARPA LOUVAREI A DEUS, DEUS, MEU SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('86', '86 - EM TEU NOME', 'EM TEU NOME ESTAMOS REUNIDOS AQUI, NOSSA ALMA ANSEIA E TEM SEDE DE TI! QUEREMOS VER TUA GLÓRIA ENCHENDO ESTE LUGAR, TU ESTÁS PRESENTE, PRONTO A ABENÇOAR.\n\nCORO ESTÁS AQUI, BOM SALVADOR, MEIGO E GENTIL, ABENÇOADOR; ESTÁS AQUI COM TEU PODER PARA DAR LIBERDADE E MUDAR O NOSSO SER. INSTRUMENTOS\n\nTEU PERFUME É SUAVE COMO A ROSA E O JASMIM, TUA PRESENÇA ME ENCHE DE ALEGRIA SEM FIM, FONTE INESGOTÁVEL QUE DO CÉU VIDA TRAZ, FONTE DE ÁGUA VIVA QUE ME CURA E DÁ PAZ.\n\nCORO (2 X) ESTÁS AQUI, BOM SALVADOR, MEIGO E GENTIL, ABENÇOADOR; ESTÁS AQUI COM TEU PODER PARA DAR LIBERDADE E MUDAR O NOSSO SER.', 'EM TEU NOME ESTAMOS REUNIDOS AQUI, NOSSA ALMA ANSEIA E TEM SEDE DE TI! QUEREMOS VER TUA GLÓRIA ENCHENDO ESTE LUGAR, TU ESTÁS PRESENTE, PRONTO A ABENÇOAR. CORO ESTÁS AQUI, BOM SALVADOR, MEIGO E GENTIL, ABENÇOADOR; ESTÁS AQUI COM TEU PODER PARA DAR LIBERDADE E MUDAR O NOSSO SER. INSTRUMENTOS TEU PERFUME É SUAVE COMO A ROSA E O JASMIM, TUA PRESENÇA ME ENCHE DE ALEGRIA SEM FIM, FONTE INESGOTÁVEL QUE DO CÉU VIDA TRAZ, FONTE DE ÁGUA VIVA QUE ME CURA E DÁ PAZ. CORO (2 X) ESTÁS AQUI, BOM SALVADOR, MEIGO E GENTIL, ABENÇOADOR; ESTÁS AQUI COM TEU PODER PARA DAR LIBERDADE E MUDAR O NOSSO SER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('87', '87 - MEU PECADO RESGATADO', 'MEU PECADO RESGATADO FOI NA CRUZ, POR TEU AMOR, E DA MORTE, TRISTE SORTE, ME LIVRASTE TU, SENHOR!\n\nCORO VEM! INFLAMA VIVA CHAMA EM MEU PEITO, BEM SEM FIM! EU TE ADORO, SEMPRE IMPLORO: Ó JESUS, HABITA EM MIM!\n\nSE HESITANTE, VACILANTE, OUÇO A VOZ DO TENTADOR, TU ME GUIAS, ME AUXILIAS E ME TORNAS VENCEDOR.\n\nCORO VEM! INFLAMA VIVA CHAMA EM MEU PEITO, BEM SEM FIM! EU TE ADORO, SEMPRE IMPLORO: Ó JESUS, HABITA EM MIM!\n\nREDIMIDA, SÓ TEM VIDA A MINHA ALMA EM TEU AMOR; COM APREÇO, RECONHEÇO QUANTO DEVO A TI, SENHOR!\n\nCORO VEM! INFLAMA VIVA CHAMA EM MEU PEITO, BEM SEM FIM! EU TE ADORO, SEMPRE IMPLORO: Ó JESUS, HABITA EM MIM!', 'MEU PECADO RESGATADO FOI NA CRUZ, POR TEU AMOR, E DA MORTE, TRISTE SORTE, ME LIVRASTE TU, SENHOR! CORO VEM! INFLAMA VIVA CHAMA EM MEU PEITO, BEM SEM FIM! EU TE ADORO, SEMPRE IMPLORO: Ó JESUS, HABITA EM MIM! SE HESITANTE, VACILANTE, OUÇO A VOZ DO TENTADOR, TU ME GUIAS, ME AUXILIAS E ME TORNAS VENCEDOR. REDIMIDA, SÓ TEM VIDA A MINHA ALMA EM TEU AMOR; COM APREÇO, RECONHEÇO QUANTO DEVO A TI, SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('88', '88 - DAS PROFUNDEZAS', 'DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR, ÉS A MINHA FORÇA, ÉS MINHA ROCHA E ALTO RETIRO MEU, EM TI EU CONFIAREI.\nBIS', 'DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR, ÉS A MINHA FORÇA, ÉS MINHA ROCHA E ALTO RETIRO MEU, EM TI EU CONFIAREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('89', '89 - DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR!', 'DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR! ESCUTA A MINHA VOZ, ESCUTA A MINHA SÚPLICA; ESPERO EM TI, SENHOR, O TEU PERDÃO, ESPERO EM TI, SENHOR, O TEU PERDÃO.\n\nAGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA) E REDENÇÃO. INSTRUMENTOS\n\nELE TE REMIRÁ DE TODA TRANSGRESSÃO POIS O SENHOR QUE ESTÁ NOS CÉUS TE OUVIRÁ; “ESPERA, POVO MEU, POIS VENHO JÁ, ESPERA, ISRAEL, NO TEU SENHOR E REI”.\n\nAGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA)\n\nAGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI, MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (ESPERA EM DEUS) ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA) E REDENÇÃO.', 'DAS PROFUNDEZAS CLAMO A TI, Ó SENHOR! ESCUTA A MINHA VOZ, ESCUTA A MINHA SÚPLICA; ESPERO EM TI, SENHOR, O TEU PERDÃO, ESPERO EM TI, SENHOR, O TEU PERDÃO. AGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA) E REDENÇÃO. INSTRUMENTOS ELE TE REMIRÁ DE TODA TRANSGRESSÃO POIS O SENHOR QUE ESTÁ NOS CÉUS TE OUVIRÁ; ESPERA, POVO MEU, POIS VENHO JÁ, ESPERA, ISRAEL, NO TEU SENHOR E REI. AGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA) AGUARDO EM TI, SENHOR, ESPERO NA TUA PALAVRA, A MINH’ALMA ANSEIA POR TI, MAIS QUE OS GUARDAS PELO ROMPER DA MANHÃ. ESPERA, ISRAEL, (ESPERA EM DEUS) ESPERA, ISRAEL, (NO SENHOR) PORQUE NELE HÁ (MISERICÓRDIA) E REDENÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('90', '90 - EM TUAS MÃOS, SENHOR', 'EM TUAS MÃOS, SENHOR, EU ME COLOCO AGORA; FALA COMIGO, NESTA HORA EM QUE MEU CORAÇÃO SUPLICA O TEU AMOR. EU PEÇO SALVAÇÃO, PEÇO TEU PERDÃO, AGORA, VEM ME DIZER QUE A DOR FINDOU E QUE A FELICIDADE NÃO DEMORA. FALA COMIGO, AGORA (4X)', 'EM TUAS MÃOS, SENHOR, EU ME COLOCO AGORA; FALA COMIGO, NESTA HORA EM QUE MEU CORAÇÃO SUPLICA O TEU AMOR. EU PEÇO SALVAÇÃO, PEÇO TEU PERDÃO, AGORA, VEM ME DIZER QUE A DOR FINDOU E QUE A FELICIDADE NÃO DEMORA. FALA COMIGO, AGORA',  1);
