INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('92', '92 - PAI CELESTE', 'PAI CELESTE, PAI CELESTE, VEM FICAR COMIGO, PAI CELESTE. JESUS CRISTO, JESUS CRISTO, VEM FICAR COMIGO, JESUS CRISTO. SANTO ESPÍRITO, SANTO ESPÍRITO, VEM FICAR COMIGO, SANTO ESPÍRITO.', 'PAI CELESTE, PAI CELESTE, VEM FICAR COMIGO, PAI CELESTE. JESUS CRISTO, JESUS CRISTO, VEM FICAR COMIGO, JESUS CRISTO. SANTO ESPÍRITO, SANTO ESPÍRITO, VEM FICAR COMIGO, SANTO ESPÍRITO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('93', '93 - LOGO DE MANHÃ', 'LOGO DE MANHÃ, QUERO TE BUSCAR, TUA VOZ OUVIR, TEU AMOR SENTIR. E ESTENDER AS MÃOS PARA TE LOUVAR, DERRAMAR MEU CORAÇÃO SOBRE TEU ALTAR. POIS TU SABES BEM TUDO QUANDO HÁ EM MIM: QUE VOU TE SEGUIR E TE AMAR ATÉ O FIM.\n\nE, NO FIM DO DIA, QUANDO O SOL SE FOR, TE ADORAREI, TE DAREI LOUVOR. MESMA ESCURA A NOITE, BRILHA A TUA LUZ. E EM TEUS BRAÇOS EU DESCANSO, MEU SENHOR JESUS.', 'LOGO DE MANHÃ, QUERO TE BUSCAR, TUA VOZ OUVIR, TEU AMOR SENTIR. E ESTENDER AS MÃOS PARA TE LOUVAR, DERRAMAR MEU CORAÇÃO SOBRE TEU ALTAR. POIS TU SABES BEM TUDO QUANDO HÁ EM MIM: QUE VOU TE SEGUIR E TE AMAR ATÉ O FIM. E, NO FIM DO DIA, QUANDO O SOL SE FOR, TE ADORAREI, TE DAREI LOUVOR. MESMA ESCURA A NOITE, BRILHA A TUA LUZ. E EM TEUS BRAÇOS EU DESCANSO, MEU SENHOR JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('94', '94 - BENDITA A HORA DE ORAÇÃO', 'BENDITA A HORA DE ORAÇÃO QUE ACALMA O AFLITO CORAÇÃO. QUE LEVA AO TRONO DE JESUS OS ROGOS PARA AUXÍLIO E LUZ! EM TEMPOS DE CUIDADO E DOR, REFÚGIO TENHO EM MEU SENHOR. VENCENDO O ARDIL E A TENTAÇÃO, BENDIGO A HORA DE ORAÇÃO.\n\nBENDITA A HORA DE ORAÇÃO, QUANDO A FERVENTE PETIÇÃO SOBE AO BENIGNO SALVADOR, QUE ATENDE A VOZ DO MEU CLAMOR! JESUS ME ORDENA A RECORRER AO SEU AMOR, AO SEU PODER. CONTENTE E SEM PERTURBAÇÃO EU BUSCO A HORA DE ORAÇÃO.\n\nBENDITA A HORA DE ORAÇÃO, DE SANTA PAZ E COMUNHÃO! DESEJO ENQUANTO AQUI ME ACHAR, COM FÉ CONSTANTE HUMILDE ORAR. E ENFIM, NO RESPLENDOR DE DEUS, NA GLÓRIA DOS MAIS ALTOS CÉUS, LEMBRAR-ME-EI COM GRATIDÃO DAS HORAS SUAVES DE ORAÇÃO. FINAL: AMÉM', 'BENDITA A HORA DE ORAÇÃO QUE ACALMA O AFLITO CORAÇÃO. QUE LEVA AO TRONO DE JESUS OS ROGOS PARA AUXÍLIO E LUZ! EM TEMPOS DE CUIDADO E DOR, REFÚGIO TENHO EM MEU SENHOR. VENCENDO O ARDIL E A TENTAÇÃO, BENDIGO A HORA DE ORAÇÃO. BENDITA A HORA DE ORAÇÃO, QUANDO A FERVENTE PETIÇÃO SOBE AO BENIGNO SALVADOR, QUE ATENDE A VOZ DO MEU CLAMOR! JESUS ME ORDENA A RECORRER AO SEU AMOR, AO SEU PODER. CONTENTE E SEM PERTURBAÇÃO EU BUSCO A HORA DE ORAÇÃO. BENDITA A HORA DE ORAÇÃO, DE SANTA PAZ E COMUNHÃO! DESEJO ENQUANTO AQUI ME ACHAR, COM FÉ CONSTANTE HUMILDE ORAR. E ENFIM, NO RESPLENDOR DE DEUS, NA GLÓRIA DOS MAIS ALTOS CÉUS, LEMBRAR-ME-EI COM GRATIDÃO DAS HORAS SUAVES DE ORAÇÃO.  AMÉM',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('95', '95 - BUSCAR-ME-EIS', 'BUSCAR-ME-EIS, E ME ACHAREIS, QUANDO ME BUSCARDES, QUANDO ME BUSCARDES, DE TODO O CORAÇÃO.\nBIS\n\nCORO INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INSTRUMENTOS\n\nBUSCAR-ME-EIS, E ME ACHAREIS, QUANDO ME BUSCARDES, QUANDO ME BUSCARDES, DE TODO O CORAÇÃO.\n\nCORO INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. FINAL: ASSIM DIZ O SENHOR.', 'BUSCAR-ME-EIS, E ME ACHAREIS, QUANDO ME BUSCARDES, QUANDO ME BUSCARDES, DE TODO O CORAÇÃO. CORO INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INSTRUMENTOS CORO INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI. INVOCAR-ME-EIS, E ORAREIS A MIM, E EU VOS OUVIREI.  ASSIM DIZ O SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('96', '96 - COM A MINHA VOZ CLAMO AO SENHOR', 'CORO COM A MINHA VOZ CLAMO AO SENHOR, COM A MINHA VOZ AO SENHOR SUPLICO. DIANTE DELE A PROSTRAR-ME EU ESTOU, DIANTE DELE EXPONHO A MINHA AFLIÇÃO.\n\nQUANDO AQUI DENTRO DE MIM ESMORECE O MEU ESPÍRITO, TU ENTÃO CONHECES MINHA VEREDA. OLHO À MÃO DIREITA E VEJO NÃO HÁ QUEM ME CONHEÇA, NÃO HÁ LUGAR ONDE ME REFUGIAR.\n\nCORO COM A MINHA VOZ CLAMO AO SENHOR, COM A MINHA VOZ AO SENHOR SUPLICO. DIANTE DELE A PROSTRAR-ME EU ESTOU, DIANTE DELE EXPONHO A MINHA AFLIÇÃO.\n\nÓ SENHOR A TI CLAMEI POIS TU ÉS O MEU REFÚGIO, E O MEU TESOURO ENTRE OS VIVENTES. VEM, ATENDE AO MEU CLAMOR, ESTOU MUITO ABATIDO. LIVRAR-ME VEM DO FORTE TENTADOR.\n\nCORO COM A MINHA VOZ CLAMO AO SENHOR, COM A MINHA VOZ AO SENHOR SUPLICO. DIANTE DELE A PROSTRAR-ME EU ESTOU, DIANTE DELE EXPONHO A MINHA AFLIÇÃO.\n\nFINAL: TIRA-ME DESTA PRISÃO E ASSIM LOUVAREI TEU NOME, E ENTÃO OS JUSTOS ME CERCARÃO. MEU SENHOR EU CLAMO A TI: Ó, VEM LIVRAR MINH’ALMA, E CANTAREI QUE ME FIZESTE BEM.', 'CORO COM A MINHA VOZ CLAMO AO SENHOR, COM A MINHA VOZ AO SENHOR SUPLICO. DIANTE DELE A PROSTRAR-ME EU ESTOU, DIANTE DELE EXPONHO A MINHA AFLIÇÃO. QUANDO AQUI DENTRO DE MIM ESMORECE O MEU ESPÍRITO, TU ENTÃO CONHECES MINHA VEREDA. OLHO À MÃO DIREITA E VEJO NÃO HÁ QUEM ME CONHEÇA, NÃO HÁ LUGAR ONDE ME REFUGIAR. Ó SENHOR A TI CLAMEI POIS TU ÉS O MEU REFÚGIO, E O MEU TESOURO ENTRE OS VIVENTES. VEM, ATENDE AO MEU CLAMOR, ESTOU MUITO ABATIDO. LIVRAR-ME VEM DO FORTE TENTADOR. TIRA-ME DESTA PRISÃO E ASSIM LOUVAREI TEU NOME, E ENTÃO OS JUSTOS ME CERCARÃO. MEU SENHOR EU CLAMO A TI: Ó, VEM LIVRAR MINH’ALMA, E CANTAREI QUE ME FIZESTE BEM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('97', '97 - BUSCOU-ME COM TERNURA', 'BUSCOU-ME COM TERNURA JESUS, O BOM PASTOR; ACHOU-ME NA MISÉRIA, SALVOU-ME COM AMOR; NO CÉU CANTARAM DE ALEGRIA OS ANJOS, SIM, EM HARMONIA.\n\nCORO OH! QUE AMOR GLORIOSO! PREÇO TÃO GRANDIOSO, QUE JESUS POR MIM NA CRUZ PAGOU; INAUDITA GRAÇA ME MOSTROU!\n\nFERIDO, ABANDONADO, JESUS ME SOCORREU; E SEGREDOU-ME: “ACHEI-TE; DE AGORA EM DIANTE ÉS MEU”. TÃO MEIGA VOZ JAMAIS OUVI, PRAZER MAIOR JAMAIS SENTI.\n\nCORO OH! QUE AMOR GLORIOSO! PREÇO TÃO GRANDIOSO, QUE JESUS POR MIM NA CRUZ PAGOU; INAUDITA GRAÇA ME MOSTROU!\n\nJESUS MOSTROU-ME AS CHAGAS QUE EM MEU LUGAR SOFREU, COROA, MAS DE ESPINHOS, A CRUZ QUE PADECEU. QUE PODERIA EM MIM ACHAR, PRA TAIS AFRONTAS SUPORTAR?\n\nCORO OH! QUE AMOR GLORIOSO! PREÇO TÃO GRANDIOSO, QUE JESUS POR MIM NA CRUZ PAGOU; INAUDITA GRAÇA ME MOSTROU!\n\nENQUANTO AS HORAS PASSAM, EU TENHO GOZO E PAZ, E AGUARDO MEU BOM MESTRE, QUE TÃO FELIZ ME FAZ! JESUS A MIM VIRÁ BUSCAR, E ENTÃO PRA SEMPRE IREI GOZAR!', 'BUSCOU-ME COM TERNURA JESUS, O BOM PASTOR; ACHOU-ME NA MISÉRIA, SALVOU-ME COM AMOR; NO CÉU CANTARAM DE ALEGRIA OS ANJOS, SIM, EM HARMONIA. CORO OH! QUE AMOR GLORIOSO! PREÇO TÃO GRANDIOSO, QUE JESUS POR MIM NA CRUZ PAGOU; INAUDITA GRAÇA ME MOSTROU! FERIDO, ABANDONADO, JESUS ME SOCORREU; E SEGREDOU-ME: ACHEI-TE; DE AGORA EM DIANTE ÉS MEU. TÃO MEIGA VOZ JAMAIS OUVI, PRAZER MAIOR JAMAIS SENTI. JESUS MOSTROU-ME AS CHAGAS QUE EM MEU LUGAR SOFREU, COROA, MAS DE ESPINHOS, A CRUZ QUE PADECEU. QUE PODERIA EM MIM ACHAR, PRA TAIS AFRONTAS SUPORTAR? ENQUANTO AS HORAS PASSAM, EU TENHO GOZO E PAZ, E AGUARDO MEU BOM MESTRE, QUE TÃO FELIZ ME FAZ! JESUS A MIM VIRÁ BUSCAR, E ENTÃO PRA SEMPRE IREI GOZAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('98', '98 - ONDE ESTARIA EU?', 'ONDE ESTARIA EU, NÃO FOSSE A MÃO DO SENHOR? SOZINHO, SOLITÁRIO NO MUNDO, JESUS ME ALCANÇOU.\n\nCORO QUERO, CADA DIA MAIS, VIVER PRA TI, Ó MEU SENHOR, QUERO, CADA DIA MAIS, OUVIR A TUA VOZ DE AMOR.\n\nHOJE ME SINTO EM PAZ, SEGURO EM TUA MÃO ESTOU; CAMINHO PARA A ETERNIDADE, CANTANDO PARA O CÉU EU VOU.\n\nCORO (2X) QUERO, CADA DIA MAIS, VIVER PRA TI, Ó MEU SENHOR, QUERO, CADA DIA MAIS, OUVIR A TUA VOZ DE AMOR. (3X)', 'ONDE ESTARIA EU, NÃO FOSSE A MÃO DO SENHOR? SOZINHO, SOLITÁRIO NO MUNDO, JESUS ME ALCANÇOU. CORO QUERO, CADA DIA MAIS, VIVER PRA TI, Ó MEU SENHOR, QUERO, CADA DIA MAIS, OUVIR A TUA VOZ DE AMOR. HOJE ME SINTO EM PAZ, SEGURO EM TUA MÃO ESTOU; CAMINHO PARA A ETERNIDADE, CANTANDO PARA O CÉU EU VOU. CORO  QUERO, CADA DIA MAIS, VIVER PRA TI, Ó MEU SENHOR, QUERO, CADA DIA MAIS, OUVIR A TUA VOZ DE AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('99', '99 - UM DIA JESUS ACHOU-ME', 'UM DIA JESUS ACHOU-ME TÃO LONGE DO MEU LAR, PERDIDO JÁ NO MUNDO SEM FORÇAS PRA VOLTAR; ERGUENDO-ME COM OS BRAÇOS, LEVANTA AS MINHAS FORÇAS, E AGORA ANDAMOS JUNTOS VOLTANDO PARA O LAR.\n\nCORO A PRESENÇA DE JESUS ENCHE O CORAÇÃO DE LUZ! MUI PRECIOSA FICA, CADA VEZ MAIS RICA, MINHA VIDA COM JESUS.\n\nPASSANDO PELOS VALES, PRA MINHA FÉ PROVAR; QUÃO DENSAS SÃO AS TREVAS QUE IMPEDEM, POIS, O ANDAR; NÃO HÁ NENHUM PERIGO, POIS TENHO JÁ COMIGO, UM MUI PRECIOSO AMIGO VOLTANDO PARA O LAR.\n\nCORO A PRESENÇA DE JESUS ENCHE O CORAÇÃO DE LUZ! MUI PRECIOSA FICA, CADA VEZ MAIS RICA, MINHA VIDA COM JESUS.\n\nENTRANDO NA ATMOSFERA QUE CERCA AQUELE LAR, ALI MEU PAI ME ESPERA, TÃO LONGE HEI DE MORAR; NÃO HÁ NO MUNDO INTEIRO UM OUTRO COMPANHEIRO QUE MOSTRE TANTO ZELO VOLTANDO PARA O LAR.\n\nCORO A PRESENÇA DE JESUS ENCHE O CORAÇÃO DE LUZ! MUI PRECIOSA FICA, CADA VEZ MAIS RICA, MINHA VIDA COM JESUS.', 'UM DIA JESUS ACHOU-ME TÃO LONGE DO MEU LAR, PERDIDO JÁ NO MUNDO SEM FORÇAS PRA VOLTAR; ERGUENDO-ME COM OS BRAÇOS, LEVANTA AS MINHAS FORÇAS, E AGORA ANDAMOS JUNTOS VOLTANDO PARA O LAR. CORO A PRESENÇA DE JESUS ENCHE O CORAÇÃO DE LUZ! MUI PRECIOSA FICA, CADA VEZ MAIS RICA, MINHA VIDA COM JESUS. PASSANDO PELOS VALES, PRA MINHA FÉ PROVAR; QUÃO DENSAS SÃO AS TREVAS QUE IMPEDEM, POIS, O ANDAR; NÃO HÁ NENHUM PERIGO, POIS TENHO JÁ COMIGO, UM MUI PRECIOSO AMIGO VOLTANDO PARA O LAR. ENTRANDO NA ATMOSFERA QUE CERCA AQUELE LAR, ALI MEU PAI ME ESPERA, TÃO LONGE HEI DE MORAR; NÃO HÁ NO MUNDO INTEIRO UM OUTRO COMPANHEIRO QUE MOSTRE TANTO ZELO VOLTANDO PARA O LAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('100', '100 - EU ME LEMBRO QUANDO JESUS ME TOCOU', 'EU ME LEMBRO QUANDO JESUS ME TOCOU, FOI UM TOQUE DE ESPERANÇA E DE AMOR; CRISTO TRANSFORMOU A MORTE NO VIVER QUANDO A ELE ENTREGUEI TODO O MEU SER.\n\nCORO DEUS AMOU O MUNDO DE TAL MANEIRA QUE ENVIOU SEU FILHO JESUS, UNIGÊNITO, A MORRER NA CRUZ PRA NOS SALVAR E LIBERTAR-NOS. CRISTO VIVE EM MIM, POSSO DIZER, O SEU TOQUE FEZ MINH’ALMA RENASCER.\nBIS\n\nEU ME LEMBRO O QUE ELE FEZ POR MIM, FOI SEU BRAÇO FORTE QUE ME FEZ ASSIM, A CONHECER A ETERNA REDENÇÃO, DE BÊNÇÃOS CRISTO ENCHEU MEU CORAÇÃO.', 'EU ME LEMBRO QUANDO JESUS ME TOCOU, FOI UM TOQUE DE ESPERANÇA E DE AMOR; CRISTO TRANSFORMOU A MORTE NO VIVER QUANDO A ELE ENTREGUEI TODO O MEU SER. CORO DEUS AMOU O MUNDO DE TAL MANEIRA QUE ENVIOU SEU FILHO JESUS, UNIGÊNITO, A MORRER NA CRUZ PRA NOS SALVAR E LIBERTAR-NOS. CRISTO VIVE EM MIM, POSSO DIZER, O SEU TOQUE FEZ MINH’ALMA RENASCER. EU ME LEMBRO O QUE ELE FEZ POR MIM, FOI SEU BRAÇO FORTE QUE ME FEZ ASSIM, A CONHECER A ETERNA REDENÇÃO, DE BÊNÇÃOS CRISTO ENCHEU MEU CORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('101', '101 - QUEM ERA EU ANTES DE CONHECER', 'QUEM ERA EU ANTES DE CONHECER A OBRA QUE DEUS OPEROU EM MEU SER? VIDA SEM DEUS, SEM A LUZ, SEM AMOR, ANDAVA SEM RUMO, TÃO TRISTE E NA DOR. JESUS ME ENCONTROU E COM GRANDE AMOR, TOMOU-ME EM SEUS BRAÇOS E ME CARREGOU.\n\nVIVO FELIZ POIS JESUS ME MUDOU, A OBRA EM MIM O SENHOR REALIZOU; NOVA CRIATURA EU SOU EM JESUS, NÃO VIVO MAIS EU, VIVE EM MIM JESUS. JESUS É A MAIOR EXPRESSÃO DE AMOR, GLÓRIA, ALELUIA, É O NOSSO LOUVOR.\nBIS', 'QUEM ERA EU ANTES DE CONHECER A OBRA QUE DEUS OPEROU EM MEU SER? VIDA SEM DEUS, SEM A LUZ, SEM AMOR, ANDAVA SEM RUMO, TÃO TRISTE E NA DOR. JESUS ME ENCONTROU E COM GRANDE AMOR, TOMOU-ME EM SEUS BRAÇOS E ME CARREGOU. VIVO FELIZ POIS JESUS ME MUDOU, A OBRA EM MIM O SENHOR REALIZOU; NOVA CRIATURA EU SOU EM JESUS, NÃO VIVO MAIS EU, VIVE EM MIM JESUS. JESUS É A MAIOR EXPRESSÃO DE AMOR, GLÓRIA, ALELUIA, É O NOSSO LOUVOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('102', '102 - MARAVILHOSO E SUBLIME PRA MIM', 'MARAVILHOSO E SUBLIME PRA MIM, SIM, NUNCA ME ESQUECEREI, DIA GLORIOSO EM QUE A CRISTO EU VI E O CORAÇÃO LHE ENTREGUEI; OH! QUÃO PRECIOSO AMIGO ELE É, SALVOU-ME DA PERDIÇÃO, TIRANDO AS CULPAS E AS TREVAS, LIVRANDO E TRAZENDO-ME PLENO PERDÃO.\n\nCORO A PAZ DO CÉU ENCHEU MEU CORAÇÃO, QUANDO JESUS ME DEU A SALVAÇÃO; MINH’ALMA ENTÃO LAVOU E A LUZ EM MIM RAIOU; A PAZ DO CÉU ENCHEU MEU CORAÇÃO.\n\nGRANDE ESPERANÇA JESUS JÁ ME DEU, QUE NÃO DESVANECERÁ, HÁ UMA GLORIOSA MORADA NO CÉU, QUE BREVE MINHA SERÁ; TUDO PORQUE NESSE DIA FELIZ O MEU SENHOR ACEITEI, GRANDES RIQUEZAS E BÊNÇÃOS CELESTES DAS MÃOS DIVINAIS ALCANCEI.\n\nCORO A PAZ DO CÉU ENCHEU MEU CORAÇÃO, QUANDO JESUS ME DEU A SALVAÇÃO; MINH’ALMA ENTÃO LAVOU E A LUZ EM MIM RAIOU; A PAZ DO CÉU ENCHEU MEU CORAÇÃO.', 'MARAVILHOSO E SUBLIME PRA MIM, SIM, NUNCA ME ESQUECEREI, DIA GLORIOSO EM QUE A CRISTO EU VI E O CORAÇÃO LHE ENTREGUEI; OH! QUÃO PRECIOSO AMIGO ELE É, SALVOU-ME DA PERDIÇÃO, TIRANDO AS CULPAS E AS TREVAS, LIVRANDO E TRAZENDO-ME PLENO PERDÃO. CORO A PAZ DO CÉU ENCHEU MEU CORAÇÃO, QUANDO JESUS ME DEU A SALVAÇÃO; MINH’ALMA ENTÃO LAVOU E A LUZ EM MIM RAIOU; A PAZ DO CÉU ENCHEU MEU CORAÇÃO. GRANDE ESPERANÇA JESUS JÁ ME DEU, QUE NÃO DESVANECERÁ, HÁ UMA GLORIOSA MORADA NO CÉU, QUE BREVE MINHA SERÁ; TUDO PORQUE NESSE DIA FELIZ O MEU SENHOR ACEITEI, GRANDES RIQUEZAS E BÊNÇÃOS CELESTES DAS MÃOS DIVINAIS ALCANCEI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('103', '103 - A MINH’ALMA ESTAVA LONGE DO CAMINHO DO CÉU', 'A MINH’ALMA ESTAVA LONGE DO CAMINHO DO CÉU, EU ERA POBRE, DESPREZÍVEL PECADOR; MAS JESUS JÁ TRANSFORMOU MINHAS TREVAS EM LUZ, QUANDO ESTENDEU A SUA MÃO PARA MIM.\n\nCORO QUANDO JESUS ESTENDEU A SUA MÃO, QUANDO ESTENDEU A SUA MÃO PARA MIM, EU ERA POBRE, PERDIDO, SEM DEUS, SEM JESUS, QUANDO ESTENDEU A SUA MÃO PARA MIM.\n\nAGORA ME REGOZIJO DESDE QUE O ACEITEI, E NA TEMPESTADE EU POSSO SOSSEGAR, POIS COM ELE ESTOU LIBERTO DO PERIGO E DO MAL, DESDE QUE ESTENDEU A SUA MÃO PARA MIM.\n\nCORO QUANDO JESUS ESTENDEU A SUA MÃO, QUANDO ESTENDEU A SUA MÃO PARA MIM, EU ERA POBRE, PERDIDO, SEM DEUS, SEM JESUS, QUANDO ESTENDEU A SUA MÃO PARA MIM.', 'A MINH’ALMA ESTAVA LONGE DO CAMINHO DO CÉU, EU ERA POBRE, DESPREZÍVEL PECADOR; MAS JESUS JÁ TRANSFORMOU MINHAS TREVAS EM LUZ, QUANDO ESTENDEU A SUA MÃO PARA MIM. CORO QUANDO JESUS ESTENDEU A SUA MÃO, QUANDO ESTENDEU A SUA MÃO PARA MIM, EU ERA POBRE, PERDIDO, SEM DEUS, SEM JESUS, QUANDO ESTENDEU A SUA MÃO PARA MIM. AGORA ME REGOZIJO DESDE QUE O ACEITEI, E NA TEMPESTADE EU POSSO SOSSEGAR, POIS COM ELE ESTOU LIBERTO DO PERIGO E DO MAL, DESDE QUE ESTENDEU A SUA MÃO PARA MIM.',  1);
