INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('105', '105 - PERDIDO FOI QUE ELE ME ENCONTROU', 'PERDIDO FOI QUE ELE ME ENCONTROU NESTE MUNDO VIL; TOMOU-ME E COM SEU SANGUE ME LAVOU; HOJE SOU FELIZ.\n\nCORO HOJE SOU FELIZ, FOI-SE O MEU TEMOR, VOU PERMANECER JUNTO AO SALVADOR; FOI-SE A ESCURIDÃO, RAIA A LUZ DO SOL, HOJE EU SEI QUE SOU DO MEU SENHOR E ELE É MEU.\n\nSE NEGRA NOITE ASSUSTAR-ME VEM, OUÇO A SUA VOZ; AS TREVAS TORNAM-SE NA LUZ DO SOL COM MEU SALVADOR.\n\nCORO HOJE SOU FELIZ, FOI-SE O MEU TEMOR, VOU PERMANECER JUNTO AO SALVADOR; FOI-SE A ESCURIDÃO, RAIA A LUZ DO SOL, HOJE EU SEI QUE SOU DO MEU SENHOR E ELE É MEU.\n\nSE MUITAS TENTAÇÕES ME SOBREVÊM, PARA ELE EU VOU; RECEBO FORÇAS QUE A MIM CONVÉM, SE COM ELE ESTOU.\n\nCORO HOJE SOU FELIZ, FOI-SE O MEU TEMOR, VOU PERMANECER JUNTO AO SALVADOR; FOI-SE A ESCURIDÃO, RAIA A LUZ DO SOL, HOJE EU SEI QUE SOU DO MEU SENHOR E ELE É MEU.', 'PERDIDO FOI QUE ELE ME ENCONTROU NESTE MUNDO VIL; TOMOU-ME E COM SEU SANGUE ME LAVOU; HOJE SOU FELIZ. CORO HOJE SOU FELIZ, FOI-SE O MEU TEMOR, VOU PERMANECER JUNTO AO SALVADOR; FOI-SE A ESCURIDÃO, RAIA A LUZ DO SOL, HOJE EU SEI QUE SOU DO MEU SENHOR E ELE É MEU. SE NEGRA NOITE ASSUSTAR-ME VEM, OUÇO A SUA VOZ; AS TREVAS TORNAM-SE NA LUZ DO SOL COM MEU SALVADOR. SE MUITAS TENTAÇÕES ME SOBREVÊM, PARA ELE EU VOU; RECEBO FORÇAS QUE A MIM CONVÉM, SE COM ELE ESTOU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('106', '106 - PELA FÉ SOMOS SALVOS', 'PELA FÉ SOMOS SALVOS, PELA GRAÇA DE DEUS REVELADA EM JESUS; PELA FÉ NO SEU SANGUE VAMOS ALÉM DO VÉU, ONDE BRILHA A DIVINA LUZ.\n\nCORO ESTE É O FIRME FUNDAMENTO DA IGREJA, A FÉ, SOMENTE A FÉ! O INVISÍVEL NÓS JÁ VEMOS, PELA FÉ CHEGAREMOS À PÁTRIA CELESTIAL, PELA FÉ!\n\nPELA FÉ ENTENDEMOS OS MISTÉRIOS DE DEUS E ESCUTAMOS SUA VOZ; PELA FÉ A PALAVRA SE REVELA A NÓS, PELA FÉ, SOMENTE A FÉ!\n\nCORO ESTE É O FIRME FUNDAMENTO DA IGREJA, A FÉ, SOMENTE A FÉ! O INVISÍVEL NÓS JÁ VEMOS, PELA FÉ CHEGAREMOS À PÁTRIA CELESTIAL, PELA FÉ!', 'PELA FÉ SOMOS SALVOS, PELA GRAÇA DE DEUS REVELADA EM JESUS; PELA FÉ NO SEU SANGUE VAMOS ALÉM DO VÉU, ONDE BRILHA A DIVINA LUZ. CORO ESTE É O FIRME FUNDAMENTO DA IGREJA, A FÉ, SOMENTE A FÉ! O INVISÍVEL NÓS JÁ VEMOS, PELA FÉ CHEGAREMOS À PÁTRIA CELESTIAL, PELA FÉ! PELA FÉ ENTENDEMOS OS MISTÉRIOS DE DEUS E ESCUTAMOS SUA VOZ; PELA FÉ A PALAVRA SE REVELA A NÓS, PELA FÉ, SOMENTE A FÉ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('107', '107 - QUANDO ANDAVA NO MUNDO ESCRAVIZADO', 'QUANDO ANDAVA NO MUNDO ESCRAVIZADO, EU VIVIA PRESO NO PECADO, MAS UM HOMEM CERTO DIA ME COMPROU E UM ALTO PREÇO PAGOU, E HOJE LIBERTO ESTOU.\n\nCORO HOJE EU NÃO SOU MAIS ESCRAVIZADO, UM HOMEM CHAMADO JESUS TEM ME LIBERTADO; LIBERDADE JESUS TEM PROCLAMADO, E VIDA NOVA TEM DADO, SOU FELIZ COM JESUS AO MEU LADO.\n\nERA ESCRAVO E VIVIA SEMPRE CANSADO, NA ANGÚSTIA E NA TRISTEZA SUBJUGADO, LIBERDADE EU NÃO TINHA, CRISTO DEU, NA CRUZ POR MIM JÁ MORREU E VIDA NOVA ME DEU.\nBIS\n\nCORO HOJE EU NÃO SOU MAIS ESCRAVIZADO, UM HOMEM CHAMADO JESUS TEM ME LIBERTADO; LIBERDADE JESUS TEM PROCLAMADO, E VIDA NOVA TEM DADO, SOU FELIZ COM JESUS AO MEU LADO.', 'QUANDO ANDAVA NO MUNDO ESCRAVIZADO, EU VIVIA PRESO NO PECADO, MAS UM HOMEM CERTO DIA ME COMPROU E UM ALTO PREÇO PAGOU, E HOJE LIBERTO ESTOU. CORO HOJE EU NÃO SOU MAIS ESCRAVIZADO, UM HOMEM CHAMADO JESUS TEM ME LIBERTADO; LIBERDADE JESUS TEM PROCLAMADO, E VIDA NOVA TEM DADO, SOU FELIZ COM JESUS AO MEU LADO. ERA ESCRAVO E VIVIA SEMPRE CANSADO, NA ANGÚSTIA E NA TRISTEZA SUBJUGADO, LIBERDADE EU NÃO TINHA, CRISTO DEU, NA CRUZ POR MIM JÁ MORREU E VIDA NOVA ME DEU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('108', '108 - ISRAEL SAIU DO EGITO', 'ISRAEL SAIU DO EGITO, FOI ORDEM QUE DEUS LHE DEU, CHEGANDO AO MAR VERMELHO, TODO O ISRAEL TEMEU; MONTES ALTOS DE TODOS OS LADOS, MOISÉS LHES RESPONDEU: NÃO TEMAS, Ó ISRAEL, SÓ O SENHOR É DEUS.\n\nCORO SÓ O SENHOR É DEUS! (BIS) CANTEMOS COM MUITA ALEGRIA, DIZENDO QUE SÓ O SENHOR É DEUS.\n\nMOISÉS ORDENOU ÀS ÁGUAS DO MAR, QUE LHE OBEDECERAM, POR TERRA SECA PASSARAM, PELO PODER DE DEUS; O EXÉRCITO DE FARAÓ, PORÉM, NO MAR PERECEU; MIRIÃ CANTOU DIZENDO: SÓ O SENHOR É DEUS.\n\nCORO SÓ O SENHOR É DEUS! (BIS) CANTEMOS COM MUITA ALEGRIA, DIZENDO QUE SÓ O SENHOR É DEUS.\n\nNO DESERTO FALTOU ÁGUA, ISRAEL SE ENTRISTECEU, E TODOS SE REVOLTARAM CONTRA O SERVO DE DEUS; MOISÉS FEZ A ORAÇÃO, JEOVÁ LHE APARECEU, DA ROCHA BROTOU ÁGUA PELO PODER DE DEUS.\n\nCORO SÓ O SENHOR É DEUS! (BIS) CANTEMOS COM MUITA ALEGRIA, DIZENDO QUE SÓ O SENHOR É DEUS.\n\nOS PROFETAS DE BAAL, EM HOLOCAUSTOS SEUS, CLAMARAM ATÉ O MEIO DIA, BAAL NÃO LHES RESPONDEU; ELIAS FEZ A ORAÇÃO, O FOGO DO CÉU DESCEU, QUEIMANDO TODO O HOLOCAUSTO, COM O PODER DE DEUS.', 'ISRAEL SAIU DO EGITO, FOI ORDEM QUE DEUS LHE DEU, CHEGANDO AO MAR VERMELHO, TODO O ISRAEL TEMEU; MONTES ALTOS DE TODOS OS LADOS, MOISÉS LHES RESPONDEU: NÃO TEMAS, Ó ISRAEL, SÓ O SENHOR É DEUS. CORO SÓ O SENHOR É DEUS!  CANTEMOS COM MUITA ALEGRIA, DIZENDO QUE SÓ O SENHOR É DEUS. MOISÉS ORDENOU ÀS ÁGUAS DO MAR, QUE LHE OBEDECERAM, POR TERRA SECA PASSARAM, PELO PODER DE DEUS; O EXÉRCITO DE FARAÓ, PORÉM, NO MAR PERECEU; MIRIÃ CANTOU DIZENDO: SÓ O SENHOR É DEUS. NO DESERTO FALTOU ÁGUA, ISRAEL SE ENTRISTECEU, E TODOS SE REVOLTARAM CONTRA O SERVO DE DEUS; MOISÉS FEZ A ORAÇÃO, JEOVÁ LHE APARECEU, DA ROCHA BROTOU ÁGUA PELO PODER DE DEUS. OS PROFETAS DE BAAL, EM HOLOCAUSTOS SEUS, CLAMARAM ATÉ O MEIO DIA, BAAL NÃO LHES RESPONDEU; ELIAS FEZ A ORAÇÃO, O FOGO DO CÉU DESCEU, QUEIMANDO TODO O HOLOCAUSTO, COM O PODER DE DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('109', '109 - A MINHA VIDA TÃO VAZIA', 'A MINHA VIDA TÃO VAZIA JESUS CRISTO TRANSFORMOU, NÃO TINHA PAZ NEM ALEGRIA TÉ QUE O SALVADOR ME ACHOU; TAL COMO A FLOR QUE NO JARDIM DESPONTA PARA A LUZ DO SOL, ASSIM BRILHOU UM DIA EM MIM, JESUS. NASCI DE NOVO PRA ENTENDER O AMOR QUE CRISTO TEM POR MIM; JESUS ACHOU-ME, AGORA SOU FELIZ.\n\nPRA ME ENCONTRAR, PERDEU SUA VIDA, MEU JESUS, O BOM PASTOR; A MÃO QUE FOI NA CRUZ FERIDA ME SALVOU, TÃO PECADOR. A TI, JESUS, CORDEIRO ETERNO, CONFESSO QUE ÉS MEU SALVADOR, JÁ ME LAVOU TEU SANGUE CARMESIM; TÃO FRÁGIL, TÃO PEQUENO SOU, CUIDA DE MIM COM TEU AMOR, COMO UMA FLOR PEQUENA EM TEU JARDIM.', 'A MINHA VIDA TÃO VAZIA JESUS CRISTO TRANSFORMOU, NÃO TINHA PAZ NEM ALEGRIA TÉ QUE O SALVADOR ME ACHOU; TAL COMO A FLOR QUE NO JARDIM DESPONTA PARA A LUZ DO SOL, ASSIM BRILHOU UM DIA EM MIM, JESUS. NASCI DE NOVO PRA ENTENDER O AMOR QUE CRISTO TEM POR MIM; JESUS ACHOU-ME, AGORA SOU FELIZ. PRA ME ENCONTRAR, PERDEU SUA VIDA, MEU JESUS, O BOM PASTOR; A MÃO QUE FOI NA CRUZ FERIDA ME SALVOU, TÃO PECADOR. A TI, JESUS, CORDEIRO ETERNO, CONFESSO QUE ÉS MEU SALVADOR, JÁ ME LAVOU TEU SANGUE CARMESIM; TÃO FRÁGIL, TÃO PEQUENO SOU, CUIDA DE MIM COM TEU AMOR, COMO UMA FLOR PEQUENA EM TEU JARDIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('110', '110 - OH! QUÃO CEGO ANDEI E PERDIDO VAGUEI', 'OH! QUÃO CEGO ANDEI E PERDIDO VAGUEI LONGE, LONGE DO MEU SALVADOR; MAS DO CÉU ELE DESCEU E SEU SANGUE VERTEU PRA SALVAR UM TÃO POBRE PECADOR.\n\nCORO FOI NA CRUZ, FOI NA CRUZ ONDE UM DIA EU VI MEU PECADO CASTIGADO EM JESUS; FOI ALI PELA FÉ QUE MEUS OLHOS ABRI E AGORA ME ALEGRO EM SUA LUZ.\n\nEU OUVIA FALAR DESSA GRAÇA SEM PAR QUE DO CÉU TROUXE O NOSSO JESUS, MAS EU SURDO ME FIZ, CONVERTER-ME NÃO QUIS AO SENHOR QUE POR MIM MORREU NA CRUZ.\n\nCORO FOI NA CRUZ, FOI NA CRUZ ONDE UM DIA EU VI MEU PECADO CASTIGADO EM JESUS; FOI ALI PELA FÉ QUE MEUS OLHOS ABRI E AGORA ME ALEGRO EM SUA LUZ.\n\nMAS UM DIA SENTI MEU PECADO E VI SOBRE MIM A ESPADA DA LEI; APRESSADO FUGI, EM JESUS ME ESCONDI E ABRIGO SEGURO NELE ACHEI.\n\nCORO FOI NA CRUZ, FOI NA CRUZ ONDE UM DIA EU VI MEU PECADO CASTIGADO EM JESUS; FOI ALI PELA FÉ QUE MEUS OLHOS ABRI E AGORA ME ALEGRO EM SUA LUZ.\n\nQUÃO DITOSO, ENTÃO, ESSE MEU CORAÇÃO CONHECENDO O EXCELSO AMOR QUE LEVOU MEU JESUS A SOFRER LÁ NA CRUZ PRA SALVAR UM TÃO POBRE PECADOR.', 'OH! QUÃO CEGO ANDEI E PERDIDO VAGUEI LONGE, LONGE DO MEU SALVADOR; MAS DO CÉU ELE DESCEU E SEU SANGUE VERTEU PRA SALVAR UM TÃO POBRE PECADOR. CORO FOI NA CRUZ, FOI NA CRUZ ONDE UM DIA EU VI MEU PECADO CASTIGADO EM JESUS; FOI ALI PELA FÉ QUE MEUS OLHOS ABRI E AGORA ME ALEGRO EM SUA LUZ. EU OUVIA FALAR DESSA GRAÇA SEM PAR QUE DO CÉU TROUXE O NOSSO JESUS, MAS EU SURDO ME FIZ, CONVERTER-ME NÃO QUIS AO SENHOR QUE POR MIM MORREU NA CRUZ. MAS UM DIA SENTI MEU PECADO E VI SOBRE MIM A ESPADA DA LEI; APRESSADO FUGI, EM JESUS ME ESCONDI E ABRIGO SEGURO NELE ACHEI. QUÃO DITOSO, ENTÃO, ESSE MEU CORAÇÃO CONHECENDO O EXCELSO AMOR QUE LEVOU MEU JESUS A SOFRER LÁ NA CRUZ PRA SALVAR UM TÃO POBRE PECADOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('111', '111 - O POVO QUE ANDAVA EM TREVAS', 'O POVO QUE ANDAVA EM TREVAS VIU UMA GRANDE LUZ, E SOBRE OS QUE ANDAVAM NA SOMBRA DA MORTE, RESPLANDECEU A LUZ; PORQUE UM MENINO NOS NASCEU, UM FILHO SE NOS DEU; O PRINCIPADO ESTÁ SOBRE OS SEUS OMBROS, ELE É JESUS, JESUS, A LUZ DO MUNDO. INSTRUMENTOS\n\nMARAVILHOSO CONSELHEIRO, SEU NOME SERÁ, DEUS FORTE, PAI DA ETERNIDADE, PRÍNCIPE DA PAZ; DO PRINCIPADO E DA PAZ NÃO HAVERÁ FIM E SOBRE O TRONO DE DAVI REINARÁ JESUS, JESUS, A LUZ DO MUNDO.', 'O POVO QUE ANDAVA EM TREVAS VIU UMA GRANDE LUZ, E SOBRE OS QUE ANDAVAM NA SOMBRA DA MORTE, RESPLANDECEU A LUZ; PORQUE UM MENINO NOS NASCEU, UM FILHO SE NOS DEU; O PRINCIPADO ESTÁ SOBRE OS SEUS OMBROS, ELE É JESUS, JESUS, A LUZ DO MUNDO. INSTRUMENTOS MARAVILHOSO CONSELHEIRO, SEU NOME SERÁ, DEUS FORTE, PAI DA ETERNIDADE, PRÍNCIPE DA PAZ; DO PRINCIPADO E DA PAZ NÃO HAVERÁ FIM E SOBRE O TRONO DE DAVI REINARÁ JESUS, JESUS, A LUZ DO MUNDO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('112', '112 - UMA LUZ BRILHOU EM MEU CAMINHO', 'UMA LUZ BRILHOU EM MEU CAMINHO, QUANDO EU IA TRISTE E SOZINHO; QUANDO EU OLHEI PRA CRUZ, NELA EU VI JESUS, FOI UM MINUTO SÓ DO SEU OLHAR.\n\nCORO FOI UM MINUTO SÓ, UM MINUTO SÓ, FOI UM MINUTO SÓ DO SEU OLHAR; TUDO EM MIM MUDOU, TUDO EM MIM CANTOU, FOI UM MINUTO SÓ DO SEU OLHAR.\n\nJESUS MARCOU A MINHA VIDA, NUNCA MAIS EU SEREI O MESMO; FOI SEU DIVINO OLHAR QUE ME ENSINOU A AMAR, FOI UM MINUTO SÓ DO SEU OLHAR.\n\nCORO FOI UM MINUTO SÓ, UM MINUTO SÓ, FOI UM MINUTO SÓ DO SEU OLHAR; TUDO EM MIM MUDOU, TUDO EM MIM CANTOU, FOI UM MINUTO SÓ DO SEU OLHAR.', 'UMA LUZ BRILHOU EM MEU CAMINHO, QUANDO EU IA TRISTE E SOZINHO; QUANDO EU OLHEI PRA CRUZ, NELA EU VI JESUS, FOI UM MINUTO SÓ DO SEU OLHAR. CORO FOI UM MINUTO SÓ, UM MINUTO SÓ, FOI UM MINUTO SÓ DO SEU OLHAR; TUDO EM MIM MUDOU, TUDO EM MIM CANTOU, FOI UM MINUTO SÓ DO SEU OLHAR. JESUS MARCOU A MINHA VIDA, NUNCA MAIS EU SEREI O MESMO; FOI SEU DIVINO OLHAR QUE ME ENSINOU A AMAR, FOI UM MINUTO SÓ DO SEU OLHAR.',  1);
