INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('114', '114 - AS RIQUEZAS DO MUNDO', 'AS RIQUEZAS DO MUNDO NADA VALEM PRA MIM, POIS, JESUS, NO TEU REINO DÁS-ME VIDA SEM FIM; E NO LIVRO DA VIDA QUE CONSERVAS AÍ, CERTO ESTOU QUE MEU NOME FOI ESCRITO POR TI.\n\nCORO FOI ESCRITO POR TI O MEU NOME NO CÉU; SIM, NO LIVRO DA VIDA, FOI ESCRITO POR TI.\n\nMEUS PECADOS SÃO MUITOS, COMO A AREIA DO MAR; MAS JESUS ME REVELA SUA GRAÇA SEM PAR; VEIO PARA SALVAR-ME, O SEU SANGUE VERTEU, E AS DELÍCIAS ETERNAS ELE ME PROMETEU.\n\nCORO FOI ESCRITO POR TI O MEU NOME NO CÉU; SIM, NO LIVRO DA VIDA, FOI ESCRITO POR TI.\n\nÓ CIDADE FESTIVA, REFULGENTE DE LUZ, ÉS MORADA DOS SANTOS E FIÉIS DE JESUS! SÓ VERÁ TUA GLÓRIA O QUE CRER NO SENHOR, CUJA VIDA FOI SALVA PELO SEU REDENTOR.\n\nCORO FOI ESCRITO POR TI O MEU NOME NO CÉU; SIM, NO LIVRO DA VIDA, FOI ESCRITO POR TI.', 'AS RIQUEZAS DO MUNDO NADA VALEM PRA MIM, POIS, JESUS, NO TEU REINO DÁS-ME VIDA SEM FIM; E NO LIVRO DA VIDA QUE CONSERVAS AÍ, CERTO ESTOU QUE MEU NOME FOI ESCRITO POR TI. CORO FOI ESCRITO POR TI O MEU NOME NO CÉU; SIM, NO LIVRO DA VIDA, FOI ESCRITO POR TI. MEUS PECADOS SÃO MUITOS, COMO A AREIA DO MAR; MAS JESUS ME REVELA SUA GRAÇA SEM PAR; VEIO PARA SALVAR-ME, O SEU SANGUE VERTEU, E AS DELÍCIAS ETERNAS ELE ME PROMETEU. Ó CIDADE FESTIVA, REFULGENTE DE LUZ, ÉS MORADA DOS SANTOS E FIÉIS DE JESUS! SÓ VERÁ TUA GLÓRIA O QUE CRER NO SENHOR, CUJA VIDA FOI SALVA PELO SEU REDENTOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('115', '115 - GRAÇAS DOU, Ó SENHOR', 'GRAÇAS DOU, Ó SENHOR, AINDA QUE TE IRASTE CONTRA MIM, TUA IRA SE RETIROU, TU ME CONSOLASTE. EIS QUE DEUS É SALVAÇÃO, CONFIAREI, E NÃO TEMEREI; O SENHOR, MINHA FORÇA E O MEU LOUVOR; SE TORNOU MINHA SALVAÇÃO.\n\nCORO (4X) COM ALEGRIA TIRAREIS ÁGUAS DAS FONTES DA SALVAÇÃO. INSTRUMENTOS FINAL: GRAÇAS DOU, Ó SENHOR.', 'GRAÇAS DOU, Ó SENHOR, AINDA QUE TE IRASTE CONTRA MIM, TUA IRA SE RETIROU, TU ME CONSOLASTE. EIS QUE DEUS É SALVAÇÃO, CONFIAREI, E NÃO TEMEREI; O SENHOR, MINHA FORÇA E O MEU LOUVOR; SE TORNOU MINHA SALVAÇÃO. CORO  COM ALEGRIA TIRAREIS ÁGUAS DAS FONTES DA SALVAÇÃO. INSTRUMENTOS  GRAÇAS DOU, Ó SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('116', '116 - ACHEI SALVAÇÃO', 'ACHEI SALVAÇÃO EM JESUS, MEU SENHOR, PERDÃO, LIBERDADE ENCONTREI; SUA ALMA RESIDE EM MEU CORAÇÃO E NO SEU AMOR TENHO PAZ.\n\nCORO PAZ DO SENHOR, CÉLICA PAZ, PAZ QUE ESTE MUNDO NÃO DÁ; QUANDO PENSO NA LUZ QUE CENTELHA DA CRUZ, TENHO PAZ, UMA CÉLICA PAZ.\n\nA CRISTO, MEU REI, EU DAREI UMA CANÇÃO TALENTO, SERVIÇO E LOUVOR; CONTENTE AOS SEUS PÉS, MEU FIEL CORAÇÃO DIRÁ: TENHO PAZ NO SENHOR.\n\nCORO PAZ DO SENHOR, CÉLICA PAZ, PAZ QUE ESTE MUNDO NÃO DÁ; QUANDO PENSO NA LUZ QUE CENTELHA DA CRUZ, TENHO PAZ, UMA CÉLICA PAZ.', 'ACHEI SALVAÇÃO EM JESUS, MEU SENHOR, PERDÃO, LIBERDADE ENCONTREI; SUA ALMA RESIDE EM MEU CORAÇÃO E NO SEU AMOR TENHO PAZ. CORO PAZ DO SENHOR, CÉLICA PAZ, PAZ QUE ESTE MUNDO NÃO DÁ; QUANDO PENSO NA LUZ QUE CENTELHA DA CRUZ, TENHO PAZ, UMA CÉLICA PAZ. A CRISTO, MEU REI, EU DAREI UMA CANÇÃO TALENTO, SERVIÇO E LOUVOR; CONTENTE AOS SEUS PÉS, MEU FIEL CORAÇÃO DIRÁ: TENHO PAZ NO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('117', '117 - COMO DESISTIR DESTE GRANDE AMOR?', 'COMO DESISTIR DESTE GRANDE AMOR? SE NÃO HÁ OUTRO AMOR IGUAL; NÃO DESISTIREI, PRA ONDE IREI? NÃO POSSO PARAR NEM DESANIMAR, TENS ME DADO FORÇAS PRA VENCER; NÃO DESISTIREI, PRA ONDE IREI?\n\nSENHOR, TU ÉS A PORÇÃO DA MINHA HERANÇA, DO MEU CÁLICE; SENHOR, ÉS MEU AMOR, PRA ONDE IREI, SE SÓ TU ÉS MEU SALVADOR? INSTRUMENTOS\n\nCOMO DESISTIR DESTE GRANDE AMOR? SE NÃO HÁ OUTRO AMOR IGUAL; NÃO DESISTIREI, PRA ONDE IREI? NÃO POSSO PARAR NEM DESANIMAR, TENS ME DADO FORÇAS PRA VENCER; NÃO DESISTIREI, PRA ONDE IREI?\n\nSENHOR, TU ÉS A PORÇÃO DA MINHA HERANÇA, DO MEU CÁLICE; SENHOR, ÉS MEU AMOR, PRA ONDE IREI, SE SÓ TU ÉS MEU SALVADOR? FINAL: ÉS MINHA HERANÇA, SENHOR.', 'COMO DESISTIR DESTE GRANDE AMOR? SE NÃO HÁ OUTRO AMOR IGUAL; NÃO DESISTIREI, PRA ONDE IREI? NÃO POSSO PARAR NEM DESANIMAR, TENS ME DADO FORÇAS PRA VENCER; NÃO DESISTIREI, PRA ONDE IREI? SENHOR, TU ÉS A PORÇÃO DA MINHA HERANÇA, DO MEU CÁLICE; SENHOR, ÉS MEU AMOR, PRA ONDE IREI, SE SÓ TU ÉS MEU SALVADOR? INSTRUMENTOS SENHOR, TU ÉS A PORÇÃO DA MINHA HERANÇA, DO MEU CÁLICE; SENHOR, ÉS MEU AMOR, PRA ONDE IREI, SE SÓ TU ÉS MEU SALVADOR?  ÉS MINHA HERANÇA, SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('118', '118 - Ó SENHOR, DEUS DE ISRAEL (EDIFIQUEI UMA CASA)', 'Ó SENHOR, DEUS DE ISRAEL! NO CÉU E NA TERRA (NÃO HÁ DEUS) NÃO HÁ DEUS COMO TU QUE GUARDAS A ALIANÇA E A MISERICÓRDIA AOS TEUS SERVOS QUE ANDAM DIANTE DE TI.\n\nCORO EDIFIQUEI UMA CASA PARA TUA MORADA, UM LUGAR PARA A TUA ETERNA HABITAÇÃO.\n\nOUVE, POIS, DESDE O CÉU, A CADA ORAÇÃO E PERDOA (E PERDOA) O TEU POVO QUANDO, ARREPENDIDO, CONFESSAR O TEU NOME E BUSCAR TUA FACE NESTE LUGAR.\n\nCORO EDIFIQUEI UMA CASA PARA TUA MORADA, UM LUGAR PARA A TUA ETERNA HABITAÇÃO.\n\nVEM, SENHOR, ACEITA E APERFEIÇOA A ADORAÇÃO QUE O TEU POVO SINCERAMENTE FAZ, COMO OFERTA SUAVE DE LOUVOR. INSTRUMENTOS COMO OFERTA SUAVE DE LOUVOR, COMO OFERTA SUAVE DE LOUVOR. (COMO OFERTA DE LOUVOR)', 'Ó SENHOR, DEUS DE ISRAEL! NO CÉU E NA TERRA (NÃO HÁ DEUS) NÃO HÁ DEUS COMO TU QUE GUARDAS A ALIANÇA E A MISERICÓRDIA AOS TEUS SERVOS QUE ANDAM DIANTE DE TI. CORO EDIFIQUEI UMA CASA PARA TUA MORADA, UM LUGAR PARA A TUA ETERNA HABITAÇÃO. OUVE, POIS, DESDE O CÉU, A CADA ORAÇÃO E PERDOA (E PERDOA) O TEU POVO QUANDO, ARREPENDIDO, CONFESSAR O TEU NOME E BUSCAR TUA FACE NESTE LUGAR. VEM, SENHOR, ACEITA E APERFEIÇOA A ADORAÇÃO QUE O TEU POVO SINCERAMENTE FAZ, COMO OFERTA SUAVE DE LOUVOR. INSTRUMENTOS COMO OFERTA SUAVE DE LOUVOR, COMO OFERTA SUAVE DE LOUVOR. (COMO OFERTA DE LOUVOR)',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('119', '119 - JESUS, NOME PRECIOSO', 'JESUS, NOME PRECIOSO, JESUS, REI E SENHOR, JESUS, POR ESTE NOME SOU MAIS QUE VENCEDOR!\n\nJESUS, LÍRIO DOS VALES, JESUS, FLOR DO JARDIM, JESUS, COM TEU AMOR VEM HOJE TOCAR EM MIM!\n\nUMA PALAVRA SÓ BASTA, SENHOR, E TODO O MAL SE VAI E TODA A DOR; UMA PALAVRA SÓ DO TEU PODER E EM NOME DE JESUS EU VOU VENCER!\n\nJESUS, NOME DE VIDA, JESUS, FILHO DE DEUS, A TI TUDO SE INCLINA NA TERRA E NOS CÉUS! (BIS)', 'JESUS, NOME PRECIOSO, JESUS, REI E SENHOR, JESUS, POR ESTE NOME SOU MAIS QUE VENCEDOR! JESUS, LÍRIO DOS VALES, JESUS, FLOR DO JARDIM, JESUS, COM TEU AMOR VEM HOJE TOCAR EM MIM! UMA PALAVRA SÓ BASTA, SENHOR, E TODO O MAL SE VAI E TODA A DOR; UMA PALAVRA SÓ DO TEU PODER E EM NOME DE JESUS EU VOU VENCER! JESUS, NOME DE VIDA, JESUS, FILHO DE DEUS, A TI TUDO SE INCLINA NA TERRA E NOS CÉUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('120', '120 - JESUS CRISTO MUDOU MEU VIVER', 'JESUS CRISTO MUDOU MEU VIVER (BIS) É A LUZ QUE ILUMINA MEU SER, SIM, JESUS CRISTO MUDOU MEU VIVER.\n\nDIFERENTE HOJE É O MEU CORAÇÃO (BIS) CRISTO DEU-ME PAZ E PERDÃO, SIM, DIFERENTE HOJE É O MEU CORAÇÃO.\n\nO AMOR QUE CONHECI EM CANÇÕES ME FALAVA DE ILUSÕES; HOJE TUDO É DIFERENTE, ISSO FALO A TODA A GENTE, QUE CRISTO SALVA O PECADOR.\n\nJESUS CRISTO MUDOU MEU VIVER (BIS) É A LUZ QUE ILUMINA MEU SER, SIM, JESUS CRISTO MUDOU MEU VIVER.', 'JESUS CRISTO MUDOU MEU VIVER  É A LUZ QUE ILUMINA MEU SER, SIM, JESUS CRISTO MUDOU MEU VIVER. DIFERENTE HOJE É O MEU CORAÇÃO  CRISTO DEU-ME PAZ E PERDÃO, SIM, DIFERENTE HOJE É O MEU CORAÇÃO. O AMOR QUE CONHECI EM CANÇÕES ME FALAVA DE ILUSÕES; HOJE TUDO É DIFERENTE, ISSO FALO A TODA A GENTE, QUE CRISTO SALVA O PECADOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('121', '121 - TUDO, Ó CRISTO, A TI ENTREGO', 'TUDO, Ó CRISTO, A TI ENTREGO, TUDO, SIM, POR TI DAREI; RESOLUTO, MAS SUBMISSO, SEMPRE, SEMPRE SEGUIREI.\n\nCORO TUDO ENTREGAREI, TUDO ENTREGAREI; SIM, POR TI, JESUS BENDITO, TUDO DEIXAREI.\n\nTUDO, Ó CRISTO, A TI ENTREGO, CORPO E ALMA, EIS-ME AQUI; ESTE MUNDO MAU RENEGO, Ó JESUS, ACEITA A MIM.\n\nCORO TUDO ENTREGAREI, TUDO ENTREGAREI; SIM, POR TI, JESUS BENDITO, TUDO DEIXAREI.\n\nTUDO, Ó CRISTO, A TI ENTREGO, OH! QUE GOZO MEU SENHOR! PAZ PERFEITA, PAZ COMPLETA, GLÓRIA, GLÓRIA AO SALVADOR.\n\nCORO TUDO ENTREGAREI, TUDO ENTREGAREI; SIM, POR TI, JESUS BENDITO, TUDO DEIXAREI.', 'TUDO, Ó CRISTO, A TI ENTREGO, TUDO, SIM, POR TI DAREI; RESOLUTO, MAS SUBMISSO, SEMPRE, SEMPRE SEGUIREI. CORO TUDO ENTREGAREI, TUDO ENTREGAREI; SIM, POR TI, JESUS BENDITO, TUDO DEIXAREI. TUDO, Ó CRISTO, A TI ENTREGO, CORPO E ALMA, EIS-ME AQUI; ESTE MUNDO MAU RENEGO, Ó JESUS, ACEITA A MIM. TUDO, Ó CRISTO, A TI ENTREGO, OH! QUE GOZO MEU SENHOR! PAZ PERFEITA, PAZ COMPLETA, GLÓRIA, GLÓRIA AO SALVADOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('122', '122 - AMO AO SENHOR PORQUE ELE ME OUVIU', 'AMO AO SENHOR PORQUE ELE ME OUVIU, INCLINOU OS SEUS OUVIDOS E ASSIM ME ATENDEU; O SENHOR, QUE É JUSTO E PIEDOSO, POSSO NELE CONFIAR, CERTAMENTE ME GUIARÁ.\n\nANDAREI PERANTE A SUA FACE, SOU SEU SERVO, POIS ME LIBERTOU; LOUVÁ-LO-EI ETERNAMENTE NOS ÁTRIOS DA SUA CASA, FAREI A DEUS SACRIFÍCIOS DE LOUVOR.\nBIS', 'AMO AO SENHOR PORQUE ELE ME OUVIU, INCLINOU OS SEUS OUVIDOS E ASSIM ME ATENDEU; O SENHOR, QUE É JUSTO E PIEDOSO, POSSO NELE CONFIAR, CERTAMENTE ME GUIARÁ. ANDAREI PERANTE A SUA FACE, SOU SEU SERVO, POIS ME LIBERTOU; LOUVÁ-LO-EI ETERNAMENTE NOS ÁTRIOS DA SUA CASA, FAREI A DEUS SACRIFÍCIOS DE LOUVOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('123', '123 - SEGUIR-TE-EI', 'SEGUIR-TE-EI POR ONDE QUER QUE EU VÁ, LOUVAR-TE-EI DE TODO O CORAÇÃO; ÉS PRECIOSO, AMADO E BOM JESUS, ÉS MINHA VIDA, ÉS MINHA LUZ.\nBIS\n\nTEU GRANDE AMOR POR MIM NÃO POSSO COMPARAR, POR ISSO EU TE LOUVO, SOFRESTE EM MEU LUGAR, POR ISSO EU TE LOUVO, MORRESTE PRA ME SALVAR. INSTRUMENTOS\n\nSEGUIR-TE-EI POR ONDE QUER QUE EU VÁ LOUVAR-TE-EI DE TODO O CORAÇÃO; ÉS PRECIOSO, AMADO E BOM JESUS, ÉS MINHA VIDA, ÉS MINHA LUZ.', 'SEGUIR-TE-EI POR ONDE QUER QUE EU VÁ, LOUVAR-TE-EI DE TODO O CORAÇÃO; ÉS PRECIOSO, AMADO E BOM JESUS, ÉS MINHA VIDA, ÉS MINHA LUZ. TEU GRANDE AMOR POR MIM NÃO POSSO COMPARAR, POR ISSO EU TE LOUVO, SOFRESTE EM MEU LUGAR, POR ISSO EU TE LOUVO, MORRESTE PRA ME SALVAR. INSTRUMENTOS SEGUIR-TE-EI POR ONDE QUER QUE EU VÁ LOUVAR-TE-EI DE TODO O CORAÇÃO; ÉS PRECIOSO, AMADO E BOM JESUS, ÉS MINHA VIDA, ÉS MINHA LUZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('124', '124 - PEREGRINANDO POR SOBRE OS MONTES', 'PEREGRINANDO POR SOBRE OS MONTES E PELOS VALES, SEMPRE NA LUZ; CRISTO PROMETE NUNCA DEIXAR-ME: “EIS-ME CONVOSCO”, DISSE JESUS.\n\nCORO BRILHO CELESTE, BRILHO CELESTE, ENCHE A MINH’ALMA A GLÓRIA DO CÉU! ALELUIA! SIGO CANTANDO, DANDO LOUVORES, CRISTO É MEU.\n\nSOMBRAS À RODA, SOMBRAS EM CIMA, O SALVADOR NÃO HÃO DE OCULTAR; ELE É A LUZ QUE NUNCA SE APAGA, JUNTO AO SEU LADO SEMPRE HEI DE ANDAR.\n\nCORO BRILHO CELESTE, BRILHO CELESTE, ENCHE A MINH’ALMA A GLÓRIA DO CÉU! ALELUIA! SIGO CANTANDO, DANDO LOUVORES, CRISTO É MEU.\n\nA LUZ BENDITA ME VAI CERCANDO, PASSOS AVANTE PARA A MANSÃO! MAIS E MAIS PERTO SEGUINDO O MESTRE, POSSUO O GOZO DA SALVAÇÃO.\n\nCORO BRILHO CELESTE, BRILHO CELESTE, ENCHE A MINH’ALMA A GLÓRIA DO CÉU! ALELUIA! SIGO CANTANDO, DANDO LOUVORES, CRISTO É MEU.', 'PEREGRINANDO POR SOBRE OS MONTES E PELOS VALES, SEMPRE NA LUZ; CRISTO PROMETE NUNCA DEIXAR-ME: EIS-ME CONVOSCO, DISSE JESUS. CORO BRILHO CELESTE, BRILHO CELESTE, ENCHE A MINH’ALMA A GLÓRIA DO CÉU! ALELUIA! SIGO CANTANDO, DANDO LOUVORES, CRISTO É MEU. SOMBRAS À RODA, SOMBRAS EM CIMA, O SALVADOR NÃO HÃO DE OCULTAR; ELE É A LUZ QUE NUNCA SE APAGA, JUNTO AO SEU LADO SEMPRE HEI DE ANDAR. A LUZ BENDITA ME VAI CERCANDO, PASSOS AVANTE PARA A MANSÃO! MAIS E MAIS PERTO SEGUINDO O MESTRE, POSSUO O GOZO DA SALVAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('125', '125 - PELA FÉ EU CAMINHO', 'PELA FÉ EU CAMINHO OLHANDO SÓ PRA JESUS, MINHA MÃO PUS NA SUA E ASSIM SEGURO VOU. COMO A LUZ DA AURORA, MINHA VEREDA VAI BRILHANDO MAIS E MAIS, ATÉ O DIA, DIA PERFEITO, CHEGAR.\nBIS', 'PELA FÉ EU CAMINHO OLHANDO SÓ PRA JESUS, MINHA MÃO PUS NA SUA E ASSIM SEGURO VOU. COMO A LUZ DA AURORA, MINHA VEREDA VAI BRILHANDO MAIS E MAIS, ATÉ O DIA, DIA PERFEITO, CHEGAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('126', '126 - QUERO PROSSEGUIR', 'QUERO PROSSEGUIR NESTA JORNADA, EU FUI CHAMADO, COM LAÇOS DE AMOR ABENÇOADO, TENHO MEU JESUS SEMPRE AO MEU LADO.\n\nNÃO TEMER, PROSSEGUIR, E VENCER TODA LUTA, ENFIM; MEU DEUS ESTÁ COMIGO, SINTO SUA PRESENÇA; DEU-ME CONFIANÇA, ESTE É MEU SENHOR! AMIGO VERDADEIRO, SINCERO E AMOROSO, LEAL E COMPANHEIRO, JESUS, TENS MEU AMOR.\nBIS', 'QUERO PROSSEGUIR NESTA JORNADA, EU FUI CHAMADO, COM LAÇOS DE AMOR ABENÇOADO, TENHO MEU JESUS SEMPRE AO MEU LADO. NÃO TEMER, PROSSEGUIR, E VENCER TODA LUTA, ENFIM; MEU DEUS ESTÁ COMIGO, SINTO SUA PRESENÇA; DEU-ME CONFIANÇA, ESTE É MEU SENHOR! AMIGO VERDADEIRO, SINCERO E AMOROSO, LEAL E COMPANHEIRO, JESUS, TENS MEU AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('127', '127 - ESTOU A CAMINHAR', 'ESTOU A CAMINHAR PARA O MEU LAR NO CÉU; SOU PEREGRINO AQUI, O VIVER ETERNO É MEU. DE GRAÇA RECEBI DE DEUS A SALVAÇÃO, BENDITO O SANGUE QUE ME LAVOU, CREIO EM MEU CORAÇÃO.', 'ESTOU A CAMINHAR PARA O MEU LAR NO CÉU; SOU PEREGRINO AQUI, O VIVER ETERNO É MEU. DE GRAÇA RECEBI DE DEUS A SALVAÇÃO, BENDITO O SANGUE QUE ME LAVOU, CREIO EM MEU CORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('128', '128 - QUANDO A MÃO DE DEUS EM MIM VEIO TOCAR', 'QUANDO A MÃO DE DEUS EM MIM VEIO TOCAR, ALEGRIA TRANSBORDOU MEU VIVER; SUA VOZ SUAVE E MEIGA EM MIM SOOU, PUDE ENTÃO SENTIR O SEU IMENSO AMOR.\n\nCORO (2X) SOU FELIZ, SOU FELIZ EM JESUS, SIM, EU SOU FELIZ.\n\nQUANDO O TOQUE DO ESPÍRITO SENTI, DEU-ME PAZ E TRANSFORMOU A MINHA VIDA; COMO O SOL NO SEU ALTO ESPLENDOR, CRISTO INUNDOU-ME COM O SEU AMOR.\n\nCORO (2X) SOU FELIZ, SOU FELIZ EM JESUS, SIM, EU SOU FELIZ. FINAL: SOU FELIZ EM JESUS, POSSO ENTÃO DIZER: FELIZ, SOU FELIZ.', 'QUANDO A MÃO DE DEUS EM MIM VEIO TOCAR, ALEGRIA TRANSBORDOU MEU VIVER; SUA VOZ SUAVE E MEIGA EM MIM SOOU, PUDE ENTÃO SENTIR O SEU IMENSO AMOR. CORO  SOU FELIZ, SOU FELIZ EM JESUS, SIM, EU SOU FELIZ. QUANDO O TOQUE DO ESPÍRITO SENTI, DEU-ME PAZ E TRANSFORMOU A MINHA VIDA; COMO O SOL NO SEU ALTO ESPLENDOR, CRISTO INUNDOU-ME COM O SEU AMOR. CORO  SOU FELIZ, SOU FELIZ EM JESUS, SIM, EU SOU FELIZ.  SOU FELIZ EM JESUS, POSSO ENTÃO DIZER: FELIZ, SOU FELIZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('129', '129 - FELICIDADE É TER CRISTO AO MEU LADO', 'FELICIDADE É TER CRISTO AO MEU LADO E POR ELE SER GUARDADO, TER MEU NOME ESCRITO NO CÉU; MEU NOME ESTÁ ESCRITO NAS MÃOS DO MEU JESUS, QUANDO MORREU ALI NA CRUZ, E ASSIM MUI FELIZ SOU EU.\n\nCORO FELICIDADE É POSSUIR O MEU JESUS E TODO DIA LEVAR A CRUZ ATÉ O DIA EM QUE ELE VOLTAR; FELICIDADE É SER LAVADO NO SANGUE DE JESUS, E SEMPRE ANDAR NA SUA LUZ, ATÉ O DIA EM QUE ELE VOLTAR.\n\nFELICIDADE É IR MORAR NUMA CIDADE, ONDE HÁ PAZ E VERDADE, É JESUS QUEM MORA LÁ! EU SOU FELIZ E EM JESUS EU TENHO ALGO, E POR ELE JÁ SOU SALVO, ELE PODE ME GUARDAR!\n\nCORO FELICIDADE É POSSUIR O MEU JESUS E TODO DIA LEVAR A CRUZ ATÉ O DIA EM QUE ELE VOLTAR; FELICIDADE É SER LAVADO NO SANGUE DE JESUS, E SEMPRE ANDAR NA SUA LUZ, ATÉ O DIA EM QUE ELE VOLTAR.', 'FELICIDADE É TER CRISTO AO MEU LADO E POR ELE SER GUARDADO, TER MEU NOME ESCRITO NO CÉU; MEU NOME ESTÁ ESCRITO NAS MÃOS DO MEU JESUS, QUANDO MORREU ALI NA CRUZ, E ASSIM MUI FELIZ SOU EU. CORO FELICIDADE É POSSUIR O MEU JESUS E TODO DIA LEVAR A CRUZ ATÉ O DIA EM QUE ELE VOLTAR; FELICIDADE É SER LAVADO NO SANGUE DE JESUS, E SEMPRE ANDAR NA SUA LUZ, ATÉ O DIA EM QUE ELE VOLTAR. FELICIDADE É IR MORAR NUMA CIDADE, ONDE HÁ PAZ E VERDADE, É JESUS QUEM MORA LÁ! EU SOU FELIZ E EM JESUS EU TENHO ALGO, E POR ELE JÁ SOU SALVO, ELE PODE ME GUARDAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('130', '130 - TENHO GRANDE GOZO', 'TENHO GRANDE GOZO DESDE QUE REPOUSO NESTE CRISTO AMADO QUE POR MIM FOI DADO; QUE DESCEU DA GLÓRIA, SUPORTOU POBREZA PRA ME DAR VITÓRIA E ME DAR RIQUEZA.\nBIS\n\nQUANTA DOR SENTIA LÁ NA CRUZ SOMBRIA QUANDO PENDURADO PELO MEU PECADO; NESSA CRUZ ERGUIDA, OH! QUE AMOR PROFUNDO! ENTREGOU SUA VIDA PRA DAR VIDA AO MUNDO.\nBIS\n\nMEU DESEJO AGORA, PELA VIDA AFORA, É SEGUIR AO LADO DESSE CRISTO AMADO; QUERO, DIA A DIA, MAIS E MAIS AMÁ-LO E, COM ALEGRIA, PARA SEMPRE HONRÁ-LO.\nBIS', 'TENHO GRANDE GOZO DESDE QUE REPOUSO NESTE CRISTO AMADO QUE POR MIM FOI DADO; QUE DESCEU DA GLÓRIA, SUPORTOU POBREZA PRA ME DAR VITÓRIA E ME DAR RIQUEZA. QUANTA DOR SENTIA LÁ NA CRUZ SOMBRIA QUANDO PENDURADO PELO MEU PECADO; NESSA CRUZ ERGUIDA, OH! QUE AMOR PROFUNDO! ENTREGOU SUA VIDA PRA DAR VIDA AO MUNDO. MEU DESEJO AGORA, PELA VIDA AFORA, É SEGUIR AO LADO DESSE CRISTO AMADO; QUERO, DIA A DIA, MAIS E MAIS AMÁ-LO E, COM ALEGRIA, PARA SEMPRE HONRÁ-LO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('131', '131 - ÀS VEZES, ALGUÉM ME PERGUNTA', 'ÀS VEZES, ALGUÉM ME PERGUNTA PORQUE VIVO ALEGRE E SEMPRE A CANTAR; RESPONDO: SOU MUITO FELIZ PORQUE JESUS CRISTO VEIO EM MIM MORAR.\n\nCORO ACHEI A FELICIDADE, EU NÃO ESPERAVA QUE A FOSSE ENCONTRAR; MAS UM DIA ENCONTREI COM JESUS E A FELICIDADE VEIO EM MIM MORAR. ÀS VEZES, O MEU CORAÇÃO FICA PERTURBADO, PERDENDO A PAZ; AOS PÉS DO MEU SALVADOR, ACHEI O SEGREDO DA PERFEITA PAZ.', 'ÀS VEZES, ALGUÉM ME PERGUNTA PORQUE VIVO ALEGRE E SEMPRE A CANTAR; RESPONDO: SOU MUITO FELIZ PORQUE JESUS CRISTO VEIO EM MIM MORAR. CORO ACHEI A FELICIDADE, EU NÃO ESPERAVA QUE A FOSSE ENCONTRAR; MAS UM DIA ENCONTREI COM JESUS E A FELICIDADE VEIO EM MIM MORAR. ÀS VEZES, O MEU CORAÇÃO FICA PERTURBADO, PERDENDO A PAZ; AOS PÉS DO MEU SALVADOR, ACHEI O SEGREDO DA PERFEITA PAZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('132', '132 - JÁ NÃO ESTOU SOZINHO', 'JÁ NÃO ESTOU SOZINHO, AGORA SOU FELIZ COM CRISTO NO MEU CORAÇÃO; ELE É MEU GRANDE AMIGO, NÃO TEMO O PERIGO, COM CRISTO NO MEU CORAÇÃO.\n\nCORO OH! EU SOU FELIZ, AGORA VOU CANTAR, POIS CONTENTE ESTOU COM MEU SALVADOR, FELIZ AGORA ENTÃO EU VIVEREI.\n\nJESUS, HÁ MUITO TEMPO MORREU EM UMA CRUZ, MORREU SÓ PARA ME SALVAR; SEU SANGUE DERRAMOU, LAVANDO OS MEUS PECADOS, E DESTA FORMA AGORA SALVO ESTOU.\n\nCORO OH! EU SOU FELIZ, AGORA VOU CANTAR, POIS CONTENTE ESTOU COM MEU SALVADOR, FELIZ AGORA ENTÃO EU VIVEREI. (BIS)', 'JÁ NÃO ESTOU SOZINHO, AGORA SOU FELIZ COM CRISTO NO MEU CORAÇÃO; ELE É MEU GRANDE AMIGO, NÃO TEMO O PERIGO, COM CRISTO NO MEU CORAÇÃO. CORO OH! EU SOU FELIZ, AGORA VOU CANTAR, POIS CONTENTE ESTOU COM MEU SALVADOR, FELIZ AGORA ENTÃO EU VIVEREI. JESUS, HÁ MUITO TEMPO MORREU EM UMA CRUZ, MORREU SÓ PARA ME SALVAR; SEU SANGUE DERRAMOU, LAVANDO OS MEUS PECADOS, E DESTA FORMA AGORA SALVO ESTOU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('133', '133 - EU ESTAVA SÓ, SEM O SENHOR', 'EU ESTAVA SÓ, SEM O SENHOR, SEM PAZ E SEM NENHUM VALOR E NEM MESMO QUERIA COMPREENDER O TEU GRANDE AMOR; ERA ASSIM O MEU VIVER.\n\nMAS UM DIA TUA VOZ OUVI, TUA PAZ EU PUDE SENTIR, O TEU AMOR ME TRANSFORMOU, NOVA CRIATURA SOU. INSTRUMENTOS\n\nMINHA VIDA ESTÁ NO SENHOR, POIS DE TI DEPENDENTE SOU, ANTES MESMO DE TUDO EXISTIR ESCOLHESTE A MIM, E ASSIM ME ENTREGO A TI. TUA VOZ NÃO ESTRANHAREI, TÃO SUAVE EU A OUVIREI, POIS DE TI SOU E TU ÉS MEU, ALELUIA! GLÓRIA A DEUS!', 'EU ESTAVA SÓ, SEM O SENHOR, SEM PAZ E SEM NENHUM VALOR E NEM MESMO QUERIA COMPREENDER O TEU GRANDE AMOR; ERA ASSIM O MEU VIVER. MAS UM DIA TUA VOZ OUVI, TUA PAZ EU PUDE SENTIR, O TEU AMOR ME TRANSFORMOU, NOVA CRIATURA SOU. INSTRUMENTOS MINHA VIDA ESTÁ NO SENHOR, POIS DE TI DEPENDENTE SOU, ANTES MESMO DE TUDO EXISTIR ESCOLHESTE A MIM, E ASSIM ME ENTREGO A TI. TUA VOZ NÃO ESTRANHAREI, TÃO SUAVE EU A OUVIREI, POIS DE TI SOU E TU ÉS MEU, ALELUIA! GLÓRIA A DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('134', '134 - QUE SEGURANÇA TENHO EM JESUS', 'QUE SEGURANÇA TENHO EM JESUS, HÁ NELE GOZO, PAZ, VIDA E LUZ; COM CRISTO HERDEIRO, DEUS ME ACEITOU MEDIANTE O FILHO QUE ME SALVOU. CORO (2X) CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM.\n\nINTEIRAMENTE ME SUBMETI, PLENA ALEGRIA NELE SENTI; ANJOS DESCENDO TRAZEM DOS CÉUS PROVAS DA GRAÇA QUE VEM DE DEUS. CORO (2X) CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM.\n\nFIRMADO EM CRISTO, NO SEU AMOR, ESTOU CONTENTE EM MEU SALVADOR; ESPERANÇOSO HEI DE VIVER POR JESUS CRISTO, POR SEU PODER! CORO (2X) CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM.', 'QUE SEGURANÇA TENHO EM JESUS, HÁ NELE GOZO, PAZ, VIDA E LUZ; COM CRISTO HERDEIRO, DEUS ME ACEITOU MEDIANTE O FILHO QUE ME SALVOU. CORO  CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM. INTEIRAMENTE ME SUBMETI, PLENA ALEGRIA NELE SENTI; ANJOS DESCENDO TRAZEM DOS CÉUS PROVAS DA GRAÇA QUE VEM DE DEUS. CORO  CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM. FIRMADO EM CRISTO, NO SEU AMOR, ESTOU CONTENTE EM MEU SALVADOR; ESPERANÇOSO HEI DE VIVER POR JESUS CRISTO, POR SEU PODER! CORO  CONTO ESTA HISTÓRIA CANTANDO ASSIM: NA CRUZ FOI CRISTO MORTO POR MIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('135', '135 - EM MEU CORAÇÃO', 'EM MEU CORAÇÃO REINA UMA DOCE PAZ DE ALGUÉM QUE POR MIM MORREU PRA ME DAR A SALVAÇÃO.\n\nESSE ALGUÉM, EU SEI, É JESUS, O REI, QUE SE FEZ PEQUENO EM MEU LUGAR PARA A VIDA ETERNA ME DAR. INSTRUMENTOS GLÓRIA E ALELUIA A JESUS CANTAREI COM GRATIDÃO E LOUVOR, PARA TODO O SEMPRE, AMÉM!\nBIS', 'EM MEU CORAÇÃO REINA UMA DOCE PAZ DE ALGUÉM QUE POR MIM MORREU PRA ME DAR A SALVAÇÃO. ESSE ALGUÉM, EU SEI, É JESUS, O REI, QUE SE FEZ PEQUENO EM MEU LUGAR PARA A VIDA ETERNA ME DAR. INSTRUMENTOS GLÓRIA E ALELUIA A JESUS CANTAREI COM GRATIDÃO E LOUVOR, PARA TODO O SEMPRE, AMÉM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('136', '136 - ESTA PAZ QUE SINTO EM MINH’ALMA', 'ESTA PAZ QUE SINTO EM MINH’ALMA NÃO É PORQUE TUDO EM MIM VAI BEM; ESTA PAZ QUE SINTO EM MINH’ALMA É PORQUE EU AMO AO MEU SENHOR.\n\nCORO NÃO OLHO AS CIRCUNSTÂNCIAS, (NÃO, NÃO, NÃO) VARÕES SÓ OLHO SEU AMOR (SEU GRANDE AMOR) VARÕES NÃO ME GUIO POR VISTA, ALEGRE ESTOU.\nBIS\n\nESTE GOZO QUE SINTO EM MINH’ALMA NÃO É PORQUE OLHO AO MEU REDOR; ESTE GOZO QUE SINTO EM MINH’ALMA É PORQUE EU AMO AO MEU SENHOR.\n\nCORO NÃO OLHO AS CIRCUNSTÂNCIAS, (NÃO, NÃO, NÃO) VARÕES SÓ OLHO SEU AMOR (SEU GRANDE AMOR) VARÕES NÃO ME GUIO POR VISTA, ALEGRE ESTOU.\nBIS\n\nAINDA QUE A TERRA NÃO FLORESÇA, E A VIDE NÃO DÊ O SEU FRUTO; AINDA QUE OS MONTES SE LANCEM AO MAR, E A TERRA TREMA, HEI DE CONFIAR.\n\nCORO NÃO OLHO AS CIRCUNSTÂNCIAS, (NÃO, NÃO, NÃO) VARÕES SÓ OLHO SEU AMOR (SEU GRANDE AMOR) VARÕES NÃO ME GUIO POR VISTA, ALEGRE ESTOU.\nBIS\n\nALELUIA, OH! GLÓRIA, ALELUIA, ALELUIA DOU AO MEU SENHOR. ALELUIA, OH! GLÓRIA, ALELUIA, ALELUIA DOU AO MEU SENHOR.\n\nCORO NÃO OLHO AS CIRCUNSTÂNCIAS, (NÃO, NÃO, NÃO) VARÕES SÓ OLHO SEU AMOR (SEU GRANDE AMOR) VARÕES NÃO ME GUIO POR VISTA, ALEGRE ESTOU.\nBIS', 'ESTA PAZ QUE SINTO EM MINH’ALMA NÃO É PORQUE TUDO EM MIM VAI BEM; ESTA PAZ QUE SINTO EM MINH’ALMA É PORQUE EU AMO AO MEU SENHOR. CORO NÃO OLHO AS CIRCUNSTÂNCIAS, (NÃO, NÃO, NÃO) VARÕES SÓ OLHO SEU AMOR (SEU GRANDE AMOR) VARÕES NÃO ME GUIO POR VISTA, ALEGRE ESTOU. ESTE GOZO QUE SINTO EM MINH’ALMA NÃO É PORQUE OLHO AO MEU REDOR; ESTE GOZO QUE SINTO EM MINH’ALMA É PORQUE EU AMO AO MEU SENHOR. AINDA QUE A TERRA NÃO FLORESÇA, E A VIDE NÃO DÊ O SEU FRUTO; AINDA QUE OS MONTES SE LANCEM AO MAR, E A TERRA TREMA, HEI DE CONFIAR. ALELUIA, OH! GLÓRIA, ALELUIA, ALELUIA DOU AO MEU SENHOR. ALELUIA, OH! GLÓRIA, ALELUIA, ALELUIA DOU AO MEU SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('137', '137 - QUE DOCE VOZ TEM MEU SENHOR', 'QUE DOCE VOZ TEM MEU SENHOR! VOZ DE AMOR, TÃO TERNA E GRACIOSA, QUE ENCHE O CORAÇÃO, DÁ CONSOLAÇÃO QUE SÓ O CRENTE GOZA.\n\nCORO QUAL MAIOR PRAZER QUE LHE OUVIR DIZER: “VEM, MEU FILHO, VEM ESCUTAR O QUE EU FIZ POR TI, TUDO O QUE SOFRI NA CRUZ PRA TE RESGATAR”?\n\nCHAMOU-ME NÃO SÓ UMA VEZ, TANTAS TÉ QUE EU TRISTE, HUMILHADO, PUDE A VOZ OUVIR, PUDE ENTÃO SAIR DAS GARRAS DO PECADO.\n\nCORO QUAL MAIOR PRAZER QUE LHE OUVIR DIZER: “VEM, MEU FILHO, VEM ESCUTAR O QUE EU FIZ POR TI, TUDO O QUE SOFRI NA CRUZ PRA TE RESGATAR”?\n\nJESUS NÃO ME DEIXA SOFRER, SUA VOZ ME ENSINA O CAMINHO DE VENCER O MAL, COM FIRMEZA TAL, QUE NUNCA ESTOU SOZINHO.', 'QUE DOCE VOZ TEM MEU SENHOR! VOZ DE AMOR, TÃO TERNA E GRACIOSA, QUE ENCHE O CORAÇÃO, DÁ CONSOLAÇÃO QUE SÓ O CRENTE GOZA. CORO QUAL MAIOR PRAZER QUE LHE OUVIR DIZER: VEM, MEU FILHO, VEM ESCUTAR O QUE EU FIZ POR TI, TUDO O QUE SOFRI NA CRUZ PRA TE RESGATAR? CHAMOU-ME NÃO SÓ UMA VEZ, TANTAS TÉ QUE EU TRISTE, HUMILHADO, PUDE A VOZ OUVIR, PUDE ENTÃO SAIR DAS GARRAS DO PECADO. JESUS NÃO ME DEIXA SOFRER, SUA VOZ ME ENSINA O CAMINHO DE VENCER O MAL, COM FIRMEZA TAL, QUE NUNCA ESTOU SOZINHO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('138', '138 - HÁ SEMPRE ALGUÉM QUE NÃO PROVOU', 'HÁ SEMPRE ALGUÉM QUE NÃO PROVOU O GRANDE AMOR DO MEU SENHOR; PORÉM EU SEI, BEM CERTO ESTOU, QUE ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR.\n\nCORO AO MEU REDOR DEUS SEMPRE ESTÁ, AO MEU REDOR, AO MEU REDOR, PRA ME GUIAR; COM SEU AMOR TÃO PURO E BOM, ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR.\n\nPOSSO PERDER TODOS OS MEUS BENS E UM BOM AMIGO ME FALTAR, PODE MEU PAI ME ABANDONAR, QUE ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR.\n\nCORO AO MEU REDOR DEUS SEMPRE ESTÁ, AO MEU REDOR, AO MEU REDOR, PRA ME GUIAR; COM SEU AMOR TÃO PURO E BOM, ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR.\n\nNÃO SEI CONTAR O QUE SENTI QUANDO JESUS ME REDIMIU; DESDE ENTÃO EU DESCOBRI QUE ELE ESTARÁ AO MEU REDOR QUANDO EU PARTIR.\n\nCORO AO MEU REDOR DEUS SEMPRE ESTÁ, AO MEU REDOR, AO MEU REDOR, PRA ME GUIAR; COM SEU AMOR TÃO PURO E BOM, ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR.', 'HÁ SEMPRE ALGUÉM QUE NÃO PROVOU O GRANDE AMOR DO MEU SENHOR; PORÉM EU SEI, BEM CERTO ESTOU, QUE ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR. CORO AO MEU REDOR DEUS SEMPRE ESTÁ, AO MEU REDOR, AO MEU REDOR, PRA ME GUIAR; COM SEU AMOR TÃO PURO E BOM, ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR. POSSO PERDER TODOS OS MEUS BENS E UM BOM AMIGO ME FALTAR, PODE MEU PAI ME ABANDONAR, QUE ELE ESTÁ SEMPRE AO MEU REDOR PRA ME GUIAR. NÃO SEI CONTAR O QUE SENTI QUANDO JESUS ME REDIMIU; DESDE ENTÃO EU DESCOBRI QUE ELE ESTARÁ AO MEU REDOR QUANDO EU PARTIR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('139', '139 - NÃO SEI POR QUE DE DEUS O AMOR', 'NÃO SEI POR QUE DE DEUS O AMOR A MIM SE REVELOU, PORQUE, A MIM, O SALVADOR, PRA SI ME RESGATOU.\n\nCORO MAS EU SEI EM QUEM TENHO CRIDO E ESTOU BEM CERTO QUE É PODEROSO PRA GUARDAR O MEU TESOURO ATÉ O DIA FINAL.\n\nIGNORO COMO O ESPÍRITO NOS CONVENCE DO MAL, REVELA A CRISTO, VERBO SEU, CONSOLADOR REAL.\n\nCORO MAS EU SEI EM QUEM TENHO CRIDO E ESTOU BEM CERTO QUE É PODEROSO PRA GUARDAR O MEU TESOURO ATÉ O DIA FINAL.\n\nNÃO SEI O QUE DE MAL OU BEM É DESTINADO A MIM, SE MAUS OU ÁUREOS DIAS VÊM, ATÉ DA VIDA O FIM?\n\nCORO MAS EU SEI EM QUEM TENHO CRIDO E ESTOU BEM CERTO QUE É PODEROSO PRA GUARDAR O MEU TESOURO ATÉ O DIA FINAL.\n\nE QUANDO VEM JESUS, NÃO SEI, SE BREVE OU TARDE VEM, MAS SEI QUE MEU SENHOR VIRÁ NA GLÓRIA QUE ELE TEM.\n\nCORO MAS EU SEI EM QUEM TENHO CRIDO E ESTOU BEM CERTO QUE É PODEROSO PRA GUARDAR O MEU TESOURO ATÉ O DIA FINAL.', 'NÃO SEI POR QUE DE DEUS O AMOR A MIM SE REVELOU, PORQUE, A MIM, O SALVADOR, PRA SI ME RESGATOU. CORO MAS EU SEI EM QUEM TENHO CRIDO E ESTOU BEM CERTO QUE É PODEROSO PRA GUARDAR O MEU TESOURO ATÉ O DIA FINAL. IGNORO COMO O ESPÍRITO NOS CONVENCE DO MAL, REVELA A CRISTO, VERBO SEU, CONSOLADOR REAL. NÃO SEI O QUE DE MAL OU BEM É DESTINADO A MIM, SE MAUS OU ÁUREOS DIAS VÊM, ATÉ DA VIDA O FIM? E QUANDO VEM JESUS, NÃO SEI, SE BREVE OU TARDE VEM, MAS SEI QUE MEU SENHOR VIRÁ NA GLÓRIA QUE ELE TEM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('140', '140 - AS MUITAS ÁGUAS', 'AS MUITAS ÁGUAS NÃO PODEM APAGAR O AMOR, O AMOR COM QUE TE AMEI; NEM RIOS PROFUNDOS PODEM AFOGAR O AMOR COM QUE ME AMOU.\n\nCORO COMO ENTÃO PODERIA ESQUECER ESSE AMOR QUE DEU VIDA AO MEU SER?\n\nMEU AMOR POR TI NÃO PODE MUDAR, A TI ENTREGUEI TODO O MEU SER; HOJE VIVES DENTRO DO MEU CORAÇÃO, ÉS MINHA VIDA, MINHA PORÇÃO.\n\nCORO COMO ENTÃO PODERIA ESQUECER ESSE AMOR QUE DEU VIDA AO MEU SER? FINAL: EM TUA PRESENÇA EU SOU TÃO FELIZ, TEU SACRIFÍCIO ME UNIU A TI; SEM O TEU AMOR O QUE SERIA EU? ÉS MINHA VIDA, MEU JESUS, ÉS MINHA VIDA, Ó MEU JESUS.', 'AS MUITAS ÁGUAS NÃO PODEM APAGAR O AMOR, O AMOR COM QUE TE AMEI; NEM RIOS PROFUNDOS PODEM AFOGAR O AMOR COM QUE ME AMOU. CORO COMO ENTÃO PODERIA ESQUECER ESSE AMOR QUE DEU VIDA AO MEU SER? MEU AMOR POR TI NÃO PODE MUDAR, A TI ENTREGUEI TODO O MEU SER; HOJE VIVES DENTRO DO MEU CORAÇÃO, ÉS MINHA VIDA, MINHA PORÇÃO. CORO COMO ENTÃO PODERIA ESQUECER ESSE AMOR QUE DEU VIDA AO MEU SER?  EM TUA PRESENÇA EU SOU TÃO FELIZ, TEU SACRIFÍCIO ME UNIU A TI; SEM O TEU AMOR O QUE SERIA EU? ÉS MINHA VIDA, MEU JESUS, ÉS MINHA VIDA, Ó MEU JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('141', '141 - O AMOR DE DEUS POR NÓS', 'O AMOR DE DEUS POR NÓS É COMO O SOL QUE BRILHA EM SEU PODER; EM JESUS MOSTROU TODO O SEU AMOR REVELANDO ASSIM O SEU QUERER. INSTRUMENTOS\nBIS\n\nQUANDO DEUS TOCOU MEU SER, EU LHE ENTREGUEI TODO O MEU CORAÇÃO; SUA PAZ SENTI INUNDAR MEU SER E O SEU AMOR ME TRANSFORMAR.\nBIS', 'O AMOR DE DEUS POR NÓS É COMO O SOL QUE BRILHA EM SEU PODER; EM JESUS MOSTROU TODO O SEU AMOR REVELANDO ASSIM O SEU QUERER. INSTRUMENTOS QUANDO DEUS TOCOU MEU SER, EU LHE ENTREGUEI TODO O MEU CORAÇÃO; SUA PAZ SENTI INUNDAR MEU SER E O SEU AMOR ME TRANSFORMAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('142', '142 - CRISTO, MEU MESTRE, SEMPRE HÁ DE AMAR-ME', 'CRISTO, MEU MESTRE, SEMPRE HÁ DE AMAR-ME E DELE O MAL NÃO PODE AFASTAR-ME; DEU SUA VIDA PRA ME SALVAR E AGORA DELE SOU.\n\nCORO SOU DE JESUS, MEU MESTRE, CRISTO JESUS É MEU; NÃO SÓ NOS DIAS QUE SE VÃO, MAS, PARA SEMPRE, AMÉM. INSTRUMENTOS\n\nPERDIDO, AFEITO AO MAL, DEGRADADO, ME CONCEDEU PERDÃO DO PECADO; NÃO ANDO TRISTE, JÁ SOU FELIZ E AGORA DELE SOU.\n\nCORO SOU DE JESUS, MEU MESTRE, CRISTO JESUS É MEU; NÃO SÓ NOS DIAS QUE SE VÃO, MAS, PARA SEMPRE, AMÉM. INSTRUMENTOS\n\nEU CANTO DE ALEGRIA DE SALVO, MEU CORAÇÃO JESUS DEIXOU ALVO, POIS O SEU SANGUE ME PURIFICOU E AGORA DELE SOU.', 'CRISTO, MEU MESTRE, SEMPRE HÁ DE AMAR-ME E DELE O MAL NÃO PODE AFASTAR-ME; DEU SUA VIDA PRA ME SALVAR E AGORA DELE SOU. CORO SOU DE JESUS, MEU MESTRE, CRISTO JESUS É MEU; NÃO SÓ NOS DIAS QUE SE VÃO, MAS, PARA SEMPRE, AMÉM. INSTRUMENTOS PERDIDO, AFEITO AO MAL, DEGRADADO, ME CONCEDEU PERDÃO DO PECADO; NÃO ANDO TRISTE, JÁ SOU FELIZ E AGORA DELE SOU. EU CANTO DE ALEGRIA DE SALVO, MEU CORAÇÃO JESUS DEIXOU ALVO, POIS O SEU SANGUE ME PURIFICOU E AGORA DELE SOU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('143', '143 - SE NÃO FOSSE O AMOR', 'SE NÃO FOSSE O AMOR QUE DEUS POR MIM REVELOU, EU NÃO ESTARIA AQUI; ELE INCLINOU SEUS OLHOS E PÔDE CONTEMPLAR TODO O SOFRIMENTO QUE PASSEI. SE NÃO FOSSE O AMOR QUE DEUS POR MIM MOSTROU, JAMAIS CONSEGUIRIA ENTENDER O GRANDE SACRIFÍCIO DE JESUS POR MIM E POR TI.\nBIS\n\nA PAZ QUE TENHO EM JESUS NÃO SE PODE COMPARAR A TESOUROS DESTA VIDA AQUI, À PAZ QUE FOI COMPRADA COM SANGUE E COM DOR; E HOJE É DADA A MIM E A TI, A MIM E A TI POR CRISTO JESUS.\nBIS', 'SE NÃO FOSSE O AMOR QUE DEUS POR MIM REVELOU, EU NÃO ESTARIA AQUI; ELE INCLINOU SEUS OLHOS E PÔDE CONTEMPLAR TODO O SOFRIMENTO QUE PASSEI. SE NÃO FOSSE O AMOR QUE DEUS POR MIM MOSTROU, JAMAIS CONSEGUIRIA ENTENDER O GRANDE SACRIFÍCIO DE JESUS POR MIM E POR TI. A PAZ QUE TENHO EM JESUS NÃO SE PODE COMPARAR A TESOUROS DESTA VIDA AQUI, À PAZ QUE FOI COMPRADA COM SANGUE E COM DOR; E HOJE É DADA A MIM E A TI, A MIM E A TI POR CRISTO JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('144', '144 - JESUS, AGORA EU BEM SEI', 'JESUS, AGORA EU BEM SEI QUÃO GRANDE É TEU AMOR, POIS SALVAÇÃO EM TI ACHEI, A CRISTO DOU LOUVOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS.\n\nDESCANSO NUNCA CONHECI, INÚTIL SEMPRE SOU; MAS CRISTO SE LEMBROU DE MIM, SUA GRAÇA ME CHAMOU. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS.\n\nCOMIGO, CRENTES, EXALTAI O GRANDE SALVADOR, POIS TUDO CRISTO ME SUPRIU EMBORA PECADOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS.\n\nLOUVOR, LOUVOR A TI DAREI, Ó CRISTO, MEU SENHOR! PROFETA, SACERDOTE, REI, DO MUNDO, O SALVADOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS.', 'JESUS, AGORA EU BEM SEI QUÃO GRANDE É TEU AMOR, POIS SALVAÇÃO EM TI ACHEI, A CRISTO DOU LOUVOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS. DESCANSO NUNCA CONHECI, INÚTIL SEMPRE SOU; MAS CRISTO SE LEMBROU DE MIM, SUA GRAÇA ME CHAMOU. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS. COMIGO, CRENTES, EXALTAI O GRANDE SALVADOR, POIS TUDO CRISTO ME SUPRIU EMBORA PECADOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS. LOUVOR, LOUVOR A TI DAREI, Ó CRISTO, MEU SENHOR! PROFETA, SACERDOTE, REI, DO MUNDO, O SALVADOR. CORO É CRISTO, SÓ, MEU SALVADOR, POR ELE EU TENHO PAZ; JESUS, A TI LOUVOR DAREI, POIS TUDO TU ME DÁS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('145', '145 - É O AMOR DE DEUS', 'É O AMOR DE DEUS A CAUSA DE NÃO SERMOS CONSUMIDOS POIS AINDA QUE FAÇAMOS PRA MERECER, NUNCA MERECEMOS ESSE AMOR QUE É LINDO E PURO, É O AMOR QUE DEUS TE OFERECE AGORA.\nBIS\n\nÉ O AMOR DE DEUS QUE TE LIVRA DE TODA CILADA E INVESTIDA, ESTÁ SEMPRE AO NOSSO LADO PRA CUIDAR DE NÓS EMBORA NÃO MEREÇAMOS ESSE AMOR. INSTRUMENTOS\n\nELE NOS GUARDA EM TODOS OS MOMENTOS DE NOSSAS VIDAS E ÀS VEZES SOMOS INGRATOS, MAS ELE NOS AMA E É POR CAUSA DESSE AMOR QUE NÃO SOMOS CONSUMIDOS.\nBIS', 'É O AMOR DE DEUS A CAUSA DE NÃO SERMOS CONSUMIDOS POIS AINDA QUE FAÇAMOS PRA MERECER, NUNCA MERECEMOS ESSE AMOR QUE É LINDO E PURO, É O AMOR QUE DEUS TE OFERECE AGORA. É O AMOR DE DEUS QUE TE LIVRA DE TODA CILADA E INVESTIDA, ESTÁ SEMPRE AO NOSSO LADO PRA CUIDAR DE NÓS EMBORA NÃO MEREÇAMOS ESSE AMOR. INSTRUMENTOS ELE NOS GUARDA EM TODOS OS MOMENTOS DE NOSSAS VIDAS E ÀS VEZES SOMOS INGRATOS, MAS ELE NOS AMA E É POR CAUSA DESSE AMOR QUE NÃO SOMOS CONSUMIDOS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('146', '146 - EXISTE UM ALGUÉM QUE CUIDA DE MIM', 'EXISTE UM ALGUÉM QUE CUIDA DE MIM, TAMBÉM DE VOCÊ; SEU NOME É DEUS FORTE, REAL CONSELHEIRO, ELE É O PODER; ELE É FIEL, EMANUEL, UM BRILHO DE LUZ, TRANSMITE A PAZ, TAMBÉM SATISFAZ, SEU NOME É JESUS.\n\nJESUS É A VIDA, A FLOR MAIS QUERIDA; NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA.\nBIS\n\nEXISTE UM ALGUÉM QUE CUIDA DE MIM, TAMBÉM DE VOCÊ; SEU NOME É DEUS FORTE, REAL CONSELHEIRO, ELE É O PODER; ELE É FIEL, EMANUEL, UM BRILHO DE LUZ, TRANSMITE A PAZ, TAMBÉM SATISFAZ, SEU NOME É JESUS.\n\nJESUS É A VIDA, A FLOR MAIS QUERIDA; NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA. FINAL: NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA.\nBIS', 'EXISTE UM ALGUÉM QUE CUIDA DE MIM, TAMBÉM DE VOCÊ; SEU NOME É DEUS FORTE, REAL CONSELHEIRO, ELE É O PODER; ELE É FIEL, EMANUEL, UM BRILHO DE LUZ, TRANSMITE A PAZ, TAMBÉM SATISFAZ, SEU NOME É JESUS. JESUS É A VIDA, A FLOR MAIS QUERIDA; NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA. JESUS É A VIDA, A FLOR MAIS QUERIDA; NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA.  NA HORA DA DOR, JESUS É AMOR, A FONTE DA VIDA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('147', '147 - CRISTO É TUDO PARA MIM', 'CORO CRISTO É TUDO PARA MIM, AMOR E PAZ, PERDÃO SEM FIM; SEM ELE A VIDA É TÃO RUIM, CRISTO JESUS É TUDO PARA MIM.\n\nCEDINHO SINTO O SEU AMOR, DE NOITE SINTO TAMBÉM; COM CRISTO ANDO SEM TEMOR, AS TREVAS NUNCA ME VÊM.\n\nCORO CRISTO É TUDO PARA MIM, AMOR E PAZ, PERDÃO SEM FIM; SEM ELE A VIDA É TÃO RUIM, CRISTO JESUS É TUDO PARA MIM.\n\nO MUNDO NÃO ME ATRAI, JESUS, TEU SER, JÁ ME ATRAIU; SEGURANÇA TENHO NO PAI, VIDA NESTE MUNDO VIL.\n\nCORO CRISTO É TUDO PARA MIM, AMOR E PAZ, PERDÃO SEM FIM; SEM ELE A VIDA É TÃO RUIM, CRISTO JESUS É TUDO PARA MIM.', 'CORO CRISTO É TUDO PARA MIM, AMOR E PAZ, PERDÃO SEM FIM; SEM ELE A VIDA É TÃO RUIM, CRISTO JESUS É TUDO PARA MIM. CEDINHO SINTO O SEU AMOR, DE NOITE SINTO TAMBÉM; COM CRISTO ANDO SEM TEMOR, AS TREVAS NUNCA ME VÊM. O MUNDO NÃO ME ATRAI, JESUS, TEU SER, JÁ ME ATRAIU; SEGURANÇA TENHO NO PAI, VIDA NESTE MUNDO VIL.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('148', '148 - UMA CERTA VEZ, JESUS CRISTO', 'UMA CERTA VEZ, JESUS CRISTO, PARA UMA GRANDE MULTIDÃO NA PRAIA DA GALILÉIA, FEZ UMA MULTIPLICAÇÃO: COM DOIS PEIXES E CINCO PÃES, TODOS JULGAVAM QUE NÃO; A MAIS DE CINCO MIL PESSOAS DEU COMIDA BOA, DEU PEIXE E DEU PÃO.\n\nCORO ESTE QUE É DEUS, DEUS PODEROSO, DO CELESTE LAR; ESTE QUE É DEUS, QUE VENDO O POVO TODO A PECAR MANDOU SEU FILHO PARA QUE O MUNDO VIESSE SALVAR, ESTE DEUS MARAVILHOSO DÁ PAZ E DÁ GOZO A QUEM O ACEITAR.\n\nJESUS ORDENOU AO POVO QUE SE ASSENTASSE NO CHÃO, E RECEBENDO UMA CESTA COM DOIS PEIXES E CINCO PÃES, DEU GRAÇAS AO PAI DIVINO; DISSE: “TENDE COMPAIXÃO”. OS DISCÍPULOS, NAQUELE MOMENTO, TIVERAM ALIMENTO PARA A MULTIDÃO.\n\nCORO ESTE QUE É DEUS, DEUS PODEROSO, DO CELESTE LAR; ESTE QUE É DEUS, QUE VENDO O POVO TODO A PECAR MANDOU SEU FILHO PARA QUE O MUNDO VIESSE SALVAR, ESTE DEUS MARAVILHOSO DÁ PAZ E DÁ GOZO A QUEM O ACEITAR.\n\nPARA QUE NADA PERDESSE, MANDOU AS SOBRAS JUNTAR: DOZE CESTOS DE PEDAÇOS FOI O QUE VEIO A SOBRAR; JESUS AINDA É O MESMO, OH! QUE ALEGRIA SEM PAR; ESTE JESUS PODEROSO, LÁ DO CÉU DE GOZO ESTÁ A NOS CONTEMPLAR.\n\nCORO ESTE QUE É DEUS, DEUS PODEROSO, DO CELESTE LAR; ESTE QUE É DEUS, QUE VENDO O POVO TODO A PECAR MANDOU SEU FILHO PARA QUE O MUNDO VIESSE SALVAR, ESTE DEUS MARAVILHOSO DÁ PAZ E DÁ GOZO A QUEM O ACEITAR.', 'UMA CERTA VEZ, JESUS CRISTO, PARA UMA GRANDE MULTIDÃO NA PRAIA DA GALILÉIA, FEZ UMA MULTIPLICAÇÃO: COM DOIS PEIXES E CINCO PÃES, TODOS JULGAVAM QUE NÃO; A MAIS DE CINCO MIL PESSOAS DEU COMIDA BOA, DEU PEIXE E DEU PÃO. CORO ESTE QUE É DEUS, DEUS PODEROSO, DO CELESTE LAR; ESTE QUE É DEUS, QUE VENDO O POVO TODO A PECAR MANDOU SEU FILHO PARA QUE O MUNDO VIESSE SALVAR, ESTE DEUS MARAVILHOSO DÁ PAZ E DÁ GOZO A QUEM O ACEITAR. JESUS ORDENOU AO POVO QUE SE ASSENTASSE NO CHÃO, E RECEBENDO UMA CESTA COM DOIS PEIXES E CINCO PÃES, DEU GRAÇAS AO PAI DIVINO; DISSE: TENDE COMPAIXÃO. OS DISCÍPULOS, NAQUELE MOMENTO, TIVERAM ALIMENTO PARA A MULTIDÃO. PARA QUE NADA PERDESSE, MANDOU AS SOBRAS JUNTAR: DOZE CESTOS DE PEDAÇOS FOI O QUE VEIO A SOBRAR; JESUS AINDA É O MESMO, OH! QUE ALEGRIA SEM PAR; ESTE JESUS PODEROSO, LÁ DO CÉU DE GOZO ESTÁ A NOS CONTEMPLAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('149', '149 - JESUS FEZ A PROMESSA', 'JESUS FEZ A PROMESSA, CONOSCO ESTARIA ATÉ O FIM; E OS SINAIS SEGUIRIAM A TODOS QUE CRESSEM, ELE FALOU ASSIM. CAIAM OS MONTES E OS HOMENS, MAS SUA PROMESSA ESTÁ DE PÉ, ELE NOS TEM PROVADO QUE NÃO MUDOU, PODEROSO ELE É!\n\nCORO PODEROSO ELE É E EM NADA MUDOU; NELE ESTÁ NOSSA FÉ, PODEROSO ELE É!\n\nELE DEU VISTA AOS CEGOS E OS OPRIMIDOS ELE LIBERTOU; CINCO PÃES E DOIS PEIXES, A MAIS DE DOIS MIL ELE ALIMENTOU. OPEROU NA JUDÉIA, EM CAFARNAUM E EM NAZARÉ; CONTINUA OPERANDO AQUI EM NOSSO MEIO, PODEROSO ELE É!\n\nCORO (2X) PODEROSO ELE É E EM NADA MUDOU; NELE ESTÁ NOSSA FÉ, PODEROSO ELE É!', 'JESUS FEZ A PROMESSA, CONOSCO ESTARIA ATÉ O FIM; E OS SINAIS SEGUIRIAM A TODOS QUE CRESSEM, ELE FALOU ASSIM. CAIAM OS MONTES E OS HOMENS, MAS SUA PROMESSA ESTÁ DE PÉ, ELE NOS TEM PROVADO QUE NÃO MUDOU, PODEROSO ELE É! CORO PODEROSO ELE É E EM NADA MUDOU; NELE ESTÁ NOSSA FÉ, PODEROSO ELE É! ELE DEU VISTA AOS CEGOS E OS OPRIMIDOS ELE LIBERTOU; CINCO PÃES E DOIS PEIXES, A MAIS DE DOIS MIL ELE ALIMENTOU. OPEROU NA JUDÉIA, EM CAFARNAUM E EM NAZARÉ; CONTINUA OPERANDO AQUI EM NOSSO MEIO, PODEROSO ELE É! CORO  PODEROSO ELE É E EM NADA MUDOU; NELE ESTÁ NOSSA FÉ, PODEROSO ELE É!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('150', '150 - VOU CONTAR-VOS O QUE PENSO DO MEU MESTRE', 'VOU CONTAR-VOS O QUE PENSO DO MEU MESTRE, COMO DELE RECEBI A LUZ E A PAZ; ELE MUDOU-ME, EU BEM SEI, COMPLETAMENTE, COMO CRISTO NENHUM OUTRO SATISFAZ.\n\nCORO SEMPRE CUIDARÁ DE MIM, MEU MESTRE, COM DESVELO E COMPAIXÃO SEM FIM; NENHUM OUTRO TIRA A CULPA DO PECADO. OH! COMO ELE AMA A MIM!\n\nCOM A VIDA TODA CHEIA DE PECADO, NA MISÉRIA E COM DOR NO CORAÇÃO, ELE ERGUEU-ME COM BRAÇOS FORTES DE TERNURA, DEU-ME GOZO, DEU-ME PAZ, CONSOLAÇÃO.\n\nCORO SEMPRE CUIDARÁ DE MIM, MEU MESTRE, COM DESVELO E COMPAIXÃO SEM FIM; NENHUM OUTRO TIRA A CULPA DO PECADO. OH! COMO ELE AMA A MIM!\n\nCADA DIA ELE VEM COM SEGURANÇA, MAIS E MAIS A REVELAR O SEU AMOR; COMO ANELO COMPREENDER COMPLETAMENTE ESTE AMOR TÃO DIVINAL, RESTAURADOR!\n\nCORO SEMPRE CUIDARÁ DE MIM, MEU MESTRE, COM DESVELO E COMPAIXÃO SEM FIM; NENHUM OUTRO TIRA A CULPA DO PECADO. OH! COMO ELE AMA A MIM!\n\nMEU MAIOR DESEJO AGORA É AMÁ-LO, PROCLAMAR O QUE ELE FEZ PRA ME SALVAR; E CANTANDO ESSE AMOR INIGUALÁVEL, ESPERO EM DEUS A MINHA VIDA TERMINAR.', 'VOU CONTAR-VOS O QUE PENSO DO MEU MESTRE, COMO DELE RECEBI A LUZ E A PAZ; ELE MUDOU-ME, EU BEM SEI, COMPLETAMENTE, COMO CRISTO NENHUM OUTRO SATISFAZ. CORO SEMPRE CUIDARÁ DE MIM, MEU MESTRE, COM DESVELO E COMPAIXÃO SEM FIM; NENHUM OUTRO TIRA A CULPA DO PECADO. OH! COMO ELE AMA A MIM! COM A VIDA TODA CHEIA DE PECADO, NA MISÉRIA E COM DOR NO CORAÇÃO, ELE ERGUEU-ME COM BRAÇOS FORTES DE TERNURA, DEU-ME GOZO, DEU-ME PAZ, CONSOLAÇÃO. CADA DIA ELE VEM COM SEGURANÇA, MAIS E MAIS A REVELAR O SEU AMOR; COMO ANELO COMPREENDER COMPLETAMENTE ESTE AMOR TÃO DIVINAL, RESTAURADOR! MEU MAIOR DESEJO AGORA É AMÁ-LO, PROCLAMAR O QUE ELE FEZ PRA ME SALVAR; E CANTANDO ESSE AMOR INIGUALÁVEL, ESPERO EM DEUS A MINHA VIDA TERMINAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('151', '151 - CRISTO JESUS É O MEU BOM SALVADOR', 'CRISTO JESUS É O MEU BOM SALVADOR; DESEJO AMÁ-LO MAIS, TODOS OS DIAS É MEU SENHOR.\n\nCORO A CRISTO EU AMO, OH! QUANTO EU AMO, POIS POR MIM MORREU; COM O SEU SANGUE, JESUS SALVOU-ME, EM GRATIDÃO SOU SEU. INSTRUMENTOS BEM CEDO AO CÉU ELE ME LEVARÁ, DESEJO AMÁ-LO MAIS, ETERNAMENTE ME GUIARÁ.', 'CRISTO JESUS É O MEU BOM SALVADOR; DESEJO AMÁ-LO MAIS, TODOS OS DIAS É MEU SENHOR. CORO A CRISTO EU AMO, OH! QUANTO EU AMO, POIS POR MIM MORREU; COM O SEU SANGUE, JESUS SALVOU-ME, EM GRATIDÃO SOU SEU. INSTRUMENTOS BEM CEDO AO CÉU ELE ME LEVARÁ, DESEJO AMÁ-LO MAIS, ETERNAMENTE ME GUIARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('152', '152 - MAIS PERTO DE JESUS', 'MAIS PERTO DE JESUS PROCURO SEMPRE EU CHEGAR, MAIS BELO DO QUE O OURO DO SOL NADO É A TI MIRAR; EM PENSAMENTOS, SONHOS, TANTA GLÓRIA NUNCA VI! POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI!\n\nCORO NÃO POSSO EXPLICAR QUÃO MEIGO ÉS JESUS; MAS FACE A FACE NO TEU LAR, EU TE VEREI, JESUS!\n\nA ESTRELA RESPLENDENTE, DA MANHÃ, É MINHA LUZ, O LÍRIO DOS VALES É O BOM SENHOR JESUS; SUAVE E DOCE É O CHEIRO QUE SÓ VEM DE SI, POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI!\n\nCORO NÃO POSSO EXPLICAR QUÃO MEIGO ÉS JESUS; MAS FACE A FACE NO TEU LAR, EU TE VEREI, JESUS!\n\nSE MÁGOAS VÊM ME PERTURBAR, O BÁLSAMO ELE TEM, ME TOMA NOS SEUS BRAÇOS E ASSIM DESCANSO BEM; NA CRUZ LEVOU JESUS O MEU PECADO SOBRE SI, POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI!\n\nCORO NÃO POSSO EXPLICAR QUÃO MEIGO ÉS JESUS; MAS FACE A FACE NO TEU LAR, EU TE VEREI, JESUS!', 'MAIS PERTO DE JESUS PROCURO SEMPRE EU CHEGAR, MAIS BELO DO QUE O OURO DO SOL NADO É A TI MIRAR; EM PENSAMENTOS, SONHOS, TANTA GLÓRIA NUNCA VI! POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI! CORO NÃO POSSO EXPLICAR QUÃO MEIGO ÉS JESUS; MAS FACE A FACE NO TEU LAR, EU TE VEREI, JESUS! A ESTRELA RESPLENDENTE, DA MANHÃ, É MINHA LUZ, O LÍRIO DOS VALES É O BOM SENHOR JESUS; SUAVE E DOCE É O CHEIRO QUE SÓ VEM DE SI, POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI! SE MÁGOAS VÊM ME PERTURBAR, O BÁLSAMO ELE TEM, ME TOMA NOS SEUS BRAÇOS E ASSIM DESCANSO BEM; NA CRUZ LEVOU JESUS O MEU PECADO SOBRE SI, POIS ELE É MAIS BELO DO QUE EU JAMAIS PREVI!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('153', '153 - CRISTO, NOME MAIS ALTO', 'CRISTO, NOME MAIS ALTO, SALVADOR NOSSO, GLORIOSO SENHOR; EMANUEL É DEUS CONOSCO, NOME DE VIDA, DIVINO SENHOR. EMANUEL, EMANUEL, SEU NOME É EMANUEL! ELE ME AMOU, ME TRANSFORMOU E VIVE EM MIM, EMANUEL.\nBIS', 'CRISTO, NOME MAIS ALTO, SALVADOR NOSSO, GLORIOSO SENHOR; EMANUEL É DEUS CONOSCO, NOME DE VIDA, DIVINO SENHOR. EMANUEL, EMANUEL, SEU NOME É EMANUEL! ELE ME AMOU, ME TRANSFORMOU E VIVE EM MIM, EMANUEL.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('154', '154 - Ó TU QUE HABITAS ENTRE OS QUERUBINS', 'Ó TU QUE HABITAS ENTRE OS QUERUBINS, RESPLANDECE TUA LUZ EM MIM; Ó DEUS EXCELSO, ETERNO CRIADOR, VEM TU, MEU DEUS, ME ENCHER DE AMOR.\n\nCORO Ó DEUS DE PAZ, Ó DEUS DE AMOR, (Ó DEUS DE AMOR) SERVAS ÉS TU MEU DEUS, MEU SALVADOR; A MINHA VIDA ENTREGO A TI, (ENTREGO A TI) SERVAS DEIXA O ESPÍRITO EM MIM FLUIR.\n\nÓ TU QUE HABITAS NO MAIS ALTO CÉU, NO CALVÁRIO TU RASGASTE O VÉU DANDO-ME PAZ E COMUNHÃO, A TI ENTREGO MEU CORAÇÃO.\n\nCORO Ó DEUS DE PAZ, Ó DEUS DE AMOR, (Ó DEUS DE AMOR) SERVAS ÉS TU MEU DEUS, MEU SALVADOR; A MINHA VIDA ENTREGO A TI, (ENTREGO A TI) SERVAS DEIXA O ESPÍRITO EM MIM FLUIR.\n\nÓ DEUS ETERNO, COMO EU TE AMO, POIS TU MUDASTE A MINHA SORTE, POR ISSO EU DIGO QUE ÉS SOBERANO POIS ME AJUDASTE A VENCER A MORTE.\n\nCORO Ó DEUS DE PAZ, Ó DEUS DE AMOR, (Ó DEUS DE AMOR) SERVAS ÉS TU MEU DEUS, MEU SALVADOR; A MINHA VIDA ENTREGO A TI, (ENTREGO A TI) SERVAS DEIXA O ESPÍRITO EM MIM FLUIR.', 'Ó TU QUE HABITAS ENTRE OS QUERUBINS, RESPLANDECE TUA LUZ EM MIM; Ó DEUS EXCELSO, ETERNO CRIADOR, VEM TU, MEU DEUS, ME ENCHER DE AMOR. CORO Ó DEUS DE PAZ, Ó DEUS DE AMOR, (Ó DEUS DE AMOR) SERVAS ÉS TU MEU DEUS, MEU SALVADOR; A MINHA VIDA ENTREGO A TI, (ENTREGO A TI) SERVAS DEIXA O ESPÍRITO EM MIM FLUIR. Ó TU QUE HABITAS NO MAIS ALTO CÉU, NO CALVÁRIO TU RASGASTE O VÉU DANDO-ME PAZ E COMUNHÃO, A TI ENTREGO MEU CORAÇÃO. Ó DEUS ETERNO, COMO EU TE AMO, POIS TU MUDASTE A MINHA SORTE, POR ISSO EU DIGO QUE ÉS SOBERANO POIS ME AJUDASTE A VENCER A MORTE.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('155', '155 - HÁ UM SER QUE VIVE EM MIM', 'HÁ UM SER QUE VIVE EM MIM E SEMPRE ESTÁ COMIGO, SIM, PROPORCIONANDO PAZ E AMOR, ALIVIANDO TODA A MINHA DOR.\n\nUM DIA MORREU PARA ME SALVAR, RESSUSCITOU E VAI VOLTAR, UM DIA ENTÃO VAI ME LEVAR PARA COM ELE ETERNAMENTE MORAR. O NOME DELE É SIMPLES E POR ISSO É SUBLIME, É O FILHO DE DEUS, ELE É JESUS, ELE É JESUS (BIS)', 'HÁ UM SER QUE VIVE EM MIM E SEMPRE ESTÁ COMIGO, SIM, PROPORCIONANDO PAZ E AMOR, ALIVIANDO TODA A MINHA DOR. UM DIA MORREU PARA ME SALVAR, RESSUSCITOU E VAI VOLTAR, UM DIA ENTÃO VAI ME LEVAR PARA COM ELE ETERNAMENTE MORAR. O NOME DELE É SIMPLES E POR ISSO É SUBLIME, É O FILHO DE DEUS, ELE É JESUS, ELE É JESUS',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('156', '156 - TOTALMENTE DESEJÁVEL É MEU AMADO', 'TOTALMENTE DESEJÁVEL É O MEU AMADO, PRECIOSO PARA MIM ELE É; O SEU FALAR É MUITÍSSIMO SUAVE, ASSIM ELE É, JESUS, O AMADO SALVADOR.\n\nTODO O SEU SER É DELEITÁVEL À MINH’ALMA, DELE QUERO SEMPRE MAIS CONHECER; NÃO HÁ PRAZER MAIS REAL QUE SUA PRESENÇA, TOTALMENTE DESEJÁVEL É JESUS.\nBIS', 'TOTALMENTE DESEJÁVEL É O MEU AMADO, PRECIOSO PARA MIM ELE É; O SEU FALAR É MUITÍSSIMO SUAVE, ASSIM ELE É, JESUS, O AMADO SALVADOR. TODO O SEU SER É DELEITÁVEL À MINH’ALMA, DELE QUERO SEMPRE MAIS CONHECER; NÃO HÁ PRAZER MAIS REAL QUE SUA PRESENÇA, TOTALMENTE DESEJÁVEL É JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('157', '157 - CRISTO É A ROSA MAIS BELA', 'CRISTO É A ROSA MAIS BELA, O MAIS PURO LÍRIO DO VALE, CRISTO É A BRILHANTE ESTRELA QUE ANUNCIA A LUZ DA MANHÃ.\n\nCRISTO É A ROCHA FERIDA BROTANDO ÁGUAS DE SALVAÇÃO, CRISTO É A FONTE DA VIDA, DANDO A PAZ, A CURA E O PERDÃO.\n\nCRISTO É SUAVE ALEGRIA, DOCE AMOR ENCHENDO O MEU SER; CRISTO É O AMADO QUE UM DIA VEM NAS ASAS DO AMANHECER.\nBIS', 'CRISTO É A ROSA MAIS BELA, O MAIS PURO LÍRIO DO VALE, CRISTO É A BRILHANTE ESTRELA QUE ANUNCIA A LUZ DA MANHÃ. CRISTO É A ROCHA FERIDA BROTANDO ÁGUAS DE SALVAÇÃO, CRISTO É A FONTE DA VIDA, DANDO A PAZ, A CURA E O PERDÃO. CRISTO É SUAVE ALEGRIA, DOCE AMOR ENCHENDO O MEU SER; CRISTO É O AMADO QUE UM DIA VEM NAS ASAS DO AMANHECER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('158', '158 - JESUS, O LÍRIO DOS VALES', 'JESUS, O LÍRIO DOS VALES, JESUS, O GRANDE AMOR, JESUS, O AMOR SUBLIME, JESUS, O NOSSO SENHOR.\n\nELE É A PUREZA, É O AMOR MAIOR, É VIDA EM ABUNDÂNCIA, É A PAZ SEM FIM. JESUS, A ESTRELA DA MANHÃ, A ROSA PERFUMADA, UM RIO QUE TRANSBORDA DE AMOR INFINITO.', 'JESUS, O LÍRIO DOS VALES, JESUS, O GRANDE AMOR, JESUS, O AMOR SUBLIME, JESUS, O NOSSO SENHOR. ELE É A PUREZA, É O AMOR MAIOR, É VIDA EM ABUNDÂNCIA, É A PAZ SEM FIM. JESUS, A ESTRELA DA MANHÃ, A ROSA PERFUMADA, UM RIO QUE TRANSBORDA DE AMOR INFINITO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('159', '159 - ELE É A ROSA DE SAROM', 'ELE É A ROSA DE SAROM, SEU FRUTO É DOCE AO PALADAR; ELE É O LÍRIO DOS VALES, À SUA SOMBRA QUERO DESCANSAR. LEVOU-ME À SALA DO BANQUETE, SEU ESTANDARTE ERA EM MIM O AMOR; Ó SUSTENTA-ME, CONFORTA-ME E ME AMPARE TUA MÃO, SENHOR.\n\nESTA É A VOZ DO AMADO MEU, QUE ME ORDENA A LEVANTAR, EI-LO QUE VEM POR SOBRE OS MONTES, ELE TEM PRESSA DE AQUI CHEGAR; SURGEM AS FLORES SOBRE A TERRA, CHEGOU O TEMPO AGORA DE CANTAR; O INVERNO PASSA, A CHUVA CESSOU E A VOZ DA ROLA POSSO ESCUTAR.\nBIS', 'ELE É A ROSA DE SAROM, SEU FRUTO É DOCE AO PALADAR; ELE É O LÍRIO DOS VALES, À SUA SOMBRA QUERO DESCANSAR. LEVOU-ME À SALA DO BANQUETE, SEU ESTANDARTE ERA EM MIM O AMOR; Ó SUSTENTA-ME, CONFORTA-ME E ME AMPARE TUA MÃO, SENHOR. ESTA É A VOZ DO AMADO MEU, QUE ME ORDENA A LEVANTAR, EI-LO QUE VEM POR SOBRE OS MONTES, ELE TEM PRESSA DE AQUI CHEGAR; SURGEM AS FLORES SOBRE A TERRA, CHEGOU O TEMPO AGORA DE CANTAR; O INVERNO PASSA, A CHUVA CESSOU E A VOZ DA ROLA POSSO ESCUTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('160', '160 - EU SOU DO MEU AMADO E ELE ME TEM AFEIÇÃO', 'EU SOU DO MEU AMADO E ELE ME TEM AFEIÇÃO. VEM, Ó MEU AMADO, SAIAMOS AO CAMPO, PASSEMOS AS NOITES NAS ALDEIAS, LEVANTEMO-NOS DE MANHÃ PARA IR ÀS VINHAS, VEJAMOS SE FLORESCEM AS VIDES, SE SE ABRE A FLOR, SE JÁ BROTAM AS ROMEIRAS, ALI TE DAREI O MEU GRANDE AMOR.\nBIS\n\nAS MANDRÁGORAS DÃO CHEIRO E ÀS NOSSAS PORTAS HÁ TODA SORTE DE EXCELENTES FRUTOS NOVOS E VELHOS, NOVOS E VELHOS, Ó AMADO MEU, EU OS GUARDEI PARA TI.\nBIS\nBIS', 'EU SOU DO MEU AMADO E ELE ME TEM AFEIÇÃO. VEM, Ó MEU AMADO, SAIAMOS AO CAMPO, PASSEMOS AS NOITES NAS ALDEIAS, LEVANTEMO-NOS DE MANHÃ PARA IR ÀS VINHAS, VEJAMOS SE FLORESCEM AS VIDES, SE SE ABRE A FLOR, SE JÁ BROTAM AS ROMEIRAS, ALI TE DAREI O MEU GRANDE AMOR. AS MANDRÁGORAS DÃO CHEIRO E ÀS NOSSAS PORTAS HÁ TODA SORTE DE EXCELENTES FRUTOS NOVOS E VELHOS, NOVOS E VELHOS, Ó AMADO MEU, EU OS GUARDEI PARA TI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('161', '161 - ERA UMA OLIVEIRA', 'ERA UMA OLIVEIRA COM OS RAMOS QUEBRADOS, E UM ZAMBUJEIRO ALI FOI ENXERTADO; ESSA OLIVEIRA É O MEU JESUS, FUI NELE ENXERTADO, HOJE TENHO LUZ.\n\nCORO OLIVEIRA VERDADEIRA, JESUS CRISTO É, NELE FUI ENXERTADO ATRAVÉS DA FÉ, POSSO EU DAR FRUTO ENXERTADO ASSIM; CRISTO É OLIVEIRA, NÃO MAIS VIVO EU MAS CRISTO VIVE EM MIM.\n\nZAMBUJEIRO BRAVO NÃO DÁ FRUTO BOM, ASSIM ERA EU NO MEU CORAÇÃO, MAS NUM BELO DIA TUDO TRANSFORMOU, EU FUI ENXERTADO NO MEU SALVADOR.\n\nCORO OLIVEIRA VERDADEIRA, JESUS CRISTO É, NELE FUI ENXERTADO ATRAVÉS DA FÉ, POSSO EU DAR FRUTO ENXERTADO ASSIM; CRISTO É OLIVEIRA, NÃO MAIS VIVO EU MAS CRISTO VIVE EM MIM.\n\nCRISTO É OLIVEIRA, NELE SOU FELIZ, NELE ESTOU LIGADO NO TRONCO E NA RAIZ; FIRMADO NA OLIVEIRA DE TODO O CORAÇÃO, POSSO EU DAR FRUTO E DAR FRUTO BOM.\n\nCORO OLIVEIRA VERDADEIRA, JESUS CRISTO É, NELE FUI ENXERTADO ATRAVÉS DA FÉ, POSSO EU DAR FRUTO ENXERTADO ASSIM; CRISTO É OLIVEIRA, NÃO MAIS VIVO EU MAS CRISTO VIVE EM MIM.', 'ERA UMA OLIVEIRA COM OS RAMOS QUEBRADOS, E UM ZAMBUJEIRO ALI FOI ENXERTADO; ESSA OLIVEIRA É O MEU JESUS, FUI NELE ENXERTADO, HOJE TENHO LUZ. CORO OLIVEIRA VERDADEIRA, JESUS CRISTO É, NELE FUI ENXERTADO ATRAVÉS DA FÉ, POSSO EU DAR FRUTO ENXERTADO ASSIM; CRISTO É OLIVEIRA, NÃO MAIS VIVO EU MAS CRISTO VIVE EM MIM. ZAMBUJEIRO BRAVO NÃO DÁ FRUTO BOM, ASSIM ERA EU NO MEU CORAÇÃO, MAS NUM BELO DIA TUDO TRANSFORMOU, EU FUI ENXERTADO NO MEU SALVADOR. CRISTO É OLIVEIRA, NELE SOU FELIZ, NELE ESTOU LIGADO NO TRONCO E NA RAIZ; FIRMADO NA OLIVEIRA DE TODO O CORAÇÃO, POSSO EU DAR FRUTO E DAR FRUTO BOM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('162', '162 - GRANDIOSO PARA MIM', 'GRANDIOSO PARA MIM É JESUS, MEU SALVADOR, SUA GRAÇA NÃO TEM FIM, COM SEU DIVINAL FULGOR; SUA GRAÇA SE ESTENDEU, DANDO-ME A SALVAÇÃO; GRANDE PAZ E ALEGRIA EU DESFRUTAREI UM DIA NA CELESTIAL SIÃO.\n\nO TEU NOME É AMOR, EU CONFESSO A TI, SENHOR; O TEU NOME É PURO E BOM, PERDOADO AGORA ESTOU; NESTE MUNDO DE ILUSÃO, ENQUANTO EU AQUI VIVER, MINHA BOCA ENTOARÁ ALELUIA, SEM CESSAR EXALTANDO O TEU PODER.\nBIS', 'GRANDIOSO PARA MIM É JESUS, MEU SALVADOR, SUA GRAÇA NÃO TEM FIM, COM SEU DIVINAL FULGOR; SUA GRAÇA SE ESTENDEU, DANDO-ME A SALVAÇÃO; GRANDE PAZ E ALEGRIA EU DESFRUTAREI UM DIA NA CELESTIAL SIÃO. O TEU NOME É AMOR, EU CONFESSO A TI, SENHOR; O TEU NOME É PURO E BOM, PERDOADO AGORA ESTOU; NESTE MUNDO DE ILUSÃO, ENQUANTO EU AQUI VIVER, MINHA BOCA ENTOARÁ ALELUIA, SEM CESSAR EXALTANDO O TEU PODER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('163', '163 - TENHO UMA PEDRA VIVA', 'TENHO UMA PEDRA VIVA, PEDRA DE ESQUINA, PEDRA ANGULAR; CRISTO É ROCHA DURA, TODA CRIATURA PODE SEGURAR. ESTA ROCHA É CRISTO TODO IMPREVISTO PODE RESOLVER, PORÉM TEUS PECADOS SERÃO PERDOADOS, CRISTO TEM PODER.\nBIS\n\nCORO CRISTO TEM PODER E SABE LUTAR, ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ; CRISTO TEM PODER E SABE LUTAR, ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ.\n\nESTA ROCHA É ÁGUA E QUEM TEM SUAS MÁGOAS ELE VAI TIRAR; NUNCA ESTOU SOZINHO, CRISTO É MEU CAMINHO, NELE TENHO PAZ. TEM PAZ VERDADEIRA QUEM NESTA ROCHA FIRMADO ESTÁ; CRISTO É A VERDADE E, SE CRERES NELE, TE LIBERTARÁ.\nBIS\n\nCORO CRISTO TEM PODER E SABE LUTAR, ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ; CRISTO TEM PODER E SABE LUTAR, ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ.\n\nFINAL: ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ.', 'TENHO UMA PEDRA VIVA, PEDRA DE ESQUINA, PEDRA ANGULAR; CRISTO É ROCHA DURA, TODA CRIATURA PODE SEGURAR. ESTA ROCHA É CRISTO TODO IMPREVISTO PODE RESOLVER, PORÉM TEUS PECADOS SERÃO PERDOADOS, CRISTO TEM PODER. CORO CRISTO TEM PODER E SABE LUTAR, ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ; CRISTO TEM PODER E SABE LUTAR, ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ. ESTA ROCHA É ÁGUA E QUEM TEM SUAS MÁGOAS ELE VAI TIRAR; NUNCA ESTOU SOZINHO, CRISTO É MEU CAMINHO, NELE TENHO PAZ. TEM PAZ VERDADEIRA QUEM NESTA ROCHA FIRMADO ESTÁ; CRISTO É A VERDADE E, SE CRERES NELE, TE LIBERTARÁ. ELE É PODEROSO, ELE É O LEÃO DA TRIBO DE JUDÁ.',  1);
