INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('165', '165 - MARAVILHOSA GRAÇA', 'MARAVILHOSA GRAÇA, MAIOR QUE O MEU PECAR, COMO PODER CANTÁ-LA, COMO HEI DE COMEÇAR? POIS ALIVIA A MINH’ALMA E VIVO EM TODA A CALMA PELA MARAVILHOSA GRAÇA DE JESUS.\n\nCORO GRAÇA, QUÃO MARAVILHOSA A GRAÇA DE JESUS, ALTA COMO O FIRMAMENTO, ALTA E SEM FIM, GRAÇA, QUÃO MARAVILHOSA, SUFICIENTE A MIM; É MAIOR QUE A MINHA VIDA INÚTIL, É MAIOR QUE O MEU PECADO VIL, O NOME DE JESUS ENGRANDECEI E GLÓRIA DAI!\n\nMARAVILHOSA GRAÇA, QUÃO RICAS BÊNÇÃOS TRAZ, O SEU PODER TRANSFORMA O PECADOR FALAZ; ESTOU SALVO COM A VERDADE POR TODA A ETERNIDADE, PELA MARAVILHOSA GRAÇA DE JESUS.\n\nCORO GRAÇA, QUÃO MARAVILHOSA A GRAÇA DE JESUS, ALTA COMO O FIRMAMENTO, ALTA E SEM FIM, GRAÇA, QUÃO MARAVILHOSA, SUFICIENTE A MIM; É MAIOR QUE A MINHA VIDA INÚTIL, É MAIOR QUE O MEU PECADO VIL, O NOME DE JESUS ENGRANDECEI E GLÓRIA DAI!', 'MARAVILHOSA GRAÇA, MAIOR QUE O MEU PECAR, COMO PODER CANTÁ-LA, COMO HEI DE COMEÇAR? POIS ALIVIA A MINH’ALMA E VIVO EM TODA A CALMA PELA MARAVILHOSA GRAÇA DE JESUS. CORO GRAÇA, QUÃO MARAVILHOSA A GRAÇA DE JESUS, ALTA COMO O FIRMAMENTO, ALTA E SEM FIM, GRAÇA, QUÃO MARAVILHOSA, SUFICIENTE A MIM; É MAIOR QUE A MINHA VIDA INÚTIL, É MAIOR QUE O MEU PECADO VIL, O NOME DE JESUS ENGRANDECEI E GLÓRIA DAI! MARAVILHOSA GRAÇA, QUÃO RICAS BÊNÇÃOS TRAZ, O SEU PODER TRANSFORMA O PECADOR FALAZ; ESTOU SALVO COM A VERDADE POR TODA A ETERNIDADE, PELA MARAVILHOSA GRAÇA DE JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('166', '166 - Ó AMADO DE MINH´ALMA', 'Ó AMADO DE MINH’ALMA, ONDE APASCENTAS O TEU REBANHO? ENTRE OS LÍRIOS APASCENTAS, EU SOU TEU E TU ÉS MEU, Ó AMADO MEU.\n\nCORO VEM, AMADO, AO TEU JARDIM, ENTRE OS LÍRIOS, PASTOREAR; Ó VEM, SIM, VEM, HÁ UM REBANHO A TE ESPERAR.\n\nÓ AMADO DE MINH’ALMA, OUÇO TUA VOZ, QUE É TÃO SUAVE, QUE ME DIZ: “LEVANTA E VEM”; LEVANTAR-ME-EI E IREI, Ó AMADO MEU.\n\nCORO VEM, AMADO, AO TEU JARDIM, ENTRE OS LÍRIOS, PASTOREAR; Ó VEM, SIM, VEM, HÁ UM REBANHO A TE ESPERAR.\n\nOUÇO A VOZ DO MEU AMADO, EI-LO GALGANDO POR SOBRE OS MONTES; QUERO ESTAR COM O MEU AMADO NO JARDIM DE FRUTOS BONS, Ó AMADO MEU.\n\nCORO VEM, AMADO, AO TEU JARDIM, ENTRE OS LÍRIOS, PASTOREAR; Ó VEM, SIM, VEM, HÁ UM REBANHO A TE ESPERAR.\nBIS', 'Ó AMADO DE MINH’ALMA, ONDE APASCENTAS O TEU REBANHO? ENTRE OS LÍRIOS APASCENTAS, EU SOU TEU E TU ÉS MEU, Ó AMADO MEU. CORO VEM, AMADO, AO TEU JARDIM, ENTRE OS LÍRIOS, PASTOREAR; Ó VEM, SIM, VEM, HÁ UM REBANHO A TE ESPERAR. Ó AMADO DE MINH’ALMA, OUÇO TUA VOZ, QUE É TÃO SUAVE, QUE ME DIZ: LEVANTA E VEM; LEVANTAR-ME-EI E IREI, Ó AMADO MEU. OUÇO A VOZ DO MEU AMADO, EI-LO GALGANDO POR SOBRE OS MONTES; QUERO ESTAR COM O MEU AMADO NO JARDIM DE FRUTOS BONS, Ó AMADO MEU.',  1);
