INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('168', '168 - TU ÉS FIEL, SENHOR', 'TU ÉS FIEL, SENHOR, MEU PAI CELESTE, PLENO PODER AOS TEUS FILHOS DARÁS; NUNCA MUDASTE, TU NUNCA FALTASTE: TAL COMO ERAS, TU SEMPRE SERÁS.\n\nCORO TU ÉS FIEL, SENHOR, TU ÉS FIEL, SENHOR, DIA APÓS DIA, COM BÊNÇÃOS SEM FIM; TUA MERCÊ ME SUSTENTA E ME GUARDA, TU ÉS FIEL, SENHOR, FIEL A MIM.\n\nFLORES E FRUTOS, MONTANHAS E MARES, SOL, LUA, ESTRELAS NO CÉU A BRILHAR, TUDO CRIASTE NA TERRA E NOS ARES, TODO O UNIVERSO VEM, POIS, TE LOUVAR!\n\nCORO TU ÉS FIEL, SENHOR, TU ÉS FIEL, SENHOR, DIA APÓS DIA, COM BÊNÇÃOS SEM FIM; TUA MERCÊ ME SUSTENTA E ME GUARDA, TU ÉS FIEL, SENHOR, FIEL A MIM.\n\nPLENO PERDÃO TU DÁS, PAZ, SEGURANÇA, CADA MOMENTO ME GUIAS, SENHOR; E NO PORVIR, OH! QUE DOCE ESPERANÇA, DESFRUTAREI DO TEU RICO FAVOR!\n\nCORO TU ÉS FIEL, SENHOR, TU ÉS FIEL, SENHOR, DIA APÓS DIA, COM BÊNÇÃOS SEM FIM; TUA MERCÊ ME SUSTENTA E ME GUARDA, TU ÉS FIEL, SENHOR, FIEL A MIM.', 'TU ÉS FIEL, SENHOR, MEU PAI CELESTE, PLENO PODER AOS TEUS FILHOS DARÁS; NUNCA MUDASTE, TU NUNCA FALTASTE: TAL COMO ERAS, TU SEMPRE SERÁS. CORO TU ÉS FIEL, SENHOR, TU ÉS FIEL, SENHOR, DIA APÓS DIA, COM BÊNÇÃOS SEM FIM; TUA MERCÊ ME SUSTENTA E ME GUARDA, TU ÉS FIEL, SENHOR, FIEL A MIM. FLORES E FRUTOS, MONTANHAS E MARES, SOL, LUA, ESTRELAS NO CÉU A BRILHAR, TUDO CRIASTE NA TERRA E NOS ARES, TODO O UNIVERSO VEM, POIS, TE LOUVAR! PLENO PERDÃO TU DÁS, PAZ, SEGURANÇA, CADA MOMENTO ME GUIAS, SENHOR; E NO PORVIR, OH! QUE DOCE ESPERANÇA, DESFRUTAREI DO TEU RICO FAVOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('169', '169 - COMO A FLOR DO CAMPO', 'COMO A FLOR DO CAMPO QUE FLORESCE, SOPRA O VENTO E JÁ DESAPARECE, COMO A NÉVOA QUE SE DESVANECE, ASSIM BREVES SÃO OS NOSSOS DIAS.\n\nMAS DE ETERNIDADE A ETERNIDADE É, Ó SENHOR DEUS, TUA BONDADE, TEU AMOR E TUA FIDELIDADE TU ESTENDES SOBRE OS QUE TE TEMEM. CORO (2X) TU ÉS FIEL, SENHOR, SIM, TU ÉS FIEL.\n\nCÉUS E TERRA ENVELHECERÃO, AS COISAS CRIADAS TODAS PASSARÃO, COMO ROUPAS VELHAS ELAS CAIRÃO, PELO TEU PODER AS MUDARÁS. CORO (2X) TU ÉS FIEL, SENHOR, SIM, TU ÉS FIEL.', 'COMO A FLOR DO CAMPO QUE FLORESCE, SOPRA O VENTO E JÁ DESAPARECE, COMO A NÉVOA QUE SE DESVANECE, ASSIM BREVES SÃO OS NOSSOS DIAS. MAS DE ETERNIDADE A ETERNIDADE É, Ó SENHOR DEUS, TUA BONDADE, TEU AMOR E TUA FIDELIDADE TU ESTENDES SOBRE OS QUE TE TEMEM. CORO  TU ÉS FIEL, SENHOR, SIM, TU ÉS FIEL. CÉUS E TERRA ENVELHECERÃO, AS COISAS CRIADAS TODAS PASSARÃO, COMO ROUPAS VELHAS ELAS CAIRÃO, PELO TEU PODER AS MUDARÁS. CORO  TU ÉS FIEL, SENHOR, SIM, TU ÉS FIEL.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('170', '170 - EU SOU TÃO FELIZ COM O MEU JESUS', 'EU SOU TÃO FELIZ COM O MEU JESUS, ELE MORREU NUMA CRUZ PRA SALVAR O PECADOR; ELE É O CAMINHO, A VERDADE E A VIDA ELE É JESUS, O SENHOR, NOSSO ETERNO REDENTOR!\n\nCORO (2X) NOVA CRIATURA SOU, PELO SANGUE DE JESUS, REDIMIDO PARA HERDAR A VIDA ETERNA!\n\nEU SOU TÃO FELIZ COM O MEU JESUS, ELE MORREU NUMA CRUZ PRA SALVAR O PECADOR.\n\nCORO (2X) NOVA CRIATURA SOU, PELO SANGUE DE JESUS, REDIMIDO PARA HERDAR A VIDA ETERNA! FINAL: REDIMIDO PARA HERDAR A VIDA ETERNA.', 'EU SOU TÃO FELIZ COM O MEU JESUS, ELE MORREU NUMA CRUZ PRA SALVAR O PECADOR; ELE É O CAMINHO, A VERDADE E A VIDA ELE É JESUS, O SENHOR, NOSSO ETERNO REDENTOR! CORO  NOVA CRIATURA SOU, PELO SANGUE DE JESUS, REDIMIDO PARA HERDAR A VIDA ETERNA! EU SOU TÃO FELIZ COM O MEU JESUS, ELE MORREU NUMA CRUZ PRA SALVAR O PECADOR. CORO  NOVA CRIATURA SOU, PELO SANGUE DE JESUS, REDIMIDO PARA HERDAR A VIDA ETERNA!  REDIMIDO PARA HERDAR A VIDA ETERNA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('171', '171 - O AMOR DE JESUS É GRANDE', 'O AMOR DE JESUS É GRANDE, IMENSURÁVEL ESSE AMOR, NÃO SE PODE COMPARAR, NEM MEDIR, O INFINITO AMOR DE DEUS.\nBIS\n\nDEMONSTRADO LÁ NA CRUZ POR JESUS, MEU REI, PRA SALVAR O POBRE PECADOR, OH! QUE GRANDE AMOR. INSTRUMENTOS\n\nO AMOR DE JESUS É GRANDE, IMENSURÁVEL ESSE AMOR, NÃO SE PODE COMPARAR, NEM MEDIR, O INFINITO AMOR DE DEUS.\n\nDEMONSTRADO LÁ NA CRUZ POR JESUS, MEU REI, PRA SALVAR O POBRE PECADOR, OH! QUE GRANDE AMOR. FINAL: (2X) OH! QUE GRANDE AMOR.\nBIS', 'O AMOR DE JESUS É GRANDE, IMENSURÁVEL ESSE AMOR, NÃO SE PODE COMPARAR, NEM MEDIR, O INFINITO AMOR DE DEUS. DEMONSTRADO LÁ NA CRUZ POR JESUS, MEU REI, PRA SALVAR O POBRE PECADOR, OH! QUE GRANDE AMOR. INSTRUMENTOS DEMONSTRADO LÁ NA CRUZ POR JESUS, MEU REI, PRA SALVAR O POBRE PECADOR, OH! QUE GRANDE AMOR.   OH! QUE GRANDE AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('172', '172 - SENHOR, TE AMO, TE AMO', 'CORO SENHOR, TE AMO, TE AMO, PORQUE SOFRESTE POR MIM, NA CRUZ MORRESTE, MEU MESTRE, EU TE AMO, TE AMO, JESUS.\n\nA DOR QUE ERA PARA MIM LEVOU JESUS, MEU SENHOR; COMO É BOM TER CRISTO AO MEU LADO, OH! QUANTO AMO JESUS, MEU SENHOR.\n\nCORO SENHOR, TE AMO, TE AMO, PORQUE SOFRESTE POR MIM, NA CRUZ MORRESTE, MEU MESTRE, EU TE AMO, TE AMO, JESUS.\n\nA CRISTO ENTREGO MEU VIVER PORQUE EU SEI QUE CUIDA DE MIM; ELE É O AMADO DE MINH’ALMA, OH! QUANTO AMO JESUS, MEU SENHOR.\n\nCORO SENHOR, TE AMO, TE AMO, PORQUE SOFRESTE POR MIM, NA CRUZ MORRESTE, MEU MESTRE, EU TE AMO, TE AMO, JESUS.', 'CORO SENHOR, TE AMO, TE AMO, PORQUE SOFRESTE POR MIM, NA CRUZ MORRESTE, MEU MESTRE, EU TE AMO, TE AMO, JESUS. A DOR QUE ERA PARA MIM LEVOU JESUS, MEU SENHOR; COMO É BOM TER CRISTO AO MEU LADO, OH! QUANTO AMO JESUS, MEU SENHOR. A CRISTO ENTREGO MEU VIVER PORQUE EU SEI QUE CUIDA DE MIM; ELE É O AMADO DE MINH’ALMA, OH! QUANTO AMO JESUS, MEU SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('173', '173 - QUANDO JESUS UM DIA', 'QUANDO JESUS UM DIA MEU PECADO CARREGOU, PELO SANGUE DERRAMADO MINHA VIDA ELE LAVOU. ALGO NOVO ACONTECEU, TÃO SUBLIME E TÃO REAL; CHORAR EU NÃO SABIA, SÓ DE AGONIA E DOR.\n\nCHORO AGORA DE ALEGRIA, ALELUIA AO MEU SENHOR; MEU PECADO RESGATOU, MINHA VIDA TRANSFORMOU. ALGO NOVO ACONTECEU, TÃO SUBLIME E TÃO REAL; CHORO AGORA DE ALEGRIA, ALELUIA AO MEU SENHOR.', 'QUANDO JESUS UM DIA MEU PECADO CARREGOU, PELO SANGUE DERRAMADO MINHA VIDA ELE LAVOU. ALGO NOVO ACONTECEU, TÃO SUBLIME E TÃO REAL; CHORAR EU NÃO SABIA, SÓ DE AGONIA E DOR. CHORO AGORA DE ALEGRIA, ALELUIA AO MEU SENHOR; MEU PECADO RESGATOU, MINHA VIDA TRANSFORMOU. ALGO NOVO ACONTECEU, TÃO SUBLIME E TÃO REAL; CHORO AGORA DE ALEGRIA, ALELUIA AO MEU SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('174', '174 - A MINHA HERANÇA É O SENHOR', 'A MINHA HERANÇA É O SENHOR (É O SENHOR), NÃO TENHO OUTRO BEM MAIOR; AINDA QUE ME OFEREÇAM. TODA RIQUEZA DESTE MUNDO, NÃO TROCAREI A MINHA HERANÇA, POIS CUSTOU UM ALTO PREÇO E ESTE PREÇO FOI O SANGUE DE JESUS.\n\nA MINHA HERANÇA É O SENHOR (É O SENHOR), NÃO TENHO OUTRO BEM MAIOR; AINDA QUE ME OFEREÇAM. TODA RIQUEZA DESTE MUNDO, NÃO TROCAREI A MINHA HERANÇA, POIS CUSTOU UM ALTO PREÇO E ESTE PREÇO FOI O SANGUE DE JESUS.\n\nFINAL: (2X) E ESTE ALTO PREÇO FOI SANGUE DE JESUS', 'A MINHA HERANÇA É O SENHOR (É O SENHOR), NÃO TENHO OUTRO BEM MAIOR; AINDA QUE ME OFEREÇAM. TODA RIQUEZA DESTE MUNDO, NÃO TROCAREI A MINHA HERANÇA, POIS CUSTOU UM ALTO PREÇO E ESTE PREÇO FOI O SANGUE DE JESUS. E ESTE ALTO PREÇO FOI SANGUE DE JESUS',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('175', '175 - DESDE O DIA EM QUE TE CONHECI', 'DESDE O DIA EM QUE TE CONHECI, TODA A MINHA VIDA DEI A TI; OPERASTE EM MEU SER ALGO QUE NÃO SEI DIZER. EU SÓ SEI QUE TU ÉS MEU BEM QUERER.\n\nCORO MARANATA! VEM, JESUS! MARANATA! VEM, SENHOR JESUS! MEU DESEJO É TE VER, FACE A FACE TE ADORAR. ORA VEM, SENHOR, ME BUSCAR.\n\nORA VEM, SENHOR, ME BUSCAR. MUITAS LUTAS EU PASSEI PRA TE SERVIR, MAS TEU CONSOLO E TEU AMOR ME SUSTENTARAM, AS PROMESSAS SE CUMPRIRAM, RENOVANDO MEU VIGOR. COMO POSSO TE ESQUECER, Ó MEU SENHOR?\n\nCORO MARANATA! VEM, JESUS! MARANATA! VEM, SENHOR JESUS! MEU DESEJO É TE VER, FACE A FACE TE ADORAR. ORA VEM, SENHOR, ME BUSCAR.\n\nTE SERVIR É TUDO QUE EU QUERO, SENHOR,TE EXALTAR, ENGRANDECER, TE BENDIZER; TU ME AMASTE, MEU SENHOR. MEU JESUS, MEU SALVADOR, MEU DESEJO É TE VER NA ETERNIDADE.\n\nCORO MARANATA! VEM, JESUS! MARANATA! VEM, SENHOR JESUS! MEU DESEJO É TE VER, FACE A FACE TE ADORAR. ORA VEM, SENHOR, ME BUSCAR.\n\nFINAL: MEU DESEJO É TE VER, FACE A FACE TE ADORAR, EU ESTOU A TE ESPERAR.', 'DESDE O DIA EM QUE TE CONHECI, TODA A MINHA VIDA DEI A TI; OPERASTE EM MEU SER ALGO QUE NÃO SEI DIZER. EU SÓ SEI QUE TU ÉS MEU BEM QUERER. CORO MARANATA! VEM, JESUS! MARANATA! VEM, SENHOR JESUS! MEU DESEJO É TE VER, FACE A FACE TE ADORAR. ORA VEM, SENHOR, ME BUSCAR. ORA VEM, SENHOR, ME BUSCAR. MUITAS LUTAS EU PASSEI PRA TE SERVIR, MAS TEU CONSOLO E TEU AMOR ME SUSTENTARAM, AS PROMESSAS SE CUMPRIRAM, RENOVANDO MEU VIGOR. COMO POSSO TE ESQUECER, Ó MEU SENHOR? TE SERVIR É TUDO QUE EU QUERO, SENHOR,TE EXALTAR, ENGRANDECER, TE BENDIZER; TU ME AMASTE, MEU SENHOR. MEU JESUS, MEU SALVADOR, MEU DESEJO É TE VER NA ETERNIDADE. MEU DESEJO É TE VER, FACE A FACE TE ADORAR, EU ESTOU A TE ESPERAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('176', '176 - NÃO EXISTE OUTRO AMOR IGUAL AO TEU, SENHOR', 'NÃO EXISTE OUTRO AMOR IGUAL AO TEU, SENHOR, NÃO EXISTE COMPAIXÃO IGUAL À TUA COMPAIXÃO, NÃO HÁ OUTRO CAMINHO QUE NOS CONDUZ AO CÉU NÃO EXISTE OUTRO NOME QUE POSSA NOS SALVAR.\n\nJESUS, TU ÉS O ÚNICO SENHOR, ÉS A LUZ, ÉS O CAMINHO, ÉS MEU AMOR; O TEU NOME É SOBRE OS NOMES. ÉS JESUS, ÉS A RAZÃO DO MEU VIVER.\n\nNÃO EXISTE OUTRO AMOR IGUAL AO TEU SENHOR, NÃO EXISTE COMPAIXÃO IGUAL À TUA COMPAIXÃO, NÃO HÁ OUTRO CAMINHO QUE NOS CONDUZ AO CÉU NÃO EXISTE OUTRO NOME QUE POSSA NOS SALVAR.\n\nJESUS, TU ÉS O ÚNICO SENHOR, ÉS A LUZ, ÉS O CAMINHO, ÉS MEU AMOR; O TEU NOME É SOBRE OS NOMES. ÉS JESUS, ÉS A RAZÃO DO MEU VIVER. FINAL: DO MEU VIVER.\nBIS', 'NÃO EXISTE OUTRO AMOR IGUAL AO TEU, SENHOR, NÃO EXISTE COMPAIXÃO IGUAL À TUA COMPAIXÃO, NÃO HÁ OUTRO CAMINHO QUE NOS CONDUZ AO CÉU NÃO EXISTE OUTRO NOME QUE POSSA NOS SALVAR. JESUS, TU ÉS O ÚNICO SENHOR, ÉS A LUZ, ÉS O CAMINHO, ÉS MEU AMOR; O TEU NOME É SOBRE OS NOMES. ÉS JESUS, ÉS A RAZÃO DO MEU VIVER. NÃO EXISTE OUTRO AMOR IGUAL AO TEU SENHOR, NÃO EXISTE COMPAIXÃO IGUAL À TUA COMPAIXÃO, NÃO HÁ OUTRO CAMINHO QUE NOS CONDUZ AO CÉU NÃO EXISTE OUTRO NOME QUE POSSA NOS SALVAR. JESUS, TU ÉS O ÚNICO SENHOR, ÉS A LUZ, ÉS O CAMINHO, ÉS MEU AMOR; O TEU NOME É SOBRE OS NOMES. ÉS JESUS, ÉS A RAZÃO DO MEU VIVER.  DO MEU VIVER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('177', '177 - SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM', 'SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM, NÃO ESQUECEREI TEU GRANDE AMOR POR MIM. SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM, NÃO ESQUECEREI TEU GRANDE AMOR POR MIM.\n\nCORO NÃO FOI EM VÃO O TEU SANGUE VERTIDO NA CRUZ, NÃO FOI EM VÃO, EU TE LOUVO PRA SEMPRE, JESUS. INSTRUMENTOS\n\nÉ TEU MEU LOUVOR, MINHA ADORAÇÃO, ÉS TU MEU PASTOR, É TEU TODO O MEU CORAÇÃO.\n\nCORO NÃO FOI EM VÃO O TEU SANGUE VERTIDO NA CRUZ, NÃO FOI EM VÃO, EU TE LOUVO PRA SEMPRE, JESUS. FINAL: EU TE LOUVO PRA SEMPRE, JESUS, EU TE LOUVO PRA SEMPRE, JESUS.', 'SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM, NÃO ESQUECEREI TEU GRANDE AMOR POR MIM. SEMPRE VOU LEMBRAR O QUE FIZESTE POR MIM, NÃO ESQUECEREI TEU GRANDE AMOR POR MIM. CORO NÃO FOI EM VÃO O TEU SANGUE VERTIDO NA CRUZ, NÃO FOI EM VÃO, EU TE LOUVO PRA SEMPRE, JESUS. INSTRUMENTOS É TEU MEU LOUVOR, MINHA ADORAÇÃO, ÉS TU MEU PASTOR, É TEU TODO O MEU CORAÇÃO. CORO NÃO FOI EM VÃO O TEU SANGUE VERTIDO NA CRUZ, NÃO FOI EM VÃO, EU TE LOUVO PRA SEMPRE, JESUS.  EU TE LOUVO PRA SEMPRE, JESUS, EU TE LOUVO PRA SEMPRE, JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('178', '178 - O DEUS A QUEM SERVIMOS', 'O DEUS A QUEM SERVIMOS É DEUS FORTE NA BATALHA, É DEUS FIEL; O DEUS A QUEM SERVIMOS NÃO NOS DESAMPARA JAMAIS. O DEUS A QUEM SERVIMOS É UM DEUS PERDOADOR, É DEUS DE AMOR; ELE É O NOSSO GUARDADOR, É O BOM PASTOR.\n\nCORO NÓS PERTENCEMOS A TI, SENHOR, SOMOS TEUS SERVOS, DE QUEM TENS CUIDADO COM AMOR; NÓS PERTENCEMOS A TI, SENHOR, E PRA TEU LOUVOR ESTAMOS AQUI.\n\nO DEUS A QUEM SERVIMOS PÕE EM NOSSOS LÁBIOS UM NOVO LOUVOR, O DEUS A QUEM SERVIMOS NOS DÁ SEMPRE A VITÓRIA. O DEUS A QUEM SERVIMOS ESTÁ AQUI PRESENTE ENTRE NÓS. PODEMOS SENTIR SUA PRESENÇA, PODEMOS OUVIR SUA DOCE VOZ, PODEMOS CONTEMPLAR AS MARAVILHAS DAS SUAS MÃOS.\n\nCORO NÓS PERTENCEMOS A TI, SENHOR, SOMOS TEUS SERVOS, DE QUEM TENS CUIDADO COM AMOR; NÓS PERTENCEMOS A TI, SENHOR, E PRA TEU LOUVOR ESTAMOS AQUI.', 'O DEUS A QUEM SERVIMOS É DEUS FORTE NA BATALHA, É DEUS FIEL; O DEUS A QUEM SERVIMOS NÃO NOS DESAMPARA JAMAIS. O DEUS A QUEM SERVIMOS É UM DEUS PERDOADOR, É DEUS DE AMOR; ELE É O NOSSO GUARDADOR, É O BOM PASTOR. CORO NÓS PERTENCEMOS A TI, SENHOR, SOMOS TEUS SERVOS, DE QUEM TENS CUIDADO COM AMOR; NÓS PERTENCEMOS A TI, SENHOR, E PRA TEU LOUVOR ESTAMOS AQUI. O DEUS A QUEM SERVIMOS PÕE EM NOSSOS LÁBIOS UM NOVO LOUVOR, O DEUS A QUEM SERVIMOS NOS DÁ SEMPRE A VITÓRIA. O DEUS A QUEM SERVIMOS ESTÁ AQUI PRESENTE ENTRE NÓS. PODEMOS SENTIR SUA PRESENÇA, PODEMOS OUVIR SUA DOCE VOZ, PODEMOS CONTEMPLAR AS MARAVILHAS DAS SUAS MÃOS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('179', '179 - PELA FÉ EU POSSO CONTEMPLAR JESUS', 'PELA FÉ EU POSSO CONTEMPLAR JESUS, (PELA FÉ EU POSSO VER) PELA FÉ EU VEJO O BRILHO DA SUA LUZ, PELA FÉ EU POSSO VER OS ANJOS CANTANDO, EXALTANDO O NOSSO REI, O SALVADOR.\n\nPELA FÉ EU SINTO NO MEU CORAÇÃO (PELA FÉ POSSO SENTIR) A PRESENÇA DO ESPÍRITO DE DEUS, PELA FÉ EU SINTO SUA MÃO TOCAR EM MIM PELA FÉ POSSO SENTIR SEU GRANDE AMOR.\n\nE PELA FÉ VOU CAMINHANDO ATÉ CHEGAR À TERRA PROMETIDA, E PELA FÉ JÁ OUÇO O CANTO DE VITÓRIA: CRISTO REINANDO COM OS FIÉIS NA SUA GLÓRIA.\nBIS', 'PELA FÉ EU POSSO CONTEMPLAR JESUS, (PELA FÉ EU POSSO VER) PELA FÉ EU VEJO O BRILHO DA SUA LUZ, PELA FÉ EU POSSO VER OS ANJOS CANTANDO, EXALTANDO O NOSSO REI, O SALVADOR. PELA FÉ EU SINTO NO MEU CORAÇÃO (PELA FÉ POSSO SENTIR) A PRESENÇA DO ESPÍRITO DE DEUS, PELA FÉ EU SINTO SUA MÃO TOCAR EM MIM PELA FÉ POSSO SENTIR SEU GRANDE AMOR. E PELA FÉ VOU CAMINHANDO ATÉ CHEGAR À TERRA PROMETIDA, E PELA FÉ JÁ OUÇO O CANTO DE VITÓRIA: CRISTO REINANDO COM OS FIÉIS NA SUA GLÓRIA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('180', '180 - ENCONTREI O GRANDE AMOR', 'ENCONTREI O GRANDE AMOR, AMOR QUE SARA A DOR; QUE CURA AS FERIDAS. E TRAZ SENTIDO À VIDA; AMOR QUE AQUECE A ALMA, CONFORTA E ACALMA O CORAÇÃO AFLITO E O FAZ ALCANÇAR A PAZ.\n\nAMOR VERDADEIRO ENCONTREI EM TI, JESUS, QUE FOI DEMONSTRADO ALI NA CRUZ; AMOR LINDO E PURO, AMOR TÃO SINCERO, QUE NÃO SE MEDE, NEM SE PODE COMPARAR.\n\nAMOR INCOMPARÁVEL QUE FEZ MEU JESUS MORRER INOCENTE NAQUELA CRUZ. AMOR QUE É MAIS FORTE QUE A PRÓPRIA MORTE, QUE ME SALVOU;\n\nCOMO AGRADECER ESSE TÃO GRANDE AMOR? E COMO EXPRESSAR COM PALAVRAS, SENHOR, TEU SACRIFÍCIO MARCADO DE DOR, POR MIM PECADOR?\nBIS', 'ENCONTREI O GRANDE AMOR, AMOR QUE SARA A DOR; QUE CURA AS FERIDAS. E TRAZ SENTIDO À VIDA; AMOR QUE AQUECE A ALMA, CONFORTA E ACALMA O CORAÇÃO AFLITO E O FAZ ALCANÇAR A PAZ. AMOR VERDADEIRO ENCONTREI EM TI, JESUS, QUE FOI DEMONSTRADO ALI NA CRUZ; AMOR LINDO E PURO, AMOR TÃO SINCERO, QUE NÃO SE MEDE, NEM SE PODE COMPARAR. AMOR INCOMPARÁVEL QUE FEZ MEU JESUS MORRER INOCENTE NAQUELA CRUZ. AMOR QUE É MAIS FORTE QUE A PRÓPRIA MORTE, QUE ME SALVOU; COMO AGRADECER ESSE TÃO GRANDE AMOR? E COMO EXPRESSAR COM PALAVRAS, SENHOR, TEU SACRIFÍCIO MARCADO DE DOR, POR MIM PECADOR?',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('181', '181 - EM TI EU ENCONTRO, Ó DEUS, TODA A PAZ', 'EM TI EU ENCONTRO, Ó DEUS, TODA A PAZ, E A VIDA QUE EU QUERO VIVER; MINHA VIDA TRANSBORDA DE GOZO E DE PAZ, JESUS ME TRANSPORTA AO CÉU. SENHOR, TE ADORO, ME ACHEGO A TI, EM TI ME ALEGRO, JESUS.\n\nCORO TU ÉS MINHA PAZ, A FONTE DA VIDA. SENHOR, SOU TEU, TU ÉS MINHA VIDA. INSTRUMENTOS\n\nJESUS ME TRANSPORTA ÀS MANSÕES CELESTIAIS, MINHA VIDA TRANSBORDA DE PAZ. ME FAZ DESCANSAR EM TEU LINDO JARDIM ENTRE OS LÍRIOS TE ADORO, SENHOR.\n\nPASSEIO ENTRE OS LÍRIOS, EM TEU LINDO JARDIM. JESUS ME TRANSPORTA AO CÉU; RENOVA MINHA FORÇA, SE FRACO ESTOU. EM TI REJUBILO, SENHOR.\n\nCORO (2X) TU ÉS MINHA PAZ, A FONTE DA VIDA. SENHOR, SOU TEU, TU ÉS MINHA VIDA.', 'EM TI EU ENCONTRO, Ó DEUS, TODA A PAZ, E A VIDA QUE EU QUERO VIVER; MINHA VIDA TRANSBORDA DE GOZO E DE PAZ, JESUS ME TRANSPORTA AO CÉU. SENHOR, TE ADORO, ME ACHEGO A TI, EM TI ME ALEGRO, JESUS. CORO TU ÉS MINHA PAZ, A FONTE DA VIDA. SENHOR, SOU TEU, TU ÉS MINHA VIDA. INSTRUMENTOS JESUS ME TRANSPORTA ÀS MANSÕES CELESTIAIS, MINHA VIDA TRANSBORDA DE PAZ. ME FAZ DESCANSAR EM TEU LINDO JARDIM ENTRE OS LÍRIOS TE ADORO, SENHOR. PASSEIO ENTRE OS LÍRIOS, EM TEU LINDO JARDIM. JESUS ME TRANSPORTA AO CÉU; RENOVA MINHA FORÇA, SE FRACO ESTOU. EM TI REJUBILO, SENHOR. CORO  TU ÉS MINHA PAZ, A FONTE DA VIDA. SENHOR, SOU TEU, TU ÉS MINHA VIDA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('182', '182 - COMO É BOM SERVIR AO SENHOR', 'COMO É BOM SERVIR AO SENHOR, OUVIR SUA VOZ A ORIENTAR: “VAI, MEU SERVO, FALAR DO MEU AMOR” E PODER DIZER DE TODO O CORAÇÃO: EIS-ME AQUI.\nBIS\n\nE MELHOR AINDA É SABER QUE NÃO VOU SOZINHO. O SENHOR VAI ADIANTE, ME DANDO CONDIÇÃO PARA A SUA OBRA REALIZAR. FINAL: PARA A SUA OBRA REALIZAR.\nBIS', 'COMO É BOM SERVIR AO SENHOR, OUVIR SUA VOZ A ORIENTAR: VAI, MEU SERVO, FALAR DO MEU AMOR E PODER DIZER DE TODO O CORAÇÃO: EIS-ME AQUI. E MELHOR AINDA É SABER QUE NÃO VOU SOZINHO. O SENHOR VAI ADIANTE, ME DANDO CONDIÇÃO PARA A SUA OBRA REALIZAR.  PARA A SUA OBRA REALIZAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('183', '183 - A MINHA ALEGRIA', 'A MINHA ALEGRIA É PERTENCER AO GRANDE SALVADOR; SABER QUE MUITO EM BREVE O VEREI; SABER QUE COM OS ANJOS EU O LOUVAREI. A MINHA ALEGRIA É SABER QUE PARA SEMPRE COM CRISTO VIVEREI.\n\nCORO EXISTE UM LUGAR DE PAZ CELESTIAL (CELESTIAL). QUANDO OS MEUS PÉS ALI TOCAREM, SEI QUE AS LUTAS VÃO CESSAR, POIS ALI É LUGAR SANTO, MORADA DO SENHOR. E AQUELE QUE VENCER DE JESUS IRÁ OUVIR: ''VINDE, BENDITOS DE MEU PAI''.\n\nA FORÇA PARA PROSSEGUIR EU SEMPRE ENCONTRO QUANDO BUSCO A TI; SABER QUE ESTÁS COMIGO A ME GUARDAR SABER QUE TUDO TU FARÁS PRA ME PRESERVAR. A FORÇA PARA PROSSEGUIR É SABER QUE PARA SEMPRE CONTIGO VIVEREI.\n\nCORO (2X) EXISTE UM LUGAR DE PAZ CELESTIAL (CELESTIAL). QUANDO OS MEUS PÉS ALI TOCAREM, SEI QUE AS LUTAS VÃO CESSAR, POIS ALI É LUGAR SANTO, MORADA DO SENHOR. E AQUELE QUE VENCER DE JESUS IRÁ OUVIR: ''VINDE, BENDITOS DE MEU PAI''.', 'A MINHA ALEGRIA É PERTENCER AO GRANDE SALVADOR; SABER QUE MUITO EM BREVE O VEREI; SABER QUE COM OS ANJOS EU O LOUVAREI. A MINHA ALEGRIA É SABER QUE PARA SEMPRE COM CRISTO VIVEREI. CORO EXISTE UM LUGAR DE PAZ CELESTIAL (CELESTIAL). QUANDO OS MEUS PÉS ALI TOCAREM, SEI QUE AS LUTAS VÃO CESSAR, POIS ALI É LUGAR SANTO, MORADA DO SENHOR. E AQUELE QUE VENCER DE JESUS IRÁ OUVIR: VINDE, BENDITOS DE MEU PAI. A FORÇA PARA PROSSEGUIR EU SEMPRE ENCONTRO QUANDO BUSCO A TI; SABER QUE ESTÁS COMIGO A ME GUARDAR SABER QUE TUDO TU FARÁS PRA ME PRESERVAR. A FORÇA PARA PROSSEGUIR É SABER QUE PARA SEMPRE CONTIGO VIVEREI. CORO  EXISTE UM LUGAR DE PAZ CELESTIAL (CELESTIAL). QUANDO OS MEUS PÉS ALI TOCAREM, SEI QUE AS LUTAS VÃO CESSAR, POIS ALI É LUGAR SANTO, MORADA DO SENHOR. E AQUELE QUE VENCER DE JESUS IRÁ OUVIR: VINDE, BENDITOS DE MEU PAI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('184', '184 - REDENTOR ONIPOTENTE', 'REDENTOR ONIPOTENTE, PODEROSO SALVADOR, REI DOS REIS, ONIPRESENTE É JESUS, MEU BOM SENHOR.\n\nCORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. ÁGUA VIVA, PÃO DA VIDA! DOCE SOMBRA NO CALOR. E AO DESCANSO NOS CONVIDA É JESUS, MEU SALVADOR.\n\nCORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. O CORDEIRO IMACULADO, QUE SEU SANGUE DERRAMOU, MEUS PECADOS EXPIANDO, A MINH''ALMA RESGATOU.\n\nCORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. O CAMINHO QUE, SEGURO, SEMPRE PARA O CÉU CONDUZ QUEM A CRISTO PRONTO SEGUE, QUEM TOMAR A SUA CRUZ.\n\nCORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI.', 'REDENTOR ONIPOTENTE, PODEROSO SALVADOR, REI DOS REIS, ONIPRESENTE É JESUS, MEU BOM SENHOR. CORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. ÁGUA VIVA, PÃO DA VIDA! DOCE SOMBRA NO CALOR. E AO DESCANSO NOS CONVIDA É JESUS, MEU SALVADOR. CORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. O CORDEIRO IMACULADO, QUE SEU SANGUE DERRAMOU, MEUS PECADOS EXPIANDO, A MINH''ALMA RESGATOU. CORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI. O CAMINHO QUE, SEGURO, SEMPRE PARA O CÉU CONDUZ QUEM A CRISTO PRONTO SEGUE, QUEM TOMAR A SUA CRUZ. CORO OH! REFÚGIO DA MINH''ALMA, TU ÉS TUDO PARA MIM! TUDO QUANTO EU CAREÇO, ACHO, JESUS, SÓ EM TI.',  1);
