INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('186', '186 - PARA QUEM IREMOS NÓS?', 'PARA QUEM IREMOS NÓS? SÓ TU, SENHOR, TENS AS PALAVRAS DE VIDA ETERNA.\n\nNÃO HÁ OUTRO AMOR MAIOR QUE O TEU AMOR, JESUS, DEMONSTRADO ALI NA CRUZ PARA NOS SALVAR. INSTRUMENTOS\nBIS\n\nMEU CORAÇÃO A TI ENTREGO, A MINHA VIDA DEPOSITO EM TEU ALTAR.\n\nTODA A MINHA ADORAÇÃO QUERO TE OFERTAR, EM SACRIFÍCIO (EM SACRIFÍCIO) VIVO EM TEU ALTAR (EM TEU ALTAR) EM SACRIFÍCIO (EM SACRIFÍCIO) VIVO EM TEU ALTAR.\nBIS', 'PARA QUEM IREMOS NÓS? SÓ TU, SENHOR, TENS AS PALAVRAS DE VIDA ETERNA. NÃO HÁ OUTRO AMOR MAIOR QUE O TEU AMOR, JESUS, DEMONSTRADO ALI NA CRUZ PARA NOS SALVAR. INSTRUMENTOS MEU CORAÇÃO A TI ENTREGO, A MINHA VIDA DEPOSITO EM TEU ALTAR. TODA A MINHA ADORAÇÃO QUERO TE OFERTAR, EM SACRIFÍCIO (EM SACRIFÍCIO) VIVO EM TEU ALTAR (EM TEU ALTAR) EM SACRIFÍCIO (EM SACRIFÍCIO) VIVO EM TEU ALTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('187', '187 - MINH´ALMA TEM SEDE DE TI', 'MINH’ALMA TEM SEDE DE TI, TE QUERO COM TODO O MEU SER! NUMA TERRA SECA E SEM PAZ, MINHA VOZ CLAMA POR DEUS, SÓ TUA PRESENÇA, SENHOR, ME SATISFAZ!\n\nPOR ISSO VENHO, SENHOR, AO TEU SANTUÁRIO TUA GLÓRIA CONTEMPLAR! TE DOU MEU LOUVOR, TE DOU MEU AMOR, MINHA VIDA PONHO EM TEU ALTAR, EU TE AMO, MEU SENHOR! FINAL: SENHOR, EU VENHO TE ADORAR!\nBIS', 'MINH’ALMA TEM SEDE DE TI, TE QUERO COM TODO O MEU SER! NUMA TERRA SECA E SEM PAZ, MINHA VOZ CLAMA POR DEUS, SÓ TUA PRESENÇA, SENHOR, ME SATISFAZ! POR ISSO VENHO, SENHOR, AO TEU SANTUÁRIO TUA GLÓRIA CONTEMPLAR! TE DOU MEU LOUVOR, TE DOU MEU AMOR, MINHA VIDA PONHO EM TEU ALTAR, EU TE AMO, MEU SENHOR!  SENHOR, EU VENHO TE ADORAR!',  1);
