INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('189', '189 - DEPENDO DE TI, SENHOR', 'DEPENDO DE TI, SENHOR, QUE SEJAS ASSIM EM MEU VIVER: TOMA O MEU CORAÇÃO, TOTALMENTE DOMINA, Ó SENHOR! NÃO QUERO ANDAR INDEPENDENTE, SENHOR, POIS TUA VONTADE É VIDA PARA O MEU CORAÇÃO.\n\nDEPENDO DE TI, SENHOR, NÃO POSSO ANDAR SEM TEU BRAÇO FORTE; SEM TUA VONTADE HAVERÁ AGONIA, TRISTEZA E MORTE. FINAL: (2X) DEPENDO DE TI, SENHOR, DEPENDO DE TI, DEPENDO DE TI, AMÉM.', 'DEPENDO DE TI, SENHOR, QUE SEJAS ASSIM EM MEU VIVER: TOMA O MEU CORAÇÃO, TOTALMENTE DOMINA, Ó SENHOR! NÃO QUERO ANDAR INDEPENDENTE, SENHOR, POIS TUA VONTADE É VIDA PARA O MEU CORAÇÃO. DEPENDO DE TI, SENHOR, NÃO POSSO ANDAR SEM TEU BRAÇO FORTE; SEM TUA VONTADE HAVERÁ AGONIA, TRISTEZA E MORTE.   DEPENDO DE TI, SENHOR, DEPENDO DE TI, DEPENDO DE TI, AMÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('190', '190 - TODO O MEU SER TE AMA', 'TODO O MEU SER TE AMA E MINH’ALMA TE ADORA, TU ÉS MEU PASTOR, SALVADOR, ÉS O MEU VIVER E TE AMO MUITO MAIS QUE POSSA DIZER.\nBIS\n\nTU ÉS REI ACIMA DE TODOS OS REIS, TUA MORADA É ETERNA, SENHOR; TU HABITAS DENTRO EM MIM, BEM MAIOR NÃO POSSO TER POIS TU ÉS SANTO, ÉS PODEROSO, TU ÉS JESUS, SENHOR E REI.\n\nTODO O MEU SER TE AMA E MINH’ALMA TE ADORA, TU ÉS MEU PASTOR SALVADOR, ÉS O MEU VIVER E TE AMO MUITO MAIS QUE POSSA DIZER.', 'TODO O MEU SER TE AMA E MINH’ALMA TE ADORA, TU ÉS MEU PASTOR, SALVADOR, ÉS O MEU VIVER E TE AMO MUITO MAIS QUE POSSA DIZER. TU ÉS REI ACIMA DE TODOS OS REIS, TUA MORADA É ETERNA, SENHOR; TU HABITAS DENTRO EM MIM, BEM MAIOR NÃO POSSO TER POIS TU ÉS SANTO, ÉS PODEROSO, TU ÉS JESUS, SENHOR E REI. TODO O MEU SER TE AMA E MINH’ALMA TE ADORA, TU ÉS MEU PASTOR SALVADOR, ÉS O MEU VIVER E TE AMO MUITO MAIS QUE POSSA DIZER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('191', '191 - EU QUERO SENTIR', 'EU QUERO SENTIR MAIS UMA VEZ O AMOR DE DEUS AGINDO EM MINHA VIDA. VIM PRA DIZER: ME ARREPENDI, ME PERDOA, SÓ AO TEU LADO EU SEI QUE POSSO SER FELIZ.\n\nME LEMBRO DE QUANDO OUVI A TUA VOZ (OUVI A TUA VOZ) FALANDO AO MEU CORAÇÃO PELA PRIMEIRA VEZ, FOI TÃO FORTE (FOI TÃO FORTE) MARAVILHOSO, SIM! QUEM UM DIA O CONHECEU NUNCA PODE SE ESQUECER.\n\nCORO VOLTEI PRA TI MEU PAI PROS BRAÇOS TEUS COM O CORAÇÃO CARENTE E FERIDO; EU RECONHEÇO QUE SEM TI NADA SOU. INSTRUMENTOS\n\nME LEMBRO DE QUANDO OUVI A TUA VOZ (OUVI A TUA VOZ) FALANDO AO MEU CORAÇÃO PELA PRIMEIRA VEZ, FOI TÃO FORTE (FOI TÃO FORTE) MARAVILHOSO, SIM! QUEM UM DIA O CONHECEU NUNCA PODE SE ESQUECER.\n\nCORO VOLTEI PRA TI MEU PAI PROS BRAÇOS TEUS COM O CORAÇÃO CARENTE E FERIDO; EU RECONHEÇO QUE SEM TI NADA SOU.', 'EU QUERO SENTIR MAIS UMA VEZ O AMOR DE DEUS AGINDO EM MINHA VIDA. VIM PRA DIZER: ME ARREPENDI, ME PERDOA, SÓ AO TEU LADO EU SEI QUE POSSO SER FELIZ. ME LEMBRO DE QUANDO OUVI A TUA VOZ (OUVI A TUA VOZ) FALANDO AO MEU CORAÇÃO PELA PRIMEIRA VEZ, FOI TÃO FORTE (FOI TÃO FORTE) MARAVILHOSO, SIM! QUEM UM DIA O CONHECEU NUNCA PODE SE ESQUECER. CORO VOLTEI PRA TI MEU PAI PROS BRAÇOS TEUS COM O CORAÇÃO CARENTE E FERIDO; EU RECONHEÇO QUE SEM TI NADA SOU. INSTRUMENTOS CORO VOLTEI PRA TI MEU PAI PROS BRAÇOS TEUS COM O CORAÇÃO CARENTE E FERIDO; EU RECONHEÇO QUE SEM TI NADA SOU.',  1);
