INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('194', '194 - QUERO ANDAR EM TEU CAMINHO, SENHOR', 'QUERO ANDAR EM TEU CAMINHO, SENHOR, MAIS E MAIS TE CONHECER E FAZER TUA VONTADE, SENHOR; TUA VOZ OBEDECER E VIVER SOMENTE PRA TEU LOUVOR, DESFRUTANDO DO TEU GRANDE AMOR; QUERO SER FIEL, QUERO SER FIEL A TI, SENHOR.\n\nCORO TE AGRADEÇO POR PRIMEIRO ME AMAR, TE AGRADEÇO POR ME ESCOLHER, TE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) TE AGRADEÇO, POIS NÃO SOU MERECEDOR.\n\nFAZE EM MIM (FAZE EM MIM) O TEU QUERER, Ó SENHOR (TEU QUERER) QUERO SER UM VENCEDOR (VENCEDOR) PRA QUE EU POSSA DIZER (PRA QUE EU POSSA DIZER) QUE TU VIVES EM MIM E DA MINHA VIDA TU ÉS O SENHOR.\n\nCORO TE AGRADEÇO POR PRIMEIRO ME AMAR, TE AGRADEÇO POR ME ESCOLHER, TE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) TE AGRADEÇO, POIS NÃO SOU MERECEDOR.\n\nFINAL: TE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) TE AGRADEÇO, POIS NÃO SOU MERECEDOR.', 'QUERO ANDAR EM TEU CAMINHO, SENHOR, MAIS E MAIS TE CONHECER E FAZER TUA VONTADE, SENHOR; TUA VOZ OBEDECER E VIVER SOMENTE PRA TEU LOUVOR, DESFRUTANDO DO TEU GRANDE AMOR; QUERO SER FIEL, QUERO SER FIEL A TI, SENHOR. CORO TE AGRADEÇO POR PRIMEIRO ME AMAR, TE AGRADEÇO POR ME ESCOLHER, TE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) TE AGRADEÇO, POIS NÃO SOU MERECEDOR. FAZE EM MIM (FAZE EM MIM) O TEU QUERER, Ó SENHOR (TEU QUERER) QUERO SER UM VENCEDOR (VENCEDOR) PRA QUE EU POSSA DIZER (PRA QUE EU POSSA DIZER) QUE TU VIVES EM MIM E DA MINHA VIDA TU ÉS O SENHOR. TE AGRADEÇO (TE AGRADEÇO, MEU SENHOR) TE AGRADEÇO, POIS NÃO SOU MERECEDOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('195', '195 - GRANDE AMOR, Ó GRANDE AMOR', 'GRANDE AMOR, Ó GRANDE AMOR, É O AMOR DE DEUS. ESTE AMOR QUE HABITA EM MIM, PELA FÉ EM DEUS. COMO POSSO COMPREENDER A GRANDEZA DO AMOR DE DEUS. QUE PROFUNDO É ESSE GRANDE AMOR QUE PREENCHE TODO MEU SER.\n\nCORO GRANDE AMOR, É O AMOR DE DEUS QUE EXCEDE TODO SABER OH! QUE SUBLIME! NÃO POSSO COMPREENDER, PORQUE ME AMOU SEM MERECER.\n\nQUANDO TUDO PARECE ACABAR E A ESPERANÇA DESVANECER. QUANDO A FÉ PARECE NAUFRAGAR E O DIA ESCURECER. MESMO ASSIM CONFIO NESTE AMOR, QUE É MAIS FORTE QUE A PRÓPRIA MORTE. AS MUITAS ÁGUAS NÃO PODEM APAGAR, NEM OS RIOS O AFOGARÃO.\n\nCORO GRANDE AMOR, É O AMOR DE DEUS QUE EXCEDE TODO SABER OH! QUE SUBLIME! NÃO POSSO COMPREENDER, PORQUE ME AMOU SEM MERECER. FINAL: GRANDE AMOR DE DEUS.', 'GRANDE AMOR, Ó GRANDE AMOR, É O AMOR DE DEUS. ESTE AMOR QUE HABITA EM MIM, PELA FÉ EM DEUS. COMO POSSO COMPREENDER A GRANDEZA DO AMOR DE DEUS. QUE PROFUNDO É ESSE GRANDE AMOR QUE PREENCHE TODO MEU SER. CORO GRANDE AMOR, É O AMOR DE DEUS QUE EXCEDE TODO SABER OH! QUE SUBLIME! NÃO POSSO COMPREENDER, PORQUE ME AMOU SEM MERECER. QUANDO TUDO PARECE ACABAR E A ESPERANÇA DESVANECER. QUANDO A FÉ PARECE NAUFRAGAR E O DIA ESCURECER. MESMO ASSIM CONFIO NESTE AMOR, QUE É MAIS FORTE QUE A PRÓPRIA MORTE. AS MUITAS ÁGUAS NÃO PODEM APAGAR, NEM OS RIOS O AFOGARÃO. CORO GRANDE AMOR, É O AMOR DE DEUS QUE EXCEDE TODO SABER OH! QUE SUBLIME! NÃO POSSO COMPREENDER, PORQUE ME AMOU SEM MERECER.  GRANDE AMOR DE DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('196', '196 - O GRANDE AMOR DO MEU SENHOR', 'O GRANDE AMOR DO MEU SENHOR (M) O REI DE TODO UNIVERSO! (H) JESUS, O SOBERANO DEUS, RESPLANDECENTE ESTRELA DA MANHÃ!\n\nCOMO ENTENDER QUE MEU JESUS, UM DIA OLHOU PRA MIM, E SEM QUERER NADA EM TROCA, TÃO GRANDE AMOR ME DEU, O MEU SENHOR VIDA ETERNA DEU, MEU SALVADOR!\n\nCORO ABENÇOADOR, DEUS DE PAZ, CONSOLADOR, MEU REI E SENHOR. HOMEM SE FEZ, MORREU EM MEU LUGAR MEU REDENTOR, MINHA ESPERANÇA. MINHA ESPERANÇA É!\n\nCOMO ENTENDER QUE MEU JESUS UM DIA OLHOU PRA MIM, E SEM QUERER NADA EM TROCA, TÃO GRANDE AMOR ME DEU O MEU SENHOR! VIDA ETERNA DEU, MEU SALVADOR.\n\nCORO ABENÇOADOR, DEUS DE PAZ, CONSOLADOR, MEU REI E SENHOR. HOMEM SE FEZ, MORREU EM MEU LUGAR MEU REDENTOR, MINHA ESPERANÇA. MINHA ESPERANÇA É!\n\nA ROCHA FIRME, (A ROCHA FIRME) REFÚGIO SEGURO, (REFÚGIO SEGURO) A MINHA ALEGRIA, MEU LOUVOR É SÓ PRA TI MEU GRANDE EU SOU!\n\nCORO ABENÇOADOR, DEUS DE PAZ, CONSOLADOR, MEU REI E SENHOR. HOMEM SE FEZ, MORREU EM MEU LUGAR MEU REDENTOR, MINHA ESPERANÇA. MINHA ESPERANÇA É!\n\nFINAL: O REI DO UNIVERSO, (UM DIA OLHOU PRA MIM) O MEU GRANDE EU SOU, JESUS.', 'O GRANDE AMOR DO MEU SENHOR  O REI DE TODO UNIVERSO!  JESUS, O SOBERANO DEUS, RESPLANDECENTE ESTRELA DA MANHÃ! COMO ENTENDER QUE MEU JESUS, UM DIA OLHOU PRA MIM, E SEM QUERER NADA EM TROCA, TÃO GRANDE AMOR ME DEU, O MEU SENHOR VIDA ETERNA DEU, MEU SALVADOR! CORO ABENÇOADOR, DEUS DE PAZ, CONSOLADOR, MEU REI E SENHOR. HOMEM SE FEZ, MORREU EM MEU LUGAR MEU REDENTOR, MINHA ESPERANÇA. MINHA ESPERANÇA É! COMO ENTENDER QUE MEU JESUS UM DIA OLHOU PRA MIM, E SEM QUERER NADA EM TROCA, TÃO GRANDE AMOR ME DEU O MEU SENHOR! VIDA ETERNA DEU, MEU SALVADOR. A ROCHA FIRME, (A ROCHA FIRME) REFÚGIO SEGURO, (REFÚGIO SEGURO) A MINHA ALEGRIA, MEU LOUVOR É SÓ PRA TI MEU GRANDE EU SOU! O REI DO UNIVERSO, (UM DIA OLHOU PRA MIM) O MEU GRANDE EU SOU, JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('197', '197 - HOJE QUERO SOMENTE AGRADECER (SOMENTE AGRADECER)', 'HOJE QUERO SOMENTE AGRADECER A TI. HOJE NADA QUERO TE PEDIR. RECEBE O MEU LOUVOR, MINHA GRATIDÃO.\n\nESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS. GUIA-ME, SENHOR (GUIA-ME, SENHOR) GUIA-ME, SENHOR (GUIA-ME, SENHOR)\n\nHOJE QUERO SOMENTE AGRADECER A TI. HOJE NADA QUERO TE PEDIR. RECEBE O MEU LOUVOR, MINHA GRATIDÃO.\n\nESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS. GUIA-ME, SENHOR (GUIA-ME, SENHOR) GUIA-ME, SENHOR (GUIA-ME, SENHOR)\n\nPELA FÉ EU VOU SEGURO EM TUAS MÃOS, PELA FÉ ALCANÇAREI A SALVAÇÃO. MOSTRA-ME A DIREÇÃO. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS PRECISO DE TI, PAI (DIRIGE MEUS PASSOS). ESTE MOMENTO É PRA TUA ADORAÇÃO FINAL: TUA ADORAÇÃO.', 'HOJE QUERO SOMENTE AGRADECER A TI. HOJE NADA QUERO TE PEDIR. RECEBE O MEU LOUVOR, MINHA GRATIDÃO. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS. GUIA-ME, SENHOR (GUIA-ME, SENHOR) GUIA-ME, SENHOR (GUIA-ME, SENHOR) PELA FÉ EU VOU SEGURO EM TUAS MÃOS, PELA FÉ ALCANÇAREI A SALVAÇÃO. MOSTRA-ME A DIREÇÃO. ESTOU AQUI, PAI (SÓ TU ME CONHECES). QUERO SERVIR-TE MAIS E MAIS PRECISO DE TI, PAI (DIRIGE MEUS PASSOS). ESTE MOMENTO É PRA TUA ADORAÇÃO  TUA ADORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('198', '198 - ELE É A MINHA ALEGRIA', 'HOJE QUERO SOMENTE AGRADECER A TI. HOJE NADA QUERO TE PEDIR. RECEBE O MEU LOUVOR, MINHA GRATIDÃO.\n\n197 - HOJE QUERO SOMENTE AGRADECER (SOMENTE AGRADECER)\n\nELE É A MINHA ALEGRIA, MESMO EM MOMENTOS DIFÍCEIS, ELE SEGURA MINHA MÃO. ELE É A MINHA SEGURANÇA, MESMO EM TEMPOS DE GUERRA, ELE É QUEM ME DÁ A PAZ.\n\nSENHOR, TU ÉS A MINHA FORTALEZA. JESUS, TU ÉS A MINHA DEFESA, ÉS MEU ESCUDO E PROTEÇÃO. SENHOR, TU ÉS A ROCHA FERIDA, TU ÉS A FONTE DA VIDA, BROTANDO ÁGUAS DA SALVAÇÃO. SENHOR, TU ÉS O MEU ALIMENTO, DE TI PROVÉM TODO SUSTENTO, ÉS MINHA ÁGUA, ÉS MEU PÃO. INSTRUMENTOS\n\nELE É A MINHA ALEGRIA, MESMO EM MOMENTOS DIFÍCEIS, ELE SEGURA MINHA MÃO. ELE É A MINHA SEGURANÇA, MESMO EM TEMPOS DE GUERRA, ELE É QUEM ME DÁ A PAZ.\n\nSENHOR, TU ÉS A MINHA FORTALEZA. JESUS, TU ÉS A MINHA DEFESA, ÉS MEU ESCUDO E PROTEÇÃO. SENHOR, TU ÉS A ROCHA FERIDA, TU ÉS A FONTE DA VIDA, BROTANDO ÁGUAS DA SALVAÇÃO. SENHOR, TU ÉS O MEU ALIMENTO, DE TI PROVÉM TODO SUSTENTO, ÉS MINHA ÁGUA, ÉS MEU PÃO.\n\nFINAL: SÓ TU ÉS, SENHOR, A VIDA DO TEU POVO. EM MEIO A TREVAS, A LUZ, ÉS O CAMINHO, JESUS (BIS)', 'HOJE QUERO SOMENTE AGRADECER A TI. HOJE NADA QUERO TE PEDIR. RECEBE O MEU LOUVOR, MINHA GRATIDÃO. 197 - HOJE QUERO SOMENTE AGRADECER (SOMENTE AGRADECER) ELE É A MINHA ALEGRIA, MESMO EM MOMENTOS DIFÍCEIS, ELE SEGURA MINHA MÃO. ELE É A MINHA SEGURANÇA, MESMO EM TEMPOS DE GUERRA, ELE É QUEM ME DÁ A PAZ. SENHOR, TU ÉS A MINHA FORTALEZA. JESUS, TU ÉS A MINHA DEFESA, ÉS MEU ESCUDO E PROTEÇÃO. SENHOR, TU ÉS A ROCHA FERIDA, TU ÉS A FONTE DA VIDA, BROTANDO ÁGUAS DA SALVAÇÃO. SENHOR, TU ÉS O MEU ALIMENTO, DE TI PROVÉM TODO SUSTENTO, ÉS MINHA ÁGUA, ÉS MEU PÃO. INSTRUMENTOS SENHOR, TU ÉS A MINHA FORTALEZA. JESUS, TU ÉS A MINHA DEFESA, ÉS MEU ESCUDO E PROTEÇÃO. SENHOR, TU ÉS A ROCHA FERIDA, TU ÉS A FONTE DA VIDA, BROTANDO ÁGUAS DA SALVAÇÃO. SENHOR, TU ÉS O MEU ALIMENTO, DE TI PROVÉM TODO SUSTENTO, ÉS