INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('199', '199 - COMO É BOM OUVIR TUA VOZ', 'COMO É BOM OUVIR TUA VOZ, SENHOR, COMO É BOM SENTIR TUA PRESENÇA. O TEU AMOR NOS CONSOLA, NOS RENOVA A CADA DIA, SENHOR, GLORIOSO ÉS TU!\nBIS\n\nCORO NÓS TE LOUVAMOS, SENHOR JESUS, POR TEU AMOR DEMONSTRADO NA CRUZ. AMOR GRANDIOSO, PODEROSO, E QUE NOS DARÁ A VIDA ETERNA. INSTRUMENTOS\n\nCORO NÓS TE LOUVAMOS, SENHOR JESUS, POR TEU AMOR DEMONSTRADO NA CRUZ. AMOR GRANDIOSO, PODEROSO, E QUE NOS DARÁ A VIDA ETERNA. FINAL: VIDA ETERNA.\nBIS', 'COMO É BOM OUVIR TUA VOZ, SENHOR, COMO É BOM SENTIR TUA PRESENÇA. O TEU AMOR NOS CONSOLA, NOS RENOVA A CADA DIA, SENHOR, GLORIOSO ÉS TU! CORO NÓS TE LOUVAMOS, SENHOR JESUS, POR TEU AMOR DEMONSTRADO NA CRUZ. AMOR GRANDIOSO, PODEROSO, E QUE NOS DARÁ A VIDA ETERNA. INSTRUMENTOS CORO NÓS TE LOUVAMOS, SENHOR JESUS, POR TEU AMOR DEMONSTRADO NA CRUZ. AMOR GRANDIOSO, PODEROSO, E QUE NOS DARÁ A VIDA ETERNA.  VIDA ETERNA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('200', '200 - EU SIRVO UM DEUS', 'EU SIRVO UM DEUS QUE É TÃO BOM PRA MIM, EU SIRVO UM DEUS QUE É MEU TUDO ENFIM. EU SIRVO UM DEUS QUE AO MEU LADO ESTÁ, EU SIRVO UM DEUS QUE AO CÉU ME LEVARÁ.\n\nCORO OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, QUE NOS LIVRA DOS PERIGOS,E NOS FAZ ASSIM CANTAR. OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, ELE É REI, ELE É AMIGO, COMO NOSSO DEUS NÃO HÁ.\n\nEU SIRVO UM DEUS QUE ABRIU O MAR VERMELHO, EU SIRVO UM DEUS QUE AMOU O MUNDO INTEIRO. EU SIRVO UM DEUS QUE ENVIOU JESUS, EU SIRVO UM DEUS QUE É GRAÇA E LUZ.\n\nCORO OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, QUE NOS LIVRA DOS PERIGOS,E NOS FAZ ASSIM CANTAR. OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, ELE É REI, ELE É AMIGO, COMO NOSSO DEUS NÃO HÁ. FINAL: NÃO HÁ, NÃO HÁ.', 'EU SIRVO UM DEUS QUE É TÃO BOM PRA MIM, EU SIRVO UM DEUS QUE É MEU TUDO ENFIM. EU SIRVO UM DEUS QUE AO MEU LADO ESTÁ, EU SIRVO UM DEUS QUE AO CÉU ME LEVARÁ. CORO OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, QUE NOS LIVRA DOS PERIGOS,E NOS FAZ ASSIM CANTAR. OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, ELE É REI, ELE É AMIGO, COMO NOSSO DEUS NÃO HÁ. EU SIRVO UM DEUS QUE ABRIU O MAR VERMELHO, EU SIRVO UM DEUS QUE AMOU O MUNDO INTEIRO. EU SIRVO UM DEUS QUE ENVIOU JESUS, EU SIRVO UM DEUS QUE É GRAÇA E LUZ. CORO OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, QUE NOS LIVRA DOS PERIGOS,E NOS FAZ ASSIM CANTAR. OH! ALELUIA, NÓS SERVIMOS A UM DEUS VIVO, ELE É REI, ELE É AMIGO, COMO NOSSO DEUS NÃO HÁ.  NÃO HÁ, NÃO HÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('201', '201 - SEU CORPO CANSADO PELAS CAMINHADAS', 'SEU CORPO CANSADO PELAS CAMINHADAS, PELAS MADRUGADAS PROSTRADO EM ORAÇÃO, MEU JESUS! COM AMOR CURANDO, VIDAS LIBERTANDO, DANDO O PÃO DO CÉU À MULTIDÃO!\n\nCORO E SEM TER UM LUGAR PARA DESCANSAR, PERSEGUIDO COMO VIL MALFEITOR; POR AMOR FOI À CRUZ SEM SE DEFENDER, PARA SER MEU SALVADOR!\n\nNÃO TINHA BELEZA PRA SER DESEJADO E FOI DESPREZADO POR UM POVO QUE ERA SEU, MEU JESUS! POR DEUS AFLIGIDO, SOFREU O CASTIGO, POR PECADO QUE NÃO COMETEU!\n\nCORO E SEM TER UM LUGAR PARA DESCANSAR, PERSEGUIDO COMO VIL MALFEITOR; POR AMOR FOI À CRUZ SEM SE DEFENDER, PARA SER MEU SALVADOR!\n\nSEM TEMER A MORTE, SOB A DOR FOI FORTE, E BEBEU O CÁLICE QUE O PAI LHE PREPAROU, MEU JESUS! COMO OVELHA MUDA FOI SACRIFICADO, SEU PRECIOSO SANGUE DERRAMOU!\n\nCORO E SEM TER UM LUGAR PARA DESCANSAR, PERSEGUIDO COMO VIL MALFEITOR; POR AMOR FOI À CRUZ SEM SE DEFENDER, PARA SER MEU SALVADOR!', 'SEU CORPO CANSADO PELAS CAMINHADAS, PELAS MADRUGADAS PROSTRADO EM ORAÇÃO, MEU JESUS! COM AMOR CURANDO, VIDAS LIBERTANDO, DANDO O PÃO DO CÉU À MULTIDÃO! CORO E SEM TER UM LUGAR PARA DESCANSAR, PERSEGUIDO COMO VIL MALFEITOR; POR AMOR FOI À CRUZ SEM SE DEFENDER, PARA SER MEU SALVADOR! NÃO TINHA BELEZA PRA SER DESEJADO E FOI DESPREZADO POR UM POVO QUE ERA SEU, MEU JESUS! POR DEUS AFLIGIDO, SOFREU O CASTIGO, POR PECADO QUE NÃO COMETEU! SEM TEMER A MORTE, SOB A DOR FOI FORTE, E BEBEU O CÁLICE QUE O PAI LHE PREPAROU, MEU JESUS! COMO OVELHA MUDA FOI SACRIFICADO, SEU PRECIOSO SANGUE DERRAMOU!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('202', '202 - HÁ UMA FONTE ABERTA NO CALVÁRIO', 'HÁ UMA FONTE ABERTA NO CALVÁRIO CONTRA A IMPUREZA E O PECADO; ÁGUAS DE PERDÃO, PURIFICAÇÃO, FLUEM DE JESUS CRUCIFICADO.\n\nHÁ UMA FONTE ABERTA NO CALVÁRIO, ONDE MEU SENHOR FOI TRASPASSADO, FONTE DE PODER PRA TODO O QUE CRER, POR SUAS PISADURAS, SER CURADO.\n\nCORO TE AGRADEÇO, JESUS, PORQUE FOSTE À CRUZ PARA ABRIR ESTA FONTE DE VIDA E LUZ.\n\nPARA QUE EU VIVESSE, TU MORRESTE, PARA ME CURARES, ENFERMASTE! CORREM DO TEU LADO, Ó CRUCIFICADO, ÁGUAS VIVAS COM QUE ME SALVASTE.', 'HÁ UMA FONTE ABERTA NO CALVÁRIO CONTRA A IMPUREZA E O PECADO; ÁGUAS DE PERDÃO, PURIFICAÇÃO, FLUEM DE JESUS CRUCIFICADO. HÁ UMA FONTE ABERTA NO CALVÁRIO, ONDE MEU SENHOR FOI TRASPASSADO, FONTE DE PODER PRA TODO O QUE CRER, POR SUAS PISADURAS, SER CURADO. CORO TE AGRADEÇO, JESUS, PORQUE FOSTE À CRUZ PARA ABRIR ESTA FONTE DE VIDA E LUZ. PARA QUE EU VIVESSE, TU MORRESTE, PARA ME CURARES, ENFERMASTE! CORREM DO TEU LADO, Ó CRUCIFICADO, ÁGUAS VIVAS COM QUE ME SALVASTE.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('203', '203 - JESUS IA SUBINDO O MONTE', 'JESUS IA SUBINDO O MONTE, CARREGANDO A NOSSA CRUZ; NOSSO FARDO DE PECADO, SOBRE SI ELE LEVOU. ELE NÃO TINHA PECADO, MAS POR NÓS JESUS MORREU, PRA NOS DAR A LIBERDADE, SUA VIDA ELE DEU.\n\nPOR NÓS JESUS MORREU, FOI POR AMOR QUE ELE SE ENTREGOU, AS NOSSAS DORES LEVOU E O PECADO PERDOOU. TODA LUZ SE ESCURECEU, O DIA EM TREVAS SE TORNOU, POIS NAQUELA DURA CRUZ MORREU O FILHO DE DEUS.\n\nMAS ELE RESSUSCITOU, VENCEU A MORTE E A CRUZ, VENCEU O MAL, ME LIBERTOU DO PESO QUE ANTES CARREGUEI, LEVOU AS NOSSAS DORES E A SALVAÇÃO NOS DEU.\nBIS\n\nPOR NÓS JESUS MORREU, POR NÓS RESSUSCITOU, O PREÇO DA LIBERDADE, COM SEU SANGUE ELE PAGOU; POR NÓS JESUS MORREU, POR NÓS RESSUSCITOU, O PREÇO DA LIBERDADE, COM SEU SANGUE ELE PAGOU. AMÉM! AMÉM!', 'JESUS IA SUBINDO O MONTE, CARREGANDO A NOSSA CRUZ; NOSSO FARDO DE PECADO, SOBRE SI ELE LEVOU. ELE NÃO TINHA PECADO, MAS POR NÓS JESUS MORREU, PRA NOS DAR A LIBERDADE, SUA VIDA ELE DEU. POR NÓS JESUS MORREU, FOI POR AMOR QUE ELE SE ENTREGOU, AS NOSSAS DORES LEVOU E O PECADO PERDOOU. TODA LUZ SE ESCURECEU, O DIA EM TREVAS SE TORNOU, POIS NAQUELA DURA CRUZ MORREU O FILHO DE DEUS. MAS ELE RESSUSCITOU, VENCEU A MORTE E A CRUZ, VENCEU O MAL, ME LIBERTOU DO PESO QUE ANTES CARREGUEI, LEVOU AS NOSSAS DORES E A SALVAÇÃO NOS DEU. POR NÓS JESUS MORREU, POR NÓS RESSUSCITOU, O PREÇO DA LIBERDADE, COM SEU SANGUE ELE PAGOU; POR NÓS JESUS MORREU, POR NÓS RESSUSCITOU, O PREÇO DA LIBERDADE, COM SEU SANGUE ELE PAGOU. AMÉM! AMÉM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('204', '204 - MORRI NA CRUZ POR TI', 'MORRI NA CRUZ POR TI, MORRI PRA TE LIVRAR; MEU SANGUE, SIM, VERTI E POSSO TE SALVAR. CORO (2X) MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM?\n\nVIVI ASSIM POR TI, COM DOR, COM DISSABOR; SIM, TUDO FIZ AQUI PRA SER TEU SALVADOR. CORO (2X) MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM?\n\nEU TROUXE A SALVAÇÃO, DOS ALTOS CÉUS LOUVOR, É LIVRE MEU PERDÃO, É GRANDE MEU AMOR. CORO (2X) MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM', 'MORRI NA CRUZ POR TI, MORRI PRA TE LIVRAR; MEU SANGUE, SIM, VERTI E POSSO TE SALVAR. CORO  MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM? VIVI ASSIM POR TI, COM DOR, COM DISSABOR; SIM, TUDO FIZ AQUI PRA SER TEU SALVADOR. CORO  MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM? EU TROUXE A SALVAÇÃO, DOS ALTOS CÉUS LOUVOR, É LIVRE MEU PERDÃO, É GRANDE MEU AMOR. CORO  MORRI, MORRI NA CRUZ POR TI, QUE FAZES TU POR MIM',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('205', '205 - JESUS IA SEGUINDO', 'JESUS IA SEGUINDO PELA ESTRADA DE JERUSALÉM, ELE IA SUBIR AO MONTE PARA AO PAI ORAR, QUANDO PRESTES A TOMAR NA CRUZ OS MEUS PECADOS, E AS MINHAS CULPAS SOBRE SI LEVAR. INSTRUMENTOS\n\nDO SEU ROSTO LÁGRIMAS COMEÇAM A DESCER, MOSTRANDO A TRISTEZA QUE VEM DO CORAÇÃO; TRISTEZA ESSA QUE REVELA TODO O SEU SOFRER, ESTE FOI O PREÇO PELO MEU PERDÃO.\n\nO SOL SE PÕE E A NOITE VEM TRAZENDO A SOLIDÃO, ENVOLVENDO TUDO NO SEU DENSO E ESCURO VÉU; NAS MÃOS DO PAI TUDO ENTREGOU, DIZENDO, ENTÃO: “SEJA FEITA A TUA VONTADE AQUI, COMO NO CÉU”.\n\nDEPOIS QUE ELE CUMPRIU A SUA PARTE, DEPOIS QUE ELE ASSUMIU TUDO O QUE ERA MEU, MOSTRANDO O CAMINHO QUE ME LEVA ATÉ DEUS PAI, QUE POSSO EU FAZER, ALÉM DA MINHA VIDA LHE ENTREGAR? INSTRUMENTOS\n\nDEPOIS QUE ELE CUMPRIU A SUA PARTE, DEPOIS QUE ELE ASSUMIU TUDO O QUE ERA MEU, MOSTRANDO O CAMINHO QUE ME LEVA ATÉ DEUS PAI, QUE POSSO EU FAZER, ALÉM DA MINHA VIDA LHE ENTREGAR?', 'JESUS IA SEGUINDO PELA ESTRADA DE JERUSALÉM, ELE IA SUBIR AO MONTE PARA AO PAI ORAR, QUANDO PRESTES A TOMAR NA CRUZ OS MEUS PECADOS, E AS MINHAS CULPAS SOBRE SI LEVAR. INSTRUMENTOS DO SEU ROSTO LÁGRIMAS COMEÇAM A DESCER, MOSTRANDO A TRISTEZA QUE VEM DO CORAÇÃO; TRISTEZA ESSA QUE REVELA TODO O SEU SOFRER, ESTE FOI O PREÇO PELO MEU PERDÃO. O SOL SE PÕE E A NOITE VEM TRAZENDO A SOLIDÃO, ENVOLVENDO TUDO NO SEU DENSO E ESCURO VÉU; NAS MÃOS DO PAI TUDO ENTREGOU, DIZENDO, ENTÃO: SEJA FEITA A TUA VONTADE AQUI, COMO NO CÉU. DEPOIS QUE ELE CUMPRIU A SUA PARTE, DEPOIS QUE ELE ASSUMIU TUDO O QUE ERA MEU, MOSTRANDO O CAMINHO QUE ME LEVA ATÉ DEUS PAI, QUE POSSO EU FAZER, ALÉM DA MINHA VIDA LHE ENTREGAR? INSTRUMENTOS DEPOIS QUE ELE CUMPRIU A SUA PARTE, DEPOIS QUE ELE ASSUMIU TUDO O QUE ERA MEU, MOSTRANDO O CAMINHO QUE ME LEVA ATÉ DEUS PAI, QUE POSSO EU FAZER, ALÉM DA MINHA VIDA LHE ENTREGAR?',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('206', '206 - PAI, A TI ENTREGO O MEU ESPÍRITO', '“PAI, A TI ENTREGO O MEU ESPÍRITO, TRAGO CONSOLO AO PECADOR PARA UMA VIDA MUITO ALÉM, MUITO ALÉM DESTE MUNDO MAU.\n\nSEI QUE É DIFÍCIL ESTE MEU SOFRER, MAS POR AMOR VOU ATÉ MORRER, LEVANDO AS MARCAS DO MOMENTO E DO SOFRIMENTO QUE AQUI PASSEI; PAI, A TI ENTREGO O MEU ESPÍRITO.\n\nMAS SEI QUE VOU RESSURGIR EM UM CORPO GLORIOSO E SEI QUE VOLTAREI PRA BUSCAR A MINHA IGREJA GLORIOSA, RESPLANDECENTE DE LUZ. FINAL: GLORIOSA, RESPLANDECENTE DE LUZ.”\nBIS', 'PAI, A TI ENTREGO O MEU ESPÍRITO, TRAGO CONSOLO AO PECADOR PARA UMA VIDA MUITO ALÉM, MUITO ALÉM DESTE MUNDO MAU. SEI QUE É DIFÍCIL ESTE MEU SOFRER, MAS POR AMOR VOU ATÉ MORRER, LEVANDO AS MARCAS DO MOMENTO E DO SOFRIMENTO QUE AQUI PASSEI; PAI, A TI ENTREGO O MEU ESPÍRITO. MAS SEI QUE VOU RESSURGIR EM UM CORPO GLORIOSO E SEI QUE VOLTAREI PRA BUSCAR A MINHA IGREJA GLORIOSA, RESPLANDECENTE DE LUZ.  GLORIOSA, RESPLANDECENTE DE LUZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('207', '207 - DEUS ENVIOU SEU FILHO AMADO', 'DEUS ENVIOU SEU FILHO AMADO PARA MORRER EM MEU LUGAR; NA CRUZ SOFREU POR MEUS PECADOS, MAS O SEPULCRO VAZIO ESTÁ PORQUE ELE VIVE.\n\nCORO PORQUE ELE VIVE, POSSO CRER NO AMANHÃ; PORQUE ELE VIVE, TEMOR NÃO HÁ. MAS EU BEM SEI (EU SEI) QUE A MINHA VIDA ESTÁ NAS MÃOS DO MEU JESUS, QUE VIVO ESTÁ.\n\nE QUANDO, ENFIM, CHEGAR A HORA EM QUE A MORTE ENFRENTAREI; SEM MEDO, ENTÃO, TEREI VITÓRIA, VEREI NA GLÓRIA O MEU JESUS QUE VIVO ESTÁ.\n\nCORO PORQUE ELE VIVE, POSSO CRER NO AMANHÃ; PORQUE ELE VIVE, TEMOR NÃO HÁ. MAS EU BEM SEI (EU SEI) QUE A MINHA VIDA ESTÁ NAS MÃOS DO MEU JESUS, QUE VIVO ESTÁ.', 'DEUS ENVIOU SEU FILHO AMADO PARA MORRER EM MEU LUGAR; NA CRUZ SOFREU POR MEUS PECADOS, MAS O SEPULCRO VAZIO ESTÁ PORQUE ELE VIVE. CORO PORQUE ELE VIVE, POSSO CRER NO AMANHÃ; PORQUE ELE VIVE, TEMOR NÃO HÁ. MAS EU BEM SEI (EU SEI) QUE A MINHA VIDA ESTÁ NAS MÃOS DO MEU JESUS, QUE VIVO ESTÁ. E QUANDO, ENFIM, CHEGAR A HORA EM QUE A MORTE ENFRENTAREI; SEM MEDO, ENTÃO, TEREI VITÓRIA, VEREI NA GLÓRIA O MEU JESUS QUE VIVO ESTÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('208', '208 - DEUS AMOU O MUNDO DE TAL MANEIRA', 'DEUS AMOU O MUNDO DE TAL MANEIRA, QUE SEU FILHO JESUS ENVIOU. VEIO PRA LIVRAR-NOS DA CONDENAÇÃO, VIDA ETERNA E TAMBÉM PAZ NOS PROPORCIONOU;\n\nJESUS, MESMO SENDO DEUS, OBEDECEU, ME AMOU, TE AMOU, POR NÓS PADECEU; E DERRAMOU SEU SANGUE EM PURIFICAÇÃO, CUMPRIU O DIVINO PLANO DA REDENÇÃO.\n\nASSIM PODEMOS TER DIRETO ACESSO AO PAI, EM COMUNHÃO VIVER ATÉ JESUS VOLTAR. EXAMINA A TI MESMO: TENS GOZADO DESTA SALVAÇÃO? OU AINDA ESTÁS PRESO EM MEIO À TUA PRÓPRIA ILUSÃO? INSTRUMENTOS\n\nRENDE AGORA A TUA VIDA E ENTREGA SOBRE O SEU ALTAR; CADA HORA É DECISIVA, JESUS DISSE: “LOGO VOU VOLTAR”. INSTRUMENTOS FINAL: ELE DISSE: “LOGO VOU VOLTAR”.\nBIS', 'DEUS AMOU O MUNDO DE TAL MANEIRA, QUE SEU FILHO JESUS ENVIOU. VEIO PRA LIVRAR-NOS DA CONDENAÇÃO, VIDA ETERNA E TAMBÉM PAZ NOS PROPORCIONOU; JESUS, MESMO SENDO DEUS, OBEDECEU, ME AMOU, TE AMOU, POR NÓS PADECEU; E DERRAMOU SEU SANGUE EM PURIFICAÇÃO, CUMPRIU O DIVINO PLANO DA REDENÇÃO. ASSIM PODEMOS TER DIRETO ACESSO AO PAI, EM COMUNHÃO VIVER ATÉ JESUS VOLTAR. EXAMINA A TI MESMO: TENS GOZADO DESTA SALVAÇÃO? OU AINDA ESTÁS PRESO EM MEIO À TUA PRÓPRIA ILUSÃO? INSTRUMENTOS RENDE AGORA A TUA VIDA E ENTREGA SOBRE O SEU ALTAR; CADA HORA É DECISIVA, JESUS DISSE: LOGO VOU VOLTAR. INSTRUMENTOS  ELE DISSE: LOGO VOU VOLTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('209', '209 - DEUS O SEU FILHO ENVIOU', 'DEUS O SEU FILHO ENVIOU PARA MORRER NUMA CRUZ; NUM DESEJO PROFUNDO DO SEU CORAÇÃO, QUIS O HOMEM SALVAR.\nBIS\n\nNESTE CAMINHO DA VIDA, ACONTECE QUE, SE DE DEUS, LONGE ESTOU, TENHO QUE IR A JESUS; LEMBRO QUE ELE SOFREU LÁ NA CRUZ.\n\nPARA RESGATAR MINHA VIDA, PARA TRANSFORMAR MEU CORAÇÃO, DANDO-ME PERDÃO, VIDA ETERNA, SEU AMOR. FINAL: SEU AMOR.\nBIS', 'DEUS O SEU FILHO ENVIOU PARA MORRER NUMA CRUZ; NUM DESEJO PROFUNDO DO SEU CORAÇÃO, QUIS O HOMEM SALVAR. NESTE CAMINHO DA VIDA, ACONTECE QUE, SE DE DEUS, LONGE ESTOU, TENHO QUE IR A JESUS; LEMBRO QUE ELE SOFREU LÁ NA CRUZ. PARA RESGATAR MINHA VIDA, PARA TRANSFORMAR MEU CORAÇÃO, DANDO-ME PERDÃO, VIDA ETERNA, SEU AMOR.  SEU AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('210', '210 - UM DIA JESUS DESCEU DA SUA GLÓRIA', 'UM DIA JESUS DESCEU DA SUA GLÓRIA E AQUI VIVEU, ENTRE NÓS, COM AMOR, FEZ MARAVILHAS, NOS DEU A VITÓRIA, E COM SEU SANGUE NOS PURIFICOU.\n\nHOSANA, HOSANA AO REI NAS ALTURAS, HOSANA, HOSANA A JESUS, O SENHOR.\nBIS\n\nMORREU NA CRUZ PELOS NOSSOS PECADOS, SOFREU DEMAIS, SÓ PARA NOS SALVAR, E, NA TRISTEZA, NOS TEM CONSOLADO, SUBIU AO PAI, MAS VIRÁ NOS BUSCAR, SUBIU AO PAI, MAS VIRÁ NOS BUSCAR.\n\nHOSANA, HOSANA AO REI NAS ALTURAS, HOSANA, HOSANA A JESUS, O SENHOR.\nBIS', 'UM DIA JESUS DESCEU DA SUA GLÓRIA E AQUI VIVEU, ENTRE NÓS, COM AMOR, FEZ MARAVILHAS, NOS DEU A VITÓRIA, E COM SEU SANGUE NOS PURIFICOU. HOSANA, HOSANA AO REI NAS ALTURAS, HOSANA, HOSANA A JESUS, O SENHOR. MORREU NA CRUZ PELOS NOSSOS PECADOS, SOFREU DEMAIS, SÓ PARA NOS SALVAR, E, NA TRISTEZA, NOS TEM CONSOLADO, SUBIU AO PAI, MAS VIRÁ NOS BUSCAR, SUBIU AO PAI, MAS VIRÁ NOS BUSCAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('211', '211 - CORTARAM O MADEIRO', 'CORTARAM O MADEIRO, FIZERAM UMA CRUZ PARA O MEU SALVADOR; MADEIRO LAVRADO, COM PREGOS CRAVADOS, PESADA FICOU. ELE CARREGOU A CRUZ, NO CAMINHO CAIU, MAS DEUS DEU-LHE GRAÇA MORRENDO NA CRUZ POR TI E POR MIM.\n\nCORO FOI FEITA ASSIM A CRUZ DO SALVADOR; MADEIRO LAVRADO, COM PREGOS CRAVADOS, PESADA FICOU.\n\nPERANTE PILATOS JESUS FOI LEVADO COMO UM MALFEITOR; CHEGANDO A ELE, OLHANDO O MESTRE, ASSIM PERGUNTOU: ÉS O REI DOS JUDEUS? DISSE JESUS: “NA VERDADE EU SOU, O MEU REINO É ETERNO, NÃO É DESTE MUNDO, DAQUI EU NÃO SOU”.\n\nCORO FOI FEITA ASSIM A CRUZ DO SALVADOR; MADEIRO LAVRADO, COM PREGOS CRAVADOS, PESADA FICOU.\n\nCHEGANDO AO CALVÁRIO, PREGARAM NA CRUZ O MEU SALVADOR; UMA COROA DE ESPINHOS, NA FRONTE SANGRANDO, AO PAI SUPLICOU; TRANSPASSADO DE DOR FICOU O MEU SALVADOR, ÀS TRÊS HORAS DA TARDE, INCLINOU A CABEÇA E ALI EXPIROU.\n\nCORO FOI FEITA ASSIM A CRUZ DO SALVADOR; MADEIRO LAVRADO, COM PREGOS CRAVADOS, PESADA FICOU.', 'CORTARAM O MADEIRO, FIZERAM UMA CRUZ PARA O MEU SALVADOR; MADEIRO LAVRADO, COM PREGOS CRAVADOS, PESADA FICOU. ELE CARREGOU A CRUZ, NO CAMINHO CAIU, MAS DEUS DEU-LHE GRAÇA MORRENDO NA CRUZ POR TI E POR MIM. CORO FOI FEITA ASSIM A CRUZ DO SALVADOR; MADEIRO LAVRADO, COM PREGOS CRAVADOS, PESADA FICOU. PERANTE PILATOS JESUS FOI LEVADO COMO UM MALFEITOR; CHEGANDO A ELE, OLHANDO O MESTRE, ASSIM PERGUNTOU: ÉS O REI DOS JUDEUS? DISSE JESUS: NA VERDADE EU SOU, O MEU REINO É ETERNO, NÃO É DESTE MUNDO, DAQUI EU NÃO SOU. CHEGANDO AO CALVÁRIO, PREGARAM NA CRUZ O MEU SALVADOR; UMA COROA DE ESPINHOS, NA FRONTE SANGRANDO, AO PAI SUPLICOU; TRANSPASSADO DE DOR FICOU O MEU SALVADOR, ÀS TRÊS HORAS DA TARDE, INCLINOU A CABEÇA E ALI EXPIROU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('212', '212 - RUDE CRUZ', 'RUDE CRUZ SE ERIGIU, DELA O DIA FUGIU, COMO EMBLEMA DE VERGONHA E DOR; MAS EU AMO ESSA CRUZ PORQUE NELA, JESUS, DEU A VIDA POR MIM, PECADOR.\n\nCORO SIM, EU AMO A MENSAGEM DA CRUZ, TÉ MORRER EU A VOU PROCLAMAR; LEVAREI EU TAMBÉM MINHA CRUZ, TÉ POR UMA COROA TROCAR.\n\nDESDE A GLÓRIA DOS CÉUS, O CORDEIRO DE DEUS AO CALVÁRIO HUMILHANTE BAIXOU; TEM A CRUZ PARA MIM ATRATIVOS SEM FIM, PORQUE NELA JESUS ME SALVOU.\n\nCORO SIM, EU AMO A MENSAGEM DA CRUZ, TÉ MORRER EU A VOU PROCLAMAR; LEVAREI EU TAMBÉM MINHA CRUZ, TÉ POR UMA COROA TROCAR.\n\nLÁ NA CRUZ PADECEU, DESPREZADO MORREU MEU JESUS PARA DAR-ME O PERDÃO; DELA AGORA PROVÉM PARA MIM TODO O BEM, TENHO NELA REAL SALVAÇÃO.\n\nCORO SIM, EU AMO A MENSAGEM DA CRUZ, TÉ MORRER EU A VOU PROCLAMAR; LEVAREI EU TAMBÉM MINHA CRUZ, TÉ POR UMA COROA TROCAR.\n\nEU AQUI COM JESUS, A VERGONHA DA CRUZ QUERO SEMPRE LEVAR E SOFRER; QUANDO CRISTO VOLTAR, PARA AQUI ME BUSCAR, SUA GLÓRIA EU IREI RECEBER.\n\nCORO SIM, EU AMO A MENSAGEM DA CRUZ, TÉ MORRER EU A VOU PROCLAMAR; LEVAREI EU TAMBÉM MINHA CRUZ, TÉ POR UMA COROA TROCAR.', 'RUDE CRUZ SE ERIGIU, DELA O DIA FUGIU, COMO EMBLEMA DE VERGONHA E DOR; MAS EU AMO ESSA CRUZ PORQUE NELA, JESUS, DEU A VIDA POR MIM, PECADOR. CORO SIM, EU AMO A MENSAGEM DA CRUZ, TÉ MORRER EU A VOU PROCLAMAR; LEVAREI EU TAMBÉM MINHA CRUZ, TÉ POR UMA COROA TROCAR. DESDE A GLÓRIA DOS CÉUS, O CORDEIRO DE DEUS AO CALVÁRIO HUMILHANTE BAIXOU; TEM A CRUZ PARA MIM ATRATIVOS SEM FIM, PORQUE NELA JESUS ME SALVOU. LÁ NA CRUZ PADECEU, DESPREZADO MORREU MEU JESUS PARA DAR-ME O PERDÃO; DELA AGORA PROVÉM PARA MIM TODO O BEM, TENHO NELA REAL SALVAÇÃO. EU AQUI COM JESUS, A VERGONHA DA CRUZ QUERO SEMPRE LEVAR E SOFRER; QUANDO CRISTO VOLTAR, PARA AQUI ME BUSCAR, SUA GLÓRIA EU IREI RECEBER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('213', '213 - CRISTO CAMINHAVA EM DIREÇÃO À CRUZ', 'CRISTO CAMINHAVA EM DIREÇÃO À CRUZ, SABENDO QUE ESTAVA PRA MORRER, ENFERMO E CANSADO, SOZINHO E DESPREZADO, CARREGAVA A CRUZ.\n\nNOSSOS PECADOS ELE SUPORTOU, SENTINDO SOLIDÃO AO PAI CLAMOU: “NÃO SABEM O QUE FAZEM, PERDOA-LHES, SENHOR, EU VIM POR AMOR.”\n\nNO CALVÁRIO PREGARAM SUAS MÃOS E PÉS, SENDO REI, COROADO DE ESPINHOS; O CÉU SE ESCURECEU, PRA BEBER LHE DERAM FEL, RASGOU-SE O VÉU E EXPIROU.\n\nAO TERCEIRO DIA ELE RESSURGIU DANDO-NOS VITÓRIA, O SEU SANGUE É GARANTIA! GESTO DE AMOR, GESTO DO SENHOR, MORRENDO EM UMA CRUZ, SENDO NOSSO SALVADOR. INSTRUMENTOS\n\nGESTO DE AMOR, GESTO DO SENHOR, MORRENDO EM UMA CRUZ, SENDO NOSSO SALVADOR. FINAL: GESTO DE AMOR!\nBIS', 'CRISTO CAMINHAVA EM DIREÇÃO À CRUZ, SABENDO QUE ESTAVA PRA MORRER, ENFERMO E CANSADO, SOZINHO E DESPREZADO, CARREGAVA A CRUZ. NOSSOS PECADOS ELE SUPORTOU, SENTINDO SOLIDÃO AO PAI CLAMOU: NÃO SABEM O QUE FAZEM, PERDOA-LHES, SENHOR, EU VIM POR AMOR. NO CALVÁRIO PREGARAM SUAS MÃOS E PÉS, SENDO REI, COROADO DE ESPINHOS; O CÉU SE ESCURECEU, PRA BEBER LHE DERAM FEL, RASGOU-SE O VÉU E EXPIROU. AO TERCEIRO DIA ELE RESSURGIU DANDO-NOS VITÓRIA, O SEU SANGUE É GARANTIA! GESTO DE AMOR, GESTO DO SENHOR, MORRENDO EM UMA CRUZ, SENDO NOSSO SALVADOR. INSTRUMENTOS GESTO DE AMOR, GESTO DO SENHOR, MORRENDO EM UMA CRUZ, SENDO NOSSO SALVADOR.  GESTO DE AMOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('214', '214 - DOIS DISCÍPULOS NO CAMINHO DE EMAÚS', 'DOIS DISCÍPULOS NO CAMINHO DE EMAÚS IAM FALANDO A RESPEITO DE JESUS; UM VIAJANTE DELES SE APROXIMOU, NÃO PERCEBERAM QUE ERA O MEIGO SALVADOR.\n\nCORO FICA CONOSCO, NOSSO AMADO SALVADOR, FICA CONOSCO PELO TEU IMENSO AMOR, COMO OS DISCÍPULOS TE PEDIRAM NO CAMINHO DE EMAÚS, FICA CONOSCO, NOSSO AMADO E BOM JESUS.\n\nELES FALAVAM A RESPEITO DE JESUS, VARÃO PROFETA QUE FOI PODEROSO EM OBRAS, DIANTE DE DEUS E TODO O POVO, ALELUIA, ESTE JESUS QUE AINDA HOJE NOS CONDUZ.\n\nCORO FICA CONOSCO, NOSSO AMADO SALVADOR, FICA CONOSCO PELO TEU IMENSO AMOR, COMO OS DISCÍPULOS TE PEDIRAM NO CAMINHO DE EMAÚS, FICA CONOSCO, NOSSO AMADO E BOM JESUS.\n\nELES FALAVAM A RESPEITO DE JESUS, VARÃO PROFETA QUE FOI PODEROSO EM OBRAS, DIANTE DE DEUS E TODO O POVO, ALELUIA, ESTE JESUS QUE AINDA HOJE NOS CONDUZ.\n\nCORO FICA CONOSCO, NOSSO AMADO SALVADOR, FICA CONOSCO PELO TEU IMENSO AMOR, COMO OS DISCÍPULOS TE PEDIRAM NO CAMINHO DE EMAÚS, FICA CONOSCO, NOSSO AMADO E BOM JESUS.\n\nSENTADO À MESA, TOMANDO O PÃO, ABENÇOOU, PARTIU E DEU, ELES ENTÃO RECONHECERAM; CHEIOS DE GOZO EM VER O FILHO DE DEUS NO MESMO INSTANTE ELE DESAPARECEU.\n\nCORO FICA CONOSCO, NOSSO AMADO SALVADOR, FICA CONOSCO PELO TEU IMENSO AMOR, COMO OS DISCÍPULOS TE PEDIRAM NO CAMINHO DE EMAÚS, FICA CONOSCO, NOSSO AMADO E BOM JESUS.', 'DOIS DISCÍPULOS NO CAMINHO DE EMAÚS IAM FALANDO A RESPEITO DE JESUS; UM VIAJANTE DELES SE APROXIMOU, NÃO PERCEBERAM QUE ERA O MEIGO SALVADOR. CORO FICA CONOSCO, NOSSO AMADO SALVADOR, FICA CONOSCO PELO TEU IMENSO AMOR, COMO OS DISCÍPULOS TE PEDIRAM NO CAMINHO DE EMAÚS, FICA CONOSCO, NOSSO AMADO E BOM JESUS. ELES FALAVAM A RESPEITO DE JESUS, VARÃO PROFETA QUE FOI PODEROSO EM OBRAS, DIANTE DE DEUS E TODO O POVO, ALELUIA, ESTE JESUS QUE AINDA HOJE NOS CONDUZ. SENTADO À MESA, TOMANDO O PÃO, ABENÇOOU, PARTIU E DEU, ELES ENTÃO RECONHECERAM; CHEIOS DE GOZO EM VER O FILHO DE DEUS NO MESMO