INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('215', '215 - CERTO HOMEM TÃO NOBRE', 'CERTO HOMEM TÃO NOBRE NESTE MUNDO DESCEU, CARREGOU MEUS PECADOS SÓ PORQUE ME AMOU; O SEU SANGUE TÃO PURO NA CRUZ DERRAMOU, ESTE HOMEM TÃO NOBRE É JESUS.\n\nCORO (2X) NOBRE E REI, ELE É NOBRE E REI, ESTE HOMEM TÃO NOBRE É JESUS.\n\nRESSURGIU DENTRE OS MORTOS PORQUE É VENCEDOR, HOJE À DESTRA DO PAI ESTÁ, CRISTO, MEU SALVADOR; PROMETEU QUE UM DIA VIRÁ ME BUSCAR, PARA SEMPRE O SEU NOME VOU LOUVAR.\n\nCORO (2X) NOBRE E REI, ELE É NOBRE E REI, ESTE HOMEM TÃO NOBRE É JESUS. FINAL: ESTE HOMEM TÃO NOBRE É JESUS.', 'CERTO HOMEM TÃO NOBRE NESTE MUNDO DESCEU, CARREGOU MEUS PECADOS SÓ PORQUE ME AMOU; O SEU SANGUE TÃO PURO NA CRUZ DERRAMOU, ESTE HOMEM TÃO NOBRE É JESUS. CORO  NOBRE E REI, ELE É NOBRE E REI, ESTE HOMEM TÃO NOBRE É JESUS. RESSURGIU DENTRE OS MORTOS PORQUE É VENCEDOR, HOJE À DESTRA DO PAI ESTÁ, CRISTO, MEU SALVADOR; PROMETEU QUE UM DIA VIRÁ ME BUSCAR, PARA SEMPRE O SEU NOME VOU LOUVAR. CORO  NOBRE E REI, ELE É NOBRE E REI, ESTE HOMEM TÃO NOBRE É JESUS.  ESTE HOMEM TÃO NOBRE É JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('216', '216 - SOMOS TEU POVO', 'SOMOS TEU POVO, TUA IGREJA, MORRESTE POR NÓS, SENDO REI, SENHOR! QUISESTE SALVAR O MUNDO, MAS O MUNDO NÃO QUIS.\n\nA TUA IGREJA TE AMA E FAZ DO TEU SANGUE ESCUDO FORTE, A TUA PALAVRA É ALIMENTO FORTE QUE FAZ NOSSA VIDA FELIZ!\n\nA TUA IGREJA TE ESPERA, NÃO SE APRESSA, PORQUE TU, JESUS, VOLTARÁS E A LEVARÁS PARA UM LAR DE PAZ, ONDE REINA O AMOR.\nBIS', 'SOMOS TEU POVO, TUA IGREJA, MORRESTE POR NÓS, SENDO REI, SENHOR! QUISESTE SALVAR O MUNDO, MAS O MUNDO NÃO QUIS. A TUA IGREJA TE AMA E FAZ DO TEU SANGUE ESCUDO FORTE, A TUA PALAVRA É ALIMENTO FORTE QUE FAZ NOSSA VIDA FELIZ! A TUA IGREJA TE ESPERA, NÃO SE APRESSA, PORQUE TU, JESUS, VOLTARÁS E A LEVARÁS PARA UM LAR DE PAZ, ONDE REINA O AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('217', '217 - DEIXOU O ESPLENDOR DE SUA GLÓRIA', 'DEIXOU O ESPLENDOR DE SUA GLÓRIA SABENDO O DESTINO AQUI; ESTAVA SÓ E FERIDO NO GÓLGOTA PARA DAR SUA VIDA POR MIM.\n\nCORO SE ISTO NÃO FOR AMOR, O OCEANO SECOU, NÃO HÁ ESTRELAS NO CÉU, AS ANDORINHAS NÃO VOAM MAIS; SE ISTO NÃO FOR AMOR, O CÉU NÃO É REAL, TUDO PERDE O VALOR SE ISTO NÃO FOR AMOR.\n\nMESMO NA MORTE LEMBROU-SE DE UM LADRÃO QUE A SEU LADO ESTAVA; COM AMOR E TERNURA FALOU-LHE: “AO PARAÍSO COMIGO IRÁS”.\n\nCORO SE ISTO NÃO FOR AMOR, O OCEANO SECOU, NÃO HÁ ESTRELAS NO CÉU, AS ANDORINHAS NÃO VOAM MAIS; SE ISTO NÃO FOR AMOR, O CÉU NÃO É REAL, TUDO PERDE O VALOR SE ISTO NÃO FOR AMOR.', 'DEIXOU O ESPLENDOR DE SUA GLÓRIA SABENDO O DESTINO AQUI; ESTAVA SÓ E FERIDO NO GÓLGOTA PARA DAR SUA VIDA POR MIM. CORO SE ISTO NÃO FOR AMOR, O OCEANO SECOU, NÃO HÁ ESTRELAS NO CÉU, AS ANDORINHAS NÃO VOAM MAIS; SE ISTO NÃO FOR AMOR, O CÉU NÃO É REAL, TUDO PERDE O VALOR SE ISTO NÃO FOR AMOR. MESMO NA MORTE LEMBROU-SE DE UM LADRÃO QUE A SEU LADO ESTAVA; COM AMOR E TERNURA FALOU-LHE: AO PARAÍSO COMIGO IRÁS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('218', '218 - POR AMOR JESUS MORREU', 'POR AMOR JESUS MORREU, VEIO PARA ME SALVAR, POR AMOR SEU SANGUE VERTEU, NAQUELA AMARGA CRUZ, POR AMOR SE FEZ MEU PRESENTE, TORNANDO-SE AMIGO ME FEZ SEU PARENTE.\nBIS\n\nPOR AMOR SE FEZ MEU PRESENTE, TORNANDO-SE AMIGO ME FEZ SEU PARENTE. VIVA O REI, (JESUS) VIVA O REI, (VIVA O REI) (BIS) VIVA O REI.', 'POR AMOR JESUS MORREU, VEIO PARA ME SALVAR, POR AMOR SEU SANGUE VERTEU, NAQUELA AMARGA CRUZ, POR AMOR SE FEZ MEU PRESENTE, TORNANDO-SE AMIGO ME FEZ SEU PARENTE. POR AMOR SE FEZ MEU PRESENTE, TORNANDO-SE AMIGO ME FEZ SEU PARENTE. VIVA O REI, (JESUS) VIVA O REI, (VIVA O REI)  VIVA O REI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('219', '219 - POR AMOR', 'POR AMOR, POR AMOR JESUS ME SALVOU, FOI NA CRUZ, FOI NA CRUZ QUE O PREÇO PAGOU, NÃO COM PRATA, NEM OURO, MAS COM SANGUE MOSTROU O INFINITO AMOR COM QUE AO MUNDO AMOU.\n\nCORO TÃO GRANDE AMOR ME TRANSFORMOU; BASTA CRER E ACEITAR, POR SEU SANGUE CLAMAR E O AMOR DE MEU DEUS VAI TUA VIDA INUNDAR. INSTRUMENTOS\n\nDO IMENSO AMOR NINGUÉM VAI SEPARAR, É MAIS FORTE QUE A MORTE, ARDE EM MIM SEM CESSAR; TUDO CRÊ E SUPORTA ATÉ O DIA CHEGAR, E O AMOR DE MEU DEUS LÁ NO CÉU VOU GOZAR.\n\nCORO TÃO GRANDE AMOR ME TRANSFORMOU; BASTA CRER E ACEITAR, POR SEU SANGUE CLAMAR E O AMOR DE MEU DEUS VAI TUA VIDA INUNDAR.', 'POR AMOR, POR AMOR JESUS ME SALVOU, FOI NA CRUZ, FOI NA CRUZ QUE O PREÇO PAGOU, NÃO COM PRATA, NEM OURO, MAS COM SANGUE MOSTROU O INFINITO AMOR COM QUE AO MUNDO AMOU. CORO TÃO GRANDE AMOR ME TRANSFORMOU; BASTA CRER E ACEITAR, POR SEU SANGUE CLAMAR E O AMOR DE MEU DEUS VAI TUA VIDA INUNDAR. INSTRUMENTOS DO IMENSO AMOR NINGUÉM VAI SEPARAR, É MAIS FORTE QUE A MORTE, ARDE EM MIM SEM CESSAR; TUDO CRÊ E SUPORTA ATÉ O DIA CHEGAR, E O AMOR DE MEU DEUS LÁ NO CÉU VOU GOZAR. CORO TÃO GRANDE AMOR ME TRANSFORMOU; BASTA CRER E ACEITAR, POR SEU SANGUE CLAMAR E O AMOR DE MEU DEUS VAI TUA VIDA INUNDAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('220', '220 - NÃO HÁ AMOR IGUAL', 'NÃO HÁ AMOR IGUAL AO AMOR DE JESUS, POR MIM MORREU NAQUELA CRUZ; DEIXOU A SUA GLÓRIA, SOFREU HUMILHAÇÃO SÓ PRA ME DAR A SALVAÇÃO.\n\nCORO POR ISSO DEI-LHE O MEU CORAÇÃO E LHE ENTREGUEI TODO O MEU SER E UM TRONO LEVANTEI PARA O MEU JESUS SER REI EM TODO O MEU VIVER.\n\nO QUE ELE VIU EM MIM PARA SE DAR ASSIM POR MIM, TÃO POBRE PECADOR? DESCEU DA SUA GLÓRIA, SOFREU HUMILHAÇÃO PARA ME DAR A SALVAÇÃO.', 'NÃO HÁ AMOR IGUAL AO AMOR DE JESUS, POR MIM MORREU NAQUELA CRUZ; DEIXOU A SUA GLÓRIA, SOFREU HUMILHAÇÃO SÓ PRA ME DAR A SALVAÇÃO. CORO POR ISSO DEI-LHE O MEU CORAÇÃO E LHE ENTREGUEI TODO O MEU SER E UM TRONO LEVANTEI PARA O MEU JESUS SER REI EM TODO O MEU VIVER. O QUE ELE VIU EM MIM PARA SE DAR ASSIM POR MIM, TÃO POBRE PECADOR? DESCEU DA SUA GLÓRIA, SOFREU HUMILHAÇÃO PARA ME DAR A SALVAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('221', '221 - JESUS, EM TUA PRESENÇA', 'JESUS, EM TUA PRESENÇA, REUNIMO-NOS AQUI, CONTEMPLAMOS TUA FACE E RENDEMO-NOS A TI; POIS UM DIA A TUA MORTE TROUXE VIDA A TODOS NÓS E NOS DEU COMPLETO ACESSO AO CORAÇÃO DO PAI. INSTRUMENTOS\n\nJESUS, EM TUA PRESENÇA, REUNIMO-NOS AQUI, CONTEMPLAMOS TUA FACE E RENDEMO-NOS A TI; POIS UM DIA A TUA MORTE TROUXE VIDA A TODOS NÓS E NOS DEU COMPLETO ACESSO AO CORAÇÃO DO PAI.\n\nO VÉU QUE SEPARAVA JÁ NÃO SEPARA MAIS, A LUZ, QUE OUTRORA APAGADA, AGORA BRILHA E CADA DIA BRILHA MAIS. SÓ PRA TE ADORAR E FAZER TEU NOME GRANDE E TE DAR O LOUVOR QUE É DEVIDO, ESTAMOS NÓS AQUI.\nBIS', 'JESUS, EM TUA PRESENÇA, REUNIMO-NOS AQUI, CONTEMPLAMOS TUA FACE E RENDEMO-NOS A TI; POIS UM DIA A TUA MORTE TROUXE VIDA A TODOS NÓS E NOS DEU COMPLETO ACESSO AO CORAÇÃO DO PAI. INSTRUMENTOS JESUS, EM TUA PRESENÇA, REUNIMO-NOS AQUI, CONTEMPLAMOS TUA FACE E RENDEMO-NOS A TI; POIS UM DIA A TUA MORTE TROUXE VIDA A TODOS NÓS E NOS DEU COMPLETO ACESSO AO CORAÇÃO DO PAI. O VÉU QUE SEPARAVA JÁ NÃO SEPARA MAIS, A LUZ, QUE OUTRORA APAGADA, AGORA BRILHA E CADA DIA BRILHA MAIS. SÓ PRA TE ADORAR E FAZER TEU NOME GRANDE E TE DAR O LOUVOR QUE É DEVIDO, ESTAMOS NÓS AQUI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('222', '222 - EIS QUE ESTOU À PORTA', 'EIS QUE ESTOU À PORTA E BATO, SE ALGUÉM OUVIR MINHA VOZ E ABRIR A PORTA, ENTRAREI EM SEU CORAÇÃO. E COM ELE CEAREI E ELE COMIGO; BASTA ABRIR A PORTA, ENTRAREI EM SEU CORAÇÃO.\nBIS\nBIS', 'EIS QUE ESTOU À PORTA E BATO, SE ALGUÉM OUVIR MINHA VOZ E ABRIR A PORTA, ENTRAREI EM SEU CORAÇÃO. E COM ELE CEAREI E ELE COMIGO; BASTA ABRIR A PORTA, ENTRAREI EM SEU CORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('223', '223 - ONDE ESTÁ O CORDEIRO?', 'ONDE ESTÁ O CORDEIRO? A PERGUNTA DE TODAS AS NAÇÕES ECOA NO MUNDO INTEIRO; ONDE ESTÁ O CORDEIRO QUE FOI MORTO EM MEU LUGAR PARA A VIDA ETERNA ME DAR? INSTRUMENTOS\n\nEIS AQUI O CORDEIRO DE DEUS, TROUXE A MIM VIDA, PAZ E LUZ, REVELOU-ME O PLANO ETERNO, O CORDEIRO SE CHAMA JESUS, (ONDE ESTÁ O CORDEIRO?) O CORDEIRO SE CHAMA JESUS. INSTRUMENTOS\n\nOUVI UMA VOZ LÁ DO CÉU A CHAMAR: “POVO MEU, SOBE AQUI”; VI UM TRONO CELESTE E O CORDEIRO ASSENTADO ALI; EM CORO OS ANJOS CANTAVAM: GLÓRIA, GLÓRIA AO CORDEIRO, AMÉM.\n\nGLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) GLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) GLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) AMÉM!', 'ONDE ESTÁ O CORDEIRO? A PERGUNTA DE TODAS AS NAÇÕES ECOA NO MUNDO INTEIRO; ONDE ESTÁ O CORDEIRO QUE FOI MORTO EM MEU LUGAR PARA A VIDA ETERNA ME DAR? INSTRUMENTOS EIS AQUI O CORDEIRO DE DEUS, TROUXE A MIM VIDA, PAZ E LUZ, REVELOU-ME O PLANO ETERNO, O CORDEIRO SE CHAMA JESUS, (ONDE ESTÁ O CORDEIRO?) O CORDEIRO SE CHAMA JESUS. INSTRUMENTOS OUVI UMA VOZ LÁ DO CÉU A CHAMAR: POVO MEU, SOBE AQUI; VI UM TRONO CELESTE E O CORDEIRO ASSENTADO ALI; EM CORO OS ANJOS CANTAVAM: GLÓRIA, GLÓRIA AO CORDEIRO, AMÉM. GLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) GLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) GLÓRIA AO CORDEIRO, AMÉM, (GLÓRIA) AMÉM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('224', '224 - ERA UM PECADOR, ANDAVA SEM JESUS', 'ERA UM PECADOR, ANDAVA SEM JESUS, NÃO TINHA ESPERANÇA NEM DIVINA LUZ, HOJE SOU REMIDO, CRISTO ME SALVOU, COM SEU SANGUE ME LAVOU.\n\nCORO QUE AMOR ME CONCEDEU JESUS, GOZO SANTO E CELESTE LUZ; CRISTO EM BREVE DO CÉU DESCERÁ E CONSIGO, ENTÃO, ME LEVARÁ.\n\nO CONSOLADOR JÁ VEIO EM MIM MORAR, A PALAVRA SANTA VEIO ILUMINAR; QUERO SER GUIADO POR TÃO CLARA LUZ QUE A CRISTO ME CONDUZ.\n\nCORO QUE AMOR ME CONCEDEU JESUS, GOZO SANTO E CELESTE LUZ; CRISTO EM BREVE DO CÉU DESCERÁ E CONSIGO, ENTÃO, ME LEVARÁ.\n\nVEM, JESUS AMADO, VEM SEM DEMORAR, EU ESTOU ANSIOSO PRA NO CÉU ENTRAR, VEM SEM MAIS TARDANÇA, FAZ RAIAR A LUZ; “ORA VEM, SENHOR JESUS”.\n\nCORO QUE AMOR ME CONCEDEU JESUS, GOZO SANTO E CELESTE LUZ; CRISTO EM BREVE DO CÉU DESCERÁ E CONSIGO, ENTÃO, ME LEVARÁ.\n\nQUERES, PECADOR, GOZAR A SALVAÇÃO? VEM A CRISTO HOJE RECEBER PERDÃO; CRISTO TE ACEITA, POBRE PECADOR, NOS SEUS BRAÇOS DE AMOR.\n\nCORO QUE AMOR ME CONCEDEU JESUS, GOZO SANTO E CELESTE LUZ; CRISTO EM BREVE DO CÉU DESCERÁ E CONSIGO, ENTÃO, ME LEVARÁ.', 'ERA UM PECADOR, ANDAVA SEM JESUS, NÃO TINHA ESPERANÇA NEM DIVINA LUZ, HOJE SOU REMIDO, CRISTO ME SALVOU, COM SEU SANGUE ME LAVOU. CORO QUE AMOR ME CONCEDEU JESUS, GOZO SANTO E CELESTE LUZ; CRISTO EM BREVE DO CÉU DESCERÁ E CONSIGO, ENTÃO, ME LEVARÁ. O CONSOLADOR JÁ VEIO EM MIM MORAR, A PALAVRA SANTA VEIO ILUMINAR; QUERO SER GUIADO POR TÃO CLARA LUZ QUE A CRISTO ME CONDUZ. VEM, JESUS AMADO, VEM SEM DEMORAR, EU ESTOU ANSIOSO PRA NO CÉU ENTRAR, VEM SEM MAIS TARDANÇA, FAZ RAIAR A LUZ; ORA VEM, SENHOR JESUS. QUERES, PECADOR, GOZAR A SALVAÇÃO? VEM A CRISTO HOJE RECEBER PERDÃO; CRISTO TE ACEITA, POBRE PECADOR, NOS SEUS BRAÇOS DE AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('225', '225 - JESUS É MELHOR, SIM, QUE OURO E BENS', 'JESUS É MELHOR, SIM, QUE OURO E BENS, JESUS É MELHOR DO QUE TUDO QUE TENS, MELHOR QUE RIQUEZAS E POSIÇÕES, MELHOR, MUITO MAIS DO QUE MILHÕES.\n\nCORO PODE SER UM REI COM PODER NAS MÃOS, MAS DO MAL, ESCRAVO, SIM; MIL VEZES PREFIRO O MEU JESUS E SERVI-LO ATÉ O FIM.\n\nJESUS É MELHOR QUE QUALQUER VALOR, AMIGO LEAL NO PRAZER E NA DOR, MELHOR DO QUE TUDO ELE É PRA MIM, MELHOR QUE QUALQUER BOM AMIGO ENFIM.\n\nCORO PODE SER UM REI COM PODER NAS MÃOS, MAS DO MAL, ESCRAVO, SIM; MIL VEZES PREFIRO O MEU JESUS E SERVI-LO ATÉ O FIM.\n\nJESUS É MAIS PURO QUE A LINDA FLOR, JESUS É MELHOR, ELE, SIM, SATISFAZ, JESUS É MELHOR, SIM, ELE É AMOR, CAMINHO, LUZ, VERDADE E PAZ.\n\nCORO PODE SER UM REI COM PODER NAS MÃOS, MAS DO MAL, ESCRAVO, SIM; MIL VEZES PREFIRO O MEU JESUS E SERVI-LO ATÉ O FIM.', 'JESUS É MELHOR, SIM, QUE OURO E BENS, JESUS É MELHOR DO QUE TUDO QUE TENS, MELHOR QUE RIQUEZAS E POSIÇÕES, MELHOR, MUITO MAIS DO QUE MILHÕES. CORO PODE SER UM REI COM PODER NAS MÃOS, MAS DO MAL, ESCRAVO, SIM; MIL VEZES PREFIRO O MEU JESUS E SERVI-LO ATÉ O FIM. JESUS É MELHOR QUE QUALQUER VALOR, AMIGO LEAL NO PRAZER E NA DOR, MELHOR DO QUE TUDO ELE É PRA MIM, MELHOR QUE QUALQUER BOM AMIGO ENFIM. JESUS É MAIS PURO QUE A LINDA FLOR, JESUS É MELHOR, ELE, SIM, SATISFAZ, JESUS É MELHOR, SIM, ELE É AMOR, CAMINHO, LUZ, VERDADE E PAZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('226', '226 - O SENTIDO DE VIVER', 'O SENTIDO DE VIVER ENCONTRAMOS EM JESUS, ELE É A SALVAÇÃO QUE HOJE PODES TER; É SÓ ABRIR TEU CORAÇÃO, DEIXAR CRISTO HABITAR, A PAZ E A ALEGRIA EM TI IRÃO REINAR.\n\nCORO (2X) JESUS, O SENTIDO DE VIVER, (DE VIVER SÓ PRA JESUS) VARÕES JESUS, O AMOR, A PAZ REAL, NÃO HÁ OUTRO AMOR IGUAL.\n\nJESUS CRISTO VAI VOLTAR, ELE UM DIA PROMETEU, E SEREMOS TRANSFORMADOS PARA O ENCONTRO COM O SENHOR; ESTAREMOS PARA SEMPRE COM JESUS, O REDENTOR, ONDE SÓ HAVERÁ PAZ, NUNCA HAVERÁ DOR.\n\nCORO (2X) JESUS, O SENTIDO DE VIVER, (DE VIVER SÓ PRA JESUS) VARÕES JESUS, O AMOR, A PAZ REAL, NÃO HÁ OUTRO AMOR IGUAL. FINAL: JESUS.', 'O SENTIDO DE VIVER ENCONTRAMOS EM JESUS, ELE É A SALVAÇÃO QUE HOJE PODES TER; É SÓ ABRIR TEU CORAÇÃO, DEIXAR CRISTO HABITAR, A PAZ E A ALEGRIA EM TI IRÃO REINAR. CORO  JESUS, O SENTIDO DE VIVER, (DE VIVER SÓ PRA JESUS) VARÕES JESUS, O AMOR, A PAZ REAL, NÃO HÁ OUTRO AMOR IGUAL. JESUS CRISTO VAI VOLTAR, ELE UM DIA PROMETEU, E SEREMOS TRANSFORMADOS PARA O ENCONTRO COM O SENHOR; ESTAREMOS PARA SEMPRE COM JESUS, O REDENTOR, ONDE SÓ HAVERÁ PAZ, NUNCA HAVERÁ DOR. CORO  JESUS, O SENTIDO DE VIVER, (DE VIVER SÓ PRA JESUS) VARÕES JESUS, O AMOR, A PAZ REAL, NÃO HÁ OUTRO AMOR IGUAL.  JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('227', '227 - SÓ JESUS TE PODE DAR O QUE PROCURAS', 'SÓ JESUS TE PODE DAR O QUE PROCURAS, ELE É A PORTA QUE SE ABRE PARA O CÉU; BATE AGORA E A PORTA SE ABRIRÁ PRA TI, E SABERÁS QUE ELE SEMPRE TE ESPEROU.\n\nCORO ENTRA E VÊ A GLÓRIA, ENTRA E VÊ O AMOR, A FARTURA QUE HÁ NA CASA DO SENHOR; NADA SE COMPARA AO QUE VÊS AQUI, TUDO ISTO DEUS PREPAROU PARA TI.\n\nTANTAS VEZES PERDESTE O RUMO EM TEU VIVER, PORTAS FECHADAS QUE NÃO PUDESTE ABRIR; MAS JESUS É A PORTA QUE ABRE PARA TI UM NOVO DIA PARA TU SERES FELIZ.\n\nCORO ENTRA E VÊ A GLÓRIA, ENTRA E VÊ O AMOR, A FARTURA QUE HÁ NA CASA DO SENHOR; NADA SE COMPARA AO QUE VÊS AQUI, TUDO ISTO DEUS PREPAROU PARA TI. FINAL: PREPAROU PARA TI.', 'SÓ JESUS TE PODE DAR O QUE PROCURAS, ELE É A PORTA QUE SE ABRE PARA O CÉU; BATE AGORA E A PORTA SE ABRIRÁ PRA TI, E SABERÁS QUE ELE SEMPRE TE ESPEROU. CORO ENTRA E VÊ A GLÓRIA, ENTRA E VÊ O AMOR, A FARTURA QUE HÁ NA CASA DO SENHOR; NADA SE COMPARA AO QUE VÊS AQUI, TUDO ISTO DEUS PREPAROU PARA TI. TANTAS VEZES PERDESTE O RUMO EM TEU VIVER, PORTAS FECHADAS QUE NÃO PUDESTE ABRIR; MAS JESUS É A PORTA QUE ABRE PARA TI UM NOVO DIA PARA TU SERES FELIZ. CORO ENTRA E VÊ A GLÓRIA, ENTRA E VÊ O AMOR, A FARTURA QUE HÁ NA CASA DO SENHOR; NADA SE COMPARA AO QUE VÊS AQUI, TUDO ISTO DEUS PREPAROU PARA TI.  PREPAROU PARA TI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('228', '228 - ENTREGA O TEU CAMINHO AO SENHOR', 'ENTREGA O TEU CAMINHO AO SENHOR E O MAIS ELE FARÁ, FARÁ SOBRESSAIR A SUA JUSTIÇA COMO A LUZ E O SEU JUÍZO; CUMPRIRÁ O TEU DESEJO, ELE TE MOSTRARÁ O SEU PERDÃO, A SALVAÇÃO, A REVELAÇÃO DA PALAVRA DE JESUS (EM TEU SER).\n\nENTREGA O TEU CAMINHO AO SENHOR E O MAIS ELE FARÁ, FARÁ SOBRESSAIR A SUA JUSTIÇA COMO A LUZ E O SEU JUÍZO, O SEU PERDÃO, A SALVAÇÃO, A REVELAÇÃO DA PALAVRA DE JESUS (EM TEU SER). ENTREGA O TEU CAMINHO AO SENHOR, (ENTREGA O TEU CAMINHO AO SENHOR) AO SENHOR.\nBIS', 'ENTREGA O TEU CAMINHO AO SENHOR E O MAIS ELE FARÁ, FARÁ SOBRESSAIR A SUA JUSTIÇA COMO A LUZ E O SEU JUÍZO; CUMPRIRÁ O TEU DESEJO, ELE TE MOSTRARÁ O SEU PERDÃO, A SALVAÇÃO, A REVELAÇÃO DA PALAVRA DE JESUS (EM TEU SER). ENTREGA O TEU CAMINHO AO SENHOR E O MAIS ELE FARÁ, FARÁ SOBRESSAIR A SUA JUSTIÇA COMO A LUZ E O SEU JUÍZO, O SEU PERDÃO, A SALVAÇÃO, A REVELAÇÃO DA PALAVRA DE JESUS (EM TEU SER). ENTREGA O TEU CAMINHO AO SENHOR, (ENTREGA O TEU CAMINHO AO SENHOR) AO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('229', '229 - AO POÇO DE JACÓ CHEGOU JESUS', 'AO POÇO DE JACÓ CHEGOU JESUS, ESTAVA CANSADO, ALI CHEGOU PRA DESCANSAR; CHEGOU TAMBÉM ALI UMA MULHER, COM SEU CÂNTARO ÁGUA FOI BUSCAR. JESUS PEDIU-LHE ÁGUA PRA BEBER, MAS A MULHER FEZ EXCEÇÃO E NÃO LHE DEU, POR SER SAMARITANA, JESUS ERA JUDEU, MAS DA ÁGUA QUE JESUS DIZIA, ELA BEBEU.\n\nCORO SE TU CONHECERAS O DOM DE DEUS, TU PEDIRIAS, ELE DARIA PRA BEBER DA ÁGUA MUI SUPERNA, QUE SALTA PARA A VIDA ETERNA, DESTA ÁGUA SÓ JESUS TEM PARA DAR.\n\nJESUS É MAIS QUE O POÇO DE JACÓ, ELE É A FONTE DE BASTANTE ÁGUA, É SÓ TOMAR; TODOS QUE TÊM PECADO É SÓ CHEGAR, ESTA ÁGUA TEM PODER PRA LIBERTAR. A ÁGUA QUE JESUS DÁ, ELA É SUAVE, ACONSELHÁVEL, E JESUS MANDA BEBER; QUEM TOMA DESTA ÁGUA VIDA ETERNA TEM, VIVER COM CRISTO, AQUI E NO ALÉM.\n\nCORO SE TU CONHECERAS O DOM DE DEUS, TU PEDIRIAS, ELE DARIA PRA BEBER DA ÁGUA MUI SUPERNA, QUE SALTA PARA A VIDA ETERNA, DESTA ÁGUA SÓ JESUS TEM PARA DAR.', 'AO POÇO DE JACÓ CHEGOU JESUS, ESTAVA CANSADO, ALI CHEGOU PRA DESCANSAR; CHEGOU TAMBÉM ALI UMA MULHER, COM SEU CÂNTARO ÁGUA FOI BUSCAR. JESUS PEDIU-LHE ÁGUA PRA BEBER, MAS A MULHER FEZ EXCEÇÃO E NÃO LHE DEU, POR SER SAMARITANA, JESUS ERA JUDEU, MAS DA ÁGUA QUE JESUS DIZIA, ELA BEBEU. CORO SE TU CONHECERAS O DOM DE DEUS, TU PEDIRIAS, ELE DARIA PRA BEBER DA ÁGUA MUI SUPERNA, QUE SALTA PARA A VIDA ETERNA, DESTA ÁGUA SÓ JESUS TEM PARA DAR. JESUS É MAIS QUE O POÇO DE JACÓ, ELE É A FONTE DE BASTANTE ÁGUA, É SÓ TOMAR; TODOS QUE TÊM PECADO É SÓ CHEGAR, ESTA ÁGUA TEM PODER PRA LIBERTAR. A ÁGUA QUE JESUS DÁ, ELA É SUAVE, ACONSELHÁVEL, E JESUS MANDA BEBER; QUEM TOMA DESTA ÁGUA VIDA ETERNA TEM, VIVER COM CRISTO, AQUI E NO ALÉM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('230', '230 - FONTE DE ÁGUAS VIVAS', 'FONTE DE ÁGUAS VIVAS, CRISTO JESUS NOS CONCEDE A PAZ, PAZ CELESTIAL; SACIA A NOSSA SEDE, CONSOLA A ALMA, LIVRA-NOS DO MAL.\n\nQUEM TIVER SEDE, SEDE DE ÁGUAS VIVAS, (DE GRAÇA LHE DAREI DA FONTE DA VIDA) DE GRAÇA LHE DAREI DA FONTE DA VIDA (FONTE DA VIDA).\n\nO CAMINHO DO SENHOR É VERDADEIRO, RETO E PURO, JUSTO E PERFEITO. GUARDA OS SEUS SERVOS, COM SEGURANÇA ESTENDE SUA MÃO.\n\nNA HORA DA ANGÚSTIA OU MESMO NA TRISTEZA, (JESUS NOSSO REFÚGIO, NOSSA FORTALEZA) JESUS NOSSO REFÚGIO, NOSSA FORTALEZA (FORTALEZA).\n\nSABEMOS QUE EM BREVE (ALELUIA) JESUS VOLTARÁ PARA A SUA GLÓRIA A IGREJA LEVAR; COM ELE HABITAREMOS (ALELUIA), SEREMOS SEU POVO E ELE O NOSSO DEUS.\n\nNÃO HAVERÁ MAIS MORTE, NEM PRANTO OU CLAMOR, VIVENDO PARA SEMPRE COM O SALVADOR.\n\nNÃO HAVERÁ MAIS MORTE, NEM PRANTO OU CLAMOR (VIVENDO PARA SEMPRE COM O SALVADOR) VIVENDO PARA SEMPRE COM O SALVADOR (COM O SALVADOR).', 'FONTE DE ÁGUAS VIVAS, CRISTO JESUS NOS CONCEDE A PAZ, PAZ CELESTIAL; SACIA A NOSSA SEDE, CONSOLA A ALMA, LIVRA-NOS DO MAL. QUEM TIVER SEDE, SEDE DE ÁGUAS VIVAS, (DE GRAÇA LHE DAREI DA FONTE DA VIDA) DE GRAÇA LHE DAREI DA FONTE DA VIDA (FONTE DA VIDA). O CAMINHO DO SENHOR É VERDADEIRO, RETO E PURO, JUSTO E PERFEITO. GUARDA OS SEUS SERVOS, COM SEGURANÇA ESTENDE SUA MÃO. NA HORA DA ANGÚSTIA OU MESMO NA TRISTEZA, (JESUS NOSSO REFÚGIO, NOSSA FORTALEZA) JESUS NOSSO REFÚGIO, NOSSA FORTALEZA (FORTALEZA). SABEMOS QUE EM BREVE (ALELUIA) JESUS VOLTARÁ PARA A SUA GLÓRIA A IGREJA LEVAR; COM ELE HABITAREMOS (ALELUIA), SEREMOS SEU POVO E ELE O NOSSO DEUS. NÃO HAVERÁ MAIS MORTE, NEM PRANTO OU CLAMOR, VIVENDO PARA SEMPRE COM O SALVADOR. NÃO HAVERÁ MAIS MORTE, NEM PRANTO OU CLAMOR (VIVENDO PARA SEMPRE COM O SALVADOR) VIVENDO PARA SEMPRE COM O SALVADOR (COM O SALVADOR).',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('231', '231 - QUEM ME DÁ ÁGUA', 'QUEM ME DÁ ÁGUA QUANDO ESTOU SEDENTO, E TRAZ A PAZ QUANDO ME ENCONTRO AFLITO, TIRANDO A ANGÚSTIA DENTRO DA MINH’ALMA, ME ALEGRANDO COM O SEU PRECIOSO AMOR?\n\nELE É JESUS, O REI, (O REI) VARÕES NELE ENCONTRO PAZ, (A PAZ) VARÕES ELE É MEU SALVADOR, (MEU REDENTOR) VARÕES DEUS FORTE, REDENTOR.\nBIS\n\nQUERES DESCANSO DENTRO DO TEU SER? CONFIA EM CRISTO, ELE TEM PODER, VENCEU A MORTE LÁ NA CRUZ. VIDA É JESUS, VIDA É JESUS.', 'QUEM ME DÁ ÁGUA QUANDO ESTOU SEDENTO, E TRAZ A PAZ QUANDO ME ENCONTRO AFLITO, TIRANDO A ANGÚSTIA DENTRO DA MINH’ALMA, ME ALEGRANDO COM O SEU PRECIOSO AMOR? ELE É JESUS, O REI, (O REI) VARÕES NELE ENCONTRO PAZ, (A PAZ) VARÕES ELE É MEU SALVADOR, (MEU REDENTOR) VARÕES DEUS FORTE, REDENTOR. QUERES DESCANSO DENTRO DO TEU SER? CONFIA EM CRISTO, ELE TEM PODER, VENCEU A MORTE LÁ NA CRUZ. VIDA É JESUS, VIDA É JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('232', '232 - QUEM BEBER DA ÁGUA QUE EU LHE DER', '“QUEM BEBER DA ÁGUA QUE EU LHE DER, JAMAIS TERÁ SEDE DE NOVO, POIS DA ÁGUA QUE EU LHE DER SE FARÁ UMA FONTE PRA VIDA ETERNA”.\nBIS\n\nO SENHOR ESTÁ JUNTO A TI, PODEROSO PARA TE SALVAR, SE HOJE OUVIRES SUA DOCE VOZ, NÃO ENDUREÇAS O TEU CORAÇÃO, NÃO DIGAS NÃO.\n\n“MAS VEM A MIM, Ó TU QUE ESTÁS CANSADO, E DESCANSO EU TE DAREI, POIS DA ÁGUA QUE EU TE DER SE FARÁ UMA FONTE PRA VIDA ETERNA”.\nBIS', 'QUEM BEBER DA ÁGUA QUE EU LHE DER, JAMAIS TERÁ SEDE DE NOVO, POIS DA ÁGUA QUE EU LHE DER SE FARÁ UMA FONTE PRA VIDA ETERNA. O SENHOR ESTÁ JUNTO A TI, PODEROSO PARA TE SALVAR, SE HOJE OUVIRES SUA DOCE VOZ, NÃO ENDUREÇAS O TEU CORAÇÃO, NÃO DIGAS NÃO. MAS VEM A MIM, Ó TU QUE ESTÁS CANSADO, E DESCANSO EU TE DAREI, POIS DA ÁGUA QUE EU TE DER SE FARÁ UMA FONTE PRA VIDA ETERNA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('233', '233 - VOU GUIAR-TE A UMA RICA FONTE', 'VOU GUIAR-TE A UMA RICA FONTE DE ÁGUAS CRISTALINAS, MATARÁS A TUA SEDE, SUBIRÁS MUITAS COLINAS, VOU LEVAR-TE AOS ALTOS MONTES.\n\nSOU O SENHOR, SOU A ROCHA FONTE QUE JORRA O AMOR, SOU A FÉ QUE DESABROCHA NAQUELE QUE DESSEDENTO. SOU REAL, SOU O SENHOR!\n\nVENHAM, Ó FILHOS QUERIDOS, MORAR ALÉM DO HORIZONTE, SALTAR PARA A ETERNIDADE, BEBER DA ÁGUA DA VIDA. ONDE EU, JESUS, SOU A FONTE ONDE EU, JESUS, SOU A FONTE ONDE EU, JESUS, SOU A FONTE\nBIS', 'VOU GUIAR-TE A UMA RICA FONTE DE ÁGUAS CRISTALINAS, MATARÁS A TUA SEDE, SUBIRÁS MUITAS COLINAS, VOU LEVAR-TE AOS ALTOS MONTES. SOU O SENHOR, SOU A ROCHA FONTE QUE JORRA O AMOR, SOU A FÉ QUE DESABROCHA NAQUELE QUE DESSEDENTO. SOU REAL, SOU O SENHOR! VENHAM, Ó FILHOS QUERIDOS, MORAR ALÉM DO HORIZONTE, SALTAR PARA A ETERNIDADE, BEBER DA ÁGUA DA VIDA. ONDE EU, JESUS, SOU A FONTE ONDE EU, JESUS, SOU A FONTE ONDE EU, JESUS, SOU A FONTE',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('234', '234 - DEUS HOJE QUER TE FALAR', 'DEUS HOJE QUER TE FALAR, ELE BATE AO TEU CORAÇÃO; SE TU ABRIRES, ELE VAI ENTRAR E TE DARÁ SALVAÇÃO.\n\nVOZ TÃO SUAVE ELE TEM, FALANDO COM TANTO AMOR; POR TI JESUS MORREU NA CRUZ, ELE É O TEU SALVADOR. SE HOJE OUVIRES SUA VOZ, ABRE-LHE TEU CORAÇÃO; ENTÃO TERÁS AMOR E PAZ QUE JAMAIS TERMINARÃO.\nBIS', 'DEUS HOJE QUER TE FALAR, ELE BATE AO TEU CORAÇÃO; SE TU ABRIRES, ELE VAI ENTRAR E TE DARÁ SALVAÇÃO. VOZ TÃO SUAVE ELE TEM, FALANDO COM TANTO AMOR; POR TI JESUS MORREU NA CRUZ, ELE É O TEU SALVADOR. SE HOJE OUVIRES SUA VOZ, ABRE-LHE TEU CORAÇÃO; ENTÃO TERÁS AMOR E PAZ QUE JAMAIS TERMINARÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('235', '235 - MANSO E SUAVE JESUS ESTÁ CHAMANDO', 'MANSO E SUAVE JESUS ESTÁ CHAMANDO, CHAMA POR TI E POR MIM; EIS QUE À PORTA ESPERA VELANDO, VELA POR TI E POR MIM.\n\nCORO VEM JÁ, VEM JÁ! ESTÁS CANSADO, VEM JÁ! MANSO E SUAVE JESUS ESTÁ CHAMANDO; CHAMA: “Ó PECADOR, VEM!”\n\nO TEMPO CORRE, AS HORAS SE PASSAM, PASSAM PRA TI E PRA MIM; MORTE E LEITO DE DOR PRESTO CHAMAM, CHAMAM POR TI E POR MIM.\n\nCORO VEM JÁ, VEM JÁ! ESTÁS CANSADO, VEM JÁ! MANSO E SUAVE JESUS ESTÁ CHAMANDO; CHAMA: “Ó PECADOR, VEM!”\n\nQUE ESPERAMOS? JESUS CONVIDANDO, CONVIDA A TI E A MIM. POR QUE DESPREZAS MERCÊ QUE ESTÁ DANDO, DANDO A TI E A MIM?\n\nCORO VEM JÁ, VEM JÁ! ESTÁS CANSADO, VEM JÁ! MANSO E SUAVE JESUS ESTÁ CHAMANDO; CHAMA: “Ó PECADOR, VEM!”\n\nOH! QUE AMOR QUE JESUS NOS TEM DADO, DADO PRA TI E PRA MIM! MORREU PRA SALVAR-NOS DO VIL PECADO, SALVAR A TI E A MIM.\n\nCORO VEM JÁ, VEM JÁ! ESTÁS CANSADO, VEM JÁ! MANSO E SUAVE JESUS ESTÁ CHAMANDO; CHAMA: “Ó PECADOR, VEM!”', 'MANSO E SUAVE JESUS ESTÁ CHAMANDO, CHAMA POR TI E POR MIM; EIS QUE À PORTA ESPERA VELANDO, VELA POR TI E POR MIM. CORO VEM JÁ, VEM JÁ! ESTÁS CANSADO, VEM JÁ! MANSO E SUAVE JESUS ESTÁ CHAMANDO; CHAMA: Ó PECADOR, VEM! O TEMPO CORRE, AS HORAS SE PASSAM, PASSAM PRA TI E PRA MIM; MORTE E LEITO DE DOR PRESTO CHAMAM, CHAMAM POR TI E POR MIM. QUE ESPERAMOS? JESUS CONVIDANDO, CONVIDA A TI E A MIM. POR QUE DESPREZAS MERCÊ QUE ESTÁ DANDO, DANDO A TI E A MIM? OH! QUE AMOR QUE JESUS NOS TEM DADO, DADO PRA TI E PRA MIM! MORREU PRA SALVAR-NOS DO VIL PECADO, SALVAR A TI E A MIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('236', '236 - OUVE A VOZ DE JESUS', 'OUVE A VOZ DE JESUS QUE É TÃO LINDA E TÃO CALMA E ESTÁ CHAMANDO: “VEM JÁ!” SE ESTÁS CANSADO, DISSE ELE ASSIM: “MEU FILHO, VEM HOJE A MIM!” INSTRUMENTOS\n\nSE ESTÁS CANSADO, DISSE ELE ASSIM: “MEU FILHO, VEM HOJE A MIM!” MANSO E SUAVE É JESUS, QUÃO DOCE E MEIGO É OUVI-LO! VEM JÁ OUVIR SUA VOZ; SE ESTÁS CANSADO, DISSE JESUS: “EU SOU O CAMINHO E A LUZ.”\nBIS', 'OUVE A VOZ DE JESUS QUE É TÃO LINDA E TÃO CALMA E ESTÁ CHAMANDO: VEM JÁ! SE ESTÁS CANSADO, DISSE ELE ASSIM: MEU FILHO, VEM HOJE A MIM! INSTRUMENTOS SE ESTÁS CANSADO, DISSE ELE ASSIM: MEU FILHO, VEM HOJE A MIM! MANSO E SUAVE É JESUS, QUÃO DOCE E MEIGO É OUVI-LO! VEM JÁ OUVIR SUA VOZ; SE ESTÁS CANSADO, DISSE JESUS: EU SOU O CAMINHO E A LUZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('237', '237 - EIS MENSAGEM DO SENHOR', 'EIS MENSAGEM DO SENHOR; ALELUIA! PALAVRAS DO BOM DEUS DE AMOR! CRISTO SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR.\n\nCORO OH! OLHAI, IRMÃOS, OLHAI, OH! OLHAI SÓ PRA JESUS. ELE SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR!\n\nVOSSA DÍVIDA PAGOU; ALELUIA! JESUS A SATISFEZ NA CRUZ. SUA VIDA ENTREGOU; ALELUIA! PARA VOS APRESENTAR A DEUS!\n\nCORO OH! OLHAI, IRMÃOS, OLHAI, OH! OLHAI SÓ PRA JESUS. ELE SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR!\n\nESTA OFERTA É FEITA A VÓS; ALELUIA! ETERNA VIDA LÁ NOS CÉUS! OH! OLHAI PRA CRISTO SÓ; ALELUIA! CONVERTEI-VOS JÁ AO VOSSO DEUS.\n\nCORO OH! OLHAI, IRMÃOS, OLHAI, OH! OLHAI SÓ PRA JESUS. ELE SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR!\n\nACEITAI A SALVAÇÃO; ALELUIA! SEGUI NOS PASSOS DO SENHOR; PUBLICAI O SEU PERDÃO; ALELUIA! PROCLAMAI O GRANDE REDENTOR!\n\nCORO OH! OLHAI, IRMÃOS, OLHAI, OH! OLHAI SÓ PRA JESUS. ELE SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR!', 'EIS MENSAGEM DO SENHOR; ALELUIA! PALAVRAS DO BOM DEUS DE AMOR! CRISTO SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR. CORO OH! OLHAI, IRMÃOS, OLHAI, OH! OLHAI SÓ PRA JESUS. ELE SALVA O PECADOR; ALELUIA! SALVA-O ATÉ POR MEIO DE UM OLHAR! VOSSA DÍVIDA PAGOU; ALELUIA! JESUS A SATISFEZ NA CRUZ. SUA VIDA ENTREGOU; ALELUIA! PARA VOS APRESENTAR A DEUS! ESTA OFERTA É FEITA A VÓS; ALELUIA! ETERNA VIDA LÁ NOS CÉUS! OH! OLHAI PRA CRISTO SÓ; ALELUIA! CONVERTEI-VOS JÁ AO VOSSO DEUS. ACEITAI A SALVAÇÃO; ALELUIA! SEGUI NOS PASSOS DO SENHOR; PUBLICAI O SEU PERDÃO; ALELUIA! PROCLAMAI O GRANDE REDENTOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('238', '238 - TANTO TEMPO JÁ FAZ QUE O CHAMADO DIVINAL', 'TANTO TEMPO JÁ FAZ QUE O CHAMADO DIVINAL TEM CHEGADO AO VIL PECADOR; “SE ESTÁS OPRIMIDO, VEM A MIM SEM TARDAR, POIS EU TE AMO, REPOUSO TE DAREI”.\n\nCORO EU PERTENÇO AO REI DO UNIVERSO, PARTICIPO DA HERANÇA REAL; AO FINDAR O LABUTAR, TÉ O FIM DA NOSSA HISTÓRIA VIVEREI AOS CUIDADOS DO REI.\n\nPROCURAVA ACHAR UM LUGAR PARA MIM, ALMEJAVA JESUS ENCONTRAR; TÃO LOGO ELE ACHOU-ME E DEU-ME UM LAR, UM NOVO HINO AGORA VOU CANTAR.\n\nCORO EU PERTENÇO AO REI DO UNIVERSO, PARTICIPO DA HERANÇA REAL; AO FINDAR O LABUTAR, TÉ O FIM DA NOSSA HISTÓRIA VIVEREI AOS CUIDADOS DO REI. (2X)', 'TANTO TEMPO JÁ FAZ QUE O CHAMADO DIVINAL TEM CHEGADO AO VIL PECADOR; SE ESTÁS OPRIMIDO, VEM A MIM SEM TARDAR, POIS EU TE AMO, REPOUSO TE DAREI. CORO EU PERTENÇO AO REI DO UNIVERSO, PARTICIPO DA HERANÇA REAL; AO FINDAR O LABUTAR, TÉ O FIM DA NOSSA HISTÓRIA VIVEREI AOS CUIDADOS DO REI. PROCURAVA ACHAR UM LUGAR PARA MIM, ALMEJAVA JESUS ENCONTRAR; TÃO LOGO ELE ACHOU-ME E DEU-ME UM LAR, UM NOVO HINO AGORA VOU CANTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('239', '239 - DEIXA DEUS FALAR AO TEU CORAÇÃO', 'DEIXA DEUS FALAR AO TEU CORAÇÃO, ELE QUER SE REVELAR A TI, SUA VOZ APRENDE A ESCUTAR, DEIXA DEUS TE FALAR; BUSCA MAIS SUA PRESENÇA, VIGIANDO EM ORAÇÃO, SUA VOZ IRÁS, ENTÃO, OUVIR FALANDO AO TEU CORAÇÃO.\n\nTEUS PROBLEMAS DEUS PODE RESOLVER, DÁ-LHE TUA VIDA SEM TEMER, SE TEUS PASSOS DEIXAS DEUS GUIAR, PAZ IRÁS EXPERIMENTAR; ELE TEM UM PLANO PARA TI E TE GUIA COM SUA MÃO, SIM, ESCUTA O QUE ELE AGORA DIZ, FALANDO AO TEU CORAÇÃO. (BIS)', 'DEIXA DEUS FALAR AO TEU CORAÇÃO, ELE QUER SE REVELAR A TI, SUA VOZ APRENDE A ESCUTAR, DEIXA DEUS TE FALAR; BUSCA MAIS SUA PRESENÇA, VIGIANDO EM ORAÇÃO, SUA VOZ IRÁS, ENTÃO, OUVIR FALANDO AO TEU CORAÇÃO. TEUS PROBLEMAS DEUS PODE RESOLVER, DÁ-LHE TUA VIDA SEM TEMER, SE TEUS PASSOS DEIXAS DEUS GUIAR, PAZ IRÁS EXPERIMENTAR; ELE TEM UM PLANO PARA TI E TE GUIA COM SUA MÃO, SIM, ESCUTA O QUE ELE AGORA DIZ, FALANDO AO TEU CORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('240', '240 - SE CANSADO TU ESTÁS', 'SE CANSADO TU ESTÁS, SOBRECARREGADO SIM, DEIXA TUDO AGORA, DIGO: VEM A MIM! EU TE ALIVIAREI, PÕE TEU FARDO SOBRE MIM, DEIXA TUDO AGORA, DIGO: VEM A MIM!\n\nVEM A MIM, Ó PECADOR, QUERO SER TEU SALVADOR, POIS SOU MANSO E HUMILDE DE CORAÇÃO; VEM A MIM, DESCANSARÁS, TUA ALMA REPOUSARÁ, QUE SUAVE É MEU JUGO E LEVE O MEU FARDO.\nBIS', 'SE CANSADO TU ESTÁS, SOBRECARREGADO SIM, DEIXA TUDO AGORA, DIGO: VEM A MIM! EU TE ALIVIAREI, PÕE TEU FARDO SOBRE MIM, DEIXA TUDO AGORA, DIGO: VEM A MIM! VEM A MIM, Ó PECADOR, QUERO SER TEU SALVADOR, POIS SOU MANSO E HUMILDE DE CORAÇÃO; VEM A MIM, DESCANSARÁS, TUA ALMA REPOUSARÁ, QUE SUAVE É MEU JUGO E LEVE O MEU FARDO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('241', '241 - MEU AMADO FILHO', 'MEU AMADO FILHO, TE ESCOLHI, NÃO TEMAS, MAS ESFORÇA-TE, ASSIM DIZ O SENHOR; MINHA MÃO ESTENDIDA PARA TI, POR ONDE QUER QUE FORES, NÃO TE DEIXAREI.\n\nCORO (2X) MINHA PAZ EU TE DOU, NÃO TE DEIXAREI, DIZ O SENHOR.\n\nA TERRA PROMETIDA TE DAREI, A ÁRVORE DA VIDA TE CONCEDEREI; RIOS DE ÁGUA VIVA FLUIRÃO E O MEU IMENSO AMOR NO TEU CORAÇÃO.\n\nCORO (2X) MINHA PAZ EU TE DOU, NÃO TE DEIXAREI, DIZ O SENHOR. NÃO TE DEIXAREI, DIZ O SENHOR. (3X)', 'MEU AMADO FILHO, TE ESCOLHI, NÃO TEMAS, MAS ESFORÇA-TE, ASSIM DIZ O SENHOR; MINHA MÃO ESTENDIDA PARA TI, POR ONDE QUER QUE FORES, NÃO TE DEIXAREI. CORO  MINHA PAZ EU TE DOU, NÃO TE DEIXAREI, DIZ O SENHOR. A TERRA PROMETIDA TE DAREI, A ÁRVORE DA VIDA TE CONCEDEREI; RIOS DE ÁGUA VIVA FLUIRÃO E O MEU IMENSO AMOR NO TEU CORAÇÃO. CORO  MINHA PAZ EU TE DOU, NÃO TE DEIXAREI, DIZ O SENHOR. NÃO TE DEIXAREI, DIZ O SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('242', '242 - SE ESTA NOITE DEUS PEDIR TUA ALMA', 'SE ESTA NOITE DEUS PEDIR TUA ALMA, O QUE TENS PREPARADO, PRA QUEM SERÁ? DE QUE SERVE CIÊNCIA OU RIQUEZA SE TUA ALMA PRA SEMPRE PERDERÁS?\n\nCORO VEM A JESUS, HOJE, SEM TARDAR, DOS FARDOS TEUS ELE QUER TE LIBERTAR; SE ESTA NOITE DEUS PEDIR TUA ALMA, PODES CRER: O CÉU SERÁ TEU LAR. INSTRUMENTOS\n\nDE QUE VALE TER TUDO NESTA VIDA E NÃO TER A CERTEZA DA SALVAÇÃO? TU PRECISAS VIR HOJE AO SALVADOR, PELA FÉ NO SEU SANGUE, TER PERDÃO.\n\nCORO VEM A JESUS, HOJE, SEM TARDAR, DOS FARDOS TEUS ELE QUER TE LIBERTAR; SE ESTA NOITE DEUS PEDIR TUA ALMA, PODES CRER: O CÉU SERÁ TEU LAR.', 'SE ESTA NOITE DEUS PEDIR TUA ALMA, O QUE TENS PREPARADO, PRA QUEM SERÁ? DE QUE SERVE CIÊNCIA OU RIQUEZA SE TUA ALMA PRA SEMPRE PERDERÁS? CORO VEM A JESUS, HOJE, SEM TARDAR, DOS FARDOS TEUS ELE QUER TE LIBERTAR; SE ESTA NOITE DEUS PEDIR TUA ALMA, PODES CRER: O CÉU SERÁ TEU LAR. INSTRUMENTOS DE QUE VALE TER TUDO NESTA VIDA E NÃO TER A CERTEZA DA SALVAÇÃO? TU PRECISAS VIR HOJE AO SALVADOR, PELA FÉ NO SEU SANGUE, TER PERDÃO. CORO VEM A JESUS, HOJE, SEM TARDAR, DOS FARDOS TEUS ELE QUER TE LIBERTAR; SE ESTA NOITE DEUS PEDIR TUA ALMA, PODES CRER: O CÉU SERÁ TEU LAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('243', '243 - JÁ ACHEI UMA FLOR GLORIOSA', 'JÁ ACHEI UMA FLOR GLORIOSA E QUEM DESEJA A MESMA TERÁ; A ROSA DE SAROM PRECIOSA ENTRE MIL A MAIS BELA SERÁ; NO VALE DE SOMBRA E MORTE, NAS ALTURAS DE GLÓRIA E DE LUZ, ESTA ROSA SERÁ A MINHA SORTE, PRECIOSO PRA MIM É JESUS!\n\nCORO PRECIOSO PRA MIM É JESUS! (BIS) EU CONFESSO NA VIDA E NA MORTE QUE TUDO PRA MIM É JESUS!\n\nJÁ DE MUITOS FOI ACHADA A ROSA E PROVADO O EXCELENTE ODOR E O PODER DESTA FLOR GLORIOSA QUE DÁ VIDA AO VIL PECADOR; MUI ZELOSO PELA LEI FOI SAULO, PERSEGUIA O POVO DE DEUS, MAS TRANSFORMADO FOI EM UM PAULO POIS ACHOU ELE A ROSA DOS CÉUS.\n\nCORO PRECIOSO PRA MIM É JESUS! (BIS) EU CONFESSO NA VIDA E NA MORTE QUE TUDO PRA MIM É JESUS!\n\nVAI BUSCAR A JESUS PRECIOSO, VAI DEPRESSA, A NOITE JÁ VEM, E SE PERDES O AMOR GLORIOSO, SERÁ TRISTE PRA TI O ALÉM; ESTA FLOR HOJE É OFERTADA A QUEM HUMILDEMENTE A BUSCAR, SERÁ LOGO DA TERRA TIRADA PARA BRILHAR EM OUTRO LUGAR.\n\nCORO PRECIOSO PRA MIM É JESUS! (BIS) EU CONFESSO NA VIDA E NA MORTE QUE TUDO PRA MIM É JESUS!', 'JÁ ACHEI UMA FLOR GLORIOSA E QUEM DESEJA A MESMA TERÁ; A ROSA DE SAROM PRECIOSA ENTRE MIL A MAIS BELA SERÁ; NO VALE DE SOMBRA E MORTE, NAS ALTURAS DE GLÓRIA E DE LUZ, ESTA ROSA SERÁ A MINHA SORTE, PRECIOSO PRA MIM É JESUS! CORO PRECIOSO PRA MIM É JESUS!  EU CONFESSO NA VIDA E NA MORTE QUE TUDO PRA MIM É JESUS! JÁ DE MUITOS FOI ACHADA A ROSA E PROVADO O EXCELENTE ODOR E O PODER DESTA FLOR GLORIOSA QUE DÁ VIDA AO VIL PECADOR; MUI ZELOSO PELA LEI FOI SAULO, PERSEGUIA O POVO DE DEUS, MAS TRANSFORMADO FOI EM UM PAULO POIS ACHOU ELE A ROSA DOS CÉUS. VAI BUSCAR A JESUS PRECIOSO, VAI DEPRESSA, A NOITE JÁ VEM, E SE PERDES O AMOR GLORIOSO, SERÁ TRISTE PRA TI O ALÉM; ESTA FLOR HOJE É OFERTADA A QUEM HUMILDEMENTE A BUSCAR, SERÁ LOGO DA TERRA TIRADA PARA BRILHAR EM OUTRO LUGAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('244', '244 - EU DESCI PARA O VALE DE BÊNÇÃO E PAZ', 'EU DESCI PARA O VALE DE BÊNÇÃO E PAZ E SINTO COMIGO JESUS; SEU SANGUE, AOS HUMILDES, GARANTE O PERDÃO, SEU ESPÍRITO OS ENCHE DE LUZ.\n\nCORO ENTRAI NESSE VALE DE BÊNÇÃO E PAZ ONDE CRISTO REVELA AFEIÇÃO; ACEITAI, ABRAÇAI, CONFESSAI-O, PUBLICAI QUE NELE HÁ SALVAÇÃO.\n\nHÁ FESTIM NESSE VALE DE BÊNÇÃO E PAZ E RIQUEZA DE INCRÍVEL VALOR; MAS SOMENTE OS FIÉIS É QUE PODEM CONTAR COM A GRAÇA DO BOM SALVADOR.\n\nCORO ENTRAI NESSE VALE DE BÊNÇÃO E PAZ ONDE CRISTO REVELA AFEIÇÃO; ACEITAI, ABRAÇAI, CONFESSAI-O, PUBLICAI QUE NELE HÁ SALVAÇÃO.\n\nHÁ SALMOS NO VALE DE BÊNÇÃO E PAZ E OS ANJOS DESEJAM SE UNIR A CANTAR COM OS HOMENS O EXCELSO LOUVOR DE JESUS QUE NOS VEIO REMIR.\n\nCORO ENTRAI NESSE VALE DE BÊNÇÃO E PAZ ONDE CRISTO REVELA AFEIÇÃO; ACEITAI, ABRAÇAI, CONFESSAI-O, PUBLICAI QUE NELE HÁ SALVAÇÃO.', 'EU DESCI PARA O VALE DE BÊNÇÃO E PAZ E SINTO COMIGO JESUS; SEU SANGUE, AOS HUMILDES, GARANTE O PERDÃO, SEU ESPÍRITO OS ENCHE DE LUZ. CORO ENTRAI NESSE VALE DE BÊNÇÃO E PAZ ONDE CRISTO REVELA AFEIÇÃO; ACEITAI, ABRAÇAI, CONFESSAI-O, PUBLICAI QUE NELE HÁ SALVAÇÃO. HÁ FESTIM NESSE VALE DE BÊNÇÃO E PAZ E RIQUEZA DE INCRÍVEL VALOR; MAS SOMENTE OS FIÉIS É QUE PODEM CONTAR COM A GRAÇA DO BOM SALVADOR. HÁ SALMOS NO VALE DE BÊNÇÃO E PAZ E OS ANJOS DESEJAM SE UNIR A CANTAR COM OS HOMENS O EXCELSO LOUVOR DE JESUS QUE NOS VEIO REMIR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('245', '245 - POR ONDE QUER QUE EU VÁ', 'POR ONDE QUER QUE EU VÁ COM CRISTO ESTAREI, NÃO IMPORTA O QUE VIER, ELE ME AJUDARÁ; MESMO QUE MINHA VIDA NO MUNDO CHEGUE AO FIM, TEREI UMA NOVA VIDA, ETERNA, COM JESUS.\n\nCORO QUERES SER FELIZ? CANTA COMO EU, SABE QUE JESUS POR TI MORREU. QUERES NESTE MUNDO SER UM VENCEDOR? ENTREGA-TE A JESUS, O SALVADOR. INSTRUMENTOS\n\nJESUS DISSE: “VINDE A MIM VÓS QUE ESTAIS CANSADOS, QUE EU VOS ALIVIAREI E TAMBÉM VOS SALVAREI”. JESUS, EU QUERO OUVIR O TOQUE DA TROMBETA, NESSE DIA EU QUERO IR AOS CÉUS TE ENCONTRAR.\n\nCORO (2X) QUERES SER FELIZ? CANTA COMO EU, SABE QUE JESUS POR TI MORREU. QUERES NESTE MUNDO SER UM VENCEDOR? ENTREGA-TE A JESUS, O SALVADOR.', 'POR ONDE QUER QUE EU VÁ COM CRISTO ESTAREI, NÃO IMPORTA O QUE VIER, ELE ME AJUDARÁ; MESMO QUE MINHA VIDA NO MUNDO CHEGUE AO FIM, TEREI UMA NOVA VIDA, ETERNA, COM JESUS. CORO QUERES SER FELIZ? CANTA COMO EU, SABE QUE JESUS POR TI MORREU. QUERES NESTE MUNDO SER UM VENCEDOR? ENTREGA-TE A JESUS, O SALVADOR. INSTRUMENTOS JESUS DISSE: VINDE A MIM VÓS QUE ESTAIS CANSADOS, QUE EU VOS ALIVIAREI E TAMBÉM VOS SALVAREI. JESUS, EU QUERO OUVIR O TOQUE DA TROMBETA, NESSE DIA EU QUERO IR AOS CÉUS TE ENCONTRAR. CORO  QUERES SER FELIZ? CANTA COMO EU, SABE QUE JESUS POR TI MORREU. QUERES NESTE MUNDO SER UM VENCEDOR? ENTREGA-TE A JESUS, O SALVADOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('246', '246 - SE EM TEU VIVER', 'SE EM TEU VIVER EXISTE UM VAZIO, SE TU NÃO PODES CANTAR COM OS QUE CANTAM PRA JESUS, NÃO TE DETENHAS, NÃO CHORES, MEU AMIGO, CRISTO TRANSFORMA AS TREVAS EM PLENA LUZ.\n\nCORO FELIZ SERÁS, JAMAIS VERÁS TUA VIDA EM PRANTO SE FINDAR; FELIZ SERÁS SE AO SENHOR TEU CORAÇÃO ENTREGAR.\n\nCONTEMPLA AGORA AS BÊNÇÃOS DE CRISTO, QUANDO, EM SORRISOS, TUAS LÁGRIMAS ELE VAI TRANSFORMAR, PRA SEMPRE GUIARÁ OS TEUS CAMINHOS E COM AMOR AO CÉU TE LEVARÁ.\n\nCORO FELIZ SERÁS, JAMAIS VERÁS TUA VIDA EM PRANTO SE FINDAR; FELIZ SERÁS SE AO SENHOR TEU CORAÇÃO ENTREGAR.', 'SE EM TEU VIVER EXISTE UM VAZIO, SE TU NÃO PODES CANTAR COM OS QUE CANTAM PRA JESUS, NÃO TE DETENHAS, NÃO CHORES, MEU AMIGO, CRISTO TRANSFORMA AS TREVAS EM PLENA LUZ. CORO FELIZ SERÁS, JAMAIS VERÁS TUA VIDA EM PRANTO SE FINDAR; FELIZ SERÁS SE AO SENHOR TEU CORAÇÃO ENTREGAR. CONTEMPLA AGORA AS BÊNÇÃOS DE CRISTO, QUANDO, EM SORRISOS, TUAS LÁGRIMAS ELE VAI TRANSFORMAR, PRA SEMPRE GUIARÁ OS TEUS CAMINHOS E COM AMOR AO CÉU TE LEVARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('247', '247 - SE ANDA TRISTE O TEU VIVER', 'SE ANDA TRISTE O TEU VIVER, SE ESTÁ VAZIO O TEU CORAÇÃO, EXISTE ALGUÉM QUE PODE DAR A PAZ QUE TANTO VENS A PROCURAR.\n\nCORO JESUS, ESSE ALGUÉM! JESUS, PODE DAR ALGO QUE VENS A PROCURAR, JESUS, BOM AMIGO QUE NÃO PODE FALTAR.\n\nSE ANDAS SEM A SOLUÇÃO, ANGÚSTIA INVADE O TEU CORAÇÃO, EXISTE ALGUÉM QUE PODE DAR A PAZ QUE TANTO VENS A PROCURAR.\n\nCORO (2X) JESUS, ESSE ALGUÉM! JESUS, PODE DAR ALGO QUE VENS A PROCURAR, JESUS, BOM AMIGO QUE NÃO PODE FALTAR.', 'SE ANDA TRISTE O TEU VIVER, SE ESTÁ VAZIO O TEU CORAÇÃO, EXISTE ALGUÉM QUE PODE DAR A PAZ QUE TANTO VENS A PROCURAR. CORO JESUS, ESSE ALGUÉM! JESUS, PODE DAR ALGO QUE VENS A PROCURAR, JESUS, BOM AMIGO QUE NÃO PODE FALTAR. SE ANDAS SEM A SOLUÇÃO, ANGÚSTIA INVADE O TEU CORAÇÃO, EXISTE ALGUÉM QUE PODE DAR A PAZ QUE TANTO VENS A PROCURAR. CORO  JESUS, ESSE ALGUÉM! JESUS, PODE DAR ALGO QUE VENS A PROCURAR, JESUS, BOM AMIGO QUE NÃO PODE FALTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('248', '248 - SE VOCÊ QUISER SABER', 'SE VOCÊ QUISER SABER COMO PODE SER FELIZ, BASTA ABRIR SEU CORAÇÃO E ACEITAR JESUS. SE VOCÊ QUER CONHECER ESTE DEUS MARAVILHOSO QUE DE BÊNÇÃOS VAI ENCHER SEU CORAÇÃO.\n\nCORO NELE HÁ TANTA VIDA, CERTEZAS E ESPERANÇAS, VIDA ETERNA E PAZ SEM FIM; NELE HÁ SALVAÇÃO. SE VOCÊ QUISER GOZAR DESTA GRANDE SALVAÇÃO, VENHA HOJE A JESUS SE ENTREGAR.\n\nCORO NELE HÁ TANTA VIDA, CERTEZAS E ESPERANÇAS, VIDA ETERNA E PAZ SEM FIM; NELE HÁ SALVAÇÃO.\n\nFINAL: SE VOCÊ QUISER SABER COMO PODE SER FELIZ, BASTA ABRIR SEU CORAÇÃO E ACEITAR JESUS.', 'SE VOCÊ QUISER SABER COMO PODE SER FELIZ, BASTA ABRIR SEU CORAÇÃO E ACEITAR JESUS. SE VOCÊ QUER CONHECER ESTE DEUS MARAVILHOSO QUE DE BÊNÇÃOS VAI ENCHER SEU CORAÇÃO. CORO NELE HÁ TANTA VIDA, CERTEZAS E ESPERANÇAS, VIDA ETERNA E PAZ SEM FIM; NELE HÁ SALVAÇÃO. SE VOCÊ QUISER GOZAR DESTA GRANDE SALVAÇÃO, VENHA HOJE A JESUS SE ENTREGAR. CORO NELE HÁ TANTA VIDA, CERTEZAS E ESPERANÇAS, VIDA ETERNA E PAZ SEM FIM; NELE HÁ SALVAÇÃO. SE VOCÊ QUISER SABER COMO PODE SER FELIZ, BASTA ABRIR SEU CORAÇÃO E ACEITAR JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('249', '249 - SE EM TUA VIDA FALTA PAZ', 'SE EM TUA VIDA FALTA PAZ, SE EM TEU VIVER NÃO EXISTE AMOR, QUERO TE FALAR DO MEU SENHOR, E UMA VIDA NOVA ENCONTRARÁS.\n\nCORO CRISTO É A VIDA, ELE É A PAZ, FONTE DE ÁGUA VIVA QUE A TODOS SATISFAZ.\n\nSÊ UMA NOVA CRIATURA, ENTREGA OS TEUS PECADOS A JESUS, ARREPENDE-TE E COMEÇA A VIVER, ANUNCIANDO QUE JESUS É A SALVAÇÃO.\n\nCORO CRISTO É A VIDA, ELE É A PAZ, FONTE DE ÁGUA VIVA QUE A TODOS SATISFAZ.', 'SE EM TUA VIDA FALTA PAZ, SE EM TEU VIVER NÃO EXISTE AMOR, QUERO TE FALAR DO MEU SENHOR, E UMA VIDA NOVA ENCONTRARÁS. CORO CRISTO É A VIDA, ELE É A PAZ, FONTE DE ÁGUA VIVA QUE A TODOS SATISFAZ. SÊ UMA NOVA CRIATURA, ENTREGA OS TEUS PECADOS A JESUS, ARREPENDE-TE E COMEÇA A VIVER, ANUNCIANDO QUE JESUS É A SALVAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('250', '250 - SE TU PROCURAS PELA PAZ', 'SE TU PROCURAS PELA PAZ, SE VAZIO ESTÁ TEU SER, NUNCA O MUNDO BUSCARÁS, MAS DEVES A JESUS TEMER; SE PROCURARES MAIS A CRISTO, SÓ VERDADE ENCONTRARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ.\n\nCORO CRISTO SATISFAZ, SÓ CRISTO A ALMA SATISFAZ, E ELE PODE COMPLETAR TEU CORAÇÃO; DARÁ A PAZ, TÃO DOCE AMOR, ENFIM NOS CÉUS HABITARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ.\n\nSE FAMA E BENS ALMEJAS TER, TÃO EM VÃO É O TEU VIVER, POIS A CRISTO NÃO TERÁS, A REDENÇÃO SÓ ELE DÁ; JAMAIS EM BENS CONFIARÁS, SALVAÇÃO ALI NÃO HÁ, POIS SÓ MEU CRISTO A ALMA SATISFAZ.\n\nCORO CRISTO SATISFAZ, SÓ CRISTO A ALMA SATISFAZ, E ELE PODE COMPLETAR TEU CORAÇÃO; DARÁ A PAZ, TÃO DOCE AMOR, ENFIM NOS CÉUS HABITARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ. FINAL: CRISTO SATISFAZ!', 'SE TU PROCURAS PELA PAZ, SE VAZIO ESTÁ TEU SER, NUNCA O MUNDO BUSCARÁS, MAS DEVES A JESUS TEMER; SE PROCURARES MAIS A CRISTO, SÓ VERDADE ENCONTRARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ. CORO CRISTO SATISFAZ, SÓ CRISTO A ALMA SATISFAZ, E ELE PODE COMPLETAR TEU CORAÇÃO; DARÁ A PAZ, TÃO DOCE AMOR, ENFIM NOS CÉUS HABITARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ. SE FAMA E BENS ALMEJAS TER, TÃO EM VÃO É O TEU VIVER, POIS A CRISTO NÃO TERÁS, A REDENÇÃO SÓ ELE DÁ; JAMAIS EM BENS CONFIARÁS, SALVAÇÃO ALI NÃO HÁ, POIS SÓ MEU CRISTO A ALMA SATISFAZ. CORO CRISTO SATISFAZ, SÓ CRISTO A ALMA SATISFAZ, E ELE PODE COMPLETAR TEU CORAÇÃO; DARÁ A PAZ, TÃO DOCE AMOR, ENFIM NOS CÉUS HABITARÁS, POIS SÓ MEU CRISTO A ALMA SATISFAZ.  CRISTO SATISFAZ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('251', '251 - A PAZ QUE TU PROCURAS', 'A PAZ QUE TU PROCURAS, A ALEGRIA QUE ALMEJAS HOJE PODES ENCONTRAR, AQUELE QUE POR TI MORREU HOJE AQUI ESTÁ E QUER TE SALVAR.\n\nCORO LOUVAI A JESUS E JUNTOS EXALTEMOS O SEU NOME, PROCLAMAI, (MARANATA!) PROCLAMAI: ELE VOLTARÁ! INSTRUMENTOS\n\nELE ESTÁ A TE CONTEMPLAR E COM SUA DOCE VOZ IRÁ FALAR AO TEU CORAÇÃO: “VEM, FILHO MEU, RECEBE A VIDA QUE ESTAVAS A PROCURAR.”\n\nCORO (2X) LOUVAI A JESUS E JUNTOS EXALTEMOS O SEU NOME, PROCLAMAI, (MARANATA!) PROCLAMAI: ELE VOLTARÁ! FINAL: PROCLAMAI (MARANATA!) PROCLAMAI: ELE VOLTARÁ!', 'A PAZ QUE TU PROCURAS, A ALEGRIA QUE ALMEJAS HOJE PODES ENCONTRAR, AQUELE QUE POR TI MORREU HOJE AQUI ESTÁ E QUER TE SALVAR. CORO LOUVAI A JESUS E JUNTOS EXALTEMOS O SEU NOME, PROCLAMAI, (MARANATA!) PROCLAMAI: ELE VOLTARÁ! INSTRUMENTOS ELE ESTÁ A TE CONTEMPLAR E COM SUA DOCE VOZ IRÁ FALAR AO TEU CORAÇÃO: VEM, FILHO MEU, RECEBE A VIDA QUE ESTAVAS A PROCURAR. CORO  LOUVAI A JESUS E JUNTOS EXALTEMOS O SEU NOME, PROCLAMAI, (MARANATA!) PROCLAMAI: ELE VOLTARÁ!  PROCLAMAI (MARANATA!) PROCLAMAI: ELE VOLTARÁ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('252', '252 - SE DISPUSERES O TEU CORAÇÃO (JÓ 11:13-19)', 'SE DISPUSERES O TEU CORAÇÃO E ESTENDERES AS MÃOS A DEUS, SE LANÇARES LONGE DE TUAS MÃOS TODA A INIQÜIDADE, SE NÃO PERMITIRES HABITAR EM TUA TENDA A INJUSTIÇA.\n\nCORO ENTÃO LEVANTARÁS O TEU ROSTO SEM MÁCULA, ESTARÁS SEGURO E NÃO TEMERÁS, POIS TE ESQUECERÁS DOS TEUS SOFRIMENTOS, DELES TE LEMBRARÁS COMO ÁGUAS QUE PASSARAM.\n\nTUA VIDA SERÁ MAIS CLARA QUE O MEIO DIA, AINDA QUE HAJA TREVAS, SERÁ COMO A MANHÃ, SENTIR-TE-ÁS SEGURO PORQUE HAVERÁ ESPERANÇA.\n\nCORO ENTÃO LEVANTARÁS O TEU ROSTO SEM MÁCULA, ESTARÁS SEGURO E NÃO TEMERÁS, POIS TE ESQUECERÁS DOS TEUS SOFRIMENTOS, DELES TE LEMBRARÁS COMO ÁGUAS QUE PASSARAM.\n\nOLHARÁS AO REDOR E DORMIRÁS TRANQUILO, DEITAR-TE-ÁS E NINGUÉM TE ESPANTARÁ, MUITOS PROCURARÃO OBTER O TEU FAVOR.\n\nCORO ENTÃO LEVANTARÁS O TEU ROSTO SEM MÁCULA, ESTARÁS SEGURO E NÃO TEMERÁS, POIS TE ESQUECERÁS DOS TEUS SOFRIMENTOS, DELES TE LEMBRARÁS COMO ÁGUAS QUE PASSARAM. SE DISPUSERES O TEU CORAÇÃO. (BIS)', 'SE DISPUSERES O TEU CORAÇÃO E ESTENDERES AS MÃOS A DEUS, SE LANÇARES LONGE DE TUAS MÃOS TODA A INIQÜIDADE, SE NÃO PERMITIRES HABITAR EM TUA TENDA A INJUSTIÇA. CORO ENTÃO LEVANTARÁS O TEU ROSTO SEM MÁCULA, ESTARÁS SEGURO E NÃO TEMERÁS, POIS TE ESQUECERÁS DOS TEUS SOFRIMENTOS, DELES TE LEMBRARÁS COMO ÁGUAS QUE PASSARAM. TUA VIDA SERÁ MAIS CLARA QUE O MEIO DIA, AINDA QUE HAJA TREVAS, SERÁ COMO A MANHÃ, SENTIR-TE-ÁS SEGURO PORQUE HAVERÁ ESPERANÇA. OLHARÁS AO REDOR E DORMIRÁS TRANQUILO, DEITAR-TE-ÁS E NINGUÉM TE ESPANTARÁ, MUITOS PROCURARÃO OBTER O TEU FAVOR. CORO ENTÃO LEVANTARÁS O TEU ROSTO SEM MÁCULA, ESTARÁS SEGURO E NÃO TEMERÁS, POIS TE ESQUECERÁS DOS TEUS SOFRIMENTOS, DELES TE LEMBRARÁS COMO ÁGUAS QUE PASSARAM. SE DISPUSERES O TEU CORAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('253', '253 - SE DISPUSERES O TEU CORAÇÃO', 'SE DISPUSERES O TEU CORAÇÃO E ESTENDERES TUA MÃO PRA DEUS, SE LANÇARES PARA LONGE A INIQUIDADE DE TUA MÃO, ESTARÁS SEGURO E NÃO TEMERÁS.\n\nSE TU NÃO PERMITIRES QUE A INJUSTIÇA, EM TUA TENDA FAÇA HABITAÇÃO, SE LANÇARES PARA LONGE A INIQUIDADE DE TUA MÃO, ESTARÁS SEGURO E NÃO TEMERÁS.\n\nTUA VIDA ENTÃO SERÁ MAIS CLARA QUE A MANHÃ, OLHARÁS EM VOLTA E DORMIRÁS TRANQUILO, MAS OS OLHOS DOS PERVERSOS DESFALECERÃO, SEU REFÚGIO PERECERÁ.', 'SE DISPUSERES O TEU CORAÇÃO E ESTENDERES TUA MÃO PRA DEUS, SE LANÇARES PARA LONGE A INIQUIDADE DE TUA MÃO, ESTARÁS SEGURO E NÃO TEMERÁS. SE TU NÃO PERMITIRES QUE A INJUSTIÇA, EM TUA TENDA FAÇA HABITAÇÃO, SE LANÇARES PARA LONGE A INIQUIDADE DE TUA MÃO, ESTARÁS SEGURO E NÃO TEMERÁS. TUA VIDA ENTÃO SERÁ MAIS CLARA QUE A MANHÃ, OLHARÁS EM VOLTA E DORMIRÁS TRANQUILO, MAS OS OLHOS DOS PERVERSOS DESFALECERÃO, SEU REFÚGIO PERECERÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('254', '254 - OLHAI PARA O ALTO', 'CORO (2X) OLHAI PARA O ALTO DE ONDE VEM A SALVAÇÃO, SEGUI PARA O ALVO, À ETERNA VOCAÇÃO.\n\nTODO JUSTO PELA FÉ VIVERÁ, SEU OLHAR ESTÁ FIRME NO SENHOR, ESPERANDO ENCONTRÁ-LO EM RESPLENDOR, PORQUE BREVE A TROMBETA SOARÁ. Ó OLHAI!\n\nCORO (2X) OLHAI PARA O ALTO DE ONDE VEM A SALVAÇÃO, SEGUI PARA O ALVO, À ETERNA VOCAÇÃO.\n\nESQUECENDO O QUE FICOU PARA TRÁS, SEGUE O JUSTO SEU CAMINHO COM JESUS, DIA A DIA CARREGANDO A SUA CRUZ, PORQUE BREVE JESUS CRISTO VOLTARÁ. Ó OLHAI!\n\nCORO (2X) OLHAI PARA O ALTO DE ONDE VEM A SALVAÇÃO, SEGUI PARA O ALVO, À ETERNA VOCAÇÃO.', 'CORO  OLHAI PARA O ALTO DE ONDE VEM A SALVAÇÃO, SEGUI PARA O ALVO, À ETERNA VOCAÇÃO. TODO JUSTO PELA FÉ VIVERÁ, SEU OLHAR ESTÁ FIRME NO SENHOR, ESPERANDO ENCONTRÁ-LO EM RESPLENDOR, PORQUE BREVE A TROMBETA SOARÁ. Ó OLHAI! ESQUECENDO O QUE FICOU PARA TRÁS, SEGUE O JUSTO SEU CAMINHO COM JESUS, DIA A DIA CARREGANDO A SUA CRUZ, PORQUE BREVE JESUS CRISTO VOLTARÁ. Ó OLHAI!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('255', '255 - MEU CORAÇÃO ENGRANDECE (CÂNTICO DE VITÓRIA)', 'MEU CORAÇÃO ENGRANDECE AO SENHOR PORQUE ELE MUDOU TODO O MEU SER, FEZ-ME ANDAR EM VEREDAS QUE EU NÃO CONHECIA.\n\nILUMINOU OS MEUS PÉS PARA QUE EU NÃO TROPEÇASSE, MEU CORAÇÃO JUBILOU DE ALEGRIA QUANDO EM MIM VEIO MORAR, FEZ EM MIM HABITAÇÃO PRO SEU ESPÍRITO. COLOCOU UM CÂNTICO NOVO EM MEUS LÁBIOS, AQUELE QUE EU NÃO CONHECIA, PELO SEU ESPÍRITO, ME FEZ CONHECER.\n\n“DESPERTA, (DESPERTA) SERVAS TU QUE DORMES, (TU QUE DORMES) SERVAS DEIXA O SOL (DEIXA O SOL) SERVAS RESPLANDECER SOBRE TUA VIDA.” INSTRUMENTOS\n\n“O SOL DESPONTA SOBRE A CIDADE QUANDO OS CONVOCO A ME ADORAR; JERUSALÉM, MORADA DO DEUS VIVO, CRIADOR DO UNIVERSO. MINHA MÃO ESTÁ SOBRE O MEU POVO, POIS EU O TENHO ESCOLHIDO, COM MINHAS MÃOS PODEROSAS FAÇO MORADA EM SEUS CORAÇÕES.”\n\n“REJUBILA, POVO MEU! (REJUBILA) SERVAS PORQUE EM BREVE VIRÁ MINHA GLÓRIA E VÓS PODEREIS LEVANTAR UM ESTANDARTE ENTRE AS NAÇÕES E PROCLAMAR QUE SOU REI.”\nBIS', 'MEU CORAÇÃO ENGRANDECE AO SENHOR PORQUE ELE MUDOU TODO O MEU SER, FEZ-ME ANDAR EM VEREDAS QUE EU NÃO CONHECIA. ILUMINOU OS MEUS PÉS PARA QUE EU NÃO TROPEÇASSE, MEU CORAÇÃO JUBILOU DE ALEGRIA QUANDO EM MIM VEIO MORAR, FEZ EM MIM HABITAÇÃO PRO SEU ESPÍRITO. COLOCOU UM CÂNTICO NOVO EM MEUS LÁBIOS, AQUELE QUE EU NÃO CONHECIA, PELO SEU ESPÍRITO, ME FEZ CONHECER. DESPERTA, (DESPERTA) SERVAS TU QUE DORMES, (TU QUE DORMES) SERVAS DEIXA O SOL (DEIXA O SOL) SERVAS RESPLANDECER SOBRE TUA VIDA. INSTRUMENTOS O SOL DESPONTA SOBRE A CIDADE QUANDO OS CONVOCO A ME ADORAR; JERUSALÉM, MORADA DO DEUS VIVO, CRIADOR DO UNIVERSO. MINHA MÃO ESTÁ SOBRE O MEU POVO, POIS EU O TENHO ESCOLHIDO, COM MINHAS MÃOS PODEROSAS FAÇO MORADA EM SEUS CORAÇÕES. REJUBILA, POVO MEU! (REJUBILA) SERVAS PORQUE EM BREVE VIRÁ MINHA GLÓRIA E VÓS PODEREIS LEVANTAR UM ESTANDARTE ENTRE AS NAÇÕES E PROCLAMAR QUE SOU REI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('256', '256 - NÃO MAIS VIVO EU MAS CRISTO VIVE EM MIM', 'NÃO MAIS VIVO EU, MAS CRISTO VIVE EM MIM DESDE O DIA EM QUE ACEITEI A JESUS, O SALVADOR; A MINHA VIDA SE TRANSFORMOU E O SEU AMOR A MIM SE REVELOU;\n\nBUSCAI A DEUS ENQUANTO SE PODE ACHAR E INVOCAI SEU NOME POIS ELE ESTÁ PERTO; JESUS VIVE EM MIM E QUER VIVER EM TI E REVELAR AS MARAVILHAS DO SEU ESPÍRITO.\n\nCORO SÃO TANTAS BÊNÇÃOS, SENHOR! SÃO TANTAS BÊNÇÃOS, SENHOR! (SÃO TANTAS BÊNÇÃOS) VARÕES BÊNÇÃOS QUE NÃO SE PODEM CONTAR; (NÃO SE PODEM CONTAR) VARÕES CANTEMOS GLÓRIA AO SENHOR, HONRA E PODER AO REI DOS REIS POIS ELE VOLTARÁ PRA NOS LEVAR E REINARÁ.\n\nDEUS QUER TE DAR PAZ, BÊNÇÃOS E O GOZO CELESTIAL, SE ASSIM QUISERES, ELE O FARÁ.\n\nCORO SÃO TANTAS BÊNÇÃOS, SENHOR! SÃO TANTAS BÊNÇÃOS, SENHOR! (SÃO TANTAS BÊNÇÃOS) VARÕES BÊNÇÃOS QUE NÃO SE PODEM CONTAR; (NÃO SE PODEM CONTAR) VARÕES CANTEMOS GLÓRIA AO SENHOR, HONRA E PODER AO REI DOS REIS POIS ELE VOLTARÁ PRA NOS LEVAR E REINARÁ.', 'NÃO MAIS VIVO EU, MAS CRISTO VIVE EM MIM DESDE O DIA EM QUE ACEITEI A JESUS, O SALVADOR; A MINHA VIDA SE TRANSFORMOU E O SEU AMOR A MIM SE REVELOU; BUSCAI A DEUS ENQUANTO SE PODE ACHAR E INVOCAI SEU NOME POIS ELE ESTÁ PERTO; JESUS VIVE EM MIM E QUER VIVER EM TI E REVELAR AS MARAVILHAS DO SEU ESPÍRITO. CORO SÃO TANTAS BÊNÇÃOS, SENHOR! SÃO TANTAS BÊNÇÃOS, SENHOR! (SÃO TANTAS BÊNÇÃOS) VARÕES BÊNÇÃOS QUE NÃO SE PODEM CONTAR; (NÃO SE PODEM CONTAR) VARÕES CANTEMOS GLÓRIA AO SENHOR, HONRA E PODER AO REI DOS REIS POIS ELE VOLTARÁ PRA NOS LEVAR E REINARÁ. DEUS QUER TE DAR PAZ, BÊNÇÃOS E O GOZO CELESTIAL, SE ASSIM QUISERES, ELE O FARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('257', '257 - TUDO SE FEZ NOVO', 'TUDO SE FEZ NOVO E AS COISAS VELHAS JÁ SE PASSARAM POIS O SENHOR FARÁ CONTIGO UMA ALIANÇA ETERNA QUE JAMAIS SE ACABARÁ.\n\nDOS TEUS PECADOS DEUS NÃO MAIS SE LEMBRARÁ, TUA VIDA ELE VAI TRANSFORMAR, O TEU FARDO SOBRE SI ELE VAI LEVAR E AS TUAS LÁGRIMAS ELE VAI ENXUGAR.\n\nCORO (2X) NÃO SOU EU MAIS QUEM VIVE MAS CRISTO VIVE EM MIM, E A VIDA QUE AGORA VIVO EU A VIVO PELA FÉ.\n\nESTE CHAMADO VEM DE DEUS, NÃO VEM DO HOMEM MAS VEM DO SENHOR; NÃO TEMAS POIS FOI DEUS QUEM TE ESCOLHEU, NÃO TEMAS POIS É ELE QUEM DÁ FORÇAS PRA VENCER; NÃO TEMAS PORQUE ELE NÃO TE DEIXARÁ, NÃO TEMAS POIS O SEU AMOR É GRANDE.\n\nCORO (2X) NÃO SOU EU MAIS QUEM VIVE MAS CRISTO VIVE EM MIM, E A VIDA QUE AGORA VIVO EU A VIVO PELA FÉ. (2X)', 'TUDO SE FEZ NOVO E AS COISAS VELHAS JÁ SE PASSARAM POIS O SENHOR FARÁ CONTIGO UMA ALIANÇA ETERNA QUE JAMAIS SE ACABARÁ. DOS TEUS PECADOS DEUS NÃO MAIS SE LEMBRARÁ, TUA VIDA ELE VAI TRANSFORMAR, O TEU FARDO SOBRE SI ELE VAI LEVAR E AS TUAS LÁGRIMAS ELE VAI ENXUGAR. CORO  NÃO SOU EU MAIS QUEM VIVE MAS CRISTO VIVE EM MIM, E A VIDA QUE AGORA VIVO EU A VIVO PELA FÉ. ESTE CHAMADO VEM DE DEUS, NÃO VEM DO HOMEM MAS VEM DO SENHOR; NÃO TEMAS POIS FOI DEUS QUEM TE ESCOLHEU, NÃO TEMAS POIS É ELE QUEM DÁ FORÇAS PRA VENCER; NÃO TEMAS PORQUE ELE NÃO TE DEIXARÁ, NÃO TEMAS POIS O SEU AMOR É GRANDE.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('258', '258 - SE TU ESTÁS TÃO LONGE DO SENHOR', 'SE TU ESTÁS TÃO LONGE DO SENHOR, SE ESQUECESTE DE TUDO O QUE ELE FEZ POR TI, AINDA HÁ TEMPO PARA VOLTARES AOS BRAÇOS DAQUELE QUE TE AMA.\n\nCORO “VOLTA, FILHO MEU, PARA OS BRAÇOS MEUS PORQUE JAMAIS ME ESQUECI DE TI, O TEU NOME TENHO GRAVADO EM MINHAS MÃOS.”\n\n“EU SOU JESUS QUEM FALA CONTIGO, O TEU CONSOLO QUANDO ESTÁS ABATIDO, O REFÚGIO EM MOMENTOS DIFÍCEIS, EU TE LEVANTO QUANDO ESTÁS CAÍDO.”\n\nCORO (2X) “VOLTA, FILHO MEU, PARA OS BRAÇOS MEUS PORQUE JAMAIS ME ESQUECI DE TI, O TEU NOME TENHO GRAVADO EM MINHAS MÃOS.” FINAL: “EM MINHAS MÃOS.”', 'SE TU ESTÁS TÃO LONGE DO SENHOR, SE ESQUECESTE DE TUDO O QUE ELE FEZ POR TI, AINDA HÁ TEMPO PARA VOLTARES AOS BRAÇOS DAQUELE QUE TE AMA. CORO VOLTA, FILHO MEU, PARA OS BRAÇOS MEUS PORQUE JAMAIS ME ESQUECI DE TI, O TEU NOME TENHO GRAVADO EM MINHAS MÃOS. EU SOU JESUS QUEM FALA CONTIGO, O TEU CONSOLO QUANDO ESTÁS ABATIDO, O REFÚGIO EM MOMENTOS DIFÍCEIS, EU TE LEVANTO QUANDO ESTÁS CAÍDO. CORO  VOLTA, FILHO MEU, PARA OS BRAÇOS MEUS PORQUE JAMAIS ME ESQUECI DE TI, O TEU NOME TENHO GRAVADO EM MINHAS MÃOS.  EM MINHAS MÃOS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('259', '259 - COMO UM BOM PASTOR', 'COMO UM BOM PASTOR PROCURA UMA OVELHA PERDIDA, JESUS ME BUSCOU E DEU LUZ À MINHA VIDA; COM TANTO AMOR FALOU AO MEU CORAÇÃO, LIVROU-ME DO MAL, CUROU MINHAS CHAGAS, DEU-ME SALVAÇÃO!\nBIS\n\nEM SEUS FORTES BRAÇOS DESCANSO E REFÚGIO ACHEI, ELE É MEU PASTOR E CUIDA DE MIM, EU SEI! COMO UMA OVELHA, NO PASTO SEU, CAMINHO; SEGUINDO A JESUS, OUVINDO SUA VOZ, SENTINDO SEU AMOR!\nBIS\n\nAMIGO, POR QUE, SEM RUMO, VIVER NESTE MUNDO? POR QUE TANTA DOR, SE EXISTE ESSE AMOR PROFUNDO? Ó VEM A JESUS, TU ÉS UMA OVELHA PERDIDA; SÓ CRISTO TE PODE DAR SALVAÇÃO, ENTREGA-LHE TUA VIDA!\nBIS', 'COMO UM BOM PASTOR PROCURA UMA OVELHA PERDIDA, JESUS ME BUSCOU E DEU LUZ À MINHA VIDA; COM TANTO AMOR FALOU AO MEU CORAÇÃO, LIVROU-ME DO MAL, CUROU MINHAS CHAGAS, DEU-ME SALVAÇÃO! EM SEUS FORTES BRAÇOS DESCANSO E REFÚGIO ACHEI, ELE É MEU PASTOR E CUIDA DE MIM, EU SEI! COMO UMA OVELHA, NO PASTO SEU, CAMINHO; SEGUINDO A JESUS, OUVINDO SUA VOZ, SENTINDO SEU AMOR! AMIGO, POR QUE, SEM RUMO, VIVER NESTE MUNDO? POR QUE TANTA DOR, SE EXISTE ESSE AMOR PROFUNDO? Ó VEM A JESUS, TU ÉS UMA OVELHA PERDIDA; SÓ CRISTO TE PODE DAR SALVAÇÃO, ENTREGA-LHE TUA VIDA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('260', '260 - DEUS ESTÁ PRESENTE AQUI NESTE LUGAR', 'DEUS ESTÁ PRESENTE AQUI NESTE LUGAR, ELE QUER O SEU AMOR NOS REVELAR, TUDO ELE CONHECE, TUDO ELE PODE, MESMO TRANSFORMAR A TREVA EM LUZ.\n\nABRE O TEU CORAÇÃO A JESUS, DEIXA SOBRE TI BRILHAR SUA LUZ, TUDO ELE CONHECE, TUDO ELE PODE, DEUS ESTÁ AQUI NESTE LUGAR. INSTRUMENTOS\n\nABRE O TEU CORAÇÃO A JESUS, DEIXA SOBRE TI BRILHAR SUA LUZ, TUDO ELE CONHECE, TUDO ELE PODE, DEUS ESTÁ AQUI NESTE LUGAR. FINAL: DEUS ESTÁ AQUI NESTE LUGAR.', 'DEUS ESTÁ PRESENTE AQUI NESTE LUGAR, ELE QUER O SEU AMOR NOS REVELAR, TUDO ELE CONHECE, TUDO ELE PODE, MESMO TRANSFORMAR A TREVA EM LUZ. ABRE O TEU CORAÇÃO A JESUS, DEIXA SOBRE TI BRILHAR SUA LUZ, TUDO ELE CONHECE, TUDO ELE PODE, DEUS ESTÁ AQUI NESTE LUGAR. INSTRUMENTOS ABRE O TEU CORAÇÃO A JESUS, DEIXA SOBRE TI BRILHAR SUA LUZ, TUDO ELE CONHECE, TUDO ELE PODE, DEUS ESTÁ AQUI NESTE LUGAR.  DEUS ESTÁ AQUI NESTE LUGAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('261', '261 - CRISTO CAMINHAVA LENTAMENTE', 'CRISTO CAMINHAVA LENTAMENTE, PASSO A PASSO COM DECISÃO; DE REPENTE ELE FALOU: “ALGUÉM TOCOU-ME, ALGUÉM NO MEIO DA MULTIDÃO”. “MESTRE, A MULTIDÃO TE OPRIME DE TODOS OS LADOS, ASSIM”; CRISTO DISSE: “ALGUÉM TOCOU-ME DIFERENTE, POIS EU SENTI SAIR VIRTUDE DE MIM”.\n\nCORO TOCA PELA FÉ AGORA EM JESUS POIS ELE VAI TE CONCEDER A LUZ, ESTENDE A MÃO, JESUS VAI TE TOCAR; TOCA PELA FÉ AGORA, É SÓ PEDIR, QUE DE JESUS VIRTUDE VAI SAIR, ESTENDE A MÃO, JESUS VAI TE TOCAR. INSTRUMENTOS\n\nENTÃO UMA MULHER APROXIMOU-SE E DISSE: “FUI EU, SENHOR, QUEM TE TOCOU”; CRISTO ALI FALOU-LHE MUI SUAVEMENTE: “VAI EM PAZ, A TUA FÉ TE SALVOU.” SE QUERES A ALEGRIA EM TUA VIDA, SE QUERES A VITÓRIA EM TEU LAR, SENTE AGORA A MORTE TRANSFORMAR-SE EM VIDA QUANDO A MÃO DE DEUS TE TOCAR.\n\nCORO TOCA PELA FÉ AGORA EM JESUS POIS ELE VAI TE CONCEDER A LUZ, ESTENDE A MÃO, JESUS VAI TE TOCAR; TOCA PELA FÉ AGORA, É SÓ PEDIR, QUE DE JESUS VIRTUDE VAI SAIR, ESTENDE A MÃO, JESUS VAI TE TOCAR.\nBIS', 'CRISTO CAMINHAVA LENTAMENTE, PASSO A PASSO COM DECISÃO; DE REPENTE ELE FALOU: ALGUÉM TOCOU-ME, ALGUÉM NO MEIO DA MULTIDÃO. MESTRE, A MULTIDÃO TE OPRIME DE TODOS OS LADOS, ASSIM; CRISTO DISSE: ALGUÉM TOCOU-ME DIFERENTE, POIS EU SENTI SAIR VIRTUDE DE MIM. CORO TOCA PELA FÉ AGORA EM JESUS POIS ELE VAI TE CONCEDER A LUZ, ESTENDE A MÃO, JESUS VAI TE TOCAR; TOCA PELA FÉ AGORA, É SÓ PEDIR, QUE DE JESUS VIRTUDE VAI SAIR, ESTENDE A MÃO, JESUS VAI TE TOCAR. INSTRUMENTOS ENTÃO UMA MULHER APROXIMOU-SE E DISSE: FUI EU, SENHOR, QUEM TE TOCOU; CRISTO ALI FALOU-LHE MUI SUAVEMENTE: VAI EM PAZ, A TUA FÉ TE SALVOU. SE QUERES A ALEGRIA EM TUA VIDA, SE QUERES A VITÓRIA EM TEU LAR, SENTE AGORA A MORTE TRANSFORMAR-SE EM VIDA QUANDO A MÃO DE DEUS TE TOCAR. CORO TOCA PELA FÉ AGORA EM JESUS POIS ELE VAI TE CONCEDER A LUZ, ESTENDE A MÃO, JESUS VAI TE TOCAR; TOCA PELA FÉ AGORA, É SÓ PEDIR, QUE DE JESUS VIRTUDE VAI SAIR, ESTENDE A MÃO, JESUS VAI TE TOCAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('262', '262 - QUERO QUE VOCÊ CONHEÇA', 'QUERO QUE VOCÊ CONHEÇA (QUERO QUE VOCÊ CONHEÇA) VARÕES JESUS, O AUTOR DA NOSSA FÉ; QUERO QUE VOCÊ CONHEÇA (QUERO QUE VOCÊ CONHEÇA) VARÕES TODO O SEU AMOR QUE TEM.\n\nCORO ELE SALVA, ELE CURA, ELE DÁ VIDA ETERNA, E TUDO O QUE ELE FAZ É POR AMOR. ABRE A BOCA DO MUDO, (DÁ VISTA AO CEGO) VARÕES DÁ PALAVRA AO UNGIDO.\nBIS\n\nOH! JESUS MARAVILHOSO, (OH! JESUS MARAVILHOSO) VARÕES JESUS, O AUTOR DA NOSSA FÉ E TUDO QUE ELE FAZ É POR AMOR.\n\nCORO ELE SALVA, ELE CURA, ELE DÁ VIDA ETERNA, E TUDO O QUE ELE FAZ É POR AMOR. ABRE A BOCA DO MUDO, (DÁ VISTA AO CEGO) VARÕES DÁ PALAVRA AO UNGIDO.\nBIS\n\nFINAL: (3X) OH! JESUS MARAVILHOSO, (OH! JESUS MARAVILHOSO) VARÕES JESUS, O AUTOR DA NOSSA FÉ.', 'QUERO QUE VOCÊ CONHEÇA (QUERO QUE VOCÊ CONHEÇA) VARÕES JESUS, O AUTOR DA NOSSA FÉ; QUERO QUE VOCÊ CONHEÇA (QUERO QUE VOCÊ CONHEÇA) VARÕES TODO O SEU AMOR QUE TEM. CORO ELE SALVA, ELE CURA, ELE DÁ VIDA ETERNA, E TUDO O QUE ELE FAZ É POR AMOR. ABRE A BOCA DO MUDO, (DÁ VISTA AO CEGO) VARÕES DÁ PALAVRA AO UNGIDO. OH! JESUS MARAVILHOSO, (OH! JESUS MARAVILHOSO) VARÕES JESUS, O AUTOR DA NOSSA FÉ E TUDO QUE ELE FAZ É POR AMOR. OH! JESUS MARAVILHOSO, (OH! JESUS MARAVILHOSO) VARÕES JESUS, O AUTOR DA NOSSA FÉ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('263', '263 - QUERES IR PARA GLÓRIA?', 'QUERES IR PRA GLÓRIA? PÁRA UM MOMENTO, ESCUTA ESTA HISTÓRIA, VEM HOJE MESMO, QUE, DO NAZARENO, FAÇO A DESCRIÇÃO; CRISTO SEM PECADO, FOI TRASPASSADO PRA TE DAR PERDÃO, VEM A CRISTO AGORA, NESTA MESMA HORA, TEM A SALVAÇÃO.\nBIS\n\nCORO QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A SALVAÇÃO! QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A CONDUÇÃO! INSTRUMENTOS\n\nMEU DISTINTO AMIGO,HÁ UM GRANDE PERIGO, QUEM NÃO ESTÁ NA LUZ, PARA UM POUQUINHO, ENTRA NO CAMINHO, ENTREGA-TE A JESUS; SE ESTÁS CANSADO, CRISTO JÁ SOFREU POR TI NA CRUZ, VEM A CRISTO AGORA E TERÁS VITÓRIA\nBIS\nBIS\n\nCORO QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A SALVAÇÃO! QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A CONDUÇÃO!', 'QUERES IR PRA GLÓRIA? PÁRA UM MOMENTO, ESCUTA ESTA HISTÓRIA, VEM HOJE MESMO, QUE, DO NAZARENO, FAÇO A DESCRIÇÃO; CRISTO SEM PECADO, FOI TRASPASSADO PRA TE DAR PERDÃO, VEM A CRISTO AGORA, NESTA MESMA HORA, TEM A SALVAÇÃO. CORO QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A SALVAÇÃO! QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A CONDUÇÃO! INSTRUMENTOS MEU DISTINTO AMIGO,HÁ UM GRANDE PERIGO, QUEM NÃO ESTÁ NA LUZ, PARA UM POUQUINHO, ENTRA NO CAMINHO, ENTREGA-TE A JESUS; SE ESTÁS CANSADO, CRISTO JÁ SOFREU POR TI NA CRUZ, VEM A CRISTO AGORA E TERÁS VITÓRIA CORO QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A SALVAÇÃO! QUERES IR PRA GLÓRIA? DÁ TEU CORAÇÃO, CRÊ EM CRISTO, AGORA, ELE É A CONDUÇÃO!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('264', '264 - OH! QUE BELOS HINOS HOJE LÁ NO CÉU', 'OH! QUE BELOS HINOS HOJE LÁ NO CÉU, JÁ DO MUNDO O PECADOR VOLTOU; VEDE NO CAMINHO O PAI ABRAÇAR ESSE FILHO QUE ELE TANTO AMOU.\n\nCORO GLÓRIA, GLÓRIA, OS ANJOS CANTAM LÁ! GLORIA, GLÓRIA, AS HARPAS TOCAM JÁ! É O SANTO CORO DANDO GLÓRIA A DEUS POR MAIS UM REMIDO ENTRAR NOS CÉUS.\n\nOH! QUE BELOS HINOS HOJE LÁ NO CÉU, É QUE JÁ SE RECONCILIOU A ALMA ARREPENDIDA QUE, RENDIDA A DEUS, RENASCIDA PARA O LAR VOLTOU.\n\nCORO GLÓRIA, GLÓRIA, OS ANJOS CANTAM LÁ! GLORIA, GLÓRIA, AS HARPAS TOCAM JÁ! É O SANTO CORO DANDO GLÓRIA A DEUS POR MAIS UM REMIDO ENTRAR NOS CÉUS.\n\nESSE ARREPENDIDO, VINDE, FESTEJAR COMO OS ANJOS FAZEM COM FERVOR; IDE, POIS, ALEGRES, IDE, ANUNCIAI, QUE SE RESGATOU UM PECADOR.', 'OH! QUE BELOS HINOS HOJE LÁ NO CÉU, JÁ DO MUNDO O PECADOR VOLTOU; VEDE NO CAMINHO O PAI ABRAÇAR ESSE FILHO QUE ELE TANTO AMOU. CORO GLÓRIA, GLÓRIA, OS ANJOS CANTAM LÁ! GLORIA, GLÓRIA, AS HARPAS TOCAM JÁ! É O SANTO CORO DANDO GLÓRIA A DEUS POR MAIS UM REMIDO ENTRAR NOS CÉUS. OH! QUE BELOS HINOS HOJE LÁ NO CÉU, É QUE JÁ SE RECONCILIOU A ALMA ARREPENDIDA QUE, RENDIDA A DEUS, RENASCIDA PARA O LAR VOLTOU. ESSE ARREPENDIDO, VINDE, FESTEJAR COMO OS ANJOS FAZEM COM FERVOR; IDE, POIS, ALEGRES, IDE, ANUNCIAI, QUE SE RESGATOU UM PECADOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('265', '265 - LEVA TU CONTIGO O NOME', 'LEVA TU CONTIGO O NOME DE JESUS, O SALVADOR; ESTE NOME DÁ CONFORTO SEMPRE, SEJA ONDE FOR. CORO (2X) NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR.\n\nESSE NOME LEVA SEMPRE PARA BEM TE DEFENDER, ELE É A ARMA AO TEU ALCANCE QUANDO O MAL TE APARECER. CORO (2X) NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR.\n\nOH! QUE NOME PRECIOSO! GOZO TRAZ AO CORAÇÃO; SENDO POR JESUS ACEITO, TU TERÁS O SEU PERDÃO. CORO (2X) NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR.\n\nSANTO NOME, ADORÁVEL, TEM JESUS, O AMADO TEU; “REI DOS REIS, SENHOR ETERNO”, TU O ACLAMARÁS NO CÉU. CORO (2X) NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR.', 'LEVA TU CONTIGO O NOME DE JESUS, O SALVADOR; ESTE NOME DÁ CONFORTO SEMPRE, SEJA ONDE FOR. CORO  NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR. ESSE NOME LEVA SEMPRE PARA BEM TE DEFENDER, ELE É A ARMA AO TEU ALCANCE QUANDO O MAL TE APARECER. CORO  NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR. OH! QUE NOME PRECIOSO! GOZO TRAZ AO CORAÇÃO; SENDO POR JESUS ACEITO, TU TERÁS O SEU PERDÃO. CORO  NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR. SANTO NOME, ADORÁVEL, TEM JESUS, O AMADO TEU; REI DOS REIS, SENHOR ETERNO, TU O ACLAMARÁS NO CÉU. CORO  NOME BOM, DOCE À FÉ, A ESPERANÇA DO PORVIR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('266', '266 - TODOS OS POVOS', 'TODOS OS POVOS SABERÃO QUE O SENHOR JESUS É O ÚNICO SALVADOR, TODAS AS NAÇÕES SE PROSTRARÃO DIANTE DELE E CONFESSARÃO QUE JESUS CRISTO É O SENHOR.\n\nCORO JESUS É O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, ELE TE TROUXE AQUI PRA TE REVELAR A VIDA ETERNA QUE NELE HÁ. INSTRUMENTOS\n\nTU QUE PROCURAS ENCONTRAR A DOCE PAZ, SABE QUE JESUS É O PRÍNCIPE DA PAZ; TU QUE ESTÁS SEM ALEGRIA NO VIVER, EM CRISTO ENCONTRARÁS TUDO O QUE PRECISAS EM TEU SER.\n\nCORO (2X) JESUS É O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, ELE TE TROUXE AQUI PRA TE REVELAR A VIDA ETERNA QUE NELE HÁ. FINAL: A VIDA ETERNA QUE NELE HÁ.', 'TODOS OS POVOS SABERÃO QUE O SENHOR JESUS É O ÚNICO SALVADOR, TODAS AS NAÇÕES SE PROSTRARÃO DIANTE DELE E CONFESSARÃO QUE JESUS CRISTO É O SENHOR. CORO JESUS É O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, ELE TE TROUXE AQUI PRA TE REVELAR A VIDA ETERNA QUE NELE HÁ. INSTRUMENTOS TU QUE PROCURAS ENCONTRAR A DOCE PAZ, SABE QUE JESUS É O PRÍNCIPE DA PAZ; TU QUE ESTÁS SEM ALEGRIA NO VIVER, EM CRISTO ENCONTRARÁS TUDO O QUE PRECISAS EM TEU SER. CORO  JESUS É O CORDEIRO DE DEUS QUE TIRA O PECADO DO MUNDO, ELE TE TROUXE AQUI PRA TE REVELAR A VIDA ETERNA QUE NELE HÁ.  A VIDA ETERNA QUE NELE HÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('267', '267 - QUANDO JESUS SEU SANGUE VERTEU', 'QUANDO JESUS SEU SANGUE VERTEU, TUDO SE FEZ NOVO, O VÉU SE RASGOU, A TERRA TREMEU, MORREU NA CRUZ O FILHO DE DEUS. INSTRUMENTOS\n\nELE RESTAUROU O MEU CORAÇÃO, DEU-ME UMA NOVA VIDA; OH! QUANTO ME AMOU, MEU MESTRE E SENHOR, PERTENCE A ELE TODO MEU LOUVOR. INSTRUMENTOS\n\nFOI POR AMAR-ME QUE JESUS SOFREU, TUDO DEVO A CRISTO; O QUE DAREI AO MEU SALVADOR? LOUVÁ-LO-EI POR SEU INFINITO AMOR.', 'QUANDO JESUS SEU SANGUE VERTEU, TUDO SE FEZ NOVO, O VÉU SE RASGOU, A TERRA TREMEU, MORREU NA CRUZ O FILHO DE DEUS. INSTRUMENTOS ELE RESTAUROU O MEU CORAÇÃO, DEU-ME UMA NOVA VIDA; OH! QUANTO ME AMOU, MEU MESTRE E SENHOR, PERTENCE A ELE TODO MEU LOUVOR. INSTRUMENTOS FOI POR AMAR-ME QUE JESUS SOFREU, TUDO DEVO A CRISTO; O QUE DAREI AO MEU SALVADOR? LOUVÁ-LO-EI POR SEU INFINITO AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('268', '268 - JESUS É O CORDEIRO', 'JESUS É O CORDEIRO ETERNO E IMACULADO QUE FOI SACRIFICADO PARA NOS SALVAR, O SUMO SACERDOTE QUE DEUS CONSTITUIU, LEVOU A NOSSA CRUZ PROVANDO O SEU AMOR.\n\nCORO JESUS RESSUSCITOU TRAZENDO VIDA ETERNA, NÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR, JESUS É O AMIGO FIEL, CONSOLADOR, ENTREGA A TUA VIDA A QUEM TANTO TE AMA.\n\nO FARDO QUE CARREGAS TE TRAZ DESILUSÃO, O MUNDO QUE TU VIVES SÓ TEM DECEPÇÃO? A VIDA EM JESUS É LEVE E SUAVE, DESCANSO PARA ALMA NELE ENCONTRARÁS.\n\nCORO JESUS RESSUSCITOU TRAZENDO VIDA ETERNA, NÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR, JESUS É O AMIGO FIEL, CONSOLADOR, ENTREGA A TUA VIDA A QUEM TANTO TE AMA.\n\nSEU NOME É CONSELHEIRO, PAI DA ETERNIDADE, MARAVILHOSO, ELE É DEUS FORTE.\n\nCORO JESUS RESSUSCITOU TRAZENDO VIDA ETERNA, NÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR, JESUS É O AMIGO FIEL, CONSOLADOR, ENTREGA A TUA VIDA A QUEM TANTO TE AMA. FINAL: JESUS TE AMA.', 'JESUS É O CORDEIRO ETERNO E IMACULADO QUE FOI SACRIFICADO PARA NOS SALVAR, O SUMO SACERDOTE QUE DEUS CONSTITUIU, LEVOU A NOSSA CRUZ PROVANDO O SEU AMOR. CORO JESUS RESSUSCITOU TRAZENDO VIDA ETERNA, NÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR, JESUS É O AMIGO FIEL, CONSOLADOR, ENTREGA A TUA VIDA A QUEM TANTO TE AMA. O FARDO QUE CARREGAS TE TRAZ DESILUSÃO, O MUNDO QUE TU VIVES SÓ TEM DECEPÇÃO? A VIDA EM JESUS É LEVE E SUAVE, DESCANSO PARA ALMA NELE ENCONTRARÁS. SEU NOME É CONSELHEIRO, PAI DA ETERNIDADE, MARAVILHOSO, ELE É DEUS FORTE. CORO JESUS RESSUSCITOU TRAZENDO VIDA ETERNA, NÃO HÁ OUTRO ALÉM DELE QUE POSSA TE SALVAR, JESUS É O AMIGO FIEL, CONSOLADOR, ENTREGA A TUA VIDA A QUEM TANTO TE AMA.  JESUS TE AMA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('269', '269 - NÃO SEI SE OUVISTE FALAR', 'NÃO SEI SE OUVISTE FALAR DE UM REI QUE MORREU PRA TE SALVAR; O SEU SANGUE PURO DERRAMOU, COMO HOLOCAUSTO FOI ENTREGUE POR AMOR.\n\nCORO ELE VIVE E QUER TE SALVAR, ENTREGA A TUA VIDA E ELA IRÁ MUDAR; ELE É JESUS, NOSSO ETERNO REDENTOR, ELE É O REI DA GLÓRIA, ELE É O MEU SENHOR.\n\nSE PROCURAS A ALEGRIA E NÃO CONSEGUES ENCONTRAR, SÓ JESUS PODE DAR A ALEGRIA QUE NUNCA CESSARÁ; SE SERVIRES A JESUS UM NOVO DIA IRÁ SURGIR, ELE TE CONDUZIRÁ AO SANTO E ETERNO LAR.\n\nCORO ELE VIVE E QUER TE SALVAR, ENTREGA A TUA VIDA E ELA IRÁ MUDAR; ELE É JESUS, NOSSO ETERNO REDENTOR, ELE É O REI DA GLÓRIA, ELE É O MEU SENHOR. INSTRUMENTOS\n\nCORO ELE VIVE E QUER TE SALVAR, ENTREGA A TUA VIDA E ELA IRÁ MUDAR; ELE É JESUS, NOSSO ETERNO REDENTOR, ELE É O REI DA GLÓRIA, ELE É O MEU SENHOR.', 'NÃO SEI SE OUVISTE FALAR DE UM REI QUE MORREU PRA TE SALVAR; O SEU SANGUE PURO DERRAMOU, COMO HOLOCAUSTO FOI ENTREGUE POR AMOR. CORO ELE VIVE E QUER TE SALVAR, ENTREGA A TUA VIDA E ELA IRÁ MUDAR; ELE É JESUS, NOSSO ETERNO REDENTOR, ELE É O REI DA GLÓRIA, ELE É O MEU SENHOR. SE PROCURAS A ALEGRIA E NÃO CONSEGUES ENCONTRAR, SÓ JESUS PODE DAR A ALEGRIA QUE NUNCA CESSARÁ; SE SERVIRES A JESUS UM NOVO DIA IRÁ SURGIR, ELE TE CONDUZIRÁ AO SANTO E ETERNO LAR. CORO ELE VIVE E QUER TE SALVAR, ENTREGA A TUA VIDA E ELA IRÁ MUDAR; ELE É JESUS, NOSSO ETERNO REDENTOR, ELE É O REI DA GLÓRIA, ELE É O MEU SENHOR. INSTRUMENTOS',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('270', '270 - SE TU PEDISTE UM SINAL', 'CORO SE TU PEDISTE UM SINAL, DEUS OUVIU TUA VOZ E TE TROUXE AQUI PARA ABENÇOAR. SE TUA VIDA É VAZIA, DEUS PODE MUDAR ELE TE TROUXE AQUI PARA ABENÇOAR.\n\nENTREGA A TUA VIDA A JESUS E A SALVAÇÃO TERÁS; ELE DIZ: “EU SOU O SENHOR, O REI DOS REIS, O REI DAS NAÇÕES”. INSTRUMENTOS\n\nCORO SE TU PEDISTE UM SINAL, DEUS OUVIU TUA VOZ E TE TROUXE AQUI PARA ABENÇOAR. SE TUA VIDA É VAZIA, DEUS PODE MUDAR ELE TE TROUXE AQUI PARA ABENÇOAR.\n\nFINAL: ENTREGA A TUA VIDA A JESUS, ELE VAI TE TRANSFORMAR. ELE DIZ: “EU SOU O SENHOR, O REI DOS REIS, O REI DAS NAÇÕES ”.\nBIS', 'CORO SE TU PEDISTE UM SINAL, DEUS OUVIU TUA VOZ E TE TROUXE AQUI PARA ABENÇOAR. SE TUA VIDA É VAZIA, DEUS PODE MUDAR ELE TE TROUXE AQUI PARA ABENÇOAR. ENTREGA A TUA VIDA A JESUS E A SALVAÇÃO TERÁS; ELE DIZ: EU SOU O SENHOR, O REI DOS REIS, O REI DAS NAÇÕES. INSTRUMENTOS ENTREGA A TUA VIDA A JESUS, ELE VAI TE TRANSFORMAR. ELE DIZ: EU SOU O SENHOR, O REI DOS REIS, O REI DAS NAÇÕES .',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('271', '271 - O CAMINHO QUE NOS LEVA AO CÉU É JESUS', 'O CAMINHO QUE NOS LEVA AO CÉU É JESUS, O ÚNICO MEDIADOR ENTRE DEUS E OS HOMENS.\n\nCORO FOI ELE QUEM SE DEU A MORRER NA CRUZ, ELE É O NOSSO SALVADOR O SEU NOME É MARAVILHOSO, CONSELHEIRO, NOBRE, PRÍNCIPE DA PAZ, ELE ERA, É, SEMPRE SERÁ O AUTOR DA NOSSA REDENÇÃO.\n\nO AMIGO QUE NUNCA FALHA É JESUS, AQUELE QUE NOS GARANTE A VIDA ETERNA.\n\nCORO FOI ELE QUEM SE DEU A MORRER NA CRUZ, ELE É O NOSSO SALVADOR O SEU NOME É MARAVILHOSO, CONSELHEIRO, NOBRE, PRÍNCIPE DA PAZ, ELE ERA, É, SEMPRE SERÁ O AUTOR DA NOSSA REDENÇÃO.\n\nFINAL: (2X) (O AUTOR DA NOSSA REDENÇÃO) O AUTOR DA NOSSA REDENÇÃO.', 'O CAMINHO QUE NOS LEVA AO CÉU É JESUS, O ÚNICO MEDIADOR ENTRE DEUS E OS HOMENS. CORO FOI ELE QUEM SE DEU A MORRER NA CRUZ, ELE É O NOSSO SALVADOR O SEU NOME É MARAVILHOSO, CONSELHEIRO, NOBRE, PRÍNCIPE DA PAZ, ELE ERA, É, SEMPRE SERÁ O AUTOR DA NOSSA REDENÇÃO. O AMIGO QUE NUNCA FALHA É JESUS, AQUELE QUE NOS GARANTE A VIDA ETERNA. (O AUTOR DA NOSSA REDENÇÃO) O AUTOR DA NOSSA REDENÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('272', '272 - NOVO CAMINHO', 'NOVO CAMINHO É O MEU JESUS, OUTRO CAMINHO NÃO PODES ENCONTRAR. PODES ENCONTRAR A PAZ, VIDA EM JESUS. SEGUE ESTE CAMINHO E TERÁS A LUZ.\n\nCORO PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS.\n\nDEIXA CRISTO SE REVELAR EM TEU CORAÇÃO, ELE TE DARÁ A PAZ, VIDA E SALVAÇÃO. CORO PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS. PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS.', 'NOVO CAMINHO É O MEU JESUS, OUTRO CAMINHO NÃO PODES ENCONTRAR. PODES ENCONTRAR A PAZ, VIDA EM JESUS. SEGUE ESTE CAMINHO E TERÁS A LUZ. CORO PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS. DEIXA CRISTO SE REVELAR EM TEU CORAÇÃO, ELE TE DARÁ A PAZ, VIDA E SALVAÇÃO. CORO PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS. PAZ, PAZ, VIDA, AMOR E LUZ, SE NÃO HÁ PAZ, PEDE A JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('273', '273 - O SENHOR É A ALEGRIA', 'O SENHOR É A ALEGRIA, É A PAZ E A REDENÇÃO, ELE É O AUTOR DA SALVAÇÃO; O SENHOR É A FORTALEZA DOS QUE CRÊEM NELE ASSIM; E A VOCÊ ESTÁ CHAMANDO: “VENHA A MIM”. PODE TUDO CONFIAR A JESUS SE ENTREGAR SUA VIDA ELE PODERÁ MUDAR.\nBIS\nBIS\n\n“VINDE A MIM”, DISSE JESUS, “VÓS TODOS QUE ESTAIS CANSADOS, Ó VINDE A MIM”. FINAL: DISSE JESUS.\nBIS', 'O SENHOR É A ALEGRIA, É A PAZ E A REDENÇÃO, ELE É O AUTOR DA SALVAÇÃO; O SENHOR É A FORTALEZA DOS QUE CRÊEM NELE ASSIM; E A VOCÊ ESTÁ CHAMANDO: VENHA A MIM. PODE TUDO CONFIAR A JESUS SE ENTREGAR SUA VIDA ELE PODERÁ MUDAR. VINDE A MIM, DISSE JESUS, VÓS TODOS QUE ESTAIS CANSADOS, Ó VINDE A MIM.  DISSE JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('274', '274 - SE EM TUA VIDA NÃO ENCONTRAS PAZ E LUZ', 'SE EM TUA VIDA NÃO ENCONTRAS PAZ E LUZ, SE O CAMINHO É DIFÍCIL E DE DOR, ABRE O TEU CORAÇÃO HOJE A JESUS, ELE VAI TE REVELAR O SEU AMOR;\n\nSE NO MUNDO TU ENFRENTAS MUITAS LUTAS, E AS LÁGRIMAS JÁ TOMAM O TEU SER, VEM A CRISTO, ELE JÁ VENCEU O MUNDO E COM ELE TAMBÉM HOJE VAIS VENCER.\n\nCORO LIMPA TUAS LÁGRIMAS, ALEGRIA E PAZ TE DÁ, VEM PARA CRISTO E A VITÓRIA TERÁS; EXISTE ESPERANÇA, DEUS PROVERÁ, ENTREGA O TEU CAMINHO E TUDO ELE FARÁ. INSTRUMENTOS\n\nELE UM DIA TRANSFORMOU O MEU VIVER E PASSOU A HABITAR MEU CORAÇÃO, HOJE POSSO CONTEMPLAR O SEU PODER E JESUS SEGURA FORTE A MINHA MÃO;\n\nNESTE MUNDO ENFRENTAMOS MUITAS LUTAS, MAS COM CRISTO É MAIS FÁCIL DE VENCER. É SÓ ENTREGAR A ELE OS TEUS PROBLEMAS E UMA NOVA VIDA HOJE VAIS VIVER.\n\nCORO (2X) LIMPA TUAS LÁGRIMAS, ALEGRIA E PAZ TE DÁ, VEM PARA CRISTO E A VITÓRIA TERÁS; EXISTE ESPERANÇA, DEUS PROVERÁ, ENTREGA O TEU CAMINHO E TUDO ELE FARÁ. FINAL: ENTREGA O TEU CAMINHO E TUDO ELE FARÁ.', 'SE EM TUA VIDA NÃO ENCONTRAS PAZ E LUZ, SE O CAMINHO É DIFÍCIL E DE DOR, ABRE O TEU CORAÇÃO HOJE A JESUS, ELE VAI TE REVELAR O SEU AMOR; SE NO MUNDO TU ENFRENTAS MUITAS LUTAS, E AS LÁGRIMAS JÁ TOMAM O TEU SER, VEM A CRISTO, ELE JÁ VENCEU O MUNDO E COM ELE TAMBÉM HOJE VAIS VENCER. CORO LIMPA TUAS LÁGRIMAS, ALEGRIA E PAZ TE DÁ, VEM PARA CRISTO E A VITÓRIA TERÁS; EXISTE ESPERANÇA, DEUS PROVERÁ, ENTREGA O TEU CAMINHO E TUDO ELE FARÁ. INSTRUMENTOS ELE UM DIA TRANSFORMOU O MEU VIVER E PASSOU A HABITAR MEU CORAÇÃO, HOJE POSSO CONTEMPLAR O SEU PODER E JESUS SEGURA FORTE A MINHA MÃO; NESTE MUNDO ENFRENTAMOS MUITAS LUTAS, MAS COM CRISTO É MAIS FÁCIL DE VENCER. É SÓ ENTREGAR A ELE OS TEUS PROBLEMAS E UMA NOVA VIDA HOJE VAIS VIVER. CORO  LIMPA TUAS LÁGRIMAS, ALEGRIA E PAZ TE DÁ, VEM PARA CRISTO E A VITÓRIA TERÁS; EXISTE ESPERANÇA, DEUS PROVERÁ, ENTREGA O TEU CAMINHO E TUDO ELE FARÁ.  ENTREGA O TEU CAMINHO E TUDO ELE 