INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('275', '275 - UM MILAGRE É', 'UM MILAGRE É A MINHA VIDA AQUI, NESTE LUGAR, SEMPRE A TEUS PÉS. DESDE AQUELE DIA EM QUE ME ALCANÇOU, EU VIVO PARA AGRADECER O TEU GRANDE AMOR.\n\nCORO ESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS.\n\nHOJE POSSO OUVIR A TUA VOZ EM MIM, EU POSSO TER A TUA DIREÇÃO. SE VOCÊ QUISER TAMBÉM O PODE TER, É SÓ ABRIR SEU CORAÇÃO E O RECEBER.\n\nCORO ESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS. INSTRUMENTOS\n\nESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS. FINAL: DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS.', 'UM MILAGRE É A MINHA VIDA AQUI, NESTE LUGAR, SEMPRE A TEUS PÉS. DESDE AQUELE DIA EM QUE ME ALCANÇOU, EU VIVO PARA AGRADECER O TEU GRANDE AMOR. CORO ESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS. HOJE POSSO OUVIR A TUA VOZ EM MIM, EU POSSO TER A TUA DIREÇÃO. SE VOCÊ QUISER TAMBÉM O PODE TER, É SÓ ABRIR SEU CORAÇÃO E O RECEBER. CORO ESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS. INSTRUMENTOS ESSE MILAGRE É, A TUA MORTE LÁ NA CRUZ, JESUS, DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS.  DANDO-ME A VIDA, CURANDO TODAS AS MINHAS FERIDAS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('276', '276 - AINDA QUE OS SEUS SONHOS', 'AINDA QUE OS SEUS SONHOS CAIAM PELO CHÃO E NÃO ENCONTRE UMA SAÍDA, UMA SOLUÇÃO, A VIDA PODE SER DIFÍCIL DE RESOLVER, E NESTA HORA QUE VOCÊ PRECISA RECONHECER. INSTRUMENTOS\n\nO MUNDO NÃO O CONHECE, POR ISSO ELE PERECE, JESUS QUER SE DAR A CONHECER. VENHA A VERDADE RECEBER.\n\nCORO O FILHO DO DEUS ETERNO É JESUS. ELE VEIO TE RESGATAR DAS TREVAS PARA A LUZ, DAS TREVAS PARA A LUZ. ABRA AGORA O TEU CORAÇÃO A JESUS. INSTRUMENTOS\n\nO MUNDO NÃO O CONHECE, POR ISSO ELE PERECE, JESUS QUER SE DAR A CONHECER. VENHA A VERDADE RECEBER.\n\nCORO (2X) O FILHO DO DEUS ETERNO É JESUS. ELE VEIO TE RESGATAR DAS TREVAS PARA A LUZ, DAS TREVAS PARA A LUZ. ABRA AGORA O TEU CORAÇÃO A JESUS.', 'AINDA QUE OS SEUS SONHOS CAIAM PELO CHÃO E NÃO ENCONTRE UMA SAÍDA, UMA SOLUÇÃO, A VIDA PODE SER DIFÍCIL DE RESOLVER, E NESTA HORA QUE VOCÊ PRECISA RECONHECER. INSTRUMENTOS O MUNDO NÃO O CONHECE, POR ISSO ELE PERECE, JESUS QUER SE DAR A CONHECER. VENHA A VERDADE RECEBER. CORO O FILHO DO DEUS ETERNO É JESUS. ELE VEIO TE RESGATAR DAS TREVAS PARA A LUZ, DAS TREVAS PARA A LUZ. ABRA AGORA O TEU CORAÇÃO A JESUS. INSTRUMENTOS CORO  O FILHO DO DEUS ETERNO É JESUS. ELE VEIO TE RESGATAR DAS TREVAS PARA A LUZ, DAS TREVAS PARA A LUZ. ABRA AGORA O TEU CORAÇÃO A JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('277', '277 - ROCHA ETERNA', 'ROCHA ETERNA, FOI NA CRUZ QUE MORRESTE TU, JESUS; VEM DE TI UM SANGUE TAL QUE ME LIMPA TODO O MAL. TRAZ AS BÊNÇÃOS DO PERDÃO: GOZO, PAZ E SALVAÇÃO.\n\nNEM TRABALHO, NEM PENAR PODE O PECADOR SALVAR; SÓ TU PODES, BOM JESUS, DAR-ME VIDA, PAZ E LUZ. PEÇO-TE PERDÃO, SENHOR, POIS CONFIO EM TEU AMOR.\n\nEIS QUE VEM A MORTE ATRÁS DESTA VIDA TÃO FUGAZ. QUANDO EU AO MEU LAR SUBIR, E TEU ROSTO EM GLÓRIA VIR, ROCHA ETERNA, QUE PRAZER EU TEREI DE EM TI VIVER!', 'ROCHA ETERNA, FOI NA CRUZ QUE MORRESTE TU, JESUS; VEM DE TI UM SANGUE TAL QUE ME LIMPA TODO O MAL. TRAZ AS BÊNÇÃOS DO PERDÃO: GOZO, PAZ E SALVAÇÃO. NEM TRABALHO, NEM PENAR PODE O PECADOR SALVAR; SÓ TU PODES, BOM JESUS, DAR-ME VIDA, PAZ E LUZ. PEÇO-TE PERDÃO, SENHOR, POIS CONFIO EM TEU AMOR. EIS QUE VEM A MORTE ATRÁS DESTA VIDA TÃO FUGAZ. QUANDO EU AO MEU LAR SUBIR, E TEU ROSTO EM GLÓRIA VIR, ROCHA ETERNA, QUE PRAZER EU TEREI DE EM TI VIVER!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('278', '278 - A NOVA DO EVANGELHO', 'A NOVA DO EVANGELHO JÁ SE FEZ OUVIR AQUI; BOAS NOVAS, TÃO ALEGRES, ELAS SÃO PRA QUEM OUVIR: ASSIM DEUS NOS AMOU, SIM, A CADA PECADOR, QUE NOS DEU SEU FILHO AMADO PRA SOFRER A NOSSA DOR.\n\nCORO SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) SÃO AS NOVAS LÁ DOS CÉUS! SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) É BENDITO O NOSSO DEUS!\n\nA NOVA DO EVANGELHO DÁ-NOS TODOS A SABER QUE FARTURA HÁ PARA TODOS, VEM, DE GRAÇA, RECEBER. DA VIDA O PÃO EU SOU; SATISFEITO FICARÁS; TEUS PECADOS E TUA ALMA LAVAREI, E PAZ TERÁS.\n\nCORO SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) SÃO AS NOVAS LÁ DOS CÉUS! SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) É BENDITO O NOSSO DEUS!\n\nA NOVA DO EVANGELHO ORA VEM NOS AVISAR DO PERIGO GRANDE E GRAVE, PARA QUEM SE DESCUIDAR: SALVAI-VOS DESDE JÁ; NÃO VOS DEMOREIS, ENTÃO, NÃO VIREIS OS VOSSOS OLHOS, OH! FUGI DA PERDIÇÃO!\n\nCORO SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) SÃO AS NOVAS LÁ DOS CÉUS! SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) É BENDITO O NOSSO DEUS!\n\nA NOVA DO EVANGELHO JUBILOSO SOM NOS É, POIS O AMOR DE JESUS CRISTO DÁ PERDÃO MEDIANTE A FÉ. AS NOVAS SE VOS DÃO QUE JÁ VEIO O SALVADOR, TÃO BONDOSO E PODEROSO, QUE PERDOA AO PECADOR.\n\nCORO SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) SÃO AS NOVAS LÁ DOS CÉUS! SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) É BENDITO O NOSSO DEUS!', 'A NOVA DO EVANGELHO JÁ SE FEZ OUVIR AQUI; BOAS NOVAS, TÃO ALEGRES, ELAS SÃO PRA QUEM OUVIR: ASSIM DEUS NOS AMOU, SIM, A CADA PECADOR, QUE NOS DEU SEU FILHO AMADO PRA SOFRER A NOSSA DOR. CORO SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) SÃO AS NOVAS LÁ DOS CÉUS! SANTA PAZ (SANTA PAZ) E PERDÃO! (E PERDÃO!) É BENDITO O NOSSO DEUS! A NOVA DO EVANGELHO DÁ-NOS TODOS A SABER QUE FARTURA HÁ PARA TODOS, VEM, DE GRAÇA, RECEBER. DA VIDA O PÃO EU SOU; SATISFEITO FICARÁS; TEUS PECADOS E TUA ALMA LAVAREI, E PAZ TERÁS. A NOVA DO EVANGELHO ORA VEM NOS AVISAR DO PERIGO 