INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('279', '279 - QUERO ESTAR AO PÉ DA CRUZ', 'QUERO ESTAR AO PÉ DA CRUZ, DE ONDE RICA FONTE CORRE FRANCA, SALUTAR, DO CALVÁRIO, MONTE.\n\nCORO SIM, NA CRUZ, SIM, NA CRUZ, SEMPRE ME GLORIO E ENFIM VOU DESCANSAR SALVO, ALÉM DO RIO.\n\nA TREMER AO PÉ DA CRUZ, GRAÇA ETERNA ACHOU-ME; MATUTINA ESTRELA ALI RAIOS SEUS MANDOU-ME.\n\nCORO SIM, NA CRUZ, SIM, NA CRUZ, SEMPRE ME GLORIO E ENFIM VOU DESCANSAR SALVO, ALÉM DO RIO.\n\nSEMPRE A CRUZ, JESUS, MEU DEUS, QUEIRAS RECORDAR-ME; DELA À SOMBRA, SALVADOR, QUEIRAS ABRIGAR-ME.\n\nCORO SIM, NA CRUZ, SIM, NA CRUZ, SEMPRE ME GLORIO E ENFIM VOU DESCANSAR SALVO, ALÉM DO RIO.\n\nJUNTO À CRUZ, ARDENDO EM FÉ, SEM TEMOR VIGIO, POIS À TERRA SANTA IREI, SALVO, ALÉM DO RIO.', 'QUERO ESTAR AO PÉ DA CRUZ, DE ONDE RICA FONTE CORRE FRANCA, SALUTAR, DO CALVÁRIO, MONTE. CORO SIM, NA CRUZ, SIM, NA CRUZ, SEMPRE ME GLORIO E ENFIM VOU DESCANSAR SALVO, ALÉM DO RIO. A TREMER AO PÉ DA CRUZ, GRAÇA ETERNA ACHOU-ME; MATUTINA ESTRELA ALI RAIOS SEUS MANDOU-ME. SEMPRE A CRUZ, JESUS, MEU DEUS, QUEIRAS RECORDAR-ME; DELA À SOMBRA, SALVADOR, QUEIRAS ABRIGAR-ME. JUNTO À CRUZ, ARDENDO EM FÉ, SEM TEMOR VIGIO, POIS À TERRA SANTA IREI, SALVO, ALÉM DO RIO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('280', '280 - HÁ HOJE ALGUÉM ESPERANDO', 'HÁ HOJE ALGUÉM ESPERANDO PARA JESUS ENCONTRAR? VENHA, SEM MAIS DEMORAR-SE, CRISTO VAI HOJE PASSAR! EI-LO DE MÃOS ESTENDIDAS, CHEIO DE GRAÇA SEM PAR. OH! QUE VENTURA INAUDITA, CRISTO VAI HOJE PASSAR!\n\nCORO CRISTO VAI HOJE PASSAR, PASSAR, PASSAR! CRISTO VAI HOJE PASSAR, PASSAR, VAI HOJE PASSAR! PASSA DE AMOR TRANSBORDANDO, TODOS A SI CONVIDANDO. O MESTRE VAI HOJE PASSAR (PASSAR) VARÕES SIM, HOJE ELE VAI PASSAR!\n\nVARÕES\n\nHÁ INDA ALGUÉM DUVIDANDO DO SEU PODER DE SALVAR? POIS VENHA EXPERIMENTÁ-LO, CRISTO VAI HOJE PASSAR! O SEU PODER É DIVINO, O SEU AMOR É SEM PAR. Ó CORAÇÃO QUEBRANTADO! CRISTO VAI HOJE PASSAR!\n\nCORO CRISTO VAI HOJE PASSAR, PASSAR, PASSAR! CRISTO VAI HOJE PASSAR, PASSAR, VAI HOJE PASSAR! PASSA DE AMOR TRANSBORDANDO, TODOS A SI CONVIDANDO. O MESTRE VAI HOJE PASSAR (PASSAR) VARÕES SIM, HOJE ELE VAI PASSAR!\n\nVARÕES\n\nHÁ INDA ALGUÉM DEMORANDO PARA JESUS ACEITAR? EIS QUE O SENHOR ESTÁ PERTO, ELE VAI HOJE PASSAR! Ó PECADOR DESDITOSO, NÃO CESSES POIS DE CLAMAR! VEM TUAS CULPAS CHORANDO; CRISTO VAI HOJE PASSAR!\n\nCORO CRISTO VAI HOJE PASSAR, PASSAR, PASSAR! CRISTO VAI HOJE PASSAR, PASSAR, VAI HOJE PASSAR! PASSA DE AMOR TRANSBORDANDO, TODOS A SI CONVIDANDO. O MESTRE VAI HOJE PASSAR (PASSAR) VARÕES SIM, HOJE ELE VAI PASSAR!\n\nVARÕES', 'HÁ HOJE ALGUÉM ESPERANDO PARA JESUS ENCONTRAR? VENHA, SEM MAIS DEMORAR-SE, CRISTO VAI HOJE PASSAR! EI-LO DE MÃOS ESTENDIDAS, CHEIO DE GRAÇA SEM PAR. OH! QUE VENTURA INAUDITA, CRISTO VAI HOJE PASSAR! CORO CRISTO VAI HOJE PASSAR, PASSAR, PASSAR! CRISTO VAI HOJE PASSAR, PASSAR, VAI HOJE PASSAR! PASSA DE AMOR TRANSBORDANDO, TODOS A SI CONVIDANDO. O MESTRE VAI HOJE PASSAR (PASSAR) VARÕES SIM, HOJE ELE VAI PASSAR! HÁ INDA ALGUÉM DUVIDANDO DO SEU PODER DE SALVAR? POIS VENHA EXPERIMENTÁ-LO, CRISTO VAI HOJE PASSAR! O SEU PODER É DIVINO, O SEU AMOR É SEM PAR. Ó CORAÇÃO QUEBRANTADO! CRISTO VAI HOJE PASSAR! HÁ INDA ALGUÉM DEMORANDO PARA JESUS ACEITAR? EIS QUE O SENHOR ESTÁ PERTO, ELE VAI HOJE PASSAR! Ó PECADOR DESDITOSO, NÃO CESSES POIS DE CLAMAR! VEM TUAS CULPAS CHORANDO; CRISTO VAI HOJE PASSAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('281', '281 - ESTE MUNDO NÃO TEM A SUA PAZ', 'ESTE MUNDO NÃO TEM A SUA PAZ, ESTE MUNDO NÃO CONHECE O QUE É AMOR, ESTE MUNDO NÃO SABE O QUE É SER FELIZ, EU TAMBÉM NÃO SABIA, MAS JESUS UM DIA ME ENCONTROU.\n\nJESUS BUSCOU-ME COM SEUS BRAÇOS DE TERNURA, FALOU-ME DE UM AMOR QUE JAMAIS CONHECI, FEZ-ME ENTENDER O QUE É A SALVAÇÃO, ENTÃO, ABRI O MEU CORAÇÃO.\n\nCORO ELE É JESUS (ELE É JESUS) (BIS) O AMOR VERDADEIRO QUE O HOMEM PODE TER. INSTRUMENTOS\n\nJESUS BUSCOU-ME COM SEUS BRAÇOS DE TERNURA, FALOU-ME DE UM AMOR QUE JAMAIS CONHECI, FEZ-ME ENTENDER O QUE É A SALVAÇÃO, ENTÃO, ABRI O MEU CORAÇÃO.\n\nCORO (2X) ELE É JESUS (ELE É JESUS) (BIS) O AMOR VERDADEIRO QUE O HOMEM PODE TER.', 'ESTE MUNDO NÃO TEM A SUA PAZ, ESTE MUNDO NÃO CONHECE O QUE É AMOR, ESTE MUNDO NÃO SABE O QUE É SER FELIZ, EU TAMBÉM NÃO SABIA, MAS JESUS UM DIA ME ENCONTROU. JESUS BUSCOU-ME COM SEUS BRAÇOS DE TERNURA, FALOU-ME DE UM AMOR QUE JAMAIS CONHECI, FEZ-ME ENTENDER O QUE É A SALVAÇÃO, ENTÃO, ABRI O MEU CORAÇÃO. CORO ELE É JESUS (ELE É JESUS)  O AMOR VERDADEIRO QUE O HOMEM PODE TER. INSTRUMENTOS CORO  ELE É JESUS (ELE É JESUS)  O AMOR VERDADEIRO QUE O HOMEM PODE TER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('282', '282 - QUEM É QUE DÁ ÁGUA AO SEDENTO?', 'QUEM É QUE DÁ ÁGUA AO SEDENTO E UMA SOMBRA FRESCA? QUEM É QUE RETIRA DO HOMEM TODO ARDOR? QUEM É QUE ABRIU NO CALVÁRIO UM NOVO CAMINHO? QUEM É A FONTE DA VIDA, O GRANDE AMOR?\n\nCORO É O FILHO DE DEUS, É JESUS, O SENHOR, É O GRANDE EU SOU, ELE É O CORDEIRO DE DEUS, O AUTOR DA ETERNA VIDA, ELE É JESUS. INSTRUMENTOS\n\nQUEM É QUE DÁ ÁGUA AO SEDENTO NUMA TERRA SECA? QUEM É A FLOR MAIS QUERIDA DO LINDO JARDIM? QUEM É QUE TRAZ PAZ E CONSOLO DENTRO DA ALMA? QUEM É O AMIGO EXCELENTE, O SALVADOR?\n\nCORO É O FILHO DE DEUS, É JESUS, O SENHOR, É O GRANDE EU SOU, ELE É O CORDEIRO DE DEUS, O AUTOR DA ETERNA VIDA, ELE É JESUS. INSTRUMENTOS\n\nCORO É O FILHO DE DEUS, É JESUS, O SENHOR, É O GRANDE EU SOU, ELE É O CORDEIRO DE DEUS, O AUTOR DA ETERNA VIDA, ELE É JESUS.', 'QUEM É QUE DÁ ÁGUA AO SEDENTO E UMA SOMBRA FRESCA? QUEM É QUE RETIRA DO HOMEM TODO ARDOR? QUEM É QUE ABRIU NO CALVÁRIO UM NOVO CAMINHO? QUEM É A FONTE DA VIDA, O GRANDE AMOR? CORO É O FILHO DE DEUS, É JESUS, O SENHOR, É O GRANDE EU SOU, ELE É O CORDEIRO DE DEUS, O AUTOR DA ETERNA VIDA, ELE É JESUS. INSTRUMENTOS QUEM É QUE DÁ ÁGUA AO SEDENTO NUMA TERRA SECA? QUEM É A FLOR MAIS QUERIDA DO LINDO JARDIM? QUEM É QUE TRAZ PAZ E CONSOLO DENTRO DA ALMA? QUEM É O AMIGO EXCELENTE, O SALVADOR? CORO É O FILHO DE DEUS, É JESUS, O SENHOR, É O GRANDE EU SOU, ELE É O CORDEIRO DE DEUS, O AUTOR DA ETERNA VIDA, ELE É JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('283', '283 - O MUNDO NÃO SABE', 'O MUNDO NÃO SABE A HISTÓRIA DE JESUS E PENSA QUE TUDO TERMINOU NA CRUZ, MAS EU SEI QUE A HISTÓRIA NÃO CHEGOU AO FIM, JESUS ESTÁ VIVO PARA MIM!\n\nNAQUELA MANHÃ QUANDO O SOL RAIOU, A PORTA DA SEPULTURA SE ABRIU E O MESTRE AOS DISCÍPULOS SE APRESENTOU, O CORDEIRO QUE FOI MORTO RESSURGIU!\n\nCORO (2X) ALELUIA, RESSUSCITOU! CRISTO RESSUSCITOU E ELE VIVO HOJE ESTÁ, E BREVE VOLTARÁ!\n\nO MUNDO NÃO MAIS O VIU NEM IRÁ VER MAS HOJE À SUA IGREJA DÁ-SE A CONHECER COM MUITOS SINAIS E PROVAS DE AMOR, JESUS CRISTO ESTÁ VIVO E É SENHOR!\n\nCORO (2X) ALELUIA, RESSUSCITOU! CRISTO RESSUSCITOU E ELE VIVO HOJE ESTÁ, E BREVE VOLTARÁ!', 'O MUNDO NÃO SABE A HISTÓRIA DE JESUS E PENSA QUE TUDO TERMINOU NA CRUZ, MAS EU SEI QUE A HISTÓRIA NÃO CHEGOU AO FIM, JESUS ESTÁ VIVO PARA MIM! NAQUELA MANHÃ QUANDO O SOL RAIOU, A PORTA DA SEPULTURA SE ABRIU E O MESTRE AOS DISCÍPULOS SE APRESENTOU, O CORDEIRO QUE FOI MORTO RESSURGIU! CORO  ALELUIA, RESSUSCITOU! CRISTO RESSUSCITOU E ELE VIVO HOJE ESTÁ, E BREVE VOLTARÁ! O MUNDO NÃO MAIS O VIU NEM IRÁ VER MAS HOJE À SUA IGREJA DÁ-SE A CONHECER COM MUITOS SINAIS E PROVAS DE AMOR, JESUS CRISTO ESTÁ VIVO E É SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('284', '284 - O AMOR DE DEUS É GRANDE', 'O AMOR DE DEUS É GRANDE, MANDOU SEU FILHO À CRUZ A MORRER POR MIM E POR TI E TAMBÉM PARA VENCER, VENCER A MORTE, VENCER A DOR; ELE É JESUS, UNIGÊNITO FILHO, REI, SENHOR, SALVADOR.\n\nCORO TE CHAMA PRA VIDA, TE CHAMA PRA LUZ, VENCEU A MORTE, ELE É JESUS, QUER TE SALVAR, VIDA TE DAR, VEM A JESUS.\n\nUM ALTO PREÇO ELE PAGOU SÓ POR AMOR AO PECADOR, ALI NA CRUZ JESUS MORREU E DEPOIS RESSUSCITOU, VENCEU A MORTE, VENCEU A DOR; ELE É JESUS, UNIGÊNITO FILHO, REI, SENHOR, SALVADOR.\n\nCORO TE CHAMA PRA VIDA, TE CHAMA PRA LUZ, VENCEU A MORTE, ELE É JESUS, QUER TE SALVAR, VIDA TE DAR, VEM A JESUS.\n\nFINAL: TE CHAMA PRA VIDA (TE CHAMA PRA VIDA) TE CHAMA PRA LUZ (TE CHAMA PRA LUZ) VENCEU A MORTE(ELE VENCEU) ELE É JESUS (ELE É JESUS) QUER TE SALVAR (QUER TE SALVAR) VIDA TE DAR, VEM A JESUS. (BIS)', 'O AMOR DE DEUS É GRANDE, MANDOU SEU FILHO À CRUZ A MORRER POR MIM E POR TI E TAMBÉM PARA VENCER, VENCER A MORTE, VENCER A DOR; ELE É JESUS, UNIGÊNITO FILHO, REI, SENHOR, SALVADOR. CORO TE CHAMA PRA VIDA, TE CHAMA PRA LUZ, VENCEU A MORTE, ELE É JESUS, QUER TE SALVAR, VIDA TE DAR, VEM A JESUS. UM ALTO PREÇO ELE PAGOU SÓ POR AMOR AO PECADOR, ALI NA CRUZ JESUS MORREU E DEPOIS RESSUSCITOU, VENCEU A MORTE, VENCEU A DOR; ELE É JESUS, UNIGÊNITO FILHO, REI, SENHOR, SALVADOR. TE CHAMA PRA VIDA (TE CHAMA PRA VIDA) TE CHAMA PRA LUZ (TE CHAMA PRA LUZ) VENCEU A MORTE(ELE VENCEU) ELE É JESUS (ELE É JESUS) QUER TE SALVAR (QUER TE SALVAR) VIDA TE DAR, VEM A JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('285', '285 - TU QUE ANDAS SÓ E TRISTE SEM SABER O CAMINHO', 'TU QUE ANDAS SÓ E TRISTE SEM SABER O CAMINHO, EXISTE ALGUÉM QUE PODE TUA FOME E TUA SEDE SACIAR; ELE ESTÁ À PORTA DO TEU CORAÇÃO E INSISTENTE BATE AGORA, DEIXA-O ENTRAR, DEIXA-O FLUIR EM TEU SER.\n\nQUEM É JESUS? “EU SOU AQUELE QUE NA CRUZ MORRI POR TI, MEU SANGUE ALI VERTI PRA TE SALVAR, (PRA TE SALVAR) POR TE AMAR, (POR TE AMAR) E QUERO TUA VIDA MUDAR.” INSTRUMENTOS\n\nTU QUE ANDAS SÓ E TRISTE SEM SABER O CAMINHO, EXISTE ALGUÉM QUE PODE TE AJUDAR E UMA VIDA NOVA TE DAR; ELE ESTÁ À PORTA DO TEU CORAÇÃO E INSISTENTE BATE AGORA, DEIXA-O ENTRAR, DEIXA-O FLUIR EM TEU SER.\n\nQUERO JESUS! (SERVAS) POIS SEI QUE NUMA CRUZ POR MIM MORREU, SEU SANGUE ALI VERTEU PRA ME SALVAR, (PRA ME SALVAR) SERVAS POR ME AMAR, (POR ME AMAR) SERVAS E PODE MINHA VIDA MUDAR.\n\nQUERO JESUS! E RENDEREI A ELE O MEU LOUVOR POR TUDO QUE A MIM FEZ, QUERO SEMPRE A TI, JESUS, SER FIEL.', 'TU QUE ANDAS SÓ E TRISTE SEM SABER O CAMINHO, EXISTE ALGUÉM QUE PODE TUA FOME E TUA SEDE SACIAR; ELE ESTÁ À PORTA DO TEU CORAÇÃO E INSISTENTE BATE AGORA, DEIXA-O ENTRAR, DEIXA-O FLUIR EM TEU SER. QUEM É JESUS? EU SOU AQUELE QUE NA CRUZ MORRI POR TI, MEU SANGUE ALI VERTI PRA TE SALVAR, (PRA TE SALVAR) POR TE AMAR, (POR TE AMAR) E QUERO TUA VIDA MUDAR. INSTRUMENTOS TU QUE ANDAS SÓ E TRISTE SEM SABER O CAMINHO, EXISTE ALGUÉM QUE PODE TE AJUDAR E UMA VIDA NOVA TE DAR; ELE ESTÁ À PORTA DO TEU CORAÇÃO E INSISTENTE BATE AGORA, DEIXA-O ENTRAR, DEIXA-O FLUIR EM TEU SER. QUERO JESUS!  POIS SEI QUE NUMA CRUZ POR MIM MORREU, SEU SANGUE ALI VERTEU PRA ME SALVAR, (PRA ME SALVAR) SERVAS POR ME AMAR, (POR ME AMAR) SERVAS E PODE MINHA VIDA MUDAR. QUERO JESUS! E RENDEREI A ELE O MEU LOUVOR POR TUDO QUE A MIM FEZ, QUERO SEMPRE A TI, JESUS, SER FIEL.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('286', '286 - UM DIA EU QUIS TE DEIXAR', 'UM DIA EU QUIS TE DEIXAR, PARTIR E SER LIVRE PARA VIVER; ABANDONEI-TE, TÃO CEGO NÃO VI COMO ENTÃO FERI TEU CORAÇÃO DE PAI. BEM CEDO TUDO PERDI, A SOLIDÃO TOMOU CONTA DE MIM; ENTÃO LEMBREI-ME DA CASA DO PAI E CONTRITO VIM, VOLTANDO PARA TI.\n\nCORO VOLTEI, MEU PAI, AO TEU AMOR INCONDICIONAL! ÉS MINHA HERANÇA, CONTIGO SOU TÃO FELIZ, MEU DEUS!\n\nDE LONGE VISTE CHEGAR E ME ABRAÇASTE COM TANTO AMOR, E HOUVE CÂNTICOS, FESTA NOS CÉUS, POIS VOLTOU À CASA UM POBRE PECADOR. FINAL: TÃO FELIZ!', 'UM DIA EU QUIS TE DEIXAR, PARTIR E SER LIVRE PARA VIVER; ABANDONEI-TE, TÃO CEGO NÃO VI COMO ENTÃO FERI TEU CORAÇÃO DE PAI. BEM CEDO TUDO PERDI, A SOLIDÃO TOMOU CONTA DE MIM; ENTÃO LEMBREI-ME DA CASA DO PAI E CONTRITO VIM, VOLTANDO PARA TI. CORO VOLTEI, MEU PAI, AO TEU AMOR INCONDICIONAL! ÉS MINHA HERANÇA, CONTIGO SOU TÃO FELIZ, MEU DEUS! DE LONGE VISTE CHEGAR E ME ABRAÇASTE COM TANTO AMOR, E HOUVE CÂNTICOS, FESTA NOS CÉUS, POIS VOLTOU À CASA UM POBRE PECADOR.  TÃO FELIZ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('287', '287 - NÃO TINHA PARECER', 'NÃO TINHA PARECER NEM FORMOSURA, ERA DESPREZADO ENTRE OS HOMENS, COMO OVELHA MUDA FOI SACRIFICADO, ELE VENCEU O MUNDO, SIM, ELE VENCEU A MORTE.\n\nCORO LÁ NA CRUZ, LÁ NA CRUZ O SEU SANGUE DERRAMOU, LÁ NA CRUZ, LÁ NA CRUZ SUA VIDA ENTREGOU; É POR ISSO QUE CANTO ALELUIA, É POR ISSO QUE AMO A JESUS, NÃO POSSO VIVER SEM O SEU AMOR, JESUS É TUDO PRA MIM. INSTRUMENTOS\n\nJESUS MORREU AQUI POR NOSSAS VIDAS, SEU AMOR É INCOMPARÁVEL, FOI SUBINDO COMO UM RENOVO E RAIZ DE UMA TERRA SECA, CONCEDENDO-NOS SEU PERDÃO, CONCEDENDO-NOS SALVAÇÃO.\n\nCORO LÁ NA CRUZ, LÁ NA CRUZ O SEU SANGUE DERRAMOU, LÁ NA CRUZ, LÁ NA CRUZ SUA VIDA ENTREGOU; É POR ISSO QUE CANTO ALELUIA, É POR ISSO QUE AMO A JESUS, NÃO POSSO VIVER SEM O SEU AMOR, JESUS É TUDO PRA MIM.', 'NÃO TINHA PARECER NEM FORMOSURA, ERA DESPREZADO ENTRE OS HOMENS, COMO OVELHA MUDA FOI SACRIFICADO, ELE VENCEU O MUNDO, SIM, ELE VENCEU A MORTE. CORO LÁ NA CRUZ, LÁ NA CRUZ O SEU SANGUE DERRAMOU, LÁ NA CRUZ, LÁ NA CRUZ SUA VIDA ENTREGOU; É POR ISSO QUE CANTO ALELUIA, É POR ISSO QUE AMO A JESUS, NÃO POSSO VIVER SEM O SEU AMOR, JESUS É TUDO PRA MIM. INSTRUMENTOS JESUS MORREU AQUI POR NOSSAS VIDAS, SEU AMOR É INCOMPARÁVEL, FOI SUBINDO COMO UM RENOVO E RAIZ DE UMA TERRA SECA, CONCEDENDO-NOS SEU PERDÃO, CONCEDENDO-NOS SALVAÇÃO. CORO LÁ NA CRUZ, LÁ NA CRUZ O SEU SANGUE DERRAMOU, LÁ NA CRUZ, LÁ NA CRUZ SUA VIDA ENTREGOU; É POR ISSO QUE CANTO ALELUIA, É POR ISSO QUE AMO A JESUS, NÃO POSSO VIVER SEM O SEU AMOR, JESUS É TUDO PRA MIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('288', '288 - BASTA DIZERES O QUE QUERES', 'BASTA DIZERES O QUE QUERES JESUS PRONTO ESTÁ A TE OUVIR, BASTA COM O SENHOR SERES SINCERO E ELE TE RESPONDERÁ.\n\nA ALEGRIA QUE NO MUNDO JAMAIS PUDESTE ENCONTRAR, EM CRISTO TERÁS O PRAZER, SIM, DELA GOZAR.\n\nCORO SENTE OS BRAÇOS DE JESUS QUERENDO TE SOCORRER, OUVE A VOZ SUAVE DO FILHO DE DEUS DIZENDO: “TE AMO E QUERO TE SALVAR.”\n\nCRISTO QUER QUE ESTEJAS CONOSCO QUANDO VIER NOS BUSCAR PROCLAMANDO: “VINDE BENDITOS DE MEU PAI COMIGO NO CÉU MORAR”.\n\nCORO SENTE OS BRAÇOS DE JESUS QUERENDO TE SOCORRER, OUVE A VOZ SUAVE DO FILHO DE DEUS DIZENDO: “TE AMO E QUERO TE SALVAR.” “TE AMO E QUERO TE SALVAR.”', 'BASTA DIZERES O QUE QUERES JESUS PRONTO ESTÁ A TE OUVIR, BASTA COM O SENHOR SERES SINCERO E ELE TE RESPONDERÁ. A ALEGRIA QUE NO MUNDO JAMAIS PUDESTE ENCONTRAR, EM CRISTO TERÁS O PRAZER, SIM, DELA GOZAR. CORO SENTE OS BRAÇOS DE JESUS QUERENDO TE SOCORRER, OUVE A VOZ SUAVE DO FILHO DE DEUS DIZENDO: TE AMO E QUERO TE SALVAR. CRISTO QUER QUE ESTEJAS CONOSCO QUANDO VIER NOS BUSCAR PROCLAMANDO: VINDE BENDITOS DE MEU PAI COMIGO NO CÉU MORAR. CORO SENTE OS BRAÇOS DE JESUS QUERENDO TE SOCORRER, OUVE A VOZ SUAVE DO FILHO DE DEUS DIZENDO: TE AMO E QUERO TE SALVAR. TE AMO E QUERO TE SALVAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('289', '289 - ESTENDE TUA MÃO', 'ESTENDE TUA MÃO AO DEUS QUE TE AMA, ABRE O CORAÇÃO AO DEUS QUE TE GUARDA; SÓ ELE SABERÁ TEUS PROBLEMAS RESOLVER, Ó ABRE O TEU CORAÇÃO A DEUS.\nBIS\n\nELE SERÁ TEU PASTOR, SÓ ELE TE GUIARÁ JUNTO ÀS ÁGUAS TRANQÜILAS, REFRIGERA TUA VIDA, ELE TE MOSTRARÁ A SALVAÇÃO.\n\nSÓ ESTENDE TUA MÃO AO DEUS QUE TE AMA, ABRE O CORAÇÃO AO DEUS QUE TE GUARDA; SÓ ELE SABERÁ TEUS PROBLEMAS RESOLVER Ó ABRE O TEU CORAÇÃO A DEUS. (2X)', 'ESTENDE TUA MÃO AO DEUS QUE TE AMA, ABRE O CORAÇÃO AO DEUS QUE TE GUARDA; SÓ ELE SABERÁ TEUS PROBLEMAS RESOLVER, Ó ABRE O TEU CORAÇÃO A DEUS. ELE SERÁ TEU PASTOR, SÓ ELE TE GUIARÁ JUNTO ÀS ÁGUAS TRANQÜILAS, REFRIGERA TUA VIDA, ELE TE MOSTRARÁ A SALVAÇÃO. SÓ ESTENDE TUA MÃO AO DEUS QUE TE AMA, ABRE O CORAÇÃO AO DEUS QUE TE GUARDA; SÓ ELE SABERÁ TEUS PROBLEMAS RESOLVER Ó ABRE O TEU CORAÇÃO A DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('290', '290 - O QUE OS OLHOS NUNCA VIRAM', 'O QUE OS OLHOS NUNCA VIRAM, E O CORAÇÃO NUNCA SENTIU, DEUS PREPAROU EM SEU FAVOR, PARA O ABENÇOAR ABRA SEU CORAÇÃO E DEIXE QUE A LUZ, VENHA BRILHAR EM SEU VIVER, ETERNAMENTE.\n\nCORO ENTÃO COMECE A GLORIFICAR PELO QUE AINDA NÃO VIU. SE DEUS PROMETEU, VAI SE CUMPRIR SUA BÊNÇÃO VAI CHEGAR.\n\nABRA SEU CORAÇÃO E DEIXE QUE A LUZ, VENHA BRILHAR EM SEU VIVER, ETERNAMENTE\n\nCORO (2X) ENTÃO COMECE A GLORIFICAR PELO QUE AINDA NÃO VIU. SE DEUS PROMETEU, VAI SE CUMPRIR SUA BÊNÇÃO VAI CHEGAR.\n\nFINAL: (2X) DEUS TEM SEMPRE O MELHOR, MESMO QUE A LUZ NÃO BRILHE EM SEUS OLHOS. VÊ, DEUS AMA VOCÊ! JÁ DECRETOU A SUA VITÓRIA. BASTA CRER, BASTA CRER.', 'O QUE OS OLHOS NUNCA VIRAM, E O CORAÇÃO NUNCA SENTIU, DEUS PREPAROU EM SEU FAVOR, PARA O ABENÇOAR ABRA SEU CORAÇÃO E DEIXE QUE A LUZ, VENHA BRILHAR EM SEU VIVER, ETERNAMENTE. CORO ENTÃO COMECE A GLORIFICAR PELO QUE AINDA NÃO VIU. SE DEUS PROMETEU, VAI SE CUMPRIR SUA BÊNÇÃO VAI CHEGAR. ABRA SEU CORAÇÃO E DEIXE QUE A LUZ, VENHA BRILHAR EM SEU VIVER, ETERNAMENTE CORO  ENTÃO COMECE A GLORIFICAR PELO QUE AINDA NÃO VIU. SE DEUS PROMETEU, VAI SE CUMPRIR SUA BÊNÇÃO VAI CHEGAR. DEUS TEM SEMPRE O MELHOR, MESMO QUE A LUZ NÃO BRILHE EM SEUS OLHOS. VÊ, DEUS AMA VOCÊ! JÁ DECRETOU A SUA VITÓRIA. BASTA CRER, BASTA CRER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('291', '291 - SEMPRE QUE QUISERES FALAREI CONTIGO', 'SEMPRE QUE QUISERES FALAREI CONTIGO, SEMPRE QUE PRECISARES, ESTAREI À DISPOSIÇÃO PRA MOSTRAR A TI O MEU AMOR E DAR-TE A MINHA SALVAÇÃO.\nBIS\n\nO MEU AMOR POR TI, Ó FILHO É MUITO GRANDE TÃO GRANDE QUE NÃO PODES NEM IMAGINAR POR VOCÊ EU DEI MEU ÚNICO FILHO, PRA MORRER NAQUELA CRUZ, PRA QUE VOCÊ TENHA VIDA EM ABUNDÂNCIA. PRA QUE VOCÊ TENHA VIDA ETERNA.\n\nO MEU AMOR POR TI, Ó FILHO É MUITO GRANDE TÃO GRANDE QUE NÃO PODES NEM IMAGINAR POR VOCÊ EU DEI MEU ÚNICO FILHO, PRA MORRER NAQUELA CRUZ, PRA QUE VOCÊ TENHA VIDA EM ABUNDÂNCIA. PRA QUE VOCÊ TENHA VIDA ETERNA.', 'SEMPRE QUE QUISERES FALAREI CONTIGO, SEMPRE QUE PRECISARES, ESTAREI À DISPOSIÇÃO PRA MOSTRAR A TI O MEU AMOR E DAR-TE A MINHA SALVAÇÃO. O MEU AMOR POR TI, Ó FILHO É MUITO GRANDE TÃO GRANDE QUE NÃO PODES NEM IMAGINAR POR VOCÊ EU DEI MEU ÚNICO FILHO, PRA MORRER NAQUELA CRUZ, PRA QUE VOCÊ TENHA VIDA EM ABUNDÂNCIA. PRA QUE VOCÊ TENHA VIDA ETERNA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('292', '292 - AO SENTIR O MUNDO AO MEU REDOR', 'AO SENTIR O MUNDO AO MEU REDOR, NADA VI QUE PUDESSE SER REAL. PERCEBI QUE TODOS BUSCAM PAZ, PORÉM EM VÃO, POIS NAQUILO QUE PROCURAM NÃO HÁ SOLUÇÃO. NÃO HÁ SOLUÇÃO.\n\nSÓ EM JESUS, A PAZ REAL EU PUDE ENCONTRAR. O SEU AMOR PUDE EXPERIMENTAR. ME ENTREGUEI A CRISTO, E A VIDA ETERNA VOU GOZAR. INSTRUMENTOS\n\nPOSSO VER QUE VOCÊ NÃO É FELIZ, VOU DIZER QUE NÃO PODE SER FELIZ, SE CONTINUAR A PROCURAR EM VÃO, POR CAMINHOS QUE NÃO TRAZEM SOLUÇÃO. NÃO TRAZEM SOLUÇÃO.\n\nSÓ EM JESUS, A PAZ REAL VOCÊ VAI ENCONTRAR. O SEU AMOR VAI EXPERIMENTAR. VENHA A JESUS CRISTO, E A VIDA ETERNA VAI GOZAR. INSTRUMENTOS\n\nSÓ EM JESUS, A PAZ REAL VOCÊ VAI ENCONTRAR. O SEU AMOR VAI EXPERIMENTAR. VENHA A JESUS CRISTO, E A VIDA ETERNA VAI GOZAR.', 'AO SENTIR O MUNDO AO MEU REDOR, NADA VI QUE PUDESSE SER REAL. PERCEBI QUE TODOS BUSCAM PAZ, PORÉM EM VÃO, POIS NAQUILO QUE PROCURAM NÃO HÁ SOLUÇÃO. NÃO HÁ SOLUÇÃO. SÓ EM JESUS, A PAZ REAL EU PUDE ENCONTRAR. O SEU AMOR PUDE EXPERIMENTAR. ME ENTREGUEI A CRISTO, E A VIDA ETERNA VOU GOZAR. INSTRUMENTOS POSSO VER QUE VOCÊ NÃO É FELIZ, VOU DIZER QUE NÃO PODE SER FELIZ, SE CONTINUAR A PROCURAR EM VÃO, POR CAMINHOS QUE NÃO TRAZEM SOLUÇÃO. NÃO TRAZEM SOLUÇÃO. SÓ EM JESUS, A PAZ REAL VOCÊ VAI ENCONTRAR. O SEU AMOR VAI EXPERIMENTAR. VENHA A JESUS CRISTO, E A VIDA ETERNA VAI GOZAR. INSTRUMENTOS SÓ EM JESUS, A PAZ REAL VOCÊ VAI ENCONTRAR. O SEU AMOR VAI EXPERIMENTAR. VENHA A JESUS CRISTO, E A VIDA ETERNA VAI GOZAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('293', '293 - ALMA CANSADA', 'ALMA CANSADA, NÃO DESESPERES, ESPERA EM DEUS. QUE ELE VEM, QUE ELE VEM, TE SOCORRER ALMA CANSADA, NÃO DESESPERES, ESPERA EM DEUS, ESPERA EM DEUS, ESPERA EM DEUS E NO SEU PODER.\n\nCORO Ó ALMA CANSADA, ESPERA EM DEUS E NO SEU AMOR. VEM, ALMA CANSADA, ESPERA EM DEUS, CONFIA SOMENTE NO SEU AMOR. INSTRUMENTOS\n\nALMA CANSADA, O TEU LAMENTO NÃO RESOLVERÁ. A HORA É TRISTE, COM FÉ INSISTE, DEUS TE OUVIRÁ. SE TEUS MOMENTOS TRAZEM TORMENTOS QUE FAZEM CHORAR, DEUS TE CHAMA AGORA, Ó VEM, SEM DEMORA EM PAZ DESCANSAR.\n\nCORO Ó ALMA CANSADA, ESPERA EM DEUS E NO SEU AMOR. VEM, ALMA CANSADA, ESPERA EM DEUS, CONFIA SOMENTE NO SEU AMOR.', 'ALMA CANSADA, NÃO DESESPERES, ESPERA EM DEUS. QUE ELE VEM, QUE ELE VEM, TE SOCORRER ALMA CANSADA, NÃO DESESPERES, ESPERA EM DEUS, ESPERA EM DEUS, ESPERA EM DEUS E NO SEU PODER. CORO Ó ALMA CANSADA, ESPERA EM DEUS E NO SEU AMOR. VEM, ALMA CANSADA, ESPERA EM DEUS, CONFIA SOMENTE NO SEU AMOR. INSTRUMENTOS ALMA CANSADA, O TEU LAMENTO NÃO RESOLVERÁ. A HORA É TRISTE, COM FÉ INSISTE, DEUS TE OUVIRÁ. SE TEUS MOMENTOS TRAZEM TORMENTOS QUE FAZEM CHORAR, DEUS TE CHAMA AGORA, Ó VEM, SEM DEMORA EM PAZ DESCANSAR. CORO Ó ALMA CANSADA, ESPERA EM DEUS E NO SEU AMOR. VEM, ALMA CANSADA, ESPERA EM DEUS, CONFIA SOMENTE NO SEU AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('294', '294 - JESUS IA CAMINHANDO (CANTA MEU POVO)', 'JESUS IA CAMINHANDO, SUBINDO PRA JERUSALÉM. COM ELE IAM OS SEUS DISCÍPULOS E OS FARISEUS TAMBÉM. OS DISCÍPULOS SE ALEGRAVAM, CANTAVAM E DAVAM LOUVOR, DIZENDO: BENDITO É O REI QUE VEM EM NOME DO SENHOR.\n\nCORO CANTA MEU POVO, ALEGRA MEU POVO, QUE A FESTA NÃO VAI ACABAR QUANDO FINDAR NA TERRA, NO CÉU VAI CONTINUAR.\n\nOS FARISEUS MURMURAVAM, FALAVAM COM ANSIEDADE MANDE QUE O POVO SE CALE, NÃO PERTURBE A CIDADE. JESUS OLHANDO PARA ELES, LHES DISSE COM SATISFAÇÃO: SE ELES SE CALAREM, LOGO AS PEDRAS CLAMARÃO.\n\nCORO CANTA MEU POVO, ALEGRA MEU POVO, QUE A FESTA NÃO VAI ACABAR QUANDO FINDAR NA TERRA, NO CÉU VAI CONTINUAR.\n\nE NÓS QUE SABEMOS ISTO, QUE FOI JESUS QUEM FALOU, ANTES QUE AS PEDRAS CLAMEM, NÓS CLAMAMOS AO SENHOR. JESUS DESCEU DA SUA GLÓRIA PRA NOS TRAZER SALVAÇÃO, NÃO HÁ NINGUÉM QUE TIRE O GOZO DO MEU CORAÇÃO.\n\nCORO (2X) CANTA MEU POVO, ALEGRA MEU POVO, QUE A FESTA NÃO VAI ACABAR QUANDO FINDAR NA TERRA, NO CÉU VAI CONTINUAR.', 'JESUS IA CAMINHANDO, SUBINDO PRA JERUSALÉM. COM ELE IAM OS SEUS DISCÍPULOS E OS FARISEUS TAMBÉM. OS DISCÍPULOS SE ALEGRAVAM, CANTAVAM E DAVAM LOUVOR, DIZENDO: BENDITO É O REI QUE VEM EM NOME DO SENHOR. CORO CANTA MEU POVO, ALEGRA MEU POVO, QUE A FESTA NÃO VAI ACABAR QUANDO FINDAR NA TERRA, NO CÉU VAI CONTINUAR. OS FARISEUS MURMURAVAM, FALAVAM COM ANSIEDADE MANDE QUE O POVO SE CALE, NÃO PERTURBE A CIDADE. JESUS OLHANDO PARA ELES, LHES DISSE COM SATISFAÇÃO: SE ELES SE CALAREM, LOGO AS PEDRAS CLAMARÃO. E NÓS QUE SABEMOS ISTO, QUE FOI JESUS QUEM FALOU, ANTES QUE AS PEDRAS CLAMEM, NÓS CLAMAMOS AO SENHOR. JESUS DESCEU DA SUA GLÓRIA PRA NOS TRAZER SALVAÇÃO, NÃO HÁ NINGUÉM QUE TIRE O GOZO DO MEU CORAÇÃO. CORO  CANTA MEU POVO, ALEGRA MEU POVO, QUE A FESTA NÃO VAI ACABAR QUANDO FINDAR NA TERRA, NO CÉU VAI CONTINUAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('295', '295 - DAS PROFUNDEZAS A TI CLAMO, Ó SENHOR', 'DAS PROFUNDEZAS A TI CLAMO, Ó SENHOR, ESCUTA A MINHA VOZ; SEJAM OS TEUS OUVIDOS ATENTOS À VOZ DAS MINHAS SÚPLICAS; SE TU, SENHOR, OBSERVARES AS INIQÜIDADES, SENHOR, QUEM SUBSISTIRÁ? MAS CONTIGO ESTÁ O PERDÃO PARA QUE SEJAS TEMIDO, AGUARDO O SENHOR, A MINHA ALMA O AGUARDA.\n\nE ESPERO NA SUA PALAVRA, A MINH’ALMA ANSEIA PELO SENHOR MAIS DO QUE OS GUARDAS PELO ROMPER DA MANHÃ, SIM, DO QUE AQUELES QUE ESPERAM PELA MANHÃ.\n\nESPERA, ISRAEL, NO SENHOR, PORQUE NO SENHOR HÁ MISERICÓRDIA E NELE HÁ ABUNDANTE REDENÇÃO, E ELE REMIRÁ A ISRAEL DE TODAS AS SUAS INIQÜIDADES.\nBIS', 'DAS PROFUNDEZAS A TI CLAMO, Ó SENHOR, ESCUTA A MINHA VOZ; SEJAM OS TEUS OUVIDOS ATENTOS À VOZ DAS MINHAS SÚPLICAS; SE TU, SENHOR, OBSERVARES AS INIQÜIDADES, SENHOR, QUEM SUBSISTIRÁ? MAS CONTIGO ESTÁ O PERDÃO PARA QUE SEJAS TEMIDO, AGUARDO O SENHOR, A MINHA ALMA O AGUARDA. E ESPERO NA SUA PALAVRA, A MINH’ALMA ANSEIA PELO SENHOR MAIS DO QUE OS GUARDAS PELO ROMPER DA MANHÃ, SIM, DO QUE AQUELES QUE ESPERAM PELA MANHÃ. ESPERA, ISRAEL, NO SENHOR, PORQUE NO SENHOR HÁ MISERICÓRDIA E NELE HÁ ABUNDANTE REDENÇÃO, E ELE REMIRÁ A ISRAEL DE TODAS AS SUAS INIQÜIDADES.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('296', '296 - TODA A MINHA DOR JESUS LEVOU', 'TODA A MINHA DOR JESUS LEVOU E A TRISTEZA E O PRANTO ELE ENXUGOU, HOJE SOU FELIZ PORQUE O SENHOR DE TODO O PECADO ME LAVOU.\n\nCORO DEIXA-ME CHORAR A TEUS PÉS, SENHOR, QUERO AGRADECER TEU GRANDE AMOR; E JUNTO DA CRUZ QUERO ESTAR, A TEUS PÉS, JESUS, DEIXA CHORAR.\n\nO SEU SANGUE PURO DERRAMOU, TODO O MAL DO MUNDO ELE VENCEU; POR SEU DIVINAL E EXCELSO AMOR A TODOS PERDÃO QUIS CONCEDER.\n\nCORO DEIXA-ME CHORAR A TEUS PÉS, SENHOR, QUERO AGRADECER TEU GRANDE AMOR; E JUNTO DA CRUZ QUERO ESTAR, A TEUS PÉS, JESUS, DEIXA CHORAR.\n\nPARA TI JESUS ESTÁ OLHANDO, COM MAIOR TERNURA ESTÁ PEDINDO: “DÁ-ME, FILHO MEU, TEU CORAÇÃO E UM DIA TERÁS UM GALARDÃO!”\n\nCORO DEIXA-ME CHORAR A TEUS PÉS, SENHOR, QUERO AGRADECER TEU GRANDE AMOR; E JUNTO DA CRUZ QUERO ESTAR, A TEUS PÉS, JESUS, DEIXA CHORAR.', 'TODA A MINHA DOR JESUS LEVOU E A TRISTEZA E O PRANTO ELE ENXUGOU, HOJE SOU FELIZ PORQUE O SENHOR DE TODO O PECADO ME LAVOU. CORO DEIXA-ME CHORAR A TEUS PÉS, SENHOR, QUERO AGRADECER TEU GRANDE AMOR; E JUNTO DA CRUZ QUERO ESTAR, A TEUS PÉS, JESUS, DEIXA CHORAR. O SEU SANGUE PURO DERRAMOU, TODO O MAL DO MUNDO ELE VENCEU; POR SEU DIVINAL E EXCELSO AMOR A TODOS PERDÃO QUIS CONCEDER. PARA TI JESUS ESTÁ OLHANDO, COM MAIOR TERNURA ESTÁ PEDINDO: DÁ-ME, FILHO MEU, TEU CORAÇÃO E UM DIA TERÁS UM GALARDÃO!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('297', '297 - EM ME VINDO O TEMOR', 'EM ME VINDO O TEMOR HEI DE CONFIAR EM TI, EM ME VINDO A DOR DESCANSAREI EM TI; POR QUE TEMER SE COMIGO ESTÁS? TE SINTO TÃO PERTO, SEGURO ME FAZES.\n\nTU ME ENSINASTE A EM TI CONFIAR, TU ME AJUDASTE A EM TI ESPERAR; DÁ-ME MAIS FÉ PARA PROSSEGUIR E NUNCA TEMER O QUE HÁ DE VIR.\n\nEM TI EU SOU FORTE, CONHEÇO TUA PAZ, CONTIGO AO MEU LADO O TEMOR SE DESFAZ; POR QUE TEMER SE COMIGO ESTÁS? TE SINTO TÃO PERTO, SEGURO ME FAZES.', 'EM ME VINDO O TEMOR HEI DE CONFIAR EM TI, EM ME VINDO A DOR DESCANSAREI EM TI; POR QUE TEMER SE COMIGO ESTÁS? TE SINTO TÃO PERTO, SEGURO ME FAZES. TU ME ENSINASTE A EM TI CONFIAR, TU ME AJUDASTE A EM TI ESPERAR; DÁ-ME MAIS FÉ PARA PROSSEGUIR E NUNCA TEMER O QUE HÁ DE VIR. EM TI EU SOU FORTE, CONHEÇO TUA PAZ, CONTIGO AO MEU LADO O TEMOR SE DESFAZ; POR QUE TEMER SE COMIGO ESTÁS? TE SINTO TÃO PERTO, SEGURO ME FAZES.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('298', '298 - NEM SEMPRE ENTENDEMOS', 'NEM SEMPRE ENTENDEMOS PORQUE DEUS AGE ASSIM, O ESPINHO QUE A BELA ROSA TEM; MAS PELA FÉ SABEMOS QUE TUDO QUE ELE FAZ É SEMPRE, SEMPRE PARA O NOSSO BEM.\n\nCORO NADA ME SEPARA DESSE AMOR, SEMPRE CONFIAREI NO MEU SENHOR; SEI QUE CUIDARÁ DE MIM, COM DESVELO ATÉ O FIM, NADA ME SEPARARÁ DO AMOR DE DEUS.\n\nA DOR E O SOFRIMENTO, A MORTE TÃO CRUEL, VENCEU NA CRUZ O NOSSO EMANUEL; DE CINZAS AS VESTES TROCA POR VESTES DE LOUVOR E ÓLEO DE ALEGRIA DÁ-NOS O SENHOR.\n\nCORO NADA ME SEPARA DESSE AMOR, SEMPRE CONFIAREI NO MEU SENHOR; SEI QUE CUIDARÁ DE MIM, COM DESVELO ATÉ O FIM, NADA ME SEPARARÁ DO AMOR DE DEUS. (BIS)', 'NEM SEMPRE ENTENDEMOS PORQUE DEUS AGE ASSIM, O ESPINHO QUE A BELA ROSA TEM; MAS PELA FÉ SABEMOS QUE TUDO QUE ELE FAZ É SEMPRE, SEMPRE PARA O NOSSO BEM. CORO NADA ME SEPARA DESSE AMOR, SEMPRE CONFIAREI NO MEU SENHOR; SEI QUE CUIDARÁ DE MIM, COM DESVELO ATÉ O FIM, NADA ME SEPARARÁ DO AMOR DE DEUS. A DOR E O SOFRIMENTO, A MORTE TÃO CRUEL, VENCEU NA CRUZ O NOSSO EMANUEL; DE CINZAS AS VESTES TROCA POR VESTES DE LOUVOR E ÓLEO DE ALEGRIA DÁ-NOS O SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('299', '299 - HÁ MOMENTOS QUE AS PALAVRAS NÃO RESOLVEM', 'HÁ MOMENTOS QUE AS PALAVRAS NÃO RESOLVEM, MAS O GESTO DE JESUS, NA CRUZ, DEMONSTRA AMOR POR NÓS.\nBIS\n\nCORO (2X) FOI NO CALVÁRIO QUE ELE SEM FALAR, MOSTROU AO MUNDO INTEIRO O QUE É AMAR.\n\nAQUI NO MUNDO AS DESILUSÕES SÃO TANTAS, MAS EXISTE UMA ESPERANÇA: QUE CRISTO VAI VOLTAR.\nBIS\n\nCORO (2X) FOI NO CALVÁRIO QUE ELE SEM FALAR, MOSTROU AO MUNDO INTEIRO O QUE É AMAR.', 'HÁ MOMENTOS QUE AS PALAVRAS NÃO RESOLVEM, MAS O GESTO DE JESUS, NA CRUZ, DEMONSTRA AMOR POR NÓS. CORO  FOI NO CALVÁRIO QUE ELE SEM FALAR, MOSTROU AO MUNDO INTEIRO O QUE É AMAR. AQUI NO MUNDO AS DESILUSÕES SÃO TANTAS, MAS EXISTE UMA ESPERANÇA: QUE CRISTO VAI VOLTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('300', '300 - QUANDO PARECE QUE TUDO ACABOU', 'QUANDO PARECE QUE TUDO ACABOU E NÃO HÁ MAIS ESPERANÇA EM TEU VIVER, ERGUE TEU OLHAR E CLAMA A JESUS, CRÊ SOMENTE: ELE TEM PODER.\n\nCORO OH! NÃO TEMAS, MAS CONFIA EM JESUS, TEUS PROBLEMAS DEUS CONHECE E TUDO VÊ; NÃO CHORES MAIS, JESUS TE AMA, NÃO TEMAS, SOMENTE CRÊ.\n\nNÃO TEMAS, CRÊ SOMENTE EM JESUS, IMPOSSÍVEIS NÃO HÁ PARA O SENHOR, PEDE EM ORAÇÃO E TÃO SOMENTE CRÊ, GRANDE É, POR TI, O SEU AMOR.\n\nCORO OH! NÃO TEMAS, MAS CONFIA EM JESUS, TEUS PROBLEMAS DEUS CONHECE E TUDO VÊ; NÃO CHORES MAIS, JESUS TE AMA, NÃO TEMAS, SOMENTE CRÊ.\n\nFINAL: NÃO CHORES MAIS, JESUS TE AMA, NÃO TEMAS, SOMENTE CRÊ.', 'QUANDO PARECE QUE TUDO ACABOU E NÃO HÁ MAIS ESPERANÇA EM TEU VIVER, ERGUE TEU OLHAR E CLAMA A JESUS, CRÊ SOMENTE: ELE TEM PODER. CORO OH! NÃO TEMAS, MAS CONFIA EM JESUS, TEUS PROBLEMAS DEUS CONHECE E TUDO VÊ; NÃO CHORES MAIS, JESUS TE AMA, NÃO TEMAS, SOMENTE CRÊ. NÃO TEMAS, CRÊ SOMENTE EM JESUS, IMPOSSÍVEIS NÃO HÁ PARA O SENHOR, PEDE EM ORAÇÃO E TÃO SOMENTE CRÊ, GRANDE É, POR TI, O SEU AMOR. NÃO CHORES MAIS, JESUS TE AMA, NÃO TEMAS, SOMENTE CRÊ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('301', '301 - QUANDO A TRISTEZA SURGIR', 'QUANDO A TRISTEZA SURGIR E O MUNDO TE ENGANAR, LEMBRA-TE QUE JESUS CRISTO COM SEU SANGUE TE PODE SALVAR.\n\nCORO (2X) CANTA FELIZ E ORA COM FERVOR, O SANGUE DE JESUS MOSTROU-NOS SEU AMOR.\n\nQUANDO A ALEGRIA VOLTAR E A PAZ INVADIR O TEU SER, DIZE ALELUIA A JESUS POIS SEU SANGUE TEM ESSE PODER.\n\nCORO (2X) CANTA FELIZ E ORA COM FERVOR, O SANGUE DE JESUS MOSTROU-NOS SEU AMOR.', 'QUANDO A TRISTEZA SURGIR E O MUNDO TE ENGANAR, LEMBRA-TE QUE JESUS CRISTO COM SEU SANGUE TE PODE SALVAR. CORO  CANTA FELIZ E ORA COM FERVOR, O SANGUE DE JESUS MOSTROU-NOS SEU AMOR. QUANDO A ALEGRIA VOLTAR E A PAZ INVADIR O TEU SER, DIZE ALELUIA A JESUS POIS SEU SANGUE TEM ESSE PODER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('302', '302 - SE A VIDA PARECE PESADA', 'SE A VIDA PARECE PESADA E ACHAS QUE CHEGOU AO FIM; Ó VEM A JESUS SEM DEMORA, PORQUE ELE DISSE ASSIM:\n\nCORO “VINDE A MIM, CANSADOS, SOBRECARREGADOS, E EU VOS ALIVIAREI; VINDE A MIM, ENTREGAI O VOSSO CORAÇÃO, E VIDA ETERNA VOS DAREI.”\n\nTÃO CEDO A TRISTEZA TE ABATE E ENTRA EM TEU CORAÇÃO; NÃO TEMAS, CONFIA EM CRISTO, TERÁS PLENA PAZ, SALVAÇÃO.\n\nCORO (2X) “VINDE A MIM, CANSADOS, SOBRECARREGADOS, E EU VOS ALIVIAREI; VINDE A MIM, ENTREGAI O VOSSO CORAÇÃO, E VIDA ETERNA VOS DAREI.” FINAL: “E VIDA ETERNA VOS DAREI.”', 'SE A VIDA PARECE PESADA E ACHAS QUE CHEGOU AO FIM; Ó VEM A JESUS SEM DEMORA, PORQUE ELE DISSE ASSIM: CORO VINDE A MIM, CANSADOS, SOBRECARREGADOS, E EU VOS ALIVIAREI; VINDE A MIM, ENTREGAI O VOSSO CORAÇÃO, E VIDA ETERNA VOS DAREI. TÃO CEDO A TRISTEZA TE ABATE E ENTRA EM TEU CORAÇÃO; NÃO TEMAS, CONFIA EM CRISTO, TERÁS PLENA PAZ, SALVAÇÃO. CORO  VINDE A MIM, CANSADOS, SOBRECARREGADOS, E EU VOS ALIVIAREI; VINDE A MIM, ENTREGAI O VOSSO CORAÇÃO, E VIDA ETERNA VOS DAREI.  E VIDA ETERNA VOS DAREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('303', '303 - SE NÃO PUDERES MAIS', 'SE NÃO PUDERES MAIS SENTIR O TOQUE DO SENHOR JESUS, É PORQUE TUA ALMA NÃO SE ABRIU, TEU CORAÇÃO NÃO SENTIU A PRESENÇA DO SENHOR JESUS.\n\nCORO SE HOJE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES A JESUS. INSTRUMENTOS\n\nQUANDO A LUTA SE TRAVAR, DO CÉU DEUS OUVIRÁ E ENVIARÁ UM ANJO PRA TE LIVRAR; CONFIA SOMENTE NO SENHOR, O MESMO DEUS DE ONTEM E HOJE JAMAIS TE ABANDONARÁ.\n\nCORO (2X) SE HOJE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES A JESUS.', 'SE NÃO PUDERES MAIS SENTIR O TOQUE DO SENHOR JESUS, É PORQUE TUA ALMA NÃO SE ABRIU, TEU CORAÇÃO NÃO SENTIU A PRESENÇA DO SENHOR JESUS. CORO SE HOJE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES A JESUS. INSTRUMENTOS QUANDO A LUTA SE TRAVAR, DO CÉU DEUS OUVIRÁ E ENVIARÁ UM ANJO PRA TE LIVRAR; CONFIA SOMENTE NO SENHOR, O MESMO DEUS DE ONTEM E HOJE JAMAIS TE ABANDONARÁ. CORO  SE HOJE OUVIRDES A SUA VOZ NÃO ENDUREÇAIS OS VOSSOS CORAÇÕES A JESUS.',  1);
