INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('306', '306 - MEDO TENS QUE O TENTADOR TE VÁ VENCER?', 'MEDO TENS QUE O TENTADOR TE VÁ VENCER? LUZ TE FALTA E ONDE ESTÁS NÃO PODES VER? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR.\n\nCORO DEIXA A LUZ DO CÉU ENTRAR, DEIXA O SOL EM TI NASCER; ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI NASCER.\n\nFRACA ESTÁ A TUA FÉ NO SALVADOR? DEUS NÃO OUVE AS TUAS PRECES COM FAVOR? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR.\n\nCORO DEIXA A LUZ DO CÉU ENTRAR, DEIXA O SOL EM TI NASCER; ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI NASCER.\n\nQUERES IR ANDANDO ALEGRE PARA O CÉU, IGNORANDO TODO O NEGRO E DENSO VÉU? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR.\n\nCORO DEIXA A LUZ DO CÉU ENTRAR, DEIXA O SOL EM TI NASCER; ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI NASCER.', 'MEDO TENS QUE O TENTADOR TE VÁ VENCER? LUZ TE FALTA E ONDE ESTÁS NÃO PODES VER? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR. CORO DEIXA A LUZ DO CÉU ENTRAR, DEIXA O SOL EM TI NASCER; ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI NASCER. FRACA ESTÁ A TUA FÉ NO SALVADOR? DEUS NÃO OUVE AS TUAS PRECES COM FAVOR? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR. QUERES IR ANDANDO ALEGRE PARA O CÉU, IGNORANDO TODO O NEGRO E DENSO VÉU? ABRE O CORAÇÃO E DEIXA CRISTO ENTRAR E O SOL EM TI RAIAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('307', '307 - PELO VALE ESCURO SEGUIREI JESUS', 'PELO VALE ESCURO SEGUIREI JESUS, MAS POR TI SEGURO, VENDO A TUA LUZ; MEUS PASSOS INCERTOS TU DIRIGIRÁS E AO SENTIR-TE PERTO NUNCA PERCO A PAZ.\n\nCORO BREVE A NOITE DESCE, NOITE DE EMAÚS, E MEU SER CARECE DE TE VER, JESUS, COMPANHEIRO AMIGO, AO MEU LADO VEM FICA, Ó DEUS, COMIGO, INFINITO BEM.\n\nOS ESPINHOS, TANTOS, QUE VEM NOS SANGRAR SÃO REMÉDIOS SANTOS PARA NOS CURAR; ONDE EXISTE A GRAÇA DO BONDOSO DEUS TUDO O QUE SE PASSA NOS CONDUZ AOS CÉUS.\n\nCORO BREVE A NOITE DESCE, NOITE DE EMAÚS, E MEU SER CARECE DE TE VER, JESUS, COMPANHEIRO AMIGO, AO MEU LADO VEM FICA, Ó DEUS, COMIGO, INFINITO BEM.\n\nNÃO HÁ DOR QUE SEJA SEM DIVINO FIM, FAZE, Ó DEUS, QUE A IGREJA COMPREENDA ASSIM; E, APESAR DAS TREVAS, POSSA VER, SENHOR, QUE TU MESMO A LEVAS COM IMENSO AMOR.\n\nCORO BREVE A NOITE DESCE, NOITE DE EMAÚS, E MEU SER CARECE DE TE VER, JESUS, COMPANHEIRO AMIGO, AO MEU LADO VEM FICA, Ó DEUS, COMIGO, INFINITO BEM.', 'PELO VALE ESCURO SEGUIREI JESUS, MAS POR TI SEGURO, VENDO A TUA LUZ; MEUS PASSOS INCERTOS TU DIRIGIRÁS E AO SENTIR-TE PERTO NUNCA PERCO A PAZ. CORO BREVE A NOITE DESCE, NOITE DE EMAÚS, E MEU SER CARECE DE TE VER, JESUS, COMPANHEIRO AMIGO, AO MEU LADO VEM FICA, Ó DEUS, COMIGO, INFINITO BEM. OS ESPINHOS, TANTOS, QUE VEM NOS SANGRAR SÃO REMÉDIOS SANTOS PARA NOS CURAR; ONDE EXISTE A GRAÇA DO BONDOSO DEUS TUDO O QUE SE PASSA NOS CONDUZ AOS CÉUS. NÃO HÁ DOR QUE SEJA SEM DIVINO FIM, FAZE, Ó DEUS, QUE A IGREJA COMPREENDA ASSIM; E, APESAR DAS TREVAS, POSSA VER, SENHOR, QUE TU MESMO A LEVAS COM IMENSO AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('308', '308 - PROFUNDOS VALES EU TEREI DE PASSAR', 'PROFUNDOS VALES EU TEREI DE PASSAR E MUITAS LUTAS EU TEREI DE VENCER, MAS SEI QUE SÓ EU NÃO ESTOU, COMIGO ESTÁS AO MEU REDOR, A TUA GRAÇA ME SATISFAZ.\n\nSE UM DIA, ACASO, TRISTE EU ME ENCONTRAR, SE SOLITÁRIO NO DESERTO A CAMINHAR, ENTÃO ALI ME FALARÁS E A TUA MÃO ME ESTENDERÁS; MOSTRAR-ME-ÁS A CELESTE MANSÃO.\n\nA TUA GRAÇA ME SUSTERÁ POIS FRACO SOU, SEM TI NÃO POSSO CAMINHAR; REVELA-ME O TEU AMOR, COM TUA MÃO VEM ME GUIAR POIS JUNTO A TI SEMPRE QUERO ESTAR.\nBIS', 'PROFUNDOS VALES EU TEREI DE PASSAR E MUITAS LUTAS EU TEREI DE VENCER, MAS SEI QUE SÓ EU NÃO ESTOU, COMIGO ESTÁS AO MEU REDOR, A TUA GRAÇA ME SATISFAZ. SE UM DIA, ACASO, TRISTE EU ME ENCONTRAR, SE SOLITÁRIO NO DESERTO A CAMINHAR, ENTÃO ALI ME FALARÁS E A TUA MÃO ME ESTENDERÁS; MOSTRAR-ME-ÁS A CELESTE MANSÃO. A TUA GRAÇA ME SUSTERÁ POIS FRACO SOU, SEM TI NÃO POSSO CAMINHAR; REVELA-ME O TEU AMOR, COM TUA MÃO VEM ME GUIAR POIS JUNTO A TI SEMPRE QUERO ESTAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('309', '309 - OH! POR QUE DUVIDAR?', 'OH! POR QUE DUVIDAR SOBRE AS ONDAS DO MAR QUANDO CRISTO CAMINHO ABRIU? QUANDO FORÇADO ÉS CONTRA AS ONDAS LUTAR, SEU AMOR A TI QUER REVELAR.\n\nCORO SOLTA O CABO DA NAU, TOMA O REMO NAS MÃOS E NAVEGA COM FÉ EM JESUS; E ENTÃO TU VERÁS QUE BONANÇA SE FAZ POIS COM ELE SEGURO SERÁS.\n\nTREVAS VEM TE ASSUSTAR, TEMPESTADES NO MAR? DA MONTANHA O MESTRE TE VÊ, E NA TRIBULAÇÃO ELE VEM SOCORRER, SUA MÃO BEM TE PODE SUSTER.\n\nCORO SOLTA O CABO DA NAU, TOMA O REMO NAS MÃOS E NAVEGA COM FÉ EM JESUS; E ENTÃO TU VERÁS QUE BONANÇA SE FAZ POIS COM ELE SEGURO SERÁS.\n\nPODES TU RECORDAR, MARAVILHAS SEM PAR? NO DESERTO AO POVO FARTOU; E O MESMO PODER ELE SEMPRE TERÁ POIS NÃO MUDA E NÃO FALHARÁ.\n\nCORO SOLTA O CABO DA NAU, TOMA O REMO NAS MÃOS E NAVEGA COM FÉ EM JESUS; E ENTÃO TU VERÁS QUE BONANÇA SE FAZ POIS COM ELE SEGURO SERÁS.\n\nQUANDO PEDES MAIS FÉ, ELE OUVE, OH! CRÊ! MESMO SENDO EM TRIBULAÇÃO; QUANDO A MÃO DE PODER O TEU EGO TIRAR SOBRE AS ONDAS PODERÁS ANDAR.\n\nCORO SOLTA O CABO DA NAU, TOMA O REMO NAS MÃOS E NAVEGA COM FÉ EM JESUS; E ENTÃO TU VERÁS QUE BONANÇA SE FAZ POIS COM ELE SEGURO SERÁS.', 'OH! POR QUE DUVIDAR SOBRE AS ONDAS DO MAR QUANDO CRISTO CAMINHO ABRIU? QUANDO FORÇADO ÉS CONTRA AS ONDAS LUTAR, SEU AMOR A TI QUER REVELAR. CORO SOLTA O CABO DA NAU, TOMA O REMO NAS MÃOS E NAVEGA COM FÉ EM JESUS; E ENTÃO TU VERÁS QUE BONANÇA SE FAZ POIS COM ELE SEGURO SERÁS. TREVAS VEM TE ASSUSTAR, TEMPESTADES NO MAR? DA MONTANHA O MESTRE TE VÊ, E NA TRIBULAÇÃO ELE VEM SOCORRER, SUA MÃO BEM TE PODE SUSTER. PODES TU RECORDAR, MARAVILHAS SEM PAR? NO DESERTO AO POVO FARTOU; E O MESMO PODER ELE SEMPRE TERÁ POIS NÃO MUDA E NÃO FALHARÁ. QUANDO PEDES MAIS FÉ, ELE OUVE, OH! CRÊ! MESMO SENDO EM TRIBULAÇÃO; QUANDO A MÃO DE PODER O TEU EGO TIRAR SOBRE AS ONDAS PODERÁS ANDAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('310', '310 - MESTRE O MAR SE REVOLTA', 'MESTRE, O MAR SE REVOLTA, AS ONDAS NOS DÃO PAVOR! O CÉU SE REVESTE DE TREVAS, NÃO TEMOS UM SALVADOR! NÃO SE TE DÁ QUE MORRAMOS? PODES ASSIM DORMIR? SE A CADA MOMENTO NOS VEMOS JÁ PRESTES A SUBMERGIR?\n\nCORO “AS ONDAS ATENDEM AO MEU MANDAR: SOSSEGAI!” SEJA O ENCAPELADO MAR, A IRA DOS HOMENS, O GÊNIO DO MAL, TAIS ÁGUAS NÃO PODEM A NAU TRAGAR QUE LEVA O MESTRE DO CÉU E MAR! “POIS TODOS OUVEM O MEU MANDAR: SOSSEGAI! SOSSEGAI! CONVOSCO ESTOU PARA VOS SALVAR: SOSSEGAI!”\n\nMESTRE, TÃO GRANDE TRISTEZA ME QUER HOJE CONSUMIR! E A DOR QUE PERTURBA MINH’ALMA TE IMPLORA: VEM ME ACUDIR! DE ONDAS DO MAL QUE ME ENCOBREM QUEM ME FARÁ SAIR? PEREÇO, PEREÇO, Ó MESTRE, TE ROGO, VEM ME ACUDIR!\n\nCORO “AS ONDAS ATENDEM AO MEU MANDAR: SOSSEGAI!” SEJA O ENCAPELADO MAR, A IRA DOS HOMENS, O GÊNIO DO MAL, TAIS ÁGUAS NÃO PODEM A NAU TRAGAR QUE LEVA O MESTRE DO CÉU E MAR! “POIS TODOS OUVEM O MEU MANDAR: SOSSEGAI! SOSSEGAI! CONVOSCO ESTOU PARA VOS SALVAR: SOSSEGAI!”\n\nMESTRE, CHEGOU A BONANÇA, EM PAZ VEJO O CÉU E O MAR, O MEU CORAÇÃO GOZA CALMA QUE NÃO PODERÁ FINDAR; FICA COMIGO, Ó MESTRE, DONO DA TERRA E CÉU, E ASSIM CHEGAREI BEM SEGURO, AO PORTO, DESTINO MEU.\n\nCORO “AS ONDAS ATENDEM AO MEU MANDAR: SOSSEGAI!” SEJA O ENCAPELADO MAR, A IRA DOS HOMENS, O GÊNIO DO MAL, TAIS ÁGUAS NÃO PODEM A NAU TRAGAR QUE LEVA O MESTRE DO CÉU E MAR! “POIS TODOS OUVEM O MEU MANDAR: SOSSEGAI! SOSSEGAI! CONVOSCO ESTOU PARA VOS SALVAR: SOSSEGAI!”', 'MESTRE, O MAR SE REVOLTA, AS ONDAS NOS DÃO PAVOR! O CÉU SE REVESTE DE TREVAS, NÃO TEMOS UM SALVADOR! NÃO SE TE DÁ QUE MORRAMOS? PODES ASSIM DORMIR? SE A CADA MOMENTO NOS VEMOS JÁ PRESTES A SUBMERGIR? CORO AS ONDAS ATENDEM AO MEU MANDAR: SOSSEGAI! SEJA O ENCAPELADO MAR, A IRA DOS HOMENS, O GÊNIO DO MAL, TAIS ÁGUAS NÃO PODEM A NAU TRAGAR QUE LEVA O MESTRE DO CÉU E MAR! POIS TODOS OUVEM O MEU MANDAR: SOSSEGAI! SOSSEGAI! CONVOSCO ESTOU PARA VOS SALVAR: SOSSEGAI! MESTRE, TÃO GRANDE TRISTEZA ME QUER HOJE CONSUMIR! E A DOR QUE PERTURBA MINH’ALMA TE IMPLORA: VEM ME ACUDIR! DE ONDAS DO MAL QUE ME ENCOBREM QUEM ME FARÁ SAIR? PEREÇO, PEREÇO, Ó MESTRE, TE ROGO, VEM ME ACUDIR! MESTRE, CHEGOU A BONANÇA, EM PAZ VEJO O CÉU E O MAR, O MEU CORAÇÃO GOZA CALMA QUE NÃO PODERÁ FINDAR; FICA COMIGO, Ó MESTRE, DONO DA TERRA E CÉU, E ASSIM CHEGAREI BEM SEGURO, AO PORTO, DESTINO MEU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('311', '311 - SÓ UMA VOZ ME BASTA', 'SÓ UMA VOZ ME BASTA NA HORA DA TRISTEZA E DOR; SÓ UMA VOZ ME CONSOLA, É A VOZ DO MEU SENHOR. ELE TIRA A ESCURIDÃO, ELE FAZ NASCER A LUZ, ELE DÁ A SOLUÇÃO REVELADA EM JESUS.\n\nSÓ UMA VOZ ME BASTA NA HORA DA TRISTEZA E DOR; SÓ UMA VOZ ME CONSOLA, É A VOZ DO MEU SENHOR. ELE TIRA A ESCURIDÃO, ELE FAZ NASCER A LUZ, ELE DÁ A SOLUÇÃO REVELADA EM JESUS.\nBIS', 'SÓ UMA VOZ ME BASTA NA HORA DA TRISTEZA E DOR; SÓ UMA VOZ ME CONSOLA, É A VOZ DO MEU SENHOR. ELE TIRA A ESCURIDÃO, ELE FAZ NASCER A LUZ, ELE DÁ A SOLUÇÃO REVELADA EM JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('312', '312 - SE, DA VIDA, AS VAGAS PROCELOSAS SÃO', 'SE, DA VIDA, AS VAGAS PROCELOSAS SÃO, SE, COM DESALENTO, JULGAS TUDO VÃO, CONTA AS MUITAS BÊNÇÃOS, DIZE-AS DE UMA VEZ, VERÁS, COM SURPRESA, QUANTO DEUS JÁ FEZ.\n\nCORO CONTA AS BÊNÇÃOS, CONTA QUANTAS SÃO RECEBIDAS DA DIVINA MÃO; VEM DIZÊ-LAS, TODAS DE UMA VEZ, E VERÁS SURPRESO QUANTO DEUS JÁ FEZ.\n\nTENS ACASO MÁGOA? TRISTE É O TEU LIDAR? É A CRUZ PESADA QUE TENS DE LEVAR? CONTA AS MUITAS BÊNÇÃOS, NÃO DUVIDARÁS, E EM CANTO ALEGRE OS DIAS PASSARÁS.\n\nCORO CONTA AS BÊNÇÃOS, CONTA QUANTAS SÃO RECEBIDAS DA DIVINA MÃO; VEM DIZÊ-LAS, TODAS DE UMA VEZ, E VERÁS SURPRESO QUANTO DEUS JÁ FEZ.\n\nQUANDO VIRES OUTROS COM SEU OURO E BENS, LEMBRA QUE TESOUROS PROMETIDOS TENS; NUNCA OS BENS DA TERRA PODERÃO COMPRAR A MANSÃO CELESTE QUE VAIS HABITAR.\n\nCORO CONTA AS BÊNÇÃOS, CONTA QUANTAS SÃO RECEBIDAS DA DIVINA MÃO; VEM DIZÊ-LAS, TODAS DE UMA VEZ, E VERÁS SURPRESO QUANTO DEUS JÁ FEZ.', 'SE, DA VIDA, AS VAGAS PROCELOSAS SÃO, SE, COM DESALENTO, JULGAS TUDO VÃO, CONTA AS MUITAS BÊNÇÃOS, DIZE-AS DE UMA VEZ, VERÁS, COM SURPRESA, QUANTO DEUS JÁ FEZ. CORO CONTA AS BÊNÇÃOS, CONTA QUANTAS SÃO RECEBIDAS DA DIVINA MÃO; VEM DIZÊ-LAS, TODAS DE UMA VEZ, E VERÁS SURPRESO QUANTO DEUS JÁ FEZ. TENS ACASO MÁGOA? TRISTE É O TEU LIDAR? É A CRUZ PESADA QUE TENS DE LEVAR? CONTA AS MUITAS BÊNÇÃOS, NÃO DUVIDARÁS, E EM CANTO ALEGRE OS DIAS PASSARÁS. QUANDO VIRES OUTROS COM SEU OURO E BENS, LEMBRA QUE TESOUROS PROMETIDOS TENS; NUNCA OS BENS DA TERRA PODERÃO COMPRAR A MANSÃO CELESTE QUE VAIS HABITAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('313', '313 - O QUE HABITA NO ABRIGO DE DEUS', 'O QUE HABITA NO ABRIGO DE DEUS MORARÁ SOB SOMBRAS DE AMOR, SOBRE ELE NÃO VIRÁ NENHUM MAL, EM SUAS ASAS FELIZ VIVERÁ.\n\nOH! EU QUERO HABITAR NO ABRIGO DE DEUS, SÓ ALI ACHAREI PAZ E PROFUNDO AMOR; MEU DESEJO É COM ELE COMUNHÃO DESFRUTAR E PRA SEMPRE O SEU NOME LOUVAR.', 'O QUE HABITA NO ABRIGO DE DEUS MORARÁ SOB SOMBRAS DE AMOR, SOBRE ELE NÃO VIRÁ NENHUM MAL, EM SUAS ASAS FELIZ VIVERÁ. OH! EU QUERO HABITAR NO ABRIGO DE DEUS, SÓ ALI ACHAREI PAZ E PROFUNDO AMOR; MEU DESEJO É COM ELE COMUNHÃO DESFRUTAR E PRA SEMPRE O SEU NOME LOUVAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('314', '314 - QUE IMPORTA', 'QUE IMPORTA SE O SOL NÃO BRILHAR, QUE IMPORTA SE DAS ESTRELAS O BRILHO CESSAR; O QUE IMPORTA PARA MIM, É DE JESUS NÃO ESQUECER; ELE É A RAZÃO DE TODO O MEU VIVER.\n\nÉ JESUS MINHA PAZ, É JESUS MINHA FORÇA, É JESUS MEU AMIGO, É JESUS A MINHA ALEGRIA, É JESUS MEU COMPANHEIRO, É JESUS MEU AMOR VERDADEIRO, ELE É A RAZÃO DE TODO O MEU VIVER.', 'QUE IMPORTA SE O SOL NÃO BRILHAR, QUE IMPORTA SE DAS ESTRELAS O BRILHO CESSAR; O QUE IMPORTA PARA MIM, É DE JESUS NÃO ESQUECER; ELE É A RAZÃO DE TODO O MEU VIVER. É JESUS MINHA PAZ, É JESUS MINHA FORÇA, É JESUS MEU AMIGO, É JESUS A MINHA ALEGRIA, É JESUS MEU COMPANHEIRO, É JESUS MEU AMOR VERDADEIRO, ELE É A RAZÃO DE TODO O MEU VIVER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('315', '315 - SE EM PROFUNDO ABISMO TE ENCONTRAS', 'SE EM PROFUNDO ABISMO TE ENCONTRAS E CAMINHAS SEM TERES DIREÇÃO, NA ESTRADA NÃO TENS O AUXÍLIO, NÃO ENCONTRAS QUEM TE DÊ A MÃO; SE A TRISTEZA SEMPRE TE DOMINA, SE O TEU FARDO É PESADO, ENTÃO, MESMO QUE TUDO VENHA A PERECER, NÃO, NÃO TEMAS, DEUS TE DÁ A MÃO.\n\nCORO DEUS SEGURA EM TUA MÃO (BIS) MESMO QUE, COM LUTAS, NA ESTRADA, DEUS SEGURA EM TUA MÃO. INSTRUMENTOS\nBIS\n\nSE O SOL PARA TI JÁ NÃO BRILHA E OS SEUS RAIOS NÃO REFLETEM MAIS; SE O AMOR EM TUA VIDA NÃO HÁ E CAMINHAS EM BUSCA DE PAZ; SE A TRISTEZA SEMPRE TE DOMINA, SE O TEU FARDO É PESADO, ENTÃO, MESMO QUE TUDO VENHA A PERECER, NÃO, NÃO TEMAS, DEUS TE DÁ A MÃO.\n\nCORO DEUS SEGURA EM TUA MÃO (BIS) MESMO QUE, COM LUTAS, NA ESTRADA, DEUS SEGURA EM TUA MÃO.\nBIS', 'SE EM PROFUNDO ABISMO TE ENCONTRAS E CAMINHAS SEM TERES DIREÇÃO, NA ESTRADA NÃO TENS O AUXÍLIO, NÃO ENCONTRAS QUEM TE DÊ A MÃO; SE A TRISTEZA SEMPRE TE DOMINA, SE O TEU FARDO É PESADO, ENTÃO, MESMO QUE TUDO VENHA A PERECER, NÃO, NÃO TEMAS, DEUS TE DÁ A MÃO. CORO DEUS SEGURA EM TUA MÃO  MESMO QUE, COM LUTAS, NA ESTRADA, DEUS SEGURA EM TUA MÃO. INSTRUMENTOS SE O SOL PARA TI JÁ NÃO BRILHA E OS SEUS RAIOS NÃO REFLETEM MAIS; SE O AMOR EM TUA VIDA NÃO HÁ E CAMINHAS EM BUSCA DE PAZ; SE A TRISTEZA SEMPRE TE DOMINA, SE O TEU FARDO É PESADO, ENTÃO, MESMO QUE TUDO VENHA A PERECER, NÃO, NÃO TEMAS, DEUS TE DÁ A MÃO. CORO DEUS SEGURA EM TUA MÃO  MESMO QUE, COM LUTAS, NA ESTRADA, DEUS SEGURA EM TUA MÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('316', '316 - A VIDA NO MUNDO É CRUEL', 'A VIDA NO MUNDO É CRUEL, TORMENTOS E LUTAS SEM FIM; CAMINHO ALMEJANDO O CÉU; JESUS, TU TE LEMBRAS DE MIM!\n\nCORO JESUS, TU TE LEMBRAS DE MIM! (BIS) NA VIDA, NA MORTE E NA DOR: JESUS, TU TE LEMBRAS DE MIM! NAVEGANDO VAI MINHA NAU NO MAR DESTA VIDA AQUI; ABRIGA-ME NO TEMPORAL; JESUS, TU TE LEMBRAS DE MIM!\n\nCORO JESUS, TU TE LEMBRAS DE MIM! (BIS) NA VIDA, NA MORTE E NA DOR: JESUS, TU TE LEMBRAS DE MIM! JESUS, O QUE HEI DE FAZER SE O MUNDO ESTÁ CONTRA MIM? ENVIA-ME O TEU PODER; JESUS, TU TE LEMBRAS DE MIM!', 'A VIDA NO MUNDO É CRUEL, TORMENTOS E LUTAS SEM FIM; CAMINHO ALMEJANDO O CÉU; JESUS, TU TE LEMBRAS DE MIM! CORO JESUS, TU TE LEMBRAS DE MIM!  NA VIDA, NA MORTE E NA DOR: JESUS, TU TE LEMBRAS DE MIM! NAVEGANDO VAI MINHA NAU NO MAR DESTA VIDA AQUI; ABRIGA-ME NO TEMPORAL; JESUS, TU TE LEMBRAS DE MIM! CORO JESUS, TU TE LEMBRAS DE MIM!  NA VIDA, NA MORTE E NA DOR: JESUS, TU TE LEMBRAS DE MIM! JESUS, O QUE HEI DE FAZER SE O MUNDO ESTÁ CONTRA MIM? ENVIA-ME O TEU PODER; JESUS, TU TE LEMBRAS DE MIM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('317', '317 - QUANDO VEM NOS OLHOS UMA LÁGRIMA', 'QUANDO VEM NOS OLHOS UMA LÁGRIMA, QUANDO NÃO CONSIGO MAIS SORRIR, DÁ-ME, MEU SENHOR, O TEU OLHAR, DÁ-ME, MEU SENHOR, O TEU PERDÃO.\n\nCORO (2X) MARANATA, ORA VEM, MEU SENHOR, MARANATA, VEM VENCER A MINHA DOR. SINTO ENTÃO A TUA MÃO ME TOCAR APLACANDO O FUROR DA TRIBULAÇÃO, MAIS UM POUCO E NÃO MAIS CHORAREI, MAIS UM POUCO E NAS NUVENS TE VEREI.', 'QUANDO VEM NOS OLHOS UMA LÁGRIMA, QUANDO NÃO CONSIGO MAIS SORRIR, DÁ-ME, MEU SENHOR, O TEU OLHAR, DÁ-ME, MEU SENHOR, O TEU PERDÃO. CORO  MARANATA, ORA VEM, MEU SENHOR, MARANATA, VEM VENCER A MINHA DOR. SINTO ENTÃO A TUA MÃO ME TOCAR APLACANDO O FUROR DA TRIBULAÇÃO, MAIS UM POUCO E NÃO MAIS CHORAREI, MAIS UM POUCO E NAS NUVENS TE VEREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('318', '318 - QUANDO A ANGÚSTIA VEM', 'QUANDO A ANGÚSTIA VEM E NOS OLHOS LÁGRIMAS, PARECE, ENTÃO, QUE TU NÃO PODES SUPORTAR; JESUS TE AJUDARÁ E TUDO ELE VERÁ, AS LÁGRIMAS FALAM, DEUS OUVIRÁ.\n\nCORO DEUS VÊ NOS OLHOS O AFLITO CORAÇÃO, É O SENHOR QUEM VÊ AS LÁGRIMAS EM ORAÇÃO; COM O HOMEM ELE VIVERÁ, PELAS MÃOS O TOMARÁ. AS LÁGRIMAS FALAM, DEUS OUVIRÁ.\n\nQUANDO NÃO PUDERES MAIS SONHO ALGUM REALIZAR, E TUDO, ENFIM, DIANTE DE TI DESMORONAR, MEU DEUS NÃO TE ESQUECERÁ, PROMESSAS CUMPRIRÁ, AS LÁGRIMAS FALAM, DEUS OUVIRÁ.\n\nCORO DEUS VÊ NOS OLHOS O AFLITO CORAÇÃO, É O SENHOR QUEM VÊ AS LÁGRIMAS EM ORAÇÃO; COM O HOMEM ELE VIVERÁ, PELAS MÃOS O TOMARÁ. AS LÁGRIMAS FALAM, DEUS OUVIRÁ.', 'QUANDO A ANGÚSTIA VEM E NOS OLHOS LÁGRIMAS, PARECE, ENTÃO, QUE TU NÃO PODES SUPORTAR; JESUS TE AJUDARÁ E TUDO ELE VERÁ, AS LÁGRIMAS FALAM, DEUS OUVIRÁ. CORO DEUS VÊ NOS OLHOS O AFLITO CORAÇÃO, É O SENHOR QUEM VÊ AS LÁGRIMAS EM ORAÇÃO; COM O HOMEM ELE VIVERÁ, PELAS MÃOS O TOMARÁ. AS LÁGRIMAS FALAM, DEUS OUVIRÁ. QUANDO NÃO PUDERES MAIS SONHO ALGUM REALIZAR, E TUDO, ENFIM, DIANTE DE TI DESMORONAR, MEU DEUS NÃO TE ESQUECERÁ, PROMESSAS CUMPRIRÁ, AS LÁGRIMAS FALAM, DEUS OUVIRÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('319', '319 - MAIS PERTO QUERO ESTAR', 'MAIS PERTO QUERO ESTAR, MEU DEUS DE TI, INDA QUE SEJA A DOR QUE ME UNA A TI! SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI!\n\nANDANDO TRISTE AQUI, NA SOLIDÃO, PAZ E DESCANSO A MIM TEUS BRAÇOS DÃO. SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI!\n\nMINH’ALMA CANTARÁ A TI, SENHOR, CHEIA DE GRATIDÃO POR TEU AMOR. SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI!\n\nE QUANDO CRISTO, ENFIM, ME VIER CHAMAR, NOS CÉUS COM O SENHOR IREI MORAR. ENTÃO ME ALEGRAREI, PERTO DE TI, MEU REI, PERTO DE TI, MEU REI, MEU DEUS, DE TI!', 'MAIS PERTO QUERO ESTAR, MEU DEUS DE TI, INDA QUE SEJA A DOR QUE ME UNA A TI! SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI! ANDANDO TRISTE AQUI, NA SOLIDÃO, PAZ E DESCANSO A MIM TEUS BRAÇOS DÃO. SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI! MINH’ALMA CANTARÁ A TI, SENHOR, CHEIA DE GRATIDÃO POR TEU AMOR. SEMPRE HEI DE SUPLICAR: MAIS PERTO QUERO ESTAR, MAIS PERTO QUERO ESTAR, MEU DEUS, DE TI! E QUANDO CRISTO, ENFIM, ME VIER CHAMAR, NOS CÉUS COM O SENHOR IREI MORAR. ENTÃO ME ALEGRAREI, PERTO DE TI, MEU REI, PERTO DE TI, MEU REI, MEU DEUS, DE TI!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('320', '320 - BOM É O SENHOR', 'BOM É O SENHOR PARA AQUELE QUE O BUSCA E QUE ESPERA EM SILÊNCIO A SUA SALVAÇÃO. CORO PORQUE O SENHOR NÃO REJEITARÁ PRA SEMPRE, NEM TEM PRAZER NA NOSSA DOR; BOM É O SENHOR.\nBIS\n\nPOIS AINDA QUE ENTRISTEÇA USARÁ DE COMPAIXÃO, SEGUNDO A SUA MISERICÓRDIA. CORO PORQUE O SENHOR NÃO REJEITARÁ PRA SEMPRE, NEM TEM PRAZER NA NOSSA DOR; BOM É O SENHOR (3X)\nBIS', 'BOM É O SENHOR PARA AQUELE QUE O BUSCA E QUE ESPERA EM SILÊNCIO A SUA SALVAÇÃO. CORO PORQUE O SENHOR NÃO REJEITARÁ PRA SEMPRE, NEM TEM PRAZER NA NOSSA DOR; BOM É O SENHOR. POIS AINDA QUE ENTRISTEÇA USARÁ DE COMPAIXÃO, SEGUNDO A SUA MISERICÓRDIA. CORO PORQUE O SENHOR NÃO REJEITARÁ PRA SEMPRE, NEM TEM PRAZER NA NOSSA DOR; BOM É O SENHOR',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('321', '321 - QUÃO BONDOSO AMIGO É CRISTO', 'QUÃO BONDOSO AMIGO É CRISTO! CARREGOU A NOSSA DOR E NOS MANDA QUE LEVEMOS OS CUIDADOS AO SENHOR; FALTA AO CORAÇÃO DORIDO GOZO, PAZ, CONSOLAÇÃO? ISSO É PORQUE NÃO LEVAMOS TUDO A DEUS EM ORAÇÃO.\n\nESTÁS FRACO E CARREGADO DE CUIDADOS E TEMOR? A JESUS, REFÚGIO ETERNO, VAI COM FÉ TEU MAL EXPOR. TEUS AMIGOS TE DESPREZAM? CONTA-LHE ISSO EM ORAÇÃO E COM SEU AMOR TÃO TERNO PAZ TERÁS NO CORAÇÃO.\n\nCRISTO É VERDADEIRO AMIGO, DISTO PROVA NOS MOSTROU QUANDO, PRA LEVAR CONSIGO, AO CULPADO, ENCARNOU; DERRAMOU SEU SANGUE PURO, NOSSA MANCHA PRA LAVAR, GOZO EM VIDA E NO FUTURO NELE PODEMOS ALCANÇAR.', 'QUÃO BONDOSO AMIGO É CRISTO! CARREGOU A NOSSA DOR E NOS MANDA QUE LEVEMOS OS CUIDADOS AO SENHOR; FALTA AO CORAÇÃO DORIDO GOZO, PAZ, CONSOLAÇÃO? ISSO É PORQUE NÃO LEVAMOS TUDO A DEUS EM ORAÇÃO. ESTÁS FRACO E CARREGADO DE CUIDADOS E TEMOR? A JESUS, REFÚGIO ETERNO, VAI COM FÉ TEU MAL EXPOR. TEUS AMIGOS TE DESPREZAM? CONTA-LHE ISSO EM ORAÇÃO E COM SEU AMOR TÃO TERNO PAZ TERÁS NO CORAÇÃO. CRISTO É VERDADEIRO AMIGO, DISTO PROVA NOS MOSTROU QUANDO, PRA LEVAR CONSIGO, AO CULPADO, ENCARNOU; DERRAMOU SEU SANGUE PURO, NOSSA MANCHA PRA LAVAR, GOZO EM VIDA E NO FUTURO NELE PODEMOS ALCANÇAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('322', '322 - HÁ UM AMIGO SEMPRE JUNTO', 'HÁ UM AMIGO SEMPRE JUNTO A MIM, HÁ UM AMIGO QUE ME OUVE, HÁ UM AMIGO TÃO QUERIDO, SIM, PRECIOSO ELE É.\n\nCORO QUANDO OUÇO A VOZ DO VENTO, QUANDO TODA A AFLIÇÃO ME SOBREVEM, ELE DIZ: “ESTOU CONTIGO”, PRECIOSO É JESUS. SUAS PROMESSAS ELE CUMPRE, NÃO HÁ COMO ELE FALHAR; MINHA PARTE QUERO EU FAZER, PRECIOSO ELE É.', 'HÁ UM AMIGO SEMPRE JUNTO A MIM, HÁ UM AMIGO QUE ME OUVE, HÁ UM AMIGO TÃO QUERIDO, SIM, PRECIOSO ELE É. CORO QUANDO OUÇO A VOZ DO VENTO, QUANDO TODA A AFLIÇÃO ME SOBREVEM, ELE DIZ: ESTOU CONTIGO, PRECIOSO É JESUS. SUAS PROMESSAS ELE CUMPRE, NÃO HÁ COMO ELE FALHAR; MINHA PARTE QUERO EU FAZER, PRECIOSO ELE É.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('323', '323 - DEUS ESTÁ PRESENTE SEMPRE PRONTO A NOS AJUDAR', 'DEUS ESTÁ PRESENTE SEMPRE PRONTO A NOS AJUDAR, COM SEUS OUVIDOSSEMPRE VOLTADOS A NOS ESCUTAR; BUSCA A JESUS PARA O TEU CORAÇÃO ALEGRAR, PARA ENCHER TEU CORAÇÃO DE GOZO E PAZ.\n\nCORO AGORA SOMOS POVO TRANSFORMADO PELA GRAÇA DE CRISTO JESUS, PELO SEU GRANDIOSO AMOR, SOMOS POVO DE CRISTO JESUS, O SENHOR. INSTRUMENTOS\n\nESTAMOS CONSCIENTES DE SUA SANTA PRESENÇA AQUI, COM SEU PODER E COM GRAÇA ELE VAI OPERAR; NÃO FICARÁ QUALQUER LUTA OU TRISTEZA AQUI POIS O SENHOR NOS LEVARÁ A SENTIR O SEU AMOR.\n\nCORO AGORA SOMOS POVO TRANSFORMADO PELA GRAÇA DE CRISTO JESUS, PELO SEU GRANDIOSO AMOR, SOMOS POVO DE CRISTO JESUS, O SENHOR. FINAL: SOMOS POVO DE CRISTO JESUS, O SENHOR.', 'DEUS ESTÁ PRESENTE SEMPRE PRONTO A NOS AJUDAR, COM SEUS OUVIDOSSEMPRE VOLTADOS A NOS ESCUTAR; BUSCA A JESUS PARA O TEU CORAÇÃO ALEGRAR, PARA ENCHER TEU CORAÇÃO DE GOZO E PAZ. CORO AGORA SOMOS POVO TRANSFORMADO PELA GRAÇA DE CRISTO JESUS, PELO SEU GRANDIOSO AMOR, SOMOS POVO DE CRISTO JESUS, O SENHOR. INSTRUMENTOS ESTAMOS CONSCIENTES DE SUA SANTA PRESENÇA AQUI, COM SEU PODER E COM GRAÇA ELE VAI OPERAR; NÃO FICARÁ QUALQUER LUTA OU TRISTEZA AQUI POIS O SENHOR NOS LEVARÁ A SENTIR O SEU AMOR. CORO AGORA SOMOS POVO TRANSFORMADO PELA GRAÇA DE CRISTO JESUS, PELO SEU GRANDIOSO AMOR, SOMOS POVO DE CRISTO JESUS, O SENHOR.  SOMOS POVO DE CRISTO JESUS, O SENHOR.',  1);
