INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('328', '328 - CONSOLAI, CONSOLAI O MEU POVO', 'CORO “CONSOLAI, CONSOLAI O MEU POVO,” DIZ O VOSSO DEUS; “PREPARAI, PREPARAI O CAMINHO DO SENHOR.”\n\nFALAI BENIGNAMENTE A JERUSALÉM: SUA INIQÜIDADE PERDOADA ESTÁ E JÁ RECEBEU EM DOBRO DO SENHOR POR TODOS OS SEUS PECADOS.\n\nCORO “CONSOLAI, CONSOLAI O MEU POVO,” DIZ O VOSSO DEUS; “PREPARAI, PREPARAI O CAMINHO DO SENHOR.”\n\nENDIREITAI NO ERMO VEREDA AO NOSSO DEUS E A GLÓRIA DO SENHOR SE MANIFESTARÁ, LEVANTA A TUA VOZ, PROCLAMA FORTEMENTE: EIS AQUI ESTÁ O VOSSO DEUS.\n\nCORO “CONSOLAI, CONSOLAI O MEU POVO,” DIZ O VOSSO DEUS; “PREPARAI, PREPARAI O CAMINHO DO SENHOR.”', 'CORO CONSOLAI, CONSOLAI O MEU POVO, DIZ O VOSSO DEUS; PREPARAI, PREPARAI O CAMINHO DO SENHOR. FALAI BENIGNAMENTE A JERUSALÉM: SUA INIQÜIDADE PERDOADA ESTÁ E JÁ RECEBEU EM DOBRO DO SENHOR POR TODOS OS SEUS PECADOS. ENDIREITAI NO ERMO VEREDA AO NOSSO DEUS E A GLÓRIA DO SENHOR SE MANIFESTARÁ, LEVANTA A TUA VOZ, PROCLAMA FORTEMENTE: EIS AQUI ESTÁ O VOSSO DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('329', '329 - ATUA EM NOSSO MEIO', 'ATUA EM NOSSO MEIO, Ó SENHOR, MANIFESTA O TEU PODER CONSOLADOR, CONFORTA OS CORAÇÕES AFLITOS E NECESSITADOS DA TUA SALVAÇÃO.\nBIS\n\nQUEREMOS SEMPRE TE LOUVAR, Ó SENHOR, REI DOS EXÉRCITOS, MOSTRAR O TEU AMOR ATRAVÉS DA NOSSA ADORAÇÃO. INSTRUMENTOS\nBIS\n\nATUA EM NOSSO MEIO, Ó SENHOR, (GLÓRIA AO SENHOR NAS ALTURAS) VARÕES MANIFESTA O TEU PODER CONSOLADOR, (GLÓRIA AO SENHOR) VARÕES GLÓRIA AO SENHOR NAS ALTURAS!', 'ATUA EM NOSSO MEIO, Ó SENHOR, MANIFESTA O TEU PODER CONSOLADOR, CONFORTA OS CORAÇÕES AFLITOS E NECESSITADOS DA TUA SALVAÇÃO. QUEREMOS SEMPRE TE LOUVAR, Ó SENHOR, REI DOS EXÉRCITOS, MOSTRAR O TEU AMOR ATRAVÉS DA NOSSA ADORAÇÃO. INSTRUMENTOS ATUA EM NOSSO MEIO, Ó SENHOR, (GLÓRIA AO SENHOR NAS ALTURAS) VARÕES MANIFESTA O TEU PODER CONSOLADOR, (GLÓRIA AO SENHOR) VARÕES GLÓRIA AO SENHOR NAS ALTURAS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('330', '330 - EIS AÍ O DIA ACEITÁVEL', 'EIS AÍ O DIA ACEITÁVEL DO SENHOR! ELE VEIO CONSOLAR TODOS OS QUE CHORAM E OS CONTRITOS DE CORAÇÃO; ELE VAI TE DAR VESTES DE LOUVOR AO INVÉS DE ESPÍRITO ANGUSTIADO, ÓLEO DE ALEGRIA AO INVÉS DE PRANTO.\n\nCORO (2X) OUVI A VOZ DO SENHOR, EU VI OS ANJOS ANUNCIAREM, SENTI A SUA MÃO A ME TOCAR, SEU AMOR SOBRE O MEU SER.\n\nAINDA QUE OS TEUS PECADOS FOSSEM COMO CARMESIM, O SENHOR PODE OS TORNAR ALVOS COMO A BRANCA LÃ; E COMO O NOIVO QUE SE ALEGRA DA NOIVA, O SENHOR SE ALEGRARÁ DE TI, ELE VAI TE DAR UMA COROA AO INVÉS DE PRANTO.\n\nCORO (2X) OUVI A VOZ DO SENHOR, EU VI OS ANJOS ANUNCIAREM, SENTI A SUA MÃO A ME TOCAR, SEU AMOR SOBRE O MEU SER.', 'EIS AÍ O DIA ACEITÁVEL DO SENHOR! ELE VEIO CONSOLAR TODOS OS QUE CHORAM E OS CONTRITOS DE CORAÇÃO; ELE VAI TE DAR VESTES DE LOUVOR AO INVÉS DE ESPÍRITO ANGUSTIADO, ÓLEO DE ALEGRIA AO INVÉS DE PRANTO. CORO  OUVI A VOZ DO SENHOR, EU VI OS ANJOS ANUNCIAREM, SENTI A SUA MÃO A ME TOCAR, SEU AMOR SOBRE O MEU SER. AINDA QUE OS TEUS PECADOS FOSSEM COMO CARMESIM, O SENHOR PODE OS TORNAR ALVOS COMO A BRANCA LÃ; E COMO O NOIVO QUE SE ALEGRA DA NOIVA, O SENHOR SE ALEGRARÁ DE TI, ELE VAI TE DAR UMA COROA AO INVÉS DE PRANTO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('331', '331 - SE DEUS É POR NÓS', 'SE DEUS É POR NÓS, QUEM SERÁ CONTRA NÓS? AQUELE QUE NEM MESMO O SEU FILHO POUPOU, ANTES O ENTREGOU À CRUZ.\n\nSE O SENHOR CONOSCO ESTÁ, QUEM NOS CONDENARÁ? AQUELE QUE NOS ESCOLHEU É QUEM NOS JUSTIFICA.\n\nCORO JESUS RESSUSCITOU, RESSUSCITOU, VIVO ESTÁ AQUI ENTRE NÓS; QUEM IRÁ NOS SEPARAR DO AMOR DE DEUS? NEM A MORTE OU TRIBULAÇÃO, NEM ALTURA OU PROFUNDIDADE PODEM NOS SEPARAR DO AMOR DE DEUS, QUE ESTÁ EM CRISTO JESUS, CRISTO JESUS, NOSSO SENHOR, CRISTO JESUS.\n\nMAS EM TODAS ESTAS COISAS SOMOS MAIS DO QUE VENCEDORES POR AQUELE QUE NOS AMOU E A JESUS NOS REVELOU.\n\nCORO JESUS RESSUSCITOU, RESSUSCITOU, VIVO ESTÁ AQUI ENTRE NÓS; QUEM IRÁ NOS SEPARAR DO AMOR DE DEUS? NEM A MORTE OU TRIBULAÇÃO, NEM ALTURA OU PROFUNDIDADE PODEM NOS SEPARAR DO AMOR DE DEUS, QUE ESTÁ EM CRISTO JESUS, CRISTO JESUS, NOSSO SENHOR, CRISTO JESUS.\n\nFINAL: NOSSO SALVADOR, CRISTO JESUS, NOSSO SALVADOR.', 'SE DEUS É POR NÓS, QUEM SERÁ CONTRA NÓS? AQUELE QUE NEM MESMO O SEU FILHO POUPOU, ANTES O ENTREGOU À CRUZ. SE O SENHOR CONOSCO ESTÁ, QUEM NOS CONDENARÁ? AQUELE QUE NOS ESCOLHEU É QUEM NOS JUSTIFICA. CORO JESUS RESSUSCITOU, RESSUSCITOU, VIVO ESTÁ AQUI ENTRE NÓS; QUEM IRÁ NOS SEPARAR DO AMOR DE DEUS? NEM A MORTE OU TRIBULAÇÃO, NEM ALTURA OU PROFUNDIDADE PODEM NOS SEPARAR DO AMOR DE DEUS, QUE ESTÁ EM CRISTO JESUS, CRISTO JESUS, NOSSO SENHOR, CRISTO JESUS. MAS EM TODAS ESTAS COISAS SOMOS MAIS DO QUE VENCEDORES POR AQUELE QUE NOS AMOU E A JESUS NOS REVELOU. NOSSO SALVADOR, CRISTO JESUS, NOSSO SALVADOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('332', '332 - O REI E EU', 'O REI E EU TRILHAMOS NA ESTRADA DA VIDA ONDE MUITAS ALMAS PASSANDO VÃO; O GRANDE SER E EU, UM POBRE MENDIGO, ANDAMOS JUNTOS, O REI E EU.\n\nCORO PORQUE ELE CUIDA DE MIM É UM MISTÉRIO, ELE TEM O MUNDO EM SUAS MÃOS, MAS QUEM SOU EU? ASSIM MINH’ALMA MUITO SE MARAVILHA DE ESTARMOS JUNTOS, O REI E EU.\n\nO REI E EU, ERA MUITO ESTRANHO, EU ANDAVA SÓ SEM SABER POR QUE; ATÉ QUE UM DIA SUAS MÃOS ME ABRAÇARAM, NÃO MAIS ESTRANHOS, JESUS E EU.\n\nCORO PORQUE ELE CUIDA DE MIM É UM MISTÉRIO, ELE TEM O MUNDO EM SUAS MÃOS, MAS QUEM SOU EU? ASSIM MINH’ALMA MUITO SE MARAVILHA DE ESTARMOS JUNTOS, O REI E EU.', 'O REI E EU TRILHAMOS NA ESTRADA DA VIDA ONDE MUITAS ALMAS PASSANDO VÃO; O GRANDE SER E EU, UM POBRE MENDIGO, ANDAMOS JUNTOS, O REI E EU. CORO PORQUE ELE CUIDA DE MIM É UM MISTÉRIO, ELE TEM O MUNDO EM SUAS MÃOS, MAS QUEM SOU EU? ASSIM MINH’ALMA MUITO SE MARAVILHA DE ESTARMOS JUNTOS, O REI E EU. O REI E EU, ERA MUITO ESTRANHO, EU ANDAVA SÓ SEM SABER POR QUE; ATÉ QUE UM DIA SUAS MÃOS ME ABRAÇARAM, NÃO MAIS ESTRANHOS, JESUS E EU.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('333', '333 - SE TE ENCONTRARES TRISTE', 'SE TE ENCONTRARES TRISTE E CANSADO E SE VACILARES NO TEU CAMINHAR, NÃO TE DETENHAS, POIS NOS TEUS PASSOS, EM TEUS CAMINHOS, DEUS PROVERÁ; NÃO DESANIMES, À TUA VOLTA ELE ESTARÁ, DEUS PROVERÁ.\n\nCORO CONFIA E ANDA, SEMPRE ORANDO, SEMPRE CANTANDO, DEUS PROVERÁ.\nBIS\n\nNÃO TE DETENHAS, (DEUS PROVERÁ) VARÕES POIS NOS TEUS PASSOS, (DEUS PROVERÁ) VARÕES EM TEUS CAMINHOS, DEUS PROVERÁ; NÃO DESANIMES, (DEUS PROVERÁ) VARÕES À TUA VOLTA (DEUS PROVERÁ) VARÕES ELE ESTARÁ, DEUS PROVERÁ. INSTRUMENTOS\n\nCORO CONFIA E ANDA, SEMPRE ORANDO, SEMPRE CANTANDO, DEUS PROVERÁ.\nBIS', 'SE TE ENCONTRARES TRISTE E CANSADO E SE VACILARES NO TEU CAMINHAR, NÃO TE DETENHAS, POIS NOS TEUS PASSOS, EM TEUS CAMINHOS, DEUS PROVERÁ; NÃO DESANIMES, À TUA VOLTA ELE ESTARÁ, DEUS PROVERÁ. CORO CONFIA E ANDA, SEMPRE ORANDO, SEMPRE CANTANDO, DEUS PROVERÁ. NÃO TE DETENHAS, (DEUS PROVERÁ) VARÕES POIS NOS TEUS PASSOS, (DEUS PROVERÁ) VARÕES EM TEUS CAMINHOS, DEUS PROVERÁ; NÃO DESANIMES, (DEUS PROVERÁ) VARÕES À TUA VOLTA (DEUS PROVERÁ) VARÕES ELE ESTARÁ, DEUS PROVERÁ. INSTRUMENTOS',  1);
