INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('335', '335 - NÃO DESANIMES! DEUS PROVERÁ', 'NÃO DESANIMES! DEUS PROVERÁ, DEUS VELARÁ POR TI; SOB SUAS ASAS TE ACOLHERÁ, DEUS VELARÁ POR TI.\n\nCORO DEUS CUIDARÁ DE TI, NA TUA DOR, COM TODO AMOR; JAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI.\n\nSE NO TEU PEITO VIBRAR A DOR, DEUS VELARÁ POR TI; TU JÁ PROVASTE SEU GRANDE AMOR, DEUS VELARÁ POR TI.\n\nCORO DEUS CUIDARÁ DE TI, NA TUA DOR, COM TODO AMOR; JAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI.\n\nNOS DESALENTOS, NAS PROVAÇÕES, DEUS VELARÁ POR TI; NAS DESVENTURAS, NAS TENTAÇÕES, DEUS VELARÁ POR TI.\n\nCORO DEUS CUIDARÁ DE TI, NA TUA DOR, COM TODO AMOR; JAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI.\n\nCOMO ESTIVERES, NÃO TEMAS, VEM! DEUS VELARÁ POR TI; TEU PAI BONDOSO TE ESPERA ALÉM, DEUS VELARÁ POR TI.\n\nCORO DEUS CUIDARÁ DE TI, NA TUA DOR, COM TODO AMOR; JAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI.', 'NÃO DESANIMES! DEUS PROVERÁ, DEUS VELARÁ POR TI; SOB SUAS ASAS TE ACOLHERÁ, DEUS VELARÁ POR TI. CORO DEUS CUIDARÁ DE TI, NA TUA DOR, COM TODO AMOR; JAMAIS TE DEIXARÁ, DEUS VELARÁ POR TI. SE NO TEU PEITO VIBRAR A DOR, DEUS VELARÁ POR TI; TU JÁ PROVASTE SEU GRANDE AMOR, DEUS VELARÁ POR TI. NOS DESALENTOS, NAS PROVAÇÕES, DEUS VELARÁ POR TI; NAS DESVENTURAS, NAS TENTAÇÕES, DEUS VELARÁ POR TI. COMO ESTIVERES, NÃO TEMAS, VEM! DEUS VELARÁ POR TI; TEU PAI BONDOSO TE ESPERA ALÉM, DEUS VELARÁ POR TI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('336', '336 - HÁ ALGUÉM QUE CUIDA DE TI', 'CORO HÁ ALGUÉM QUE CUIDA DE TI, (BIS) E QUANDO SENTES SOLIDÃO E UM VAZIO NO CORAÇÃO, LEMBRA QUE ALGUÉM CUIDA DE TI.\n\nSE AS NUVENS TE ENCOBREM E NÃO QUEREM PASSAR, SE O TEU BARCO SE AGITA COM AS ONDAS DO MAR, NÃO TE ESQUEÇAS: ALGUÉM CUIDA DE TI SEM CESSAR, COM SEU AMOR ELE VEM TE AJUDAR.\n\nCORO HÁ ALGUÉM QUE CUIDA DE TI, (BIS) E QUANDO SENTES SOLIDÃO E UM VAZIO NO CORAÇÃO, LEMBRA QUE ALGUÉM CUIDA DE TI.\n\nQUANDO LEVAS TUDO A ELE EM ORAÇÃO E COM LÁGRIMAS SINCERAS NO CORAÇÃO, CRISTO VÊ E SE ALEGRA DE TE AJUDAR E SUA PAZ ELE VEM DERRAMAR.\n\nCORO HÁ ALGUÉM QUE CUIDA DE TI, (BIS) E QUANDO SENTES SOLIDÃO E UM VAZIO NO CORAÇÃO, LEMBRA QUE ALGUÉM CUIDA DE TI.', 'CORO HÁ ALGUÉM QUE CUIDA DE TI,  E QUANDO SENTES SOLIDÃO E UM VAZIO NO CORAÇÃO, LEMBRA QUE ALGUÉM CUIDA DE TI. SE AS NUVENS TE ENCOBREM E NÃO QUEREM PASSAR, SE O TEU BARCO SE AGITA COM AS ONDAS DO MAR, NÃO TE ESQUEÇAS: ALGUÉM CUIDA DE TI SEM CESSAR, COM SEU AMOR ELE VEM TE AJUDAR. QUANDO LEVAS TUDO A ELE EM ORAÇÃO E COM LÁGRIMAS SINCERAS NO CORAÇÃO, CRISTO VÊ E SE ALEGRA DE TE AJUDAR E SUA PAZ ELE VEM DERRAMAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('337', '337 - O MESMO DEUS', 'O MESMO DEUS QUE ONTEM DE MIM CUIDOU, VIVO ESTÁ HOJE EM MEU VIVER, MISERICÓRDIAS ME SEGUIRÃO CADA DIA DO MEU VIVER; NADA TENHO QUE TEMER POIS JESUS ME GUARDARÁ, NADA TENHO QUE TEMER POIS JESUS ME GUIARÁ; E ME LEVARÁ À FONTE DA VIDA E NO CÉU ME RECEBERÁ.\n\nO MESMO DEUS QUE ONTEM DE MIM CUIDOU, VIVO ESTÁ HOJE EM MEU VIVER, MISERICÓRDIAS ME SEGUIRÃO CADA DIA DO MEU VIVER; NADA TENHO QUE TEMER POIS JESUS ME GUARDARÁ, NADA TENHO QUE TEMER POIS JESUS ME GUIARÁ; E ME LEVARÁ À FONTE DA VIDA E NO CÉU ME RECEBERÁ (3X NO FINAL)', 'O MESMO DEUS QUE ONTEM DE MIM CUIDOU, VIVO ESTÁ HOJE EM MEU VIVER, MISERICÓRDIAS ME SEGUIRÃO CADA DIA DO MEU VIVER; NADA TENHO QUE TEMER POIS JESUS ME GUARDARÁ, NADA TENHO QUE TEMER POIS JESUS ME GUIARÁ; E ME LEVARÁ À FONTE DA VIDA E NO CÉU ME RECEBERÁ. O MESMO DEUS QUE ONTEM DE MIM CUIDOU, VIVO ESTÁ HOJE EM MEU VIVER, MISERICÓRDIAS ME SEGUIRÃO CADA DIA DO MEU VIVER; NADA TENHO QUE TEMER POIS JESUS ME GUARDARÁ, NADA TENHO QUE TEMER POIS JESUS ME GUIARÁ; E ME LEVARÁ À FONTE DA VIDA E NO CÉU ME RECEBERÁ ( NO FINAL)',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('338', '338 - DIVINO COMPANHEIRO', 'DIVINO COMPANHEIRO NO CAMINHO, TUA PRESENÇA SINTO LOGO AO TRANSITAR, EIS QUE DISSIPASTE TODA SOMBRA, JÁ TENHO LUZ, A LUZ BENDITA DO AMOR.\n\nCORO FICA, SENHOR, JÁ SE FAZ TARDE, TENS MEU CORAÇÃO PARA POUSAR; FAZE EM MIM MORADA PERMANENTE, FICA, SENHOR, FICA, SENHOR, MEU SALVADOR.\n\nA SOMBRA DA NOITE SE APROXIMA, E NELA O TENTADOR PODE ATACAR; NÃO, NÃO ME DEIXES SÓ, NO CAMINHO, AJUDA-ME, AJUDA-ME AO CÉU CHEGAR.\n\nCORO FICA, SENHOR, JÁ SE FAZ TARDE, TENS MEU CORAÇÃO PARA POUSAR; FAZE EM MIM MORADA PERMANENTE, FICA, SENHOR, FICA, SENHOR, MEU SALVADOR.', 'DIVINO COMPANHEIRO NO CAMINHO, TUA PRESENÇA SINTO LOGO AO TRANSITAR, EIS QUE DISSIPASTE TODA SOMBRA, JÁ TENHO LUZ, A LUZ BENDITA DO AMOR. CORO FICA, SENHOR, JÁ SE FAZ TARDE, TENS MEU CORAÇÃO PARA POUSAR; FAZE EM MIM MORADA PERMANENTE, FICA, SENHOR, FICA, SENHOR, MEU SALVADOR. A SOMBRA DA NOITE SE APROXIMA, E NELA O TENTADOR PODE ATACAR; NÃO, NÃO ME DEIXES SÓ, NO CAMINHO, AJUDA-ME, AJUDA-ME AO CÉU CHEGAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('339', '339 - PARA OS MONTES OLHAREI', 'PARA OS MONTES OLHAREI, DE ONDE VEM A SALVAÇÃO? MEU SOCORRO VEM DE DEUS, O SENHOR DA CRIAÇÃO.\n\nCORO O SENHOR É QUEM TE GUARDA, GUARDARÁ DE TODO MAL; TUA ENTRADA E SAÍDA, DESDE AGORA ATÉ O FINAL. O SENHOR É QUEM TE GUARDA, SEMPRE À TUA DESTRA ESTÁ; NEM DE DIA, NEM DE NOITE, MAL ALGUM TE OFENDERÁ.\n\nCORO O SENHOR É QUEM TE GUARDA, GUARDARÁ DE TODO MAL; TUA ENTRADA E SAÍDA, DESDE AGORA ATÉ O FINAL. O SENHOR É QUEM TE GUARDA, NÃO TE DEIXARÁ CAIR; COMO A ISRAEL GUARDAVA, NÃO DORMIU NEM VAI DORMIR.', 'PARA OS MONTES OLHAREI, DE ONDE VEM A SALVAÇÃO? MEU SOCORRO VEM DE DEUS, O SENHOR DA CRIAÇÃO. CORO O SENHOR É QUEM TE GUARDA, GUARDARÁ DE TODO MAL; TUA ENTRADA E SAÍDA, DESDE AGORA ATÉ O FINAL. O SENHOR É QUEM TE GUARDA, SEMPRE À TUA DESTRA ESTÁ; NEM DE DIA, NEM DE NOITE, MAL ALGUM TE OFENDERÁ. CORO O SENHOR É QUEM TE GUARDA, GUARDARÁ DE TODO MAL; TUA ENTRADA E SAÍDA, DESDE AGORA ATÉ O FINAL. O SENHOR É QUEM TE GUARDA, NÃO TE DEIXARÁ CAIR; COMO A ISRAEL GUARDAVA, NÃO DORMIU NEM VAI DORMIR.',  1);
