INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('341', '341 - QUE CONSOLAÇÃO', 'QUE CONSOLAÇÃO TEM MEU CORAÇÃO, DESCANSANDO NO PODER DE DEUS; ELE TEM PRAZER EM ME PROTEGER, DESCANSANDO NO PODER DE DEUS.\n\nCORO DESCANSANDO NOS ETERNOS BRAÇOS DO MEU DEUS; VOU SEGURO, DESCANSANDO NO PODER DE DEUS. SEMPRE AVANTE VOU, BEM CONTENTE ESTOU, DESCANSANDO NO PODER DE DEUS; TUDO HEI DE VENCER PELO SEU PODER, DESCANSANDO NO PODER DE DEUS.\n\nCORO DESCANSANDO NOS ETERNOS BRAÇOS DO MEU DEUS; VOU SEGURO, DESCANSANDO NO PODER DE DEUS. NÃO RECEAREI, NADA TEMEREI, DESCANSANDO NO PODER DE DEUS; GOZO, PAZ E AMOR JUNTO A MEU SENHOR DESCANSANDO NO PODER DE DEUS.', 'QUE CONSOLAÇÃO TEM MEU CORAÇÃO, DESCANSANDO NO PODER DE DEUS; ELE TEM PRAZER EM ME PROTEGER, DESCANSANDO NO PODER DE DEUS. CORO DESCANSANDO NOS ETERNOS BRAÇOS DO MEU DEUS; VOU SEGURO, DESCANSANDO NO PODER DE DEUS. SEMPRE AVANTE VOU, BEM CONTENTE ESTOU, DESCANSANDO NO PODER DE DEUS; TUDO HEI DE VENCER PELO SEU PODER, DESCANSANDO NO PODER DE DEUS. CORO DESCANSANDO NOS ETERNOS BRAÇOS DO MEU DEUS; VOU SEGURO, DESCANSANDO NO PODER DE DEUS. NÃO RECEAREI, NADA TEMEREI, DESCANSANDO NO PODER DE DEUS; GOZO, PAZ E AMOR JUNTO A MEU SENHOR DESCANSANDO NO PODER DE DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('342', '342 - ROCHA MINHA', 'ROCHA MINHA, LIBERTADOR MEU, EM TI EU CONFIAREI; O PODER NA TERRA E NO CÉU SÓ A TI PERTENCE, MEU DEUS E REI.\n\nCORO ESPERO EM TI, Ó MEU DEUS, TUDO ESTÁ EM TUAS MÃOS! ROCHA MINHA, LIBERTADOR MEU, SÓ TU ÉS, SENHOR, MINHA SALVAÇÃO\n\nCOM LOUVOR EM TUA CASA ENTRAREI E CANTAREI AO SENHOR, NA ALEGRIA OU TRISTEZA, EU SEI, SEMPRE ESTÁS COMIGO, MEU GRANDE AMOR.\n\nCORO ESPERO EM TI, Ó MEU DEUS, TUDO ESTÁ EM TUAS MÃOS! ROCHA MINHA, LIBERTADOR MEU, SÓ TU ÉS, SENHOR, MINHA SALVAÇÃO', 'ROCHA MINHA, LIBERTADOR MEU, EM TI EU CONFIAREI; O PODER NA TERRA E NO CÉU SÓ A TI PERTENCE, MEU DEUS E REI. CORO ESPERO EM TI, Ó MEU DEUS, TUDO ESTÁ EM TUAS MÃOS! ROCHA MINHA, LIBERTADOR MEU, SÓ TU ÉS, SENHOR, MINHA SALVAÇÃO COM LOUVOR EM TUA CASA ENTRAREI E CANTAREI AO SENHOR, NA ALEGRIA OU TRISTEZA, EU SEI, SEMPRE ESTÁS COMIGO, MEU GRANDE AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('343', '343 - OS QUE CONFIAM NO SENHOR', 'OS QUE CONFIAM NO SENHOR SERÃO FORTES E INABALÁVEIS, NÃO SERÃO CONFUNDIDOS E EM SEUS BRAÇOS DESCANSARÃO. O SENHOR CUMPRIRÁ SEUS DESEJOS, OUVIRÁ SEU CLAMOR E OS SALVARÁ, ELE SERÁ SEU AUXÍLIO E EM SEUS BRAÇOS DESCANSARÃO.\nBIS', 'OS QUE CONFIAM NO SENHOR SERÃO FORTES E INABALÁVEIS, NÃO SERÃO CONFUNDIDOS E EM SEUS BRAÇOS DESCANSARÃO. O SENHOR CUMPRIRÁ SEUS DESEJOS, OUVIRÁ SEU CLAMOR E OS SALVARÁ, ELE SERÁ SEU AUXÍLIO E EM SEUS BRAÇOS DESCANSARÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('344', '344 - CONFIO EM DEUS', 'CONFIO EM DEUS AONDE QUER QUE EU VÁ, EM TERRA FIRME OU SOBRE O IRADO MAR; E SEJA POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR.\n\nCORO CONFIO EM DEUS, EU SEI QUE ME AMARÁ E PELA SUA MÃO ME GUIARÁ; E SEJA, POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR. A LINDA FLOR RECEBE O SEU CUIDAR, ELE GUIA OS ANJOS NO ESPAÇOSO AR; SE ELE ASSIM, CUIDA DE MIM, CONFIO EM DEUS E EM SEU EXCELSO AMOR.\n\nCORO CONFIO EM DEUS, EU SEI QUE ME AMARÁ E PELA SUA MÃO ME GUIARÁ; E SEJA, POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR. POR VALES OU DESERTO ABRASADOR COM ELE IREI E SEM NENHUM TEMOR; E PELA MÃO ME CONDUZIRÁ, CONFIO EM DEUS E EM SEU EXCELSO AMOR.', 'CONFIO EM DEUS AONDE QUER QUE EU VÁ, EM TERRA FIRME OU SOBRE O IRADO MAR; E SEJA POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR. CORO CONFIO EM DEUS, EU SEI QUE ME AMARÁ E PELA SUA MÃO ME GUIARÁ; E SEJA, POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR. A LINDA FLOR RECEBE O SEU CUIDAR, ELE GUIA OS ANJOS NO ESPAÇOSO AR; SE ELE ASSIM, CUIDA DE MIM, CONFIO EM DEUS E EM SEU EXCELSO AMOR. CORO CONFIO EM DEUS, EU SEI QUE ME AMARÁ E PELA SUA MÃO ME GUIARÁ; E SEJA, POIS, POR ONDE FOR, CONFIO EM DEUS E EM SEU EXCELSO AMOR. POR VALES OU DESERTO ABRASADOR COM ELE IREI E SEM NENHUM TEMOR; E PELA MÃO ME CONDUZIRÁ, CONFIO EM DEUS E EM SEU EXCELSO AMOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('345', '345 - EM NADA PONHO A MINHA FÉ', 'EM NADA PONHO A MINHA FÉ, SENÃO NA GRAÇA DE JESUS; NO SACRIFÍCIO REMIDOR, NO SANGUE DO BOM REDENTOR. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. (BIS)\n\nSE NÃO LHE POSSO A FACE VER, NA SUA GRAÇA VOU VIVER; A CADA DIA, SEM FALHAR, SEMPRE HEI DE NELE CONFIAR. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. (BIS)\n\nSEU JURAMENTO É MUI LEAL, ABRIGA-ME NO TEMPORAL; AO VIR CERCAR-ME A TENTAÇÃO É CRISTO A MINHA SALVAÇÃO. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. (BIS)\n\nASSIM QUE O SEU CLARIM SOAR, IREI COM ELE ME ENCONTRAR E GOZAREI DA REDENÇÃO COM TODOS QUE NO CÉU ESTÃO. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. (BIS)', 'EM NADA PONHO A MINHA FÉ, SENÃO NA GRAÇA DE JESUS; NO SACRIFÍCIO REMIDOR, NO SANGUE DO BOM REDENTOR. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. SE NÃO LHE POSSO A FACE VER, NA SUA GRAÇA VOU VIVER; A CADA DIA, SEM FALHAR, SEMPRE HEI DE NELE CONFIAR. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. SEU JURAMENTO É MUI LEAL, ABRIGA-ME NO TEMPORAL; AO VIR CERCAR-ME A TENTAÇÃO É CRISTO A MINHA SALVAÇÃO. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR. ASSIM QUE O SEU CLARIM SOAR, IREI COM ELE ME ENCONTRAR E GOZAREI DA REDENÇÃO COM TODOS QUE NO CÉU ESTÃO. CORO A MINHA FÉ E O MEU AMOR ESTÃO FIRMADOS NO SENHOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('346', '346 - SE A FÉ POR VEZES FALTA', '346 - SE A FÉ POR VEZES FALTA\n\nSE A FÉ POR VEZES FALTA, QUANDO O SOL NÃO POSSO VER, A JESUS EU DIGO HUMILDE: Ó VEM MEU PILOTO SER.\n\nCORO “Ó NÃO TEMAS, SOU CONTIGO, TEU PILOTO ATÉ O FIM! NÃO TE IMPORTES COM O PERIGO, EIS A MÃO, CONFIA EM MIM.”\n\nQUANDO A TENTAÇÃO ME ENVOLVE E NÃO POSSO A CRISTO VER, EIS QUE EM MEIO À TEMPESTADE, OUÇO O SALVADOR DIZER:\n\nCORO “Ó NÃO TEMAS, SOU CONTIGO, TEU PILOTO ATÉ O FIM! NÃO TE IMPORTES COM O PERIGO, EIS A MÃO, CONFIA EM MIM.”\n\nQUANDO A ALMA SE ABATE, QUANDO OS DIAS LONGOS SÃO, VOLVO O OLHAR AO MEU PILOTO E ESSE CANTO ESCUTO ENTÃO:\n\nCORO “Ó NÃO TEMAS, SOU CONTIGO, TEU PILOTO ATÉ O FIM! NÃO TE IMPORTES COM O PERIGO, EIS A MÃO, CONFIA EM MIM.”', '346 - SE A FÉ POR VEZES FALTA SE A FÉ POR VEZES FALTA, QUANDO O SOL NÃO POSSO VER, A JESUS EU DIGO HUMILDE: Ó VEM MEU PILOTO SER. CORO Ó NÃO TEMAS, SOU CONTIGO, TEU PILOTO ATÉ O FIM! NÃO TE IMPORTES COM O PERIGO, EIS A MÃO, CONFIA EM MIM. QUANDO A TENTAÇÃO ME ENVOLVE E NÃO POSSO A CRISTO VER, EIS QUE EM MEIO À TEMPESTADE, OUÇO O SALVADOR DIZER: QUANDO A ALMA SE ABATE, QUANDO OS DIAS LONGOS SÃO, VOLVO O OLHAR AO MEU PILOTO E ESSE CANTO ESCUTO ENTÃO:',  1);
