INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('349', '349 - SE NÃO FOSSE O SENHOR', 'SE NÃO FOSSE O SENHOR (SE NÃO FOSSE O SENHOR) VARÕES QUE ESTIVESSE AO NOSSO LADO QUANDO OS HOMENS SE LEVANTARAM, SE LEVANTARAM CONTRA NÓS.\n\nO SOCORRO ESTÁ NO SENHOR QUE FEZ O CÉU E A TERRA. OS LAÇOS SÃO QUEBRADOS E NÓS ESCAPAMOS. BENDITO SEJA O SENHOR QUE FEZ O CÉU E A TERRA. INSTRUMENTOS\n\nSE NÃO FOSSE O SENHOR (SE NÃO FOSSE O SENHOR) VARÕES QUE ESTIVESSE AO NOSSO LADO QUANDO OS HOMENS SE LEVANTARAM, SE LEVANTARAM CONTRA NÓS. O SOCORRO ESTÁ NO SENHOR QUE FEZ O CÉU E A TERRA. OS LAÇOS SÃO QUEBRADOS E NÓS ESCAPAMOS.\n\nBENDITO SEJA O SENHOR QUE FEZ O CÉU E A TERRA. INSTRUMENTOS FINAL: SE NÃO FOSSE O SENHOR, (SE NÃO FOSSE O SENHOR) (SE NÃO FOSSE O SENHOR) O SENHOR!\nBIS', 'SE NÃO FOSSE O SENHOR (SE NÃO FOSSE O SENHOR) VARÕES QUE ESTIVESSE AO NOSSO LADO QUANDO OS HOMENS SE LEVANTARAM, SE LEVANTARAM CONTRA NÓS. O SOCORRO ESTÁ NO SENHOR QUE FEZ O CÉU E A TERRA. OS LAÇOS SÃO QUEBRADOS E NÓS ESCAPAMOS. BENDITO SEJA O SENHOR QUE FEZ O CÉU E A TERRA. INSTRUMENTOS SE NÃO FOSSE O SENHOR (SE NÃO FOSSE O SENHOR) VARÕES QUE ESTIVESSE AO NOSSO LADO QUANDO OS HOMENS SE LEVANTARAM, SE LEVANTARAM CONTRA NÓS. O SOCORRO ESTÁ NO SENHOR QUE FEZ O CÉU E A TERRA. OS LAÇOS SÃO QUEBRADOS E NÓS ESCAPAMOS. BENDITO SEJA O SENHOR QUE FEZ O CÉU E A TERRA. INSTRUMENTOS  SE NÃO FOSSE O SENHOR, (SE NÃO FOSSE O SENHOR) (SE NÃO FOSSE O SENHOR) O SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('350', '350 - FAZ-ME INABALÁVEL', 'FAZ-ME INABALÁVEL, SENHOR, EM TEU CAMINHO, FIRMA OS MEUS PASSOS E NÃO TRIUNFE O INIMIGO; OUVE A MINHA ORAÇÃO E SEJA EU SOCORRIDO.\n\nPOR QUE TE ABATES DENTRO EM MIM, Ó MINH’ALMA? ESPERA EM TEU DEUS, ELE É TUA FORÇA E GLÓRIA; NÃO TE DESAMPAROU, SEMPRE ESTEVE AO TEU LADO. A DEUS TODA A GLÓRIA, ELE NOS FAZ TRIUNFANTES, SUA GRAÇA É FORTE E SUA PALAVRA É VERDADE, AMÉM!', 'FAZ-ME INABALÁVEL, SENHOR, EM TEU CAMINHO, FIRMA OS MEUS PASSOS E NÃO TRIUNFE O INIMIGO; OUVE A MINHA ORAÇÃO E SEJA EU SOCORRIDO. POR QUE TE ABATES DENTRO EM MIM, Ó MINH’ALMA? ESPERA EM TEU DEUS, ELE É TUA FORÇA E GLÓRIA; NÃO TE DESAMPAROU, SEMPRE ESTEVE AO TEU LADO. A DEUS TODA A GLÓRIA, ELE NOS FAZ TRIUNFANTES, SUA GRAÇA É FORTE E SUA PALAVRA É VERDADE, AMÉM!',  1);
