INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('354', '354 - CRISTO ME DARÁ', 'CRISTO ME DARÁ A SUA PAZ, SIM, GUARDARÁ MEU CAMINHO; POR ONDE QUER QUE EU ANDAR, SENHOR, A TUA LUZ ME GUIARÁ.\n\nCORO SÓ EM JESUS (SÓ EM JESUS) EU POSSO CONFIAR, (EU POSSO CONFIAR) ETERNAMENTE E EXALTAR (E EXALTAR) COM ALEGRIA SEU SANTO NOME, (GLORIOSO JESUS) COMO É BOM VIVER O AMOR DE DEUS, QUE PRECIOSO É TER JESUS EM MIM.\n\nJESUS ME DARÁ O SEU AMOR, MESMO EM LUTAS OU PROVAÇÕES, COM SEU ESPÍRITO EM MINHA VIDA NADA TEMEREI, SEMPRE O LOUVAREI, ATÉ O FIM.\n\nCORO SÓ EM JESUS (SÓ EM JESUS) EU POSSO CONFIAR, (EU POSSO CONFIAR) ETERNAMENTE E EXALTAR (E EXALTAR) COM ALEGRIA SEU SANTO NOME, (GLORIOSO JESUS) COMO É BOM VIVER O AMOR DE DEUS, QUE PRECIOSO É TER JESUS EM MIM.\n\nFINAL: AMO AO SENHOR, LOUVO A JESUS! CRISTO ESTÁ EM MIM!', 'CRISTO ME DARÁ A SUA PAZ, SIM, GUARDARÁ MEU CAMINHO; POR ONDE QUER QUE EU ANDAR, SENHOR, A TUA LUZ ME GUIARÁ. CORO SÓ EM JESUS (SÓ EM JESUS) EU POSSO CONFIAR, (EU POSSO CONFIAR) ETERNAMENTE E EXALTAR (E EXALTAR) COM ALEGRIA SEU SANTO NOME, (GLORIOSO JESUS) COMO É BOM VIVER O AMOR DE DEUS, QUE PRECIOSO É TER JESUS EM MIM. JESUS ME DARÁ O SEU AMOR, MESMO EM LUTAS OU PROVAÇÕES, COM SEU ESPÍRITO EM MINHA VIDA NADA TEMEREI, SEMPRE O LOUVAREI, ATÉ O FIM. AMO AO SENHOR, LOUVO A JESUS! CRISTO ESTÁ EM MIM!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('355', '355 - VENCER, VENCER', 'VENCER, VENCER, EU FUI CHAMADO PRA VENCER; VENCER O MAL, VENCER O MUNDO POR AMOR AO MEU DEUS. (BIS)\n\nPORQUE SEREI, SEREI COMO OS ANJOS QUE LOUVAM A CRISTO JESUS, O MEU REI, E ESTAREI NAS LINDAS MANSÕES CELESTIAIS, PELAS RUAS DE OURO ANDAREI. VENCER, VENCER, EU FUI CHAMADO PRA VENCER ATÉ CHEGAR O LINDO DIA ONDE O MAL CESSARÁ, ONDE A PAZ REINARÁ.\n\nPORQUE JESUS JÁ VENCEU A MORTE NA CRUZ E DEU SUA GRAÇA, SUA LUZ; PORQUE SEREI, SEREI COMO OS ANJOS QUE LOUVAM A CRISTO JESUS, O MEU REI. VENCER, VENCER, EU FUI CHAMADO PRA VENCER, VENCER O MAL, VENCER O MUNDO POR AMOR AO MEU DEUS. (3X)', 'VENCER, VENCER, EU FUI CHAMADO PRA VENCER; VENCER O MAL, VENCER O MUNDO POR AMOR AO MEU DEUS. PORQUE SEREI, SEREI COMO OS ANJOS QUE LOUVAM A CRISTO JESUS, O MEU REI, E ESTAREI NAS LINDAS MANSÕES CELESTIAIS, PELAS RUAS DE OURO ANDAREI. VENCER, VENCER, EU FUI CHAMADO PRA VENCER ATÉ CHEGAR O LINDO DIA ONDE O MAL CESSARÁ, ONDE A PAZ REINARÁ. PORQUE JESUS JÁ VENCEU A MORTE NA CRUZ E DEU SUA GRAÇA, SUA LUZ; PORQUE SEREI, SEREI COMO OS ANJOS QUE LOUVAM A CRISTO JESUS, O MEU REI. VENCER, VENCER, EU FUI CHAMADO PRA VENCER, VENCER O MAL, VENCER O MUNDO POR AMOR AO MEU DEUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('356', '356 - O SENHOR É O MEU PASTOR (REFRIGERA A MINHA ALMA)', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, DEITAR-ME FAZ EM VERDES PASTOS, E ÀS ÁGUAS, MANSAMENTE, MINHA ALMA GUIARÁ.\n\nCORO REFRIGERA A MINHA ALMA, (BIS) Ó SENHOR, TEU SER ME ACALMA, REFRIGERA A MINHA ALMA; GUIA-ME PELAS VEREDAS, AS VEREDAS DA JUSTIÇA E SENTIR SOMENTE O AMOR, O AMOR DE DEUS.\n\nSE SOZINHO EU ESTOU E NÃO POSSO MAIS LUTAR, O SENHOR ESTÁ COMIGO, COM SUA VARA E SEU CAJADO NÃO VAI ME DESAMPARAR.\n\nCORO REFRIGERA A MINHA ALMA, (BIS) Ó SENHOR, TEU SER ME ACALMA, REFRIGERA A MINHA ALMA; GUIA-ME PELAS VEREDAS, AS VEREDAS DA JUSTIÇA E SENTIR SOMENTE O AMOR, O AMOR DE DEUS.', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, DEITAR-ME FAZ EM VERDES PASTOS, E ÀS ÁGUAS, MANSAMENTE, MINHA ALMA GUIARÁ. CORO REFRIGERA A MINHA ALMA,  Ó SENHOR, TEU SER ME ACALMA, REFRIGERA A MINHA ALMA; GUIA-ME PELAS VEREDAS, AS VEREDAS DA JUSTIÇA E SENTIR SOMENTE O AMOR, O AMOR DE DEUS. SE SOZINHO EU ESTOU E NÃO POSSO MAIS LUTAR, O SENHOR ESTÁ COMIGO, COM SUA VARA E SEU CAJADO NÃO VAI ME DESAMPARAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('357', '357 - O SENHOR É O MEU PASTOR (BONDADE E MISERICÓRDIA)', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, POR VERDES CAMPOS ME CONDUZIRÁ, EM PLANAS CAMPINAS, JUNTO ÀS ÁGUAS TRANQÜILAS DO SEU AMOR ELE ME DARÁ.\n\nCORO BONDADE E MISERICÓRDIA CERTAMENTE ME SEGUIRÃO E HABITAREI COM MEU SENHOR PRA TODO O SEMPRE; AINDA QUE EU ANDASSE PELO VALE DA SOMBRA DA MORTE, NÃO TEMERIA MAL ALGUM PORQUE COMIGO ESTÁS.\n\nME UNGES COM ÓLEO DA ALEGRIA, EM TUA PRESENÇA ME ALEGRO, SENHOR; UMA MESA FARTA TU ME DARÁS, TUA DESTRA EM VERDADE ME SUSTENTARÁ.\n\nCORO BONDADE E MISERICÓRDIA CERTAMENTE ME SEGUIRÃO E HABITAREI COM MEU SENHOR PRA TODO O SEMPRE; AINDA QUE EU ANDASSE PELO VALE DA SOMBRA DA MORTE, NÃO TEMERIA MAL ALGUM PORQUE COMIGO ESTÁS.\n\nFINAL: O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, POR VERDES CAMPOS ME CONDUZIRÁ.', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, POR VERDES CAMPOS ME CONDUZIRÁ, EM PLANAS CAMPINAS, JUNTO ÀS ÁGUAS TRANQÜILAS DO SEU AMOR ELE ME DARÁ. CORO BONDADE E MISERICÓRDIA CERTAMENTE ME SEGUIRÃO E HABITAREI COM MEU SENHOR PRA TODO O SEMPRE; AINDA QUE EU ANDASSE PELO VALE DA SOMBRA DA MORTE, NÃO TEMERIA MAL ALGUM PORQUE COMIGO ESTÁS. ME UNGES COM ÓLEO DA ALEGRIA, EM TUA PRESENÇA ME ALEGRO, SENHOR; UMA MESA FARTA TU ME DARÁS, TUA DESTRA EM VERDADE ME SUSTENTARÁ. O SENHOR É O MEU PASTOR, NADA ME FALTARÁ, POR VERDES CAMPOS ME CONDUZIRÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('358', '358 - O SENHOR É O MEU PASTOR (SALMO 23)', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ; PELOS PASTOS VERDEJANTES ELE ME CONDUZIRÁ.\nBIS\n\nREFRIGERA A MINHA ALMA COM SUA PAZ E SEU AMOR, SEU CAJADO ME CONSOLA, ELE É O MEU PASTOR. QUANDO NO VALE DA MORTE MAL ALGUM EU TEMEREI, O SENHOR É O MEU PASTOR, EM SUA CASA HABITAREI.\nBIS', 'O SENHOR É O MEU PASTOR, NADA ME FALTARÁ; PELOS PASTOS VERDEJANTES ELE ME CONDUZIRÁ. REFRIGERA A MINHA ALMA COM SUA PAZ E SEU AMOR, SEU CAJADO ME CONSOLA, ELE É O MEU PASTOR. QUANDO NO VALE DA MORTE MAL ALGUM EU TEMEREI, O SENHOR É O MEU PASTOR, EM SUA CASA HABITAREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('359', '359 - DEUS É NOSSO AMPARO, NOSSA FORTALEZA', 'DEUS É NOSSO AMPARO, NOSSA FORTALEZA, NOSSO PRONTO AUXÍLIO NA TRIBULAÇÃO, AINDA QUE SE MUDEM OS MONTES PARA O MAR, AINDA QUE A TERRA TREMA, TEREMOS QUE CONFIAR.\nBIS\n\nCÉUS E TERRA PODERÃO PASSAR, MAS SUA PALAVRA NÃO PASSARÁ. NÃO, NÃO, NÃO PASSARÁ. (BIS) SANTO, SANTO, SANTO, DIZ O SERAFIM, SANTO, SANTO, SANTO, É O SENHOR JEOVÁ, SANTO, SANTO, SANTO, É O DEUS QUE NOS REDIME, PORQUE TRÊS VEZES SANTO, A TERRA TREME ONDE DEUS ESTÁ.\nBIS\nBIS\n\nCÉUS E TERRA PODERÃO PASSAR, MAS SUA PALAVRA NÃO PASSARÁ. NÃO, NÃO, NÃO PASSARÁ. (BIS)\nBIS', 'DEUS É NOSSO AMPARO, NOSSA FORTALEZA, NOSSO PRONTO AUXÍLIO NA TRIBULAÇÃO, AINDA QUE SE MUDEM OS MONTES PARA O MAR, AINDA QUE A TERRA TREMA, TEREMOS QUE CONFIAR. CÉUS E TERRA PODERÃO PASSAR, MAS SUA PALAVRA NÃO PASSARÁ. NÃO, NÃO, NÃO PASSARÁ.  SANTO, SANTO, SANTO, DIZ O SERAFIM, SANTO, SANTO, SANTO, É O SENHOR JEOVÁ, SANTO, SANTO, SANTO, É O DEUS QUE NOS REDIME, PORQUE TRÊS VEZES SANTO, A TERRA TREME ONDE DEUS ESTÁ. CÉUS E TERRA PODERÃO PASSAR, MAS SUA PALAVRA NÃO PASSARÁ. NÃO, NÃO, NÃO PASSARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('360', '360 - ROCHEDO FORTE É O SENHOR', 'ROCHEDO FORTE É O SENHOR, REFÚGIO NA TRIBULAÇÃO! CONSTANTE E FIRME, AMPARADOR, REFÚGIO NA TRIBULAÇÃO!\n\nCORO OH! CRISTO É NOSSO ABRIGO NO TEMPORAL, NO TEMPORAL, NO TEMPORAL! OH! CRISTO É NOSSO ABRIGO NO TEMPORAL! REFÚGIO NA TRIBULAÇÃO.\n\nLUGAR DE SOMBRA NO VERÃO, DESCANSO NA TRIBULAÇÃO! VIGIA FIEL NA ESCURIDÃO, DESCANSO NA TRIBULAÇÃO!\n\nCORO OH! CRISTO É NOSSO ABRIGO NO TEMPORAL, NO TEMPORAL, NO TEMPORAL! OH! CRISTO É NOSSO ABRIGO NO TEMPORAL! REFÚGIO NA TRIBULAÇÃO.\n\nPILOTO BOM NO BRAVO MAR, CONSOLO NA TRIBULAÇÃO! ANCORADOURO SINGULAR, CONSOLO NA TRIBULAÇÃO!\n\nCORO OH! CRISTO É NOSSO ABRIGO NO TEMPORAL, NO TEMPORAL, NO TEMPORAL! OH! CRISTO É NOSSO ABRIGO NO TEMPORAL! REFÚGIO NA TRIBULAÇÃO.\n\nJESUS É O NOSSO BENFEITOR, AUXÍLIO NA TRIBULAÇÃO! PRESENTE, ETERNO SALVADOR, AUXÍLIO NA TRIBULAÇÃO!\n\nCORO OH! CRISTO É NOSSO ABRIGO NO TEMPORAL, NO TEMPORAL, NO TEMPORAL! OH! CRISTO É NOSSO ABRIGO NO TEMPORAL! REFÚGIO NA TRIBULAÇÃO.', 'ROCHEDO FORTE É O SENHOR, REFÚGIO NA TRIBULAÇÃO! CONSTANTE E FIRME, AMPARADOR, REFÚGIO NA TRIBULAÇÃO! CORO OH! CRISTO É NOSSO ABRIGO NO TEMPORAL, NO TEMPORAL, NO TEMPORAL! OH! CRISTO É NOSSO ABRIGO NO TEMPORAL! REFÚGIO NA TRIBULAÇÃO. LUGAR DE SOMBRA NO VERÃO, DESCANSO NA TRIBULAÇÃO! VIGIA FIEL NA ESCURIDÃO, DESCANSO NA TRIBULAÇÃO! PILOTO BOM NO BRAVO MAR, CONSOLO NA TRIBULAÇÃO! ANCORADOURO SINGULAR, CONSOLO NA TRIBULAÇÃO! JESUS É O NOSSO BENFEITOR, AUXÍLIO NA TRIBULAÇÃO! PRESENTE, ETERNO SALVADOR, AUXÍLIO NA TRIBULAÇÃO!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('361', '361 - NÃO HÁ DEUS ACIMA DO SENHOR', 'NÃO HÁ DEUS ACIMA DO SENHOR, NÃO HÁ ROCHA COMO NOSSO DEUS; É FORÇA E PROTEÇÃO, ELE É A TORRE DE LIBERTAÇÃO AOS QUE CONFIAM NO SENHOR.\nBIS\n\nNÃO HÁ DEUS ACIMA DO SENHOR, NÃO HÁ ROCHA COMO NOSSO DEUS; ME TREINA PRA LUTAR, ME MOSTRA ONDE DEVO EU ANDAR, GLÓRIA AO NOME DE JESUS.\n\nME TREINA PRA LUTAR, ME MOSTRA ONDE DEVO EU ANDAR, GLÓRIA AO NOME DE JESUS. GLÓRIA AO NOME DE JESUS.', 'NÃO HÁ DEUS ACIMA DO SENHOR, NÃO HÁ ROCHA COMO NOSSO DEUS; É FORÇA E PROTEÇÃO, ELE É A TORRE DE LIBERTAÇÃO AOS QUE CONFIAM NO SENHOR. NÃO HÁ DEUS ACIMA DO SENHOR, NÃO HÁ ROCHA COMO NOSSO DEUS; ME TREINA PRA LUTAR, ME MOSTRA ONDE DEVO EU ANDAR, GLÓRIA AO NOME DE JESUS. ME TREINA PRA LUTAR, ME MOSTRA ONDE DEVO EU ANDAR, GLÓRIA AO NOME DE JESUS. GLÓRIA AO NOME DE JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('362', '362 - DEUS PROVERÁ', 'CORO (2X) DEUS PROVERÁ TUDO PARA O SEU POVO, PAZ, ALEGRIA, CONSOLO E GOZO, CANTANDO E LOUVANDO AO DEUS CRIADOR, AQUELE QUE REINA E QUE É O SENHOR, MESMO NAS LUTAS E AFLIÇÕES, DEUS PROVERÁ PARA NÓS A VITÓRIA.\n\n“QUANDO PASSARES PELAS ÁGUAS ESTAREI CONTIGO, QUANDO PASSARES PELOS RIOS ELES NÃO TE COBRIRÃO QUANDO PASSARES PELO FOGO ELE NÃO TE QUEIMARÁ, PORQUE EU SOU O TEU DEUS, O SANTO DE ISRAEL O TEU SALVADOR”.\n\nVARÕES\n\nVARÕES\n\nCORO DEUS PROVERÁ TUDO PARA O SEU POVO, PAZ, ALEGRIA, CONSOLO E GOZO, CANTANDO E LOUVANDO AO DEUS CRIADOR, AQUELE QUE REINA E QUE É O SENHOR, MESMO NAS LUTAS E AFLIÇÕES, DEUS PROVERÁ PARA NÓS A VITÓRIA.\n\n“QUANDO PASSARES PELAS ÁGUAS ESTAREI CONTIGO, QUANDO PASSARES PELOS RIOS ELES NÃO TE COBRIRÃO ACHASTE GRAÇA AOS MEUS OLHOS, DIGNO DE HONRA E EU TE AMEI, NÃO TEMAS, EU SOU CONTIGO, O SANTO DE ISRAEL, O TEU SALVADOR”.\n\nVARÕES\n\nVARÕES\n\nCORO DEUS PROVERÁ TUDO PARA O SEU POVO, PAZ, ALEGRIA, CONSOLO E GOZO, CANTANDO E LOUVANDO AO DEUS CRIADOR, AQUELE QUE REINA E QUE É O SENHOR, MESMO NAS LUTAS E AFLIÇÕES, DEUS PROVERÁ PARA NÓS A VITÓRIA.\nBIS', 'CORO  DEUS PROVERÁ TUDO PARA O SEU POVO, PAZ, ALEGRIA, CONSOLO E GOZO, CANTANDO E LOUVANDO AO DEUS CRIADOR, AQUELE QUE REINA E QUE É O SENHOR, MESMO NAS LUTAS E AFLIÇÕES, DEUS PROVERÁ PARA NÓS A VITÓRIA. QUANDO PASSARES PELAS ÁGUAS ESTAREI CONTIGO, QUANDO PASSARES PELOS RIOS ELES NÃO TE COBRIRÃO QUANDO PASSARES PELO FOGO ELE NÃO TE QUEIMARÁ, PORQUE EU SOU O TEU DEUS, O SANTO DE ISRAEL O TEU SALVADOR. CORO DEUS PROVERÁ TUDO PARA O SEU POVO, PAZ, ALEGRIA, CONSOLO E GOZO, CANTANDO E LOUVANDO AO DEUS CRIADOR, AQUELE QUE REINA E QUE É O SENHOR, MESMO NAS LUTAS E AFLIÇÕES, DEUS PROVERÁ PARA NÓS A VITÓRIA. QUANDO PASSARES PELAS ÁGUAS ESTAREI CONTIGO, QUANDO PASSARES PELOS RIOS ELES NÃO TE COBRIRÃO ACHASTE GRAÇA AOS MEUS OLHOS, DIGNO DE HONRA E EU TE AMEI, NÃO TEMAS, EU SOU CONTIGO, O SANTO DE ISRAEL, O TEU SALVADOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('363', '363 - QUANDO AS LUTAS ME CERCAM', 'QUANDO AS LUTAS ME CERCAM, ME ENTRISTECEM E ME FAZEM CHORAR, EU CORRO PARA O TEU ALTAR.\n\nCORO (2X) DE JOELHOS COMEÇO A ORAR SINTO A MÃO DE JESUS ME TOCAR. INSTRUMENTOS\n\nO RENOVO ASSIM VEM, TEU ESPÍRITO A ME CONSOLAR. DE ALEGRIA, QUERO TE LOUVAR.\n\nCORO (2X) DE JOELHOS COMEÇO A ORAR SINTO A MÃO DE JESUS ME TOCAR. FINAL: DE ALEGRIA, QUERO TE LOUVAR.', 'QUANDO AS LUTAS ME CERCAM, ME ENTRISTECEM E ME FAZEM CHORAR, EU CORRO PARA O TEU ALTAR. CORO  DE JOELHOS COMEÇO A ORAR SINTO A MÃO DE JESUS ME TOCAR. INSTRUMENTOS O RENOVO ASSIM VEM, TEU ESPÍRITO A ME CONSOLAR. DE ALEGRIA, QUERO TE LOUVAR. CORO  DE JOELHOS COMEÇO A ORAR SINTO A MÃO DE JESUS ME TOCAR.  DE ALEGRIA, QUERO TE LOUVAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('364', '364 - DÁ-ME MAIS FÉ', 'DÁ-ME MAIS FÉ PRA QUE O MEU CHORO SOMENTE ESTA NOITE POSSA DURAR E A TUA ALEGRIA, QUE É A MINHA FORÇA, BEM CEDO POSSA CHEGAR;\n\nDÁ-ME MAIS FÉ E A CERTEZA DE QUE EU POSSO ESPERAR BÊNÇÃOS QUE HOJE EU NÃO POSSO VER, MAS EM TI ESPERAREI, SEI QUE UM DIA VÃO CHEGAR E PELA FÉ EU VENCEREI.\n\nCORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR.\n\nDÁ-ME MAIS FÉ E A FORÇA DE LUTAR SEM, DO CAMINHO, EU ME DESVIAR, MESMO TENDO AFLIÇÕES NÃO QUERO JAMAIS PECAR, Ó VEM, SENHOR, ME SUSTENTAR;\n\nDÁ-ME MAIS FÉ PARA ENTENDER QUE O QUE ME ACONTECER É SOMENTE PRA EU CRESCER, E SEGUINDO SEMPRE EM FRENTE, COM A TUA SALVAÇÃO REPOUSO O MEU CORAÇÃO.\n\nCORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR. INSTRUMENTOS\n\nCORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR. FINAL: DÁ-ME MAIS FÉ', 'DÁ-ME MAIS FÉ PRA QUE O MEU CHORO SOMENTE ESTA NOITE POSSA DURAR E A TUA ALEGRIA, QUE É A MINHA FORÇA, BEM CEDO POSSA CHEGAR; DÁ-ME MAIS FÉ E A CERTEZA DE QUE EU POSSO ESPERAR BÊNÇÃOS QUE HOJE EU NÃO POSSO VER, MAS EM TI ESPERAREI, SEI QUE UM DIA VÃO CHEGAR E PELA FÉ EU VENCEREI. CORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR. DÁ-ME MAIS FÉ E A FORÇA DE LUTAR SEM, DO CAMINHO, EU ME DESVIAR, MESMO TENDO AFLIÇÕES NÃO QUERO JAMAIS PECAR, Ó VEM, SENHOR, ME SUSTENTAR; DÁ-ME MAIS FÉ PARA ENTENDER QUE O QUE ME ACONTECER É SOMENTE PRA EU CRESCER, E SEGUINDO SEMPRE EM FRENTE, COM A TUA SALVAÇÃO REPOUSO O MEU CORAÇÃO. CORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR. INSTRUMENTOS CORO O IMPOSSÍVEL NÃO DEVO TEMER, TUDO É POSSÍVEL AO HOMEM QUE CRÊ; DEUS TEM PRAZER NO HOMEM DE FÉ QUE CRÊ QUE DEUS NUNCA FALHA, QUE MILAGRES E MARAVILHAS OPERARÁ, POR AMOR ÀQUELE QUE DE DEUS NÃO DUVIDAR.  DÁ-ME MAIS FÉ',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('365', '365 - NÃO TEMAS, POIS TE ESCOLHI (QUANDO PELAS ÁGUAS)', '“NÃO TEMAS, POIS TE ESCOLHI, TE REMI, NÃO TEMAS, POIS, PORQUE CONTIGO ESTOU; AS COISAS PASSADAS TU NÃO DEVES LEMBRAR, POIS NOVO CAMINHO O TEU DEUS ABRIRÁ.\n\nCORO QUANDO PELAS ÁGUAS PASSARES CONTIGO ESTAREI, QUANDO PELO FOGO TU PASSARES NÃO TE QUEIMARÁS, EU SOU TEU DEUS, TEU SENHOR, O SALVADOR. INSTRUMENTOS\n\nEU ANUNCIEI E SALVEI, FIZ OUVIR QUE NÃO HÁ DEUS SALVADOR FORA DE MIM; FOSTE PRECIOSO AOS MEUS OLHOS, EU TE AMEI. QUANDO O TEU DEUS OPERAR, QUEM IMPEDIRÁ?\n\nCORO QUANDO PELAS ÁGUAS PASSARES CONTIGO ESTAREI, QUANDO PELO FOGO TU PASSARES NÃO TE QUEIMARÁS, EU SOU TEU DEUS, TEU SENHOR, O SALVADOR.\n\nFINAL: QUANDO PELAS ÁGUAS (O SALVADOR) ESTAREI CONTIGO (O SALVADOR).”', 'NÃO TEMAS, POIS TE ESCOLHI, TE REMI, NÃO TEMAS, POIS, PORQUE CONTIGO ESTOU; AS COISAS PASSADAS TU NÃO DEVES LEMBRAR, POIS NOVO CAMINHO O TEU DEUS ABRIRÁ. CORO QUANDO PELAS ÁGUAS PASSARES CONTIGO ESTAREI, QUANDO PELO FOGO TU PASSARES NÃO TE QUEIMARÁS, EU SOU TEU DEUS, TEU SENHOR, O SALVADOR. INSTRUMENTOS EU ANUNCIEI E SALVEI, FIZ OUVIR QUE NÃO HÁ DEUS SALVADOR FORA DE MIM; FOSTE PRECIOSO AOS MEUS OLHOS, EU TE AMEI. QUANDO O TEU DEUS OPERAR, QUEM IMPEDIRÁ? CORO QUANDO PELAS ÁGUAS PASSARES CONTIGO ESTAREI, QUANDO PELO FOGO TU PASSARES NÃO TE QUEIMARÁS, EU SOU TEU DEUS, TEU SENHOR, O SALVADOR. QUANDO PELAS ÁGUAS (O SALVADOR) ESTAREI CONTIGO (O SALVADOR).',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('366', '366 - AS LÁGRIMAS FALAM, JESUS TE ESCUTA', 'AS LÁGRIMAS FALAM, JESUS TE ESCUTA AS LÁGRIMAS FALAM PRA JESUS A TUA DOR. “BEM-AVENTURADO É O HOMEM QUE CHORA, SERÁ CONSOLADO”, POIS ASSIM DIZ O SENHOR.\n\nDOBRA OS TEUS JOELHOS PERANTE O ALTAR DO SENHOR E A VITÓRIA VIRÁ. ENTREGA TUA ALMA, CONFIA EM CRISTO JESUS E AS TUAS LÁGRIMAS JESUS ENXUGARÁ. INSTRUMENTOS\n\nAS LÁGRIMAS FALAM, JESUS TE ESCUTA AS LÁGRIMAS FALAM PRA JESUS A TUA DOR. “BEM-AVENTURADO É O HOMEM QUE CHORA, SERÁ CONSOLADO”, POIS ASSIM DIZ O SENHOR.\n\nDOBRA OS TEUS JOELHOS PERANTE O ALTAR DO SENHOR E A VITÓRIA VIRÁ. ENTREGA TUA ALMA, CONFIA EM CRISTO JESUS E AS TUAS LÁGRIMAS JESUS ENXUGARÁ. FINAL: (ENXUGARÁ) E AS TUAS LÁGRIMAS JESUS ENXUGARÁ.', 'AS LÁGRIMAS FALAM, JESUS TE ESCUTA AS LÁGRIMAS FALAM PRA JESUS A TUA DOR. BEM-AVENTURADO É O HOMEM QUE CHORA, SERÁ CONSOLADO, POIS ASSIM DIZ O SENHOR. DOBRA OS TEUS JOELHOS PERANTE O ALTAR DO SENHOR E A VITÓRIA VIRÁ. ENTREGA TUA ALMA, CONFIA EM CRISTO JESUS E AS TUAS LÁGRIMAS JESUS ENXUGARÁ. INSTRUMENTOS DOBRA OS TEUS JOELHOS PERANTE O ALTAR DO SENHOR E A VITÓRIA VIRÁ. ENTREGA TUA ALMA, CONFIA EM CRISTO JESUS E AS TUAS LÁGRIMAS JESUS ENXUGARÁ.  (ENXUGARÁ) E AS TUAS LÁGRIMAS JESUS ENXUGARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('367', '367 - SE AS LUTAS DA VIDA', 'SE AS LUTAS DA VIDA TE FAZEM CHORAR E ÀS VEZES TE FALTAM FORÇAS PRA VENCER, EXISTE UM DEUS QUE AO TEU LADO ESTÁ, ELE É DEUS FORTE, POR TI VENCERÁ; EXISTE UM DEUS QUE AO TEU LADO ESTÁ, ELE É FIEL E NÃO FALHARÁ.\n\nDEUS CUIDA DE TI (BIS) É TEU AMIGO, ESTÁ CONTIGO, DEUS CUIDA DE TI. SÓ EXISTE UM DEUS E AO TEU LADO ESTÁ, ELE É DEUS FORTE, POR TI VENCERÁ; SIM, EXISTE UM DEUS E AO TEU LADO ESTÁ, ELE É FIEL E NÃO FALHARÁ. (BIS)', 'SE AS LUTAS DA VIDA TE FAZEM CHORAR E ÀS VEZES TE FALTAM FORÇAS PRA VENCER, EXISTE UM DEUS QUE AO TEU LADO ESTÁ, ELE É DEUS FORTE, POR TI VENCERÁ; EXISTE UM DEUS QUE AO TEU LADO ESTÁ, ELE É FIEL E NÃO FALHARÁ. DEUS CUIDA DE TI  É TEU AMIGO, ESTÁ CONTIGO, DEUS CUIDA DE TI. SÓ EXISTE UM DEUS E AO TEU LADO ESTÁ, ELE É DEUS FORTE, POR TI VENCERÁ; SIM, EXISTE UM DEUS E AO TEU LADO ESTÁ, ELE É FIEL E NÃO FALHARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('368', '368 - ESFORÇA-TE, TEM BOM ÂNIMO!', '“ESFORÇA-TE, TEM BOM ÂNIMO!” ASSIM DIZ O SENHOR TEU DEUS, O DEUS DE ISRAEL, O DEUS QUE TE AMA, O DEUS QUE CONTIGO SEMPRE ESTÁ; TÃO-SOMENTE ESFORÇA-TE.\n\nCORO (2X) ELE CONTIGO IRÁ POR ONDE QUER QUE VÁS, SUA BOA MÃO TE SUSTERÁ; O SENHOR CONTIGO ESTARÁ.\n\nNÃO TEMAS, NÃO TE APAVORES QUANDO A LUTA SURGIR, LEMBRA QUE O TEU DEUS É MAIS FORTE E PODE TE AJUDAR; TÃO-SOMENTE ESFORÇA-TE.\n\nCORO (2X) ELE CONTIGO IRÁ POR ONDE QUER QUE VÁS, SUA BOA MÃO TE SUSTERÁ; O SENHOR CONTIGO ESTARÁ. FINAL: TÃO-SOMENTE ESFORÇA-TE.', 'ESFORÇA-TE, TEM BOM ÂNIMO! ASSIM DIZ O SENHOR TEU DEUS, O DEUS DE ISRAEL, O DEUS QUE TE AMA, O DEUS QUE CONTIGO SEMPRE ESTÁ; TÃO-SOMENTE ESFORÇA-TE. CORO  ELE CONTIGO IRÁ POR ONDE QUER QUE VÁS, SUA BOA MÃO TE SUSTERÁ; O SENHOR CONTIGO ESTARÁ. NÃO TEMAS, NÃO TE APAVORES QUANDO A LUTA SURGIR, LEMBRA QUE O TEU DEUS É MAIS FORTE E PODE TE AJUDAR; TÃO-SOMENTE ESFORÇA-TE. CORO  ELE CONTIGO IRÁ POR ONDE QUER QUE VÁS, SUA BOA MÃO TE SUSTERÁ; O SENHOR CONTIGO ESTARÁ.  TÃO-SOMENTE ESFORÇA-TE.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('369', '369 - QUANDO MAIS NINGUÉM TE COMPREENDE', 'QUANDO MAIS NINGUÉM TE COMPREENDE, TEUS AMIGOS TE ABANDONARAM, JESUS TE ABRAÇA E DIZ: “ESTOU AQUI PRA TE OUVIR”.\n\nCORO “FALA, FILHO MEU, CONTA PARA MIM OS TEUS PROBLEMAS, CLAMA A MIM EM ORAÇÃO, COM SINCERIDADE E RESPONDER-TE-EI. FALAREI CONTIGO, SOU O DEUS DA PROVISÃO, SOU JESUS, A TUA SOLUÇÃO”. INSTRUMENTOS\n\n“QUANDO INVOCARES O MEU NOME, DE MADRUGADA ME BUSCARES E DE JOELHOS TU ORARES, ESTAREI ATENTO A TE OUVIR.”\n\nCORO (2X) “FALA, FILHO MEU, CONTA PARA MIM OS TEUS PROBLEMAS, CLAMA A MIM EM ORAÇÃO, COM SINCERIDADE E RESPONDER-TE-EI. FALAREI CONTIGO, SOU O DEUS DA PROVISÃO, SOU JESUS, A TUA SOLUÇÃO”. FINAL: “SOU JESUS, A TUA SOLUÇÃO”.', 'QUANDO MAIS NINGUÉM TE COMPREENDE, TEUS AMIGOS TE ABANDONARAM, JESUS TE ABRAÇA E DIZ: ESTOU AQUI PRA TE OUVIR. CORO FALA, FILHO MEU, CONTA PARA MIM OS TEUS PROBLEMAS, CLAMA A MIM EM ORAÇÃO, COM SINCERIDADE E RESPONDER-TE-EI. FALAREI CONTIGO, SOU O DEUS DA PROVISÃO, SOU JESUS, A TUA SOLUÇÃO. INSTRUMENTOS QUANDO INVOCARES O MEU NOME, DE MADRUGADA ME BUSCARES E DE JOELHOS TU ORARES, ESTAREI ATENTO A TE OUVIR. CORO  FALA, FILHO MEU, CONTA PARA MIM OS TEUS PROBLEMAS, CLAMA A MIM EM ORAÇÃO, COM SINCERIDADE E RESPONDER-TE-EI. FALAREI CONTIGO, SOU O DEUS DA PROVISÃO, SOU JESUS, A TUA SOLUÇÃO.  SOU JESUS, A TUA SOLUÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('370', '370 - JESUS É A ROCHA INABALÁVEL', 'JESUS É A ROCHA INABALÁVEL, REFÚGIO E FORTALEZA DOS FIÉIS; MESMO QUE TEU CHORO DURE TODA A NOITE, MAS AO AMANHECER A ALEGRIA ENCHERÁ TEU CORAÇÃO, PORQUE JESUS TE FALARÁ: “NÃO CHORES, ÉS MEU FILHO MUITO AMADO”.\n\nAINDA QUE AS PROVAS SEJAM MUITAS, NÃO TEMAS, CONFIA EM JESUS; MESMO QUE TEU PRANTO SE CONVERTA EM DOR, MAS AO ROMPER O DIA A FELICIDADE ENCHERÁ TEU CORAÇÃO, PORQUE JESUS TE FALARÁ: “NÃO CHORES, ÉS MEU FILHO MUITO AMADO”.\n\nJESUS É A ROCHA INABALÁVEL, REFÚGIO E FORTALEZA DOS FIÉIS; AINDA QUE AS PROVAS SEJAM MUITAS, NÃO TEMAS, CONFIA EM JESUS. FINAL: NÃO TEMAS, CONFIA EM JESUS.', 'JESUS É A ROCHA INABALÁVEL, REFÚGIO E FORTALEZA DOS FIÉIS; MESMO QUE TEU CHORO DURE TODA A NOITE, MAS AO AMANHECER A ALEGRIA ENCHERÁ TEU CORAÇÃO, PORQUE JESUS TE FALARÁ: NÃO CHORES, ÉS MEU FILHO MUITO AMADO. AINDA QUE AS PROVAS SEJAM MUITAS, NÃO TEMAS, CONFIA EM JESUS; MESMO QUE TEU PRANTO SE CONVERTA EM DOR, MAS AO ROMPER O DIA A FELICIDADE ENCHERÁ TEU CORAÇÃO, PORQUE JESUS TE FALARÁ: NÃO CHORES, ÉS MEU FILHO MUITO AMADO. JESUS É A ROCHA INABALÁVEL, REFÚGIO E FORTALEZA DOS FIÉIS; AINDA QUE AS PROVAS SEJAM MUITAS, NÃO TEMAS, CONFIA EM JESUS.  NÃO TEMAS, CONFIA EM JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('371', '371 - TODA PROMESSA', 'TODA PROMESSA, TODA PALAVRA QUE O SENHOR FALOU SE CUMPRIRÁ, EM BREVE VIRÁ O MEU SALVADOR; NAS NUVENS DESCENDO, COM PODER E GRAÇA VEREI JESUS, MOLDADO O MEU CORPO, COMO O DOS ANJOS SERÁ. EM BREVE ESTAREMOS NO CÉU, COM OS ANJOS E O SALVADOR E JUNTOS UNIDOS, LOUVAREMOS AO SENHOR.\n\nCORO ESPERA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E AS TUAS FORÇAS SE RENOVARÃO (SIM, RENOVARÃO) CONFIA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E SERÁS COMO O MONTE DE SIÃO QUE NÃO SE ABALA.\n\nA SUA PALAVRA É DIGNA DE TODA ACEITAÇÃO E NÃO VOLTARÁ VAZIA, ANTES TUDO CUMPRIRÁ, POIS O SENHOR DISSE E FARÁ. BEM-AVENTURADOS VÓS QUE AGORA CHORAIS, POIS É GRANDE A ALEGRIA QUE O SENHOR HÁ DE VOS DAR; BEM-AVENTURADOS VÓS QUE AGORA SOFREIS POIS SEREIS CONSOLADOS PELO GRANDE AMOR DO DEUS DE ISRAEL.\n\nCORO (2X) ESPERA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E AS TUAS FORÇAS SE RENOVARÃO (SIM, RENOVARÃO) CONFIA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E SERÁS COMO O MONTE DE SIÃO QUE NÃO SE ABALA. FINAL: COMO O MONTE DE SIÃO QUE NÃO SE ABALA.', 'TODA PROMESSA, TODA PALAVRA QUE O SENHOR FALOU SE CUMPRIRÁ, EM BREVE VIRÁ O MEU SALVADOR; NAS NUVENS DESCENDO, COM PODER E GRAÇA VEREI JESUS, MOLDADO O MEU CORPO, COMO O DOS ANJOS SERÁ. EM BREVE ESTAREMOS NO CÉU, COM OS ANJOS E O SALVADOR E JUNTOS UNIDOS, LOUVAREMOS AO SENHOR. CORO ESPERA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E AS TUAS FORÇAS SE RENOVARÃO (SIM, RENOVARÃO) CONFIA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E SERÁS COMO O MONTE DE SIÃO QUE NÃO SE ABALA. A SUA PALAVRA É DIGNA DE TODA ACEITAÇÃO E NÃO VOLTARÁ VAZIA, ANTES TUDO CUMPRIRÁ, POIS O SENHOR DISSE E FARÁ. BEM-AVENTURADOS VÓS QUE AGORA CHORAIS, POIS É GRANDE A ALEGRIA QUE O SENHOR HÁ DE VOS DAR; BEM-AVENTURADOS VÓS QUE AGORA SOFREIS POIS SEREIS CONSOLADOS PELO GRANDE AMOR DO DEUS DE ISRAEL. CORO  ESPERA NO DEUS DE ISRAEL (NO DEUS DE ISRAEL) E AS TUAS FORÇAS SE RENOVARÃO (SIM, RENOVARÃO) CONFIA NO DEUS 