INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('372', '372 - MUITOS SÃO OS QUE CONFIAM', 'MUITOS SÃO OS QUE CONFIAM EM SUA FORÇA E SEU SABER E POR ISSO SE ENCURVAM, CAEM SEM PODER VENCER; MAS EU FAÇO MENÇÃO DO TEU NOME, SÓ EM TI CONFIAREI, E NO DIA DA PELEJA E ANGÚSTIA COMIGO ESTARÁS, MEU REI!\n\nLEMBRA-TE DA MINHA OFERTA, OUVE A MINHA PETIÇÃO E EU ME ALEGRAREI COM JÚBILO PELA TUA SALVAÇÃO; EM TEU NOME ARVORAREI BANDEIRAS, POIS COMIGO ESTÁS, SENHOR, EM TEU NOME SALTAREI BARREIRAS E SEREI UM VENCEDOR!\nBIS', 'MUITOS SÃO OS QUE CONFIAM EM SUA FORÇA E SEU SABER E POR ISSO SE ENCURVAM, CAEM SEM PODER VENCER; MAS EU FAÇO MENÇÃO DO TEU NOME, SÓ EM TI CONFIAREI, E NO DIA DA PELEJA E ANGÚSTIA COMIGO ESTARÁS, MEU REI! LEMBRA-TE DA MINHA OFERTA, OUVE A MINHA PETIÇÃO E EU ME ALEGRAREI COM JÚBILO PELA TUA SALVAÇÃO; EM TEU NOME ARVORAREI BANDEIRAS, POIS COMIGO ESTÁS, SENHOR, EM TEU NOME SALTAREI BARREIRAS E SEREI UM VENCEDOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('373', '373 - SE UM DIA DEUS PROMETEU', 'SE UM DIA DEUS PROMETEU A VITÓRIA TE CONCEDER, SE IMPOSSÍVEL PARECE SER TUA BÊNÇÃO ACONTECER, SABE QUE O QUE ELE PROMETEU É PODEROSO PRA CUMPRIR, O TEMPO DE DEUS VAI CHEGAR, ELE NÃO FALHARÁ.\n\nCORO SOMENTE CRER (SOMENTE CRER) E DESCANSAR (E DESCANSAR) CONFIA EM DEUS E TUDO MAIS (E TUDO MAIS) ELE FARÁ, NÃO TARDARÁ.\n\nSE NÃO PODES IMAGINAR COMO DEUS PODE OPERAR, DEIXA DIANTE DO ALTAR PRA QUE DEUS POSSA TE PROVAR QUE NÃO HÁ NADA IMPOSSÍVEL QUE DEUS NÃO POSSA RESOLVER, O SEU ANJO ENVIARÁ E A VITÓRIA ELE TRARÁ.\n\nCORO SOMENTE CRER (SOMENTE CRER) E DESCANSAR (E DESCANSAR) CONFIA EM DEUS E TUDO MAIS (E TUDO MAIS) ELE FARÁ, NÃO TARDARÁ. FINAL: ELE FARÁ.', 'SE UM DIA DEUS PROMETEU A VITÓRIA TE CONCEDER, SE IMPOSSÍVEL PARECE SER TUA BÊNÇÃO ACONTECER, SABE QUE O QUE ELE PROMETEU É PODEROSO PRA CUMPRIR, O TEMPO DE DEUS VAI CHEGAR, ELE NÃO FALHARÁ. CORO SOMENTE CRER (SOMENTE CRER) E DESCANSAR (E DESCANSAR) CONFIA EM DEUS E TUDO MAIS (E TUDO MAIS) ELE FARÁ, NÃO TARDARÁ. SE NÃO PODES IMAGINAR COMO DEUS PODE OPERAR, DEIXA DIANTE DO ALTAR PRA QUE DEUS POSSA TE PROVAR QUE NÃO HÁ NADA IMPOSSÍVEL QUE DEUS NÃO POSSA RESOLVER, O SEU ANJO ENVIARÁ E A VITÓRIA ELE TRARÁ. CORO SOMENTE CRER (SOMENTE CRER) E DESCANSAR (E DESCANSAR) CONFIA EM DEUS E TUDO MAIS (E TUDO MAIS) ELE FARÁ, NÃO TARDARÁ.  ELE FARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('374', '374 - QUERO TOCAR EM TUAS VESTES, SENHOR', 'QUERO TOCAR EM TUAS VESTES, SENHOR, COMO AQUELA MULHER TOCOU. E QUE SAIA, SENHOR, VIRTUDE, PRA QUE EU VEJA O TEU PODER.\nBIS\n\nCORO CREIO, SENHOR (CREIO, SENHOR) QUE ESTÁS AQUI E NÃO HÁ IMPOSSÍVEL PRA TI, (IMPOSSÍVEL NÃO HÁ) BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER. INSTRUMENTOS\n\nSE VOCÊ ESTÁ EM AFLIÇÕES E VÊ SEUS PROBLEMAS SEM SOLUÇÃO, O SENHOR AGORA AO SEU LADO ESTÁ, BASTA EM SUAS VESTES TOCAR.\n\nCORO (2X) CREIO, SENHOR (CREIO, SENHOR) QUE ESTÁS AQUI E NÃO HÁ IMPOSSÍVEL PRA TI, (IMPOSSÍVEL NÃO HÁ) BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER.\n\nFINAL: BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER.', 'QUERO TOCAR EM TUAS VESTES, SENHOR, COMO AQUELA MULHER TOCOU. E QUE SAIA, SENHOR, VIRTUDE, PRA QUE EU VEJA O TEU PODER. CORO CREIO, SENHOR (CREIO, SENHOR) QUE ESTÁS AQUI E NÃO HÁ IMPOSSÍVEL PRA TI, (IMPOSSÍVEL NÃO HÁ) BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER. INSTRUMENTOS SE VOCÊ ESTÁ EM AFLIÇÕES E VÊ SEUS PROBLEMAS SEM SOLUÇÃO, O SENHOR AGORA AO SEU LADO ESTÁ, BASTA EM SUAS VESTES TOCAR. CORO  CREIO, SENHOR (CREIO, SENHOR) QUE ESTÁS AQUI E NÃO HÁ IMPOSSÍVEL PRA TI, (IMPOSSÍVEL NÃO HÁ) BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER. BASTA, SENHOR, UMA PALAVRA DIZER (UMA SÓ) PRA QUE EU VEJA O TEU PODER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('375', '375 - OS SEGREDOS DO TEU CORAÇÃO', 'OS SEGREDOS DO TEU CORAÇÃO DEUS CONHECE, DEUS CONHECE; TUA DOR, TUA INDAGAÇÃO DEUS CONHECE, SIM, DEUS CONHECE.\n\nCORO CRISTO QUER SEU AMOR REVELAR, OPERAR MARAVILHAS NESTE LUGAR; VIVO ESTÁ JESUS, PRONTO A AJUDAR AQUELE QUE QUER SUA LUZ.\n\nO QUE TU NÃO CONSEGUES FAZER CRISTO PODE, TUDO PODE; IMPOSSÍVEIS NÃO HÁ PRA QUEM CRER, CRISTO PODE, SIM, TUDO PODE.\n\nCORO CRISTO QUER SEU AMOR REVELAR, OPERAR MARAVILHAS NESTE LUGAR; VIVO ESTÁ JESUS, PRONTO A AJUDAR AQUELE QUE QUER SUA LUZ.', 'OS SEGREDOS DO TEU CORAÇÃO DEUS CONHECE, DEUS CONHECE; TUA DOR, TUA INDAGAÇÃO DEUS CONHECE, SIM, DEUS CONHECE. CORO CRISTO QUER SEU AMOR REVELAR, OPERAR MARAVILHAS NESTE LUGAR; VIVO ESTÁ JESUS, PRONTO A AJUDAR AQUELE QUE QUER SUA LUZ. O QUE TU NÃO CONSEGUES FAZER CRISTO PODE, TUDO PODE; IMPOSSÍVEIS NÃO HÁ PRA QUEM CRER, CRISTO PODE, SIM, TUDO PODE.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('376', '376 - O SENHOR TE GUIARÁ', 'O SENHOR TE GUIARÁ CONTINUAMENTE E FARTARÁ TUA ALMA EM LUGARES SECOS E FORTIFICARÁ TEUS OSSOS.\n\nE SERÁS COMO UM JARDIM REGADO E COMO UM MANANCIAL CUJAS ÁGUAS NUNCA FALTAM, NUNCA FALTAM, E SERÁS COMO UM MANANCIAL CUJAS ÁGUAS NUNCA FALTAM.\n\nO SENHOR TE GUIARÁ CONTINUAMENTE E FARTARÁ TUA ALMA EM LUGARES SECOS E FORTIFICARÁ TEUS OSSOS.\n\nE SERÁS COMO UM JARDIM REGADO E COMO UM MANANCIAL CUJAS ÁGUAS NUNCA FALTAM, NUNCA FALTAM, E SERÁS COMO UM MANANCIAL CUJAS ÁGUAS NUNCA FALTAM.', 'O SENHOR TE GUIARÁ CONTINUAMENTE E FARTARÁ TUA ALMA EM LUGARES SECOS E FORTIFICARÁ TEUS OSSOS. E SERÁS COMO UM JARDIM REGADO E COMO UM MANANCIAL CUJAS ÁGUAS NUNCA FALTAM, NUNCA FALTAM, E SERÁS COMO UM MANANCIAL CUJAS ÁGUAS NUNCA FALTAM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('377', '377 - DEUS É MINHA ROCHA', 'DEUS É MINHA ROCHA, MEU REFÚGIO E FORTALEZA. DO ALTÍSSIMO SE OUVIU UMA GRANDE VOZ. FLECHAS DISPARADAS E O INIMIGO EM RETIRADA. ESTENDEU DO ALTO A SUA PODEROSA MÃO. POIS QUEM É DEUS, SENÃO NOSSO DEUS? POIS QUEM É DEUS, SENÃO O SENHOR?\n\nCONDUZIU-ME PARA UM LUGAR MAIS ESPAÇOSO, E RETRIBUI-ME SEGUNDO A SUA JUSTIÇA. CELEBRAREI, POIS, ENTRE AS NAÇÕES Ó SENHOR, E CANTAREI LOUVOR AO TEU NOME. POIS QUEM É DEUS, SENÃO NOSSO DEUS? POIS QUEM É DEUS, SENÃO O SENHOR?\n\nROCHA ETERNA (ROCHA ETERNA) QUE FIRMA MEUS PASSOS (TU ÉS) PEDRA DE ESQUINA, ABRIGO SEGURO.\nBIS', 'DEUS É MINHA ROCHA, MEU REFÚGIO E FORTALEZA. DO ALTÍSSIMO SE OUVIU UMA GRANDE VOZ. FLECHAS DISPARADAS E O INIMIGO EM RETIRADA. ESTENDEU DO ALTO A SUA PODEROSA MÃO. POIS QUEM É DEUS, SENÃO NOSSO DEUS? POIS QUEM É DEUS, SENÃO O SENHOR? CONDUZIU-ME PARA UM LUGAR MAIS ESPAÇOSO, E RETRIBUI-ME SEGUNDO A SUA JUSTIÇA. CELEBRAREI, POIS, ENTRE AS NAÇÕES Ó SENHOR, E CANTAREI LOUVOR AO TEU NOME. POIS QUEM É DEUS, SENÃO NOSSO DEUS? POIS QUEM É DEUS, SENÃO O SENHOR? ROCHA ETERNA (ROCHA ETERNA) QUE FIRMA MEUS PASSOS (TU ÉS) PEDRA DE ESQUINA, ABRIGO SEGURO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('378', '378 - CONFIA E ORA', 'CONFIA E ORA, CERTAMENTE O SENHOR TE OUVIRÁ. ENTREGA AGORA, EM SUAS MÃOS, E TUDO VAI PASSAR, TUDO ELE MUDARÁ. E TUDO VAI PASSAR, TUDO ELE MUDARÁ.\n\nO TEU DEUS PODEROSO É EM SUAS MÃOS VOCÊ AGORA ESTÁ. MESMO EM TEMPESTADES, OU NA FÚRIA DO MAR, O SENHOR TE ESCONDERÁ.\n\nO TEU DEUS ESTARÁ CONTIGO ONDE QUER QUE FOR ELE É FIEL E NÃO FALHARÁ. CONFIA NO SENHOR, ELE É FIEL E NÃO FALHARÁ, CONFIA NO SENHOR. INSTRUMENTOS\n\nFINAL: CONFIA E ORA, CERTAMENTE O SENHOR TE OUVIRÁ. ENTREGA AGORA, EM SUAS MÃOS, E TUDO VAI PASSAR, TUDO ELE MUDARÁ. E TUDO VAI PASSAR.\nBIS', 'CONFIA E ORA, CERTAMENTE O SENHOR TE OUVIRÁ. ENTREGA AGORA, EM SUAS MÃOS, E TUDO VAI PASSAR, TUDO ELE MUDARÁ. E TUDO VAI PASSAR, TUDO ELE MUDARÁ. O TEU DEUS PODEROSO É EM SUAS MÃOS VOCÊ AGORA ESTÁ. MESMO EM TEMPESTADES, OU NA FÚRIA DO MAR, O SENHOR TE ESCONDERÁ. O TEU DEUS ESTARÁ CONTIGO ONDE QUER QUE FOR ELE É FIEL E NÃO FALHARÁ. CONFIA NO SENHOR, ELE É FIEL E NÃO FALHARÁ, CONFIA NO SENHOR. INSTRUMENTOS CONFIA E ORA, CERTAMENTE O SENHOR TE OUVIRÁ. ENTREGA AGORA, EM SUAS MÃOS, E TUDO VAI PASSAR, TUDO ELE MUDARÁ. E TUDO VAI PASSAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('379', '379 - ALGEMADO POR UM PESO', 'ALGEMADO POR UM PESO, OH! QUÃO TRISTE EU ANDEI, TÉ SENTIR A MÃO DE CRISTO, NÃO SOU MAIS COMO ERA, EU SEI.\n\nCORO TOCOU-ME, JESUS, TOCOU-ME, DE PAZ ELE ENCHEU MEU CORAÇÃO. QUANDO O SENHOR JESUS ME TOCOU, LIVROU-ME DA ESCURIDÃO.\n\nDESDE QUE ENCONTREI A CRISTO, E SENTI SEU TERNO AMOR, TENHO ACHADO PAZ E VIDA, PRA SEMPRE CANTAREI EM SEU LOUVOR.\n\nCORO TOCOU-ME, JESUS, TOCOU-ME, DE PAZ ELE ENCHEU MEU CORAÇÃO. QUANDO O SENHOR JESUS ME TOCOU, LIVROU-ME DA ESCURIDÃO.', 'ALGEMADO POR UM PESO, OH! QUÃO TRISTE EU ANDEI, TÉ SENTIR A MÃO DE CRISTO, NÃO SOU MAIS COMO ERA, EU SEI. CORO TOCOU-ME, JESUS, TOCOU-ME, DE PAZ ELE ENCHEU MEU CORAÇÃO. QUANDO O SENHOR JESUS ME TOCOU, LIVROU-ME DA ESCURIDÃO. DESDE QUE ENCONTREI A CRISTO, E SENTI SEU TERNO AMOR, TENHO ACHADO PAZ E VIDA, PRA SEMPRE CANTAREI EM SEU LOUVOR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('380', '380 - POSSO SENTIR O SEU TOQUE EM MIM (GLORIFICA)', 'POSSO SENTIR O SEU TOQUE EM MIM, SINTO O SEU CALOR AQUECENDO O CORAÇÃO. POSSO OUVIR SUA VOZ A SOAR: “MEU SERVO, NÃO TEMAS! VOU TE AJUDAR. QUANDO AS LUTAS CHEGAREM, NÃO OLHES PARA TRÁS OLHA PARA O ALTO E A VITÓRIA ALCANÇARÁS!”\n\nCORO É DO ALTO QUE VÊM NOSSAS VITÓRIAS, CRISTO ESTÁ PRONTO A TE ESCUTAR. NÃO ACHES QUE TUDO TERMINOU, POIS DEUS TRAVA UMA BATALHA EM TEU FAVOR! GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR.\n\nCORO É DO ALTO QUE VÊM NOSSAS VITÓRIAS, CRISTO ESTÁ PRONTO A TE ESCUTAR. NÃO ACHES QUE TUDO TERMINOU, POIS DEUS TRAVA UMA BATALHA EM TEU FAVOR! GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. FINAL: GLORIFICA!', 'POSSO SENTIR O SEU TOQUE EM MIM, SINTO O SEU CALOR AQUECENDO O CORAÇÃO. POSSO OUVIR SUA VOZ A SOAR: MEU SERVO, NÃO TEMAS! VOU TE AJUDAR. QUANDO AS LUTAS CHEGAREM, NÃO OLHES PARA TRÁS OLHA PARA O ALTO E A VITÓRIA ALCANÇARÁS! CORO É DO ALTO QUE VÊM NOSSAS VITÓRIAS, CRISTO ESTÁ PRONTO A TE ESCUTAR. NÃO ACHES QUE TUDO TERMINOU, POIS DEUS TRAVA UMA BATALHA EM TEU FAVOR! GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. CORO É DO ALTO QUE VÊM NOSSAS VITÓRIAS, CRISTO ESTÁ PRONTO A TE ESCUTAR. NÃO ACHES QUE TUDO TERMINOU, POIS DEUS TRAVA UMA BATALHA EM TEU FAVOR! GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR. GLORIFICA, SÓ GLORIFICA! ERGUE A VOZ E AGRADECE AO REI SENHOR.  GLORIFICA!',  1);
