INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('382', '382 - JÁ CHEGUEI ATÉ AQUI (VITÓRIAS)', 'JÁ CHEGUEI ATÉ AQUI, EU NÃO POSSO DESISTIR, DEVO ACHAR FORÇAS PRA ANDAR. AS LUTAS VIERAM, MAS TAMBÉM PASSARAM, VENDAVAIS TENTARAM ME DERRUBAR.\n\nMAS BUSQUEI A CRISTO, MEU FIEL AMIGO, QUE ESTEVE COMIGO TODO DIA MESMO QUE CHORANDO, MESMO QUE SOFRENDO, JÁ ESTAVA CERTO QUE A VITÓRIA GANHARIA NO AMANHÃ. INSTRUMENTOS\n\nJÁ CHEGAMOS ATÉ AQUI, NÃO PODEMOS DESISTIR, VAMOS ACHAR FORÇAS PRA ANDAR. AS LUTAS VIERAM, MAS TAMBÉM PASSARAM, VENDAVAIS TENTARAM NOS DERRUBAR.\n\nNADA NOS FALTOU, TUDO DEUS PROVEU, NOS DEU COROA PRA GUARDAR. E FEZ TUDO NOVO COM AMOR GLORIOSO, E NOS DEU VITÓRIA ONTEM, HOJE E NOS DARÁ NO AMANHÃ.\nBIS', 'JÁ CHEGUEI ATÉ AQUI, EU NÃO POSSO DESISTIR, DEVO ACHAR FORÇAS PRA ANDAR. AS LUTAS VIERAM, MAS TAMBÉM PASSARAM, VENDAVAIS TENTARAM ME DERRUBAR. MAS BUSQUEI A CRISTO, MEU FIEL AMIGO, QUE ESTEVE COMIGO TODO DIA MESMO QUE CHORANDO, MESMO QUE SOFRENDO, JÁ ESTAVA CERTO QUE A VITÓRIA GANHARIA NO AMANHÃ. INSTRUMENTOS JÁ CHEGAMOS ATÉ AQUI, NÃO PODEMOS DESISTIR, VAMOS ACHAR FORÇAS PRA ANDAR. AS LUTAS VIERAM, MAS TAMBÉM PASSARAM, VENDAVAIS TENTARAM NOS DERRUBAR. NADA NOS FALTOU, TUDO DEUS PROVEU, NOS DEU COROA PRA GUARDAR. E FEZ TUDO NOVO COM AMOR GLORIOSO, E NOS DEU VITÓRIA ONTEM, HOJE E NOS DARÁ NO AMANHÃ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('383', '383 - LÁ ONDE EU NÃO POSSO VER (A ORAÇÃO)', 'LÁ ONDE EU NÃO POSSO VER, ONDE NÃO POSSO ESTAR, TEM PESSOAS QUE EU NÃO POSSO AJUDAR, SEUS PROBLEMAS RESOLVER, MAS EU SEI QUE POSSO ORAR.\n\nM\n\nT\n\nMEUS TEMORES E AFLIÇÕES, MINHA DOR, DECEPÇÕES. APRENDI A COLOCAR MINHA VIDA SOBRE O ALTAR, ENTÃO SEI QUE POSSO ORAR E EM DEUS POSSO CONFIAR.\n\nH\n\nT\n\nCORO A ORAÇÃO VAI AONDE EU NÃO POSSO IR, DEUS PROMETEU ME SOCORRER QUANDO EU PEDIR. A ORAÇÃO DOS QUE AMAM A DEUS PODE MUITO EM SEU PODER, E QUANDO ESTOU EM ORAÇÃO, TUDO PODE ACONTECER.\n\nSOU LAVADO E REMIDO, NÃO PRECISO TEMER POIS O SANGUE DE JESUS É QUE TEM O PODER, DE ME FAZER ENTRAR NA PRESENÇA DE DEUS, NÃO HÁ MAIS IMPEDIMENTO DE FICAR MAIS PERTO DE DEUS.\n\nM\n\nH\n\nT\n\nCORO (2X) A ORAÇÃO VAI AONDE EU NÃO POSSO IR, DEUS PROMETEU ME SOCORRER QUANDO EU PEDIR. A ORAÇÃO DOS QUE AMAM A DEUS PODE MUITO EM SEU PODER, E QUANDO ESTOU EM ORAÇÃO, TUDO PODE ACONTECER.', 'LÁ ONDE EU NÃO POSSO VER, ONDE NÃO POSSO ESTAR, TEM PESSOAS QUE EU NÃO POSSO AJUDAR, SEUS PROBLEMAS RESOLVER, MAS EU SEI QUE POSSO ORAR. MEUS TEMORES E AFLIÇÕES, MINHA DOR, DECEPÇÕES. APRENDI A COLOCAR MINHA VIDA SOBRE O ALTAR, ENTÃO SEI QUE POSSO ORAR E EM DEUS POSSO CONFIAR. CORO A ORAÇÃO VAI AONDE EU NÃO POSSO IR, DEUS PROMETEU ME SOCORRER QUANDO EU PEDIR. A ORAÇÃO DOS QUE AMAM A DEUS PODE MUITO EM SEU PODER, E QUANDO ESTOU EM ORAÇÃO, TUDO PODE ACONTECER. SOU LAVADO E REMIDO, NÃO PRECISO TEMER POIS O SANGUE DE JESUS É QUE TEM O PODER, DE ME FAZER ENTRAR NA PRESENÇA DE DEUS, NÃO HÁ MAIS IMPEDIMENTO DE FICAR MAIS PERTO DE DEUS. CORO  A ORAÇÃO VAI AONDE EU NÃO POSSO IR, DEUS PROMETEU ME SOCORRER QUANDO EU PEDIR. A ORAÇÃO DOS QUE AMAM A DEUS PODE MUITO EM SEU PODER, E QUANDO ESTOU EM ORAÇÃO, TUDO PODE ACONTECER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('384', '384 - BEM SEI QUE TUDO PODES', '384 - BEM SEI QUE TUDO PODES\n\nBEM SEI QUE TUDO PODES, MEU AMADO. NENHUM DOS TEUS PLANOS É FRUSTRADO. AINDA QUE COM PERDAS E COM DOR EU SEMPRE SEGUIREI A TI, SENHOR.\n\nOS TEUS CAMINHOS POSSO NÃO ENTENDER, SENHOR, MAS SEI QUE TUDO É VISANDO O MEU CRESCER. SE LUTAS E TRIBULAÇÕES EU TENHO QUE PASSAR, TE PEÇO FORÇAS PRA CONTINUAR.\n\nCONTINUAR A CRER E A CONFIAR, NO GRANDE AMOR QUE TENS, MEU PAI, POR MIM. O MEU DESEJO ETERNO É TE ADORAR, E AOS TEUS PÉS ME DERRAMAR SEM FIM.\n\nBEM SEI QUE TUDO PODES, MEU AMADO. NENHUM DOS TEUS PLANOS É FRUSTRADO. AINDA QUE COM PERDAS E COM DOR EU SEMPRE SEGUIREI A TI, SENHOR.\n\nOS TEUS CAMINHOS POSSO NÃO ENTENDER, SENHOR, MAS SEI QUE TUDO É VISANDO O MEU CRESCER. SE LUTAS E TRIBULAÇÕES EU TENHO QUE PASSAR, TE PEÇO FORÇAS PRA CONTINUAR.\n\nCONTINUAR A CRER E A CONFIAR, NO GRANDE AMOR QUE TENS, MEU PAI, POR MIM. O MEU DESEJO ETERNO É TE ADORAR, E AOS TEUS PÉS ME DERRAMAR SEM FIM. FINAL: E AOS TEUS PÉS ME DERRAMAR SEM FIM.', '384 - BEM SEI QUE TUDO PODES BEM SEI QUE TUDO PODES, MEU AMADO. NENHUM DOS TEUS PLANOS É FRUSTRADO. AINDA QUE COM PERDAS E COM DOR EU SEMPRE SEGUIREI A TI, SENHOR. OS TEUS CAMINHOS POSSO NÃO ENTENDER, SENHOR, MAS SEI QUE TUDO É VISANDO O MEU CRESCER. SE LUTAS E TRIBULAÇÕES EU TENHO QUE PASSAR, TE PEÇO FORÇAS PRA CONTINUAR. CONTINUAR A CRER E A CONFIAR, NO GRANDE AMOR QUE TENS, MEU PAI, POR MIM. O MEU DESEJO ETERNO É TE ADORAR, E AOS TEUS PÉS ME DERRAMAR SEM FIM. CONTINUAR A CRER E A CONFIAR, NO GRANDE AMOR QUE TENS, MEU PAI, POR MIM. O MEU DESEJO ETERNO É TE ADORAR, E AOS TEUS PÉS ME DERRAMAR SEM FIM.  E AOS TEUS PÉS ME DERRAMAR SEM FIM.',  1);
