INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('386', '386 - QUAL O ADORNO DESTA VIDA?', 'QUAL O ADORNO DESTA VIDA? É O AMOR, É O AMOR; ALEGRIA É CONCEDIDA PELO AMOR, PELO AMOR; É BENIGNO, É PACIENTE, NÃO SE TORNA MALDIZENTE, (BIS) ESTE MEIGO AMOR.\n\nCOM SUSPEITAS NÃO SE ALCANÇA DOCE AMOR, DOCE AMOR; ONDE HOUVER DESCONFIANÇA, AI DO AMOR, AI DO AMOR; POIS AMEMOS A VERDADE, ONDE REINA A FALSIDADE, (BIS) NÃO PERDURA O AMOR.\n\nNÃO TE IRRITES, MAS TOLERA COM AMOR, COM AMOR; TUDO SOFRE, TUDO ESPERA PELO AMOR, PELO AMOR; POIS MOSTREMOS TOLERÂNCIA, MUITAS VEZES A ARROGÂNCIA, (BIS) MURCHA E MATA O AMOR.\n\nÓ CRISTÃO, AO TEU VIZINHO MOSTRA AMOR, MOSTRA AMOR; O VALOR NÃO É MESQUINHO DESTE AMOR, DESTE AMOR; O SUPREMO DEUS NOS AMA; CRISTO PARA O CÉU NOS CHAMA, (BIS) ONDE REINA O AMOR!', 'QUAL O ADORNO DESTA VIDA? É O AMOR, É O AMOR; ALEGRIA É CONCEDIDA PELO AMOR, PELO AMOR; É BENIGNO, É PACIENTE, NÃO SE TORNA MALDIZENTE,  ESTE MEIGO AMOR. COM SUSPEITAS NÃO SE ALCANÇA DOCE AMOR, DOCE AMOR; ONDE HOUVER DESCONFIANÇA, AI DO AMOR, AI DO AMOR; POIS AMEMOS A VERDADE, ONDE REINA A FALSIDADE,  NÃO PERDURA O AMOR. NÃO TE IRRITES, MAS TOLERA COM AMOR, COM AMOR; TUDO SOFRE, TUDO ESPERA PELO AMOR, PELO AMOR; POIS MOSTREMOS TOLERÂNCIA, MUITAS VEZES A ARROGÂNCIA,  MURCHA E MATA O AMOR. Ó CRISTÃO, AO TEU VIZINHO MOSTRA AMOR, MOSTRA AMOR; O VALOR NÃO É MESQUINHO DESTE AMOR, DESTE AMOR; O SUPREMO DEUS NOS AMA; CRISTO PARA O CÉU NOS CHAMA,  ONDE REINA O AMOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('387', '387 - SONDA-ME, Ó DEUS', 'SONDA-ME, Ó DEUS, POIS VÊS MEU CORAÇÃO, PROVA-ME, Ó PAI, TE PEÇO EM ORAÇÃO; DE TODO O MAL LIBERTA-ME, SENHOR,TAMBÉM DA TRANSGRESSÃO QUE OCULTA FOR.\n\nTODO O MEU SER QUE JÁ NÃO CHAMO MEU, QUERO GASTÁ-LO NO SERVIÇO TEU; MINHAS PAIXÕES TU PODES DOMINAR, EU ME SUBMETO, EM MIM VEM SEMPRE ESTAR.\n\nLÁ DO ALTO CÉU O AVIVAMENTO VEM A COMEÇAR EM MIM E INDO ALÉM; O TEU PODER, TUAS BÊNÇÃOS, TEU FAVOR, CONCEDE AOS FILHOS TEUS, Ó PAI DE AMOR.', 'SONDA-ME, Ó DEUS, POIS VÊS MEU CORAÇÃO, PROVA-ME, Ó PAI, TE PEÇO EM ORAÇÃO; DE TODO O MAL LIBERTA-ME, SENHOR,TAMBÉM DA TRANSGRESSÃO QUE OCULTA FOR. TODO O MEU SER QUE JÁ NÃO CHAMO MEU, QUERO GASTÁ-LO NO SERVIÇO TEU; MINHAS PAIXÕES TU PODES DOMINAR, EU ME SUBMETO, EM MIM VEM SEMPRE ESTAR. LÁ DO ALTO CÉU O AVIVAMENTO VEM A COMEÇAR EM MIM E INDO ALÉM; O TEU PODER, TUAS BÊNÇÃOS, TEU FAVOR, CONCEDE AOS FILHOS TEUS, Ó PAI DE AMOR.',  1);
