INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('393', '393 - QUERO SER FIEL AO MEU SENHOR', 'QUERO SER FIEL AO MEU SENHOR, SEGUIR COMO ENSINOU ESTE CAMINHO, LEVAR ESTA MENSAGEM DE AMOR, SEGUIR AO SALVADOR.\n\nCORO QUERO TER A PAZ QUE CRISTO DÁ, SENTIR O SEU AMOR POR MIM ASSIM, NÃO TEMER A LUTA QUE VIER, SENHOR, AJUDA-ME.\n\nE QUANDO ME ENCONTRAR AFLITO ASSIM, LEMBRAR QUE MEU JESUS TANTO SOFREU POR MIM, E VIVO ANSIOSO A ESPERAR MORAR LÁ NO PORVIR.\n\nCORO (2X) QUERO TER A PAZ QUE CRISTO DÁ, SENTIR O SEU AMOR POR MIM ASSIM, NÃO TEMER A LUTA QUE VIER, SENHOR, AJUDA-ME. FINAL: SENHOR, AJUDA-ME.', 'QUERO SER FIEL AO MEU SENHOR, SEGUIR COMO ENSINOU ESTE CAMINHO, LEVAR ESTA MENSAGEM DE AMOR, SEGUIR AO SALVADOR. CORO QUERO TER A PAZ QUE CRISTO DÁ, SENTIR O SEU AMOR POR MIM ASSIM, NÃO TEMER A LUTA QUE VIER, SENHOR, AJUDA-ME. E QUANDO ME ENCONTRAR AFLITO ASSIM, LEMBRAR QUE MEU JESUS TANTO SOFREU POR MIM, E VIVO ANSIOSO A ESPERAR MORAR LÁ NO PORVIR. CORO  QUERO TER A PAZ QUE CRISTO DÁ, SENTIR O SEU AMOR POR MIM ASSIM, NÃO TEMER A LUTA QUE VIER, SENHOR, AJUDA-ME.  SENHOR, AJUDA-ME.',  1);
