INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('399', '399 - QUAL MADEIRA', 'QUAL MADEIRA INÚTIL ASSIM, FUI PLANTADO, NO DESERTO DO MUNDO CRUEL FUI DEIXADO; MAS JESUS ME ENCONTROU E ME LIBERTOU DE UMA VIDA VAZIA, SEM PAZ, SEM AMOR. INSTRUMENTOS\n\nDE MINH’ALMA TIROU O MEU FARDO TÃO PESADO, QUAL A CASCA NA DURA MADEIRA, MEU PECADO. TRABALHOU-ME POR INTEIRO, COMO UM BOM CARPINTEIRO, FEZ-ME VASO DE HONRA, DE GLÓRIA E LOUVOR.\nBIS', 'QUAL MADEIRA INÚTIL ASSIM, FUI PLANTADO, NO DESERTO DO MUNDO CRUEL FUI DEIXADO; MAS JESUS ME ENCONTROU E ME LIBERTOU DE UMA VIDA VAZIA, SEM PAZ, SEM AMOR. INSTRUMENTOS DE MINH’ALMA TIROU O MEU FARDO TÃO PESADO, QUAL A CASCA NA DURA MADEIRA, MEU PECADO. TRABALHOU-ME POR INTEIRO, COMO UM BOM CARPINTEIRO, FEZ-ME VASO DE HONRA, DE GLÓRIA E LOUVOR.',  1);
