INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('402', '402 - A LEI DO SENHOR É PERFEITA', 'A LEI DO SENHOR É PERFEITA E RESTAURA A ALMA; O TESTEMUNHO DO SENHOR É FIEL E DÁ SABEDORIA AOS SÍMPLICES.\n\nCORO SÃO MAIS DESEJÁVEIS DO QUE O OURO DEPURADO, SÃO MAIS DOCES DO QUE O MEL E O DESTILAR DOS FAVOS.\n\nOS PRECEITOS DO SENHOR SÃO RETOS E ALEGRAM O CORAÇÃO; O MANDAMENTO DO SENHOR É PURO E ILUMINA OS OLHOS.\n\nCORO SÃO MAIS DESEJÁVEIS DO QUE O OURO DEPURADO, SÃO MAIS DOCES DO QUE O MEL E O DESTILAR DOS FAVOS.\n\nO TEMOR DO SENHOR É LÍMPIDO E PERMANECE PARA SEMPRE; OS JUÍZOS DO SENHOR SÃO VERDADEIROS E TODOS IGUALMENTE JUSTOS.\n\nCORO SÃO MAIS DESEJÁVEIS DO QUE O OURO DEPURADO, SÃO MAIS DOCES DO QUE O MEL E O DESTILAR DOS FAVOS.', 'A LEI DO SENHOR É PERFEITA E RESTAURA A ALMA; O TESTEMUNHO DO SENHOR É FIEL E DÁ SABEDORIA AOS SÍMPLICES. CORO SÃO MAIS DESEJÁVEIS DO QUE O OURO DEPURADO, SÃO MAIS DOCES DO QUE O MEL E O DESTILAR DOS FAVOS. OS PRECEITOS DO SENHOR SÃO RETOS E ALEGRAM O CORAÇÃO; O MANDAMENTO DO SENHOR É PURO E ILUMINA OS OLHOS. O TEMOR DO SENHOR É LÍMPIDO E PERMANECE PARA SEMPRE; OS JUÍZOS DO SENHOR SÃO VERDADEIROS E TODOS IGUALMENTE JUSTOS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('403', '403 - AQUELE QUE TEM OS MEUS MANDAMENTOS', 'AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA.\n\nE AQUELE QUE ME AMA SERÁ AMADO POR MEU PAI, E EU TAMBÉM O AMAREI, E ME MANIFESTAREI A ELE. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. INSTRUMENTOS\n\nE AQUELE QUE ME AMA SERÁ AMADO POR MEU PAI, E EU TAMBÉM O AMAREI, E ME MANIFESTAREI A ELE. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA.\n\nAQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. FINAL: ESSE É O QUE ME AMA. (BIS)', 'AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. E AQUELE QUE ME AMA SERÁ AMADO POR MEU PAI, E EU TAMBÉM O AMAREI, E ME MANIFESTAREI A ELE. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. INSTRUMENTOS E AQUELE QUE ME AMA SERÁ AMADO POR MEU PAI, E EU TAMBÉM O AMAREI, E ME MANIFESTAREI A ELE. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA. AQUELE QUE TEM OS MEUS MANDAMENTOS E OS GUARDA, ESSE É O QUE ME AMA.  ESSE É O QUE ME AMA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('404', '404 - JESUS, QUERO AMAR-TE', 'JESUS, QUERO AMAR-TE ATÉ MORRER QUERO SEGUIR-TE EM MEU VIVER, PARA O CÉU DE AMOR GANHAR. CORO (2X) OH! ALELUIA, AMÉM, AMÉM! (3X) SENHOR!\nBIS\n\nJESUS, TODO O MEU SER QUERO TE DAR, SE FOR PRECISO ATÉ MORRER PARA O CÉU DE AMOR GANHAR; PARA TE DEDICAR AQUILO QUE ME CONCEDESTE E A TUA OBRA REALIZAR. CORO (2X) OH! ALELUIA, AMÉM, AMÉM! (3X) SENHOR!', 'JESUS, QUERO AMAR-TE ATÉ MORRER QUERO SEGUIR-TE EM MEU VIVER, PARA O CÉU DE AMOR GANHAR. CORO  OH! ALELUIA, AMÉM, AMÉM!  SENHOR! JESUS, TODO O MEU SER QUERO TE DAR, SE FOR PRECISO ATÉ MORRER PARA O CÉU DE AMOR GANHAR; PARA TE DEDICAR AQUILO QUE ME CONCEDESTE E A TUA OBRA REALIZAR. CORO  OH! ALELUIA, AMÉM, AMÉM!  SENHOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('405', '405 - AMO AO SENHOR DE TODO O MEU CORAÇÃO', 'AMO AO SENHOR DE TODO O MEU CORAÇÃO; NA PRESENÇA DE TODOS OS POVOS A TI, SENHOR, LOUVAREI. A TI SERVIREI, SIM, SERVIREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI.\n\nNÃO ME DEIXES CAIR, SEGURA EM MINHAS MÃOS, FAZE DE MIM UM VENCEDOR PRA QUE EU POSSA TE LOUVAR.\n\nA TI LOUVAREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI LOUVAREI; A TI SERVIREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI, SERVIREI. INSTRUMENTOS\n\nA TI LOUVAREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI LOUVAREI; A TI SERVIREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI, SERVIREI.', 'AMO AO SENHOR DE TODO O MEU CORAÇÃO; NA PRESENÇA DE TODOS OS POVOS A TI, SENHOR, LOUVAREI. A TI SERVIREI, SIM, SERVIREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI. NÃO ME DEIXES CAIR, SEGURA EM MINHAS MÃOS, FAZE DE MIM UM VENCEDOR PRA QUE EU POSSA TE LOUVAR. A TI LOUVAREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI LOUVAREI; A TI SERVIREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI, SERVIREI. INSTRUMENTOS A TI LOUVAREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI LOUVAREI; A TI SERVIREI, SIM, LOUVAREI, ENQUANTO EU VIVER, SENHOR, A TI SERVIREI, SERVIREI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('406', '406 - JESUS, O QUE MAIS QUERO NO MUNDO', 'JESUS, O QUE EU MAIS QUERO NO MUNDO É VIVER PARA TI, TE SERVIR POIS FIZESTE JÁ TUDO POR MIM; MINHA VIDA MUDASTE E TEU AMOR ME MOSTRASTE, MUITAS COISAS EU PUDE, ENTÃO, VER, GRANDE AMOR INUNDOU O MEU SER, NÃO REJEITASTE O MEU CORAÇÃO QUANDO CURVEI-ME A TI EM ORAÇÃO.\n\nCORO TUDO NA VIDA DEPENDE DE FÉ, DE CONFIARMOS EM TI, COMO ÉS; SEMPRE ME GUIAS, ÉS DEUS DE AMOR; CRISTO, ÉS TUDO PARA MIM. INSTRUMENTOS\n\nCORO TUDO NA VIDA DEPENDE DE FÉ, DE CONFIARMOS EM TI, COMO ÉS; SEMPRE ME GUIAS, ÉS DEUS DE AMOR; CRISTO, ÉS TUDO PARA MIM. FINAL: CRISTO, ÉS TUDO PARA MIM.', 'JESUS, O QUE EU MAIS QUERO NO MUNDO É VIVER PARA TI, TE SERVIR POIS FIZESTE JÁ TUDO POR MIM; MINHA VIDA MUDASTE E TEU AMOR ME MOSTRASTE, MUITAS COISAS EU PUDE, ENTÃO, VER, GRANDE AMOR INUNDOU O MEU SER, NÃO REJEITASTE O MEU CORAÇÃO QUANDO CURVEI-ME A TI EM ORAÇÃO. CORO TUDO NA VIDA DEPENDE DE FÉ, DE CONFIARMOS EM TI, COMO ÉS; SEMPRE ME GUIAS, ÉS DEUS DE AMOR; CRISTO, ÉS TUDO PARA MIM. INSTRUMENTOS CORO TUDO NA VIDA DEPENDE DE FÉ, DE CONFIARMOS EM TI, COMO ÉS; SEMPRE ME GUIAS, ÉS DEUS DE AMOR; CRISTO, ÉS TUDO PARA MIM.  CRISTO, ÉS TUDO PARA MIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('407', '407 - EU VI O SENHOR', 'EU VI O SENHOR NUM ALTO E SUBLIME TRONO E AS ABAS DE SUAS VESTES ENCHIAM O TEMPLO; OS ANJOS VOAVAM POR SOBRE O SEU TRONO, COBRIAM O ROSTO E OS PÉS, UNS AOS OUTROS CLAMAVAM:\n\nCORO (2X) SANTO, SANTO, SANTO, É O SENHOR DOS EXÉRCITOS, TODA A TERRA ESTÁ CHEIA DA SUA GLÓRIA! INSTRUMENTOS\n\nENTÃO DISSE EU: SOU UM HOMEM IMPURO E OS MEUS OLHOS VIRAM O REI, O SENHOR DOS EXÉRCITOS! COM UMA BRASA DO ALTAR OS MEUS LÁBIOS FORAM TOCADOS, MEU PECADO FOI PERDOADO E OUVI A VOZ DO SENHOR:\nBIS\n\n“A QUEM ENVIAREI, E QUEM HÁ DE IR POR NÓS?” DISSE EU: EIS-ME AQUI, ENVIA-ME A MIM.\n\nCORO (2X) SANTO, SANTO, SANTO, É O SENHOR DOS EXÉRCITOS, TODA A TERRA ESTÁ CHEIA DA SUA GLÓRIA!', 'EU VI O SENHOR NUM ALTO E SUBLIME TRONO E AS ABAS DE SUAS VESTES ENCHIAM O TEMPLO; OS ANJOS VOAVAM POR SOBRE O SEU TRONO, COBRIAM O ROSTO E OS PÉS, UNS AOS OUTROS CLAMAVAM: CORO  SANTO, SANTO, SANTO, É O SENHOR DOS EXÉRCITOS, TODA A TERRA ESTÁ CHEIA DA SUA GLÓRIA! INSTRUMENTOS ENTÃO DISSE EU: SOU UM HOMEM IMPURO E OS MEUS OLHOS VIRAM O REI, O SENHOR DOS EXÉRCITOS! COM UMA BRASA DO ALTAR OS MEUS LÁBIOS FORAM TOCADOS, MEU PECADO FOI PERDOADO E OUVI A VOZ DO SENHOR: A QUEM ENVIAREI, E QUEM HÁ DE IR POR NÓS? DISSE EU: EIS-ME AQUI, ENVIA-ME A MIM. CORO  SANTO, SANTO, SANTO, É O SENHOR DOS EXÉRCITOS, TODA A TERRA ESTÁ CHEIA DA SUA GLÓRIA!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('408', '408 - EIS QUE SURGE UM POVO FORTE', 'EIS QUE SURGE UM POVO FORTE, REVESTIDO DE PODER; E NÃO TEME NEM A MORTE, QUEM A ELE PERTENCER, E TERÁ SUBLIME SORTE, POIS COM CRISTO AO CÉU VAI, PODES TU DIZER TAMBÉM: SOU UM DOS TAIS?\n\nCORO (2X) UM DOS TAIS, UM DOS TAIS, PODES TU TAMBÉM DIZER: SOU UM DOS TAIS?\n\nNO CENÁCULO REUNIDOS, O PODER BUSCAVAM ENTÃO, PELO AMOR DE DEUS UNIDOS A CLAMAR EM ORAÇÃO; EIS QUE UM VENTO É DESCIDO E O FOGO DO CÉU CAI; PODES TU DIZER TAMBÉM: SOU UM DOS TAIS?\n\nCORO (2X) UM DOS TAIS, UM DOS TAIS, PODES TU TAMBÉM DIZER: SOU UM DOS TAIS?\n\nESTE POVO DESTEMIDO, SÃO DISCÍPULOS DE JESUS PELO MUNDO PERSEGUIDO, POR LEVAR A SUA CRUZ, E AGORA REVESTIDO COM PODER, AO MUNDO SAI; PODES TU DIZER TAMBÉM: SOU UM DOS TAIS?\n\nCORO (2X) UM DOS TAIS, UM DOS TAIS, PODES TU TAMBÉM DIZER: SOU UM DOS TAIS?\n\nOH! NÃO SEJAS DESCUIDOSO PRA BUSCAR O DOM DE DEUS, DOM QUE TE FARÁ DITOSO, DAR-TE-Á VISÕES DO CÉU, E JESUS MARAVILHOSO PROCLAMANDO AOS OUTROS VAI; PODERÁS ENTÃO DIZER: SOU UM DOS TAIS.\n\nCORO (2X) UM DOS TAIS, UM DOS TAIS, PODES TU TAMBÉM DIZER: SOU UM DOS TAIS?', 'EIS QUE SURGE UM POVO FORTE, REVESTIDO DE PODER; E NÃO TEME NEM A MORTE, QUEM A ELE PERTENCER, E TERÁ SUBLIME SORTE, POIS COM CRISTO AO CÉU VAI, PODES TU DIZER TAMBÉM: SOU UM DOS TAIS? CORO  UM DOS TAIS, UM DOS TAIS, PODES TU TAMBÉM DIZER: SOU UM DOS TAIS? NO CENÁCULO REUNIDOS, O PODER BUSCAVAM ENTÃO, PELO AMOR DE DEUS UNIDOS A CLAMAR EM ORAÇÃO; EIS QUE UM VENTO É DESCIDO E O FOGO DO CÉU CAI; PODES TU DIZER TAMBÉM: SOU UM DOS TAIS? ESTE POVO DESTEMIDO, SÃO DISCÍPULOS DE JESUS PELO MUNDO PERSEGUIDO, POR LEVAR A SUA CRUZ, E AGORA REVESTIDO COM PODER, AO MUNDO SAI; PODES TU DIZER TAMBÉM: SOU UM DOS TAIS? OH! NÃO SEJAS DESCUIDOSO PRA BUSCAR O DOM DE DEUS, DOM QUE TE FARÁ DITOSO, DAR-TE-Á VISÕES DO CÉU, E JESUS MARAVILHOSO PROCLAMANDO AOS OUTROS VAI; PODERÁS ENTÃO DIZER: SOU UM DOS TAIS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('409', '409 - EIS MARCHAMOS PARA AQUELE BOM PAÍS', 'EIS MARCHAMOS PARA AQUELE BOM PAÍS, ONDE O CRENTE, SIM, É CRISTO QUEM O DIZ COM SEU SALVADOR, PRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR;\n\nTRABALHEMOS POIS, COM ZELO E COM VIGOR, CONSTRANGIDOS PELO SEU IMENSO AMOR; TRABALHEMOS PELO NOSSO SALVADOR, EIS QUE A VIDA VAI FINDAR!\n\nCORO ACORDAI! ACORDAI! DESPERTAI! DESPERTAI! E CANTAI! SIM, CANTAI! O SENHOR NÃO TARDARÁ! EIS MARCHAMOS PARA AQUELE BOM PAÍS, ONDE O CRENTE SIM, É CRISTO QUEM O DIZ, COM SEU SALVADOR, PRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR.\n\nEIS CONOSCO NOSSO INSIGNE CAPITÃO QUE NOS ASSEGURA ETERNA SALVAÇÃO, EIS DA SANTA FÉ O INVICTO PAVILHÃO, VAMOS, VAMOS TRABALHAR! EIA, AVANTE! NADA TEMOS QUE TEMER, POR JESUS HAVEMOS SEMPRE DE VENCER; TRABALHEMOS, POIS, ATÉ O AMANHECER E O TRABALHO AQUI FINDAR!\n\nCORO ACORDAI! ACORDAI! DESPERTAI! DESPERTAI! E CANTAI! SIM, CANTAI! O SENHOR NÃO TARDARÁ! EIS MARCHAMOS PARA AQUELE BOM PAÍS, ONDE O CRENTE SIM, É CRISTO QUEM O DIZ, COM SEU SALVADOR, PRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR.\n\nREVESTIDOS DA CONFIANÇA DE JESUS, COMO SERVOS SEUS E FILHOS, SIM, DA LUZ, GLORIANDO-NOS EM CRISTO E SUA CRUZ, VAMOS, VAMOS TRABALHAR! OS PERDIDOS, VAMOS COM AMOR BUSCAR, AOS DESESPERADOS VAMOS DECLARAR QUE JESUS ESTÁ PRONTO TODOS A SALVAR! OH! SIM, VAMOS TRABALHAR!\n\nCORO ACORDAI! ACORDAI! DESPERTAI! DESPERTAI! E CANTAI! SIM, CANTAI! O SENHOR NÃO TARDARÁ! EIS MARCHAMOS PARA AQUELE BOM PAÍS, ONDE O CRENTE SIM, É CRISTO QUEM O DIZ, COM SEU SALVADOR, PRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR.', 'EIS MARCHAMOS PARA AQUELE BOM PAÍS, ONDE O CRENTE, SIM, É CRISTO QUEM O DIZ COM SEU SALVADOR, PRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR; TRABALHEMOS POIS, COM ZELO E COM VIGOR, CONSTRANGIDOS PELO SEU IMENSO AMOR; TRABALHEMOS PELO NOSSO SALVADOR, EIS QUE A VIDA VAI FINDAR! CORO ACORDAI! ACORDAI! DESPERTAI! DESPERTAI! E CANTAI! SIM, CANTAI! O SENHOR NÃO TARDARÁ! EIS MARCHAMOS PARA AQUELE BOM PAÍS, ONDE O CRENTE SIM, É CRISTO QUEM O DIZ, COM SEU SALVADOR, PRA SEMPRE ALI FELIZ, VAI COM ELE DESCANSAR. EIS CONOSCO NOSSO INSIGNE CAPITÃO QUE NOS ASSEGURA ETERNA SALVAÇÃO, EIS DA SANTA FÉ O INVICTO PAVILHÃO, VAMOS, VAMOS TRABALHAR! EIA, AVANTE! NADA TEMOS QUE TEMER, POR JESUS HAVEMOS SEMPRE DE VENCER; TRABALHEMOS, POIS, ATÉ O AMANHECER E O TRABALHO AQUI FINDAR! REVESTIDOS DA CONFIANÇA DE JESUS, COMO SERVOS SEUS E FILHOS, SIM, DA LUZ, GLORIANDO-NOS EM CRISTO E SUA CRUZ, VAMOS, VAMOS TRABALHAR! OS PERDIDOS, VAMOS COM AMOR BUSCAR, AOS DESESPERADOS VAMOS DECLARAR QUE JESUS ESTÁ PRONTO TODOS A SALVAR! OH! SIM, VAMOS TRABALHAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('410', '410 - UM PENDÃO REAL VOS ENTREGOU O REI', 'UM PENDÃO REAL VOS ENTREGOU O REI A VÓS, SOLDADOS SEUS; CORAJOSOS, POIS, EM TUDO O DEFENDEI MARCHANDO PARA OS CÉUS.\n\nCORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! EIS FORMADOS JÁ OS NEGROS BATALHÕES DO GRANDE USURPADOR; DECLARAI-VOS, HOJE, BRAVOS CAMPEÕES, AVANTE, SEM TEMOR!\n\nCORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! QUEM RECEIO SENTE NO SEU CORAÇÃO E FRACO SE MOSTRAR, NÃO RECEBERÁ O ETERNO GALARDÃO QUE CRISTO TEM PRA DAR.\n\nCORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! POIS SEJAMOS TODOS A JESUS LEAIS E AO SEU REAL PENDÃO; OS QUE NA BATALHA SEMPRE SÃO FIÉIS COM ELE REINARÃO.', 'UM PENDÃO REAL VOS ENTREGOU O REI A VÓS, SOLDADOS SEUS; CORAJOSOS, POIS, EM TUDO O DEFENDEI MARCHANDO PARA OS CÉUS. CORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! EIS FORMADOS JÁ OS NEGROS BATALHÕES DO GRANDE USURPADOR; DECLARAI-VOS, HOJE, BRAVOS CAMPEÕES, AVANTE, SEM TEMOR! CORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! QUEM RECEIO SENTE NO SEU CORAÇÃO E FRACO SE MOSTRAR, NÃO RECEBERÁ O ETERNO GALARDÃO QUE CRISTO TEM PRA DAR. CORO COM VALOR! SEM TEMOR! POR CRISTO PRONTOS A SOFRER; BEM ALTO ERGUEI O SEU PENDÃO, FIRMES SEMPRE ATÉ MORRER! POIS SEJAMOS TODOS A JESUS LEAIS E AO SEU REAL PENDÃO; OS QUE NA BATALHA SEMPRE SÃO FIÉIS COM ELE REINARÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('411', '411 - O SENHOR DA CEIFA ESTÁ CHAMANDO', 'O SENHOR DA CEIFA ESTÁ CHAMANDO: “QUEM QUER IR POR MIM A PROCURAR ALMAS QUE NO MUNDO VÃO CHORANDO SEM DA SALVAÇÃO PARTICIPAR?”\n\nCORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. MUITOS SÃO OS QUE VÃO EXPIRANDO SEM TER ESPERANÇA DE VER DEUS; VAI, DEPRESSA, LHES ANUNCIANDO QUE JESUS OS LEVA PARA OS CÉUS.\n\nCORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. O PROFETA A DEUS SE APROXIMANDO, CONSIDERA-SE UM PECADOR, MAS, O FOGO SANTO O QUEIMANDO, TORNA-O ÚTIL PARA SEU SENHOR.\n\nCORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. BREVE OS TRABALHOS SERÃO FINDOS, BÊNÇÃOS VÃO OS SERVOS DESFRUTAR, E JESUS OS SAUDARÁ: “BEM-VINDOS”, ESTA GLÓRIA ESPERO ALCANÇAR!', 'O SENHOR DA CEIFA ESTÁ CHAMANDO: QUEM QUER IR POR MIM A PROCURAR ALMAS QUE NO MUNDO VÃO CHORANDO SEM DA SALVAÇÃO PARTICIPAR? CORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. MUITOS SÃO OS QUE VÃO EXPIRANDO SEM TER ESPERANÇA DE VER DEUS; VAI, DEPRESSA, LHES ANUNCIANDO QUE JESUS OS LEVA PARA OS CÉUS. CORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. O PROFETA A DEUS SE APROXIMANDO, CONSIDERA-SE UM PECADOR, MAS, O FOGO SANTO O QUEIMANDO, TORNA-O ÚTIL PARA SEU SENHOR. CORO FALA DEUS! FALA DEUS! TOCA-ME COM BRASAS DO ALTAR; FALA DEUS! FALA DEUS! SIM, ALEGRE ATENDO AO TEU MANDAR. BREVE OS TRABALHOS SERÃO FINDOS, BÊNÇÃOS VÃO OS SERVOS DESFRUTAR, E JESUS OS SAUDARÁ: BEM-VINDOS, ESTA GLÓRIA ESPERO ALCANÇAR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('412', '412 - SOU FORASTEIRO AQUI', 'SOU FORASTEIRO AQUI, EM TERRA ESTRANHA ESTOU, CELESTE PÁTRIA, SIM, É PARA ONDE VOU; EMBAIXADOR, POR DEUS, DE REINOS D’ALÉM CÉUS, VENHO A SERVIÇO DO MEU REI.\n\nCORO EIS A MENSAGEM QUE ME DEU, QUE OS ANJOS CANTAM LÁ NO CÉU: “RECONCILIAI-VOS JÁ”, DIZ O SENHOR, REI MEU, “RECONCILIAI-VOS JÁ COM DEUS”.\n\nPOR DEUS MANDADO ESTÁ QUE O HOMEM PECADOR, ARREPENDIDO JÁ, SE CHEGUE AO SALVADOR; POIS QUEM O RECEBER NO REINO VAI VIVER, VENHO A SERVIÇO DO MEU REI.\n\nCORO EIS A MENSAGEM QUE ME DEU, QUE OS ANJOS CANTAM LÁ NO CÉU: “RECONCILIAI-VOS JÁ”, DIZ O SENHOR, REI MEU, “RECONCILIAI-VOS JÁ COM DEUS”.\n\nMAIS BELO QUE O ROSAL, O LAR CELESTE TEM A BÊNÇÃO PRO MORTAL, O GOZO ETERNO ALÉM; ALI SÓ HÁ PRAZER, VOS MANDA O REI DIZER; VENHO A SERVIÇO DO MEU REI.\n\nCORO EIS A MENSAGEM QUE ME DEU, QUE OS ANJOS CANTAM LÁ NO CÉU: “RECONCILIAI-VOS JÁ”, DIZ O SENHOR, REI MEU, “RECONCILIAI-VOS JÁ COM DEUS”.', 'SOU FORASTEIRO AQUI, EM TERRA ESTRANHA ESTOU, CELESTE PÁTRIA, SIM, É PARA ONDE VOU; EMBAIXADOR, POR DEUS, DE REINOS D’ALÉM CÉUS, VENHO A SERVIÇO DO MEU REI. CORO EIS A MENSAGEM QUE ME DEU, QUE OS ANJOS CANTAM LÁ NO CÉU: RECONCILIAI-VOS JÁ, DIZ O SENHOR, REI MEU, RECONCILIAI-VOS JÁ COM DEUS. POR DEUS MANDADO ESTÁ QUE O HOMEM PECADOR, ARREPENDIDO JÁ, SE CHEGUE AO SALVADOR; POIS QUEM O RECEBER NO REINO VAI VIVER, VENHO A SERVIÇO DO MEU REI. MAIS BELO QUE O ROSAL, O LAR CELESTE TEM A BÊNÇÃO PRO MORTAL, O GOZO ETERNO ALÉM; ALI SÓ HÁ PRAZER, VOS MANDA O REI DIZER; VENHO A SERVIÇO DO MEU REI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('413', '413 - OUVI, Ó CÉUS, E FALAREI', 'OUVI, Ó CÉUS, E FALAREI E OUÇA A TERRA A MINHA VOZ, ESCUTAI, POVO MEU! GOTEJE A MINHA DOUTRINA COMO A CHUVA, DESTILE A MINHA PALAVRA COMO O ORVALHO; TAL COMO SUAVE CHUVISCO SOBRE A RELVA E COMO AS GOTAS DE ÁGUA SOBRE A ERVA.\n\nCORO ENTÃO PROCLAMAREI O NOME DO SENHOR! ENGRANDECEI O NOSSO DEUS! INSTRUMENTOS\n\nDEUS É MINHA ROCHA, SUA OBRA É PERFEITA, PORQUE TODOS OS SEUS CAMINHOS SÃO JUÍZO; DEUS É A VERDADE, NÃO HÁ NELE INJUSTIÇA, É JUSTO E RETO O SENHOR MEU, ALELUIA!\n\nCORO (2X) ENTÃO PROCLAMAREI O NOME DO SENHOR! ENGRANDECEI O NOSSO DEUS! FINAL: ENGRANDECEI O NOSSO DEUS!', 'OUVI, Ó CÉUS, E FALAREI E OUÇA A TERRA A MINHA VOZ, ESCUTAI, POVO MEU! GOTEJE A MINHA DOUTRINA COMO A CHUVA, DESTILE A MINHA PALAVRA COMO O ORVALHO; TAL COMO SUAVE CHUVISCO SOBRE A RELVA E COMO AS GOTAS DE ÁGUA SOBRE A ERVA. CORO ENTÃO PROCLAMAREI O NOME DO SENHOR! ENGRANDECEI O NOSSO DEUS! INSTRUMENTOS DEUS É MINHA ROCHA, SUA OBRA É PERFEITA, PORQUE TODOS OS SEUS CAMINHOS SÃO JUÍZO; DEUS É A VERDADE, NÃO HÁ NELE INJUSTIÇA, É JUSTO E RETO O SENHOR MEU, ALELUIA! CORO  ENTÃO PROCLAMAREI O NOME DO SENHOR! ENGRANDECEI O NOSSO DEUS!  ENGRANDECEI O NOSSO DEUS!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('414', '414 - MEU DEUS, MEU BOM PASTOR', 'MEU DEUS, MEU BOM PASTOR, GUIAR-ME VEM PELA MÃO PORQUE SOU FRACO, SENHOR; DÁ-ME TUA PROTEÇÃO! JESUS, MEU SALVADOR, TRANSBORDA-ME DE AMOR E EU SEREI TEU FIEL SERVIDOR!\n\nDE MANHÃ, LOGO CEDO, TUA FACE BUSCAR QUERO, SENHOR, E EM SEGREDO, MINHA VIDA TE DAR; E QUANDO O DIA FINDAR POR TEU SANGUE VOU CLAMAR, QUERO TORNAR-ME UM FIEL SERVIDOR!\n\nSENDO AGORA TEU SOMENTE, VEM DE PODER E AMOR ENCHER-ME, Ó ONIPOTENTE, E GLÓRIA TE DOU, MEU SENHOR; MEU DEUS, MEU GUARDADOR, GLÓRIA E ALELUIA COM ARDOR, EIS-ME TORNADO UM FIEL SERVIDOR! GLÓRIA E ALELUIA COM ARDOR, EIS-ME TORNADO UM FIEL SERVIDOR!', 'MEU DEUS, MEU BOM PASTOR, GUIAR-ME VEM PELA MÃO PORQUE SOU FRACO, SENHOR; DÁ-ME TUA PROTEÇÃO! JESUS, MEU SALVADOR, TRANSBORDA-ME DE AMOR E EU SEREI TEU FIEL SERVIDOR! DE MANHÃ, LOGO CEDO, TUA FACE BUSCAR QUERO, SENHOR, E EM SEGREDO, MINHA VIDA TE DAR; E QUANDO O DIA FINDAR POR TEU SANGUE VOU CLAMAR, QUERO TORNAR-ME UM FIEL SERVIDOR! SENDO AGORA TEU SOMENTE, VEM DE PODER E AMOR ENCHER-ME, Ó ONIPOTENTE, E GLÓRIA TE DOU, MEU SENHOR; MEU DEUS, MEU GUARDADOR, GLÓRIA E ALELUIA COM ARDOR, EIS-ME TORNADO UM FIEL SERVIDOR! GLÓRIA E ALELUIA COM ARDOR, EIS-ME TORNADO UM FIEL SERVIDOR!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('415', '415 - QUERO SER UM VASO DE BÊNÇÃO', 'QUERO SER UM VASO DE BÊNÇÃO, SIM, UM VASO ESCOLHIDO DE DEUS, PARA AS NOVAS LEVAR AOS PERDIDOS, BOAS NOVAS QUE VÊM LÁ DOS CÉUS.\n\nCORO FAZE-ME VASO DE BÊNÇÃO, SENHOR, VASO QUE LEVE A MENSAGEM DE AMOR! EIS-ME SUBMISSO AO TEU SERVIÇO; EU ME CONSAGRO, BENDITO SENHOR.\n\nQUERO SER UM VASO DE BÊNÇÃO PARA TODOS OS DIAS FAZER, AOS CULPADOS QUE VIVEM NAS TREVAS, O PERDÃO DE JESUS CONHECER.\n\nCORO FAZE-ME VASO DE BÊNÇÃO, SENHOR, VASO QUE LEVE A MENSAGEM DE AMOR! EIS-ME SUBMISSO AO TEU SERVIÇO; EU ME CONSAGRO, BENDITO SENHOR.\n\nQUERO SER UM VASO DE BÊNÇÃO, SIM, UM VASO DE BÊNÇÃO SEM PAR, ANUNCIANDO QUE OS CRENTES EM CRISTO JUBILOSOS NO CÉU HÃO DE ENTRAR.\n\nCORO FAZE-ME VASO DE BÊNÇÃO, SENHOR, VASO QUE LEVE A MENSAGEM DE AMOR! EIS-ME SUBMISSO AO TEU SERVIÇO; EU ME CONSAGRO, BENDITO SENHOR.', 'QUERO SER UM VASO DE BÊNÇÃO, SIM, UM VASO ESCOLHIDO DE DEUS, PARA AS NOVAS LEVAR AOS PERDIDOS, BOAS NOVAS QUE VÊM LÁ DOS CÉUS. CORO FAZE-ME VASO DE BÊNÇÃO, SENHOR, VASO QUE LEVE A MENSAGEM DE AMOR! EIS-ME SUBMISSO AO TEU SERVIÇO; EU ME CONSAGRO, BENDITO SENHOR. QUERO SER UM VASO DE BÊNÇÃO PARA TODOS OS DIAS FAZER, AOS CULPADOS QUE VIVEM NAS TREVAS, O PERDÃO DE JESUS CONHECER. QUERO SER UM VASO DE BÊNÇÃO, SIM, UM VASO DE BÊNÇÃO SEM PAR, ANUNCIANDO QUE OS CRENTES EM CRISTO JUBILOSOS NO CÉU HÃO DE ENTRAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('416', '416 - PELAS PRAIAS GALILÉIAS VEM JESUS', 'PELAS PRAIAS GALILÉIAS VEM JESUS, (BIS) JUNTO ÀS REDES, OS PESCADORES, PRA O SEGUIREM VAI CHAMAR E SEGUIRAM A JESUS SEM VACILAR.\n\nTODA A NOITE DE VIGÍLIA TUDO EM VÃO, (BIS) DIZ JESUS: “LANÇAI ALÉM VOSSAS REDES E ACHAREIS, E DE PEIXES ENCHEREIS LOGO OS BATÉIS”.\n\nASSENTADOS, MUI CANSADOS E FAMINTOS, (BIS) CRISTO TOMA O PÃO E O PEIXE, ORA AO PAI E PARTE O PÃO, E ALIMENTA, COM SOBEJO, A MULTIDÃO.\n\nEU TAMBÉM SEGUIR-TE QUERO, MEU SENHOR, (BIS) SÓ EM TI PODE ENCONTRAR, MEU FAMINTO CORAÇÃO, ALEGRIA COM REAL SATISFAÇÃO.', 'PELAS PRAIAS GALILÉIAS VEM JESUS,  JUNTO ÀS REDES, OS PESCADORES, PRA O SEGUIREM VAI CHAMAR E SEGUIRAM A JESUS SEM VACILAR. TODA A NOITE DE VIGÍLIA TUDO EM VÃO,  DIZ JESUS: LANÇAI ALÉM VOSSAS REDES E ACHAREIS, E DE PEIXES ENCHEREIS LOGO OS BATÉIS. ASSENTADOS, MUI CANSADOS E FAMINTOS,  CRISTO TOMA O PÃO E O PEIXE, ORA AO PAI E PARTE O PÃO, E ALIMENTA, COM SOBEJO, A MULTIDÃO. EU TAMBÉM SEGUIR-TE QUERO, MEU SENHOR,  SÓ EM TI PODE ENCONTRAR, MEU FAMINTO CORAÇÃO, ALEGRIA COM REAL SATISFAÇÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('417', '417 - MINHA DEDICAÇÃO', 'MINHA DEDICAÇÃO EU VIM TE OFERECER, TU ÉS O MEU AMADO SALVADOR; PEDISTE MAIS DE MIM EIS-ME AQUI SENHOR TENS A MINHA VIDA E MEU LOUVOR.\n\nVIVER SÓ PARA TI COM TODO O MEU AMOR, OBEDECER TUA VOZ EM TODO O MEU VIVER, SEGUINDO OS TEUS PASSOS OBSERVANDO OS MEUS, EM TUDO AGRADANDO A TI, MEU TERNO DEUS.\n\nÓ VINDE, ADOREMOS AO SENHOR, O GRANDE REI, JUSTO E PODEROSO, EXCELSO DEUS, MEU REDENTOR, TEMEI DIANTE DELE TODA A TERRA, ADORAI AO REI. ALELUIA, ALELUIA, ALELUIA, ALELUIA (BIS) TEMEI DIANTE DELE TODA A TERRA, ADORAI AO REI.', 'MINHA DEDICAÇÃO EU VIM TE OFERECER, TU ÉS O MEU AMADO SALVADOR; PEDISTE MAIS DE MIM EIS-ME AQUI SENHOR TENS A MINHA VIDA E MEU LOUVOR. VIVER SÓ PARA TI COM TODO O MEU AMOR, OBEDECER TUA VOZ EM TODO O MEU VIVER, SEGUINDO OS TEUS PASSOS OBSERVANDO OS MEUS, EM TUDO AGRADANDO A TI, MEU TERNO DEUS. Ó VINDE, ADOREMOS AO SENHOR, O GRANDE REI, JUSTO E PODEROSO, EXCELSO DEUS, MEU REDENTOR, TEMEI DIANTE DELE TODA A TERRA, ADORAI AO REI. ALELUIA, ALELUIA, ALELUIA, ALELUIA  TEMEI DIANTE DELE TODA A TERRA, ADORAI AO REI.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('418', '418 - HÁ UM POVO QUE TE ADORA', 'HÁ UM POVO QUE TE ADORA EM ESPÍRITO, ATENDENDO À TUA VOZ, SENHOR, QUE TEM UMA NOVA FORMA DE VIDA, LUTA COM AS ARMAS CELESTIAIS E VENCE PELO PODER DE JESUS.\n\nCORO O SENHOR É QUEM DIRIGE, O SENHOR É QUEM CONDUZ, DEPENDENTE SOU DO SEU AMOR QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS.\n\nVARÕES\n\nNO PROJETO DE DEUS ESTOU LUTANDO COM AS ARMAS CELESTIAIS, VENCENDO PELO PODER DO SENHOR JESUS, COM SEU PERDÃO, NA SALVAÇÃO, NA REVELAÇÃO DE JESUS.\n\nCORO O SENHOR É QUEM DIRIGE, O SENHOR É QUEM CONDUZ, DEPENDENTE SOU DO SEU AMOR QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS. INSTRUMENTOS\n\nVARÕES\n\nFINAL: QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS.', 'HÁ UM POVO QUE TE ADORA EM ESPÍRITO, ATENDENDO À TUA VOZ, SENHOR, QUE TEM UMA NOVA FORMA DE VIDA, LUTA COM AS ARMAS CELESTIAIS E VENCE PELO PODER DE JESUS. CORO O SENHOR É QUEM DIRIGE, O SENHOR É QUEM CONDUZ, DEPENDENTE SOU DO SEU AMOR QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS. NO PROJETO DE DEUS ESTOU LUTANDO COM AS ARMAS CELESTIAIS, VENCENDO PELO PODER DO SENHOR JESUS, COM SEU PERDÃO, NA SALVAÇÃO, NA REVELAÇÃO DE JESUS. CORO O SENHOR É QUEM DIRIGE, O SENHOR É QUEM CONDUZ, DEPENDENTE SOU DO SEU AMOR QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS. INSTRUMENTOS QUERO VIVER UMA NOVA FORMA DE VIDA NO AMOR, NO AMOR DE JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('419', '419 - HÁ UMA OBRA QUE DEUS QUER FAZER', 'HÁ UMA OBRA QUE DEUS QUER FAZER, OBRA DE SANTIDADE E DE PODER; QUEM ESTÁ DISPOSTO A SE CONSAGRAR E A ARCA DE DEUS AOS OMBROS CARREGAR?\n\nCORO O ESPÍRITO DE DEUS QUER A TI, COMO ELE UM DIA POSSUIU DAVI, SIM, HÁ UMA OBRA A REALIZAR ANTES QUE VENHA O FIM.\n\nDESCE AO RIBEIRO E ENCHE O CORAÇÃO, TERÁS VITÓRIA NA REVELAÇÃO; COM ÓLEO DE PODER DEUS TE UNGIRÁ, JAMAIS VAZIA A ESPADA VOLTARÁ.\n\nCORO O ESPÍRITO DE DEUS QUER A TI, COMO ELE UM DIA POSSUIU DAVI, SIM, HÁ UMA OBRA A REALIZAR ANTES QUE VENHA O FIM.', 'HÁ UMA OBRA QUE DEUS QUER FAZER, OBRA DE SANTIDADE E DE PODER; QUEM ESTÁ DISPOSTO A SE CONSAGRAR E A ARCA DE DEUS AOS OMBROS CARREGAR? CORO O ESPÍRITO DE DEUS QUER A TI, COMO ELE UM DIA POSSUIU DAVI, SIM, HÁ UMA OBRA A REALIZAR ANTES QUE VENHA O FIM. DESCE AO RIBEIRO E ENCHE O CORAÇÃO, TERÁS VITÓRIA NA REVELAÇÃO; COM ÓLEO DE PODER DEUS TE UNGIRÁ, JAMAIS VAZIA A ESPADA VOLTARÁ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('420', '420 - SOMOS TEU POVO E QUEREMOS', 'SOMOS TEU POVO E QUEREMOS TUA VONTADE FAZER; LIVRA-NOS DO VELHO HOMEM E CONCEDE-NOS UM NOVO VIVER, PARA QUE POSSAMOS SEMPRE TE OBEDECER. E DEFINIR NOSSAS VIDAS EM TI E SERMOS TUA IGREJA FIEL.\nBIS\n\nESTA É UMA OBRA ETERNA QUE O TEU ESPÍRITO NOS TRAZ, TODOS EM UM SÓ LOUVOR CANTARÃO: MARANATA! JESUS VOLTARÁ!\nBIS', 'SOMOS TEU POVO E QUEREMOS TUA VONTADE FAZER; LIVRA-NOS DO VELHO HOMEM E CONCEDE-NOS UM NOVO VIVER, PARA QUE POSSAMOS SEMPRE TE OBEDECER. E DEFINIR NOSSAS VIDAS EM TI E SERMOS TUA IGREJA FIEL. ESTA É UMA OBRA ETERNA QUE O TEU ESPÍRITO NOS TRAZ, TODOS EM UM SÓ LOUVOR CANTARÃO: MARANATA! JESUS VOLTARÁ!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('421', '421 - A OBRA DO SENHOR É PERFEITA', 'A OBRA DO SENHOR É PERFEITA, É COMO A LUZ DO SOL EM SEU ESPLENDOR, É CHUVA SOBRE A TERRA, SEMENTE QUE PROSPERA, A OBRA DO SENHOR. (BIS)\n\nA OBRA DO SENHOR É PERFEITA, É COMO A LUZ DO SOL EM SEU ESPLENDOR, É CHUVA SOBRE A TERRA, SEMENTE QUE PROSPERA, A OBRA DO SENHOR. A OBRA DO SENHOR É PERFEITA, A OBRA DO SENHOR É PRECIOSA.', 'A OBRA DO SENHOR É PERFEITA, É COMO A LUZ DO SOL EM SEU ESPLENDOR, É CHUVA SOBRE A TERRA, SEMENTE QUE PROSPERA, A OBRA DO SENHOR. A OBRA DO SENHOR É PERFEITA, É COMO A LUZ DO SOL EM SEU ESPLENDOR, É CHUVA SOBRE A TERRA, SEMENTE QUE PROSPERA, A OBRA DO SENHOR. A OBRA DO SENHOR É PERFEITA, A OBRA DO SENHOR É PRECIOSA.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('422', '422 - A MINHA HERANÇA', 'A MINHA HERANÇA ÉS TU, SENHOR, E TUA OBRA É PARA MIM MEU BEM MAIOR; EM CADA DIA TE CONHECER E DESCOBRIR TEU PLANO PARA O MEU VIVER.\n\nCORO DAS MÃOS DO MEU JESUS HERANÇA RECEBI, ESTA VINHA QUE ME DEU PARA GUARDAR; POR OUTRO BEM QUALQUER DO MUNDO AQUI JAMAIS EU PODEREI MINHA HERANÇA TROCAR.\n\nE SE VIER A TENTAÇÃO RESISTIREI FIRMADO EM TUA SALVAÇÃO; A VIDA AQUI POSSO PERDER MAS UMA HERANÇA ETERNA VOU NO CÉU RECEBER.\n\nCORO DAS MÃOS DO MEU JESUS HERANÇA RECEBI, ESTA VINHA QUE ME DEU PARA GUARDAR; POR OUTRO BEM QUALQUER DO MUNDO AQUI JAMAIS EU PODEREI MINHA HERANÇA TROCAR.', 'A MINHA HERANÇA ÉS TU, SENHOR, E TUA OBRA É PARA MIM MEU BEM MAIOR; EM CADA DIA TE CONHECER E DESCOBRIR TEU PLANO PARA O MEU VIVER. CORO DAS MÃOS DO MEU JESUS HERANÇA RECEBI, ESTA VINHA QUE ME DEU PARA GUARDAR; POR OUTRO BEM QUALQUER DO MUNDO AQUI JAMAIS EU PODEREI MINHA HERANÇA TROCAR. E SE VIER A TENTAÇÃO RESISTIREI FIRMADO EM TUA SALVAÇÃO; A VIDA AQUI POSSO PERDER MAS UMA HERANÇA ETERNA VOU NO CÉU RECEBER.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('423', '423 - Ó PASTOR DE ISRAEL', 'Ó PASTOR DE ISRAEL, QUE GUIAS TEU POVO E TE ASSENTAS ENTRE OS QUERUBINS, RESPLANDECE!\n\nCORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO!\n\nÓ PASTOR DE ISRAEL, FAZE BRILHAR TUA GLÓRIA E DESPERTA O TEU PODER NO MEIO DO TEU POVO.\n\nCORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO! INSTRUMENTOS\n\nCORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO! FINAL: O TEU ROSTO!', 'Ó PASTOR DE ISRAEL, QUE GUIAS TEU POVO E TE ASSENTAS ENTRE OS QUERUBINS, RESPLANDECE! CORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO! Ó PASTOR DE ISRAEL, FAZE BRILHAR TUA GLÓRIA E DESPERTA O TEU PODER NO MEIO DO TEU POVO. CORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO! INSTRUMENTOS CORO VEM, VISITA A TUA VINHA QUE PLANTASTE COM TUA DESTRA, RESPLANDECE SOBRE NÓS O TEU ROSTO; DOS QUE A QUEREM DEVORAR, TUA VINHA VEM GUARDAR, RESPLANDECE SOBRE NÓS O TEU ROSTO!  O TEU ROSTO!',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('424', '424 - O MEU AMADO DESCEU AO SEU JARDIM', 'O MEU AMADO DESCEU AO SEU JARDIM, AOS CANTEIROS DE BÁLSAMO. PARA PASTOREAR NO JARDIM PARA COLHER OS LÍRIOS.\nBIS\nBIS\n\nEU SOU DO MEU AMADO E O MEU AMADO É MEU; ELE PASTOREIA ENTRE OS LÍRIOS, ELE PASTOREIA ENTRE OS LÍRIOS.\nBIS\nBIS', 'O MEU AMADO DESCEU AO SEU JARDIM, AOS CANTEIROS DE BÁLSAMO. PARA PASTOREAR NO JARDIM PARA COLHER OS LÍRIOS. EU SOU DO MEU AMADO E O MEU AMADO É MEU; ELE PASTOREIA ENTRE OS LÍRIOS, ELE PASTOREIA ENTRE OS LÍRIOS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('425', '425 - QUE VISTA AMÁVEL É (SALMO 132)', 'QUE VISTA AMÁVEL É QUANDO, COM SANTO AMOR, IRMÃOS UNIDOS PELA FÉ, ADORAM O SENHOR.\n\nO MUNDO OBSERVARÁ AQUELA SANTA PAZ, COMO UM PERFUME SENTIRÁ O GOZO QUE ELA TRAZ. ENVIA-NOS, JESUS, DO TEU MONTE SIÃO, O SANTO ESPÍRITO QUE PRODUZ AQUELA DOCE UNIÃO.', 'QUE VISTA AMÁVEL É QUANDO, COM SANTO AMOR, IRMÃOS UNIDOS PELA FÉ, ADORAM O SENHOR. O MUNDO OBSERVARÁ AQUELA SANTA PAZ, COMO UM PERFUME SENTIRÁ O GOZO QUE ELA TRAZ. ENVIA-NOS, JESUS, DO TEU MONTE SIÃO, O SANTO ESPÍRITO QUE PRODUZ AQUELA DOCE UNIÃO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('426', '426 - UM CORPO SÓ, UM SÓ ESPÍRITO', 'UM CORPO SÓ, UM SÓ ESPÍRITO, UMA ESPERANÇA, UM SÓ SENHOR, UMA FÉ, UM SÓ BATISMO E UM SÓ DEUS E PAI, UM CORPO SÓ, UM SÓ ESPÍRITO. (BIS)\n\nCRISTO É A CABEÇA, O CORPO É A SUA IGREJA, COM SEU PODER NOS BATIZOU O SENHOR; NÃO HÁ NO MUNDO UM POVO UNIDO ASSIM, UM CORPO SÓ, UM SÓ ESPÍRITO. (BIS)\n\nDE SUA GRAÇA CRISTO DERRAMOU, EM MINISTÉRIOS, DONS E MUITO AMOR, MAS SOMOS TODOS UM SÓ EM JESUS, UM CORPO SÓ, UM SÓ ESPÍRITO. (BIS)', 'UM CORPO SÓ, UM SÓ ESPÍRITO, UMA ESPERANÇA, UM SÓ SENHOR, UMA FÉ, UM SÓ BATISMO E UM SÓ DEUS E PAI, UM CORPO SÓ, UM SÓ ESPÍRITO. CRISTO É A CABEÇA, O CORPO É A SUA IGREJA, COM SEU PODER NOS BATIZOU O SENHOR; NÃO HÁ NO MUNDO UM POVO UNIDO ASSIM, UM CORPO SÓ, UM SÓ ESPÍRITO. DE SUA GRAÇA CRISTO DERRAMOU, EM MINISTÉRIOS, DONS E MUITO AMOR, MAS SOMOS TODOS UM SÓ EM JESUS, UM CORPO SÓ, UM SÓ ESPÍRITO.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('427', '427 - OH! QUÃO BOM E QUÃO SUAVE É', 'OH! QUÃO BOM E QUÃO SUAVE É A UNIÃO ENTRE OS IRMÃOS, É COMO ÓLEO PRECIOSO SOBRE A CABEÇA DE ARÃO, É COMO ÓLEO PRECIOSO SOBRE O CORPO DE ARÃO.\n\nCOMO O ORVALHO DE HERMOM QUE DESCE SOBRE OS MONTES DE SIÃO PORQUE ALI O SENHOR ORDENA A BÊNÇÃO PARA SEMPRE, ORDENA A VIDA PARA SEMPRE. INSTRUMENTOS\nBIS\n\nOH! QUÃO BOM E QUÃO SUAVE É A UNIÃO ENTRE OS IRMÃOS, É COMO ÓLEO PRECIOSO SOBRE A CABEÇA DE ARÃO, É COMO ÓLEO PRECIOSO SOBRE O CORPO DE ARÃO.\n\nCOMO O ORVALHO DE HERMOM QUE DESCE SOBRE OS MONTES DE SIÃO PORQUE ALI O SENHOR ORDENA A BÊNÇÃO PARA SEMPRE, ORDENA A VIDA PARA SEMPRE.\nBIS', 'OH! QUÃO BOM E QUÃO SUAVE É A UNIÃO ENTRE OS IRMÃOS, É COMO ÓLEO PRECIOSO SOBRE A CABEÇA DE ARÃO, É COMO ÓLEO PRECIOSO SOBRE O CORPO DE ARÃO. COMO O ORVALHO DE HERMOM QUE DESCE SOBRE OS MONTES DE SIÃO PORQUE ALI O SENHOR ORDENA A BÊNÇÃO PARA SEMPRE, ORDENA A VIDA PARA SEMPRE. INSTRUMENTOS COMO O ORVALHO DE HERMOM QUE DESCE SOBRE OS MONTES DE SIÃO PORQUE ALI O SENHOR ORDENA A BÊNÇÃO PARA SEMPRE, ORDENA A VIDA PARA SEMPRE.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('428', '428 - VEM, Ó CRISTO, ENCHER MEU CÁLICE', 'VEM, Ó CRISTO, ENCHER MEU CÁLICE, SACIAR A SEDE DO MEU CORAÇÃO; PÃO DA VIDA, VEM ALIMENTAR MEU SER, VEM MEU CÁLICE DE GOZO TRANSBORDAR.', 'VEM, Ó CRISTO, ENCHER MEU CÁLICE, SACIAR A SEDE DO MEU CORAÇÃO; PÃO DA VIDA, VEM ALIMENTAR MEU SER, VEM MEU CÁLICE DE GOZO TRANSBORDAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('429', '429 - ÉS O PÃO DA VIDA', 'ÉS O PÃO DA VIDA, CELESTIAL MANÁ, DELE, QUEM COMER, PRA SEMPRE VIVERÁ; EM TEU CORPO REPARTIDO, SOMOS UM SÓ POVO UNIDO PELOS LAÇOS DE AMOR NO TEU SANTO ESPÍRITO.\n\nÉS JESUS, DA VIDE, O DOCE FRUTO, VIDA ABUNDANTE QUE NOS SATISFAZ; PELO SANGUE DA TUA CRUZ ALCANÇAMOS VIDA E LUZ, CÁLICE TRANSBORDANTE DE ALEGRIA E DE PAZ.\nBIS', 'ÉS O PÃO DA VIDA, CELESTIAL MANÁ, DELE, QUEM COMER, PRA SEMPRE VIVERÁ; EM TEU CORPO REPARTIDO, SOMOS UM SÓ POVO UNIDO PELOS LAÇOS DE AMOR NO TEU SANTO ESPÍRITO. ÉS JESUS, DA VIDE, O DOCE FRUTO, VIDA ABUNDANTE QUE NOS SATISFAZ; PELO SANGUE DA TUA CRUZ ALCANÇAMOS VIDA E LUZ, CÁLICE TRANSBORDANTE DE ALEGRIA E DE PAZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('430', '430 - COMO CRISTO ENSINOU', 'COMO CRISTO ENSINOU PARTILHAMOS DESTE PÃO, O SEU CORPO REPARTIDO EM COMUNHÃO; E DO CÁLICE BEBAMOS, QUE É SEU SANGUE DERRAMADO, PREÇO PAGO PELA NOSSA SALVAÇÃO;\n\nE LEMBRAMOS ESSE DIA, ESSA NOITE TÃO SOMBRIA EM QUE ELE NO JARDIM AO PAI CLAMOU; E TRAÍDO E ABANDONADO FOI NA CRUZ SACRIFICADO, O CORDEIRO NOSSA CULPA EXPIOU.\n\nDO SEU LADO TRASPASSADO PELO NOSSO VIL PECADO, NOS GEROU PRA SERMOS SUA POSSESSÃO; UM SÓ CORPO FEZ DE NÓS, UM SÓ POVO, UM SÓ PÃO BATIZOU-NOS COM A MESMA SANTA UNÇÃO; NO LUGAR DA SUA DOR HÁ PRA NÓS GOZO E LOUVOR, NOSSO CÁLICE TRANSBORDA DE ALEGRIA; POIS A VIDA NOS FOI DADA, A VITÓRIA ALCANÇADA POR JESUS, O NOSSO AMADO SALVADOR.\n\nPOR ESPELHO NÓS JÁ VEMOS, POR ENIGMA PERCEBEMOS MARAVILHAS QUE ELE VAI NOS REVELAR, AS MORADAS ETERNAIS NOS LUGARES CELESTIAIS QUE JESUS JUNTO DO PAI FOI PREPARAR.', 'COMO CRISTO ENSINOU PARTILHAMOS DESTE PÃO, O SEU CORPO REPARTIDO EM COMUNHÃO; E DO CÁLICE BEBAMOS, QUE É SEU SANGUE DERRAMADO, PREÇO PAGO PELA NOSSA SALVAÇÃO; E LEMBRAMOS ESSE DIA, ESSA NOITE TÃO SOMBRIA EM QUE ELE NO JARDIM AO PAI CLAMOU; E TRAÍDO E ABANDONADO FOI NA CRUZ SACRIFICADO, O CORDEIRO NOSSA CULPA EXPIOU. DO SEU LADO TRASPASSADO PELO NOSSO VIL PECADO, NOS GEROU PRA SERMOS SUA POSSESSÃO; UM SÓ CORPO FEZ DE NÓS, UM SÓ POVO, UM SÓ PÃO BATIZOU-NOS COM A MESMA SANTA UNÇÃO; NO LUGAR DA SUA DOR HÁ PRA NÓS GOZO E LOUVOR, NOSSO CÁLICE TRANSBORDA DE ALEGRIA; POIS A VIDA NOS FOI DADA, A VITÓRIA ALCANÇADA POR JESUS, O NOSSO AMADO SALVADOR. POR ESPELHO NÓS JÁ VEMOS, POR ENIGMA PERCEBEMOS MARAVILHAS QUE ELE VAI NOS REVELAR, AS MORADAS ETERNAIS NOS LUGARES CELESTIAIS QUE JESUS JUNTO DO PAI FOI PREPARAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('431', '431 - CRISTO JÁ NOS PREPAROU', 'CRISTO JÁ NOS PREPAROU UM MANJAR QUE NOS COMPROU E AGORA NOS CONVIDA A CEAR; COM CELESTIAL MANÁ QUE DE GRAÇA DEUS TE DÁ, VEM FAMINTO, TUA ALMA SACIAR.\n\nCORO “VEM CEAR”, O MESTRE CHAMA: “VEM CEAR”, MESMO HOJE TU TE PODES SACIAR; POUCOS PÃES MULTIPLICOU, ÁGUA EM VINHO TRANSFORMOU, VEM, FAMINTO, A JESUS, “VEM CEAR!”\n\nEIS DISCÍPULOS A VOLTAR, SEM OS PEIXES APANHAR, MAS JESUS OS MANDA OUTRA VEZ PARTIR; AO TORNAR À PRAIA, ENTÃO, VÊEM NO FOGO PEIXE E PÃO E JESUS QUE OS CONVIDA À CEIA VIR.\n\nCORO “VEM CEAR”, O MESTRE CHAMA: “VEM CEAR”, MESMO HOJE TU TE PODES SACIAR; POUCOS PÃES MULTIPLICOU, ÁGUA EM VINHO TRANSFORMOU, VEM, FAMINTO, A JESUS, “VEM CEAR!”\n\nQUEM SEDENTO SE ACHAR, VENHA A CRISTO SEM TARDAR POIS UM VINHO SEM MISTURA ELE DÁ, E TAMBÉM DA VIDA, O PÃO, QUE NOS TRAZ CONSOLAÇÃO; EIS QUE TUDO PREPARADO JÁ ESTÁ.\n\nCORO “VEM CEAR”, O MESTRE CHAMA: “VEM CEAR”, MESMO HOJE TU TE PODES SACIAR; POUCOS PÃES MULTIPLICOU, ÁGUA EM VINHO TRANSFORMOU, VEM, FAMINTO, A JESUS, “VEM CEAR!”\n\nBREVE CRISTO VAI DESCER E A NOIVA RECEBER SEU LUGAR AO LADO DO SENHOR JESUS; QUEM A FOME SUPORTOU E A SEDE JÁ PASSOU, LÁ NO CÉU IRÁ REINAR EM SANTA LUZ.\n\nCORO “VEM CEAR”, O MESTRE CHAMA: “VEM CEAR”, MESMO HOJE TU TE PODES SACIAR; POUCOS PÃES MULTIPLICOU, ÁGUA EM VINHO TRANSFORMOU, VEM, FAMINTO, A JESUS, “VEM CEAR!”', 'CRISTO JÁ NOS PREPAROU UM MANJAR QUE NOS COMPROU E AGORA NOS CONVIDA A CEAR; COM CELESTIAL MANÁ QUE DE GRAÇA DEUS TE DÁ, VEM FAMINTO, TUA ALMA SACIAR. CORO VEM CEAR, O MESTRE CHAMA: VEM CEAR, MESMO HOJE TU TE PODES SACIAR; POUCOS PÃES MULTIPLICOU, ÁGUA EM VINHO TRANSFORMOU, VEM, FAMINTO, A JESUS, VEM CEAR! EIS DISCÍPULOS A VOLTAR, SEM OS PEIXES APANHAR, MAS JESUS OS MANDA OUTRA VEZ PARTIR; AO TORNAR À PRAIA, ENTÃO, VÊEM NO FOGO PEIXE E PÃO E JESUS QUE OS CONVIDA À CEIA VIR. QUEM SEDENTO SE ACHAR, VENHA A CRISTO SEM TARDAR POIS UM VINHO SEM MISTURA ELE DÁ, E TAMBÉM DA VIDA, O PÃO, QUE NOS TRAZ CONSOLAÇÃO; EIS QUE TUDO PREPARADO JÁ ESTÁ. BREVE CRISTO VAI DESCER E A NOIVA RECEBER SEU LUGAR AO LADO DO SENHOR JESUS; QUEM A FOME SUPORTOU E A SEDE JÁ PASSOU, LÁ NO CÉU IRÁ REINAR EM SANTA LUZ.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('432', '432 - OUÇO A VOZ DO MEU AMADO ME CHAMAR', 'CORO OUÇO A VOZ DO MEU AMADO ME CHAMAR “FILHO, VEM COMIGO À MESA A CEAR; TUDO FIZ, NA CRUZ MORRI POR AMOR, PRA DAR VIDA AO PERDIDO PECADOR”.\n\n“ESTE PÃO QUE EU VOS DOU REPRESENTA O MEU CORPO QUE ENTREGUEI NA CRUZ POR AMOR”.\n\nCORO OUÇO A VOZ DO MEU AMADO ME CHAMAR “FILHO, VEM COMIGO À MESA A CEAR; TUDO FIZ, NA CRUZ MORRI POR AMOR, PRA DAR VIDA AO PERDIDO PECADOR”.\n\n“ESTE CÁLICE VOS DOU, REPRESENTA O MEU SANGUE QUE DERRAMEI NA CRUZ POR AMOR”. FINAL: OUÇO A VOZ DO MEU AMADO ME CHAMAR “FILHO, VEM COMIGO À MESA A CEAR”.', 'CORO OUÇO A VOZ DO MEU AMADO ME CHAMAR FILHO, VEM COMIGO À MESA A CEAR; TUDO FIZ, NA CRUZ MORRI POR AMOR, PRA DAR VIDA AO PERDIDO PECADOR. ESTE PÃO QUE EU VOS DOU REPRESENTA O MEU CORPO QUE ENTREGUEI NA CRUZ POR AMOR. ESTE CÁLICE VOS DOU, REPRESENTA O MEU SANGUE QUE DERRAMEI NA CRUZ POR AMOR.  OUÇO A VOZ DO MEU AMADO ME CHAMAR FILHO, VEM COMIGO À MESA A CEAR.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('433', '433 - TOMAI AGORA O PÃO', 'TOMAI AGORA O PÃO, EU O PARTI POR VÓS, E AO FAZERDES, LEMBRAI-VOS DE MIM; FAZEI ISTO E COMEI-O, QUANDO O FIZERDES, LEMBRAI-VOS DE MIM. ESTE PÃO É O MEU CORPO, PARTIDO SOMENTE POR VÓS, TOMAI-O, COMEI-O, FAZEI-O EM MEMÓRIA DE MIM.\n\nTOMAI O CÁLICE, EU O ENCHI POR VÓS, E AO FAZERDES, LEMBRAI-VOS DE MIM; FAZEI ISTO E BEBEI-O, QUANDO O FIZERDES, LEMBRAI-VOS DE MIM. ESTE É O NOVO CONCERTO QUE FAÇO SOMENTE POR VÓS, TOMAI-O, BEBEI-O, FAZEI-O, EM MEMÓRIA DE MIM. (3X)', 'TOMAI AGORA O PÃO, EU O PARTI POR VÓS, E AO FAZERDES, LEMBRAI-VOS DE MIM; FAZEI ISTO E COMEI-O, QUANDO O FIZERDES, LEMBRAI-VOS DE MIM. ESTE PÃO É O MEU CORPO, PARTIDO SOMENTE POR VÓS, TOMAI-O, COMEI-O, FAZEI-O EM MEMÓRIA DE MIM. TOMAI O CÁLICE, EU O ENCHI POR VÓS, E AO FAZERDES, LEMBRAI-VOS DE MIM; FAZEI ISTO E BEBEI-O, QUANDO O FIZERDES, LEMBRAI-VOS DE MIM. ESTE É O NOVO CONCERTO QUE FAÇO SOMENTE POR VÓS, TOMAI-O, BEBEI-O, FAZEI-O, EM MEMÓRIA DE MIM.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('434', '434 - HÁ UM DOCE ESPÍRITO AQUI', 'HÁ UM DOCE ESPÍRITO AQUI E EU SEI QUE É O ESPÍRITO DE DEUS; JÁ PODEMOS TODOS PERCEBER A PRESENÇA DE JESUS, O SALVADOR.\n\nCORO DOCE PRESENÇA, PRESENÇA SANTA, VEM SOBRE NÓS, ENCHENDO-NOS DO TEU PODER; TE ADORAREMOS POR TUA PRESENÇA AQUI E QUE RENOVARÁ OS NOSSOS CORAÇÕES, LOUVADO SEJA O SENHOR\n\nSE TE ACHAS SALVO, E AO INVÉS, ESTÁS DÉBIL, OPRIMIDO E SEM VIGOR, RENDE-TE À PRESENÇA DE JESUS E O ESPÍRITO TE ENCHERÁ DE AMOR.\n\nCORO DOCE PRESENÇA, PRESENÇA SANTA, VEM SOBRE NÓS, ENCHENDO-NOS DO TEU PODER; TE ADORAREMOS POR TUA PRESENÇA AQUI E QUE RENOVARÁ OS NOSSOS CORAÇÕES, LOUVADO SEJA O SENHOR (BIS)', 'HÁ UM DOCE ESPÍRITO AQUI E EU SEI QUE É O ESPÍRITO DE DEUS; JÁ PODEMOS TODOS PERCEBER A PRESENÇA DE JESUS, O SALVADOR. CORO DOCE PRESENÇA, PRESENÇA SANTA, VEM SOBRE NÓS, ENCHENDO-NOS DO TEU PODER; TE ADORAREMOS POR TUA PRESENÇA AQUI E QUE RENOVARÁ OS NOSSOS CORAÇÕES, LOUVADO SEJA O SENHOR SE TE ACHAS SALVO, E AO INVÉS, ESTÁS DÉBIL, OPRIMIDO E SEM VIGOR, RENDE-TE À PRESENÇA DE JESUS E O ESPÍRITO TE ENCHERÁ DE AMOR.',  1);
