INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('437', '437 - VEM SOBRE MIM, ESPÍRITO DE DEUS', 'VEM SOBRE MIM, ESPÍRITO DE DEUS, A TI ME RENDO EM COMPLETA SUBMISSÃO VEM COM PODER, COM GRAÇA DOS CÉUS, VEM HOJE ENCHER MEU CORAÇÃO.\n\nCORO VEM, SIM, VEM, COMO A BRISA DA MANHÃ TRAZENDO O AROMA DOS JARDINS CELESTIAIS; VEM, SIM, VEM, COMO UM FORTE VENTO VEM, DE TI EU QUERO MAIS E MAIS.\n\nSEM TEU PODER NÃO PODEREI VIVER, VEM SOBRE MIM, Ó ESPÍRITO DE DEUS; SEM TUA UNÇÃO NÃO PODEREI VENCER, VEM HOJE ENCHER MEU CORAÇÃO.\n\nCORO VEM, SIM, VEM, COMO A BRISA DA MANHÃ TRAZENDO O AROMA DOS JARDINS CELESTIAIS; VEM, SIM, VEM, COMO UM FORTE VENTO VEM, DE TI EU QUERO MAIS E MAIS. FINAL: DE TI EU QUERO MAIS E MAIS.', 'VEM SOBRE MIM, ESPÍRITO DE DEUS, A TI ME RENDO EM COMPLETA SUBMISSÃO VEM COM PODER, COM GRAÇA DOS CÉUS, VEM HOJE ENCHER MEU CORAÇÃO. CORO VEM, SIM, VEM, COMO A BRISA DA MANHÃ TRAZENDO O AROMA DOS JARDINS CELESTIAIS; VEM, SIM, VEM, COMO UM FORTE VENTO VEM, DE TI EU QUERO MAIS E MAIS. SEM TEU PODER NÃO PODEREI VIVER, VEM SOBRE MIM, Ó ESPÍRITO DE DEUS; SEM TUA UNÇÃO NÃO PODEREI VENCER, VEM HOJE ENCHER MEU CORAÇÃO. CORO VEM, SIM, VEM, COMO A BRISA DA MANHÃ TRAZENDO O AROMA DOS JARDINS CELESTIAIS; VEM, SIM, VEM, COMO UM FORTE VENTO VEM, DE TI EU QUERO MAIS E MAIS.  DE TI EU QUERO MAIS E MAIS.',  1);
