INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('439', '439 - Ó VEM DERRAMAR', 'Ó VEM DERRAMAR, MEU SENHOR, A PORÇÃO DO TEU SANTO ESPÍRITO SOBRE MIM, SOBRE MIM. CORO PERMITE ENTRAR NA TUA GLÓRIA, SENTIR O TOQUE DE JESUS E EM MISTÉRIO ENTÃO FALAR, LOUVAR O NOME DE JESUS.\n\nQUERO EM COMUNHÃO TE ADORAR, TE EXALTAR, Ó TRIUNO DEUS, TEU AMOR PROCLAMAR. CORO (2X) PERMITE ENTRAR NA TUA GLÓRIA, SENTIR O TOQUE DE JESUS E EM MISTÉRIO ENTÃO FALAR, LOUVAR O NOME DE JESUS. FINAL: LOUVAR O NOME DE JESUS. (2X)', 'Ó VEM DERRAMAR, MEU SENHOR, A PORÇÃO DO TEU SANTO ESPÍRITO SOBRE MIM, SOBRE MIM. CORO PERMITE ENTRAR NA TUA GLÓRIA, SENTIR O TOQUE DE JESUS E EM MISTÉRIO ENTÃO FALAR, LOUVAR O NOME DE JESUS. QUERO EM COMUNHÃO TE ADORAR, TE EXALTAR, Ó TRIUNO DEUS, TEU AMOR PROCLAMAR. CORO  PERMITE ENTRAR NA TUA GLÓRIA, SENTIR O TOQUE DE JESUS E EM MISTÉRIO ENTÃO FALAR, LOUVAR O NOME DE JESUS.  LOUVAR O NOME DE JESUS.',  1);
INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('440', '440 - DERRAMAREI MEU ESPÍRITO', 'DERRAMAREI MEU ESPÍRITO SOBRE TODA A CARNE E VOSSOS FILHOS E VOSSAS FILHAS PROFETIZARÃO, OS VOSSOS VELHOS TERÃO SONHOS, VOSSOS MANCEBOS, VISÕES.\n\nCORO (2X) POIS DERRAMAREI MEU ESPÍRITO SOBRE TODA A CARNE. ESPERAI, POIS, A PROMESSA QUE DE MIM OUVISTES: JOÃO BATIZOU COM ÁGUA MAS EU VOS BATIZO COM MEU ESPÍRITO, COM MEU PODER, DE MIM SEREIS TESTEMUNHAS.', 'DERRAMAREI MEU ESPÍRITO SOBRE TODA A CARNE E VOSSOS FILHOS E VOSSAS FILHAS PROFETIZARÃO, OS VOSSOS VELHOS TERÃO SONHOS, VOSSOS MANCEBOS, VISÕES. CORO  POIS DERRAMAREI MEU ESPÍRITO SOBRE TODA A CARNE. ESPERAI, POIS, A PROMESSA QUE DE MIM OUVISTES: JOÃO BATIZOU COM ÁGUA MAS EU VOS BATIZO COM MEU ESPÍRITO, COM MEU PODER, DE MIM SEREIS TESTEMUNHAS.',  1);
