INSERT INTO hino (numero, nome, texto, texto_limpo, coletanea_id) VALUES ('443', '443 - HÁ UM RIO DE ÁGUAS VIVAS', 'HÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDEM DO TRONO DO SENHOR, ONDE PASSAM NASCE A VIDA, DOS DESERTOS FAZEM JARDINS DE AMOR.\n\nCORO OH! EU QUERO ESTAR ONDE CORREM ESSAS ÁGUAS DE PROFUNDA PAZ. ESSE RIO É O ESPÍRITO DERRAMADO SOBRE TODO O QUE CRÊ; ELE PASSA HOJE POR AQUI E TE QUER ENCHER DE GOZO E PODER.\n\nCORO OH! EU QUERO ESTAR ONDE CORREM ESSAS ÁGUAS DE PROFUNDA PAZ. SE QUERES JUBILAR POR TUA SALVAÇÃO, ENTRA NESSE RIO E VEM SEM TEMOR; DEIXA SUBMERGIR TODO O TEU SER NAS PROFUNDAS ÁGUAS DO SEU AMOR.', 'HÁ UM RIO DE ÁGUAS VIVAS QUE PROCEDEM DO TRONO DO SENHOR, ONDE PASSAM NASCE A VIDA, DOS DESERTOS FAZEM JARDINS DE AMOR. CORO OH! EU QUERO ESTAR ONDE CORREM ESSAS ÁGUAS DE PROFUNDA PAZ. ESSE RIO É O ESPÍRITO DERRAMADO SOBRE TODO O QUE CRÊ; ELE PASSA HOJE POR AQUI E TE QUER ENCHER DE GOZO E PODER. CORO OH! EU QUERO ESTAR ONDE CORREM ESSAS ÁGUAS DE PROFUNDA PAZ. SE QUERES JUBILAR POR TUA SALVAÇÃO, ENTRA NESSE RIO E VEM SEM TEMOR; DEIXA SUBMERGIR TODO O TEU SER NAS PROFUNDAS ÁGUAS DO SEU AMOR.',  1);
